INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '0', '0', 'Toć jest wypisanie narodu Jezusa Krystusa syna Dawidowego, syna Abrahamowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '1', '1', 'Z Abrahama poszedł Izaak, z Izaaka poszedł Jakub, a z Jakuba poszedł Juda i bracia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '2', '2', 'Z Judy poszedł Fares i Zara z Tamar, z Faresa poszedł Ezrom, z Ezroma poszedł Aram.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '3', '3', 'Z Arama poszedł Aminadab, z Aminadaba poszedł Naason, z Naasona poszedł Salmon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '4', '4', 'Z Salmona poszedł Boos z Rahab, z Boosa poszedł Obed z Rut, a z Obed poszedł Jesse.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '5', '5', 'Z Jessego poszedł król Dawid, a z Dawida króla poszedł Salomon, z tej, która była przed tym żoną Urjaszową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '6', '6', 'Z Salomona poszedł Roboam, z Roboama poszedł Abija, z Abije poszedł Asa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '7', '7', 'Z Asy poszedł Jozafat, a z Jozafata poszedł Joram, a z Jorama poszedł Ozjasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '8', '8', 'Z Ozjasza poszedł Joatam, z Joatama poszedł Achas, z Achasa poszedł Ezechijasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '9', '9', 'Z Ezechijasza poszedł Manases, z Manasesa poszedł Amon, z Amona poszedł Jozjasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '10', '10', 'Z Jozjasza poszedł Joakim, a z Joakima poszedł Jechonijasz i bracia jego, gdy byli zaprowadzeni do Babilonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '11', '11', 'A po zaprowadzeniu do Babilonu, z Jechonijasza poszedł Salatjel, a z Salatjela poszedł Zorobabel.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '12', '12', 'Z Zorobabela poszedł Abijud, z Abijuda poszedł Elijakim, z Elijakima poszedł Azor.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '13', '13', 'Z Azora poszedł Sadok, z Sadoka poszedł Achin, z Achina poszedł Elijud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '14', '14', 'Z Elijuda poszedł Eleazar, z Eleazara poszedł Matan, a z Matana poszedł Jakub.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '15', '15', 'Z Jakuba poszedł Józef mąż Maryjej, z której się narodził Jezus, który jest nazwan Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '16', '16', 'A tak wszytkiego pokolenia, które było od Abrahama aż do Dawida, jest czternaście, a od Dawida aż do zaprowadzenia do Babilonijej, pokolenia czternaście, a od zaprowadzenia do Babilonijej aż do Krystusa, pokolenia czternaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '17', '17', 'A narodzenie Jezusa Krystusa takowe było, abowiem gdy matka jego Marja była zmówiona za Józefa, pierwej niźli się zeszli, naleziona jest brzemienną z Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '18', '18', 'Tedy Józef mąż jej będąc sprawiedliwym i nie chcąc jej osławić, chciał ją potajemnie opuścić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '19', '19', 'A gdy on ty rzeczy myślił, oto Anioł Pański ukazał mu się we śnie mówiąc: Józefie synu Dawidów, nie bój się przyjąć Maryjej za małżonkę twą, abowiem to co się w niej począło jest z Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '20', '20', 'A ona porodzi syna, którego imię nazowiesz Jezus, abowiem on wybawi lud swój od grzechów jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '21', '21', 'A to się wszytko stało, aby się spełniło to, co jest powiedziano od Pana przez proroka tak mówiącego:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '22', '22', 'Oto niektóra panna będzie brzemienna i porodzi syna i nazowiesz imię jego Emmanuel, co jeślibyś wykładał, znaczy, Bóg z nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '23', '23', 'A Józef ocuciwszy się ze snu, uczynił jako mu był roskazał Anioł Pański i wziął żonę swoję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '0', '24', '24', 'I nie uznał jej, aż porodziła onego syna swojego pierworodnego i nazwał imię jego Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '0', '0', 'A gdy się był narodził Jezus w Betleem mieście ziemie żydowskiej za czasów Heroda  króla, oto przyszli mędrcy ze Wschodu słońca do Jeruzalem mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '1', '1', 'I gdzież jest on król żydowski, który się narodził? Abowiemeśmy widzieli gwiazdę jego na wschodu słońca i przyszlichmy chwalić go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '2', '2', 'Co gdy usłyszał król Herod, był zatrwożon i wszytko Jeruzalem z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '3', '3', 'I zebrawszy wszytki książęta kapłańskie i nauczyciele ludu, dowiedował się od nich gdzie się miał Krystus narodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '4', '4', 'A oni mu rzekli: W onym Betleem, które jest w ziemi Juda; gdyż tak jest napisano przez proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '5', '5', 'I ty Betleem, ziemio Judska, nie jesteś napodlejsze między książęty judskimi, abowiem z ciebie wynidzie wódz, który będzie rządził on mój lud izraelski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '6', '6', 'Tedy Herod wezwawszy potajemnie onych mędrców, pilnie się wywiadował od nich, którego czasu gwiazda się ukazała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '7', '7', 'I posławszy je do Betleem rzekl: Idźcie tam a pilnie się wywiadujcie o dzieciątku, a nalazszy je, oznajmicie mi, abym ja też przyjechawszy, chwalił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '8', '8', 'Oni tedy wysłuchawszy króla szli a oto gwiazda, którą byli widzieli na wschodu słońca, szła przed nimi, aż przyszedszy, stanęła nad miejscem, gdzie było dzieciątko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '9', '9', 'A gdy ujźrzeli onę gwiazdę, barzo wielką radością uradowali się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '10', '10', 'I uszedszy w dom, naleźli dzieciątko z Marją matką jego, a upadszy na ziemię, chwalili je, a otworzywszy skarby swoje, ofiarowali mu dary: złoto, kadzidło i mirrę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '11', '11', 'Wziąwszy tedy roskazanie od Boga we śnie, aby się nie wracali do Heroda, inszą drogą odeszli do ziemie swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '12', '12', 'A gdy już byli odeszli, oto Anioł Pański ukazał się we śnie Józefowi mówiąc: Wstań, a weźmi dzieciątko i matkę jego i uciekaj do Egiptu, a bądź tam ażci opowiem, abowiem to przyjdzie, iż Herod będzie szukał dzieciątka, aby je stracił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '13', '13', 'A on ocuciwszy się, wziął dzieciątko i matkę jego w nocy i poszedł do Egiptu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '14', '14', 'I tamże był aż do śmierci Herodowej, aby się spełniło to co Pan mówił przez proroka rzekąc: Jam wezwał syna swojego z Egiptu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '15', '15', 'Zatym Herod widząc, iż go nagrawali mędrcowie, barzo się rozgniewał i posławszy, kazał pomordować wszytki dzieci, które były w Betleem i po wszytkich granicach jego, ode dwu lat i młodsze, wedle którego się był pilnie wywiedział od mędrców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '16', '16', 'Tedy się wypełniło to, co jest rzeczono przez Jeremjasza proroka mówiącego:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '17', '17', 'Głos był słyszan w Ramie, narzekanie, płacz i łkanie wielkie, Rachel płacząc synów swoich, nie chciała być pocieszona dlatego, iż ich nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '18', '18', 'Ale po śmierci Herodowej, oto Anioł Pański we śnie ukazał się Józefowi w Egipcie mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '19', '19', 'Wstań, a weźmi dzieciątko i matkę jego, a idź do ziemie izraelskiej, abowiem ci, którzy dzieciątku o żywot stali, już pomarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '20', '20', 'Tedy on ocuciwszy się, wziął dzieciątko i matkę jego i przyszedł do ziemie izraelskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '21', '21', 'Ale gdy usłyszał, iż Archelaus królował w Judzie na miejscu Heroda ojca swojego, bał się tam idź, ale wziąwszy roskazanie od Boga we śnie, odszedł w stronę Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '1', '22', '22', 'A gdy tam przyszedł, mieszkał w mieście, które zową Nazaret, aby się wypełniło to co jest powiedziano przez proroki, iż miał być nazwan Nazarejczykiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '0', '0', 'A za onych czasów przyszedł Jan Krzciciel przepowiedając na puszczy żydowskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '1', '1', 'I mówiąc: Pokutujcie, abowiem przybliżyło się Królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '2', '2', 'Gdyż ten jest on, o którym mówił Ezajasz prorok rzekąc: Głos wołającego na puszczy, gotujcie drogę Pańską i proste czyńcie ścieszki jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '3', '3', 'Tenże Jan miał odzienie swoje z sierści wielbłądowej i pas rzemienny około biodry swojej, a pokarm jego były szarańce i miód leśny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '4', '4', 'Tedy wyszło do niego Jeruzalem i wszytek kraj około Jordanu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '5', '5', 'I byli krzczeni w Jordanie od niego, wyznawając grzechy swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '6', '6', 'A gdy widział wiele z Faryzeuszów i Saduceuszów przychodzących do krztu swego rzekł do nich: Rodzaje żmijów i któż wam pokazał abyście uciekali od gniewu przyszłego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '7', '7', 'Czyńcie tedy owoce godne upamiętania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '8', '8', 'A nie mniemajcie, aby się wam godziło mówić samym w sobie: Ojca mamy Abrahama. Abowiem wam powiedam, iż może Bóg i z tego kamienia wzbudzić dzieci Abrahamowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '9', '9', 'A już siekiera jest położona u korzenia drzew. Przeto wszelkie drzewo, które nie czyni owocu dobrego, bywa wycięte i w ogień wrzucone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '10', '10', 'Jać was krzczę wodą ku upamiętaniu, ale ten, który idzie za mną, mocniejszy jest niż ja, którego nie jestem godzien nosić trzewików. Tenci was okrzci Duchem świętym i ogniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '11', '11', 'Którego łopata jest w ręku jego, a będzie wyczyściał bojowisko swoje i zbierze pszenicę swoję do gumna, a plewy spali ogniem nie ugaszonym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '12', '12', 'Tedy Jezus przyszedł do Galilejej do Jordanu ku Janowi aby był okrzczon od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '13', '13', 'Ale Jan mu nie dopuszczał mówiąc: Ja muszę być okrzczon od ciebie, a ty idziesz do mnie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '14', '14', 'A Jezus odpowiedając rzekł k niemu: Zaniechaj teraz, abowiem tak się nam godzi wypełnić wszytkę sprawiedliwość, a także go zaniechał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '15', '15', 'A gdy był Jezus okrzczon, wnet wystąpił z wody, a oto się otworzyły jemu nieba i widział ducha Bożego zstępującego jako gołębicę i przychodzącego nań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '2', '16', '16', 'A oto głos z niebios mówiący: Ten jest syn on mój miły, na którym ja przestawam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '0', '0', 'Tedy Jezus był zawiedzion od Ducha na puszczę, aby był kuszon ode diabła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '1', '1', 'A gdy pościł czterdzieści dni i czterdzieści nocy, potym łaknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '2', '2', 'I przystąpiwszy do niego ten, który kusi rzekł: Jeśliżeś syn Boży rzecz, aby to kamienie stało się chlebem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '3', '3', 'Ale on odpowiedział i rzekł: Napisano jest; Nie samym tylko chlebem żyw będzie człowiek, ale wszelkiem słowem, które pochodzi z ust Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '4', '4', 'Tedy diabeł wziął go do miasta świętego i postawił go na wierzchu kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '5', '5', 'I rzekł k niemu: Jeśliżeś syn Boży, spuść się na dół. Abowiem napisano jest : Iż Aniołom swoim przykażę o tobie i będą cię na ręku nosić, abyś kiedy nie otracił nogi swej o kamień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '6', '6', 'A Jezus mu rzekł: Zasię napisano jest: Nie będziesz kusił Pana Boga twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '7', '7', 'Powtóre wziął go diabeł na górę wysoką i ukazał mu wszytki królestwa świata i chwałę ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '8', '8', 'I rzekł mu: Wszytki ty rzeczy tobie dam, jeśli upadszy chwalić mię będziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '9', '9', 'Tedy mu Jezus powiedział: Pódź precz Szatanie. Abowiem jest napisano: Będziesz chwalił Pana Boga twego, a jemu samemu służyć będziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '10', '10', 'I opuścił go diabeł, a oto Aniołowie przychodzili i służyli jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '11', '11', 'A gdy usłyszał Jezus, iż Jan był w więzieniu, wrócił się do Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '12', '12', 'I opuściwszy Nazaret, przyszedł i mieszkał w Kapernaum, które jest nad morzem, w granicach Zabulon i Naftali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '13', '13', 'Aby się wypełniło to co jest rzeczono przez Ezajasza proroka temi słowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '14', '14', 'Ziemia Zabulon i ziemia Neftali przy drodze Morskiej, za Jordanem, Galilea poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '15', '15', 'Lud, który siedział w ciemnościach, widział wielką światłość i tym, którzy leżeli w krainie i w cieniu śmierci wzeszła światłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '16', '16', 'Od onego czasu Jezus począł przepowiedać i mówić: Upamiętajcie się, abowiem się przybliża Królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '17', '17', 'A gdy Jezus chodził nad Morzem Galilejskiem, ujźrzał dwu braciej: Symona, którego zowią Piotrem i Andrzeja brata jego, którzy rzucali sieci w morze, abowiem oni byli rybitwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '18', '18', 'I rzekł im: Podźcie za mną, a uczynię was rybitwami ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '19', '19', 'A oni natychmiast opuściwszy sieci szli za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '20', '20', 'Tedy odszedszy z tamtąd, ujźrzał drugich dwu braciej, Jakuba syna Zebedeowego i Jana brata jego w łodzi z Zebedeem ojcem ich, gdy oprawiali sieci swoje i wezwał ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '21', '21', 'A oni zarazem opuściwszy łódź i ojca swego, szli za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '22', '22', 'I przeszedł Jezus wszytkie Galilea, ucząc w bożnicach ich i przepowiedając Ewanjeliję Królestwa, a uzdrawiając wszelaką chorobę i wszelaką niemoc między ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '23', '23', 'Na ten czas rozeszła się sława jego aż do wszytkiej Syrijej i ofiarowali mu wszytki, którzy się źle mieli, a byli zjęci rozmaitemi chorobami i dręczeniem, także opętane i lunatyki i powietrzem zarażone, które on uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '3', '24', '24', 'I szło za nim barzo wiele ludu od Galilejej, Dekapoli i z Jeruzalem i z ziemie żydowskiej i z krain, które są za Jordanem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '0', '0', 'A Jezus widząc ono zgromadzenie ludu, wstąpił na górę. A gdy siadł przyszli do niego jego uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '1', '1', 'Tedy otworzywszy usta swe uczył je mówiąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '2', '2', 'Błogosławieni są ubodzy w Duchu, abowiem ich jest Królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '3', '3', 'Błogosławieni są ci, którzy smętni są, abowiem pocieszeni będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '4', '4', 'Błogosławieni są ciszy, abowiem ziemię dziedzicznie osiędą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '5', '5', 'Błogosławieni są ci, którzy łakną i pragną sprawiedliwości, abowiem oni nasyceni będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '6', '6', 'Błogosławieni są miłosierni, abowiem oni miłosierdzie otrzymają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '7', '7', 'Błogosławieni są, którzy są serca czystego, abowiem oni Boga oglądają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '8', '8', 'Błogosławieni są spokojni, abowiem oni będą nazywani Synami Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '9', '9', 'Błogosławieni są ci, którzy cierpią prześladowanie dla sprawiedliwości, abowiem ich jest Królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '10', '10', 'Błogosławieni będziecie, gdy was sromocić i prześladować będą, mówiąc wszelaką rzecz złą przeciwko wam kłamając dla mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '11', '11', 'Radujcie się i weselcie, abowiem zapłata wasza wielka jest w niebie, gdyż tak prześladowali proroki, którzy przed wami byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '12', '12', 'Wy jesteście sól zieme. A jeśli sól smak straci, czymże solić będą? Do niczego się już nie godzi, jedno aby była precz wyrzucona i podeptana od ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '13', '13', 'Wy jesteście światłością świata. Nie może się miasto zataić na górze leżące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '14', '14', 'Ani zapalają świecę i kładą jej pod korzec, ale w lichtarz i świeci wszytkim. którzy są w domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '15', '15', 'Także też niechaj świeci światłość wasza przed ludźmi, aby obaczyli wasze uczynki dobre i chwalili ojca waszego, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '16', '16', 'Nie mniemajcie abym przyszedł niszczyć zakonu, abo proroki. Nie przyszedłem ja niszczyć zakonu, ale wypełnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '17', '17', 'Boć zaprawdę wam powiedam, aż przeminie niebo i ziemia, jedna literka abo jedna kreska nie przeminie z zakonu, aż się wszytko wypełni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '18', '18', 'Ktokolwiek tedy przestąpi jedno z tego to rozkazania namniejszego i tak ludzi nauczy, będzie zwan namniejszym w Królestwie niebieskiem. Ale ktokolwiek je czynić będzie i nauczy, ten będzie zwan wielkim w Królestwie niebieskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '19', '19', 'Abowiem powiedam wam: Jeśli nie będzie obfitowała sprawiedliwość wasza więcej niż nauczycielów i Faryzeuszów, nie wnidziecie do Królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '20', '20', 'Słyszeliście, iż rzeczono jest starym: Nie będziesz zabijał. A ktokolwiek zabije, będzie winien sądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '21', '21', 'Ale ja wam powiedam, iż każdy, który się gniewa na brata swego bez przyczyny, będzie winien sądu. A ktokolwiek rzecze bratu swemu: Racha! Będzie winien, aby był karan od rady. A ktokolwiek rzecze: Błaznie !  Będzie winien męki ogniowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '22', '22', 'Przeto jeśli dar twój przyniesiesz do ołtarza, a tam by wspomniał, iż brat twój ma co przeciwko tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '23', '23', 'Zostaw tam dar twój przed ołtarzem, a idź pierwej się z bratem swem pojednaj, a potym przyszedszy, ofiaruj dar twój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '24', '24', 'Bądź przyjacielem sprzeciwnikowi twemu rychło pókiś z nim w drodze, by cię snadź kiedy sprzeciwnik nie podał sędziemu, a sędzia ceklarzowi, a tak byś był wrzucon do ciemnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '25', '25', 'Zaprawdęć powiedam: Nie wynidziesz z tamtąd, aż oddasz ostatni kwartnik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '26', '26', 'Słyszeliście, iż powiedziano starym: Nie będziesz cudzołożył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '27', '27', 'Aleć ja powiedam, iż każdy który wejźrzy na niewiastę aby jej pożądał, już z nią cudzołóstwo popełnił w sercu swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '28', '28', 'Jeśli tedy prawe oko twoje ciebie w grzech przywodzi, wyrwi je, a zarzuć od siebie. Abowiemci pożyteczniej jest, aby zginął jeden z członków twoich, niźliby miało być podane wszytko ciało twe na męki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '29', '29', 'Jeśli cię też prawa ręka twoja w grzech przywodzi, odetni ją i zarzuć od siebie. Abowiemci pożyteczniej jest aby zginął jeden z członków twoich, niźliby miało być podane wszytko ciało na męki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '30', '30', 'K temu powiedziano jest: Ktokolwiek opuści żonę swoję, niech jej da list rozwodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '31', '31', 'Ale ja wam powiedam, iż ktobykolwiek opuścił żonę swoje oprócz przyczyny cudzołóstwa, przywodzi ją w cudzołóstwo. A ktoby opszczoną pojął, cudzołoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '32', '32', 'Słyszeliście zasię, iż jest powiedziano starym: Nie będziesz gwałcił przysięgi, ale oddasz Panu rzeczy przez przysięgę poślubione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '33', '33', 'Ale ja wam powiedam, abyście żadnym obyczajem nie przysięgali, ani na niebo, gdyż jest stolicą Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '34', '34', 'Ani na ziemię, abowiem jest podnóżkiem nóg jego, ani na Jeruzalem, abowiem jest miasto wielkiego Króla.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '35', '35', 'Ani na głowę twoję będziesz przysięgał, abowiem nie możesz uczynić jednego włosa białego abo czarnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '36', '36', 'Niechajże tedy mowa wasza będzie: Tak, tak. Nie, nie. A coby nad to więcej było, to jest od złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '37', '37', 'Słyszeliście, iż powiedziano: Oko za oko i ząb za ząb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '38', '38', 'Ale ja wam powiedam: Nie sprzeciwjajcie się złemu, owszem kto by cię kolwiek oderzył w prawy policzek twój, obróć mu i drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '39', '39', 'I temu, który się chce z tobą prawem obchodzić, a żupicę twoję wziąć, puść mu i płaszcz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '40', '40', 'A ktoby cię kolwiek gwałtem przymuszał na jednę milę, idź z nim dwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '41', '41', 'Temu, który cię prosi daj, a ni odmawiaj temu, który chce u ciebie pożyczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '42', '42', 'Słyszeliście, iż powiedziano: Będziesz miłował bliźniego twego, a będziesz miał w nienawiści nieprzyjaciela twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '43', '43', 'Ale ja wam powiedam: Miłujcie nieprzyjacioły wasze, błogosławcie ty, którzy was przeklinają, dobrze czyńcie tym, którzy was mają w nienawiści, módlcie się za tymi, którzy się na was targają i prześladują was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '44', '44', 'Abyście byli synami ojca waszego, który jest na niebie. Abowiem to sprawuje, iż wschodzi słońce jego na złe i na dobre i spuszcza deszcz na sprawiedliwe i na niesprawiedliwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '45', '45', 'Abowiem jeślibyście miłowali ty co was miłują, cóż za zapłatę mieć będziecie? A zaź i celnicy tego nie czynią?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '46', '46', 'A jeślibyście tylko bracia wasze łaskawie przyjmowali, cóż więcej czynicie? A zaź i celnicy tak nie czynią?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '4', '47', '47', 'Bądźcie wy tedy doskonali, jako ociec wasz, który jest w niebie doskonałym jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '0', '0', 'Strzeżcie się, abyście jałmużny waszej nie czynili przed ludźmi, dlatego abyście byli widzeni od nich, bo inaczej zapłaty mieć nie będziecie u ojca waszego, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '1', '1', 'Gdy tedy czynisz jałmużnę, nie każ przed sobą trąbić, jako pokryci ludzie czynią w bożnicach i po ulicach, aby byli w dobrym mniemamiu u ludzi. Zaprawdę powiedam wam, biorą zapłatę swoję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '2', '2', 'A gdy ty czynisz jałmużnę, niechaj nie wie lewica twoja, co prawica twoja czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '3', '3', 'Aby jałmużna twoja była w skrytości, a ociec twój, który obacza w skrytości, on odda tobie jawnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '4', '4', 'A gdy się modlisz, nie bądź jako pokryci ludzie, abowiem oni w zgromadzeniu na rogach ulic stojąc radzi się modlą, aby się ukazali ludziam. Zaprawdę wam powiedam, iż biorą zapłatę swoję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '5', '5', 'Ale ty gdy się modlisz, wnidź do pokoju swego, a zawarszy drzwi swoje, proś ojca twego, który jest w skrytości, a ociec twój, który obacza w skrytości, odda tobie jawnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '6', '6', 'A modląc się, nie bądźcie wielomówni, jako pogani, abowiemci oni mniemają, iż będą wysłuchani dla onej swej wielomówności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '7', '7', 'Nie bądźcie tedy im podobnymi, abowiemci wie ociec wasz czego potrzebujecie pierwej niż byście go prosili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '8', '8', 'Wy tedy tak się módlcie: Ojcze nasz, który jesteś w niebie. Święć się imię twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '9', '9', 'Przydź królestwo twoje. Bądź wola twa jako w niebie tak i na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '10', '10', 'Chleba naszego powszedniego daj nam dzisia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '11', '11', 'I odpuść nam nasze winy, jako i my odpuszczamy naszym winowajcom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '12', '12', 'I nie w wodź nas na pokuszenie, ale nas zbaw ode złego. Abowiem twoje jest królestwo i moc i chwała na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '13', '13', 'Bo jeśliże odpuścicie ludziam upadki ich, odpuści też wam ociec wasz on niebieski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '14', '14', 'Ale jeśli nie odpuścicie ludziam upadków ich i ociecci wasz nie odpuści wam upadków waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '15', '15', 'A gdy pościcie nie bądźcie jako pokryci ludzie smętnej twarzy. Abowiem oni twarzy swoje odmieniają, aby się ludziom zdało, iż poszczą. Zaprawdęć wam powiedam, żeć biorą zapłatę swoję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '16', '16', 'Ale ty gdy pościsz, pomaż głowę twoję i umyj oblicze twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '17', '17', 'Aby nie było jawno ludziom, iż pościsz, ale ojcu twojemu, który w skrytości jest. A ociec twój, który obacza w skrytości, odda tobie jawnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '18', '18', 'Nie skarbcie sobie skarbów na ziemi, gdzie mól i rdza kazi i gdzie złodzieje wykopywają i kradną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '19', '19', 'Ale skarbcie sobie skarby na niebie, gdzie ani mól, ani rdza kazi, gdzie też złodzieje nie wykopają i nie ukradną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '20', '20', 'Abowiem gdzie jest skarb wasz, tam będzie i serce wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '21', '21', 'Oko jestci światłem ciała, jeśliby tedy było proste oko twoje, wszytko ciało twoje światłe będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '22', '22', 'Jeśliby zaś oko twoje złościwe było, wszytko ciało twoje ciemne będzie. Jeśli tedy światło, które jest w tobie ciemnością jest, sama ciemność jakoż wielka jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '23', '23', 'Żaden nie może dwiema panom służyć, gdyż abo jednego będzie miał w nienawiści, a drugiego miłować będzie, abo przy jednym zostanie, a drugiego wzgardzi. Nie możecie Bogu służyć i Mamonie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '24', '24', 'Dlatego wam powiedam: Nie troszczcie się o żywot wasz, cobyście jedli abo cobyście pili, ani o ciało wasze w czymbyście chodzili. A zaź dusza nie jest ważniejsza niż pokarm? I ciało niźli odzienie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '25', '25', 'Patrzajcie na ptaki niebieskie, bo ani sieją, ani żną, ani zbierają do gumien, a wżdy Ociec wasz niebieski żywi je. Izali wy nie jesteście daleko ważniejszy niż oni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '26', '26', 'I któż z was troskliwie myśląc, może przydać ku wzrostowi swemu łokieć jeden?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '27', '27', 'A o odzienie przeczże się frasujecie? Uznajcie jako lilie polne rostą, nie pracują się ani przędą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '28', '28', 'A ja wam powiedam, iż ani Salomon we wszytkiej chwale swej nie był przyobleczon jako jedna z tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '29', '29', 'Jeśliż tedy trawę polną, która dziś jest, a nazajutrz bywa w piec wrzucona, Bóg tak przyodziewa, a zaź was nie daleko więcej, o mało ufający?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '30', '30', 'Nie bądźcie tedy troskliwi mówiąc: Cóż będziem jeść, abo co będziem pić, abo czym przyodziani będziemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '31', '31', '(Boć się o tym wszytkim pogani pytają) Abowiem ociec wasz niebieski wie, że tego wszytkiego potrzebujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '32', '32', 'Ale szukajcie naprzód Królestwa Bożego i sprawiedliwości jego, a ty wszytki rzeczy będą wam przydane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '5', '33', '33', 'Nie troszczcie się o jutrze, abowiem jutrzejszy dzień o swoje potrzeby troskać się będzie. Dosyćci ma dzień na swym utrapieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '0', '0', 'Nie sądźcie, abyście nie byli sądzeni. Abowiem, którym sądem sądzicie, sądzeni będziecie i którą miarą mierzycie, zasię wam odmierzono będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '2', '2', 'I czemu obaczasz źdźbło, które jest w oku brata twego, a bierzma, które jest w oku twym, nie obaczasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '3', '3', 'Abo jako mówisz bratu twemu: Dopuść iżci wyjmę źdźbło z oka twego, a oto bierzmo jest w oku twoim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '4', '4', 'Pokryty człowiecze, wyjmi pierwej ono bierzmo z oka twego, a tedy przejźrzysz, abyś wyjął źdźbło z oka brata twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '5', '5', 'Nie dawajcie psom to, co jest świętego, ani mieccie pereł waszych przed świnię, by ich kiedy nie podeptały nogami swemi i obróciwszy się, aby nie podrapały was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '6', '6', 'Proście, a będzie wam dano. Szukajcie, a najdziecie. Kołaccie, a będzie wam otworzono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '7', '7', 'Abowiem wszelki, który prosi, bierze. A kto szuka, najduje. A temu co kołace, otworzono będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '8', '8', 'I któż z was jest człowiek, którego jeśliby syn prosił o chleb, izali mu poda kamień?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '9', '9', 'Jeśliby o rybę prosił, izali mu poda węża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '10', '10', 'Jeśli wy tedy, będąc złymi, umiecie dary dobre dawać dzieciam waszym, jakoż więcej Ociec wasz, który jest na niebie, da rzeczy dobre tym, którzy go proszą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '11', '11', 'Wszytko tedy cobyście chcieli, aby wam ludzie czynili, także i wy im czyńcie, boć ten jest zakon i Prorocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '12', '12', 'Wchodźcie ciasną braną, abowiemci szeroka jest brana i przestrona droga, która zawodzi na stracenie, a wiele ich jest, którzy nią wchodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '13', '13', 'Abowiem ciasna jest brana i wąska droga, która prowadzi do żywota, a mało ich jest, którzy ją najdują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '14', '14', 'Strzeżcie się fałszywych proroków, którzy do was przychodzą w szatach owczych, ale wewnątrz są wilki drapieżymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '15', '15', 'Z owoców ich poznacie je. Izali zbierają z ciernia grona winne? Abo z ostu figi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '16', '16', 'Takci wszelkie drzewo dobre owoce dobre rodzi, ale spróchniałe drzewo owoce złe rodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '17', '17', 'Nie może dobre drzewo owoców złych rodzić, ani drzewo spróchniałe owoców dobrych rodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '18', '18', 'Wszelkie drzewo, które nie rodzi owocu dobrego, wycinają i w ogień wrzucają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '19', '19', 'A przeto z owoców ich poznacie je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '20', '20', 'Nie każdy, który mi mówi: Panie, Panie, wnidzie do Królestwa niebieskiego, ale który czyni wolę Ojca mego, który jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '21', '21', 'Wiele ich rzecze mi dnia onego: Panie, Panie, izaliśmy przez imię twoje nie prorokowali? I przez imię twe czartów nie wyganiali? I przez imię twoje wiele cudów nie czynili?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '22', '22', 'A tedy im jawnie powiem: Nigdym was nie znał. Odstąpcie odemnie wy, którzy nieprawości czynicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '23', '23', 'Wszelki tedy, który słucha odemnie tych słów i czyni je, ja go przypodobiam mężowi rostropnemu, który zbudował dom swój na opoce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '24', '24', 'Tedy spadł deżdż i przyszły rzeki i wiały wiatry i uderzyły na on dom, a on nie upadł, bo był na opoce ugruntowany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '25', '25', 'Także wszelki, który odemnie słucha tych słów a nie czyni ich, będzie przypodobion mężowi głupiemu, który zbudował dom swój na piasku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '26', '26', 'Tedy spadł deżdż i przyszły rzeki i wiały wiatry, a uderzyły na on dom i upadł i był upadek jego wielki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '27', '27', 'I stało się, gdy dokończył Jezus tych mów, zdumiewało się ono zebranie ludzi nad jego nauką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '6', '28', '28', 'Abowiem je uczył jako ten, który miał zwierzchność, a nie jako nauczycielowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '0', '0', 'A gdy zstąpił z góry, szło za nim wielkie zebranie ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '1', '1', 'A oto trędowaty przystępując dał mu chwałę mówiąc: Panie jeśli chcesz możesz mię oczyścić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '2', '2', 'I ściągnąwszy Jezus rękę dotknął się go mówiąc: Chcę. Bądź oczyścion. I był zarazem oczyścion trąd jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '3', '3', 'Tedy mu rzekł Jezus: Bacz abyś nikomu nie powiedał, ale idź ukaż się kapłanowi, ty ofiaruj dar, on który przykazał Mojżesz, na świadectwo im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '4', '4', 'A gdy Jezus wszedł do Kapernaum, przyszedł do niego setnik prosząc go i mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '5', '5', 'Panie, sługa mój leży w domu powietrzem zarażony i barzo utrapiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '6', '6', 'Tedy mu rzekł Jezus: Ja gdy przyjdę uzdrowię go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '7', '7', 'I odpowiedając setnik, rzekł: Panie, nie jestem godzien abyś wszedł pod dach mój, ale tylko rzec słówo, a będzie uzdrowion sługa mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '8', '8', 'Bociem i ja jest człowiek pod mocą inego postanowiony i mam pod sobą żołnierze i mówię temu: Idź, a on idzie, a drugiemu: Chodź, a on przychodzi. Słudze też mojemu: Czyń to, a czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '9', '9', 'A gdy to usłyszał Jezus, dziwował się i rzekł tym, którzy szli za nim: Zaprawdę wam powiedam, iżem i w Izraelu nie nalazł tak wielkiej wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '10', '10', 'I opowiedam wam. Iż wiele ich ze wschodu i z zachodu słońca przyjdzie i siedzieć będą z Abrahamem i z Izaakiem i z Jakubem w Królestwie niebieskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '11', '11', 'A synowie Królestwa będą wyrzuceni w ciemności zewnętrzne, tamże będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '12', '12', 'I rzekł Jezus setnikowi: Idź, a jakoś uwierzył niechci się stanie. I był uzdrowion jego sługa tejże godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '13', '13', 'A gdy przyszedł Jezus do domu Piotrowego, widział świekrę jego leżącą na łożu, a mającą gorączkę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '14', '14', 'I dotknął się ręki jej i opuściła ją gorączka, a także wstała i posługowała im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '15', '15', 'A gdy wieczór przyszedł, ofiarowali mu wiele opętanych, a on wygnał duchy słowem i wszytki, którzy się źle mieli uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '16', '16', 'Aby się wypełniło co jest rzeczono przez Ezajasza proroka mówiącego: Onci niemocy nasze przyjął i choroby nosił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '17', '17', 'A ujźrzawszy Jezus wielkie zgromadzenie ludu około siebie, kazał aby się przeprawili do drugiego brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '18', '18', 'I przystąpiwszy niektóry Nauczyciel rzekł mu: Mistrzu, pójdę za tobą gdzie jedno pójdziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '19', '19', 'Tedy mu rzekł Jezus: Liszki jamy mają i ptacy niebiescy gniazda, ale Syn człowieczy nie ma gdzieby głowę skłonił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '20', '20', 'A drugi z uczniów jego rzekł ku niemu: Panie, dopuść mi pierwej odyść i pogrześć ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '21', '21', 'I rzekł Jezus do niego: Pódź za mną, a niechaj umarli pogrzebią umarłe swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '22', '22', 'A gdy on wstąpił w łódź, weszli za nim uczniowie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '23', '23', 'A oto wzruszenie wielkie stało się na morzu, tak iż łódź była okrywana od wałów, a on spał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '24', '24', 'I przystapiwszy uczniowie jego, obudzili go mówiąc: Panie zachowaj nas, giniemyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '25', '25', 'I rzekł do nich: Przeczżeście bojaźliwi? O mało ufający! Tedy ocuciwszy się, zgromił wiatry i morze i stała się cichość wielka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '26', '26', 'A ludzie się dziwowali, mówiąc: Jakiż to jest, że go i wiatry i morze słuchają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '27', '27', 'A gdy sam przyjechał do brzegu drugiego, do krainy Gergesenów, zabieżeli mu dwa opętani z grobów wychodzący, barzo okrutni, tak iż nie mógł żaden prześć oną drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '28', '28', 'A oto wrzeszczeli mówiąc: I cóż my z tobą mamy Jezusie synu Boży? Przyszedłeś tu przed czasem dręczyć nas?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '29', '29', 'I była daleko od nich trzoda wielka świni pasąca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '30', '30', 'A czartowie prosili go mówiąc: Jeśli nas wyrzucasz, dopuść nam wniść w trzodę świni onych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '31', '31', 'I rzekł do nich: Idźcie. A oni wyszedszy, weszli w onę trzodę świni, a oto porwała się ona wszytka trzoda świni z przykra w morze i pozdychały w wodach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '32', '32', 'I zuciekali pastyrze, a odszedszy do miasta, opowiedzieli wszytko i to co się z onymi opętanymi stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '7', '33', '33', 'A oto wszytko miasto wyszło przeciwko Jezusowi i ujźrzawszy go, prosili aby z ich granic poszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '0', '0', 'Tedy on wszedszy w łódź, przeprawił się i przyszedł do miasta swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '1', '1', 'A oto ofiarowali mu powietrzem zarażonego, który leżał na łożu. A obaczywszy Jezus wiarę ich, rzekł powietrzem zarażonemu: Bądź dobrego serca synu, odpuszczoneć są grzechy twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '2', '2', 'A oto mówili niektórzy z nauczycielów sami w sobie: Ten bluźni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '3', '3', 'A obaczywszy Jezus myśli ich rzekł: Przeczże wżdy myślicie złe rzeczy w sercach waszych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '4', '4', 'Abowiem co jest łatwiej rzec: Odpuszczoneć są grzechy twoje, czyli rzec: Wstań a chodź?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '5', '5', 'Ale iżbyście wiedzieli, że moc ma Syn człowieczy na ziemi odpuszczać grzechy (tedy rzekł powietrzem zarażonemu): Wstań a weźmi łoże twe, a idź do domu twojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '6', '6', 'A on wstał i poszedł do domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '7', '7', 'Co ujźrzawszy oną wielkość ludzie, dziwowali się i chwalili Boga, który był dał takową moc ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '8', '8', 'A gdy z tamtąd szedł Jezus, ujźrzał niektórego człowieka siedzącego na cle, którego zwano Mateusz. I rzekł ku niemu: Pódź za mną. A on wstawszy poszedł za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '9', '9', 'I stało się gdy on siedział u stołu w domu jego, oto wiele celników i grzeszników, którzy byli przyszli, wespół siedzieli z Jezusem i z uczniami jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '10', '10', 'A gdy to obaczyli Faryzeuszowie, rzekli uczniom jego: Przeczże z celniki i z grzeszniki je mistrz wasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '11', '11', 'I usłyszawszy to Jezus rzekł im: Którzy zdrowi są nie potrzebują lekarza, ale ci co się źle mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '12', '12', 'Ale szedszy nauczcie się co jest: Miłosierdzia chcę a nie ofiary, bom ci ja nie przyszedł abym wzywał sprawiedliwych, ale grzeszników tu upamiętaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '13', '13', 'Tedy przyszli do niego uczniowie Janowi mówiąc: Przecz my i Faryzeuszowie pościmy często, a uczniowie twoi nie poszczają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '14', '14', 'I rzekł im Jezus: Izali mogą wezwani na wesele smutnymi być póki z nimi jest oblubieniec? Ale przyjdą dni, gdy będzie wzięt od nich oblubieniec, a tedyć będą pościć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '15', '15', 'A teżci nikt nie wpuszcza płata nowo uczynionego sukna w wiotchą szatę, abowiem on płat jej ujmnie nieco z szaty i stawa się gorsze przedarcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '16', '16', 'Wina też młodego nie leją w stare naczynia, bo inaczej pukają się naczynia i wino się rozlewa i naczynia giną, ale zlewają młode wino w naczynia nowe, a tak oboje społem bywają zachowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '17', '17', 'A gdy on ty rzeczy mówił, oto niektóre książę przyszedszy dawało mu chwalę, mówiąc: Córka moja dopiero skonała, ale pódź, a włóż na nię rękę twoję, a będzie żyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '18', '18', 'Tedy wstawszy Jezus, szedł za nim i uczniowie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '19', '19', 'A oto niewiasta, która płynienie krwie przez dwanaście lat cierpiała, przystąpiwszy z tyłu dotknęła się kraju odzienia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '20', '20', 'Bo mówiła sama w sobie: Bych się tylko dotknęła odzienia jego, będę zachowana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '21', '21', 'A Jezus obróciwszy się i ujźrzawszy ja rzekł: Bądź serca dobrego córko, wiara twoja cię zachowała. I zachowana jest niewiasta od onej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '22', '22', 'A gdy przyszedł Jezus w dom onego książęcia, ujźrzawszy piszczki i nie mało ludzi co zgiełk czynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '23', '23', 'Rzekł im: Odstąpcie, abowiemci nie umarła dzieweczka ale śpi. I śmiali się z niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '24', '24', 'Ale gdy oni ludzie byli wygnani precz, wszedszy ujął ją za rękę jej i powstała dzieweczka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '25', '25', 'I rozeszła się ta sława po wszytkiej onej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '26', '26', 'A gdy Jezus przechodził z onąd, szli za nim dwa niewidomi, wołając i mówiąc: Zmiłuj się nad nami Synu Dawidów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '27', '27', 'A gdy on wszedł w dom, przyszli k niemu oni niewidomi, którym rzekł Jezus: Wierzycie iż to mogę uczynić? A oni mu rzekli: Tak Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '28', '28', 'Tedy się dotknął oczu ich mówiąc: Wedle wiary waszej niechaj się wam stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '29', '29', 'I otworzyły się oczy ich, a Jezus im srodze zakazał mówiąc: Baczcie aby nikt nie wiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '30', '30', 'Lecz oni wyszedszy, rozsławiali go po wszytkiej onej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '31', '31', 'A gdy oni wychodzili, oto ofiarowali mu człowieka niemego, który był opętany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '32', '32', 'A gdy był wygnan on diabeł, przemówił niemy i dziwowało się ono zebranie ludzi mówiąc: Nigdy się rzecz takowa nie okazała w Izraelu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '33', '33', 'Ale Faryzeuszowie powiedzieli: Przez książę Diabłów wygania diabły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '34', '34', 'I obchodził Jezus wszytki miasta i wsi, ucząc w bożnicach ich i przepowiedając Ewanjeliję Królestwa, a uzdrawiając wszelką chorobę i wszelką niemoc między ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '35', '35', 'A gdy ujźrzał tak wiele ludzi, to zlitował się nad nimi, iż byli spracowani i rosproszeni, jako owce, nie mające pastyrza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '36', '36', 'Tedy rzekł uczniom swoim: Jestci samo żniwo wielkie, ale robotników mało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '8', '37', '37', 'Proścież tedy Pana żniwa, aby wysłał robotniki na żniwo swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '0', '0', 'Wezwawszy tedy dwanaście uczniów swoich, dał im moc nad duchy nieczystymi, aby je wyganiali i uzdrawiali wszelaką chorobę i wszelaką niemoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '1', '1', 'A dwunaście Apostołów ty są imiona: Pierwszy Symon, który jest rzeczon Piotr i Andrzej brat jego, Jakub Syn Zebedeów i Jan brat jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '2', '2', 'Filip i Bartłomiej, Tomasz i Mateusz on celnik, Jakub syn Alfeuszów i Lebeusz przezwiskiem Tadeusz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '3', '3', 'Szymon Kannitczyk i Judasz Iskarjotes, który go też wydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '4', '4', 'Tych dwunaście je posłał Jezus, dawszy im roskazania mówiąc: Na drogę poganów nie udawajcie się i do miast Samarytanów nie chodźcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '5', '5', 'Ale raczy idźcie do owiec zginionych domu izraelskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '6', '6', 'A szedszy, przepowiedajcie mówiąc: Przybliżyło się Królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '7', '7', 'Niemocne uzdrawiajcie, trędowate oczyściajcie, wzbudzajcie umarłe, diabły wyganiajcie, daremnieście wzięli, darem dawajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '8', '8', 'Nie nabywajcie złota ani śrebra, ani miedzi w trzosy wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '9', '9', 'Ani tajstry na drogę, ani dwu sukień, ani trzewików, ani laski. Abowiem godzien jest robotnik żywności swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '10', '10', 'A do którego miasta abo wsi wnidziecie, wywiadujcie się, któryby w niej był godzien i tamże mieszkajcie pókibyście z onąd nie wyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '11', '11', 'Wchodząc też w dom pozdrawiajcie go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '12', '12', 'A jeśli by był on dom godny, niech nań przyjdzie pokój wasz. A jeśliby nie był godny, pokój wasz niech się wróci do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '13', '13', 'I ktoby was nie przyjął, ani waszych mów słuchał, wychodząc z domu abo z miasta onego, wybijcie proch nóg waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '14', '14', 'Zaprawdęć wam powiedam: Lżej będzie ziemi Sodomczyków i Gomorrejczyków w dzień sądny, niźli miastu onemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '15', '15', 'Oto ja was posyłam jako owce wpośrodku wilków. Bądźcież tedy ostrożnemi jako wężowie, a szczyrymi jako gołębice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '16', '16', 'Przytym strzeżcie się ludzi. Abowiem was podadzą do zgromadzenia i w bożnicach swoich was biczować będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '17', '17', 'Owszem przed książęta i przed króle będziecie wodzeni dla mnie, na świadectwo im i poganom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '18', '18', 'Ale gdy was podadzą, nie troszczcie się jako, abo cobyście mówili, abowiem wam będzie dano onej godziny cobyście mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '19', '19', 'Abowiem nie wy jesteście, którzy mówicie, ale Duch Ojca waszego, który mówi w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '20', '20', 'I wydać brat brata na śmierć, a ociec syna. I powstaną dzieci przeciw rodzicom, a mordować je będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '21', '21', 'Wy też będziecie nienawidzeni od wszytkich dla imienia mego. Ale kto wytrwa aż do końca, ten będzie zachowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '22', '22', 'A gdy was prześladować będą w onym mieście, uciekajcie do drugiego, boć wam zaprawdę powiedam, że nie przejdziecie miast izraelskich, póki nie przyjdzie Syn człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '23', '23', 'Nie jestci uczeń nad mistrza, ani sługa nad Pana swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '24', '24', 'Dosyć uczniowi aby był jako mistrz jego, a sługa jako Pan jego. Jeśliżeć samego gospodarza Beelzebułem nazwali, jakoż daleko więcej domowniki jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '25', '25', 'Nie bójcież się ich tedy, abowiem nie jest nic skrytego coby odkryto być nie miało, ani tajemnego nic nie jest, coby wiedzieć nie miano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '26', '26', 'Co wam w ciemności mówię, powiedajcie na światle, a co w ucho słyszycie, przepowiedajcie na domiech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '27', '27', 'A nie bójcie się tych, którzy zabijają ciało, a duszę zabić nie mogą, ale raczej bójcie się tego, który i duszę i ciało może zatracić w piekle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '28', '28', 'Izali dwu wróblików za pieniądz nie przedają? A wżdy jeden z tych nie upadnie na ziemię okrom Ojca waszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '29', '29', 'Włosy też głowy waszej wszytki policzone są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '30', '30', 'Nie bójcież się tedy, wiele wróblików wy przewyszszacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '31', '31', 'Wszelki tedy, któryby mię wyznał przed ludźmi, wyznam go ja też przed ojcem moim, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '32', '32', 'A któryby się mnie zaprzał przed ludźmi, zaprzę się go i ja przed Ojcem moim, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '33', '33', 'Nie mniemajcie żebym przyszedł puszczać pokój na ziemię. Nie przyszedłem abym puścił pokój, ale miecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '9', '34', '34', 'Bom przyszedł wadzić człowieka z ojcem jego i córkę z matką jej i niewiastkę z świekrą jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '0', '0', 'I stało się gdy Jezus przestał roskazować dwiemanaście zwolennikom swoim, poszedł zonąd aby uczył i przepowiedał w miastach ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '1', '1', 'A Jan usłyszawszy w więzieniu uczynki Krystusowe, posławszy dwu z uczniów swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '2', '2', 'Rzekł mu: I tyżeś jest on, któryś miał przyść, czyli inszego mamy czekać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '3', '3', 'A odpowiedając Jezus rzekł im: Szedszy oznajmcie Janowi ty rzeczy, które słyszycie i widzicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '4', '4', 'Ślepi wzrok biorą, a chromi chodzą, trędowaci bywają oczyścieni, a głuszy słyszą, umarli bywają wskrzeszeni i ubogim opowiedana bywa Ewanjelija.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '5', '5', 'A błogosławiony jest ten, który się nie zgorszy we mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '6', '6', 'A gdy oni odchodzili, począł Jezus mówić wielkiemu zgromadzeniu ludu o Janie: Cożeście wyszli na puszcza widzieć? Izali trzcinę chwiejącą się od wiatru?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '7', '7', 'Ale coście byli wyszli widzieć? Izali człowieka w roskoszne szaty obleczonego? Oto którzy roskoszne szaty noszą, w domach królewskich są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '8', '8', 'Ale coście byli wyszli widzieć? Izali proroka? Owszem powiedam wam, więcej niż proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '9', '9', 'Boć ten jest, o którym napisano jest: Oto ja posyłam posła swego przed oblicznością twoją, który zgotuje drogę twoję przed tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '10', '10', 'Zaprawdęć wam powiedam, że między tymi, którzy się z niewiast rodzą, nie powstał żaden więczszy nad Jana Krzciciela. Ale ten, który jest namniejszym w Królestwie niebieskiem, więczszy jest niźli on.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '11', '11', 'A od czasów Jana Krzciciela aż dotąd Królestwu niebieskiemu gwałt się dzieje, a gwałtownicy otrzymawają je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '12', '12', 'Bo wszyscy Prorocy i zakon aż do Jana prorokowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '13', '13', 'A jeśli chcecie to przyjąć, tenci jest Eljasz on, który miał przyść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '14', '14', 'Kto ma uszy ku słuchaniu, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '15', '15', 'Ale komu przypodobam ten rodzaj? Podobny jest dziatkom, które siedzą na rynkoch i wołają ku towarzyszom swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '16', '16', 'A mówią: Graliśmy wam na piszczałce, a wyście nie tańcowali. Narzekaliśmy wam, a wyście nie płakali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '17', '17', 'Abowiem przyszedł Jan nie jedząc ani pijąc i powiedają iż diabelstwo ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '18', '18', 'Przyszedł Syn człowieczy jedząc i pijąc i mówią: Oto ten człowiek jest obżerca i pijanica, przyjaciel celników i grzeszników, ale usprawiedliwiona jest mądrość od synów swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '19', '19', 'Tedy począł tak wyrzucać na oczy miastam, w których się stało barzo wiele cudów jego, że się nie upamiętały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '20', '20', 'Biada tobie  Chorazym, biada tobie Betsajdan, bo gdyby się były w Tyrze i w Sydonie ty cuda stały, które się stały u was, jużby się były dawno siedząc z worem i z popiołem upamiętały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '21', '21', 'Nad to powiedam wam, iż Tyrowi i Sydonowi lżej będzie w dzień sądny niźli  wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '22', '22', 'A ty Kapernaum, któreś się wywyższyło aż do nieba, strącone będziesz aż do piekła. Bo gdyby się były w Sodomach ty cuda stały, które się stały w tobie, trwałyby były aż do dnia dzisiejszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '23', '23', 'Ktemu powiedam wam, iż znośniej będzie ziemi Sodomskiej w dzień sądny niźli tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '24', '24', 'W on czas odpowiedając Jezus rzekł: Dziękuję tobie Ojcze Panie nieba i ziemi, żeś ty rzeczy zataił przed mądremi i rostropnemi, a oznajmiłeś je niemowiątkom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '25', '25', 'Takci jest Ojcze, gdyżci się tak upodobało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '26', '26', 'Wszytki rzeczy dane mi są od Ojca mego i nie zna nikt syna jedno Ojciec, ani Ojca nikt nie zna jedno Syn, a  komuby chciał Syn objawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '27', '27', 'Chodźcie do mnie wszyscy, którzy jesteście spracowani i obciążeni, a ja dam wam odpoczynienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '28', '28', 'Weźmicie jarzmo moje na się, a uczcie się ode mnie, iżem jest cichy i pokornego serca, a najdziecie odpoczynienie duszam waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '10', '29', '29', 'Abowiem jarzmo moje jest łatwie i brzemię moje lekkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '0', '0', 'W on czas szedł Jezus w Sobotę przez zboża, a uczniom jego jeść się zachciało i poczęli rwać kłosie i jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '1', '1', 'A ujźrzawszy je Faryzeuszowie, rzekli mu: Oto uczniowie twoi czynią czego się nie godzi czynić w Sobotę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '2', '2', 'A on im powiedział: Nie czytaliście co uczynił Dawid, gdy się mu jeść zachciało i tym, którzy z nim byli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '3', '3', 'Jako wszedł do domu Bożego i chleby pokładne jadł, których się mu nie godziło jeść, ani tym, którzy z nim byli, tylko samym kapłanom?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '4', '4', 'Aboście nie czytali w zakonie, że w Soboty kapłani w kościele gwałcą Sobotę, a niewinni są?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '5', '5', 'A ja wam powiedam, iż na tym miejscu jeden jest więczszy niż kościół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '6', '6', 'A gdybyście wiedzieli, co to jest, Miłosierdzia chcę, a nie ofiary, nie osądzalibyście niewinnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '7', '7', 'Abowiemci i Soboty Panem jest Syn człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '8', '8', 'I poszedszy ztamtąd, przyszedł do bożnice ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '9', '9', 'A oto był tam niektóry co miał uschłą rękę i pytali go mówiąc: Godzili się w Soboty uzdrawiać? Aby go oskarżyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '10', '10', 'Którym on powiedział: I który człowiek z was będzie, któryby mając owcę jednę, gdyby ona upadła w dół w Sobotę, nie ujął jej i nie podniósł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '11', '11', 'A jako daleko ważniejszy jest człowiek nad owcę? Przetoż się godzi w Soboty dobrze czynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '12', '12', 'Tedy rzekł człowiekowi onemu: Wyciągni rękę twoję. A on wyciągnął i stała się zdrowa jako i druga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '13', '13', 'Potym Faryzeuszowie wyszedszy zaczęli radę przeciw niemu jakoby go stracili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '14', '14', 'Ale Jezus poznawszy to poszedł  ztamtąd i szło za nim wielkie zgromadzenie ludu i uzdrowił ony wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '15', '15', 'I srodze im zakazał, aby go nie objawiali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '16', '16', 'Aby się wypełniło to, co było powiedziano przez Ezajasza proroka mówiącego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '17', '17', 'Oto sługa mój, któregom obrał, miłośnik mój, na którym przystawa dusza moja. Położę ducha mojego na nim, a sąd poganom opowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '18', '18', 'Nie będzie się wadził ani będzie wołał, ani żaden usłyszy głosu jego po ulicach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '19', '19', 'Trzciny stłuczonej nie złamie, a lnu kurzącego się nie zagasi, aż wysadzi sąd ku zwycięstwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '20', '20', 'A w imieniu jego pogani będą nadzieję mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '21', '21', 'Tedy mu przywiedzion jest opętany, ślepy i niemy. I uzdrowił go tak, iż on, który był ślepym i niemym, mówił i widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '22', '22', 'I zdumiało się wszytko zgromadzenie ludu i mówili: Izali ten jest on syn Dawidów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '23', '23', 'Co Faryzeuszowie usłyszawszy rzekli: Ten nie wygania diabłów jedno przez Beelzebuła książę diabłów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '24', '24', 'Ale Jezus wiedząc myśli ich rzekł im: Wszelkie królestwo rozdzielone przeciwko sobie, bywa spustoszone. I wszelkie miasto, abo dom rozdzielony przeciwko sobie, nie ostoi się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '25', '25', 'Ale jeśli szatan szatana wygania, przeciwko sobie jest rozdzielon. Jakoż tedy ostoi się królestwo jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '26', '26', 'A jeśliże ja przez Beelzebuła wyganiam diabelstwa, synowie waszy przez kogoż wyganiają? A przetoż oni będą sędziami waszymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '27', '27', 'Ale jeśliż ja Duchem Bożym wyganiam diabelstwa, iścieć do was przyszło Królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '28', '28', 'Abowiem jako może kto wniść do domu mocarza jakiego, a statek jego zabrać, jeśliby pierwej nie związał mocarza onego? Gdyż tak dopiero dom jego złupić może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '29', '29', 'Kto nie jest ze mną, przeciwko mnie jest, a kto nie zbiera ze mną, rozprasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '30', '30', 'A przetoż powiedam wam: Wszelki grzech i bluźnierstwo będzie odpuszczone ludziom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '31', '31', 'A ktokolwiek będzie mówił przeciwko Synowi człowieczemu, odpuszczono mu będzie, ale ktoby mówił przeciwko Duchowi Świętemu, nie będzie mu odpuszczono ani w tym wieku, ani w przyszłym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '32', '32', 'Abo czyńcie drzewo dobre i owoc jego dobry, abo czyńcie drzewo zgniłe i owoc jego zgniły. Abowiem z owocu drzewo bywa poznane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '33', '33', 'Rodzaje żmijów, jakobyście mogli rzeczy dobre mówić będąc złemi? Gdyż z obfitości serca usta mówią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '34', '34', 'Dobry człowiek z dobrego skarbu serca wywodzi rzeczy dobre, a zły człowiek ze złego skarbu wywodzi złe rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '35', '35', 'Ale powiedam wam, iż którebykolwiek słowo próżne mówili ludzie, dadzą z niego liczbę w dzień sądny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '36', '36', 'Abowiem z powieści twoich będziesz usprawiedliwion i z powieści twoich będziesz osądzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '37', '37', 'Tedy odpowiedzieli niektórzy z nauczycielów i Faryzeuszów mówiąc: Mistrzu, żądamy od ciebie znamię widzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '38', '38', 'Ale on odpowiedając rzekł im: Rodzaj zły i cudzołożny znamienia szuka, ale mu nie będzie znamię dano, jedno znamię Jonasza proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '39', '39', 'Abowiem jako był Jonasz w brzuchu wieloryba trzy dni i trzy nocy, także będzie Syn człowieczy w sercu zieme trzy dni i trzy nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '40', '40', 'Mężowie z Niniwe powstaną w sąd z tym narodem i osądzą ji, iż oni upamiętali się na przepowiedanie Jonaszowe. A oto więcej jest na tym miejscu niż Jonasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '41', '41', 'Królowa z południa powstanie w sąd z tym narodem i osądzi ji przeto, iż ona przyszła z granic ziemie, aby słuchała mądrości Salomonowej, a oto więcej jest na tym miejscu niż Salomon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '42', '42', 'A gdy nieczysty duch wynidzie od człowieka, przechodzi się po miejscach gdzie wód nie masz, szukając odpoczynienia, a iż go nie najduje,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '43', '43', 'Tedy mówi: Wrócę się do domu mego z kądem wyszedł. A przyszedszy najduje ji próżnujący i umieciony i ochędożony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '44', '44', 'Tedy idzie i bierze z sobą siedmi inszych duchów gorszych niż sam jest i wszedszy, mieszkają tam, a koniec onego człowieka stawa się gorszy niż początek. Takci się stanie i temu narodowi złemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '45', '45', 'A gdy on jeszcze mówił do wielkiego zgromadzenia ludu, oto matka i bracia jego stali na dworze chcąc z nim mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '46', '46', 'I rzekł mu niektóry: Oto matka twoja i bracia twoi na dworze stoją chcąc ku tobie mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '47', '47', 'A on odpowiedając rzekł temu, który mu to był powiedział: Któraż jest matka moja? I którzy są bracia moi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '48', '48', 'I ściągnąwszy rękę swą na ucznie swoje rzekł: Oto matka moja i bracia moi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '11', '49', '49', 'Abowiem ktobykolwiek uczynił wolą ojca mojego, który jest na niebie, tenci jest bratem moim i siostrą i matką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '0', '0', 'Onego dnia wyszedszy Jezus z domu, siedział nad morzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '1', '1', 'I zebrało się do niego wielkie zgromadzenie ludu, tak iż on wszedszy w łódź siedział, a wszytko zgromadzenie ludu na brzegu stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '2', '2', 'I mówił do nich wiele przez podobieństwa rzekąc: Oto niektóry rozsiewca wyszedł ku sianiu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '3', '3', 'A gdy on siał, niektóre ziarna padły przy drodze i przybiegli ptacy a pożarli je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '4', '4', 'Drugie zasię padły na miejsca opoczyste, gdzie ziemię niewiele miały i wnet weszły, dlatego że nie miały głębokości ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '5', '5', 'A gdy słońce weszło, wygorzały, a dlatego, iż nie miały korzenia, uschły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '6', '6', 'Drugie też padły w ciernie, a ciernie zrosło ku górze i zadusiło je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '7', '7', 'Drugie zasię padły na ziemię dobrą i dały owoc. Jedno tyle stokroć, drugie tylo sześćdziesiąt kroć, a drugie zaś tyle trzydzieści kroć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '8', '8', 'Kto ma uszy ku słuchaniu, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '9', '9', 'Tedy przystąpiwszy uczniowie rzekli mu: Dlaczego im przez podobieństwa mówisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '10', '10', 'A on odpowiedając rzekł im: Przeto, iż wam dano jest wiedzieć tajemnice Królestwa niebieskiego, a onym nie jest dano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '11', '11', 'Abowiem ktokolwiek ma, będzie mu dano i stanie się obfitszym, a ktokolwiek nie ma i to co ma, będzie wzięto od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '12', '12', 'Dlategoć im przez podobieństwa mówię, iż widząc nie widzą i słysząc nie słyszą, ani rozumieją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '13', '13', 'A przeto wypełnia się w nich proroctwo Ezajaszowe, które mówi: Słyszeniem słuchać będziecie, a nie zrozumiecie i widząc widzieć będziecie, a nie obaczycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '14', '14', 'Abowiem rostyło serce ludu tego, a uszyma cięszko słyszeli i oczyma swemi mrugali, żeby kiedy oczyma nie oglądali i uszyma nie słyszeli, a sercem nie zrozumieli i nie nawrócili się, a ja ich nie uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '15', '15', 'Ale oczy wasze szczęśliwe są, iż widzą i uszy wasze iże słyszą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '16', '16', 'Abowiem zaprawdę wam powiedam, iż wiele proroków i sprawiedliwych ludzi, żądali widzieć rzeczy, które widzicie, a nie widzieli i słyszeć rzeczy, które słyszycie, a nie słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '17', '17', 'Wy tedy słuchajcie podobieństwa onego rozsiewce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '18', '18', 'Kiedykolwiek kto słyszy słowo o onym królestwie, a nie rozumie, przychodzi on zły i gwałtem bierze to co jest wsiano w sercu jego, a tenci jest, który przy drodze nasienie przyjął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '19', '19', 'A ten, który na opoczystych miejscach nasienie przyjął, tenci jest który słucha słowa i zarazem je z radością przyjmnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '20', '20', 'Ale nie ma korzenia w sobie, owszem docześny jest. A gdy przyjdzie utrapienie abo prześladowanie dla słowa, wnet się gorszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '21', '21', 'A on, który w cierniu nasienie przyjął jestci ten, który słucha słowa, ale frasunek wieku tego i zawodzenie bogactw zaduszą słowo i stawa się niepożyteczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '22', '22', 'On zasię, który na dobrej ziemi przyjął nasienie tenci jest, który słucha słowa i rozumie, a tenci owoc przynosi i czyni jeden tyle stokroć, drugi zaś tyle sześćdziesiątkroć, a drugi tyle trzydzieścikroć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '23', '23', 'Drugie podobieństwo powiedział im mówiąc: Przypodobane jest Królestwo niebieskie człowiekowi, który siał dobre nasienie na roli swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '24', '24', 'A gdy ludzie spali, przyszedł nieprzyjaciel jego i nasiał kąkolu między pszenicą i poszedł precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '25', '25', 'A gdy wschodziła trawa i owoc czyniła, tedy się pokazał i kąkol.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '26', '26', 'I przystąpiwszy słudzy gospodarscy rzekli mu: Panie, izaś ty nie siał dobrego nasienia na roli twojej? Zkąd tedy kąkol ma?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '27', '27', 'A on rzekł im: Nieprzyjaciel człowiek to uczynił. I rzekli słudzy do niego: A chceżże tedy abyśmy szli i pozbierali ji?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '28', '28', 'A on rzekł: Nie, byście snać zbierając kąkol nie wykorzenili zaraz z nim i pszenicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '29', '29', 'Dopuśćcie obojgu społu róść aż do żniwa, a czasu żniwa rzekę żeńcom: Zbierzcie pierwej kąkol, a zwiąście ji w snopki ku spaleniu, a pszenicę zgromadźcie do gumna mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '30', '30', 'Drugie podobieństwo powiedział im mówiąc: Podobne jest królestwo niebieskie ziarnu gorczycznemu, które wziąwszy człowiek, wsiał na roli swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '31', '31', 'Które namniejszeć jest ze wszego nasienia, ale kiedy uroście, nawięczsze jest ze wszech jarzyn i stawa się drzewem tak, iż przychodzą ptacy niebiescy i gniazda swe czynią na gałązkach jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '32', '32', 'Drugie podobieństwo powiedział im mówiąc: Podobne jest królestwo niebieskie kwasowi, który wziąwszy niewiasta zakryła we trzy miary mąki, aż wszytka skwaśniała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '33', '33', 'Ty wszytki rzeczy mówił Jezus przez podobieństwa onemu zgromadzeniu ludu, a krom podobieństwa nic im nie mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '34', '34', 'Aby się wypełniło to co jest rzeczono przez proroka mówiącego: Otworzę usta moje w podobieństwach i opowiem skryte rzeczy od założenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '35', '35', 'Tedy rospuściwszy ono zgromadzenie ludu, Jezus przyszedł w dom i przystąpili kniemu uczniowie jego mówiąc: Rospowiedz nam podobieństwo o kąkolu onej rolej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '36', '36', 'A on odpowiedając rzekł im: Ten, który sieje dobre nasienie, jestci Syn człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '37', '37', 'Rolą jest świat, a dobre nasienie, cić są synowie Królestwa. Kąkol też, są synowie onego złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '38', '38', 'Nieprzyjaciel zasię, który go nasiał, jestci diabeł, a żniwo jest dokonanie świata. Żeńcy też są Aniołowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '39', '39', 'Jako tedy kąkol zbierają i palą ji w ogniu, tak będzie w dokonaniu świata tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '40', '40', 'Pośle Syn człowieczy Anioły swoje, a oni zbiorą z Królestwa jego wszytki pogorszenia i ty, którzy czynią nieprawość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '41', '41', 'I wrzucą je w piec ognisty, tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '42', '42', 'Tedy sprawiedliwi rozjaśnią się jako słońce w Królestwie ojca swego. Kto ma uszy ku słuchaniu, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '43', '43', 'Zasię podobne jest królestwo niebieskie skarbowi skrytemu na roli, który nalazszy człowiek, skrył ji i od radości, którą ma z niego odchodzi precz, a wszytko co ma sprzeda, a onę rolą kupi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '44', '44', 'Zasię podobne jest królestwo niebieskie człowiekowi kupcowi szukającemu cudnych pereł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '45', '45', 'Który nalazszy jednę perłę barzo drogą, szedszy poprzedał wszytko co miał i kupił ją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '46', '46', 'Zasię podobne jest Królestwo niebieskie włokowi zapuszczonemu w morze, który wszelakie rzeczy bierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '47', '47', 'Który gdy jest napełnion, wyciągnęli go na brzeg, a siadszy wybrali dobre rzeczy w naczynia, a zgniłe precz zarzucili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '48', '48', 'Takci będzie w dokonaniu wieku. Wynidą Aniołowie i wyłączą złe z pośrzodku sprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '49', '49', 'I wrzucą je w piec ognisty. Tam będzie płacz i zgrzytanie ząb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '50', '50', 'Rzekł im Jezus: Wyrozumieliżeście ty wszytki rzeczy? A oni mu powiedzieli: Tak Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '51', '51', 'A on im rzekł: Przeto wszelki nauczyciel uczony w Królestwie niebieskim, podobny jest człowiekowi gospodarzowi, który wyjmuje z skarbu swego rzeczy nowe i stare.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '52', '52', 'I stało się gdy Jezus dokończył tych podobieństw, przeprawił się z onąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '53', '53', 'A gdy przyszedł do ojczyzny swojej, nauczał je w bożnicy ich tak, iż się zdumiewali i mówili: Zkądże temu ta mądrość i tuda?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '54', '54', 'Izaź  ten nie jest on syn cieśle? Izaź matki jego nie zowią Marja? A bracia jego: Jakub i Jozes i Symon i Judas?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '55', '55', 'Siostry też jego wszytki izaź u nas nie są? Zkądże tedy temu ty wszytki rzeczy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '56', '56', 'I zgorszyli się z niego. A Jezus rzekł im: Nie jest prorok okrom czci, jedno w ojczyźnie swojej a w domu swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '12', '57', '57', 'I nie uczynił tam cudów wiele dla niedowiarstwa ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '0', '0', 'W on czas przyszedł słuch do Heroda króla o Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '1', '1', 'I rzekł służebnikom swoim: Tenci jest Jan Krzciciel, który zmartwychwstał i dlatego cuda się przezeń dzieją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '2', '2', 'Abowiem był Herod pojmał Jana i dał go związać i do więzienia wsadzić, dla  Herodjady żony Filipa brata swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '3', '3', 'Bo mu Jan mówił: Nie godzić się jej mieć tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '4', '4', 'A gdy go chciał zamordować, bał się zgromadzenia ludu, abowiem mieli go jako proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '5', '5', 'Gdy tedy obchodzono dzień narodzenia Herodowego, tańcowała córka Herodjady przedewszytkimi i podobała się Herodowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '6', '6', 'Zkąd pod przysięgą obiecał jej dać czegobykolwiek żądała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '7', '7', 'A ona naprawiona od matki swej rzekła: Daj mi tu na misie głowę Jana Krzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '8', '8', 'I zasmucił się król, ale dla przysięgi i tych, którzy pospołu siedzieli, kazał dać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '9', '9', 'A posławszy dał ściąć Jana w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '10', '10', 'I przyniesiona jest głowa jego na misie, a oddana dzieweczce, ona ją zaś ofiarowała matce swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '11', '11', 'Tedy przyszli uczniowie jego i wzięli ciało a pogrzebali je i szedszy opowiedzieli to Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '12', '12', 'A usłyszawszy to Jezus, odszedł z onąd w łodzi na miejsce puste, co gdy usłyszało zgromadzenie ludzi, szli za nim z miast pieszo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '13', '13', 'Wyszedszy tedy Jezus, ujźrzał zgromadzenie ludzi i rozlitował się nad nimi i uzdrawiał niemocne z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '14', '14', 'A gdy przyszedł wieczór, szli ku niemu uczniowie jego mówiąc: Puste jest miejsce to, a czas już przeminął. Opuść to zgromadzenie ludu, aby szli do miasteczek i kupili sobie żywności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '15', '15', 'I rzekł im Jezus: Nie potrzeba im odchodzić. Dajcie wy im coby jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '16', '16', 'A oni mu rzekli: Nie mamy tu jedno pięcioro chleba a dwie rybie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '17', '17', 'A on rzekł: Przynieście mi je tu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '18', '18', 'I roskazawszy onemu zgromadzeniu ludzi sięść na trawie, wziął ono pięcioro chleba i dwie rybie, a wejźrzawszy w niebio błogosławił, a połamawszy, dał uczniom chleby, a uczniowie zgromadzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '19', '19', 'I jedli wszyscy a nasyceni byli. I zebrali co zbywało ułomków dwanaście koszów pełnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '20', '20', 'A tych, którzy jedli było około piąci tysięcy mężów, oprócz niewiast i dziatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '21', '21', 'Tedy Jezus przymusił zarazem ucznie swoje wstąpić w łódź, a iść przed sobą do drugiego brzegu, ażby odprawił ono zgromadzenie ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '22', '22', 'A opuściwszy zgromadzenie ludzi, wstąpił na górę z osobna, aby się modlił. A gdy był wieczór, tamże sam został.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '23', '23', 'A łódź już była w pośrzodku morza, cięszko miotana od wałów, abowiem był wiatr przeciwny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '24', '24', 'Tedy czwartej straży nocnej szedł do nich Jezus, chodząc po morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '25', '25', 'I ujźrzawszy go gdy chodził po morzu, zatrwożyli się uczniowie mówiąc: Pokusa jest i od bojaźni krzykneli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '26', '26', 'Ale wnet mówił Jezus do nich rzekąc: Bądźcie dobrego serca, jaciem jest, nie bójcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '27', '27', 'A Piotr odpowiedając mu rzekł: Panie jeśliś ty jest, każ mi przyść do siebie po wodach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '28', '28', 'A on powiedział: Chodź. I wystąpiwszy Piotr z łodzi, chodził po wodach, aby przyszedł do Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '29', '29', 'I widząc wiatr gwałtowny, zlękł się. A gdy już począł tonąć, zawołał mówiąc: Panie zachowaj mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '30', '30', 'A wnet Jezus ściągnąwszy rękę, zachwycił go i rzekł mu: O mało ufający, przeczżeś wątpił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '31', '31', 'A gdy oni weszli w łódź, ucichnął wiatr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '32', '32', 'A ci, którzy byli w łodzi, przyszedszy dali mu chwałę, mówiąc: Prawdziwieś jest syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '33', '33', 'I przeprawiwszy się, przyszli do ziemie Genezaret.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '34', '34', 'A poznawszy go mężowie miejsca onego, posłali po wszytkiej onej przyległej krainie i przynieśli mu wszytki, którzy się źle mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '13', '35', '35', 'I prosili go aby się tylko dotykali kraja płaszcza jego. A którzy się kolwiek dotknęli, są zachowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '0', '0', 'Tedy przychodzili do Jezusa z Jeruzalem niektórzy nauczycielowie i  Faryzeuszowie, mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '1', '1', 'Czemu uczniowie twoi przestępują ustawę starszych? Abowiem nie umywają ręku swych gdy chleb jedzą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '2', '2', 'A on odpowiedając rzekł im: Czemu też wy przestępujecie roskazanie Boże przez ustawę waszę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '3', '3', 'Abowiem Bóg przykazał mówiąc: Czci ojca twego i matkę twoję. A ktoby złorzeczył ojcu abo matce, śmiercią niechaj umrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '4', '4', 'A wy powiedacie: Któryby rzekł ojcu abo matce: Darem którykolwiek będę ofiarował, wspomożone będziesz, choćby nie czcił ojca swego abo matki swojej (przedsię niewinien będzie).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '5', '5', 'I wniweczeście obrócili roskazanie Boże przez ustawę waszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '6', '6', 'Pokryci ludzie. Dobrze o was prorokował Izajasz mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '7', '7', 'Przychodzi do mnie ten lud ustami swojemi i czci mię wargami, ale serce ich daleko jest odemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '8', '8', 'Lecz darmo mię chwalą ucząc nauki, które są ustawami ludzkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '9', '9', 'A wezwawszy wielkiego zgromadzenia ludzi rzekł im: Słuchajcie a rozumiejcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '10', '10', 'Nie to co wchodzi w usta, pokala człowieka, ale to co wychodzi z ust pokala człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '11', '11', 'Tedy przystępując uczniowie jego, rzekli mu: Wiesz, że Faryzeuszowie usłyszawszy to słowo, obrażeni są?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '12', '12', 'A on odpowiedając rzekł: Wszelkie sądzenie, którego nie sądził Ociec mój niebieski, wykorzenione będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '13', '13', 'Opuśćcie je, są ślepi wodzowie ślepych, A ślepy jeśliby ślepego prowadził, obadwa w dół wpadną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '14', '14', 'A Piotr odpowiedając rzekł mu: Rospowiedz nam to podobieństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '15', '15', 'I rzekł Jezus: Jeszczeż i wy krom porozumienia jesteście?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '16', '16', 'Jeszczeż nie rozumiecie, iż wszelka rzecz która wchodzi w usta, w brzuch idzie i do wychodu bywa wyrzucona?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '17', '17', 'Ale ty rzeczy, które wychodzą z ust, z samego serca pochodzą i pokalają człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '18', '18', 'Abowiem z serca wychodzą złe myśli: morderstwa, cudzołóstwa, nieczystości, kradziestwa, fałszywe świadectwa, bluźnierstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '19', '19', 'Tyć są rzeczy, które pokalają człowieka. Ale jeść nie umywszy rąk, człowieka nie pokala.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '20', '20', 'I wyszedszy Jezus z onąd, poszedł w strony Tyru i Sydonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '21', '21', 'A oto niewiasta Chananejska wyszedszy z onych krajów, zawołała mówiąc kniemu: Zmiłuj się nade mną Panie Synu Dawidów. Córka moja cięszko bywa ode diabła udręczona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '22', '22', 'A on nie odpowiedział jej by słowa. Tedy przystąpiwszy uczniowie jego prosili go mówiąc: Odpraw ją, abowiem woła za nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '23', '23', 'A on jej odpowiedając rzekł: Nie jestem posłan, jeno do owiec, które zginęły z domu izraelskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '24', '24', 'Ale ona przyszedszy, dała mu chwałę mówiąc: Panie pomóż mi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '25', '25', 'A on jej odpowiedając rzekł: Nie jest dobrą rzecz brać chleb dzieci i miotać ji szczeniętom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '26', '26', 'Zatym ona rzekła: Takci jest Panie. Abowiem i szczenięta jedzą odrobiny, które padają z stołu Panów ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '27', '27', 'Tedy odpowiedając Jezus rzekł jej: O niewiasto, wielkać jest wiara twoja, niechajci się stanie jako chcesz. I uzdrowiona jest córka jej od onej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '28', '28', 'A Jezus poszedszy ztamtąd przyszedł nad Morze Galilejskie. I gdy wstąpił na górę siedział tam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '29', '29', 'Tedy szło do niego wielkie zgromadzenie ludu, mając z sobą chrome, ślepe, nieme, niedołężne i inszych wiele i miotali je u nóg Jezusowych i uzdrowił je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '30', '30', 'Tak iż się wielkie zgromadzenie ludu dziwowało, widząc iż niemi mówią, niedołężni zdrowi są, chromi chodzą, ślepi widzą. I chwalili Boga izraelskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '31', '31', 'A Jezus wezwawszy uczniów swoich rzekł: Mam litość nad tym zgromadzeniem ludu, abowiem już trzy dni trwają przy mnie, a nie mają coby jedli. A nie chcę ich opuścić bez pokarmu, aby w drodze nie ustali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '32', '32', 'Tedy mu rzekli uczniowie jego: Zkądże możem mieć tak wiele chlebów na pustyni, abyśmy tak wielkie zgromadzenie nakarmili?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '33', '33', 'I rzekł im Jezus: Wiele macie chlebów? A oni rzekli: Siedmioro i trochę rybek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '34', '34', 'Tedy roskazał onemu zgromadzeniu ludu sięść na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '35', '35', 'I wziął ono siedmioro chleba i ryby. A gdy podziękował, złamał i dał uczniom swoim, a uczniowie onemu zgromadzeniu ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '36', '36', 'Jedli tedy wszyscy, a nasyceni są. I zebrali co zbywało ułomków siedem koszów pełnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '37', '37', 'A było tych, którzy jedli czterzy tysiące mężów, oprócz niewiast i dziatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '14', '38', '38', 'Tedy opuściwszy ono zgromadzenie ludu, wsiadł w łódź i przejechał do krajów Magdala.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '0', '0', 'A przystąpiwszy Faryzeuszowie i Saduceuszowie kusząc go, prosili go, aby im znamię z nieba okazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '1', '1', 'A on odpowiedając rzekł im: Wy mówicie: Gdy wieczór jest, to na pogodę, bo się niebo czerwieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '2', '2', 'A rano: dziś będzie niepogoda. Abowiem niebo się czerwieni pochmurne. Pokryci ludzie! Postawę nieba rozsądzić umiecie a znamion czasów nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '3', '3', 'Rodzaj zły i cudzołożny, znamienia szuka, ale znamię nie będzie mu dano, jedno znamię ono Jonasza proroka. I opuściwszy je poszedł precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '4', '4', 'A gdy uczniowie przyszli do drugiego brzegu, zapamiętali wziąć chlebów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '5', '5', 'I rzekł im Jezus: Baczcie a strzeżcie się kwasu Faryzeuszów i Saduceuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '6', '6', 'A oni rozmawiali między sobą rzekąc: Nie wzięliśmy chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '7', '7', 'Co wiedząc Krystus rzekł im: Cóż między sobą mówicie, o mało ufający, żeście chlebów nie wzięli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '8', '8', 'Jeszczeź nie rozumiecie ani pamiętacie onego pięciorga chleba i piąci tysięcy ludu i jakoście wiele koszów nabrali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '9', '9', 'Ani onych siedmiorga chleba i czterech tysięcy ludzi, a jakoście wiele koszów nazbierali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '10', '10', 'Jakoż nie rozumiecie, żem wam nie o chlebie powiedział, abyście się strzegli kwasu Faryzeuszów i Saduceuszów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '11', '11', 'Tedy porozumieli, że nie  mówił aby się strzegli kwasu chlebowego, ale nauki Faryzeuszów i Saduceuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '12', '12', 'A gdy Jezus przyszedł w strony Cezareji Filipowej, pytał uczniów swoich mówiąc: Kimże mię powiedają być ludzie? Izali synem onym człowieczym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '13', '13', 'A oni rzekli: Jedni Janem Krzcicielem, drudzy Eljaszem, niektórzy też Jeremjaszem, abo jednym z proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '14', '14', 'I rzekł im: A wy kiem mię być powiedacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '15', '15', 'A Szymon Piotr odpowiedając rzekł: Tyś jest Krystus on Syn Boga żywiącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '16', '16', 'Tedy odpowiedając Jezus rzekł jemu: Błogosławiony jesteś Symonie Barjona. Abowiem ciało i krew nie objawiły tobie, ale ociec mój, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '17', '17', 'Ale ja tobie powiedam: Iżeś ty jest Piotr, a na tej opoce zbuduję kościół mój i bramy piekielne nie zwyciężą go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '18', '18', 'A dam tobie klucze Królestwa niebieskiego. Iż cokolwiek zwiążesz na ziemi, będzie związano na niebie. A cokolwiek rozwiążesz na ziemi, będzie rozwiązano na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '19', '19', 'Tedy przykazał uczniom swoim, aby nikomu nie powiedali, że on jest Jezusem Krystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '20', '20', 'A od onego czasu począł Jezus oznajmiać uczniom swoim, iż musi iść do Jeruzalem i wiele cierpieć od starszych i od książąt kapłańskich i od nauczycielów; a być zabit i trzeciego dnia wskrzeszonym być.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '21', '21', 'I odwiódszy go Piotr na stronę, począł go fukać mówiąc: Bądź nad sobą miłościw Panie, żadnym obyczajem nie przyjdzie to na cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '22', '22', 'A on obejźrzawszy się rzekł Piotrowi: Pódź ode mnie szatanie! Jesteś mi na zawadzie. Abowiem nie rozumiesz co jest Bożego, ale co jest ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '23', '23', 'Tedy Jezus rzekł uczniom swoim: Ktoby chciał za mną iść, niechaj się sam siebie zaprzy i weźmie krzyż swój, a za mną idzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '24', '24', 'Bo ktoby chciał zachować duszę swoję - starci ją, a ktoby stracił duszę swoję dla mnie - najdzie ją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '25', '25', 'Abowiem cóż pomoże człowiekowi jeśliby wszytek świat zyskał, a na duszy swej był karan? Abo co da człowiek czymby okupił duszę swoję?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '26', '26', 'Gdyż przyjdzie Syn człowieczy w chwale ojca swego z Anioły swoimi, a tedy odda każdemu według uczynków jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '15', '27', '27', 'Zaprawdę wam powiedam, iże są niektórzy z tych co tu stoją, którzy nie ukuszą śmierci, aż ujźrzą Syna człowieczego gdy przyjdzie do Królestwa swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '0', '0', 'A szóstego dnia, potym wziął Jezus Piotra i Jakuba i Jana brata jego i wprowadził je osobno na górę wysoką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '1', '1', 'I przemienion jest przed nimi, a oblicze jego lśniało się jako słońce, odzienie też jego stało się białe jako światłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '2', '2', 'A oto ukazali się im Mojżesz i Eljasz z nim rozmawiający.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '3', '3', 'I odpowiedając Piotr rzekł Jezusowi: Panie, dobrze nam tu być. Jeśli chcesz uczynimy tu trzy przybytki: tobie jeden, Mojżeszowi jeden, a Eljaszowi jeden.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '4', '4', 'A gdy on jeszcze mówił, oto obłok jasny zaćmił je, a oto głos z obłoku tak mówiący: Ten jest mój syn miły, na którym przestawam, jego słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '5', '5', 'A usłyszawszy to uczniowie, upadli na oblicze swoje i zlękli się barzo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '6', '6', 'Tedy przystąpiwszy Jezus dotknął się ich i rzekł: Wstańcie a nie bójcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '7', '7', 'A oni podniózszy oczy swoje, nikogo nie widzieli, okrom Jezusa samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '8', '8', 'A gdy zstępowali z góry, przykazał im Jezus mówiąc: Nikomu nie powiedajcie widzenia, aż Syn człowieczy z martwych wstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '9', '9', 'I pytali go uczniowie jego mówiąc: Cóż tedy nauczycielowie powiedają, iż Eljasz musi pierwej przyść?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '10', '10', 'A Jezus odpowiedając rzekł im: Eljaszci pierwej przyjdzie i naprawi wszytki rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '11', '11', 'Ale ja wam powiedam: Iż Eljasz już przyszedł, a nie poznali go, ale uczynili nad nim cokolwiek chcieli, takci i Syn człowieczy będzie od nich cierpiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '12', '12', 'Tedy porozumieli uczniowie, że im o Janie Krzcicielu powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '13', '13', 'A gdy oni przyszli do onego zgromadzenia ludu, przystąpił kniemu człowiek i padł mu u kolan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '14', '14', 'Mówiąc: Panie, zmiłuj się nad synem moim, abowiemci lunatykiem jest i źle się ma, gdyż często kroć wpada w ogień i często kroć w wodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '15', '15', 'I przynosiłem go do uczniów twoich, ale go nie mogli uzdrowić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '16', '16', 'Tedy Jezus odpowiedając rzekł: O rodzaju niewierny i przewrotny i pókiż będę z wami? I pókiż was będę cierpiał? Przywiedźcie mi go tu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '17', '17', 'I zfukał go Jezus, a wyszedł z niego diabeł i uzdrowione jest dziecię od onej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '18', '18', 'Tedy przyszedszy uczniowie do Jezusa osobno rzekli mu: Czemuśmy my go wygnać nie mogli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '19', '19', 'A Jezus rzekł im: Dla niedowiarstwa waszego. Bo wam zaprawdę powiedam, byście mieli wiarę jako ziarno gorczyczne, rzeczecie tej górze: Przejdzi ztąd tam, tedy przejdzie. I nic niepodobnego wam nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '20', '20', 'A tenci rodzaj nie wychodzi, jedno przez Modlitwę i post.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '21', '21', 'I gdy oni przebywali w Galilejej rzekł do nich Jezus: Będzie syn człowieczy wydan w ręce ludzkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '22', '22', 'I zabiją go, ale trzeciego dnia wzbudzon będzie. I zasmucili się barzo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '23', '23', 'A gdy oni przyszli do Kapernaum, przystąpili do Piotra ci, którzy wybierają cła i rzekli: Za Mistrz wasz nie płaci cła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '24', '24', 'I rzekł: Płaci. A gdy wszedł w dom, uprzedził go Jezus mówiąc: Cóż się tobie zda Szymonie? Królowie ziemscy od kogo biorą cła i czynsz? Czyli od synów swoich, abo od obcych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '25', '25', 'I rzekł mu Piotr: Od obcych. Powiedział mu Jezus: Tedyć synowie są wolni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '16', '26', '26', 'Wszakże abyśmy ich nie obrazili, szedszy do morza, rzuć wędę, a onę rybę, która napierwej wynidzie weźmi i otworzywszy gębę jej najdziesz stater, który wziąwszy daj im za mię i za się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '0', '0', 'W on czas przyszli do Jezusa uczniowie mówiąc: I któż więczszy jest w Królestwie niebieskim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '1', '1', 'A wezwawszy Jezus dzieciątka, postawił je w pośrodku ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '2', '2', 'I rzekł: Zaprawdę powiedam wam, jeśli się nie nawrócicie i nie staniecie się jako dziatki, nie wnidziecie do Królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '3', '3', 'Kto tedy się nie uniży jako to dzieciątko, tenci jest więczszy w królestwie niebieskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '4', '4', 'A ktoby przyjął jedno dzieciątko takowe w imię moje, mnie przyjmie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '5', '5', 'Ktoby zaś obraził jednego z tych to małych, którzy w mię wierzą, lepiejby mu było, aby zawieszono kamień młyński u szyję jego i zatopiono go w głębokości morskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '6', '6', 'Biada światu dla zgorszenia! Abowiem  muszą przyść zgorszenia, a wszakże biada człowiekowi onemu, przez którego zgorszenie przychodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '7', '7', 'A jeśli ręka twoja, abo noga twoja ciebie w zgorszenie przywodzi, odetni ją i zarzuć od siebie! Lepiej tobie chromym abo niedołężnym wnidź do żywota, a niźli mając dwie ręce abo nodze, być wrzuconem w ogień wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '8', '8', 'A jeśli oko twoje ciebie w zgorszenie przywodzi, wyłup je i zarzuć od siebie. Lepiejci jednookie wnidź do żywota, a niźli dwie oczy mając być wrzuconym na mękę ognistą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '9', '9', 'Baczcież abyście nie wzgardzali jednego z tych to małych, abowiem wam powiedam, iż Aniołowie ich na niebie zawsze widzą oblicze Ojca mego, który jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '10', '10', 'Bo Syn człowieczy przyszedł, aby zachował to co było zginęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '11', '11', 'I cóż się wam zda? Jeśliby miał kto sto owiec, a zabłądziłaby jedna z nich, azaź opuściwszy dziewięćdziesiąt i dziewięć nie idzie na góry, szukać onej, która zabłądziła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '12', '12', 'A jeśli się tak trefi, iż ją najdzie, zaprawdę powiedam wam, iż się jej więcej raduje niźli onym dziewiącidziesiąt i dziewiąci, które nie błądziły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '13', '13', 'Takci nie jest wolą Ojca waszego, który jest na niebie, aby zginął jeden z tych małych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '14', '14', 'A jeśliby zgrzeszył przeciwko tobie brat twój, idź, a karz go między tobą i onym samym. Jeśli cię  usłucha, zyskałeś brata swojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '15', '15', 'Ale jeśli cię nie usłucha, weźmi z sobą jeszcze jednego abo dwu, aby w uściech dwu abo trzech świadków stanęła każda rzecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '16', '16', 'A jeśliby ich nie usłuchał, powiedz zgromadzeniu. A jeśliby i zgromadzenia nie usłuchał, niechci będzie jako który poganin i celnik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '17', '17', 'Zaprawdę powiedam wam: Cobyście kolwiek związali na ziemi, będzie związano i na niebie. A cobyście rozwiązali na ziemi, będzie rozwiązano i w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '18', '18', 'Zasię powiedam wam, iż gdyby się was dwa zezwolili na ziemi, o wszelką rzecz którejby prosili, stanie się im od Ojca mego, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '19', '19', 'Abowiem gdzie są dwa abo trzej zgromadzeni w imię moje, tamem jest w pośrodku ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '20', '20', 'Tedy Piotr przystąpiwszy do niego rzekł: Panie, ile kroć brat mój zgrzeszy przeciwko mnie, mamli mu odpuścić? Izali do siódmego razu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '21', '21', 'I rzekł mu Jezus: Nie powiedamci aż do siedmikroć, ale aż do siedmidziesiąt kroć siedmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '22', '22', 'Dlatego przypodobane jest Królestwo niebieskie niektóremu królowi, który chciał liczbę odbierać od sług swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '23', '23', 'A gdy począł odbierać stawiono mu niektórego co był winien dziesięć tysięcy talentów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '24', '24', 'A gdy on nie mógł zapłacić, kazał go Pan jego zaprzedać i żonę jego i dzieci i wszytko co miał i płacić dług.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '25', '25', 'A on sługa upadszy kłaniał mu się mówiąc: Panie, odłóż gniew swój przeciwko mnie, a wszytko tobie oddam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '26', '26', 'Tedy Pan zlitowawszy się nad onym sługą, wypuścił go i dług mu odpuścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '27', '27', 'A on sługa wyszedszy, nalazł jednego z tych co z nim służyli, który mu był winien sto groszy i popadszy go, dusił ji mówiąc: Oddaj mi coś winien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '28', '28', 'I upadszy mu towarzysz jego u nóg, prosił go mówiąc: Odłóż gniew swój przeciwko mnie, a oddam ci wszytko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '29', '29', 'Ale on nie chciał, owszem szedszy wsadził go do ciemnice, mówiąc iżby mu oddał co był winien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '30', '30', 'A ujźrzawszy towarzysze jego co się stało, zasmucili się barzo i szedszy oznajmili Panu swemu wszytko co się było stało,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '31', '31', 'Tedy wezwawszy go Pan jego rzekł mu: Sługo złościwy. Jamci wszytek on dług odpuścił, iżeś mię prosił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '32', '32', 'Izażeś i ty nie miał się zmiłować nad towarzyszem twoim, jakom się ja też zmiłował nad tobą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '33', '33', 'I rozgniewawszy się Pan jego, podał go oprawcom, ażby oddał wszytko co mu winien był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '17', '34', '34', 'Takci i Ociec mój niebieski uczyni wam, jeślibyście nie odpuścili każdy bratu swemu z serc waszych upadków ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '0', '0', 'I stało się gdy dokończył Jezus tych rzeczy, poszedł z Galilejej i przyszedł do granic Juda za Jordanem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '1', '1', 'I szło za nim wielkie zgromadzenie ludu i uzdrowił je tam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '2', '2', 'Tedy przyszli do niego Faryzeuszowie kusząc go i mówiąc kniemu: Godzili się człowiekowi opuścić żonę dla każdej przyczyny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '3', '3', 'A on odpowiedziawszy rzekł im: Nie czytaliście, iż Stworzyciel od początku mężczyznę i niewiastę uczynił je?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '4', '4', 'I rzekł: Przeto opuści człowiek ojca i matkę i będzie przyłączon ku żenie swej, a którzy dwa byli, staną się jednym ciałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '5', '5', 'A tak już nie są dwa, ale jedno ciało: Co tedy Bóg złączył, człowiek niechaj nie rozłącza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '6', '6', 'Rzekli mu: Przeczże tedy Mojżesz roskazał dawać list rozwodowy i opuścić ją?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '7', '7', 'A on rzekł im: Mojżesz prze zatwardziałość serca waszego dopuścił wam opuszczać żony wasze, acz od początku nie było tak.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '8', '8', 'A ja powiedam wam: Iż ktobykolwiek opuścił żonę swoję, oprócz dla nieczystości, a inszą by pojął - cudzołoży. A ktobykolwiek też opuszczoną pojął - cudzołoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '9', '9', 'Rzekli mu uczniowie jego: Jeśli tak się ma sprawa człowieka z żoną, nie jest pożyteczną rzecz się ożenić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '10', '10', 'A on im rzekł: Nie wszyscy pojmują ty słowa, ale którym jest dano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '11', '11', 'Abowiem są rzezańcy, którzy z żywota matki tak się narodzili i są też rzezańcy, którzy od ludzi są urzezani i są też rzezańcy którzy się sami urzezali dla Królestwa niebieskiego. Kto może pojąć, niechaj pojmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '12', '12', 'Tedy mu ofiarowano dziatki, aby ręce włożył na nie i modlił się, a uczniowie fukali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '13', '13', 'Ale Jezus rzekł: Zaniechajcie dziatek, ani im zabraniajcie przychodzić do mnie, abowiem takowych jest Królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '14', '14', 'A włożywszy na nie ręce, poszedł z onąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '15', '15', 'I oto jeden przystąpiwszy rzekł jemu: Mistrzu dobry, co dobrego mam czynić, abym miał żywot wieczny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '16', '16', 'A on rzekł jemu: Przeczże mię zowiesz dobrym? Żaden nie jest dobry oprócz jednego, to jest Boga. A jeśli chcesz do żywota wnidź, zachowaj przykazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '17', '17', 'I rzekł jemu: Które? I powiedział Jezus: Nie będziesz zabijał, nie będziesz cudzołożył, nie będziesz kradł, nie będziesz mówił fałszywego świadectwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '18', '18', 'Czci ojca i matkę, a bliźniego twego miłować będziesz jako samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '19', '19', 'Rzekł mu młodzieniec: Jam tego wszytkiego strzegł z młodości mojej. Czegoż mi jeszcze niedostawa?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '20', '20', 'Rzekł mu Jezus: Jeśli chcesz być doskonałym, idź przedaj co jedno masz i daj ubogim, a będziesz miał skarb w niebie, a przyszedszy, pódź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '21', '21', 'A gdy ty słowa młodzieniec usłyszał, poszedł smutny, abowiem miał majętności wiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '22', '22', 'Tedy Jezus rzekł uczniom swoim: Zaprawdę powiedam wam, iż bogaty trudno wnidzie do Królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '23', '23', 'Zasię powiedam wam: Łatwiej jest wielbłądowi przez dziurę igielną prześć, niż bogatemu wnidź do Królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '24', '24', 'Co usłyszawszy uczniowie jego, zlękli się barzo mówiąc: Któż tedy może być zbawion?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '25', '25', 'A Jezus wejźrzawszy na nie rzekł im: U ludzi to nie podobno jest, ale u Boga wszytki rzeczy podobne są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '26', '26', 'Tedy Piotr odpowiedając rzekł jemu: Otośmy my opuścili wszytko i poszlichmy za tobą: Cóż się nam tedy dostanie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '27', '27', 'A Jezus rzekł im: Zaprawdę powiedam wam, iż wy którzyście ze mną szli w odrodzeniu, gdy usiędzie Syn człowieczy na stolcu chwały swej, będziecie i wy siedzieć na dwunaście stolcach, sądząc dwanaście pokolenia izraelskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '28', '28', 'I wszelki, któryby opuścił domy, abo bracią, abo siostry, abo ojca, abo matkę, abo żonę, abo dzieci, abo rolę dla imienia mojego, tyle stokroć weźmie i żywot wieczny dziedzicznie otrzyma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '18', '29', '29', 'A wiele pierwszych będą ostatecznymi, a ostatecznych pierwszemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '0', '0', 'Abowiem podobne jest Królestwo niebieskie człowiekowi gospodarzowi, który wyszedł skoro na świtaniu najmować robotniki do winnice swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '1', '1', 'A gdy się zmówił z robotnikami z grosza na dzień, posłał je do winnice swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '2', '2', 'I wyszedszy około trzeciej godziny, ujźrzał drugie, którzy stali na rynku próżnując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '3', '3', 'Rzekł im: Idźcie i wy do winnice, a coby sprawiedliwego było, dam wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '4', '4', 'A oni poszli. Zasię wyszedszy około szóstej i dziewiątej godziny, także uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '5', '5', 'Potym około jedennastej godziny wyszedszy, nalazł drugie, którzy stali próżnujący i rzekł im: Przecz tu stoicie cały dzień próżnujący?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '6', '6', 'Rzekli mu: Iż nas żaden nie najął. Rzekł im: Idźcie i wy do winnice, a coby było sprawiedliwego, weźmiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '7', '7', 'A gdy wieczór przyszedł rzekł pan winnice sprawcy swemu: Zawołaj robotników i oddaj im zapłatę, począwszy od ostatnich aż do pierwszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '8', '8', 'A gdy przyszli oni, którzy o jedennastej godzinie byli zmówieni, wzięli każdy po groszu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '9', '9', 'Przyszedszy też i pierwszy, mniemali żeby więcej wziąć, ale wzięli i oni każdy po groszu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '10', '10', 'A wziąwszy, szemrali przeciwko gospodarzowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '11', '11', 'Mówiąc: Ci ostateczni jednę godzinę robili, a tyś je nam uczynił równymi, którzyśmy cięszką pracą dnia i też upalenie znosili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '12', '12', 'A on odpowiedając rzekł jednemu z nich: Towarzyszu nie czynięć krzywdy, a zaś się nie zmówił z grosza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '13', '13', 'Weźmi co twego jest, a idź precz. Chcęć ja temu ostatecznemu dać jako i tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '14', '14', 'Izaź mi się nie godzi uczynić z moim co chcę? Abo oko twoje złościwe jest, iżem ja dobry?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '15', '15', 'Takci ostateczni będą pierwszemi, a pierwszy ostatecznymi. Abowiem wiele jest wezwanych, ale mało wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '16', '16', 'A gdy szedł Jezus do Jeruzalem, wziął z sobą dwanaście uczniów osobno na drodze i rzekł im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '17', '17', 'Oto idziemy do Jeruzalem, a Syn człowieczy będzie wydan książętam kapłańskim i nauczycielom i osądzą go na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '18', '18', 'I podadzą go poganom na to aby się z niego nagrali i biczowali go i ukrzyżowali go. A trzeciego dnia powstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '19', '19', 'Tedy przyszła do niego matka synów Zebedeuszowych z syny swymi, dając mu chwałę i prosząc nieco od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '20', '20', 'A on jej powiedział: Czego chcesz? A ona mu rzekła: Rzecz aby siedzieli dwa synowie moi, jeden po prawicy twojej, a drugi po lewicy w Królestwie twoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '21', '21', 'A Jezus odpowiedając rzekł: Nie wiecie czego prosicie. Możecieli pić kielich, który ja będę pił? I krztem, którym bywam krzczon być okrzczeni? Rzekli mu: Możemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '22', '22', 'Tedy im rzekł: Kielichci mój pić będziecie i krztem, którym mię krzczą okrzczeni będziecie. Ale siedzieć po prawicy mojej i po lewicy mojej nie jest moja rzecz dać. Ale będzie dano tym, którym jest zgotowano od ojca mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '23', '23', 'Co gdy usłyszeli drudzy dziesięć, za złe mieli onym dwiema bratom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '24', '24', 'A Jezus wezwawszy ich rzekł im: Wiecie iż książęta narodów panują nad nimi. A którzy wielcy są używają zwierzchności nad nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '25', '25', 'Ale nie tak będzie między wami. Owszem, ktobykolwiek między wami chciał wielkim zostać, niech będzie sługą waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '26', '26', 'A ktobykolwiek między wami chciał pierwszym być, niech będzie służebnikiem waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '27', '27', 'Jako Syn człowieczy nie przyszedł aby mu służono, ale aby służył i bał żywot swój w okup za wiele ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '28', '28', 'A gdy oni wychodzili z Jerycha, szło za nim barzo wiele ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '29', '29', 'A oto dwa ślepi siedząc przy drodze, usłyszawszy iż Jezus przyszedł, zawołali mówiąc: Zmiłuj się nad nami Panie, Synu Dawidów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '30', '30', 'Ale ona wielkość ludzi fukała je aby milczeli. A oni tym więcej wołali mówiąc: Zmiłuj się nad nami Panie, Synu Dawidów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '31', '31', 'A stanowszy Jezus zawołał ich i rzekł: Co chcecie abym wam uczynił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '32', '32', 'Rzekli mu: Panie, aby się otworzyły oczy nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '19', '33', '33', ': A Jezus ulitowawszy się, dotknął się oczu ich, a natychmiast przejźrzały oczy ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '0', '0', 'A gdy się przybliżali ku Jeruzalem i przyszli do Betfage nad górę oliwną, tedy Jezus posłał dwi uczniów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '1', '1', 'Mówiąc knim: Idźcie do miasteczka, które jest przeciwko wam, a natychmiast najdziecie oślicę uwiązaną i oślę z nią, odwiązawszy przywiedźcie mi je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '2', '2', 'A jeśliby wam co kto rzekł powiedzcie, iż Pan tych rzeczy potrzebuje, abowiem zarazem pośle je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '3', '3', 'A to się wszytko stało, aby się wypełniło co jest powiedziano przez proroka mówiącego:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '4', '4', 'Powiedzcie córce Syjońskiej: Oto Król twój idzie do ciebie cichy, siedząc na oślicy i na oślęciu tej, która jest pod jarzmem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '5', '5', 'Szedszy tedy uczniowie, tak uczynili jako im był roskazał Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '6', '6', 'I przywiedli oślicę i oślę, a włożywszy na nie szaty swoje, usadzili go na nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '7', '7', 'A barzo wielkie zebranie ludu słali szaty swoje na drodze, a drudzy rąbali gałązki z drzew i na drodze słali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '8', '8', 'Zebranie też ludzi, które wprzód szło i za nim, krzyknęło mówiąc: Hosanna Synowi Dawidowemu, błogosławiony ten, który idzie w imię Pańskie. Hosanna któryś jest w niebie najwyższym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '9', '9', 'A gdy wjechał do Jeruzalem, wzruszyło się wszytko miasto mówiąc: Któż to jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '10', '10', 'A pospolity lud mówił: Tenci jest Jezus on prorok z Nazaret Galilejskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '11', '11', 'Tedy Jezus wszedł do Kościoła Bożego i wygnał wszytki, którzy przedawali i kupowali w Kościele, a stoły bankierzów i stolice tych, którzy przedawali gołębie, spodwracał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '12', '12', 'I rzekł im: Napisano jest: Dom mój, dom modlitwy nazwan będzie, a wyście ji uczynili jaskinią zbójców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '13', '13', 'Tedy przystąpili kniemu ślepi i chromi w kościele, a uzdrowił je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '14', '14', 'A obaczywszy książęta kapłańskie i nauczycielowie dziwy, które był uczynił i dzieci, które wołały w kościele i mówiły: Hosanna Synowi Dawidowemu, rozgniewali się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '15', '15', 'I rzekli mu: Słyszyszże co ci mówią? A Jezus rzekł im: Słyszę. Nie czytaliście nigdy, iż z ust niemówiątek i tych, którzy zsą piersi, wykonałeś chwałę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '16', '16', 'A opuściwszy je, wyszedł precz z miasta do Betanijej i tamże nocował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '17', '17', 'Tedy rano, gdy się wrócił do miasta, łaknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '18', '18', 'I ujźrzawszy niektóre figowe drzewo na drodze, przystąpił kniemu i nie nalazł nic na nim, jedno liście samo i rzekł: Niechaj się więcej z ciebie owoc nie rodzi na wieki i uschło zarazem figowe drzewo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '19', '19', 'To tedy uźrzawszy uczniowie, dziwowali się mówiąc: Jakoż natychmiast uschło figowe drzewo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '20', '20', 'A odpowiedając Jezus rzekł im: Zaprawdę powiedam wam, jeślibyście mieli wiarę a nie wątpili, nie tylko to co się stało z figowym drzewem uczynicie, ale też gdybyście tej górze rzekli: Podnieś się a rzuć się w morze, stanie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '21', '21', 'I wszytki rzeczy, o które prosić będziecie w modlitwie wierząc, weźmiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '22', '22', 'A gdy on przyszedł do Kościoła, książęta kapłańskie i starszy z ludu, przystąpili kniemu gdy uczył i mówili: Którą mocą to czynisz? A ktoć dał tę zwierzchność?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '23', '23', 'A Jezus odpowiedając rzekł im: Spytam ja też was o jednę rzecz, którą jeśli mi powiecie, ja też wam powiem, którą mocą to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '24', '24', 'Krzest Janów zkąd był, z nieba czyli z ludzi? A oni myślili sami w sobie mówiąc: Jeśli powiemy z nieba, rzecze nam: Czemużeście tedy nie uwierzyli jemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '25', '25', 'Jeśli zaś powiemy, z ludzi, boimy się zgromadzenia ludzi, bo wszyscy mają Jana za proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '26', '26', 'I odpowiedając Jezusowi rzekli: Nie wiemy. Rzekł im też i on: Ani ja też wam powiedam, którą mocą to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '27', '27', 'I cóż się wam zda? Niektóry miał dwu synu i przyszedszy do pierwszego rzekł: Synu, idź, rób dziś w winnicy mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '28', '28', 'A on odpowiedając rzekł: Nie chcę. Ale potym obaczywszy się, szedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '29', '29', 'Przyszedszy po tym do drugiego i rzekł mu takież. A on odpowiedając rzekł: Idę ja panie, a nie szedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '30', '30', 'Który z tych dwu uczynił wolą ojcowską? Rzekli mu: Pierwszy. Powiedział im Jezus: Zaprawdę powiedam wam, iż celnicy i wszetecznice uprzedzają was do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '31', '31', 'Abowiem przyszedł do was Jan drogą sprawiedliwości, a nie uwierzyliście mu. Celnicy zaś i wszetecznice uwierzyli mu, ale wy chociaście to widzieli, nie upamiętaliście się, abyście mu uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '32', '32', 'Drugiego podobieństwa słuchajcie. Był niektóry gospodarz, który nasadził winnicę i płotem ją ogrodził i wkopał w niej prasę, a zbudował wieżę i najął ją robotnikom i pojechał precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '33', '33', 'A gdy się przybliżył czas owoców, posłał sługi swoje do onych robotników, aby odebrali pożytki jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '34', '34', 'A robotnicy pojmawszy sługi jego, jednego ubili, drugiego zamordowali, drugiego zaś ukamionowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '35', '35', 'Powtóre posłał insze sługi, których więcej było niż pierwszych, a oni także im uczynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '36', '36', 'Na ostatek posłał do nich syna swego mówiąc: Będą się wstydać syna mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '37', '37', 'Ale robotnicy ujźrzawszy syna, rzekli sami w sobie: Tenci jest dziedzic, pódźcie, zabijmy go i posiądźmy dziedzictwo jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '38', '38', 'Tedy pojmawszy go, wyrzucili precz z winnice i zamordowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '39', '39', 'Gdy tedy przyjdzie pan winnice, co uczyni robotnikom onym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '40', '40', 'I rzekli mu: Ony złe. Złe potraci, a winnicę swoję najmie inszym robotnikom, którzy oddadzą mu owoc czasu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '41', '41', 'Rzekł im Jezus: Nie czytaliście nigdy w piśmiech? Kamień, który odrzucili budujący, ten się stał głową u węgła, od Pana to jest uczyniono i dziwno jest przed oczyma naszymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '42', '42', 'A przetoż powiedam wam, iż będzie odjęte od was Królestwo Boże i będzie dane narodowi, któryby podał owoc jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '43', '43', 'A kto padnie na ten kamień, będzie skruszon, a na kogoby zaś on upadł, zetrze go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '44', '44', 'I usłyszawszy książęta kapłańskie i Faryzeuszowie podobieństwa jego, poznali iż o nich mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '20', '45', '45', 'A szukając jakoby go pojmali, bali się pospólstwa, abowiem go jako proroka mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '0', '0', 'Tedy odpowiedając Jezus powtóre, mówił im przez podobieństwa rzekąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '1', '1', 'Przypodobane jest królestwo niebieskie niektóremu królowi, który sprawił wesele synowi swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '2', '2', 'I posłał służebniki swoje wzywać tych, którzy byli proszeni na wesele, ale nie chcieli przyść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '3', '3', 'Zasię posłał insze służebniki mówiąc: Powiedzcie wezwanym: Otom nagotował obiad mój, woły moje i karmne rzeczy są pobite i wszytki rzeczy gotowe. Podźcie na wesele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '4', '4', 'A  oni nie dbając na to, poszli precz, jeden do swej rolej, a drugi do kupiectwa swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '5', '5', 'Ktemu drudzy pojmawszy służebniki jego i zelżywość im uczyniwszy, pomordowali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '6', '6', 'Co usłyszawszy król, rozgniewał się i posławszy wojska swe, wytracił ony zabijacze i miasto ich spalił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '7', '7', 'Tedy rzekł służebnikom swoim: Wesele jestci gotowe, lecz wezwani nie byli godnymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '8', '8', 'A przeto idźcie na rostanie dróg, a ilebyście naleźli wzówcie na wesele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '9', '9', 'I wyszedszy służebnicy oni na drogi, zebrali wszytki ile ich naleźli, złe i dobre i napełnione jest wesele tymi, którzy siedzieli przy stole.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '10', '10', 'A wszedszy król, aby oglądał siedzące, obaczył tam człowieka, który nie miał na sobie szaty wesela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '11', '11', 'I rzekł mu: Towarzyszu jakoś tu wszedł nie mając szaty wesela? A on zamilknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '12', '12', 'Tedy rzekł król przystawom: Związawszy nogi i ręce jego, zeźmicie go i wrzućcie w ciemność zewnętrzną, tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '13', '13', 'Abowiem wiele jest wezwanych, ale mało wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '14', '14', 'Tedy odszedszy Faryzeuszowie, radzili się aby go podchwycili w mowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '15', '15', 'A przeto posłali do niego ucznie swoje z Herodjany mówiąc: Mistrzu, Wiemy iżeś prawdziwy i drogi Bożej w prawdzie uczysz, a nie dbasz ni na kogo, abowiem nie patrzysz na osobę ludzką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '16', '16', 'Powiedz nam tedy, co się tobie zda: Godzili się dać czynsz cesarzowi, abo nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '17', '17', 'A Jezus poznawszy złość ich rzekł: Czemu mię kusicie obłudni ludzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '18', '18', 'Pokażcie mi monetę czynszową. A oni mu przynieśli grosz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '19', '19', 'Tedy rzekł im: Czyj jest ten obraz i napis?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '20', '20', 'Rzekli mu: Cesarski. Tedy powiedział im: Przeto oddawajcie cesarzowi, co na cesarza należy, a Bogu co jest Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '21', '21', 'Co usłyszawszy, dziwowali się, a opuściwszy go poszli precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '22', '22', 'Dnia onego przyszli kniemu Saduceuszowie, którzy powiedają iż nie masz zmartwychwstania i pytali go');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '23', '23', 'Mówiąc: Mistrzu, Mojżesz rzekł: Jeśliby kto umarł nie mając dziatek, niech brat jego prawem powinowatym pojmie żonę jego i wzbudzi potomstwo bratu swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '24', '24', 'Byli tedy siedm braciej u nas, a pierwszy ożeniwszy się umarł, a gdy nie miał potomstwa, zostawił żonę swoję bratu swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '25', '25', 'Także też wtóry i trzeci aż do siódmego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '26', '26', 'A po wszytkich umarła też i żona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '27', '27', 'W zmartwych wstaniu tedy, którego z onych siedmi będzie żoną? Gdyż ją wszyscy mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '28', '28', 'A odpowiedając Jezus rzekł im: Błądzicie nie umiejąc pism ani mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '29', '29', 'Abowiem w zmartwych wstaniu ani się ożeniają, ani za mąż dawają, ale są jako Aniołowie Boży w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '30', '30', 'A o zmartwych wstaniu nie czytaliście, co wam powiedziano jest od Boga, gdzie mówi:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '31', '31', 'Jam jest Bóg Abrahamów, Bóg Izaaków i Bóg Jakubów. Bóg nie jest Bogiem umarłych, ale żywych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '32', '32', 'A gdy to ono zgromadzenie ludu słyszało, zdumiało się dla nauki jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '33', '33', 'A usłyszawszy Faryzeuszowie, iż usta zawarł Saduceuszom, zeszli się pospołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '34', '34', 'Tedy go spytał jeden z nich doktor w zakonie, kusząc go i mówiąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '35', '35', 'Mistrzu, które jest nawięczsze roskazanie w zakonie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '36', '36', 'A Jezus rzekł mu: Będziesz miłował Pana Boga twego ze wszytkiego serca twego i ze wszytkiej duszę twojej i ze wszytkiej myśli twojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '37', '37', 'Toć jest pierwsze i wielkie roskazanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '38', '38', 'A wtóre podobne jest temu: Będziesz miłował bliźniego twego jako samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '39', '39', 'Na tym dwojgu roskazaniu wszytek zakon i Prorocy zawisnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '40', '40', 'A gdy się Faryzeuszowie zebrali, spytał ich Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '41', '41', 'Mówiąc: Co się wam zda o Krystusie? Czyj jest Syn? Rzekli mu: Dawidów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '42', '42', 'I rzekł im: Jakoż tedy Dawid w Duchu zowie go Panem? Mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '43', '43', 'Rzekł Pan Panu memu: Siędź po prawicy mojej, aż położę nieprzyjacioły twoje podnóżkiem nóg twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '44', '44', 'Jeśli tedy Dawid zowie go Panem, jakoż jest Synem jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '21', '45', '45', 'A żaden nie mógł mu nic odpowiedzieć, ani śmiał żaden od onego dnia ni ocz go pytać więcej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '0', '0', 'Tedy Jezus mówił onemu zgromadzeniu ludu i uczniom swoim');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '1', '1', 'Rzekąc: Na stolcu Mojżeszowym siedzą doktorowie i Faryzeuszowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '2', '2', 'Wszytki tedy rzeczy, któreby wam powiedali abyście zachowali, zachowywajcie i czyńcie, ale wedle uczynków ich nie czyńcie, abowiem powiedają, a nie czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '3', '3', 'Bo wiążą brzemiona cięszkie i trudne ku noszeniu i kładą je na ramiona ludzkie, a palcem swym nie chcą ich ruszyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '4', '4', 'Owszem wszytki sprawy swe czynią, aby byli widzeni od ludzi, abowiem rozszerzają bramy swe popisane i przedłużają podołki płaszczów swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '5', '5', 'Ktemu miłują pierwsze siedzenia na wieczerzach i pierwsze stolice w bożnicach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '6', '6', 'A w rynkach pozdrawiania, a iżby je ludzie zwali Mistrzami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '7', '7', 'Ale wy nie dajcie się zwać mistrzami, abowiem jeden jest nauczyciel wasz, to jest Krystus, a wy wszyscy jesteście bracia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '8', '8', 'A nikogo nie zowcie ojcem waszym na ziemi, abowiem jeden jest ociec wasz, który jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '9', '9', 'Nie zowcie się nauczycielami, gdyż jeden jest nauczyciel wasz, to jest Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '10', '10', 'Ale ten, który jest nawięczszy z was niechaj będzie sługą waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '11', '11', 'Abowiem kto się wywyższa, będzie uniżon, a ktoby się uniżył, będzie wywyższon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '22', '12', '12', 'A biada wam doktorowie i Faryzeuszowie pokryci, abowiem zamykacie Królestwo niebieskie przed ludźmi, gdyż wy sami nie wchodzicie, ani wchodzącym dopuszczacie wnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '0', '0', 'A  wyszedszy Jezus z kościoła, szedł precz i przystąpili uczniowie jego, aby mu ukazali budowania kościelne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '1', '1', 'I rzekł im Jezus: A zaź nie widzicie tych wszytkich rzeczy? Zaprawdę powiedam wam, że tu nie będzie zostawion kamień na kamieniu, któryby nie był rozwalon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '2', '2', 'A gdy on siedział na górze oliwnej, przystąpili kniemu osobno uczniowie jego rzekąc: Powiedz nam kiedy ty rzeczy będą i co za znak przyścia twego i dokonania świata?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '3', '3', 'I odpowiedając Jezus rzekł im: Patrzcie aby was kto nie zawiódł w błąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '4', '4', 'Abowiem wiele ich przyjdzie w imię moję rzekąc: Jam jest Krystus i wiele ich w błąd zawiodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '5', '5', 'I usłyszycie walki i wieści o walkach. Patrzcie abyście sobą nie trwożyli; abowiem muszą się stać ty wszytki rzeczy. Ale jeszcze nie jest koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '6', '6', 'Abowiem powstanie naród przeciwko narodowi i królestwo przeciwko królestwu i będą mory, głody i drżenia ziemie po wielu miejscach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '7', '7', 'A ty wszytki rzeczy są początkiem boleści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '8', '8', 'Tedy podadzą was w udręczenie i zabiją was i będziecie nienawidzeni od wszech narodów dla imienia mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '9', '9', 'A tedy wiele się ich obrazi, a jeden drugiego wyda i będą się mieć w nienawiści społu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '10', '10', 'I wiele fałszywych proroków powstanie i wiele ich w błąd zawiodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '11', '11', 'A dlatego, iż się rozmnoży nieprawość, oziębnie miłość wiela ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '12', '12', 'A kto wytrwa do końca, ten będzie zachowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '13', '13', 'I będzie przepowiedana ta Ewanjelija Królestwa po wszytkiej ziemi, na świadectwo wszytkim narodom, a tedyć przyjdzie koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '14', '14', 'Gdy tedy ujźrzycie brzydliwe spustoszenie, które jest przepowiedziane przez Daniela proroka, stojące na miejscu świętym (kto czyta niechaj rozumie).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '15', '15', 'Tedy którzyby w ziemi Żydowskiej byli, niech uciekają na góry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '16', '16', 'A ktoby był na domie, niechaj nie zstępuje aby co wziął z domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '17', '17', 'A ktoby był na roli, niech się na zad nie wraca brać szat swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '18', '18', 'I biada będzie brzemiennym i piersiami karmiącym w ony dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '19', '19', 'A proście, aby uciekanie wasze nie było zimie ani w sobotę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '20', '20', 'Abowiem na on czas będzie wielki ucisk, jaki nie był od początku świata aż do tego czasu, ani będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '21', '21', 'A gdyby nie były skrócone ony dni, żadne ciało nie było by zachowane, ale dla wybranych będą skrócone dni ony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '22', '22', 'Tedy jeśliby wam kto rzekł: Oto tu Krystus, abo ówdzie. Nie wierzcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '23', '23', 'Abowiem powstaną fałszywi Krystusowie i fałszywi Prorocy i czynić będą znamiona wielkie i cuda, tak iżeby zwiedli (by mogło być) i same wybrane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '24', '24', 'Otomci wam opowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '25', '25', 'Jeśliby tedy  wam rzekli: Oto na puszczy jest, nie wychodźcie. Oto w miejscach skrytych jest, nie wierzcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '26', '26', 'Abowiem jako błyskawica wychodzi od wschodu słońca i okazuje się aż na zachodzie, tak będzie i przyście Syna człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '27', '27', 'Bo gdzieby był ścierw, tam się  orłowie zgromadzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '28', '28', 'A natychmiast po utrapieniu onych dni, słońce zaćmi się i księżyc nie da światłości swojej, a gwiazdy upadną z nieba i mocy niebieskie poruszone będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '29', '29', 'Tedy się ukaże znak Syna człowieczego na niebie i tedy też będą narzekać wszytki pokolenia ziemie i ujźrzą Syna człowieczego gdy przyjdzie po obłokach niebieskich z mocą i chwałą wielką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '30', '30', 'I pośle Anioły swoje z trąbą z wielkim głosem i zgromadzą wybrane jego ze czterech wiatrów, od jednego kraju nieba aż do drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '31', '31', 'A od figi uczcie się podobieństwa, gdy już gałąź jej  odmładza się i liście wypuszcza, znacie iż blisko jest lato.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '32', '32', 'Także i wy, gdy ujźrzycie ty wszytki rzeczy, poznacie iż blisko jest u drzwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '33', '33', 'Zaprawdę powiedam wam, nie przeminieć ten wiek, ażby się stały ty wszytki rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '34', '34', 'Niebo i ziemia przeminą, ale słowa moje nie przeminą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '35', '35', 'A o onym dniu i godzinie nikt nie wie, ani Aniołowie niebiescy, jedno sam Ociec mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '36', '36', 'Ale jako było za dni Noego, tak będzie i przyście Syna człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '37', '37', 'Abowiem jako za czasów, które były przed potopem, jedli i pili i ożeniali się i za mąż dawali, aż do onego dnia, którego wszedł Noe do archi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '38', '38', 'I nie poznali, aż przyszedł potop i zabrał wszytki. Tak też będzie przyście Syna człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '39', '39', 'Tedy będą dwa na roli, z których jeden bywa wzięt, a drugi zostawion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '40', '40', 'Dwie będą mleć we młynie, jedna bywa wzięta, a druga zostawiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '41', '41', 'Czujcie tedy, abowiem nie wiecie, której godziny wasz Pan przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '42', '42', 'A wiedźcie to, że gospodarz by był wiedział, który godziny złodziej miał przyść, czułby był, aniby domu swego podkopać dopuścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '43', '43', 'Przeto i wy bądźcie gotowi, bo której godziny nie mniemacie, Syn człowieczy przydzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '44', '44', 'I któryż jest sługa wierny i rostropny, którego postanowił Pan nad czeladzią swoją, aby im dał pokarm w czas?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '45', '45', 'Szczęśliwy sługa on, którego przyszedszy pan jego najdzie, a on tak czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '46', '46', 'Zaprawdę powiedam wam, że go postawi nad wszytkimi majętnościami swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '47', '47', 'A jeśliby rzekł sługa w sercu swoim: Odwłacza Pan mój przyście swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '48', '48', 'I począłby bić towarzysze swoje, a ktemu jeść i pić z pijanicami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '23', '49', '49', 'Przyjdzie Pan sługi onego dnia, którego on nie oczekawa i w godzinę, której nie wie i odłączy go, a część jego położy z obłudnikami, tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '0', '0', 'Tedy podobne będzie Królestwo niebieskie dziesiąci pannam, które wziąwszy kagańce swoje wyszły przeciw oblubieńcowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '1', '1', 'A pięć z nich było mądrych, a pięć głupich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '2', '2', 'Które były głupie, wziąwszy kagańce swoje, nie wzięły oleju z sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '3', '3', 'Ale mądre wzięły były oleju w naczynia swoje z kagańcami swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '4', '4', 'A gdy oblubieniec omieszkawał, zdrzymały się wszytki i posnęły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '5', '5', 'I stał się o północy krzyk: Oto oblubieniec idzie, wynidźcie przeciwko jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '6', '6', 'Tedy wstały ony wszytki panny i nagotowały kagańce swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '7', '7', 'A głupie rzekły mądrym: Dajcie nam z oleju waszego, boć kagańce nasze gasną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '8', '8', 'I odpowiedziały one mądre mówiąc: Obawamy się, żeby i nam i wam nie dostało. Ale idźcie raczej do tych co przedają i kupcie sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '9', '9', 'A gdy ony szły kupować, przyszedł oblubieniec. A one, które były gotowe, weszły z nim na wesele i zamknione są drzwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '10', '10', 'Potym przyszły i drugie panny mówiąc: Panie, panie otwórz nam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '11', '11', 'A on odpowiedając rzekł: Zaprawdę powiedam wam: Nie znam was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '12', '12', 'Czujcie tedy, bo nie wiecie dnia ani godziny, której Syn człowieczy przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '13', '13', 'Abowiem takie jest Królestwo Boże, jako gdy kto w drogę jadąc, wezwał służebników swoich i poruczył im majętności swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '14', '14', 'Jednemu dał pięć talentów, drugiemu dwa, trzeciemu jeden. Każdemu wedle godności jego i wnet odjechał precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '15', '15', 'Potym szedszy on, który był wziął pięć talentów, robił imi i zyskał drugie pięć talentów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '16', '16', 'Także i ten, który był wziął dwa, zyskał też on drugie dwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '17', '17', 'A ten, który był wziął jeden, szedszy zakopał ji w ziemię i skrył srebro pana swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '18', '18', 'A po niemałym czasie przyszedł pan onych służebników i uczynił liczbę z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '19', '19', 'Tedy przystąpiwszy ten, który był wziął pięć talentów, przyniósł drugie pięć talentów mówiąc: Panie, poruczyłeś mi pięć talentów, otom drugie pięć talentów zyskał imi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '20', '20', 'I rzekł pan jego: Dobrzeć sługo dobry i wierny, nad małym wiernymeś był, nad wielem cię postanowię. Wnidź do radości pana twojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '21', '21', 'A przystąpiwszy ten, który był dwa talenty wziął rzekł: Panie poruczyłeś mi dwa talenty, otom drugie dwa imi zyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '22', '22', 'Rzekł mu pan jego: Dobrzeć sługo dobry i wierny, nad małymeś wierny był, nad wielem cię postanowię. Wnidź do radości pana twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '23', '23', 'A przystąpiwszy i ten, który był jeden talent wziął rzekł: Panie znałem cię, iż jesteś człowiek srogi, który żniesz gdzieś nie siał i zbierasz ztąd gdzieś nie rosproszył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '24', '24', 'Przeto bojąc się, szedłem i skryłem talent twój w ziemię. Oto masz co jest twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '25', '25', 'A odpowiedając pan jego rzekł mu:  Sługo zły i niedbały! Wiedziałeś iż żnę gdziem nie siał i ztąd zgromadzam gdziem nie rosproszył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '26', '26', 'Miałeś tedy pieniądze moje poruczyć kupcom, a ja przyszedszy wziąłbym był co mojego jest z lichwą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '27', '27', 'A przeto weźmicie od niego talent, a dajcie ji temu, który ma dziesięć talentów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '28', '28', 'Abowiem wszelkiemu, który ma, będzie dano i obfitować będzie, a który nie ma i to co ma będzie wzięto od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '29', '29', 'A niepożytecznego sługę wrzućcie w ciemność nadalszą. Tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '30', '30', 'Gdy tedy przyjdzie Syn człowieczy w chwale swej i wszyscy święci Aniołowie z nim, tedy siędzie na stolicy chwały swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '31', '31', 'I będą zgromadzone przedeń wszytki narody i odłączy jedny od drugich, jako pasterz odłącza owce od kozłów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '32', '32', 'A postawi owce po prawej ręce swojej, a kozły po lewej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '33', '33', 'Tedy rzecze król tym, którzy będą po prawicy jego: Pódźcie błogosławieni ojca mego. Posiądźcie dziedzicznie Królestwo wam zgotowane od założenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '34', '34', 'Abowiem łaknąłem, a wyście mi dali jeść, pragnąłem i daliście mi pić, byłem gościem i przyjęliście mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '35', '35', 'Byłem nagim, a wyście mię przyodziali, byłem niemocnym a wyście mię nawiedzili, byłem w więzieniu, a wyście do mnie przyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '36', '36', 'Tedy mu odpowiedzą sprawiedliwi mówiąc: Panie kiedyśmy cię widzieli łaknącym i nakarmiliśmy cię, abo pragnącym i daliśmy tobie pić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '37', '37', 'I kiedyśmy cię widzieli gościem i przyjęliśmy cię, abo nagim i przyodzialiśmy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '38', '38', 'Abo kiedyśmy cię widzieli niemocnym, abo w więzieniu i przyszlichmy do ciebie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '39', '39', 'A odpowiedając król rzecze im: Zaprawdę powiedam wam: Ileście uczynili jednemu z tych braciej mojej namniejszych, mnieście uczynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '40', '40', 'Tedy rzecze i tym, którzy po lewicy będą: Idźcie ode mnie przeklęci w ogień  wieczny, który zgotowany jest diabłu i Aniołom jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '41', '41', 'Abowiemem łaknął i nie daliście mi jeść, pragnąłem i nie daliście mi pić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '42', '42', 'Byłem gościem, a nie przyjęliście mię, byłem nagim a nie przyodzialiście mię, byłem niemocnym i w więzieniu, a nie nawiedziliście mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '43', '43', 'Tedy mu odpowiedzą i oni mówiąc: Panie, kiedyżeśmy cię widzieli łaknącym, abo pragnącym, abo gościem, abo nagim, abo niemocnym, abo w więzieniu, a nie służyliśmy tobie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '44', '44', 'Tedy im odpowie mówiąc: Zaprawdę powiedam wam: Ileście nie czynili jednemu z tych namniejszych, aniście mnie uczynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '24', '45', '45', 'I pójdą ci na mękę wieczną, a sprawiedliwi do żywota wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '0', '0', 'I stało się gdy dokończył Jezus tych wszytkich słów, rzekł uczniom swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '1', '1', 'Po dwu dniu będzie Wielka noc, a Syn człowieczy będzie podan, aby był ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '2', '2', 'Tedy się zebrali książęta kapłańskie, doktorowie i starszy z ludu do dworu kapłana, którego zwano Kajafas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '3', '3', 'I radzili się społu, aby Jezusa zdradą pojmali i zabili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '4', '4', 'I rzekli: Nie w święto, aby się nie stało zamieszanie w ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '5', '5', 'A gdy Jezus był w Betanijej w domu Symona trędowatego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '6', '6', 'Przyszła była do niego niewiasta mając słojek maści kosztownej i wylała ją była na głowę jego gdy siedział u stołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '7', '7', 'Co widząc uczniowie jego, rozgniewali się mówiąc: I na cóż ta utrata?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '8', '8', 'Abowiem możono tę maść drogo przedać i rozdać to ubogim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '9', '9', 'Co gdy poznał Krystus rzekł im: Przeczże frasunek zadawacie tej niewieście? Gdyż dobry uczynek przeciwko mnie uczyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '10', '10', 'Abowiem zawsze będziecie mieć ubogie między sobą, ale mnie nie zawsze mieć będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '11', '11', 'Bo iż ta onę maść wypuściła na ciało moje, to uczyniła dla pogrzebu mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '12', '12', 'Zaprawdę powiedam wam: Gdziekolwiek będzie przepowiedana Ewanjelija ta po wszytkim świecie i to co ona uczyniła, będzie powiedano na pamiątkę jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '13', '13', 'Tedy szedszy jeden ze dwunaście, którego zwano Judasz Iskarjotes do książąt kapłańskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '14', '14', 'Rzekł im: Co mi chcecie dać, a ja go wam wydam? A oni mu naznaczyli trzydzieści pieniędzy srebrnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '15', '15', 'A od onego czasu upatrywał czas aby go wydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '16', '16', 'I pierwszego dnia święta przaśników, przyszli do niego uczniowie jego, mówiąc mu: Gdzie chcesz abyśmyć zgotowali jeść baranka wielkonocnego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '17', '17', 'A on rzekł: Idźcie do miasta, do niektórego człowieka, a rzeczcie mu: Mistrz mówi: Czas mój blisko jest, u ciebie będę obchodził wielką noc z uczniami memi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '18', '18', 'I uczynili uczniowie jako im roskazał Jezus i zgotowali baranka wielkonocnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '19', '19', 'A gdy był wieczór, siadł u stołu ze dwiemanaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '20', '20', 'I gdy oni jedli rzekł: Zaprawdę powiedam wam, iż jeden z was mnie wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '21', '21', 'Tedy się zasmucili barzo i poczęli każdy z nich mówić jemu: A zaź ja jest Panie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '22', '22', 'A on odpowiedając rzekł: Który omoczył ze mną rękę w misie, ten mię wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '23', '23', 'Synci człowieczy idzie jako jest napisano o nim, ale biada człowiekowi onemu, przez którego Syn człowieczy jest wydan: Dobrze mu było, aby się był nie narodził człowiek ten.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '24', '24', 'A odpowiedając Judasz, który go wydał, rzekł: A zaź ja jest Mistrzu? Rzekł mu: Powiedziałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '25', '25', 'A gdy oni jedli, wziął Jezus chleb, a podziękowawszy, łamał i dał uczniom i rzekł: Bierzcie, jedzcie, toć jest ciało moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '26', '26', 'A wziąwszy Kielich, podziękował i dał im rzekąc: Pijcie z tego wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '27', '27', 'Abowiem toć jest krew moja nowego Testamentu, która dla wiela ich bywa rozlana na odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '28', '28', 'A powiedam wam, iż nie będę pił od tąd z tego to owocu winnice, aż w on dzień, gdy ji będę pił z wami nowy w Królestwie ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '29', '29', 'I zaśpiewawszy pieśń, wyszli na górę oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '30', '30', 'Tedy im rzekł Jezus: Wszyscy wy zgorszycie się mną tej nocy, abowiem jest napisano: Uderzę pasterza i będą rosproszone  owce trzody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '31', '31', 'A gdy powstanę, pójdę przed wami do Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '32', '32', 'I odpowiedając Piotr rzekł jemu: Choćby się wszyscy zgorszyli tobą, ja nigdy się nie zgorszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '33', '33', 'Rzekł mu Jezus: Zaprawdęć powiedam, iż tej nocy pierwej niż kur zapoje, trzy kroć się mnie zaprzysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '34', '34', 'Rzekł mu Piotr: Choćbym z tobą musiał umrzeć, ja się ciebie nie zaprzę, także i wszyscy uczniowie mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '35', '35', 'Tedy przyszedł Jezus z nimi na miejsce, które zową Getsemane i rzekł uczniom: Siedźcie tu, aż ja szedszy będę się tam modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '36', '36', 'A wziąwszy Piotra i dwu synów Zebedeuszowych, począł się smucić i cięszko frasować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '37', '37', 'Tedy rzekł im Jezus: Dusza moja jest prawie smętna aż do śmierci, zostańcie tu, a  czujcie ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '38', '38', 'A szedszy trochę dalej, padł na oblicze swoje, modląc się i mówiąc: Ojcze mój, jeśli można rzecz niechaj odejdzie odemnie kubek ten, a wszakże nie jako ja chcę, ale jako ty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '39', '39', 'Tedy przyszedł do uczniów i nalazł je śpiące i rzekł Piotrowi: I takżeście nie mogli jednę godzinę czuć ze mną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '40', '40', 'Czujcie i módlcie się, abyście nie weszli w pokusę: Duchci jest chutki, ale ciało mdłe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '41', '41', 'I zasię powtóre szedszy modlił się, rzekąc: Ojcze mój, jeśli nie może ten kubek odejdź odemnie abych go nie pił, niech się dzieje wola twoja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '42', '42', 'Nalazł je powtóre śpiące, abowiem były oczy ich obciążone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '43', '43', 'A opuściwszy je powtóre, odszedł i trzeci raz się modlił tyż słowa mówiąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '44', '44', 'Tedy przyszedł do uczniów swoich i rzekł im: Już teraz śpicie i odpoczywacie, oto przybliżyła się godzina, a Syn człowieczy będzie wydan w ręce grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '45', '45', 'Wstańcie, pódźmy, oto blisko jest ten, który mię wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '46', '46', 'A  gdy jeszcze mówił, oto Judasz, jeden ze dwunaście przyszedł, a z nim wielki poczet ludzi z mieczami i z kijmi, od  książąt kapłańskich i starszych ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '47', '47', 'A ten, który go wydał, dał im był znak mówiąc: Kogobych ja całował, tenci jest. Pojmajcie go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '48', '48', 'I wnet przystąpiwszy ku Jezusowi rzekł: Bądź pozdrowion Mistrzu i pocałował go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '49', '49', 'A Jezus rzekł mu: Towarzyszu po coś przyszedł? Tedy przystąpiwszy, wrzucili ręce na Jezusa i pojmali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '50', '50', 'A oto jeden z tych, którzy byli z Jezusem, wyciągnąwszy rękę, dobył miecza swego, a uderzywszy sługi kapłana nawyższego, uciął mu ucho,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '51', '51', 'Tedy mu rzekł Jezus: Obróć miecz swój na miejsce jego, abowiem wszyscy którzyby miecz brali, mieczem poginą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '52', '52', 'A zaź mnimasz, abym nie mógł teraz prosić ojca mego, któryby mi stawił więcej niż dwanaście wojsk Aniołów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '53', '53', 'I jakoż się mogą wypełnić Pisma powiedając, iż tak się zstać musi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '54', '54', 'Onej godziny mówił Jezus onemu zebraniu ludzi: Wyszliście jako na złodzieja z mieczmi i z  kijmi pojmać mię: Siedziałem na każdy dzień u was ucząc w kościele, a wżdyście mię nie pojmali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '55', '55', 'A to wszytko się działo, aby się wypełniły pisma prorockie. Tedy uczniowie wszyscy opuściwszy go, zuciękali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '56', '56', 'I pojmawszy oni Jezusa, przywiedli do Kajafi przedniejszego kapłana, gdzie się byli zebrali doktorowie i starszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '57', '57', 'A Piotr szedł za nim z daleka, aż do dworu naprzedniejszego kapłana, gdzie wszedszy siedział z sługami, aby widział koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '58', '58', 'A Krystusa kapłani i starszy i wszytko zgromadzenie, szukali fałszywego świadectwa przeciwko Jezusowi, aby go zamordowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '59', '59', 'I nie naleźli. Gdy też wiele fałszywych światków przychodziło, nie najdowali. A na koniec wystąpiwszy dwa fałszywi świadkowie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '60', '60', 'Rzekli: Ten mówił: Mogę zburzyć Kościół Boży, a za trzy dni zbudować ji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '61', '61', 'A wstawszy Krystusa kapłan rzekł mu: Nic nie odpowiedasz? I cóż to jest co ci przeciwko tobie świadczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '62', '62', 'Lecz Jezus milczał. A odpowiedając przedniejszy kapłan rzekł mu: Poprzysięgam cię przez Boga żywego, abyś nam powiedział, jeśliś ty jest Krystus on Syn Boży?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '63', '63', 'I rzekł mu Jezus: Tyś powiedział. Nad to powiedam wam: Od tego czasu ujźrzycie Syna człowieczego siedzącego na prawicy mocy Bożej i przychodzącego w obłokach niebieskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '64', '64', 'Tedy przedniejszy kapłan rozdarł odzienie swoje mówiąc: Bluźnił! Cóż dalej potrzebujemy świadków? Otoście teraz słyszeli bluźnierstwo jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '65', '65', 'I cóż się wam zda? A oni odpowiedając rzekli: Winien jest śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '66', '66', 'Tedy plwali na oblicze jego i policzkowali go, a drudzy go kijmi bili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '67', '67', 'Mówiąc: Prorokuj nam Krystusie kto jest, który cię uderzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '68', '68', 'A Piotr siedział ze wnątrz we dworze. I przystąpiła kniemu jedna służebnica mówiąc: I tyś był z Jezusem Galilejskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '69', '69', 'I zaprzał się on przed wszytkimi rzekąc: Nie wiem co mówisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '70', '70', 'A gdy on wyszedł do sieni, ujźrzała go druga służebnica i rzekła tym, którzy tam byli: I ten był z Jezusem Nazarejskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '71', '71', 'Tedy powtóre zaprzał się z przysięgą: Nie znam człowieka tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '72', '72', 'I przystąpiwszy mało po tym ci, którzy tam stali, rzekli Piotrowi: Iścieś i ty jest z nich, bo i mowa twoja ciebie jawnie okazuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '73', '73', 'Tedy się począł przeklinać mówiąc: Nie znam człowieka tego. A zarazem kur zapiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '25', '74', '74', 'I rospomniał Piotr na słowa Jezusowe, który mu był powiedział: Pierwej niż kur zapoje, trzy ktoć się mnie zaprzysz. A wyszedszy precz, gorzko płakał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '0', '0', 'A gdy było rano, weszli w radę wszyscy przedniejszy kapłani i starszy ludu przeciwko Jezusowi, aby go zamordowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '1', '1', 'I związawszy go, odwiedli i podali go Poncjuszowi Piłatowi staroście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '2', '2', 'Tedy ujźrzawszy Judasz, który go był wydał, iż był osądzon, żałując wrócił trzydzieści srebrników przedniejszym kapłanom i starszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '3', '3', 'Mówiąc: Zgrzeszyłem wydawszy krew niewinną. Ale oni rzekli: Cóż nam do tego? Ty ujźrzysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '4', '4', 'A porzuciwszy srebniki w kościele, odszedł na stronę i poszedszy zadawił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '5', '5', 'A przedniejszy kapłani wziąwszy ony srebrniki mówili: Nie godzi się ich kłaść do skarbu, abowiem jest zapłatą krwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '6', '6', 'I wszedszy w radę, kupili za nie rolą zduna na pogrzeb gości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '7', '7', 'Dlatego ona rola nazwana jest rolą krwie aż do dnia dzisiejszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '8', '8', 'Tedy się wypełniło to co jest powiedziano przez Jeremjasza proroka, gdzie mówi: I wzięli trzydzieści srebrników za zapłatę oszacowanego, który był oszacowan od synów izraelskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '9', '9', 'I dali je na rolą zdunową, jako mi postanowił Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '10', '10', 'A Jezus stał przed starostą i pytał go starosta mówiąc: Izaś ty jest on Król Żydowski? A Jezus rzekł jemu: Ty powiedasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '11', '11', 'I gdy nań skarżyli przedniejszy kapłani i starszy, nic nie odpowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '12', '12', 'Tedy mu rzekł Piłat: Nie słyszysz jako wiele przeciw tobie świadczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '13', '13', 'I nie odpowiedział jemu na żadne słowo, tak iż się barzo dziwował starosta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '14', '14', 'A w święto zwykł był starosta wypuszczać pospolitemu ludowi jednego więźnia, którego by chcieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '15', '15', 'I mieli na ten czas więźnia znacznego, którego zwano Barabasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '16', '16', 'A gdy się byli zebrali, rzekł im Piłat: Którego chcecie abym wam wypuścił? Barabasza, czyli Jezusa który jest rzeczon Krystus?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '17', '17', 'Bo wiedział, iż go przez zazdrość byli wydali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '18', '18', 'A gdy on siedział na stolicy sądowcy, posłała ku niemu żona jego mówiąc: Nie miej żadnej sprawy z onym sprawiedliwym, abowiem wielem cierpiała dziś we śnie dla niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '19', '19', 'A przedniejszy kapłani i starszy na to pospólstwo przywiedli, aby prosili o Barabasza, a Krystusa aby stracili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '20', '20', 'I odpowiedając starosta rzekł im: Którego chcecie abym wam wypuścił z tych dwu? A oni powiedzieli: Barabasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '21', '21', 'Rzekł im Piłat: Cóż tedy uczynię Jezusowi, który jest rzeczon Krystus? Rzekli mu wszyscy: Niech będzie ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '22', '22', 'A starosta rzekł: Cóż wżdy złego uczynił? A oni tym więcej wołali mówiąc: Niech będzie ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '23', '23', 'I widząc Piłat, iż to nic nie pomogło, owszem więcej się zamieszanie mnożyło, wziąwszy wodę, umył ręce przed zebraniem ludzi mówiąc: Jam jest nie winien krwie tego sprawiedliwego, wy się w tym czujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '24', '24', 'A odpowiedając wszytek lud, rzekł: Krew jego niech będzie na nas i na syny nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '25', '25', 'Tedy im wypuścił Barabasza, a ubiczowawszy Jezusa, podał go na ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '26', '26', 'Tedy żołnierze starościni wziąwszy Jezusa do ratusza, zebrali do niego wszytkę rotę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '27', '27', 'A gdy go zewlekli, oblekli go w szatę szkarłatową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '28', '28', 'I uplotszy koronę z ciernia, włożyli na głowę jego i trzcinę w prawicę jego, a kłaniając się przed nim, nagrawali go mówiąc: Bądź pozdrowion Królu Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '29', '29', 'A gdy nań plwali, wzięli trzcinę i bili go w głowę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '30', '30', 'A gdy go nagrali, zwlekli go z onej szaty i ubrali go w odzienie jego i wiedli go na ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '31', '31', 'A wychodząc, naleźli niektórego Cyrenejczyka imieniem Symona, tego przymusili aby niósł krzyż jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '32', '32', 'I przyszedszy na miejsce rzeczone Golgota, które jest miejscem trupich głów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '33', '33', 'Dali mu pić ocet zmieszany z żółcią, a skosztowawszy, nie chciał pić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '34', '34', 'Po tym ukrzyżowawszy go, rozdzielili szaty jego rzuciwszy los, aby się wypełniło to co jest rzeczono przez proroka: Rozdzielili sobie szaty moje i na odzienie moje los miotali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '35', '35', 'A siedząc, strzegli go tam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '36', '36', 'I włożyli nad głowę winę jego opisaną: Tenci jest Jezus on Król Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '37', '37', 'Tedy byli ukrzyżowani z nim dwa zbójce, jeden na prawej a drugi na lewej ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '38', '38', 'A ci, którzy przechodzili tamtędy, bluźnili go kiwając głowami swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '39', '39', 'I mówiąc: Tyś on, który burzysz Kościół, a przez trzy dni zbudujesz ji, zachowaj sam siebie. Jeśliś syn Boży, zstąp z krzyża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '40', '40', 'Także przedniejszy kapłani z doktormi i z starszymi nagrawając mówili:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '41', '41', 'Insze zachował, a sam siebie zachować nie może: Jeśli jest Król izraelski niech teraz zstąpi z krzyża, a uwierzymy jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '42', '42', 'Dufa w Bogu, niech go teraz wybawi jeśli się w nim kocha, boć powiedział: Jestem Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '43', '43', 'Toż też i zbójce, którzy byli z nim ukrzyżowani, wyrzucali mu na oczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '44', '44', 'A od szóstej godziny, stała się ciemność po wszej ziemi, aż do dziewiątej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '45', '45', 'A około dziewiątej godziny, zawołał Jezus wielkim głosem rzekąc: Eli, Eli, lama sabachtani? To jest: Boże mój, Boże mój czemuś mię opuścił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '46', '46', 'Tedy niektórzy z  tych co tam stali, usłyszawszy to rzekli: Eljasza ten woła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '47', '47', 'A natychmiast bieżawszy jeden z nich i wziąwszy gębkę, napełnił ją octem i włożywszy na trzcinę, podał mu aby pił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '48', '48', 'A drudzy mówili: Zaniechaj, patrzajmy jeśli przyjdzie Eljasz, aby go wybawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '49', '49', 'I gdy powtóre Jezus wołał wielkim głosem, wypuścił ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '50', '50', 'A oto zasłona kościelna przepadła się na dwoje, od wierzchu aż do dołu i zadrżała ziemia, a skały się padały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '51', '51', 'Groby też otworzyły się i wiele ciał świętych, którzy byli posnęli, powstało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '52', '52', 'I wyszedszy z grobów po zmartwychwstaniu jego, weszły do miasta świętego i wielam się ich okazały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '53', '53', 'A setnik i ci, który z nim byli strzegąc Jezusa, ujźrzawszy trzęsienie ziemi i to co cię było stało, zlękli się barzo mówiąc: Zaisteć ten był synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '54', '54', 'I było tam wiele niewiast z daleka patrzących, które były szły za Jezusem od Galilejej posługując mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '55', '55', 'Między którymi była Marja Magdalena i Marja matka Jakuba i Józefa i matka synów Zebedeuszowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '56', '56', 'A gdy był wieczór, przyszedł bogaty człowiek z Arymatiej, imieniem Józef, który też był uczniem Jezusowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '57', '57', 'Ten szedł do Piłata i prosił go o ciało Krystusowe, tedy Piłat kazał oddać ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '58', '58', 'A Józef wziąwszy ciało, uwinął je w czyste prześcieradło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '59', '59', 'I położył je w nowym grobie swoim, który był w skale wykował i tamże przywaliwszy ku drzwiam grobu kamień wielki, odszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '60', '60', 'A była tam Marja Magdalena i druga Marja siedząc przeciwko grobowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '61', '61', 'Nazajutrz, który jest dzień po dniu przygotowania zebrali się przedniejszy kapłani i Faryzeuszowie do Piłata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '62', '62', 'Mówiąc: Panie, rospomnieliśmy, iż on zwodziciel powiedział jeszcze będąc żyw: Po trzech dni powstanę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '63', '63', 'Roskaż tedy obwarować grób aż do dnia trzeciego, by snadź przyszedszy uczniowie jego nocą nie ukradli go i nie  powiedzieli ludowi, iż powstał z martwych i będzie ostateczny błąd gorszy niż pierwszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '64', '64', 'Powiedział im Piłat: Macie straż, idźcie obwarujcie jako umiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '26', '65', '65', 'A oni szedszy, obwarowali grób, zapieczętowawszy kamień i z strażą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '0', '0', 'A w ostatnią sobotę, gdy świtało na pirwszy dzień po sobocie, przyszła Marja Magdalena i druga Marja, aby grób oglądały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '1', '1', 'A oto się stało wielkie drżenie ziemie, abowiem Anioł Pański zstąpiwszy z nieba, przystąpił i odwalił kamień ode drzwi i siedział na nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '2', '2', 'I było wejźrzenie jego jako błyskawica, a odzienie jego białe jako śnieg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '3', '3', 'A od bojaźni jego straże przestraszeni są i stali się jakoby umarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '4', '4', 'I odpowiedając Anioł rzekł niewiastam: Nie bójcie się wy, boć ja wiem, iż Jezusa ukrzyżowanego szukacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '5', '5', 'Nie maszci go tu, abowiem wstał z martwych jako powiedział. Chodźcie oglądajcie miejsce gdzie leżał Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '6', '6', 'A przeto idąc, powiedzcie uczniom jego, iż powstał z martwych, a oto idzie przed wami do Galilejej. Tam go ujźrzycie, otomci wam powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '7', '7', 'Tedy wyszedszy rychło z grobu z bojaźnią i z radością wielką, bieżały aby oznajmiły uczniom jego. A gdy szły aby to opowiedziały uczniom jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '8', '8', 'Oto Jezus zaszedł im drogę mówiąc: Bądźcie pozdrowione. A ony przystąpiwszy uchwyciły się nóg jego i chwałę mu dały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '9', '9', 'Tedy im rzekł Jezus: Nie bójcie się. Idźcie, oznajmicie braciej mojej aby szli do Galilejej, tamże mię ujźrzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '10', '10', 'A gdy ony szły, oto niektórzy z strażej przyszli do miasta i oznajmili przedniejszym kapłanom wszytki rzeczy, które się stały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '11', '11', 'Tedy się zebrali z starszymi i poradziwszy się, wiele pieniędzy dali żołnierzom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '12', '12', 'Mówiąc: Powiedajcie, iż uczniowie jego nocą przyszedszy, ukradli go nam gdyśmy spali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '13', '13', 'A jeśliby to starosty doszło, my go na to namówiemy, a beśpiecznymi was uczyniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '14', '14', 'A oni wziąwszy pieniądze, uczynili jako je nauczono i rozniosła się ta powieść między Żydy aż do dnia tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '15', '15', 'Jedennaście też uczniów szli do Galilejej, na górę gdzie im postanowił Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '16', '16', 'I ujźrzawszy go, dali mu chwałę, a niektórzy wątpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '17', '17', 'A przystąpiwszy Jezus mówił im rzekąc: Dana mi jest wszytka zwierzchność na niebie i na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '18', '18', 'Idąc tedy uczcie wszytki narody, krzcząc je w imię Ojca i Syna i Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mt', '27', '19', '19', 'Ucząc je chować wszytkich rzeczy com wam przykazał. A oto ja jestem z wami po wszytki dni aż do skończenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '0', '0', 'Początek Ewanjelijej Jezusa Krystusa Syna Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '1', '1', 'Jako napisano jest w prorokach: Oto ja posyłam posła mego przed oblicznością twoją, który zgotuje drogę twą przed tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '2', '2', 'Głos wołającego na puszczy ten jest: Gotujcie drogę Pańską, czyńcie proste ścieszki Jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '3', '3', 'I był Jan na puszczy krzcząc, a opowiedał krzest uznania ku odpuszczeniu grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '4', '4', 'Wyszła tedy wszytka ziemia Judska ku niemu i Jerozolimczycy, a wszyscy od niego krzczeni byli w rzece Jordanie, wyznawając grzechy swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '5', '5', 'A Jan obłóczył się w sierść wielbłądzą i pasem skórzanym około biódr swych, a jadał szarańczę i miód leśny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '6', '6', 'I kazał mówiąc: Idzieć za mną ten, który możniejszy jest niźli ja, któremum ja niegodzien nakłoniwszy się rozwiązać rzemyka trzewików jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '7', '7', 'Aczciem ja was krzcił wodą, aleć on będzie was krzcił Duchem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '8', '8', 'I stało się na on czas, iż Jezus przyszedł do Nazaret miasta Galilejskiego, a okrzczon jest od Jana w Jordanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '9', '9', 'Który jako skoro wyszedł z wody ujźrzał iż się rostępowało niebo, a Ducha jako gołębicę nań zstępującego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '10', '10', 'I stał się głos z nieba mówiąc: Tyś jest on namilszy Syn mój, na którym ja przestawam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '11', '11', 'A natychmiast Duch wyrzucił ji na puszczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '12', '12', 'I był tam na puszczy przez czterdzieści dni, będąc kuszon od szatana i był z zwierzęty, a Aniołowie służyli jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '13', '13', 'Potym gdy Jan był wydan, przyszedł Jezus do Galilejej opowiedając Ewanjeliję Królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '14', '14', 'A mówiąc: Wypełnił się czas i przybliża się Królestwo Boże. Uznajcież się a wierzcie Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '15', '15', 'Przechadzając się tedy nad Morzem Galilejskim, ujźrzał Symona i Andrzeja brata jego, iż zarzucali sieci do morza, bo byli rybitwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '16', '16', 'Ku którym rzekł Jezus: Pódźcie za mną, a ja uczynię iż będziecie rybitwi ludzkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '17', '17', 'Oni tedy natychmiast opuściwszy sieci swe, szli za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '18', '18', 'Ztamtąd odszedszy maluczko, ujźrzał Jakuba syna Zebedeuszowego i Jana brata jego, którzy oprawowali sieci swe w łodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '19', '19', 'I wnet ich zawołał, a oni zostawiwszy ojca swego w łodzi z najemniki, poszli za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '20', '20', 'Potym weszli do Kapernaum, a wnet w sabat wszedszy bo bożnice uczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '21', '21', 'I zdumiewali się na naukę jego, abowiem je uczył jako mając zwierzchność, a nie tak jako Nauczycielowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '22', '22', 'A był w bożnicy ich człowiek, w którym był duch nieczysty, tenże zawołał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '23', '23', 'Mówiąc: Ach cóż tobie do nas Jezusie Nazareński? Przyszedłeś abyś nas wytracił? Znam cię ktoś jest, to jest, on Święty Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '24', '24', 'I sfukał go Jezus mówiąc: Milczże a wynidź od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '25', '25', 'Tedy rozdarszy go duch nieczysty wołał głosem wielkim, a wyszedł od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '26', '26', 'I ulękli się wszyscy tak, iż między sobą pytali mówiąc: I cóż to jest? A co to za nowa jest nauka? Iż mocą też i duchom nieczystym roskazuje, a są posłuszni jemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '27', '27', 'A tak o nim przyszła sława po onej wszytkiej krainie podległej Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '28', '28', 'I natychmiast wyszedszy z bożnice, przeszli do domu Symonowego i Andrzejowego, z Jakubem i z Janem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '29', '29', 'A świekra Symonowa leżała w febrze, o której mu wnet powiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '30', '30', 'Tedy przyszedszy wzwiódł ją ująwszy za rękę, a natychmiast ją febra opuściła, a ona służyła im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '31', '31', 'A już w wieczór, gdy słońce zachodziło, przywodzili do niego wszytki niemocne i opętane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '32', '32', 'A wszytko miasto zbieżało się ku drzwiam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '33', '33', 'I uzdrawiał wiele tych, którzy rozlicznymi niemocami złożeni byli, a wyrzucił wiele diabłów, nie dopuszczając mówić diabłom, żeby go znać mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '34', '34', 'Gdy potym wstał barzo rano, dobrze przed świtaniem wyszedł precz, a szedł na pustynię i tam się modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '35', '35', 'A za nim szli Szymon i ci, którzy przy nim byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '36', '36', 'Którzy gdy go znaleźli rzekli kniemu: Wszyscy cię szukają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '37', '37', 'Tedy im on rzekł: Idźmy do bliższych miasteczek, abych też i tam kazał, abowiemciem dlatego wyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '38', '38', 'I kazał w bożnicach ich po wszytkiej Galilejej wyrzucając diabelstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '39', '39', 'Zatym przyszedł trędowaty prosząc go, a upadając u kolan jego i mówiąc kniemu: Jeśliż chcesz możesz mię oczyścić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '40', '40', 'A tak Jezus ruszony miłosierdziem, ściągnąwszy rękę dotknął się go mówiąc ku niemu: Chcę, bądźże oczyszczon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '41', '41', 'A gdy to on rzekł, natychmiast trąd zginął z niego i był oczyścion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '42', '42', 'I zagroziwszy mu, Jezus natychmiast go odprawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '43', '43', 'I rzekł kniemu. Strzeżże abyś nikomu nic nie powiedał, ale idź a ukaż się sam kapłanowi, ofiarowawszy za oczyścienie twe to co roskazał Mojżesz ku świadectwu im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '0', '44', '44', 'Ale on odszedszy, począł wiele opowiedać a te rzeczy rozsławiać tak, iż już Jezus nie mógł jawnie wnidź do miasta, ale był na stronie na miejscach pustych, gdzie się zewsząd schadzali do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '0', '0', 'Potym po kilku dni przyszedł zasię do Kapernaum i dowiedziano się, że był doma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '1', '1', 'A wnet się ich wiele zebrało tak, iż się zmieścić nie mogli na onych miejscach przede drzwiami i uczynił rzecz do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '2', '2', 'Tedy przyszli k niemu niektórzy niosąc powietrzem ruszonego, którego nieśli czterzej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '3', '3', 'A gdy się kniemu przycisnąć nie mogli dla zgromadzenia, oddarli dach tam gdzie był, a oddarszy, na powroziech spuścili łóżko, na którym on powietrzem ruszony leżał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '4', '4', 'Tedy Jezus obaczywszy wiarę ich rzekł powietrzem ruszonemu: Synu odpuszczone są tobie grzechy twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '5', '5', 'Byli też tam niektórzy z Nauczycielów siedząc i rozmyślając w sercoch swych:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '6', '6', 'I cóż? A także ten mówi bluźnierstwa? I kto z grzechy odpuścić może jedno sam Bóg?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '7', '7', 'A poznawszy wnet Jezus Duchem swym iż tak u siebie myślili rzekł do nich: I czemuż to rozmyślacie w sercach waszych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '8', '8', 'I cóż łacwiej rzec temu powietrzem ruszonemu? Odpuszczone są tobie grzechy, abo rzec, wstań a weźmi łóżko twe i chodź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '9', '9', 'A tak żebyście widzieli, iż Syn człowieczy ma tę zwierzchność odpuszczać grzechy na ziemi, (rzekł do ruszonego powietrzem).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '10', '10', 'Tobie mówię wstań, a weźmi łóżko twe i pójdzi do domu twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '11', '11', 'Tedy on natychmiast wstał, a wziąwszy łóżko wyszedł przed wszytkimi tak, iż wszyscy zdumieli, a chwalili Boga mówiąc: Nigdychmy nic takowego nie widali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '12', '12', 'Wyszedł zasię potym do morza, a wszytek lud przychodził ku niemu i nauczał je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '13', '13', 'A gdy szedł dalej, ujźrzał Lewi syna Alfeuszowego siedzącego na cle, ku któremu rzekł: Idź za mną. A on wstawszy szedł za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '14', '14', 'I przydało się gdy siedzieli w domu jego, gdzie też siedziało wiele celników i grzesznych społu z Jezusem i ze zwolenniki jego, bo ich wiele było, którzy za nim szli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '15', '15', 'A gdy ujźrzeli nauczycielowie i Faryzeuszowie, iż jadł z celniki i grzeszniki rzekli do zwolenników jego: Cóż to jest, iż z celniki i z grzeszniki je i pije?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '16', '16', 'To gdy usłyszał Jezus rzekł do nich: Ci którzy są zdrowi nie potrzebują lekarza, ale ci którzy się źle mają. Nie przyszedłciem abych wzywał sprawiedliwych, ale grzesznych ku uznaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '17', '17', 'A zwolennicy Janowi i Faryzejscy pościli, a przyszedszy mówili do niego: Przeczże zwolennicy Janowi i Faryzejscy poszczają? A twoi zwolennicy nie poszczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '18', '18', 'Tedy do nich rzekł Jezus: Zali mogą synowie oblubieńcowi na ten czas pościć póki przy nich jest oblubieniec? Pókić z sobą oblubieńca mają, nie mogą pościć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '19', '19', 'Aleć przyjdą czasy, gdy od nich odejmą oblubieńca, a tedy będą pościć w ty czasy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '20', '20', 'A żadenci nie przyszywa sukna nowego nie folowanego do starej szaty, bo inak ona sztuka nowa drze starę i bywa gorszy rospór.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '21', '21', 'I żadenci nie leje wina nowego w stare naczynia, bo inak wino nowe rozsadza naczynie i rozlewa się wino, a naczynia giną. Aleć wino nowe ma być wlewane do naczynia nowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '22', '22', 'Stało się potym, iż w dzień Sabatny szedł przez siane pola, a zwolennicy jego idąc drogą poczęli wyrywać kłosy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '23', '23', 'Tedy rzekli kniemu Faryzeuszowie: Przecz oto w sabat czynią to co nie słuszy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '24', '24', 'A on im powiedział: Zaście nigdy nie czytali co uczynił Dawid gdy potrzebował i gdy był łaczen sam i ci, którzy z nim byli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '25', '25', 'To jest: Jako wszedszy do Domu Bożego za Abiatara kapłana nawyższego, jadł chleby pokładne, których się nie godziło jeść, jedno kapłanom, a dawał i tym co z nim byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '26', '26', 'Ktemu im powiedział: Szabat ci uczynion jest dla człowieka, a nie człowiek dla Szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '1', '27', '27', 'A takżeć Syn człowieczy jest też Panem Szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '0', '0', 'I wszedł powtóre do bożnice, gdzie tam był człowiek co miał rękę uschłą');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '1', '1', 'Tedy go pilnowali jeśliby go w szabat uzdrowił, aby go obwinowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '2', '2', 'Rzekł tedy człowiekowi mającemu rękę uschłą: Stań w pośrzodku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '3', '3', 'I rzekł do nich: Godzili się w sabat dobrze czynić abo źle? Człowieka zachować abo zamordować? A oni zamilknęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '4', '4', 'A pojźrzawszy po nich z gniewem, rozżalił się, że serce ich było zatwardzone. I rzekł człowiekowi: Wyciągni rękę twą, a on ją wyciągnął. I uzdrowiona jest ręka jego tak jako i druga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '5', '5', 'Tedy wyszedszy Faryzeuszowie, zeszli się natychmiast w radę z Herodjany przeciwko jemu aby go stracili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '6', '6', 'Ale Jezus z swymi zwolenniki uszedł do morza, a wielki poczet ludzi z Galilejej i z Judy zanim szedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '7', '7', 'Także z Jeruzalem i z Idumejej i z krainy za Jordanem, także i tych wielkie mnóstwo, którzy mieszkali około Tyru i Sydonu, zeszli się kniemu słysząc o tym co czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '8', '8', 'A przetoż mówił swym zwolennikom aby zawżdy miał łódź po gotowiu dla zebrania ludzi, żeby go nie ścisnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '9', '9', 'Abowiem ich był wiele uzdrowił, tak iż się kniemu zbiegali aby się go dotykali ile ich jedno mieli jakie razy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '10', '10', 'A gdy go duchowie nieczyści zajźrzeli, upadli mu u nóg i wołali mówiąc: Tyś jest Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '11', '11', 'Ale on na nie barzo fukał, aby go nie oznajmowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '12', '12', 'Tedy wszedł na górę i wezwał do siebie ty, które sam chciał, a przyszli kniemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '13', '13', 'I postanowił dwanaście aby byli przy nim, a iżby je posłał aby kazali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '14', '14', 'Mając też i moc uzdrawiać niemocy i wyganiać diabelstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '15', '15', 'Naprzód Symona, któremu imię dał, Piotr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '16', '16', 'I Jakuba, Syna Zebedeuszowego i Jana brata Jakubowego i nazwał je Boanergos, co się wykłada, synowie gromu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '17', '17', 'Przytym Andrzeja, Filipa i Bartłomieja, Mateusza, Tomasza i Jakuba syna Alfeuszowego, Tadeusza i Symona Chananejczyka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '18', '18', 'Też i Judasza Iskarjotę, który go zdradził, a przyszli do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '19', '19', 'Zeszła się potym wielkość ludzi, tak iż oni nie mogli i chleba jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '20', '20', 'A gdy to usłyszeli jego powinowaci, przyszli aby go pojmali, bo mówili żeby odszedł od rozumu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '21', '21', 'Tedy nauczycielowie, którzy przyszli z Jeruzalem mówili, iż Belzebub był przy nim, a iż przez książę diabelskie, diabelstwa wyganiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '22', '22', 'I wezwawszy ich mówił ku nim w przypowieściach: I jakoż może szatan szatana wyganiać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '23', '23', 'Gdyż i królestwo, któreby przeciw sobie było rozdwojone, nie może się ostać królestwo ono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '24', '24', 'Także i dom jeśliby przeciw sobie był rozdwojon, nie może się ostać takowy dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '25', '25', 'Takżeć też i szatan jeśliby sam przeciw sobie się oburzył, a byłby rozdwojon, nie możeć się ostać, a już jest koniec jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '26', '26', 'Nie możeć żaden sprzętu jakiego mocarza wszedszy w dom jego roschwycić, jeśliby pierwej mocarza onego nie związał, a tedyć dom jego wybierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '27', '27', 'Powiedamci wam zaprawdę, że wszytki grzechy będą odpuszczone synom ludzkim i bluźnierstwa którymikolwiekby bluźnili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '28', '28', 'Ale ktobykolwiek bluźnił przeciw Duchowi świętemu, nie ma odpuszczenia na wieki, owszem jest winien potępienia wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '29', '29', 'Abowiem mówili, że ma ducha nieczystego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '30', '30', 'Tedy zatym przyszli matka i bracia jego, a stojąc na dworze, posłali kniemu wzywając go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '31', '31', 'A wielkość ludzi siedziała około niego i opowiedziano mu: Oto matka twa i bracia twoi szukają cię na dworze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '32', '32', 'Tedy im odpowiedział mówiąc: Któż jest matka moja i bracia moi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '33', '33', 'A pojźrzawszy wszędy w około i na zwolenniki, którzy siedzieli około niego rzekł: Otoć matka moja i bracia moi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '2', '34', '34', 'Abowiem ten kto będzie czynił wolą Bożą, tenci jest brat mój, siostra moja i matka moja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '0', '0', 'I począł zasię uczyć przy morzu, a zeszło się ku niemu wiele ludzi tak iż on wszedszy do łódki siedział na morzu, a wszytka ona wielkość była nad morzem na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '1', '1', 'Tedy je nauczał wiela rzeczy w przypowieściach a mówił do nich nauczając je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '2', '2', 'Słuchajcie, oto niektóry co siał wyszedł ku sianiu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '3', '3', 'I stało się gdy siał, jedno padło podle drogi, a ptacy powietrzni przyleciawszy wyjedli je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '4', '4', 'Drugie zasię upadło na opokę, gdzie nie mając wiele ziemię natychmiast wzeszło przeto, iż nie miało głębicy w ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '5', '5', 'A gdy słońce wzeszło zgorzało a przeto, iż korzenia nie miało, uschło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '6', '6', 'Drugie zasię upadło w ciernie, a gdy wzeszły ciernia, zagłuszyły je i nie dało owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '7', '7', 'Drugie też upadło na ziemię dobrą i podało owoc urodzajny i urosły, a jedno przyniosło trzydzieści, drugie sześćdziesiąt, trzecie zasie sto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '8', '8', 'Tedy rzekł do nich: Kto ma uszy ku słuchaniu, niech słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '9', '9', 'A gdy sam był osobno, pytali go ci co przy nim byli ze dwiemanaście, około tej przypowieści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '10', '10', 'A on im powiedział: Wamci dano poznać tajemnicę Królestwa Bożego. Ale tym, którzy na stronie są, wszytko bywa w przypowieściach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '11', '11', 'Aby widząc widzieli a nie obaczyli, a słysząc słyszeli a nie zrozumieli, by się kiedy nie nawrócili, a byłyby im odpuszczone grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '12', '12', 'I rzekł do nich: Nie wiecież przypowieści tej? I jakoż możecie poznać wszytki przypowieści?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '13', '13', 'Rozsiewca on słowa sieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '14', '14', 'Cić zasie są którzy podle drogi nasienie przyjmują, którym rozsiewają słowo, a gdy słyszą, natychmiast przychodzi szatan, a wybiera słowo wsiane w sercach ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '15', '15', 'Takżeć i ci są, którzy na opoce słowo przyjmują, którzy usłyszawszy słowo, natychmiast je z radością przyjmują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '16', '16', 'Ale nie mają korzenia w sobie, owszem są doczesnymi, a potym gdy przyjdzie uciśnienie, abo prześladowanie dla słowa, natychmiast się obrażają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '17', '17', 'A zasie ci są, którzy między ciernie słowo przyjmują, a powiedam, że ci są, którzy słowa słuchają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '18', '18', 'Ale starania świata tego i zdradliwe bogactwa i chciwości, które są w inych rzeczach wszedszy zagłuszają słowo, tak iż bywa bez pożytku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '19', '19', 'A cić zasie są którzy w dobrą ziemię nasienie przyjmują, którzy słuchają słowo i przyjmują i przynoszą owoc, tak iż jedno ziarno przynosi trzydzieści, drugie sześćdziesiąt, trzecie sto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '20', '20', 'Nadto im rzekł: Izali świecę zaświecają aby ją kładziono pod korzec abo pod łoże? Zaź nie dlatego, aby była w lichtarz postawiona?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '21', '21', 'Abowiemci nic nie jest tak tajemnego coby się objawić nie miało, ani jest to coby skryto było, gdyż potrzeba aby się na jaśnie okazało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '22', '22', 'Jeśliż kto uszy ma ku słuchaniu, niech słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '23', '23', 'Zatem rzekł do nich: Obaczcież czego słuchacie. Jaką miarą mierzycie, drudzy wam też odmierzą, a będzie wam przydano którzy słuchacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '24', '24', 'Abowiem ten kto ma, będzie mu dano, a kto nie ma, też to co ma, odejmą od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '25', '25', 'I mówił: Takoweć jest Królestwo niebieskie, iż jeśli człowiek wrzuci nasienie w ziemię,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '26', '26', 'A zaśnię i wstanie w nocy i we dnie, a nasienie wzejdzie i uroście tak jako on nie wzwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '27', '27', 'Boć ziemia dobrowolnie owoc podawa, naprzód trawkę, potym kłos, a potym kłos zboża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '28', '28', 'A gdy się ukaże owoc, wnet zapuści sierp, bo żniwo przyszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '29', '29', 'Nad to powiedał: Ku którejże rzeczy przypodobiemy Królestwo Boże? Abo ku czemu je przyrównamy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '30', '30', 'Takoweć jest jako ziarno gorczyczne, które gdy wsieją w ziemię, namniejsze jest ze wszytkiego nasienia, które jest na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '31', '31', 'Ale gdy będzie wsiane, wschodzi i bywa więczsze nad ine jarzyny, a podawa z siebie gałęzie wielkie, tak iż pod cieniem jego mogą mieć gniazda ptacy powietrzni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '32', '32', 'I mówił do nich słowa swe w takowych przypowieściach jako słuchać mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '33', '33', 'A oprócz przypowieści nie mówił do nich, wszakże zwolennikom swoim osobno wszytko wykładał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '34', '34', 'Potym dnia onego mówił do nich, gdy było k wieczoru: Przeprawmy się do dalszego brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '35', '35', 'Tedy opuściwszy zebranie ludzi, przyjęli go tak jako był w łodzi, a ine łodzie też były z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '36', '36', 'Zatym powstał wicher wielki, a nawałności obijały się o łódź tak, iż już była pełna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '37', '37', 'A on był na zadzie łodzi śpiąc na wezgłowiu, tedy go obudzili mówiąc mu: Mistrzu, nie masz tego na pieczy iż giniemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '38', '38', 'A tak się ocuciwszy, sfukał wiatr, a rzekł morzu: Milcz a uśmierz się i uciszył się wiatr, a stało się barzo cicho.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '39', '39', 'Rzekł zatym do nich: Czemużeście tak bojaźliwemi? I jakoż wiary nie macie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '3', '40', '40', 'Ulękli się tedy wielkim strachem, mówiąc jeden ku drugiemu: I cóż to zacz jest? Że też wiatry i morze są posłuszni jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '0', '0', 'Stamtąd przyszli do dalszego brzegu morskiego, do ziemi Gedareńczyków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '1', '1', 'A wyszedszy z łodzi, natychmiast mu zabieżał z grobów człowiek, w którym był duch nieczysty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '2', '2', 'Który miał mieszkanie w grobach, a nie mógł go nikt i łańcuchem związać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '3', '3', 'Przeto, iż on często był wiązan łańcuchy i pętami, ale się na nim łańcuchy rwały, a pęta targały i nie mogli go żaden okrócić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '4', '4', 'A zawżdy we dnie i w nocy wołał w górach i w grobiech, a tłukł się sam kamienimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '5', '5', 'I ujźrzawszy Jezusa z daleka przybierzał, kłaniając się przed nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '6', '6', 'A wołając głosem wielkim mówił: Cóż mi do ciebie Jezusie Synu Boga Nawyższego? Poprzysięgamcię przez Boga abyś mię nie męczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '7', '7', '(Abowiem mu mówił: Wynidź duchu nieczysty z człowieka tego).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '8', '8', 'Tedy go pytał: Jako cię zową? A on mu odpowiedział mówiąc: Zową mie hufem, abowiem jest nas wiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '9', '9', 'I prosił go barzo, aby ich nie wyganiał z onej ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '10', '10', 'Było też tam po górach wielkie stado wieprzów co się paśli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '11', '11', 'A wszyscy diabli prosili go mówiąc: Puść nas w wieprze abychmy w nie wstąpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '12', '12', 'I natychmiast im dopuścił Jezus, a wyszedszy duchowie nieczyści wstąpili w wieprze i wpadło ono stado z góry do morza, a było ich około dwu tysiącu i potonęli w morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '13', '13', 'Oni też co paśli wieprze zuciekali i oznajmili to w mieściech i po wsiach. Tedy oni wyszli aby oglądali to co się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '14', '14', 'A przyszedszy do Jezusa, patrzyli na onego, który był opętany, iż siedział obleczony, będąc przy dobrym rozumie, a w którym był huf.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '15', '15', 'Ci tedy, którzy to widzieli, opowiedzieli im jako się to przydało onemu opętanemu i o wieprzach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '16', '16', 'Którzy go wnet prosić poczęli aby wyszedł z granic ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '17', '17', 'A gdy wszedł w łódź, prosił go on co był opętanym aby został przy nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '18', '18', 'Ale mu Jezus nie dopuścił, owszem mu rzekł: Idź do domu twojego, do swych, a oznajmi im jako wielkie rzeczy z tobą Pan uczynił, a jako się nad tobą zlitował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '19', '19', 'Tedy odszedł i począł opowiadać w Dekapolim jako wielkie rzeczy z nim uczynił Jezus, a było to u wszytkich w podziw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '20', '20', 'A gdy się zasię Jezus przeprawił w łodzi ku dalszemu brzegu, zebrała się do niego wielkość ludzi, a on był nad morzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '21', '21', 'A oto przyszło książę bożnice imieniem Jair, a ujźrzawszy go padł u nóg jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '22', '22', 'I prosił go wielce mówiąc: Iż dzieweczka moja już przywiedziona ku skonaniu proszę abyś przyszedł a włożył na nię ręce, żeby była zachowana i żywa została.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '23', '23', 'Tedy szedł z nim, a przy nim wielkość ludzi, tak iż go ucisnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '24', '24', 'A niewiasta niektóra, co cierpiała płynienie krwie od dwunaście lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '25', '25', 'I wiele ucierpiała od wiela lekarzów, a wszystko swe na to nałożyła, a wżdy jej to nic nie pomogło, owszem się jej tym bardziej pogarszało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '26', '26', 'Ta gdy usłyszała o Jezusie, przyszła z tyłu między zebraniem ludzi i tknęła się szaty jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '27', '27', 'Bo tak sobie mówiła: Jeśliż się tylko tknę szat jego, będę zachowana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '28', '28', 'I natychmiast wyschło źrzódło krwie jej, a poczuła na ciele iż jest uzdrowiona od tego trapienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '29', '29', 'A poznawszy wnet Jezus moc, która od niego wyszła, obróciwszy się między zebraniem ludzi, rzekł: I któż się tykał szat moich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '30', '30', 'Tedy mu rzekli zwolennicy jego: Widzisz iż cię wielkość ludzi ciśnie, a mówisz: Któż mię dotknął?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '31', '31', 'I obejźrzał się aby obaczył onę, która to uczyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '32', '32', 'A bojąc się i drżąc niewiasta, widząc co się jej przydało, przyszła i upadła przed nim, a powiedziała mu wszytkę prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '33', '33', 'Zatym on rzekł do niej: Córko, wiara twoja wybawiła cię, idźże w pokoju, a bądź zdrowa od trapienia twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '34', '34', 'A gdy tego jeszcze domawiał, przyszli niektórzy od książęcia bożnice mówiąc: Dziewka twa umarła i czemuż więcej gabasz mistrza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '35', '35', 'A Jezus wnet usłyszawszy ony słowa, które powiedano, rzekł książęciu bożnice: Nie lękaj się jedno tylko wierz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '36', '36', 'I nie dopuścił żadnemu chodzić za sobą, jedno Piotrowi, Jakubowi i Janowi bratu Jakubowemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '37', '37', 'A przyszedszy w dom książęcia bożnice, widzi zamieszanie i ludzie płaczące i barzo narzekające.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '38', '38', 'I wszedszy rzekł im: Przeczże płaczecie a sobą trwożycie? Dzieweczka ta nie umarła, ale zasnęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '39', '39', 'I śmiali się z niego, ale on roskazawszy wszytkim wynidź, wziął ojca i matkę onej dzieweczki i ony którzy przy nim byli, a wszedł tam gdzie dzieweczka leżała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '40', '40', 'A wziąwszy ją za rękę, rzekł do niej: Talita Kumi, to jest, jeślibyś wykładał: Dzieweczko (tobie mówię) wstań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '41', '41', 'I natychmiast dzieweczka wstawszy, chodziła, abowiem była we dwunaście leciech i zdumieli się z podziwieniem wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '4', '42', '42', 'Tedy im wielce zakazał, aby tego żaden nie wiedział, a rzekł, aby mu dano co jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '0', '0', 'A wyszedszy z tamtąd, przyszedł do ojczyzny swej, a za nim szli zwolennicy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '1', '1', 'A gdy przyszedł sabat, począł uczyć w bożnicy, a wielu ich słuchając zdumiewali się i mówili: Zkądże ten to mąż? A co za mądrość, która dana jest jemu, iż też cuda takie dzieją się przez ręce jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '2', '2', 'Izali to nie on cieśla, który jest Syn Marjej, brat Jakubowy i Jozy, Judaszowy i Symonów? Zaź nie masz u nas i sióstr jego? I obrażali się w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '3', '3', 'Tedy mówił ku nim Jezus: Żaden prorok nie jest beze czci chyba w ojczyźnie swojej, a między krewnymi i w domu swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '4', '4', 'I nie mógł tam okazać żadnej mocy, jedno iż mało chorych wkładając na nie ręce uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '5', '5', 'A dziwował się niedowiarstwu ich i obchodził po wsiach okolicznych nauczając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '6', '6', 'Tedy wezwał onych dwunaście i począł je po dwu rozsyłać, dawając im moc nad duchy nieczystymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '7', '7', 'I roskazał im aby nic z sobą na drogę nie brali jedno tylko laskę, ani sumek, ani chleba, ani pieniędzy w trzosie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '8', '8', 'Ale iżby się obuli w trzewiki, a iżby nie obłóczyli dwu sukien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '9', '9', 'Zatym do nich mówił: Gdziekolwiek wnidziecie do domu, tam mieszkajcie aż się z tamtąd ruszycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '10', '10', 'A którzyby was kolwiek nie przyjmowali, ani was słuchali, wyszedszy z tamtąd, otrzecie proch, który jest pod nogami waszemi ku przeświadczeniu ich: Zaprawdę powiedam: Lżej będzie Sodomie i Gomorze w dzień sądny, a niż miastu onemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '11', '11', 'A tak oni wyszedszy, opowiedali ludziam aby się uznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '12', '12', 'I wyrzucali wiele diabelstwa, a wiele chorych olejkiem mazali i uzdrawiali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '13', '13', 'To usłyszał król Herod (bo imię jego było sławne) i rzekł: Jan, on który krzcił, wstał z martwych i dlategoż się w nim ty mocy okazują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '14', '14', 'A drudzy mówili: Heljaszci to, drudzy zasię powiedali: Prorok jest, abo jeden z onych proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '15', '15', 'Co słysząc Herod rzekł: Tenci jest Jan, któregom dał ściąć, a onci wstał z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '16', '16', 'Abowiem ten Herod posłał był żołnierze i pojmał Jana podawszy go do więzienia, dla Herodjady żony Filipa brata swego, który ją był pojął za żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '17', '17', 'Bo Jan mówił Herodowi: Nie słuszy abyś miał żonę brata twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '18', '18', 'I miała nań waśń Herodjas, a chciała go zamordować, ale nie mogła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '19', '19', 'Abowiem Herod obawał się Jana, wiedząc że był mąż sprawiedliwy i święty, a miał go w uczciwości, a słuchając go, wiele czynił i słuchał go rad.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '20', '20', 'Gdy tedy był dzień po temu, aby Herod w dzień narodzenia swojego sprawił wieczerzę na pany swoje, na rotmistrze i na przedniejsze w Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '21', '21', 'Tam gdy przyszła dziewka onej Herodjady, tańcowała i spodobała się Herodowi i inym społu siedzącym i rzekł król ku dziewce: Proś mię ocz jedno chcesz, a dam tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '22', '22', 'I przysiągł jej: Oczkolwiek mię prosić będziesz, damci, aż i do połowicę królestwa mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '23', '23', 'Ale ona wyszedszy, rzekła matce swej: Oczże mam prosić? A ona jej powiedziała: O głowę Jana Krzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '24', '24', 'A tak ona natychmiast wszedszy umyślnie do króla, prosiła mówiąc: Teraz już chcę abyś mi dał na misie głowę Jana krzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '25', '25', 'Ale się król barzo zafrasował, dla przysięgi i dla onych, którzy z nim społu siedzieli, nie chcąc jej odrzucać na stronę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '26', '26', 'Zatym wnet król posłał kata, roskazując aby przyniesiono głowę jego, tedy on szedszy, ściął jego w ciemnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '27', '27', 'I przyniósł głowę jego na misie, a oddał ją dziewce, dziewka zasię oddała ją matce swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '28', '28', 'Co gdy usłyszeli zwolennicy jego, przyszli i wzięli trupa jego i położyli go w grobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '29', '29', 'Zeszli się potym Apostołowie do Jezusa i opowiedzieli mu wszytko co czynili i czego uczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '30', '30', 'Ku którym rzekł: Pódźcie wy osobno na miejsce puste, a odpoczyńcie maluczko, bo wiele tych było co przychadzali i odchadzali, tak, iż nie mieli czasu żeby się byli najedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '31', '31', 'A tak szli na miejsce puste, osobno przeprawiwszy się w łodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '32', '32', 'Ale ono zebranie ludu widząc ony odchodzące, poznali go wiele ich i zbieżali się tam pieszo ze wszytkich miast, a uprzedziwszy je, zeszli się do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '33', '33', 'Tedy Jezus ujźrzawszy ono wielkie zebranie ludzi, ruszony miłosierdziem nad nimi, którzy byli jako owce nie mające pastyrza, począł je uczyć wiela rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '34', '34', 'A gdy już było dobrze na dzień, przyszli kniemu zwolennicy jego mówiąc: Miejsce jest puste, a już daleko na dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '35', '35', 'Rospuść je, aby się rozszedszy do miasteczek i do wsi okolicznych nakupili sobie chlebów, bo nie mają co jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '36', '36', 'Ale on odpowiedziawszy rzekł: Dajcie wy im co jeść. A oni rzekli ku niemu: Idźmysz kupmy za dwie ście pieniędzy chleba, a dajmy im coby jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '37', '37', 'Zatym on rzekł do nich: Wielesz chlebów macie? Idźcie a dowiedzcie się. A oni dowiedziawszy się, powiedzieli: Pięcioro chleba i dwie rybie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '38', '38', 'Tedy im roskazał, aby wszytkim siadać kazali rzędmi na zielonej trawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '39', '39', 'A tak siedli rzędmi po grzędach, indzie po stu indzie po pięcidziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '40', '40', 'Wziąwszy tedy ono pięcioro chleba i ony dwie rybie, wejźrzawszy w niebo, błogosławił a łamiąc chleby, dawał zwolennikom swoim aby przed nie kładli i dwie rybie podzielił między wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '41', '41', 'I jedli wszyscy a najedli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '42', '42', 'Tamże zebrali okruszków dwanaście koszów pełnych i ostatki ryb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '43', '43', 'A tych, którzy jedli, było około piąci tysięcy mężów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '44', '44', 'Tedy wnet roskazał zwolennikom swym wnidź w łódź, aby się naprzód przeprawili do Betsajda ku dalszemu brzegowi, ażby on rozpuścił lud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '45', '45', 'A gdy je odprawił, wszedł na górę aby się modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '46', '46', 'Zatym gdy się dzień k wieczoru chylił, była łódź w pośrzód morza, a on sam był na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '47', '47', 'I wyźrzał, że się pracowali wioząc, (abowiem wiatr był im sprzeciwny). A tak o czwartej w noc przyszedł do nich chodząc po morzu i chciał je minąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '48', '48', 'Tedy oni ujźrzawszy go chodząc po morzu, mniemali żeby była obłuda i zawołali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '49', '49', 'Abowiem wszyscy widząc go zatrwożyli sobą. Ale on rzekł do nich temi słowy: Wierzcie jamci jest, a nie lękajcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '50', '50', 'Zatym wstąpił do nich w łódź, a uciszył się wiatr, z kąd się barzo aż i na zbyt sami w sobie zdumiewali i dziwowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '51', '51', 'Bo nie zrozumieli (co się działo) w onych chlebiech, gdyż serce ich było zatwardzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '52', '52', 'A gdy się przeprawili, przyszli do ziemie Genezaret i przybili się ku brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '53', '53', 'Tedy wyszedszy z łodzi, natychmiast go poznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '54', '54', 'A gdy onę wszytkę krainę okoliczną przyszli, poczęto nosić na łożach, gdy usłyszeli iż tam był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '5', '55', '55', 'A gdziekolwiek jedno przyszedł, do wsi, abo do miast, abo na pola, kładli przedeń niemocne po ulicach, prosząc go aby się tylko dotknęli podołka szat jego. A ile się ich dotknęli go, przychodzili ku zdrowiu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '0', '0', 'Tedy się k niemu zeszli Faryzeuszowie i z doktorów niektórzy co byli przyszli z Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '1', '1', 'A ujźrzawszy niektóre z zwolenników jego, iż jedli chleb z brudnemi rękoma i nie umywszy się, utyskowali na nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '2', '2', 'Abowiem Faryzeuszowie i wszyscy Żydowie, jeśli często rąk nie umywali, tedy nie jedli, trzymając ustawy przodków swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '3', '3', 'Także z dworu przyszedszy jeśliby się nie umyli, tedy nie jadali i wiele są inych rzeczy, które przyjęli ku trzymaniu, to jest, umywanie kubków i konewek i inego naczynia miedzianego i łóżek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '4', '4', 'Potem go pytali Faryzeuszowie i doktorowie: Przecz zwolennicy twoi nie chodzą wedle ustaw przodków, ale jedzą chleb nie umywszy rąk?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '5', '5', 'Tedy on odpowiedziawszy rzekł im: Iście dobrze Ezajasz o was ludziach pokrytych prorokował, jako jest napisano: Lud ten usty mię chwali, ale serce ich daleko jest odemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '6', '6', 'Próżnoć mię tedy chwalą ucząc nauk, które są roskazaniem ludzkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '7', '7', 'Abowiem opuściwszy roskazanie Boże trzymacie ustawy ludzkie, umywanie konewek i kubków, nadto i wiele inych takowych rzeczy czynicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '8', '8', 'Mówił też do nich: Czyście czynicie iż w niewcz obracacie przykazanie Boże, zachowywając ustawy wasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '9', '9', 'Bo Mojżesz rzekł: Ćci ojca twego y matkę twoję, a ktoby złorzeczył ojcu abo matce, niech śmiercią umrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '10', '10', 'Lecz wy mówicie: Jeśliżby kto rzekł ojcu abo matce: Korban (to jest dar) którykolwiek będzie odemnie, pomożeć (tedy nie winien będzie).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '11', '11', 'I nie dopuszczacie mu więcej nic dobrego czynić ojcu swemu abo matce swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '12', '12', 'W niwecz obracając słowo Boże ustawą waszą, którąście ustawili i wiele inych rzeczy tym podobnych czynicie .');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '13', '13', 'A wezwawszy wszytkiego zgromadzenia rzekł im: Słuchajcie mię wszyscy, a zrozumiejcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '14', '14', 'Nie masz nic zwierzchownego co wchodzi w człowieka, coby go mogło splugawić. Ale co wychodzi od niego, toć jest co szpeci człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '15', '15', 'Jeśliż kto ma uszy ku słuchaniu, niech słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '16', '16', 'A gdy odszedszy od zgromadzenia wszedł do domu, pytali go zwolennicy jego o onej przypowieści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '17', '17', 'Tedy rzekł do nich: A także i wy jesteście bez rozumienia? Nie rozumiejcież, iż wszytko co z wierzchu przychodzi w człowieka, nie może go splugawić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '18', '18', 'Abowiem nie wchodzi w serce jego, ale w brzuch i wychodzi na dwór przeczyszczając wszytki potrawy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '19', '19', 'I powiedał, że to co pochodzi od człowieka, toż oszpeca człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '20', '20', 'Bo ze wnątrz serca ludzkiego wychodzą myśli złe, cudzołóstwa, nieczystoty i morderstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '21', '21', 'Złodziejstwa, łakomstwa, niecnoty, zdrada, wszeteczeństwo, oko złe, obmówisko, pycha, szaleństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '22', '22', 'Wszytki ty złości ze wnątrz pochodzą i plugawią człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '23', '23', 'A tak on wstawszy z tamtąd szedł do granic Tyrskich i Sydońskich, a wszedszy do domu, nie chciał aby o nim kto miał wiedzieć, lecz się potaić nie mógł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '24', '24', 'Abowiem usłyszawszy o nim niewiasta, której dzieweczka miała ducha nieczystego, przyszła a padła u nóg jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '25', '25', 'A niewiasta ta była: Grecka, rodem z Syrofenicijej i prosiła go aby diabelstwo wyrzucił od dziewki jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '26', '26', 'Ale rzekł jej Jezus: Zetrwaj aż pierwej synowie nakarmieni będą, bo nie służsza brać chleb dzieciam i podawać szczeniętam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '27', '27', 'Na to mu ona odpowiedziała mówiąc: Takci jest Panie. Aleć i szczeniątka jadają pod stołem odrobiny dziatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '28', '28', 'Tedy on rzekł do niej: Dla tej powieści idźże, wyszło diabelstwo z dziewki twej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '29', '29', 'A gdy ona odeszła do domu swego, znalazła iż wyszło diabelstwo, a dziewka porzucona leżała na łożu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '30', '30', 'A tak wróciwszy się z granic Tyrskich i Sydońskich, przyszedł ku Morzu Galilejskiemu, przez pośrzodek granic Dekapolis.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '31', '31', 'I przywiedli mu głuchego i ledwie mówiącego, a prosili go aby nań rękę włożył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '32', '32', 'A gdy go odwiódł od zebrania ludzi na stronę, włożył palce swe w uszy jego, a plunąwszy tknął języka jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '33', '33', 'Zatym wejźrzawszy w niebo, westchnął i rzekł kniemu: Efata, to jest, otwórz się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '34', '34', 'I wnet się otworzyły uszy jego, a związka języka jego rozwiązana jest i mówił wybornie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '35', '35', 'Tedy im roskazał, aby żadnemu nie powiedali. Ale aczkolwiek im on zakazował, a wżdy oni to tym więcej opowiedali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '6', '36', '36', 'I zdumiawszy się aż i nazbyt mówili: Dobrzeć wszystko uczynił dawając to głuchym iż słyszą, niemym iż mówią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '0', '0', 'Za tych tam czasów gdy zebranie ludzi barzo wielkie było, a nie mieli coby jedli, wezwał Jezus zwolenników swych i rzekł im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '1', '1', 'Rozlitowałem się nad zebraniem ludzi, abowiem już przez trzy dni zostawają przy mnie, a nie mają co jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '2', '2', 'Gdzie więc jeśli je opuszczę bez pokarmu do domów ich, ustaną na drodze, abowiem niektórzy z nich z daleka przyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '3', '3', 'Tedy mu odpowiedzieli zwolennicy jego: Z kądże tych może kto tu na puszczy nakarmić chleba?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '4', '4', 'A on ich spytał: Wieleć chleba macie? Tedy oni odpowiedzieli: Siedmioro.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '5', '5', 'Roskazał tedy wielkości ludzi aby siadła na ziemi, a wziąwszy one siedmioro chleba gdy uczynił dzięki, połamał i dał zwolennikom swoim aby przed nie kładli i położyli przed ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '6', '6', 'Mieli też i trochę rybek, a gdy błogosławił, rzekł aby i ony położyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '7', '7', 'Jedli tedy i najedli się, a zebrali ułomków których było zostało siedm koszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '8', '8', 'A było tych co jedli około czterech tysięcy i rozpuścił je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '9', '9', 'A wnet wszedszy w łódź z zwolenniki swojemi, przyszedł do granic Dalmanuta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '10', '10', 'I przyszli Faryzeusze, a poczęli się z nim swarzyć, prosząc od niego znaku z nieba, a kusząc go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '11', '11', 'Tedy serdecznie westchnąwszy w duchu swem, rzekł: Przeczże naród ten znaków żąda? Zaprawdę powiedam wam: Żadenci znak nie będzie dan narodowi temu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '12', '12', 'I opuściwszy je, wszedł zasię w łódź, a przeprawił się do dalszego brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '13', '13', 'Zapomnieli tedy z sobą zabrać chleba, a jedno tylko jeden chleb mieli z sobą w łodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '14', '14', 'Zatym im roskazał mówiąc: Obaczcież, a strzeżcież się od kwasu Faryzeuszów i od kwasu Herodowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '15', '15', 'A tak między sobą społu rozmawiali powiedając: Dlategoż, że chleba nie mamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '16', '16', 'Ale to gdy poznał Jezus, rzekł im: O czymże się gadacie, że nie macie chleba? Jeszczeż nie baczycie ani rozumiecie? I jeszczeż macie serca zatwardzone?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '17', '17', 'Oczy mając nie widzicie i uszy mając nie słyszycie, ani pamiętacie tego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '18', '18', 'Gdym ony pięcioro chleba łamał piąciu tysięcy ludziom, wieleście zebrali koszów okruszków? A oni mu powiedzieli: Dwanaście?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '19', '19', 'A gdy i ono siedmioro na cztery tysiące, wieleście koszów pełnych zebrali okruszków? A oni mu powiedzieli: Siedm.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '20', '20', 'Tedy rzekł do nich: I jakoż nie zrozumiewacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '21', '21', 'Potym przyszedł do Betsajda, a przywiedli mu ślepego, prosząc aby się go dotknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '22', '22', 'A tak ująwszy ślepego za rękę, odwiódł go precz za miasteczko, a plun&nbsp;ąwszy na oczy jego, włożył nań ręce i pytał go jeśliby co widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '23', '23', 'A on przejźrzawszy, rzekł: Widzę ludzi, a widzę ich chodzące jako drzewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '24', '24', 'Potym zasię położył ręce na oczy jego, a sprawił to iż znowu patrzał, a przywrócon jest ku wzroku i widział z daleka a jaśnie wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '25', '25', 'Tedy go odesłał Jezus do domu jego mówiąc: Nie chodźże ani do onego miasteczka, ani żadnemu w miasteczku powiedaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '26', '26', 'A wyszedszy Jezus i zwolennicy jego do miasteczek Cezareji, którą nazywano Filipową, pytał zwolenników swoich na drodze, mówiąc do nich: Którymże mię ludzie być powiedają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '27', '27', 'A oni mu odpowiedzieli: Janem Krzcicielem, drudzy też Heljaszem, a drudzy jednym z proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '28', '28', 'Ale on im rzekł: Wy zasie, którym mię być powiedacie? A odpowiedziawszy Piotr, rzekł kniemu: Tyś jest on Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '29', '29', 'Tedy on zakazał aby tego o nim nikomu nie powiedali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '30', '30', 'I począł je nauczać, że potrzeba było Synowi człowieczemu wiele cierpieć i wzgardzonym być od starszych i od książąt kapłańskich i doktorów, a iż miał być zamordowan, potym iż po trzecim dniu miał zmartwychwstać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '31', '31', 'A mówił bespiecznie ty słowa. Tedy Piotr odwiódszy się z nim, począł go z tego fukać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '32', '32', 'Ale on obróciwszy się i wejźrzawszy na swe zwolenniki, sfukał Piotra mówiąc: Odejdź ode mnie szatanie, abowiem nie rozumiesz to co jest Bożego, ale to co jest ludzkiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '33', '33', 'A wezwawszy zebranie ludu i z zwolenniki swemi rzekł im: Którzkolwiek chce za mną idź, niech samego siebie zaprzy a weźmie krzyż swój i niech pójdzie za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '34', '34', 'Abowiem ktokolwiek by chciał zachować duszę swą, straci ją, lecz ktokolwiek utraci duszę swą dla mnie i dla Ewanjelijej, ten ją zachowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '35', '35', 'Bo cóż pomoże człowiekowi jeśliby pozyskał wszytek świat, a żeby utracił duszę swą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '36', '36', 'Abo co za nagrodę da człowiek za swą duszę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '7', '37', '37', 'Abowiem ktoć się wstydać będzie mnie i słów moich między narodem tym cudzołożnym i grzesznym, za tego się też i Syn człowieczy wstydać będzie, gdy przyjdzie z chwałą Ojca swojego z Aniołami świętymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '0', '0', 'Potym rzekł do nich: Zaprawdę powiedam wam iż niektórzy są z tych co tu stoją, którzy nigdy nie ukuszą śmierci, aż gdy ujźrzą Królestwo Boże przychodzące z mocą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '1', '1', 'A szóstego dnia potym wziął Jezus Piotra i Jakuba i Jana, a wwiódł je na górę wysoką samy osobno i przemienił się przed nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '2', '2', 'A szaty jego stały się lśniące barzo, białe jako śnieg, jakich nie może blecharz na świecie wybielić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '3', '3', 'I ujźrzeli Eljasza z Mojżeszem, którzy rozmawiali z Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '4', '4', 'Tedy począł Piotr mówić, a rzekł Jezusowi: Mistrzu dobrzeć nam tu być, postawmyż tedy tu trzy namioty, tobie jeden, Mojżeszowi jeden, a Heljaszowi jeden.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '5', '5', 'Abowiem nie wiedział co mówił, gdyż byli w strachu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '6', '6', 'I nastał obłok, który je zaćmił, a pochodził głos z obłoku mówiąc: Ten ci jest namilszy Syn mój. Tegoż słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '7', '7', 'A wnet gdy się obejźrzali, żadnego więcej nie widzieli, jedno iż tylko Jezus był z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '8', '8', 'Tedy gdy zeszli z góry, roskazał im aby tego nikomu nie powiedali co widzieli, jedno ażby Syn człowieczy wstał z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '9', '9', 'A tak oni zatrzymawali to u siebie, pytając się spólnie coby to było z martwych wstać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '10', '10', 'I pytali go mówiąc: Przeczże doktorowie powiedają, iż pierwej potrzeba aby przyszedł Heljasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '11', '11', 'A on im odpowiedział mówiąc: Naprzód gdy Heljasz przyjdzie naprawi wszystko, a jako jest napisano o synu człowieczym. Potrzeba aby wiele ucierpiał i aby go nizacz nie miano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '12', '12', 'Ale wam powiedam żeć przyszedł Heljasz i czynili mu co chcieli, jako o nim jest napisano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '13', '13', 'A gdy przyszedł do swych zwolenników ujźrzał lud wielki i około nich i doktory swarzące się z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '14', '14', 'Tedy wnet ujźrzawszy go lud wszytek, lękli się, a przyszedszy przywitali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '15', '15', 'I pytali doktorów: Cóż się swarzycie między sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '16', '16', 'A jeden z onego zgromadzenia ludu rzekł: Mistrzu przywiodłem do ciebie syna swego, który ma niemego ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '17', '17', 'Który gdzie go kolwiek napadnie, rozdziera go, a on się ślini i zgrzyta zębami swemi, a schnie. I mówiłem twym zwolennikom aby go wyrzucili, ale nie mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '18', '18', 'Tedy mu on odpowiedziawszy rzekł: O narodzie niewierny i dokądże z wami będę? I dokądże was cierpieć mam? Przywiedźcież go do mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '19', '19', 'I przywiedli go k niemu, a gdy on go ujźrzał; natychmiast go duch rostargnął, a on gdy padł na ziemię, przewracał się śliniąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '20', '20', 'Tedy pytał ojca jego, jako dawno to się przydało jemu, a on powiedział: Jeszcze od dzieciństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '21', '21', 'I często miotał go do ognia i do wody, aby go stracił, ale możeszli co, pomóż nam ruszywszy się przeciw nam miłosierdziem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '22', '22', 'Tedy mu rzekł Jezus: Jeśliż to wierzyć możesz, tedyć wszytko wierzącemu stać się może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '23', '23', 'A gdy natychmiast zawołał ociec onego dziecięcia, ze łzami rzekł: Wierzę Panie! Podpomóżże niedowiarstwa mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '24', '24', 'A gdy ujźrzał Jezus iż się lud społu zbiegał, sfukał onego ducha nieczystego mówiąc mu: Ty duchu niemy i głuchy, ja tobie roskazuję wynidź z niego, a nie wchodź więcej weń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '25', '25', 'Zawoławszy tedy duch, a barzo go rozdarszy wyszedł; tak iż się on stał jako umarły, a wiele ich mówili iż by umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '26', '26', 'Ale Jezus wziąwszy go za rękę podniósł go, a on wstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '27', '27', 'A gdy sam wszedł do domu, zwolennicy jego pytali go osobno: Przecz żechmy my go wyrzucić nie mogli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '28', '28', 'A on im rzekł: Ten rodzaj żadnym sposobem wynidź nie może jedno modlitwą a postem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '29', '29', 'I wyszedszy z tamtąd szli społu w drogę przez Galileję, a nie chciał aby był to kto wiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '30', '30', 'Abowiem uczył zwolenniki swe mówiąc im: Syn człowieczy będzie wydan w ręce ludzkie, którzy go zamordują, ale zamordowany wstanie dnia trzeciego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '31', '31', 'Ale oni tej powieści nie zrozumieli i bali się go spytać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '32', '32', 'Zatym przyszedł do Kapernaum a przyszedszy do domu pytał ich: Ocz żeście się swarzyli w drodze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '33', '33', 'Lecz oni milczeli, abowiem jedni z drugimi poswarzyli się byli ktoby był więczszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '34', '34', 'A gdy siadł wezwał dwunaście i rzekł im: Jeśliż kto chce być pierwszym, będzie ostatecznym po wszytkich i sługa wszytkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '35', '35', 'I wziąwszy dzieciątko postawił je w pośród ich, a wziąwszy go na ręce rzekł im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '36', '36', 'Któżkolwiek jedno z takowych dziateczek przyjmie w imię moje, mnie przyjmuje, a ktokolwiek mnie przyjmuje, nie mnie przyjmuje, ale onego który mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '37', '37', 'Tedy mu Jan odpowiedział mówiąc: Mistrzu, widzielichmy niekt&nbsp;órego co przez imię twe wyganiał diabelstwo, który nie chodzi za nami i zakazalichmy mu, bo za nami nie chodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '38', '38', 'A Jezus rzekł: Nie zakazujcie mu; abowiemci żaden nie jest któryby czynił cuda przez imię moje, a mógł mi prędko złorzeczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '39', '39', 'Bo ten który nie jest przeciw nam, za namić jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '40', '40', 'Abowiem któżkolwiekby wam dał ku piciu kubek wody w imię moje, iżeście są Krystusowi, zaprawdę powiedam wam, żadnym sposobem nie utraci zapłaty swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '41', '41', 'A ktokolwiekby też obraził jednego z tych maluczkich, którzy w mię wierzą, lepiej aby mu był włożon młyński kamień około szyje jego, a iżby był wrzucon w morze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '8', '42', '42', 'A jeśliż ręka twa przywodzi cię ku grzechowi, odetni ją. Bo lepiej jest tobie wnidź do żywota kliszawym, niż mając obie ręce być wrzuconym do piekła w ogień nieugaszony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '0', '0', 'Stamtąd wstawszy, przyszedł w granice judskie przez ziemice, która jest za Jordanem, a zasię kniemu zeszło się nie mało ludu, który on uczył tak jako był zwykł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '1', '1', 'Tedy przystąpiwszy Faryzeuszowie pytali go: Jeśliż się godzi mężowi żonę opuścić? Kusząc go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '2', '2', 'Na to im on odpowiedziawszy rzekł: Cóż wam roskazał Mojżesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '3', '3', 'A oni mu rzekli: Mojżesz dopuścił napisać list rozwodowy i opuścić żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '4', '4', 'Tedy Jezus odpowiedziawszy im rzekł: Dla zatwardzenia serca waszego napisał wam to roskazanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '5', '5', 'Aleć od początku stworzenia mężczyznę i niewiastę uczynił je Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '6', '6', 'A dlategoż opuści człowiek ojca swego i matkę swą, a przyłączy się ku żenie swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '7', '7', 'A którzy byli dwiema, będą jednem ciałem, a tak już nie są dwa, ale jedno ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '8', '8', 'Co tedy Bóg złączył, niechżeć człowiek nie rozłącza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '9', '9', 'Tedy w domu zasię zwolennicy jego o tejże rzeczy pytali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '10', '10', 'A on im rzekł: Któżkolwiek opuści żonę swą a pojmie iną, cudzołoży przeciw niej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '11', '11', 'A jeśliż też niewiasta opuści męża swego, a pójdzie za drugiego, cudzołoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '12', '12', 'Tedy mu przywiedziono dziateczki aby się ich dotykał, ale zwolennicy fukali na ony, którzy je przywodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '13', '13', 'Co gdy obaczył Jezus, gniewał się mówiąc do nich: Dopuśćcie dziateczkam idź do mnie, a nie zakazujcie im, abowiem takowych jest Królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '14', '14', 'Zaprawdę wam powiedam: Któżkolwiek Królestwa Bożego nie przyjmie jako dzieciątko, żadnym sposobem do niego nie wnidzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '15', '15', 'I biorąc je na ręce, a wkładając na nie ręce, błogosławił im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '16', '16', 'A tak wyszedszy w drogę, niektóry gdy przybieżał a upadł mu u kolan, pytał go: Mistrzu dobry, cóż czynić mam abych otrzymał żywot wieczny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '17', '17', 'A Jezus mu rzekł: Czemuż mię nazywasz dobrym? Żadenci nie jest dobrym jedno jeden, to jest Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '18', '18', 'Umieszże przykazanie? Nie cudzołóż, nie zabijaj, nie kradni, nie powiedaj fałszywego świadectwa, nie czyń żadnemu szkody, czci ojca twego i matkę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '19', '19', 'Tedy mu on odpowiedziawszy rzekł: Mistrzu, jam tego wszytkiego strzegł od młodości mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '20', '20', 'A Jezus wejźrzawszy nań, rozmiłował się go i rzekł mu: Jednegożci nie dostawa: Idźże a przedaj wszytko co masz i rozdaj ubogim, a będziesz miał skarb w niebie, przydźże a chodź za mną wziąwszy krzyż na się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '21', '21', 'Ale się on zasmucił dla onej powieści i odszedł frasowanym, abowiem miał wiele majętności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '22', '22', 'A obejźrzawszy się Jezus, rzekł do zwolenników swych: Z jakąż trudnością ci, którzy bogactwa mają wnidą do Królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '23', '23', 'Ulękli się tedy zwolennicy dla tych słów jego. A Jezus im zasię odpowiedziawszy rzekł: Symonie, jakoż jest trudno tym co bogactwam ufają wnidź do Królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '24', '24', 'Łacwiej jest wielbłądowi wnidź przez ucho igielne, niż bogaczowi wnidź do Królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '25', '25', 'A oni się tym więcej dziwowali, mówiąc sami ku sobie: I któż może być zbawion?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '26', '26', 'A Jezus pojźrzawszy na nie rzekł: U ludzić to jest niepodobno, ale nie u Boga. Abowiem u Boga wszytko jest podobno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '27', '27', 'I począł mu Piotr mówić: Otochmy my wszystko opuścili, a szlichmy za tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '28', '28', 'Na to Jezus odpowiedziawszy rzekł: Zaprwadę mówię wam: Żaden nie jest któryby opuścił dom, abo bracią, abo siostry, abo ojca, abo matkę, abo żonę, abo dzieci, abo rolę dla mnie i dla Ewanjelijej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '29', '29', 'Żeby nie miał wziąć sto kroć więcej, teraz za tego czasu, domów i braciej, i sióstr, i matek, i braci, i ról z prześladowaniem, a w przyszłym wieku żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '30', '30', 'Abowiem wiele pirwszych będą poślednymi, a pośledni pierwszemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '31', '31', 'I byli w drodze idąc ku Jeruzalem, a Jezus szedł przed nimi i lękali się, a idąc za nim bali się. I zasie wziąwszy z sobą ony dwanaście, począł im powiedać co się z nim dziać miało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '32', '32', 'Mówiąc: Oto idziemy do Jeruzalem, a Syn człowieczy będzie wydan książętom kapłańskim i doktorom, a skażą go na śmierć i podadzą go poganom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '33', '33', 'A oni będą go nagrawać i ubiczują go i będą nań plwać, a zamordują go, wszakoż dnia trzeciego wstanie z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '34', '34', 'Ale do niego przyszli Jakub i Jan, synowie Zebedeuszowi mówiąc: Mistrzu, żądamy abyś to ocz prosić będziem uczynił dla nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '35', '35', 'Tedy im on rzekł: Czegoż chcecie abych dla was uczynił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '36', '36', 'A oni mu powiedzieli: Daj nam to abychmy siedzieli jeden po prawej, a drugi po lewej twej stronie w chwale twojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '37', '37', 'Ale im Jezus rzekł: Nie wiecie ocz prosicie, możecieli pić kubek który ja piję? I być krzczeni krztem którym bywam okrzczon?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '38', '38', 'A oni mu powiedzieli: Możemy! Tedy im rzekł Jezus: Kubek który ja piję pić będziecie i krztem którym bywam okrzczon, krzczeni będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '39', '39', 'Ale siedzieć po prawej abo po lewej mej stronie, nie moja to rzecz dawać, ale dadzą tym którym jest nagotowano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '40', '40', 'Co gdy dziesięć inych usłyszeli, poczęli za złe mieć Jakubowi i Janowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '41', '41', 'A tak Jezus wezwawszy ich, powiedział im: Wiecie, że onym, którym się podoba panować nad narody, panują nad nimi, a którzy są wielcy między nimi, używają nad nimi zwierzchności swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '42', '42', 'Aleć między wami tak nie będzie. Owszem kto będzie chciał być wielkim między wami, niech będzie sługą waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '43', '43', 'I którzkolwiek z was będzie chciał być pierwszym, ten będzie wszytkich służebnikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '44', '44', 'Abowiem i Syn człowieczy nie przyszedł aby mu służono, ale żeby służył i dał duszę swą zapłatę odkupienia za wiele ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '45', '45', 'Tedy przyszli ku Jerychu. A gdy wychadzał z zwolenniki swemi z Jerycha i z wielkim ludem, syn Tymeuszów, Bartymeusz ślepy siedział podle drogi żebrząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '46', '46', 'A gdy usłyszał iż to był on Jezus Nazareński, począł wołać mówiąc: Jezusie Synu Dawidów zmiłuj się nade mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '47', '47', 'I fukali nań wiele ich aby milczał, ale on tym więcej wołał: Synu Dawidów zmiłuj się nade mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '48', '48', 'Tedy Jezus zastanowiwszy się, kazał go zawołać i zawołano ślepego mówiąc mu: Bądź dobrego serca a wstań, woła cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '49', '49', 'A on porzuciwszy płaszcz , gdy wstał, przyszedł ku Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '50', '50', 'I odpowiedziawszy Jezus rzekł mu: Czego chcesz abychci uczynił? A ślepy powiedział jemu: Mistrzu niechaj wzrok wezmę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '9', '51', '51', 'Rzekł mu tedy Jezus: Idź, wiara twa wybawiła cię, a tak natychmiast wziął wzrok i szedł za Jezusem w drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '0', '0', 'A gdy byli blisko Jeruzalem, Betfage i Betanijej, przy górze oliwnej posłał dwu z uczniów swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '1', '1', 'I rzekł ku nim: Idźcie do miasteczka, które jest przeciwko wam, a natychmiast wszedszy do niego, najdziecie oślę uwiązane, na którym żaden człowiek nie siedział, rozwiążcież je a przywiedźcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '2', '2', 'A jeśliby wam kto rzekł: Przecz to czynicie? Powiedzcie iż Pan ma tego potrzebę i wnet je tu poszlę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '3', '3', 'Tedy szli, a naleźli oślę uwiązane u forty, na dworze, gdzie się dwie drodze schadzały i odwiązali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '4', '4', 'Niektórzy tedy z onych co tam stali rzekli im: Cóż czynicie, iż odwiązujecie oślę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '5', '5', 'Ale oni powiedzieli tak jako im roskazał Jezus i puścili je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '6', '6', 'Przywiedli tedy oślę do Jezusa, wrzuciwszy na nie szaty swe i wsiadł na nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '7', '7', 'A wiele ich słali odzienia swoje po drodze, drudzy zasię rąbali gałązki z drzew i kładli na drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '8', '8', 'Ci też, którzy szli wprzód i pozad wołali mówiąc: Hosanna, błogosławiony który idzie w imię Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '9', '9', 'Błogosławione Królestwo, które przyszło w imię Pańskie, ojca naszego Dawida. Hosanna, o ty któryś jest na wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '10', '10', 'A wszedł Pan do Jeruzalem i do Kościoła i gdy wszytko oglądał, a już była wieczorna godzina, przyszedł do Betanijej z dwiemanaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '11', '11', 'A nazajutrz wyszedszy z Betanijej łaknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '12', '12', 'A gdy ujźrzał z daleka figowe drzewo, na którym było liście, przyszedł patrzyć ażaby był co na nim nalazł i gdy do niego przyszedł, nic nie nalazł, jedno liście, bo nie był czas figam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '13', '13', 'Tedy Jezus rzekł figowemu drzewu: Niechżeć nikt na wieki nie używa owocu z ciebie, co słyszeli uczniowie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '14', '14', 'A tak przyszli do Jeruzalem, a Jezus wszedszy do kościoła, począł wyrzucać ty, którzy kupowali i przedawali w kościele a sprzewracał stoły bankierzów i stołki tych co przedawali gołębie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '15', '15', 'Ani dopuścił aby miał kto nieść naczynie przez kościół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '16', '16', 'Tamże uczył, mówiąc im: Izaź nie jest napisano: Że dom mój, dom modlitwy będzie wezwan wszytkim narodom? A wyście ji uczynili jaskinią zbójców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '17', '17', 'To słysząc doktorowie i książęta kapłańskie, szukali jakoby go zatracili, abowiem się go bali, gdyż wszytek lud zdumiał się na naukę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '18', '18', 'Ale gdy wieczór nadchodził, wyszedł Jezus z miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '19', '19', 'A gdy się rano przechadzali, ujźrzeli iż figowe drzewo z korzenia uschło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '20', '20', 'Tedy Piotr wspomniawszy rzekł mu: Mistrzu, oto figowe drzewo coś je klął, uschło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '21', '21', 'A Jezus odpowiedziawszy rzekł mu: Miejcie wiarę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '22', '22', 'Bo zaprawdę wam powiedam: Iż którykolwiek rzecze tej górze: Podnieś się a rzuć się w morze, a nie będzie się wahał w sercu swem, lecz uwierzy, że się stanie co mówi, tedyć się stanie co jedno rzecze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '23', '23', 'A przetoż wam powiedam: Oczkolwiek modląc się prosicie, wierzcie, że weźmiecie a stanie się wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '10', '24', '24', 'Gdy też staniecie modląc się, odpuśćcie jeśli co przeciw komu macie, aby on ociec wasz, który jest w niebie odpuścił wam przestępstwa wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '0', '0', 'Począł do nich mówić przez przypowieści: Niektóry człek sadził winnicę, ugrodził płot i wykopał dół, a zbudował wieżę, a nająwszy ją oraczom, sam precz w drogę odjechał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '1', '1', 'Tedy posłał sługę do oraczów, gdy był czas aby wziął od oraczów owoce z winnic.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '2', '2', 'Ale go oni pojmawszy ubili i odesłali próżnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '3', '3', 'Potym do nich i drugiego sługę posłał, ale go oni ukamionowawszy ranili w głowę, a odesłali go z zelżywością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '4', '4', 'I zasię posłał drugiego, ale onego zabili i wiele inych, jedny bijąc, drugie zasię mordując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '5', '5', 'A tak gdy jeszcze miał jednego syna swego namilszego, posłał go też do nich na ostatek mówiąc: Wżdyć się będą bać syna mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '6', '6', 'Ale oni oracze rzekli między sobą: Tenci jest dziedzic, pódźcież zabijmy go, a nasze będzie dziedzictwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '7', '7', 'I pojmawszy go zamordowali, a wyrzucili precz z winnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '8', '8', 'Cóż tedy uczyni pan onej winnice? Przyjdzie a potraci oracze i da winnice inym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '9', '9', 'I nie czytaliżeście Pisma tego: Który kamień odrzucili budujący, ten się stał głową u węgła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '10', '10', 'Od Panać się to stało, a jest z podziwianiem w oczach naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '11', '11', 'Starali się tedy jakoby go pojmać, ale się pospólstwa bali, bo poznali iż przeciw nim onę przypowieść powiedział, a tak go opuściwszy odeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '12', '12', 'I posłali kniemu niektóre z Faryzeuszów i z Herodjanów, aby go podchwycili słowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '13', '13', 'A tak oni przyszedszy, rzekli do niego: Mistrzu, wiemy żeś prawdziwy jest, a nie dbasz nina kogo, abowiem się nie oglądasz na osobę ludzką, ale w prawdzie drogi Bożej uczysz. Słusznali rzecz dać czynsz cesarzowi abo nie? Zali damy, zali nie damy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '14', '14', 'Ale on wiedząc obłudność ich rzekł im: Czemuż mię kusicie? Przynieście mi pieniądz abych go oglądał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '15', '15', 'Tedy mu oni przynieśli i rzekł im: Czyjże to jest obraz i napis? A oni powiedzieli: Cesarski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '16', '16', 'I odpowiedziawszy Jezus rzekł im: Wróccież co jest cesarskiego, cesarzowi, a co jest Bożego, Bogu i zadziwili się mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '17', '17', 'Zatym do niego przyszli Saduceuszowie, którzy powiedają iż nie masz zmartwychwstania i pytali go mówiąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '18', '18', 'Mistrzu, Mojżesz nam napisał: Jeśliżby czyj brat umarł i pozostawił żonę, a synów by nie pozostawił, aby wziął brat jego żonę jego, a wzbudził potomstwo bratu swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '19', '19', 'A było siedm braciej, a pierwszy pojąwszy żonę, umarł nie pozostawiwszy potomstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '20', '20', 'Tedy ją wziął drugi i umarł, lecz też oni on pozostawił potomstwa i trzeci także.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '21', '21', 'Owa tak oni siedm pojmowali ją, ani pozostawili potomstwa, na ostatku po wszytkich umarła też i niewiasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '22', '22', 'A tak czasu z martwychwstania, gdy powstaną, czyją to z nich będzie żoną? Bo siedm ich mieli ją za żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '23', '23', 'Na to Jezus odpowiedziawszy rzekł im: I zaź nie dlatego błądzicie, że nie umiecie pisma ani mocy Bożej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '24', '24', 'Abowiem gdy z martwych wstaną, ani żon pojmować będą, ani za mąż wydawać będą. Ale są jako Aniołowie w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '25', '25', 'A o umarłych, iż będą wzbudzeni, nie czytaliście w księgach waszych Mojżeszowych? Jako we krzu mówił Bóg do niego rzekąc: Jaciem jest Bóg Abraamów, Bóg Izaaków i Bóg Jakubów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '26', '26', 'Nie jest Bogiem umarłych, ale Bogiem żywiących. A przetoż wy barzo błądzicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '27', '27', 'A gdy przyszedł niektóry z doktorów, co słyszał gdy się gadali, wiedząc że im dobrze odpowiedział, spytał go które jest pierwsze między wszytkim przykazanie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '28', '28', 'Tedy mu Jezus odpowiedział: Pirwsze ze wszytkich roskazanie jest: Słuchaj Izraelu: Pan Bóg nasz Pan jeden jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '29', '29', 'A przetoż miłuj Pana Boga twego ze wszytkiego serca twego, ze wszytkiej dusze twojej i ze wszytkiej twojej myśli i ze wszytkich sił twoich, a toć jest pierwsze roskazanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '30', '30', 'A wtóre zasię temuż podobne, to jest: Miłuj bliźniego jako samego siebie. Nad tyć oto nie masz inego więczszego roskazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '31', '31', 'Tedy mu rzekł on doktor: Dobrześ Mistrzu wprawdzie powiedział, iż jeden jest Bóg, a nadeń nie masz inego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '32', '32', 'I miłować go ze wszytkiego serca, ze wszytkiego zmysłu, ze wszytkiej dusze i ze wszytkich sił, także miłować bliźniego jako samego siebie, co jest rzecz więczsza niż wszytki palone i ine ofiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '33', '33', 'Widząc tedy Jezus, iż rostropnie odpowiedział rzekł mu: Niedaleko jesteś od królestwa Bożego. I nie śmiał go żaden dalej pytać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '34', '34', 'Tedy Jezus odpowiedziawszy rzekł: Nauczając w Kościele, jakoż mówią doktorowie: Iż Krystus jest syn Dawidów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '35', '35', 'Bo i sam Dawid powiedział przez Ducha świętego: Rzekł Pan Panu memu: Siądź na prawicy mojej, aż podłożę nieprzyjacioły twoje podnóżkiem nóg twych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '36', '36', 'A tak sam Dawid powieda go być Panem: Z kądże tedy jest Synem jego? A barzo wiele ludu rado go słuchało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '37', '37', 'I mówił do nich w nauce swojej: Strzeżcie się doktorów którzy chcą chodzić w długich szatach, a kochają się w pozdrawianiu na rynkoch.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '38', '38', 'I pierwsze siadania na schadzkach i pierwsze zasiadania na wieczerzach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '39', '39', 'Którzy do gruntu pożyrają domy wdów, a chcąc być widziani, bawią się długimi modlitwami, cić odniosą cięższy sąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '40', '40', 'A gdy Jezus siedział przeciw skarbnicy, patrzał jako lud kładł pieniądze do skarbu, a wiele bogaczów wiele kładli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '41', '41', 'Kędy przyszedszy też niektóra wdowa uboga, włożyła dwa drobne pieniądze, które ważyły kwadrant.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '42', '42', 'Tedy on wezwawszy ku sobie zwolenników swych rzekł im: Zaprawdę wam powiedam: Ta uboga wdowa więcej włożyła niż wszyscy ci, którzy kładli do skarbu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '11', '43', '43', 'Abowiem ci wszyscy to co im zbywało znieśli, ale ta z niedostatku swego co miała włożyła i wszytko swe pożywanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '0', '0', 'A gdy wychodził z kościoła, rzekł mu niektóry z zwolenników jego: Mistrzu obacz jakie to kamienie i jakie budowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '1', '1', 'Tedy Jezus odpowiedziawszy rzekł: Widzisz to wielkie budowanie? Nie zostanieć kamień na kamieniu, któryby nie był rozwalon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '2', '2', 'I siedząc na górze oliwnej przeciw kościołowi, pytali go osobno: Piotr, Jakub, Jan i Andrzej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '3', '3', 'Powiedzże nam kiedy ty rzeczy będą, a co za znak będzie, gdy przyjdzie czas iż się wszystko ma skończyć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '4', '4', 'A odpowiedziawszy im Jezus, począł mówić: Baczcież aby was nikt nie zwodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '5', '5', 'Abowiem wieleć ich przyjdzie w imieniu moim, powiedając: Jaciem jest Krystus i wiele ich zwiodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '6', '6', 'Gdy tedy usłyszycie walki i wieści o walkach, nie trwóżcież sobą, boć potrzeba być temu, aleć nie jeszcze koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '7', '7', 'Abowiem powstanie naród przeciw narodowi, a królestwo przeciw królestwu i będą trzęsienia ziemie na każdych miejscach i będą głody i zamieszania, a ty są początki boleści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '8', '8', 'Ale wy się ostrzegajcie, boć was wydadzą przed sądy i do bożnic i będą was bić, a przed przełożonymi i królami dla mnie stawać będziecie ku przeświadczeniu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '9', '9', 'A potrzeba aby naprzód u wszytkich narodów była opowiedana Ewanjelija.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '10', '10', 'A gdy was wydane wodzić będą, nie troszczcież się przed tym cobyście mówić mieli, ani o tym myślcie, ale cokolwiek wam będzie podano na on czas to mówcie. Abowiem nie wy jesteście, którzy mówicie, ale Duch on Święty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '11', '11', 'I podać brat brata na śmierć, a ociec syna i powstaną dzieci przeciwko rodzicom, starając się jakoby je o śmierć przyprawili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '12', '12', 'Będziecie u wszytkich w nienawiści dla imienia mego, lecz który dotrwa aż do końca, ten będzie wybawion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '13', '13', 'Gdy tedy ujźrzycie obrzydłe spusztoszenie, które opowiedziane jest od Daniela proroka, będące tam gdzie nie trzeba, (kto czyta niechżeć obacza) tedy ci co w Żydowstwie będą, niech uciekają na góry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '14', '14', 'Ten zasię, który będzie na domu, niech nie schodzi do domu, ani wchodzi aby miał co wziąć z domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '15', '15', 'Który też będzie na polu, niech się nie wraca na zad, aby wziąć płaszcz swój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '16', '16', 'Biada tedy niewiastam brzemiennym i dziatkam zsącym na ten czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '17', '17', 'Módlcież się aby wasze uciekanie nie było zimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '18', '18', 'Abowiem ty tam dni będą takowem uciśnieniem, jakie nie było od początku rzeczy stworzonych, które sprawił Bóg aż do tego czasu, ani będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '19', '19', 'A gdyby nie ukrócił Pan dni onych, żadnegoby ciało nie było wybawione. Ale dla przebranych, które obrał, ukrócił dni onych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '20', '20', 'A jeśliby wam kto rzekł: Oto tuć Krystus, abo oto tam, nie wierzcież.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '21', '21', 'Boć nastaną fałeszni Krystusowie i fałeszni Prorocy i będą wydawać znaki i cuda ku zwiedzeniu, by mogło być i wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '22', '22', 'Wy się tedy strzeżcie, otomci wam wszystko opowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '23', '23', 'Nadto w onych czasiech po uciśnieniu tym, zaćmi się słońce, a miesiąc nie poda jasności swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '24', '24', 'I będą spadać gwiazdy niebieskie, a mocy które są na niebie chwiać się będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '25', '25', 'A tedyć ujźrzą Syna człowieczego przychodzącego na obłocech z wielką mocą i chwałą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '26', '26', 'Tedyć też on poszle Anioły swe, a zbierze wszytki swe przebrane od czterech wiatrów, od kończyn ziemie aż do kończyn nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '27', '27', 'Ale się nauczcie przypowieści od figowego drzewa, gdy już gałązka jej będzie młoda, a wypuszcza listki, znacie iż lato jest blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '28', '28', 'Także i wy gdy obaczycie iż się to dziać będzie, znajcież żeć już blisko, a we drzwiach jest Królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '29', '29', 'Zaprawdęć wam powiedam: Żadnym sposobem nie przeminie ten wiek, aż gdyby wszytki ty rzeczy były uczynione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '30', '30', 'Nieboć i ziemia przeminą, lecz słowa moje żadnym sposobem nie przeminą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '31', '31', 'Aleć o tym dniu i godzinie żaden nie wie ani też i Anieli, którzy są w niebie, ani sam Syn, tylkoż sam Ociec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '32', '32', 'Strzeżcie się, czujcie a módlcie się bo nie wiecie kiedyć czas będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '33', '33', 'Jako ten, który idzie precz zostawiwszy dom swój, a dawszy moc służebnikom swym, każdego nad swą robotą, a wrotnemu roskazawszy aby czuł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '34', '34', 'Czujcież tedy, (bo nie wiecie gdy Pan domu onego przyjdzie, jeśliż z wieczora, jeśli w północy, jeśli gdy kurzy zapoją, jeśli z poranku)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '35', '35', 'Aby gdy z prędka przyjdzie nie znalazł was śpiących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '12', '36', '36', 'A tak to co wam mówię, wszytkimci mówę: Czujcież!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '0', '0', 'A była wielka noc i święto Przaśników po dwu dni i szukali książęta kapłańskie i doktorowie, jakoby go zdradą pojmawszy zabili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '1', '1', 'Wszakoż mówili: Nie w święto, aby nie był rozruch między ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '2', '2', 'Gdy tedy był w Betanijej w domu Symona trędowatego, gdy siedział, przyszła niewiasta mając słojek alabastrowy olejku spikanardowego, barzo drogiego, a stłukszy alabastr, wylała ji na głowę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '3', '3', 'I byli niektórzy co się gniewali sami w sobie, a mówili: Naczże się stała utrata olejku tego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '4', '4', 'Abowiem to możono przedać więcej niż za trzysta pieniędzy i rozdać ubogim i zgrzytali na nię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '5', '5', 'Tedy Jezus rzekł: Dajcie jej pokój i przeczże ją frasujecie? Dobryć uczynek przeciwko mnie uczyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '6', '6', 'Bo zawżdy ubogie będziecie mieć zsobą i kiedykolwiek zachcecie, możecie im dobrze czynić, lecz mnie nie zawżdy mieć będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '7', '7', 'Co mogła to uczyniła, abowiem w czas uprzedziła pomazać ciało me ku pogrzebowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '8', '8', 'Zaprawdę powiedam wam: Kędyżkolwiek opowiedana będzie Ewanjelija ta po wszytkim świecie, też co ta uczyniła powiedzą na pamiątkę jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '9', '9', 'Tedy Judasz Iszkarjot, jeden ze dwunaście, przyszedł do książąt kapłańskich aby go im wydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '10', '10', 'To gdy oni usłyszeli, uradowali się i obiecali mu dać pieniądze, a przetoż czyhał na czas jakoby go był wydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '11', '11', 'Pierwszego tedy dnia Przaśników, gdy obchodzili wielką noc, rzekli mu zwolennicy jego: Gdzież chceż abychmyć szedszy nagotowali używać wielkiej nocy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '12', '12', 'A tak posłał dwu z swych zwolenników i rzekł im: Idźcie do miasta, a podka się z wami niektóry niosąc dzban wody. Idźcież za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '13', '13', 'I gdziekolwiek wnidzie, powiedzcież gospodarzowi: Mistrz mówi: Gdzież jest gospoda kędy będę pożywał wielkiej nocy z zwolenniki mojemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '14', '14', 'A onci wam ukaże salę wielką, obitą i przygotowaną, tamże nagotujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '15', '15', 'Odszedszy tedy zwolennicy, przyszli do miasta i naleźli wszytko jako im powiedział i nagotowali wielką noc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '16', '16', 'A gdy k wieczoru było, przyszedł ze dwiemanaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '17', '17', 'Potym gdy siedzieli i jedli rzekł Jezus: Zaprawdę wam powiedam, jeden z was zdradzi mię, który je ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '18', '18', 'Tedy oni poczęli być smętni i mówili mu z osobna: Azali ja? A drudzy: Aza ja?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '19', '19', 'Lecz on im odpowiedziawszy rzekł: Jeden ze dwunaście, który ze mną macza w misie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '20', '20', 'Aczci Syn człowieczy idzie jako o nim jest napisano. Ale biada człowiekowi onemu, przez którego Syn człowieczy będzie wydan. Dobrzesz było by się był człowiek on nie narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '21', '21', 'A gdy jeszcze jedli, wziął Jezus chleb, a błogosławiąc łamał i dał im mówiąc: Bierzcie jedzcie, to jest ciało moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '22', '22', 'I wziąwszy kubek a uczyniwszy dzięki, dał im i pili z niego wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '23', '23', 'Zatymże im rzekł: To jest krew moja nowego testamentu, która za wiele ich bywa wylana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '24', '24', 'Zaprawdę powiedam wam: Nie będę dalej pił z owocu winnego, aż do onego dnia, gdy je nowe pić będę w Królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '25', '25', 'A odśpiewawszy pieśń, weszli na górę oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '26', '26', 'Tedy do nich rzekł Jezus: Wszyscy się zgorszycie we mnie tej nocy. Bo jest napisano: Uderzę pastyrza, a będą rosproszone owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '27', '27', 'Lecz gdy powstanę, uprzedzę was do Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '28', '28', 'Ale mu Piotr powiedział: Chociażby się też wszyscy zgorszyli, aleć ja nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '29', '29', 'Rzekł mu tedy Jezus: Zaprwadę powiedam, iż dziś tej nocy pierwej niż dwa kroć kur zapoje, trzy kroć się mnie zaprzesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '30', '30', 'Ale on tym więcej mówił: By potrzeba i umrzeć mi z tobą, nie zaprzę się ciebie, a także też i wszyscy mówili .');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '31', '31', 'A tak przyszli na miejsce które zwano Getsemane. Tedy rzek&nbsp;ł swym zwolennikom: Siedźcie tu póki się ja modlić będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '32', '32', 'I wziąwszy Piotra i Jakuba i Jana z sobą, począł się lękać i cięszko smucić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '33', '33', 'Mówiąc ku nim: Barzo smutna jest dusza ma aż do śmierci, zostańcie tu a czujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '34', '34', 'Odszedszy tedy maluczko, padł na ziemię i modlił się, jeśliby być mogło aby od niego odeszła godzina ona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '35', '35', 'I mówił: Abba Ojcze, wszytkoć tobie jest podobne, przenieś odemnie ten kubek; wszakże nie to co ja chcę, ale co ty chcesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '36', '36', 'Zatem przyszedł a znalazł je śpiące i rzekł Piotrowi: Symon śpisz? Nie mógłżeś ze mną czuć jednę godzinę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '37', '37', 'Czujcież a módlcie się, byście nie przyszli w pokuszenie. Aczci duch jest prędki, ale ciało mdłe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '38', '38', 'I wróciwszy się zasię, modlił się mówiąc onyż słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '39', '39', 'A wróciwszy się, nalazł je zasię śpiące, bo oczy ich były ociążone, ani wiedzieli coby mu odpowiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '40', '40', 'I przyszedł po trzecie, a rzekł im: śpicież dalej i odpoczywajcie. Dosyć ci. Przyszłać godzina, oto będzie wydan Syn człowieczy w ręce grzesznych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '41', '41', 'Wstancież a pódźmy. Oto który mię wydawa blisko jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '42', '42', 'I wnet pokąd to jeszcze mówił przyszedł Judasz, który był jeden ze dwunaście, a z nim wielki poczet, z mieczmi i z kijmi od książąt kapłańskich, od doktorów i od starszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '43', '43', 'A ten co go wydał, dał im był znak społeczny mówiąc: Któregoć jakolwiek pocałuję, tenci jest. Imajcież go a wiedźcie bespiecznie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '44', '44', 'A tak przyszedszy natychmiast się przystąpił kniemu i rzekł mu: Mistrzu, mistrzu i pocałował go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '45', '45', 'Tedy się nań rękami swemi rzucili i pojmali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '46', '46', 'A niektóry z tych co stali dobywszy miecza, uderzył sługę książęcia kapłańskiego i uciął mu ucho.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '47', '47', 'Wszakoż Jezus odpowiedziawszy rzekł im: Jakoby na zbójcę wyśliście z mieczmi i z kijmi pojmać mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '48', '48', 'Na każdymci dzień był u was w kościele ucząc, a wżdyście mię nie pojmali, lecz się to dzieje, aby się wypełniły pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '49', '49', 'A tak zostawiwszy go wszyscy uciekli');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '50', '50', 'Ale niektóry młodzieniaszek szedł za nim obwinąwszy się prześcieradłem na nagie ciało i pojmali go młodzieńcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '51', '51', 'Ale on porzuciwszy prześcieradło nago im uciekł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '52', '52', 'Tedy wiedli Jezusa do nawyższego kapłana, a zeszły się z nim wszytki książęta kapłańskie i starszy i doktorowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '53', '53', 'A Piotr za nim szedł z daleka, aż w dwór książęcia kapłańskiego i siedział z służebniki grzejąc się u ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '54', '54', 'I szukali książęta kapłańskie i wszystko zgromadzenie przeciw Jezusowi świadectwa, aby go na śmierć zdali, ale nie naleźli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '55', '55', 'Abowiem wiele ich przeciw jemu mówili fałszywe świadectwa, ale świadectwa ich zgodne nie były.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '56', '56', 'Tedy niektórzy powstawszy, fałszywe świadectwo przeciw jemu wznosili mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '57', '57', 'My słyszelichmy gdy mówił: Ja rozwalę kościół ten, który jest rękami zbudowany, a w trzech dnioch iny, nie ręką uczyniony, zbuduję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '58', '58', 'Lecz ani i tak świadectwo ich zgodne było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '59', '59', 'I powstawszy w pośrzodek nawyższy kapłan pytał Jezusa mówiąc: Nicże nie odpowiedasz? Coż ci przeciw tobie świadczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '60', '60', 'Ale on milczał, a nic nie odpowiedział. Potym go pytał nawyższy kapłan i rzekł mu: I tyżeś jest on Krystus Syn Błogosławionego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '61', '61', 'A Jezus rzekł: Jam jest i ujźrzycie Syna człowieczego siedzącego na prawicy mocy Bożej i przychodzącego z obłoki niebieskimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '62', '62', 'Rozdarszy tedy kapłan nawyższy swe szaty rzekł: Cóż nam jeszcze potrzeba świadectwa?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '63', '63', 'Słyszeliście bluźnierstwo. Cóż się wam zda? A oni wszyscy skazali przeciw jemu, &nbsp;że jest winien śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '64', '64', 'I poczęli nań niektórzy plwać i zakrywać oblicze je, a dawając mu policzki, mówili mu: Gadajże! Służebnicy też bili go kijmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '65', '65', 'A gdy Piotr był w dworze na dole, przyszła jedna z służebnic nawyższego kapłana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '66', '66', 'I ujźrzawszy Piotra, a on się grzeje, patrząc nań mówiła: I tyś był z Jezusem Nazareńskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '67', '67', 'Ale on zarzekł, mówiąc: Nie znam go, ani wiem co ty mówisz. I wyszedł precz do sieni, a kur zapiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '68', '68', 'Tedy służebnica, która go zasię widziała, poczęła mówić tym, którzy stali: I tenci jest z onych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '69', '69', 'A on zasię zaprzał. I potym zaś maluczko, którzy tam stali, rzekli Piotrowi: Wieręś i ty z onych, boś i Galilejczyk i mowa twa jest podobna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '70', '70', 'Tedy się począł kląć i przysięgać mówiąc: Nie znamci człowieka tego, o którym powiedacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '13', '71', '71', 'Tedy powtóre kur zapiał i wspomniał Piotr słowa, które mu mówił Jezus, że pierwej niż kur dwa kroć zapoje, trzykroć się mnie zaprzesz, a zamyśliwszy się, płakał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '0', '0', 'Tedy wnet raniuczko zszedszy się do rady nawyszy kapłani z starszymi i z doktory i ze wszytkim zgromadzeniem, przywiedli Jezusa związanego i podali go Piłatowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '1', '1', 'I pytał go Piłat: Tyżeś jest on Król Żydowski? A on mu odpowiedziawszy rzekł: Ty powiedasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '2', '2', 'Skarżyli nań tedy wielce książęta kapłańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '3', '3', 'Ale go Piłat powtóre spytał mówiąc: Nicże nie odpowiedasz? Oto jako wiele świadczą przeciwko tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '4', '4', 'A Jezus przedsię nic nie odpowiedział, tak iż się Piłat dziwował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '5', '5', 'Ale w święto wypuszczał im więźnia jednego, któregoby żądali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '6', '6', 'I był niektóry co go nazywano Barabasz w więzieniu, z tymi co z nim rostyrk uczynili, a w tymże rostyrku mężobójstwa się byli dopuścili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '7', '7', 'Tedy pospólstwo zawoławszy poczęli prosić o to jako im zawżdy czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '8', '8', 'I odpowiedziawszy Piłat rzekł im: Chcecież puszczę wam Króla onego Żydowskiego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '9', '9', 'Abowiem wiedział, iż przez zazdrość wydały go książęta kapłańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '10', '10', 'Lecz książęta kapłańskie poduszczali pospólstwo, iżby im radszej Barabasza puścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '11', '11', 'A odpowiedziawszy Piłat rzekł im powtóre: Cóż wżdy chcecie abych uczynił temu, którego powiedacie królem Żydowskim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '12', '12', 'Tedy oni zasię wołali: Ukrzyżuj go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '13', '13', 'I mówił do nich Piłat: I cóż wżdy złego uczynił? Ale oni tym więcej wołali: Ukrzyżujże go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '14', '14', 'A tak Piłat chcąc pospólstwu dosyć uczynić, puścił im Barabasza, a Jezusa ubiczowanego podał, aby był ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '15', '15', 'I wwiódszy go żołnierze do dwora, to jest do ratusza, przyzwali wszytkiej roty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '16', '16', 'A oblókszy go w szkarłat, włożyli nań koronę uplecioną z ciernia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '17', '17', 'I poczęli go witać mówiąc: Witajże Królu Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '18', '18', 'A tłukli głowę jego trzciną i plwali nań, a nachylając kolan, kłaniali się jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '19', '19', 'A gdy go nagrawali, zwlekli go z szarłatu i oblekli w szaty własne, a wywiedli ji aby go ukrzyżowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '20', '20', 'Tedy przymusili niektórego mimo idącego Symona Cyrenejczyka (który szedł z pola; ojca Aleksandrowego i Rufiego), aby wziął krzyż jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '21', '21', 'I przywiedli go na miejsce Golgota, co się wykłada: Miejsce trupich głów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '22', '22', 'I podali mu ku piciu wino z mirrą. Ale go on nie przyjął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '23', '23', 'A gdy go ukrzyżowali, podzielili szaty jego, miecąc o nie los, co kto miał wziąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '24', '24', 'A była trzecia godzina gdy go ukrzyżowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '25', '25', 'Był też i napis występku jego napisany: Tenci jest Król on Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '26', '26', 'Ukrzyżowali też przy nim dwa zbójce, jednego po prawicy a drugiego po lewej stronie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '27', '27', 'Wypełniło się tedy Pismo, które mówi: A z złośniki jest policzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '28', '28', 'Ci też, którzy tam mimo chodzili, sromocili go, chwiejąc głowami swemi, a mówiąc: A tyżeś to co rozwalasz kościół i we trzech dnioch ji zbudujesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '29', '29', 'Wybawże się sam, a zstąp z krzyża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '30', '30', 'Także też i książęta kapłańskie nagrawając go, mówili jedni do drugich z doktory: Ine zachował, a samego siebie zachować nie może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '31', '31', 'Niechże teraz Krystus on Król izraelski zstąpi z krzyża, abyśmy ujźrzeli i uwierzyli: Ci też co z nim byli ukrzyżowani, sromocili go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '32', '32', 'A gdy przychodziła godzina szósta, stały się ciemności po wszytkiej ziemi aż do dziewiątej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '33', '33', 'Tedy dziewiątej godziny krzyczał Jezus głosem wielkim, mówiąc: Eloi, Eloi, lamma sabachtani? Co się wykłada: Boże mój, Boże mój, czemuś mię opuścił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '34', '34', 'A niektórzy z tych co tam stali, mówili: Otoć woła Eljasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '35', '35', 'Zatym przybierzawszy niektóry napełnił gębkę octu i włożywszy na trzcinę dał mu pić mówiąc: Niechajcież ujźrzemy jeśli przyjdzie Eljasz a zdejmie go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '36', '36', 'Tedy Jezus wołając głosem wielkim, wypuścił ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '37', '37', 'I rospadła się opona kościelna od wierzchu aż do dołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '38', '38', 'Co gdy widział setnik, który stał przeciwko jemu, iż tak z wołaniem wypuszczał ducha rzekł: Zaprawdę człowiek ten był Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '39', '39', 'Były też i niewiasty patrzając z daleka, między którymi była Marja Magdalena i Marja Jakuba mniejszego i Jozego matka i Salome.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '40', '40', 'Które też gdy był w Galilejej, przyszły były za nim, a służyły mu i wiele inych, które były z nim, zstąpili do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '41', '41', 'A gdy już był wieczór, (abowiem był dzień przygotowania, to jest, dzień przed Szabatem)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '42', '42', 'Przyszedszy Józef, który był z Arymatjej, uczciwy senator, który też oczekiwał królestwa Bożego, śmiele szedł do Piłata i prosił o ciało Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '43', '43', 'Tedy się Piłat dziwował jeśliby już umarł, a wezwawszy setnika, pytał go dawnoli umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '44', '44', 'I dowiedziawszy się od setnika, darował ciało Józefowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '45', '45', 'Ten kupiwszy prześcieradło i zjąwszy go, uwinął w prześcieradło i położył w grobie, który był wykopan w opoce i zatoczył kamień do drzwi grobowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '14', '46', '46', 'Tedy Marja Magdalena i Marja matka Jozego, patrzyły kędy go położono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '0', '0', 'A gdy minął szabat, Marja Magdalena i Marja Jakubowa matka i Salome, nakupiły maści, aby przyszedszy namazały go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '1', '1', 'A tak pierwszego dnia po święcie przyszły do grobu gdy wzeszło słońce .');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '2', '2', 'I mówiły między sobą: Któż odwali nam kamień od drzwi grobowych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '3', '3', '(A gdy pojźrzały, obaczyły kamień odwalony) abowiem był barzo wielki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '4', '4', 'Wszedszy tedy w grób, ujźrzały młodzieniaszka siedzącego po prawej stronie, ubranego w szaty białe i lękły się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '5', '5', 'Ale on rzekł do nich: Nie lękajcie się. Jezusa onego szukacie Nazarańskiego, który był ukrzyżowan. Wzbudzonci jest. Nie masz go tu. Oto miejsce gdzie go było położono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '6', '6', 'A tak idźcie a powiedzcie to zwolennikom jego i Piotrowi, iż was uprzedzi do Galilejej. Tam go oglądacie, jako wam powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '7', '7', 'Tedy wnet odszedszy, uciekły od grobu, abowiem ze drżeniem zdumiały się, a nikomu nic nie powiedały, bo się bały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '8', '8', 'A tak gdy wstał Jezus raniuczko pierwszego dnia po świecie, ukazał się Marjej Magdalenie, z której był wyrzucił siedmioro diabelstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '9', '9', 'A ona szedszy, opowiedziała tym, którzy przy nim bywali, a oni narzekali i płakali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '10', '10', 'A gdy oni usłyszeli iż jest żyw, a iż jest widzian od niej, nie uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '11', '11', 'Potym też dwiema z nich przechadzającym się ukazał się w inym kształcie, gdy szli do niektórego miasteczka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '12', '12', 'A oni też szedszy opowiedzieli drugim, którzy też im nie uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '13', '13', 'Na ostatek onym jedennaście społu siedzącym ukazał się i wyrzucał im na oczy niedowiarstwo ich i zatwardzenie serca, iż tym, którzy go widzieli wzbudzonego, nie wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '14', '14', 'I rzekł im: Wyszedszy na wszytek świat opowiedajcie Ewanjeliję wszemu stworzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '15', '15', 'Ktoć uwierzy a okrzści się, zbawion będzie, ale kto nie uwierzy będzie potępion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '16', '16', 'A tyć znamiona za tymi pójdą, którzy uwierzą: przez imię moje diabelstwa wyrzucać będą, nowemi języki mówić będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '17', '17', 'Węże będą brać i choćby co śmiertelnego pili, żadnym sposobem im nie zaszkodzi, a ręce na niemocnych położą i będą zdrowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '18', '18', 'A tak Pan, gdy do nich mówił, wzięty jest wzgórę do nieba i siadł na prawicy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'mk', '15', '19', '19', 'Oni też wyszedszy przepowiedali wszędy gdzie Pan społu z nimi sprawował i utwierdził słowo przez znaki, które się działy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '0', '0', 'Gdyż wiele ich podjęli się wypisać sprawę tych rzeczy, które między nami były dostatecznie upewnione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '1', '1', 'Tak jako nam podali ci którzy od początku na nie patrzyli i byli sługami słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '2', '2', 'Zdało się też i mnie wszytkiego dostatecznie z początku doszedszy, porządnie o tym do ciebie pisać, o nazacniejszy Teofile.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '3', '3', 'Abyś poznał prawdę tych rzeczy, których cię z przodku uczono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '4', '4', 'Był za czasów Heroda, onego króla judskiego, kapłan niektóry imieniem Zacharjasz z rzędu Abijego, a żona jego była z córek Aaronowych, której imię było Elżbieta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '5', '5', 'A byli oboje sprawiedliwemi przed Bogiem, chodząc we wszytkim roskazaniu i ustawach pańskich oprócz naganienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '6', '6', 'A nie mieli potomstwa przeto, iż Elżbieta była niepłodna, a już byli oboje podeszłymi w leciech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '7', '7', 'Stało się tedy, gdy sprawował urząd kapłański w rzędzie pokolenia swego przed Bogiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '8', '8', 'Według zwyczaju urzędu kapłańskiego, przypadł nań los, iż miał sprawować kadzenie wszedszy do kościoła Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '9', '9', 'A wszytko zgromadzenie ludu było na dworze modląc się czasu kadzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '10', '10', 'I ukazał się mu Anioł Pański stojąc po prawej stronie ołtarza kadzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '11', '11', 'Zatrwożył się tedy Zacharjasz ujźrzawszy go i przypadła nań bojaźń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '12', '12', 'I rzekł do niego Anioł: Nie bój się Zacharjaszu, boć jest wysłuchana modlitwa twoja, a żona twa Elżbieta porodzi tobie syna i wzowiesz imię jego Jan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '13', '13', 'A będzie tobie ku weselu i ku radości, a rozradują się ich wiele z narodzenia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '14', '14', 'Abowiem będzie wielkim przed Panem, a nie będzie pił wina ani sekar i będzie napełnion Duchem świętym jeszcze z żywota matki swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '15', '15', 'A nawróci wiele synów izraelskich ku Panu Bogu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '16', '16', 'Bo on pójdzie wprzód przed oblicznością jego z duchem i z mocą Eljaszową, aby obrócił serca ojców ku synom, a uporne ku rostropności sprawiedliwych, iżby zgotował Panu lud przyprawny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '17', '17', 'Tedy rzekł Zacharjasz do Anioła: Poczymże wżdy to poznam? Bom ja stary, a żona moja podeszła w leciech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '18', '18', 'A odpowiedziawszy Anioł rzekł mu: Jaciem jest Gabrijel, który stoi przed Panem, a posłan jestem abych mówił do ciebie, a iżbychci ty radości opowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '19', '19', 'Otóż zamilkniesz ani będziesz mógł mówić, aż do onego dnia gdy się to stanie, dlatego żeś nie uwierzył słowom moim, które się wypełnią czasu swojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '20', '20', 'I był lud czekając Zacharjasza, a dziwowali się że się tak omieszkawał w kościele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '21', '21', 'Potym gdy wyszedł, nie mógł do nich przemówić i poznali, że widzenie widział w kościele, bo im przez znaki ukazował i został niemym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '22', '22', 'I stało się gdy się wypełniły dni urzędu jego, wrócił się ku domowi swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '23', '23', 'Zatymże po onych dniach poczęła Elżbieta żona jego i kryła się przez pięć miesięcy, mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '24', '24', 'Takcimi Pan uczynił w tych czasiech gdy na mię wejźrzał, aby odjął moje pohańbienie między ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '25', '25', 'Potym miesiąca szóstego posłan jest Anioł Gabrijel od Boga, do miasta Galilejskiego które zwano Nazaret,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '26', '26', 'Do panienki za mąż zmówionej, któremu imię było Józef z domu Dawidowego, a imię panienki Marja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '27', '27', 'Wszedszy tedy Anioł do niej rzekł: Bądź pozdrowiona z łaski zmiłowana. Pan z tobą jest. Błogosławionaś ty między niewiastami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '28', '28', 'Ale ona ujźrzawszy go, zatrwożyła się na słowa jego, myśląc jakieby to było pozdrowienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '29', '29', 'Tedy jej rzekł Anioł: Nie bój się Marja, gdyżeś jest w łasce u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '30', '30', 'Bo oto poczniesz w żywocie i porodzisz syna, a nazowiesz imię jego Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '31', '31', 'Tenci będzie wielki, a będzie zwan syn Nawyższego i da mu Pan Bóg stolicę Dawida ojca jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '32', '32', 'Będzie też królował nad domem Jakubowym na wieki, a królestwu jego nie będzie końca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '33', '33', 'Zatym Marja rzekła do Anioła: I jakoż to ma być, gdyż ja męża nie mam uznać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '34', '34', 'Ale Anioł odpowiedziawszy rzekł jej: Duch święty przyjdzie w cię, a moc Nawyższego zaćmi cię, a dlategoż też to co się z ciebie narodzi święte. Będzie nazwano Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '35', '35', 'A otoć Elżbieta krewna twa i sama poczęła syna w starości swej, a już ten jest miesiąc szósty, onej którą nazywano niepłodną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '36', '36', 'Bo u Boga nie będzie żadne słowo niepodobne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '37', '37', 'A tak rzekła Marja: Otoż służebnica Pańska. Niech się ze mną dzieje wedle słowa twego. I odszedł od niej Anioł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '38', '38', 'Zatym wstawszy Marja w owych dnioch, poszła w górną krainę z kwapieniem do miasta judskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '39', '39', 'A wszedszy w dom Zacharjaszowy przywitała Elżbietę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '40', '40', 'Tam gdy usłyszała Elżbieta witanie Marjej, skoczyło dzieciątko w żywocie jej i napełniona jest Ducha świętego Elżbieta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '41', '41', 'A zawołała głosem wielkim i rzekła: Błogosławionaś ty między niewiastami, iż błogosławiony owoc żywota twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '42', '42', 'Ale skądże mnie to, iż przyszła matka Pana mego do mnie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '43', '43', 'Abowiem oto jako doszedł głos witania twego do uszu moich, skoczyło z weselem niemowniątko w żywocie moim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '44', '44', 'A błogosławiona, która uwierzyła, gdyż się wykonają ty rzeczy, które jej powiedziano od Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '45', '45', 'Tedy rzekła Marja: Niechżeć wielbi dusza moja Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '46', '46', 'A raduje się duch mój w Bogu, Zbawicielu moim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '47', '47', 'Gdyż wejźrzał na pokorną służebnicę swą, bo oto od tego czasu błogosławioną mię powiedać będą wszytki wieki');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '48', '48', 'Abowiem zacnie zemną poczynał on możny, którego jest święte imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '49', '49', 'A którego miłosierdzie na wszytki wieki jest gotowe tym, co się go boją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '50', '50', 'Mocnyć uczynek sprawił ramieniem swem, rosproszył pyszne myślą serca ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '51', '51', 'Stargnął mocarze z stolic, a podniósł pokorne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '52', '52', 'Zgłodzone napełnił dobrami, a bogacze odprawił próżne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '53', '53', 'Podparł Izraela sługę swojego, aby pamiętał na miłosierdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '54', '54', 'Jako mówił do ojców naszych, to jest do Abrahama i jego potomków na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '55', '55', 'A tak z nią mieszkała Marja, jakoby przez trzy miesiące, a potym się wróciła do domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '56', '56', 'Elżbiecie też wypełnił się czas porodzenia i urodziła syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '57', '57', 'Co usłyszawszy sąsiedzi i krewni jej, iż zacnie Pan użył miłosierdzia przeciw jej, radowali się z nią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '58', '58', 'A gdy było dnia ósmego, zeszli się aby obrzezali dzieciątko i nazywali go imieniem ojca jego, Zacharjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '59', '59', 'Ale odpowiedziawszy matka jego rzekła: Nie tak, aleć będzie wezwan Janem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '60', '60', 'Tedy do niej rzekli: Żadnego nie masz w narodzie twym coby go nazywano tym imieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '61', '61', 'I ukazowali przez znaki ojcu jego jakoby go chciał przezwać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '62', '62', 'A on kazawszy sobie podać tablicę, napisał mówiąc: Jan jest imię jego. I dziwowali się wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '63', '63', 'Zatym się wnet otworzyły usta jego i język jego i mówił błogosławiąc Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '64', '64', 'A tak przyszedł strach na wszytki sąsiady ich, a po wszytkiej górnej krainie judskiej rozsławione są wszytki słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '65', '65', 'Tedy wszyscy którzy usłyszeli, zachowywali je w sercu swojem mówiąc: I cóż to za dzieciątko będzie? A tak ręka Pańska była z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '66', '66', 'Zacharjasz tedy ociec jego napełnion jest Ducha świętego i prorokował mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '67', '67', 'Błogosławiony Pan Bóg izraelski, co nawiedził i odkupił lud swój');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '68', '68', 'I podniósł róg zbawienia nam w domu Dawida służebnika swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '69', '69', 'Jako powiedał przez usta świętych, którzy od wieku byli proroków swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '70', '70', 'Iż nas miał wybawić od nieprzyjaciół naszych i z ręki wszytkich, którzy nas nienawidzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '71', '71', 'Aby użył miłosierdzia nad ojcy naszymi, a rospomniał na testament swój święty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '72', '72', 'I na przysięgę, którą przysiągł Abrahamowi ojcu naszemu, że nam to dać miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '73', '73', 'Iż prócz strachu będąc z ręki nieprzyjacielskiej wybawieni, służyciechmy mu mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '74', '74', 'W świętobliwości i w sprawiedliwości przed nim po wszytki dni żywota naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '75', '75', 'A ty też dzieciątko Prorokiem Nawyższego będziesz nazwane, bo pójdziesz w przód przed oblicznością Pańską, abyś gotował drogi jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '76', '76', 'A iżbyś podał wiadomość zbawienia ludowi jego, przez odpuszczenie grzechów ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '77', '77', 'Dla wnętrzności miłosierdzia Boga naszego, którymi nawiedził nas latorośl z wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '78', '78', 'Aby się ukazała tym którzy w ciemnościach i w cieniu śmierci położeni są, ku wyprostowaniu nóg naszych na drogę pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '0', '79', '79', 'A tak dzieciątko ono podrastało i było umocnione Duchem i było na pustyniach, aż do onego dnia kiedy się ukazało przed Izraelem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '0', '0', 'I stało się za onych czasów, że wyszedł dekret od cesarza Augusta aby popisano wszytkie ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '1', '1', '(A ten pierwszy popis był za starosty syryjskiego Cyrenjusza).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '2', '2', 'A przetoż wszyscy szli ku popisowi, każdy do miasta swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '3', '3', 'Szedł też i Józef od Galilejej z miasta Nazaret, ku Juda do miasta Dawidowego, które zową Betleem, dlatego iż był z domu i z narodu Dawidowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '4', '4', 'Aby był popisan z Marją poślubioną sobie żoną, która była brzemienna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '5', '5', 'I stało się gdy tam byli wypełniły się dni, których porodzić miała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '6', '6', 'Porodziła syna swojego pierworodnego, a uwinęła go w pieluszki i położyła go w żłobie dlatego, iż im miejsca nie było w gospodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '7', '7', 'W tejże krainie byli pastyrze leżąc na polach i nocną straż trzymając nad stadem swem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '8', '8', 'A oto Anioł Pański z nagła knim przyszedł, a jasność Pańska oświeciła je zewsząd i lękli się wielką bojaźnią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '9', '9', 'Tedy im rzekł Anioł: Nie bójcież się, abowiemci oto oznajmuję wam wesele wielkie, które będzie wszytkiemu ludowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '10', '10', 'Iż się wam dziś narodził Zbawiciel, który jest Krystus Pan w mieście Dawidowem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '11', '11', 'I będziecie ten mieć znak, znajdziecie niemowiątko uwinione w pieluszki i leżące w żłobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '12', '12', 'A wnet z onym Aniołem przyszło wiele zastępów niebieskich, chwaląc Boga i mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '13', '13', 'Chwała na wysokości Bogu, a na ziemi pokój, przeciw ludziam dobrą wolą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '14', '14', 'Potym gdy  odeszli Aniołowie od nich do nieba, tedy pastyrze oni mówili jedni do drugich: Pódźmyż już aż do Betleem, a oglądajmy tę sprawę, którą nam Pan oznajmił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '15', '15', 'A tak spiesznie przyszli i naleźli Marją z Józefem i niemowiątko leżące w żłobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '16', '16', 'I gdy ujźrzeli, rozsławiali to co im było powiedziano o dzieciątku onem');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '17', '17', 'A wszyscy, którzy to słyszeli co mówili do nich pastyrze, dziwowali się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '18', '18', 'Tedy Marja to wszytko zachowywała, znaszając to do serca swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '19', '19', 'I wrócili się pastyrze wysławiając i chwaląc Boga, za to wszytko co słyszeli i widzieli jako im powiedano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '20', '20', 'A gdy przyszedł dzień ósmy, iżby obrzezano dzieciątko, tedy imię jego nazwano jest Jezus, które było mianowano od Anioła pierwej niż się w żywocie poczęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '21', '21', 'Gdy się też wypełniły dni oczyściania Marjej wedle zakonu Mojżeszowego, przywiedli go do Jeruzalem, aby go stawili Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '22', '22', '(Jako było opisano w zakonie Pańskim: Wszelki mężczyzna pierworodny świętym Pańskim będzie).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '23', '23', 'A temu, aby oddali ofiarę wedle tego co jest powiedziano w zakonie Pańskim, parę synogarlic abo dwoje gołąbiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '24', '24', 'A oto był niektóry w Jeruzalem, któremu imię było Symeon, człowiek sprawiedliwy i bogobojny, oczekawając pociechy izraelskiej, a Duch święty był nad nim,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '25', '25', 'I oznajmiono mu to było przez Ducha świętego, że nie miał oglądać śmierci, ażby pierwej oglądał Krystusa onego Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '26', '26', 'Ten z natchnienia Ducha świętego przyszedł do kościoła, a gdy  rodzicy wwiedli dzieciątko Jezusa aby zań uczynili wedle zwyczaju zakonnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '27', '27', 'Tedy on wziąwszy go na ręce swe, błogosławił Boga mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '28', '28', 'Teraz Panie odpuszczasz sługę twego w pokoju, według słowa twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '29', '29', 'Gdyż oczy moje oglądały zbawienie twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '31', '31', 'Światłość ku objawieniu poganom i chwałę ludu twego izraelskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '32', '32', 'A tak ociec i matka jego dziwowali się temu, co powiedziano o nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '33', '33', 'I błogosławił im Symeon, mówiąc do Marjej matki jego: Oto ten położon jest na upad i na powstanie wielu ich w Izraelu i na znak, któremu się sprzeciwić będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '34', '34', '(Ktemu i duszę twą przeniknie miecz), aby były objawione myśli z wiela serc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '35', '35', 'Była też i Anna prorokini, córka Fanuelowa z pokolenia Aser, ta już była barzo podeszła w leciech, a siedm lat żyła z mężem od panieństwa swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '36', '36', 'I będąc wdową około ośmidziesiąt i czterzech lat, nie odchadzała od kościoła, a poszcząc i modląc się służyła Bogu w nocy i we dnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '37', '37', 'Ta przyszedszy tegoż czasu, chwaliła też Pana i powiedała o nim wszytkim, którzy oczekawali odkupienia w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '38', '38', 'A tak oni odprawiwszy wszytko wedle zakonu Pańskiego, wrócili się ku Galilejej do Nazaret miasta swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '39', '39', 'A podrastało dzieciątko, a umocniło się w duchu, będąc pełne mądrości i łaska Boża była nad nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '40', '40', 'Tedy rodzicy jego chodzili na każdy rok do Jeruzalem na święto Wielkonocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '41', '41', 'A gdy już był we dwunaście leciech, tedy oni szli do Jeruzalem, wedle zwyczaju święta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '42', '42', 'I gdy się dni wypełniły, oni się wrócili, a zostało dzieciątko Jezus w Jeruzalem, o czym nie wiedział Józef i matka jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '43', '43', 'A mniemając żeby był w towarzystwie, szli w drogę dzień jeden, a szukali go między krewnymi i znajomemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '44', '44', 'A gdy go nie naleźli, wrócili się do Jeruzalem szukając go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '45', '45', 'Potym po trzeciem dniu naleźli go siedzącego w kościele między doktory, a on ich słucha i pyta się ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '46', '46', 'Wszyscy tedy, którzy go słuchali, zdumiewali się nad rozumem i nad odpowiedziami jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '47', '47', 'A ujźrzawszy go zadziwili się. Tedy kniemu rzekła matka jego: Synu przeczżeś nam tak uczynił? Oto ociec twój i ja frasując się szukalichmy cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '48', '48', 'Ale on rzekł do nich: Cóż jest żeście mię szukali? Zażeście nie wiedzieli, iż w tych rzeczach, które są ojca mego potrzeba mnie być?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '49', '49', 'Lecz oni nie zrozumieli słów które im mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '50', '50', 'A tak szedszy z nimi, przyszedł do Nazaret, a był ich posłuszen. Matka też jego wszystko to zachowywała w sercu swem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '1', '51', '51', 'Jezus też pomnażał się w mądrości i we wzroście i w łasce u Boga i u ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '0', '0', 'Potym piątego nastego roku za panowania Tyberjusza cesarza, za Poncjusa Piłata starosty judskiego, a za Heroda króla galilejskiego, a za Filipa brata jego króla iturejskiego, trachonitskiej ziemice i za Lizanijasza abileńskiego króla.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '1', '1', 'Gdy nawyższemi kapłany byli Annas i Kajafasz, stało się słowo Pańskie do Jana, Zacharjaszowego syna, na puszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '2', '2', 'A tak przyszedł do wszytkiej krainy leżącej nad Jordanem, opowiedając krzest uznania na odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '3', '3', 'Jako jest napisano w księgach powieści Ezajasza proroka, który tak mówi: Głos wołającego na puszczy. Gotujcież drogę Pańską, czyńcież proste ścieżki jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '4', '4', 'Każda dolina będzie napełniona, a każda góra i pagórek zniżon będzie, a miejsca krzywe staną się prostymi, a nierówne drogi równymi będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '5', '5', 'I ogląda wszelkie ciało zbawienie Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '6', '6', 'Tedy mówił zgromadzeniu ludzi przychodzących aby byli okrzczeni od niego. Narodowie żmijów i któż to wam okazał żebyście uciekali przed gniewem przyszłym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '7', '7', 'Przynoścież tedy pożytki godne ku uznaniu, a nie poczynajcie mówić sami u siebie: Ojca mamy Abrahama. Abowiem wam powiedam: Iżby też mógł Bóg z tych kamieni wzbudzić dzieci Abrahamowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '8', '8', 'Aleć już i siekiera do korzenia drzew przyłożona jest: A przetoż każde drzewo nieprzynoszące owocu dobrego bywa wycięte i w ogień wrzucone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '9', '9', 'Tedy go pytał lud, mówiąc: Cóż tedy czynić będziem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '10', '10', 'A on im odpowiedziawszy rzekł: Kto ma dwie sukni, niech podzieli z tym co nie ma, a kto ma potrawy, niech także uczyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '11', '11', 'Zatym przyszli i celnicy aby byli krzczeni i rzekli kniemu: Mistrzu cóż mamy czynić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '12', '12', 'Ale im on powiedział: Nic więcej nie wyciągajcie jedno to co wam postanowiono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '13', '13', 'Pytali go też żołnierze mówiąc: A my cóżwżdy czynić mamy? Tedy im odpowiedział: Żadnego nie tłuczcie ani potwarzajcie, ale miejcie dosyć na swej zapłacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '14', '14', 'A gdy się lud nadziewał i myślili wszyscy w sercach swych o Janie jeśliby on był Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '15', '15', 'Odpowiedział Jan mówiąc wszytkim: Chociażci ja was krzczę wodą, aleć idzie który jest możniejszy niż ja. Któremu ja nie jestem godzien abych miał rozwiązać rzemień u trzewików. Tenci was będzie krzcił Duchem świętym i ogniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '16', '16', 'Który ma łopatę w ręku swych, a wychędoży bojsko swoje i zbierze pszenicę do szpiklerza swego, a plewy spali ogniem nieugaszonym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '17', '17', 'A tak wiele inych rzeczy napominając opowiedał ludowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '18', '18', 'Tedy Herod król karan był od niego, dla Herodjady żony Filipa brata jego i dla wszytkich złości które czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '19', '19', 'Przydał też i to nad wszystko, iż wsadził Jana do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '20', '20', 'I stało się gdy był krzczon wszytek lud i Jezus był okrzczon i modlił się, tedy się niebo otworzyło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '21', '21', 'A stąpił nań Duch święty w osobie cielesnej jako gołębica i stał się głos z nieba mówiąc: Ty jesteś on namilszy Syn mój. W tobieć przestawam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '22', '22', 'Tedy Jezus poczynał być jakoby we trzechdziesiąt lat, gdy jeszcze mnimano żeby był synem Józefowym, który był syn Heli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '23', '23', 'Który był syn Matat, który był syn Lewi, który był syn Melchy, który był syn Janna, który był syn Józefa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '24', '24', 'Który był syn Matatjasza, który był syn Amosa, który był syn Nauma, który był syn Esli, który był syn Nagge.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '25', '25', 'Który był syn Maat, który był syn Matatjasza, który był syn Semej, który był syn Józefa, który był syn Juda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '26', '26', 'Który był syn Joanna, który był syn Resa, który był syn Zorobabela, który był syn Salatjela, który był syn Nery.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '27', '27', 'Który był syn Melchy, który był syn Abby, który był syn Kosam, który był syn Elmodam, który był syn Er.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '28', '28', 'Który był syn Jose, który był syn Elijezer, który był syn Jorym, który był syn Mata, który był syn Lewi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '29', '29', 'Który był syn Symeonów, który był syn Juda, który był syn Józefa, który był syn Jonama, który był syn Elijacym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '30', '30', 'Który był syn Melea, który był syn Menam, który był syn Matata, który był syn Natana, który był syn Dawida.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '31', '31', 'Który był syn Jeffe, który był syn Obed, który był syn Booz, który był syn Salmon, który był syn Naason.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '32', '32', 'Który był syn Aminabab, który był syn Aram, który był syn Ezron, który był syn Fares, który był syn Juda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '33', '33', 'Który był syn Jakub, który był syn Izaaka, który był syn Abrahama, który był syn Tare, który był syn Nachorów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '34', '34', 'Który był syn Saruch, który był syn Ragau, który był syn Faleg, który był syn Heber, który był syn Sale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '35', '35', 'Który był syn Arfaksad, który był syn Sem, który był syn Noe, który był syn Lamecha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '36', '36', 'Który był syn Matusala, który był syn Enocha, który był syn Jared, który był syn Mahalaleel, który był syn Kainan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '2', '37', '37', 'Który był syn Henos, który był syn Set, który był syn Adamów, który był syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '0', '0', 'Tedy Jezus będąc pełen Ducha świętego, wrócił się od Jordanu i przywiedzion jest od tegoż ducha na puszczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '1', '1', 'Będąc przez czterdzieści dni kuszon od diabła, ani jadł nic przez ony dni, ale gdy się ony skończyły, po tym łaknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '2', '2', 'Rzekł mu tedy diabeł: Jeśliś jest Syn Boży, rzecz kamieniowi temu, aby się stał chlebem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '3', '3', 'Ale Jezus odpowiedając mu rzekł: Napisano jest: Iż nie chlebem samym żywie człowiek, ale wszelkiem słowem Bożem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '4', '4', 'Zatym gdy go wwiódł diabeł na górę wysoką, ukazał mu wszytki królestwa ziemskie w jednejże chwilce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '5', '5', 'I rzekł mu diabeł: Te wszytki moc dam tobie i chwałę tych królestw, bo mi jest podana, a komu jedno chcę dawam ją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '6', '6', 'A tak będzieszli ty mnie chwalił, będąć twoje wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '7', '7', 'Nato Jezus odpowiedziawszy rzekł mu: Odejdź precz odemnie szatanie. Abowiemci napisano: Będziesz Pana Boga twego chwalił i jemu samemu służył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '8', '8', 'Wiódł go potym do Jeruzalem, a postawił go na szczytu kościelnym i rzekł mu: Jeśliś jest Syn Boży, rzuć się sam ztąd na dół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '9', '9', 'Abowiemci jest napisano: Że Aniołom swoim roskazał o tobie, aby cię strzegli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '10', '10', 'A iż cię na ręku podniosą, żebyś kiedy nie obraził o kamień nogi twojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '11', '11', 'Tedy Jezus odpowiedziawszy rzekł mu: Rzeczonoć jest: Nie będziesz kusił Pana Boga twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '12', '12', 'A gdy się skończyła wszytka pokusa, odszedł diabeł na czas od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '13', '13', 'A tak Jezus wrócił się z mocą Ducha do Galilejej, a przyszła o nim sława po wszytkiej okolicznej krainie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '14', '14', 'Abowiem on uczył w bożnicach ich tak, iż go wszyscy wysławiali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '15', '15', 'Potym przyszedł do Nazaret, gdzie był wychowan i wszedł według zwyczaju swego w dzień szabatny do bożnice i powstał aby czytał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '16', '16', 'Podano mu tedy księgi Ezajasza proroka, a gdy je otworzył, znalazł miejsce, w którym było napisano:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '17', '17', 'Duch Pański nademną dlatego, iż mię pomazał abych opowiedał Ewanjeliją ubogim. Posłał mię abych uzdrowił skruszone na sercu, abych opowiedział więźniom wyzwolenie, a ślepym naprawienie wzroku, a żebych złamane wypuścił na wolność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '18', '18', 'Abych opowiedział rok Pański wdzięczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '19', '19', 'Zamknąwszy potym księgi, gdy je wrócił słudze siadł, a wszytcy w bożnicy pilnie się mu przypatrowali oczyma swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '20', '20', 'I począł do nich mówić: Dziści się to Pismo wypełniło coście wy słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '21', '21', 'Wszyscy tedy dawali mu swe świadectwo, dziwując się onym słowam pełnym wdzięczności, które pochodziły z ust jego, a mówili: Izaź ten nie jest syn Józefów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '22', '22', 'Zatym rzekł do nich: Iście wy mnie rzeczecie tę przypowieść: Lekarzu uzdrów samego siebie. Wszytko cochmy słyszeli iż się stało w Kapernaum uczyńże i tu w ojczyźnie twojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '23', '23', 'Tedy rzekł: Zaprawdę wam powiedam, Żaden prorok nie jest przyjemnym w ojczyźnie swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '24', '24', 'Aleć wam w prawdzie powiedam: Byłoć wiele wdów za czasów Eljaszowych w Izraelu, gdy było zamknione niebo do trzech lat i sześci miesięcy tak, iż się był wszczął wielki głód po wszytkiej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '25', '25', 'Lecz do żadnej z nich nie był posłan Eljasz, jedno do Sarepty  miasta Sydońskiego, do niewiasty wdowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '26', '26', 'Byłoć też i wiele trędowatych w Izraelu za czasów Elizeusza proroka, a wżdy żaden z nich nie był oczyścion, jednoż Naaman Syryjczyk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '27', '27', 'Tedy wszyscy w bożnicy napełnieni są gniewu to słysząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '28', '28', 'I wstawszy wyrzucili go precz z miasta, a wywiedli go na koniec góry (na której miasto ich zbudowane było) aby go zrzucili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '29', '29', 'Ale on przeszedszy przez pośrzodek ich, odszedł precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '30', '30', 'I przyszedł do Kapernaum miasta Galilejskiego, a tam je uczył w szabaty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '31', '31', 'Zdumiewali się tedy na naukę jego, bo były poważne słowa jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '32', '32', 'I był tam w bożnicy niektóry opętany, co miał w sobie ducha nieczystego, który zawołał głosem wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '33', '33', 'Mówiąc: Ach cości do nas Jezusie Nazareński? Przyszedłeś abyś nas wytracił? Znam cię ktoś jest, On Święty Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '34', '34', 'Sfukał go tedy Jezus mówiąc: Umilknisz a wynidź od niego. Tedy diabelstwo porzuciwszy go w pośrzodku, wyszło od niego, a nic mu nie szkodziło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '35', '35', 'I przyszedł strach na wszytki, a mówili jedni do drugich: Cóż to jest za rzecz, iż wierzchnością i mocą rozkazuje duchom nieczystym i wychodzą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '36', '36', 'A tak o nim przyszła wieść na wszytki miejsca okolicznej ziemice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '37', '37', 'Potym Jezus wstawszy z bożnice wszedł w dom Symonów, a świekrę Symonowę trzymała gorączka wielka i prosili go za nią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '38', '38', 'Tedy on stojąc nad nią sfukał gorączkę i wnet jej przestała. Ona tedy natychmiast wstawszy służyła im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '39', '39', 'A gdy słońce zachodziło, wszyscy którzy mieli niemocne rozlicznemi chorobami złożone, przywodzili je do niego, a on na któregokolwiek z nich ręce włożył, uzdrawiał je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '40', '40', 'Ktemu od wiela ich wychodziły diabelstwa wołając i mówiąc: Tyś jest on Krystus Syn Boży. Lecz on na nie fukając nie dopuszczał im mówić, żeby wiedziały, iż on jest Krystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '41', '41', 'A gdy nastał dzień, szedł na miejsce puste, a zgromadzenie ludzi szukało go i przyszedszy aż do niego, zatrzymawali go aby odchodził od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '42', '42', 'Ale on rzekł do nich: Zaiste też potrzeba abych i inem miastam oznajmował królestwo Boże, gdyżem jest na to posłan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '3', '43', '43', 'A tak opowiedał w bożnicach Galilejskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '0', '0', 'Stało się potym gdy wielkość ludzi nalegała go dla słuchania słowa Bożego, tedy on stanął podle jeziora Genezaret.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '1', '1', 'I ujźrzał dwie łodzi, które były przy jezierze, a rybitwi wyszedszy z nich, płukali sieci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '2', '2', 'A tak wszedszy w jednę onych łodzi, która była Symonowa, prosił go aby maluczko odjechał od brzegu, a siadszy uczył ono zgromadzenie z łodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '3', '3', 'Potym gdy przestał powiedać, rzekł do Symona: Wyjedź na głębię a zapuśćcie sieci wasze ku łowieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '4', '4', 'Tedy Symon odpowiedziawszy, rzekł mu: Mistrzu, gdyciechmy przez wszytką noc pracowali, przedsięśmy nic nie pojmali. Wszakoż na roskazanie twe zapuszczę sieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '5', '5', 'To gdy uczynili, zagarnęli barzo wiele ryb tak, iż się rwała sieć ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '6', '6', 'I dali znać towarzyszom co byli w drugiej łodzi, aby przyszli a ratowali je, a tak przyszli a napełnili obie łodzie tak, iż się aż zanarzały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '7', '7', 'Co gdy ujźrzał Symon Piotr, upadł u kolan Jezusowi, mówiąc: Wynidź odemnie, bociem jest człowiek grzeszny Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '8', '8', 'Abowiem go był strach ogarnął i wszytki co przy nim byli, z onego łowienia ryb, które zagarnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '9', '9', 'Także Jakuba i Jana syny Zebedeuszowe, którzy byli towarzysze Symonowi. A zatym rzekł Jezus do Symona: Nie bój się, od tego czasu ludzi żywe imać będziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '10', '10', 'A oni wyciągnąwszy łodzi na brzeg, zostawiwszy wszytko szli za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '11', '11', 'Potym, gdy był w mieście niektórym, tedy oto był tam człek niektóry pełny trądu, a ujźrzawszy Jezusa padł na oblicze prosząc go a mówiąc: Panie jeśliż chceż, możesz mię oczyścić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '12', '12', 'I ściągnąwszy Jezus rękę, tknął go mówiąc: Chcę. Bądź oczyścion.  I natychmiast zszedł trąd od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '13', '13', 'Lecz mu on przykazał żeby tego nikomu nie powiedał. Ale rzekł: Idź ukaż się sam kapłanowi i uczyń ofiarę za oczyścienie twoje, jako roskazał Mojżesz na przeświadczenie im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '14', '14', 'I roznaszały się o nim tym więcej powieści, a schadzało się barzo wiele ludzi, aby słuchali a iżby uzdrowieni byli od niemocy swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '15', '15', 'A tak on wszedszy mieszkał na pustyniach i modlił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '16', '16', 'I przydało się dnia niektórego gdy on nauczał, byli też siedzący Faryzeuszowie i zakonni doktorowie, którzy się byli zeszli ze wszytkich miasteczek Galilejskich, z Juda i z Jeruzalem, a moc Pańska była z nim ku uzdrowieniu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '17', '17', 'Tedy oto niektórzy przynieśli na łóżku człowieka, który był ruszony powietrzem i szukali kędyby go wnieść a postawić przed nim, a gdy nie naleźli którąby go stroną wnieść mieli przed wielkością ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '18', '18', 'Wszedszy na dom, przez dachówki spuścili go z łóżkiem na pośrzodek przed Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '19', '19', 'Który ujźrzawszy wiarę ich rzekł do niego: Człowiecze odpuszczone są tobie grzechy twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '20', '20', 'Tedy poczęli myślić doktorowie i Faryzeuszowie mówiąc: I cóż zacz jest ten co mówi bluźnierstwa? I któż może odpuścić grzechy jedno sam Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '21', '21', 'Ale Jezus poznawszy myśli ich, odpowiedziawszy rzekł do nich: Cóż myślicie w sercach waszych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '22', '22', 'I cóż łacwiej rzec: Odpuszczone są tobie grzechy? Czyli rzec: Wstań a chodź?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '23', '23', 'Żebyście tedy wiedzieli, iż Syn człowieczy ma zwierzchność odpuszczać grzechy na ziemi, (rzekł ruszonemu powietrzem). Tobie powiedam: Wstań, a wziąwszy łóżko twoje idź do domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '24', '24', 'Tedy on wnet wstawszy przed oblicznośćią ich, wziąwszy to na czym leżał, szedł do domu swego chwaląc Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '25', '25', 'I zdumieli się wszyscy, a chwalili Boga i byli pełni bojaźni, mówiąc: Zaistechmy widzieli niedomniemane rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '26', '26', 'Potym wyszedł i ujźrzał celnika imieniem Lewi siedzącego na cle i rzekł mu: Pódź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '27', '27', 'Tedy on wszystko pozostawiwszy, wstał i szedł za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '28', '28', 'A uczynił przeń Lewi w domu swoim wielką ucztę, gdzie było wiele celników i inych, którzy z nim siedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '29', '29', 'A tak oni którzy byli z doktorów i z Faryzeuszów, szemrali przeciw zwolennikom jego mówiąc: Przeczże z celniki i z grzeszniki jadacie i pijacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '30', '30', 'Ale Jezus odpowiedziawszy, rzekł do nich: Nie potrzebać tym, którzy zdrowi są lekarza, jedno tym którzy się źle mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '31', '31', 'Nie przyszedłciem abych wzywał sprawiedliwe, ale grzeszniki ku uznaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '32', '32', 'Ale oni rzekli kniemu: Przeczże Janowi zwolennicy poszczą często? I modlitwy czynią, także i Faryzejscy? Aleć twoi jedzą i piją?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '33', '33', 'Tedy on rzekł do nich: Zali możecie uczynić żeby synowie wesela mieli pościć póki z nimi jest oblubieniec?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '34', '34', 'Lecz ci przyjdą dni, gdy oblubieniec będzie wzięt od nich, a tedyć na on czas pościć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '35', '35', 'I powiedział też im podobieństwo: Żaden płata sukna nowego nie przyszywa do szaty starej, bo inaczej ono nowe drze stare i nie zgadza się nowy płat z starym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '36', '36', 'I nie nalewa też żaden wina nowego w stare naczynia, bo inaczej wino nowe rozerwie naczynia i rozleje się, a naczynia poginą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '37', '37', 'Ale młode wino ma być lano w naczynia nowe, a tak oboje w całe bywają zachowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '4', '38', '38', 'A żadny napiwszy się wina starego, nie zarazem chce pić nowego, bo mówi: Pożyteczniejszeć jest stare.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '0', '0', 'Stało się po tym, iż on w sabat wtóry po pirwszym szedł przez zboża i uczniowie jego wyrywali kłosy i jedli je trąc je rękoma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '1', '1', 'Tedy niektórzy z Faryzeuszów mówili im: Przeczto czynicie czego się w święta czynić nie godzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '2', '2', 'Ale Jezus odpowiedziawszy rzekł do nich: Izaliście tego nie czytali co uczynił Dawid, gdy był łaknącym i z onymi, którzy z nim byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '3', '3', 'Jako wszedł do domu Bożego i wziął chleby pokładne a jadł, dawając też i tym którzy z nim byli? Których to chlebów nie godziło się jadać jedno samym kapłanom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '4', '4', 'I mówił do nich: Syn człowieczy jestci też Panem Szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '5', '5', 'I stało się w drugi szabat, iż wszedł do bożnice a uczył i był tam niektóry co miał prawą rękę uschłą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '6', '6', 'Pilnowali go tedy doktorowie i Faryzeuszowie jeśliby był w szabat uzdrawiał, aby naleźli przyczynę skargi przeciw jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '7', '7', 'Ale on wiedział myśli ich i rzekł człowiekowi, który miał rękę uschłą: Wstań a stań pośrzodku, a tak on wstawszy stanął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '8', '8', 'Zatym do nich rzekł Jezus: Spytam was o niektórą rzecz: Słuszyli w szabat czynić dobrze czyli źle? Zachowaćli człowieka czy stracić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '9', '9', 'A tak pojźrzawszy po wszytkich rzekł człowiekowi: Wyciągni rękę twoję. A on tak uczynił i wrócona mu jest ręka jego tak zdrowa jako i druga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '10', '10', 'Tedy oni będąc pełni szaleństwa, rozmawiali jedni z drugimi coby uczynić Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '11', '11', 'I stało się tegoż czasu odszedł na górę ku modleniu i nocował tam modląc się Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '12', '12', 'A gdy był dzień, przyzwał zwolenników swych, a wybrawszy z nich dwanaście, które nazwał Apostoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '13', '13', 'Symona, którego nazwał Piotrem i Andrzeja brata jego, Jakuba, Jana, Filipa i Bartłomieja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '14', '14', 'Mateusza i Tomasa, Jakuba Alfeuszowego syna i Symona, którego zową Zelotes.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '15', '15', 'Judę brata Jakubowego i Judasza Iskarjota, który też był zdrajcą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '16', '16', 'Potym zstąpił z nimi, a stanął na równinie w polu, i poczet zwolenników jego i wielkie mnóstwo ludu ze wszytkiego Judy i z Jeruzalem i z krain pomorskich i z Tyru i z Sydonu, co się schodzili, aby go słuchali i byli uzdrowionemi od niemocy swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '17', '17', 'I którzy też przykrości cierpieli od duchów nieczystych, uzdrowieni bywali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '18', '18', 'Owa się wszytka wielkość ludzi starała jakoby się dotknąć, abowiem moc wychodziła od niego i uzdrawiał wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '19', '19', 'A tak podnióższy oczu swych na zwolenniki swoje mówił: Szczęśliwi ubodzy, boć wasze jest królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '20', '20', 'Szczęśliwi, którzy teraz łakniecie, bo będziecie nasyceni; Szczęśliwi, którzy płaczecie teraz, bo się śmiać będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '21', '21', 'Szczęśliwi będziecie, gdy was nie będą nawidzieć ludzie i wyłączą was a będą was sromocić, a imię wasze wyrzucać będą jako złe, dla Syna człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '22', '22', 'Weselcie się dnia onego i radość okazujcie, boć oto nagroda wasza jest obfita w niebiosach, takci też i prorokom czynili ojcowie ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '23', '23', 'Ale biada wam bogaczom, boście już odnieśli pociechę waszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '24', '24', 'Biadaż wam, którzyście napełnieni, abowiem łaknąć będziecie; Biadaż wam, którzy się teraz śmiejecie, bo będziecie narzekać i płakać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '25', '25', 'Biadaż wam gdy ludzie wszyscy dobrorzeczyć wam będą, abowiem ci też toż czynili fałesznym prorokom ojcowie ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '26', '26', 'Aleć wam powiedam, którzy słuchacie: Miłujcież nieprzyjacioły wasze, czyńcie dobrze tym, którzy was nienawidzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '27', '27', 'Dobrorzeczcie tym co was przeklinają, a módlcie się za tymi, którzy was drażnią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '28', '28', 'Temu, któryby cię uderzył po czeluści, podaj i drugą i temu, któryćby brał płaszcz i sukniej nie zbraniaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '29', '29', 'Każdemu, któryby cię ocz prosił, daj, a temu co od ciebie rzeczy twe pobierze, nie upominaj się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '30', '30', 'I jako chcecie aby wam ludzie czynili, także też im i wy czyńcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '31', '31', 'Bo jeśliż miłujecie ty, którzy was miłują, co za łaskę mieć będziecie? Abowiemci i grzesznicy miłują ty, od których miłowani bywają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '32', '32', 'A jeśli dobrze czynicie tym, którzy wam dobrze czynią, cóż za łaskę mieć będziecie? Abowiemci i grzesznicy toż czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '33', '33', 'A jeśli pożyczacie tym, od których się nadziewacie odebrać, cóż za łaskę nadziewacie odebrać, cóż za łaskę mieć będziecie? Abowiemci i grzesznicy grzesznikom pożyczają, aby równo odebrali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '34', '34', 'Miłujcież tedy nieprzyjacioły wasze i czyńcie im dobrze i pożyczajcie nic się z tego nie nadziewając, tedyć będzie wielka nagroda wasza, a będziecie synowie onego Nawyższego. Abowiemci on jest dobrotliwy przeciw niewdzięcznikom i złośnikom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '35', '35', 'A tak bądźcie miłosierni jako i ociec wasz miłosierny jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '36', '36', 'Nie sądźcież, a nie będziecie sądzeni; nie skazujcie, a nie będziecie skazani; rozwięzujcie a będziecie rozwięzani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '37', '37', 'Dajcież, a będzie wam dano, miarę dobrą, natłoczoną, potrzęsioną i opływającą dadzą w zanadrza wasze. Abowiem tąż miarą, która mierzycie, tąż wam zasię drudzy odmierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '38', '38', 'I powiedział im podobieństwo: Izali może ślepy ślepego prowadzić? Azaź nie obadwa w dół wpadną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '39', '39', 'Nie jestci uczeń nad mistrza swego, ale któżkolwiek chce być uczniem doskonałym, ma być jako mistrz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '40', '40', 'Ale czemuż wpatrujesz paździorko w oku brata twego a tramu, który masz w oku własnym, nie baczysz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '41', '41', 'Abo jako możesz rzec bratu twemu: Bracie dajci wyrzucę paździoro, które jest w twym oku, gdyż tramu który jest w twym oku nie baczysz. Pokryty człowiecze, wyrzuć wprzód tram on z oka twego, a tedy przypatrzysz się, abyś wyrzucił paździorko, które jest w oku brata twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '42', '42', 'Abowiemci drzewo dobre nie przynasza owocu złego, ani drzewo napruchniałe owocu dobrego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '43', '43', 'Gdyż każde drzewo z własnego owocu poznawają. Boć też nie zbierają z ciernia fig, ani ze krza zbierają grona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '44', '44', 'Człowiek dobry z dobrego skarbu serca swego, podawa rzeczy dobre, a zły człowiek ze złego skarbu serca swego podawa złą rzecz, abowiem z obfitości serca mówią usta jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '45', '45', 'Przeczże mię tedy zowiecie: Panie, Panie! A tego nie czynicie co powiedam?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '46', '46', 'Któżkolwiek przychodzi do mnie, a słucha słów mych i czyni im dosyć, okaże wam komu jest podobnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '47', '47', 'Podobny jest człowiekowi dom budującemu, który wykopawszy i wybrawszy głęboko zakłada grunty na opoce, a gdy wzbiera powódź otrąca się rzeka a on dom, ale go nie może poderwać, bo jest założony na opoce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '5', '48', '48', 'Ten zasię, który słucha a nie czyni, podobien jest człowiekowi, który buduje dom swój na ziemi bez gruntu, a który gdy się rzeka otrąci, natychmiast się obalił i stał się wielki upad domu onego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '0', '0', 'Potym wszytkich słów swoich dokończywszy, gdy słuchał lud, wszedł do Kapernaum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '1', '1', 'A niektórego setnika sługa niemocą złożony miał umrzeć, którego sobie wielce ważył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '2', '2', 'Ten, gdy się osłyszał o Jezusie, posłał kniemu starsze Żydowskie, prosząc go, aby przyszedł a ratował sługę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '3', '3', 'Oni tedy przyszli po Jezusa i prosili go z pilnością mówiąc: Godna rzecz abyś mu to okazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '4', '4', 'Abowiem naród nasz miłuje i onżeć nam zbudował bożnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '5', '5', 'A tak Jezus szedł z nimi, ale gdy nie daleko był od domu, posłał do niego on setnik przyjacioły, mówiąc mu: Panie nie trudźże się, abowiemci ja nie jest godzien abyś wszedł pod dach mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '6', '6', 'A dlategoż i sam nie mniemiałem się być godnym przyjdź do ciebie. Ale rzecz słowem, a ozdrowiejeć sługa mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '7', '7', 'Bociem też i ja jest człowiek pod zwierzchnością postanowiony, mając pod sobą służebne i mówię temu: Idź, a pójdzie; a drugiemu: Przyjdź i przyjdzie; a słudze mojemu: Uczyń to i uczyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '8', '8', 'To usłyszawszy Jezus dziwował się mu i obróciwszy się do zgromadzenia ludzi, które szło za nim rzekł: Powiedam wam, żeciem ani w Izraelu tak wielkiej wiary nie znalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '9', '9', 'Tedy oni co byli posłani, wróciwszy się do domu naleźli sługę, który chorował, zdrowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '10', '10', 'Potym dnia drugiego szedł do miasta, które zową Naim. A przy nim szło wiele zwolenników jego i zebranie ludzi wielkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '11', '11', 'A gdy przychodził ku branie miejskiej, tedy oto umarłego wynoszono, który był jedyny syn u matki swej, a ktemu wdowy i była z nią wielkość ludu z miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '12', '12', 'Onę tedy ujźrzawszy Pan, ruszon jest nad nią miłosierdziem i rzekł jej: Nie płacz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '13', '13', 'I przystąpiwszy się mar (a ci co go nieśli stanęli) i rzekł: Młodzieniaszku powiedamci: Wstań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '14', '14', 'Tedy siadł on, który był umarł i począł mówić, a tak go oddał matce jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '15', '15', 'I zjął wszytkich strach, a chwalili Boga mówiąc: Zaprawdę prorok wielki powstał między nami , a zaisteć Bóg wyjźrzał na lud swój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '16', '16', 'I przyszła o nim ta powieść po wszytkim Juda i po wszytkiej ziemiej pogranicznej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '17', '17', 'Oznajmili też i Janowi zwolennicy jego o tym wszytkim, a on wezwawszy dwu niektórych z zwolenników swoich,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '18', '18', 'Posłał je do Jezusa mówiąc: Tyżeś jest ten, który przyjdź miał, czyli drugiego czekać będziemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '19', '19', 'Gdy tedy przyszli do niego mężowie oni rzekli: Jan Krzciciel posłał nas do ciebie mówiąc: I tyżeś jest ten, który miał przyjdź, czyli drugiego czekać będziemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '20', '20', 'Tedy onejże godziny wiele ich uzdrowił od niemocy, od trapienia i od duchów złych, a wiele ślepych wzrokiem darował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '21', '21', 'Odpowiedziawszy tedy Jezus rzekł do nich: Szedszy opowiedzcie Janowi coście widzieli i słyszeli, iż ślepi wzrok biorą, chromi chodzą, trędowaci są oczyścieni, głuszy słyszą, umarli powstawają, ubogim Ewanjeliją opowiedają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '22', '22', 'A szczęśliwy jest ten, którykolwiek nie będzie obrażon we mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '23', '23', 'Zatym gdy odeszli posłowie Janowi, począł o Janie mówić do zgromadzenia ludzi: Czemużeście się wyszli dziwować na puszczą? Trzcinie, która się od wiatru chwieje?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '24', '24', 'Ale cóżeście wyszli widzieć, człowieka w miękie szaty obleczonego? Oto ci, którzy są w odzieniu kosztownym i w roskoszach, sąć w pałacach królewskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '25', '25', 'Ale cóżeście wyszli widzieć, proroka? A powiedamci też wam, że więtszego niż proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '26', '26', 'Tenci jest, o którym pisano: Oto posyłam posła mego przed obliczem twoim, który zgotuje drogę twoję przed tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '27', '27', 'Abowiem powiedam wam: Między tymi, którzy się rodzą z niewiast, żaden nie jest więczszy prorok nad Jana Krzciciela. Lecz który namniejszym jest w Królestwie Bożym, ten więczszym jest niż on.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '28', '28', 'Tedy lud wszytek to słysząc i celnicy, wyznawali sprawiedliwego Boga, którzy byli pokrzczeni krztem Janowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '29', '29', 'Ale Faryzeuszowie i nauczeni w zakonie odrzucili radę Bożę sami przeciw sobie, którzy nie byli pokrzczeni od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '30', '30', 'Zatymże rzekł Pan: I komuż wżdy przyrównani ludzie narodu tego, a ku której rzeczy wżdy podobni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '31', '31', 'Podobnić są dzieciom na rynku siedzącym, które wołając jedny ku drugim mówią: Gralichmy wam na piszczałkach, a nie tańcowaliście. Narzekaliśmy wam, a nie płakaliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '32', '32', 'Abowiem przyszedł Jan Krzciciel nie jedząc chleba ani pijąc wina, a wżdy mówicie: Ma diabelstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '33', '33', 'Przyszedł Syn człowieczy jedząc i pijąc, a też mówicie: Otóż człowiek obżerca i opilca winem, przyjaciel celników i grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '34', '34', 'Aleć jest usprawiedliwiona mądrość od wszytkich synów swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '35', '35', 'Tedy go prosił niektóry z Faryzeuszów aby z nim jadł, a tak wszedszy w dom Faryzeuszów, siedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '36', '36', 'A oto niewiasta, która była w mieście grzesznicą, dowiedziawszy się, iż siedział w domu Faryzeuszowym, przyniosła słojek alabastrowy olejku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '37', '37', 'I stanąwszy u nóg jego z tyłu, płacząc poczęła łzami pokrapiać nogi jego, a włosami głowy swojej ucierać, a całując nogi jego olejkiem mazała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '38', '38', 'Co gdy ujźrzał Faryzeus on, który go był wezwał, rzekł sam w siebie mówiąc: Być ten był prorokiem, wżdyć by znał, którą i jaką to jest niewiasta co się go dotyka, bo jest grzesznicą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '39', '39', 'Ale Jezus odpowiedziawszy, rzekł do niego: Symon, mamci nieco powiedzieć. A on rzekł: Powiedz Mistrzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '40', '40', 'Dwa dłużnicy byli winni niektóremu, jeden pięć set groszy, drugi pięć dziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '41', '41', 'A gdy mu oni nie mieli czym płacić, darował ich tym obudwu. A przetoż powiedz mi: Z tych dwu, którysz go więcej miłować będzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '42', '42', 'Ale Symon odpowiedziawszy rzekł: Tak mniemam, iż ten, któremu więcej darował. A tedy on rzekł: Dobrześ rozeznał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '43', '43', 'I obróciwszy się ku niewieście, rzekł Symonowi: Widziszże tę niewiastę? Wszedłemci do domu twego, a nie dałeś wody na nogi moje. A ta oto łzami pokropiła nogi moje i włosami głowy swej utarła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '44', '44', 'Nie pocałowałeś mię, a ta oto jakożem tu przyszedł, nie przestała całować nóg moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '45', '45', 'Nie pomazałeś olejkiem głowy mojej, a ta oto olejkiem nogi moje natarła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '46', '46', 'A dlategożci powiedam, że jej odpuszczono wiele grzechów i przetoż wiele umiłowała. Ale komuć mało odpuszczają, mało miłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '47', '47', 'Rzekł zatym od niej: Odpuszczone są tobie grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '48', '48', 'I poczęli niektórzy co społu siedzieli mówić między sobą: I cóż zacz jest ten, który też i grzechy odpuszcza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '6', '49', '49', 'Ale on rzekł ku niewieście: Wiara twa zachowała cię. Idźże w pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '0', '0', 'Stało się potym, iż chodził po wszytkich mieściech i po wsiach łażąc i opowiedając Królestwo Boże, a oni dwanaście byli z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '1', '1', 'I niektóre niewiasty, które był uzdrowił od duchów złych i od chorób, jako Marja, którą zwano Magdalena, z której siedmioro diabelstwa wyszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '2', '2', 'Joanna też żona Chuzego sprawcę Herodowego, ktemu Zuzanna i inych wiele, które mu służyły z majętności swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '3', '3', 'A gdy wielkie zgromadzenie ludzi schodziło się i którzy byli we wszytkich mieściech szły do niego, rzekł przez podobieństwo:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '4', '4', 'Niektóry co siał wyszedł siać nasienia swego, a gdy siał, padało jedno podle drogi i podeptano je, a przyleciawszy ptacy powietrzni wyjedli je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '5', '5', 'Drugie zaś upadło na opokę, a wszedszy uschło, abowiem nie miało wilgotności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '6', '6', 'Drugie też padło między ciernie, a gdy wyrosło, ciernie zagłuszyło je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '7', '7', 'Drugie zasię padło na ziemię dobrą, a urozszy przyniosło owoc stokrotny. Tedy mówiąc to wołał: Kto uszy ma ku słuchaniu niech słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '8', '8', 'I pytali go zwolennicy jego mówiąc: Cóż wżdy to jest za podobieństwo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '9', '9', 'Tedy im on rzekł: Wamci dano znać tajemnice królestwa Bożego, ale inem tylko przez podobieństwa, aby widząc nie widzieli, a słysząc nie rozumieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '10', '10', 'Toć tedy jest podobieństwo, nasienie jestci słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '11', '11', 'A którzy podle drogi, cić są, którzy słuchają, potym przychodzi diabeł i wybiera słowo z serca ich, aby uwierzywszy nie byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '12', '12', 'Którzy zasię na opokę, cić są którzy gdy słuchają z weselem przyjmują słowo, aleć ci nie mają korzenia, którzy do czasu wierząc, czasu pokusy odstępują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '13', '13', 'Co zasię padło między ciernie cić są, którzy słuchali i odszedszy, od starania i od bogactw i od rozkoszy żywota są zagłuszeni i nie przynoszą owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '14', '14', 'To co też padło na ziemię dobrą cić są, którzy dobrym a uprzejmym sercem słyszane słowo trzymają, a owoc przynoszą przez cierpliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '15', '15', 'A nie jestci żaden, któryby zapaliwszy świecę przykrył ją naczyniem, abo kładł pod łoże, ale ją stawia w lichtarz, aby ci, którzy wchodzą widzieli światło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '16', '16', 'Abowiemci nie masz nic tak tajemnego co by się objawić nie miało, ani tak skrytego czegoby poznać nie miano, ażeby na jaśnią nie wyszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '17', '17', 'A przetoż patrzcie jako słuchacie. Abowiem ktokolwiek ma, będzie mu przydano, a kto nie ma, też i to co się zda jakoby miał, będzie od niego odjęto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '18', '18', 'Przyszli potym kniemu matka i bracia jego, ale go dojdź nie mogli dla zgromadzenia ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '19', '19', 'I oznajmiono mu to mówiąc: Matka twa i bracia twoi stoją na dworze chcąc się z tobą widzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '20', '20', 'Ale on odpowiedziawszy rzekł do nich: Matka moja i bracia moi onić są, którzy słowa Bożego słuchają i czynią mu dosyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '21', '21', 'I stało się potym dnia niektórego, iż wszedł w łódź i zwolennicy jego tedy rzekł do nich: Przeprawmy się do dalszego brzegu jeziora i wieźli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '22', '22', 'Tamże wioząc się usnął i przyszedł wiatr z wichrem na jezioro, aż się przepełniali a byli w niebespieczeństwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '23', '23', 'A tak przystąpiwszy obudzili go mówiąc: Mistrzu giniemy, a on się ocuciwszy zfukał wiatr i wały wód, zatymże przestały a było uciszenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '24', '24', 'Tedy im rzekł: I gdzież jest wiara wasza? Ale oni zlęknąwszy się dziwowali się mówiąc jedni do drugich: I cóż zacz jest ten, że wiatrom i wodam roskazuje a słuchają go?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '25', '25', 'Potym się wieźli do krainy Gadareńczyków, która jest na brzegu przeciw Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '26', '26', 'A gdy wyszedł na brzeg zabieżał mu z miasta onego mąż niektóry co miał diabelstwo odwiela czasów, ani się obłóczył w szatę i nie mieszkawał w domu jedno w grobiech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '27', '27', 'Ten gdy ujźrzał Jezusa krzyczał, a przypadszy do nóg jego głosem wielkim mówił: Cóż mi do ciebie Jezusie synu Boga nawyższego? Proszę cię nie męcz mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '28', '28', '(Abowiem roskazował duchowi nieczystemu, aby wyszedł od człowieka onego bo go od niemałego czasu trapił, a także związany łańcuchem i pęty był pod strażą, wszakże połamawszy zwiąski pędzon był od diabła na pustynię)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '29', '29', 'Pytał go tedy Jezus mówiąc: Które jest twe imię? A on powiedział huf, abowiem wiele diabelstwa wstąpiło było weń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '30', '30', 'I prosił go, aby im nie roskazował idź w przepaści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '31', '31', 'A było tam wielkie stado wieprzów pasących się po górze i prosili go aby im wnie dopuścił wnidź. I dopuścił im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '32', '32', 'Tedy diabelstwa wyszedszy od człowieka onego, weszły w wieprze, których stado wpadło z góry w jezioro i potonęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '33', '33', 'A gdy pasterze ujźrzeli co się stało, uciekli. A przyszedszy oznajmili to w mieście i po wsiach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '34', '34', 'A przetoż wyszli aby oglądali co się było stało, a przyszedszy do Jezusa naleźli człowieka onego, od którego wyszło diabelstwo ubranego i z dobrą pamięcią, siedzącego u nóg Jezusowych i ulękli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '35', '35', 'Powiedzieli im tedy, ci którzy widzieli jako był ku zdrowiu przywrócon ten, który był opętany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '36', '36', 'A tak go prosił wszytek lud okolicznie przyległej ziemiej Gadareńczyków, aby szedł precz od nich, abowiem byli wielkim strachem zjęci, a on też wsiadszy w łódź wrócił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '37', '37', 'I prosił go on człowiek, od którego wyszło diabelstwo, aby był przy nim, ale go Jezus odprawił mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '38', '38', 'Wróć się do domu twego, a opowiedz jako wielkie rzeczy z tobą Pan uczynił. Także odszedł opowiedając po wszytkim mieście jak wielkie rzeczy uczynił z nim Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '39', '39', 'Potym gdy się Jezus wrócił, przyjęło go zgromadzenie ludzi, abowiem go wszyscy czekali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '40', '40', 'Tedy oto przyszedł mąż niektóry imieniem Jair, a ten był książę Bożnice i przypadszy do nóg Jezusowych prosił go aby wszedł w dom jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '41', '41', 'Abowiem tylko jedyną dziewkę miał, która była jakoby we dwunaście lat, a ta już konała. Idąc tedy ściśnion był od zgromadzenia ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '42', '42', 'Tamże niewiasta, która płynienie krwie cierpiała od lat dwunaście, a nałożyła na lekarze wszytko swe pożywienie i nie mogła być od żadnego zleczona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '43', '43', 'Przystąpiwszy z tyłu tknęła się podołka szaty jego, a natychmiast się zastanowiło płynienie krwie jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '44', '44', 'Tedy rzekł Jezus: Któż jest co się mnie tknął? A gdy wszyscy przeli rzekł Piotr i ci, którzy z nim byli: Mistrzu wielkość ludzi cię ściska i trąca, a ty mówisz: Któż się mnie dotknął?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '45', '45', 'Ale Jezus rzekł: Tknął ci się mnie ktoś, bom poznał, że moc wyszła odemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '46', '46', 'A gdy ujźrzała niewiasta ona, iż się skryć nie mogła, drżąc przyszła, a upadszy przed nim, oznajmiła mu przed wszytkim ludem, dla której się go przyczyny tknęła i jako natychmiast uzdrowiona była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '47', '47', 'Tedy jej on powiedział: Córko bądź serca dobrego, wiara twa zachowała cię. Idźże z pokojem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '48', '48', 'Niż tego domówił, oto niektóry przyszedł od książęcia bożnice powiedając mu: Umarła córka twoja, nie przykrzże się Mistrzowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '49', '49', 'To usłyszawszy Jezus odpowiedział mu: Nie bój się a wierz jedno. Będzieć zachowana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '50', '50', 'Zatym wszedszy w dom nie dopuścił wnidź nikomu jedno Piotrowi, Jakubowi i Janowi, a ojcu i matce dzieweczki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '51', '51', 'A płakali jej wszyscy i narzekali, ale on im rzekł: Nie płaczcie, boć nie umarła, ale śpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '52', '52', 'Tedy się z niego śmiali wiedząc iż była umarła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '53', '53', 'A tak on wyrzuciwszy precz wszytki, a wziąwszy ją za rękę zawołał mówiąc: Dzieweczko wstań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '54', '54', 'Wrócił się tedy duch jej i wstała wnet, a on roskazał żeby jej jeść dano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '7', '55', '55', 'I zdumieli się rodzicy jej, a on im też zakazał, aby żadnemu nie powiedali tego co się było stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '0', '0', 'Potym wezwał Jezus dwunaście zwolenników swych, a dał im moc i zwierzchność na wszytki diabelstwa i choroby uzdrawiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '1', '1', 'A rozesłał je, iżby opowiedali królestwo Boże i uzdrawiali niemocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '2', '2', 'Tedy rzekł do nic: Nie bierzcie nic na drogę, ani lasek, ani sumek, ani chleba, ani pieniędzy, ani miejcie dwu sukien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '3', '3', 'A do któregokolwiek domu wnidziecie, tamże mieszkajcie i ztamtąd nie wychadzajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '4', '4', 'A którzyby was kolwiek nie przyjęli, wyszedszy z miasta onego, też i proch otrząśnicie z nóg waszych, ku świadectwu przeciw nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '5', '5', 'Wyszedszy tedy obchodzili wszytki miasteczka opowiedając Ewanjeliją, a wszędzie uzdrawiając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '6', '6', 'I usłyszał król Herod o wszytkim co się działo od niego i wątpił dlatego, że niektórzy powiedali iżby Jan wstał od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '7', '7', 'A niektórzy zasię, iż się Heljasz ukazał, a drudzy też, iż prorok niektóry z onych starych zmartwych wstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '8', '8', 'Tedy rzekł Herod: Janaciem ja dał ściąć, lecz ten co wżdy jest zacz, o którym ja takowe rzeczy słyszę? I szukał tego jakoby go widzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '9', '9', 'Zatym się wrócili Apostołowie i powiedzieli mu cokolwiek czynili. Tedy wziąwszy je z sobą szedł osobno na miejsce puste u miasta, które zową Betsajda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '10', '10', 'Ale gdy to zrozumiało zgromadzenie ludzi, szło za nim, a on je przyjął i powiedał im o królestwie Bożym. A ty którzy uzdrowienia potrzebowali, uzdrawiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '11', '11', 'A gdy się dzień chylił kwieczoru, przystąpiwszy oni dwanaście rzekli jemu: Rospuść to zgromadzenie, aby odszedszy do miasteczek i wsi okolicznych, tam się udali a znaleźli coby jeść, bochmy tu na miejscu spustoszałym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '12', '12', 'Ale on rzekł do nich: Dajcie wy im co jeść A oni powiedzieli: Nie mamyć więcej, tylko pięcioro chleba a dwie rybie, wyjąwszy bychmy szli a nakupili co jeść prze ten lud wszytek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '13', '13', 'Abowiem było mężów jakoby pięć tysięcy. I rzekł zwolennikom swoim: Posadźcie je w każdym rzędzie po pięcidziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '14', '14', 'Tedy ono tak uczynili i siedli ono wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '15', '15', 'A on wziąwszy ono pięcioro chleba i dwie rybie, pojźrzawszy w niebo, błogosławił im, a łamał i dawał zwolennikom, aby kładli przed ono zgromadzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '16', '16', 'A tak jedli ono wszyscy i najedli się i nazbierano co im pozostało okruszków koszów dwanaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '17', '17', 'I stało się, gdy on sam był modląc się, a z nim byli zwolennicy, tedy ich pytał mówiąc: Kimże mię powieda być ten lud?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '18', '18', 'A oni mu odpowiedając rzekli: Janem Krzcicielem, drudzy zasię Heljaszem, a drudzy też iż prorok niektóry z onych starych zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '19', '19', 'Tedy im on rzekł: A wy kim mię być powiedacie? A odpowiedziawszy Symon Piotr rzekł: Krystusem onym Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '20', '20', 'Ale on im zagroziwszy roskazał, aby tego żadnemu nie powiedali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '21', '21', 'Mówiąc: Potrzebać aby Syn człowieczy wiele cierpiał, a był wzgardzon od starszych i od książąt kapłańskich i od doktorów i aby był zabit, a trzeciego dnia wzbudzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '22', '22', 'I mówił do wszytkich: Jeśliż kto chce za mną idź, niechżeć zaprzy samego siebie, a weźmie krzyż swój na każdy dzień i niech idzie za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '23', '23', 'Abowiemci kto będzie chciał zachować duszę swą, straci ją, a ktokolwiek utraci duszę swą dla mnie, ten ci ją zachowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '24', '24', 'Bo cóż to podpomoże człowieka jeśliby zyskał wszytek świat, a samby siebie stracił, albo pozbył samego siebie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '25', '25', 'Abowiemci ten, który się zawstydzi mnie i słów moich, tego się Syn człowieczy wstydać będzie, gdy przydzie z chwałą swoją i ojcowską i świętych Aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '26', '26', 'A powiedam wam prawdziwie, są niektórzy z tych co tu stoją, którzy żadnym sposobem nie ukuszą śmierci, aż gdy ujźrzą królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '27', '27', 'I stało się po tych słowiech jakoby po ośmi dnioch, wziął z sobą Piotra, Jana i Jakuba, a wszedł na górę aby się modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '28', '28', 'A czasu modlenia stała się insza osoba oblicza jego, a ubiór jego biały i lśnący się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '29', '29', 'A oto dwa mężowie rozmawiali z nim, którzy byli Mojżesz i Heljasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '30', '30', 'Którzy będąc widziani z chwałą opowiedali jego zeście, które być miało, iż miał być pogrzebion w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '31', '31', 'Ale Piotr i drudzy, którzy byli z nim, snem ociążeni byli, a gdy ocucili, ujźrzeli chwałę jego i onych dwu mężów, którzy z nim stali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '32', '32', 'Potym gdy oni odeszli od niego, rzekł Piotr do Jezusa: Mistrzu dobrzećby nam tu być. A przetoż postawmy trzy namioty. Jeden tobie, jeden Mojżeszowi i jeden Heljaszowi, lecz nie wiedział co mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '33', '33', 'A gdy tego on jeszcze domawiał, powstał obłok, który je zaćmił i bali się gdy wchodzili w obłok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '34', '34', 'Stał się tedy głos z obłoku mówiąc: Tenci jest Syn on mój namilszy, tegoż słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '35', '35', 'A gdy się zstał on głos, nalezion jest sam Jezus, a oni też milczeli, a przez on czas żadnemu nic nie powiedali z tych rzeczy, które widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '36', '36', 'I zstało się nazajutrz, gdy oni zstępowali z góry, zabieżało mu zgromadzenie wielkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '37', '37', 'A oto niektóry zawołał z onego zgromadzenia mówiąc: Mistrzu proszęcię wejźrzy na syna mego, boć tylkoż tego jedynego mam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '38', '38', 'A oto go duch chwyta, a z prętka woła i rozdziera go aż do ślin, a zaledwie odchodzi od niego, krusząc go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '39', '39', 'I prosiłciem zwolenników twoich, aby go wyrzucili, ale nie mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '40', '40', 'Tedy Jezus odpowiedziawszy rzekł: O narodzie niewierny i przewrotny i dokądże wżdy będę między wami, a długo was cierpieć będę? Przywiedźże tu syna twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '41', '41', 'A gdy się jeszcze przystępował, rostargało go diabelstwo i rozszarpało, ale Jezus zfukał ducha nieczystego i uzdrowił dziecię a wrócił je ojcu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '42', '42', 'I zdumieli się wszyscy nad tak zacną mocą Bożą, a gdy się wszyscy dziwowali wszytkim rzeczam, które czynił, rzekł do zwolenników swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '43', '43', 'Pokładajciesz wy w uszach waszych słowa ty, abowiemci to przyjdzie, iż Syn człowieczy będzie podan w ręce ludzkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '44', '44', 'Ale oni nie rozumieli tej powieści i było to od nich skryto, że się o tym nie czuli i bali się pytać go o tej powieści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '45', '45', 'I weszła między nie myśl, ktoby z nich był nawiętszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '46', '46', 'Tedy Jezus widząc myśl serca ich, wziął dzieciątko i postawił je podle siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '47', '47', 'Zatym rzekł ku nim: Któżkolwiek przyjmie dzieciątko to imieniem moim, mnie przyjmuje. A ktokolwiek mię przyjmie, przyjmuje onego, który mię posłał. Abowiem ktoć jest namniejszym między wszytkimi wami, tenci będzie wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '48', '48', 'A odpowiedziawszy Jan rzekł: Mistrzu widzielichmy niektórego, co pod imieniem twym wyrzucał diabelstwa i pohamowalichmy go, abowiem nie chodzi za tobą z nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '49', '49', 'Tedy do niego rzekł Jezus: Nie hamujciesz go, bo ktoć nie jest przeciwko nam, za namić jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '50', '50', 'Stało się potym gdy czas przychodził, że miał być wzięt ku górze, tedy się on sam zmocnił, aby szedł do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '51', '51', 'I wyprawił posły przed sobą, którzy idąc, weszli do miasteczka Samarytańskiego, aby mu nagotowali gospodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '52', '52', 'Ale go oni nie przyjęli, iż postawę okazował idącego do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '53', '53', 'Co gdy ujźrzeli zwolennicy jego Jakub i Jan rzekli: Panie chceszże, iż rzeczemy aby ogień stąpił z nieba i popalił je, tak jako też był i Heljasz uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '54', '54', 'Ale Jezus obróciwszy się sfukał je mówiąc: Nie wiecie jakiego ducha wy jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '55', '55', 'Abowiemci Syn człowieczy nie przyszedł aby tracił dusze ludzkie, ale iżby zachowywał, a także szli do drugiego miasteczka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '56', '56', 'I przydało się gdy oni szli, że niektóry człowiek w drodze rzekł do niego: Pójdę za tobą gdzie jedno pójdziesz Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '57', '57', 'Ale mu powiedział Jezus: Liszkić mają jamy swe i ptaszkowie powietrzni gniazdka, a Syn człowieczy nie ma gdzie przykłonić głowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '58', '58', 'Zatym rzekł ku drugiemu: Pódź za mną, a on rzekł: Panie dopuść mi abych pirwej szedł a pogrzebł ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '59', '59', 'Na to mu powiedział Jezus: Daj pokój. Niechaj umarli pogrzebują umarłe swoje, ale ty szedszy opowiedaj królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '60', '60', 'Tedy potym rzekł drugi: Pójdę za tobą Panie, ale mi dopuść żebych pirwej ty pożegnał, którzy są w domu moim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '8', '61', '61', 'Zatym rzekł do niego Jezus: Żadny, który rękę swą przyłożywszy ku pługowi obeźrzy się na to co za nim jest pozad, nie jest godny ku królestwu Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '0', '0', 'Potym naznaczył też Pan drugich siedmdziesiąt i rozesłał je po dwu przed oblicznością swoją do każdego miasta i miejsca, kędy jedno sam przydź miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '1', '1', 'I mówił im: Aczci żniwo wielkie jest, ale robotników mało, prościesz tedy pana żniwa, aby wysłał robotniki na żniwo swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '2', '2', 'Idźciesz. Oto ja was posyłam jako baranki w pośrzodek wilków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '3', '3', 'Nie nościesz mieszka ani sumek ani botów, a żadnego w drodze nie witajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '4', '4', 'Do któregokolwiek domu wnidziecie, naprzódże mówcie: Pokój temu domowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '5', '5', 'A jeśliby tam był który syn pokoju, zostanie nad nim pokój wasz, a jeśliż nie, wróci się do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '6', '6', 'Tamże w tym domu mieszkajcie jedząc i pijąc, cóżkolwiek przed was położą. Abowiemci godzien jest najemnik zapłaty swej, a nie przechadzajcie się z domu do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '7', '7', 'Także, do któregokolwiek byście miasta weszli, a przyjętoby was, jedzcie co wam położą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '8', '8', 'I uzdrawiajcie niemocne, którzyby w nim byli, a powiedajcie im: Przybliżyło się ku wam królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '9', '9', 'Do któregokolwiek też miasta wnidziecie, a nie przyjętoby was, wyszedszy na ulice jego mówcie:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '10', '10', 'Otóż i proch, który się nas jął z miasta waszego, ocieramy na was, wszakoż to wiedzcie żeć się do was przybliżyło królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '11', '11', 'A powiedamci wam, że Sodomie na on czas lżej będzie, niźli miastu onemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '12', '12', 'Biadaż tobie Chorazyn, biadaż tobie Betsajda, abowiem gdyćby w Tyru i Sydonie takie cuda czynione były, jakie się w tobie działy, uznaliby się, siedząc w worze i w popiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '13', '13', 'A dlategoż Tyrowi i Sydonowi lekcej będzie czasu sądu, niźli wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '14', '14', 'I ty Kapernaum, któreś jest aż do nieba wywyzszone, aż do piekła będziesz zniżone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '15', '15', 'Ktoć was słucha, mnie słucha, a kto was odrzuca, mnie odrzuca, a kto mnie odrzuca onego odrzuca, który mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '16', '16', 'A tak oni siedmdziesiąt wrócili się z weselem mówiąc: Panie i diabelstwać się nam poddają przez imię Twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '17', '17', 'Tedy im on rzekł: Patrzałem gdy szatan jako błyskawica z nieba spadał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '18', '18', 'Otóż wam moc dawam deptać węże i niedźwiadki i wszytkę moc nieprzyjacielską, a nic was nie obrazi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '19', '19', 'Ale się z tego nie radujcie, iż się wam duchowie poddają, owszem się weselcie z tego, żeć imiona wasze napisane są w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '20', '20', 'Tegoż tam czasu rozradował się Jezus w duchu i mówił: Chwalę cię Ojcze, Panie nieba i ziemię, iżeś to zakrył od mądrych i rozumnych, a objawiłeś to niemowniątkom, a to Ojcze iż się tak upodobało tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '21', '21', 'Wszytkoć mnie jest podano od Ojca mego, a żaden nie zna co zacz jest Syn, jedno Ociec, a co zacz jest Ociec, jednoż Syn, a komukolwiek będzie chciał Syn objawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '22', '22', 'Tedy się obróciwszy ku zwolennikom, rzekł osobno: Szczęśliweż to oczy, które widzą to co wy widziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '23', '23', 'Bo powiedam wam: Iż wiele proroków i królów żądali widzieć co wy widzicie, a nie widzieli i słyszeć co wy słyszycie, a wżdy nie słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '24', '24', 'Tam oto niektóry wykładacz zakonu wstawszy kusił go mówiąc: Mistrzu cóż czyniąc otrzymam żywot wieczny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '25', '25', 'A on mu odpowiedziawszy rzekł: I cóż jest napisano w zakonie? Jakoż czytasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '26', '26', 'On tedy odpowiedziawszy rzekł: Miłuj Pana Boga twego ze wszytkiego serca twego, ze wszytkiej dusze twej, ze wszytkich sił twoich, ze wszytkiej myśli twej. A bliźniego twego jako samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '27', '27', 'Na to mu rzekł: Dobrześ powiedział, toż czyń a będziesz żył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '28', '28', 'Ale on chcąc się sam usprawiedliwić rzekł do Jezusa: I któż wżdy jest mój bliźni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '29', '29', 'To przyjąwszy Jezus rzekł: Człowiek niektóry szedł z Jeruzalem do Jerycho i wpadł między zbójce, którzy go złupiwszy i zraniwszy, odeszli napoły zabitego zostawiwszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '30', '30', 'I przydało się, że tąż drogą szedł niektóry kapłan, a ujźrzawszy go z przeciwka, minął go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '31', '31', 'Także równie i Lewita, gdy przychodził ku miejscu onemu, a ujźrzał go z przeciwka, minął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '32', '32', 'A Samarytan niektóry w tęż drogę idąc nadszedł go i ujźrzawszy go, ruszon jest miłosierdziem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '33', '33', 'I przyszedszy zawiązał rany jego, a nalawszy oliwy i wina, włożył go na bydlątko swe i wiódł do gospody, a miał o nim pracą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '34', '34', 'Tedy nazajutrz odjeżdżając, wyjąwszy dwa grosza dał gospodarzowi mówiąc mu: Miejże o nim pracą, a cokolwiek nad to wydasz, ja wróciwszy się oddam tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '35', '35', 'A tak z tych trzech, któryżci się widzi blizszym być onemu co był wpadł między zbójce?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '36', '36', 'A on rzekł: Ten, który użył miłosierdzia przeciw jemu. Rzekł mu tedy Jezus: Idźże precz a uczyń także.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '37', '37', 'I zstało się w drodze, iż on wszedł do miasteczka niektórego, a niewiasta niektóra imieniem Marta przyjęła go w dom swój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '38', '38', 'A tać miała siostrę, którą zwano Marja, która też siedząc u nóg Jezusowych, słuchała słowa jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '39', '39', 'Ale Marta odrywała się około częstej posługi, a tak przyszedszy rzekła: Panie nie masz tego na pieczy, iż siostra moja opuściła mię, że więc sama służę? A przetoż jej powiedz, aby mi wżdy co pomagała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '40', '40', 'A odpowiedziawszy Jezus rzekł jej: Marto, Marto. Starasz się i frasujesz około wiela rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '9', '41', '41', 'Lecz jednej rzeczy potrzeba: Marja dobrąć cząstkę obrała, która od niej odjęta nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '0', '0', 'Stało się, iż się modlił na niektórym miejscu, a gdy przestał, rzekli kniemu niektórzy z zwolenników jego: Panie naucz nas modlić się, tak jako i Jan nauczył zwolenników swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '1', '1', 'Tedy im rzekł: Gdy się modlicie, mówcie: Ojcze nasz, któryś jest w niebiosach, święć się imię twoje. Przydź królestwo twoje. Bądź wola twa, jako w niebie, tak i na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '2', '2', 'Chleba naszego powszedniego daj nam na każdy dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '3', '3', 'I odpuść nam grzechy nasze, boć też i my odpuszczamy wszytkim którzy nam są winni. Nie w wódźże nas w pokuszenie, ale nas wybaw od złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '4', '4', 'Zatym też rzekł do nich: I któż z was mając przyjaciela, a szedszy do niego o północy, rzekłby mu: Przyjacielu pożycz mi trojga chleba?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '5', '5', 'Abowiem przyjaciel mój przyszedł do mnie z drogi, a niemam czego przedeń położyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '6', '6', 'A on będąc w domu odpowiedziałby mówiąc: Nie uprzykrzaj mi się. Jużci drzwi zamkniono, a dziatki moje są na łożu zemną, i nie mogęć wstać abychci dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '7', '7', 'Powiedamci wam: Chociażby też wstać nie chciał, aby dał jemu przeto, że jest przyjaciel jego, wszakoż dla pilnej prośby jego wstanie i da mu ile potrzebuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '8', '8', 'I jać też wam powiedam: Proście a będzie wam dano. Szukajcie a najdziecie. Kołaccie a będzie wam otworzono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '9', '9', 'Abowiem każdy, który prosi, bierze; a który szuka, znajduje; a kołacącemu będzie otworzono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '10', '10', 'Zali z was który ociec, gdy go prosi syn jego o chleb, poda mu kamień? Abo o rybę, izali miasto ryby poda mu węża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '11', '11', 'Abo prosiliby o jaje, izali mu poda niedźwiadka?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '12', '12', 'A przetoż wy, gdyżeście są źli, umiecie dawać dobre dary dzieciam waszym. Jakoż daleko więcej Ociec wasz niebieski da Ducha świętego tym, którzy od niego proszą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '13', '13', 'Tedy wyrzucił diabelstwo, które było nieme, a gdy ono diabelstwo wyszło, przemówił niemy i dziwowało się zgromadzenie ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '14', '14', 'A niektórzy z nich mówili: Przez Beelzebuła książę diabelskie wyrzuca diabelstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '15', '15', 'Drudzy zasię kusząc go, prosili znaku od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '16', '16', 'Ale on, gdyż wiedział myśli ich, rzekł im: Każde królestwo rozdwojone przeciwko sobie bywa zburzone, a dom między sobą różny upadnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '17', '17', 'A jeśliżeć i szatan rozdwojon jest przeciw sobie, jakoż się ostoi królestwo jego? Abowiem powiedacie, iż ja przez Beelzebuła wyrzucam diabelstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '18', '18', 'Gdzie więc, jeśli ja przez Beelzebuła wyrzucam diabelstwa, synowie waszy przez kogo wyrzucają? A dlategoż oni będą sędźmi waszymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '19', '19', 'Ale jeśliż ja palcem Bożym wyrzucam diabelstwa, zaisteć do was przyszło królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '20', '20', 'Gdy który mocarz zbrojny strzeże domu swego, w pokojuć są majętności jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '21', '21', 'Ale gdy mocniejszy kto nadeń targnąwszy się nań zwycięży go, odejmnie wszytkie jego broń, w której ufał, a łupy jego roździeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '22', '22', 'Któryć nie jest ze mną, jestci przeciwko mnie, a który nie zbiera ze mną, rosprasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '23', '23', 'Gdyć duch nieczysty wychodzi od którego człowieka, chodzić po miejscach oschłych szukając odpoczynku, a gdy nie znajduje mówi: Wrócę się do domu mego, skądem wyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '24', '24', 'A tak przyszedszy znajduje ji umieciony i wychędożony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '25', '25', 'Tedy szedszy, bierze z sobą siedm inych duchów gorszych niż sam jest, którzy wszedszy mieszkają tam i stawa się stan pośledni człowieka onego gorszy a niż pirwszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '26', '26', 'A tak gdy to mówił, krzyknęła wyniosłym głosem niektóra niewiasta z zgromadzenia mówiąc mu: Błogosławiony żywot, który cię nosił i piersi któreś sał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '27', '27', 'Ale on rzekł: I owszem błogosławieni ci, którzy słuchają słowa Bożego i strzegą go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '28', '28', 'A gdy się niemało ludu zeszło około niego począł mówić: Ten naród jest złościwy, żąda znaków, aleć im znak nie będzie dan, tylkoż znak on Jonasza proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '29', '29', 'Abowiem jako Jonasz był na znak Niniwitom, takżeć będzie i Syn człowieczy narodowi temu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '30', '30', 'Królowa od południa wstanie na sąd z mężmi narodu tego i osądzi je dlatego, iż przyszła od kończyn ziemię, aby słuchała mądrości Salomonowej, a otoć na miejscu tym więtszy jest niż Salomon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '31', '31', 'Ludzie Niniwitscy powstaną na sąd z narodem tym i osądzą je dlatego, iż się uznali na Jonaszowe odpowiedanie. A oto więtszy niż Jonasz jest na tym miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '32', '32', 'A żadenci świece zapalonej nie kładzie do miejsca skrytego, ani pod korzec, ale do lichtarza, aby ci, którzy wchodzą widzieli światło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '33', '33', 'Światłość ciała jest oko, a przetoż gdyby oko twe było proste i wszytkoć ciało twe świetne będzie. Ale gdy będzie złe, też i ciało twe ciemne będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '34', '34', 'Obaczajże tedy, jeśliż światło, które masz w sobie jest ciemnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '35', '35', 'Jeśliż tedy ciało twe wszytko świetne będzie, niemając jakiej cząstki zaćmionej, takci wszytko świetne będzie, jako gdy świeca blaskiem swoim oświeca cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '36', '36', 'Tedy, gdy jeszcze domawiał, niektóry Faryzeusz prosił go, aby był na obiedzie u niego, wszedszy tedy siadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '37', '37', 'A widząc Faryzeusz dziwował się, że się pirwej nie umył przed obiadem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '38', '38', 'Rzekł tedy Pan kniemu: Aczci wy Faryzeuszowie zwirzchu kubek i misę umywacie, lecz u was wewnątrz pełno jest drapiestwa i złości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '39', '39', 'Szaleni ! Izaź ten, który uczynił to co jest zwirzchu, nie uczynił też tego co jest i wewnątrz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '40', '40', 'A przetoż to, co w nich jest dajcie za jałmużnę, a otoć wam wszytki rzeczy będą czystemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '41', '41', 'Ale biada wam Faryzeuszowie, którzy dawacie dziesięcinę z miętki i z ruty i z każdej jarzyny, a opuszczacie sąd i miłość Bożą. Ano wam to było trzeba czynić, a onych nie opuszczać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '42', '42', 'Biadaż wam Faryzueszowie, którzy miłujecie pirwsze zasiadania w zebraniu i witania na rynkach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '43', '43', 'Biadaż wam doktorowie i Faryzeuszowie pokryci. Abowiem jesteście jako groby, których nie widać, co o nich nie wiedzą ci, którzy nad nimi chodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '44', '44', 'Odpowiedziawszy tedy niektóry z zakonnych wykładaczów rzekł mu: Mistrzu, gdy to powiedasz, tedy nam też czynisz krzywdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '45', '45', 'Ale on rzekł: Biadaż też i wam zakonnym wykładaczom. Abowiem ociążacie ludzi brzemiony trudnemi knoszeniu, a sami się i jednym swym palcem nie tkniecie brzemienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '46', '46', 'Biadaż wam ! Bo budujecie groby prorokom, a ojcowie waszy pomordowali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '47', '47', 'Zaisteć świadectwem waszym poświadczacie uczynki ojców waszych, abowiemci je oni pomordowali, a wy zasię budujecie groby ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '48', '48', 'A dlategoż też Mądrość Boża rzekła: Posyłam do nich proroki i Apostoły, a z nich jedny pomordują, drugie wypędzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '49', '49', 'Aby od narodu tego upominano się krwie wszytkich proroków, która jest wylana od założenia fundamentów świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '50', '50', 'Od krwie Abelowej, aż do krwie Zacharjaszowej, który zginął między ołtarzem i kościołem. Zaprawdęć wam powiedam, będąć się jej upominać od narodu tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '51', '51', 'Biadaż wam zakonnym wykładaczom, abowiemeście odjęli klucz poznania, samiżeście nie weszli, a tych którzy wnidź chcieli, hamowaliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '52', '52', 'A tak gdy to knim mówił, poczęli doktorowie i Faryzeuszowie barzo jemu dokuczać a przyłudzić go ku rozmowach o wielu rzeczach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '10', '53', '53', 'Czyhając nań, a chciwie nieczego łowiąc z ust jego, aby go w czym oskarżyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '0', '0', 'Pod tymże czasem, gdy się barzo wiele ludzi zebrało do niego, że jedni drugie podeptawali, począł mówić do zwolenników swoich: Naprzódże się strzeżcie od kwasu Faryzeuszowego, który jest pokryctwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '1', '1', 'Abowiemci nie masz nic skrytego, coby się objawić nie miało, ani tak tajemnego, coby ku wiadomości przydź nie miało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '2', '2', 'A przetoż to coście w ciemnościach mówili, na jaśni słyszeć będzie a coście w ucho mówili w zamknieniu, na domach będzie opowiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '3', '3', 'A tak wam mówię przyjaciołom moim: Nie bójcie się tych, którzy zabijają ciało, a potym coby dalej mieli czynić, nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '4', '4', 'Ale wam okażę kogo się bać macie. Bójcie się onego, który gdy zabije, ma jeszcze zwirzchność wrzucić do piekła. A toć wam powiedam, tego się bójcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '5', '5', 'Izali piąci wróblików nie przedają za dwa pieniądza? A wszakoż i jednego z nich Bóg nie zapomina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '6', '6', 'A owszemci włosy na głowie waszej wszytki są zliczone, a przetoż się nie bójcie, zacniejszyście wy są niż wiele wróblików.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '7', '7', 'A toć wam powiedam: Wszelki, który mnie wyzna przed ludźmi, tego i Syn człowieczy wyzna przed Anioły Bożemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '8', '8', 'Ale który się mnie zaprzy przed ludźmi, będzie zaprzan przed Anioły Bożemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '9', '9', 'A któżkolwiek co rzecze przeciw Synowi człowieczemu, będzie mu odpuszczono. Ale temu, któryby bluźnił przeciw Duchowi świętemu, nie będzie odpuszczono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '10', '10', 'Gdy was tedy będą wodzić do bożnic, do urzędów i do zwierzchności, nie starajciesz się jako i cobyście odpowiedzieć, abo mówić mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '11', '11', 'Abowiemci Duch święty nauczy was na ten czas co wam będzie potrzeba mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '12', '12', 'Zatym niektóry z zebrania rzekł do niego: Mistrzu rzecz bratu memu aby się zemną podzielił dziedzictwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '13', '13', 'Ale on mu odpowiedział: O człowiecze ! I któż mię postanowił sędzią abo dzielczym nad wami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '14', '14', 'A tak rzekł do nich: Baczciesz a strzeżcie się łakomstwa, abowiemci nie każdego żywot w tym należy, który ma dosyć majętności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '15', '15', 'I powiedział im podobieństwo tymi słowy: Niektóremu bogatemu człowiekowi zrobiło się obficie na gruncie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '16', '16', 'Tedy tak myślił sam w sobie mówiąc: Cóż będę czynił, gdyż nie mam dokądbych zebrać miał urodzaje moje?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '17', '17', 'I mówił: Tak uczynię. Pokażę gumna moje, a ine pobuduję, a tam zbiorę wszytki urodzaje moje i dobra moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '18', '18', 'I powiem duszy swojej: Duszo masz wiele dobra zachowanego na wiele lat. Odpoczyńże, jedz, pij, a bądź wesoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '19', '19', 'Ale mu Bóg powiedział: O szalony ! Tejci oto nocy duszę upominać się będą od ciebie. To tedy coś nagotował, czyjesz będzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '20', '20', 'Takci jest ten, który sobie skarbi, a nie jest w Bogu bogatym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '21', '21', 'Tedy rzekł do zwolenników swoich: Dlategoć wam tak powiedam, nie starajciesz się o żywot wasz cobyście jeść mieli, ani o ciele w cobyście się oblec mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '22', '22', 'Więtszać jest dusza niż pożywienie, a ciało niźli szata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '23', '23', 'Przypatrzciesz się żeć krucy nie sieją ani żną i nie mają spiżarnej ani gumien, a wżdy je Bóg karmi. Azaźeście wy nie są zacniejszy niż ptacy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '24', '24', 'I któż z was pilnym swym staraniem, myśląc może jeden łokieć przyłożyć nad wzrost swój?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '25', '25', 'Jeśliż tedy tego co namniejsze jest nie możecie, czemuż się o inych pilnie staracie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '26', '26', 'Obaczajciesz jako rostą lilie. Nie spracująć się, ani przędą, a powiedam wam, że i Salomon we wszytkiej swej chwale nie był tak ubranym, jako jedno z tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '27', '27', 'A jeśliżeć ziółko, które dziś jest na polu, a jutro będzie w piec wrzucone, tak Bóg przyobłóczy, jakoż dalej was mało ufających?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '28', '28', 'A przetoż wy nie pytajcie się cobyście jeść abo pić mieli, ani o tym myślcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '29', '29', 'Abowiemci o tym wszytkim ludzie tego świata się pytają, aleć ociec wasz zna, że wy tego potrzebujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '30', '30', 'Radszej tedy szukajcie królestwa Bożego, a tyć wszytki ine rzeczy przydane wam będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '31', '31', 'Nie bójże się ty maluczkie stadko! Abowiemci się upodobało Ojcu waszemu dać wam królestwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '32', '32', 'Poprzedajciesz majętności wasze, a dajcie jałmużnę, gotujcie sobie mieszki, które nie zwiotszeją i skarb w niebie, który nigdy nie ustanie, ani go złodziej dojdzie, ani mol zepsuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '33', '33', 'Abowiemci gdzie jest skarb wasz, tamci i serce wasze będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '34', '34', 'Niechżeć będą przepasane biodra wasze i pochodnie zapalone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '35', '35', 'A wy bądźcie podobni ludziam, którzy czekają pana swego, kiedyby się wrócił z wesela, iż gdyby przyszedł a zakołatał we drzwi, aby mu wnet otworzono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '36', '36', 'Szczęśliwisz to są oni słudzy, które gdy przyjdzie pan znajdzie czujące. Zaprawdę powiedam wam, iż się przepasze i roskaże im sięść i wyszedszy będzie im służył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '37', '37', 'I jeśliby też przyszedł o wtórej, abo o trzeciej straży, a tak je znalazł, szczęśliwisz to są słudzy oni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '38', '38', 'A tak to wiedzcie, gdyćby wiedział gospodarz, której godziny miałby przydź złodziej, czułciby, a nie dopuściłby podkopać domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '39', '39', 'A dlategoż i wy bądźcie gotowi, bo której się godziny nie domnimacie, Syn człowieczy przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '40', '40', 'Zatym rzekł Piotr kniemu: Panie, a do nasze mówisz to podobieństwo, czyli do wszytkich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '41', '41', 'A Pan powiedział: I któryż jest wierny a rostropny szafarz, którego pan postanowi nad czeladzią swoją, aby im wczas rozdał obrok?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '42', '42', 'Szczęśliwyć jest ten sługa, którego gdy przyjdzie pan, znajdzie tak czującego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '43', '43', 'Zaprawdęć wam powiedam, żeć nad wszytką swoją majętnością postanowi go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '44', '44', 'Lecz jeśliby rzekł on sługa: Nierychłoć przychodzi pan mój i począłby bić sługi i służebnice i jeść, pić a opijać się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '45', '45', 'Przydzieć pan sługi onego w dzień, w który się nie nadziewa, a w godzinę, której nie wie i wyłączy go, a w poczet niewiernych położy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '46', '46', 'On zasię sługa, który wiedział wolą pana swego, a nie sprawował się tak, ani czynił wedle jego wolej, wielce będzie karan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '47', '47', 'Ale który z niewiadomości zasłużył karanie, mniej będzie karan. Komuć tedy więcej dano, więcej się mu też upominać będą, a u kogo więcej schowają, więcej też będą chcieć od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '48', '48', 'Przyszedłem abych puścił ogień na ziemię i czegóż chcę jeśliż już jest zapalon?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '49', '49', 'Aleć mam być krztem okrzczon i jakom jest, uciśnion aż się to dokończy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '50', '50', 'Mniemacież abych przyszedł dawać pokój na ziemię? Nie, owszemci wam powiedam żeć różnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '51', '51', 'Abowiem od tego czasu będzie ich pięć w jednym domu różnych. Trzej przeciwko dwiema, a dwa przeciwko trzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '52', '52', 'Różnym będzie ociec od syna, a syn od ojca. Matka przeciw dziewce, dziewka przeciw matce. Świekra przeciw niewiestce, niewiastka przeciw świekrze swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '53', '53', 'Mówił też i do zgromadzenia: Gdy widziecie obłok wschodzący od zachodu, wnet powiedacie: Przychodzić deszcz i tak się stawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '54', '54', 'A gdy powienie wiatr od południa, powiedacie iż będą gorąca i bywa tak.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '55', '55', 'Pokryci ludzie, obliczność nieba i ziemię rozeznać winiecie, cóż się dzieje iż tego czasu nie rozeznawacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '56', '56', 'Przeczże i sami z siebie nie posądzacie co jest sprawiedliwego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '57', '57', 'Gdy tedy idziesz z twym przeciwnikiem przed urząd, starajże się w drodze jakobyś wolen był od niego, aby cię nie pociągnął przed sędziego, a sędzia żeby cię nie podał oprawcy, a oprawca żeby cię nie wrzucił do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '11', '58', '58', 'Powiedamci, nie wynidziesz stamtąd, aż też i ostatni drobny pieniądz wrócisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '0', '0', 'Przyszli też tam niektórzy na ten czas oznajmiając mu o Galilejczykoch, których krew mieszał Piłat z ofiarami ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '1', '1', 'Ale Jezus odpowiedziawszy rzekł im: I mniemaciesz, że ci Galilejczycy nad wszytki ine Galilejczyki grzeszniejszymi byli, iż takowe rzeczy cierpieli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '2', '2', 'Koniecznie nie ! A powiedam wam jeśliż się nie uznacie, wszyscy także zginiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '3', '3', 'Albo także i óśmnaście onych, na które się obaliła wieża Syloe i pobiła je, także mnimacie, żeby winniejszymi byli nad ine ludzi, którzy mieszkają w Jeruzalem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '4', '4', 'Iście nie ! A powiedamci wam i owszem jeśliż się nie uznacie, wszyscy także zginiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '5', '5', 'Powiedział potym to podobieństwo: Miał niektóry człowiek figowe drzewo wsadzone w winnicy swojej, a przyszedszy szukał za nim owocu i nie nalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '6', '6', 'Tedy rzekł ku winarzowi: Oto od trzech lat jako chodzę szukając owocu na tym drzewie figowym, a nic nie najduję, wytnisz je, bo cóż po tym, iż próżno ziemię nieużyteczną czyni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '7', '7', 'Ale on mu odpowiedziawszy rzekł: Panie zostawże je jeszcze na ten rok, aż je okopam i podsypę gnojem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '8', '8', 'A jeśliż poda owoc, zostanie; a nie podali, tedy je potym wytniesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '9', '9', 'Potym uczył w niektórej bożnicy w szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '10', '10', 'Tam oto była niewiasta, która miała ducha niemocy od ośminaście lat, a była skurczona, tak iż się rozkurczyć nie mogła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '11', '11', 'Te gdy ujźrzał Jezus zawoławszy na nię rzekł jej: Niewiasto uzdrowionaś jest od niemocy swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '12', '12', 'Tedy na nię ręce włożył, a natychmiast jest rosprostowana i chwaliła Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '13', '13', 'Ale odpowiedziawszy książę bożnice gniewając się, że Jezus w sabat uzdrawiał, rzekł do zgromadzenia: Sąć sześć dni, w które trzeba robić, wty tedy dni przychodźcie i będziecie uzdrowieni, ale nie w dzień sobotni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '14', '14', 'Na to jemu Pan odpowiedział mówiąc: Pokryty człowiecze, azaź każdy z was w sabat nie odwięzuje wołu swego, abo oślice swej od żłobu, a nie wiedzie napawać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '15', '15', 'A tej oto córki Abrahamowej, którą był związał Szatan przez óśmnaście lat, zali nie potrzeba było od  zwiąski tej rozwięzać w dzień sabatni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '16', '16', 'To gdy powiedział zawstydali się wszyscy ci, którzy się zastawowali, ale wszystko zgromadzenie radowało się ze wszytkich chwalebnych spraw, które się działy od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '17', '17', 'Zatymże rzekł Jezus: Ku komuż podobne jest Królestwo Boże, a ku której je rzeczy przypodobnię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '18', '18', 'Podobneć jest ziarnu gorczycznemu, które wziąwszy człek wrzucił do ogroda swego, a urozszy bywa z niego drzewo wielkie i ptaszkowie powietrzni czynią sobie gniazdka na gałązkach jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '19', '19', 'I rzekł po wtóre: Ku czemuż jeszcze przypodobnić królestwo Boże?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '20', '20', 'Podobneć jest kwasowi, który wziąwszy niewiasta do trzech miar mąki zamiesi tak, iż wszytka zakwaśnieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '21', '21', 'Tedy chodził po wszytkich miejściech i po wsiach, a uczył idąc w drogę do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '22', '22', 'I rzekł mu niektóry: Panie ! Izali mało tych są, którzy mają być zbawieni? Ale on rzekł ku nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '23', '23', 'Starajcie się abyście weszli ciasną furtką, bo powiedam ci wam żeć ich wiele będą wnidź chcieli, ale nie będą mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '24', '24', 'Gdyć wstanie gospodarz i zamknie drzwi, a poczniecie stać na dworze kołacząc we drzwi a mówiąc: Panie, panie ! Otwórz nam. A on wam odpowie mówiąc: Niewiemci zkąd wy jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '25', '25', 'Tedy poczniecie mówić: Jadalichmy przed tobą i pijali i uczyłeś na ulicach naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '26', '26', 'Aleć on rzecze: Powiadamci wam żeć nie wiem zkąd jesteście. Odstąpcież odemnie wszyscy, którzy broicie złości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '27', '27', 'Tam będzie płacz i zgrzytanie zębów, gdy ujźrzycie Abrahama, Izaaka i Jakuba i wszytki proroki w Królestwie Bożym zkąd was precz wypędzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '28', '28', 'Tedy się ich wiele zejdzie od wschodu i od zachodu słońca. Od północy i od południa, a będą siedzieć w Królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '29', '29', 'A otóż są ostatecznemi, którzy będą pierwszemi, a są pierwszemi, którzy będą ostatecznemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '30', '30', 'Tegoż dnia przyszli niektórzy Faryzeuszowie mówiąc mu: Wynidź a idź ztąd precz, bo cię Herod chce zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '31', '31', 'Tedy im odpowiedział: Idźcież, a mówcie tej liszcze: Otoć będę wyrzucał diabelstwo i będę uzdrawiał dziś i jutro a trzeciego dnia będę dokonan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '32', '32', 'A wszakoż potrzeba mi dziś i jutro i trzeciego dnia idź w drogę, abowiem się nie przydawa, aby prorok miał zginąć oprócz w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '33', '33', 'O Jeruzalem, Jeruzalem !  Które zabijasz proroki, a kamienujesz ty, którzy są do ciebie posłani, ilekroćżem chciał zebrać dziatki twe tak, jako kokosz kurczątka pod skrzydła swoje, a nie chcieliście?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '12', '34', '34', 'Otóż wam jest zostawion dom wasz opustoszony, a zaprawdęć wam powiedam, że mię nie ujźrzycie, aż gdy przyjdzie czas kiedy rzeczecie: Błogosławiony, który przyszedł w imię Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '0', '0', 'I stało się gdy on przyszedł do domu książęcia niektórego Faryzejskiego w sabat, iż by tam jadł, tedy go oni podstrzegali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '1', '1', 'A oto człowiek niektóry spuchły był przed nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '2', '2', 'Tedy Jezus odpowiedziawszy rzekł do zakonnych wykładaczów i do Faryzeuszów: Godzili się uzdrawiać w sabat?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '3', '3', 'Ale oni milczeli. Tedy go uzdrowił i puścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '4', '4', 'A odpowiedziawszy rzekł im: Gdyby osieł abo wół, którego z was wpadł w studnią, zali go nie natychmiast wyciągnie w dzień sobotni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '5', '5', 'I nie mogli mu nic odpowiedzieć przeciwko temu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '6', '6', 'Zatym przytoczył podobieństwo na ty, którzy byli wezwani, bacząc jako pierwsze zasiadania obierali mówiąc do nich:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '7', '7', 'Gdy będziesz od kogo wezwan na wesele, nie siadajże na pierwszym miejscu, by kiedy zacniejszy kto niż ty nie był wezwan od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '8', '8', 'A tedy przyszedszy ten, który ciebie i onego wezwał, rzekłby tobie: Postęp mu miejsca, a tak byś ze wstydem musiał otrzymać pośledniejsze miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '9', '9', 'Ale gdy będziesz wezwan, idźże siedzieć na poślednim miejscu, a gdy przyjdzie ten, który cię wezwał rzecze tobie: Przyjacielu wstąp wyższej, a tedyć tobie będzie cześć przed wszytkiemi, którzy z tobą siedzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '10', '10', 'Abowiemci wszelki co się wynosi zniżon będzie, a kto się uniża wywyższon będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '11', '11', 'Zatym rzekł ku onemu, który go był wezwał: Gdy sprawujesz obiad abo wieczerzą, nie wzywajże przyjaciół twoich ani braciej twej, ani krewnych twych ani sąsiad bogatych, żeby cię też zasię wzajem nie wezwali i oddali to tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '12', '12', 'Ale gdy sprawujesz ucztę wzówże ubogich, ułomnych, chromych i ślepych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '13', '13', 'A będziesz szczęśliwym, iż nie będą mogli oddać tobie, abowiem ci będzie oddano przy zmartwychwstaniu sprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '14', '14', 'To słysząc niektóry z tych, którzy społu siedzieli rzekł jemu: Szczęśliwyć ten jest, który je chleb w Królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '15', '15', 'Ale mu on powiedział: Człowiek niektóry sprawił wieczerzą wielką i wezwał ich wiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '16', '16', 'I gdy była godzina wieczerzej posłał sługę swego, aby powiedział wezwanym: Pódźcież, boć już wszystko gotowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '17', '17', 'Lecz się wszyscy jednako wymawiać poczęli. Pierwszy mu rzekł: Kupiłem rolą i mam potrzebę idź a oglądać ją. Proszę cię miej mię za wymówionego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '18', '18', 'A drugi rzekł: Kupiłem pięć par wołów i idę ich doświadczać. Proszę cię, miej mię za wymówionego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '19', '19', 'Drugi zasię rzekł: Żonem pojął i dlategoż przydź nie mogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '20', '20', 'A gdy się sługa od nich wrócił, oznajmił to Panu swemu. Tedy się gospodarz rozgniewawszy rzekł słudze swemu: Idź rychło na ulice i na przecznice miesckie, a ubogie, ułomne, chrome i ślepe tu przywiedź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '21', '21', 'I rzekł sługa: Panie stało się jakoś roskazał i jesczżeć miejsce zostało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '22', '22', 'Rzekł tedy Pan słudze: Idź na drogi i między opłotki, a pądź, niech idą, aby był napełnion dom mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '23', '23', 'Abowiemci wam powiedam, żeć żaden z onych mężów, którzy są wezwani, nie ukusi wieczerzej mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '24', '24', 'Szło tedy przy nim wielkie zgromadzenie, a obróciwszy się rzekł do nich:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '25', '25', 'Jeśliż kto idzie do mnie, a nie ma w nienawiści ojca swego i matki swej, żony i dzieci, braciej i sióstr i co też więcej, duszę swojej, ten ci nie może być moim zwolennikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '26', '26', 'Ktokolwiek też nie nosi krzyża swego, a idzie za mną, nie może być mym zwolennikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '27', '27', 'Bo któż z was jest, który gdy by chciał budować wieżę, zasz nie pierwej siedząc rachuje nakład jeśliby mu dostarczył ku dokonaniu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '28', '28', 'Aby gdyby założył fundamenty, a dokończyć nie mógł, ci którzy widzą nie poczęli szydzić z niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '29', '29', 'Mówiąc: Człowiek ten począł budować, a nie mógł dokonać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '30', '30', 'Abo który król coby wyjechawszy chciał zwieść bitwę z drugim królem, nie pierwej siadł radząc się jeśliż może z dziesiącią tysięcy zaciągnąć onemu, który z dwiemadziesty tysięcy jedzie przeciw niemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '31', '31', 'I owszem póki on jeszcze jest daleko, wysyła z poselstwem, prosząc oto coby było ku pokojowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '32', '32', 'Także tedy, którykolwiek z was nie wyrzekł się wszytkich swych majętności, ten ci nie może być moim zwolennikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '33', '33', 'Dobrać jest sól, lecz jeśli sól smak straci, czymże ją naprawią?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '13', '34', '34', ', Gdyż się ani na ziemię ani w gnój nie godzi, ale bywa precz wyrzucona. A tak kto ma uszy ku słuchaniu, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '0', '0', 'Potym się do niego schodzili wszyscy celnicy i grzesznicy, aby go słuchali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '1', '1', 'I szemrali Faryzeuszowie i doktorowie mówiąc: Ten przyjmuje grzeszniki i jada z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '2', '2', 'Ale on to podobieństwo mówił do nich rzekąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '3', '3', 'Którysz jest z was człowiek coby mając sto owiec stracił jednę z nich. Izali opuszcza onych dziewiącidziesiąt i dziewiąci na puszczy, a szedszy nie szuka onej, która zginęła, aż ją znajdzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '4', '4', 'A znalazszy ją, kładzie na ramiona swe radując się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '5', '5', 'I przyszedszy do domu wzywa przyjaciół i sąsiad mówiąc im: Radujcie się społu ze mną, abowiemciem nalazł owieczkę moję, która była zginęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '6', '6', 'Powiedamci wam, żeć też także będzie radość w niebie nad jednym grzesznikiem pokutującym, więcej niż nad dziewiąciądziesiąt i dziewiącią sprawiedliwych, którzy nie potrzebują uznania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '7', '7', 'Abo niewiasta, któraby miała dziesięć dragm, jeśliżby straciła dragmę jednę, zali nie zapala świecę i nie umiata domu, a nie szuka z pilnością ażby znalazła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '8', '8', 'A gdyć znajdzie, wzywa przyjaciółek i sąsiadek swych mówiąc: Radujcie się społu ze mną, abowiemciem nalazła dragmę com ją była straciła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '9', '9', 'Powiedamci wam: Takować radość będzie przed Anioły Bożymi, nad jednym grzesznikiem pokutującym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '10', '10', 'Nad to rzekł: Człowiek niektóry miał dwu synu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '11', '11', 'Z których młodszy rzekł ojcu: Daj mi część majętności mnie należącej, a także on rozdzielił im majętność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '12', '12', 'Po maluczkim tedy czasie, gdy wszytko zebrał on młodszy syn, odjechał barzo w dalekie krainy i rosproszył majętność swą żywąc rospustnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '13', '13', 'A gdy wszytko potrawił, stał się głód wielki w krainie onej, tak iż onemu poczęto nie stawać żywności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '14', '14', 'A tak szedszy przystał do jednego z mieszczan onej krainy, który go posłał na pole swe, aby pasł wieprze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '15', '15', 'I żądał jakoby natkał brzuch swój łuszczynami, których nie dojadały wieprze, ale mu ich nikt nie dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '16', '16', 'Potym ksobie przyszedszy rzekł: Jako wiele najemników ojca mego mają dosyć chleba, a ja oto od głodu ginę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '17', '17', 'Ale wstawszy pójdę do ojca mego i rzekę mu: Ojcze zgrzeszyłem przeciw niebu i przed tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '18', '18', 'I jużem dalej nie jest godzien ozywać się synem twoim. Uczyńże mię jednym z najemników twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '19', '19', 'Tedy wstawszy szedł do ojca swego, a gdy jeszcze był opodal, ujźrzał go ociec jego, a ruszony miłosierdziem, wybieżawszy padł na szyi jego i całował go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '20', '20', 'I rzekł mu syn: Ojcze zgrzeszyłem przeciw niebu i przed tobą, a nie jestem dalej godzien ozywać się synem twoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '21', '21', 'Rzekł tedy ociec do sług swoich: Przynieście onę osobliwą szatę i obleczcie go. Podajcież pierścień na rękę jego i trzewiki na nogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '22', '22', 'A przywiódszy tłuste cielę zarzeźcie, abychmy jedli i weselili się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '23', '23', 'Abowiemci ten mój syn umarł był i ożył. Zginął i znalazł się. A także poczęli być weseli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '24', '24', 'Na ten czas starszy syn jego był na polu, który gdy przyszedł a był blisko domu, usłyszał śpiewanie i granie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '25', '25', 'A wezwawszy jednego z służebników, pytał coby to było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '26', '26', 'Tedy on mu powiedział: Brat twój przyszedł i zarżnął ociec twój ono cielę tłuste, iż się mu zdrów wrócił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '27', '27', 'Rozgniewał się tedy, a nie chciał wnidź, ale ociec jego wyszedszy, prosił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '28', '28', 'Wszakże on odpowiedziawszy rzekł ojcu: Oto przez tak wiele lat służę tobie, a nigdym nie przestąpił roskazania twego, a wżdyś mi nigdy nie dał koźlęcia, iżbych się weselił z przyjacioły moimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '29', '29', 'A teraz, gdy syn twój, który pożarł wszytkę swą majętność z wszetecznicami, przyszedł. Zarznąłeś przeń ono tłuste cielę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '30', '30', 'Na to mu on powiedział: Synu, tyś zawżdy jest ze mną, a wszytki dobra moje sąć twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '14', '31', '31', 'Aleć było potrzeba radować się i weselić, iż ten brat twój, który był umarł, ożył, a który był zginął, nalezion jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '0', '0', 'Potym rzekł do zwolenników swych: Był niektóry człowiek, który miał szafarza, a ten był przed nim oskarżon, jakoby rozpraszać miał dobra jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '1', '1', 'A tak on wezwawszy go, rzekł mu: Cóż to o tobie słyszę? Oddaj liczbę szafarstwa twego, abowiem już więcej nie możesz być szafarzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '2', '2', 'Tedy rzekł on szafarz sam u siebie: Cóż czynić będę, gdyż Pan mój odejmuje mi szafarstwo? Kopać nie mogę, a żebrać się wstydzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '3', '3', 'Lecz wiem co uczynię, iż gdy będę złożon z szafarstwa, przyjmą mię wżdy do domów swoich niektórzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '4', '4', 'A tak przyzwawszy wszytkich dłużników pana swego rzekł pierwszemu: Jak wieleś winien panu memu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '5', '5', 'A on powiedział: Sto batów oliwy. Tedy mu on rzekł: Weźmi tablicę twę i siadszy natychmiast napisz pięćdziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '6', '6', 'Zatym drugiemu rzekł: A ty jakoś wiele winien? Tedy on rzekł: Sto korcy pszenice, ale mu on powiedział: Weźmisz tablicę twę, a napisz ośmdziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '7', '7', 'I pochwalił pan szafarza złościwego, iż rostropnie uczynił: A dlategoż synowie tego świata rostropniejszy są nad syny światłości w rodzaju swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '8', '8', 'A jać wam powiedam: Czyńcie sobie przyjacioły z pieniędzy złościwych, aby gdy ustaniecie przyjęli was do wiecznych przybytków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '9', '9', 'Kto wierny jest w rzeczach namniejszych, tenci i w wielkich wierny jest, a kto w namniejszych jest złościwym, też i w więczszych jest złościwym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '10', '10', 'Jeśliżeście tedy w złościwych pieniądzach wiernymi nie byli, prawdziwych któż wam wierzyć będzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '11', '11', 'A jeśliżeście w cudzym wiernymi nie byli, co wasze jest któż wam da?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '12', '12', 'Żaden sługa dwiema panom służyć nie może, bo abo jednego będzie miał w nienawiści, a drugiego miłować będzie, abo ku jednemu przystanie a drugiego wzgardzi. Nie możecie Bogu służyć i pieniądzam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '13', '13', 'Tego też i Faryzeuszowie słuchali, którzy byli łakomi, a wszakoż szydzili z niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '14', '14', 'Tedy im on rzekł: Wy jesteście, którzy sami siebie usprawiedliwiacie przed ludźmi, ale Bóg zna serca wasze, abowiem to co jest u ludzi wyniosłego, obrzydłe jest przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '15', '15', 'Zakon i prorocy aż do Jana, od tego czasu królestwo Boże opowiedają, a każdy się do niego wrywa gwałtem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '16', '16', 'Aleć łacwiej niebu i ziemi przeminąć a niż jednej kresce zakonu upaść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '17', '17', 'Któżkolwiek opuszcza żonę swą a drugą pojmuje, ten cudzołoży, a ktokolwiek od męża opuszczoną pojmie, też cudzołoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '18', '18', 'Był niektóry człowiek bogaty, a obłóczył się w szarłat i w cienkie lniane odzienie i na każdy dzień hojnie używał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '19', '19', 'A był też niektóry żebrak imieniem Łazarz, który był wyrzucon przed sień jego owrzedziały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '20', '20', 'Chcąc być nasycon z odrobin, które padały z stołu bogaczowego, lecz psi przychodząc lizali rany jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '21', '21', 'I przydało się, że umarł żebrak, a niesion był od Aniołów na łono Abrahamowe. Potym umarł i bogacz, a pogrzebion jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '22', '22', 'A będąc w piekle podniósł oczu swoich gdy był w mękach i ujźrzał Abraama z daleka a Łazarza na łonie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '23', '23', 'Tedy zawołał mówiąc: Ojcze Abrahamie, zmiłuj się nade mną, a poszli Łazarza, aby omoczył koniec palca swego w wodzie a ochłodził język mój, bociem utrapion w tym płomieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '24', '24', 'Ale Abraham powiedział: Synu, rospomni żeś brał dobra twe za żywota twego, a Łazarz też niewczasy, a tak on teraz używa pociechy, a tyś zasię jest męczon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '25', '25', 'A nad to wszytko między nami i wami odchłań wielka postanowiona jest, iż ci, którzyby ztąd chcieli przejdź do was, nie mogą, ani z tamtąd przejdź ówdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '26', '26', 'Ale on rzekł: Proszę cię tedy Ojcze abyś go posłał do domu ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '27', '27', 'Abowiem mam pięć braciej, aby im tego poświadczył, iżby też oni nie przyszli na to miejsce męki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '28', '28', 'Lecz mu powiedział Abraham: Mająć Mojżesza i proroki, niechże ich słuchają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '29', '29', 'Ale on rzekł: Nie Ojcze Abrahamie, ale gdyby kto z umarłych szedł do nich, wżdy się uznają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '15', '30', '30', 'Tedy mu powiedział Abraham: Jeśliżeć Mojżesza i proroków nie posłuchają, tedyć i żadnemu choćby z martwych ocucił, nie uwierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '0', '0', 'Potym mówił do swych zwolenników: Nie możeć być, aby zgorszenia nie miały przyjdź, ale biada temu, przez którego przychodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '1', '1', 'Lepiej aby mu był założon kamień młyński około szyję, a był wrzucon w morze, niźliby miał zgorszyć jednego tych maluczkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '2', '2', 'Strzeżcież się. A jeźliby zgrzeszył przeciw tobie brat twój, karz go słowy, a jeśliżby się uznał, odpuść mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '3', '3', 'A chociażby siedm kroć zgrzeszył przeciw tobie i siedm kroć przez dzień nawrócił się ktobie mówiąc: Uznałem się, odpuść mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '4', '4', 'Tedy rzekli Apostołowie Panu: Przymnóż nam wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '5', '5', 'Ale im Pan powiedział: Jeślibyście mieli tak wiele wiary jako ziarno gorczyczne, a rzeklibyście temu sykaminowi: Wyrwi się z korzenia, a wsadź się w morzu, tedyćby wam było posłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '6', '6', 'I któż z was jest coby miał sługę orzącego i pasącego, który gdyby się z pola wrócił, natychmiastby mu rzekł: Chodź a siądź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '7', '7', 'A zaź mu radszej nie rzecze: Nagotuj cobych wieczerzał, a przepasawszy się służ mi aż się najem i napiję, a potym też ty jedz i pij?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '8', '8', 'Zali dziękuje słudze onemu, który czyni to, co mu roskazano? Nie widzimi się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '9', '9', 'Także i wy, gdy uczynicie wszytko co wam jest roskazano mówcie: Słudzy nieużyteczni jestechmy, bo to cochmy byli winni uczynilichmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '10', '10', 'I stało się gdy szedł do Jeruzalem, iż on przechodził przez pośrzodek Samarijej i Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '11', '11', 'A gdy wchodził do niektórego miasteczka, tedy zabieżeli mu dziesięć mężów trędowatych, którzy stanęli z daleka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '12', '12', 'I krzyknęli głosem mówiąc: Jezusie, Mistrzu zmiłuj się nad nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '13', '13', 'A on je ujźrzawszy, rzekł do nich: Odszedszy okażcie się kapłanom i stało się, że gdy szli w drogę, oczyścieni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '14', '14', 'Ale jeden z nich gdy ujźrzał, że był uzdrowion, wrócił się chwaląc Boga głosem wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '15', '15', 'I padał na oblicze swe u nóg jego, czyniąc jemu dzięki, a tenże był Samarytan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '16', '16', 'A tak Jezus odpowiedziawszy rzekł: Zaź nie dziesięć onych oczyścieni są? A oni dziewięć gdzież są?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '17', '17', 'Nie nalezieni są którzyby się wrócili, aby dali Bogu chwałę, jednoż ten cudzoziemiec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '18', '18', 'Rzekł mu tedy: Wstań a idź. Wiara twa wybawiła cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '19', '19', 'Pytali go potym Faryzeuszowie, gdyby miało przyjdź królestwo Boże. A on im odpowiedziawszy rzekł: Nie przyjdźieć królestwo Boże tak, jakoby je obaczyć miano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '20', '20', 'Anić rzeką: Otoć tu, abo tam oto jest. Abowiem oto królestwo Boże między wami jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '21', '21', 'Zatym rzekł do zwolenników: Przyjdzieć czas, gdy będziecie żądali oglądać jeden z onych dni Syna człowieczego, a nie oglądacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '22', '22', 'I będąć wam mówić: Otoż tu, otoż tam, ale nie chodźcie, ani idźcie za nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '23', '23', 'Abowiem jako błyskawica błyskając się od jednej strony, która jest pod niebem, do drugiej strony, która jest pod niebem rozjaśnia się. Takżeć będzie i Syn Człowieczy w dzień swój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '24', '24', 'Lecz potrzeba pierwej, aby wiele ucierpiał i był wzgardzon od narodu tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '25', '25', 'I tak jako się stało za czasów Noego, takżeć będzie i za czasów Syna człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '26', '26', 'Jedli, pili, żony pojmowali i za mąż wydawali, aż do onego dnia, w który wszedł Noe do archy i przyszedł potop, a wytracił wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '27', '27', 'Takżeć równie jako się działo za czasów Lotowych, jedli, pili, kupowali, przedawali, sadzili i budowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '28', '28', 'A tegoż dnia, gdy wyszedł Lot z Sodomy, spadł jak deżdż ogień z siarką z nieba, a wytracił wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '29', '29', 'Wedle tychżeć rzeczy będzie i on dzień, w który syn człowieczy się objawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '30', '30', 'Onego to dnia jeśliżby kto był na domu, a naczynie jego w domu, niechajżeć nie schodzi aby je brać miał, a ktoby też był na polu, niechżeć się tak że nie wraca do tego co na zad pozostawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '31', '31', 'Przypomnicież sobie żonę Lotowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '32', '32', 'Któżkolwiek będzie się starał jakoby zachować duszę swą, straci ją, a ktokolwiek ją straci zgotuje jej żywot.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '33', '33', 'Powiedamci wam: Onej nocy będą dwa na łożu jednym, jednego wezmą a drugiego zostawą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '34', '34', 'Dwie będą mleć społu, jednęć wezmą a drugą zostawią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '35', '35', 'Bedąć dwa na polu, a jeden będzie wzięt, drugi zostawion będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '16', '36', '36', 'Tedy mu oni dawszy odpowiedź rzekli: Gdzież Panie? Ale on im rzekł: Gdzieć będzie ciało, tam się zbiorą i orłowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '0', '0', 'I powiedział im też podobieństwo ktemuż należące, iż się zawżdy modlić potrzeba a nie leniwieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '1', '1', 'Mówiąc: Był sędzia w niektórym mieście, który się Boga nie bał, ani się żadnego wstydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '2', '2', 'A w tymże też mieście była w doma niektóra, co do niego przyszła, mówiąc: Uczyń mię wolną od przeciwnika mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '3', '3', 'Lecz on nie chciał przez niemały czas. Potym mówił sam u siebie: Aczci się Boga nie boję, ani się żadnego wstydzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '4', '4', 'Wszakoż iż mi się uprzykrza ta wdowa, uczynię ją wolną, aby mi na ostatek przyszedszy więcej nie dokuczała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '5', '5', 'Rzekł tedy Pan: Słuchajcież co mówi ten złościwy sędzia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '6', '6', 'A cóż Bóg, zali nie uwolni wybranych swoich wołających do siebie we dnie i w nocy? Chociażby też gniewu swego przewłaczał nad nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '7', '7', 'Powiedamci wam, żeć je wolnymi uczyni w rychle. A wszakoż Syn człowieczy, gdy przyjdzie, a zaź znajdzie wiarę na ziemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '8', '8', 'Zatym rzekł do niektórych wiele o sobie trzymających jakoby byli sprawiedliwemi, a którzy sobie ine za nic mieli, to podobieństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '9', '9', 'Dwoje ludzi zstąpiło do kościoła, aby się modlili. Jeden Faryzeusz, a drugi celnik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '10', '10', 'I stanąwszy Faryzeusz tak się sam u siebie modlił: Boże czynię tobie dzięki, żem nie jest jako ini ludzie drapieżni, niesprawiedliwi i cudzołożni, abo jak też i ten celnik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '11', '11', 'Poszczę dwa kroć w tegodniu, dawam dziesięcinę ze wszytkiego co mam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '12', '12', 'Celnik zasię stanąwszy z daleka, nie chciał podnieść i oczu swych w niebo, ale tłukł piersi swe mówiąc: Boże bądź miłościw mnie grzesznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '13', '13', 'I powiedam to wam: Żeć ten odszedł usprawiedliwionym do domu swego więcej niźli on, abowiem ktokolwiek się podwyższa, będzie uniżon, a kto się uniża, będzie podwyższon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '14', '14', 'Przynoszono też kniemu dziateczki, aby się ich dotykał, co gdy widzieli zwolennicy, fukali na nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '15', '15', 'Ale Jezus wezwawszy dziateczek powiedział: Dopuśćcie dziateczkam przyjdź do mnie, a nie hamujcie ich, abowiemci takowych jest Królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '16', '16', 'Zaprawdęć wam powiedam: Ktożkolwiek nie przyjmie królestwa Bożego jako dzieciątko, żadnym sposobem nie wnidzie do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '17', '17', 'Tedy go pytało niektóre książę mówiąc: Mistrzu dobry cóż czyniąc otrzymam żywot wieczny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '18', '18', 'I powiedział mu Jezus: Przeczże mię powiedasz być dobrym? Żadenci nie jest dobrym, jednoż jeden to jest Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '19', '19', 'Znaszże roskazania? Nie cudzołóż, nie zabijaj, nie kradń, nie powiedaj świadectwa fałszywego, czci ojca twego i matkę twą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '20', '20', 'Tedy on rzekł: Tegom wszytkiego przestrzegał od młodości mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '21', '21', 'Co Jezus usłyszawszy powiedział jemu: Jednejżeć rzeczy jeszcze nie dostawa: Wszystko co masz poprzedaj, a podziel między ubogie, a będziesz miał skarb w niebie, przydźże, a chodź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '22', '22', 'Lecz on to usłyszawszy, barzo się zatroskał, bowiem był barzo bogatym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '23', '23', 'A gdy go Jezus ujźrzał tak barzo zasmuconego rzekł: Jakoż trudno temu co ma pieniądze wnidź to Królestwa Bożego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '24', '24', 'Abowiemci łacwiej jest wielbłądowi prześć przez ucho igielne, niż wnidź bogaczowi do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '25', '25', 'Ci tedy, którzy to słyszeli rzekli: I któryż może być zbawion?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '26', '26', 'Ku temu on powiedział: To co jest niepodobno u ludzi, podobno jest u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '27', '27', 'Zatym rzekł Piotr: Oto my opuścilichmy wszytko, a szliśmy za tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '28', '28', 'Tedy im on powiedział: Zaprawdę wam powiedam. Żaden nie jest, któryby opuścić miał dom, abo rodzice, abo bracią, abo żonę, abo dzieci, dla Królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '29', '29', 'Któryby nie miał wziąć daleko więcej tego czasu, a w przyszłym wieku żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '30', '30', 'Wziąwszy potym zsobą Jezus onych dwunaście rzekł im: Oto wstępujem do Jeruzalem, a wykonają się Synowi człowieczemu pisma ony, które są przez proroki napisane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '31', '31', 'Bo będzie podan poganom i nagrawan i spotwarzon i uplwan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '32', '32', 'A ubiczowawszy go, zamordują ji, aleć dnia trzeciego powstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '33', '33', 'Ale oni tego nic nie zrozumieli, a były ty słowa zakryte od nich, ani wiedzieli co powiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '34', '34', 'I stało się, gdy przychodził ku Jerychu, ślepy niektóry siedział wedle drogi żebrząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '35', '35', 'A gdy usłyszał, iż przechodziło zgromadzenie ludzi, pytał coby to było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '36', '36', 'I powiedziano mu, że przeszedł Jezus Nazareński.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '37', '37', 'Tedy on wołał mówiąc: Jezusie synu Dawidów zmiłuj się nademną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '38', '38', 'Lecz ci co szli wprzód fukali go aby milczał. Ale on tym więcej wołał: Jezusie Synu Dawidów zmiłuj się nademną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '39', '39', 'A tak Jezus stanąwszy roskazał go przywieść do siebie, a gdy był blisko, pytał go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '40', '40', 'Mówiąc: Cóż chcesz abychci uczynił? A on powiedział: Panie abych wziął wzrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '41', '41', 'I rzekł Jezus: Weźmisz wzrok, wiara twa zachowała cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '17', '42', '42', 'A przetoż natychmiast wziął wzrok i szedł za nim chwaląc Boga. Co gdy też lud wszytek widział, dawał chwałę Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '0', '0', 'Wszedszy potym Jezus do Jerycho, przechadzał się po nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '1', '1', 'A oto niektóry imieniem Zacheusz, który był przedniejszym nad celniki, a był bogaty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '2', '2', 'Starał się, aby widział, który wżdy jest Jezus, ale nie mógł przed zgromadzeniem, bo był małego wzrostu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '3', '3', 'A tak wprzód bieżawszy, wstąpił na sykomor, aby go ujźrzał, bowiem tamtędy idź miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '4', '4', 'Ale gdy przyszedł ku onemu miejscu, pojźrzawszy ku górze Jezus ujźrzał go i rzekł ku niemu: Zacheuszu zstąp rychło, abowiem dziś potrzeba mi zostać w domu twym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '5', '5', 'Tedy on wnet zstąpił, a przyjął go radując się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '6', '6', 'Co gdy widzieli wszyscy, szemrali mówiąc: Iż tam wszedł, a udał się do człowieka w grzechu żywiącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '7', '7', 'I stanąwszy Zacheusz rzekł ku Panu: Oto Panie połowicę dóbr mych dawał ubogim, a jeśliżem co komu z krzywdą wydarł, wracam w dwójnasób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '8', '8', 'I rzekł mu Jezus: Dziści się zbawienie stało domowi twemu dlatego, żeś i sam jest synem Abrahamowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '9', '9', 'Bo przyszedł Syn człowieczy, aby szukał a zachował to co było zginęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '10', '10', 'To gdy oni słyszeli, przydawszy dalej powiedział podobieństwo dlatego, że był blisko Jeruzalem, a iż mniemali, że się wnet królestwo Boże okazać miało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '11', '11', 'A przetoż rzekł: Człowiek niektóry rodu zacnego, jechał w daleką krainę dostawać sobie królestwa, a potym się wrócił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '12', '12', 'I przyzwawszy dziesiąci służebników swych, dał im dziesięć grzywien, a rzekł do nich: Kupczcież pokąd ja nie przyjdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '13', '13', 'A mieszczanie jego mieli go w nienawiści i wyprawili za nim poselstwo mówiąc: Nie chcemy aby ten królował nad nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '14', '14', 'Potym gdy się wrócił dostawszy królestwa, tedy roskazał do siebie wezwać onych sług, którym był dał pieniądze, aby się dowiedział coby który na kupi zyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '15', '15', 'A pierwszy przyszedszy rzekł: Grzywna twa  dziesięć grzywien zyskała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '16', '16', 'Tedy mu on rzekł: Dobrze sługo dobry, bo gdyżeś był na tak małe wiernym, miejże zwierzchność nad dziesiącią miast.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '17', '17', 'Przyszedł i drugi rzekąc: Panie grzywna twa pięć grzywien przyczyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '18', '18', 'A on też rzekł jemu: I ty też bądź nad dziesiącią miast.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '19', '19', 'Drugi potym przyszedł, mówiąc: Panie oto grzywna twa, któram miał zachowaną w chustce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '20', '20', 'Bom się bał, wiedząc żeś jest człowiek srogi i bierzesz czegoś nie położył, a żniesz czegoś nie siał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '21', '21', 'Tedy on rzekł: Z ust twych cię sądzę, sługo zły. Wiedziałeś żem ja jest człowiek srogi, biorąc czegom nie położył a żnąc czegom nie siał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '22', '22', 'Przeczżeś tedy nie dał pieniędzy moich na bank, a ja przyszedszy wziąłbych je był z lichwą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '23', '23', 'I rzekł tym, którzy tuż stali: Weźmicie od niego grzywnę, a dajcie ją temu, który ma ich dziesięć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '24', '24', 'Ale mu oni rzekli: Panie mać dziesięć grzywien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '25', '25', 'A on im zasię rzekł: Powiedamci wam, iż wszelkiemu, który ma, będzie dano, a od tego, który nie ma, to co ma, wezmą mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '26', '26', 'Ktemu jeszcze i nieprzyjacioły moje, którzy nie chcieli abych królował nad nimi, przywiedźcie tu, a pomordujcie przede mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '27', '27', 'A tak to rzekłszy, szedł wprzód idąc do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '28', '28', 'I stało się, gdy był blisko Betfage i Betanijej u góry, którą zową oliwną, posłał dwu z zwolenników swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '29', '29', 'Mówiąc: Idźcie do wsi, która jest przeciw wam, do której wszedszy najdziecie oślę uwiązane, na którym żaden człek nigdy nie siedział, odwiązawszy przywiedźcież je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '30', '30', 'A jeśliby was kto spytał: Przecz odwięzujecie? Także mu powiedzcie: Iż tego Panu potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '31', '31', 'A tak szedszy ci, którzy byli posłani, znaleźli tak jako im powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '32', '32', 'Gdy tedy rozwięzowali oślę, rzekli panowie jego do nich: Przecz odwięzujecie oślę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '33', '33', 'Ale im oni powiedzieli: Iż tego Panu potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '34', '34', 'I wiedli je do Jezusa, a namiotawszy szat swych na ono oślę, wsadzili na nie Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '35', '35', 'A tak gdy jechał, pościełali szaty swe na drodze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '36', '36', 'I gdy był blisko już na końcu góry oliwnej, poczęła wszytka wielkość zwolenników weseląc się chwalić Boga głosem wielkim, ze wszytkich cudów, które widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '37', '37', 'Mówiąc: Błogosławiony on Król, który idzie w imię Pańskie, pokój na niebie, a chwała na wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '38', '38', 'Tedy niektórzy Faryzeuszowie z onego zgromadzenia rzekli do niego: Mistrzu pofukaj zwolenniki twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '39', '39', 'Ale on odpowiedź dawszy rzekł im: Powiedam to wam: Jeśliżećby ci milczeli, wnet kamienie wołać będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '40', '40', 'A gdy był blisko ujźrzawszy miasto zapłakał nad nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '41', '41', 'Mówiąc: O gdybyżeś ty znało, abo tylko to dnia twego, co jest ku pokojowi temu, aleć to zakryto jest od oczu twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '42', '42', 'Abowiem ten czas na cię przyjdzie, kiedy cię obtoczą nieprzyjaciele twoi wałem i ścisną cię zewsząd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '43', '43', 'A położą cię równą z ziemią i syny twe w tobie, ani zostawią kamienia na kamieniu dlatego, iżeś nie poznało onego czasu nawiedzenia swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '44', '44', 'Zatym wszedszy do kościoła począł wyrzucać ty, którzy przedawali w nim i kupowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '45', '45', 'Mówiąc im: Napisanoć jest: Dom mój, dom modlitwy jest, a wyście z niego uczynili jaskinią zbójców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '46', '46', 'I uczył ustawicznie w kościele, lecz książęta kapłańskie, doktorowie i przedniejszy z ludu, szukali tego jakoby go stracić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '18', '47', '47', 'Ale nie mogli znaleźć coby mieli czynić, abowiem wszytek lud pospolity na tym przestawał gdy co słyszał od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '0', '0', 'I stało się niektórego dnia z onych, gdy uczył lud w kościele i opowiedał Ewanjeliją, nadeszły książęta kapłańskie i doktorowie z starszymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '1', '1', 'A rzekli do niego tymi słowy: Powiedz nam, którą mocą to czynisz, abo kto jest ten, któryć dał zwierzchność oto tę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '2', '2', 'Tedy Jezus odpowiedając rzekł do nich: Spytam was i ja o rzecz niektórą, jedno mi powiedzcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '3', '3', 'Krzest Janowy byłli z nieba czyli z ludzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '4', '4', 'A tak oni myślili u siebie mówiąc: Jeśliż powiemy z nieba, tedyć rzecze: A przeczżeście mu nie wierzyli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '5', '5', 'Jeśliż zasię rzeczemy mu, że jest z ludzi, tedy wszytek lud ukamionuje nas. Bo to za rzecz pewną mają, iż Jan był prorokiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '6', '6', 'Odpowiedzieli tedy, że nie wiedzieli skąd był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '7', '7', 'A Jezus im powiedział: Anić też ja wam powiem, którą mocą to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '8', '8', 'I począł do ludu powiedać to podobieństwo: Człowiek niektóry sadził winnice i najął ją oraczom, a sam odjechał precz na niemały czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '9', '9', 'Gdy potym czas przyszedł, posłał do onych oraczów sługę aby mu pożytki z winnice oddali, którzy ubiwszy go odesłali z niszczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '10', '10', 'Tedy on przedsię posłał sługę drugiego, lecz też oni i tego ubiwszy i zesromociwszy odesłali z niszczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '11', '11', 'Zatym on jeszcze posłał trzeciego, ale oni i tego zraniwszy wyrzucili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '12', '12', 'A tak Pan onej winnice rzekł: Cóż mam czynić? Poślę syna mego miłego, snadź gdy go ujźrzą, wstydać się go będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '13', '13', 'Ale gdy onego ujźrzeli oracze, rozmówili między sobą w ten sposób: Tenci jest dziedzic, pódźcież a zabijmy go, aby nasze było dziedzictwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '14', '14', 'Wyrzuciwszy go tedy z winnice zamordowali. A przetoż cóż im uczyni Pan onej winnice?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '15', '15', 'Przyjdzieć a potraci oracze ty a da winnicę inym. To gdy oni słyszeli rzekli: Boże tego nie daj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '16', '16', 'Lecz on pojźrzawszy na nie rzekł: I cóż wżdy ono jest napisano: Kamień, który odrzucili budujący, ten się stał głową węgielną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '17', '17', 'Wszelki, któryć upadnie na ten kamień, stłucze się, a na kogo zasię upadnie, pokruszy go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '18', '18', 'Tedy książęta kapłańskie i doktorowie chcieli się nań rzucić rękoma tegoż tam czasu, jedno iż się ludu bali, abowiem poznali, iż na nie przytaczał to podobieństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '19', '19', 'A tak czyhając nań posłali szpiegi, którzyby się zmyślali być sprawiedliwemi, aby podchwytywali słowa jego, chcąc go podać pod zwierzchność i moc staroście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '20', '20', 'Którzyby go pytali mówiąc: Mistrzu wiemy, że dobrze powiedasz i uczysz, ani sobie ważysz osoby, owszem drogi Bożej w prawdzie nauczasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '21', '21', 'Słuszyli nam dać podatek cesarzowi, czyli nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '22', '22', 'Lecz on obaczywszy chytrość ich rzekł ku nim: Czemuż mię kusicie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '23', '23', 'Ukażcie mi pieniądz. Czyj ma na sobie obraz i napis? I odpowiedziawszy mu rzekli: Cesarski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '24', '24', 'Zatym powiedział im: Oddajcież tedy co jest cesarskiego cesarzowi, a co jest Bożego Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '25', '25', 'A tak nie mogli podchwycić słów jego przed ludem, a zdziwiwszy się odpowiedzi jego, milczeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '26', '26', 'Potym przyszli niektórzy z Saduceuszów, (którzy powiedają, iż nie masz zmartwychwstania) i pytali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '27', '27', 'Mówiąc: Mistrzu Mojżesz nam napisał: Jeśliżby czyj brat umarł mając żonę, a zszedłby przez dziatek, aby brat jego wziął onę żonę, a wzbudził potomstwo bratu swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '28', '28', 'Było tedy siedm braciej, z których pierwszy pojąwszy żonę, umarł bez dziatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '29', '29', 'Po nim ją wziął wtóry, lecz i ten umarł nie mając dziatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '30', '30', 'Potym ją wziął trzeci ową, tak i oni siedm, a nie zostawiwszy dziatek pomarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '31', '31', 'Po wszytkich też umarła i niewiasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '32', '32', 'A tak przy zmartwychwstaniu czyją to z nich będzie żoną? Abowiem siedm mieli ją za żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '33', '33', 'Tedy odpowiedziawszy rzekł im Jezus: Ludzie tego wieku pojmują żony i za mąż wydawają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '34', '34', 'Aleć ci, którzy tego godni będą, aby wieku onego dostąpili i powstania od umarłych, ani żon pojmują, ani za mąż idą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '35', '35', 'Ani już więcej umierać mogą, abowiem równi są Aniołom, a są synmi Bożymi, gdyż są synowie zmartwychwstania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '36', '36', 'A iż umarli wstać mają: Teżci to i Mojżesz oznajmił u krza, gdy powieda być Pana Bogiem Abrahamowym i Bogiem Izaakowym i Bogiem Jakubowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '37', '37', 'Nie jestci tedy Bóg umarłych, ale żywych, bowiem jemu wszyscy żywą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '38', '38', 'Niektórzy tedy z doktorów odpowiedając rzekli: Mistrzu dobrześ powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '39', '39', 'A także go dalej ni ocz nie śmieli pytać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '40', '40', 'Nad to rzekł do nich: Jakoż wżdy powiedają żeby Krystus był syn Dawidów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '41', '41', 'A sam Dawid powieda w księgach psalmów: Rzekł Pan Panu mojemu: Siądź na prawicy mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '42', '42', 'Aż położę nieprzyjacioły twe podnóżkiem nóg twoich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '43', '43', 'A tak Dawid nazywa go Panem: I jakoż jest tedy synem jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '44', '44', 'A przetoż gdzie wszytek lud słyszał, rzekł zwolennikom swoim:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '45', '45', 'Przestrzegajcież się doktorów, którzy chcą chodzić w szatach szerokich i miłują witania na rynkach, a pierwsze stolice w zgromadzeniu i pirwsze zasiadania przy wieczerzach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '19', '46', '46', 'Którzy do szczątka pożerają domy wdów, a rzkomo się długiemi modlitwami bawią. A cić odnoszą cięższy sąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '0', '0', 'Potym wejźrzawszy widział bogacze oddawające dary swe do skarbu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '1', '1', 'Widział też i niektórą wdowę ubogą kładącą tam dwa drobne pieniądze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '2', '2', 'I rzekł: Prawdziwieć wam powiedam: Wdowa ta uboga więcej niż ci wszyscy włożyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '3', '3', 'Abowiem wszyscy ci z tego co im zbywało kładli, by tym darowali Boga. Ale ta z niedostatku swego, wszytko pożywienie które miała włożyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '4', '4', 'I gdy niektórzy powiedali o kościele, iż był pięknym kamieniem i dary ochędożony rzekł im:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '5', '5', 'A na toż patrzacie? Przyjdąć czasy, w które nie będzie zostawion kamień na kamieniu, któryby nie był rozwalon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '6', '6', 'Tedy go pytali mówiąc: Mistrzu a kiedyż to wżdy będzie? I który znak przyjdź ma kiedy się to stanie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '7', '7', 'Ale on powiedział: Baczcież żebyście nie byli zwiedzeni, boć wiele ich przyjdzie w imię moje mówiąc: Jaciem jest Krystus, a czasci się on przybliża, nie chodźcież tedy za nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '8', '8', 'A gdy też usłyszycie walki i niesnaski, nie trwóżcież się. Abowiemci potrzeba aby to wprzód było, aleć nie wnet koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '9', '9', 'Zatym rzekł ku nim: Powstanieć naród przeciw narodowi i królestwo przeciw królestwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '10', '10', 'A będą wielkie trzęsienia ziemie na każdzych miejscach i głód i powietrza a strachy i znaki wielkie z nieba będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '11', '11', 'Aleć przed wszytkiem tym rzucą się na was rękoma swemi i będą was prześladować, podawając do bożnic i do więzienia, a wodząc przed króle i przed starosty, dla imienia mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '12', '12', 'A tyć rzeczy wam będą na świadectwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '13', '13', 'A przetoż miejcie to w sercach waszych, abyście nie myślili czymby się bronić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '14', '14', 'Abowiemci wam dam usta i mądrość, której nie będą mogli odeprzeć ani się sprzeciwić wszyscy, którzy się wam zastawować będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '15', '15', 'Będziecie też wydani od rodziców, od braciej i od krewnych i od przyjaciół, a niektóre z was o śmierć przyprawią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '16', '16', 'I będziecie przemierzłymi u wszytkich dla imienia mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '17', '17', 'Aleć i włos z głowy waszej nie zginie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '18', '18', 'Przez waszę cierpliwość odzierżawajcie dusze wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '19', '19', 'A gdy ujźrzycie Jeruzalem wojskami obtoczone, tedy poznajcie żeć bliskie jest spustoszenie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '20', '20', 'Tedy  ci co są w Juda, niech zuciekają na góry, a którzy są w pośrzód jego, niech wynidą, a ci co są na polach, niechaj nie wchodzą do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '21', '21', 'Abowiemci są ty dni pomsty, aby się wypełniło wszytko co jest napisano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '22', '22', 'Biada tedy będzie brzemiennym i dziateczkam zsącym w onych czasiech, abowiem będzie ścisk wielki w tej ziemi, a gniew w ludu tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '23', '23', 'I polegą od miecza, a będą odwiedzieni do więzienia między wszytki narody i będzie Jeruzalem podeptane od poganów, aż się też wypełnią czasy ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '24', '24', 'Tedyć będą znaki na słońcu, miesiącu i gwiazdach, a na ziemi uciśnienie narodów, gdy nie stanie rady, a gdy zaszumi morze i wały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '25', '25', 'Tak, iż się ludzie z duszę wylękną, przed strachem i oczekowaniem nieszczęścia przychodzącego na okrąg ziemię. Abowiem mocy niebieskie chwiać się będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '26', '26', 'A tedyć ujźrzą Syna człowieczego przychodzącego w obłoku z mocą i chwałą wielką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '27', '27', 'To gdy się dziać pocznie, poglądajcież a podnoście głowy wasze, iżci się już przybliża odkupienie wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '28', '28', 'I powiedział im też to podobieństwo: Pojźrzycież na figowe drzewo i na wszytki drzewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '29', '29', 'Gdy już wypuszczają listki, tedy wy sami z siebie poznawacie żeć już blisko lato.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '30', '30', 'Także i wy gdy ujźrzycie iż się to będzie działo, poznawajcie żeć blisko jest królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '31', '31', 'Zaprawdę wam powiedam: Żadnymci sposobem nie przeminie ten wiek, ażdy się to wszytko stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '32', '32', 'Nieboć i ziemia przeminie, ale słowa moje żadnym sposobem nie przeminą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '33', '33', 'Bądźcież tedy tego pilni sami u siebie, aby kiedy nie były ociążone serca wasze obżarstwem i opilstwem i staraniem tego żywota, żebyć na was z prędka nie przyszedł dzień on.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '34', '34', 'Abowiemci jako sidło przypadnie na wszytki, którzy mieszkają po wszytkiej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '35', '35', 'A przetoż czujcie modląc się na każdy czas, aby was miano godnemi, którzybyście uszli tego wszytkiego co przyjdź ma, a iżbyście stanęli przed Synem człowieczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '36', '36', 'A tak we dnie uczył w kościele, a w nocy wyszedszy przemieszkawał na górze, którą zową oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '20', '37', '37', 'Ale wszytek lud raniuczko się schodził do niego, aby go w kościele słuchał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '0', '0', 'A było blisko święto Przaśników, które zową Wielkanoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '1', '1', 'I szukały tego książęta kapłańskie i doktorowie jakoby go zabić, ale się ludu pospolitego bali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '2', '2', 'Wszedł tedy szatan w Judasza, którego zwano Iskarjotem, z liczby dwunaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '3', '3', 'Który szedszy zmówił się z książęty kapłańskimi i z przełożonymi, jakoby go im wydać miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '4', '4', 'Którzy temu byli radzi i obiecali dać jemu pieniądze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '5', '5', 'Za co im on przyrzekł i szukał czasu jakoby go im wydał przez ludu pospolitego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '6', '6', 'Zatym przyszedł dzień Przaśników, w który było potrzeba obchodzić Wielkanoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '7', '7', 'Posłał tedy Piotra i Jana mówiąc: Szedszy zgotujcie nam Wielkanoc, abychmy jej pożywali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '8', '8', 'A oni jemu rzekli: Gdzież chcesz bychmy ją nagotowali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '9', '9', 'Ale on rzekł do nich: Oto gdy wy wnidziecie do miasta, zajdzie wam niektóry niosąc dzban wody. Idźcież za nim do domu, w który wnidzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '10', '10', 'A powiedzcie gospodarzowi domu onego: Kazałci Mistrz powiedzieć: Gdzież jest gospoda kędyby miał pożywać Wielkiejnocy z zwolenniki swemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '11', '11', 'Tedyć on wam ukaże Salę wielką obitą, tamże nagotujcie Wielkanoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '12', '12', 'A tak szedszy wszytko to naleźli jako im powiedział i zgotowali Wielkanoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '13', '13', 'Potym gdy on czas postanowiony przyszedł, tedy siadł i dwanaście Apostołów z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '14', '14', 'I rzekł do nich: Z chuciam żądał pożywać tej Wielkiejnocy z wami, przed tym niż ja cierpieć będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '15', '15', 'A powiedam wam, że już więcej nie będę tego używał, ażby się wypełniło w królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '16', '16', 'I wziąwszy kubek, a uczyniwszy dzięki rzekł: Weźmicie to, a podzielcie między się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '17', '17', 'Abowiem wam to powiedam: Żeć nie będę pił z owocu winnego, aż przyjdzie królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '18', '18', 'I wziąwszy chleb, gdy uczynił dzięki, łamał i dał im mówiąc: To jest ciało, które się za was dawa, to czyńcie na wspominanie moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '19', '19', 'Także im też dał i kubek gdy odwieczerzał mówiąc: Ten kubek jest ci on testament nowy przez krew moję, która się za was wylewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '20', '20', 'Wszakoż oto ręka zdradzającego mię ze mną jest na stole.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '21', '21', 'Aczci Syn człowieczy, jako jest postanowiono, idzie. Wszakoż biada człowiekowi onemu, przez którego ma być zdradzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '22', '22', 'Tedy oni poczęli się między sobą społu pytać, któryby to z nich był coby to uczynić miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '23', '23', 'Wszczął się też między nimi poswarek, ktoby miał być więczszym z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '24', '24', 'Ale on im powiedział: Królowie narodów panująć nad nimi, a którzy nad nimi moc mają, zową się dobrodziejmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '25', '25', 'Leczci wy nie tak owszem, który jest między wami nawięczszym, ten niech będzie namniejszym, a który jest przedniejszym, niech będzie jako ten co służy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '26', '26', 'Abowiem któż jest więczszy? Tenli co siedzi, czy ten co służy? Izali nie ten, który siedzi? Aleciem ja jest w pośrzodku was jako ten co służy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '27', '27', 'A wy jesteście, którzyście trwali przy mnie w pokusach moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '28', '28', 'A przeto jako mi odkazał ociec mój królestwo, tak ja też wam odkazuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '29', '29', 'Abyście jedli i pili na stole mym w królestwie mym i siedzieli na stolicach sądząc dwojenaście pokolenie izraelskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '30', '30', 'Zatym też rzekł Pan: Symon, Symon, oto szatan pożądał pilnie, aby was odwiewał jako pszenicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '31', '31', 'Alem ja prosił za tobą, aby nie ustała wiara twa, a przetoż i ty niekiedy nawróciwszy się, potwierdzaj bracią twą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '32', '32', 'Tedy on rzekł: Panie z tobą jestem gotów idź i do więzienia i na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '33', '33', 'Lecz mu on powiedział: Pietrze powiedamci, nie zapoje dziś kur, aż się trzy kroć zaprzysz, że mię nie znasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '34', '34', 'Zatym rzekł do nich: Natenczas, gdym was posyłał bez mieszka, bez sumek i bez trzewików, zali wam czego nie dostawało? Tedy rzekli: Nic.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '35', '35', 'A tak im powiedział: Terazci już jeśli kto ma mieszek, niechże go weźmie, także i sumki, a kto nie ma, niech przeda płaszcz swój i kupi miecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '36', '36', 'Abowiemci wam powiedam: Jeszcze potrzeba, aby się we mnie wypełniło ono Pismo: I policzon jest między złośniki, boć ty rzeczy co o mnie są napisane, koniec swój mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '37', '37', 'Tedy oni rzekli: Panie oto tu dwa miecze. A on im powiedział: Dosyć jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '38', '38', 'I wyszedszy jako był zwykł, szedł na górę oliwną, a za nim też szli zwolennicy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '39', '39', 'A gdy przyszedł na ono miejsce rzekł im: Módlcież się abyście nie przyszli w pokuszenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '40', '40', 'On tedy odszedł od nich jakoby mógł rzucić kamieniem, a klęknąwszy na kolana modlił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '41', '41', 'Mówiąc: Ojcze jeśliż chcesz przenieś ode mnie ten kubek, a wszakoż nie wola moja, lecz się twa niechaj stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '42', '42', 'Ukazał się mu tedy Anioł z nieba posilając go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '43', '43', 'Ale będąc w utrapieniu złożony, tym pilniej się modlił, a pot jego był jako krople krwie padające na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '44', '44', 'A gdy wstał od modlitwy, przyszedł do swych zwolenników i nalazł je śpiące przed frasunkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '45', '45', 'I rzekł do nich: Przeczże śpicie? Wstańcież a módlcie się, byście nie przyszli na pokuszenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '46', '46', 'Tego gdy jeszcze domawiał, oto wielkość ludzi i ten, którego zwano Judasz jeden ze dwunaście przed nimi szedł, a przystąpił się ku Jezusowi aby go pocałował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '47', '47', 'Rzekł mu Jezus: Judaszu pocałowaniem zdradzasz Syna człowieczego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '48', '48', 'A widząc ci, którzy przy nim byli co się dziać miało rzekli do niego: Panie mamyli bić mieczem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '49', '49', 'Tedy niektóry z nich uderzył sługę książęcia kapłańskiego i uciął mu ucho prawe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '50', '50', 'Ale Jezus odpowiedziawszy rzekł: Zaniechajcież na ten czas i tknąwszy ucha jego, uzdrowił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '51', '51', 'Zatym rzekł Jezus do onych książąt kapłańskich i przełożonych kościoła i do starszych, którzy byli kniemu przyszli: Wyszliście jako na zbójce z mieczmi i z kijmi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '52', '52', 'Gdyżem ustawicznie bywał z wami w kościele a nie śćiągnęliście rąk na mię. Ale ta oto jest ona godzina wasza i moc ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '53', '53', 'Wiedli go tedy pojmawszy i przyprowadzili go w dom nawyższego kapłana, a Piotr za nim szedł z daleka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '54', '54', 'I rospaliwszy ogień w pośrzód dworu, siedli społu, gdzie też i Piotr siadł między nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '55', '55', 'Tam gdy go ujźrzała niektóra służebnica u ognia siedzącego, pilnie się mu przypatrując rzekła: I tenci z nim był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '56', '56', 'Ale się go on zaprzał mówiąc: Niewiasto nie znamci go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '57', '57', 'Potym maluczko ujźrzawszy go drugi rzekł: I ty jesteś z onych? Ale Piotr rzekł: Człowiecze nie jestciem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '58', '58', 'A gdy było jakoby w godzinie, tedy drugi z nich twierdził tak mówiąc: Zaprawdę i ten z nim był, bo jest i Galilejczyk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '59', '59', 'Ale Piotr powiedział: Człowiecze, ani wiem co mówisz i natychmiast póki jeszcze tego domawiał, zapiał kur.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '60', '60', 'Tedy Pan obróciwszy się wejźrzał na Piotra, a rospomniał Piotr na słowo Pańskie jako mu był powiedział, iż przedtym niż kur miał zapiać, trzy kroć się mnie zaprzysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '61', '61', 'I wyszedszy Piotr precz, gorzko płakał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '62', '62', 'Ci tedy, którzy społu trzymali Jezusa, nagrawali go bijąc go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '63', '63', 'A zasłoniwszy go, bili oblicze jego, pytając go a mówiąc: Gadajże kto jest ten, który cię uderzył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '64', '64', 'I wiele inych rzeczy sromocąc go mówili nań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '65', '65', 'Potym gdy dzień nastał zeszli się starszy z ludu i książęta kapłańskie i doktorowie, a przywiedli go przed zgromadzenie swe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '66', '66', 'Mówiąc: Powiedzże nam jeśliżeś ty jest Krystus. Ale on powiedział: Chociażcibych ja wam i powiedział, tedy wy żadnym sposobem wierzyć nie będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '67', '67', 'A jeślibych też was ocz pytał, żadnym sposobem mi nie odpowiecie, ani mię opuścicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '68', '68', 'A tak od tegoć czasu będzie Syn człowieczy siedział na prawicy mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '69', '69', 'Zatym oni rzekli: A więceś ty jest Syn Boży? A on rzekł do nich: Wy powiedacie żem jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '21', '70', '70', 'Oni tedy rzekli: I cóż jeszcze potrzebujem świadectwa? Otośmy już sami słyszeli z ust jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '0', '0', 'Tedy wstawszy wszytka ona wielkość, wiodła go do Piłata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '1', '1', 'I poczęli nań skarżyć mówiąc: Otochmy znaleźli, iż ten wzburza lud, a nie każe dań dawać cesarzowi powiedając, że on jest Krystus Król.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '2', '2', 'I pytał go Piłat mówiąc: A tyżeś jest on Król Żydowski? Na co mu on odpowiedziawszy rzekł: Ty powiedasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '3', '3', 'A tak Piłat rzekł do książąt i do ludu pospolitego: Żadnego ja przewinienia nie najduję w tym człowieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '4', '4', 'Lecz oni się więcej dopierali mówiąc: Burzy lud ucząc po wszytkim Juda, począwszy od Galilejej aż dotąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '5', '5', 'I ujźrzawszy Piłat, że wspominali Galileą, pytał go jeśliby był Galilejczyk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '6', '6', 'A gdy zrozumiał, iż należał pod zwierzchność Herodowę, odesłał go do Heroda, który też i sam w Jeruzalem był przez ony dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '7', '7', 'I ujźrzawszy Herod Jezusa, radował się barzo, bo go pragnął widzieć od niemałego czasu, dlatego, iż wiele o nim słychał i nadziewał się, że miał widzieć jaki znak przezeń uczyniony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '8', '8', 'Pytał go tedy wielem słów, ale on jemu nic nie odpowiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '9', '9', 'A książęta kapłańskie i doktorowie stali, barzo mocnie skarżąc się nań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '10', '10', 'A także Herod z swym pocztem za nic go sobie nie miał i naszydziwszy się z niego, dał go oblec w szaty świetne, a odesłał go do Piłata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '11', '11', 'I zstali się sobie przyjacioły Piłat z Herodem onegoż to dnia, bo byli przed tym nieprzyjacielmi między sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '12', '12', 'Tedy Piłat wezwawszy ksobie książąt kapłańskich, przełożonych i ludu pospolitego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '13', '13', 'Rzekł do nich: Podaliście mi człowieka tego jakoby lud burzyć miał, a oto ja pytając się o nim przed wami, żadnego przewinienia w tym człowieku nie najduję, w tym w czym wy go winujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '14', '14', 'Ktemu ani Herod, bowiem go odesłał do nas, oto nie uczynił nic dla czegoby był śmierci godzien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '15', '15', 'A przetoż go starawszy wypuszczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '16', '16', 'I potrzeba bywało, że im na święto wypuszczał jednego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '17', '17', 'Ale wszytek lud wołał społu mówiąc: Daj stracić tego, a puść nam Barabasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '18', '18', 'Który był dla niejakiego rostyrku w mieście uczynionego i dla mordestwa wrzucon do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '19', '19', 'Ale Piłat mówił do nich powtóre, żeby im był puścił Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '20', '20', 'Lecz tym barziej wołali mówiąc: Ukrzyżuj, ukrzyżuj go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '21', '21', 'A on po trzecie rzekł do nich: I cóż wżdy on złego uczynił? Żadnegom przewinienia na śmierć w nim nie nalazł, a tak go starawszy puszczę wolno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '22', '22', 'Ale się oni dopierali, prosząc głosem wielkim, aby był ukrzyżowan i tym się więcej głosy ich i książąt kapłańskich wzmagały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '23', '23', 'Tedy Piłat przysądził, aby tak było jako żądali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '24', '24', 'I wypuścił onego, który był dla rostyrku i mordu podan do więzienia, o którego prosili, a Jezusa podał na wolą ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '25', '25', 'Gdy go tedy wiedli, pojmawszy Symona niejakiego Cyrenejczyka, który ze wsi szedł, włożyli nań krzyż, aby go niósł za Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '26', '26', 'I szło za nim barzo wiele ludu i niewiast, które go płakały, i żałowały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '27', '27', 'Tedy się Jezus obróciwszy do nich rzekł: Córki Jerozolimskie nie nade mną płaczcie, ale nad samy sobą i nad dziatakmi waszemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '28', '28', 'Abowiemci oto przyjdą dni, w których będą mówić: Szczęśliwesz są niepłodne i których żywoty nie rodziły, ani piersi mleka dawały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '29', '29', 'Tedyć poczną mówić góram: Upadnicie na nas, a pagórkam: Okryjcie nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '30', '30', 'Abowiem jeślić to na surowym drzewie czynią, a cóż na suchem będzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '31', '31', 'Wiedziono też z nim i drugich dwu złoczyńców, aby je z nim stracono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '32', '32', 'A tak gdy przyszli na miejsce, które zową Trupich głów, tam go ukrzyżowali z onymi złoczyńcami. Jednego po prawej, a drugiego po lewej stronie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '33', '33', 'I mówił Jezus: Ojcze odpuść im boć nie wiedzą co czynią. A rozdzieliwszy szaty jego, puścili o nie los.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '34', '34', 'Stał tedy lud dziwując się, a książęta kapłańskie śmieszki sobie z niego stroili i z nimi mówiąc: Ine wybawiał, niechże się i sam wybawi, jeśliżeć ten jest on Krystus Boży wybrany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '35', '35', 'Także i służebni szydzili z niego przychodząc, a podawali mu pić ocet.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '36', '36', 'Mówiąc: Jeśliżeś ty jest Król Żydowski, wybawże się sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '37', '37', 'Był też nad nim napis wypisany greckiemi, rzymskiemi i żydowskimi literami: Tenci jest on Król Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '38', '38', 'Tamże jeden z onych złoczyńców co byli obieszeni, sromocił go mówiąc: Jeśliżeś ty jest on Krystus, wybawże i samego siebie i nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '39', '39', 'Ale odpowiedziawszy drugi, fukał go mówiąc: I nie boiszże się ty Boga, gdyżeś jest w tymże skazaniu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '40', '40', 'A iście my sprawiedliwie, bo godne bierzem za swe sprawy karanie, aleć ten nic złego nie uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '41', '41', 'Zatymże rzekł do Jezusa: Panie pomni na mię, gdy przyjdziesz do królestwa twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '42', '42', 'Tedy mu powiedział Jezus: Zaprawdę powiedam tobie: Dziś ze mną będziesz w raju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '43', '43', 'A było jakoby o szóstej godzinie, że się stały ciemności po wszytkiej ziemi, aż do dziewiątej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '44', '44', 'I zaćmiło się słońce, a zasłona kościelna rozdarła się w pośrzodku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '45', '45', 'Zawołał tedy Jezus głosem wielkim, a rzekł: Ojcze w ręce twe podawam ducha mojego. A to rzekszy skonał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '46', '46', 'To gdy ujźrzał setnik co się działo, chwalił Boga mówiąc: Zaprawdęć to był człowiek sprawiedliwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '47', '47', 'Także lud wszytek, który się był społu zszedł dziwować temu, widząc to co się stało, bił się w piersi i nawracał się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '48', '48', 'A wszyscy jego znajomi z daleka stali i niewiasty, które były społu za nim przyszły od Galilejej, przypatrując się temu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '49', '49', 'Tedy oto mąż niektóre imieniem Józef Senator, a dobry i sprawiedliwy człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '50', '50', 'Który nie zezwalał na radę i na uczynek ich, a był z Arymatjej miasta judzkiego, który też sam oczekawał królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '51', '51', 'Ten przyszedszy do Piłata prosił go o ciało Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '52', '52', 'I ziąwszy je uwinął w prześcieradło, a położył w grobie, który był z kamienia wyciosany, w którym jeszcze nigdy żaden nie był położon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '53', '53', 'A był dzień przygotowania na święto i dzień szabatu nastawał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '54', '54', 'Szły też za nim niewiasty, które były z nim przyszły z Galilejej, aby widziały grób i jako było położono ciało jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '22', '55', '55', 'A tak wróciwszy się nagotowały wonnych rzeczy i olejków, ale w szabat według roskazania zaniechały tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '0', '0', 'Tedy dnia pirwszego, tego dnia onego barzo raniuczko przyszły do grobu, niosąc ony rzeczy wonne, które były nagotowały i były też niektóre z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '1', '1', 'I znalazły kamień odwalony od grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '2', '2', 'A wszedszy nie nalazły ciała Pana Jezusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '3', '3', 'I stało się, gdy będąc zatrwożone w tej rzeczy wątpiły, oto dwa mężowie stanęli przy nich, będąc obleczenia w szaty błyszczące się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '4', '4', 'A gdy się ony lękły, a schyliły twarz ku ziemi, rzekli do nich: Przeczże między umarłymi szukacie tego, który jest żyw?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '5', '5', 'Niemaszci go tu, aleć wstał, wspomnicież sobie jako wam powiedał, gdy jeszcze był w Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '6', '6', 'Mówiąc: Potrzebać aby Syn człowieczy był wydan w ręce ludzi grzesznych, a żeby był ukrzyżowan, a trzeciego zasię dnia aby zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '7', '7', 'Tedy ony sobie wspomniały na słowa jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '8', '8', 'I wróciwszy się od grobu, oznajmiły to wszytko onym jedennaściom i inym wszytkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '9', '9', 'A byłać Marja Magdalena i Joanna i Marja Jakubowa matka i ine z nimi, które to powiedały Apostołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '10', '10', 'Ale się im zdały jakoby błazeńskie słowa ich i nie wierzyli im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '11', '11', 'Wszakoż Piotr wstawszy bieżał ku grobowi, a gdy w wnątrz wejźrzał, obaczył tylko sami prześcieradła leżące i odszedł dziwując się sam u siebie tej sprawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '12', '12', 'A oto dwa z nich tegoż dnia szli do miasteczka, które było na sześcidziesiąt stajan od Jeruzalem, a zwano je Emmaus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '13', '13', 'I rozmawiali z sobą o tym wszytkim co się było przydarzyło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '14', '14', 'A gdy tak rozmawiali i społu się pytali, tedy Jezus przybliżywszy się szedł społu z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '15', '15', 'Ale oczy ich były zatrzymane, aby go nie poznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '16', '16', 'Tedy rzekł do nich: Cóż to są za rozmowy, które idąc macie między sobą, a przeczżeście smętnej twarzy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '17', '17', 'I odpowiedając mu jeden, któremu było imię Kleofas rzekł: I ty sam przemieszkawasz w Jeruzalem, a nie wiesz co się w nim przez ty dni działo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '18', '18', 'I rzekł im: A cóż to? A oni rzekli jemu: O Jezusie Nazareńskim, który był prorok mocny w sprawach i w słowiech, przed Bogiem i wszytkim ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '19', '19', 'A jako go książęta kapłańskie i urząd nasz wydali pod skazanie na śmierć i ukrzyżowali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '20', '20', 'A mychmy się nadziewali, iż to on był, który miał odkupić Izrael, a temu wszytkiemu już to dziś trzeci dzień wychodzi, jako się to stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '21', '21', 'Lecz i niewiasty niektóre z naszych przestraszyły nas, które raniuczko były u grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '22', '22', 'I nie znalazszy ciała jego, przyszły powiedając, iż widzenie Anielskie widziały, którzy powiedzieli iżby żyw był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '23', '23', 'A tak niektórzy co z nami byli chodzili do grobu i tak znaleźli jako niewiasty powiedały, ale samego nie widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '24', '24', 'Tedy on rzekł do nich: O głupi, a nierychłego serca ku wierzeniu temu wszytkiemu co powiedzieli Prorocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '25', '25', 'Izali tego nie było potrzeba cirpieć Krystusowi i wnidź do chwały swojej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '26', '26', 'A począwszy od Mojżesza i wszytkich proroków, wykładał im we wszytkich piśmiech, co o nim napisano było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '27', '27', 'A gdy byli blisko miasteczka, do którego szli, tedy on tak zmyślał jakoby miał dalej idź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '28', '28', 'Ale go oni przynucili mówiąc: Zostań z nami, boć już jest tuż k wieczoru i schodzi dzień. A także wyszedł, aby został z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '29', '29', 'I zstało się, gdy on siedział z nimi, wziąwszy chleb błogosławił, a złamawszy dał im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '30', '30', 'Otworzyły się tedy oczy ich i poznali go, ale on zniknął od ich obliczności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '31', '31', 'I mówili między sobą: Izali serce nasze nie pałało w nas gdy mówił z nami w drodze i gdy nam otwirał Pisma?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '32', '32', 'A tak tegoż czasu wstawszy, wrócili się do Jeruzalem i znaleźli zgromadzonych jedennaście i ony, którzy z nimi byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '33', '33', 'Powiedając im: Wstałci Pan prawdziwie i ukazał się Symonowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '34', '34', 'Powiedzieli im też co się działo w drodze i jako od nich był poznan w łamaniu chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '35', '35', 'O tym gdy oni mówili, tedy Jezus stanął w pośród ich i rzekł im: Pokój wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '36', '36', 'Zlęknąwszy się tedy i strwożywszy mnimali, iż ducha widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '37', '37', 'Ale on rzekł do nich: Przeczżeście się zatrwożyli, a co za myśli nadchodzą serca wasze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '38', '38', 'Oglądajcie ręce moje i nogi moje, bomci ja sam jest, dotknicie się mnie a obaczcie, boć duch nie ma ciała ani kości, jako widzicie, że ja mam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '39', '39', 'I rzekszy to, ukazał im ręce i nogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '40', '40', 'Lecz gdy jeszcze oni nie wierzyli przed radością, a iż się dziwowali rzekł im: Macie wżdy tu co jeść?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '41', '41', 'I dali mu pół ryby pieczonej i sztukę plastru miodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '42', '42', 'Co on wziąwszy jadł przed nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '43', '43', 'Zatym rzekł do nich: Tyć są słowa, którem mówił do was, pókim jeszcze był z wami, iż było potrzeba wypełnić się wszytkiemu co napisano jest w zakonie Mojżeszowym, w prorokach i w psalmach o mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '44', '44', 'Otworzył tedy smysł ich, iż zrozumieli Pisma i rzekł do nich:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '45', '45', 'Takci jest napisano i tak było potrzeba Krystusowi cirpieć i wstać od umarłych dnia trzeciego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '46', '46', 'Aby w imię jego było opowiedano uznanie i odpuszczenie grzechów u wszytkich narodów, począwszy od Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '47', '47', 'A wy jesteście świadkowie tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '48', '48', 'I otoć ja poślę obietnicę Ojca mego na was. Mieszkajcież wy tedy w mieście Jeruzalem, aż będziecie obleczeni mocą z wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '49', '49', 'Zatymże je wiódł precz, aż ku Betanijej, a podniózszy ręce swe, błogosławił im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '50', '50', 'I zstało się, gdy im błogosławił, rozstał się z nimi, a był niesion w zgórę do nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '51', '51', 'Oni tedy dawszy mu chwałę, wrócili się do Jeruzalem z weselem wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'lk', '23', '52', '52', 'I byli zawżdy w kościele chwaląc i błogosławiąc Boga. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '0', '0', 'Na początku było Słowo, a Słowo ono było u Boga i było ono Słowo Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '1', '1', 'To Słowo było na początku u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '2', '2', 'Wszytkoć się przez to Słowo stało, a oprócz niego nic się nie stało, cóżkolwiek było uczyniono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '3', '3', 'W nimci był żywot, a żywot był światłość ona ludzka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '4', '4', 'A tać światłość w ciemnościach świeci, ale jej ciemności nie ogarnęły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '5', '5', 'Był człowiek posłany od Boga, któremu było imię Jan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '6', '6', 'Ten przyszedł ku świadectwu, aby świadczył o światłości, aby przezeń wszyscy wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '7', '7', 'Nie byłci on światłością oną, ale był posłan iżby świadczył o onej światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '8', '8', 'Toć było ona światłość prawdziwa, która oświeca wszelkiego człowieka na świat przychodzącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '9', '9', 'Na świecie był i przezeń świat sprawion jest, leczci go świat nie poznał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '10', '10', 'Przyszedł do swoich, ale go swoi nie przyjęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '11', '11', 'A ilekolwiek ich go przyjęli, dał im tę godność, aby się stali synmi Bożymi, tym którzyby wierzyli w imię jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '12', '12', 'Którzy nie ze krwie, ani z chciwości ciała, ani z chciwości męża, lecz z Boga są narodzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '13', '13', 'A Słowoć ono Ciałem się stało i przemieszkawało między nami i widzielichmy chwałę jego, chwałę jako jedynorodnego od Ojca, napełnione łaski i prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '14', '14', 'Janci świadczył o nim i obwoływał mówiąc: Tenci jest, o którymem powiedał, Ten, który po mnie idzie, jest przedniejszym nad mię, bo jest zacniejszym niż ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '15', '15', 'A z pełności jego wszyscychmy brali i łaskę za łaskę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '16', '16', 'Abowiem zakon przez Mojżesza jest dan, a łaska i prawda przez Jezusa Krystusa się stała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '17', '17', 'Żadenci nigdy Boga nie widział, lecz on jedynorodny syn, który jest w łonie Ojcowym, ten nam opowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '18', '18', 'A taktoć jest świadectwo Janowe, gdy posyłali Żydowie z Jeruzalem kapłany i Lewity pytając go: Ty coś jest zacz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '19', '19', 'I wyznałci a nie zaprzał, a wyznał że on nie był Krystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '20', '20', 'Tedy go pytali: Cóżeś wżdy jest? I Heljasz żeś ty? A on rzekł: Nie jestem. A oni rzekli: Izaś jest prorokiem onym? A on odpowiedział: Nie jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '21', '21', 'Ale mu oni rzekli: Któżeś wżdy jest? Żebychmy dali odpowiedź tym, którzy nas posłali i cóż wżdy powiedasz o sobie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '22', '22', 'Tedy on rzekł: Jam jest głos wołającego na puszczy; Przeprawujciesz drogę Pańską, jako powiedział Ezajasz prorok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '23', '23', 'A cić, którzy byli posłani, byli z Faryzeuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '24', '24', 'I pytali go a mówili mu : Czemuż tedy krzcisz, gdyżeś ty nie jest Krystus, ani Heljasz, ani prorok on?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '25', '25', 'A odpowiedział im Jan mówiąc: Jać krzczę wodą, aleć w pośród was stoi, o którym wy nie wiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '26', '26', 'Onci jest który po mnie idzie, który jest przedniejszy nad mię, któremu ja nie jestem godzien rozwięzać rzemienia u trzewików.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '27', '27', 'A toć się działo w Betabara za Jordanem, gdzie Jan krzcił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '28', '28', 'Nazajutrz ujźrzał Jan Jezusa idącego do siebie i rzekł: Oto Baranek Boży, który gładzi grzech tego świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '29', '29', 'Tenci jest, o którymem powiedał: Idzie po mnie mąż, który jest przełożon nad mię, bo zacniejszym jest niźli ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '30', '30', 'A jaciem go nie znał, ale iżby był objawion Izraelowi, dlategom przyszedł krzcząc wodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '31', '31', 'Tedy Jan świadczył mówiąc: Widziałem Ducha zstępującego jako gołębicę z nieba, który też został nad nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '32', '32', 'I nie poznałciem go ja, ale ten, który mię posłał, abym krzcił wodą, ten mi powiedział: Nad którym ujźrzysz Ducha zstępującego i nad nim zostawającego, tenci jest, który krzci Duchem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '33', '33', 'A przetożciem ja widział i świadczę, że ten jest on Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '34', '34', 'Potym zasię nazajutrz stał Jan i dwa z zwolenników jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '35', '35', 'I wejźrzawszy na Jezusa idącego rzekł: Oto Baranek on Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '36', '36', 'To gdy usłyszeli dwa jego zwolennicy co powiedział, szli za Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '37', '37', 'Obróciwszy się tedy Jezus, a ujźrzawszy je za sobą idące, rzekł do nich: Czegóż szukacie? A oni rzekli: Rabbi (co jeślibyś wykładał jest Mistrzu) gdzież mieszkasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '38', '38', 'Tedy im rzekł: Pódźcież a oglądajcie. A tak szli i widzieli gdzie mieszkał, a zostali przy nim onego dnia, bo była jakoby dziesiąta godzina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '39', '39', 'A był Andrzej brat Symona Piotra, jeden z onych dwu, którzy to słyszeli od Jana, a szli byli za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '40', '40', 'Ten napirwej nalazszy Symona brata swego, powiedział mu: Naleźliciechmy Mesjasza onego, co jest, jeślibyś wykładał, Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '41', '41', 'I przywiódł go do Jezusa, a wejźrzawszy nań Jezus, rzekł: Tyś jest Symon syn Jonasów, ty będziesz zwan Kefas, co się znaczy Kamień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '42', '42', 'Nazajutrz chciał Jezus idź do Galilejej, a znalazszy Filipa, rzekł mu: Pódź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '43', '43', 'A Filip był z Betsajda, z miasta Andrzejowego i Piotrowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '44', '44', 'Potym Filip znalazszy Natanaela, powiedział mu: Znaleźliciechmy onego, o którym pisał Mojżesz w zakonie i prorocy, onego Jezusa Nazareńskiego syna Józefowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '45', '45', 'Na to mu powiedział Natanael: I możesz być od Nazaret co dobrego? Tedy mu rzekł Filip: Pódź a oglądaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '46', '46', 'Ujźrzawszy tedy Jezus Natanaela idącego do siebie, powiedział o nim: Tenci jest zaprawdę Izraelita, w którym nie masz zdrady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '47', '47', 'I rzekł mu Natanael: Skądże mię znasz? A Jezus odpowiedziawszy, rzekł mu: Jeszcze pirwej niż cię tu Filip wezwał, gdyś pod figowym drzewem był, widziałem cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '48', '48', 'A odpowiedziawszy Natanael rzekł: Mistrzu, Tyś jest on Syn Boży, Tyś jest on Król izraelski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '49', '49', 'Tedy mu odpowiedział Jezus i rzekł: Żem tobie mówił, widziałem cię pod figowym drzewem, dlatego wierzysz? Jeszcze nad ty więtsze rzeczy ujźrzysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '0', '50', '50', 'Zatymże mu powiedział: Zaprawdę, zaprawdę wam powiedam, od tego czasu będziecie widzieć niebo otworzone i Anioły Boże wstępujące i zstępujące, nad Syna człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '0', '0', 'Tedy dnia trzeciego było sprawowano wesele w Kanie Galilejskim miasteczku, gdzie też była i matka Jezusowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '1', '1', 'A był też wezwan Jezus i zwolennicy jego na wesele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '2', '2', 'Tam gdy nie zstało wina, rzekła matka Jezusowa do niego: Wina nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '3', '3', 'Ale jej rzekł Jezus: Cóżmi do ciebie niewiasto: jeszczeć nie przyszła godzina moja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '4', '4', 'Zatym rzekła matka jego sługam: Cokolwiek wam rzecze, czyńcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '5', '5', 'I były tam sześć stągiew kamiennych według oczyściania Żydowskiego postawionych, biorąc każda w się dwa abo trzy dzbany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '6', '6', 'I rzekł im Jezus: Nalejcie pełne ty stągwie wody, a tak nalali je pełne aż i z wierzchem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '7', '7', 'Tedy im rzekł: Czyrpajcież teraz a podajcie przedniejszemu sprawcy, a tak oni podali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '8', '8', 'Skoro tedy skosztował przedniejszy sprawca wody onej, która się zstała winem ( a nie wiedział skądby było, lecz słudzy wiedzieli, którzy wodę czyrpali ) wezwał k sobie oblubieńca onto przedniejszy sprawca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '9', '9', 'I rzekł mu: Wszyscyć naprzód dawają wino dobre, a gdy się goście wedle potrzeby napiją, tedy to co jest podlejsze, a tyś dobre wino chował aż do tąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '10', '10', 'Tenci początek cudów uczynił Jezus w Kanie miasteczku Galilejskim, aby oznajmił chwałę swą i uwierzyli weń zwolennicy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '11', '11', 'Potym szedł do Kapernaum sam i matka i bracia i zwolennicy jego, a mieszkali tam przez mały czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '12', '12', 'Abowiem była blisko Wielkanoc Żydowska, a dlategoż szedł Jezus do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '13', '13', 'I znalazł w kościele ony, którzy przedawali woły, owce i gołąbie, ktemu i bankierze siedzące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '14', '14', 'A uczyniwszy bicz z powrozów, wygnał wszytki z kościoła, a owce i woły i pieniądze bankierzów rosypał, a stoły zprzewracał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '15', '15', 'Także i tym co gołębie przedawali rzekł: Wynieście to ztąd, a nie czyńcie z domu Ojca mego, domu kupieckiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '16', '16', 'I przypomnieli sobie zwolennicy jego to, co było napisano: Zapalczywa miłość k domowi twemu żarła mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '17', '17', 'A tak Żydowie odpowiedziawszy rzekli mu: Cóż za znak okazujesz nam, iż to czynisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '18', '18', 'A Jezus odpowiedziawszy rzekł im: Rozwalcie ten kościół, a ja ji we trzech dnioch wystawię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '19', '19', 'Tedy rzekli Żydowie: Przez czterdzieści i sześć lat budowan jest ten kościół, a ty go chcesz we trzech dnioch wystawić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '20', '20', 'Ale on mówił o kościele ciała swojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '21', '21', 'I gdy potym zmartwychwstał, wspomjonęli zwolennicy jego, iż im to powiedał i uwierzyli Pismu i słowu, które mówił Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '22', '22', 'Tamże gdy był w Jeruzalem na dzień święty Wielkonocny, wiele ich uwierzyli w imię jego, patrząc na jego cuda, które okazował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '23', '23', 'Ale sam Jezus nie zwierzał się im dlatego, iż znał wszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '1', '24', '24', 'A nie było mu potrzeba, aby był kto miał dać jemu świadectwo o człowieku, abowiem on wiedział co było w człowieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '0', '0', 'Był też niektóry z Faryzeuszów, któremu było imię Nikodem, książę żydowskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '1', '1', 'Ten przyszedł do Jezusa w nocy i rzekł mu: Mistrzu, wiemy żeś przyszedł od Boga Mistrzem, bo żaden tych znamion czynić nie może, które ty okazujesz, jeśliby z nim Bóg nie był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '2', '2', 'Tedy mu Jezus odpowiedając rzekł: Zaprawdę, zaprawdę tobie powiedam, jeśli się kto nie narodzi znowu, nie może widzieć królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '3', '3', 'Rzekł do niego Nikodem: Jakoż się może człowiek rodzić, gdy jest stary? Izali może wnidź w żywot matki swej powtóre i narodzić się?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '4', '4', 'Na to mu odpowiedział Jezus: Zaprawdę, zaprawdę tobie powiedam, jeśliż się kto nie narodzi z wody a z Ducha, nie może wnidź do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '5', '5', 'Coć się narodziło z ciała, ciało jest, a co się narodziło z Ducha, Duch jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '6', '6', 'Nie dziwujże się, iżemci powiedział; Potrzebać się wam narodzić znowu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '7', '7', 'Wiatrci kędy chce wieje i szum jego słyszysz, ale nie wiesz skąd przychodzi i dokąd idzie, takżeć jest każdy, który się rodzi z Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '8', '8', 'I odpowiedziawszy Nikodem rzekł jemu: Jakoż to być może?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '9', '9', 'A Jezus dawszy odpowiedź rzekł mu: Tyś jest Mistrz izraelski, a tego nie umiesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '10', '10', 'Zaprawdę, zaprawdę powiedam tobie: Co wiemy mówimy, a cochmy widzieli świadczymy, lecz świadectwa naszego nie przyjmujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '11', '11', 'Jeśliż wam sprawy ziemskie powiedam a nie wierzycie, jakoż gdybych wam niebieskie opowiedał, wierzyć będziecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '12', '12', 'Abowiemci żaden nie wstąpił do nieba, jedno ten, który zstąpił z nieba, Syn człowieczy, który jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '13', '13', 'Jako tedy Mojżesz wystawił był węża na puszczy, tak potrzeba aby wystawion był Syn człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '14', '14', 'Iżby każdy, który weń uwierzy nie zginął, lecz aby miał żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '15', '15', 'Abowiemci Bóg tak umiłował świat, iż Syna swego jednorodzonego dał, aby wszelki kto wierzy weń nie zginął, lecz aby miał żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '16', '16', 'Gdyż nie posłał Bóg Syna swojego na świat, aby miał potępiać świat, ale iżby świat wybawion był przezeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '17', '17', 'Któryć wierzy weń nie będzie potępion, a kto zasię nie wierzy, już potępionym jest, abowiem nie uwierzył w imię jednorodzonego Syna Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '18', '18', 'A toć jest potępienie, że światłość przyszła na świat, ale ludzie radszej umiłowali ciemności niż światłość, gdyż są złościwe sprawy ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '19', '19', 'Boć każdy, który się przewrotnie obchodzi, nienawidzi światłości, ani idzie ku światłości, żeby nie były okazane uczynki jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '20', '20', 'Lecz kto się szczyrze obchodzi, idzieć do światłości, aby były znaczne sprawy jego, iż są wedle Boga sprawowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '21', '21', 'Potym Jezus i zwolennicy jego przyszli do powiatu judskiego, tamże mieszkał z nimi i krzcił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '22', '22', 'Krzcił też i Jan w Enon blisko Salim, bo tam było wiele wód, a ludzie przychodzili i krzcili się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '23', '23', 'Bo jeszcze w ten czas Jan nie był podan do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '24', '24', 'Wszczęła się potym gadka między zwolenniki Janowemi z Żydy o oczyścieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '25', '25', 'A przetoż przyszli do Jana i rzekli mu: Mistrzu, ten, który z tobą był za Jordanem, o którymeś ty dał świadectwo, ten też oto krzci, a wszyscy idą do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '26', '26', 'A dawszy odpowiedź Jan rzekł: Nie możeć nic wziąć człowiek, jeśli mu nie będzie podano z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '27', '27', 'Wy tedy będziecie mi świadkowie żem powiedział: Iżciem ja nie jest on Krystus, alem jest posłan przed nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '28', '28', 'Ktoć ma oblubienicę, tenci jest oblubieniec, a przyjaciel oblubieńców, który stoi a słucha go, weseli się z radością dla głosu oblubieńcowego, a toć wesele moje wypełnione jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '29', '29', 'Onemuć potrzeba róść, a mnie ubywać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '30', '30', 'Ktoć z wysoka przyszedł, nad wszytkimić jest, a ktoć z ziemię jest, ziemski jest i ziemskie rzeczy powieda. Któryć z nieba przyszedł, jestci nadewszytki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '31', '31', 'A co widział i słyszał toż świadczy, lecz świadectwa jego żaden nie przyjmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '32', '32', 'Ktoć jego świadectwo przyjmuje, tenci zapieczętował, iż Bóg jest prawdziwym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '33', '33', 'Abowiem ten. którego Bóg posłał, słowa Boże powieda, bo temu Bóg nie pod miarą dawa Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '34', '34', 'Ociec miłuje Syna i wszytko dał w ręce jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '2', '35', '35', 'Kto wierzy w Syna, ma żywot wieczny, a kto nie jest posłuszny Synowi, nie ogląda żywota, ale gniew Boży zostawa nad nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '0', '0', 'Potym gdy Pan zrozumiał, iż się osłyszeli Faryzeuszowie, że on więcej zwolenników czynił i krzcił niźli Jan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '1', '1', '(Chociaż sam Jezus nie krzcił, jedno zwolennicy jego.)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '2', '2', 'Opuścił Judę i szedł zasię do Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '3', '3', 'I przyszło mu prawie przejdź przez Samarija.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '4', '4', 'A tak przyszedł do miasta Samarijej, które zową Sychar, podle folwarku, który dał Jakub Józefowi synowi swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '5', '5', 'I był tam stok Jakubów, a przetoż Jezus będąc spracowany z drogi, siadł tak u stoku, a było jakoby o szóstej godzinie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '6', '6', 'Zatym przyszła niewiasta Samarytańska, aby czyrpała wodę, ku której rzekł Jezus: Daj mi pić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '7', '7', 'Bo zwolennicy jego szli byli do miasta, aby kupili co jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '8', '8', 'Tedy mu rzekła ona Samarytańska niewiasta: Jakoż ty Żydem będąc żądasz odemnie picia, któram jest niewiasta Samarytańska? Gdyż Żydowie nie towarzyszą z Samarytany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '9', '9', 'Ale Jezus dawszy jej odpowiedź rzekł: O byżeś wiedziała ten dar Boży i kto ten jest, który mówi tobie: Daj mi pić, tedybyś ty go prosiła, a onby tobie dał wodę żywą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '10', '10', 'Rzekła mu niewiasta: Panie nie masz czym czyrpać, a studnia jest głęboka, zkądże tedy masz tę wodę żywą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '11', '11', 'Izażeś ty większym jest nad ojca naszego Jakuba, który nam tę studnię dał, z której i sam pił i synowie jego i bydła jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '12', '12', 'A odpowiedziawszy Jezus rzekł jej: Każdy który pije tę wodę, zasię będzie pragnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '13', '13', 'Lecz ktoby pił onę wodę, którą ja dam jemu, nie upragnie na wieki. Owszem woda ona, którą ja dam jemu, stanie się w nim źrzódłem wód wyskakujących ku żywotowi wiecznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '14', '14', 'Zatym mu rzekła niewiasta: Panie daj mi tej wody, abych nie upragnęła, a żebych tu czyrpać nie chodziła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '15', '15', 'Tedy jej rzekł Jezus: Idź wzów męża twego, a przydź tu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '16', '16', 'I odpowiedziawszy niewiasta rzekła mu: Nie mam męża. A Jezus jej rzekł: Dobrześ powiedziała, Nie mam męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '17', '17', 'Abowiemeś piąci mężow miała, a teraz ten, którego masz nie jestci twój mąż, toś prawdziwie powiedziała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '18', '18', 'I rzekła mu niewiasta: Panie widzę iżeś ty jest prorok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '19', '19', 'Ojcowie naszy chwalili Boga na górze tej, a wy powiedacie, że w Jeruzalem jest miejsce kędy potrzeba Boga chwalić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '20', '20', 'Na to jej powiedział Jezus: Niewiasto wierz mi, przyjdzieć czas, gdy ani na górze tej, ani w Jeruzalem będziecie chwalić Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '21', '21', 'Wy chwalicie co nie wiecie, a myć chwalimy co wiemy, abowiem jest zbawienie z Żydow.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '22', '22', 'Aleć przyjdzie czas i teraz jest, gdy prawdziwi chwalcy będą chwalić Ojca duchem i prawdą. Bo i Ociec takowych szuka, którzyby go chwalili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '23', '23', 'Bóg jestci duch, a tak ci, którzy go chwalą, potrzeba aby go chwalili w duchu i w prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '24', '24', 'Rzekła mu niewiasta: Wiem żeć przyjdzie Mesjasz, który rzeczon jest Krystus, onci gdy przyjdzie oznajmi nam wszytko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '25', '25', 'I powiedział jej Jezus: Jaciem jest, który mówię tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '26', '26', 'A na ten czas przyszli zwolennicy jego i dziwowali się, że z niewiastą rozmawiał, wszakoż mu żaden nie rzekł ocz pytasz, abo co z nią rozmawiasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '27', '27', 'Zostawiwszy tedy dzban swój, niewiasta szła ku miastu i powiedziała ludziam miejsca onego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '28', '28', 'Pódźcie a oglądajcie człowieka, który mi powiedział wszytko comkolwiek czyniła, zali nie ten jest Krystus?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '29', '29', 'A przetoż wyszli z miasta i przyszli do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '30', '30', 'W ten czas go prosili zwolennicy jego mówiąc: Mistrzu jedz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '31', '31', 'Ale im on rzekł: Mam ja pokarm co go pożywać będę, o którym wy nie wiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '32', '32', 'I mówili zwolennicy jedni ku drugim: Izali mu kto przyniósł coby jadł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '33', '33', 'Tedy rzekł Jezus: Mójci pokarm jest abych czynił wolę tego, który mię posłał, a dokończył sprawy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '34', '34', 'Izaź wy nie mówicie: Jeszcze są czterzy miesiące a tedyć żniwo przyjdzie? Otoż wam powiedam: Podnieście oczu waszych, a przypatrzcie się krainam, boć już białe są ku żniwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '35', '35', 'A któryć żnie, bierze zapłatę i zbiera owoc do żywota wiecznego, aby i ten, który sieje, weselił się społu z tym, który żnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '36', '36', 'Abowiem w tym powieść ona jest prawdziwa: Inszyć jest, który sieje, a inszy, który żnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '37', '37', 'Jaciem was posłał żąć to, około czegoście wy nie robili, inszyć robili, a wyście weszli w robotę ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '38', '38', 'Tedy z miasta onego wiele Samarytanów uwierzyli weń na powieść onej niewiasty, która świadczyła, że jej wszytko powiedział co jedno kolwiek czyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '39', '39', 'A tak gdy się do niego zeszli Samarytani, prosili go aby u nich został i został tam przez dwa dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '40', '40', 'I uwierzyło ich więcej na słowa jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '41', '41', 'A mówili niewieście: Już nie na twą powieść więcej wierzymy, abowiem jużechmy sami słyszeli i wiemy żeć ten jest prawdziwie on zbawiciel świata Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '42', '42', 'Potym po dwu dnioch odszedł ztamtąd i szedł do Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '43', '43', 'Abowiem oświadczał się sam Jezus: Iż prorok w ojczyźnie swej nie jest w uczciwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '44', '44', 'Gdy tedy przyszedł do Galilejej, przyjęli go Galilejczycy, bo widzieli wszytko co czynił w Jeruzalem na dzień święty, bo też byli i sami zeszli się na dzień święty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '45', '45', 'Zatym Jezus przyszedł zasię do Kany, miasteczka Galilejskie, gdzie był uczynił z wody wino. A w Kapernaum był niektóry dworzanin królewski, którego syn był niemocen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '46', '46', 'Ten gdy usłyszał, iż Jezus przyszedł z Judy do Galilejej, szedł do niego prosząc go aby wstąpił a uzdrowił syna jego, bo już miał był umrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '47', '47', 'A przetoż mu rzekł Jezus: Jeśliż znaków i cudów nie ujźrzycie, nie uwierzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '48', '48', 'Ale mu powiedział on królewski dworzanin: Panie wstąp pierwej niż umrze syn mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '49', '49', 'Rzekł mu Jezus: Idź syn twój żyw jest. I uwierzył człowiek słowam, które mu był powiedział Jezus, a odszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '50', '50', 'I gdy już przychadzał, zabieżeli mu słudzy i oznajmili mówiąc: Syn twój żyw jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '51', '51', 'A przetoż pytał od nich godziny, o którejby się lepiej mieć poczuł, a oni mu powiedzieli: Wczora około siódmej godziny opuściła go gorączka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '52', '52', 'I poznał ociec, iż się to onej godziny stało, gdy mu powiedział Jezus: Syn twój jest żyw. Uwierzył tedy sam i wszytek dom jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '3', '53', '53', 'A tenci zasię wtóry cud okazał Jezus, gdy przyszedł z Judy do Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '0', '0', 'Był potym dzień święty żydowski i szedł Jezus do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '1', '1', 'A była w Jeruzalem w rynku gdzie owce przedawano sadzawka, którą po Żydowsku zwano Betezda, mająca pięcioro podsienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '2', '2', 'W których leżało barzo wiele niemocnych, ślepych, chromych, wyschłych, którzy czekali poruszenia wody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '3', '3', 'Abowiem Anioł zstępował pewnego czasu w sadzawkę i poruszał wodę. A tak ten, który napirwej wstąpił po wzruszeniu wody, stawał się zdrowym od każdej niemocy, w którejby jedno był zadzierżan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '4', '4', 'I był tam niektóry od trzechdziesiąt i ośmi lat niemocą słożony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '5', '5', 'Tego, gdy Jezus ujźrzał leżącego a poznał, że przez długi czas był niemocen rzekł do niego: A chceszże być zdrów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '6', '6', 'Tedy mu on chory odpowiedział: Panie niemam żadnego, któryby mię włożył do sadzawki gdy bywa poruszona woda, owszem niż ja przyjdę już drugi wnidzie przedemną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '7', '7', 'I rzekł mu Jezus: Wstań a weźmi łoże twe i chodź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '8', '8', 'I natychmiast się zdrowym stał on człowiek, a wziąwszy łoże swoje chodził, choćże był szabat dnia onego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '9', '9', 'Tedy Żydowie mówili onemu, który był uzdrowion: Nie godzić się tobie łoża nosić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '10', '10', 'Ale im on odpowiedział: Ten który mnie zdrowym uczynił, tenże mi rzekł: Weźmi łoże twe a chodź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '11', '11', 'I pytali go: Któryż jest ten coć powiedział: Weźmi łoże twe a chodź?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '12', '12', 'Lecz ten co był uzdrowion niewiedział kto to był, abowiem Jezus uszedł był z onego zgromadzenia, które na onym miejscu było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '13', '13', 'Potym go Jezus znalazł w kościele i rzekł mu: Otożeś się zstał zdrowym. Nie grzeszże więcej, aby cię potym co gorszego nie podkało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '14', '14', 'A tak on odszedszy oznajmił Żydom, iż to był Jezus, który go ku zdrowiu przywrócił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '15', '15', 'A dlategoż Żydowie prześladowali Jezusa i szukali tego jakoby go zamordowali, że to uczynił w szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '16', '16', 'Ale im Jezus tę dał odpowiedź: Ociec mój jeszczeć aż potąd pracuje i ja pracuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '17', '17', 'I dlategoż jeszcze tym więcej szukali Żydowie jakoby go zamordowali, nie tylko iż gwałcił szabat, ale też i Boga powiedał być Ojcem swoim, czyniąc się równym Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '18', '18', 'Tedy Jezus odpowiedziawszy im rzekł: Zaprawdę, zaprawdę wam powiedam, nie możeć Syn sam od siebie sprawować, aż ujźrzy Ojca sprawującego. Abowiem to co on kolwiek czyni, toż też także i Syn czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '19', '19', 'Bóg Ociec miłuje Syna i wszytko mu ukazał co sam czyni i jeszcze mu więtsze sprawy okaże, abyście się wy dziwowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '20', '20', 'Abowiem jako Ociec wzbudza umarłe i ożywia, tak i Syn, które chce ożywia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '21', '21', 'Gdyż Ociec nikogo nie sądzi, lecz wszytek sąd dał Synowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '22', '22', 'Aby wszyscy cześć dawali Synowi, jako cześć dawają Ojcu. Ktoć nie czci Syna, nie czcić i Ojca, który go posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '23', '23', 'Zaprawdę, zaprawdę wam powiedam: Kto słowa mego słucha, a wierzy onemu, który mię posłał, ma żywot wieczny, ani przyjdzie w potępienie, owszem przeszedł z śmierci do żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '24', '24', 'Zaprawdę, zaprawdę wam powiedam: Żeć przyjdzie czas i teraz jest, gdy umarli usłyszą głos Syna Bożego, a którzy usłyszą, żyć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '25', '25', 'Abowiemci jako Ociec ma żywot sam w sobie, tak dał i Synowi mieć żywot w samym sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '26', '26', 'I podał mu też zwirzchność czynić sąd, ile jest Syn człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '27', '27', 'Nie dziwujcież się temu, boć przyjdzie godzina, w którą wszyscy co są w grobiech usłyszą głos jego:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '28', '28', 'I wynidą ci którzy dobrze czynili na powstanie żywota, a którzy źle czynili na powstanie potępienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '29', '29', 'Nie mogęć ja sam od siebie nic czynić i jako słyszę sądzę, a sąd mój jest sprawiedliwy, gdyż nie szukam wolej mojej, jedno wolej tego, który mię posłał Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '30', '30', 'Jeśliżeć ja sam o sobie świadectwo dawam, świadectwo moje nie jestci słuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '31', '31', 'Inszyć jest co o mnie świadectwo dawa i wiem, że prawdziwe jest świadectwo, które świadczy o mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '32', '32', 'Wyście słali do Jana, a on dał świadectwo prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '33', '33', 'Jać od człowieka świadectwa nie biorę, ale to mówię byście wy byli wybawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '34', '34', 'Onci był świecą gorającą i świecącą, a wyście się chcieli do czasu poradować w światłości jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '35', '35', 'Aleć ja mam świadectwo więtsze niźli Jan, abowiem sprawy, które mi Ociec dał abych je odprawował, tyż samy sprawy, które ja czynię, dawają o mnie świadectwo iż mię Ociec posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '36', '36', 'A ten Ociec, który mię posłał, onżeć świadczył o mnie i nie słyszeliście nigdy głosu jego, aniście osoby jego widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '37', '37', 'I słowa jego nie macie w sobie mieszkającego, abowiem temu, którego on posłał, wy nie wierzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '38', '38', 'Dowiedujcież się pism, boć się wam zda, że wy w nich żywot wieczny macie, a onyć są, które dawają świadectwo o mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '39', '39', 'A wżdy do mnie przydź nie chcecie, abyście mieli żywot.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '40', '40', 'Chwałyć od ludzi nie biorę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '41', '41', 'Ale znam, że wy miłości Bożej nie macie w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '42', '42', 'Jaciem przyszedł imieniem Ojca mego, a nie przyjmujecie mię. A jeśliżby przyszedł iny swym imieniem, tedy go przyjmiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '43', '43', 'Jakoż wy możecie wierzyć, gdy jedni chwalę od drugich bierzecie a chwały, która od samego Boga pochodzi, nie szukacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '44', '44', 'Nie mnimajcież abychci ja was winować miał u Ojca, jestci który was obwinia, Mojżesz, w którym wy nadzieję macie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '45', '45', 'Bo jeślibyście wierzyli Mojżeszowi, wierzylibyście i mnie, gdyż on o mnie pisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '4', '46', '46', 'Lecz gdy jego pismom nie wierzycie, jakoż słowu memu wierzyć będziecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '0', '0', 'Po tym Jezus szedł za Morze Galilejskie, które jest Tyberjadskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '1', '1', 'I szło za nim mnóstwo wielkie ludu, bo widzieli znaki które czynił w tych co chorzy byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '2', '2', 'A wszedszy Jezus na górę, siadł tam z zwolennikami swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '3', '3', 'Była też blisko Wielkanoc święto Żydowskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '4', '4', 'Tedy podniózszy oczu swych Jezus ujźrzał, iż wielkie mnóstwo ludzi schodziło się do niego i rzekł do Filipa: Skądże nakupiem chlebów, aby się ci najedli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '5', '5', '(A mówił to kusząc go, bo on już wiedział co miał czynić.)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '6', '6', 'I odpowiedział mu Filip: Za dwie ście groszy chleba nie dosyć im będzie, chociażby po kąsku każdy z nich wziął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '7', '7', 'Ale mu jeden rzekł z zwolenników jego, Andrzej brat Symona Piotra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '8', '8', 'Jest tu dziecię jedno co ma pięcioro chleba jęczmiennego i dwie rybce, ale cóż to jest między tak wielki lud?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '9', '9', 'Tedy rzekł Jezus: Roskażcież aby ci siadali, a było tam trawy dosyć na onym miejscu. Siadło tedy mężów w liczbie około piąci tysięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '10', '10', 'A tak Jezus wziąwszy ony chleby i uczyniwszy dzięki, podzielił zwolennikom, zwolennicy zasię tym co siedzieli, tak że też i ony ryby ile jedno chcieli');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '11', '11', 'A gdy już mieli dosyć, rzekł Jezus zwolennikom swym: Zbierzcie okruszki, które zostały, żeby nic nie ginęło');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '12', '12', 'Zebrali tedy i pełne nakładli dwanaście koszów okruszków z pięciorga chleba jęczmiennego, które pozostały tym co jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '13', '13', 'A przetoż oni ujźrzawszy cud, który Jezus okazał, mówili: Tenci jest zaprawdę on prorok, który miał przydź na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '14', '14', 'I wiedząc Jezus, iż go przyszedszy uchwycić mieli a uczynić królem, uszedł zasię sam tylko na górę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '15', '15', 'A gdy był wieczór, puścili się zwolennicy jego do morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '16', '16', 'I wsiadszy w łódź jechali za morze ku Kapernaum, ano już były ciemności, a Jezus nie przyszedł do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '17', '17', 'Tedy morze, gdy wstał wielki wiatr, poczęło się burzyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '18', '18', 'A gdy już odpłynęli jakoby na dwudziestu i piąci abo na trzechdziesiąt stajan, ujźrzeli Jezusa chodzącego po morzu a przychodzącego ku łodzi i ulękli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '19', '19', 'Lecz im on rzekł: Jaciem jest, nie bójcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '20', '20', 'A tak go z chucią wzięli w łódź i natychmiast łódź stanęła u onej krainy, do której jechali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '21', '21', 'Nazajutrz lud, który stał za morzem, gdy obaczyli że tam nie masz drugiej łodzi, tylkoż ona jedna w którą byli wsiedli zwolennicy jego, a iż Jezus nie wsiadł w łódź z zwolenniki swemi, jednoż sami zwolennicy jego ujechali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '22', '22', 'Przyszły też były drugie łodzie od Tyberjady, nie daleko miejsca onego gdzie jedli chleb, gdy był Pan uczynił dzięki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '23', '23', 'To gdy obaczył lud, że tam Krystus nie był ani zwolennicy jego, wsiedli i sami w łodzie, a przyszli do Kapernaum szukając Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '24', '24', 'I znalazszy go za morzem, rzekli mu: Mistrzu a kiedyś tu przyszedł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '25', '25', 'Odpowiedział im Jezus i rzekł: Zaprawdę, zaprawdę wam powiedam, szukacie mię, nie iżeście widzieli cuda, ale iżeście ony chleb jedli i byliście nasyceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '26', '26', 'Pracujciesz nie o pokarmie, który ginie, ale o onym pokarmie który trwa ku żywotowi wiecznemu, który wam da Syn człowieczy. Abowiem tego zapieczętował Ociec, to jest Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '27', '27', 'Tedy oni rzekli: Cóż czynić będziemy, abychmy sprawowali uczynki Boże?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '28', '28', 'A Jezus odpowiedziawszy rzekł im: Tać jest sprawa Boża, abyście wierzyli w tego, którego on posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '29', '29', 'Ale mu oni rzekli: Cóż wżdy ty za znak okazujesz, abychmy ujźrzeli i wierzyli tobie? Cóż wżdy czynisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '30', '30', 'Ojcowieć naszy jedli mannę na puszczy, jako jest napisano: Chleb niebieski dał im aby go używali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '31', '31', 'Na to im powiedział Jezus: Zaprawdę zaprawdę wam powiedam, nie dałci wam Mojżesz tego chleba niebieskiego, aleć wam Ociec mój dawa prawdziwie on chleb niebieski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '32', '32', 'Abowiemci on chleb Boży ten jest, który z nieba zstąpił i dawa żywot światu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '33', '33', 'Rzekli tedy k niemu: Panie daj nam zawżdy tego chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '34', '34', 'Ale im rzekł Jezus: Jamci jest on chleb żywota, ten który do mnie przychodzi, żadnym sposobem łaczen nie będzie, a kto wierzy w mię, nigdy nie upragnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '35', '35', 'Leczciem wam powiedział: Iżeście mię też widzieli, a wżdy nie wierzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '36', '36', 'Cóżkolwiek mnie dawa Ociec, do mnieć przyjdzie, a tego co do mnie przyjdzie, nie wyrzucę precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '37', '37', 'Gdyżem zstąpił z nieba, nie iżbych czynił wolę moję, lecz wolę onego, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '38', '38', 'A tać jest wola Ojca onego, który mię posłał, iżbym cokolwiekby on mnie dał, nie stracił tego, ale to wzbudził w on ostateczny dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '39', '39', 'A tak jestci ta wola onego, który mię posłał, iżby każdy, który widzi Syna a wierzy weń, miał żywot wieczny, a ja go wzbudzę w on ostatni dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '40', '40', 'A przetoż Żydowie szemrali o nim iż powiedał: Jamci jest on chleb, którym z nieba zstąpił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '41', '41', 'I mówili: Izaź ten nie jest Jezus syn Józefów, któregośmy znali ojca i matkę? Jakoż tedy ten powieda, że z nieba zstąpił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '42', '42', 'Ale Jezus uczyniwszy odpowiedź rzekł do nich: Nie szemrzycie między sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '43', '43', 'Żadenci do mnie przyjdź nie może, jeśliż Ociec, który mię posłał nie pociągnie go, a ja go wzbudzę w on ostateczny dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '44', '44', 'Jestci napisano w prorocech: I będąć wszyscy uczonemi od Boga, a przetoż ktokolwiek od Ojca słyszał i nauczył się, ten do mnie przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '45', '45', 'Nie, iżećby kto Ojca widzieć miał, tylko, ten który jest od Boga, tenci widział Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '46', '46', 'Zaprawdę, zaprawdę wam powiedam: Ten który wierzy w mię, ma żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '47', '47', 'Jamci jest chleb on żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '48', '48', 'Ojcowieć waszy jedli Mannę na puszczy i pomarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '49', '49', 'Tenci jest chleb, on który z nieba zstąpił, iż kto go pożywa, nie umrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '50', '50', 'Jaciem jest chleb ożywiający, którym z nieba zstąpił, jeśliby kto jadł tego chleba, żyć będzie na wieki. A chleb który ja dam, jestci moje ciało, które ja dam za żywot świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '51', '51', 'Wadzili się tedy Żydowie jedni przeciw drugim mówiąc: Jaktoż ten może nam dać ciało swe abyśmy je jedli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '52', '52', 'Ale im rzekł Jezus: Zaprawdę, zaprawdę wam powiedam, jeśli nie będziecie jeść ciała Syna człowieczego i pić jego krwie, nie macie żywota w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '53', '53', 'Ktoć używa ciała mego i pije moję krew, ma żywot wieczny, a ja go wzbudzę w ostatni dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '54', '54', 'Abowiem ciało moje prawdziwie jest pokarm, a krew moja prawdziwie jest picie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '55', '55', 'Kto pożywa mego ciała, a pije moję krew, we mnie mieszka a ja w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '56', '56', 'Jako mię posłał on żywiący Ociec i ja żywę przez Ojca, także i ten co mię będzie pożywał, żyć będzie przez mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '57', '57', 'Tenci jest chleb, on który z nieba zstąpił, nie tak jako Ojcowie waszy jedli Mannę i pomarli. Kto jeść będzie ten chleb, żyć będzie na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '58', '58', 'To mówił w bożnicy ucząc w Kapernaum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '59', '59', 'Wiele ich tedy z zwolenników jego to słysząc mówili: Przykreć to są słowa i któż tego słuchać może?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '60', '60', 'A tak wiedząc Jezus sam u siebie, iż o tym szemrali zwolennicy jego, rzekł do nich: I toż was obraża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '61', '61', 'Cóż gdybyście ujźrzeli Syna człowieczego wstępującego tam gdzie był pierwej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '62', '62', 'Duchci jest, który ożywia, ciałoć nic nie jest pożyteczno. Słowa, które ja wam powiedam, duch są i żywot.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '63', '63', 'Leczci są niektórzy z was co nie wierzą. Abowiem wiedział od początku Jezus, którzy byli ci co nie wierzyli i tego, który go zdradzić miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '64', '64', 'I mówił: Dlategomci wam powiedział, iż żaden nie może przyjdź ku mnie, jeśliż mu to nie będzie dano od Ojca mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '65', '65', 'Od tegoż czasu wiele z zwolenników odstąpili go, ani znim więcej chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '66', '66', 'Zatym Jezus rzekł onym dwiemanaście: Azali i wy chcecie idź precz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '67', '67', 'Ale mu na to odpowiedział Symon Piotr: Panie do kogoż idź mamy? Ty masz słowa żywota wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '68', '68', 'A myśmy uwierzyli i poznalichmy, &nbsp;żeś ty jest Krystus on Syn Boga żywego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '69', '69', 'Na to im odpowiedział Jezus: Izalim ja nie dwunaście was obrał, lecz jeden z was jest diabeł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '5', '70', '70', 'A toć mówił o Judaszu Symonowym synu Iskarjocie, bo go ten zdradzić miał, gdyż był jeden z onych dwunaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '0', '0', 'Tedy Jezus przemieszkawał potym w Galilejej, bo nie chciał w Żydostwie przebywać, iż nań czyhali Żydowie jakoby go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '1', '1', 'I było blisko święto Kuczek żydowskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '2', '2', 'A przetoż rzekli k niemu bracia jego: Wynidź stąd a idź do Judy, żeby i zwolennicy twoi oglądali sprawy twe które czynisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '3', '3', 'Abowiemci żaden nic w skrytości nie czyni, owszem tego szuka jakoby sławnym był. Jeśliż to czynisz, objawże się sam światu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '4', '4', 'Bo ani bracia jego weń nie wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '5', '5', 'Ale im rzekł Jezus: Jeszczeć nie masz mego czasu, lecz czas wasz zawżdy jest pogotowiu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '6', '6', 'Niemożeć was świat mieć w nienawiści, aleć mnie ma w nienawiści, bo ja oświadczam o nim, iż sprawy jego są złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '7', '7', 'Idźciesz wy na to święto, jać jeszcze na nie nie pójdę, bo mój czas jeszcze się nie wypełnił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '8', '8', 'A tak im to powiedziawszy, został w Galilejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '9', '9', 'Potym gdy szli bracia jego, tedy też i on szedł na święto, nie jawnie ale jakoby potajemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '10', '10', 'Szukali go tedy Żydowie w święto, a mówili: A on gdzież jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '11', '11', 'I było o nim wielkie szemranie między ludem, bo jedni powiedali że jest dobry człowiek, drudzy zasię mówili: Nie tak jest, ale zwodzi lud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '12', '12', 'Wszakoż o nim żaden jawnie nie mówił, dla bojaźni Żydowskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '13', '13', 'A gdy już było w pół dnia świętego, wszedł Jezus do kościoła i uczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '14', '14', 'Dziwowali się tedy Żydowie mówiąc: Jakoż wżdy ten umie Pismo, gdyż się nie uczył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '15', '15', 'Lecz Jezus odpowiedziawszy im rzekł: Nauka moja nie jestci moją, ale tego, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '16', '16', 'Jeśliż kto będzie chciał czynić wolę jego, rozeznać o nauce jeśliż jest z Boga, abo jeśliż ja też sam z siebie mówię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '17', '17', 'Ktoć z samego siebie mówi, chwałyć własnej szuka. Ale ten co szuka chwały tego, który go posłał, tenci jest prawdziwy, a nie masz w nim niesprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '18', '18', 'Izali wam Mojżesz nie dał zakonu, a wżdy żaden z was nie czyni dosyć zakonowi? Przeczże tego szukacie abyście mię zamordowali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '19', '19', 'Odpowiedział lud i rzekł: Masz diabelstwo i któż tego szuka aby cię zamordował?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '20', '20', 'Ale Jezus dawszy odpowiedź rzekł: Jedenżem uczynił uczynek, a wszyscy się dziwujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '21', '21', 'A wszak wam Mojżesz podał obrzezanie (nie iżby było od Mojżesza, ale iż było od ojców) i obrzezujecie w szabat człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '22', '22', 'Jeśliż człowiek przyjmnie obrzezanie w szabat, a nie łamie się zakon Mojżeszów, na mię będziecież się gniewać żem wszytkiego człowieka uzdrowił w szabat?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '23', '23', 'Nie sądzcie według postawy, ale sądzcie sądem sprawiedliwym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '24', '24', 'A tak rzekł niektóry z Jerozolimczyków: Izali nie ten jest, którego Żydowie zamordować szukają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '25', '25', 'I oto jawnie mówi a nic mu nie rzeką? Zali prawdziwie poznali książęta, iż ten jest prawdziwie on Krystus?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '26', '26', 'Lecz my wiemy skąd ten jest, a gdyć przydzie Krystus żaden nie wzwie skądby był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '27', '27', 'Wołał tedy Jezus w kościele ucząc a mówiąc: I mnie znacie i skąd jestem wiecie. A nie przyszedłciem sam od siebie, ale jest prawdziwy, który mię posłał, którego wy nie znacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '28', '28', 'Leczci go ja znam, bom od niego jest, a on mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '29', '29', 'I szukali tego jakoby go pojmać, ale żaden nie targnął się nań ręką, bo była nie przyszła godzina jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '30', '30', 'A wiele ich z ludu uwierzyli weń i mówili. Gdy Krystus przyjdzie azaź więcej cudów uczyni nad ty, któren ten uczynił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '31', '31', 'Tedy słysząc Faryzeuszowie, iż to o nim lud pospolity szemrał, posłali ciż to Faryzeuszowie i książęta kapłańskie służebniki aby go pojmali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '32', '32', 'A przetoż rzekł im Jezus: Jeszcze maluczko będę z wami, a potym odejdę do tego, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '33', '33', 'Będziecie mię szukać, a nie znajdziecie i gdzie ja będę tam wy przyjdź nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '34', '34', 'A przetoż Żydowie mówili między sobą. I dokądże ten ma idź iż my go nie znajdziemy? Czyli pójdzie do rosproszonych między Greki a będzie uczył pogany?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '35', '35', 'Coż to za słowa, które mówi: Szukać mię będziecie a nie znajdziecie i gdzie ja jestem, tam wy przyjdź nie możecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '36', '36', 'A tak w on ostateczny dzień wielki święta, stanął Jezus i wołał mówiąc. Jeśliż kto pragnie, niechżeć ku mnie przyjdzie a pije.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '37', '37', 'Kto wierzy w mię, jako powieda Pismo, rzeki wody żywej popłyną z żywota jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '38', '38', '(A toć mówił o duchu, którego wziąć mieli wierzący weń, abowiem jeszcze nie był Duch święty, bo Jezus jeszcze nie był uwielbion).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '39', '39', 'Wiele ich tedy z onego zgromadzenia, gdy usłyszeli ty słowa, mówili: Zaprawdęć ten jest on prorok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '40', '40', 'A drudzy zasię mówili: Tenci jest Krystus on. A drudzy powiedali: Azaź Krystus przyjdzie od Galilejej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '41', '41', 'Izaź Pismo nie powieda: Iż Krystus przyjdzie z nasienia Dawidowego i z miasteczka Betleem gdzie był Dawid?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '42', '42', 'Stało się tedy rozerwanie o nim między ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '43', '43', 'I chcieli niektórzy z nich pojmać go, ale się nań żaden nie targnął ręką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '44', '44', 'Zatym przyszli słudzy do nawyższych kapłanów i do Faryzeuszów, którzy im rzekli, Przeczżeście go nie przywiedli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '45', '45', 'Odpowiedzieli słudzy: Nigdy tak żaden nie mówił jako ten to człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '46', '46', 'Lecz im odpowiedzieli Faryzeuszowie: Zaliście już i wy zwiedzieni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '47', '47', 'I zaź który z książąt abo z Faryzeuszów uwierzył weń?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '48', '48', 'Ale to pospólstwo, które nie zna zakonu, jest przeklęte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '49', '49', 'Tedy do nich rzekł Nikodem, on który był w nocy przyszedł do niego, a był jeden z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '50', '50', 'Izali zakon nasz skazuje kogo jeśliżby pierwej nie słyszał od niego a nie wyrozumiał co czyni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '51', '51', 'A oni mu odpowiedziawszy rzekli: Zali i ty jesteś Galilejczyk? Dowiedniże się a obacz, żeć od Galilejej prorok nie powstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '6', '52', '52', 'A tak się rozeszli każdy do domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '0', '0', 'Zatym Jezus szedł na górę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '1', '1', 'I raniuczko potym był w kościele, a lud wszytek zszedł się do niego i siadszy uczył je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '2', '2', 'Przywiedli tedy do niego doktorowie i Faryzeuszowie niewiastę, którą na cudzołóstwie zastano, a postawiwszy ją w pośrzodku,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '3', '3', 'Rzekli mu: Tę niewiastę zastano na samym uczynku cudzołożąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '4', '4', 'A w zakonie roskazał nam Mojżesz aby takie kamionowano. Cóż tedy ty mówisz ktemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '5', '5', 'A to mówili kusząc go aby ji oskarżyć mogli, lecz Jezus schyliwszy się ku dołu, pisał palcem na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '6', '6', 'Gdy go tedy nie przestawali pytać, podnióższy się rzekł im: Kto z was nie jest winien grzechu, niechżeć na nię pierwszy rzuci kamieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '7', '7', 'A zasię schyliwszy się ku dołu pisał na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '8', '8', 'Co gdy usłyszeli, tedy będąc od własnego sumnienia karani, z osobna jeden za drugim wychodzili, począwszy od starszych aż do ostatecznych, iż tylko został sam Jezus a niewiasta w pośrzodku stojąca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '9', '9', 'A gdy się podniósł Jezus i nie widział żadnego oprócz niewiasty, rzekł jej: Niewiasto, gdzież są oni co na cię skarżyli? Żadenże cię nie skazał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '10', '10', 'Tedy ona rzekła: Żaden Panie. A Jezus jej powiedział: Ani ja ciebie skazuję. Idźże a więcej nie grzesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '11', '11', 'A tak potym Jezus powiedział im mówiąc: Jamci jest ona światłość świata, ktoć zamną idzie, nie będzie chodził w ciemnościach, ale będzie mieć światłość żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '12', '12', 'A przetoż mu rzekli Faryzeuszowie: Ty sam o sobie świadczysz, a świedectwo twe nie jest słuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '13', '13', 'Ale Jezus odpowiedział i rzekł im: Chociażbych ja też świadczył sam o sobie, wżdyć jest słuszne świadectwo moje, gdyż wiem skądem przyszedł i dokąd idę, lecz wy nie wiecie skądem przyszedł i dokąd idę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '14', '14', 'Wy wedle ciała sądzicie, a ja nikogo nie sądzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '15', '15', 'A choczbych też ja sądził, sąd mój jestci prawdziwy, bo nie jestem sam, ale ja i który mię posłał Ociec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '16', '16', 'I napisanoć jest w zakonie waszym, iż dwojga ludzi świadectwo słuszne jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '17', '17', 'Jać jestem ten który sam o sobie świadczę i świadczy o mnie ten który mię posłał Ociec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '18', '18', 'Tedy mu mówili: Gdzież wżdy jest ten twój Ociec? A odpowiedział Jezus: Ani mnie znacie, ani Ojca mego, byście mię znali i Ojca byście mego znali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '19', '19', 'Tyć słowa mówił Jezus w skarbnicy, ucząc w kościele, a żaden go nie pojmał, bo jeszcze nie przyszła była godzina jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '20', '20', 'Powiedział im po wtóre Jezus: Jać idę i będziecie mię szukać, a w grzechu waszym pomrzecie i gdzieć ja idę, wy przyjdź nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '21', '21', 'Mówili tedy Żydowie: Aboć się sam zabije, że mówi: Gdzie ja idę wy przyjdź nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '22', '22', 'Tedy rzekł do nich: Wy jesteście z niskości, a jam jest z wysoka, wy z tego świata jesteście, a jamci nie jest ż świata tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '23', '23', 'Powiedziałciem wam tedy, iż pomrzecie w grzechoch waszych. Bo jeśliż nie uwierzycie żeciem ja on jest, pomrzecie w grzechoch waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '24', '24', 'Zatymże mu oni rzekli: Ty coś jest zacz? A Jezus im powiedział: To co wam i zprzodku powiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '25', '25', 'Mamci o was wiele mówić i sądzić, lecz ten któryć mię posłał jestci prawdziwy, a ja to com od niego słyszał, powiedam na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '26', '26', 'I nie zrozumieli, że im o Ojcu powiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '27', '27', 'A przetoż im rzekł Jezus: Gdy podniesiecie Syna człowieczego, tedy poznacie żemci ja jest, a iż sam z siebie nic nie czynię, lecz jako mię nauczył Ociec, to mówię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '28', '28', 'Abowiem ten który mię posłał, ze mną jest, a nie zostawił mię samego Ociec, bo ja to co się jemu podoba zawżdy czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '29', '29', 'To gdy powiedał, wiele ich weń uwierzyło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '30', '30', 'Rzekł tedy Jezus do tych co weń uwierzyli Żydow: Jeśliż wy zostawać będziecie w słowie moim, prawdziwie moimi zwolenniki będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '31', '31', 'I poznacie prawdę, a prawda was oswobodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '32', '32', 'Na to oni jemu odpowiedzieli: Jestechmy potomstwo Abrahamowe, a nigdyśmy nie służyli nikomu. I jakoż ty powiedasz żeby nas oswobodzić miano?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '33', '33', 'Ale im odpowiedział Jezus: Zaprawdę, zaprawdę wam powiedam: wszelkić, który się dopuszcza grzechu, jest sługa grzechowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '34', '34', 'A sługać nie mieszka w domu na wieki, jednoż syn mieszka wiecznie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '35', '35', 'I przetoż jeśli was syn oswobodzi, prawdziwie wolni będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '36', '36', 'Wiem że jesteście potomstwo Abrahamowe, lecz szukacie jakobyście mię zamordowali, gdyż słowo moje nie ma w was miejsca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '37', '37', 'Jać powiedam to com widział u Ojca mego, a wy też czynicie to coście widzieli u ojca swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '38', '38', 'Tedy oni odpowiedając rzekli: Ociecci nasz jest Abraham. A Jezus im rzekł: Byście byli syny Abrahamowymi, czynilibyście uczynki Abrahamowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '39', '39', 'A teraz oto szukacie jakobyście mię zabić mieli, człowieka tego który wam mówię prawdę, któram słyszał od Boga, tegoć nie czynił Abraham.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '40', '40', 'Wy czynicie uczynki Ojca waszego. Ale mu oni rzekli: Wżdyciechmy my nie z cudzołóstwa są narodzeni jednegoż Ojca mamy Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '41', '41', 'Tedy im powiedział Jezus: Być był Bóg Ojcem waszym, tedybyście mię miłowali, gdyżem ja od Boga wyszedł i przyszedł, a nie samem od siebie przyszedł, ale mię on posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '42', '42', 'Czemuż tej powieści mojej nie pojmujecie? Przeto, iż nie możecie słuchać słowa mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '43', '43', 'Wy z ojca diabła jesteście, a pożądliwości ojca waszego czynić chcecie, onci był mężobójca od początku i w prawdzie nie został, bo w nim nie masz prawdy, ilekroćkolwiek powieda, kłamstwo sam z siebie powieda, abowiem jest kłamca i ociec kłamstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '44', '44', 'A iż prawdę mówię, wy mnie nie wierzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '45', '45', 'I któryż z was sztrofuje mię z grzechu? Jeśliż prawdę mówię przeczże mi nie wierzycie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '46', '46', 'Ktoć z Boga jest, słów Bożych słucha. A dlategoż wy nie słuchacie, że nie jesteście z Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '47', '47', 'A dlategoż Żydowie odpowiedzieli mu i rzekli: Izali my dobrze nie powiedamy, żeś ty jest Samarytan i diabelstwo masz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '48', '48', 'Na to im dał odpowiedź Jezus: Jać diabelstwa nie mam, ale czczę Ojca mego, a wy mię sromocicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '49', '49', 'Jać nie szukam chwały swej, jestci ten który szuka i sądzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '50', '50', 'Zaprawdę, zaprawdę wam powiedam. Jeśliż kto słowa me zachowywać będzie, śmierci nie ogląda na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '51', '51', 'Rzekli mu tedy Żydowie: Terazciechmy poznali, że diabelstwo masz. Abraham umarł i Prorocy. A ty powiedasz: Jeślikto słowo me zachowywać będzie, nie skosztuje śmierci na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '52', '52', 'Izaś ty jest więczszy nad onego Ojca naszego Abrahama, który umarł i Prorocy pomarli. Czymże się ty wżdy czynisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '53', '53', 'Tedy odpowiedział Jezus: Jeśliż się ja sam chwalę, chwała moja nic nie jest. Onci Ociec mój jest ten, który mię chwali, którego wy powiedacie być Bogiem waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '54', '54', 'Lecz go ani znacie, a jać go znam i jeślibych rzekł, że go nie znam, będę równym wam kłamcą. Ale go znam i słowo jego zachowywam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '55', '55', 'Abraham, ociec wasz, ochotnie żądał oglądać ten dzień mój i oglądał a radował się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '56', '56', 'Rzekli mu tedy Żydowie: Piącidziesiąt lat jeszcze nie masz, a Abrahamaś widział?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '57', '57', 'Ale im rzekł Jezus: Zaprawdę, zaprawdę wam powiedam, pierwej niż Abraham był, ja jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '7', '58', '58', 'Zatym porwali kamienie aby nań ciskali, ale Jezus ochroniwszy się, wyszedł z kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '0', '0', 'I przychodząc ujźrzał człowieka ślepego od narodzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '1', '1', 'I pytali go zwolennicy jego mówiąc: Mistrzu kto zgrzeszył? Tenli, czyli rodzicy jego iż się ślepym narodził?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '2', '2', 'Odpowiedzia&nbsp;ł Jezus: Ani ten zgrzeszył, ani rodzicy jego, ale żeby się sprawy Boże w nim okazały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '3', '3', 'Mnie&nbsp;ć potrzeba sprawować sprawy onego, który mię posłał, pokąd dzień jest. Przychodzić noc gdy żaden nie będzie mógł nic sprawować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '4', '4', 'Póki będę na świecie, jestciem światłością świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '5', '5', 'I rzekszy to, plunął na ziemię, a uczynił błoto z ślin i maczał błotem na oczach jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '6', '6', 'Przytym mu rzekł: Idź umyj się w Siloam (co jest, jeślibyś wykładał, Posłany). A przetoż szedszy, umył się i wrócił się widząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '7', '7', 'A tak sąsiedzi, którzy go i przed tym widali, że on był ślepy mówili: Izali to nie ten jest, który ono siadał żebrząc?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '8', '8', 'Tedy drudzy mówili: Tenci jest. A drudzy zasię: Iż jest jemu podobien. Lecz on powiedał: Jaciem jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '9', '9', 'A przetoż mu mówili: Jakożci się oczy otworzyły?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '10', '10', 'On tedy odpowiedział i rzekł: Niektóry co go zową Jezus uczynił błoto i pomazał oczy me, a rzekł mi: Idź do sadzawki Siloam, a umyj się. Skorom tedy odszedł i umyłem się, wziąłem wzrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '11', '11', 'A tak mu oni rzekli: Gdzież on jest? Tedy powiedział: Nie wiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '12', '12', 'Zatym przywiedli do Faryzeuszów onego to co przed tym był ślepy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '13', '13', 'A naten czas był szabat, gdy Jezus uczynił błoto, a otworzył oczy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '14', '14', 'Potym go też pytali i Faryzeuszowie jako wziął wzrok? A on im powiedział: Włożył błoto na oczy moje i umyłem się i widzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '15', '15', 'A przetoż niektórzy z Faryzeuszów mówili: Człowiek ten nie jest od Boga, bo nie strzeże szabatu: Drudzy zasię mówili i jakożby mógł człowiek grzeszny takowe cuda czynić? I było rozerwanie między nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '16', '16', 'Powtóre rzekli ślepemu: Ty co też mówisz o nim że otworzył oczy twoje? A on powiedział: Prorokci jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '17', '17', 'I nie wierzyli Żydowie o nim żeby miał być ślepym, a żeby wziął wzrok, aż wezwali rodziców onego, który wzrok otrzymał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '18', '18', 'Pytali ich tedy mówiąc: I tenże jest syn wasz, on któregoście powiedali być śleponarodzonym? Jakoż wżdy teraz widzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '19', '19', 'Odpowiedzieli im rodzicy jego i rzekli: Wiemy żeć to jest on syn nasz, a iż się ślepo narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '20', '20', 'Lecz jako teraz przejźrzał nie wiemy, abo kto otworzył oczy jego, my nie wiemy, mać lata, pytajcież samego, a onci o sobie powie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '21', '21', 'Toć mówili rodzicy jego, że się bali Żydów, abowiem już postanowili byli Żydowie, iż jeśliby go kto wyznał Krystusem, aby był z bożnice wyrzucon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '22', '22', 'A dlategoż powiedzieli rodzicy jego: Mać lata pytajcież samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '23', '23', 'Wezwali tedy powtóre człowieka, który był ślepym i rzekli: Daj chwałę Bogu, bo my wiemy, że to jest człowiek grzeszny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '24', '24', 'Ale on odpowiedział i rzekł: Niewiemci jeśli jest grzeszny, to jedno wiem, iż będąc ślepym, teraz widzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '25', '25', 'I pytali go jeszcze po wtóre: Cóż ci czynił? I jakoć otworzył oczy twe?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '26', '26', 'Odpowiedział im tedy: Jużciem wam powiedał, a nie słuchaliście. Przeczże jeszcze drugi raz słyszeć chcecie? Zali i wy chcielibyście być zwolenniki jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '27', '27', 'Tedy go oni sromocili a mówili: Bądź ty sam jego zwolennikiem, myciechmy są Mojżeszowi zwolennicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '28', '28', 'Myć wiemy że Bóg z Mojżeszem mówił, a ten skądby był nie wiemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '29', '29', 'A tak im on odpowiedział i rzekł: Toć w tym dziwno że wy nie wiecie skąd jest, a wżdy otworzył oczy moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '30', '30', 'A wiemy też, iż grzesznych Bóg nie wysłuchawa, jedno tego, który jest chwalcą Bożym, a wolę jego czyni, tegoć wysłuchawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '31', '31', 'Od wieków to nie jest rzecz słychana, aby kto otworzył oczy śleponarodzonego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '32', '32', 'A jeśliżećby ten nie był od Boga, nie mógłciby nic uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '33', '33', 'Odpowiedzieli mu tedy i rzekli: Tyś jest w grzechach narodzony wszytek i tyż nas masz uczyć? I także go precz wyrzucili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '34', '34', 'I usłyszał Jezus, iż go precz wyrzucili, a gdy go znalazł, rzekł mu: Ty wierzyszli w Syna Bożego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '35', '35', 'A on odpowiedział i rzekł: Któż jest Panie abych weń wierzył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '36', '36', 'A odpowiedział mu Jezus: I widziałeś go i który mówi z tobą sam ci jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '37', '37', 'Zatym on rzekł: Wierzę Panie i dał mu chwałę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '38', '38', 'Potym rzekł Jezus: Jaciem przyszedł na ten świat odprawować sądu, aby ci którzy nie widzą, widzieli, a którzy widzą, aby ślepi byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '39', '39', 'To usłyszeli niektórzy z Faryzeuszów, którzy byli przy nim i rzekli mu: Zali my jestechmy ślepymi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '8', '40', '40', 'Na to im powiedział Jezus: Byście byli ślepymi, nie mielibyście grzechu, lecz iż teraz mówicie: Widziemy, a tak grzech wasz zostawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '0', '0', 'Zaprawdę, zaprawdę wam powiedam, kto nie wchodzi drzwiami do obory owczej, ale wchodzi inędy, ten jest złodziej i zbójca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '1', '1', 'Któryć zasię wchodzi drzwiami, tenci jest pastyrz owiec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '2', '2', 'Temu odźwierny otwiera, a owce słuchają głosu jego i nazywa owieczki swe mianowicie, a prowadzi je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '3', '3', 'Potym gdy wypuści owce swe, idzie przed nimi, a owieczki idą za nim, bowiem znają głos jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '4', '4', 'Ale za cudzym żadnym sposobem nie idą, owszem uciekają od niego, bo nie znają głosu obcych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '5', '5', 'To podobieństwo powiedał im Jezus, lecz oni nie rozumieli tego co im powiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '6', '6', 'Rzekł im tedy po wtóre Jezus: Zaprawdę, zaprawdę wam powiedam. Jaciem jest drzwiami owiec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '7', '7', 'Wszyscy, ile ich przedemną przyszło, złodziejeć są i zbójcowie, a też ich nie słuchały owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '8', '8', 'Jamci jest drzwiami, przez mię jeśli kto wnidzie, będzie zachowan, a wnidzie i wynidzie i pastwiska znajdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '9', '9', 'Złodziej nie idzie jedno żeby kradł a mordował i tracił owce, lecz ja przyszedłem aby żywot miały i obfitowały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '10', '10', 'Jaciem jest on dobry pastyrz. Dobryć pastyrz zdrowie swe dawa za owieczki swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '11', '11', 'Lecz najemnik i który pastyrzem nie jest, którego nie są owce własne, widząc wilka przychodzącego odbiega owiec i ucieka, a wilk też drapie i rosprasza owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '12', '12', 'A najemnik ucieka, bo jest najemnik i nie ma nic pracej o owcach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '13', '13', 'Jamci jest on pastyrz dobry i znam owieczki moje, a moje też mnie znają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '14', '14', 'Jako mię zna Ociec, tak i ja znam Ojca, a zdrowie moje pokładam za owieczki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '15', '15', 'I drugieć też owce mam, które nie są z tej obory, a potrzeba żebych też i ony przywiódł, bo głosu mego słuchać będą i będzie jedna owczarnia i jeden pastyrz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '16', '16', 'Dlategożci mię miłuje Ociec, iż ja pokładam zdrowie moje abych je zasię wziął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '17', '17', 'Żadenci go nie odejmie odemnie, ale ja pokładam je sam od siebie i mam moc położyć je, a mam moc zasię wziąć je. To roskazanie wziąłem od Ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '18', '18', 'A przetoż się stało rozerwanie między Żydy dla słów tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '19', '19', 'Bo wiele ich mówili między sobą: Diabelstwoć ma i szaleje. Czemuż go słuchacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '20', '20', 'Drudzy zasię powiedali: Tyć słowa nie są tego, coby miał diabelstwo. Izali diabeł ślepych oczy otwarzyć może?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '21', '21', 'A był w Jeruzalem obchód Poświącania i zima była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '22', '22', 'Tedy się Jezus przechadzał po kościele w przysionku Salomonowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '23', '23', 'I obstąpili go Żydowie, a rzekli mu: I dokądże tak dusze nasze zawieszać będziesz? Jeśliżeś ty jest on Krystus, powiedzże nam jawnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '24', '24', 'Na to im dał odpowiedź Jezus: Powiedziałem wam, a nie wierzycie. Sprawy, które ja czynię w imię Ojca mego, tyć o mnie świadczą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '25', '25', 'Ale wy nie wierzycie, bo nie jesteście z owieczek moich, jakom wam powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '26', '26', 'Owieczki moje słuchając głosu mego, a jać je znam i idą za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '27', '27', 'Jać im też wieczny żywot dawam i nie zginą na wieki, ani ich żaden wydrze z mej ręki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '28', '28', 'Ociec mój, który je mnie dał, więcszy jest niż wszyscy, a żaden nie może ich wydrzeć z rąk Ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '29', '29', 'Ja i Ociec, jedno jestechmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '30', '30', 'A tak porwali zasię kamienie Żydowie, aby go ukamienowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '31', '31', 'Odpowiedział im tedy Jezus: Wiele dobrych spraw ukazałem wam z Ojca mego. Dla któregoż tedy z uczynków tych kamionujecie mię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '32', '32', 'Na to mu Żydowie odpowiedź dali mówiąc: Dla dobrego uczynku nie kamionujemy cię, ale dla bluźnierstwa, to jest, iż ty, gdyżeś jest człowiekiem, czynisz się sam Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '33', '33', 'Tedy im rzekł Jezus: Zali nie jest napisano w zakonie waszym; Jam rzekł, bogowie jesteście?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '34', '34', 'Jeśliżeć powiedział ony być bogi, do których się stało słowo Boże ( a nie może być Pismo pokażone).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '35', '35', 'Mnie, którego Ociec poświęcił i posłał na świat, wy powiedacie, żebych bluźnić miał, żem rzekł; Jestem Syn Boży?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '36', '36', 'Jeśliż nie czynię spraw Ojca mego, nie wierzcież mi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '37', '37', 'Ale jeślić czynię, chociażbyście też mnie nie wierzyli, wierzcież sprawam, abyście poznali i wierzyli żeć Ociec jest we mnie, a ja w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '38', '38', 'A tak powtóre czyhali nań jakoby go pojmać, ale uszedł z ręku ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '39', '39', 'I szedł potym za Jordan na ono miejsce gdzie był Jan krzcząc napierwej i tamże mieszkał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '40', '40', 'Lecz wiele ich do niego przychodzili a mówili: Aczci Jan żadnego cudu nie uczynił, wszakoż wszytko cokolwiek sam powiedał o tym, prawdziwe było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '9', '41', '41', 'I wiele ich tam uwierzyli weń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '0', '0', 'Niektóry też imieniem Łazarz był niemocen z Betanijej z miasteczka Marjej i Marty siostry jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '1', '1', 'A Marja ta była, która pomazała Pana olejkiem i ucierała nogi jego włosami swoimi, której brat Łazarz był niemocen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '2', '2', 'Posłały tedy siostry jego kniemu mówiąc: Panie oto ten, którego miłujesz, niemocen jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '3', '3', 'To gdy usłyszał Jezus rzekł: Niemoc ta nie jest ku śmierci, ale ku chwale Bożej, aby był uwielbion Syn Boży przez nię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '4', '4', 'I miłował Jezus Martę i siostrę jej i Łazarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '5', '5', 'A przeto słysząc, że był niemocen, przedsię jednak został przez dwa dni na onym że miejscu gdzie był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '6', '6', 'Potym mówił do zwolenników swoich: Idźmyż zasię do Juda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '7', '7', 'Rzekli mu tedy zwolennicy: Mistrzu, teraz szukali Żydowie jakoby cię ukamionować, a przedsię tam idziesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '8', '8', 'Na to Jezus odpowiedział: Zali nie dwanaście są godzin dnia? Jeśliż kto chodzi we dnie, nie obrazić się, bo widzi światło tego świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '9', '9', 'Jeśliż też kto chodzi w nocy, obrazi się, bo w nim światła nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '10', '10', 'To rzekłszy, potym do nich mówił: Łazarz przyjaciel nasz usnął, ale pójdę abych go ze snu obudził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '11', '11', 'Rzekli mu tedy zwolennicy jego: Panie jeśliżeć śpi, będzie zdrów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '12', '12', 'Aleć im Jezus powiedział o śmierci jego, wszakoż oni mniemali, iż o zaśnięciu snem powiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '13', '13', 'Tedy Jezus powiedział im jawnie: Łazarz umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '14', '14', 'I raduję się dla was (abyście wierzyli), żem nie był tam, ale idźmy ku niemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '15', '15', 'Rzekł zatym Tomasz, którego zwano Dydymus do inych zwolenników: Pódźmyż i my abychmy z nim pomarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '16', '16', 'A tak przyszedł Jezus i nalazł go już trzy dni w grobie leżącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '17', '17', '(I była Batanija blisko Jeruzalem jakoby na piącinaście stajan).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '18', '18', 'Tam się zeszło wiele Żydów do Marty i Marjej, aby je cieszyli po bracie ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '19', '19', 'Marta tedy usłyszawszy, że Jezus przyszedł, bieżała przeciwko jemu, a Marja doma siedziała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '20', '20', 'I rzekła Marta do Jezusa: Panie byś ty tu był, nie umarłby był brat mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '21', '21', 'Lecz ja i teraz wiem, że czegożkolwiek będziesz prosił od Boga, da tobie Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '22', '22', 'Powiedział jej Jezus: Wstanieć brat twój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '23', '23', 'A Marta rzekła: Wiem iż wstanie czasu z martwychwstania w on ostatni dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '24', '24', 'I rzekł jej Jezus: Jam jest Powstanie i żywot. Kto w mię wierzy, choćby i umarł, żyw będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '25', '25', 'A wszelki który żyw jest, a wierzy w mię, nie umrze na wieki. Wierzyszże temu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '26', '26', 'Powiedziała mu: I owszem Panie. Ja wierzę żeś ty jest Krystus Syn Boży, który na świat przyjdź miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '27', '27', 'A to rzekszy, szła i po cichu wezwała Marjej siostry swojej mówiąc: Mistrz tu jest i wzywa cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '28', '28', 'Ona tedy usłyszawszy, wnet wstała i szła do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '29', '29', 'A Jezus jeszcze był nie przyszedł do miasteczka, ale był na tym miejscu, gdzie wyszła była przeciw jemu Marta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '30', '30', 'A przetoż Żydowie, którzy z nią byli w domu, a cieszyli ją, gdy ujźrzeli iż Marja prędko wstała i wyszła, szli za nią mówiąc: Idzie do grobu aby tam płakała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '31', '31', 'A tak Marja przyszedszy tam kędy był Jezus, ujźrzawszy go przypadła do nóg jego mówiąc mu: Panie byś ty tu był, nie umarłby był brat mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '32', '32', 'Jezus tedy, gdy ją ujźrzał płaczącą i Żydy, którzy z nią społu przyszli płacząc, wzruszył się w duchu i zafrasował się sam w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '33', '33', 'Zatym rzekł: Gdzieżeście go położyli? Powiedzieli mu: Panie, pódź a poglądaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '34', '34', 'I płakał Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '35', '35', 'Rzekli tedy Żydowie: Oto jakoć go miłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '36', '36', 'A niektórzy z nich mówili: Nie mógłże ten, który otworzył oczy ślepego uczynić tego, żeby był ten nie umarł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '37', '37', 'Ale Jezus wzruszywszy się zaś sam w sobie, przyszedł do grobu, a była jaskinia i kamień na niej położony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '38', '38', 'Potym rzekł Jezus: Odejmicie kamień. A Marta mu powiedziała, siostra onego, który był umarł: Panie jużci cuchnie, bo mu już wyszły cztery dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '39', '39', 'Powiedział jej Jezus: Zażemci nie powiedział; Jeśli uwierzysz, oglądasz chwałę Bożą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '40', '40', 'Odjęli tedy kamień z tamtąd gdzie on umarły był położon i podniózszy Jezus oczu swych wzgórę rzekł: Ojcze, dziękuję tobie, żeś mię wysłuchał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '41', '41', 'A jaciem wiedział, że ty mnie zawżdy wysłuchawasz, leczciem to rzekł dla ludu około stojącego, aby wierzyli, żeś ty mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '42', '42', 'To tedy rzekszy zawołał głosem wielkim: Łazarzu wynidź sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '43', '43', 'A tak on, który był umarł, wyszedł mając ręce i nogi zawiązane chustkami, a twarz jego była chustą zawiniona. I rzekł im Jezus: Rozwiążcie go niechaj idzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '44', '44', 'A przetoż wiele Żydów, którzy byli przyszli do Marjej, a patrzyli na to co uczynił Jezus, uwierzyli weń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '45', '45', 'Niektórzy też z nich szli do Faryzeuszów i powiedzieli im co uczynił Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '46', '46', 'Tedy zebrali się kapłani nawyższy i Faryzeuszowie w radę i mówili: Cóż czynić będziemy? Abowiem ten człowiek wiele cudów czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '47', '47', 'Jeśliż go tak zaniechamy, wszyscyć weń uwierzą i przyjdąć Rzymianie a poburzą, tak miejsce nasze, jak i naród.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '48', '48', 'Ale jeden z nich Kajafasz, co był na on rok nawyższym kapłanem rzekł im: Wy nic nie wiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '49', '49', 'Ani myślicie co nam jest pożyteczno, żeby jeden człowiek umarł za lud, a żeby wszytek naród nie zginął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '50', '50', 'I nie mówiłci tego sam z siebie, ale gdyż był kapłanem nawyższym roku onego prorokował, że to miało być, iż Jezus miał umrzeć za on naród.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '51', '51', 'A nie tylko za on naród, ale żeby też syny Boże rosproszone, zgromadził w jedno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '52', '52', 'I także od onego dnia radzili się społu aby go zamordowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '53', '53', 'A dlategoż Jezus już nie bywał jawnie między Żydy, ale z tamtąd odszedł do krainy, która jest blisko puszczej , do miasta, które zową Efraim i tamże mieszkał z zwolenniki swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '54', '54', 'I była blisko Wielka noc Żydowska, a wiele ich szło do Jeruzalem z onej krainy przed Wielką nocą, aby się oczyścili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '55', '55', 'Szukali tedy Jezusa i mówili jedni do drugich stojąc w kościele: Co się wam zda, izali nie przyjdzie na święto?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '10', '56', '56', 'A kapłani nawyższy i Faryzeuszowie podali byli roskazanie, iż jeśliby się kto dowiedział gdzie by był, żeby oznajmił, iżby go pojmano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '0', '0', 'Potym Jezus dnia szóstego przed Wielką nocą przyszedł do Betanijej, kędy był Łazarz umarł, którego on wzbudził od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '1', '1', 'Tamże tedy, sprawili mu wieczerzę, a Marta służyła i był też Łazarz jednym z onych, którzy z nim społu siedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '2', '2', 'Tedy Marja, wziąwszy funt olejku spikanardowego, barzo drogiego, natarła nogi Jezusowe i ucierała nogi jego włosami swoimi tak, iż dom pełen był wonności maści onej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '3', '3', 'I rzekł niektóry z zwolenników jego Judasz, Symonów syn, Iskarjot, który go miał zdradzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '4', '4', 'Przeczże tego olejku nie przedano za trzy sta groszy, a nie dano ubogim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '5', '5', 'A toć mówił, nie iżby miał pieczę o ubogich, ale iż był złodziej i mieszek miał, a cokolwiek tam włożono, to nosił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '6', '6', 'A przetoż rzekł Jezus: Zaniechaj jej, na dzieńci pogrzebu mego zachowała to.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '7', '7', 'Abowiem ubogie zawżdy zsobą mieć będziecie, lecz mnie nie zawżdy będziecie mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '8', '8', 'Dowiedziało się tedy wielkie zgromadzenie Żydów, iż tam był i przyszli nie tylko dla Jezusa, ale żeby też byli ujźrzeli Łazarza, którego wzbudził od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '9', '9', 'I radziły się książęta kapłańskie żeby i Łazarza zabili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '10', '10', 'Bo wiele ich z Żydów dla niego odstali i wierzyli w Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '11', '11', 'Nazajutrz zgromadzenie wielkie, które się było zebrało na święto, gdy usłyszeli, że Jezus szedł do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '12', '12', 'Nabrali rózdżek palmowych i wyszli w drogę przeciw jemu, a wołali: Hosanna ! Błogosławiony, który przyszedł w imię Pańskie, Król on izraelski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '13', '13', 'A nalazszy Jezus oślątko, wsiadł na nie, jako napisano jest:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '14', '14', 'Nie bój się córko Syońska, otoć Król on twój jedzie, siedząc na oślęciu oślice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '15', '15', 'A temuć z przodku nie rozumieli zwolennicy jego, ale gdy był uwielbion Jezus, tedy wspomnieli, iż to było o nim napisano, a iż mu to czynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '16', '16', 'I poświadczył lud, który był przy nim, iż Łazarza wyzwał z grobu a wybudził go od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '17', '17', 'A dlategoż też wyszło mnóstwo ludzi przeciwko jemu w drogę, bo o nim słyszeli iż ten cud uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '18', '18', 'Mówili tedy Faryzeuszowie między sobą: Widzicie, że nic uczynić nie możecie. Oto lud za nim poszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '19', '19', 'Byli też niektórzy Grekowie z tych, którzy zwykli chodzić aby się modlili w święto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '20', '20', 'A tak oni przyszli do Filipa, który był z Betsajdy Galilejskiej i prosili go mówiąc: Panie chcemy Jezusa widzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '21', '21', 'I przyszedszy Filip powiedział Andrzejowi, Andrzej zasię i Filip powiedzieli Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '22', '22', 'Tedy im Jezus odpowiedział mówiąc: Przyszedłci czas aby był uwielbion Syn człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '23', '23', 'Zaprawdę, zaprawdę wam powiedam, jeśli ziarno pszeniczne upadszy w ziemię nie obmartwieje, samo tak zostanie, lecz jeśli obmartwieje, wielki owoc przyniesie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '24', '24', 'Ktoć miłuje duszę swą, utraci ją, a kto nie nawidzi duszę swojej na tym świecie, ku wiecznemu żywotowi zachowa ją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '25', '25', 'Jeśli mnie kto służy, niechżeć za mną idzie, abowiem gdzie ja będę, tam i sługa mój będzie. A jeśli mnie kto służyć będzie, uczci go Ojciec mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '26', '26', 'Terazci dusza moja zatrwożona jest i cóż rzekę? Ojcze zachowaj mię od tej godziny, leczciem dlatego przyszedł na tę godzinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '27', '27', 'Ojcze uwielbi imię twoje. A tak przyszedł głos z nieba mówiąc: I uwielbiłem i jeszcze uwielbię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '28', '28', 'A tak lud, który stał i słyszał, powiedał iż zagrzmiało, a drudzy mówili, Anioł mówił do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '29', '29', 'I odpowiedziawszy Jezus rzekł: Nie dla mnieć się ten głos stał, ale dla was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '30', '30', 'Terazci jest sąd świata tego. Terazci książę tego świata precz wyrzucone będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '31', '31', 'A ja, jeśli będę podwyższon od ziemię, pociągnę wszytki za sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '32', '32', '(I mówił to oznajmując jakową śmiercią miał umrzeć).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '33', '33', 'Tedy mu odpowiedziało zgromadzenie: My słyszelichmy z zakonu, że Krystus trwa wiecznie. Jakoż ty tedy powiedasz , iż potrzeba aby był powyższon Syn człowieczy? I któryż to jest Syn człowieczy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '34', '34', 'A przetoż im rzekł Jezus: Jeszcze do małego czasu jest w was światłość. Chodźcież tedy póki światłość macie, żeby was ciemności nie ogarnęły, bo kto w pociemku chodzi, nie wie gdzie idzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '35', '35', 'Gdy światłość macie, wierzcież w światłość, abyście synami światłości byli. A to powiedziawszy Jezus, wyszedł i schronił się od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '36', '36', 'I gdy tak wiele cudów czynił przed nimi, przedsię weń nie wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '37', '37', 'Aby się wypełniło słowo Ezajasza proroka, które powiedział: Panie i któż wierzył powieści naszej? A ramię Pańskie komuż jest objawione?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '38', '38', 'I dlategoć wierzyć nie mogli. Bo też jeszcze powiedział Ezajasz:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '39', '39', 'Zaślepił oczy ich i zatwardził serca ich, aby nie widzieli oczyma, a nie rozumieli sercami, żeby się nie nawrócili, a nie uleczyłbych je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '40', '40', 'Toć powiedał Ezajasz, gdy widział chwałę jego, a mówił o nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '41', '41', 'Wiele też książąt uwierzyli weń, lecz dla Faryzeuszów nie uznawali, aby z bożnice ruszeni nie byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '42', '42', 'Bo umiłowali byli chwałę ludzką radszej niźli chwałę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '43', '43', 'I wołał Jezus głosem wielkim a mówił: Kto w mię wierzy, nie w mię wierzy, ale w onego, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '44', '44', 'A ktoć na mię patrza, patrza na onego, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '45', '45', 'Ja światłość przyszedłem na świat, aby żaden, który wierzy w mię w ciemnościach nie został.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '46', '46', 'Abowiem jeśli kto słuchać będzie słów moich, a nie uwierzy, jać go nie potępiam, bom nie przyszedł żebych miał potępiać świat, ale żebych świat zachował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '47', '47', 'Kto mię odmiata, a nie przyjmuje słów moich, ma tego, który go potępi. Słowo, któreciem ja powiedał, onoż go osądzi w on ostateczny dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '48', '48', 'Gdyżem ja nic z siebie samego nie mówił, lecz ten, który mię posłał Ojciec, ten mi roskazał co mam mówić i powiedać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '11', '49', '49', 'I wiem, że roskazanie jego jest żywot wieczny. A przetoż to co ja powiedam, jako mi powiedział Ociec, tak powiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '0', '0', 'Tedy przed świętem Wielkonocnym, wiedząc Jezus, iże przyszła godzina jego, aby odszedł z tego świata do Ojca, gdyż umiłował swe, którzy byli na świecie i aż do końca umiłował je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '1', '1', 'A odprawiwszy wieczerzę wieczerzę, (gdy diabeł już był przypuścił k sercu Judaszowi, synowi Symonowemu Iskarjotowi, żeby go zdradził).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '2', '2', 'Wiedząc Jezus, że wszytko ociec podał do rąk jemu, a iż od Boga wyszedł i do Boga idź miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '3', '3', 'Wstał od wieczerzy, a ziąwszy płaszcz, wziął ręcznik i przepasał się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '4', '4', 'Potym nalał wody do miednice i począł nogi umywać zwolennikom, a ucierać ręcznikiem, którym się był opasał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '5', '5', 'Zatym przyszedł do Symona Piotra, ale mu on rzekł: Panie a tyż to mnie nogi masz umywać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '6', '6', 'Odpowiedział Jezus i rzekł mu: Co ja czynię ty nie wiesz teraz, ale się dowiesz potym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '7', '7', 'Powiedział mu Piotr: Nie będziesz umywał moich nóg na wieki. A Jezus rzekł: Jeślicie nie umyję, nic ze mną społu mieć nie będziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '8', '8', 'Tedy mu rzekł Symon Piotr: Panie nie tylko nogi moje, lecz i ręce i głowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '9', '9', 'Powiedział mu Jezus: Ktoć umyt jest, nie potrzeba mu jedno aby nogi umył, ale jest czyst wszytek i wy jesteście czystemi, jedno nie wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '10', '10', 'Abowiem wiedział, który był co go zdradzić miał i dlategoż powiedział: Nie wszyscyście czystymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '11', '11', 'Umywszy potym nogi ich, a wziąwszy szaty swe, siadł zasię i rzekł do nich: Rozumiecież to com wam uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '12', '12', 'Wy mnie nazywacie Mistrzem i Panem, a dobrze mówicie, bom im jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '13', '13', 'A przetoż jeślim ja umył nogi wasze, będąc Panem a Mistrzem, tedy wy także społu powinniście jedni drugim umywać nogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '14', '14', 'Abowiemem wam dał przykład, iż jakom ja wam uczynił, abyście i wy tak czynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '15', '15', 'Zaprawdę, zaprawdę wam powiedam; Sługa nie jestci więczszy nad pana swego, ani poseł jest więcszy nad onego, który go posyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '16', '16', 'Jeśliż temu rozumiecie, błogosławionemi będziecie, jeśliż to uczynicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '17', '17', 'Nie o wszytkichci was mówię, jać wiem, którem obrał, ale żeby się wypełniło Pismo ten, który zemną je chleb, podniósł już teraz przeciwko mnie piętę swą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '18', '18', 'Terazci wam powiedam przed tym niż będzie, że gdy się to stanie abyście wierzyli iżciem ja on jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '19', '19', 'Zaprawdę, zaprawdę wam powiedam: Któżkolwiek przyjmuje tego, któregobym posłał, mnie przyjmie, a który mnie przyjmuje, onego przyjmuje, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '20', '20', 'To gdy rzekł Jezus, zatrwożył się w duchu a oświadczając się rzekł: Zaprawdę, zaprawdę wam powiedam, że jeden z was zdradzi mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '21', '21', 'A przetoż pojźrzeli zwolennicy po sobie, wątpiąc o kimby powiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '22', '22', 'A był niektóry z zwolenników Jezusowych, który się położył na łonie jego, on którego miłował Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '23', '23', 'Tedy onemu dał znać Symon Piotr, aby się wypytał, któryby to był co o nim powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '24', '24', 'A tak on położywszy się na piersiach Jezusowych rzekł mu: Panie, któryż to jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '25', '25', 'Odpowiedział Jezus: Onci jest, któremu ja omoczywszy sztuczkę chleba podam, a rozmoczywszy podał sztuczkę synowi Symonowemu, Iskarjotowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '26', '26', 'Po onej tedy sztuce wstąpił weń szatan. A przetoż mu rzekł Jezus: Co czynisz, czyń rychlej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '27', '27', 'A tego żaden nie rozumiał z tych co siedzieli nacz mu to rzekł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '28', '28', 'Abowiem niektórzy mnimali, gdyż Judasz miał mieszek, iż mu rzekł Jezus: Nakup tych rzeczy, których nam potrzeba na święto, abo iżby co dał ubogim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '29', '29', 'A tak on wziąwszy onę sztuczkę, natychmiast wyszedł, a noc też już była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '30', '30', 'Gdy tedy wyszedł, rzekł Jezus: Terazci jest uwielbion Syn człowieczy, a Bóg uwielbion jest w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '31', '31', 'I jeśliż Bóg uwielbion jest w nim, tedyć też Bóg uwielbi go w sobie samym i natychmiast go uwielbi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '32', '32', 'Synaczkowie jeszczeciem maluczko jest z wami, będziecie mię szukać, ale jakom powiedział Żydom: Gdzie ja idę wy przyjdź nie możecie, takżeć też i wam teraz powiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '33', '33', 'Roskazanie nowe dawam wam, abyście jedni drugie miłowali i jakom ja was umiłował, abyście się i wy społu miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '34', '34', 'Z tądci poznają wszyscy, że wy jesteście zwolennicy moi, jeśliż miłość mieć będziecie jedni przeciw drugim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '35', '35', 'Powiedział mu Symon Piotr: Panie dokądże idziesz? Odpowiedział mu Jezus: Tam gdzie ja idę, nie możesz teraz za mną idź, lecz potym pójdziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '36', '36', 'Tedy mu rzekł Piotr: Panie przecz za tobą nie mogę idź teraz? Zdrowie moje położę za cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '12', '37', '37', 'I odpowiedział mu Jezus: Zdrowie twe za mię położysz? Zaprawdę, zaprawdę powiedam tobie: Nie zapoje kur aż się mnie po trzy kroć zaprzysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '0', '0', 'Niechaj się nie frasuje serce wasze, wierzycie w Boga, też i w mię wierzcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '1', '1', 'W domu Ojca mojego wieleć jest mieszkania, jeśliżby inaczej było, powiedziałcibych wam. Idęć abych wam nagotował miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '2', '2', 'A gdy odejdę i zgotuję wam miejsce, przyjdeć zasię i wezmę was do siebie, iż gdzie ja jestem, abyście i wy byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '3', '3', 'A wiecie do kądci ja idę i drogę wiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '4', '4', 'Powiedział mu Tomasz: Panie nie wiemy dokąd idziesz i jakoż możem drogę wiedzieć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '5', '5', 'Rzekł mu tedy Jezus: Jać jestem droga, prawda i żywot. Żaden nie przychodzi do Ojca jedno przez mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '6', '6', 'Gdybyście mię znali z Ojca byście też mego znali a już go teraz znacie i widzieliście go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '7', '7', 'Powiedział mu Filip: Panie, ukaż nam Ojca, a dosyć nam na tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '8', '8', 'Ale mu rzekł Jezus: Przez tak długi czas jestem z wami i jeszczeż mię nie znacie? Filipie! Ktoć mnie widział widział i Ojca. Jakoż ty tedy mówisz: Ukaż nam Ojca?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '9', '9', 'Nie wierzyszże, iż ja w Ojcu jestem a Ojciec jest we mnie? Słowa któreć ja wam powiedam, nie od samego siebie ich powiedam, ale Ociec któryć we mnie mieszka, tenci sam czyni sprawy ty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '10', '10', 'Wierzcież mi iżci ja w Ojcu jestem, a Ojciec we mnie. Jeśliż inaczej, dla onych samych spraw wierzcie mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '11', '11', 'Zaprawdę, zaprawdę wam powiedam. Kto wierzy w mię, sprawy ty które ja czynię i on czynić będzie i więczsze a niż ty, czynić będzie, bo ja do Ojca odchodzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '12', '12', 'Oczkolwiek tedy będziecie prosić w imię moje, to uczynię, aby był uwielbion Ociec w Synu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '13', '13', 'A tak oczkolwiek będziecie prosić w imię moje, ja uczynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '14', '14', 'Jeśliż mię miłujecie, chowajcież roskazania moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '15', '15', 'I będęć ja prosił Ojca, a inego Pocieszyciela da wam, aby z wami mieszkał na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '16', '16', 'A to, Ducha onego prawdy, którego świat przyjąć nie może, bo go nie widzi, ani go zna, lecz wy znacie go, gdyż u was mieszka i w was będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '17', '17', 'Nie zostawięć was osierociałymi, a wrócęć się do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '18', '18', 'Jeszczeć maluczko a świat mię już więcej nie ogląda, lecz wy oglądacie, bo ja żywę i wy żyć będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '19', '19', 'W on dzień wy poznacie , żeciem ja jest w Ojcu moim, a wy we mnie i ja w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '20', '20', 'Któryć się trzyma roskazania mego i zachowywa je, tenci jest który mnie miłuje. A kto mnie miłuje, będzie go też miłował Ociec mój i ja go miłować będę, a objawię mu siebie samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '21', '21', 'Powiedział mu Judasz, nie on Iskarjot: Panie, cóż to jest? Przecz się tylko nam objawić masz a nie światu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '22', '22', 'Odpowiedział Jezus i rzekł mu: Jeśli mię kto miłuje, słowa moje zachowywać będzie, a Ociec mój umiłuje go i do niego przyjdziemy, a mieszkanie u niego uczyniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '23', '23', 'Ktoć mnie nie miłuje, słówci moich nie zachowywa, a słowo które słyszycie, nie jestci moje, ale onego, który mię posłał, Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '24', '24', 'Tomci wam powiedział u was mieszkając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '25', '25', 'Lecz Pocieszyciel, on Duch święty, którego poszle Ociec w imię moje, onci was nauczy wszytkiego i przywiedzie wam ku pamięci wszytko com wam powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '26', '26', 'Pokój zostawuję wam, pokój on mój dawam wam, nie tak jako dawa świat, ja wam dawam. Niechżeć się nie trwoży serce wasze, ani się lęka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '27', '27', 'Słyszeliście żem ja wam powiedział: Odchodzę i wrócę się do was, gdybyście mię miłowali, wżdybyście się weselili, żem wam powiedział: Idę do Ojca. Bo Ociec więczszy niźlim ja jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '28', '28', 'I terazciem wam powiedział przed tym niż się stanie, iż gdy się to stanie, żebyście wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '29', '29', 'Już dalej z wami wiele mówić nie będę, abowiem idzie książę świata tego, a we mnieć nic nie ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '13', '30', '30', 'Ale iżby poznał świat, że ja miłuję Ojca, a jako mnie roskazał Ociec, tak czynię. Wstańcież, pódźmy z tąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '0', '0', 'Jam jest ona winna macica prawdziwa, a Ociec mój jest winarzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '1', '1', 'Wszelką latorośl we mnie nie przynoszącą owocu odetnie a wszelką, która przynosi owoc oczyścia, aby obfitszy owoc przyniosła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '2', '2', 'Już wy jesteście czystymi dla słowa, którem wam powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '3', '3', 'Mieszkajcież we mnie, a ja w was. Bo jako latorośl nie może przynieść owocu sama z siebie jeśliż nie będzie trwać w macicy, także ani wy jeśli we mnie trwać nie będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '4', '4', 'Jaciem jest macica, a wyście są latorośl. Ten który mieszka we mnie i w kim ja też mieszkam, ten przynosi wielki owoc, bo przezemnie nic nie możecie uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '5', '5', 'Jeśliż kto we mnie trwać nie będzie, tedy precz wyrzuconym będąc, wnet jako latorośl uschnie i potym ty latorośli zbierają i do ognia miecą a pogorają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '6', '6', 'Jeśliż we mnie trwać będziecie, a słowa też moje w was trwać będą, czegokolwiek będziecie chcieć, proście i stanie się wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '7', '7', 'W tymci jest uwielbion Ociec mój, iżbyście wy wiele owocu przynieśli, a będziecie moimi zwolenniki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '8', '8', 'Jako mię umiłował Ociec, tak i ja umiłowałem was. Trwajcież tedy w onej miłości mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '9', '9', 'Jeśliż roskazania moje zachowywać będziecie, zostaniecie w miłości mojej, tak jakom ja zachowywał roskazania Ojca mego i zostawam w miłości jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '10', '10', 'Tomci wam powiedział, aby ono wesele moje w was zostało, a wypełniło się wesele wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '11', '11', 'Toć jest moje roskazanie, abyście jedni drugie miłowali, jakom ja was umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '12', '12', 'Więczszej nad tę miłości żaden nie ma, jedno gdy kto zdrowie swe kładzie za swe przyjacioły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '13', '13', 'Wy jesteście przyjaciele moi, jeśliż czynić będziecie to cokolwiek ja wam roskazuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '14', '14', 'Już was dalej nie będę nazywał sługami, gdyż sługa nie wie co czyni Pan jego, lecz was nazwałem przyjacioły, bo to wszytko com słyszał od Ojca mego, oznajmiłem wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '15', '15', 'Nie wyście mnie obrali, alem ja obrał was i postanowiłem was, abyście szli i przynieśli owoc, a iżby owoc wasz trwał, iż oczkolwiekbyście prosili od Ojca w imię moje, żeby wam dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '16', '16', 'Toć wam powiedam abyście się społecznie miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '17', '17', 'Jeśliżeć was świat ma w nienawiści, wiecie żeć i mnie pierwej miał w nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '18', '18', 'Byście byli z świata, tedyćby świat to co jest jego miłował, lecz iżeście nie są z świata, a jam was wybrał z świata i dlategoż was świat ma w nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '19', '19', 'Wspomnicie na słowa, którem ja wam powiedział. Nie jestci sługa więczszy niż Pan jego, jeśliżeć mnie prześladowali i wasci prześladować będą, a jeślić słowa me zachowywali i wasze zachowywać będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '20', '20', 'Aleć wam to wszytko czynić będą dla imienia mego, bo nie znali onego, który mię posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '21', '21', 'Bychci był nie przyszedł, a nie powiedał im, nie mieliby grzechu, lecz teraz nie mają czymby się wywieść z grzechu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '22', '22', 'Ktoć mnie ma w nienawiści, ma i Ojca mego w nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '23', '23', 'Bych był między nimi tych spraw nie czynił, których żaden nie czynił, nie mieliby grzechu, lecz teraz widzieli, a mieli nie tylko mnie w nienawiści, ale też i Ojca mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '24', '24', 'Aleć się to dzieje, aby się wypełniło słowo, które jest w zakonie ich napisano. Mieli mię niesłusznie w nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '25', '25', 'A tak gdyć przyjdzie Pocieszyciel, którego ja wam poślę od Ojca, Duch on prawdy, który od Ojca pochodzi, onci o mnie da świadectwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '14', '26', '26', 'I wy też dacie świadectwo, bo ze mną jesteście od początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '0', '0', 'Tomci wam powiedział abyście się nie obrażali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '1', '1', 'Będąć was ruszać z bożnic, owszem przydzie czas, gdy wszelki, który was zabijać będzie, mniemać będzie że czyni chwałę Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '2', '2', 'A toć wam czynić będą, iż nie znali Ojca ani mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '3', '3', 'Alemci to wam powiedział, abyście gdy czas ten przyjdzie, wspomnieli na to żem ja powiedział wam. A tych rzeczy nie powiedałem wam z przodku, bom z wami był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '4', '4', 'Lecz teraz odchodzę do onego, który mię posłał i nie pyta mię żaden z was do kąd idę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '5', '5', 'A tak, iżem to wam powiedział, smutek napełnił serce wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '6', '6', 'I powiedamci wam prawdę. Wamci to pożyteczno iż ja odejdę, bo jeślibych nie odszedł, Pocieszyciel on nie przyjdzie do was, ale jeśliż pójdę, poślę go do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '7', '7', 'Gdyć on tedy przyjdzie, będzieć karał świat z grzechu, z sprawiedliwości i z sądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '8', '8', 'Z grzechu, iż nie uwierzyli w mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '9', '9', 'Z sprawiedliwości zasię, iż do Ojca odchodzę, a już mię więcej nie ujźrzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '10', '10', 'Z sądu też zasię, że książę tego świata już osądzone jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '11', '11', 'Jeszczeć wiele mam cobych z wami mówił, jedno iż teraz znieść nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '12', '12', 'Ale gdyć przyjdzie od Duch prawdy, tedyć was wprowadzi we wszelką prawdę, bo nie sam od siebie mówić będzie, ale co usłyszy to powie, a co będzie przyjdź miało oznajmi wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '13', '13', 'Onci mię uwielbi, bowiem z mego weźmie co wam opowiedać będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '14', '14', 'Wszytki rzeczy, które ma Ociec, moje są i dlategożem powiedział, że z mego weźmie, co wam opowiedać będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '15', '15', 'Maluczko a nie ujźrzycie mię i zasię maluczko a ujźrzycie mię, bo ja idę do Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '16', '16', 'Tedy niektórzy z zwolenników jego rzekli jedni ku drugim: Cóż to jest co nam powieda: Maluczko a nie ujźrzycie mię i zasię maluczko a ujźrzycie mię, a iż ja idę do Ojca?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '17', '17', 'I mówili: Cóż wżdy to jest co mówi; maluczko? Nie wiemy coć powieda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '18', '18', 'Poznał tedy Jezus, że go pytać chcieli i rzekł im: O tym się pytacie między sobą com rzekł: Maluczko, a nie ujźrzycie mię i zasię maluczko a ujźrzycie mię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '19', '19', 'Zaprawdę, zaprawdę wam powiedam: Wy będziecie płakać i narzekać a świat się będzie weselił. Wy smutni będziecie, ale smutek wasz wynidzie wam ku radości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '20', '20', 'Gdy niewiasta rodzi, miewać boleści, bo przyszła godzina jej, lecz gdy porodzi dzieciątko, już nie pamięta uciśnienia, że się weseli z człowieka na świat narodzonego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '21', '21', 'I wy też acz teraz smutek macie, ale zasię oglądam was i będzie się weselić serce wasze, a wesela waszego żaden nie odejmie od was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '22', '22', 'Na on czas nie będziecie mię ni ocz pytać. Zaprawdę, zaprawdę powiedamci wam, oczkolwiek prosić będziecie Ojca w imię moje, da wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '23', '23', 'Do tego czasu jeszczeście ni ocz nie prosili w imię moje. Prościesz a weźmiecie, aby wesele wasze było doskonałe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '24', '24', 'Tociem wam powiedał przez podobieństwa, leczci czas przyjdzie, gdy nie przez podobieństwa mówić wam będę, ale jawnie o Ojcu moim oznajmię wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '25', '25', 'Na on czas w imię moje prosić będziecie i nie powiedam wam, iżbych ja miał Ojca prosić za wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '26', '26', 'Abowiem sam Ociec miłuje was, żeście i wy mnie miłowali i uwierzyliście żem od Boga wyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '27', '27', 'Wyszedłciem od Ojca, a przyszedłem na świat i powtóre opuszczam świat, a idę do Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '28', '28', 'Powiedzieli mu zwolennicy: Oto teraz jawnie mówisz i żadnego podobieństwa nie powiedasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '29', '29', 'Teraz wiemy, że wszytko wiesz, a nie potrzeba żeby cię kto pytał , a przez to wierzymy, żeś od Boga wyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '30', '30', 'Odpowiedział im Jezus: I terazże wierzycie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '31', '31', 'Otoć przyjdzie czas i już przyszedł, gdy wszyscy rosproszeni będziecie, każdy do swych, a mnie samego zostawicie, aleć nie jestem sam, bo jest Ociec zemną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '15', '32', '32', 'Tomci wam powiedział, abyście we mnie pokój mieli. Na świecie ścisk macie, ale ufajcie we mnie. Jamci zwyciężył świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '0', '0', 'To powiedziawszy, Jezus podniósł oczu swych w niebo i rzekł: Ojcze przyszłać godzina. Uwielbi Syna twego, aby też i Syn uwielbił ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '1', '1', 'Jakoś jemu dał moc nad wszelkim ciałem, iż ileś mu ich podał, aby im dał żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '2', '2', 'A tenci jest żywot wieczny, aby ciebie samego znali być Bogiem prawdziwym i któregoś posłał Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '3', '3', 'Jaciem uwielbił ciebie na ziemi i dokończyłem sprawy, którąś mi dał, abych ją czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '4', '4', 'A przetoż teraz uwielb mię ty Ojcze u siebie samego tą chwałą, któram u ciebie miał, pirwej niż świat był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '5', '5', 'Objawiłciem imię twe ludziam, któreś mi podał z tego świata, twoić byli i dałeś mi je, a zachowywali słowo twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '6', '6', 'Terazci poznali iż wszytki rzeczy, któreś mnie dał, od ciebie są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '7', '7', 'Abowiem słowa, któreś mi podał, jam im podał, a oni je wzięli i poznali prawdziwie, żem od ciebie wyszedł, a uwierzyli, żeś ty mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '8', '8', 'Jać za nimi proszę, a nie za światem proszę, lecz za tymi, któreś mi dał, boć są twoi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '9', '9', 'I wszytko moje jest twoje, a twoje moje jest i uwielbionem jest w nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '10', '10', 'A jużem nie jest na świecie, jedno oni na świecie są. Jać już do ciebie idę. Ojcze święty zachowajże je przez imię twoje, ty któreś mnie podał, aby byli jedno tak jako i my.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '11', '11', 'Gdyciem z nimi był na świecie, jaciem je zachowywał w imię twoje, a któreś mnie oddał jam ich strzegł i żaden z nich nie zginął, jednoż on syn zatracenia, żeby się Pismo wypełniło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '12', '12', 'A terazci do ciebie idę i mówię to na świecie, aby mieli wesele moje wypełnine w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '13', '13', 'Jaciem im podał słowo twoje, a świat miał je w nienawiści, bo nie są z świata, jako i ja nie jestem z świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '14', '14', 'Nie proszę abyś je ziął z świata, ale żebyś je zachował ode złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '15', '15', 'Nie sąć z świata, jako i ja nie jestem z świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '16', '16', 'Poświęćże je oną twoją prawdą; słowoć twoje jest prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '17', '17', 'Jakoś ty mnie posłał na świat, tak i ja posłałem je na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '18', '18', 'A za nieć ja poświęcam samego siebie, aby i oni byli poświęceni przez prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '19', '19', 'I nie tylkoć się za nimi modlę, lecz i za onymi, którzy przez słowo ich mają uwierzyć w mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '20', '20', 'Aby wszyscy byli jedno, jako ty Ojcze we mnie a ja w tobie, aby też oni w nas jedno byli, a iżby uwierzył świat żeś ty mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '21', '21', 'Jać tę chwałę, którąś ty mnie dał, dałem im, aby byli jedno, jako i my jedno jestechmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '22', '22', 'Ja w nich a ty we mnie, aby byli doskonałymi w jedno, a iżby poznał świat żeś ty mnie posłał i umiłowałeś je jakoś i mnie umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '23', '23', 'Ojcze, ty któreś mnie podał, żądam aby tam gdzie ja jestem i oni byli zemną, aby patrzyli na onę moję chwałę, którąś ty mnie dał. Abowiemeś ty mnie umiłował przed tym niż jeszcze były założone fundamenty świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '24', '24', 'Ojcze sprawiedliwy ciebieć świat nie poznał, alem ja ciebie poznał i ci poznali żeś ty mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '16', '25', '25', 'A oznajmiłem im imię twoje i oznajmię, aby miłość, którąś mnie umiłował w nich była i ja w nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '0', '0', 'Ty rzeczy powiedziawszy Jezus, wyszedł z zwolennikami swemi za potok Cedron kędy był ogród, do którego on wszedł i zwolennicy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '1', '1', 'I wiedział też ono miejsce Judasz, który go zdradzał, abowiem tam częstokroć się zchadzał Jezus z zwolenniki swojemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '2', '2', 'A tak Judasz wziąwszy rotę i od nawyższych kapłanów i od Faryzeuszów służebniki, przyszedł tam z laternami, z pochodniami i z zbrojami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '3', '3', 'Tedy Jezus wiedząc o wszytkim co nań przydź miało, wyszedszy rzekł im: Kogoż szukacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '4', '4', 'Odpowiedzieli mu: Jezusa Nazareńskiego. Rzekł im Jezus: Jamci jest. A z nimi też stał Judasz, który go zdradził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '5', '5', 'Jak im tedy skoro rzekł: Jaciem jest, postąpili się na zad i padli na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '6', '6', 'Potym ich powtóre pytał: Kogoż szukacie? A oni powiedzieli: Jezusa Nazareńskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '7', '7', 'Odpowiedział Jezus: Powiedziałem wam iżciem ja jest. Jeśliż tedy mnie szukacie, dopuśćciesz tym wolno odejdź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '8', '8', 'A to się działo, żeby się wypełniło Pismo, które powiedziało: Iż z tych, któreś mi podał, żadnegom nie stracił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '9', '9', 'A tak Symon Piotr mając miecz, dobył go i uderzył sługę kapłana nawyższego, a uciął mu ucho prawe, a temu słudze było imię Malchusz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '10', '10', 'Tedy Jezus rzekł Piotrowi: Włóż miecz twój w poszwy, izali nie mam pić kubka, który mnie podał ociec mój?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '11', '11', 'Rota tedy i rotmistrz i służebnicy żydowscy pojmali Jezusa i związali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '12', '12', 'A wiedli go naprzód do Annasza (bo był świekier Kajafasów, który był nawyższym kapłanem roku onego). Ten zasię odesłał go związanego Kajafasowi kapłanowi nawyższemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '13', '13', 'Tenci Kajafas był, który dał Żydom radę: Iżby to pożyteczno było, aby jeden człowiek umarł za lud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '14', '14', 'I szedł za Jezusem Symon Piotr i drugi zwolennik, a ten to zwolennik był znajomy kapłanowi nawyższemu i wszedł społu z Jezusem do dworu kapłana nawyższego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '15', '15', 'A Piotr stał u drzwi na dworze i wyszedszy on drugi zwolennik, którego znał nawyższy kapłan, mówił z odźwierną i w wiódł Piotra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '16', '16', 'Rzekła tedy Piotrowi służebnica odźwierna: Zaliś i ty nie jest z zwolenników człowieka tego? A on powiedział: Nie jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '17', '17', 'I stali słudzy i oprawcy, którzy byli nakładli ogień, bo zimno było i grzali się. A stał też z nimi i Piotr grzejąc się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '18', '18', 'A tak nawyższy kapłan pytał Jezusa o jego zwolennikoch i o nauce jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '19', '19', 'Odpowiedział mu Jezus: Jaciem jawnie powiedał światu, jamci zawsze uczył w bożnicy i w kościele, gdzie się zewsząd Żydowie schadzają a skrycie nicem nie mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '20', '20', 'Przeczże mię pytasz? Pytaj tych, którzy słuchali com im mówił, cić oto wiedzą com ja powiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '21', '21', 'To gdy mówił, tedy jeden z sług, który tuż stał, uderzył palcatem Jezusa mówiąc: I także odpowiedasz nawyższemu kapłanowi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '22', '22', 'Odpowiedział mu Jezus: Jeśliżem źle co rzekł, daj świadectwo o złym, a jeśliż dobrze, przeczże mię bijesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '23', '23', 'A takci go był Annasz odesłał związanego do Kajafasa, nawyższego kapłana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '24', '24', 'A Symon Piotr stał grzejąc się i rzekli do niego: Azaź i ty nie jesteś z jego zwolenników? A on zaprzał się mówiąc: Nie jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '25', '25', 'Tedy mu rzekł niektóry z sług kapłana nawyższego, powinowaty onego, któremu był Piotr uciął ucho: Izażem ja ciebie nie widział w ogrodzie przy nim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '26', '26', 'Ale zasię Piotr zaprzał i natychmiast kur zapiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '27', '27', 'Odwiedli tedy Jezusa od Kajafasa na ratusz, a było poranu i nie weszli sami na Ratusz, aby się nie splugawili, ale iżby pożywali Wielkiejnocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '28', '28', 'A tak Piłat wyszedł do nich na dwór i rzekł: Jakąż skargę przynosicie przeciw człowiekowi temu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '29', '29', 'Odpowiedzieli mu i rzekli: Być ten nie był złoczyńca tedyćbychmy go nie podali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '30', '30', 'Rzekł im tedy Piłat: Weźmicież go wy, a wedle zakonu waszego osądzcie go. Ale mu powiedzieli Żydowie: Namci się nie godzi zabijać nikogo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '31', '31', 'Aby się wypełniło słowo Jezusowe, które powiedział oznajmując, którą śmiercią miał umrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '32', '32', 'A przetoż zasię wszedł Piłat do ratusza i wezwawszy Jezusa rzekł mu: Tyżeś jest Król Żydowski?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '33', '33', 'Odpowiedział mu Jezus: A samże to od siebie mówisz, czylić drudzy o mnie powiedali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '34', '34', 'Odpowiedział Piłat: Azaźem ja jest Żyd? Naród twój i przedniejszy twoi kapłani podali mi cię. Cóżeś wżdy uczynił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '35', '35', 'Odpowiedział Jezus: Królestwo moje nie jestci z tego świata. Jeśliżeć by królestwo moje z tego świata było, słudzy moi walczyliby żebych nie był wydan Żydom. Aleć teraz królestwo moje nie jest ztąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '36', '36', 'Rzekł mu tedy Piłat: A więceś ty jest Król? Odpowiedział Jezus: Ty mówisz żemci ja jest Król. Jaciem się na to narodził i na tom przyszedł na świat, abych świadectwo dał prawdzie. Wszelki, który jest z prawdy, słucha głosu mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '37', '37', 'Powiedział mu Piłat: A cóż jest prawda? I to rzekszy wyszedł zasię do Żydów i rzekł do nich: Ja żadnej winy do niego nie najduję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '38', '38', 'A też u was jest ten zwyczaj, iż wam jednego wypuszczam na Wielkanoc. Chceciesz tedy, że wam wypuszczę Króla Żydowskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '17', '39', '39', 'Tedy wszyscy zawołali mówiąc: Nie tego, ale Barabasza. A tenci Barabasz był zbójca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '0', '0', 'A tak że Piłat wziąwszy Jezusa, dał go ubiczować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '1', '1', 'A żołnierze uplotszy koronę z cirnia, włożyli na głowę jego i w płaszcz szkarłatny oblekli go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '2', '2', 'A mówili: Witajże królu żydowski i bili go palcatmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '3', '3', 'Wyszedł potem Piłat na dwór i rzekł im: Oto wywiodłem go na dwór, abyście widzieli, że w nim żadnej winy nie najduję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '4', '4', 'Tedy Jezus wyszedł niosąc na sobie cirniową koronę i szarłatny płaszcz, a Piłat rzekł im: Otoż człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '5', '5', 'A gdy go ujźrzeli książęta kapłańskie i służebni, zawołali mówiąc: Ukrzyżuj, ukrzyżuj. I rzekł Piłat: Weźmiciesz go wy, a ukrzyżujcie, boć ja w nim żadnej winy nie najduję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '6', '6', 'Odpowiedzieli jemu Żydowie: Myć zakon mamy i wedle zakonu naszego ma umrzeć, bowiem się Synem Bożym czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '7', '7', 'A przetoż Piłat usłyszawszy ty słowa, tym się więcej lękł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '8', '8', 'I wszedł zasię do ratusza a rzekł do Jezusa: Skądeś ty jest? Lecz mu Jezus nie dał odpowiedzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '9', '9', 'Tedy mu rzekł Piłat: Nie mówiszże zemną? Nie wiesz, iż mam moc ukrzyżować cię i mam moc wyprawić cię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '10', '10', 'I odpowiedział Jezus: Nie miałbyś żadnej mocy przeciw mnie, gdybyć jej nie podano z wirzchu. A przetoż ten, który mię wydał tobie, więtszy grzech ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '11', '11', 'Zatymże Piłat szukał tego jakoby go wybawił, ale Żydowie wołali mówiąc: Jeśliż go wybawisz, nie jesteś przyjaciel cesarski, bo ktoć się kolwiek czyni królem, sprzeciwić się cesarzowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '12', '12', 'A przetoż Piłat usłyszawszy ty słowa, wywiódł na dwór Jezusa i siadł na stolicy na miejscu, które zową Litostrotos, a po hebrajsku Gabbata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '13', '13', 'Na ten czas był dzień przygotowania na święto Wielkonocne, a było o szóstej godzinie gdy powiedział Żydom: Oto Król wasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '14', '14', 'A oni wołali: Strać, strać i ukrzyżuj go. A Piłat im rzekł: Królaż waszego ukrzyżuję? Lecz odpowiedzieli książęta kapłańskie: Nie mamyć króla jedno cesarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '15', '15', 'A także go im wydał, żeby był ukrzyżowan. I wziąwszy Jezusa wywiedli go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '16', '16', 'On tedy niosąc krzyż swój, przyszedł na ono miejsce, które zwano Trupich głów, a po Hebrajsku Golgota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '17', '17', 'Gdzie go tam ukrzyżowali, a z nim drugich dwu i stąd i zowąd, a w pośrodku Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '18', '18', 'Napisał też Piłat i tytuł i postawił go nad krzyżem, a był tak napisan: Jezus Nazareński Król Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '19', '19', 'Tenci tytuł czytało wiele Żydów, bo blisko miasta było miejsce ono gdzie był ukrzyżowan Jezus, a był napisan po hebrajsku, po grecku i po łacinie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '20', '20', 'A dlategoż mówili Piłatowi książęta kapłanów żydowskich: Nie pisz Król Żydowski, ale iż on powiedał: Jestem Król Żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '21', '21', 'Odpowiedział Piłat: Com pisał tom pisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '22', '22', 'Żołnierze tedy, gdy ukrzyżowali Jezusa, pobrali szaty jego (i podzielili na czterzy części, każdemu żołnierzowi część) i suknię. A była ta suknia nie szyta, lecz od wirzchu wszytka a dziana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '23', '23', 'Tedy mówili między sobą: Nie rzeżmy jej, ale o nie rzućmy losy czyja ma być, a to się stało iżby się było Pismo wypełniło, w którym powiedziano: Podzielili między się odzienie moje, a o szaty moje rzucili los. A toć uczynili żołnierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '24', '24', 'I stały podle krzyża Jezusowego matka jego i siostra matki jego, Marja Klopaszowa żona i Marja Magdalena.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '25', '25', 'A gdy ujźrzał Jezus matkę i zwolennika tuż stojącego, którego miłował, rzekł matce swojej: Niewiasto oto syn twój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '26', '26', 'Potym rzekł zwolennikowi: Oto matka twoja. I od onegoż czasu wziął ją zwolennik do domu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '27', '27', 'Wiedząc tedy Jezus, iż wszytki ine rzeczy już były wykonane, aby się skończyło Pismo rzekł: Pragnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '28', '28', 'A było tam naczynie postawione octu pełne, a tak oni napoiwszy gębkę octem i obłożywszy hizopem, podali do ust jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '29', '29', 'Tedy Jezus gdy wziął ocet rzekł: Wykończonoć jest. I skłoniwszy głowę podał ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '30', '30', 'A tak Żydowie, aby nie zostały na krzyżu ciała w szabat, abowiem był dzień Przygotowania ( a był on wielki dzień Szabatu ), prosili Piłata, aby im goleni połamano i zjęto je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '31', '31', 'Przyszli tedy żołnierze, a pierwszemu zbójcy złamyli goleni i drugiemu, który z nim był ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '32', '32', 'A gdy przyszli do Jezusa, ujźrzawszy iż już umarł, nie łamali goleni jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '33', '33', 'Ale niektóry z żołnierzów oszczepam przebódł bok jego i natychmiast się rzuciła krew i woda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '34', '34', 'A ten który to widział świadczy i prawdziwe jest świadectwo jego, a onci wie iż prawdę powieda, abyście i wy wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '35', '35', 'I zstałoć się to dlatego, aby się wypełniło Pismo: Żadna kość jego nie będzie złamana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '36', '36', 'A zasię drugie Pismo mówi: Ujźrzą kogo przebodli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '37', '37', 'Potym prosił Piłata Józef z Arymatjej (który był zwolennik Jezusów, ale tajemny dla bojaźni Żydowskiej), iżby zdjął ciało Jezusowe, czego mu pozwolił Piłat. A tak przyszedszy wziął ciało Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '38', '38', 'Przyszedł też i Nikodem ( on który był przed tym przyszedł w nocy do niego ) a przyniósł maści z mirry i z aloes, jakoby sto funtów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '39', '39', 'A tak wziąwszy ciało Jezusowe, uwinęli je w prześciradła z rzeczami wonnemi, jako jest zwyczaj żydowski przy pogrzebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '40', '40', 'Tamże na tym miejscu gdzie był ukrzyżowan, był ogród, a w ogrodzie grób nowy, w którym jeszcze nigdy żaden położon nie był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '18', '41', '41', 'A przetoż tam dla przygotowania Żydowskiego, iż on grób był blisko, położył Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '0', '0', 'A tak w pirwszy dzień tegodniowy, Marja Magdalena przyszła rano, gdy jeszcze było ciemno, ku grobowi i ujźrzała kamień odwalony od grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '1', '1', 'Tedy odbieżawszy przyszła do Symona Piotra i do onego drugiego zwolennika, którego miłował Jezus, a rzekła im: Wyjętoć Pana z grobu, a nie wiemy kędy go położono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '2', '2', 'A przetoż wyszedł Piotr i on drugi zwolennik, a przyszli do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '3', '3', 'I bieżeli oba społu, ale on drugi zwolennik wprzód przybiegł prędzej niźli Piotr i pirwszy przyszedł do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '4', '4', 'A nachyliwszy się ujźrzał ano leżą prześciradła, wszakoż tam nie wszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '5', '5', 'Przyszedł też i Symon Piotr idąc za nim, a wszedszy w grób, ujźrzał prześciradła leżące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '6', '6', 'I facelet, który był na głowie jego, nie z prześciradły położony, ale na inym miejscu z osobna zwiniony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '7', '7', 'Tedy też wszedł i on drugi zwolennik, który był pirwszy przyszedł do grobu, a ujźrzawszy uwierzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '8', '8', 'Abowiem jeszcze nie rozumieli pisma, iż jemu potrzeba była wstać zmartwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '9', '9', 'Zatym się zwolennicy wrócili do swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '10', '10', 'Ale Marja stała u grobu z dworu płacząc, a gdy płakała, nachyliła się w grób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '11', '11', 'I ujźrzała dwa Anioły w bieli siedzące. Jednego u głowy, a drugiego u nóg, kędy leżało ciało Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '12', '12', 'Którzy jej rzekli: Niewiasto przecz płaczesz? A ona im rzekła: Wyjęto Pana mego, a nie wiem kędy go położono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '13', '13', 'A gdy to mówiła, obróciła się nazad i ujźrzała Jezusa stojącego, ale nie wiedziała iż Jezus był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '14', '14', 'Rzekł jej Jezus: Niewiasto przecz płaczesz? A kogo szukasz? A ona mnimając żeby był ogrodnik rzekła mu: Panie, jeśliżeś go ty wyniósł, powiedz mi gdzieś go położył, a ja go wezmę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '15', '15', 'I rzekł jej Jezus: Marja. A ona obróciwszy się rzekła: Rabboni, to jest Mistrzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '16', '16', 'Powiedział jej Jezus: Nie tykaj się mnie, abowiemem jeszcze nie wstąpił do Ojca mego, ale idź do braciej mojej i powiedz im: Wstępuję do Ojca mojego i Ojca waszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '17', '17', 'Przyszła tedy Marja Magdalena oznajmując zwolennikom, że widziała Pana i to co jej powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '18', '18', 'A tak, gdy wieczór był dnia onego pirwszego tegodniowego, a drzwi były zamknięte tam gdzie się byli zebrali zwolennicy dla bojaźni Żydowskiej, przyszedł Jezus i stanął w pośrodku, a rzekł im: Pokój wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '19', '19', 'A gdy to rzekł, ukazał im ręce i bok swój. Rozradowali się tedy zwolennicy ujźrzawszy Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '20', '20', 'I rzekł do nich powtóre: Pokój wam. Tak jako mię posłał Ociec, także i ja was posyłam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '21', '21', 'A to powiedziawszy, tchnął na nie i rzekł im: Weźmicie Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '22', '22', 'Którymkolwiek odpuścicie grzechy, będą im odpuszczone, a którymkolwiek zatrzymacie, będą zatrzymane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '23', '23', 'Tedy Tomasz, jeden ze dwunaście, którego zową Dydymus, nie był z nimi, gdy był przyszedł Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '24', '24', 'Powiedzieli mu tedy drudzy zwolennicy: Widzielichmy Pana. Ale im on rzekł: Jeśli nie ujźrzę w ręku jego znaków goździ, a nie włożę palca mego w znaki goździ i nie włożęli ręki w bok jego, żadnym sposobem nie uwierzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '25', '25', 'Potym po ośmi dni byli zasię zwolennicy tam wewnątrz i Tomasz z nimi. Zatym Jezus przyszedł gdy były zamknięte drzwi i stanąwszy w pośrodku rzekł im: Pokój wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '26', '26', 'Potym rzekł Tomaszowi: Włóż palec twój ówdzie, a oglądaj ręce moje. Podajże rękę twą, a włóż ją w bok mój i nie bądź niedowiarkiem, ale wierzącym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '27', '27', 'Odpowiedział tedy Tomasz i rzekł: Pan mój i Bóg mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '28', '28', 'Powiedział mu Jezus: Żeś mię widział Tomaszu, uwierzyłeś. Błogosławienisz, którzy nie widzieli a uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '29', '29', 'A tak wiele i innych znamion uczynił Jezus przed zwolenniki swojemi, które nie są wypisane w tych księgach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '19', '30', '30', 'A tyć rzeczy są napisane, abyście wierzyli, że Jezus jest Krystus on Syn Boży, a iżbyście wierząc żywot mieli przez imię jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '0', '0', 'Potym się dał widzieć Jezus zwolennikom u Morza Tyberjadskiego i tak się oznajmił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '1', '1', 'Byli społu Symon Piotr i Tomasz, którego zową Dydymus i Natanael, który był z Kany Galilejskiej i synowie Zebedeuszowi i drudzy dwa z zwolenników jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '2', '2', 'Tedy im rzekł Symon Piotr: Idę ryb łowić. A oni rzekli: Pójdziem i my z tobą. A tak szli i wsiedli w łódź natychmiast, lecz onej nocy nic nie pojmali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '3', '3', 'A gdy już było poranu, stanął Jezus na brzegu, wszakoż nie poznali zwolennicy żeby był Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '4', '4', 'A tak im rzekł Jezus: Dziateczki, a macież co jeść? Odpowiedzieli mu: Nie masz nic.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '5', '5', 'I rzekł im: Zapuśćcie tu sieci po prawej stronie łodzi, a najdziecie. A tak zapuścili, a już dalej nie mogli wywlec przed wielkim mnóstwem ryb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '6', '6', 'Rzekł tedy zwolennik, on którego miłował Jezus Piotrowi: Panci jest. I usłyszawszy Symon Piotr, że to Pan był, przepasał się koszulą (abowiem był nagi) i rzucił się w morze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '7', '7', 'A drudzy zasię zwolennicy przyszli w łodzi (bo nie daleko byli od brzegu, ale jakoby na dwu set łokiet ) ciągnąc sieć pełną ryb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '8', '8', 'A tak wyszedszy na brzeg, ujźrzeli ogień nalożony i rybę na nim i chleb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '9', '9', 'A rzekł im Jezus: Przynieście ty ryby, któreście teraz pojmali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '10', '10', 'Wstąpiwszy tedy Symon Piotr, wyciągnął sieć na brzeg pełnę wielkich ryb, których było sto pięćdziesiąt i trzy, a gdy ich tak wiele było, nie stargała się sieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '11', '11', 'Tedy im rzekł Jezus: Pódźcież obiedwajcie. I nie śmiał żaden z zwolenników pytać go: Ty ktoś, gdyż wiedzieli że był Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '12', '12', 'Zatym przyszedł Jezus i wziął chleb, a dał im także i rybę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '13', '13', 'A toć już trzeci raz dał się widzieć Jezus zwolennikom swoim, gdy wstał z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '14', '14', 'Gdy potym obiedwali rzekł Symonowi Piotrowi Jezus: Symonie synu Jonasów, miłujeszże mię więcej niźli ci? A on powiedział: Panie, ty znasz że cię miłuję. Tedy mu rzekł: Paś baranki moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '15', '15', 'Rzekł mu zasię znowu: Symonie synu Jonasów, miłujeszże mię? A on powiedział: Jest tak Panie, ty znasz, że cię miłuję. Rzekł mu tedy: Paś owce moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '16', '16', 'Zatym mu potrzecie rzekł: Symonie synu Jonaszów, miłujeszże mię? I zasmucił się Piotr, że mu trzeci kroć rzekł: Miłujeszże mię? I powiedział mu: Panie, ty wszytko wiesz, ty znasz, że cię miłuję. Rzekł mu tedy Jezus: Paśże owce moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '17', '17', 'Zaprawdę, zaprawdę powiedam tobie: Gdyś młodszym był, przepasowałeś się i chodziłeś kędyś chciał. Lecz gdy się zstarzejesz, rościągniesz ręce twe, a iny cię opasze i przyniesie gdzie nie będziesz chciał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '18', '18', 'A toć mówił dawając znać, którą śmiercią miał uwielbić Boga. A gdy to rzekł, powiedział mu: Idź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '19', '19', 'Tedy Piotr obróciwszy się ujźrzał onego zwolennika, którego miłował Jezus pozad idącego, a który się był przy wieczerzy położył na piersiach jego i mówił: Panie, któryś jest ten, co cię zdradza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '20', '20', 'Tego tedy gdy ujźrzał Piotr rzekł Jezusowi: Panie, a ten co?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '21', '21', 'Powiedział mu Jezus: Jeśli ja chcę żeby został aż przyjdę, cóż tobie do tego? Ty idź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '22', '22', 'A tak to słowo wyszło między bracią, iż on zwolennik nie miał umrzeć, aleć mu nie rzekł Jezus: Nie umrze, ale jeśli chcę żeby został, aż przyjdę, cóż tobie do tego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '23', '23', 'Tenci jest on zwolennik, który świadczy o tych rzeczach i to wypisał, a wiemy, że prawdziwe jest świadectwo jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jn', '20', '24', '24', 'Są też jeszcze wiele innych rzeczy, które czynił Jezus, które gdyby miały być wszytki z osobna spisane, nie tuszę, aby i sam świat ogarnąć miał ty księgi, któreby o tym pisano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '0', '0', 'Wyprawiłem pirwsze księgi Teofile, o wszytkim co poczynał Jezus czynić i uczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '1', '1', 'Aż do dnia tego, w którym dawszy roskazanie Apostołom przez Ducha świętego, które był obrał, wzięt jest ku górze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '2', '2', 'Którym się też sam potym, gdy był umęczon zstawił żywym w wielu niewątpliwych znakach, przez czterdzieści dni się im ukazując i powiedając ty rzeczy, które ku królestwu Bożemu należą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '3', '3', 'A zebrawszy je, roskazał im aby nie odchadzali z Jeruzalem, ale iżby czekali obietnice Ojcowej, którąście słyszeli ode mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '4', '4', 'Abowiem Jan krzciłci wodą, ale wy będziecie pokrzczeni Duchem świętym po niewielu tych dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '5', '5', 'A tak oni zszedszy się, pytali go mówiąc: Panie zali w tym czasie naprawisz królestwo izraelskie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '6', '6', 'Lecz on rzekł do nich: Nie waszać rzecz znać czasy i chwile, które Ociec w swej własnej mocy położył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '7', '7', 'Ale weźmiecie moc Ducha świętego, gdy przyjdzie na was i będziecie mi świadki w Jeruzalem i we wszytkim Juda i w Samarijej, aż i do ostatecznych ziem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '8', '8', 'A to rzekszy, gdy oni patrzali podniesion jest, a obłok porwał go od oczu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '9', '9', 'I gdy tak pilnie oczy swe podnosili w niebo, a on szedł, oto dwa mężowie tuż stanęli przy nich w białych szatach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '10', '10', 'Którzy też rzekli: Mężowie Galilejczycy przeczże stoicie, patrzając w niebo? Ten Jezus, który wzgórę wzięt jest od was w niebo, takci przyjdzie jakoście widzieli go idącego do nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '11', '11', 'Tedy się wrócili do Jeruzalem od góry, którą zowią Oliwną, co jest blisko Jeruzalem, tak daleko jakoby szabat chodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '12', '12', 'A gdy weszli, wstąpili na salę kędy mieszkali, Piotr i Jakub i Jan i Andrzej i Filip i Tomasz, Bartłomiej i Mateusz, Jakub syn Alfeuszów, Symon Zelotes i Judas Jakubów brat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '13', '13', 'Cić wszyscy trwali zgodnie na modlitwie i prośbach z żonami i z matką Jezusową i z bracią jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '14', '14', 'W tychże tam czasiech powstawszy Piotr w pośrodek zwolenników rzekł: (a było ich w kupie mianowicie tam na onym miejscu jakoby sto i dwadzieścia).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '15', '15', 'Mężowie bracia, potrzebać się było wypełnić Pismu onemu, które opowiedział Duch święty przez usta Dawidowe o Judaszu, który był przewodnikiem w drodze onym, co pojmali Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '16', '16', 'Abowiemci był policzon między nas i dostał był cząstki tego urzędu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '17', '17', 'A przetoż on dostał był rolej z najmu złościwego, a obwiesiwszy się rozpukł się na poły i wypłynęły trzewa jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '18', '18', 'I jawnoć to jest wszytkim mieszkającym w Jeruzalem tak, iż nazwano pole ono własnym ich językiem Akeldama, to jest rola krwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '19', '19', 'Abowiem napisano w Księgach Psalmów: Niechaj będzie mieszkanie jego opustoszone, a niech nikt nie będzie, ktoby w nim mieszkał i przełożeństwo jego niechaj weźmie drugi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '20', '20', 'A tak potrzeba z tych mężów, którzy się z nami złączyli po wszytek ten czas, jako Pan Jezus sprawował urząd swój między nami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '21', '21', 'Począwszy od krztu Janowego, aż do tego dnia, w który jest wzięt ku górze od nas, aby jeden z nich był z nami świadkiem zmartwychwstania jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '22', '22', 'A przetoż postawili dwu Józefa, którego zwano Barsabas, a który miał przezwisko Sprawiedliwy i Macieja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '23', '23', 'Mówili tedy modląc się: Ty Panie, który znasz serca wszytkich, ukaż nam któregoś obrał z tych dwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '24', '24', 'Aby wziął urzędu tego i Apostolstwa, od którego się obłądził Judasz, aby odszedł na miejsce swe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '0', '25', '25', 'A tak położyli losy ich i padł los na Macieja, który za spólnym wszech zwoleniem, przyłączon jest ku jedenaście Apostołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '0', '0', 'A gdy przychodził dzień pięćdziesiąty, byli wszyscy zgodnie na jednym miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '1', '1', 'Tedy się zstał z prędka z nieba szum, jakoby przypadającego wiatru gwałtownego, który napełnił wszytek dom, kędy siedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '2', '2', 'I ukazały się im rozdzielone języki na kształt ognia, który stanął nad każdym z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '3', '3', 'A tak napełnieni są wszyscy Duchem świętym, a poczęli mówić obcemi języki, jako im Duch on dawał wymawiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '4', '4', 'A byli w Jeruzalem mieszkający Żydowie, ludzie bogobojni, ze wszego narodu tych, którzy są pod niebem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '5', '5', 'Gdy się tedy ta wieść wszczęła, zbieżało się mnóstwo ludzi i zstało się w nich zamieszanie, że każdy słyszał własnym swym językiem ony mówiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '6', '6', 'I zdumiewali się wszyscy, a dziwowali się mówiąc jedni do drugich: Izali oto ci wszyscy, którzy mówią nie są Galilejczycy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '7', '7', 'Jakoż my tedy słyszymy u nich każdy swój własny język, w którymechmy się zrodzili?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '8', '8', 'My Partowie, Medowie i Elamitowie i którzy mieszkamy w Mezopotanijej, w Judzie, w Kapadocijej, w Poncie i w Azijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '9', '9', 'W Frygijej, w Pamfiljej, w Egiptcie i w stronach Libijej, która podległa Cyrenie i my przychodniowie Rzymscy, Żydowie i nowo do Żydostwa przystawający.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '10', '10', 'Kreteńczycy i my Arabczycy, słyszymy je mówiące języki naszemi zacne rzeczy o Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '11', '11', 'A tak się wszyscy zdumieli i wątpili mówiąc jedni do drugich: Coż to wżdy za rzecz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '12', '12', 'Lecz drudzy chytrze szydząc mówili: Cić opili się wina młodego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '13', '13', 'Tedy Piotr stojąc z onymi jedenaścią, podniósł głos swój i mówił do nich tymi słowy: Mężowie Żydowscy i wy wszyscy, którzy mieszkacie w Jeruzalem, niechajżeć to wam jawno będzie, a przyjmicie w uszy swoje słowa moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '14', '14', 'Abowiem nie sąć pijani ci, jako wy mnimacie, gdyż dopiro trzecia godzina na dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '15', '15', 'Aleć to jest ono, co powiedziano przez proroka Joela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '16', '16', 'I będzie to w ostatecznych czasiech (mówi Bóg): Wyleję z Ducha mego na wszelkie ciało, a będą prorokować synowie waszy i córki wasze, a młodzieńcy waszy widzenia widzieć będą, a starcom waszym sny się śnić będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '17', '17', 'A pewnie w onych czasiech na sługi me i na służebnice moje wyleję z Ducha mego i będą prorokować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '18', '18', 'Uczynię cuda na niebie wzgórę i znamiona na ziemi nisko, krew, ogień i mgłę dymną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '19', '19', 'Słońce się obróci w ciemności, a miesiąc w krew, przed tym niż przyjdzie on dzień Pański, wielki a znaczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '20', '20', 'I przyjdzie to, że ktokolwiek będzie wzywał imienia Pańskiego, będzie wybawion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '21', '21', 'Mężowie Izraelscy, słuchajciesz słów tych, Jezusa onego Nazareńskiego, męża od Boga pochwalonego u was w zacnych sprawach, cudach i znamionach, które czynił Bóg przezeń w pośrodku was, jako i sami wiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '22', '22', 'Tego, gdyście za naznaczoną radą i opatrznością Bożą podanego, wzięli rękami niesprawiedliwemi, zabiliście go wbiwszy na krzyż.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '23', '23', 'Którego Bóg wzbudził, odjąwszy boleści śmierci dlatego, iż to była rzecz niepodobna, aby od niej miał być zatrzyman.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '24', '24', 'Abowiem o nim powieda Dawid: Upatrowałem zawżdy Pana przed sobą, bo mi jest po prawej mej stronie, abych nie był wzruszon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '25', '25', 'I dlategoż się rozweseliło serce moje, a rozradował się język mój, gdyż też i ciało moje odpoczynie w nadziei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '26', '26', 'Abowiem nie zostawisz duszy mojej w piekle, a nie dopuścisz świętemu twemu poczuć nakażenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '27', '27', 'Oznajmiłeś mi drogi żywota, a napełnisz mię radością z obliczem twoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '28', '28', 'Mężowie bracia, mogę bespiecznie mówić u was o patriarsze Dawidzie, żeć umarł i pogrzebion jest, a grób jego jest u was aż i do dnia tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '29', '29', 'A przeto gdyż był prorokiem i wiedział, że mu Bóg przyrzekł pod przysięgą, iż z owocu biódr jego, ile wedle ciała, Krystusa miał wzbudzić, a posadzić na stolicy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '30', '30', 'Tedy przepatrując powiedał o zmartwychwstaniu Krystusowym, iż nie została w piekle dusza jego, ani ciało jego nie poczuło zepsowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '31', '31', 'Tegoć Jezusa wzbudził Bóg, której rzeczy my wszyscy jestechmy świadkowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '32', '32', 'Potym, gdy prawicą Bożą był podwyższon i obiecanego Ducha świętego wziął od Ojca, toć teraz wylał, na co wy patrzycie i co słyszycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '33', '33', 'Abowiemci Dawid nie wstąpił do nieba, lecz sam powieda: Rzekł Pan Panu memu, siądź po prawicy mej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '34', '34', 'Aż położę nieprzyjacioły twe podnóżkiem nóg twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '35', '35', 'Niechajżeć tedy wie zapewne wszytek dom izraelski, iż i Panem i Krystusem uczynił Bóg tego to Jezusa, któregoście wy ukrzyżowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '36', '36', 'A tak to słysząc rozżalili się na sercu i rzekli do Piotra i do innych Apostołów: Cóż mamy czynić mężowie bracia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '37', '37', 'Zatym Piotr rzekł do nich: Uznajcie się, a okrzci się każdy z was w imię Jezusa Krystusa ku odpuszczeniu grzechów, a weźmiecie dar Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '38', '38', 'Abowiemci się wam ta obietnica zstała i dziatkom waszym i wszytkim, którzy daleko są i ile ich kolwiek wzowie Pan Bóg nasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '39', '39', 'I wielem innych słów oświadczał się a napominał je mówiąc: Wyzwólcie się od tego narodu złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '40', '40', 'Którzy tedy wdzięcznie przyjęli słowa jego, okrzcili się. I przystało dnia onego ku kościołowi około trzech tysięcy osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '41', '41', 'I trwali w nauce Apostolskiej i w społeczności i w łamaniu chleba i w modlitwach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '42', '42', 'Przyszedł tedy na wszytki strach, a wiele cudów i znamion przez Apostoły było okazanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '43', '43', 'I wszyscy, którzy wierzyli byli na jednym miejscu, wszytkiego społecznie używając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '44', '44', 'Osiadłości i majętności przedawali, dzieląc je wszytkim, ile komu było potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '45', '45', 'A ustawicznie trwając zgodnie w kościele i łamiąc w domu chleb, przyjmowali pokarm z weselem i w prostocie serdecznej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '1', '46', '46', 'Chwaląc Boga i mając łaskę u wszego ludu, a Pan zawżdy przydawał kościołowi swemu ty, którzy mieli być zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '0', '0', 'Tedy Piotr i Jan szli społu do kościoła, w godzinę modlitwy dziewiąta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '1', '1', 'A niektóry chromy jeszcze z żywota matki swojej był noszon, którego ustawicznie kładziono u bramy kościelnej, którą zwano Nadobna, aby prosił jałmużny od tych, którzy szli do kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '2', '2', 'Ten gdy ujźrzał Piotra i Jana wchodzące do kościoła, prosił aby od nich wziął jałmużnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '3', '3', 'A tak Piotr i Jan pilnie nań patrząc oczyma swojemi, rzekli: Patrz na nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '4', '4', 'Tedy on patrzał nadziewając się co wziąć od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '5', '5', 'Ale mu Piotr powiedział: Srebrać ani złota nie mam, lecz co mam, to tobie dawam: W imię Jezusa Krystusa Nazareńskiego wstań a chodź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '6', '6', 'A ująwszy prawą jego rękę podniósł go i wnet były umocnione nogi jego i kostki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '7', '7', 'Wyskoczywszy tedy stanął i chodził i wszedł z nimi do kościoła, a chodząc skakał i chwalił Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '8', '8', 'I ujźrzał wszytek lud, iż chodził a chwalił Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '9', '9', 'A poznali go, iż to on był, który dla jałmużny siadał u bramy Nadobnej kościelnej i napełnieni są strachu i zdumienia nad tym, co się było przydało jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '10', '10', 'Gdy się tedy trzymał on chromy, który był uzdrowion, Piotra i Jana, zbieżał się wszytek lud ku nim do przysionka, który był zwan Salomonów, ulęknąwszy się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '11', '11', 'Co widząc Piotr tak mówił do ludu: Mężowie Izraelscy, cóż się temu dziwujecie? Abo przeczeście na nas udali oczy wasze, jakobychmy to mocą i świątobliwością naszą uczynili, iż ten to chodzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '12', '12', 'Bóg on Abraamów, Izaaków i Jakubów, a Bóg ojców naszych, uwielbiłci Jezusa Syna swego, któregoście wy wydali i zaprzeliście się go przed Piłatem, gdyż go on wolnym być osądził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '13', '13', 'Aleście wy się onego świętego i sprawiedliwego zaprzeli, a żądaliście aby wam darował mężobójcę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '14', '14', 'I zamordowaliście sprawcę żywota, którego Bóg wzbudził od umarłych, a mychmyć są świadkowie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '15', '15', 'A przez wiarę w imię jego, tego którego teraz widzicie i znacie, utwierdziło imię jego, a wiara, która przezeń jest dała temu to całe zdrowie przed wami wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '16', '16', 'Aleć teraz bracia wiem, żeście z niewiadomości to uczynili, tak jako i książęta wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '17', '17', 'Leczci Bóg to, co przez usta wszech swoich proroków opowiedział, iż Krystus cierpieć miał, tak wypełnił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '18', '18', 'A przetoż uznajcie się a nawróćcie się, aby były zgładzone grzechy wasze, gdyż przyszły czasy ochłodzenia od obliczności Pańskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '19', '19', 'A posłał wam onego, który był przed tym opowiedzian, Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '20', '20', 'Któremuć potrzeba być w niebie, aż do czasów naprawienia wszech rzeczy, które opowiedział Bóg od wieku przez usta wszech swych świętych proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '21', '21', 'Abowiem mówił Mojżesz do ojców: Proroka wzbudzi wam Pan Bóg wasz z braciej waszej jako mnie, a będziecie go słuchać we wszytkim cokolwiek wam powie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '22', '22', 'I zstanie się to, że kto nie będzie słuchał proroka onego, będzie wykorzenion z ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '23', '23', 'Aleć i wszyscy prorocy, od Samuela i ini, którzy po nich byli, ile ich kolwiek prorokowało, też wszyscy o tych dnioch powiedali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '24', '24', 'Wyście są syny prorockimi i przymierza, które postanowił Bóg z ojcy naszemi, mówiąc do Abraama: Iż w potomstwie twoim błogosławieństwa dostąpią wszytki narody na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '2', '25', '25', 'Wamci naprzód Bóg wzbudziwszy Syna swojego Jezusa posłał, któryby wam błogosławił, odwracając każdego od jego złości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '0', '0', 'A gdy oni tak mówili do ludu, tedy przyszli kapłani i przełożeni kościoła i Saduceuszowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '1', '1', 'Frasując się barzo, iż uczyli lud, a opowiedali w imię Jezusowe powstanie od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '2', '2', 'I targnęli się na nie rękoma,a podali je do wi&nbsp;ęzienia aż do jutra, bo już był wieczór.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '3', '3', 'A wiele tych, którzy słowa ony słyszeli uwierzyli i było w liczbie mężów około piąci tysięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '4', '4', 'A gdy było nazajutrz, zebrali się książęta ich i starszy i doktorowie w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '5', '5', 'I Annasz nawyzszy kapłan i Kajfasz i Jan i Aleksander i ile ich jedno było z rodu kapłańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '6', '6', 'Gdy je tedy postawili w pośrodku pytali ich: Którą mocą, a którymeście wy to imieniem uczynili?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '7', '7', 'Tedy Piotr będąc napełnion Ducha świętego rzekł do nich: Książęta ludu i starszy Izraelscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '8', '8', 'Gdyż o nas dziś ten sąd jest zasądzon, dla dobrodziejstwa nad człowiekiem niedołężnym uczynionego, jako on jest uzdrowion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '9', '9', 'Niechżeć wam wszem będzie jawno i wszytkiemu ludowi izraelskiemu, żeć przez imię Jezusa Krystusa Nazareńskiego, któregoście wy ukrzyżowali, którego Bóg wzbudził od umarłych, przez tegoć ten stoi zdrowym przed wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '10', '10', 'Tenci jest kamień on, którego sobie nie mieliście nizacz wy budujący, który się stał głową węgłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '11', '11', 'I niemaszci w żadnym inym zbawienia, ani jest żadne imię pod niebem, któreby było podane między ludzi, w którym potrzeba abyśmy byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '12', '12', 'A tak oni widząc śmiałość w mowie Piotrowę i Janowę i wiedząc pewnie, że nie byli ludźmi uczonemi ale prostaki, dziwowali się i poznawali, że oni byli z Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '13', '13', 'Ale widząć onego z nimi stojącego, który był uzdrowion, nie mogli nic mówić przeciw nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '14', '14', 'I kazawszy im precz z rady wynidź, namawiali między sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '15', '15', 'Mówiąc: Cóż z tymi ludźmi czynić mamy? Gdyż znamienity cud przez nie jest uczynion, jawnoć to jest wszytkim mieszkającym w Jeruzalem, ani możem tego zaprzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '16', '16', 'Ale iżby się to więcej nie roznaszało między ludźmi, zagróźmy im srodze, aby potym w imię to z żadnym człowiekiem nie mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '17', '17', 'A przetoż wezwawszy ich, zakazali im aby więcej nie uczyli w imię Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '18', '18', 'Tedy Piotr i Jan odpowiedziawszy rzekli do nich: Jeśliż to jest rzecz sprawiedliwa przed Bogiem, was radszej słuchać niż Boga, rozsądzciesz to?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '19', '19', 'Abowiem o tym cochmy widzieli i słyszeli, mówić musimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '20', '20', 'Oni tedy nie mogąc znaleźć jakoby je mieli karać, zagroziwszy im, puścili je dla ludu. Abowiem wszyscy chwalili Boga za to, co się było stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '21', '21', 'Bo onemu człowiekowi było dalej niż czterdzieści lat, na którym się był okazał on cud uzdrowienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '22', '22', 'A tak, gdy je puszczono, przyszli do swych i oznajmili im to, co książęta kapłańskie i starszy mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '23', '23', 'Ci zasię, którzy to słyszeli, zgodnie podniósłszy głos swój ku Bogu mówili: Iza nie ty jesteś Bóg on, któryś sprawił niebo i ziemię, morze i wszytki rzeczy co w nich są?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '24', '24', 'Któryś przez usta Dawida sługi swego powiedział: Przeczże wzburzyli pogani, a narodowie zmyślali rzeczy próżne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '25', '25', 'Stanęli królowie ziemię i książęta zebrały się na jedno miejsce przeciwko Panu i przeciw Krystusowi jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '26', '26', 'Abowiemci się zebrali prawdziwie przeciw świętemu Synowi twemu Jezusowi, któregoś ty był pomazał, Herod i Poncjusz Piłat z pogany i z ludem izraelskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '27', '27', 'Aby to uczynili, cokolwiek ręka twa i rada twa przed tym uczynić postanowiła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '28', '28', 'A przetoż teraz Panie wejźrzyj na pogróżki ich, a daj służebnikom twoim, aby ze wszytką śmiałością opowiedali słowo twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '29', '29', 'Ściągnąwszy rękę twą ku uzdrowieniu, aby też cuda i znamiona były czynione przez imię świętego Syna twego Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '30', '30', 'A tak oni, gdy się modlili, zatrząsnęło się ono miejsce, w którym byli zgromadzeni i napełnieni są wszyscy Ducha świętego, a opowiedali śmiele słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '31', '31', 'Tedy onej wszytkiej wielkości, którzy byli uwierzyli było serce jedno i dusza jedna, a żaden z nich cokolwiek mieli, nic swego być nie powiedał, ale wszytkiego na spół używali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '32', '32', 'A wielką mocą Apostołowie dawali świadectwo o zmartwychwstaniu Pana Jezusowym i była wielka łaska nad nimi wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '33', '33', 'Bo żadnego między nimi nie było potrzebującego, gdyż ile ich kolwiek mieli pola abo domy, przedawali a przynosili pieniądze za ono co poprzedali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '34', '34', 'I kładli przed nogi Apostolskie, a potym to podzielano każdemu, ile komu była potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '35', '35', 'Tedy Jozes, który nazwan jest Barnabas od Apostołów (co jeślibyś wykładał, jest syn pocieszenia) z pokolenia Lewi, rodem z Cypru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '3', '36', '36', 'Mając pole przedał je, a przyniózszy pieniądze, położył je u nóg Apostołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '0', '0', 'Tedy też mąż niektóry imieniem Ananijasz z Safirą żoną swą przedał osiadłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '1', '1', 'I wyjął nieco z pieniędzy, o czym wiedziała i żona jego, a przyniózszy część niektórą, położył przed nogi Apostolskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '2', '2', 'Rzekł tedy Piotr: Ananijaszu, przeczże szatan napełnił serce twe, iżeś skłamał przeciw Duchowi świętemu, a ująłeś pieniędzy za ten folwark?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '3', '3', 'A zaźby gdybyś był chował, tobie to było nie zostało,a przedawszy za&nbsp;żeś tego nie miał w mocy swej? Przeczżeś sobie tę rzecz na myśl przywodził? Nie ludźmiś skłamał, ale Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '4', '4', 'Ty słowa słysząc Ananijasz upadszy umarł. I przyszedł strach wielki na wszytki, którzy to słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '5', '5', 'A tak wstawszy młodzieńcy porwali go, a wyniózszy pogrzebli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '6', '6', 'I gdy już było jakoby po trzech godzinach, gdy żona jego nie wiedząc co się stało, tam weszła,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '7', '7', 'Rzekł do niej Piotr: Powiedz mi, za tylkoliście przedali ten folwark? A ona rzekła: Za tylko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '8', '8', 'Tedy Piotr rzekł do niej: Cóż to jest żeście się na to zmówili, abyście kusili Ducha Pańskiego? Oto nogi tych, którzy pogrzebali męża twego tuż u drzwi są i ciebieć wyniosą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '9', '9', 'Padła tedy wnet u nóg jego bez duszę, a wszedszy młodzieńcy znaleźli ją umarłą, a wyniózszy pogrzebli ją podle męża jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '10', '10', 'I przyszedł wielki strach na wszytek kościół i na wszytki, którzy to słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '11', '11', 'A tak przez ręce Apostolskie działo się wiele znaków i cudów między ludźmi, a byli wszyscy zgodnie w przysionku Salomonowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '12', '12', 'A z inych żaden nie śmiał się do nich przyłączyć, ale je sobie miał lud w wielkiej powadze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '13', '13', 'I tym więcej przyrastało wielkości wierzących Panu, mężów społu i niewiast.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '14', '14', 'Tak, iż też na ulice wynaszali niemocne i kładli je na łóżkach i na pościeli, aby choć tylko cień Piotra idącego zaćmił którego z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '15', '15', 'Zchodził się też lud z okolicznych miast do Jeruzalem wioząc niemocne i którzy byli przenagabani od duchów nieczystych, a ci wszyscy byli uzdrowieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '16', '16', 'Tedy powstawszy kapłan nawyższy i wszyscy, którzy z nim byli z sekty Saduceuszów, będąc pełni nienawiści,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '17', '17', 'Targnęli się rękoma na Apostoły i podali je do więzienia pospolitego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '18', '18', 'Ale Anioł Pański w nocy otworzył drzwi w ciemnice, a wywiódszy je rzekł:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '19', '19', 'Idźcież, a stanąwszy powiedajcie ludowi w kościele wszytki słowa żywota tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '20', '20', 'To oni gdy usłyszeli, weszli raniuczko do kościoła i uczyli. A przyszedszy kapłan nawyższy i ci co z nim byli, zebrali radę i wszytki starsze synów izraelskich, a posłali do ciemnice oprawce, aby byli ony przywiedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '21', '21', 'Ale gdy oprawce przyszli, nie naleźli ich w ciemnicy, co wróciwszy się oznajmili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '22', '22', 'Powiedając: Aczciechmy ciemnicę znaleźli zamknioną ze wszytką pilnością i straż z dworu stojącą przede drzwiami, lecz gdyśmy otworzyli, żadnegośmy we wnątrz nie naleźli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '23', '23', 'A gdy ty słowa usłyszeli i nawyższy kapłan i przełożony kościoła, także i książęta kapłańskie, wątpili o nich coby to było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '24', '24', 'I przyszedszy niektóry powiedział im: Oto mężowie, któreście byli dali do więzienia, stoją w kościele, a uczą lud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '25', '25', 'Tedy rotmistrz z sługami przywiedli je, wszakoż nie gwałtem (bo się ludzi bali by ich nie ukamionowali)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '26', '26', 'Przywiódszy je tedy postawili przed radą. A tak ich pytało książę kapłańskie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '27', '27', 'Mówiąc: Zalichmy wam srodze nie zakazali, abyście w imię to nie uczyli? A oto już pełno w Jeruzalem nauki waszej i chcecie na nas wnieść rozlanie krwie człowieka tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '28', '28', 'A odpowiedając im Apostołowie rzekli: Więcej nam potrzeba być posłusznymi Bogu niż ludziom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '29', '29', 'Bóg ojców naszych wzbudził Jezusa, któregoście wy zabili zawiesiwszy na drzewie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '30', '30', 'Tenci jest, którego Bóg wywyższył prawicą swą, aby był Książęciem i Zbawicielem, a dał uznanie Izraelowi i odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '31', '31', 'A mychmy są świadki jemu tych rzeczy, które powiedamy, także i Duch święty, którego dał Bóg tym, którzy są posłuszni jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '32', '32', 'Ale oni to słysząc prawie się pukali i radzili się, jakoby je pomordować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '33', '33', 'Tedy wstawszy w radzie, niektóry Faryzeusz imieniem Gamalijel, zakonny doktor zacny u wszego ludu, roskazał aby maluczko wywiedziono precz Apostoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '34', '34', 'I rzekł do nich: Mężowie Izraelscy, obaczcie pilnie co macie z tymi ludźmi czynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '35', '35', 'Abowiem przed tymi czasy nastał Teudas, powiedając się być kim zacnym, do którego przystało mężów w liczbie około czterzech set, który zabit jest, a wszyscy którzy mu posłuszni byli, rozbiegli się i w niwecz są obróceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '36', '36', 'Po nim nastał Judas, on Galilejczyk, czasu popisu i wiele ludu uwiódł za sobą, lecz i ten zginął i wszyscy, którzy mu posłuszni byli rosproszeni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '37', '37', 'I terazci wam powiedam: Dajcie pokój tym ludziam i zaniechajcie ich, abowiem jeślić jest z ludzi ta rada abo ta sprawa, obrócić się w niwecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '38', '38', 'Ale jeślić jest z Boga, nie możecie jej przekazić, aby was snadź nie doznano, że się Bogu sprzeciwiacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '39', '39', 'A tak przestali na jego słowie, a wezwawszy Apostołów i dawszy je bić, zakazali im aby nie mówili nic w imię Jezusowe i puścili je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '40', '40', 'A przetoż oni z radością szli od obliczności onej rady, iż się stali godni dla imienia Jezusowego podejmować zelżywość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '4', '41', '41', 'I nie przestawali ustawicznie, tak w kościele jako w domu, nauczać i opowiedać Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '0', '0', 'Za onych czasów, gdy się przymnażało pocztu Apostołów, wszczęło się szemranie Greków przeciw Żydom, iż w posługowaniu powszednim były gardzone wdowy ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '1', '1', 'A tak oni dwanaście wezwawszy wielkości zwolenników rzekli: Nie jestci rzecz słuszna, żebychmy my opuściwszy słowo Boże mieli stołom służyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '2', '2', 'Upatrzcież tedy między sobą bracia siedmi mężów, dobre świadectwo o sobie mających, coby byli pełni Ducha świętego i mądrości, którebychmy przełożyli ku tej sprawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '3', '3', 'A my na modlitwie i na posłudze słowa trwać będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '4', '4', 'I podobała się ta rzecz onej wszytkiej wielkości. A tak obrali Stefana, męża pełnego wiary i Ducha świętego, Filipa, Prochora i Nikanora, Tymona i Parmena i Mikołaja nowo nawróconego Antjocheńczyka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '5', '5', 'Które postawili przed Apostoły, a oni się modliwszy, kładli na nie ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '6', '6', 'A tak słowo Boże rozrastało się, a barzo się przymnażało pocztu zwolenników w Jeruzalem i wielkie mnóstwo kapłanów byli posłuszni wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '7', '7', 'A Stefan będąc pełen wiary i mocy, czynił cuda i znamiona wielkie między ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '8', '8', 'Tedy powstali niektórzy z bożnice, którą zową Libertynów, Cyrenejczyków i Aleksandrijanów i tych, którzy są z Cylicijej i Asijej, swarząc się z Stefanem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '9', '9', 'I nie mogli dać odporu mądrości i duchowi, którym mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '10', '10', 'A przetoż naprawili niektóre, coby powiedzieli, iż go słyszeli mówiącego słowa bluźnierskie przeciw Mojżeszowi i Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '11', '11', 'I wzruszyli pospólstwo i starsze i doktory, a zbieżawszy się pojmali go i przywiedli do rady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '12', '12', 'Postawili też fałszywe świadki, którzy by mówili: Że ten człowiek nie przestawa mówić słów bluźnierskich przeciw temu świętemu miejscu i zakonowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '13', '13', 'Abowiemechmy go słyszeli mówiącego, iż ten Jezus Nazareński zburzy miejsce to i odmieni obchody, które nam podał Mojżesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '5', '14', '14', 'Tedy patrząc nań pilnie oczyma swemi oni wszyscy, co siedzieli w radzie, widzieli oblicze jego jako oblicze Anielskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '0', '0', 'A tak rzekł książę kapłańskie: Takli się ma ta rzecz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '1', '1', 'Tedy on powiedział: Mężowie bracia i ojcowie słuchajcie. Bóg on chwały ukazał się ojcu naszemu Abrahamowi, gdy był w Mezopotamijej, przed tym niż mieszkał w Charran.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '2', '2', 'I powiedział mu: Wynidź z ziemię twej i od narodu twego, a idź do ziemię, którą ja ukażę tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '3', '3', 'A tak wyszedł z ziemie Kaldejczyków i mieszkał w Charran, a ztamtąd, gdy umarł ociec jego, przeniósł go do ziemie tej, w której wy teraz mieszkacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '4', '4', 'I nie dał mu w niej osiadłości i na stopę nogi, chociaż mu obiecał ją dać ku dzierżeniu i po nim potomstwu jego, chociaż nie miał dzieci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '5', '5', 'Mówił tedy tak Bóg: Będzie potomstwo twe mieszkać w ziemi obcej, gdzie będzie podane w niewolą, a będzie ściśnione doległościami przez cztery sta lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '6', '6', 'Ale naród, któremu służyć będą, ja będę karał, mówi Bóg i potym wynidą i będą mi służyć na miejscu tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '7', '7', 'I dał mu przymierze obrzezania i także Abrahamowi urodził się Izaak, a obrzezał go dnia ósmego, Izaakowi zasię urodził się Jakub, a Jakubowi dwanaście patriarchów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '8', '8', 'Tedy patriarchowie ruszeni nienawiścią, przedali Józefa do Egiptu, ale był Bóg z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '9', '9', 'I wyjął go ze wszytkich jego ucisków, a dał mu wdzięczność i mądrość przed Faraonem, królem Egiptskim, który go uczynił przełożonym nad Egiptem i nad wszytkim domem swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '10', '10', 'Potym przyszedł głód na wszytkę ziemię Egiptską i Chananejską, a wielki ścisk tak, iż nie najdowali żywności ojcowie naszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '11', '11', 'A gdy usłyszał Jakub o zbożu w Egiptcie, posłał ojce nasze naprzód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '12', '12', 'I za wtórym jechaniem poznan jest Józef od braciej swej i oznajmion jest Faraonowi naród Józefów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '13', '13', 'A tak Józef wyprawiwszy posły, przyzwał ojca swego Jakuba i wszytkiego swego narodu, siedm dziesiąt i pięć osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '14', '14', 'Szedł tedy Jakub do Egiptu, a tam umarł i ojcowie naszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '15', '15', 'I przeniesieni są do Sychem, a położeni w grobie, który kupił Abraham za pieniądze u synów Emora, syna Sychemowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '16', '16', 'Gdy tedy blisko przychodził czas obietnice, którą był zaprzysiągł Bóg Abrahamowi, rozrodził się lud i rozmnożył się w Egipcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '17', '17', 'Aż potym nastał iny król, który nie znał Józefa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '18', '18', 'Ten schytrzał na nasz naród, a wielem złego uciskał ojce nasze tak, iż musieli dziatki swe wymiatać, żeby żywo nie zostały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '19', '19', 'W który czas narodził się Mojżesz, który był wziętym u Boga i chowan jest przez trzy miesiące w domu ojca swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '20', '20', 'Gdy go potym wyrzucono, wzięła go córka Faraonowa i wychowała go sobie za syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '21', '21', 'I wyćwiczon jest Mojżesz we wszelkiej mądrości Egiptskiej, a był mężnym w powieściach i w sprawach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '22', '22', 'A gdy mu już było czterdzieści lat, przyszło mu na myśl aby nawiedził bracią swą, syny izraelskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '23', '23', 'Tam widząc niektórego krzywdę cierpiącego, obronił i pomścił się za onego, co miał krzywdę, zabiwszy Egiptcjanina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '24', '24', 'Tak sobie tusząc, jakoby mieli rozumieć bracia jego, że Bóg miał im dać wybawienie przez rękę jego, lecz oni tego nie rozumieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '25', '25', 'Ale nazajutrz ujźrzał je wadzące się społu i wiódł je ku pokojowi, mówiąc: Mężowie, bracia jesteście, przeczże jedni drugim krzywdę czynicie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '26', '26', 'Tedy ten co krzywdę czynił bliźniemu, odegnał go, mówiąc: Któż cię postawił książęciem i sędzią nad nami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '27', '27', 'Zali ty chcesz zabić i mnie, jakoś zabił wczora Egiptcjanina?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '28', '28', 'Za tymi słowy Mojżesz uciekł i mieszkał jako gość w ziemi Madjańskiej, gdzie się mu urodzili dwa synowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '29', '29', 'A gdy wyszło czterdzieści lat, ukazał się mu na puszczy przy górze Synaj Anioł Pański we krzu ogniem pałającym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '30', '30', 'To gdy ujźrzał Mojżesz, dziwował się onemu widzeniu, a chcąc bliżej przyjdź aby to oglądał, stał się do niego głos Pański.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '31', '31', 'Jam jest on Bóg ojców twoich, Bóg Abrahamów, Bóg Izaaków i Bóg Jakubów. Tedy Mojżesz jął drżeć, ani tam śmiał pojźrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '32', '32', 'Potym mu Pan rzekł: Zzuj trzewiki z nóg swoich, bo miejsce na którym stoisz, jest ziemia święta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '33', '33', 'Widziałciem widział utrapienie ludu mego, który jest w Egiptcie i słyszałem wzdychanie ich, a zstąpiłem żebych je wyjął. A przetoż teraz chodź, a poślę cię do Egiptu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '34', '34', 'Tegoć Mojżesza, którego się byli zaprzeli mówiąc: Któż cię postanowił książęciem i sędzią? Tegoć Bóg książęciem i odkupicielem posłał, za prowadzeniem Anioła onego, którego widział we krzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '35', '35', 'Tenci je wywiódł uczyniwszy cuda i znaki w Egiptcie i w Morzu Czerwonym i na puszczy, przez czterdzieści lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '36', '36', 'Tenci jest Mojżesz on, który powiedział synom izraelskim: Proroka wam wzbudzi Pan Bóg wasz z braciej waszej, jako mnie, jegoż słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '37', '37', 'Tenci jest, który był w zgromadzeniu na puszczy z Aniołem, który z nim rozmawiał na górze Synaj i z ojcy naszymi, który przyjął wyroki żywe aby je nam podał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '38', '38', 'Któremu nie chcieli być posłuszni ojcowie naszy, ale odrzucali i odwracali serca swe do Egiptu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '39', '39', 'Mówiąc do Aarona: Uczyń nam Bogi, którzyby szli przed nami, abowiem Mojżeszowi onemu, który nas wywiódł z ziemie Egiptskiej, nie wiemy co się przydało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '40', '40', 'I uczynili w onych czasach cielca, a sprawowali ofiarę bałwanowi i kochali się w sprawach rąk swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '41', '41', 'Tedy się Bóg odwrócił, a podał je na to, aby służyli zastępom niebieskim, jako napisano jest w księgach prorockich: Zażeście mi palone i ine ofiary ofiarowali na puszczy przez czterdzieści lat narodzie izraelski?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '42', '42', 'Owszemeście nosili namiot Molochów i gwiazdę Boga waszego Remfan, któreście kształty czynili abyście chwalili. A przetoż was przeniosę do Babilonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '43', '43', 'Namiot świadectwa mielić przodkowie naszy na puszczy, jako był rozrządził ten, który powiedział Mojżeszowi, aby go uczynił wedle kształtu, który widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '44', '44', 'Który wziąwszy wnieśli przodkowie nasi z Jozuem, wchodząc w osiadłość poganów, które Bóg wygnał przed ojcy naszymi, aż do czasów Dawidowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '45', '45', 'Który był w łasce u Boga i prosił, aby znalazł przybytek Bogu Jakubowemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '46', '46', 'A Salomon zbudował jemu dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '47', '47', 'Ale on Nawyższy nie mieszka w kościelech ręką uczynionych, jako prorok powieda,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '48', '48', 'Niebo jestci stolicą moją, a ziemia podnóżek nóg moich. Cóż mi za dom zbudujecie, mówi Pan, abo które jest miejsce odpoczynienia mego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '49', '49', 'Zali ręka moja tego wszytkiego nie uczyniła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '50', '50', 'Ludzie twardego karku i nie obrzezanego serca i uszu, wy się zawżdy zastawujecie Duchowi świętemu, jako ojcowie waszy także też i wy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '51', '51', 'Któregoż proroka nie prześladowali ojcowie waszy? Pomordowalić ony, którzy opowiedali przyście onego Sprawiedliwego, któregoście wy teraz byli zdrajcami i morderzmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '52', '52', 'Którzyście wzięli zakon przez rozrządzenie Anielskie, a nie zachowaliście go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '53', '53', 'To oni słysząc, prawie się pukali w sercach swych i zgrzytali nań zębami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '54', '54', 'A tak on będąc pełen Ducha świętego, patrząc pilnie w niebo oczyma swemi, ujźrzał chwałę Bożą i Jezusa stojącego na prawicy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '55', '55', 'Oto widzę nieba otworzone, a Syna człowieczego stojącego na prawicy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '56', '56', 'Tedy oni krzyknęli głosem wielkim, a zatulili sobie uszy i rzucili się nań jednostajnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '57', '57', 'I wyrzuciwszy go z miasta kamionowali, a świadkowie składli płaszcze swe u nóg młodzieńca, którego zwano Saul.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '58', '58', 'Ukamionowali tedy Stefana, gdy się modlił i mówił: Panie Jezu przyjmi ducha mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '6', '59', '59', 'I klęknąwszy na kolana, wołał głosem wielkim: Panie, nie przyczytaj im tego grzechu. A gdy to wymówił, zasnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '0', '0', 'Tedy Saul zezwalał na jego śmierć. A onegoż czasu wszczęło się wielkie prześladowanie przeciw kościołowi, który był w Jeruzalem i wszyscy rosproszyli się po krainach judzkich i Samarijej, oprócz Apostołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '1', '1', 'Wynieśli tedy społu Stefana ludzie bogobojni, a uczynili nad nim wielkie narzekanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '2', '2', 'A Saul burzył kościół, chodząc po wszytkich domiech i wywłócząc męże i niewiasty podawał je do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '3', '3', 'Oni tedy będąc rosproszeni, chodzili opowiedając słowa Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '4', '4', 'A Filip też gdy przyszedł do niektórego miasta w Samarijej, opowiedał im Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '5', '5', 'I słuchali z pilnością lub zgodnie tego, co Filip powiedał, słysząc i widząc znamiona, które czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '6', '6', 'Abowiem duchowie nieczyści od wiela tych, którzy je mieli, wychadzali wołając głosem wielkim, a wiele powietrzem ruszonych i chromych uzdrowieni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '7', '7', 'I było wielkie wesele w onym mieście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '8', '8', 'Tedy niektóry mąż imieniem Symon, był przed tym w onym mieście bawiąc się nauką czarnoksięską, a pomamił był lud w Samarijej, opowiedając się być niejakim wielkim człowiekiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '9', '9', 'Na którego się pilnie oglądali wszyscy, od namniejszego aż do nawięcszego mówiąc: Tenci jest ona moc Boża wielka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '10', '10', 'A dlatego się nań tak pilnie oglądali, iż od niemałego czasu zbłaźnił je był naukami czarnoksięskiemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '11', '11', 'Ale gdy uwierzyli Filipowi opowiedającemu ty rzeczy, które ku Królestwu Bożemu i imieniowi Pana Jezusa Krystusa należały, tedy się krzcili społu mężowie i niewiasty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '12', '12', 'Tak iż i sam Symon uwierzył, a okrzciwszy się mieszkał z Filipem i widząc jako czynił znamiona i cuda wielkie, lękał się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '13', '13', 'A gdy usłyszeli Apostołowie, którzy byli w Jeruzalem, iż Samarija przyjęła słowo Boże, posłali do nich Piotra i Jana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '14', '14', 'Którzy gdy tam przyszli, modlili się za nimi aby przyjęli Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '15', '15', '(Abowiem jeszcze nie przypadł był na żadnego z nich, jedno iż tylko pokrzczeni byli w imię Pana Jezusowe)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '16', '16', 'Potym na nie wkładali ręce i brali Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '17', '17', 'To gdy widział Symon, że przez wkładanie rąk Apostolskich był rozdawan Duch święty, przyniósł im pieniądze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '18', '18', 'Mówiąc: Dajcie mi też moc, iż na kogobych ręce włożył, aby brał Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '19', '19', 'Tedy rzekł Piotr do niego: Srebro twoje niech z tobą zginie, któryś mnimał, aby dary Boże miały być za pieniądze nabywane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '20', '20', 'Abowiem w tej sprawie nie masz ani cząstki ani działu, gdyż serce twe nie jest szczyre przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '21', '21', 'Uznajże się tedy od tej twej złości, a proś Boga aczci snadź będzie odpuszczona myśl serca twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '22', '22', 'Abowiem cię widzę być w gorzkości żółci i związaniu złości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '23', '23', 'Odpowiedziawszy tedy Symon rzekł: Módlcież się wy za mną u Pana, aby mi się co nie przydało z tych rzeczy, któreście powiedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '24', '24', 'A tak oni oświadczali i opowiedali słowo Boże i zatym się wrócili do Jeruzalem i w wielu miasteczkach Samarytańskich Ewanjeliją opowiedali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '25', '25', 'Potym Anioł Pański mówił do Filipa powiedając: Wstań a idź ku południowi drogą, która od Jeruzalem bieży ku Gazie, które jest spustoszałe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '26', '26', 'Tedy on wstawszy szedł, a oto niektóry Etijopczyk ochmistrz i zacny sprawca Kandacy królowej Etijopskiej, który był przełożonym nad wszytkimi skarby jej, a przyjechał był ku chwale Bożej do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '27', '27', 'I gdy się wrócił siedząc na wozie swym, czytał sobie Izajasza proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '28', '28', 'A tak rzekł Duch Filipowi: Przystąp a przyłącz się do wozu tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '29', '29', 'Przybiegszy tedy Filip, usłyszał go a on czyta Izajasza proroka i rzekł mu: Rozumnieszli co czytasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '30', '30', 'A on rzekł: Jakoż mam rozumieć, jeśli mi kto nie pokaże? I prosił Filipa, aby wstąpił i wsiadł do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '31', '31', 'A miejsce pisma było to: Jako owca ku zabiciu wiedzion jest, a jako baranek milczący przed tym, który go goli, tak nie otworzył ust swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '32', '32', 'W uniżeniu jego sąd jego wywyższon jest, a wiek jego i któż wypowie? Abowiem odjęt jest żywot jego z ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '33', '33', 'Tedy odpowiedziawszy ochmistrz on Filipowi, mówił: Proszę cię, o kimże tu prorok mówi? Samli o sobie czyli o kim inym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '34', '34', 'A tak Filip otworzywszy usta swe, a począwszy od onego pisma, opowiedał mu Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '35', '35', 'I gdy dalej jechali w drogę, przyszli do niektórej wody, tedy rzekł ochmistrz: Otóż woda, cóż przekaza, żebych nie miał być ochrzczon?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '36', '36', 'Ale mu powiedział Filip: Jeśliż ze wszytkiego serca wierzysz, tedy się godzi. A on mu odpowiedziawszy rzekł: Wierzę, iż Jezus Krystus jest Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '37', '37', 'Kazał tedy stanąć wozowi i wstąpili obadwa w wodę Filip z ochmistrzem, a okrzcił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '38', '38', 'A gdy wyszli z wody, Duch Pański porwał Filipa, tak iż go on Ochmistrz więcej nie widział i jechał drogą swą radując się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '7', '39', '39', 'A Filip aż w Azocie jest znalezion i chodząc po wszytkich mieściech opowiedał Ewanjeliją, aż przyszedł do Cezareji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '0', '0', 'Tedy Saul będąc jeszcze zapalony pogróżkami i morderstwy przeciw zwolennikom Pańskim, szedł do książęcia kapłańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '1', '1', 'I prosił go o listy do Damaszku do bożnic, iż gdzieby niektóre znalazł tej sekty, tak męże jako i niewiasty, aby je powiązawszy przywiódł do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '2', '2', 'A tak gdy był w drodze i gdy już był blisko Damaszku, tedy z prędka oświeciła się około niego światłość z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '3', '3', 'I gdy padł na ziemię usłyszał głos mówiący do siebie: Saulu, Saulu, przecz mię prześladujesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '4', '4', 'Tedy on rzekł: Któżeś jest Panie? A Pan rzekł: Jaciem jest Jezus, którego ty prześladujesz. Trudnać jest rzecz tobie przeciwko osnom wirzgać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '5', '5', 'A on drżąc i lękając się powiedział: Panie, cóż chcesz abych czynił? Tedy Pan rzekł kniemu: Wstań a idź do miasta i powiedząć tobie, coć będzie potrzeba czynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '6', '6', 'Tedy mężowie oni, którzy byli w drodze z Saulem, stanęli zlęknąwszy się, acz głos słysząc, ale żadnego nie widząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '7', '7', 'Zatym Saul wstał z ziemie, a otworzywszy oczy swoje, żadnego nie widział i także go ująwszy za ręce, wwiedli do Damaszku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '8', '8', 'Kędy był przez trzy dni nie widząc, ani jadł ani pił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '9', '9', 'Był też niektóry zwolennik w Damaszku imieniem Ananijasz, do którego Pan rzekł w widzeniu: Ananijaszu. A on rzekł: Otom ja jest Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '10', '10', 'Tedy Pan rzekł kniemu: Wstań a idź na ulicę, którą zową Prostą i szukaj w domu judowym niektórego na imię Saula z Tarsu, abowiemci się oto modli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '11', '11', 'I ujźrzał w widzeniu męża Ananijasza imieniem, wchodzącego i ręce nań wkładającego, aby wziął wzrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '12', '12', 'Odpowiedział tedy Ananiasz: Panie, słyszałem od wiela ich o mężu tym, jako wiele złego czynił świętym twym w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '13', '13', 'Ktemu też na tym miejscu ma moc od książąt kapłańskich wiązać wszytki, którzy wzywają imienia twego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '14', '14', 'Ale Pan rzekł do niego: Idźże, abowiem ten jest mnie naczyniem wybranem, aby nosił imię moje przed narody, królmi i syny izraelskimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '15', '15', 'Abowiem ja ukażę jemu jako wiele potrzeba mu cierpieć dla imienia mego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '16', '16', 'Szedł tedy Ananiasz i wszedł do domu onego, a włożywszy nań ręce, rzekł: Saulu bracie, Pan mnie posłał (Jezus on, któryć się ukazał w drodze, którąś szedł), abyś wziął wzrok, a był napełnion Duchem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '17', '17', 'A także natychmiast spadły od oczu jego jako łuski i wnet wziął wzrok, a wstawszy okrzczon jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '18', '18', 'A gdy wziął pokarm, posilił się, tamże Saul z zwolenniki, którzy byli w Damaszku, był przez kilka dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '19', '19', 'I natychmiast opowiedał w bożnicach Krystusa, że on był Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '20', '20', 'Zdumiewali się tedy wszyscy, którzy go słuchali, a mówili: Izali nie ten jest, który burzył w Jeruzalem ty, którzy wzywali imienia tego i tuć był dlatego przyszedł, aby je powiązawszy wiódł do książąt Kapłańskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '21', '21', 'Tedy się Saul tym więcej wzmagał i porabiał Żydy, którzy mieszkali w Damaszku, twierdząc, iż to był on Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '22', '22', 'I gdy wyszedł czas niemały, weszli Żydowie między sobą w radę, aby go stracili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '23', '23', 'Ale Saul zrozumiał zdrady ich, acz strzeżono bram we dnie i w nocy, jakoby go zatracili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '24', '24', 'A przetoż zwolennicy wziąwszy go w nocy przez mur wypuścili, powrozem spuściwszy w koszu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '25', '25', 'Gdy potym przyszedł Saul do Jeruzalem, kusił się przyłączyć do zwolenników, ale się go wszyscy bali, nie wierząc żeby był zwolennikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '26', '26', 'Tedy go Barnabasz wziąwszy, przywiódł do Apostołów i opowiedział im, jako w drodze widział Pana, który mówił do niego i jako w Damaszku beśpiecznie mówił w imię Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '27', '27', 'I był w Jeruzalem towarzysząc z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '28', '28', 'A bespiecznie mówił w imię Pana Jezusowe i gadał się z Greki tak, iż się oni starali jakoby go stracić mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '29', '29', 'Co gdy obaczyli bracia, odprowadzili go do Cezarejej i odesłali do Tarsu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '30', '30', 'A tak kościoły po wszytkim Żydostwie i Galilejej i Samarijej miały pokój i budowały się, a chodząc w bojaźni Bożej i w pociesze Ducha świętego rozmnażały się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '31', '31', 'Stało się potym, iż Piotr, gdy obchadzał po wszytkich, przyszedł też do świętych, którzy mieszkali w Lidji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '32', '32', 'Tamże nalazł niektórego imieniem Eneasz, od ośmi lat na łożu leżącego, który był ruszony powietrzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '33', '33', 'I rzekł mu Piotr: Eneaszu, uzdrawia cię Jezus Krystus. Wstańże i pościel sobie. Tedy on natychmiast wstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '34', '34', 'A widzieli go wszyscy ci, którzy mieszkali w Lidji i w Saronie, którzy się też nawrócili do Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '35', '35', 'Była też w Joppe niektóra zwolenniczka imieniem Tabita, która ktoby chciał wyłożyć, zowie się Dorkas, ta była bogata w dobre uczynki i w jałmużny, które czyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '36', '36', 'I stało się, że w onych czasiech będąc niemocą złożona, umarła, którą omywszy położono na sali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '37', '37', 'A iż Lidda była blisko Joppen, zwolennicy osłyszawszy się, że był Piotr w niej, posłali do niego dwu mężów prosząc, aby do nich przez omieszkania przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '38', '38', 'Tedy wstawszy Piotr, przyszedł z nimi, którego gdy przyszedł wwiedli na salę, gdzie też przy nim stanęły wszytki wdowy płacząc i ukazując suknie i płaszcze, które robiła Dorkas, gdy była z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '39', '39', 'A tak wyrzuciwszy precz wszytki, Piotr poklęknąwszy na kolana, modlił się, a obróciwszy się do ciała rzekł: Tabito wstań, tedy ona otworzyła oczy swoje, a ujźrzawszy Piotra, siadła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '40', '40', 'I podawszy jej rękę, podniósł ją, a wezwawszy świętych i wdów, postawił ją żywą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '41', '41', 'Tedy to wszytko było jawno po wszytkim Joppen i wiele ich uwierzyło w Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '8', '42', '42', 'Mieszkał tedy przez niemały czas w Joppe u niektórego Symona garbarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '0', '0', 'Był też w Cezareji niektóry imieniem Korneliusz, setnik z roty, którą zwano włoska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '1', '1', 'Człowiek pobożny i bojący się Boga, ze wszytkim domem swym, dawając jałmużny wielkie ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '2', '2', 'Który, gdy się modlił Bogu po wszytek czas, widział jawnie w widzeniu, jakoby o dziewiątej godzinie na dzień, Anioła Bożego wchodzącego do siebie i mówiącego jemu: Korneli!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '3', '3', 'A on nań pilnie patrząc oczyma swoimi, lęknąwszy się rzekł: Ktoś jest mój Panie? Tedy mu on rzekł: Modlitwy twe i jałmużny twoje wstąpiły ku pamiątce przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '4', '4', 'A przetoż teraz pośli kogo do Jeppen, a wzów Symona, którego nazywają Piotrem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '5', '5', 'Tenci mieszka u niektórego Symona Garbarza, co ma dom nad morzem, a tenci tobie powie, coć będzie czynić potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '6', '6', 'I jako skoro odszedł Anioł, który mówił z Korneliuszem, wezwał dwu z służebników swoich i żołnierza bogobojnego z tych, którzy przy nim ustawicznie byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '7', '7', 'A opowiedziawszy im wszytki rzeczy, posłał je do Joppen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '8', '8', 'A tak nazajutrz, gdy byli w drodze i już niedaleko miasta, wszedł Piotr wzgórę na dom, aby się modlił około szóstej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '9', '9', 'I przydało się, że na ten czas był łacznym, a chciał jeść i gdy mu oni gotowali, przyszło nań odeście od pamięci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '10', '10', 'Tedy ujźrzał niebo otworzone, a zstępujące kniemu czynie niejakie, jakoby prześcieradło wielkie, za cztery końce uwiązane, które spuszczono aż ku ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '11', '11', 'Na którym były wszytki o czterech nóg zwierzęta i bestie i gadziny i ptastwa powietrzne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '12', '12', 'Stał się tedy głos do niego: Wstań Pietrze! Rzeź a jedz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '13', '13', 'Ale Piotr rzekł: Nie będę Panie, abowiemem nigdy nie jadł nic pospolitego abo nieczystego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '14', '14', 'Tedy powtóre stał się głos kniemu: Co Bóg oczyścił, tedy ty nie miej sobie za nieczyste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '15', '15', 'A toć się po trzy kroć stało i zasię naczynie ono wzięto jest w niebo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '16', '16', 'I gdy Piotr sam w sobie wątpił, coby to za widzenie było, które widział, oto mężowie, którzy od Korneliusza posłani byli, pytali się o domu Symonowym i przyszli do przysionka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '17', '17', 'A wyzwawszy niektórego, pytali jeśliby tam Symon, którego nazywają Piotr gospodą mieszkał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '18', '18', 'I gdy tak myślił Piotr o widzeniu onym rzekł mu Duch: Oto cię szukają trzej mężowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '19', '19', 'A przetoż wstawszy idź z nimi nic nie mieszkając, bom ja posłał ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '20', '20', 'Tedy Piotr szedszy do onych mężów, którzy od Korneliusza posłani byli do niego rzekł im: Otomci ja jest, którego szukacie. Cóż tego za przyczyną iżeście tu przyszli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '21', '21', 'A oni mu powiedzieli: Korneliusz setnik, człowiek sprawiedliwy i bojący się Boga i mający dobre świadectwo o sobie od ludu żydowskiego, wziął Boskie napominanie od Anioła świętego, aby cię wezwał w dom swój, a usłyszał niektóre słowa od ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '22', '22', 'A tak wezwawszy ich w dom, przyjął je do gospody, a nazajutrz Piotr szedł z nimi i niektórzy bracia z Joppeńczyków szli przy nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '23', '23', 'Tedy nazajutrz weszli do Cezareji, a Korneliusz czekał ich wezwawszy krewnych swych i powinnych przyjaciół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '24', '24', 'A gdy już wchadzał Piotr, zabieżawszy Korneliusz przypadł do nóg jego, kłaniając się jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '25', '25', 'Ale go Piotr podniósł, mówiąc: Wstań, bomci też ja jest człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '26', '26', 'I rozmawiając z nim wszedł, a nalazł wiele tych, co się byli zeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '27', '27', 'Zatym rzekł do nich: Wy wiecie, że się nie godzi żadnemu Żydowi przyłączać się a przychodzić do cudzoziemca. Ale mnie Bóg ukazał, żebych żadnego człowieka nie nazywał plugawym i nieczystym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '28', '28', 'A przetożem też oprócz wymówki przyszedł wezwany i pytam się, dla czegoście mię wezwali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '29', '29', 'Rzekł tedy Korneliusz: Tak czwarty dzień aż do tej godziny pościłem, a o dziewiątej godzinie modliłem się w domu moim, a oto mąż niektóry stanął przede mną w odzieniu jasnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '30', '30', 'I powiedział mi: Korneli! Wysłuchanać jest modlitwa twoja i jałmużny twoje były na dobrej pamięci przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '31', '31', 'A przetoż pośli do Joppen, a wzwów Symona, którego nazywają Piotr. Tenci mieszka w domu Symona garbarza nad morzem, któryć gdy przyjdzie, rozmówi się z tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '32', '32', 'A takem tegoż czasu posłał do ciebie, a tyś dobrze uczynił żeś przyszedł, a teraz już jestechmy wszyscy przed Bogiem, abyśmy słuchali wszytkiego tego, co tobie jest roskazano od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '33', '33', 'Tedy Piotr otworzywszy usta, rzekł: Prawdziwie uznawam, żeć Bóg nie brakuje osobami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '34', '34', 'Ale w każdym narodzie, ktokolwiek się go boi a czyni sprawiedliwość, jest u niego wziętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '35', '35', 'To co oznajmił synom izraelskim, opowiedając pokój przez Jezusa Krystusa, który jest Panem wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '36', '36', 'Wy wiecie, co się stało po wszytkim Żydostwie, począwszy od Galilejej, po krzcie, który Jan opowiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '37', '37', 'Iż Jezusa Nazareńskiego pomazał Bóg Duchem świętym i mocą, który chodził czyniąc wiele dobrego i uzdrawiając wszytki uciśnione od diabła, abowiem z nim był Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '38', '38', 'A tego wszytkiego i co w krainach żydowskich i w Jeruzalem czynił, mychmy też są świadkowie, którego podniesiono zawiesiwszy na drzewie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '39', '39', 'Tegoć Bóg wzbudził dnia trzeciego i sprawił to, żeby był objawionym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '40', '40', 'Nie wszytkiemu ludowi, ale nam świadkom, które sobie pierwej naznaczył, którzychmy z nim społu jedli i pili, gdy powstał od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '41', '41', 'I roskazał nam, abychmy opowiedali ludowi, a oświadczali iżci to on jest, którego Bóg naznaczył, sędzią żywych i umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '42', '42', 'Zatymci też i wszyscy prorocy świadectwo przynaszają, iż każdy weźmie grzechów odpuszczenie przez imię jego, któżkolwiek weń uwierzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '43', '43', 'Ty słowa, gdy jeszcze Piotr mówił, przypadł Duch święty na wszytki, którzy tych słów słuchali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '44', '44', 'A tak wierni, co byli z obrzezania, a przyszli z Piotrem, polękali się widząc, że i na pogany dar Ducha świętego był wylan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '45', '45', 'Abowiem słyszeli je, gdy mówili inemi języki i wielbili Boga. Zatymże Piotr odpowiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '46', '46', 'Izali kto może bronić wody, żeby ci nie byli pokrzczeni, którzy wzięli Ducha świętego jako i my?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '9', '47', '47', 'Kazał je tedy krzcić w imię Pańskie i prosili go, aby u nich został na kilka dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '0', '0', 'Usłyszeli tedy Apostołowie i bracia, którzy w Żydostwie byli, że i pogani przyjmowali słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '1', '1', 'A przetoż, gdy Piotr przyszedł do Jeruzalem, wadzili się z nim ci, którzy byli z obrzezania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '2', '2', 'Mówiąc: Żeś wszedł do mężów nieobrzezanych, a jadłeś społu z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '3', '3', 'A począwszy Piotr, wszytko im oznajmił, mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '4', '4', 'Ja gdym był w mieście Joppe modląc się, widziałem w zachwyceniu widzenie. A ono niektóre naczynie zstępowało jakoby prześcieradło wielkie za cztery końce uwiązane, które było spuszczane z nieba, a przyszło aż do mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '5', '5', 'Na które, gdym pilnie patrzył, obaczyłem i ujźrzałem wszytki o czterech nóg zwierzęta ziemie i bestie i gadziny i ptastwa powietrzne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '6', '6', 'Przytymem też usłyszał głos mówiący do mnie: Wstań Pietrze, rzeź a jedz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '7', '7', 'Alem ja powiedział: Nie będę Panie, abowiem nigdy nic pospolitego abo nieczystego nie wchadzało w usta me.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '8', '8', 'Tedy mi powtóre odpowiedział głos z nieba: To, co Bóg oczyścił, ty nie miej za nieczyste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '9', '9', 'A toć się stało po trzy kroć, zatym zasię ono wściągniono do nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '10', '10', 'Potym oto w tenże czas przyszli trzej mężowie w dom ten, w którymem ja był, z Cesarjej ku mnie posłany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '11', '11', 'I powiedział mi Duch, abych z nimi szedł nic się nie ociągając i szli też ze mną ci to sześć braciej, a weszliśmy w dom człowieka niktórego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '12', '12', 'Który nam powiedział, jako widział Anioła w domu swym, który stanąwszy rzekł mu: Pośli niektóre do Joppen, a wzów Symona co go nazywają Piotr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '13', '13', 'Któryć tobie powie przez co i ty i dom twój zachowan będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '14', '14', 'A tak, gdym ja począł mówić, tedy Duch Święty przypadł na nie, tak jako i na nas z początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '15', '15', 'Przypomniałem tedy sobie ony słowa Pańskie, jako mówił: Aczci Jan krzcił wodą, ale wy będziecie okrzczeni Duchem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '16', '16', 'Gdyż im tedy równy dar dał Bóg jako i nam, gdychmy uwierzyli w Pana Jezusa Krystusa. Cóżem ja tedy zacz był, którybych mógł pohamować Boga?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '17', '17', 'To oni usłyszawszy uciszyli się i chwalili Boga, mówiąc: Że też i poganom dał Bóg upamiętanie ku żywotowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '18', '18', 'Oni zasię, którzy byli rosproszeni w onym okrucieństwie, które się zaczęło dla Stefana, zachodzili aż do Fenicijej, do Cypru i do Antjochijej, żadnemu nie opowiedając słowa Bożego, jedno samym Żydom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '19', '19', 'Lecz niektórzy z tych Cypryjczycy i Cyrenejczycy, którzy wszedszy do Antjochijej, mówili Grekom oznajmując Pana Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '20', '20', 'I była z nimi ręka Pańska, a wielki poczet wierzących nawrócił się ku Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '21', '21', 'Zatym o nich przyszła wieść do uszu kościołowi, który był w Jeruzalem. A przetoż posłali Barnabasza aż do Antjochijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '22', '22', 'Który gdy tam przyszedł, a ujźrzał łaskę Bożą, uradował się i prosił wszytkich, aby w przedsię wiźęciu serdecznym trwali w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '23', '23', 'Abowiem był mąż dobry i pełen Ducha świętego i wiary, a przystało wielkie mnóstwo ludzi ku Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '24', '24', 'Potym Barnabasz szedł do Tarsu, aby szukał Saula.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '25', '25', 'A znalazszy go, przywiódł do Antjochijej. Tam tedy przez wszytek rok schadzali się do kościoła i uczyli lud wielki. A tak zwolenniki na przód w Antjochijej nazywano Krześciany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '26', '26', 'Pod onymże czasem przyszli prorocy z Jeruzalem do Antjochijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '27', '27', 'A powstawszy jeden z nich imieniem Agabus, oznajmował przez ducha, iż miał przyjdź głód wielki po wszytkim świecie, który się stał za Klaudjusza cesarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '28', '28', 'A tak wszyscy zwolennicy, jako który mógł wystarczyć, postanowili posłać nieco na ratunek braciej, którzy mieszkali w Żydostwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '10', '29', '29', 'Co i uczynili posławszy do starszych przez Barnabasza i Saula.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '0', '0', 'Przytym tegoż czasu począł Herod król trapić niektóre z kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '1', '1', 'I zamordował Jakuba brata Janowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '2', '2', 'A gdy to ujźrzał być wdzięczną rzecz Żydom, starał się jakoby był pojmał i Piotra (a były dni Przaśników)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '3', '3', 'Którego też pojmawszy, podał do więzienia, poruczywszy go pod czworę straż, gdzie było w każdej po czterech drabiech, aby go strzegli, chcąc po Wielkiej nocy wywieść go ludowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '4', '4', 'A przetoż Piotr był chowan pod strażą, ale modlitwa ustawiczna od kościoła ku Bogu bywała za nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '5', '5', 'Gdy temu przyjdź miało, aby go był Herod wywiódł, tedy onej nocy spał Piotr między dwiema draby, związany dwiema łańcuchy, a stróże przed drzwiami strzegli ciemnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '6', '6', 'A oto Anioł Pański przyszedł, a jasność się oświeciła w ciemnicy i trąciwszy w bok Piotrów, obudził go mówiąc: Wstań rychło. I opadły łańcuchy z rąk jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '7', '7', 'Zatym Anioł rzekł kniemu: Opasz się, a podwiąż trzewiki twoje. I uczynił tak. Tedy mu on rzekł: Oblecz na się płaszcz twój, a idź za mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '8', '8', 'A tak wyszedszy Piotr, szedł za nim, ani wiedział żeby to było prawdziwie, co się działo przez Anioła, lecz mniemał, żeby widzenie widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '9', '9', 'Potym gdy przeminęli pierwszą i wtórą straż, przyszli do bramy żelaznej, która wiedzie ku miastu, a ta się im dobrowolnie otworzyła i wyszedszy przeszli jednę ulicę, a tak natychmiast odszedł Anioł od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '10', '10', 'Tedy Piotr przyszedszy ksobie rzekł: Teraz znam prawie, iż posłał Pan Anioła swojego i wyjął mnie z rąk Herodowych i ze wszytkiego oczekawania ludu Żydowskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '11', '11', 'A zrozumiawszy rzecz, przyszedł do domu Marjej matki Janowej, którego nazywano Marek, gdzie było wiele zgromadzonych i modlących się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '12', '12', 'Gdy tedy Piotr zakołatał we drzwi u przysionka, dzieweczka imieniem Rode wyszła słuchać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '13', '13', 'Która poznawszy Piotrów głos, przed radością nie otworzyła przysionka, ale w dom wbieżawszy oznajmiła, iż Piotr stał przed przysionkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '14', '14', 'Ale oni rzekli do niej: Szalejesz! Wszakoż ona twierdziła, iż się tak rzecz ma. A oni mówili: Jest Anioł jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '15', '15', 'I trwał Piotr kołacząc, a gdy otworzyli, ujźrzeli go i lękli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '16', '16', 'A gdy na nie ręką kinął aby umilknęli, powiedział im jako Pan wywiódł go z strażej i rzekł: Oznajmicie to Jakubowi i braciej, a wyszedszy szedł na ine miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '17', '17', 'I gdy dzień nastał, tedy był rozruch niemały między draby, coby się z Piotrem stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '18', '18', 'A tak, gdy się o nim Herod wywiadował, a nie nalazł go, osądziwszy prawo o stróżach, skazał je na stracenie i wyjechawszy z Żydostwa do Cesarejej, tamże mieszkał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '19', '19', 'Zatym Herod umyślił podnieść wojnę na Tyryjczyki i Sydończyki, ale oni zgodnie przyszli do niego, a naprawiwszy Blasta, który był przedniejszym w komorze królewskiej, prosili o pokój dlatego, iż kraina ich miała żywność z dzierżaw królewskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '20', '20', 'Potym czasu niektórego Herod oblókszy się w szatę królewską i siadszy na stolicy, uczynił rzecz do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '21', '21', 'Tedy lud wołał: Głos Boży, a nie człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '22', '22', 'I natychmiast go uderzył Anioł Pański, dlatego, że nie dał chwały Bogu, a będąc stoczony od robactwa, zdechł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '23', '23', 'Ale słowo Pańskie rozrastało się i rozmnażało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '11', '24', '24', 'A tak Barnabasz i Saul wrócili się z Jeruzalem uczyniwszy dosyć posłudze, wziąwszy z sobą społu i Jana, który był nazwan Markiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '0', '0', 'Tedy byli w Antjochijej w kościele, który tam był, niektórzy prorocy i nauczyciele, jako Barnabasz i Symeon, którego zwano Czarny i Lucjusz Cyrenejczyk i Manaen, który był społu wychowan z Herodem królem i Saul.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '1', '1', 'Gdy tedy ci służyli Panu i pościli, rzekł Duch święty: Wyłączcie mi Barnabasza i Saula ku tej sprawie, do którejem ich wezwał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '2', '2', 'A tak, gdy post odprawili i modlili się, włożywszy na nie ręce odesłali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '3', '3', 'Oni tedy odesłani od Ducha świętego, szli do Seleucjej, a ztamtąd się wieźli do Cypru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '4', '4', 'I gdy przyszli do Salaminy, oznajmowali słowo Boże w bożnicach Żydowskich, mając z sobą i Jana służebnika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '5', '5', 'A przeszedszy wysep aż do Pafu, naleźli tam niektórego czarnoksiężnika, fałesznego proroka, Żyda, któremu było imię Barjesus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '6', '6', 'Który był przy staroście Sergjuszu Pawle człowieku mądrym. Ten zawoławszy Barnabasza i Saula, żądał od nich słuchać słowa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '7', '7', 'Ale im przekażał Elimas, to jest czarnoksiężnik (abowiem się tak wykłada imię jego) starając się jakoby odwrócić starostę od wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '8', '8', 'Tedy Saul (którego zwano i Paweł), napełniony Duchem świętym pojźrzawszy nań pilnie oczyma,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '9', '9', 'Powiedział: O pełny wszelkiej zdrady i wszelakiej niecnoty synu diabelski, nieprzyjacielu wszelkiej sprawiedliwości. I nie przestanieszże podwracać prostych dróg Pańskich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '10', '10', 'A przetoż oto teraz ręka Pańska jest przeciw tobie, a będziesz ślepym, ani oglądasz słońca aż do słusznego czasu. A tak wnet przypadło nań zaćmienie i ciemności i chodząc w około szukał tych, którzyby go za rękę wywiedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '11', '11', 'To gdy starosta ujźrzał co się stało, uwierzył, będąc przestraszon nauką Pańską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '12', '12', 'Potym wyjechawszy z Pafu Paweł i ci, którzy z nim byli, przyszli do Pergi w Pamfiljej, a Jan odszedszy od nich wrócił się do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '13', '13', 'Ale oni gdy wyszli z Pergi, przyszli do Antjochijej miasta w Pizydji, a wszedszy do bożnice dnia szabatnego, siedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '14', '14', 'A tak po przeczytaniu zakonu i proroków, posłały książęta bożnice do nich z tymi słowy: Mężowie bracia, jeśliż jest między wami jakie słowo napominania do ludu, powiedajcież.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '15', '15', 'A przetoż powstawszy Paweł, a ręką milczenie uczyniwszy rzekł: Mężowie Izraelscy i którzy się boicie Boga, słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '16', '16', 'Bóg ludu tego izraelskiego obrał ojce nasze, a wywyższył lud, gdy przemieszkawali w ziemi Egiptskiej i wywiódł je z niej wyniosłem ramieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '17', '17', 'I około czterdzieści lat czasu cierpiał zwyczaje ich na puszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '18', '18', 'A gdy wygładził siedm narodów w ziemi Chananejskiej, losem im podzielił ziemię ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '19', '19', 'Potym około czterzech set i piącidziesiąt lat, dał im sędzie aż do Samuela proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '20', '20', 'Potym prosili o króla i dał im Bóg Saula, syna Cys, męża z pokolenia Benjamina, przez czterdzieści lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '21', '21', 'I odjąwszy onego, wzbudził im Dawida króla, o którym dawszy świadectwo powiedział: Znalazłem Dawida syna Jesse, męża wedle serca mego, który czynić będzie wszytkę wolą moję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '22', '22', 'Z jegożci nasienia Bóg według obietnice swojej wzbudził Izraelowi Zbawiciela, Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '23', '23', 'Gdyż przed tym Jan opowiedał przed nim już przychodzącym krzest uznania Izraelowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '24', '24', 'I gdy Jan wypełnił bieg swój powiedział: Kimże mię być mnimacie? Nie jestciem ja ten, ale oto idzie za mną, z którego nóg nie jestem godzien abych rozwiązał trzewik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '25', '25', 'Mężowie bracia, synowie narodu Abraamowego i którzy między wami boją się Boga. Wamci są posłane słowa zbawienia tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '26', '26', 'Abowiem ci, co mieszkają w Jeruzalem i książęta ich, gdy go nie znali a skazali go, słowa prorockie, które przez każdy szabat bywają czytane, wypełnili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '27', '27', 'A nie znalazszy żadnej przyczyny śmierci, prosili u Piłata aby był zabit.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '28', '28', 'I gdy wszytkiemu dosyć uczynili, co o nim napisano było, zjąwszy go z drzewa, położyli w grobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '29', '29', 'Ale go Bóg wzbudził od umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '30', '30', 'Który widzian jest przez wiele dni, od tych którzy z nim społu przyszli z Galilejej do Jeruzalem, a są świadki jego u ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '31', '31', 'A także i my oznajmujemy wam tę obietnicę, która się ojcom zstała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '32', '32', 'Żeć ją Bóg wypełnił nam synom ich, wzbudziwszy Jezusa, jako też jest napisano w Psalmie wtórym: Syn mój jesteś ty, ja dziś porodziłem ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '33', '33', 'A iż go wzbudził od umarłych, już się więcej nie wróci do grobu i tak powiedział: Dam wam ony mocne Dawidowe dobrodziejstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '34', '34', 'I dlategoż indziej powiedział: Nie dopuścisz, aby święty twój miał czuć naruszenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '35', '35', 'Abowiem Dawid, gdy za wieku swego służył radzie Bożej, zasnął i przyłączon jest do ojców swoich, a uczuł naruszenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '36', '36', 'Ale ten, którego Bóg wzbudził, nie uczuł naruszenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '37', '37', 'Niechżeć wam tedy będzie jawno bracia, iż przez tego oznajmione wam jest odpuszczenie grzechów i to wszytko od czegoście przez zakon Mojżeszów nie mogli być rozwiązani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '38', '38', 'Przez tegoć każdy, który wierzy, bywa rozwiązan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '39', '39', 'A przetoż baczcie, aby na was nie przyszło to, co powiedziano w prorocech:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '40', '40', 'Obaczcie wy, którzy gardzicie. Dziwujcie się a zniszczejcie, bo ja sprawuję sprawę za waszych czasów, sprawę której nie uwierzycie jeśliby wam kto powiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '41', '41', 'Zatym gdy wyszli z bożnice Żydowskiej, prosili ich pogani, aby w drugi szabat mówili do nich ty słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '42', '42', 'A gdy się rozeszło zgromadzenie, szło wiele Żydów i bogobojnych ludzi, nowo do Żydostwa przystających, za Pawłem i Barnabaszem, którzy mówiąc do nich radzili im, aby trwali w łasce Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '43', '43', 'A tak w drugi szabat prawie wszytko miasto zebrało się ku słuchaniu słowa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '44', '44', 'Tedy Żydowie ujźrzawszy wielki lud, napełnieni są zazdrości i sprzeciwili się temu, co Paweł powiedał, mówiąc przeciw temu i bluźniąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '45', '45', 'Ale Paweł i Barnabas świebodnie mówili: Wamci było naprzód potrzeba opowiedać słowo Boże, ale gdy je odrzucacie, a skazujecie się sami być niegodni wiecznego żywota, otóż my obracamy się do poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '46', '46', 'Abowiem nam tak roskazał Pan mówiąc: Postanowiłem cię światłością poganom, abyś był zbawieniem aż do ostatecznym ziem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '47', '47', 'To tedy słysząc pogani uradowali się i chwalili słowo Pańskie, a uwierzyli ile ich jedno było zrządzonych ku żywotowi wiecznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '48', '48', 'Roznaszało się tedy słowo Pańskie po wszytkiej onej krainie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '49', '49', 'Ale Żydowie poduszczali niewiasty bogobojne i uczciwe i przedniejsze w mieście, a wzruszyli prześladowanie na Pawła i Barnabasa i wyrzucili je z granic swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '50', '50', 'A tak oni, otrząsnąwszy proch z nóg swoich na nie, przyszli do Ikonjum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '12', '51', '51', 'A zwolennicy byli napełnieni wesela i Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '0', '0', 'Stało się potym w Ikonjum, iż społu weszli do bożnice Żydowskiej i mówili tak, że wielkie mnóstwo Żydów i Greków uwierzyli pospołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '1', '1', 'Ale Żydowie, którzy niewierni byli, pobudzali lud, a uczynili złe serce poganom przeciw braciej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '2', '2', 'A przetoż niemały czas strawili bespiecznie powiedając za pomocą Pańską, który dawał świadectwo słowu łaski swojej i dawał to, że się znamiona i cuda okazowały przez ręce ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '3', '3', 'Rozdzielił się tedy lud w mieście, a jedni przestawali z Żydy, drudzy z Apostoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '4', '4', 'A gdy się zstało gwałtowne naście poganów i Żydów społu z książęty ich, a czynili im krzywdę i miotali na nie kamieńmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '5', '5', 'Tedy tę rzecz obaczywszy, uciekli do miast Likaońskich, do Listry i do Derben i do okolicznej krainy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '6', '6', 'I tamże opowiedali Ewanjeliją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '7', '7', 'Tedy niektóry człowiek w Listrze niemocny na nogi siedział, będąc chrom jeszcze z żywota matki swojej, który nigdy nie chodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '8', '8', 'Ten słuchał, gdy Paweł mówił, a gdy on nań pilnie patrzał i ujźrzawszy, że miał wiarę, aby był uzdrowion,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '9', '9', 'Rzekł głosem wielkim: Wstań prosto na twoje nogi! Tedy on wyskoczywszy chodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '10', '10', 'A gdy lud ujźrzał to, co uczynił Paweł, podnieśli głos swój mówiąc po likaońsku: Bogowie podobni ludziam przyszli do nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '11', '11', 'I nazywali Barnabasa Jowiszem, a Pawła Merkurjuszem, iż on sam mawiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '12', '12', 'Tedy kapłan Jowiszów, który był przed miastem ich, cielce koronowane do przysionka przywiódł, gdy lud chciał ofiary sprawować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '13', '13', 'Co gdy usłyszeli Apostołowie Barnabas i Paweł, rozdarszy płaszcze swoje, wskoczyli między lud wołając');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '14', '14', 'I mówiąc: Mężowie przecz to czynicie? I myć też tymże co i wy niedostatkom jestechmy poddani ludzie, oznajmując wam byście się od tych próżnych bogów obrócili do onego Boga żywego, który stworzył niebo i ziemię i morze i wszytko, co w nich jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '15', '15', 'Który za lat przeszłych dopuszczał wszytkim poganom, iż sami swojemi drogami chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '16', '16', 'Aczkolwiek nie zaniechał, aby o nim świadectwa wiedzieć nie miano, gdy nam dobrze czynił, dawając nam z nieba dżdże i czasy urodzajne, napełniając pokarmem i radością serca nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '17', '17', 'A to mówiąc, zaledwie lud zawściągnęli, że im nie ofiarowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '18', '18', 'Zatym przyszli niektórzy z Antiochijej i z Ikonium Żydowie, którzy odwiódszy lud, Pawła ukamionowanego wywlekli precz z miasta, mnimając żeby już umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '19', '19', 'Ale gdy go zwolennicy obstąpili, wstał i wszedł do miasta, a nazajutrz odszedł do Derben z Barnabasem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '20', '20', 'I gdy opowiedali Ewanjeliją miastu onemu, a uczyli ich wiele, wrócili się do Listry i do Ikonium i do Antiochijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '21', '21', 'A tak utwierdzając umysły zwolenników i napominając je, aby trwali w wierze mówili: Żeć nam potrzeba przez wielkie uciski wnidź do Królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '22', '22', 'I gdy im za społecznym zezwoleniem obrali w każdym kościele starsze i modlili się z posty, zalecili je Panu, w którego uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '23', '23', 'A przeszedszy Pisydia, przyszli do Pamfilijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '24', '24', 'Opowiedziawszy też słowo Boże w Pergen, szli do Attalijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '25', '25', 'I ztamtąd wieźli się do Antiochijej, skąd byli zaleceni łasce Bożej ku tej sprawie, której dosyć czynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '26', '26', 'A tak przyszedszy zebrali kościół i oznajmili to, cokolwiek Bóg uczynił przez nie, a iż poganom otworzył drzwi wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '13', '27', '27', 'I strawili tam czas niemały z zwolenniki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '0', '0', 'Potym niektórzy co byli z Żydostwa przyszli, uczyli bracią mówiąc: Jeśliż się nie obrzeżecie wedle zwyczaju Mojżeszowego, nie możecie być zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '1', '1', 'A przetoż się wszczęła niesnaska i poswarek niemały Pawłowi z Barnabasem przeciwko nim i postanowili, aby szedł Paweł i Barnabas i niektórzy z nich do Apostołów i do starszych w Jeruzalem, pytając się o tej gadce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '2', '2', 'Tedy oni będąc posłani od kościoła, przeszli przez Fenicen i Samarja, oznajmując nawrócenie poganów i przynosili wielką radość wszytkiej braciej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '3', '3', 'A gdy przyszli do Jeruzalem, przyjęci są od kościoła i od Apostołów i od starszych, a oznajmili cokolwiek Bóg przez nie czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '4', '4', 'Ale niektórzy powstawszy z sekty Faryzeuszów, którzy byli uwierzyli powiedali: Iż się im potrzeba obrzezować, a roszkazać, aby był zachowywan zakon Mojżeszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '5', '5', 'Zebrali się tedy Apostołowie i starszy, aby wybaczyli tę sprawę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '6', '6', 'A gdy był wielki poswarek, wstawszy Piotr rzekł do nich: Mężowie bracia. Wy wiecie że już Bóg dawno mię obrał między wami, aby przez usta moje pogani słuchali słowa Ewanjelijej i wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '7', '7', 'Bóg tedy, który zna serca, dał im świadectwo, dawszy im i Ducha świętego jako i nam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '8', '8', 'I nie uczynił żadnej różnice między nami i między nimi, wiarą oczyściwszy serca ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '9', '9', 'A tak teraz przeczże kusicie Boga, kładąc jarzmo na szyje zwolenników, którego ani ojcowie naszy ani my znosiciechmy nie mogli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '10', '10', 'Ale prze łaskę Pana Jezusa Krystusa wierzymy, iż będziem zbawieni, tak jako i oni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '11', '11', 'Tedy umilknęła wszytka wielkość, a słuchali Barnabasza i Pawła, którzy opowiedali, co za znamiona i cuda czynił Bóg między pogany przez nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '12', '12', 'Zatym gdy umilknęli, odpowiedziawszy Jakub rzekł: Mężowie bracia, słuchajcie mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '13', '13', 'Symeon naprzód opowiedział, jako Bóg wejźrzał na pogany, aby z nich wziął lud imieniowi swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '14', '14', 'A z tąć się rzeczą zgadzają słowa prorockie, jako jest napisano:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '15', '15', 'Potym się wrócę, a pobuduję przybytek Dawidów obleciały i pobuduję upadki jego i zasię ji wystawię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '16', '16', 'Aby ci ludzie, którzy pozostali szukali Pana i wszyscy narodowie, nad któremi wzywano imienia mojego, mówi Pan, który czyni to wszytko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '17', '17', 'Jawneć są od wszytkiego wieku u Boga wszytki sprawy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '18', '18', 'A dlategoż ja tak radzę nie przenagabać tych, którzy się z poganów nawracają ku Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '19', '19', 'Ale pisać do nich, aby się wstrzymawali od splugawienia bałwanów, od grzechu nieczystego, od rzeczy dawionych i od krwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '20', '20', 'Abowiemci Mojżesz od starodawnych czasów ma w każdym mieście ty, którzy go opowiedają, gdyż go w bożnicach na każdy szabat czytają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '21', '21', 'Tedy się zdało Apostołom i starszym, ze wszytkim kościołem, przebrane męże z pośród siebie posłać do Antjochijej z Pawłem i Barnabaszem, Judę, którego zwano Barsabas i Sylę, męże przedniejsze między bracią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '22', '22', 'A to pisanie posłać przez ręce ich. Apostołowie, starszy i bracia, tym którzy są w Antjochijej, w Syrijej i w Cylicijej. Braciej, którzy są z poganów, zdrowia życzymy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '23', '23', 'Gdyżechmy słyszeli, iż niektórzy z nas wyszedszy zatrwożyli was słowy, posłabiając dusze wasze, a każąc was obrzezować i zachowywać zakon Mojżeszów, którymechmy my tego nie roskazowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '24', '24', 'Zdało się nam tedy wszytkim jednostajnie zebranym, posłać do was męże przebrane z namilejszymi naszymi, Barnabaszem i z Pawłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '25', '25', 'Z ludźmi tymi, którzy wydali zdrowia swe dla imienia Pana naszego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '26', '26', 'A przetożechmy posłali Judasa i Sylę, którzy wam i sami słowy to oznajmią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '27', '27', 'Abowiem tak się zdało Duchowi świętemu i nam, abyśmy więcej nie kładli na was brzemion, jedno ty potrzebne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '28', '28', 'Abyście się wstrzymawali od tych rzeczy, które są ofiarowane bałwanom, od krwie, od rzeczy dawionych i od grzechu nieczystego. Od których rzeczy jeśli się sami zachowywać będziecie, dobrze uczynicie. Zatym się dobrze miejcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '29', '29', 'A tak oni odprawieni, przyszli do Antjochijej, a zebrawszy lud oddali list.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '30', '30', 'Który gdy przeczciono, uradowali się z pocieszenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '31', '31', 'Tedy Judas i Sylas, gdyż i sami byli proroki, długiemi słowy napominali bracią i utwirdzali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '32', '32', 'I gdy tam kilka czasów zmieszkali, odesłani są z pokojem od braciej do Apostołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '33', '33', 'Ale Syli zdało się tam zostać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '34', '34', 'Paweł też i Barnabas mieszkali w Antjochijej, nauczając i każąc z wielem inych słowa Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '35', '35', 'Potym po kilku dni rzekł do Barnabasza Paweł:Wr&nbsp;óćmyż się, a nawiedźmy bracią naszę po wszytkich mieściech, w którycheśmy opowiedali słowo Pańskie i dowiemy się, jako się też mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '36', '36', 'I radził Barnabas, aby z sobą wzięli Jana, którego zwano Marek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '37', '37', 'Ale się Pawłowi nie zdało za rzecz słuszną brać onego, który odstał od nich z Pamfiljej, a nie był w onej sprawie towarzyszem ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '38', '38', 'Także przeto są obruszeni, iż odszedł jeden od drugiego, a wziąwszy z sobą Barnabas Marka wiózł się do Cypru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '39', '39', 'Paweł zasię przyjąwszy do siebie Sylę szedł precz, będąc poruczon łasce Bożej od braciej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '14', '40', '40', 'I chodził po Syrijej i Cylicijej, utwierdzając kościoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '0', '0', 'Przyszedł potym do Derben i do Listry, a oto był tam zwolennik niektóry imieniem Tymoteusz, syn niektórej niewiasty Żydówki wiernej, wszakoż z ojca Greka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '1', '1', 'Ten za sobą miał świadectwo braciej, którzy byli w Listrze i w Ikonjum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '2', '2', 'Tegoż Paweł chciał mieć przy sobie, aby z nim szedł i wziąwszy go obrzezał dla Żydów, którzy tam na onych miejscach byli, bo wszyscy wiedzieli, że ociec jego był Grek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '3', '3', 'A tak chodząc po mieściech, podawali im ku chowaniu ustawy, które były postanowione od Apostołów i starszych, którzy byli w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '4', '4', 'Utwierdzały się tedy kościoły w wierze, a ustawicznie pocztu ich obficie przybywało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '5', '5', 'Tedy przeszedszy Frygja i krainę Galatcką, pohamowani są od Ducha świętego, aby nie opowiedali słowa Bożego w Azijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '6', '6', 'Zatym, gdy przyszli do Mysijej, kusili się wnidź do Bitynijej, ale im Duch nie dopuścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '7', '7', 'Tedy przeszedszy Mysija, wstąpili do Troady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '8', '8', 'I widział Paweł w nocy widzenie, ano mąż niektóry Macedoński przyszedszy stanął i prosił go mówiąc: Przepraw się do Macedonijej, a ratuj nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '9', '9', 'I ujźrzawszy to widzenie, natychmiast staralichmy się jakoby idź do Macedonijej, mając za to, iż nas Pan powoływał ku opowiedaniu im Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '10', '10', 'A tak wyszedszy z Troady, trzymalichmy się prostej drogi do Samotracijej, a nazajutrz do Neapolim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '11', '11', 'Ztamtąd do Filippos, które jest przedniejsze miasto w stronie Macedońskiej, cudzoziemcy osadzone, tamżeśmy w nim kilka dni zmieszkali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '12', '12', 'A w dzień szabatu wyszlichmy z miasta podle rzeki, gdzie zwykły bywać modlitwy, a tam gdyśmy siedli, rozmawialichmy z niewiastami, które się były zeszły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '13', '13', 'Tedy niektóra niewiasta imieniem Lidja, która szkarłat przedawała w mieście Tyatyrczyków, chwaląc Boga, słuchała nas, której Pan otworzył serce, iż pilnie tego słuchała, co powiedał Paweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '14', '14', 'A gdy się okrzciła i dom jej, prosiła mówiąc: Jeśliż mię rozumiecie być wierną Panu, wnidźciesz do domu mego a mieszkajcie i także nas przymusiła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '15', '15', 'I przydało się gdychmy szli na modlitwę, iż dzieweczka niektóra, co miała ducha wieszczego, zabieżała nam, a ta wielki zysk panom swoim czyniła wróżąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '16', '16', 'Ta, chodząc za Pawłem i za nami, wołała mówiąc: Ci ludzie sąć słudzy onego nawyższego Boga, którzy nam oznajmują drogę zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '17', '17', 'A toć czyniła przez wiele dni, ale Paweł mając za złe, obrócił się i rzekł duchowi: Roskazujęć przez imię Jezusa Krystusa, abyś od niej wyszedł. A przetoż wnet tegoż czasu wyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '18', '18', 'To widząc panowie jej, iż minęła nadzieja zysku ich, pojmawszy Pawła i Sylę, wlekli je na rynek do urzędu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '19', '19', 'A postawiwszy je przed przełożonymi mówili: Ludzie ci zaburzyli miasto nasze, gdyż są Żydowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '20', '20', 'I opowiedają nam nowe zwyczaje, których się nam przyjmować nie godzi ani przywłaszczać, gdyżechmy są Rzymianie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '21', '21', 'Tedy społu pospólstwo powstało przeciw im, a przełożeni rozdarszy szaty ich, kazali je chlustać rózgami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '22', '22', 'A gdy im wiele ran zadali, wrzucili je do więzienia, roskazując stróżowi ciemnice, aby ich pilnie strzegł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '23', '23', 'Który wziąwszy takowe roskazanie, wsadził je do wnętrznego tarasu, a nogi ich zamknął w pęta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '24', '24', 'Ale o północy modlili się Paweł i Sylas, a śpiewali piosnki i słuchali ich ci, którzy też w więzieniu byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '25', '25', 'Zatym natychmiast stało się wielkie trzęsienie ziemie tak, iż się potłukły fundamenty tarasu i wnet się otworzyły wszytki drzwi, a rozwiązały się pęta na wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '26', '26', 'Ocucił się potym stróż ciemnice, a gdy ujźrzał drzwi otworzone u tarasu, dobywszy miecza chciał się sam zabić, mnimając iż więźniowie pouciekali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '27', '27', 'Tedy Paweł zawołał głosem wielkim mówiąc: Nie czyń sobie nic złego, bociechmy hajw wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '28', '28', 'Wziąwszy on tedy światło, werwał się i przypadł drżąc do nóg Pawłowi i Syli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '29', '29', 'I wywiódszy je precz rzekł: Panowie, cóż mam czynić abych był zbawion?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '30', '30', 'A oni mu rzekli: Wierz w Pana Jezusa Krystusa, a będziesz zbawion z domem twoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '31', '31', 'I opowiedali mu słowo Pańskie i wszytkim, którzy byli w domu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '32', '32', 'Tedy je on wziąwszy w nocy onejże godziny, omył rany ich, a okrzcił się natychmiast i wszyscy domownicy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '33', '33', 'A gdy je przywiódł do domu swego, nagotował im stół i radował się, że ze wszytkim domem swym uwierzył Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '34', '34', 'Potym gdy dzień nastawał, posłali przełożeni oprawce, którzyby powiedzieli, aby wypuścił ludzi ony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '35', '35', 'Ty słowa opowiedział stróż ciemnice Pawłowi, iż przełożeni posłali, aby je puszczono, a przetoż teraz wyszedszy idźcie w pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '36', '36', 'Ale im rzekł Paweł: Obiwszy nas jawnie bez winności, gdyżeśmy Rzymianie, a podawszy nas do więzienia, teraz nas potajemnie wyrzucają, iścieć nic z tego, ale niech sami przyjdą a wywiodą nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '37', '37', 'Oznajmili tedy przełożonym oprawce ty słowa i zlękli się usłyszawszy, że Rzymianie byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '38', '38', 'A przyszedszy przepraszali je i wywiódszy prosili ich, aby wyszli z miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '15', '39', '39', 'A tak wyszedszy z więzienia, weszli do Lidijej i ujźrzawszy bracią, cieszyli je i szli precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '0', '0', 'Potym gdy szli w drogę przez Amfipolim i Apollonja, przyszli do Tesaloniki, gdzie była bożnica Żydowska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '1', '1', 'Tedy Paweł wedle zwyczaju swego wszedł do nich, a przez trzy Szabaty rozmawiał się z nimi z pism.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '2', '2', 'Okazując i przekładając, że była potrzeba Krystusowi cirpieć i wstać z martwych, a iżci ten jest Jezus Krystus, którego ja wam opowiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '3', '3', 'I uwierzyli niektórzy z nich, a stowarzyszyli się z Pawłem i Sylą, ktemu wiele bogobojnych Greków i niewiast przedniejszych niemało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '4', '4', 'Ale Żydowie, którzy słuchać nie chcieli, ruszeni zazdrością, nabrawszy niektórych łotrasów ludzi złych, a zebrawszy lud wzburzyli miasto, a naszedszy na dom Jazonów, szukali ich, aby je wywiedli do ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '5', '5', 'A gdy ich nie znaleźli, wywlekli Jazona i niektórą bracią do przełożonych miasta wołając: Cić są, którzy stan wszytkiego świata podwracali i tuć oto przyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '6', '6', 'Które przyjął Jazon, a wszyscyć ci czynią przeciw dekretom cesarskim, opowiedając Jezusa Króla inego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '7', '7', 'I podburzyli lud i przełożone miasta, którzy tego słuchali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '8', '8', 'Wszakoż oni przyjąwszy od Jazona rękojemstwo i od inych, puścili je na postawienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '9', '9', 'Ale bracia wnet w nocy wypuścili Pawła społu z Sylą do Berea, którzy gdy tam przyszli, weszli do bożnice Żydowskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '10', '10', 'Lecz oni byli stateczniejszy niż ci, co byli w Tesalonice a to, iż przyjęli słowo Boże ze wszytką ochotą, ustawicznie się wywiadując pism, jeśliby się to tak miało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '11', '11', 'A przetoż wiele ich uwierzyło i greckie niewiasty uczciwe i mężów niemało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '12', '12', 'O tym gdy się dowiedzieli tesaloniceńscy Żydowie, że i w Berei opowiedane jest od Pawła słowo Boże, przyszli też i tam podburzając lud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '13', '13', 'Ale wnet Pawła wypuścili bracia, aby szedł jakoby do morza, wszakoż Syla i Tymoteusz zostali tam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '14', '14', 'A ci, którzy się podjęli postawić Pawła na miejscu bespiecznym, przeprowadzili go aż do Aten, a wziąwszy roskazanie do Syle i Tymoteusza, żeby tymrychlej bywali u niego, szli w drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '15', '15', 'Zatym gdy ich Paweł w Atenjech czekał, tedy się w nim wzruszał duch jego, gdy widział miasto poddane bałwochwalstwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '16', '16', 'A przetoż rozmawiał w bożnicy z Żydy i z ludźmi bogobojnemi, także i w rynku na każdy dzień, z kim się jeno podkał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '17', '17', 'Tedy niektórzy Epikurejczycy i Stoicy filozofowie gadali się z nim, a mówili niektórzy: Cóż wżdy ten plotka chce powiedać? A drudzy mówili: Zda się być opowiedaczem obcych bogów, bo im Jezusa i zmartwychwstanie opowiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '18', '18', 'A tak go pochwyciwszy wiedli ku Areopagu, mówiąc: Możemyli wiedzieć, co to jest za nowa nauka, którą opowiedasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '19', '19', 'Bo jakieś obce rzeczy przynosisz do uszu naszych, a przetoż chcemy wiedzieć, co wżdy z tego ma być.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '20', '20', 'Abowiem Atenieńczycy i ci, którzy tam mieszkali goście, żadną się iną rzeczą nie bawili, jedno w powiedaniu a w słuchaniu jakich nowin.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '21', '21', 'Tedy Paweł stojąc wpośród Areopagu rzekł: Mężowie Atenieńscy, we wszytkim widzę, iż barzo nabożnemi jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '22', '22', 'Gdyż chodząc i przypatrując się waszym nabożeństwam, nalazłem też ołtarz, na którym jest napisano: NIEZNAJOMEMU BOGU. A przetoż tego, którego wy nie znając chwalicie, tegożci ja wam oznajmuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '23', '23', 'Bóg, któryć stworzył świat i wszytko, co na nim jest, ten gdyż jest Panem nieba i ziemie, nie mieszkać w kościelech ręką zbudowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '24', '24', 'Ani bywa chwalon rękoma ludzkiemi, jakoby czego potrzebować miał, gdyż on daje wszytkim żywot i oddech i wszytko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '25', '25', 'I sprawił z jednej krwie wszytek naród ludzki, aby mieszkali po wszytkiej ziemi, zamierzywszy pewność czasów, które przed tym postanowił i założywszy granice mieszkania ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '26', '26', 'Aby szukali Boga, owaby snadź macając znaleźli go, chociażci koniecznie do każdego z nas nie jest daleko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '27', '27', 'Abowiem przezeń żywiemy i ruchamy sobą i jestechmy, jako i niektórzy z waszych poetów to powiedali, gdyżechmy też są narodem jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '28', '28', 'A przetoż gdychmy są narodem Bożym, nie mamy tak rozumieć, aby złotu, abo srebru, abo kamieniowi misternie rytemu, abo zmyśleniu człowieczemu bóstwo miało być podobne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '29', '29', 'Lecz przebaczając czasów tej nieumiejętności, teraz Bóg wszędy oznajmuje wszem ludziam, aby się uznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '30', '30', 'Dlatego, iż postanowił dzień, w który sprawiedliwie sądzić będzie ziemię, przez tego męża, którego naznaczył, jawnie to okazawszy wszytkim, iż on go z martwych wzbudził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '31', '31', 'Tedy oni usłyszawszy o zmartwych wstaniu, niektórzy z nich z pośmiechem to przyjmowali, a drudzy zasię powiedzieli: Będziem cię o tym drugi raz słuchać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '32', '32', 'Zatymże Paweł wyszedł z pośródku ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '16', '33', '33', 'Ale niektórzy przystali do niego i uwierzyli, między któremi był Djonizus Areopagita i niewiasta imieniem Damarys i ini z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '0', '0', 'Potym Paweł odszedszy z Aten, przyszedł do Koryntu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '1', '1', 'I nalazszy niektórego Żyda imieniem Atyllę rodem z Pontu, który był niedawno z Włoch przyszedł z Pryscyllą żoną swą (dlatego, iż było roskazanie Klaudjuszowe, aby Żydowie wszyscy szli precz z Rzyma) przyszedł do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '2', '2', 'A iż był tegoż rzemiesła, mieszkał u nich i robił, abowiem to było rzemiesło ich robić namioty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '3', '3', 'Tedy się gadał w bożnicy na każdy szabat, namawiając Żydy społu i Greki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '4', '4', 'A gdy przyszli z Macedonijej Sylas i Tymoteusz, ściśnion był Paweł od ducha, oświadczając Żydom, iż Jezus był Krystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '5', '5', 'Lecz oni, gdy się zastawiali i bluźnili, wstrzązszy szaty swe, rzekł do nich: Krew wasza na głowy wasze, jaciem czyst, a od tegoż czasu idę do poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '6', '6', 'I odszedszy ztamtąd, wszedł w dom niektórego człowieka imieniem Just, który służył Bogu, a jego dom był podle bożnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '7', '7', 'Tedy Kryspus przedniejszy w bożnicy uwierzył Panu ze wszytkim swym domem i wiele Koryntczyków słuchając wierzyli i krzcili się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '8', '8', 'Zatym Pan rzekł Pawłowi w nocy przez widzenie: Nie bój się, ale mów i nie milcz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '9', '9', 'Abowiemcijem ja jest z tobą, a żaden się na cię nie targnie, abyć miał co złego uczynić, abowiem tu wielki lud mam w mieście tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '10', '10', 'A tak tam mieszkał rok i sześć miesięcy, nauczając u nich słowa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '11', '11', 'A gdy Gallijo był starostą w Achaijej, tedy Żydowie jednostajnie powstali przeciw Pawłowi i przywiedli go przed sąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '12', '12', 'Mówiąc: Iż ten przeciw zakonowi namawia ludzi chwalić Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '13', '13', 'A gdy już Paweł miał usta otworzyć rzekł Gallijo do Żydów: Być wam była krzywda jaka, abo co za złoczyństwo o Żydowie, ileby sprawiedliwości należało, broniłbych was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '14', '14', 'Ale jeśli tylko gadka jest o słowiech i o imionach, a o zakonie waszym, wy to sami rozeznajcie, abowiem ja nie chcę być tej rzeczy sędzią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '15', '15', 'I wygnał je od sądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '16', '16', 'Tedy wszyscy Grecy Sostena, przedniejszego z bożnice, pochwyciwszy bili przed sądem, ale tego Gallijo nic na pieczy nie miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '17', '17', 'Potym Paweł, gdy tam jeszcze przez niemały czas mieszkał, pożegnawszy się z bracia, wiózł się do Syrijej (mając z sobą w towarzystwie Pryscyllę i Atyllę), gdy ogolił głowę w Kenchreach, bo miał ślub.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '18', '18', 'Zatym przyszedł do Efezu i tam je zostawił, a sam wszedszy do bożnice rozmawiał z Żydy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '19', '19', 'A gdy go prosili, żeby u nich dłużej mieszkał, nie zezwolił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '20', '20', 'Ale je pożegnał mówiąc: Koniecznie potrzeba mi obchodzić święto, które blisko jest w Jeruzalem, lecz się zasię do was wrócę za wolą Bożą i odszedł z Efezu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '21', '21', 'A zstąpiwszy do Cezarjej i przyszedszy do Jeruzalem, tam przywitawszy się z kościołem, szedł do Antjochijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '22', '22', 'I gdy tam przemieszkał niektóry czas, odszedł, idąc porządnie przez krainę Galatską i przez Frygją, a utwirdzał wszytki zwolenniki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '23', '23', 'Tedy Żyd niektóry imieniem Apollos rodem z Aleksandrijej, człowiek wymowny, przyszedł do Efezu, będąc mocnym w piśmiech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '24', '24', 'Ten był wprawiony w drodze Pańskiej, a pałając w duchu mówił i nauczał pilnie tych rzeczy, które były Pańskie, tylko wiedząc o krzcie Janowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '25', '25', 'I począł bespiecznie mówić w bożnicy, którego usłyszawszy Pryscylla i Atylla, wzięli go i dostateczniej mu oznajmili drogę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '26', '26', 'A gdy chciał idź do Achajej, napominali go bracia i pisali do zwolenników, aby go przyjęli, który gdy przyszedł, wiele pomagał tym, którzy uwierzyli z łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '17', '27', '27', 'Abowiem dostatecznemi wywody tym więcej a więcej sztrofował jawnie Żydy, okazując przez pisma, iż Jezus był onym Krystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '0', '0', 'Zstało się potym, gdy Apollos był w Koryntcie, iż Paweł obszedszy górne krainy przyszedł do Efezu, gdzie tam znalazł kilka zwolenników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '1', '1', 'I rzekł do nich: Zaliście wzięli Ducha świętego, gdyście uwierzyli? Ale mu oni powiedzieli: I owszemechmy o tym ani słychali, jeśliż jest Duch święty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '2', '2', 'Tedy rzekł: W cóżeście tedy są okrzczeni? A oni powiedzieli: W Janów krzest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '3', '3', 'Zatym rzekł Paweł: Janci tylko krzcił krztem uznania, mówiąc ludowi, aby w onego, który miał przyjdź po nim uwierzyli, to jest w Krystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '4', '4', 'A przetoż oni usłyszawszy to, pokrzczeni są w imię Pana Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '5', '5', 'I gdy na nie wkładał ręce Paweł, przyszedł na nie Duch święty, a mówili obcemi języki i prorokowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '6', '6', 'A tych wszytkich mężów było około dwunaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '7', '7', 'Potym on wszedszy do bożnice, mówił bespiecznie przez trzy miesiące, rozmawiając i radząc o tym, coby ku królestwu Bożemu przysłuszało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '8', '8', 'A gdy niektórzy byli zatwardzeni i nie chcieli być posłuszni, mówiąc źle o drodze Pańskiej przed ludem, odszedszy od nich, wyłączył zwolenniki na każdy dzień ucząc w szkole niektórego króla.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '9', '9', 'A toć się działo przez dwie lecie tak, iż wszyscy którzy mieszkali w Azijej, słuchali słowa Pana Jezusowego, tak Żydowie jako i Grecy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '10', '10', 'I nie leda cuda sprawował Bóg przez ręce Pawłowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '11', '11', 'Tak, iż też na ludzi chore przynoszono facelety abo ręczniki od ciała jego, a odchodziły od nich choroby i duchowie źli odstępowali od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '12', '12', 'Tedy niektórzy tułacy Żydowie bawiący się zaklinaniem, kusili się wzywać nad tymi, którzy mieli duchy złe, imienia Pana Jezusowego mówiąc: Poprzysięgamy was przez Jezusa, którego Paweł opowieda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '13', '13', 'A było synów Scewy Żyda książęcia kapłańskiego siedm, którzy to czynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '14', '14', 'Ale odpowiedziawszy duch zły rzekł: Znamci Jezusa i wiem co Paweł, ale wy coście są zacz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '15', '15', 'I skoczywszy na nie człowiek on, w którym był duch zły, a opanowawszy je zmocnił się im tak, iż nago zranieni zuciekali z domu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '16', '16', 'A toć się rozsławiło u wszytkich tak Żydów, jako i Greków, którzy mieszkali w Efezie i przyszedł na nie wszytki strach, a wielbili imię Pana Jezusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '17', '17', 'A wiele onych, którzy uwierzyli, przychodziło wyznawając i oznajmując sprawy swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '18', '18', 'Także i wiele z onych, którzy się naukami niepotrzebnemi parali, przyniózszy księgi popalili przed wszytkimi, które oszacowawszy, znaleźli pięćdziesiąt tysięcy srebrników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '19', '19', 'A takci się mocnie rozrastało słowo Pańskie i brało moc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '20', '20', 'To gdy się spełniło, umyślił Paweł przez Ducha, przeszedszy Macedonją i Achają idź do Jeruzalem i mówił tak: Bywszy tam, potrzeba mi też i Rzym oglądać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '21', '21', 'Tedy posławszy dwu z onych, którzy mu służyli, Tymoteusza i Erasta, sam do czasu został w Azijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '22', '22', 'Tegoż czasu wszczął się rozruch niemały o onej sekcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '23', '23', 'Abowiem niektóry imieniem Demetrjus złotnik, który robił kościoły srebrne Djanie, czynił niemały pożytek rzemieślnikom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '24', '24', 'Które zebrawszy i ine, którzy byli takowychże rzeczy rzemieśnicy rzekli: Mężowie ! Wieciesz, iż z tego rzemiesła są nasze majętności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '25', '25', 'Widzicie tedy i słyszycie, że nie tylko w Efezie, ale prawie po wszytkiej Azijej ten Paweł namówił a odwrócił wiele ludu, mówiąc że nie są bogowie ci, którzy są rękami uczynieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '26', '26', 'A tak potrzeba się tego obawiać, by nam przezeń ta cząstka naganiona nie zginęła i owszem też, aby tej Boginijej Djany kościół nie był nizacz mian i przyszłoby ktemu, żeby też był zepsowan majestat jej, którą chwali wszytka Azja i wszytek świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '27', '27', 'Ty rzeczy oni usłyszawszy, napełnieni gniewu krzyknęli mówiąc: Wielkać jest Djana Efeska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '28', '28', 'I było pełne wszystko miasto zamieszania i wtargnęli jednostajnie na rynek, pojmawszy Gajego i Arystarcha Macedończyki, którzy byli towarzyszmi w drogach Pawłowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '29', '29', 'A gdy Paweł chciał wynidź do ludu, nie dopuścili mu zwolennicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '30', '30', 'Ale i niektórzy przedniejszy z Azjej, gdy mu byli przyjacielmi, posławszy do niego prosili go, aby nie wychodził na rynek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '31', '31', 'Tedy jedni inaczej wołali, abowiem było zebranie zamieszane, a wiele ich było, którzy nie wiedzieli dlaczego się zeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '32', '32', 'A niektórzy z ludu przywiedli Aleksandra, którego pędzili Żydowie. A przetoż Aleksander, gdy ręką milczenie uczynił, chciał uczynić obmowę przed ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '33', '33', 'Którego gdy poznali być Żydem, tedy się wszczął krzyk wszech społu, jakoby przez dwie godzinie, wołających: Wielka jest Djana Efeska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '34', '34', 'Tedy pisarz uśmierzywszy lud rzekł: Mężowie Efescy, któryż jest człowiek coby nie wiedział, iż miasto Efeskie jest chwalcą wielkiej Boginijej Djany i obrazu, który spadł od Jowisza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '35', '35', 'A przetoż, gdyż się tym nikt sprzeciwić nie może, potrzeba się wam pohamować, a nic skwapliwie nie czynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '36', '36', 'Abowiemeście przywiedli ludzi ty, ani świętokradce, ani bluźnierze przeciw Boginijej waszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '37', '37', 'A jeśliż Demetrjus i ci, którzy z nim są rzemieślnicy mają co za przyczynę przeciw komu, wszak prawo siedzi i są rajce. Tamże niech na się skarżą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '38', '38', 'Jeśliż się też o jakich inych rzeczach badacie, to się na słusznym ześciu odprawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '18', '39', '39', 'Abowiem się nam obawiać potrzeba, aby nas nie obwiniono o niesnaskę dnia dzisiejszego, gdyż nie masz żadnej przyczyny, jakobychmy mogli dać o sobie sprawę tego naszego ześcia nieporządnego. A gdy to rzekł, rospuścił ono zgromadzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '0', '0', 'A gdy się uciszył on rozruch, tedy Paweł wezwał zwolenników, a pożegnawszy je, szedł w drogę do Macedonijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '1', '1', 'Tedy chodząc po onych stronach, napominał je długiemi powieściami, zatym przyszedł do Grecijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '2', '2', 'A tam zmieszkawszy trzy miesiące, gdy nań Żydowie zasadki chytre uczynili tam, kędy miał idź do Syrijej i zdało się mu na zad idź przez Macedonja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '3', '3', 'I byli z nim w towarzystwie aż do Azjej: Sopater Berreński i Arystarchus z Sekundusem Tesaloniceńscy i Gajus Derbejczyk i Tymoteusz, a zasię z Azjej Tychikus i Trofimus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '4', '4', 'Którzy jechawszy wprzód, czekali nas w Troadzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '5', '5', 'My zasię po dniach Przaśników, odpłynęlichmy od Filipis i przyszlichmy do Troady za pięć dni, gdzieśmy zmieszkali przez siedm dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '6', '6', 'A tak pirwszego dnia w tegodniu, zebrawszy zwolenniki ku łamaniu chleba, mówił do nich, iż miał jechać nazajutrz i przedłużył kazanie aż do północy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '7', '7', 'A było wiele lamp na onej sali, gdzie byli zgromadzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '8', '8', 'Tam siedząc niektóry młodzieniec imieniem Eutychus w oknie, będąc zjęt cięszkim snem, gdy Paweł dłużej mówił, zachwiawszy się od snu, spadł na dół z trzeciego piętra i wzięt jest umarły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '9', '9', 'Tedy zstąpiwszy Paweł, położył się na nim, a obłapiając go mówił: Nie frasujcie się, boć w nim jest dusza jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '10', '10', 'A tak, gdy zasię wszedł, a łamał chleb i jadł długo mówił, aż ku świtaniu, a zatymże precz szedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '11', '11', 'Przywiedli też i młodzieńca żywego i byli niepomału pocieszeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '12', '12', 'A my też szedszy do okrętu, wieźlichmy się do Assu, abychmy ztamtąd wzięli Pawła. Abowiem był tak postanowił, iż tam miał pieszo idź w drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '13', '13', 'A gdy się do nas przyłączył w Assu, wziąwszy go przyjechalichmy do Mityleny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '14', '14', 'Ztamtąd się zaś odwiózszy, przyszlichmy nazajutrz ku Chiu, a drugiego dnia przypłynęlichmy ku Samu. A pomiwszkawszy w Trogillu, nazajutrz przyszlichmy do Miletu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '15', '15', 'Abowiem Paweł postanowił, iż miał minąć Efez, aby czasu próżno nie tracił w Azjej, bo się kwapił jeśliby mogło być, aby na dzień świąteczny był w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '16', '16', 'Tedy od Miletu posławszy do Efezu, wezwał starszych z kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '17', '17', 'Którzy, gdy do niego przyszli, powiedział im: Wy wiecie, iż od pierwszego dnia, w którym wszedł do Azjej, jakom z wami po wszytek czas był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '18', '18', 'Służąc Panu ze wszytkąskromno&nbsp;ścią, z wiela łez i z pokusami, które na mię przychadzały z zasadek żydowskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '19', '19', 'Żem się nie skradał z niczym coby wam było pożyteczno, żebych wam był tego oznajmić nie miał, ucząc was jawnie po wszytkich domiech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '20', '20', 'A oświadczając Żydom społu z Greki ono uznanie, które jest ku Bogu i wiarę ku Panu naszemu Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '21', '21', 'Ale teraz oto ja związany w duchu, jadę do Jeruzalem, nie wiedząc co się tam ze mną dziać będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '22', '22', 'Jedno żeć Duch święty po wszytkich mieściech oświadcza, że mię więzienie i utrapienie czeka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '23', '23', 'Wszakoż ja na to żadnego baczenia nie mam, ani mi jest mił żywot samemu, jedno abych dokończył biegu mojego z weselem i posługi, któram wziął od Pana Jezusa, ku oświadczaniu Ewanjelijej łaski Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '24', '24', 'A terazci ja oto znam, że żaden z was wszech nie oglądacie oblicza mojego, przez którem ja przeszedł opowiedając królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '25', '25', 'I dlategoż się wam oświadczam dnia dzisiejszego, żeciem ja jest czyst od krwie wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '26', '26', 'Abowiemem się nie chronił, żebych wam nie miał oznajmiać wszelkiej rady Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '27', '27', 'A przetoż miejcie się sami na pilnej pieczy i trzodę, nad którą was Duch święty postanowił biskupy, abyście paśli kościół Boży, którego on nabył sobie oną krwią swoją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '28', '28', 'Boć ja wiem, że po odeściu moim wnidą między was srodzy wilcy, którzy nie sfolgują trzodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '29', '29', 'I z was samych wynidą mężowie mówiący rzeczy przewrotne, aby za sobą zwolenniki odtargnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '30', '30', 'A dlategoż czujcie, pomniąc żeciem przez trzy lata w nocy i we dnie nie przestawał napominać z was każdego z łzami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '31', '31', 'A tak teraz bracia poruczam was Bogu i słowu łaski jego, któryć Bóg może pobudować i dać wam to, co prawem dziedzicznym otrzymacie ze wszytkimi poświęconemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '32', '32', 'Nie pożądałem od żadnego z was srebra, złota ani szaty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '33', '33', 'I owszem sami wiecie, że moim potrzebam i tych, którzy są ze mną, służyły ręce oto ty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '34', '34', 'We wszytkimciem wam okazał, iż tak pracując potrzeba przyjmować niemocne, a pamiętać słowa Pana Jezusowe, iż on powiedział: Szczęśliwsza rzecz jest dawać niźli brać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '35', '35', 'A to rzekszy, klęknął na kolana swe i modlił się z nimi wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '36', '36', 'Tedy się wszczął wielki płacz wszytkich, a upadając na szyi Pawłowi, całowali go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '19', '37', '37', 'Będąc barzo żałościwi z słów, które im mówił, że więcej nie mieli ujźrzeć oblicza jego i także go prowadzili aż do okrętu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '0', '0', 'Zatym gdychmy odjechali rozstawszy się z nimi, przyszlichmy prostym pędem do Koum, a nazajutrz do Kobis, ztamtąd do Patara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '1', '1', 'Tam znalazszy okręt, który miał płynąć do Fenice, wsiadszy weń jechalichmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '2', '2', 'A gdy się nam począł ukazować Cypr, zostawiwszy go po lewej, wieźlichmy się do Syrijej i przyjechalichmy do Tyru, abowiem tam z okrętu sprzątać miano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '3', '3', 'Tamżechmy zmieszkali przez siedmi dni nalazszy zwolenniki, którzy do Pawła mówili przez ducha, aby nie zachodził do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '4', '4', 'Ale gdychmy wymieszkali ony dni, odeszlichmy, a wszyscy nas prowadzili z żonami i z dziatkami, ażechmy wyszli z miasta, tedy u brzegu poklęknąwszy na kolana, modlilichmy się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '5', '5', 'Zatym obłapiając jedni drugie, wsiedlichmy w okręt, a oni też wrócili się do swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '6', '6', 'My zasię odprawiwszy drogę, przyszlichmy z Tyru do Ptolemajdy, tamże się przywitawszy z bracią, mieszkalichmy u nich przez jeden dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '7', '7', 'A nazajutrz wyszedszy Paweł i my, którzychmy z nim byli, przyszlichmy do Cezarjej i wszedszy w dom Filipa Ewanjelisty (który był jeden z onych siedmi), zostalichmy u niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '8', '8', 'A ten miał czterzy córki panienki, które prorokowały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '9', '9', 'I gdychmy tam przez niemało dni mieszkali, przyszedł z Żydostwa prorok niektóry imieniem Agab.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '10', '10', 'Który przyszedszy do nas, wziął pas Pawłów i związawszy sobie nogi i ręce mówił: Toć mówi Duch święty: Męża, którego jest ten pas, tak zwiążą w Jeruzalem Żydowie i podadzą go w ręce poganom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '11', '11', 'To skorochmy usłyszeli, prosilichmy i sami i ci, którzy na onym miejscu byli, aby nie chodził do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '12', '12', 'Tedy odpowiedział Paweł: Cóż czynicie płacząc, a krusząc serce moje? Koniecznieć ja nie tylko abych miał być związan, alem i umrzeć gotów w Jeruzalem, dla imienia Pana Jezusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '13', '13', 'A tak, gdychmy mu rozradzić nie mogli, przestalichmy na tym mówiąc: Niechajżeć się zstanie wola Pańska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '14', '14', 'Zatym po onych dnioch, zebrawszy tłumoki, szlichmy do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '15', '15', 'I przyszli też społu z nami niktórzy zwolennicy z Cezarjej, przywiódszy z sobą tego, u któregochmy gospodą stać mieli, Mnazona Cypryjczyka, starego zwolennika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '16', '16', 'Gdyśmy tedy przyszli do Jeruzalem, wdzięcznie nas przyjęli bracia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '17', '17', 'Nazajutrz wszedł z nami Paweł do Jakuba, kędy wszyscy starszy byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '18', '18', 'Które obłapiwszy opowiedział im porządnie, co Bóg czynił między pogany przez służbę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '19', '19', 'Co oni słysząc chwalili Pana, a powiedzieli mu: Widziszże bracie, jako wiele jest tysięcy Żydów, którzy uwierzyli a wszyscy zapalonemi są chotliwością ku zakonowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '20', '20', 'Słyszeli tedy o tobie z powieści innych, iż uczysz odstania od Mojżesza, a wszyscy Żydowie, którzy są między pogany powiedasz, że się obrzezować nie mają, ani mają chodzić wedle ustaw jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '21', '21', 'Cóż tedy jest ? Koniecznieć się potrzeba zejdź ludowi, boć usłyszą żeś przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '22', '22', 'A przetoż czyń to, coć powiedamy. Mamyć czterzech mężów, co społu ślub mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '23', '23', 'Ty wziąwszy, oczyśćże się z nimi i złóżże się z nimi, aby ogolili głowy, a iżby poznali wszyscy, że to co o tobie słyszeli z powieści innych, nic nie jest, ale iż i sam chodzisz zachowywając zakon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '24', '24', 'O tych też, którzy uwierzyli z poganów, mychmy pisali, gdychmy postanowili, aby nic takowego nie zachowywali, tylkoż aby się przestrzegali od tego, co jest ofiarowano bałwanom, od krwie, od rzeczy dawionych i od grzechu nieczystego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '25', '25', 'Tedy Paweł wziąwszy ony męże, nazajutrz oczyścion jest z nimi i wszedł do kościoła, oznajmując wypełnienie dniów oczyścienia, aż za każdego z nich była uczyniona ofiara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '26', '26', 'A gdy już było prawie siedm dni wypełnienia, niektórzy Żydowie z Azjej, gdy go ujźrzeli w kościele, wzburzyli wszytek lud i targnęli się nań rękoma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '27', '27', 'Wołając: Mężowie Izraelscy ratujcież. Tenci jest człowiek on, który przeciw ludowi i zakonowi i miejscu temu wszytkich uczy wszędy, a już nawiódł i Greków w kościół i plugawi to miejsce święte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '28', '28', 'Abowiem przed tym widzieli z nim w mieście Trofima Efeskiego i mnimali, aby go Paweł wwiódł do kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '29', '29', 'Wzruszyło się tedy miasto wszytko i zbieżało się mnóstwo ludu, a pojmawszy Pawła, wywlekli z kościoła i natychmiast drzwi zamkniono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '30', '30', 'I gdy się starali jakoby go zamordować, przyszła wieść do rotmistrza nad wojskiem, iż się wzburzyło wszytko Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '31', '31', 'Który tegoż czasu wziąwszy służebne i setniki, przybieżał do nich. Tedy oni ujźrzawszy rotmistrza i służebne, przestali bić Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '32', '32', 'A tak Rotmistrz przyszedszy bliżej, uchwycił go i kazał dwiema łańcuchy zwięzać, pytając się coby był zacz i co uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '33', '33', 'Ale jedni tak drudzy inaczej między ludem wołali i gdy nic pewnego o nim się dowiedzieć nie mógł w onym rozruchu, roskazał go wieść do zamku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '34', '34', 'I gdy przyszedł na wschody, przydało się, iż go prawie nieśli służebni, przed gwałtem ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '35', '35', 'Abowiem mnóstwo ludu szło za nim wołając: Daj go stracić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '36', '36', 'Gdy tedy poczynano wieść Pawła do zamku, rzekł rotmistrzowi: Godzili mi się co rzec do ciebie? Który rzekł k niemu: A umiesz Greckie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '37', '37', 'I nie tyżeś to jest on Egiptcjanin, który przed tymi dni wzruszyłeś burzkę i wywiodłeś na puszczą czterzy tysiące zbójców?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '38', '38', 'Tedy mu rzekł Paweł: Jaciem jest człowiek Żyd z Tarsu, mieszczanin nie z podlejszego miasta Cylicijej i proszę cię, abyś mi dopuścił mówić do ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '20', '39', '39', 'A tak, gdy mu on dopuścił, tedy Paweł stojąc na wschodziech, kinął ręką na lud i uczyniwszy wielkie milczenie, mówił do nich Żydowskim językiem, tymi słowy:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '0', '0', 'Mężowie bracia i ojcowie, słuchajciesz tej mojej, którą teraz czynię do was obmowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '1', '1', '(A gdy usłyszeli, iż do nich mówił Żydowskim językiem, tedy tym więtsze milczenie było i rzekł)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '2', '2', 'Jać jestem mąż Żyd rodem z Tarsu, miasta w Cylicijej, a tu jestem wychowan w mieście tym, u nóg Gamalijelowych i wyćwiczony dostatecznie w zakonie ojczystym, chutliwą miłością ku Bogu będąc zapalony, tak jako i wy wszyscy dziś jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '3', '3', 'Którym prześladował tę sektę aż do śmierci, wiążąc i podawając do więzienia tak męże jako i niewiasty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '4', '4', 'Jako mi tego i książę kapłańskie jest świadkiem i wszyscy starszy, od których ja też listy wziąwszy do braciej, wiozłem do Damaszku, abych był powiązawszy przywiódł do Jeruzalem ty, co tam byli, żeby je skarano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '5', '5', 'I przydało mi się, gdym jechał w drogę, a byłem blisko Damaszku, jakoby w południe, iż nagle oświeciła się około mnie światłość wielka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '6', '6', 'I upadłem na ziemię, a usłyszałem głos mówiący ku mnie: Saulu, Saulu, przecz mię prześladujesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '7', '7', 'Tedym ja odpowiedział: Ktoś ty jest, Panie? A on ku mnie rzekł: Jaciem jest on Jezus Nazareński, którego ty prześladujesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '8', '8', 'Ci tedy, którzy byli ze mną, acz widzieli światłość i polękali się, ale głosu nie słyszeli onego, który ze mną mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '9', '9', 'Alem ja rzekł: Cóż czynić mam Panie? A Pan mi odpowiedział: Wstań a idź do Damaszku, a tam tobie powiedzą o wszytkim, co tobie postanowiono, abyś czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '10', '10', 'A gdym nic nie widział przed chwałą światłości onej, będąc widzion rękę od nich co ze mną byli, przyszedłem do Damaszku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '11', '11', 'Tam Ananiasz, niektóry mąż pobożny wedle zakonu, mając o sobie dobre świadectwo od wszytkich Żydów tam mieszkających.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '12', '12', 'Przyszedł do mnie, a stanąwszy rzekł mi: Saulu bracie, weźmi wzrok, a ja zarazem tegoż czasu ujźrzałem go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '13', '13', 'Tedy on mnie rzekł: Bóg ojców naszych obrał ciebie, abyś poznał wolę jego, a iżbyś oglądał onego Sprawiedliwego i słuchał głosu z ust jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '14', '14', 'Abowiem będziesz jego świadkiem u wszytkich ludzi, o tym coś widział i słyszał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '15', '15', 'A przetoż teraz przecz omieszkawasz? Wstań a okrzci się, a omyj grzechy twe wzywając imienia Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '16', '16', 'Stało się potym, gdym się wrócił do Jeruzalem a modliłem się w kościele, żem był zachwycon bez pamięci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '17', '17', 'Tedym go widział, a on mówi ku mnie: Spiesz się a wynidź rychło z Jeruzalem, abowiem świadectwa twego nie przyjmą o mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '18', '18', 'Alem ja powiedział: Panie, onić wiedzą żeciem ja włóczył do więzienia i mordowałem po wszytkich bożnicach ty, którzy w cię wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '19', '19', 'I gdy była rozlana krew Stefana męczennika twego, jam też stał przy tym, a zezwalałem na zamordowanie jego i strzegłem szat tych, którzy go zabijali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '20', '20', 'Tedy on rzekł ku mnie: Idźże, bo ja do poganów daleko posyłam ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '21', '21', 'Słuchali go tedy aż do tych słów, zatym podniózszy głosy swe mówili: Daj stracić z ziemie człowieka tego, bo nie słusza, aby miał żyw zostać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '22', '22', 'I także wołali i miotali swe szaty, ciskając proch na powietrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '23', '23', 'A rotmistrz roskazał go wieść do zamku, kazawszy aby go biczmi bito i męczono, chcąc się dowiedzieć przyczyny, dla czegoby tak nań wołano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '24', '24', 'A gdy go rzemieńmi związano, tedy Paweł rzekł setnikowi, który tuż stał: Izali się wam godzi człowieka Rzymianina nie osądzonego bić biczmi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '25', '25', 'To gdy usłyszał setnik, szedł do rotmistrza i oznajmił mu mówiąc: Bacz co masz czynić, bo ten człowiek jest Rzymianin.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '26', '26', 'Tedy przyszedszy rotmistrz rzekł mu: Powiedz mi jeśliś ty jest Rzymianin? A on rzekł: Tak jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '27', '27', 'Odpowiedział mu rotmistrz: Jam się za wielką sumę pieniędzy dokupił prawa miasta tego. A Paweł rzekł: Ale się ja też urodził i mieszczaninem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '28', '28', 'A dlategoż natychmiast odstąpili od niego ci, co go męczyć mieli, ktemu się i rotmistrz zlękł, doznawszy, że był Rzymianinem, a iż go był związał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '21', '29', '29', 'A tak nazajutrz chcąc pewnie wiedzieć, o którąby rzecz od Żydów był obwinowan, rozwiązał go, a roskazał się zejdź książętom kapłańskim i wszytkiemu ich zgromadzeniu, a wywiódszy Pawła, postawił go przed nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '0', '0', 'Tedy Paweł patrząc pilnie oczyma swemi na ono zgromadzenie rzekł: Mężowie bracia, ja ze wszytkiego sumnienia dobrego służyłem Bogu aż oto do dnia tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '1', '1', 'Ale Ananijasz, kapłan nawyższy, roskazał tym, którzy stali aby go w usta bili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '2', '2', 'Rzekł tedy Paweł kniemu: Przyjdzie ten czas, iż cię będzie bił Bóg, ściano pobielona, ty siedzisz sądząc mię wedle zakonu, a roskazujesz mię bić przeciw zakonowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '3', '3', 'Zatym ci, którzy stali w koło rzekli: Złorzeczysz nawyższemu kapłanowi Bożemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '4', '4', 'A Paweł rzekł: Nie wiedziałem bracia, żeby był nawyższym kapłanem, bo napisano jest: Książęciu ludu twego złorzeczyć nie będziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '5', '5', 'I gdy obaczył Paweł, że ich jedna część byli Saduceuszowie, a drudzy Faryzeuszowie, zawołał do zgromadzenia: Mężowie bracia, jaciem jest Faryzeusz, syn Faryzeuszów, jaciem oskarżon o nadzieję i powstanie umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '6', '6', 'A to gdy rzekł, wnet się wszczęły niesnaski między Faryzeuszmi i Saduceuszmi i także się rozerwało ono zgromadzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '7', '7', 'Abowiem Saduceuszowie powiedają, iż nie masz zmartwychwstania, ani Anioła, ani Ducha, Faryzeuszowie zasię to oboje wyznawają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '8', '8', 'I także się wsczęło wołanie wielkie, a wstawszy doktorowie z strony Faryzeuszów, zaparli się oto, mówiąc: Nic złego nie najdujemy w tym człowieku. A jeśliż co powiedział Duch abo Anioł, nie przeciwiajmyż się Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '9', '9', 'A gdy był wielki rostyrk, bojąc się rotmistrz aby Pawła nie rozszarpali między sobą, roskazał idź służebnym a wydrzeć go z pośrzód ich i odwieść do zamku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '10', '10', 'Tedy przyszłej nocy stanąwszy przy nim Pan rzekł: Bądź dobrego serca Pawle, abowiem jakoś o mnie dawał świadectwo w Jeruzalem, tak potrzeba abyś mię też oświadczył w Rzymie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '11', '11', 'A tak, gdy nastał dzień, zszedszy się niektórzy z Żydów przyrzekli sobie społu pod klątwą mówiąc: Nie będziem jeść ani pić aż zamordujem Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '12', '12', 'I było ich więcej niż czterdzieści co to sprzysiężenie uczynili między sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '13', '13', 'Ci przyszedszy do książąt kapłańskich i do starszych powiedzieli: Przyrzekliśmy sobie ślub, że nic ukąsić nie mamy, ażbyśmy zamordowali Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '14', '14', 'A przetoż wy teraz dajcie znać rotmistrzowi z wolej spólnego zgromadzenia, aby go jutro do was wywiódł, iż się rzekomo chcecie dostateczniej wywiedzieć o tych rzeczach, co się tkną strony jego, a my pirwej niż przed was przyjdzie, gotowiśmy go zamordować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '15', '15', 'A gdy usłyszał syn siostry Pawłowej tę zdradę, przybieżał i wszedszy do zamku oznajmił to Pawłowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '16', '16', 'I zawoławszy Paweł niektórego z setników rzekł: Dowiedź tego młodzieńca do rotmistrza, bowiem mu coś ma powiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '17', '17', 'A tak go on wziąwszy wiódł do Rotmistrza i rzekł: Paweł więzień wezwawszy mię prosił, abych tego młodzieńca dowiódł do ciebie, iż ma nieco mówić z tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '18', '18', 'Tedy rotmistrz wziąwszy go za rękę odwiódł się z nim na stronę i pytał go: Cóż to jest co mi oznajmić masz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '19', '19', 'A on powiedział: Postanowili Żydowie prosić cię, abyś jutro wywiódł Pawła do zgromadzenia, jakoby się mieli nieco o nim dostateczniej dowiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '20', '20', 'A tak się im nie daj uwodzić, boć się nań nasadziło z nich więcej niż czterdzieści mężów, którzy sobie pod klątwą przyrzekli ani jeść ani pić ażby go zamordowali i teraz już są w pogotowiu, czekając cobyś im oznajmił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '21', '21', 'Zatym rotmistrz odprawił onego młodzieńca, roskazując mu aby tego przed żadnym nie powiedał, iż mu to oznajmił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '22', '22', 'I wezwawszy dwu niektórych z setników, roskazał aby nagotowali dwie ście służebnych, a iżby szli aż do Cesareji, ktemu siedmidziesiąt jezdnych i dwie ście drabów, po trzeciej godzinie w noc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '23', '23', 'Iżby też byli po gotowiu z końmi, aby Pawła wsadziwszy, zdrowo odprowadzili do Feliksa starosty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '24', '24', 'Napisawszy też list, który to w sobie zamykał:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '25', '25', 'Klaudjusz Lisjas nazacniejszemu staroście Feliksowi zdrowia życzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '26', '26', 'Tego męża pojmanego od Żydów, gdy już od nich miał być zabit, przyszedszy z hufcem odjąłem, dowiedziawszy się, iż był Rzymianin.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '27', '27', 'I chcąc się dowiedzieć przyczyny oczby nań skarżyli, wywiodłem go do ich zgromadzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '28', '28', 'Dowiedziałem się tedy, iż nań skarżą o jakieś gadki zakonu ich, lecz nie ma żadnego występku godnego śmierci abo więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '29', '29', 'A iż mi oznajmiono o przyszłej nań zdradzie żydowskiej, tedym tejże godziny odesłał go do ciebie, abyś też oznajmił tym, którzy nań skarżą co by mieli przeciw niemu, aby mówili przed tobą. Miejże się dobrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '30', '30', 'Tedy służebni jako im było roskazano, wziąwszy Pawła prowadzili go nocą do Antypatryda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '31', '31', 'A nazajutrz odprawiwszy jezdne, aby z nim jechali, wrócili się sami do zamku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '32', '32', 'Którzy gdy przyszli do Cezarjej a oddali list staroście, postawili też przed nim i Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '33', '33', 'A tak starosta, gdy przeczedł list, pytał go, z którejby był ziemie i zrozumiał, że był z Cylicijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '22', '34', '34', 'Zatym rzekł: Będę cię słuchał, gdy też będą ci, którzy na cię skarżyć mają. I także go roskazał strzedz na ratuszu Herodowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '0', '0', 'Szedł potym piątego dnia nawyższy kapłan Ananiasz z starszymi i z Tertulem, niejakim wymówcą, którzy stanęli przed starostą przeciw Pawłowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '1', '1', 'A tak pozwawszy go, począł nań skarżyć Tertulus mówiąc: My przez cię i przez twej rostropności zacne sprawy w narodzie tym, dostąpilichmy pokoju wielkiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '2', '2', 'I prawie wszędzie to uznawamy nazacniejszy Feliksie, dziękując ci za to wszelakim sposobem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '3', '3', 'Wszakoż bych cię długo nie bawił, proszę cię abyś nas maluczko posłuchał wedle twej sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '4', '4', 'Abowiemeśmy znaleźli tego człowieka zaraźliwego i wzruszającego niesnaski między wszytkimi Żydy po wszytkim świecie i jest powodem tej sekty Nazareńczyków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '5', '5', 'Który się też kusił, aby był zgwałcił kościół, a które my pojmawszy, chcieliśmy osądzić wedle zakonu naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '6', '6', 'Ale przyszedszy rotmistrz Lisjasz z wielkim gwałtem wywiódł go z rąk naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '7', '7', 'Roskazawszy tym, którzy nań skarżą idź do ciebie, skąd i sam możesz pytanie uczyniwszy o wszytkim tym rozeznać oczmy nań skarżemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '8', '8', 'Na to i Żydowie zezwolili mówiąc: Takci się rzecz ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '9', '9', 'Zatym odpowiedział Paweł, gdy mu starosta mówić dozwolił: Gdyż wiem, że od wiela lat jesteś przełożon nad tym narodem, tedy z lepszą myślą o tych rzeczach, które na mię należą odpowiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '10', '10', 'Gdyż i sam znać możesz, iż mnie nie wyszło więcej dni jedno dwanaście, jakom przyszedł modlić się do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '11', '11', 'I nie naleźli mię w kościele żebych miał z kim mówić, abo iżbych czynić miał schadzki między ludem, ani w bożnicach ani w mieście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '12', '12', 'A nie mogąć tego dowieść ocz tu teraz na mię skarżą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '13', '13', 'Aleć to przed tobą wyznawam, że według drogi onej, którą oni powiedają być sektą, tak chwalę ojczystego Boga, wierząc wszytkiemu co w zakonie i w prorocech jest napisano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '14', '14', 'I mając nadzieję w Bogu, znam być toż którego i ci sami czekają, powstanie umarłych, sprawiedliwych społu i niesprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '15', '15', 'W tym się tedy sam ćwiczę, abych miał sumnienie przez obrażenia przed Bogiem i przed ludźmi zawżdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '16', '16', 'A gdy przeminęło wiele lat, tedym przyszedł oddawając jałmużny narodowi mojemu i ofiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '17', '17', 'Któregoż czasu znaleźli mię oczyścionego w kościele, nie z wielkością ludu, ani żebych miał czynić rozruch.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '18', '18', 'Niektórzy z Azjej Żydowie, którym było potrzeba tu stanąć przed tobą i skarżyć jeśliby co mieli przeciw mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '19', '19', 'Abo i ciż sami niechaj powiedzą jeśliż we mnie znaleźli jaki iny niesprawiedliwy uczynek, gdym ja stał przed zebraniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '20', '20', 'Tylkoż co się dotycze jednego tego słowa, którem głośno powiedział stojąc między nimi, iż o powstaniu umarłych ja dziś jestem od was oskarżon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '21', '21', 'To usłyszawszy Feliks odłożył im mówiąc: Gdy się pewniej dowiem co się dotycze tej sekty kiedy rotmistrz Lisjasz przyjdzie, rozeznam waszę sprawę');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '22', '22', 'I roskazał setnikowi, aby był pod strażą Paweł, a iżby był wolniejszy i aby nie broniono któremukolwiek z przyjaciół jego służyć mu i chodzić do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '23', '23', 'Potym po kilku dni, gdy Feliks był z Drusyllą żoną swą, która była Żydówka, wezwał do siebie Pawła i słuchał go o wierze, która jest w Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '24', '24', 'A gdy on wywodził o sprawiedliwości i powściągliwości i o sądzie przyszłym, ulększy się Feliks odpowiedział: Już teraz odejdź, wszak potym czas upatrzywszy wzowę cię sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '25', '25', 'Nadziewając się, iżby mu Paweł miał dać pieniędzy żeby go wyprawił i dlategoż go tym częściej wzywając do siebie rozmawiał z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '23', '26', '26', 'I gdy się spełniły dwie lecie, miał Feliks po sobie namiastka Porcjusza Festa, a chcąc Feliks zjednać sobie łaskę u Żydów, zostawił Pawła w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '0', '0', 'Tedy Festus wszedszy na urząd, trzeciego dnia jechał do Jeruzalem z miasta Cesarejej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '1', '1', 'I oznajmili mu, nawyższy kapłan i przedniejszy Żydowie o Pawle, a prosili go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '2', '2', 'Żądając łaski przeciw jemu, aby go wezwał do Jeruzalem, uczyniwszy nań zasadkę na drodze, żeby go zabito.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '3', '3', 'Ale Festus odpowiedział: Iż Pawła będą strzedz w Cesareji, wszakoż ja tam w rychle pojadę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '4', '4', 'A przetoż którzy z was mogą niech społu ze mną idą, a jeśliż w tym człowieku jest co takowego, niechżeć nań skarżą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '5', '5', 'I zmieszkawszy między nimi nic więcej jedno dziesięć dni gdy przyjechał do Cesareji, nazajutrz siadł na stolicy a kazał Pawła przywieść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '6', '6', 'Który gdy stanął, obstąpili go oni Żydowie, którzy przyszli z Jeruzalem, wiele cięszkich skarg przynosząc przeciw Pawłowi, których dowieść nie mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '7', '7', 'Gdyż on odpowiedział, że ani przeciw zakonowi Żydowskiemu, ani przeciw kościołowi, ani przeciw cesarzowi nic nie wystąpił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '8', '8', 'Ale Festus chcąc sobie zjednać łaskę u Żydów, odpowiedziawszy Pawłowi rzekł: A chcesz idź do Jeruzalem, a tam o tych rzeczach być rozsądzon przedemną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '9', '9', 'Na to powiedział Paweł: Przed stolicą Cesarską stawam, gdzie muszę być osądzon, w żadnej rzeczy nie uczyniłem krzywdy Żydom, jako to i sam lepiej znasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '10', '10', 'Bo jeśliżem co krzyw, a uczyniłem co śmierci godnego, nie zbraniam się umrzeć. A jeśliż nie masz nic takiego w czym mię ci winują, żaden się im mną zachować nie może. A tak apeluję do cesarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '11', '11', 'Tedy Festus rozmówiwszy się z radą, odpowiedział: Apelowałeś do cesarza? Pójdziesz do cesarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '12', '12', 'Gdy potym wyszło kilka dni, Agrypa król i Bernice przyjechali do Cesarejej witać Festa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '13', '13', 'Tam zmieszkawszy przez wiele dni, oznajmił Festus królowi o tym co się Pawła dotykało, mówiąc: Mąż niektóry zostawion jest od Feliksa w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '14', '14', 'O którym gdym przyjechał do Jeruzalem, oznajmili mi książęta kapłańskie i starszy żydowscy prosząc mię o skazanie przeciw jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '15', '15', 'Leczem ja im odpowiedział, że Rzymianom nie jest ten zwyczaj aby kogo mieli skazać na stracenie, aż by pirwej ten, którego winują miał przeciw sobie ty, coby nań skarżyli, a iżby też miał miesce obrony w swym obwinieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '16', '16', 'A tak, gdy tu oprócz żadnej odwłoki się zeszli, nazajutrz gdym siadł na stolicy, kazałem przywieść tego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '17', '17', 'Tedy ci co nań skarżyli, gdy stanęli, żadnej winy nie kładli nań takiej jakiejem się ja domnimawał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '18', '18', 'Jedno tylko jakieś gadki o swym nabożeństwie wtaczali przeciw niemu i o niejakim Jezusie umarłym, o którym twierdzi Paweł żeby miał żyw być.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '19', '19', 'Ja tedy wątpiąc o takowych gadkach, rzekłem mu jeśliby chciał idź do Jeruzalem a tam się o tym rozsądzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '20', '20', 'Ale on, iż apelował aby zachowan był do Augustowego rozeznania, roskazałem go chować aż go odeślę do cesarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '21', '21', 'Zatym Agrypa rzekł do Festa: I samem chciał słuchać człowieka tego; A on mu rzekł: Jutro go usłyszysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '22', '22', 'Tedy nazajutrz, gdy przyszedł Agrypa i Bernice z wielką okazałością, a wszedł na salę gdzie sądy odprawowano z rotmistrzmi i z inemi zacnemi miasta onego mieszczany, na roskazanie Festowe przywiedzion jest Paweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '23', '23', 'Zatym rzekł Festus: Agrypo królu i wy wszyscy, którzy jesteście społu z nami, widzicież tego człowieka, o którego mię wszytko pospólstwo żydowskie prosiło w Jeruzalem i tu, wołając, żeby dalej nie miał być żyw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '24', '24', 'Ja tedy, gdym się dowiedział, że nie uczynił nic śmierci godnego, wszakoż iż on sam apelował do Augusta, a takżem go umyślił odesłać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '25', '25', 'O którym cobych panu pewnego pisać miał, nie mam i dlategożem go wywiódł do was, a nawięcej do ciebie królu Agrypo, abych się wywiedziawszy słusznej sprawy, miał wżdy co pisać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '24', '26', '26', 'Bo mi się to rzecz niesłuszna zda, posłać więźnia a nie oznajmić nań tego w czym go winują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '0', '0', 'Zatym Agrypa rzekł do Pawła: Otoć dopuszczają mówić od siebie: Tedy Paweł wyciągnąwszy ręce, odpowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '1', '1', 'We wszytkim ocz jedno jestem od Żydów obwinion, królu Agrypo, szczęśliwym się być rozumiem, iż dziś mam przed tobą odpowiedać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '2', '2', 'A nawięcej, iż wiem żeś świadom wszytkiego co jest między Żydy i zwyczajów i gadek. A przetoż cię proszę, żebyś mię cierpliwie słuchał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '3', '3', 'A tak acz żywot mój, którym wiódł w swej młodości od początku między narodem moim w Jeruzalem, wiedzą wszyscy Żydowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '4', '4', 'Gdyż przed tym znali jakoż jeszcze z przodków moich, (by świadczyć chcieli), iż według onej nadostateczniejszej sekty nabożeństwa naszego; żyłem Faryzeuszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '5', '5', 'A teraz dla nadzieje obietnice, która jest ojcom naszym od Boga uczyniona, stoję przed tym sądem oskarżony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '6', '6', 'Do której dwojenaście nasze pokolenie we dnie i w nocy ustawicznie chwaląc Boga nadziewając się przyjdź, o którą nadzieję oskarżonem jest od Żydów królu Agrypo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '7', '7', 'Przeczże się wam zda rzecz trudna ku wierze, iż Bóg wzbudza umarłe?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '8', '8', 'I mnieć się iście widziało, że przeciw imieniowi Jezusa Nazareńskiego miałem wielkie sprzeciwieństwa czynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '9', '9', 'Com i czynił w Jeruzalem, abowiem wiele świętych ja dawałem do więzienia, wziąwszy moc od książąt kapłańskich i gdy bywali zabijani teżem na to wotował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '10', '10', 'I zabijałem je po wszytkich bożnicach częstokroć, przymuszając ku bluźnieniu i szalenie się nad nimi srożąc, prześladowałem je aż w postronnych mieściech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '11', '11', 'W której sprawie jadąc też i do Damaszku z zwierzchnością i poruczeństwem książąt kapłańskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '12', '12', 'Prawie w południe będąc w drodze, widziałem królu światłość z nieba, która przewyższała jasność słoneczną, a oświeciła mię i ty co ze mną szli w drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '13', '13', 'Tam, gdychmy wszyscy padli na ziemię, usłyszałem głos mówiący do mnie, a mówiący żydowskim językiem: Saul, Saul, przeczże mię prześladujesz? Trudnoć tobie przeciw osnom wierzgać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '14', '14', 'A jam odpowiedział: Ktoś ty jest Panie? A on mi rzekł: Jam jest Jezus, którego ty prześladujesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '15', '15', 'Ale powstań a stań na nogi twe, abowiemciem się dlatego ukazał, abych cię postanowił sługą i świadkiem, tak tych rzeczy któreś widział, jako i inych, w których się ja ukażę tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '16', '16', 'Wyjmując cię z ludu tego i z poganów, do których cię teraz posyłam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '17', '17', 'Abyś otworzył oczy ich, iżby się nawrócili z ciemności do światłości, a z mocy szatańskiej do Boga. Aby wzięli odpuszczenie grzechów i dział między świętymi, przez wiarę, która jest ku mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '18', '18', 'A przetoż królu Agrypo nie byłem nieposłusznym niebieskiemu widzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '19', '19', 'Lecz naprzód tym, którzy są w Damaszku i w Jeruzalem i po wszytkiej krainie żydowskiej, po tym i poganom, oznajmowałem aby się uznali i nawrócili do Boga, czyniąc uczynki godne, jako ci, którzy się uznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '20', '20', 'Dla tejci mię przyczyny Żydowie w kościele pojmawszy kusili się zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '21', '21', 'Ale dostawszy ratunku od Boga, zostałem jeszcze aż do dnia tego świadcząc, tak małym jako i wielkim, a nic inego nie powiedając, jedno ty rzeczy, które prorocy i Mojżesz opowiedzieli, iż przyjdź miały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '22', '22', 'A to, iż Krystus miał cierpieć, a miał być pierwszy z zmartwychwstania, światłość opowiedając ludowi temu i poganom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '23', '23', 'To gdy on odpowiedał, rzekł Festus głosem wielkim: Szalejesz Pawle! Wiele pisma przywodzi cię ku szaleństwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '24', '24', 'Tedy on rzekł: Nie szalejęć o zacny Feste, aleć prawdziwe a trzeźwie słowa powiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '25', '25', 'Boć to wie i król, przed którym bespiecznie mówię, gdyż nie mniemam aby co z tych rzeczy u niego było tajno, boć się to nie działo w kącie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '26', '26', 'Wierzysz królu Agrypo prorokom? Wiem, iż wierzysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '27', '27', 'Zatym Agrypa rzekł do Pawła: Maluczko mię nie namówisz, abych został krześcianinem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '28', '28', 'Rzekł tedy Paweł: Żądałbych od Boga, aby nie maluczko, ale tym więcej, żebyś nie tylko ty, ale i ci wszyscy, którzy mię dziś słuchają stali się takowemi jakim ja jest, oprócz tylko tych związek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '29', '29', 'A gdy to rzekł, tedy wstał król i starosta i Bernice i ci, którzy społu siedzieli z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '30', '30', 'I wyszedszy mówili jedni do drugich tymi słowy: Nic nie czyni ten człowiek coby miało być godnego śmierci abo więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '25', '31', '31', 'A Agrypa rzekł Festowi: Ten człowiek mógł był wypuszczon być, by był do cesarza nie apelował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '0', '0', 'I gdy już skazano żebyśmy się wieźli do Włoch, tedy poruczono tak Pawła jako i niektóre ine więźnie setnikowi, imieniem Juljuszowi z roty Augustowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '1', '1', 'Tedy wsiadszy w okręt Adramiteński, przyszło nam płynąć podle kraju Azjej, takżechmy się wieźli, a był z nami Arystarchus Macedończyk Tesaloniceński.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '2', '2', 'I przypłynęliśmy dnia drugiego do Sydonu, a Juljus ludzkie się zachowywając przeciw Pawłowi, dopuścił mu idź do przyjaciół, aby go czym opatrzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '3', '3', 'Z tamtąd się puściwszy, przybilichmy się pod Cypru dlatego, że nam były wiatry sprzeciwne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '4', '4', 'A przeprawiwszy się przez morze pod Cylicją i Pamfiliją, przyszlichmy do Myry miasta w Licijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '5', '5', 'Tam setnik znalazszy okręt Aleksandryjski, który płynął do Włoch, wsadził nas weń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '6', '6', 'Gdychmy tedy przez niemały czas nie rychło się wieźli, a ledwochmy przyszli ku Gnidu, iż nam nie dopuszczał wiatr, podpłynęlichmy pod Kretę podle Salmonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '7', '7', 'A ledwie się tam tedy przeprawiwszy, przyszlichmy na miejsce, które zową: Piękne Porty, u którego było blisko miasto Lasea.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '8', '8', 'Po tym po niemałym czasie, gdy już było niebespieczne jechanie morzem, a iż też już był post przeminął, napominał je Paweł,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '9', '9', 'Mówiąc im: Mężowie widzę, że z niebespieczeństwem i z wielką szkodą, nie tylko ładunku i okrętu, ale też i osób naszych przyjdzie nam ta droga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '10', '10', 'Wszakoż setnik więcej wierzył sprawcy okrętu i styrnikowi, a niż temu co powiedał Paweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '11', '11', 'I gdyż nie było portu słusznego ku zimowaniu, tedy ich wiele radzili, żeby się też i z tamtąd puścili, kosztując jeśliby jako mogli przeprawić się ku Fenicę zimować, który jest port w Krecie, leżąc ku wiatru z strony południa i zachodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '12', '12', 'A gdy powionął wiatr prawie z południa, mnimając aby się im wedle myśli dziać miało, gdy się puścili od portu, przeprawiali się bliżej imo Kretę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '13', '13', 'Wszakoż nie długo po tym wszczął się przeciw niemu wiatr gwałtowny, który zową Euroklidon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '14', '14', 'A gdy był okręt porwan, a nie mógł się wiatrowi oprzeć, puściwszy okręt za wiatry, płynęlichmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '15', '15', 'I gdychmy przybiegli ku niektóremu małemu wyspowi, który zową Klauda, ledwochmy mogli barkę u okrętu zatrzymać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '16', '16', 'Którą wściągnąwszy, dodawali sobie pomocy, podpasawszy okręt, a bojąc się żeby nie wpadli w Syrtyu, spuściwszy barkę, tak płynęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '17', '17', 'A gdy nas wielka niepogoda pędziła, tedy nazajutrz niektóre rzeczy precz miotali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '18', '18', 'A trzeciego dnia rękoma naszymi wszytki naczynia z okrętu wyrzucilichmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '19', '19', 'I gdy się ani słońce, ani gwiazdy przez wiele dni nie ukazały, a niepogoda tym więczsza nastawała, tedy nam już była odjęta wszytka nadzieja zdrowia naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '20', '20', 'Także gdy przez nie mały czas nic nie jedli, tedy Paweł stojąc w pośrzód ich, mówił: O mężowie, zaisteć wam było potrzeba słuchać mnie, a nie odjeżdżać z Krety, abyście byli uszli tego niebespieczeństwa i szkody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '21', '21', 'Aleć was teraz upominam, abyście byli dobrej myśli, boć w żadnym z was szkody nie będzie, oprócz tylko okrętu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '22', '22', 'Abowiem mi się tej nocy ukazał Anioł Boży, któregom ja jest i którego chwalę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '23', '23', 'Opowiedając mi: Nie bój się Pawle, potrzebać abyś stanął przed cesarzem, a oto cię Bóg wszytkimi darował, którzy się wiozą z tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '24', '24', 'A przetoż bądźcie dobrej myśli o mężowie, abowiemci ja wierzę Bogu, że tak będzie jako mi powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '25', '25', 'A musiemy być wyrzuceni na niektórym wyspie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '26', '26', 'Zatym gdy nadeszła noc czternasta, gdyśmy pływali po Morzu Adrjatyckim, około północy zdało się żeglarzom, iż była blisko nich niektóra kraina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '27', '27', 'Którzy spuściwszy sznur z ołowem, naleźli głębiej dwadzieścia sążon, a maluczko dalej postąpiwszy, zasię spuściwszy ołów, naleźli piętnaście sążon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '28', '28', 'I bojąc się aby na miejsca nierówne nie przyszli, rzuciwszy cztery kotwice z okrętu, pragnęli aby dzień nastał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '29', '29', 'Tedy żeglarze, gdy się starali jakoby uciec z okrętu puściwszy barkę na morze, rzkomo chcieli od przodku zarzucać kotwicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '30', '30', 'Rzekł tedy Paweł setnikowi i służebnym: Jeśliż ci nie zostaną w okręcie, żeby wy nie możecie być zachowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '31', '31', 'A tak służebni obcięli powrozy u barki i dali jej upaść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '32', '32', 'Zatym gdy dzień począł nastawać, napominał Paweł wszytki aby jedli mówiąc: Tenci dzień jest czternasty, jako czekając trwacie łaczni nic nie jedząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '33', '33', 'Dlategoż was napominam abyście jedli, abowiemci wam to będzie ku zdrowiu waszemu pożyteczno, gdyż żadnego z was i włos z głowy nie spadnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '34', '34', 'To rzekszy wziął chleb i czynił dzięki Bogu przed wszytkimi i ułomiwszy, począł jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '35', '35', 'Ale gdy wszyscy już byli lepszej myśli, poczęli też jeść i oni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '36', '36', ', A było nas wszytkich osobami w okręcie, dwie ście i siedmdziesiąt i sześć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '37', '37', 'Tedy najadszy się polekczali okrętu, wyrzucając pszenicę w morze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '38', '38', 'A gdy już dzień nadszedł, nie znali ziemię, wszakoż ujźrzeli niektórą odnogę mającą brzeg, na którą myślili, jeśliby mogli wprawić okręt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '39', '39', 'Zatym wyciągnąwszy kotwicę, puścili się na morze i odjąwszy panwie od styru, a podniózszy mniejszy żagiel, puścili się za wiatrem ku brzegowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '40', '40', 'A gdy napadli na miejsce, gdzie się dwie morza schodzą, otracił się okręt, a przodek okrętu tak stanął, że się ani ruszył, ale zad łamał się od nawałności wód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '41', '41', 'Tedy służebni radzili, aby więźnie pomordowali, iżby który wypłynąwszy nie uciekł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '42', '42', 'Ale setnik chcąc zachować Pawła, nie dopuścił im tej rady, a roskazał, aby ci, którzy mogą przepłynąć naprzód wyskakowali i na brzeg wychodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '26', '43', '43', 'Drudzy zasię część ich na deszczkach, a część na kawałcoch okrętu. I stało się tak, że wszyscy zdrowo wyszli na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '0', '0', 'Tedy gdy zdrowo uszli, poznali iż to był wysep, który zową Melita.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '1', '1', 'A pogani okazowali nam nie leda ludzkość, abowiem zapaliwszy stos drew, przyjęli nas wszytkich dla dżdża, który miał być i dla zimna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '2', '2', 'A gdy Paweł nazbierał nieco gałęzia winnego i kładł na stos, wyrwawszy się żmija z ciepła, uchwyciła go za rękę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '3', '3', 'I ujźrzawszy pogani bestią wiszącą u ręki jego, mówili jedni do drugich: Zaisteć ten człowiek jest mężobójca, któremu chociaż uszedł zdrowo z morza, pomsta żywym być nie dopuści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '4', '4', 'Tedy on otrząsnąwszy bestią w ogień, nic złego nie ucierpiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '5', '5', 'A oni czekali ażby puchnął, abo padł nagle umarszy, ale gdy niemały czas czekali, a widzieli, iż się mu nic złego nie działo, odmieniwszy myśl, powiedali go być bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '6', '6', 'Tam przy onym miejscu były folwarki książęcia wyspu onego imieniem Publjusza, który nas przyjąwszy przez trzy dni, przyjacielskie chował w gospodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '7', '7', 'I przydało się, że ociec Publjuszów leżał mając febrę i czerwoną niemoc, którego Paweł wszedszy gdzie się modlił, a włożył nań ręce, uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '8', '8', 'Zatym gdy się to stało, tedy też i drudzy, którzy na się mieli niemocy na wyspie, przychodzili i bywali uzdrowieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '9', '9', 'Którzy nam też wielkie uczciwości czynili, a gdychmy odjeżdżali, nadali nam czego była potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '10', '10', 'A tak po trzech miesiącach jechalichmy w okręcie Aleksandryjskim, który zimował na wyspie, na którym był herb Kastor i Polluks.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '11', '11', 'Przybiwszy się tedy k Syrakusom, mieszkalichmy tam przez trzy dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '12', '12', 'Stąd przeprawując się mimo, przyszlichmy do Regijum, a nazajutrz mając wiatr południowy, wtórego dnia przyszlichmy do Puteolos.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '13', '13', 'Gdzie znalazszy bracią, uprosili nas, żechmy u nich przez siedm dni zmieszkali. I tak żechmy przyszli do Rzymu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '14', '14', 'Stąd bracia usłyszawszy o nas, wyszli przeciwko nam aż do rynku Appjuszowego ku trzem domom gościnnym, które gdy ujźrzał Paweł, dzięki uczyniwszy Bogu, wziął dobre ufanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '15', '15', 'Zatym gdychmy przyszli do Rzyma, setnik oddał więźnie hetmanowi nad wojski, ale Pawłowi dopuszczono, aby sam mieszkał z służebnym, który miał straż nad nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '16', '16', 'I stało się, iż po trzecim dniu wezwał Paweł przedniejszych z Żydów i gdy się zeszli, rzekł do nich: Mężowie bracia, ja gdym nic nie uczynił przeciw ludowi i ustawam ojczystym, będąc związan w Jeruzalem, podanem jest w ręce Rzymianom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '17', '17', 'Którzy wywiedziawszy się pilnie o mnie, chcieli mię wypuścić, dlatego, że we mnie żadnego występku śmierci godnego nie było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '18', '18', 'Lecz gdy temu na odpór byli Żydowie, przymuszonem jest apelować do cesarza, nie przeto jakobych miał ocz przeciw narodowi memu skarżyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '19', '19', 'A przetoż dla tejem was przyczyny wezwał, abych się z wami ujźrzawszy rozmówił, abowiem dla nadzieje izraelskiej jestem tym łańcuchem związan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '20', '20', 'Tedy oni rzekli do niego: My anichmy wzięli listów o tobie z Żydostwa, ani nam żaden z braciej przyszedszy oznajmił, ani opowiedział o tobie nic złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '21', '21', 'A tak chcemy od ciebie słyszeć co też rozumiesz, abowiem o tej sekcie jawno nam jest, iż jej są wszędy na odpór.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '22', '22', 'I także mu postanowiwszy dzień, przyszło ich kniemu nie mało do gospody, którym z oświadczaniem wykładał królestwo Boże, a wywodząc im o Jezusie z zakonu Mojżeszowego i z proroków, od poranku aż do wieczora.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '23', '23', 'Tedy jedni zezwalali na to co powiedał, niektórzy zasię nie wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '24', '24', 'A gdy nie byli zgodni między sobą, rozeszli się. A Paweł rzekł jednoż to słowo: Zaisteć dobrze Duch święty powiedział przez Ezajasza proroka do ojców naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '25', '25', 'Mówiąc: Idź do ludu tego a powiedz: Uszyma usłyszycie, a nie zrozumiecie; a widząc ujźrzycie i nie upatrzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '26', '26', 'Abowiem zatyło serce ludu tego, a cięszko uszyma słuchali i zamrużyli oczy swe aby oczyma nie widzieli, a uszyma nie słyszeli i sercem nie zrozumieli i nie byli nawróceni, abych je uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '27', '27', 'Niechżeć wam tedy jawno będzie, iżeć poganom posłano jest to zbawienie Boże, a oni słuchać będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '28', '28', 'A gdy to rzekł, wyszli od niego Żydowie, mając mi&nbsp;ędzy sobą wielki poswarek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '29', '29', 'I mieszkał Paweł przez całe dwie lecie w swym najmie, a przyjmował wszytki, którzy przychodzili do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ac', '27', '30', '30', 'Opowiedając królestwo Boże i ucząc tych rzeczy, które są o Panu Jezusie, z wszelkim bespieczeństwem w mowie, a żaden mu nie przekazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '0', '0', 'Paweł sługa Jezusa Krystusa powołaniem Apostoł. Wyłączony ku przepowiedaniu Ewanjelijej Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '1', '1', '(Którą przed tym obiecał był przez proroki swoje w piśmiech świętych).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '2', '2', 'O synu swoim, który się stał z plemienia Dawidowego ile wedle ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '3', '3', 'A jest okazan Synem Bożym mocnie wedle Ducha poświęcenia, przez zmartwychwstanie, to jest o Jezusie Krystusie Panie naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '4', '4', 'Przez którego wzięliśmy łaskę i urząd Apostolski ku posłuszeństwu wiary między wszytkimi narody dla imienia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '5', '5', 'Między którymi i wy jesteście wezwani Jezu Krystowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '6', '6', 'Wszytkim, którzy jesteście w Rzymie Bogu miłym, wezwanym abyście byli świętymi, łaska niech wam będzie i pokój od Boga Ojca naszego i Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '7', '7', 'Naprzód dziękuję Bogu mojemu przez Jezusa Krystusa za was wszytkich, iż wiara wasza bywa opowiedana po wszytkim świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '8', '8', 'Abowiem Bóg, któremu ja służę duchem moim w Ewanjelijej Syna jego jest mi świadek, iż ustawicznie o was wspominanie czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '9', '9', 'Zawsze w modlitwach moich prosząc abym do was przyszedł, jeśliby mi się którym sposobem szczęśliwa droga zdarzyła z wolej Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '10', '10', 'Abowiem pragnę was widzieć, abym wam użyczył jakiego daru duchownego ku utwierdzeniu waszemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '11', '11', 'To jest abych był uczestnikiem spólnego napominania z wami przez społeczną wiarę waszę i moję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '12', '12', 'Przytym nie chcę abyście wiedzieć nie mieli bracia, iżem często umyślił przyjdź do was (alem jest do tąd zawściągnion), abych miał jaki pożytek i między wami. Jako i między inszemi pogany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '13', '13', 'I Grekom i prostym ludziam i mądrym i głupim powinien jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '14', '14', 'A przeto ile ze mnie być może, gotowem jest Ewanjeliję opowiedać i wam, którzy jesteście w Rzymie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '15', '15', 'Abowiem nie wstydam się Ewanjelijej Krystusowej, gdyż jest moc Boża ku zbawieniu każdemu wierzącemu. Żydowi naprzód, potym też i Grekowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '16', '16', 'Abowiem sprawiedliwość Boża przez onę bywa objawiona z wiary w wiarę, jako jest napisano: Sprawiedliwy z wiary będzie żył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '17', '17', 'Bo gniew Boży jawny jest z nieba przeciwko wszelakiej niepobożności i niesprawiedliwości ludzkiej, którzy zatrzymawają prawdę niesprawiedliwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '18', '18', 'Przeto, iż co może być wiedziano o Bogu, jest w nich jawno, gdyż im to Bóg objawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '19', '19', 'Abowiem niewidome rzeczy jego, gdy przez stworzone rzeczy zrozumiane bywają, z stworzenia świata bywają upatrowane, to jest wieczna jego moc i bóstwo dlatego, aby oni nie mieli wymówki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '20', '20', 'Przeto, iż znając Boga, przedsię go nie chwalili jako Boga, ani mu dziękowali. Owszem próżnemi się stali w myślach swych i zaćmione jest głupie serce ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '21', '21', 'Którzy mieniąc się być mądrymi, głupimi się stali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '22', '22', 'Gdyż odmienili chwałę nieśmiertelnego Boga, sprawiwszy obraz człowieka śmiertelnego i ptaków i źwierząt o czterech nogach i gadziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '23', '23', 'A przetoż podał je Bóg pożądliwościam serc ich na nieczystość, aby między sobą ciała swe lżyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '24', '24', 'Dlatego, iż oni prawdę Bożą odmienili w kłamstwo i chwalili a służyli rzeczam stworzonym, mimo stworzyciela, który jest błogosławiony na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '25', '25', 'A przetoż podał je Bóg pożądliwościam sromotnym, gdyż niewiasty ich odmieniły używanie przyrodzone w to, które jest przeciw przyrodzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '26', '26', 'Także i mężczyzny opuściwszy przyrodzone używanie niewiasty, zapalili się pożądliwością swoją jeden ku drugiemu. Mężczyzna z mężczyzną sromotę popełniając, a zapłatę słuszną błędu swego na się biorąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '27', '27', 'A jako się im nie zdało aby Boga poznali, także też podał je Bóg w myśl bezrozumną, aby czynili to co się nie godzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '28', '28', 'Napełnieni wszelakiej niesprawiedliwości, nieczystoty, złości, łakomstwa, niecnoty, pełni zazdrości, morderstwa, swaru, zdrady, przewrotności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '29', '29', 'Zausznicy, obmowce, nienawidzący Boga, gwałtownicy, pyszni, chlubni, wynalazce złości, nieposłuszni rodzicom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '30', '30', 'Bezrozumni, nie dzierżący umowy, bez miłości przyrodzonej, nieprzejednani, niemiłosierni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '0', '31', '31', 'Którzy znając prawo Boże, iż ci co takowe rzeczy czynią, godni są śmierci, nie tylko sami ty rzeczy czynią, ale też zezwalają tym, którzy je czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '0', '0', 'A przetoż wymówki nie masz o człowiecze, którykolwiek ine posądzasz , abowiem tym, iż drugiego sądzisz, samego siebie osądzasz, gdyż toż czynisz ine posądzając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '1', '1', 'A my wiemy, iż sąd Boży jest wedle prawdy przeciwko tym, którzy takowe rzeczy czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '2', '2', 'I mnimasz ty o człowiecze, który posądzasz ty co takowe rzeczy czynią i toż działasz, żebyś miał ujdź sądu Bożego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '3', '3', 'Abo gardzisz obfitością dobroci jego i cierpliwości i długiego oczekiwania nie wiedząc, iż dobroć Boża ku upamiętaniu ciebie przywodzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '4', '4', 'Ale podług zatwardziałości twej i serca nieupamiętałego skarbisz sobie samemu gniew w dzień gniewu i zjawienia sprawiedliwego sądu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '5', '5', 'Który odda każdemu podług uczynków jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '6', '6', 'Tymci, którzy trwają, dobrego uczynku chwałę, cześć i nieśmiertelność, to jest tym, którzy szukają żywota wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '7', '7', 'Zasię swarliwym i którzy nie są posłuszni prawdzie, ale słuchają niesprawiedliwości, oddana będzie popędliwość i gniew.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '8', '8', 'Utrapienie i ucisk przeciwko każdej duszy człowieka, który złość popełnia. Żyda naprzód, potym Greka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '9', '9', 'A chwała i cześć i pokój wszelkiemu, który czyni dobrze. Naprzód Żydowi, potym i Grekowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '10', '10', 'Abowiem nie masz braku osoby u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '11', '11', 'Gdyż, którzy jedno bez zakonu zgrzeszyli, bez zakonu poginą, a którzy w zakonie zgrzeszyli, przez zakon będą osądzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '12', '12', 'Abowiem nie ci, którzy słuchają zakonu są sprawiedliwymi u Boga, ale ci, którzy zakon pełnią będą usprawiedliwieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '13', '13', 'Bo gdyż pogani, którzy zakonu nie mają, z przyrodzenia czynią to, co jest zakonnego. Ci zakonu nie mając, sami sobie są zakonem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '14', '14', 'Którzy okazują uczynek zakonu być napisany w sercach swych, a sumnienie ich wespół im świadczy i myśli ich wespół siebie oskarżające, abo też wymawiające.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '15', '15', 'Dnia, którego Bóg osądzi tajemnice ludzkie, według Ewanjelijej mojej przez Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '16', '16', 'Oto ciebie nazywają Żydem i przestawasz na zakonie a chlubisz się w Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '17', '17', 'I znasz wolą jego i rozeznawasz rzeczy, które są różne od niej, będąc nauczony z zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '18', '18', 'Który dufasz, iżeś jest wodzem ślepych i światłością tych, którzy są w ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '19', '19', 'Nauczycielem nieumiejętnych, mistrzem niemowniątek, mając sposób wiadomości i prawdy przez zakon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '20', '20', 'Który tedy uczysz drugiego, siebie samego nie uczysz, który przepowiedasz, iż nie mają kraść, ty kradniesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '21', '21', 'Który powiedasz, iż nie mają cudzołożyć, cudzołożysz; który się brzydzisz bałwany, świętokradziestwo pełnisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '22', '22', 'Który się z zakonu chlubisz, przestępowaniem zakonu Boga lżysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '23', '23', 'Abowiem imię Boże dla was bluźnione jest między pogany, jako napisano jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '24', '24', 'Boć obrzezanie jest pożyteczne jeśli zachowasz zakon, a jeślibyś był przestępcą zakonu, twoje obrzezanie stało się nieobrzezaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '25', '25', 'Jeśli tedy nieobrzezany chowa prawa zakonne, a zaź jego nieobrzezanie nie będzie poczytane za obrzezanie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '26', '26', 'A jeśli nieobrzezany z przyrodzenia chowa zakonu, a zaź cię nie osądzi, który przez Pismo i obrzezanie przestępcą zakonu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '27', '27', 'Abowiem nie ten, który tylko jawnie będzie Żydem, ani obrzezanie ciała, które jest tylko jawnie, obrzezaniem jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '1', '28', '28', 'Ale który jest w skrytości Żydem i obrzezanie serca, to jest, w duchu nie w piśmie, którego chwała nie jest z ludzi ale z Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '0', '0', 'Cóż tedy za godność ma Żyd, abo co za pożytek obrzezania?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '1', '1', 'Wielka wszelakim sposobem, abowiem naprzedniejsza to jest rzecz, iż im zwierzone są słowa Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '2', '2', 'Bo cóż na tym, jeśli niektórzy byli niedowiarki, a zaź niedowiarstwo ich wynicuje wiarę Bożą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '3', '3', 'Nie daj tego Boże i owszem niech Bóg będzie prawdziwy, a wszelki człowiek kłamcą. Jako napisano jest: Abyś był nalezion sprawiedliwym w powieściach twoich i abyś zwyciężył, gdy bywasz sądzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '4', '4', 'A jeśli niesprawiedliwość nasza Bożą sprawiedliwość zaleca, cóż rzeczemy? A zaź niesprawiedliwy Bóg, który karanie przepuszcza? (mówię wedle człowieka).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '5', '5', 'Nie takci jest, bo inaczej jakoż Bóg sądzić będzie świat?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '6', '6', 'Abowiem jeśli prawda Boża przez moje kłamstwo obfitowała ku chwale jego, czemu ja dalej bywam osądzon jako grzeszny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '7', '7', 'A (jako źle o nas mówią i jako niektórzy powiedają, iż mówiemy) czemuż złych rzeczy nie czynimy, aby przyszły dobre? Których potępienie jest sprawiedliwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '8', '8', 'Cóż tedy, a zaź my jesteśmy zacniejszymi? Żadnym sposobem, gdyżechmy przedtym przeparli Żydy i Greki, iż szyscy są pod grzechem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '9', '9', 'Jako napisano jest: Nie masz żadnego sprawiedliwego, aż do jednego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '10', '10', 'Nie jest żaden, któryby rozumiał, a nie masz, ktoby Boga szukał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '11', '11', 'Wszyscy odstąpili i społu stali się niepożytecznymi. Nie masz ktoby dobrze czynił, nie masz aż do jednego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '12', '12', 'Gardło ich jest grobem otwartym. Języków swych używali na zdradę, a pod wargami ich jest jad aspidów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '13', '13', 'Których usta napełnione są przeklinania i gorzkości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '14', '14', 'Nogi ich są prędkie ku rozlaniu krwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '15', '15', 'Spustoszenie i utrapienie jest na drogach ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '16', '16', 'A drogi pokoju nie poznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '17', '17', 'Bojaźń Boża nie jest przed oczyma ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '18', '18', 'A my wiemy, iż cokolwiek zakon mówi, tym którzy w zakonie są mówi, aby wszelkie usta były zatulone i aby był winien wszytek świat sądu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '19', '19', 'Przeto żadne ciało nie będzie usprawiedliwione przed oblicznością jego z uczynków zakonu, gdyż przez zakon bywa poznanie grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '20', '20', 'Ale teraz sprawiedliwość Boża jest objawiona przez zakon, poświadczona świadectwem zakonu i proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '21', '21', 'Sprawiedliwość mówię Boża, przez wiarę Jezusa Krystusa, wszytkim i na wszytki, którzy wierzą, gdyż nie masz różności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '22', '22', 'Abowiem wszyscy zgrzeszyli i nie dostawa im chwały Bożej');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '23', '23', 'Ale usprawiedliwieni są darmo, to jest z łaski jego, przez odkupienie sprawione przez Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '24', '24', 'Którego Bóg postanowił, aby był ubłaganiem przez wiarę, wylawszy krew swoję ku okazaniu sprawiedliwości swojej, dla odpuszczenia grzechów przeszłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '25', '25', 'Z cierpliwości Bożej, aby się okazała sprawiedliwość terazniejszego czasu, aby on był sprawiedliwy i usprawiedliwiający tego, który jest z wiary Jezusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '26', '26', 'Gdzież tedy chluba? Wyrzucona jest. Przez który zakon? Uczynkowyli? Nie, ale przez zakon wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '27', '27', 'My tedy dowodzimy, iż człowiek bywa usprawiedliwion przez wiarę, oprócz uczynków zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '28', '28', 'Izali Bóg jest tylko Żydów? Izaź nie jest i poganów? Iścieć i poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '29', '29', 'Abowiem jeden jest Bóg, który usprawiedliwi obrzezanie z wiary i nieobrzezanie przez wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '2', '30', '30', 'Zakon tedy nicniemy przez wiarę? Bynamniej! Ale zakon stanowimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '0', '0', 'Cóż tedy rzeczemy, iż Abraam ociec nasz otrzymał wedle ciała?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '1', '1', 'Abowiem jeśli Abraam z uczynków był usprawiedliwion, ma się z czego chlubić, ale nie przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '2', '2', 'Abowiem cóż Pismo mówi ? Uwierzył Abraam Bogu i poczytano mu jest ku sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '3', '3', 'A temu co robi, zapłata nie jest poczytana za łaskę, ale za powinną rzecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '4', '4', 'Temu zaś co nie robi, ale wierzy w tego, który usprawiedliwia niepobożnego, jest poczytana wiara jego za sprawiedliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '5', '5', 'Jako też Dawid opowieda: szczęśliwość człowieka, któremu Bóg poczyta sprawiedliwość bez uczynków mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '6', '6', 'Błogosławieni są, których nieprawości są odpuszczone, a których zakryte są grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '7', '7', 'Błogosławiony ten, któremu Pan grzechu nie poczyta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '8', '8', 'To tedy błogosławieństwo izaź w obrzezaniu tylko jest, abo też nie jest w nieobrzezaniu? Gdyż powiedamy, iż poczytana jest Abraamowi wiara za sprawiedliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '9', '9', 'Jakoż mu tedy jest poczytana? Gdy był w obrzezaniu, abo gdy był w nieobrzezaniu? Nie w obrzezaniu, ale w nieobrzezaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '10', '10', 'I wziął znak obrzezania jako pieczęć sprawiedliwości wiary, która była w nieobrzezaniu, aby był ojcem wszytkich wierzących w nieobrzezaniu, iżby im też poczytana była sprawiedliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '11', '11', 'I ojcem obrzezania tym, którzy nie tylko są z obrzezania, ale którzy też chodzą w tyż stopy wiary ojca naszego Abraama, która była w nieobrzezaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '12', '12', 'Abowiem nie przez zakon obietnica się zstała Abraamowi, abo plemieniu jego, aby był dziedzicem świata, ale przez sprawiedliwość wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '13', '13', 'Bo jeśli ci, którzy są z zakonu dziedzicami są, tedyć próżna została wiara, a w niwecz się obróciła obietnica.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '14', '14', 'Gdyż zakon gniew sprawuje, abowiem gdzie zakonu nie masz, tam i przestępstwa nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '15', '15', 'A przeto z wiary jest dziedzictwo, aby było z łaski, żeby była trwała obietnica wszytkiemu plemieniowi, to jest nie tylko temu, które jest z zakonu, ale i temu, które jest z wiary Abraamowej, który jest ociec nas wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '16', '16', '(Jako jest napisano: Ojcem wiela narodów postanowiłem cię) a to przed tym Bogiem, któremu on uwierzył, ożywiającym umarłe i wzywającym ty rzeczy, które nie są, jakoby były.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '17', '17', 'Który Abraam przeciw nadziei pod nadzieją uwierzył, aby się zstał ojcem wiela narodów, podług tego, co mu było rzeczono: Tak będzie plemię twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '18', '18', 'A nie będąc zemdlonym w wierze, nie oglądał się na swe ciało już zmartwiałe, gdyż miał około sta lat, ani na zmartwiały żywot Sary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '19', '19', 'I przeciwko obietnicy Bożej nie swarzył się niedowiarstwem, ale się zstał mocnym w wierze i dał chwałę Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '20', '20', 'A prawdziwie wiedząc, iż on co był obiecał, mógł też i uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '21', '21', 'Dlatego i to mu poczytano za sprawiedliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '22', '22', 'A nie napisano jest dla niego tylko, iż to mu przyczytano jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '23', '23', 'Ale też dla nas, którym ma być poczytano, cobychmy uwierzyć mieli w tego, który wzbudził Jezusa Pana naszego zmartwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '3', '24', '24', 'Który był wydan na śmierć dla upadków naszych, a wzbudzon jest dla usprawiedliwienia naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '0', '0', 'Będąc tedy przez wiarę usprawiedliwieni, pokój u Boga mamy przez Pana naszego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '1', '1', 'Przez któregośmy też wiarą otrzymali przystęp ku tej łasce, przez którą stoimy i chlubimy się w nadziei chwały Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '2', '2', 'A nie tylko to, ale się też i w uciskach chlubimy wiedząc, iż ucisk cierpliwość sprawuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '3', '3', 'A cierpliwość doświadczenie, doświadczenie nadzieję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '4', '4', 'A nadzieja nie pohańbia przeto, iż miłość Boża jest wylana w sercach naszych przez Ducha świętego, który nam jest dan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '5', '5', 'Abowiemci Krystus, gdyśmy my jeszcze żadnej mocy nie mieli, czasu swego umarł za niepobożne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '6', '6', 'Zaisteć ledwieby kto umarł za sprawiedliwego, abowiem snadźby też kto śmiał umrzeć za dobrodzieja swojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '7', '7', 'Ale Bóg zaleca miłość swoję ku nam, że gdyśmy my jeszcze byli grzesznymi, Krystus za nas umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '8', '8', 'Będąc tedy usprawiedliwieni krwią jego, daleko więcej teraz będziemy przezeń od gniewu zachowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '9', '9', 'Bo jeśliże będąc nieprzyjacielami, jestechmy zjednani z Bogiem przez śmierć Syna jego, daleko więcej będąc zjednani, będziemy przez żywot jego zachowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '10', '10', 'A nie tylko to, ale się też i chlubimy w Bogu przez Pana naszego Jezusa Krystusa, przez któregośmy teraz zjednanie otrzymali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '11', '11', 'A dlategoż, jako przez jednego człowieka grzech na świat wszedł, a przez grzech śmierć i także na wszytki ludzi śmierć przeszła, w którym wszyscy zgrzeszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '12', '12', 'Abowiem aż do zakonu grzech był na świecie, a grzech nie bywa przyczytan, gdy zakonu nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '13', '13', 'Ale śmierć królowała od Adama aż do Mojżesza nad tymi też, którzy byli nie zgrzeszyli na podobieństwo przestępstwa Adamowego, który jest kształtem onego, który miał przyjdź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '14', '14', 'A wszakoż dar nie jest jako przestępstwo, abowiem jeśli przestępstwem jednego wiele ich pomarło, dalekoć więcej na wiele ich opłynęła łaska Boża i dar z łaski, która jest jednego człowieka Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '15', '15', 'A dobodziejstwo nie takowe jest jako śmierć co przyszła przez jednego, który zgrzeszył, gdyż wina jednego występku przyszła ku potępieniu. Ale dar bywa dan z wiela występków ku usprawiedliwieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '16', '16', 'Abowiem jeśli jednego przestępstwem śmierć królowała przez jednego, daleko więcej ci, którzy obfitą onę łaskę i dar sprawiedliwości biorą, w żywocie królować będą przez jednego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '17', '17', 'A także tedy jako przez jedno przestępstwo zstali się wszyscy ludzie winni potępienia, także przez jednego usprawiedliwienie, dobrodziejstwo opłynęło na wszytki ludzi, ku usprawiedliwieniu żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '18', '18', 'Bo jako przez nieposłuszeństwo jednego człowieka wiele ich zstało się grzesznymi, także przez posłuszeństwo jednego wiele ich zstanie się sprawiedliwymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '19', '19', 'A zakonci ktemu przyszedł, aby się mnożyło przestępstwo, ale gdzie się grzech rozmnożył, tam łaska obfitowała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '4', '20', '20', 'Aby jako grzech królował ku śmierci, tak też aby łaska królowała przez sprawiedliwość ku żywotowi wiecznemu, przez Jezusa Krystusa Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '0', '0', 'Cóż tedy rzeczemy? Będziemyli w grzechu trwać, aby się łaska obfitsza stała?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '1', '1', 'Nie daj tego Boże, abowiem którzyśmy umarli grzechowi, jakoż dalej w nim żyć będziemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '2', '2', 'Azaź nie wiecie, iż ilekolwiek nas w Krystusa Jezusa jest okrzczonych, jesteśmy okrzczeni w śmierci jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '3', '3', 'Jesteśmy tedy z nim pospołu pogrzebieni w śmierci jego przez krzest, aby jako Krystus wzbudzon jest zmartwych ku chwale Ojcowskiej, takżebyśmy też i my w nowości żywota chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '4', '4', 'Bo jeśliżeśmy społu rośli podobieństwem śmierci jego, iście też i podobieństwem zmartwychwstania jego społu róść będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '5', '5', 'Wiedząc to, że stary on nasz człowiek pospołu jest z nim ukrzyżowan, aby ciało grzechu było zniszczone, żebyśmy już dalej nie służyli grzechowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '6', '6', 'Boć ten, który umarł, jest od grzechu wolny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '7', '7', 'A jeśliżeśmy z Krystusem umarli, wierzymy iż też z nim żyć będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '8', '8', 'Wiedząc, że Krystus powstawszy zmartwych, już dalej nie umiera, a śmierć nad nim dalej nie panuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '9', '9', 'Bo iż umarł, grzechowi raz umarł. A iż żywie, żywieć Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '10', '10', 'Tak też i wy rozumiejcie się być umarłymi grzechowi, a żywemi Bogu, przez Krystusa Jezusa Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '11', '11', 'Niechajże tedy nie króluje grzech w waszym śmiertelnym ciele, abyście grzechowi posłuszni byli w żądzach cielesnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '12', '12', 'Ani stawcie członków waszych, aby były zbroją niesprawiedliwości grzechowi, ale stawcie siebie samych Bogu jako z umarłych żywymi i członki wasze, aby były zbroją sprawiedliwości Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '13', '13', 'Abowiemci grzech nad wami panować nie będzie, bo nie jesteście pod zakonem, ale pod łaską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '14', '14', 'Cóż tedy? Będziemyli grzeszyć, że nie jesteśmy pod zakonem, ale pod łaską? Nie daj tego Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '15', '15', 'Zaź nie wiecie, że komu się stawicie sługami ku posłuszeństwu, tego jesteście słudzy. Komu jesteście posłuszni, choć grzechu na śmierć, choć posłuszeństwu ku sprawiedliwości?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '16', '16', 'Ale niech będzie dzięki Bogu, że chociaście byli sługami grzechu, usłuchaliścież z serca sposobu onej nauki, pod którą jesteście poddani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '17', '17', 'A będąc wyswobodzeni od grzechu, zstaliście się sługami sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '18', '18', 'Ludzkimci obyczajem mówię dla mdłości ciała waszego, abowiem jakoście stawili członki wasze sługami nieczystości i nieprawości, ku wypełnieniu nieprawości, tak też i teraz stawcie członki wasze sługami sprawiedliwości, ku świątobliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '19', '19', 'Bo gdyście byli sługami grzechu, byliście wolnymi sprawiedliwości, któryżeście tedy na on czas pożytek mieli? Tych rzeczy, za które się dziś wstydzicie? Boć koniec ich jest śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '20', '20', 'A teraz będąc wyswobodzeni od grzechu, a zstawszy się służebniki Bogu, macie pożytek wasz ku świątobliwości, a koniec żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '5', '21', '21', 'Abowiem zapłata grzechu jest śmierć, a dar Boży jestci żywot wieczny, przez Krystusa Jezusa Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '0', '0', 'Abo nie wiecie bracia (boć z uczonymi w zakonie mówię), iż zakon panuje człowiekowi pókiby żyw był?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '1', '1', 'Abowiemci niewiasta, która ma męża, jest zakonem mężowi obowiązana póki on żyw. A jeśliby mąż umarł, jest wyswobodzona od zakonu mężowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '2', '2', 'A tak póki mąż żyw, będzie zwana cudzołożnicą, jeśliby męża inszego żoną została. A jeśliby mąż umarł, wolna jest od zakonu onego, aby nie była cudzołożnicą, jeśliby inszego męża żoną została.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '3', '3', 'A także moi bracia i wy też jesteście umartwieni zakonowi w ciele Krystusowym, abyście byli przyłączeni inszemu, to jest temu, który powstał zmartwych, abyśmy owoc przynieśli Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '4', '4', 'Abowiem gdyśmy byli w ciele, pożądliwości grzechów, które się zstały przez zakon, moc miały w członkach naszych, ku przynoszeniu owocu na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '5', '5', 'A teraz jesteśmy wolni od zakonu, gdyż ten umarł, w którymeśmy byli zatrzymani, abyśmy służyli w nowości ducha, a nie w starości pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '6', '6', 'Cóż tedy rzeczemy? Zakon jestli grzechem? Nie daj tego Boże. Owszem grzechu nie poznał jedno przez zakon, bobych też i pożądliwości nie poznał, by był zakon nie rzekł: Nie będziesz pożądał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '7', '7', 'Ale grzech wziąwszy przyczynę, urodził we mnie wszelaką pożądliwość przez ono przykazanie, abowiem bez zakonu grzech jest martwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '8', '8', 'Bom ja niekiedy żył bez zakonu, a gdy przyszło ono roskazanie, grzech ożył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '9', '9', 'Alem ja umarł i nalazło się ono przykazanie, które było postanowione ku żywotowi, iż mi się obraca ku śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '10', '10', 'Gdyż grzech wziąwszy przyczynę zwiódł mię onym przykazaniem i przez nie zabił mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '11', '11', 'A także zakon sam jestci święty i ono przykazanie święte i sprawiedliwe i dobre.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '12', '12', 'To tedy co jest dobrego, izaź mi się zstało śmiercią? Nie daj tego Boże! Owszem grzech zstał mi się śmiertelny, gdyż aby się okazał być grzechem, na mię śmierć przywiódł, przez to co jest dobrego, to jest aby on sam zstał się nad miarę grzeszący, przez ono przykazanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '13', '13', 'Bo wiem, iż zakon jest duchowny, ale ja cielesny jestem, zaprzedany pod grzech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '14', '14', 'Abowiem co czynię, nie chwalę, gdyż nie czynię tego co chcę, ale czego nienawidzę to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '15', '15', 'A jeśli czego nie chcę to czynię, przyzwalamci zakonowi, że dobry jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '16', '16', 'Teraz tedy już nie ja to czynię, ale grzech, który we mnie mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '17', '17', 'Gdyż wiem, iż nie mieszka we mnie, to jest w ciele moim, nic dobrego. Abowiem mamci wolą, ale abym to czynił ci jest dobrego, nie najduję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '18', '18', 'Bo nie czynię dobrego, którego chcę, ale czego nie chcę złego, to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '19', '19', 'A jeśli to czynię czego nie chcę, już nie ja to czynię, ale grzech, który we mnie mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '20', '20', 'Znajduję tedy, gdy chcę dobrze czynić, ten być zakon, że mi przylega złość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '21', '21', 'Abowiem raduję się zakonowi Bożemu wedle wnętrznego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '22', '22', 'Ale widzę inszy zakon w członkach moich, odporny zakonowi umysłu mego i który mię czyni więźniem zakonowi grzechu, który jest w członkach moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '23', '23', 'Nędzny ja człowiek, któż mię wybawi z tego ciała śmierci?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '6', '24', '24', 'Dziękuję Bogu przez Jezusa Krystusa Pana naszego. Jać tedy umysłem służę zakonowi Bożemu, a ciałem zakonowi grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '0', '0', 'Teraz tedy żadne nie jest potępienie tym, którzy są w Jezusie Krystusie, to jest, którzy nie wedle ciała chodzą, ale według ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '1', '1', 'Abowiem zakon ducha żywota, który jest w Krystusie Jezusie wyswobodził mię od zakonu grzechu i śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '2', '2', 'Bo mdłość zakonu, gdy nie mogła nic w ciele, Bóg posławszy własnego Syna swego w podobieństwie grzesznego ciała, a to dla grzechu, potępił grzech w ciele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '3', '3', 'Aby ono prawo zakonne było wypełnione w nas, którzy nie podług ciała chodzimy, ale według ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '4', '4', 'Abowiem którzy są wedle ciała, bawią się rzeczami cielesnymi, a którzy są wedle ducha, rzeczami duchownemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '5', '5', 'Gdyż zrozumienie cielesne jest śmierć, a rozumienie duchowne jest żywot i pokój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '6', '6', 'Przeto, iż rozumienie ciała jest nieprzyjacielstwem przeciwko Bogu, bo nie bywa poddane zakonowi Bożemu, gdyż też ani być może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '7', '7', 'A tak, którzy są w ciele, Bogu się podobać nie mogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '8', '8', 'Wy tedy nie jesteście w ciele, ale w duchu, gdyż Duch Boży mieszka w was. A jeśli kto Ducha Krystusowego nie ma, tenci nie jest jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '9', '9', 'A jeśli Krystus w was jest, ciało jestci martwe dla grzechu, a Duch jest żywot dla sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '10', '10', 'A jeśli Duch onego, który Jezusa wzbudził zmartwych w was mieszka ten, który wzbudził Krystusa z martwych, ożywi i ciała wasze śmiertelne przez Ducha swojego, który w was mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '11', '11', 'A także bracia powinni jesteśmy nie ciału, abyśmy podług ciała żyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '12', '12', 'Abowiem jeślibyście według ciała żyli, pomrzecie, ale jeśli Duchem sprawy ciała umartwicie, żyć będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '13', '13', 'Bo wszyscy, którzy Duchem Bożym bywają rządzeni, ci są synami Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '14', '14', 'Gdyżeście nie wzięli ducha niewolstwa ku bojaźni, ale wzięliście ducha przysposobienia, przez którego wołamy Abba, to jest Ociec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '15', '15', 'Który Duch sam świadczy społu i z duchem naszym, iż jesteśmy syny Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '16', '16', 'A jeśli synami, tedy też i dziedzicmi, dziedzicy Boży mówię, a społu dziedzicy z Krystusem, jeśli z nim cierpimy, abyśmy i z nim byli uwielbieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '17', '17', 'Abowiem rozumiem, iż nie są równe utrapienia, które czasu tego cierpimy przyszłej chwale, która się nam objawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '18', '18', 'Bo rzeczy stworzone z wielką chucią oczekiwania, ażby się okazali synowie Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '19', '19', 'Gdyż próżności poddane są rzeczy stworzone, nie dobrowolnie, ale dlatego, który je poddanemi uczynił pod nadzieją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '20', '20', 'Dlatego, iż ony będą wyswobodzone z niewolstwa skażenia, na wolność chwały synów Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '21', '21', 'Gdyż wiemy, iż wszytki rzeczy stworzone społu wzdychają i społu boleją, aż i do czasu tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '22', '22', 'A nie tylko ony rzeczy, ale i ci, którzy mają początki ducha, to jest my sami w sobie wzdychamy, oczekiwając przysposobienia synów, to jest odkupienia ciała naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '23', '23', 'Abowiem nadzieją jesteśmy zachowani, a nadzieja jeśliby widziana była, nie jestci nadzieją. A co kto widzi przeczżeby się tego nadziewał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '24', '24', 'Ale jeśli się tego nadziewamy czego nie widzimy, iście z cierpliwością oczekawamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '25', '25', 'Także też i duch dopomaga krewkościam naszym, abowiem tego o co byśmy prosili, jakoby potrzeba, nie wiemy, ale sam duch prosi za nami wzdychaniem niewymówionym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '26', '26', 'A ten, który się wywiaduje serc wieć, który jest umysł ducha, iż według Boga prosi za świętymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '27', '27', 'Wiemy też, że tym, którzy miłują Boga, wszytki rzeczy społu dopomagają ku dobremu, to jest tym, którzy są według naznaczenia jego wezwani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '28', '28', 'Abowiem które on przed tym poznał, ty też przed tym naznaczył, aby się zstali podobnymi wyobrażeniu Syna jego, żeby on był pierworodnym między wielem braciej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '29', '29', 'A które on przed tym naznaczył, tych też wezwał, a których wezwał, ty też usprawiedliwił, a które usprawiedliwił, ty też i uwielbił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '30', '30', 'Cóż tedy rzeczemy na to? Jeśli Bóg za nami, któż przeciwko nam?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '31', '31', 'Który też własnemu Synowi nie sfolgował, ale go za nas wszytki wydał na śmierć. Jakoż też nam wszytkich rzeczy z nim nie daruje?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '32', '32', 'Któż będzie skarżył na wybrane Boże? Bógci jest, który usprawiedliwia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '33', '33', 'Któryż jest coby potępił? Krystus jestci tym, który umarł i owszem też, który powstał i który jest na prawicy Bożej, który jest na nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '34', '34', 'Któż nas odłączy od miłości Krystusowej? Izali utrapienie? Izali ucisk ? Izali prześladowanie? Izali głód? Izali nagość? Izali niebespieczeństwo? Izali miecz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '35', '35', 'Jako napisano jest: Dla ciebie cały dzień zabijani bywamy, byliśmy poczytani jako owce na zabicie naznaczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '36', '36', 'Ale w tym wszytkim przezwyciążamy przez tego, który nas umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '37', '37', 'Abowiem mocnie wierzę, iż ani śmierć, ani żywot, ani Aniołowie, ani zwierzchności, ani przełożeństwa, ani teraźniejsze rzeczy, ani przyszłe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '7', '38', '38', 'Ani wysokość, ani głębokość, ani żadna insza rzecz stworzona nie będzie mogła nas odłączyć od miłości Bożej, która jest w Jezusie Krystusie Panie naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '0', '0', 'Prawdęć mówię przez Krystusa, nie kłamamci, w czym mi poświadcza sumnienie moje przez Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '1', '1', 'Że mam wielką żałość i serce moje ustawiczną boleść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '2', '2', 'Abowiem ja sam żądałbych odłączonym być od Krystusa za bracią moję to jest, za krewne moje według ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '3', '3', 'Którzy są Izraelczycy, a których jest sposobienie synów i chwała i postanowienie zakonu i służba Boża i obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '4', '4', 'Których ojcowie są i ci co z nich poszedł Krystus według ciała, który jest nad wszytkimi Bóg błogosławiony na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '5', '5', 'Ale to nie może być żeby w niwecz poszło słowo Boże, abowiemci nie wszyscy są Izraelem, którzy z Izraela poszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '6', '6', 'Ani wszyscy są synami, że są plemieniem Abraamowym, ale w Izaaku będzie tobie plemię nazwane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '7', '7', 'To jest, nie którzy są synowie ciała, ci też są synowie Boży, ale którzy są synami obietnice, ci bywają w plemię policzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '8', '8', 'Abowiem słowa obietnice ty są: W ten to czas przyjdę, a będzie Sara syna miała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '9', '9', 'A nie tylko to, ale też i Rebbeka zastąpiwszy w brzemię z jednego, to jest z Izaaka ojca naszego syny miała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '10', '10', 'Abowiem gdy się były jeszcze nie narodziły dziatki, ani co dobrego abo złego uczyniły, aby mocne zostało naznaczenie Boże, które jest według wybrania jego, to jest, nie z uczynków, ale z tego, który wzywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '11', '11', 'Rzeczono jej, że więtszy będzie służył mniejszemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '12', '12', 'Jako jest napisano: Jakubam umiłował, a Ezawam nienawidział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '13', '13', 'Cóż tedy rzeczemy? I jest że niesprawiedliwość u Boga? Nie daj tego Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '14', '14', 'Abowiemci Mojżeszowi mówi: Zmiłuję się nad kim się zmiłuję, a zlituję się nad kim się zlituję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '15', '15', 'A tedyć wybranie nie jest tego, któryby chciał, ani tego, któryby biegał, ale Boga, który się zmiłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '16', '16', 'Abowiemci mówi Pismo Faraonowi: Na tom cię pobudził, abych okazał moc moję w tobie i żeby było opowiedane imię moje po wszytkiej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '17', '17', 'Nad kimci tedy chcę, nad tym się zmiłuję, a kogo chcę, tego zatwardzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '18', '18', 'Rzeczesz mi tedy: Przeczże się już gniewa? Bo któż się sprzeciwił wolej jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '19', '19', 'I owszem o człowiecze, którześ ty jest, który odpowiedasz naprzeciwko Bogu? Zali rzecz ulepiona powie temu, który ją ulepił: Przecześ mię tak uczynił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '20', '20', 'Zali garncarz gliny w mocy nie ma, ale z tejże sztuki uczynił jedno naczynie na uczciwość, a drugie na zelżywość?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '21', '21', 'A jeśli też Bóg chcąc gniew okazać i znaczną uczynić możność swoję, zniósł wielką cierpliwością naczynia gniewu, na stracenie przyprawione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '22', '22', 'A iżby wiadome uczynił bogactwo chwały swojej nad naczyniem miłosierdzia, które ku chwale zgotował;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '23', '23', 'To jest nas, których też i wezwał, nie tylko z Żydów, ale też i z poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '24', '24', 'Jako też u Ozeasza mówi: Wzowę lud, który moim nie był, ludem moim i tę, która umiłowana nie była, umiłowaną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '25', '25', 'A będzie miasto tego gdzie im powiedziano: Wy nie jesteście ludem moim, tam je będą zwać syny Boga żywego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '26', '26', 'A Izajasz nad Izraelem woła: By liczba synów izraelskich była jako piasek morski, ostatek tylko zachowan będzie. Abowiem liczbę uczyni i krótko wyprawi sprawiedliwością, gdyż liczbę, którą uczyni Pan na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '27', '27', 'I jako przed tym powiedział Ezajasz: By był Pan zastępów nam plemienia nie zostawił, zostalibyśmy się jako Sodoma i bylibychmy byli Gomorze podobni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '28', '28', 'Cóż tedy rzeczemy? Iż pogani, którzy nie starali się o sprawiedliwość, dostali sprawiedliwość, a tej sprawiedliwości, która jest z wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '29', '29', 'A lud izraelski starając się o sprawiedliwość zakonu, nie przyszedł ku zakonowi sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '30', '30', 'Dla czegóż? Iż nie z wiary, ale jako z uczynków zakonu, abowiem się otrącili o kamień obrażenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '8', '31', '31', 'Jako napisano jest: Oto pokładam na Syjonie kamień obrażenia i skałę pogorszenia. A wszelki, który weń wierzy, zawstydzon nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '0', '0', 'Bracia, uprzejma chuć serca mego i prośba ku Bogu za ludem izraelskim jest, aby byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '1', '1', 'Abowiemci o nich świadczę, żeć chuć ku Bogu mają, ale nie według wiadomości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '2', '2', 'Bo sprawiedliwości Bożej nie znając i własną sprawiedliwość chcąc postanowić, sprawiedliwości Bożej nie byli poddani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '3', '3', 'Gdyż Krystus jest koniec zakonu ku sprawiedliwości wszelkiemu, który wierzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '4', '4', 'Abowiem Mojżesz opisuje sprawiedliwość, która jest z zakonu: Iż człowiek, któryby je czynił, będzie żył przez nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '5', '5', 'A sprawiedliwość, która jest z wiary tak mówi: Nie mów w sercu twoim, któż zstąpi w niebo? Toć jest Krystusa zwodzić z wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '6', '6', 'Abo kto zstąpi w przepaść? Toć jest Krystusa zasię zmartwych przywodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '7', '7', 'Ale cóż powieda? Blisko ciebie jest słowo w ustach twoich i w sercu twoim, toć jest słowo wiary, które przepowiedamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '8', '8', 'Że jeślibyś wyznał usty twoimi Pana Jezusa i uwierzył w sercu twoim, że go Bóg zmartwych wzbudził, będziesz zbawion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '9', '9', 'Abowiemci sercem wierzą ku sprawiedliwości, a usty wyznanie czynią ku zbawieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '10', '10', 'Gdyż Pismo powieda: Wszelki kto weń wierzy, pohańbion nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '11', '11', 'Abowiemci nie masz różności ani Żyda, ani Greka, bo tenże Pan wszytkich bogaty jest na wszytki, którzy go wzywają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '12', '12', 'Abowiem wszelki, któryby wzywał imienia Pańskiego, będzie zbawion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '13', '13', 'Jakoż tedy będą wzywali tego, w którego nie wierzyli? Jakoż zaś uwierzą temu, o którym nie słyszeli? A jako usłyszą oprócz przepowiedającego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '14', '14', 'Jakoż zasię będą przepowiedali, jeśliby nie byli posłani? Gdyż tak napisano jest: Jakoż piękne nogi tych, którzy pokój opowiedają. Którzy opowiedają dobre rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '15', '15', 'Aleć nie wszyscy usłuchali Ewanjelijej, abowiem Ezajasz mówi: Panie, któż uwierzył przepowiedaniu naszemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '16', '16', 'Wiara tedy jest z słuchania, a słuchanie przez słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '17', '17', 'Ale powiedam, zali nie słyszeli? I owszem na wszytkę ziemię wyszedł głos ich i na kończyny świata słowa ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '18', '18', 'Lecz powiedam, zali Izrael nie poznał Boga? Pirwszyć Mojżesz mówi: Ja was poruszę ku zazdrości przez naród, który nie jest narodem, przez naród nierozumny zagniewam was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '19', '19', 'A Ezajasz bierze śmiałość i mówi: Jestem nalezion od tych, którzy mię nie szukali, a zstałem się jawnym tym, którzy się o mnie nie pytali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '9', '20', '20', 'A przeciw Izraelowi mówi: Przez cały dzień rozszyrzałem ręce moje do ludu nieposłusznego i odpornego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '0', '0', 'Mówię tedy, izali Bóg odrzucił lud swój? Nie daj tego Boże. Abowiemci i jam jest Izraelczykiem z plemienia Abraamowego, z pokolenia Benjaminowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '1', '1', 'Nie odrzuciłci Bóg ludu swego, który on przed tym poznał. Azaź nie wiecie co Pismo o Heljaszu mówi? Jako rozmawia z Bogiem, mówiąc przeciwko Izraelowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '2', '2', 'Panie proroki twoje pobili i ołtarze twoje poburzyli, a jam sam został i szukają duszę mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '3', '3', 'Ale cóż mu odpowiedziano od Boga? Zostawiłem sobie siedm tysięcy mężów, którzy się nie kłaniali przed obrazem Baalowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '4', '4', 'Także tedy i czasu tego zstało się to zostawienie podług wybrania z łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '5', '5', 'A jeśli z łaski, jużci nie z uczynków, bo inaczej łaska już nie jest łaską. A jeśli z uczynków, już nie jest łaska bo inaczej uczynek już nie jest uczynkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '6', '6', 'Cóż tedy? Czego Izrael szuka, tego nie dostał; ale wybrani dostali, a drudzy zakamienieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '7', '7', 'Jako jest napisano: Dał im Bóg ducha strętfienia, oczy aby nie widziały i uszy aby nie słyszały, aż do dzisiejszego dnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '8', '8', 'A Dawid mówi: Niechaj im będzie stół ich miasto sidła i zasadki i otrącenia i równej nagrody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '9', '9', 'Niech zaćmione będą oczy ich, aby nie przejźrzeli, a skurcz zawżdy grzbiet ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '10', '10', 'Mówię tedy: Azaź się otrącili aby padli? Nie daj tego Boże, ale przez ich przestępstwo zstało się zbawienie poganom dlatego, aby je wzruszył ku zazdrości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '11', '11', 'A jeśli ich przestępstwo jest bogactwem świata, a umniejszenie ich bogactwem poganów, jakoż daleko więcej będzie ich napełnienie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '12', '12', 'Abowiem powiedam wam poganom, ilem jest Apostołem poganów, iż ja urząd mój zacnym czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '13', '13', 'Jeśliżbych, którym sposobem pobudził ku zazdrości powinowate moje i zachował niektóre z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '14', '14', 'Abowiem jeśli odrzucenie ich jest zjednanie świata, jakie będzie przyjęcie, jedno żywot zmartwych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '15', '15', 'A jeśli pirworództwa są święte, jestci święte i zadziałanie. A jeśli korzeń święty jest, tedy też i gałązki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '16', '16', 'A jeśli się niektóre gałązki wyłamały, a ty będąc płoną oliwą, miasto ich jesteś wszczepion i zstałeś się uczestnikiem korzenia i tłustości oliwnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '17', '17', 'Nie chlubże się przeciw gałązkam, bo jeśli się chlubisz, nie ty korzenia nosisz, ale korzeń ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '18', '18', 'Rzeczesz tedy: Wyłamane są gałązki, abym ja był wszczepion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '19', '19', 'Dobrze, przez niewiarę wyłamane są, a ty przez wiarę stoisz. Nie wynośże się, ale się bój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '20', '20', 'Abowiem jeśli Bóg przyrodzonym gałązkam nie przepuścił, a jakoż tobie przepuści?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '21', '21', 'Obacz tedy dobroć i srogość Bożą, przeciwko tym, którzy upadli, srogość, a przeciwko tobie dobroć, jeślibyś trwał w dobroci, bo inaczej i ty będziesz wycięt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '22', '22', 'Aleć i oni, jeśli nie będą trwać w niedowiarstwie, będą wszczepieni, gdyż je może Bóg zaś wszczepić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '23', '23', 'Abowiem jeśliś ty jest wycięty z oliwy, która z przyrodzenia była plona i przeciwko przyrodzeniu jesteś wszczepion w prawdziwą oliwę, jako daleko więcej ci, którzy przyrodzeni są, będą w własną oliwę wszczepieni?.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '24', '24', 'Bo nie chcę abyście nie mieli wiedzieć bracia tej tajemnice (żebyście nie byli sami w sobie nadętemi), iż zatwardziałość poczęści przyszła na Izrael, ażby napełnienie poganów weszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '25', '25', 'A tak wszytek Izrael będzie zbawion, jako napisano jest: Przyjdzie z Syjonu on zbawiciel i odejmie niepobożności od Jakuba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '26', '26', 'A to przymierze będą mieć ode mnie. Gdy ja grzechy ich wygładzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '27', '27', 'A tak co się tycze Ewanjelijej, oni są nieprzyjacielami dla was, a podług wybrania bywają miłowani dla ojców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '28', '28', 'Abowiem dary i wezwanie Boże takowe są, iż ich żałować nie może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '29', '29', 'Bo jako i wy przed tym nie byliście posłusznymi Bogu, a teraz dostąpiliście miłosierdzia, przez nieposłuszeństwo tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '30', '30', 'Tak i ci teraz nie byli posłusznymi, aby przez miłosierdzie wasze i oni miłosierdzia dostąpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '31', '31', 'Abowiem zamknął Bóg wszytki w nieposłuszeństwo, aby się zmiłował nad wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '32', '32', 'O głębokie bogactwa mądrości i poznania Bożego, jako są niewybadane sądy jego i niedoścignione drogi jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '33', '33', 'Bo któż poznał umysł Pański, abo kto mu był na radzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '34', '34', 'Abo kto mu pirwszy dał, a będzie mu oddano?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '10', '35', '35', 'Gdyż od niego i przezeń i w nim są wszytki rzeczy. Jemu niech będzie chwała na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '0', '0', 'Proszę was tedy, bracia, przez litość Bożę, abyście stawili ciała wasze na ofiarę żywą, świętą, przyjemną Bogu, która jest ono słuszne wasze posługowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '1', '1', 'A nie przypodobywajcie się temu wiekowi, ale się przemieńcie przez odnowienie umysłu waszego, ktemu, abyście doświadczyli, któraby była wola Boża dobra, wdzięczna i doskonała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '2', '2', 'Abowiem z łaski, która mi jest dana, opowiedam każdemu, który między wami przebywa, żeby o sobie nie rozumiał nad to, co ma rozumieć, ale iżby rozumiał skromnie, ile każdemu Bóg udzielił miarę wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '3', '3', 'Abowiem jako w jednym ciele wiele członków mamy, a wszytki członki jednejże sprawy nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '4', '4', 'Także wiele nas jest jednym ciałem w Krystusie, a z osobna jedni drugich członkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '5', '5', 'Mając różne dary wedle łaski, która nam jest dana, choć proroctwo, abyśmy prorokowali wedle rozmierzenia wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '6', '6', 'Choć posługowanie abyśmy służyli, tak też i on, który uczy, aby uczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '7', '7', 'Też i on, który napomina, aby napominał, który szafuje, żeby w szczyrości, który jest przełożony, iżby w pilności, który czyni miłosierdzie, iżby z ochotą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '8', '8', 'Miłość niech nie będzie obłudna, brzydźcie się złością, uprzejmie przystawszy ku dobremu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '9', '9', 'Bądźcie jedni ku drugim braterską miłością przychylnymi, uczciwością jedni drugie uprzedzając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '10', '10', 'Nie leniwemi w pilności, duchem rozżaleni, Panu służąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '11', '11', 'W nadziei się weseląc, w utrapieniu cierpliwi, trwający w modlitwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '12', '12', 'Potrzebam świętych udzielając, goście radzi przyjmując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '13', '13', 'Błogosławcie tym, którzy was prześladują, dobrorzeczcie im a nie przeklinajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '14', '14', 'Weselcie się z tymi, którzy się weselą, a płaczcie z tymi, którzy płaczą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '15', '15', 'Bądźcie jednostajnej myśli jeden przeciw drugiemu. Nie pysznie o sobie rozumiejąc, ale pokornym folgując, a nie bądźcie mądrymi sami u siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '16', '16', 'Żadnemu złym za złe nie oddawajcie, starając się o poczciwe rzeczy przed wszytkimi ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '17', '17', 'Jeślibyć może, ile w was jest, ze wszytkimi ludźmi w pokoju żywiąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '18', '18', 'Nie mszcząc się sami, o namilejszy, owszem dajcie miejsce gniewowi, abowiem napisano jest: Mojać jest pomsta, a jać oddam, mówi Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '19', '19', 'Jeśli tedy łaknie nieprzyjaciel twój, nakarmi go; jeśli pragnie, napój go. Dobro czyniąc, węgle ogniste zgarniasz na głowę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '11', '20', '20', 'Nie bądź zwyciężon od złego, ale zwycięż złe dobrym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '0', '0', 'Każdy człowiek niechaj będzie podobny zwirzchnim przełożeństwam; abowiem nie jest przełożeństwo jedno od Boga, a przełożeństwa, które bywają od Boga, są porządnie postanowione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '1', '1', 'Przeto, który się zastawuje przełożeństwu, sprzeciwia się postanowieniu Bożemu, a który się sprzeciwia oni na się karanie wnoszą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '2', '2', 'Abowiem przełożeni nie są ku strachowi za dobre uczynki, ale za złe. A chcesz się nie bać przełożeństwa? Co jest dobrego czyń i odniesiesz chwałę od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '3', '3', 'Abowiem jest sługą Bożym tobie ku dobremu, a jeśli uczynisz co jest złego, bój się, bo nie darmo miecz nosi, gdyż jest sługą Bożym, mścicielem ku gniewu przeciwko temu, któryby źle czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '4', '4', 'A przeto potrzeba jest aby się człowiek poddał, nie tylko dla gniewu, ale też dla sumnienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '5', '5', 'Abowiem i dlatego cła dawacie, gdyż są sługami Bożymi tego ustawicznie pilnujący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '6', '6', 'Oddawajcie tedy wszytkim co jesteście powinni: komu podatek, podatek; komu cło, cło; komu bojaźń, bojaźń; komu cześć, cześć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '7', '7', 'Nie bądźcie nikomu nic winni, jedno to, abyście się społecznie miłowali; bo kto miłuje drugiego, zakon wypełnił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '8', '8', 'Gdyż to: Nie będziesz cudzołożył, nie będziesz zabijał, nie będziesz kradł, nie będziesz fałszywie świadczył, nie będziesz pożądał i jeśli które insze jest roskazanie, w tym słowie, które się zamyka. To jest: Będziesz miłował bliźniego twego jako samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '9', '9', 'Miłość bliźniemu złości nie wyrządza, a tak wypełnienie zakonu jest miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '10', '10', 'Zwłaszcza wiedząc, iż jest czas i godzina abyśmy się ocucili ze snu, bo już teraz blizsze nam jest zbawienie, a niż kiedyśmy uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '11', '11', 'Nocci przeminęła, a dzień się przybliżył. Odrzućmysz tedy uczynki ciemności, a obleczmy się w zbroję światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '12', '12', 'Tak abyśmy uczciwie chodzili jako we dnie, nie w biesiadach ani w pijaństwach, nie w łożach, ani w nieczystościach, nie w zwadzie, ani w zazdrości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '12', '13', '13', 'Ale przyobleczcie Pana Jezu Krysta, a nie miejcie starania o ciele ku popełnieniu pożądliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '0', '0', 'Przytym tego, który jest mdły w wierze przyjmujcie, nie abyście wszczynali poswarki około gadek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '1', '1', 'Boć jeden wierzy, iż się godzi każdą rzecz jeść, a drugi, który jest mdły, jarzyny jada.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '2', '2', 'Który je, niechaj sobie za nic nie ma tego, który nie je, a który nie je, niechaj tego nie osądza, który je, gdyż go Bóg przyjął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '3', '3', 'Ty któryś jest co osądzasz cudzego sługę? Własnemu panu stoi abo upada, a będzie utwirdzon, abowiem może go Bóg utwirdzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '4', '4', 'Drugi zasię więcej sobie waży jeden dzień niż drugi, a drugi równo sobie każdy poczyta, a każdy niech będzie upewnion w umyśle swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '5', '5', 'Który dnia pilnuje, Panu pilnuje; a który dnia nie pilnuje, Panu nie pilnuje, który je, Panu je, gdyż dzięki czyni Bogu. A kto nie je, Panu nie je, a wżdy dzięki czyni Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '6', '6', 'Abowiem żaden z nas sobie samemu nie żywie, a żaden sobie samemu nie umiera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '7', '7', 'Bo choć żywiemy, Panu żywiemy; choć umieramy, Panu umieramy. Pańscy jesteśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '8', '8', 'Gdyż na to Krystus umarł i powstał i ożył, aby i nad umarłymi i nad żywiącemi panował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '9', '9', 'A ty preczże sądzisz brata twego? Abo też ty czemu sobie nizacz nie masz brata twego? Abowiem wszyscy postawieni będziemy przed stolicą Krystusową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '10', '10', 'Boć jest napisano: Żywęć ja, mówi Pan, iż mi się wszelkie kolano skłoni i wszelki język da chwałę Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '11', '11', 'Przeto tedy, każdy z nas za się liczbę da Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '12', '12', 'A tak już nie sądźmy jeden drugiego, ale więcej tego strzeżcie, aby zgorszenie bratu nie było położone, abo też obrażenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '13', '13', 'Wiem i pewienem tego przez Pana Jezusa, iż nie masz nic coby przez się było nieczystego, jedno temu, który mnima co być nieczystym, to jemu nieczystym jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '14', '14', 'A wszakże jeśliby dla pokarmu brat twój był zasmucon, już nie według miłości postępujesz. Pokarmem twoim nie zatracaj tego, za którego Krystus umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '15', '15', 'Niechaj tedy dobro wasze osławione nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '16', '16', 'Abowiem królestwo Boże nie jest pokarm ani picie, ale sprawiedliwość i pokój i wesele przez Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '17', '17', 'Bo który przez ty rzeczy służy Krystusowi, jest wdzięczny Bogu i przyjemny ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '18', '18', 'Przetoż tedy starajmy się o rzeczy, które pokojowi należą i też społecznemu budowaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '19', '19', 'Dla pokarmu nie psuj sprawy Bożej, wszytkić rzeczy są czyste, ale źle jest człowiekowi, który je z zgorszeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '20', '20', 'Dobra jest rzecz nie jeść mięsa, a nie pić wina, ani żadnej rzeczy, w której się twój brat obraża, abo pogarsza, abo się mdłym zstawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '21', '21', 'Ty masz wiarę, miejże ją u siebie samego przed Bogiem. Błogosławiony, który nie osądza siebie samego w tym co pochwala.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '13', '22', '22', 'A kto wątpi, jeśliby jadł, jest osądzon, gdyż nie je z wiary. A cokolwiek nie jest z wiary, grzech jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '0', '0', 'A my, którzy jestechmy mocni, powinnichmy znosić krewkości mdłych, a nie folgować samym sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '1', '1', 'Przeto każdy z nas niechaj bliźniemu swojemu folguje ku dobremu, to jest ku jego zbudowaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '2', '2', 'Abowiem Krystus nie folgował sam sobie, ale jako napisano: Sromocenia tych, którzy cię sromocą, na mię wpadły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '3', '3', 'Bo ty rzeczy, które przed tym są napisane, ku naszej nauce napisane są, abyśmy przez cierpliwość i pociechę pism nadzieję mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '4', '4', 'A Bóg cierpliwości i pociechy niech wam da, abyście jednostajny umysł między sobą zachowali społecznie, wedle Krystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '5', '5', 'Abyście zgodnie jednymi usty chwalili Boga i Ojca Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '6', '6', 'Przeto przyjmujcie jedni drugie, jako i Krystus przyjął nas do chwały Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '7', '7', 'A ja to powiedam, iż Jezus Krystus był sługą obrzezania, dla prawdy Bożej, aby utwierdził obietnicę ojców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '8', '8', 'I aby pogani za miłosierdzie chwalili Boga, jako napisano jest: Dlatego będę cię wyznawał między pogany i imieniowi twemu śpiewać będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '9', '9', 'I zasię powieda: Weselcie się pogani z ludem jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '10', '10', 'I zasię: Chwalcie Pana wszyscy pogani, a chwalcie go społu wszyscy narodowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '11', '11', 'Zasię Ezajasz mówi: Będzie korzeń Jeffe i ten, który powstanie roskazować poganom, a pogani w nim ufać będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '12', '12', 'A Bóg nadzieję niech was napełni wszelakiej radości i pokoju w wierzeniu, abyście obfitowali w nadziei przez moc Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '13', '13', 'A jestem tego pewien bracia moi i ja sam o was, iż wy sami przez się jesteście pełni dobroci i napełnieni wszelakiej umiejętności i możecie też społecznie się napominać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '14', '14', 'A jam śmielej do was pisał bracia po niektórej części, jakoby upominając was z tej łaski, która mi jest dana od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '15', '15', 'Ku temu, abych był sługą Jezusa Krystusa u poganów, świętobliwie sprawując Ewanjeliją Bożą, aby się zstała ofiara poganów wdzięczną, poświęcona przez Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '16', '16', 'Mam tedy z czego się chlubić przez Krystusa Jezusa w tych rzeczach, które Bogu należą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '17', '17', 'Abowiem nie śmiałbym nic mówić, czegoby Krystus nie uczynił przeze mię, przywodząc pogany ku posłuszeństwu słowem i uczynkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '18', '18', 'Mocą cudów i znamion i mocą Ducha Bożego tak, iż od Jeruzalem i około przyległych krain aż do Słowaków, jam wypełnił urząd przepowiedania Ewanjelijej Krystusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '19', '19', 'Tymże sposobem chcąc przepowiedać Ewanjeliją, nie gdzieby był mianowan Krystus, abych na cudzym fundamencie nie budował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '20', '20', 'Ale jako jest napisano: Którym nie jest oznajmiono o nim, kędyć ci ujźrzą; a którzy nie słyszeli, zrozumieją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '21', '21', 'Dla czego też często jestem hamowan iżem do was nie przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '22', '22', 'Ale teraz, gdy już miejsca nie mam w tych krainach, a mam wolą przyjdź do was od wielu lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '23', '23', 'Kiedykolwiek pójdę do Hiszpanijej, przyjdę do was, bo mam nadzieję, że tamtędy idąc ujźrzę was i tamże od was odprowadzon będę, pirwej się społeczności waszej poczęści nasyciwszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '24', '24', 'A teraz idę do Jeruzalem posługując świętym, abowiem się podobało Macedończykom i Achajczykom nieco złożyć dla ubogich świętych, którzy są w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '25', '25', 'Także się im podobało i nad to są dłużnicy ich. Abowiem jeśli duchownych dóbr ich pogani używali, powinni też są w rzeczach cielesnych im służyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '26', '26', 'To tedy wykonawszy i im zapieczętowawszy ten owoc, pójdę nie mijając was do Hiszpanijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '27', '27', 'A wiemci, iż gdy do was przyjdę, z zupełnym błogosławieństwem Ewanjelijej Krystusowej przyjdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '28', '28', 'A proszę was bracia przez Pana naszego Jezu Krysta i przez miłość Ducha, abyście zemną bojowali modlitwami waszemi za mną u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '29', '29', 'Abych był wyzwolon od tych, którzy nieposłusznymi są w ziemi Żydowskiej, a iżby posługowanie moje wdzięczne było świętym, którzy są w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '30', '30', 'Abych z weselem przyszedł do was przez wolą Bożą i społu z wami był ochłodzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '14', '31', '31', 'A Bóg pokoju niech będzie z wami wszytkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '0', '0', 'Zalecam wam Febę siostrę naszę, która jest służebnicą kościoła Kenchreeńskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '1', '1', 'Abyście ją przyjęli w Panu, jako przystoi świętym i ratujcie ją, w którejby was rzeczy potrzebowała, abowiem ta wiele ich w dom swój przyjmowała i mnie też samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '2', '2', 'Pozdrówcie Pryscyllę i Atyllę pomocniki moje w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '3', '3', 'Którzy za duszę moję szyje swojej nastawiali, którym nie ja sam dziękuję, ale i wszytki kościoły pogańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '4', '4', 'Przytym zbór, który jest w domu ich. Pozdrówcie Epeneta mnie miłego, który jest początkiem Achajej w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '5', '5', 'Pozdrówcie Marją, która wiele pracowała dla nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '6', '6', 'Pozdrówcie Andronika i Junjasza powinowate moje i towarzysze więzienia mego, którzy znacznymi są między Apostoły i którzy też przede mną byli w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '7', '7', 'Pozdrówcie Amplijasza mnie miłego w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '8', '8', 'Pozdrówcie Urbana pomocnika naszego w Krystusie i Stachina mnie miłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '9', '9', 'Pozdrówcie Apellę doświadczonego w Krystusie. Pozdrówcie ty, którzy są z przyjaciół Arystobulusowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '10', '10', 'Pozdrówcie Herodjona krewnego mego. Pozdrówcie ty, którzy są z przyjaciół Marcysowych, co są w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '11', '11', 'Pozdrówcie Tryfenę i Tryfozę, które pracują w Panu. Pozdrówcie Persydę miłą, która wiele pracowała w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '12', '12', 'Pozdrówcie Rufa wybranego w Panu i matkę jego i moję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '13', '13', 'Pozdrówcie Asynkryta, Flegonta, Hermasza, Patrobę, Hermana i bracią, którzy są z nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '14', '14', 'Pozdrówcie Filologa i Julję, Nerego i siostrę jego i Olimpa i wszytki święte, którzy z nimi są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '15', '15', 'Pozdrówcie jedni drugie z pocałowaniem świętym. Pozdrawiają was zbory Krystusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '16', '16', 'Przytym proszę was bracia, abyście obaczali ty, którzy czynią zwady i pogorszenia, oprócz nauki którejeście się nauczyli, a chrońcie się ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '17', '17', 'Abowiem takowi Panu naszemu Jezusowi Krystusowi nie służą, ale brzuchowi swemu i przez łagodną mowę i pobłażanie, serca prostych zwodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '18', '18', 'Bo posłuszeństwo wasze do wszytkich doszło, przeto się z was weselę, ale chcę abyście byli mądrymi na dobre, a prostemi na złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '19', '19', 'A Bóg pokoju zetrze szatana pod nogami waszemi w rychle. Łaska Pana naszego Jezusa Krystusa niech będzie z wami. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '20', '20', 'Pozdrawiają was Tymoteusz pomocnik mój i Lucjus i Jazon i Sozypater krewni moi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '21', '21', 'Pozdrawiam was w Panu ja Tercjus, którym list napisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '22', '22', 'Pozdrawia was Gajus gospodarz mój i wszytkiego zboru. Pozdrawia was Erastus szafarz miejski i Kwartus brat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '23', '23', 'Łaska Pana naszego Jezusa Krystusa niech będzie z wami wszytkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '24', '24', 'A temu, który może was utwierdzić według Ewanjelijej mojej i przepowiedania Jezu Krystusowego, z objawienia tajemnicę, którą od czasów wiecznych zamilczana była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '25', '25', 'Ale teraz objawiona jest i przez pisma prorockie z roskazania wiecznego Boga, ku posłuszeństwu wiary wszelakim narodom oznajmiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'ro', '15', '26', '26', 'Samemu mądremu Bogu chwała przez Jezusa Krystusa na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '0', '0', 'Paweł powołaniem Apostoł Jezu Krysta, przez wolą Bożą i Sostenes brat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '1', '1', 'Kościołowi Bożemu, który jest w Koryncie, poświęconym w Jezusie Krystusie, wezwanym świętym, ze wszytkimi, którzy wzywają imienia Pana naszego Jezu Krysta na wszelkim miejscu, tak ich jako i naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '2', '2', 'Łaska wam i pokój od Boga ojca naszego i Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '3', '3', 'Dziękuje Bogu mojemu zawsze za was, dla łaski Bożej, która jest wam dana w Jezusie Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '4', '4', 'Iż we wszytkich rzeczach jesteście ubogaceni w nim, wszelką powieścią i wszelką umiejętnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '5', '5', 'Jako świadectwo Krystusowe potwierdzone jest w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '6', '6', 'Tak iż wam na żadnym darze nie schodzi, oczekawając zjawienia Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '7', '7', 'Który (Bóg) też was utwierdzi aż do końca, abyście byli nie naganionymi w dzień Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '8', '8', 'Wiernyć Bóg, przez którego jesteście wezwani ku społeczności syna jego Jezu Krysta Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '9', '9', 'Proszę was tedy bracia przez imię Pana naszego Jezu Krysta, abyście toż mówili wszyscy, a iżby nie były między wami rozerwania, ale żebyście byli spojeni jednakimże umysłem i zdaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '10', '10', 'Abowiemci jest oznajmiono o was bracia moi, od domowników Chloi, iż poswarki bywają między wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '11', '11', 'A to powiedam, iż każdy z was mówi: Jam jest Pawłów, a ja Apollów, ja zasię Kefy, a ja Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '12', '12', 'Izali jest rozdzielon Krystus? A zaś Paweł jest ukrzyżowan za was? Abo w imię Pawłowe jesteście pokrzczeni');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '13', '13', 'Dziękuję Bogu memu iżem nikogo z was nie krzcił, jedno Kryspa i Gajusza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '14', '14', 'Aby żaden nie rzekł żebych krzcił w imię moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '15', '15', 'Okrzciłem też dom Stefanin, za tym nie wiem jeślim kogo drugiego krzcił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '16', '16', 'Boć Krystus nie posłał mię abym krzcił, ale bym Ewanjeliję przepowiedał, a wszakoż nie zacną wymową, aby się nie stał próżnym krzyż Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '17', '17', 'Abowiem powieść ona o krzyżu tym, którzy giną jest głupstwem, a nam, którzy bywamy zbawieni, jestci mocą Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '18', '18', 'Bo jest napisano: Wytracę mądrość mądrych, a zrozumienie rozumnych odrzucę precz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '19', '19', 'Gdzież mądry? Gdzież nauczyciel? Gdzież badacz świata tego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '20', '20', 'Abowiem gdyż w mądrości Bożej, świat nie poznał Boga przez mądrość, podobało się Bogu przez głupie przepowiedanie zachować wierzące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '21', '21', 'Gdyży Żydowie cuda żądają, a Grekowie mądrości szukają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '22', '22', 'Ale my przepowiedamy Krystusa ukrzyżowanego, który jest Żydom zgorszeniem, a Grekom głupstwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '23', '23', 'Samymci wezwaniem tak Żydom jako i Grekom przepowiedamy Krystusa, moc Bożą i mądrość Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '24', '24', 'Abowiem głupstwo Boże jest mędrsze niż ludzie, a mdłość Boża jest możniejsza niż ludzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '25', '25', 'Bo widzicie powołanie wasze bracia, iż niewiele was mądrych jest wedle ciała, niewiele możnych, niewiele zacnego narodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '26', '26', 'Ale wybrał Bóg rzeczy głupie tego świata, aby zawstydził mądre, a mdłe rzeczy świata wybrał Bóg, aby zawstydził ty które są mocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '27', '27', 'A podłe rzeczy świata i których za nic nie mają wybrał Bóg i ty, których nie masz, aby zniszczył ty, które są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '28', '28', 'Aby się żadne ciało przed nim nie chlubiło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '29', '29', 'Ale z niego wy jesteście w Krystusie Jezusie, który się nam stał mądrością od Boga i sprawiedliwością i poświęceniem i odkupieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '0', '30', '30', 'Aby jako jest napisano: Kto się chlubi, niech się w Panu chlubi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '0', '0', 'Ja tedy idąc do was bracia, nie przyszedłem z zacną wymową abo mądrością, opowiedając wam świadectwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '1', '1', 'Abowiem nie obrałem sobie nic między wami cobych umiał, jedno Jezusa Krystusa i tego ukrzyżowanego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '2', '2', 'A jam był ze mdłością, z bojaźnią i ze drżeniem wielkim u was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '3', '3', 'A mowa moja i przepowiedanie moje nie parało się słowy przyłudzającemi mądrości ludzkiej, ale było w okazaniu duchownej mocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '4', '4', 'Aby wiara wasza nie była w mądrości ludzkiej, ale w mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '5', '5', 'A my powiedamy mądrość między doskonałymi, mądrość nie świata tego, ani książąt świata tego, którzy bywają zniszczeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '6', '6', 'Ale powiedamy mądrość skrytą w tajemnicy, którą Bóg przejźrzał przed wieki ku chwale naszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '7', '7', 'Której żaden z książąt świata tego nie poznał, bo gdyby byli poznali, nie ukrzyżowaliby byli Pana chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '8', '8', 'Ale przepowiedamy jako jest napisano: Czego oko nie widziało i ucho nie słyszało i w serce człowiecze nie wstąpiło, co nagotował Bóg tym, którzy go miłują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '9', '9', 'Ale nam to Bóg objawił przez Ducha świętego, abowiemci duch wszytkich rzeczy się wywiaduje, też i głębokości Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '10', '10', 'Bo któż z ludzi wie rzeczy, które są człowiecze jeno duch człowieczy, który w nim jest? Także też rzeczy Bożych nikt nie wie jedno Duch Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '11', '11', 'A my nie wzieliśmy ducha świata, ale Ducha, który jest z Boga, abyśmy wiedzieli co nam Bóg darował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '12', '12', 'Które rzeczy też opowiedamy, nie słowy których uczy ludzka mądrość, ale których uczy Duch Święty, duchowne rzeczy z duchownemi stosując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '13', '13', 'A człowiek z przyrodzenia swego nie pojmuje tych rzeczy, które są Ducha Bożego, abowiem są jemu głupstwem, a nie może ich poznać, gdyż duchownym obyczajem bywają rozsądzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '14', '14', 'Ale duchowny rozsądza wszytki rzeczy, a sam od żadnego nie bywa rozsądzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '1', '15', '15', 'Abowiem któż poznał umysł Pański, żeby go nauczyć miał? A myć umysł Krystusów mamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '0', '0', 'I ja bracia nie mogłem wam mówić jako duchownym, ale jako cielesnym i prawie jako niemowniątkam w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '1', '1', 'Karmiłem was napojem mleka, a nie pokarmem, boście byli jeszcze tego nie mogli znosić, a owszem i teraz jeszcze nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '2', '2', 'Bo jeszcze cieleśni jesteście, abowiem gdyż między wami są zadrość, swar i niesnaski. A zaź nie jesteście cieleśni i według człowieka postępujecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '3', '3', 'Abowiem gdy niektóry powieda: Ja jestem Pawłów, a drugi: Jam jest Apollów, a zaź cielesnymi nie jesteście?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '4', '4', 'Czymże tedy jest Paweł? A czym jest Apollo? Jedno słudzy, przez któreście uwierzyli, a jako każdemu Pan dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '5', '5', 'Jamci wsadził, Apollo polewał, ale Bóg pomnożył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '6', '6', 'A tak, ani ten, który sadzi jest czym, ani ten, który polewa, ale Bóg, który pomnaża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '7', '7', 'A ten który sadzi i ten co polewa, jedno są, a każdy swoję zapłatę weźmie według pracej swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '8', '8', 'Abowiem jesteśmy pomocniki Bożymi, rolą Bożą i budowaniem Bożym jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '9', '9', 'Wedle łaski Bożej, która mi jest dana, jako mądry budowniczy założyłem grunt, a drugi na nim buduje, a wszakoż każdy niech baczy jako na nim buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '10', '10', 'Abowiem gruntu inego nikt nie może założyć, oprócz tego co jest założon, który jest Jezus Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '11', '11', 'A jeśliby kto budował na tym gruncie złoto, srebro, kamienie drogie, drwa, siano, ścierń,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '12', '12', 'Każdego robota jawna będzie, bo dzień okaże, gdyż przez ogień objawiona będzie, a każdą robotą jaką jest, ogień doświadczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '13', '13', 'Jeśli którego robota zostanie którą zbudował, zapłatę weźmie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '14', '14', 'Jeśli którego robota zgore, szkodę popadnie, a on będzie zachowan, ale tak jako przez ogień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '15', '15', 'A zaź nie wiecie, iż kościołem Bożym jesteście, a Duch Boży mieszka w was?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '16', '16', 'A jeśli kto gwałci kościól Boży, tego straci Bóg, abowiem kościół Boży święty jest, którym jesteście wy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '17', '17', 'Niech żaden samego siebie nie oszukawa. Jeśli kto zda się sobie być mądrym między wami na tym świecie, niech się stanie głupim, aby został mądrym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '18', '18', 'Abowiem mądrość świata tego głupstwem jest u Boga, bo napisano jest: Podchwytawa mędrce w chytrości ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '19', '19', 'I zasię: Pan zna, iż myśli mędrców są próżne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '20', '20', 'A tak niech się nie chlubi żaden w ludziach, abowiem wszytki rzeczy są wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '21', '21', 'Choć Paweł, choć Apollo, choć Kefas, choć świat, choć żywot, choć śmierć, choć teraźniejsze rzeczy, choć przyszłe, wszytki rzeczy wasze są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '2', '22', '22', 'A wy Krystusowi, a Krystus Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '0', '0', 'Niech człowiek tak o nas rozumie, jako o sługach Krystusowych i szafarzoch tajemnic Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '1', '1', 'A na ostatek tego potrzeba w szafarzoch, aby każdy był nalezion wiernym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '2', '2', 'Ale ja sobie za namniejszą rzecz mam być rozsądzon od was, abo od sądu ludzkiego i owszem ja sam siebie nie rozsądzam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '3', '3', 'Abowiem nie czuję się niwczym winnym, a wżdy nie przez to jestem usprawiedliwion, ale Panci jest, który mnie rozsądza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '4', '4', 'A także nie sądźcie nic przed czasem, ażby Pan przyszedł, który oświeci skryte ciemności i objawi rady serc, a tedy chwała każdemu będzie od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '5', '5', 'A ty rzeczy bracia przez podobieństwo przystosowałem ku sobie samemu i ku Apollosowi dla was, abyście się nauczyli z nas o sobie nie rozumieć, nadto co jest napisano. Iżbyście się jeden za drugim nie nadymali przeciwko drugiemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '6', '6', 'Abowiem którz cię rozsądza? A co też masz czegobyś nie wziął? A jeśliś wziął, przecz się chlubisz jakobyś nie wziął?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '7', '7', 'Jużeście nasyceni, już ubogaceni i oprócz nas dostaliście królestwa, a daj to Boże abyście dostali królestwa, żebyśmy społecznie z wami królowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '8', '8', 'Bo mnimam, iż Bóg nas ostatnie Apostoły naznaczył, jakoby na śmierć skazane, abowiem staliśmy się na podziw światu, Aniołom i ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '9', '9', 'My głupi dla Krystusa, a wy mądrzy w Krystusie; my mdli, a wy mocni; wy zacnymi, a my bez ućciwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '10', '10', 'I aż do tej godziny łakniemy i pragniemy i nadzy jesteśmy i policzkowani bywamy i tułamy się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '11', '11', 'I pracujemy, robiąc własnymi rękoma, gdy o nas źle mówią, dobro rzeczymy, gdy nas prześladują, cierpiemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '12', '12', 'Gdy bywamy osłwieni, modlimy się, staliśmy się śmieciami świata i jako strużyny wszytkich aż do tąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '13', '13', 'Nie piszeć wam tych rzeczy, abych was zawstydził, ale napominam jako syny moje miłe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '14', '14', 'Abowiem choćbyście mieli dziesięć tysięcy mistrzów w Krystusie, wszakże niewiele ojców macie, bomci ja was w Krystusie Jezusie przez Ewanjeliję urodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '15', '15', 'Przeto was proszę bądźcie naśladowcami moimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '16', '16', 'Dlategomci wam posłał Tymteusza, który jest syn mój miły i wierny w Panie, który wam przywiedzie na pamięć drogi moje w Krystusie, jako w każdym kościele uczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '17', '17', 'Ale jakobych ja nie miał przyjdź do was, nadęli się niektórzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '18', '18', 'Lecz rychło przyjdę do was, jeśli Pan będzie raczył i poznam nie powieść tych to nadętych, ale moc (duchowną).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '19', '19', 'Abowiem nie w mowie należy królestwo Boże, ale w mocy (ducha).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '3', '20', '20', 'Cóż chcecie? Z rózgąli mam przyjdź do was, czyli z miłością i z duchem cichości?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '0', '0', 'Koniecznie słychać, iż między wami jest nieczystość i taka nieczystość, która i między pogany nie bywa wspominana, to jest, iż niektóry ma żonę ojca swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '1', '1', 'A wyście się nadęli i owszemeście tego nie żałowali, aby był odjęt z pośrzodku was, który uczynek ten popełnił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '2', '2', 'Jać zaiste jako nieobecny ciałem, ale obecny duchem, jużciem jako obecny osądził, aby ten który to tak uczynił,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '3', '3', '(A wy będąc w imię Pana naszego Jezu Krysta zgromadzeni z duchem moim) mocą Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '4', '4', 'Był podan szatanowi na zginienie ciała, aby duch był zachowan w dzień on Pana Jezusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '5', '5', 'Chluba wasza nie jest dobra, a zaź nie wiecie, iż trocha kwasu wszytko zadziałanie zakwasza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '6', '6', 'Wyczyśćcież tedy stary kwas, abyście byli nowym zadziałaniem jako bez kwasu jesteście, abowiem ofiara nasza Wielkonocna jest za nas ofiarowana, to jest Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '7', '7', 'A także obchodźmy święto nie starym kwasem, ani z kwasem złości i przewrotności, ale z przaśniki szczyrości i prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '8', '8', 'Pisałem wam w liście abyście się nie mieszali z nieczystymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '9', '9', 'A nie iżby koniecznie z nieczystymi świata tego, abo z łakomymi, abo z drapieżnymi, abo z bałwochwalcami, bo byście zaiste musieli z tego świata wynidź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '10', '10', 'A teraz pisałem wam abyście się nie mieszali z nimi, jeśliby się kto ozywał bratem, a byłby abo nieczystym, abo łakomym, abo bałwochwalcą, abo obmówcą, abo pijanicą, abo drapieżnym, żebyście z takowym ani jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '11', '11', 'Abowiem co mnie do tego, abym ja sądzić miał i obce? A zaź domowych nie sądzicie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '4', '12', '12', 'A Bóg sądzi obce, przeto wyjmicie od siebie samych tego złościwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '0', '0', 'Gdy który z was ma sprawę z drugim, jako śmie wdać się w prawo przed niesprawiedliwymi, a nie przed świętym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '1', '1', 'A zaź nie wiecie iż święci będą sądzić świat? A jeśli świat bywa sądzon przez was, zażeście nie godni sądów mniejszych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '2', '2', 'A zaź nie wiecie, że my Anioły sądzić będziemy? Nie rzkąc abyśmy rzeczy tych, które należą k temu żywotowi sądzić nie mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '3', '3', 'A tak jeśli macie sądy około tych rzeczy ktemu żywotowi należących, posadźcież na ty, którzy są podlejszy w kościele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '4', '4', 'Toć mówię wam ku zawstydzeniu. I także nie masz między wami mądrego i jednego, któryby mógł rozsądzić między braty swemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '5', '5', 'I owszem brat z bratem prawo wiedzie, a to przed niewiernymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '6', '6', 'Już tedy koniecznie jest w was co ganić, iż się między sobą prawujecie. Czemuż radszej krzywdy nie cierpicie? Czemu raczej szkody nie podejmujecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '7', '7', 'Ale wy krzywdzicie i szkodę czynicie, a ktemu braciej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '8', '8', 'Abo nie wiecie, iż niesprawiedliwi nie będą dziedzicmi królestwa niebieskiego? Nie mylcie się. Ani nieczyści, ani bałwochwalcy, ani cudzołożnicy, ani roskosznicy, ani Sodomczycy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '9', '9', 'Ani złodzieje, ani łakomi, ani pijanice, ani obmowce, ani drapieżce, nie będą dziedziczmi królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '10', '10', 'I temiście byli niektórzy, ale jesteście obmyci, ale jesteście poświęceni, ale jesteście usprawiedliwieni w imię Pana Jezusowo i przez Ducha Boga naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '11', '11', 'Wszytko mi wolno, ale nie wszytko pożyteczno, wszytko mi wolno, ale ja nie będę zniewolon od żadnej rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '12', '12', 'Pokarmy są dla brzucha, a brzuch dla pokarmów, a Bóg i ten i ony w niwecz obróci, a ciało nie jest dla nieczystości, ale dla Pana, a Pan dla ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '13', '13', 'A Bóg i Pana wzbudził, i nas wzbudzi mocą swoją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '14', '14', 'Izaź nie wiecie, iż ciała wasze są członkami Krystusowemi? Zali tedy wziąwszy członki Krystusowe uczynię je członkami wszetecznice? Nie daj tego Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '15', '15', 'A zaź nie wiecie, iż ten co się przyłącza wszetecznicy, jednym ciałem jest? Abowiem dwa (mówi) będą jednym ciałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '16', '16', 'A kto się przyłącza Panu, jednym duchem jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '17', '17', 'Strzeżcie się nieczystości. Wszelki grzech, któryby człowiek popełnił, nie jest w ciele, ale ten, który się dopuszcza nieczystości, przeciwko własnemu ciału grzeszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '18', '18', 'A zaź nie wiecie, iż ciało wasze jest przybytkiem Ducha świętego, który w was jest? Którego macie od Boga, a nie jesteście sami swoi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '5', '19', '19', 'Abowiem jesteście za zapłatę kupieni. Chwalcież tedy Boga w ciele waszym i w duchu waszym, które są Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '0', '0', 'Przytym co się tycze tych rzeczy, o którycheście do mnie pisali, dobraby rzecz była mężowi nie tykać się niewiasty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '1', '1', 'Ale dla uwiarowania nieczystości, niech każdy ma swoję żonę, a każda niechaj ma własnego męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '2', '2', 'Żenie niech oddaje mąż powinną życzliwość, także też i żona mężowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '3', '3', 'Żona własnego ciała nie ma w mocy, ale mąż. Także też i mąż własnego ciała nie ma w mocy, ale żona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '4', '4', 'Nie ujmujcie nic jeden drugiemu, chybaby ze zwolenia społecznego do czasu, abyście wolniejszy byli na post i modlitwy. A zasię społu się zejdźcie, aby was szatan nie kusił przez niemierność waszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '5', '5', 'Ale ja to mówię z dozwolenia, nie z roskazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '6', '6', 'Abowiem chciałbym aby wszyscy ludzie byli jakom ja sam jest, ale każdy ma własny dar od Boga, jeden tak, a drugi inak.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '7', '7', 'Powiedam tedy tym, którzy w małżeństwie nie są i wdowam, dobrze im jest jeśliby tak trwali jako ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '8', '8', 'Ale jeśli się nie wtrzymawają, niech w małżeństwo wstąpią, bo lepiej w małżeństwo wstąpić, a niż zapalenie cierpieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '9', '9', 'A tym, którzy są w małżeństwie oznajmuję, nie ja, ale Pan, iżby żona od męża swego nie odstępowała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '10', '10', 'A jeśliby odstąpiła, niechajżeć za mąż nie chodzi, abo się niech z mężem pojedna, a mąż żony swej niech nie opuszcza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '11', '11', 'Inym zasię ja powiedam, nie Pan: Jeśli który brat ma żonę niewierną, a ta z nim zezwala mieszkać, niechaj jej nie opuszcza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '12', '12', 'A żona, która ma męża niewiernego, a on zezwala mieszkać z nią, niechaj go ona nie opuszcza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '13', '13', 'Abowiem poświęcon bywa mąż niewierny przez żonę i żona niewierna bywa poświęcona przez męża, bo inaczej byłyby dzieci wasze nieczystymi, a teraz świętymi są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '14', '14', 'A jeśli niewierny odstępuje, niech odstępuje, abowiem nie jest zniewolon brat abo siostra w takowych rzeczach, ale ku pokojowi wezwał nas Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '15', '15', 'Abowiem co ty wiesz żono jeśli zachowasz męża swego? Abo co ty wiesz mężu zachowaszli żonę abo nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '16', '16', 'Jako każdemu udzielił Bóg, jako każdego wezwał Pan, tak niech postępuje i także w każdym kościele postanawiam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '17', '17', 'Jestli kto wezwany w obrzezaniu? Niechaj nie przyciąga nieobrzezki. Jestli kto w nieobrzezaniu wezwan? Niechaj się nie obrzezuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '18', '18', 'Obrzezanie nic nie jest, a nieobrzezanie nic nie jest, ale zachowanie przykazania Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '19', '19', 'Każdy w tym wezwaniu, w którym jest wezwan, niechaj trwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '20', '20', 'Jesteś wezwan sługą, nie frasujże się o to, ale jeśli też możesz być wolnym, używaj tego radszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '21', '21', 'Abowiem sługa, który jest wezwany w Panie, darowan jest wolnością od Pana, także też który jest wezwan wolnym, jestci sługą Krystusowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '22', '22', 'Jesteście zapłatą kupieni, nie bądźcież sługami ludzkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '23', '23', 'Każdy w czym jest wezwan bracia, w tym niechaj trwa przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '24', '24', 'A o dziewicach nie mamci rozkazania Pańskiego, ale ja o tym swe zdanie powiedam, jako ten, któremu Pan z miłosierdzia dał być wiernym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '25', '25', 'Rozumiem tedy, iż to jest dobra rzecz dla nadchodzącej potrzeby, a mówię, iż dobra rzecz człowiekowi tak być.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '26', '26', 'Jesteś obowiązan żoną? Nie szukajże rozwiązania. Jesteś rozwiązany od żony? Nie szukajże żony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '27', '27', 'A jeślibyś się też ożenił, nie zgrzeszyłeś, jeśliżby też panna szła za mąż, nie zgrzeszyła, a wszakże trapienie w ciele mieć będą, którzy takowi są, ale ja wam folguję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '28', '28', 'A toć powiedam bracia: Iżeć czas skrócił się na potym, aby i ci, którzy żony mają, byli jakoby ich nie mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '29', '29', 'Którzy zaś płaczą jakoby nie płakali, a którzy się radują jakoby się nie radowali, a którzy kupują jakoby nie dzierżeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '30', '30', 'A którzy używają świata tego, jakoby go nie używali, boć przemija kształt świata tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '31', '31', 'A chciałbym abyście byli bez starania, który żony nie ma, stara się o rzeczach Pańskich, jakoby się podobał Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '32', '32', 'Ale który się ożenił, stara się o rzeczach świeckich i jakoby się podobał żenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '33', '33', 'Żona i panienka różneć są, która nie szła za mąż, stara się o rzeczach Pańskich, aby była święta, tak ciałem jako i duchem. A która szła za mąż, stara się o rzeczach świeckich jakoby się podobała mężowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '34', '34', 'A toć powiedam, abym okazał co wam jest pożyteczno, nie abym na was sidło rzucił, ale abym okazał jako przystojnie i słusznie macie stać przy Panie, przez żadnego rostargnienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '35', '35', 'Ale jeśliby kto mnimał iż co nieprzystojnego czyni przeciwko panience swej, jeśliżby z młodych lat wyszła, a iż tak ma być, to co chce niech czyni, nie grzeszy, niechajżeć wstąpią w małżeństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '36', '36', 'A ten, który trwa mocnie w sercu swym i nie przymusza go potrzeba, owszem ma w mocy swą własną wolą i to postanowił w sercu swym, aby zachował dziewicę swą, dobrze czyni');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '37', '37', 'A tak który ją daje za mąż, dobrze czyni, a który nie daje za mąż, lepiej czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '38', '38', 'Żona jestci obowiązana zakonem póki żywie mąż jej, a jeśliby zasnął mąż jej, wolna jest aby szła za kogo chce, tylko aby w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '6', '39', '39', 'Ale szczęśliwsza jest jeśliby tak trwała, wedle mego zdania, a mniemam żeć i ja mam Ducha Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '0', '0', 'Co się zaś tycze rzeczy bałwanom ofiarowanych, wiemy wszyscy, że o tym wiadomość mamy, a wiadomośćci nadyma, ale miłość buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '1', '1', 'A jeśli się kto sobie zda jakoby co umiał, jeszcze nic nie wie jako wiedzieć potrzeba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '2', '2', 'Ale jeśli kto miłuje Boga, tenci jest nauczonym od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '3', '3', 'Około tedy jedzenia rzeczy bałwanom ofiarowanych wiemy, iż bałwan nic nie jest na świecie, a iż żaden inszy Bóg nie jest jednoż jeden.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '4', '4', 'Abowiem choć są, które bogi zową, tak w niebie jako i na ziemi, jakoż jest wiele bogów i wiele panów');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '5', '5', 'Ale my jednegoż Boga mamy, Ojca onego od którego wszytki rzeczy są, a my w nim. I jednego Pana Jezu Krysta, przez którego wszytki rzeczy są, a my przezeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '6', '6', 'Lecz ona wiadomość nie jest we wszytkich, abowiem niektórzy z sumnieniem bałwana aż do tego czasu, jedzą jako rzec bałwanom ofiarowaną, a będąc sumienie ich mdłe, pokalane bywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '7', '7', 'Wszakże pokarm nas nie zaleca Bogu, bo choćbyśmy jedli, nic nam nie przybywa, a choćbyśmy nie jedli, nic nam nie ubywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '8', '8', 'Ale baczcie iżby jakim obyczajem moc wasza nie była ku zgorszeniu tym, którzy są mdłymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '9', '9', 'Abowiem jeśliby kto widział ciebie, który masz wiadomość, a siedzisz w kościele bałwańskim, izaź sumnienie jego, który jest mdłym nie będzie przywiedzione ku jedzeniu rzeczy bałwanom ofiarowanych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '10', '10', 'I przez też twą wiadomość zginie brat mdłym, dla którego Krystus umarł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '11', '11', 'A gdy tak grzeszycie przeciw braciej i ranicie mdłe ich sumnienie, przeciw Krystusowi grzeszycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '7', '12', '12', 'A przeto jeśli dla pokarmu bywa obrażon brat mój, nie będę jadł mięsa na wieki, abym brata swego nie obraził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '0', '0', 'Izalim nie jest Apostoł? Izalim nie jest wolny? Izalim Jezusa Krystusa Pana naszego nie wiedział? Izali sprawą moją wy nie jesteście w Panu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '1', '1', 'Jeśli inym nie jestem Apostołem, ale wam iście jestem, abowiem pieczęcią urzędu mego Apostolskiego wy jesteście w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '2', '2', 'Moja odpowiedź tym, którzy się o mnie wywiadują, tać jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '3', '3', 'Izali nie mamy mocy jeść i pić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '4', '4', 'Izali nie mamy mocy wodzić z sobą siestry żony, jako i ini Apostołowie i bracia Pańscy i Kefas?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '5', '5', 'I zaź ja sam i Barnabasz nie mamy mocy nie robić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '6', '6', 'Któż kiedy walczy swoim kosztem? Któż sadzi winnicę, a owocu nie pożywa? Abo któż trzodę pasie, a mleka trzody nie pożywa?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '7', '7', 'Izali według człowieka ty rzeczy mówię? A zali też zakon tego nie mówi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '8', '8', 'Abowiem w zakonie Mojżeszowym napisano jest: Nie zawiążesz gęby wołowi, który zboże wymłca. A izali się Bóg o woły stara?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '9', '9', 'A zaź to iście nie dla nas mówi? Gdyż dla nas jest napisano: Abowiem w nadziei ma orać ten, który orze, a który młóci w nadziei, ma być uczestnikiem nadzieję swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '10', '10', 'Jeśliśmy wam duchowne rzeczy siali, a zaź to wielka rzecz jeślibyśmy żęli wasze cielesne rzeczy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '11', '11', 'Jeśliż inszy tej mocy nad wami używają, czemuż nie więcej my? A wżdyśmy tej mocy nie używali, ale wszytki rzeczy cierpiemy, abychmy nic nie zamieszkawali Ewanjelijej Krystusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '12', '12', 'A zaź nie wiecie, iż ci, którzy około świętych rzeczy pracują, pożywają tych rzeczy, które są z miejsca świętego? A którzy przy ołtarzu zawżdy są, z ołtarza też pożywają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '13', '13', 'Tak też Pan postanowił tym, którzy Ewanjeliję opowiedają, aby z Ewanjelijej żyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '14', '14', 'A wszakże żadnejem z tych rzeczy nie używał, anim tych rzeczy pisał aby się tak we mnie działo, abowiem lepiej mi umrzeć, niżby kto przechwalanie moje miał próżnem uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '15', '15', 'Bo jeśli Ewanjeliję opowiedam, nie mam się przecz chlubić, gdyż mię potrzeba przynuca, a bieda mnie jeślibym Ewanjelijej nie opowiedał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '16', '16', 'Abowiem jeśli dobrowolnie czynię, mam zapłatę, jeśliż nie rad, to szafowanie jestci mnie zwierzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '17', '17', 'Któraż tedy jest moja zapłata? Iż gdy Ewanjeliję opowiedam, żeby nie czyniono żadnych nakładów na Ewanjeliję Krystusowę, iżbych źle nie używał mocy mej w Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '18', '18', 'Abowiem będąc wolnym od wszytkich, uczyniłem samego siebie wszytkim sługą, abym ich więcej zyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '19', '19', 'I stałem się Żydom jako Żyd, abych Żydy zyskał, a tym, którzy pod zakonem są, jakobychbył pod zakonem, abym ty co pod zakonem są zyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '20', '20', 'Tym którzy są bez zakonu, jako bez zakonu, (aczkolwiek nie jestem bez zakonu Bogu, ale w zakonie w Krystusie), abym zyskał ty, którzy są bez zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '21', '21', 'Stałem się mdłym jako mdły, abych mdłe zyskał, wszytkim stałem się wszytko, abych wżdy niektóre zachował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '22', '22', 'I to czynię dla Ewanjelijej, abym się stał uczestnikiem jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '23', '23', 'A zaź nie wiecie, iż ci, którzy w zawód biegają, aczci wszyscy bieżą, ale jeden zakład bierze? Także bieżcie abyście otrzymali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '24', '24', 'I ten, któryć zapasy chodzi, na wszytkim się miernie chowa. Oni tedy miernemi są, aby wzięli koronę śmiertelną, a my nieśmiertelną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '25', '25', 'A przetoż ja tak bieżę, nie jako na niepewną, tak szermuję nie jako wiatr bijąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '8', '26', '26', 'Ale podbijam ciało moje i zniewalam je, abym ja snadź przepowiedziawszy inszym , sam się nie stał naganionym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '0', '0', 'A nie chcę abyście nie mieli wiedzieć bracia, iż ojcowie naszy wszyscy pod onym obłokiem byli, a wszyscy przez morze przeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '1', '1', 'I wszyscy byli okrzczeni w Mojżeszu, w obłoku i w morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '2', '2', 'A wszyscy używali jednego pokarmu duchownego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '3', '3', 'I wszyscy pili jedno picie duchowne, abowiem pili z skały duchownej, która za nimi szła. A ta skała była Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '4', '4', 'Ale wiele ich nie podobało się Bogu, abowiem porażeni są na puszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '5', '5', 'A ty rzeczy były wzormi naszymi, ku temu abyśmy złych rzeczy nie pożądali, jako oni żądali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '6', '6', 'Nie bądźcież bałwochwalcami., jako niektórzy z nich, tak jako jest napisano: Siadł lud aby jadł i pił i wstali ku igraniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '7', '7', 'Ani się dopuszczajmy nieczystoty, jako niektórzy z nich nieczystymi byli i poległo ich dnia jednego trzy i dwadzieścia tysięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '8', '8', 'Ani kusimy Krystusa, jako niektórzy z nich kusili i od wężów poginęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '9', '9', 'Ani szemrzycie, jako niektórzy z nich szemrali i poginęli od tego, który wytracał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '10', '10', 'A ty wszytki rzeczy przydały się im ku przykładowi, a są napisane dla napominania naszego, na które skończenia wieków przyszły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '11', '11', 'A tak który się zda sobie iże stoi, niechże patrza aby nie upadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '12', '12', 'Pokuszenie was nie zięło jedno ludzkie, a Bógci jest wierny, kt&nbsp;óry nie dopuści abyście byli kuszeni nad to, co możecie, owszem wam da ujdź z pokusy abyście znieść mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '13', '13', 'Dlatego, bracia namilszy moi, strzeżcie się bałwochwalstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '14', '14', 'Mówieć jako mądrym, rozsądźcież wy co powiedam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '15', '15', 'Kubek błogosławienia, który błogosławiemy, izali społecznością krwie Krystusowej nie jest? A chleb, który łamiemy, izali nie jest społecznością ciała Krystusowego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '16', '16', 'Abowiemci wiele nas jest jednym chlebem i jednym ciałem, gdyż wszyscy jednego chleba jesteśmy uczestniki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '17', '17', 'Patrzcie na Izraela, który jest wedle ciała. Izaź ci, którzy pożywają ofiar nie są uczestniki ołtarza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '18', '18', 'Cóż tedy powiedam aby bałwan miał czym być? Abo iżby to co jest bałwanom ofiarowano miało czym być?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '19', '19', 'I owszem to powiedam: Iż rzeczy, które poganie ofiarują, diabłom ofiarują, a nie Bogu, a nie chciałbym, abyście byli uczestnicy diabłów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '20', '20', 'Nie możecie pić kubka Pańskiego i kubka diabłów, nie możecie być uczestniki stołu Pańskiego i stołu diabłów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '21', '21', 'I mamyż wzruszyć ku gniewu Pana? Izali my jestechmy mocniejszy niż on?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '22', '22', 'Wszystko mi wolno, ale nie wszystko pożyteczno. Wszystko mi wolno, ale nie wszystko buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '23', '23', 'Niechaj nikt nie szuka co jest jego, ale każdy co jest drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '24', '24', 'Cokolwiek w jatce przedawają, jedzcie, nic się nie pytając dla sumnienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '25', '25', 'Abowiemci ziemia jest Pańska i wszytko co na niej jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '26', '26', 'A jeśli który z niewiernych was prosi, a chcecie idź, cokolwiek przed was położą, jedzcie, nic nie pytając dla sumnienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '27', '27', 'Ale jeśliby kto wam rzekł: Toć jest bałwanom ofiarowano. Nie jedzcie dla onego, który oznajmił i dla sumnienia. Abowiemci Pańska jest ziemia i wszytko co na niej jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '28', '28', 'A powiedam dla sumnienia, nie twego, ale onego drugiego. Abowiem przeczże wolność moja bywa osądzona od sumnienia inego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '29', '29', 'A jeśli z łaski pożywam, czemuż dlatego bywam sądzon niepobożnym, za co ja dziękuję?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '30', '30', 'Choć tedy jecie, choć pijecie, choć co czynicie, wszytko ku chwale Bożej czyńcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '31', '31', 'Bądźcie bez pogorszenia i Żydom i Grekom i kościołowi Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '9', '32', '32', 'Jako i ja we wszytkich rzeczach wszytkim się podobam, nie szukając pożytku mego, ale wiela ich aby byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '0', '0', 'Bądźcie naśladowcami moimi jako i ja Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '1', '1', 'Chwalę was bracia, iż pamiętacie wszytki moje rzeczy a jakom wam podał, trzymajcie się tego co wam podano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '2', '2', 'A chcę abyście wiedzieli, iż każdego męża głowa Krystus jest, a głowa niewiasty mąż, a głowa Krystusa, Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '3', '3', 'Każdy mąż, modląc się abo prorokując zakrywszy głowę, sromoci głowę swoję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '4', '4', 'A każda niewiasta, modląc się abo prorokując nie zakrywszy głowy swojej, sromoci głowę swą, boć to wszytko jest za jedno jakoby i ogolona była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '5', '5', 'Abowiem niewiasta jeśli się nie przykrywa, niech się też ostrzyże, a jeśli sromota niewieście strzydz się abo golić, niech się przykrywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '6', '6', 'Abowiem mąż nie ma zakrywać głowy swojej, gdyż jest wyobrażeniem i chwałą Bożą, ale niewiasta jest chwała mężowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '7', '7', 'Bo mąż nie jest z niewiasty, ale niewiasta z męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '8', '8', 'Zaiste nie jest stworzon mąż dla niewiasty, ale niewiasta dla męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '9', '9', 'A przetoż niewiasta ma mieć znak mocy na głowie dla Aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '10', '10', 'A wszakże mąż nie jest bez niewiasty i niewiasta nie jest bez męża w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '11', '11', 'Abowiem jako niewiasta z męża jest, tak też mąż przez niewiastę, a wszytki rzeczy z Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '12', '12', 'Sami u siebie rozsądźcie, słuszali niewieście nie przykrywszy się modlić się Bogu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '13', '13', 'A zaź samo przyrodzenie was tego nie naucza? Iż mąż gdyby włosy zapuszczał, jest jemu ku zelżywości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '14', '14', 'Ale jeśli niewiasta zapuszcza włosy, jest jej ku chwale przeto, iż włosy są jej dane za przykrycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '15', '15', 'A jeśliby się kto zdał być swarliwym, nie mamy takiego obyczaju, ani kościoły Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '16', '16', 'A ja tak wam opowiedam, nie chwaląc was, iż nie z pożytkiem, ale z szkodą schadzacie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '17', '17', 'Abowiem naprzód gdy się schodzicie w kościele słyszę, iż rozerwania bywają między wami i temu po części wierzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '18', '18', 'Abowiem musząć też być odszczepieństwa między wami, aby ci, którzy doświadczeni są, byli jawni między wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '19', '19', 'Gdy się tedy w jedno schodzicie, toć nie dla używania wieczerzej Pańskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '20', '20', 'Abowiem każdy wprzód wieczerzejswojej u&nbsp;żywa i łaknie jeden, a drugi jest pijanym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '21', '21', 'Zali domów nie macie ku jedzeniu i piciu? Abo kościołem Bożym gardzicie i zawstydawacie ty, którzy nie mają? Cóż wam rzekę? Pochwalęli was w tym? Nie chwalę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '22', '22', 'Abowiemciem ja wziął od Pana com też wam podał, iż Pan Jezus tej nocy, której był wydan, wziął chleb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '23', '23', 'I podziękowawszy złamał i rzekł: Bierzcie, jedzcie, toć jest ciało moje, które za was bywa łamane, to czyńcie na pamiątkę moję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '24', '24', 'Także i kubek po wieczerzy, mówiąc: Ten kubek jest nowy Testament przez krew moję. To czyńcie ilekroć będziecie pić na pamiątkę moję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '25', '25', 'Abowiem ile kroć będziecie jeść ten chleb i ten kubek będziecie pić, śmierć Pańską opowiedać będziecie aż przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '26', '26', 'A tak ktoby jadł chleb ten, abo pił ten kubek Pański niegodnie, będzie winien, iż wzgardził ciało i krew Pańską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '27', '27', 'A każdy niech się doświadczy, a także niechaj je on chleb i z kubka onego pije.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '28', '28', 'Abowiem który je i pije niegodnie, potępienie sobie samemu je i pije, nie rozeznawając ciała Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '29', '29', 'Dlatego między wami wieleć jest mdłych i niemocnych, a wiele ich zasnęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '30', '30', 'Bo byśmy się sami rozsądzali, nie bylibyśmy karani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '31', '31', 'Ale gdy bywamy karani, od Pana bywamy nauczani, abyśmy nie byli z światem osądzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '32', '32', 'A tak bracia moi, gdy się schodzicie ku jedzeniu, oczekawajcie jeden drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '10', '33', '33', 'A jeśli kto łaknie, niechaj się doma naje, abyście się na potępienie nie schodzili. Ine rzeczy przyszedszy postanowię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '0', '0', 'Przy tym o duchownych darzech bracia nie chcę abyście wiedzieć nie mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '1', '1', 'Wiecie, iż będąc pogany, do niemycheście bałwanów chodzili, prawie się za nimi porywając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '2', '2', 'A przeto oznajmuję wam, iż żaden przez Ducha Bożego mówiąc, nie powieda Jezusa być przeklęctwem, a nikt nie może powiedzieć Jezusa Panem, jedno przez Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '3', '3', 'Aczci są różności darów, ale Duch tenże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '4', '4', 'I są różności posług, ale Pan tenże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '5', '5', 'Także różności spraw, ale Bóg tenże. Sprawując ony wszytki we wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '6', '6', 'A każdemu bywa dane objawienie ducha ku pożytkowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '7', '7', 'Abowiem jednemu przez ducha bywa dana mowa mądrości, a drugiemu mowa umiejętności przez tegoż ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '8', '8', 'Drugiemu zaś wiara przez tegoż ducha, a drugiemu dary uzdrawiania przez tegoż ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '9', '9', 'Drugiemu zaś mocy czynienia cudów, drugiemu proroctwo, drugiemu rozeznania duchów, drugiemu rozmaite języki, a drugiemu wykładanie języków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '10', '10', 'Ale ty wszytki rzeczy sprawuje jeden i tenże duch udzielając ich z osobna każdemu jako chce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '11', '11', 'Abowiem jako jest jedno ciało, a członków wiele ma, a wszytki członki ciała jednego choć ich wiele jest, ale jednym ciałem są, takżeć i Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '12', '12', 'Bo przez jednego ducha my wszyscy w jedno ciało jesteśmy okrzczeni, choć Żydowie, choć Grekowie, choć niewolnicy, choć wolni, a wszyscy jesteśmy napojeni jednym napojem w jednegoż Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '13', '13', 'Abowiem ciało nie jest jednym członkiem, ale wiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '14', '14', 'Jeśliby rzekła noga: Nie jestemci ręką, nie jestem z ciała; izali dlatego z ciała nie jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '15', '15', 'A jeśliby rzekło ucho: Nie jestem okiem, nie jestem z ciała; izali dlatego z ciała nie jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '16', '16', 'Jeśliż wszytko ciało okiem, gdzież słuch jest? Jeśliż wszytko słuchem, gdzież wonianie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '17', '17', 'A teraz Bóg położył wszytki członki z osobna w ciele jako chciał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '18', '18', 'A jeśliby wszytki były jednym członkiem, gdzież ciało?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '19', '19', 'Ale teraz jestci wiele członków, a wżdy jedno ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '20', '20', 'Ani może rzec oko ręce: Ja ciebie nie potrzebuję, abo zasię głowa nogam: Nie potrzebuję was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '21', '21', 'I owszem daleko więcej członki ciała, które się zdadzą być namdlejsze, potrzebne są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '22', '22', 'A członki ciała, które mnimamy być nasprośniejsze, tymci więcszą poczciwość przydawamy, a nieuczciwe członki nasze więcszą poczciwość mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '23', '23', 'Abowiem to co w nas jest ochędożnego, ochędogi nie potrzebuje, ale Bóg społu umiarkował ciało, temu członkowi, który nie miał, więcszą cześć podawszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '24', '24', 'Aby nie było rozerwanie w ciele, ale iżby jedny członki o drugich toż staranie miały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '25', '25', 'A przeto choć cierpi jeden członek, społu boleją wszytki członki, choć bywa uczcion jeden członek, wespół się radują wszytki członki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '26', '26', 'A tak wy jesteście ciałem Krystusowym i członkami po części.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '27', '27', 'I postanowił Bóg niektóre w kościele, naprzód Apostoły, potym proroki, trzecie nauczyciele, potym mocy czynienia cudów, ktemu dary uzdrawiania, pomocy, rządzenia, różność języków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '28', '28', 'Izali wszyscy Apostołami? Izali wszyscy proroki? Izali wszyscy nauczycielmi? Izali wszyscy cuda czynią?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '29', '29', 'Izali wszyscy dary mają uzdrawiania? Izali wszyscy języki mówią? Izali wszyscy wykładają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '11', '30', '30', 'A żądacież lepszych darów? Otoż jeszcze wam z wielką pilnością drogę okażę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '0', '0', 'Gdybych mówił języki ludzkiemi i Anielskimi, a miłości bych nie miał, stałem się miedzią brząkającą, abo cymbałem brzmiącym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '1', '1', 'I gdybych miał proroctwo i wiedział wszytki tajemnice i wszelką naukę, a gdybych miał wszelką wiarę, żebych też i góry przenosił, a miłości bych nie miał, nicem nie jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '2', '2', 'I gdybych wszytki majętności moje rozdał ubogim, a podałbych ciało moje abych był spalon, a miłości bych nie miał, nic mi to nie pomaga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '3', '3', 'Miłośćci jest cierpliwa, dobrotliwa, miłość nie zajźrzy, miłość nie jest harda i nie nadęta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '4', '4', 'Miłość niczym się nie brzydzi jakoby nieprzystojnymi, nie szuka co jest jej, nie bywa wzruszona ku gniewu, nie myśli złego.&nbsp;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '5', '5', 'Nie raduje się z niesprawiedliwości, ale się weseli z prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '6', '6', 'Wszytko znosi, wszytkiemu wierzy, wszytkiego się nadziewa, wszytko cierpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '7', '7', 'Miłość nigdy nie odpada, choć i proroctwa będą zgładzone, języki ustaną i umiejętność będzie zniszczona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '8', '8', 'Abowiem po części znamy i po części prorokujemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '9', '9', 'Ale gdy przyjdzie to co jest doskonałego, tedy to co jest po części zginie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '10', '10', 'Gdym był dziecięciem, mówiłem jako dziecię, myśliłem jako dziecię, rozumiałem jako dziecię, lecz gdym się stał mężem, zatarłem ty rzeczy, które dziecinne były.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '11', '11', 'Abowiemci teraz widziemy przez zwierciadło i przez podobieństwo, ale w on czas oblicznie uj&nbsp;źrzymy. Teraz znam po części, a w on czas poznam jako nauczon będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '12', '12', '12', 'A terazci trwa wiara, nadzieja, miłość. Ty trzy rzeczy, a z nich nawięcsza jest miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '0', '0', 'Naśladujcie miłości, żądajcie darów duchownych, ale więcej abyście prorokowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '1', '1', 'Abowiem który mówi języki obcymi, nie ludziam mówi ale Bogu, bo go żaden nie słyszy, a on duchem mówi tajemnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '2', '2', 'A który prorokuje, opowieda ludziam zbudowanie i napominanie i pocieszenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '3', '3', 'Który językiem obcym mówi, samego siebie buduje, a który prorokuje, zbór buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '4', '4', 'A chciałbym abyście wszyscy mówili języki, ale więcej abyście prorokowali, abowiem więcszy jest ten, który prorokuje, a niż ten co języki mówi, wyjąwszyby wykładał, aby zbór wziął budowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '5', '5', 'A teraz bracia gdybych przyszedł do was języki mówiąc, cóż wam pomogę jeśli wam nie będę mówił, abo przez objawienie, abo przez wiadomość, abo przez proroctwo abo przez naukę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '6', '6', 'Wszak ty rzeczy, które dusze nie mając głos dawają, choć piszczałka, choć harfa, jeśliby dźwięków różności nie dawały, jakoż będzie poznano to co na piszczałce abo na harfie grają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '7', '7', 'Abowiem jeśliby nie pewny głos trąba dała, któż się ku wojnie gotować będzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '8', '8', 'Także i wy językiem jeślibyście nie dali mowy znacznej, jakoż będzie rozumiano co się powieda? Bo będziecie na wiatr mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '9', '9', 'Jako na przykładzie, tak wiele jest głosów na świecie, a żaden z nich nie jest niemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '10', '10', 'Jeślibych tedy nie wiedział co znaczy głos, będę obcego języka człowiekiem, a ten który mówi, będzie też u mnie obcego języka człowiekiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '11', '11', 'Tak też i wy gdyż duchownych rzeczy żądacie, szukajcież abyście w nich przewyższali ku zbudowaniu kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '12', '12', 'Dlatego kto mówi językiem obcym, niech prosi aby mógł wykładać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '13', '13', 'Bo jeślibych się modlił językiem, modlić się duch mój, ale wyrozumienie moje jest niepożyteczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '14', '14', 'Cóż tedy jest? Będę się modlił duchem, będę się też modlił wyrozumieniem, będę śpiewał duchem, a też będę śpiewał wyrozumieniem.&nbsp;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '15', '15', 'Bo jeślibych błogosławił duchem ten, który jest z pospólstwa, jakoż rzecze Amen na twe dziękowanie, gdyż nie wie co ty mówisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '16', '16', 'Abowiem dobrze ty dziękujesz, ale drugi się nie buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '17', '17', 'Dziękujęć Bogu mojemu, iż więcej niż wy wszyscy mówię języki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '18', '18', 'Lecz w zgromadzeniu wolę pięć słów moim wyrozumieniem powiedzieć, abych też i drugie nauczył, niżeli dziesięć tysięcy słów językiem obcym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '19', '19', 'Bracia! Nie bądźcie dziećmi w rozumieniu, ale bądźcie dziećmi złością i rozumem doskonali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '20', '20', 'W zakonie napisano jest: Przeto przez ludzi obcego języka i cudzemi wargami będę mówił ludowi temu, a przedsię mię i tak nie wysłuchają, mówi Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '21', '21', 'A także języki są na znak nie tym, którzy wierzą, ale tym, którzy nie wierzą. Proroctwo zasię nie tym, którzy nie wierzą, ale tym którzy wierzą.&nbsp;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '22', '22', 'Gdyby się tedy wszytko zgromadzenie wespół zeszło i wszyscy języki mówili, a weszliby nienauczeni abo niewierni, izali nie rzeką, że szalejecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '23', '23', 'A gdyby wszyscy prorokowali, a wszedł kto nie wierny, abo nie nauczony, bywać od wszytkich sztrofowan, od wszytkich bywa rozsądzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '24', '24', 'I także skrytości serca jego bywają objawione, a tak upadszy na oblicze da chwałę Bogu, opowiedając że Bóg w was jest prawdziwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '25', '25', 'Cóż tedy jest bracia? Gdy się schodzicie każdy z was ma psalm, ma naukę, ma język, ma objawienie, ma wykładanie, wszytko tedy niech będzie ku zbudowaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '26', '26', 'Chociaż kto językiem mówi, niech to będzie po dwu, abo nawięcej po trzech, a ktemu jeden po drugim, a jeden niechaj wykłada.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '27', '27', 'A jeśliby wykładacza nie było, niech milczy ten w zgromadzeniu, który obcym językiem mówi, a niech mówi sobie a Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '28', '28', 'Prorocy też niech mówią dwa abo trzej, a inszy niechaj rozsądzają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '29', '29', 'A jeśliby inszemu, który siedzi było objawiono, pirwszy niechaj milczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '30', '30', 'Bo możecie wszyscy po jednemu prorokować, aby się wszyscy uczyli i wszyscy pocieszeni byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '31', '31', 'I duchowieć proroków są poddani prorokom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '32', '32', 'Abowiemci B&nbsp;óg nie jest Bogiem zamieszania, ale pokoju, jako we wszytkich zborzech świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '33', '33', 'Niewiasty wasze niech milczą we zborzech, abowiemci mówić im nie jest dopuszczono, ale im roskazano aby poddane były, jako i zakon mówi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '34', '34', 'A jeśli się czego nauczyć chcą, niech doma swych mężów pytają, abowiemci nieprzystojna rzecz jest niewiastam w zgromadzeniu mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '35', '35', 'Izali od was weszło słowo Boże? Izali ku wam tylko przyszło?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '36', '36', 'Jeśliże kto zda się być prorokiem, abo duchownym, niech uzna ty rzeczy, które wam piszę być Pańskim roskazaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '37', '37', 'A jeśli kto nie wie, niechaj nie wie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '38', '38', 'A także bracia żądajcie tego, abyście prorokowali, a języki mówić nie zabraniajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '13', '39', '39', 'A wszytki rzeczy niechaj będą uczciwie i porządnie sprawowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '0', '0', 'A oto bracia oznajmuję wam Ewanjeliję, któram wam przepowiedział, którąście też przyjęli i w której też stoicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '1', '1', 'Przez którą też zachowani jesteście, jeśli pamiętacie którym sposobem przepowiedziałem wam, wyjąwszy byście próżno uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '2', '2', 'Abowiem naprzód podałem wam com też sam wziął, to jest, że Krystus umarł za grzechy nasze, według pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '3', '3', 'Ktemu że jest pogrzebion, że też wzbudzon jest trzeciego dnia według pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '4', '4', 'I był widzian od Kefasa, potym od onych dwunaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '5', '5', 'Zatym widzian jest więcej niż od piąci set braciej zaraz, z których wiele ich trwa aż do tego czasu, a niektórzy też zasnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '6', '6', 'Potym jest widzian od Jakuba, potym od wszytkich Apostołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '7', '7', 'Na ostatek po wszytkich, ukazał się też i mnie jakoby poronionemu płodowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '8', '8', 'Bomci ja jest namniejszy z Apostołów, którym nie jest godzien abych był nazywan Apostołem, iżem prześladował kościół Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '9', '9', 'Ale z łaski Bożej jestem tym, czymem jest, a łaska jego przeciwko mnie próżna się nie stała, alem więcej pracował niż oni wszyscy, wszakże nie ja, ale łaska Boża, która jest ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '10', '10', 'I ja tedy i oni, tak przepowiedamy i takeście uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '11', '11', 'A jeśliże Krystus przepowiedan bywa, iż zmartwych wzbudzon jest: Jakoż mówią niektórzy między wami, że zmartwychwstania nie masz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '12', '12', 'Abowiem jeśli zmartwychwstania nie masz, tedyć ani Krystus jest wzbudzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '13', '13', 'A jeślić Krystus nie jest wzbudzon, iścieć jest próżne nasze przepowiedanie, próżna też i wiara wasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '14', '14', 'I my też najdujemy się fałszywemi świadki Bożymi, iżeśmy świadczyli o Bogu, że Krystusa wzbudził, któregoć nie wzbudził, jeśliże umarli nie bywają wzbudzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '15', '15', 'Abowiem jeśliżeć umarli wzbudzeni nie bywają, anić Krystus wzbudzon jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '16', '16', 'A jeśliż Krystus nie wzbudzon, próżnać jest wiara nasza. Jeszcze jesteście w grzechach waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '17', '17', 'A także i ci też poginęli, którzy zasnęli w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '18', '18', 'Jeśliż tylko w tym żywocie w Krystusie nadzieję mamy, nad wszytki ludzi jesteśmy nanędzniejszy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '19', '19', 'Aleć teraz Krystus zmartwych wzbudzon i stał się pierwszym tych, którzy zasnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '20', '20', 'Bo gdyż przez człowieka śmierć przyszła, też i przez człowieka powstanie zmartwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '21', '21', 'Abowiem jako przez Adama wszyscy umierają, tak i w Krystusie wszyscy ożywieni będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '22', '22', 'A każdy porządkiem swoim: Krystus pierwszy powstał, potym ci, którzy są Krystusowymi, powstaną w przyście jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '23', '23', 'Potym koniec będzie, gdy poda królestwo Bogu a ojcu, gdy zniszczy wszelkie przełożeństwo i wszelką zwierzchność i moc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '24', '24', 'Boć on królować musi, ażby położył wszytki nieprzyjacioły pod nogi swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '25', '25', 'A ostatni nieprzyjaciel śmierć będzie zniszczona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '26', '26', 'Boć wszytki rzeczy podał pod nogi jego. A gdy mówi, że mu wszytki rzeczy poddane są, jawna rzecz jest, iż to bywa mówiono okrom tego, który mu poddał wszytki rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '27', '27', 'A gdy mu wszytki rzeczy poddane będą, tedyć też i sam syn będzie poddan temu, który mu poddał wszystko, aby Bóg był wszytkim we wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '28', '28', 'Bo inaczej cóż uczynią ci, którzy się za umarłe krzczą? Jeśliże koniecznie umarli wzbudzeni nie bywają, przeczże za umarłe krzczeni bywają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '29', '29', 'Przecz też i my na każdy czas w niebeśpieczeństwie jesteśmy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '30', '30', 'Każdyć dzień umieram przez chwałę waszę, którą mam w Jezusie Krystusie panie naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '31', '31', 'Jeśliżem według człowieka w Efezie z źwierzęty walczył, któryż mam ztąd pożytek, jeśli umarli wzbudzeni nie bywają? Jedzmy i pijmy, boć jutro pomrzemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '32', '32', 'Nie błądźcież. Złeć rozmowy psują obyczaje dobre.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '33', '33', 'Ocućcież się sprawiedliwie, a nie grzeszcie: boć niektórzy nie znają Boga. Mówieć to abych was zawstydził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '34', '34', 'Ale rzecze który: Jakoż wzbudzeni bywają umarli, a w jakiem ciele wychodzą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '35', '35', 'Szalony! To co ty siejesz, nie bywać ożywiono, ażby obumarło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '36', '36', 'I co siejesz, nie siejesz ciała, które ma wynidź, ale gołe ziarno (jako na przykład) pszeniczne abo jakie insze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '37', '37', 'Ale Bóg daje mu ciało jako chciał i każdemu nasieniu jego własne ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '38', '38', 'Nie jestci wszelkie ciało jednymże ciałem, ale insze jest ciało ludzkie, a insze ciało bydlęce, insze ryb, a insze ptaków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '39', '39', 'Są też ciała niebieskie i ciała ziemskie, aleć insza jest chwała niebieskich, a insza ziemskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '40', '40', 'Insza jest chwała słońca, a insza miesiąca i insza gwiazd. Abowiem gwiazda od gwiazdy jest różna w chwale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '41', '41', 'Takci i zmartwychwstanie. Bywa wsiane ciało poddane skażeniu, a bywa wzbudzone nie skażone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '42', '42', 'Bywa wsiane prócz uczciwości, a bywa wzbudzone uwielbione. Bywa wsiane mdłe, a bywa wzbudzone mocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '43', '43', 'Bywa wsiane ciało do czasu duszą żywiące, a bywa wzbudzone ciałem duchownym. Jestci ciało duszą do czasu żywiące, jest też i ciało duchowne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '44', '44', 'Takci też jest napisano: Stał się pierwszy człowiek Adam w duszę żywą, a pośledni Adam stał się duchem ożywiającym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '45', '45', 'Ale się nie pierwszym stało co jest duchowne, ale to które jest duszą do czasu żywe, potym które duchowne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '46', '46', 'Pierwszy człowiek z ziemie, ziemski jest. Wtóry człowiek sam Pan z nieba jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '47', '47', 'Jaki ziemski, tacy i ci, którzy ziemscy są, a jaki on niebieski, tacy i ci którzy są niebiescy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '48', '48', 'Jakośmy nosili wyobrażenie ziemskiego, będziem też nosić wyobrażenie i niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '49', '49', 'A toć mówię bracia, żeć ciało i krew królestwa Bożego dziedzicznie otrzymać nie mogą, ani skażenie nie otrzymawa dziedzictwa nieskażonego przyrodzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '50', '50', 'Otoć wam tajemnice powiedam: Nie wszyscyć zaśniemy, ale wszyscy odmienieni będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '51', '51', 'Barzo prędko i we mgnieniu oka na głos trąby ostatecznej (abowiemci zatrąbi, a umarli wzbudzeni będą nie skażeni i my będziemy odmienieni).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '52', '52', 'Boć musi to skażone, przyoblec nieskażone przyrodzenie i to śmiertelne przyoblec nieśmiertelność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '53', '53', 'A gdy to skażone przyoblecze nieskażone przyrodzenie i to śmiertelne przyoblecze nieśmiertelność, tedy się stanie słowo napisane: Śmierć jest pożarta w zwycięstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '54', '54', 'Gdzież jest zwycięstwo twe o śmierci? Gdzież jest bodziec twój o grobie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '55', '55', 'A bodziec śmierci jestci grzech, a moc grzechu zakon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '56', '56', 'Ale dziękuję Bogu, który nam dał zwycięstwo przez Pana naszego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '14', '57', '57', 'A tak bracia moi mili, bądźcie mocni, nieporuszeni, obfitującemi w uczynku Pańskim zawsze, wiedząc, iż praca wasza nie jestci próżna w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '0', '0', 'Zatym około składania dla świętych, jakom postanowił w kościelech Galacijej, tak też i wy czyńcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '1', '1', 'Pierwszego dnia w tego dniu każdy z was niechaj odłoży zbierając, co z dobrotliwości Bożej będzie mógł, iżby gdy przyjdę składania nie czyniono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '2', '2', 'A gdy przyjdę, którebyście kolwiek chwalili przez listy, ty poślę, aby odnieśli szczodrobliwość waszę do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '3', '3', 'A jeśliby słuszną rzecz była, abych też i sam szedł, oni zemną pójdą.&nbsp;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '4', '4', 'I przyjdęć do was przeszedłszy przez Macedonią; bo przez Macedonią pójdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '5', '5', 'A podobno pomieszkam przy was, abo i zimować będę abyście mię prowadzili kędy kolwiek pójdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '6', '6', 'Abowiem nie chcę was teraz widzieć śpiesząc się w drogę, ale spodziewam się, iż pomieszkam z wami do czasu, jeśli Pan dopuści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '7', '7', 'Pomieszkam też w Efezie aż do świątek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '8', '8', 'Abowiem drzwi mi są otworzone wielkie i skuteczne i wiele przeciwników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '9', '9', 'A jeśliby przyszedł Tymoteusz, patrzcie aby bespiecznie mieszkał między wami, bo uczynek Pański sprawuje jako i ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '10', '10', 'Niechaj go tedy nikt sobie lekce nie waży, ale odprowadźcie go spokojnie, aby do mnie przyszedł, gdyż go czekam z bracią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '11', '11', 'Przytym co się tycze Apollosa brata, barzom go prosił, aby szedł do was z bracią, ale koniecznie nie miał teraz wolej idź, ale przyjdzie gdy czas po temu mieć będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '12', '12', 'Czujcież i stójcie w wierze, mężnie sobie poczynacie, bądźcie stałemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '13', '13', 'Wszytki rzeczy wasze niech będą z miłością sprawowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '14', '14', 'A proszę was bracia: Wiecie dom Stefany być pierwszym w Achaijej, a iż się podał na posługowanie świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '15', '15', 'Abyście byli poddani takowym i każdemu, który pomaga i pracuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '16', '16', 'Raduję się z przyjścia Stefaniego i Fortunata i Achaika, abowiem ci nagrodzili mi niebytność waszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '17', '17', 'Bo ochłodzili ducha mego i waszego, a przetoż poznajcie takowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '18', '18', 'Pozdrawiają was zbory Azijej, pozdrawiają was barzo w Panu Akilla i Pryscylla i z tym zborem, który jest w domu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '19', '19', 'Pozdrawiają was bracia wszyscy. Pozdrówcie jedni drugie świętym pocałowaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '20', '20', 'Pozdrowienie ręką moją Pawłową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '21', '21', 'Jeśli kto nie miłuje Pana Jezusa Krystusa, niech będzie przeklętym i Maranata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '22', '22', 'Łaska Pana Jezusa Krystusa niech będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1co', '15', '23', '23', 'Miłość moja jest z wami wszytkimi w Krystusie Jezusie Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '0', '0', 'Paweł Apostoł Jezusa Krystusa przez wolę Bożą i Tymoteusz brat, kościołowi Bożemu, który jest w Koryncie, ze wszytkimi świętemi, którzy są we wszytkiej Achaijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '1', '1', 'Łaska niech wam będzie i pokój od Boga Ojca naszego i od Pana Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '2', '2', 'Niech będzie błogosławiony Bóg i Ojciec Pana naszego Jezusa Krystusa, Ojciec on namiłosierniejszy, a Bóg wszelakiego pocieszenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '3', '3', 'Który nas cieszy we wszelkim ucisku naszym, ku temu abyśmy mogli cieszyć ty, którzy są w każdym utrapieniu przez pociechę, którą nas samy cieszy Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '4', '4', 'Bo jako w nas obfitują utrapienia Krystusowe, tak przez Krystusa obfituje i pociecha nasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '5', '5', 'I choć bywamy utrapieni, toć bywa dla pociechy waszej i zbawienia, które się sprawuje w cierpliwości tychże ucisków, które i my cierpimy, a nadzieja nasza o was mocna jest. Choć też pociechy bierzemy, tedy to bywa dla waszej pociechy i zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '6', '6', 'Wiedząc to, iż jako jesteście uczestniki utrapienia, tak też będziecie uczestniki pociechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '7', '7', 'Abowiem nie chcemy abyście nie mieli wiedzieć bracia, o ucisku naszym, któryśmy mieli w Azijej, to jest, iżeśmy barzo nad przemożenie nasze byli obciążeni, tak iżeśmy prawie zwątpili i o żywocie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '8', '8', 'Ale i sami u siebie mieliśmy wyrok śmierci, abyśmy nie dufali sami w sobie, ale w Bogu, który wzbudza umarłe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '9', '9', 'A który z takowej śmierci wybawił nas i wybawia, w którym nadzieję mamy, iż też jeszcze wybawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '10', '10', 'Gdy też wy będziecie nam pomagać modlitwą, którą za nas czynić będziecie, aby za dobrodziejstwo, które nam jest od wiela ich dane, były czynione dzięki i za nas od wiela ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '11', '11', 'Abowiem toć jest przechwalanie nasze, świadectwo sumnienia naszego, iż z prostością i szczerością Bożą, nie z mądrością cielesną, ale z łaską Bożą przebywaliśmy na świecie, a zwłaszcza między wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '12', '12', 'Bo nie insze rzeczy piszemy wam, niźli czytacie, abo też znacie, a nadziewam się, iż do końca też poznacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '13', '13', 'Jakoście też poznali nas poczęści, iż jesteśmy przechwaleniem waszym, jako wy naszym, w dzień Pana Jezusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '14', '14', 'A tak z tym ufaniem chciałem przyjdź do was przedtym, abyście wtóre dobrodziejstwo mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '15', '15', 'I przez was przejdź do Macedonijej i zasie z Macedonijej przejdź do was i od was być doprowadzon do Żydowskiej ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '16', '16', 'A gdym to umyślił, izalim użył jakiej niestateczności? Abo izali ty rzeczy, które myślę, według ciała myślę, aby we mnie było Tak i Nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '17', '17', 'Owszem wierny jest Bóg, iż mowa nasza do was nie była tak i nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '18', '18', 'Abowiem Syn Boży Jezus Krystus, który między wami przez nas jest przepowiedan, to jest przez mię i przez Sylwana i Tymoteusza, nie był Tak i Nie, ale Tak w nim było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '19', '19', 'Abowiem ile jest obietnic Bożych, w nim są Tak, a w nim Amen, ku chwale Bożej przez nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '20', '20', 'Przytym ten, który nas potwierdza z wami w Krystusie i który nas pomazał, jestci Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '21', '21', 'Który też zapieczętował nas i dał zadatek ducha w sercach naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '22', '22', 'A ja wzywam Boga na świadectwo, na duszę moję, iż folgując wam jeszczem nie przyszedł do Koryntu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '0', '23', '23', 'Nie iżebyśmy panowali nad wiarą waszą, ale iż jesteśmy słudzy wesela waszego, abowiem wiarą stoicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '0', '0', 'Alem to umyślnie uczynił, abych zasię nie przyszedł z zasmuceniem do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '1', '1', 'Bo jeślibych ja was zasmucił, a któż jest coby mię uweselił, jedno ten, który smutek ma ze mnie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '2', '2', 'A tom ci wam napisał, abych przyszedszy nie miał z tych smutku, z których bych się miał weselić, ufając o was wszytkich, że wesele moje wszytkich was jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '3', '3', 'Bomci wam napisał z wielkiego ucisku i utrapienia serca mojego z wiela łez, nie abyście byli zasmuceni, ale abyście miłość moję poznali, którą ja mam barzo obfitą przeciwko wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '4', '4', 'A jeśliż kto przyniósł to zasmucenie, nie mnieć zasmucił, ale poczęści was wszytki, abych onego nie obciążył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '5', '5', 'Dosyćci ma takowy człowiek na zfukaniu tym, które się od wiela ich stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '6', '6', 'Tak iżbyście wy mu daleko więcej odpuścić mieli i cieszyć go, by snadź zbytnim smutkiem nie był pochłanion takowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '7', '7', 'Przeto was proszę, abyście miłość przeciwko niemu skuteczną okazali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '8', '8', 'Abowiemci też dlategom był napisał, abych doświadczenie wasze poznał, to jest jeśliże na wszytko posłuszni jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '9', '9', 'A komu co przepuścicie i ja też, abowiem jeślim ja co przepuścił komu, dla wasem przepuścił przed oblicznością Krystusową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '10', '10', 'Aby nas szatan nie posiadł, abowiemci wiem i zmyślania jego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '11', '11', 'A gdym przyszedł do Troady ku przepowiedaniu Ewanjelijej Krystusowej i gdy mi są drzwi przez Pana otworzone,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '12', '12', 'Nie miałem ulżenia duchowi mojemu, iżem nie nalazł Tyta brata mojego, ale pożegnawszy się z nimi, wyszedłem do Macedonijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '13', '13', 'A dziękuję Bogu, który nas zawsze zwycięzcami czyni w Krystusie i wonność uznania swego objawia przez nas na każdym miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '14', '14', 'Bo jesteśmy dobrą wonnością Krystusową Bogu w tych, którzy zbawieni bywają i w tych, którzy giną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '15', '15', 'Tym wonnością śmierci na śmierć, a onym wonnością żywota ku żywotowi, ale ku tym rzeczom któż jest godny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '1', '16', '16', 'Bo nie przekupujemy, jako wiele ich, słowem Bożym, ale jako z szczerości, a jako z Boga przed oblicznością Bożą o Krystusie mówimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '0', '0', 'Izaź się zasię zalecać poczynamy? Czyli potrzebujemy, jako niektórzy, listów zalecających do was, abo też od was zalecających?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '1', '1', 'Listem naszym wy jesteście napisanym w sercach naszych, który znają i czytają wszyscy ludzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '2', '2', 'Gdy się okazuje, że jesteście listem Krystusowym przez naszę posługę sprawionym, napisanym nie inkaustem, ale duchem Boga żywego, nie na tablicach kamiennych, ale na tablicach serca cielesnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '3', '3', 'A mamyć takowe ufanie ku Bogu przez Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '4', '4', 'Nie iżebyśmy byli godni sami od siebie co myśleć jakoby z nas samych, ale godność nasza z Boga jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '5', '5', 'Który też uczynił nas godnemi sługami Nowego Testamentu, nie pisma, ale ducha, abowiem Pismo zabija, a duch ożywia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '6', '6', 'A jeślić też posługa śmierci w piśmie wyrażona na kamieniach była chwalebna tak, iż też synowie Izraelowi nie mogli patrzyć na oblicze Mojżeszowe, dla chwały oblicza jego, która miała zniszczeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '7', '7', 'Jakoż nie daleko więcej posługa ducha będzie chwalebna?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '8', '8', 'Bo jeślić posługa potępienia jest chwalebna, dalekoć więcej posługa sprawiedliwości obfituje w chwalę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '9', '9', 'Abowiem to, co było uwielbiono, nie z tejci strony było uwielbiono, to jest co się tycze chwały przewyższającej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '10', '10', 'Bo jeślić to, co miało zniszczeć było chwalebne, dalekoć to więcej co trwa jest chwalebne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '11', '11', 'Przeto mając taką nadzieję, prawie jawnej mowy używamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '12', '12', 'A nie jesteśmy jako Mojżesz, który kładł zasłonę na oblicze swoje, dlatego aby synowie Izraelowi nie patrzyli na koniec tego, co miało zniszczeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '13', '13', 'Ale zatwardzone są zmysły ich. Abowiem aż i do dzisiejszego dnia taż zasłona w czytaniu Starego Testamentu trwa, nie będąc odkryta, która przez Krystusa zniszcona bywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '14', '14', 'Ale aż do dzisiejszego dnia, gdy Mojżesz czytan bywa, zasłona leży na sercu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '15', '15', 'A gdy się do Pana nawróci, będzie odjęta zasłona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '16', '16', 'A Pan jestci duch, a gdzieć duch Pański, tamci wolność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '2', '17', '17', 'A my wszyscy odkrytym obliczem w chwale Pańskiej, jako w zwierciedle się przypatrując, w toż wyobrażenie przemienieni bywamy z chwały w chwalę, tak jako od ducha Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '0', '0', 'Dlatego mając urząd takowy, jako ci, którzyśmy otrzymali miłosierdzie, nie osłabiamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '1', '1', 'Aleśmy odrzucili precz pokrywania nieprzystojne, nie postępując w przewrotności, ani fałszując słowa Bożego, ale okazaniem prawdy zalecając się u wszelkiego sumnienia ludzi przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '2', '2', 'A jeśli też jest zakryta Ewanjelija nasza, jestci zakryta tym, którzy giną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '3', '3', 'W których Bóg świata tego oślepił zmysły, to jest w niewiernych, aby im nie świeciła światłość Ewanjelijej Krystusowej, który jest wyobrażeniem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '4', '4', 'Abowiem nie sami siebie przepowiedamy, ale Krystusa Jezusa Panem, a sami siebie sługami waszemi dla Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '5', '5', 'Abowiem Bóg, który rzekł, aby się z ciemności światłość wyjaśniła, tenci jest, który się oświecił w sercach naszych ku podaniu światłości uznania chwały Bożej w obliczu Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '6', '6', 'A mamyć ten skarb w naczyniu glinianym, aby tej mocy zacność była Boża, a nie z nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '7', '7', 'Gdy my we wszem bywamy utrapieni, ale przedsię nie jesteśmy ścieśnieni, wątpimy, ale nie zwątpimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '8', '8', 'Prześladowanie cierpimy, ale nie bywamy opuszczeni, bywamy zrzuceni, ale nie giniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '9', '9', 'Zawsze umartwienie Pana Jezusowe nosząc na ciele, aby on żywot Pana Jezusów w naszym ciele był okazan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '10', '10', 'Abowiem my, którzy żywiemy, zawsze bywamy wydani na śmierć dla Jezusa, aby też żywot Jezusów był okazan w śmiertelnym ciele naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '11', '11', 'A także w nas okazuje śmierć moc swoję, a żywot w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '12', '12', 'A iż mamy tegoż ducha wiary, jako napisano jest: Uwierzyłem, a przeto żem mówił i my też wierzymy i dlatego też mówimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '13', '13', 'Wiedząc, iż ten, który wzbudził Pana Jezusa i nas wzbudzi przez Jezusa i postawi z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '14', '14', 'Abowiemci wszytki rzeczy są dla was, aby łaska ona barzo obfita, z dziękowaniem wiela ich, obfitsza się stała ku chwale Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '15', '15', 'Dlategoć nie osłabiamy, ale choć też nasz zwierzchni człowiek zepsowan bywa, przedsię wnętrzny człowiek odnawia się na każdy dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '16', '16', 'Abowiem barzo krótki a letki ucisk nasz, zacnie a znamienicie sprawuje nam wiekuistą wagę chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '3', '17', '17', 'Gdyż my nie patrzymy na rzeczy, które są widome, ale na te, które niewidome są, abowiem te, które są widome, są doczesne; abowiem niewidome są wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '0', '0', 'Zaiste wiemy, iż jeśli przybytek tego ziemskiego domu naszego będzie skażon, zbudowanie mamy od Boga, to jest mieszkanie nie urobione ręką, wiekuiste na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '1', '1', 'Abowiem dlatego wzdychamy, żądając być przyobleczeni przybytkiem naszym, który jest z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '2', '2', 'Jeśliże będziemy nalezieni przyobleczeni, a nie nagiemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '3', '3', 'Bo my, którzy jesteśmy w tym przybytku, wzdychamy będąc obciążonemi, w którym nie żądamy być zwleczeni, ale przyobleczeni, aby pożarta była śmiertelność od żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '4', '4', 'Przy tym ten, który nas stworzył ku temu, jestci Bóg, który też nam dał zadatek Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '5', '5', 'Dufając tedy zawsze i wiedząc, że jesteśmy przychodniami w ciele, jesteśmy od Pana daleko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '6', '6', 'Abowiem przez wiarę chodzimy, a nie przez widzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '7', '7', 'Dufamy też i wolimy precz wynidź z ciała, a iść do Pana mieszkać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '8', '8', 'A przeto też uprzejmie żądamy i mieszkając i precz idąc, byśmy byli jemu wdzięcznymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '9', '9', 'Abowiem mamy stawać jawnie wszyscy przed stolicą Krystusową, aby każdy odniósł coby na ciele wziął słusznie wedle tego, co uczynił, abo dobrego abo złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '10', '10', 'Wiedząc tedy on strach Pański, ludzie ku wierze przywodzimy, a jestechmy u Boga jawnemi i nadziewam się, iż też w sumnieniach waszych jesteśmy jawnemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '11', '11', 'A iście nie powtóre sami siebie zalecamy wam, ale wam dajemy przyczynę, abyście się chlubili z nas, żebyście mieli co rzec przeciwko tym, którzy z oblicza chwały szukają, a nie z serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '12', '12', 'Bo choć szalejemy, Bogu szalejemy, choć jesteśmy rozumu zupełnego, wam jesteśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '13', '13', 'Gdyż ona miłość Krystusowa przyciska nas i gdy żechmy też to postanowili, iż jeśli jeden za wszytki umarł, tedy wszyscy byli umarłymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '14', '14', 'A za wszytki umarł, aby ci, którzy żyją napotym sobie nie żyli, ale temu który za nie umarł i powstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '15', '15', 'Przeto napotym żadnego nie znamy wedle ciała, choć też uznaliśmy Krystusa wedle ciała, wszakże teraz więcej nie znamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '16', '16', 'A tak, jeśli kto jest w Krystusie, nowemci stworzeniem jest, stare rzeczy przeminęły, oto się wszytki nowe stały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '17', '17', 'A wszytki rzeczy są z Boga, który nas sobie pojednał przez Jezusa Krystusa i dał nam posługowanie zjednania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '18', '18', 'Abowiem Bóg był w Krystusie świat sobie jednając, nie przyczytając im grzechów ich i położył w nas mowę tę zjednania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '19', '19', 'A tak w imię Krystusowe jesteśmy posły, jakoby Bóg was prosił przez nas. Prosimy tedy dla Krystusa, abyście się pojednali z Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '4', '20', '20', 'Abowiem to uczynił iż ten, który nie uznał grzechu, stał się grzechem za nas, abyśmy się stali sprawiedliwością Bożą w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '0', '0', 'Owszem, jako słudzy napominamy was, abyście wy darmo łaski Bożej nie przyjęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '1', '1', 'Abowiem mówi Bóg: Czasu przyjemnego wysłuchałem cię, a w dzień zbawienia ratowałem cię. Oto teraz czas przyjemny, oto teraz dzień zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '2', '2', 'Nie dawajmy żadnego pogorszenia w żadnej rzeczy, aby posługowanie nie było posromocone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '3', '3', 'Ale i we wszytkim stawamy się jako słudzy Boży, z wielką cierpliwością, w utrapieniu i w potrzebach i w uciskach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '4', '4', 'W ubiciu, w więzieniu, w rosterkach, w pracach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '5', '5', 'Przez czujności, przez posty, przez szczyrość, przez umiejętność, przez cichość, przez dobroć, przez Ducha Świętego, przez miłość nieobłudną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '6', '6', 'W mowie prawdy, w mocy Bożej, przez zbroję sprawiedliwości, ręką prawą i lewą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '7', '7', 'Przez chwałę i sromotę, przez osławienie i dobrą sławę; jakoby zwodnicy, ale prawdziwi jesteśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '8', '8', 'Jako nieznajomi, ale znajomi jesteśmy; jako umierający, ale oto żywiemy; jako będąc pokarani, ale nie jesteśmy zabici.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '9', '9', 'Jako żałujący, ale zawsze weseli; jako ubodzy, ale ich wiele ubogacamy; jako nic niemający, ale wszytko trzymamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '10', '10', 'O Koryntowie! Usta nasze otworzone są ku wam, serce nasze rozszerzyło się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '11', '11', 'Nie mieszkacie ciasno w nas, ale jesteście ściśnieni w wnętrznościach waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '12', '12', 'A ja od was żądam, abyście mi toż oddali, mówię jako synom, rozprzestrzeńcież się i wy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '13', '13', 'Nie bądźcie złączeni nierządnym jarzmem z niewiernemi. Abowiem co za towarzystwo sprawiedliwości z niesprawiedliwością? A co za społeczność światłości z ciemnościami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '14', '14', 'A co za zgoda jest Krystusowi z Belijalem? Abo co za część wiernemu z niewiernym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '15', '15', 'A co za zezwolenie jest kościołowi Bożemu z bałwany? Abowiem wy jesteście kościołem Boga żywego, jako mówił Bóg: Będę mieszkał w nich i chodzić będę i będę Bogiem, a oni mnie będą ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '16', '16', 'A przeto wynidźcie z pośrodku ich i odłączcie się, mówi Pan, a nieczystego nie tykajcie się, tedy przyjmę was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '5', '17', '17', 'I będę wam za ojca, a wy będziecie mi za syny i córki, mówi Pan wszechmogący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '0', '0', 'Mając tedy namilejszy ty obietnice, oczyśćmyż sami siebie od wszelakiego splugawienia ciała i ducha, dokonywając poświęcenia z bojaźnią Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '1', '1', 'Przyjmicież nas, żadnemuśmy krzywdy nie uczynili, żadnegośmy nie skazili, żadnegośmy nie złupili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '2', '2', 'Ku osądzeniu waszemu nie mówię, bo przedtymem rzekł, iż wy w sercach naszych jesteście ku społecznej śmierci, także i społecznemu życiu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '3', '3', 'Mam wielkie beśpieczeństwo mówienia u was, mam wielkie chlubienie z was, jestemci napełnion pociechy, a dziwnie obfituję weselem we wszytkim utrapieniu naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '4', '4', 'Abowiem przyszedszy do Macedonijej, ciało nasze żadnego ulżenia nie miało, owszem we wszytkim byliśmy uciśnieni, zewnątrz bitwy, a wewnątrz strachy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '5', '5', 'Ale ten, który cieszy utrapione, pocieszył nas Bóg przyściem Tytusowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '6', '6', 'A nie tylko przyściem jego, ale też pociechą, którą on wziął z was, gdy nam oznajmił uprzejme żądanie wasze, płacz wasz i chuć waszę dla mnie, tak iżem się jeszcze więcej weselił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '7', '7', 'Abowiem chociam was zasmucił przez list, tego mi nie lito, chociam żałował, bo widzę, iż on list was zafrasował, chociaż do czasu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '8', '8', 'Teraz raduję się, nie iżeście zasmuceni byli, ale iżeście byli zafrasowani ku upamiętaniu. Abowiem byliście zasmuceni wedle Boga tak, iż w żadnej rzeczy szkodyście nie podjęli dla nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '9', '9', 'Abowiem smutek, który jest wedle Boga, tenże upamiętanie sprawuje ku zbawieniu, którego nigdy żałować nie możemy, aleć smutek świata śmierć sprawuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '10', '10', 'Abowiem oto iżeście wedle Boga byli zasmuceni, jaką wielką pilność w was sprawiło i owszem, jako pilną obmowę, owszem żałość, owszem bojaźń, owszem uprzejme żądanie, owszem zapalczywą chuć, owszem pomstę? Na koniec we wszytkim jawnieście okazali, iżeście czystemi w tej rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '11', '11', 'Zaiste chociam do was pisał, nie pisałem wam dla onego, który był krzywdę uczynił, ani dlatego, któremu się krzywda stała, ale iżby między wami znaczna była ona pilność nasza o was przed oblicznością Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '12', '12', 'Dlategośmy pociechę wzięli z pociechy waszej, aleśmy się nawięcej radowali z wesela Tytusowego, iż ochłodzon jest duch jego od was wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '13', '13', 'Abowiem jeślim u niego z was się w czym przechwalał, nie zawstydziłem się, jakośmy wszytki rzeczy prawdziwie mówili wam, tak też i przechwalanie nasze u Tytusa, prawdziwym jest znalezione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '14', '14', 'A wnętrzności jego ku wam więcej wzbudzone są, gdy wspamiętywa posłuszeństwo wszytkich was i jakoście go z bojaźnią i z strachem przyjęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '6', '15', '15', 'Raduję się tedy, iż wam we wszytkim dufam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '0', '0', 'Przytym bracia, oznajmujemy wam łaskę Bożą daną kościołom Macedonijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '1', '1', 'Iż w wielkim doświadczeniu utrapienia obfitowało wesele ich a ubóstwo, którym byli prawie do gruntu wyniszczeni opłynęło ku wielkiej szczodrobliwości ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '2', '2', 'Abowiem świadkiem jestem, iż oni wedle możności i też nad możność dobrotliwemi byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '3', '3', 'Z wiela próźb żądając nas, abyśmy przyjęli łaskę i społeczność posługi, która świętym bywa czyniona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '4', '4', 'A nie tak, jakośmy się spodziewali, ale sami siebie oddali naprzód Panu, potym i nam przez wolę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '5', '5', 'Ku temu, abyśmy napominali Tytusa, iżby jako przed tym począł, tak też dokonał u was tejże łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '6', '6', 'A tak jako we wszytkim obfitujecie wiarą i mową i umiejętnością i wszelaką pilnością i swą miłością przeciwko nam, tak też abyście w tej łasce obfitowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '7', '7', 'Nie roskazującci mówię, ale przez staranie inych, szczyrość miłości waszej oświadczając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '8', '8', 'Abowiem wiecie dobrotliwość Pana naszego Jezu Krysta, iż się dla nas ubogim stał, będąc bogaty, abyście ubogaceni byli ubóstwem jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '9', '9', 'A w tej rzeczy zdanie moje opowiedam: abowiem to wam pożyteczno, którzy nie tylko czynić, ale i chcieć przedtymeście poczęli, roku przeszłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '10', '10', 'A teraz to, coście czynić poczęli, dokonywajcie, aby jako było ochotne ono chcenie, tak też aby było toż dokończenie z tego, co macie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '11', '11', 'Abowiem jeśli przed tym był ochotny umysł, tenże jest przyjemny wedle tego, co kto ma, a nie wedle tego, co nie ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '12', '12', 'Bo nie aby inszy mieli ulżenie, a wy utrapienie, ale zarówno na ten czas wasza obfitość nagradza ich niedostatek, aby też i ich obfitość wasz niedostatek nagradzała, żeby było porównanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '13', '13', 'Jako jest napisano: Który wiele nazbierał, nie więcej miał, a kto mało, nie mniej miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '14', '14', 'A dziękuję Bogu, który włożył to staranie o was w serce Tytusowe,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '15', '15', 'Iż ono napominanie za wdzięczne przyjął, że też więtszą pilność okazując, dobrowolnie do was przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '16', '16', 'Posłaliśmy też wespół z nim brata onego, którego chwała jest w Ewanjelijej po wszytkich kościołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '17', '17', '(A nie tylko to, ale też obran jest od kościołów za towarzysza drogi naszej, z tą dobrotliwością, która bywa szafowana od nas ku chwale samego Pana i ochotnemu sercu waszemu).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '18', '18', 'Wystrzegając się tego, aby was kto nie ganił, w tej szczodrości, która bywa szafowana od nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '19', '19', 'Którzy poczciwych rzeczy pilnujemny, nie tylko przed Bogiem, ale też przed ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '20', '20', 'Posłaliśmy też z nimi brata naszego, któregośmy często doświadczali w wielu rzeczach być pilnym, a teraz daleko pilniejszym dla wielkiego ufania, które mam o was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '21', '21', 'A jeśli idzie o Tytusa, jestci moim towarzyszem i u was pomocnikiem, a jeśliż o bracia nasze, sąć posłowie kośćiołowi i chwałą Krystusową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '7', '22', '22', 'A tak doświadczenie miłości waszej i przechwalania naszego o was, pokażcie nad nimi przed oblicznością kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '0', '0', 'Abowiem o posługiwaniu, które bywa czynione świętym, nie potrzeba abych wam pisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '1', '1', 'Bo znam ochotne serce wasze, którym się przechwalam o was u Macedończyków, iż Achaja gotowa jest od roku przeszłego, a chuć która od was wyszła, wiele ich pobudziła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '2', '2', 'I posłałem ty braty, iżby przechwalanie nasze o was nie było próżne w tej mierze, abyście jakom powiedział gotowymi byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '3', '3', 'By snadź jeśliby ze mną przyszli Macedończycy, a zastaliby was niegotowe, abychmy my nie byli zawstydzeni (a mówię, by jedno nie wy) w tym tak beśpiecznym przechwalaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '4', '4', 'A tak zdało mi się za potrzebną rzecz napomionać bracia, aby pierwej do was przyszli, a pierwej zgotowali waszę szczodrobliwość przedtym opowiedzianą, aby była gotowa tak jako szczodrobliwość, a nie jako rzecz poniewolna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '5', '5', 'A toć mówię: Który sieje skąpie, skąpie też żąć będzie; a kto sieje hojnie, też hojnie żąć będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '6', '6', 'Każdy jako umyślił w sercu swoim, tak niechaj da, nie z frasunkiem, ani z przymuszenia: Abowiem wesołego rozdawcę Bóg miłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '7', '7', 'A Bóg mocen jest uczynić, aby opłynęła w was wszelaka dobrotliwość, abyście we wszytkim zawsze mając ile potrzeba, obfitowali w każdym uczynku dobrym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '8', '8', 'Jako napisano jest: Rozdzielił i rozdał ubogim, szczodrobliwość jego trwa na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '9', '9', 'A kto dodaje nasienia siejącemu, tenże wam doda i chleba na pokarmy i rozmnoży osiewki wasze i przysporzy owoców szczodrobliwości waszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '10', '10', 'Abyście we wszytkich rzeczach byli ubogaceni ku wszelakiej szczodrobliwości, którą przez nas to sprawuje, iż dzięki Bogu bywają czynione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '11', '11', 'Abowiem szafowanie tej jałmużny, nie tylko nagradza niedostatek świętych, ale też w tym obfituje, iż za to wiele ich Bogu dziękują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '12', '12', '(Gdyż przez doświadczenie tej posługi Boga chwalą, za zgodne wasze poddaństwo pod Ewanjeliją Krystusowę i za życzliwe przeciwko sobie i wszytkim użyczanie).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '13', '13', 'A iż się też oni modlą za wami i żądając was dla łaski Bożej, która się w was zacnie okazuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '8', '14', '14', 'A niech będzie Bogu chwała za niewymowny dar jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '0', '0', 'A ja sam Paweł proszę was przez dobrotliwość i litość Krystusowę, który oblicznie jestem pokorny między wami, a w niebytności jestem śmiały przeciwko wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '1', '1', 'A otoć proszę, abych będąc oblicznie nie był śmiałym tym ufaniem, którym myślę być śmiałym na niektóre, co o nas rozumieją, jakobyśmy według ciała chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '2', '2', 'Abowiem chodząc w ciele, nie według ciała walczymy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '3', '3', 'Bo zbroje walki naszej nie są cielesne, ale z mocy Bożej mocne i ku podwróceniu miejsc obronnych, któremi rady podwracamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '4', '4', 'I wszelaka wysokość, która się wyszła przeciw poznaniu Bożemu i pojmaną wiedziemy wszelką myśl ku posłuszeństwu Krystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '5', '5', 'I gotową mamy pomstę wszelakiej krnąbrności, gdy napełnione będzie posłuszeństwo wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '6', '6', 'Izali się na rzeczy zwierzchowne oglądacie? Jeśli kto sobie dufa, iż jest Krystusów, niech zasię to sam z siebie myśli, iż jako sam jest Krystusów, tak też i my Krystusowemi jesteśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '7', '7', 'Abowiem jeślibych się ja nieco więcej przechwalał z mocy naszej, którą nam dał Pan ku zbudowaniu, a nie ku podwróceniu waszemu, nie zawstydzę się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '8', '8', 'Abym się nie zdał, jakobych was chciał przestraszyć przez listy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '9', '9', 'Abowiemci (mówi) listy cięszkie są i mocne, ale obliczność ciała jest niemocna, a mowa niczemna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '10', '10', 'Toć niechaj myśli takowy, iż jakowi jesteśmy słowem przez listy w niebytności, takowi też oblicznie będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '11', '11', 'Abowiem nie śmiemy się policzać abo równać z niektóremi, którzy sami siebie zalecają, ale nie rozumieją, iż się sami sobą miarkują i sami się sobie przyrównawają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '12', '12', 'Ale my nie będziem się chlubić z tych rzeczy, które naszego pomiaru nie są, owszem wedle rozmierzenia słusznego onego pomiaru, którego nam Bóg udzielił, będziem się chlubić, iżeśmy aż też do was przyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '13', '13', 'Abowiem jakobyśmy do was nie przyszli, nazbyt sami siebie nie wyciągamy, bośmy też aż do was przyszli w Ewanjelijej Krystusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '14', '14', 'Nie przechwalając się z tych rzeczy, które naszego pomiaru nie są, to jest z cudzych prac, ale nadziewając się, iż gdy się rozmnoży wiara wasza przez nas będziem barzo wywyższeni w naznaczonym pomiarze naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '15', '15', 'Abym w onych stronach, które za wami są, opowiedał Ewanjeliją, nie abyśmy się przechwalali w pomiarze drugiemu naznaczonym, to jest w rzeczach gotowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '16', '16', 'Ale który się przechwala, niech się w Panu przechwala.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '9', '17', '17', 'Abowiem ten, który się zaleca, nie bywa pochwalon, ale ten którego Pan zaleca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '0', '0', 'Boże daj to, abyście znosili maluczko głupstwo moje, a iścieć wy mię znosicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '1', '1', 'Abowiem żądam was z chutliwą miłością Bożą, bom was nagotował, którebym jednemu mężowi za panienkę czystą stawił, to jest Krystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '2', '2', 'Ale się boję, iż jako wąż zwiódł Ewę chytrością swoją, tak aby skażone umysły wasze nie odstąpiły od szczerości, która jest w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '3', '3', 'Abowiem jeśliby przyszedł ten, co by inszego Jezusa przepowiedał, któregośmy my nie przepowiedali, abo jeśli przyjmujecie inego ducha, któregoście nie wzięli, abo inszą Ewanjeliją, którejeście nie przyjęli, tedybyście go dobrze cierpieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '4', '4', 'Boć mnimam, iżem ni w czym nie był niższym niźli nazacniejszy Apostołowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '5', '5', 'A chociem jest prostak w mowie, a wszakże nie w umiejętności: aleśmy koniecznie byli jawni we wszytkich rzeczach w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '6', '6', 'Izalim zgrzeszył, żem samego siebie uniżył, abyście byli wywyższeni, a iżem darmo Ewanjeliję Bożą przepowiedał wam?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '7', '7', 'Złupiłem ine kościoły, wziąwszy od nich zapłatę ku temu, abym wam posłużył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '8', '8', 'A gdym był u was i potrzebowałem, nie byłem próżnującym z czyją szkodą, abowiem czego mi nie dostawało, dodali bracia, którzy byli przyszli z Macedonijej, a we wszytkim strzegłem, abym wam nie był cięszkim i strzedz będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '9', '9', 'Jestci prawda Krystusowa we mnie, iż to przechwalanie nie będzie zawarte przeciwko mnie w krainach Achajskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '10', '10', 'A czemuż? Przetoli, iż was nie miłuję? Bóg to wie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '11', '11', 'Ale co czynię, jeszcze uczynię, abym odjął przyczynę tym, którzy przyczyny żądają, aby w tym z czego się chlubią byli nalezieni takowi jako my.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '12', '12', 'Abowiem takowi fałszywi apostołowie są robotniki chytremi, przemienieni w Apostoły Krystusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '13', '13', 'A toć nie dziwno, abowiem sam szatan przemienia się w Anioła światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '14', '14', 'Nie wielkać tedy rzecz, jeśli też służebnicy jego przemieniają się na kształt sług sprawiedliwości, których dokończenie będzie podług uczynków ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '15', '15', 'Powtóre powiedam, aby mię kto nie mnimał być głupim. Bo inaczej przyjmujecie mię też, choć jako głupiego, abym się ja też maluczko przechwalał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '16', '16', 'Co mówię, nie mówięć wedle Pana, ale jako przez głupstwo, w tym bespiecznym przechwalaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '17', '17', 'Gdyż wiele się ich przechwala wedle ciała i ja się też przechwalać będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '18', '18', 'Abowiem radzi znosicie głupie, będąc sami mądremi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '19', '19', 'Bo znosicie, jeśli was kto zniewala, jeśli was kto pożyra, jeśli kto bierze, jeśli się kto wywyższa, jeśli kto was w lice bije.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '20', '20', 'Jać to ku sromocie mówię, dlatego iżechmy byli mdłymi i owszem, w czymby kolwiek kto chciał być śmiały (z głupstwa to mówię) i ja też śmiałym będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '21', '21', 'Ebrejczycy są? Jestem i ja. Izraelczycy są? I jam też jest. Plemieniem Abrahamowym są? Jestem i ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '22', '22', 'Słudzy Krystusowi są (głupieć mówię) przewyższam ja w pracach obficie, w ubiczowaniu nad miarę, w więzieniu obficie, w śmierciach często.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '23', '23', 'Od Żydów podjąłem pięć kroć czterdzieści plag bez jednej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '24', '24', 'Trzy kroć byłem ubiczowan, razem był ukamienowan, trzy kroć byłem w rozbiciu okrętu, byłem w morzu głębokiem we dnie i w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '25', '25', 'W drogach częstom bywał, w niebeśpieczeństwach rzek, w niebeśpieczeństwach zbójców, w niebeśpieczeństwach od swoich przyrodnych, w niebeśpieczeństwach od poganów, w niebeśpieczeństwach w mieście, w niebeśpieczeństwach na puszczy, w niebeśpieczeństwach na morzu, w niebeśpieczeństwach między fałszywą bracią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '26', '26', 'W pracy, w kłopociech, w czujnościach często, w głodzie i w pragnieniu, w pościech często, w zimnie i w nagości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '27', '27', 'Oprócz tych rzeczy, które się zwierzchu przydają, ciśnie mię ono wojsko, które każdy dzień na mię powstawa, to jest staranie o wszytkich kościelech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '28', '28', 'Kto bywa trapion, żebych ja też nie miał być trapion? Kto bywa obrażon, żebym ja też nie miał być upalon?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '29', '29', 'Jeśli się potrzeba przechwalać, będę się przechwalał z tego, co na moję krewkość należy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '30', '30', 'Bóg i ojciec Pana naszego Jezu Krysta chwalebny na wieki wie, iż ci nie kłamam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '31', '31', 'W Damaszku starosta króla Arety osadził był straż w mieście Damaszczeńczyków chcąc mię pojmać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '10', '32', '32', 'Alem był oknem w koszu spuszczon z muru i uszedłem rąk jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '0', '0', 'Iście mi nie jest pożyteczna rzecz przechwalać się, abowiemci przyjdę do widzenia i objawienia Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '1', '1', 'Znamci człowieka w Krystusie przed czternaścią lat (jeśli w ciele nie wiem, jeśli nie w ciele, nie wiem, Bógci wie), który był zachwycon aż do trzeciego nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '2', '2', 'A znam takowego człowieka (jeśli w ciele nie wiem, jeśli nie w ciele nie wiem, Bógci wie),');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '3', '3', 'Iż był zachwycon do raju i słyszał niewymówione słowa, których się człowiekowi nie godzi powiedać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '4', '4', 'Z takowego człowieka będę się przechwalał, a z siebie samego nie będę się przechwalał, wyjąwszy z krewkości moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '5', '5', 'Abowiem jeślibym się chciał przechwalać, nie będę głupim, boć prawdę powiem, aleć skromnie mówię, aby kto o mnie nie myślał nad to, czym mię być widzi, abo co słyszy odemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '6', '6', 'A iżbych zacnością objawienia nie był nad miarę wywyższon, dan mi jest stark w ciało i Anioł szatan, aby mię policzkował, żebych nie był nad miarę wywyższon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '7', '7', 'Dla czegom trzy kroć Pana prosił, aby ode mnie odszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '8', '8', 'I rzekł mi: Dosyć tobie na mej łasce, abowiem możność moja przez krewkość bywa dokończona. Barzo rad tedy będę się przechwalał radszej z krewkości mojej, aby we mnie mieszkała moc Krystusowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '9', '9', 'Dlatego się kocham w krewkościach, w letkościach, w potrzebach, w prześladowaniu, w uciskach dla Krystusa. Abowiem gdy nie mogę, tedym możny jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '10', '10', 'Stałem się głupim przechwalając się. Wyście mię przymusili, bomci ja od was miał być zalecan. Abowiem w żadnej rzeczy nie byłem podlejszym nad przedniejsze Apostoły, aczem też nic nie jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '11', '11', 'Znaki Apostolstwa mojego wykonane są u was, przez wszelaką cierpliwość, znaki i cuda i mocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '12', '12', 'Bo cóż jest czymbyście podlejszy byli nad insze zbory? Jedno iżem nie był próżnującym z waszą szkodą, przepuśćcie mi tę krzywdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '13', '13', 'Otoć trzeci raz jestem gotów przyść do was, a nie będę próżnującym z waszą szkodą, abowiem nie szukam waszych rzeczy, ale was, boć nie synowie mają rodzicom skarbić, ale rodzice synom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '14', '14', 'A jać barzo rad nałożę i będę wydan za dusze wasze, acz więcej was miłując, mniej sam miewam miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '15', '15', 'Ale niech tak będzie. Jamci was nie obciążył, aleć będąc chytrym, zdradom was pojmał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '16', '16', 'Izalim was na dobrach waszych złupił przez którego z tych, którem do was posłał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '17', '17', 'Prosiłem Tyta i posłałem z nim brata. Izali was w czym waszym złupił Tytus? Izaliśmy nie tymże duchem postępowali? Izali nie tymiż stopami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '18', '18', 'Izali zasię mniemacie, abyśmy się wam wymawiali? Przed oblicznością Bożą w Krystusie mówimy, a wszytkoć namilejszy dla waszego zbudowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '19', '19', 'Abowiem się boję, bych snadź przyszedszy, nie znalazł was nie jakiemi bych chciał, a ja też żebych nie był znalezion od was, jakiego byście wy mieć nie chcieli, by jako nie były swary, zazdrości, gniewy, zwady, obmowy, szemrania, pychy, rostyrki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2co', '11', '20', '20', 'By mię zasię Bóg mój, gdy przyjdę, nie poniżył w was, abych też nie żałował wiela z tych ludzi, którzy przedtym grzeszyli, a nie upamiętali się z nieczystości, wszeteczeństwa i nierządności, której się dopuścili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '0', '0', 'Paweł Apostoł nie od ludzi, ani przez człowieka, ale przez Jezusa Krystusa i Boga Ojca, który go wzbudził z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '1', '1', 'I wszyscy bracia, którzy są ze mną, kościołom Galicijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '2', '2', 'Łaska niech wam będzie i pokój od Boga Ojca i Pana naszego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '3', '3', 'Który samego siebie wydał za grzechy nasze, aby nas wyjął z teraźnijszego świata złego wedle wolej Boga i Ojca naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '4', '4', 'Któremu niech będzie chwała na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '5', '5', 'Dziwuję się, iż opuściwszy tego, który was był wezwał ku łasce Krystusowej, tak prętko bywacie przeniesieni do inszej Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '6', '6', 'Gdyż żadnej inszej nie masz, jedno niektórzy są co was trwożą i chcą wywrócić Ewnieliją Krystusowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '7', '7', 'Ale choćby i my, abo Anioł z nieba przepowiedał wam nad to cośmy wam przepowiedali, niech będzie przeklęty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '8', '8', 'Jakośmy przed tym powiedali, też teraz zasię powiedam. Jeśliby kto wam opowiedał nad to coście wzięli, niech będzie przeklętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '9', '9', 'Abowiem teraz izali wam do ludzi czyli do Boga radzę? Abo szukamli abym się podobał ludziam?  Abowiem jeślibych się jeszcze ludziam podobał, nie byłbych sługą Krystusowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '10', '10', 'A oznajmuję wam bracia, iż Ewanjelija przepowiedana odemnie, nie jest wedle człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '11', '11', 'Abowiem anim jej wziął od człowieka, anim nauczon, ale przez objawienie Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '12', '12', 'Boście słyszeli co za postępek mój był niekiedy w żydostwie, iżem nad miarę prześladował kościół Boży i burzyłem ji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '13', '13', 'I poprawowałem się w żydostwie nad wiele równych w narodzie moim, gdym był większym miłośnikiem ustaw, którem był wziął od ojca mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '14', '14', 'Ale gdy się podobało Bogu, który mię był odłączył z żywota matki mojej i wezwał z łaski swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '15', '15', 'Objawiać Syna swego we mnie, abym go przepowiedał między pogany, nie zarazemem się dokładał ciała i krwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '16', '16', 'Anim się wrócił  do Jeruzalem do tych, którzy przedemną byli Apostołami, alem szedł do Arabijej i wróciłem się zasię do Damaszku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '17', '17', 'Zatym po trzech lat wróciłem się do Jeruzalem, abym nawiedził Piotra i mieszkałem przy nim piętnaście dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '18', '18', 'A inszego z Apostołów nie widziałem żadnego, oprócz Jakuba brata Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '19', '19', 'A co wam piszę, oto przed Bogiem świadczę, iż nie wymyślam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '20', '20', 'Zatym przyszedłem do krain Syrijej i Cylicijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '21', '21', 'I byłem nieznajomym z twarzy kościołom ziemię Żydowskiej, które były w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '22', '22', 'Ale tylko usłyszeli: Ten, który prześladował nas niekiedy, teraz opwieda wiarę, którą przed tym burzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '0', '23', '23', 'I chwalili Boga zemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '0', '0', 'Potym we czternaście lat szedłem zasię do Jeruzalem wespół i z Barnabaszem, wziąwszy z sobą i Tytusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '1', '1', 'A szedłem z obiawienia i oznajmiłem im Ewanjeliją, którą przepowiedam między pogany, ale to z osobna z tymi, którzy byli zacnymi, iżbym snadź darmo nie biegł, abo też nie biegał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '2', '2', 'Ale i Tytus, który ze mną był, będąc Grekiem, nie był przymuszon obrzezać się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '3', '3', 'A to dla braciej obłudnych i fałszywych, którzy wkradli się aby wyszpiegowali wolność naszę, którą mamy w Jezusie Krystusie, aby nas zniewolili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '4', '4', 'Którym nie ustąpiliśmy bynamniej jakoby się im poddawając, aby prawda Ewanjelijej trwała u was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '5', '5', 'A od tych, którzy się zdadzą być czym (nie uczyłem się) i jakimi byli przed tym, nic mi do tego. Osobęć człowieczą Bóg nie brakuje. Abowiem którzy są w powadze, nic mi nie dołożyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '6', '6', 'I owszem, gdy widzieli, iż mi jest zwierzona Ewanjelija nieobrzezania, jako Piotrowi obrzezania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '7', '7', 'Abowiem ten, który był mocny przez Piotra na Apostolstwo obrzezania, tak też był mocny przez mię do poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '8', '8', 'A gdy poznali łaskę mnie daną Jakub, Kefas i Jan, które miano za słupy, dali mi i Barnabaszowi prawicę społeczności, abyśmy między pogany przepowiedali, a oni między obrzezanymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '9', '9', 'Tylko upominali abyśmy pamiętali na ubogie, o czym też starałem się abych tak czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '10', '10', 'A gdy przyszedł Piotr do Antjochijej , sprzeciwiłem się mu oblicznie przeto, iż był godzien naganienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '11', '11', 'Abowiem przed tym niż przyszli niektórzy od Jakuba, wespół i z pogany jadał, a gdy ci przyszli, skradał się i odłączył się od nich, bojąc się tych, którzy byli z obrzezania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '12', '12', 'I obłudnie się obchodzili wespół z nim ini też Żydowie tak, iż Barnabas też był uwiedzion w obłudność ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '13', '13', 'Ale gdym obaczył, iż nie prosto chodzili, jako przystało prawdzie Ewanjelijej, rzekłem Piotrowi przed wszytkimi: Jeśli ty będąc Żydem pogańskie żywiesz, a nie po żydowsku, czemu pogany przymuszasz, aby po żydowsku żyli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '14', '14', 'My z przyrodzenia jesteśmy Żydowie, a nie z poganów grzesznicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '15', '15', 'Wiedząc, iż nie bywa usprawiedliwion człowiek z uczynków zakonu, ale przez wiarę w Jezusa Krystusa, teżeśmy my w Krystusa Jezusa uwierzyli, abyśmy byli usprawiedliwieni z wiary Krystusowej, a nie z uczynków zakonu przeto, iż żadne ciało nie będzie usprawiedliwione z uczynków zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '16', '16', 'A jeśli my szukając abyśmy byli usprawiedliwieni przez Krystusa, bywamy znalezieni sami grzesznymi, izali Krystus jest sługa grzechu? Nie daj tego Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '17', '17', 'Abowiem jeśli zasię buduję ty rzeczy, którem popsował, czynię siebie samego przestępcą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '18', '18', 'Bom ja przez zakon zakonowi umarł, abym Bogu żył i jestem wespół z Krystusem ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '19', '19', 'A żywę już nie ja, ale żywie we mnie Krystus. A żywot, którym teraz żywę w ciele, w wierze Syna Bożego żywę, który umiłował mię i wydał samego siebie za mię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '1', '20', '20', 'Nie obracamci w niwecz łaski Bożej, bo jeśli przez zakon jest sprawiedliwość, tedyć Krystus próżno umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '0', '0', 'O bezrozumni Galatowie i któż was zmamił, abyście prawdzie nie byli posłusznymi, którym przed oczyma Krystus przed tym był wymalowan i między wami ukrzyżowan?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '1', '1', 'Tego się tylko żądam uczyć od was, z uczynkówli zakonnych wzięliście ducha, czyli z wiary przez słuchanie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '2', '2', 'Takeście bezrozumni, począwszy duchem, teraz bywacie dokończeni ciałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '3', '3', 'I takżeście wiele cierpieli darmo? Jeśli też jedno darmo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '4', '4', 'Który tedy wam dodawa ducha i sprawuje mocy w was, izali to czyni z uczynków zakonu, czyli z wiary przez słuchanie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '5', '5', 'Jako Abraam uwierzył Bogu i poczytano mu jest ku sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '6', '6', 'Tak też wiecie, iż którzy z wiary są, ci są synami Abraamowymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '7', '7', 'A obaczając wprzód Pismo, iż z wiary Bóg usprawiedliwia pogany, przed tym opowiedało Abraamowi: Błogosławieni będą w tobie wszyscy narodowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '8', '8', 'A tak, którzy z wiary są, błogosławieni bywają z wiernym onym Abraamem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '9', '9', 'Abowiem ile ich jest z uczynków zakonu, pod przeklęctwem są, bo napisano jest: Przeklęty każdy któryby nie trwał we wszytkich rzeczach, które napisane są w księgach zakonu, aby je czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '10', '10', 'A iż przez zakon żaden nie bywa usprawiedliwion u Boga, jawnoć jest, gdyż sprawiedliwy z wiary żyć będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '11', '11', 'A zakon nie jestci z wiary, ale człowiek któryby im dosyć uczynił, w nich żyć będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '12', '12', 'Lecz Krystus nas odkupił od przeklęctwa zakonu, gdyż za nas stał się przeklęctwem, abowiem napisano jest: Przeklęty którykolwiek wisi na drzewie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '13', '13', 'Aby w poganoch błogosławieństwo Abraamowe było w Krystusie Jezusie i abyśmy obiecanego Ducha wzięli przez wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '14', '14', 'Bracia! Ludzkimci obyczajem mówię: Choć człowieczy testament jest, wszakże gdy jest utwierdzony, nikt go nie łamie, ani kniemu przydawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '15', '15', 'A Abraamowi opowiedziane są obietnice i plemieniowi jego. Nie mówi plemieniom, jako o wielu, ale jako o jednym: I plemieniowi twojemu, które jest Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '16', '16', 'A toć powiedam, iż umowy przed tym od Boga utwirdzonej, względem Krystusa zakon, który po czterech set i trzechdziesiąt lat nastał, w niwecz obrócić nie może, aby zgładził obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '17', '17', 'Abowiem jeśliż z zakonu jest dziedzictwo, już nie z obietnice, aleć Abraamowi przez obietnicę darował Bóg dziedzictwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '18', '18', 'Cóż tedy zakon? Przydan jest dla występków, ażby przyszło ono plemię, któremu uczyniona jest obietnica, a jestci postanowion przez Anioły, przez rękę pośrednika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '19', '19', 'A pośrednik nie jestci jednego, a Bógci jeden jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '20', '20', 'Zakon tedy izali jest przeciwko obietnicam Bożym? Nie daj tego Boże! Abowiem gdyby był dan zakon, który by mógł usprawiedliwić, prawdziwieby z zakonu była sprawiedliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '21', '21', 'Ale Pismo zamknęło wszytko pod grzech, aby obietnica z wiary Jezusa Krystusa była dana tym, którzy wierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '22', '22', 'A przed tym niż przyszła wiara, byliśmy zamknieni pod strażą zakonu, abyśmy przyszli w tę wiarę, która miała być obiawiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '23', '23', 'A przetoż zakon nauczycielem naszym był ku Krystusowi, abychmy z wiary byli usprawiedliwieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '24', '24', 'Ale gdy przyszła wiara, już pod nauczycielem nie jestechmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '25', '25', 'Abowiem wszyscy synami Bożymi jesteście przez wiarę w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '26', '26', 'Bo którzykolwiek jesteście okrzczeni w Krystusie, oblekliście Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '27', '27', 'Nie jest Żyd ani Grek, nie jestci niewolnik ani wolny, nie jest męszczyzna ani niewiasta. Abowiem wszyscy wy jednym jesteście w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '2', '28', '28', 'A jeśli jesteście Krystusowi, iście plemieniem Abraamowym jesteście, dziedzicmi wedle obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '0', '0', 'A toć powiedam: Póki dziedzic jest dziecięciem, nic nie jest różny od sługi, chociaż jest panem wszech rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '1', '1', 'Aleć jest pod opiekuny i sprawcami aż do czasu, któryby ociec naznaczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '2', '2', 'Także i my, gdyśmy byli dziećmi, byliśmy pod początki świata zniewoleni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '3', '3', 'Ale gdy przyszło wypełnienie czasu, posłał Bóg Syna swojego sprawionego z niewiasty, który się zstał poddanym zakonowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '4', '4', 'Aby wykupił ty, którzy byli pod zakonem, żebyśmy otrzymali sposobienie synów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '5', '5', 'A iż wy jesteście synowie, posłał Bóg ducha Syna swego w serca wasze wołającego Abba, to jest, Ociec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '6', '6', 'Przeto teraz nie jesteś sługą, ale synem, a jeśli syn, też i dziedzic Boży przez Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '7', '7', 'I owszem, gdyście Boga nie znali, służyliście tym, którzy z przyrodzenia nie są bogowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '8', '8', 'Ale teraz, gdy znacie Boga, abo radszej jesteście uznani od Boga, jakoż się wracacie na zad ku początkom mdłym i niedostatecznym, którym zasię chcecie służyć jako i pierwej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '9', '9', 'Zachowywacie dni i księżyce i czasy i lata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '10', '10', 'Boję się o was, bym snadź darmo nie pracował około was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '11', '11', 'Bądźcie jako i ja, abowiem jam jest jako i wy. Bracia, proszę was żadnejeście mi krzywdy nie uczynili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '12', '12', 'Wiecie też iżem przez krewkość ciała przepowiedał wam pirwej Ewanjeliją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '13', '13', 'A doświadczenia mojego, które było w ciele moim, nie lekceście sobie ważyli, aniście odrzucili, aleście mię przyjęli jako Anioła Bożego, jako Krystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '14', '14', 'Któreż tedy było przechwalanie błogosławieństwa waszego? Abowiem świadczę wam, iż jeśliby było mogło być, dalibyście mi byli wyłupiwszy, oczy swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '15', '15', 'A tak, izalim się wam stał nieprzyjacielem, gdy wam prawdę mówię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '16', '16', 'Miłująć was nie dobrze i owszem chcą nas wyłączyć, abyście je miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '17', '17', 'Aleć cudna jest rzecz barzo miłować w dobrej rzeczy zawżdy, a nie tylko gdy obecnie bywam u was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '18', '18', 'Dziatki moje, które zaś rodzę, ażby był Krystus w was wykształtowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '19', '19', 'A chciałbym teraz być przy was i odmienić głos mój przeto, iż wątpię w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '20', '20', 'Powiedzcie mi, którzy pod zakonem chcecie być, zakonu nie słuchacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '21', '21', 'Abowiem napisano jest: Iż Abraam dwu synu miał. Jednego z służebnicę, a drugiego z wolnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '22', '22', 'Ale ten, który się z służebnicę urodził, jest wedle ciała; a który z wolnej, przez obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '23', '23', 'Przez co znaczą się insze rzeczy, abowiem ty są dwie umowie, jedna, która jest Agar, od góry Syon, rodząc płód ku niewolstwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '24', '24', 'Abowiem Synaj jestci góra w Arabijej, która się prawie zgadza z tą Jeruzalem, która teraz jest i służy z synami swojemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '25', '25', 'Ale ona wysoka Jeruzalem, wolnać jest, która jest matka wszytkich nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '26', '26', 'Abowiem napisano jest: Wesel się niepłodna, która nie rodzisz, wyrwi się a zawołaj ty, która nie bolejesz. Abowiem wi&nbsp;ęcej dziatek jest opuszczonej, niż tej, która ma męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '27', '27', 'My tedy bracia, według Izaaka jesteśmy synowie obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '28', '28', 'A jako na on czas ten, który się był narodził wedle ciała, prześladował tego, który się był narodził wedle ducha, także i teraz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '29', '29', 'Ale co mówi Pismo? Wyrzuć służebnicę i syna jej, abowiem nie będzie dziedzicem syn służebnice z synem wolnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '3', '30', '30', 'A koniecznie bracia nie jesteśmy synowie służebnice, ale wolnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '0', '0', 'Stójcie tedy w wolności, którą nas Krystus wyzwolił, ani bądźcie znowu uwikłani jarzmem niewolej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '1', '1', 'Oto ja Paweł powiedam wam, iż jeśli będziecie obrzezani, Krystus wam nic nie pomoże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '2', '2', 'Abowiem powtóre świadczę każdemu człowiekowi, który bywa obrzezan, iż powinien jest wszytek zakon zachowywać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '3', '3', 'Próżnym się wam zstał Krystus przez was samych, którzykolwiek przez zakon bywacie usprawiedliwieni i wypadliście z łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '4', '4', 'Abowiem my duchem z wiary nadzieję sprawiedliwości oczekawamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '5', '5', 'Bo w Krystusie Jezusie ani obrzezanie nic nie waży, ani nieobrzezanie, ale wiara przez miłość się sprawująca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '6', '6', 'Bieżeliście nadobnie, któż wam przekaził, abyście nie byli posłusznymi prawdzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '7', '7', 'Tać pewność nie jest z tego, który was wzywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '8', '8', 'Trocha kwasu wszytko zadziałanie zakwasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '9', '9', 'Ja dufam o was w Panie, iż nic inszego rozumieć nie będziecie. A ten, który wami trwoży, odniesie potępienie, któżbykolwiek był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '10', '10', 'A ja bracia jeśli jeszcze obrzezanie powiedam, czemuż jeszcze prześladowanie cierpię? Iście zniszczone jest zgorszenie krzyżowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '11', '11', 'Boże daj to, aby byli wycięci, którzy was niepokoją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '12', '12', 'Abowiem wy wezwani jesteście ku wolności bracia, tylko nie mnimajcie, aby była wolność dana ciału dla przyczyny. Owszem przez miłość służcie jedni drugim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '13', '13', 'Bo wszytek zakon bywa wypełnion tym jednym słowem: Będziesz miłował bliźniego twojego jako samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '14', '14', 'A jeśli jeden drugiego kąsacie i pożyracie, patrzajcież, abyście jeden od drugiego nie byli zniszczeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '15', '15', 'Powiedam tedy, duchem postępujcie, a pożądliwości ciała nie wykonywajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '16', '16', 'Abowiem ciało pożąda przeciwko duchowi, a duch przeciwko ciału, a ty rzeczy między sobą przeciwne są, abyście nie czynili tego cobyście chcieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '17', '17', 'A jeśli duchem bywacie wiedzieni, nie jesteście pod zakonem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '18', '18', 'Boć są jawne sprawy ciała, które ty są: Cudzołustwo, nieczystość, wszeteczeństwo, niepowściągliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '19', '19', 'Bałwochwalstwo, trucizna, nieprzyjaźni, sfar, zawiści, gniewy, niesnaski, rosterki, odszczepieństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '20', '20', 'Zazdrość, morderstwa, pijaństwa, biesiady i tym podobne rzeczy, które przepowiedam wam jakom przepowiedział, iż którzy takowe rzeczy czynią, królestwa Bożego dziedzicmi nie będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '21', '21', 'Ale owoc ducha jestci: Miłość, wesele, pokój, cierpliwość, dobrotliwość, dobroć, wiara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '22', '22', 'Cichość, trzeźwość. Przeciwko tym, którzy są takowi, niemaszci zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '23', '23', 'Abowiem którzy są Krystusowi, ciało ukrzyżowali z namiętnościami i z pożądliwościami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '24', '24', 'Jeśli żywiemy duchem, duchem też postępujemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '4', '25', '25', 'Nie bądźmy chciwi próżnej chwały, jedni drugie drażniąc, a jedni drugim zajźrząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '0', '0', 'Bracia! Choćby był uwiedzion człowiek w jaki upadek, wy duchowni naprawujcie takiego człowieka duchem cichości, obaczając samego siebie, abyś i ty nie był kuszon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '1', '1', 'Jedni drugich brzemiona noście, a tak wypełniajcie zakon Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '2', '2', 'Abowiem jeśli się kto sobie zda być czym, gdyż nic nie jest, tego umysł jego samego oszukawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '3', '3', 'A każdy niech sprawy swojej doświadcza, tedy w sobie samym przechwalanie mieć będzie, a nie w drugim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '4', '4', 'Abowiem każdy swoje brzemię poniesie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '5', '5', 'Przytym niechaj użycza wszytkich dóbr ten, który bywa nauczan w słowie temu, który go naucza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '6', '6', 'Nie błądźcie, Bógci nie bywa pośmiewan, abowiem cobykolwiek siał człowiek, to też żąć będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '7', '7', 'Bo który sieje ciału swemu, z ciała żąć będzie zginienie. Który zaś sieje duchowi, z ducha żąć będzie żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '8', '8', 'A dobrze czyniąc nie bądźmy leniwemi, abowiem czasu swego żąć będziemy, jeśli nie ustaniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '9', '9', 'Przeto tedy póki czas mamy, bądźmy dobrotliwymi przeciwko wszytkim, a nawięcej przeciw domownikom wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '10', '10', 'Widzicie jakom długie listy wam pisał ręką moją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '11', '11', 'Którzykolwiek chcą zdać się ozdobnymi w ciele, ci przymuszają was abyście się obrzezali, tylko aby dla krzyża Krystusowego prześladowania nie cirpieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '12', '12', 'Abowiem i sami, którzy bywają obrzezani, zakonu nie zachowywają, ale chcą abyście się obrzezali, żeby się z ciała waszego chlubili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '13', '13', 'A nie daj tego Boże abym się chlubił, jedno w krzyżu Pana naszego Jezu Krysta, przez którego świat mnie jest ukrzyżowan a ja światu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '14', '14', 'Abowiem w Krystusie Jezusie, ani obrzezanie nic nie waży, ani nieobrzezanie, ale nowe stworzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '15', '15', 'A którzykolwiek tym sposobem postępować będą, pokój będzie nad nimi i miłosierdzie i nad Izraelem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '16', '16', 'Napotym niechaj mi żaden kłopotu nie zadawa, abowiem ja piętna Pana Jezusowe na ciele moim noszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'gal', '5', '17', '17', 'Łaska Pana naszego Jezusa Krystusa, niech będzie bracia z duchem waszym. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '0', '0', 'Paweł Apostoł Jezusa Krystusa, przez wolą Bożą świętym, którzy są w Efezie, i wiernym w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '1', '1', 'Łaska i pokój niech wam będzie od Boga Ojca naszego i od Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '2', '2', 'Niech będzie pochwalon Bóg i Ociec Pana naszego Jezu Krysta, który nas błogosławił wszelakim blogosławieństwem duchownym w niebie, przez Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '3', '3', 'Jako nas wybrał w nim przed tym niż był założon świat, obyśmy byli świętymi i nienaganionymi przed nim, przez miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '4', '4', 'Który przeznaczył nas, aby nas sposobił za syny przez Jezusa Krystusa w sobie, wedle dobrowolnego upodobania wolej swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '5', '5', 'Ku czci chwalebnej łaski swojej, którą nas wdzięcznymi uczynił w onym najmilszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '6', '6', 'W którym mamy odkupienie przez krew jego i odpuszczenie grzechów, z obfitej łaski jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '7', '7', 'Którą obfitował w nas wszelaką mądrością i rostropnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '8', '8', 'Objawiwszy nam tajemnicę wolej swojej, według dobrowolnej życzliwości swojej, którą był postanowił w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '9', '9', 'Aby w wypełnieniu czasu zupełnego, zebrał w jedno wszytki rzeczy w Krystusie, tak ty, które są w niebie, jako i ty, które na ziemi, w tymże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '10', '10', 'W którym też dostaliśmy działu, będąc przeznaczeni wedle postanowienia jego, który wszytki rzeczy sprawuje wedle rady wolej swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '11', '11', 'Abyśmy byli ku czci chwały jego, którzyśmy pirwszy nadzieję mieli w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '12', '12', 'W którym ufaliście i wy, usłyszawszy słowo prawdy, to jest, Ewanjeliję zbawienia waszego, w którą też uwierzywszy, jesteście zapieczętowani Duchem onym świętym obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '13', '13', 'Który jest zadatkiem dziedzictwa naszego, aż ku wolności będziem przywiedzieni ku czci chwały jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '14', '14', 'Dlatego i ja usłyszawszy onę wiarę która w was jest w Panie Jezusie i miłość ku wszytkim świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '15', '15', 'Nie przestawam dziękować za was, przypominając was w modlitwach moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '16', '16', 'Aby Bóg Pana naszego Jezu Krysta, Ociec on chwalebny, dał wam Ducha mądrości i objawienia, przez poznanie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '17', '17', 'Oświeciwszy oczy umysłu waszego, abyście wiedzieli, któraby była nadzieja ona wezwania jego i które bogactwa chwały dziedzictwa jego w świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '18', '18', 'I która jest zacna ona wielkość mocy jego przeciwko nam, którzy wierzymy według skutecznej mocy i siły jego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '19', '19', 'Którą okazał w Krystusie gdy go wzbudził zmartwych i posadził go na prawicy swojej w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '20', '20', 'Daleko nad wszelakie państwo i zwierzchność i moc i panowanie i nad wszelkie imię, które mianowane bywa, nie tylko na tym świecie, ale też na przyszłym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '21', '21', 'I wszytki rzeczy podał pod nogi jego i postanowił go głową nad wszytki  rzeczy kościołowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '0', '22', '22', 'Który jest ciałem jego i wypełnieniem tego, który wszytki rzeczy we wszytkich napełnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '0', '0', 'A was społu ożywił, gdyżeście byli umarli w upadkach i w grzechach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '1', '1', 'W którycheście niekiedy chodzili według biegu świata tego, według książęcia, który ma zwierzchność na powietrzu i ducha, który teraz moc swoję okazuje w ludziach nieposłusznych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '2', '2', 'Między którymi też i  my wszyscy obieralichmy się niekiedy w pożądliwościach ciała naszego, czyniąc ty rzeczy, które się podobały ciału i myślam i bylichmy z przyrodzenia synowie gniewu jako i drudzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '3', '3', 'Ale Bóg, który jest bogaty w miłosierdziu, dla wielkiej miłości swojej, którą nas umiłował,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '4', '4', 'Też gdychmy przez upadki umarłymi byli, pospołu ożywił nas przez Krystusa, którego łaską jesteście zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '5', '5', 'I wzbudził pospołu i wespole posadził w niebie w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '6', '6', 'Aby okazał w przyszłych czasiech wielkie bogactwa łaski swojej, przez dobrotliwość przeciwko nam w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '7', '7', 'Abowiem łaską jesteście zbawieni przez wiarę i to nie z was, dar to Boży jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '8', '8', 'Nie z uczynków, aby się nikt nie chlubił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '9', '9', 'Abowiem jestechmy sprawą jego stworzeni w Krystusie Jezusie ku uczynkam dobrym, które przed tym Bóg nagotował, abychmy w nich chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '10', '10', 'A przetoż pamiętajcie, że wy niekiedy będąc pogany w ciele, którzyście byli zwani nieobrzezaniem od onego, które jest zwano obrzezaniem w ciele i które rękoma bywa sprawowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '11', '11', 'Iżeście byli na on czas bez Krystusa, oddaleni od rzeczy pospolitej izraelskiej i obcemi od umów, w których się zamykały obietnice, nadzieje nie mając i przez Boga będąc na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '12', '12', 'Ale teraz w Krystusie Jezusie wy, którzyście niekiedy byli daleko, zstaliście się blisko przez krew Krystusowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '13', '13', 'Abowiem on jest pokojem naszym, który oboje jednym uczynił i zepsował przegrodzenie śrzedniej ściany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '14', '14', 'Zgładziwszy przez ciało swoje nieprzyjaźni, to jest, zakon przykazania, który się zamyka w ceremoniach, aby dwu stworzył w samym sobie w jednego nowego człowieka, czyniąc pokój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '15', '15', 'Aby też pojednał z Bogiem oboje w jednym ciele przez krzyż, zburzywszy nieprzyjaźń przezeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '16', '16', 'A przyszedszy opowiedział pokój wam, którzyście daleko i którzy blisko byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '17', '17', 'Abowiem przezeń mamy przystęp oboj w jednym duchu do Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '18', '18', 'Przeto tedy już nie jesteście gośćmi i komorniki, ale mieszczany pospołu z świętymi i domowniki Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '19', '19', 'Zbudowani na fundamencie Apostołów i proroków i owszem na samym Jezusie Krystusie, który jest kamieniem węgłowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '20', '20', 'Na którym wszytko budowanie słusznie spojone roście w kościół święty Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '1', '21', '21', 'Na którym też i wy pospołu jesteście zbudowani, abyście byli mieszkaniem Bożym w duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '0', '0', 'Dlatego ja Paweł jestem więźniem Krystusa Jezusa za was pogany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '1', '1', 'Jeśliście słyszeli szafowanie łaski Bożej, która mnie jest dana do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '2', '2', 'Iż Bóg przez objawienie oznajmił mi tajemnice (jakom wam przedtym pisał na krótce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '3', '3', 'Ztąd czytając, możecie obaczyć wy rozumienie moje, w tajemnicy Krystusowej),');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '4', '4', 'Która inszych wieków nie była znaczna synom ludzkim, jako teraz oznajmiona jest świętym Apostołom jego i prorokom przez Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '5', '5', 'To jest, iż pogani są społu dziedzicami i jednego ciała i społecznymi uczestniki obietnice jego w Krystusie, przez Ewanjeliję,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '6', '6', 'Którejem się zstał sługą z daru łaski Bożej, który dan jest mnie, wedle skutecznej mocy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '7', '7', 'Mnie namniejszemu ze wszytkich świętych dana jest łaska ta, abym między pogany przepowiedał niedościgłe bogactwa Krystusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '8', '8', 'A iżbym objaśnił wszytkim, która jest społeczność tajemnice skrytej od wieków w Bogu, który wszytki rzeczy stworzył przez Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '9', '9', 'Aby była teraz przez kościół oznajmiona państwam i zwierzchnościam, które na niebie są, rozmaita mądrość Boża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '10', '10', 'Wedle postanowienia wiecznego, które uczynił w Krystusie Jezusie Panie naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '11', '11', 'W którym mamy wolność i przystęp z ufaniem przez wiarę jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '12', '12', 'A przetoż proszę abyście się nie zstawali leniwymi dla ucisków moich za was, która jest chwała wasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '13', '13', 'Dlatego skłaniam kolana moje ku Ojcu Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '14', '14', 'Od którego wszytek naród na niebie i na ziemi bywa nazwan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '15', '15', 'Aby dał wam z obfitej łaski swojej, żebyście byli mocnie utwierdzeni przez Ducha jego, w wnętrznym człowiecze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '16', '16', 'Aby mieszkał Krystus przez wiarę w sercach waszych, żebyście w miłości wkorzenieni i ugruntowani,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '17', '17', 'Mogli doścignąć ze wszytkimi świętymi, która jest szyrokość i długość i głębokość i wysokość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '18', '18', 'I poznać miłość Krystusowę, która przewyższa wszelakie wyrozumienie, a iżbyście byli napełnieni wszelakim napełnieniem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '19', '19', 'A temu, który może daleko obficiej wszytko uczynić nadto o co prosimy abo myślimy, z mocy onej, która się w nas okazuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '2', '20', '20', 'Niechaj będzie chwała w kościele przez Krystusa Jezusa, po wszytki czasy i na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '0', '0', 'Proszę tedy ja, więzień w Panu, abyście postępowali jako się godzi w powołaniu, którym jesteście wezwani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '1', '1', 'Z wszelaką skromnością, cichością i z cierpliwością, znosząc jedni drugich w miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '2', '2', 'Starając się abyście zachowali jedność ducha przez zwioskę  pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '3', '3', 'Jedno jest ciało i jeden duch, tak jako jesteście wezwani w jednę nadzieję powołania waszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '4', '4', 'Jeden Pan, jedna wiara, jeden krzest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '5', '5', 'Jeden Bóg i Ociec wszytkich, który jest nad wszytki rzeczy i po wszytkich rzeczach i we wszytkich was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '6', '6', 'Ale każdemu z nas dana jest łaska wedle miary daru Krystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '7', '7', 'Dlatego mówi: Gdy wstąpił na górę, wiódł wiele więźniów w pojmanie i dał dary ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '8', '8', 'A to co wstąpił, cóż jest, jedno iż pirwej był zstąpił do naniższych stron ziemie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '9', '9', 'Który zstąpił, tenżeć jest, który też wstąpił na wszytki niebiosa, aby napełnił wszytko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '10', '10', 'I tenże dał niektóre Apostoły, a drugie proroki, drugie zasię Ewanjelisty, niektóre też pasterze i uczyciele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '11', '11', 'Ku zjednoczeniu świętych do sprawy posługowania i ku budowaniu ciała Krystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '12', '12', 'Aż wszyscy przyjdziemy w jedność wiary i poznania Syna Bożego, w męża doskonałego, w miarę zupełnego wzrostu Krystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '13', '13', 'Abyśmy już nie byli dziećmi chwiejącemi się i nie byli miotani tam i sam leda wiatrem nauki, przez chytrość ludzi i przez omylność, która jest na zdradę postawiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '14', '14', 'Ale zachowywając prawdę z miłością, rośćmy weń we wszytkim, który jest głową, to jest Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '15', '15', 'Z którego wszytko ciało słusznie spojone i społu złączone, w każdym spojeniu związania mocy, którą wewnątrz sprawuje, według miary każdego członku bierze pomnożenie przystojne ciału, ku zbudowaniu samego siebie przez miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '16', '16', 'To tedy powiedam i uprzejmie proszę przez Pana, abyście więcej nie postępowali, jako ini pogani postępują w próżności umysłu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '17', '17', 'Mając myśl zaćmioną, a będąc oddaleni od żywota Bożego przez nieumiejętność, która w nich jest, z zatwardziałości serca ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '18', '18', 'Którzy bez żałości udali sami siebie na haniebną sromotę, aby się z chciwością wszelakiej nieczystości dopuszczali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '19', '19', 'Aleście wy nie tak się Krystusa nauczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '20', '20', 'Jeśliście go słuchali i przezeń jesteście nauczeni, jako jest prawda w Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '21', '21', 'To jest, abyście złożyli z siebie co się dotycze pirwszego postępowania starego onego człowieka, który zdradliwemi pożądliwościami bywa zepsowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '22', '22', 'A iżbyście się odnowili duchem umysłu waszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '23', '23', 'I przyoblekli nowego człowieka, który wedle Boga stworzon jest ku sprawiedliwości i świętobliwości prawdziwej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '24', '24', 'A przetoż odłożywszy kłamstwo, mówcie prawdę każdy bliźniemu swemu, abowiem jesteśmy członkami jedni drugich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '25', '25', 'Gniewajcie się a nie grzeszcie, słońce niechaj nie zachodzi nad rozgniewaniem waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '26', '26', 'A nie dawajcie miejsca diabłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '27', '27', 'Ten, który kradł, niechaj już nie kradnie, ale raczej niechaj robi, pracując rękoma coby było dobrego, aby mógł użyczać potrzebnemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '28', '28', 'Niechaj żadna mowa sprośna z ust waszych nie wychodzi, ale jeśli która jest dobra ku potrzebnemu zbudowaniu, aby łaskę tym, którzy słuchają przyniosła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '29', '29', 'A nie zasmucajcie Ducha onego świętego Bożego, przez którego jesteście zapieczętowani na dzień odkupienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '30', '30', 'Wszelaka przykrość i popędliwość i gniew i wołanie i złorzeczenie, niech będzie odjęte od was, z wszelaką złością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '3', '31', '31', 'Ale bądźcie dobrotliwymi jedni przeciwko drugim, miłosierni, odpuszczając sobie, jako i Bóg w Krystusie wam odpuścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '0', '0', 'Bądźcież tedy naśladowniki Boga, jako synowie mili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '1', '1', 'A chodźcie w miłości, jako i Krystus umiłował nas i wydał samego siebie za nas na ofiarę i na zabicie Bogu, ku wonności dobrej woniej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '2', '2', 'Grzech nieczysty i wszelkie plugastwo abo łakomstwo, niechaj nie będzie między wami ani pomieniono, jako świętym przystoi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '3', '3', 'Niewstydliwość i błazeńska mowa, żarty i rzeczy, które nie przystoją, ale raczej dziękowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '4', '4', 'Bo to wiecie, iż wszelki niewstydliwy, abo nieczysty, abo łakomy, który jest bałwochwalca, nie ma dziedzictwa w królestwie Krystusowym i Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '5', '5', 'Niechaj was nikt nie zwodzi próżnemi słowy, abowiem dla tych rzeczy przychodzi gniew Boży na ludzi nieposłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '6', '6', 'Nie bądźciesz tedy towarzyszmi ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '7', '7', 'Abowiemeście byli niekiedy ciemnościami, a teraz światłością w Panie. Chodźciesz tedy jako synowie światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '8', '8', 'Bo owoc ducha należy w wszelkiej dobroci i w sprawiedliwości, i w prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '9', '9', 'Doświadczając co jest wdzięcznego Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '10', '10', 'Ani miejcie społeczności z uczynki niepożytecznemi ciemności, ale je owszem jawnemi czyńcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '11', '11', 'Abowiem ty rzeczy, które się tajemnie od tych dzieją, sromota i powiedać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '12', '12', 'Ale wszytki rzeczy, gdy są okazane od światłości, bywają objawione, gdyż światłość jest to co wszytki rzeczy objawia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '13', '13', 'Dlatego mówi: Ocuć się, który śpisz i powstań zmartwych, a oświeci cię Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '14', '14', 'Patrzciesz tedy jakobyście ostrożnie chodzili, nie jako głupi, ale jako mądrzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '15', '15', 'Czas odkupując przeto, iż dni złe są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '16', '16', 'A przeto nie bądźcie głupi, ale rozumiejcie, która jest wola Boża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '17', '17', 'A nie upijajcie się winem, w którym jest zbytek, ale bądźcie napełnieni ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '18', '18', 'Mówiąc między sobą przez psalmy i pieśni i śpiewania duchowne, śpiewając i grając w sercu swoim Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '19', '19', 'Dzięki czyniąc zawsze za wszytko Bogu i Ojcu, w imię Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '20', '20', 'Będąc poddanemi jedni drugim, z bojaźnią Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '21', '21', 'Żony, bądźcie poddane własnym mężom, jako Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '22', '22', 'Abowiem mąż jest głową żony, tako i Krystus jest głową kościoła, a tenci jest, który zbawienie ciału dawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '23', '23', 'Jako tedy kościół poddany jest Krystusowi, tak też żony swoim mężom niech będą poddane we wszytkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '24', '24', 'Wy mężowie, miłujcie żony wasze, jako i Krystus umiłował kościół i samego siebie wydał zań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '25', '25', 'Aby ji poświęcił, oczyściając omyciem wody przez słowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '26', '26', 'Aby ji sobie stawił chwalebnym, to jest kościołem, któryby nie miał zmazy, abo zmarsku, abo czego takowego, ale iżby był święty i krom przygany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '27', '27', 'Także mężowie mają miłować żony swoje, jako swoje własne ciała. Kto miłuje żonę swoję, samego siebie miłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '28', '28', 'Abowiem nigdy żaden swego własnego ciała nie miał w nienawiści, owszem je wychowywa i zagrzewa, jako też Pan kościół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '29', '29', 'Bo jesteśmy członkami ciała jego, z ciała jego i z kości jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '30', '30', 'A przeto opuści człowiek ojca i matkę i przyłączy się ku żenie swojej i będą dwa w jednym ciele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '31', '31', 'Tajemnica ta jestci wielka, a mówięć o Krystusie i kościele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '4', '32', '32', 'A tak i z was każdy z osobna niech miłuje żonę swoję jako siebie samego, a żona niechaj ma w uczciwości męża swojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '0', '0', 'Dzieci, słuchajcie rodziców waszych w Panie, boć to jest rzecz sprawiedliwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '1', '1', 'Czci ojca twego i matkę (które jest pirwsze przykazanie z obietnicą).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '2', '2', 'Abyć się dobrze działo i abyś był długo żyw na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '3', '3', 'A wy ojcowie, nie pobudzajcie ku gniewowi dzieci waszych, ale je wychowajcie w nauce i w karności Pańskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '4', '4', 'Słudzy, słuchajcie tych, którzy są pany wedle ciała, z bojaźnią i ze drżeniem, z prostością serca waszego, jako Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '5', '5', 'Nie na oko służąc jako ci, którzy się chcą podobać ludziam, ale jako słudzy Krystusowi, czyniąc z serca wolą Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '6', '6', 'Z życzliwością służąc Panu, a nie ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '7', '7', 'Wiedząc, iż każdy coby uczynił dobrego, to odniesie od Pana, choć sługa, choć wolny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '8', '8', 'A wy panowie toż czyńcie przeciwko im, odpuszczając groźby wiedząc, iż też i samych was Pan jest na niebie, a brakowania osób u niego nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '9', '9', 'Na ostatek bracia moi, bądźcie umocnieni przez Pana i przez moc siły jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '10', '10', 'Przyobleczcież zupełną zbroję Bożą, abyście mogli stać przeciwko najazdom czartowskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '11', '11', 'Abowiem nie mamy boju przeciwko krwi i ciału, ale przeciwko państwam, przeciwko zwierzchnościam, przeciwko panom świata, sprawcam ciemności wieku tego, przeciwko duchownym złościam, które są na wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '12', '12', 'A przeto weźmcie zupełną zbroję Bożą, abyście mogli sprzeciwić się czasu złego i sprawiwszy wszytki rzeczy, mocnie stanąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '13', '13', 'Bądźcie tedy trwałemi, opasawszy biodra wasze prawdą i wziąwszy na się przedni pleszek sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '14', '14', 'Obuwszy nogi w gotowanie Ewanjelijej pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '15', '15', 'Ktemu wziąwszy tarczą wiary, którąbyście mogli wszytki strzały ogniste onego złego pogasić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '16', '16', 'Hełm też zbawienny weźmicie i miecz duchowy, to jest Słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '17', '17', 'Z dzięką,  modlitwą i prośbą, modląc się na każdy czas przez Ducha i w tymże czując wszelaką twałością i prośbą za wszytki święte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '18', '18', 'I za mię, aby mi była dana mowa, ku otworzeniu ust moich z bespieczeństwem, abym oznajmił tajemnice Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '19', '19', 'Za którą jestem posłem w łańcuchu, abym o niej bespiecznie mówił, tak jako mam mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '20', '20', 'A iżbyście i wy wiedzieli o moich rzeczach i co czynię, wszytko wam oznajmi Tychikus miły brat i wierny sługa w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '21', '21', 'Któregom posłał do was na to, abyście wiedzieli o naszych rzeczach i aby pocieszył serca wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '22', '22', 'Pokój niech będzie braciej i miłość z wiarą, od Boga Ojca i Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'eph', '5', '23', '23', 'Łaska niech będzie ze wszytkimi, którzy miłują Pana naszego Jezu Krysta, ku nieśmiertelności. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '0', '0', 'Paweł i Tymoteusz słudzy Jezu Krysta, wszytkim świętym w Jezusie Krystusie, którzy są w Filipiech, z biskupy i diakony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '1', '1', 'Łaska niech wam będzie i pokój od Boga Ojca naszego i od Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '2', '2', 'Dziękuję Bogu memu ustawicznie was pamiętając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '3', '3', '(Zawżdy we wszelkiej prośbie mojej za wszytki was z weselem modlitwę czyniąc).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '4', '4', 'Iżeście przyszli ku społeczności Ewanjelijej, od pirwszego dnia aż do czasu tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '5', '5', 'Pewnie to wiedząc, iż ten który począł w was dobrą sprawę, dokona aż do dnia Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '6', '6', 'Jako sprawiedliwa rzecz jest, abych to o was wszytkich rozumiał dlatego, iż was mam w sercu moim wszytki uczestniki łaski zemną, tak w moim więzieniu jako i w obronie i potwierdzeniu Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '7', '7', 'Abowiem świadkiem mi jest Bóg jako was serdecznie miłuje wszytki we wnętrznościach Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '8', '8', 'I o to proszę, aby miłość wasza jeszcze więcej obfitowała w uznaniu i we wszelkim rozsądku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '9', '9', 'Na to, abyście rozeznali ty rzeczy, które się nie zgadzają. Żebyście byli szczyremi i bez przekazy bieg wykonali, aż do dnia Krystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '10', '10', 'Będąc napełnieni owoców sprawiedliwości, które są przez Jezusa Krystusa, ku chwale i czci Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '11', '11', 'Zatym bracia, chcę abyście wiedzieli, iż ty rzeczy, które mi się przytrefiały, ku więtszemu pomnożeniu Ewanjelijej przyszły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '12', '12', 'Tak iż zwiąski moje w Krystusie znaczne są przy wszytkim dworze Cesarskim i indziej wszędzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '13', '13', 'A wiele ich z braciej w Panie, dufając więzieniu memu, więcej śmieją i bespieczniej słowo opowiedać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '14', '14', 'A niektórzy przez zazdrość i swar, niektórzy też i z dobrą wolą Krystusa opowiedają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '15', '15', 'Niektórzy z waśni Krystusa opowiedają nieszczyrze mnimając, iż przydawają ucisku więzieniu memu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '16', '16', 'Drudzy zasię z miłości, wiedząc iżem jest postanowion ku obronie Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '17', '17', 'Ale cóż? Wżdy jakimkolwiek sposobem, choć pod pokrywką, choć prawdziwie Krystus bywa opowiedan, z tego też raduję się i jeszcze radować się będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '18', '18', 'Gdyż wiem, iż mi się to obróci ku zbawieniu przez prośbę waszę i dodawanie ducha Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '19', '19', 'Według uprzejmego oczekawania i nadzieje mojej, iż w żadnej rzeczy nie zawstydzę się, ale z wszelakim bespieczeństwem, jako zawsze tak też i teraz zacnie będziecie wywyzszon Krystus w moim ciele, choć przez żywot, choć przez śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '20', '20', 'Abowiem Krystus mnie jest zyskiem, tak w żywocie jako w śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '21', '21', 'Ale nie wiem jeślimi pożyteczno jest żyć w ciele, a cobych ja miał obrać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '22', '22', 'Abowiem jestem ściśnion z obudwu stron. Żądając odyjdź i być z Krystusem, bo to daleko lepiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '23', '23', 'Ale zostać w ciele, potrzebniej jest dla was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '24', '24', 'I temu ufając wiem, iż zostanę i z wami wszytkimi przetrwam, ku waszemu pożytkowi i weselu wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '25', '25', 'Aby obfitowało przechwalanie wasze w Krystusie Jezusie ze mnie, gdy się do was powtórę wrócę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '26', '26', 'Tylko się sprawujcie jako się godzi Ewanjelijej Krystusowej, aby choćbych przyszedł i ujźrzał was, a choć nieobecny o waszych rzeczach usłyszał, iż stoicie w jednym duchu, jednostajnie i jednym umysłem bojując przez wiarę Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '27', '27', 'Ani się trwóżcie ni w czym dla przeciwników, co onym jest znak zginienia, a nam zbawienia i to od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '28', '28', 'Dlatego, iż wam dano jest w sprawie Krystusowej nie tylko weń wierzyć, ale też dla niego cirpieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '0', '29', '29', 'Tenże bój mając jakiście widzieli we mnie i terazeście we mnie słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '0', '0', 'Jeśli tedy które pocieszenie jest w Krystusie, jeśli która pociecha miłości, jeśli która społeczność ducha, jeśli które wnętrzności i ulitowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '1', '1', 'Napełńcież wesele moje, abyście toż rozumieli, też miłość mając, jednomyślnie i jednegoż rozumienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '2', '2', 'Nic nie czyńcie przez swar, abo przez próżną chwałę, ale pokory. Każdy niech rozumie drugiego być zacniejszym niźli sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '3', '3', 'Nie patrzcie każdy swego, ale każdy też co jest drugich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '4', '4', 'A niech ta chuć będzie w was, która była i w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '5', '5', 'Który będąc w kształcie Bożym, nie miałby był za drapiestwo równym być Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '6', '6', 'Ale wyniszczył samego siebie, wziąwszy kształt służebniczy, zstawszy się podobny ludziam i osobą nalezion jako człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '7', '7', 'A sam się uniżył, stawszy się posłusznym aż do śmierci, a śmierci krzyżowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '8', '8', 'Dlatego też Bóg wywyższył go barzo zacnie i dał mu imię, które jest nad wszelkie imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '9', '9', 'Aby w imię Jezusowe wszelkie kolano się skłoniło, niebieskich i ziemskich i tych, które są pod ziemią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '10', '10', 'A iżby wszelki język wyznawał, iż Krystus Jezus jest Panem, ku chwale Boga Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '11', '11', 'Przetoż namilszy moi jakoście zawsze posłuszni byli, nie tylko jako przy mojej bytności, ale teraz daleko więcej w nieobecności mojej, z bojaźnią i z drżeniem zbawienie wasze sprawujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '12', '12', 'Abowiem Bóg jestci ten, który sprawuje w was to, abyście chcieli i uczynili podług darowanego upodobania jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '13', '13', 'Wszytko czyńcie krom szemrania i poswarków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '14', '14', 'Abyście byli bez przygany i szczyremi syny Bożymi, nie naganionymi w pośrzodku narodu złego i przewrotnego, między którymi ludźmi świecicie jako światła na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '15', '15', 'Podawające słowo żywota, abym się mógł przechwalać w dzień Krystusów, iżem darmo nie biegał, anim darmo pracował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '16', '16', 'I owszem choćby mię ofiarowano jako ofiarę mokrą na ofierze zabitej i na świetnym posługiwaniu wiary waszej, raduję i weselę się z wami wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '17', '17', 'Przeto też i wy radujcie się i weselcie się ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '18', '18', 'Przytym spodziewam się w Panie Jezusie, iż rychło Tymoteusza poślę do was, abym też i ja był dobrej myśli, dowiedziawszy się co się z wami dzieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '19', '19', 'Abowiem nikogo nie mam, któryby był serca takiego i ktoby się uprzejmie o waszych rzeczach starać miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '20', '20', 'Bo wszyscy swego szukają, nie rzeczy Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '21', '21', 'Ale wyście świadomi doświadczenia jego, iż jako z ojcem syn ze mną posługował Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '22', '22', 'Nadziewam się tedy, iż tego poślę skoro jedno obaczę postanowienie rzeczy moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '23', '23', 'Dufam też w Panie, iż i sam rychło przyjdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '24', '24', 'Ale zdało mi się za rzecz potrzebną posłać do was Epafrodyta, brata i pomocnika i społecznego żołnierza ze mną, a posła waszego, który mi dodawał czego mi potrzeba było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '25', '25', 'Abowiem żądał barzo was wszytkich i wielce się frasował oto, iżeście słyszeli, iż był niemocen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '26', '26', 'A iście chorował, że był blisko śmierci, ale Bóg zmiłował się nad nim, a nie tylko nad nim, ale też nade mną, iżbym nie miał smutku na smutek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '27', '27', 'A takem go chutliwiej posłał, abyście go powtóre ujźrzawszy, uweselili się i ja też mniej się frasował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '28', '28', 'Przyjmicież go tedy w Panie z wszelakim weselem, a takowe wielce sobie ważcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '1', '29', '29', 'Abowiem dla sprawy Krystusowej przyszedł aż ku śmierci nie litując zdrowia swego, aby wypełnił niedostatek powinności waszej przeciwko mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '0', '0', 'Na ostatek, bracia moi, radujcie się w Panie. Tyż rzeczy wam pisać, mnieć nie cięszko, a wam prześpieczno jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '1', '1', 'Strzeżcie się psów, strzeżcie się złych robotników, strzeżcie się rościęcia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '2', '2', 'Abowiem my jesteśmy obrzezaniem, którzy duchem służymy Bogu i chlubimy się w Krystusie Jezusie, a w ciele nie ufamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '3', '3', 'Acz ja mam z czego ufać też w ciele. Jeśli kto inszy zda się mieć ufanie w ciele, ja więcej mam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '4', '4', 'Będąc obrzezany ósmego dnia, z narodu izraelskiego, z pokolenia Benjamin, Ebrejczyk z Ebrejczyków, wedle zakonu Faryzeusz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '5', '5', 'Wedle uprzejmej chuci prześladując kościół. Według sprawiedliwości zakonu, bez przygany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '6', '6', 'Ale to co mi było zyskiem, tom rozumiał dla Krystusa być szkodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '7', '7', 'Ktemu zaiste mam wszytki rzeczy sobie za szkodę dla zacności uznania Jezusa Krystusa Pana mego, dla którego utraciłem ty wszytki rzeczy i mam je sobie za śmieci, abym Krystusa zyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '8', '8', 'A iżebym był nalezion w nim jako nie mając sprawiedliwości mojej, któraby była z zakonu, ale tę która jest przez wiarę Krystusowę, to jest sprawiedliwość, która jest z Boga przez wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '9', '9', 'Iżbym go poznał i moc zmartwych wstania jego i społeczność w tych, które on cierpiał, gdy się zstawam podobnym śmierci jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '10', '10', 'Doświadczając którymbym sposobem przyszedł ku wstaniu z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '11', '11', 'Nie iżbym już przyszedł do kresu, abo już doskonałym był, ale ścigam, doświadczając jeślibych też doszedł dlaczego też jestem od Krystusa przyjęt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '12', '12', 'Bracia, ja o sobie nie rozumiem iżbych już kresu doszedł, ale jednę rzecz czynię, zapamiętywając tych rzeczy, które są za mną, a mając się do tych które są przedemną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '13', '13', 'Bieżę ku zakładowi wezwania Boskiego tego, które jest z góry, przez Krystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '14', '14', 'Ile nas tedy jest doskonałych, to rozumiemy, a jeśli co inaczej rozumiecie, Bóg też i to wam objawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '15', '15', 'A wszakoż w tym do czegośmy przyszli, chodźmy tymże sposobem i toż rozumiejmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '16', '16', 'Bądźcie też naśladowcami moimi bracia, a obaczajcie ty, którzy tak postępują, jako nas macie za wzór.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '17', '17', 'Abowiem wiele ich chodzi, o których częstom wam powiedał, a teraz i z płaczem powiedam, iż są nieprzyjacielami krzyża Krystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '18', '18', 'Których koniec jest zatracenie, których Bóg jest brzuch i chwała z pohańbieniem ich, którzy o ziemskie rzeczy starają się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '19', '19', 'Abowiem rzecz pospolita wasza jest w niebie, skąd też zbawiciela czekamy Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '2', '20', '20', 'Który przemieni ciało nasze nikczemne, aby się podobnym stało chwalebnemu ciału jego, według mocy, przez którą też może poddać sobie wszytki rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '0', '0', 'A tak, bracia moi mili i pożądani, wesele i korono moja, tak stójcie w Panie namilszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '1', '1', 'Proszę Ewodyjej i Syntychy, aby toż rozumiały w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '2', '2', 'A proszę cię też towarzyszu własny, pomagaj tym, które w Ewanjelijej społu ze mną walczyły z Klementem też i z inymi pomocniki moimi, których imiona są w księgach żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '3', '3', 'Weselcie się zawsze w Panie i powtóre powiedam wam weselcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '4', '4', 'Skromność wasza niech będzie wiadoma wszytkim ludziam; Panci jest blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '5', '5', 'Nie starajcie się ni ocz, ale we wszytkim przez modlitwę i prośbę z dziękowaniem niech żądania wasze będą znaczne u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '6', '6', 'A pokój Boży, który przewyższa wszelki rozum, będzie obroną sercam waszym i myślam waszym przez Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '7', '7', 'Na ostatek bracia, którekolwiek rzeczy są prawdziwe, którekolwiek poczciwe, którekolwiek czyste, którekolwiek miłe, którekolwiek dobrej sławy, jeśli która cnota i jeśli która chwała, o tych myślcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '8', '8', 'Którycheście się też nauczyli i przyjęli i słyszeli i widzieli we mnie, ty czyńcie, a Bóg pokoju będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '9', '9', 'Jam się też uweselił wielce w Panie, iżeście już oświeżeli w swoim staraniu o mnie, aczeście się o tym starali, aleście czasu po temu nie mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '10', '10', 'Nie żebych to mówił względem niedostatku jakiego, bom się nauczył na tym przestawać co mam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '11', '11', 'A umiemci uniżonym być. Umiem obfitować, wszędy i we wszytkich rzeczach jestem nauczony i ku nasyceniu i ku łaknieniu, to jest, tak obfitować jako i niedostatek cierpieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '12', '12', 'Wszytko mogę w Krystusie, który mię umacnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '13', '13', 'A wszakże dobrzeście uczynili, iżeście społu podpomagali ucisk mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '14', '14', 'Wiecie też i wy Filipensowie, iż na początku Ewanjelijej, gdym wyszedł z Macedonijej, żaden mi kościół nie użyczał co się tycze dania i wzięcia, jedno wy sami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '15', '15', 'Abowiem i w Tesalonice gdym był, wyście mi raz i powtóre czego mi potrzeba było posłali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '16', '16', 'Nie iżbych daru pożądał, ale żądam owocu obfitującego, któryby wszedł w rachunek wasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '17', '17', 'Wziąłem wszytko i obfituję. Jestem napełnion wziąwszy od Epafrodyta ty rzeczy, które były od was posłane, na wonność dobrej woniej, ofiarę Bogu przyjemną i wdzięczną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '18', '18', 'A Bóg mój doda zupełnie co potrzebować będziecie, według bogactw swoich chwalebnie w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '19', '19', 'Przytym Bogu i Ojcu naszemu niechaj będzie chwała na wieki wieków Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '20', '20', 'Pozdrówcie wszytki święte w Krystusie Jezusie. Pozdrawiają was bracia, którzy sam są ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '21', '21', 'Pozdrawiają was wszyscy święci, a zwłaszcza którzy są z Cesarskiego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'php', '3', '22', '22', 'Łaska Pana naszego Jezu Krysta niech będzie z wami wszytkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '0', '0', 'Paweł Apostoł Jezu Krysta przez wolę Bożą i Tymoteusz brat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '1', '1', 'Tym, którzy są w Kolosiech świętym i wiernym bratom w Krystusie. Łaska niech wam będzie i pokój od Boga Ojca naszego i od Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '2', '2', 'Dziękujemy Bogu i Ojcu Pana naszego Jezu Krysta zawsze za was się modląc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '3', '3', 'Usłyszawszy wiarę waszę w Krystusie Jezusie i miłość przeciwko wszem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '4', '4', 'Dla nadzieję wam odłożonej w niebie, którąście przed tym słyszeli, przez prawdziwe słowo Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '5', '5', 'Która przyszła do was jako i na wszytek świat i owoc przynosi jako też u was od tego dnia jakoście prawdziwie uznali łaskę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '6', '6', 'Jakoście się też nauczyli od Epafry miłego, który z nami służy i jest wiernym sługą Krystusowym za was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '7', '7', 'Który też nam oznajmił miłość waszę, którą macie według ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '8', '8', 'Dlatego i my od tego dnia jakochmy to usłyszeli, nie przestawamy za was się modlić i żądać, abyście byli napełnieni wszelakim uznaniem wolej jego, z wszelaką mądrością i rozumieniem duchownym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '9', '9', 'Abyście chodzili jako Panu należy, iżbyście się mu podobali we wszytkim, wszelakim uczynkiem dobrym, owoc przynoszący rostąc w uznaniu Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '10', '10', 'Będąc umocnieni wszelaką mocą według chwalebnej możności jego, ku wszelakiej cierpliwości i cichości z weselem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '11', '11', 'Dziękując Ojcu, który nas godnymi uczynił, abychmy byli uczestniki dziedzictwa świętych w światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '12', '12', 'Który wybawił nas z mocy ciemności i przeniósł w królestwo Syna swego miłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '13', '13', 'W którym mamy odkupienie przez krew jego, to jest odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '14', '14', 'Który jest wyobrażeniem Boga niewidomego. Pierworodnym przed wszytkim stworzeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '15', '15', 'Abowiem przezeń są wszytki rzeczy stworzone na niebie i na ziemi, widome i niewidome. Choć majestaty, choć państwa, choć przełożeństwa, choć zwierzchności, wszytki rzeczy przezeń i względem jego są stworzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '16', '16', 'A on jest przed wszytkimi rzeczami, a wszytki rzeczy przezeń stoją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '17', '17', 'I on jest głową ciału kościoła. Początkiem i pierworodnym z umarłych, aby on był we wszytkich przodek trzymając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '18', '18', 'Abowiem upodobało się Ojcu, aby wszytka doskonałość mieszkała w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '19', '19', 'I przezeń aby sobie pojednał wszytki rzeczy, uspokoiwszy przez krew krzyża jego ty rzeczy, które były na ziemi i na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '20', '20', 'A tak was, którzyście niekiedy byli oddaleni i nieprzyjaciele, umysłem który się bawił złemi uczynkami, teraz zaiste pojednał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '21', '21', 'W ciele swoim własnym przez śmierć, aby stawił nas święte i nienaganione i niewinne przed oblicznością swoją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '22', '22', 'Jeśli trwacie w wierze ugruntowani i mocni, ani bywacie poruszeni od nadzieje Ewanjelijej, którąście  słyszeli przepowiedaną wszelkiemu stworzeniu, które jest pod niebem, której ja Paweł stałem się sługą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '23', '23', 'Teraz raduję się z tych rzeczy, które cierpię dla was i ostatki ucisków Krystusowych wypełniam w ciele moim, za ciało jego, które jest kościół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '24', '24', 'Którego kościoła stałem się sługą wedle szafowania Bożego, które mi jest dane u was, abych wypełnił słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '25', '25', 'To jest tajemnicę, która była skryta od wieków i od rodzajów, a teraz objawiona jest świętym jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '26', '26', 'Którym chciał Bóg oznajmić co są za bogactwa tej chwalebnej tajemnice między pogany, to jest Krystusa między wami, który jest nadzieją chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '27', '27', 'Którego my przepowiedamy, upominając każdego człowieka i ucząc każdego człowieka wszelakiej mądrości, abychmy stawili każdego człowieka doskonałym w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '0', '28', '28', 'Na co też pracuję bojując wedle możności jego, która się mocnie we mnie okazuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '0', '0', 'Przytym chcę abyście wiedzieli jako wielkie bojowanie mam dla was i dla tych, którzy są w Laodycei i dla tych, którzykolwiek nie widzieli oblicza mego wedle ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '1', '1', 'Aby pocieszenie miały serca ich, gdy będą społecznie złączeni miłością, a to na wszelkie bogactwo pewnego wyrozumienia, to jest ku uznaniu tajemnice Boga i Ojca i Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '2', '2', 'W którym skryte są wszytki skarby mądrości i umiejętności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '3', '3', 'A toć powiedam, iżby nikt was nie oszukawał łagodnymi słowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '4', '4', 'Bo aczkolwiek nie jestem obecny wedle ciała, a wszakże umysłem jestem z wami, weseląc się i widząc porządek wasz i stateczność wiary waszej w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '5', '5', 'Jakoście tedy przyjęli Krystusa Jezusa Pana, tak w nim postępujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '6', '6', 'Będąc ukorzenieni i zbudowani na nim i utwierdzeni wiarą jakoście się nauczyli, obfitując w niej z dziękowaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '7', '7', 'Baczcie tedy iżby żaden nie był któryby was złupić miał przez mądrość ludzką i próżną zdradę. Ucząc wedle ustaw ludzkich, podług nauk świeckich, a nie według Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '8', '8', 'Dlatego iż w nim mieszka wszytka pełność Bóstwa istotnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '9', '9', 'I jesteście w nim napełnieni, który jest głową wszelkiego państwa i przełożeństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '10', '10', 'Przez którego też obrzezani jesteście obrzezaniem nie rękoma uczynionym, gdyście ciało wasze grzechowi poddane zwlekli przez obrzezanie Krystusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '11', '11', 'Pogrzebani z nim przez krzest, przez który też wyście z nim powstali przez wiarę możnej sprawy Bożej, który go wzbudził z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '12', '12', 'I was też, gdyście byli umarli w grzechach i w nieobrzezce ciała waszego, ożywił z nim pospołu, odpuściwszy wam wszytki grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '13', '13', 'I wymazawszy cyrograf ustaw, który był przeciwko nam i był nam przeciwny, a on ji precz odjął z pośrzodku, przybiwszy ku krzyżowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '14', '14', 'I zewlokszy państwa i przełożeństwa, jawnie je wywiódł na podziw, zacnie je zwyciężywszy przezeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '15', '15', 'Niechaj tedy was nikt nie osądza dla pokarmu abo picia, abo względem świata, abo nastania miesiąca abo sabatów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '16', '16', 'Które rzeczy są cieniem rzeczy przyszłych, ale ciało jest Krystusowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '17', '17', 'Żaden się niech nie waży stanowić czego około was, z poddaństwa i chwały Aniołów, w ty się rzeczy wtrącając, których nie widział, a próżno się nadymając z umysłu ciała swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '18', '18', 'Nie trzymając się głowy, z której wszytko ciało przez spojenia i złączenia związane i ściągnione roście, z pomnożenia Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '19', '19', 'Przeto jeśli będąc umarłymi z Krystusem, wolni jesteście od ustaw świeckich, przeczże jakoby żywiąc na świecie, ustawami obciążeni bywacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '20', '20', 'Nie dotykaj się, nie kosztuj, nie macaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '21', '21', 'Które rzeczy wszytki samym używaniem giną, będąc ustawione z roskazania i nauk ludzkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '1', '22', '22', 'A mają niejakie podobieństwo mądrości, w dobrowolnej chwale i w pokorze i w tym, iż ciału nie folgują, ale ni zacz nie stoją, gdyż się ku tym rzeczam ściągają, którymi ciało bywa nasycone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '0', '0', 'A tak jeśliście wstali z Krystusem, rzeczy tych które wzgórę są szukajcie, gdzie Krystus na prawicy Bożej siedzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '1', '1', 'Starajcie się o rzeczach, które są wzgórę, nie które są na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '2', '2', 'Abowiem jesteście umarli, a żywot wasz skryty jest z Krystusem w Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '3', '3', 'Gdy Krystus on żywot nasz się okaże, tedy i wy z nim okażecie się chwalebnymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '4', '4', 'Umartwiejcież tedy członki wasze ziemskie, grzech nieczysty, plugastwo, sprośną roskosz, złą pożądliwość i łakomstwo, które jest bałwochwalstwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '5', '5', 'Dla których rzeczy przychodzi gniew Boży na syny nieposłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '6', '6', 'W którycheście wy niekiedy chodzili, gdyście żyli w nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '7', '7', 'Ale teraz odłóżcie i wy ty wszytki rzeczy: gniew, popędliwość, złość, złorzeczeństwo, plugawe słowa z ust waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '8', '8', 'Nie kłamajcie jeden przeciw drugiemu, gdyście zwlekli onego starego człowieka z uczynkami jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '9', '9', 'I oblekli nowego, który się odnawia ku uznaniu, podług wyobrażenia tego, który go stworzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '10', '10', 'Gdzie nie masz Greka i Żyda, obrzezania i nieobrzezki, człowieka prostego, Tatarzyna, niewolnika, wolnego, ale wszytko i we wszech Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '11', '11', 'Przyobleczcież się tedy (jako wybrani Boży, święci i umiłowani) w wnętrzności miłosierdzia, w dobrotliwość, w pokorę, w cichość, w cierpliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '12', '12', 'Folgując jeden drugiemu i odpuszczając sobie, jeśliby kto miał skargę przeciwko drugiemu. Jako też Krystus odpuścił wam, tak też i wy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '13', '13', 'A nad to wszytko zachowajcie miłość, która jest związką doskonałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '14', '14', 'A pokój Boży niech roskazuje w sercach waszych, ku któremu też wezwani jesteście w jedno ciało, a bądźcie wdzięczni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '15', '15', 'Słowo Krystusowe niechaj mieszka w was obficie, z wszelaką mądrością ucząc i społecznie sami siebie napominając przez psalmy, pieśni i śpiewania duchowne, z łaską śpiewając w sercu waszym Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '16', '16', 'A cokolwiek uczynicie mową abo skutkiem, czyńcie w imię Jezusowe, dziękując Bogu i Ojcu przezeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '17', '17', 'Żony, bądźcie poddane swoim mężom, jako przystoi w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '18', '18', 'Mężowie miłujcie żony, ani się srożcie przeciwko nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '19', '19', 'Synowie słuchajcie rodziców we wszytkim, abowiem to Panu miło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '20', '20', 'Ojcowie nie drażnicie dziatek waszych, iżby w rospacz nie przyszły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '21', '21', 'Słudzy słuchajcie we wszem tych, którzy są pany wedle ciała, nie służąc na oko, jako ci, którzy się chcą ludziam podobać, ale z szczerością serca Boga się bojąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '22', '22', 'A cokolwiek uczynicie, z serca czyńcie, jako Panu a nie ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '23', '23', 'Wiedząc iż od Pana weźmiecie zapłatę dziedzictwa, abowiem Panu Krystusowi służycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '2', '24', '24', 'A któryby krzywdy czynił, odniesie krzywdę którą uczynił, a nie maszci brakowania osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '0', '0', 'Wy panowie zachowajcie się sprawiedliwie i miernie przeciw sługam, wiedząc iż i wy macie Pana w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '1', '1', 'W modlitwach trwajcie, czując w nich z dziękowaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '2', '2', 'Społecznie się i za nas modląc, aby Bóg nam otworzył drzwi ku mowie, żebyśmy mówili tajemnicę Krystusowę, dla której też jestem w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '3', '3', 'Abych ją tak objawił, jako mnie się godzi mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '4', '4', 'Mądrze się obchodźcie z obcymi, czas odkupując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '5', '5', 'Mowa wasza niechaj będzie zawsze z łaską ozdobiona solą, abyście wiedzieli jako macie każdemu odpowiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '6', '6', 'Wszytki sprawy może wam oznajmi Tychykus, miły brat i wierny sługa, który ze mną służy w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '7', '7', 'Któregom posłał do was umyślnie, na to, aby się wywiedział o sprawach waszych i pocieszył serca wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '8', '8', 'Z Onesymem wiernym a miłym bratem, który jest z was, wszytko tedy wam oznajmia co się tu dzieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '9', '9', 'Pozdrawia was Arystarchus, który jest ze mną w więzieniu i Marek, stryjeczny brat Barnabaszów, o którym macie roskazanie, jeśliby przyszedł abyście go przyjęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '10', '10', 'I Jezus przezwiskiem Sprawiedliwy, którzy są z obrzezania. Ci tylko pomocniki w królestwie Bożym byli mnie ku pociesze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '11', '11', 'Pozdrawia was Epafras, który was jest sługa Krystusów, zawsze bojując za was w modlitwach, abyście się stali doskonałymi i zupełnymi w wszelakiej wolej Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '12', '12', 'Abowiemci mu świadczę, iż uprzejmą chuć ma przeciwko wam i tym, którzy są w Laodycejej i w Hierapolim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '13', '13', 'Pozdrawia was Łukasz, lekarz miły i Demas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '14', '14', 'Pozdrawiajcie bracię, która jest w Laodycjej i Nymfasa i kościół, który jest w domu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '15', '15', 'A gdy u was przeczytan będzie ten list, starajcie się, aby ji też przeczytano w kościele Laodyceńskim, a iżbyście wy też przeczytali ten, który jest napisan z Laodycjej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '16', '16', 'A powiedzcie Archippowi: Patrzaj abyś wypełnił urząd, któryś przyjął w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'col', '3', '17', '17', 'Pozdrowienie ręką moją Pawłową. Pamiętajcie związanie moje. Łaska niech będzie z wami. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '0', '0', 'I Paweł i Sylwan i Tymoteusz kościołowi Tesaloniceńskiemu, który jest w Bogu Ojcu i w Panie Jezusie Krystusie. Łaska i pokój niech wam będzie od Boga Ojca naszego i od Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '1', '1', 'Dziękujemy Bogu zawsze za was, wspominając was w modlitwach naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '2', '2', 'Bez przestanku wspominając skuteczną wiarę waszę i pracowitą miłość i cierpliwą onę nadzieję w Panu naszym Jezusie Krystusie, przed Bogiem i Ojcem naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '3', '3', 'Wiedząc bracia mili iż jesteście wybrani od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '4', '4', 'Przeto, iż Ewanjelija nasza była u was nie powieścią tylko, ale też i mocą w Duchu świętym i prawdziwym upewnieniu, jako wiecie jakowymichmy byli między wami dla was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '5', '5', 'A wyście się stali naśladowcami naszymi i Pańskimi, przyjąwszy słowo z uciskiem wielkim, z weselem Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '6', '6', 'Tak iżeście byli wzormi wszytkim wierzącym w Macedonijej i w Achajej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '7', '7', 'Abowiem od was nie tylko brzmiało słowo w Macedonijej i w Achajej, ale też na wszytkim miejscu wiara wasza, która jest w Boga tak słynęła, iż nam nie potrzeba nic mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '8', '8', 'Gdyż oni o was powiedają, cochmy za przystęp do was mieli, a jakoście nawróceni byli do Boga opuściwszy bałwany, abyście służyli Bogu żywemu i prawdziwemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '0', '9', '9', 'I oczekawali Syna jego z nieba, kt&nbsp;órego wzbudził z martwych, to jest Jezusa, który wybawia nas od onego gniewu przyszłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '0', '0', 'Gdyż sami wiecie bracia, iż przystęp nasz do was nie był próżny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '1', '1', 'Ale chociaż my przed tym cierpieli i zelżeni byli w Filipiech jako wiecie, a wszakże śmieleśmy sobie poczęli w Bogu naszym, ku przepowiedaniu u was Ewanjelijej Bożej z wielkim bojowaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '2', '2', 'Abowiem napominanie nasze nie było z zdrady ani z nieczystości, ani z chytrością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '3', '3', 'Ale jakośmy się Bogu podobali aby nam była zwierzona Ewanjelija, tak mówimy nie jako się ludziam podobając, ale Bogu, który doświadcza serc naszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '4', '4', 'Abowiem nigdyśmy nie używali powieści pochlebnej, jako wiecie, aniśmy dla łakomstwa nic nie zmyślali, Bóg świadkiem jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '5', '5', 'Ani szukając chwały z ludzi, ani od was, ani od inszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '6', '6', 'Mogąc was obciążać jako Apostołowie Krystusowi, ale byliśmy cichemi w pośrzodku, jakoby mamka ogrzewając dziatki swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '7', '7', 'Tak tedy chutliwi przeciw wam będąc, żądaliśmy wam użyczyć nie tylko Ewanjelijej Bożej, ale też i dusz naszych dlatego, iżeście nam miłemi byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '8', '8', 'Abowiem bracia pamiętacie pracę naszę i kłopot, gdyż w nocy i we dnie robiąc, abyśmy żadnego z was nie obciążyli, przepowiedalichmy u was Ewanjeliję Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '9', '9', 'Wy świadkowie jesteście i Bóg, jakośmy świętobliwie i sprawiedliwie i bez naganienia żyli między wami którzyście uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '10', '10', 'Jako wiecie którymeśmy sposobem każdego z was (jakoby ociec dziatki swoje) napominali i cieszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '11', '11', 'I barzo prosili abyście postępowali jako należy Bogu, który was wezwał do swego królestwa i chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '12', '12', 'A przeto też dziękujemy Bogu ustawicznie, iż wy przyjąwszy słowo Boże, któreście słyszeli od nas, przyjęliście nie za słowo ludzkie, ale (jako było prawdziwie) za słowo Boże, który też skutecznie się okazuje w was, którzy wierzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '13', '13', 'Abowiem bracia staliście się naśladowcami kościołów Bożych, które są w Żydowskiej ziemi w Krystusie Jezusie, iżeście wy ty rzeczy cierpieli od swoich przyrodnych, jako i oni od Żydów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '14', '14', 'Którzy też Pana Jezusa zabili i własne proroki i nas wygnali, ani się Bogu podobają i wszytkim się ludziam przeciwiają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '15', '15', 'Broniąc nam żebychmy nie mówili poganom czymby byli zbawieni, aby zawsze wypełniali grzechy swe, abowiem ogarnął je gniew aż do końca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '16', '16', 'A my bracia b&nbsp;ędąc osierociałymi bez was na krótki czas oblicznością nie sercem, uprzejmieśmy się starali widzieć oblicze wasze z wielką chucią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '17', '17', 'Dlategośmy chcieli przyjść do was, (zwłaszcza ja Paweł) raz i powtóre, ale przekaził nam szatan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '18', '18', 'Abowiem któraż jest nadzieja nasza, abo wesele, abo korona, z której bym się ja chwalił? Izali nie wy przed oblicznością Pana naszego Jezu Krysta w przyście jego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '1', '19', '19', 'Zaisteć wy jesteście chwałą naszą i weselem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '0', '0', 'A tak gdyśmy już wstrzymać się nie mogli, zdało się nam za rzecz nalepszą abyśmy sami w Atenach zostali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '1', '1', 'A posłalichmy Tymoteusza brata naszego i sługę Bożego i pomocnika naszego w Ewanjelijej Krystusowej, aby was potwierdził i napominał około wiary waszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '2', '2', 'Tak aby żaden nie poruszył się w tych uciskach, abowiem sami wiecie żeśmy na to wysadzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '3', '3', 'Bo gdychmy byli u was, przedtymechmy to wam opowiedali żeśmy mieli być uciśnieni, co się też stało i wiecie to.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '4', '4', 'Przeto też ja już nie mogąc się wstrzymać, posłałem go abym się dowiedział o wierze waszej, iżby was snadź był nie kusił on kusiciel, a iżby się próżna nie stała praca nasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '5', '5', 'Nie dawno tedy Tymoteusz od was przyszedszy do nas, oznajmił nam wiarę i miłość waszę, a iż nas macie w dobrej pamięci, zawsze nas żądacie widzieć, jako i my też was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '6', '6', 'A przetoż bracia jesteśmy pocieszeni z was w każdym utrapieniu i potrzebie naszej przez wiarę waszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '7', '7', 'Abowiem teraz żywiemy jeśli wy stoicie w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '8', '8', 'Bo któreż dziękowanie możemy oddać Bogu za was z wszelkiego wesela, którym się weselemy dla was przed Bogiem naszym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '9', '9', 'W nocy i wednie barzo pilno prosząc abyśmy widzieli oblicze wasze i abychmy wypełnili czego jeszcze w wierze waszej nie dostawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '10', '10', 'Ale sam Bóg Ociec nasz i Pan nasz Jezus Krystus niech wyprostuje drogi nasze do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '11', '11', 'A Pan niechaj was pomnoży i obfitymi uczyni, społeczną miłością między wami i przeciw wszytkim, jako i my przeciwko wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '2', '12', '12', 'Aby serca wasze nienaganione utwierdził w świętobliwości przed Bogiem i Ojcem naszym, na przyście Pana naszego Jezu Krysta ze wszytkimi świętymi jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '0', '0', 'Na ostatek tedy bracia prosimy was i napominamy przez Pana Jezusa, jakoście wzięli od nas, którym sposobem macie chodzić i Bogu się podobać, abyście więcej przewyższali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '1', '1', 'Gdyż wiecie które, roskazania wziąwszy dalichmy wam od Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '2', '2', 'Abowiem tać jest wola Boża, toć jest poświęcenie wasze, żebyście się powściągali od nieczystoty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '3', '3', 'I aby umiał każdy z was naczynie swe trzymać w świętobliwości i uczciwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '4', '4', 'Nie będąc zjęci pożądliwością jako pogani, którzy nie znają Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '5', '5', 'Niechaj żaden nie uciska, abo podchodzi w żadnej sprawie brata swego, abowiemci Pan jest, który się mści wszystkich tych rzeczy, jakochmy to i przed tym powiedzieli wam i oświadczyli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '6', '6', 'Bo nie wezwał nas Bóg ku nieczystości, ale ku świętobliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '7', '7', 'A przeto który to odrzuca, nie odrzuca człowieka, ale Boga, który wam dał Ducha swego świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '8', '8', 'Przytym co się tycze braterskiej miłości, nie potrzeba wam tego abym do was pisał, abowiem sami od Boga jesteście nauczeni, abyście jedni drugie miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '9', '9', 'Bo też i to czynicie przeciwko wszytkim bratom, którzy są we wszytkiej Macedonijej. A prosimy was bracia iżbyście więcej przewyższali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '10', '10', 'I starali się abyście byli spokojnymi i pilnowali rzeczy swoich, a robili własnymi rękami waszymi, jakośmy wam opowiedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '11', '11', 'Abyście się poczciwie zachowali obcym i aby niczego wam potrzeba nie było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '12', '12', 'A nie chcę bracia abyście wiedzieć nie mieli, co się tycze tych, którzy zasnęli, iżbyście się nie smucili jako i drudzy, którzy nadzieję nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '13', '13', 'Abowiem jeśli wierzymy iż Jezus umarł i powstał, tak też Bóg ty którzy zasnęli w Jezusie, przywiedzie z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '14', '14', 'Bo to wam powiedamy słowy Pańskimi: Iż my żywi, którzy pozostaniemy aż do przyścia Pańskiego, nie uprzedziemy tych, którzy zasnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '15', '15', 'Gdyż sam Pan z krzykiem i z głosem Anielskim i z trąbą Bożą zstąpi z nieba, a którzy byli umarli w Krystusie, powstaną pierwej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '16', '16', 'Potym my żywi, którzy pozostaniemy, zachwyceni będziemy społu z nimi na obłoki, żebychmy zaszli Panu na powietrze. A tak zawsze z Panem będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '3', '17', '17', 'A przetoż pocieszajcie jedni drugie tymi słowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '0', '0', 'Zatym co się tycze czasów i chwil zamierzonych bracia, nie potrzebujecie abym wam pisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '1', '1', 'Abowiem sami dostatecznie wiecie, iż on dzień Pański tak przyjdzie jako złodziej w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '2', '2', 'Bo gdy rzeką: Pokój i bespieczeństwo, tedy nagle zginienie przyjdzie na nie, jako ból niewiasty brzemiennej, a nie ujdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '3', '3', 'Ale wy bracia nie jesteście w ciemności, aby on dzień was miał jako złodziej zastać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '4', '4', 'Wszyscy wy jesteście synowie światłości i synowie dnia, nie jesteśmy nocy ani ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '5', '5', 'Przeto tedy nie śpimy jako inszy, ale czujmy i bądźmy trzeźwiemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '6', '6', 'Abowiem ci, którzy śpią, w nocyć śpią, a którzy się upijają, w nocyć się upijają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '7', '7', 'Ale my, którzy jestechmy dniowi, bądźmy trzeźwi, oblókszy przedni pleszek wiary i miłości , a za hełm nadzieję zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '8', '8', 'Abowiem Bóg nie postanowił nas na gniew, ale na otrzymanie zbawienia przez Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '9', '9', 'Który umarł za nas, aby choćbyśmy czuli choć spali, abychmy społu z nimi żyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '10', '10', 'A przetoż napominajcie jeden drugiego i budujcie jedni drugich, jako też czynicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '11', '11', 'A prosimy was bracia abyście poznali ty którzy pracują między wami i którzy są przełożonymi waszymi w Panie i napominają was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '12', '12', 'A iżbyście je wielce miłowali dla sprawy ich, pokój zachowajcie społecznie między sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '13', '13', 'I prosimy was bracia napominajcie nierządne, cieszcie mdłe, wspomagajcie niemocne, bądźcie cierpliwymi przeciwko wszytkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '14', '14', 'Baczcie aby żaden złem za złe nikomu oddawał, zawsze dobrotliwości naśladujcie, tak między wami społecznie, jako i przeciwko wszytkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '15', '15', 'Zawsze się weselcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '16', '16', 'Bez przestanku się módlcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '17', '17', 'We wszytkim dziękujcie, abowiem tać jest wola Boża przez Jezusa Krystusa przeciwko wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '18', '18', 'Ducha nie zagaszajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '19', '19', 'Proroctwa nie wzgardzajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '20', '20', 'Wszytkich rzeczy doświadczajcie, a co jest dobrego tego się trzymajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '21', '21', 'Od wszelakiego podobieństwa złości wstrzymawajcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '22', '22', 'A sam Bóg pokoju niech poświęci was zupełnie, a duch wasz cały i dusza i ciało bez naganienia w przyście Pana naszego Jezu Krysta niechaj będą zachowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '23', '23', 'Wiernyć jest który was wezwał, który też to uczyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '24', '24', 'Bracia módlcie się za nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '25', '25', 'Pozdrówcie bracią wszystkę z pocałowaniem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '26', '26', 'Poprzysięgam was przez Pana, aby ten list przeczytan był wszytkiej świętej braciej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1th', '4', '27', '27', 'Łaska Pana naszego Jezu Krysta niech będzie z wami. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '0', '0', 'Paweł i Sylwanus, i Tymoteusz kościołowi Tesaloniceńskiemu, który jest w Bogu Ojcu naszym i w Panie Jezusie Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '1', '1', 'Łaska niech wam będzie i pokój od Boga Ojca naszego i Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '2', '2', 'Bracia mamy Bogu zawsze dziękować za was jako się godzi przeto, iż barzo się pomnaża wiara wasza i obfituje społeczna miłość każdego z was wszytkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '3', '3', 'Tak, iż my się z was przechwalamy u kościołów Bożych, to jest z cierpliwości waszej i wiary w wszelkim prześladowaniu waszym i w uciskach, które nosicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '4', '4', 'Która rzecz jest jawnym okazaniem sprawiedliwego sądu Bożego, na to aby was miano za godne królestwa Bożego, dla którego też ty rzeczy cierpicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '5', '5', 'Abowiemci sprawiedliwa rzecz jest u Boga, zasię tym utrapienie oddać, którzy was trapią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '6', '6', 'A wam, którzy bywacie utrapieni, ulżenie z nami, gdy się objawi Pan Jezus z nieba z Anioły możności swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '7', '7', 'Płomienistym zapaleniem się mszcząc nad tymi, którzy Boga nie znali i ani słuchają Ewanjelijej Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '8', '8', 'Którzy karani będą wiecznym zatraceniem, skazani od obliczności Pańskiej i od możnej chwały jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '9', '9', 'Gdy przyjdzie aby był uwielbion w świętych swoich i aby się dziwnym okazał we wszytkich wiernych w on dzień przeto, iż uwierzono świadectwu naszemu u was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '10', '10', 'Dla czego też modlimy się zawsze za was, aby Bóg nasz was godnymi czynił wezwania tego i wypełnił wszytkę daremną życzliwość dobrotliwości swojej i sprawę wiary mocnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '0', '11', '11', 'Aby było chwalone imię Pana naszego Jezu Krysta w was, a wy w nim, z łaski Boga naszego i Pana Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '0', '0', 'Zatym prosimy was bracia przez przyście Pana naszego Jezu Krysta i nasze zgromadzenie do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '1', '1', 'Abyście się rychło nie poruszyli od umysłu waszego, ani sobą trwożyli, ani przez ducha, ani przez powieść, ani przez list rzekomo od nas pisany, jakoby już blisko był dzień Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '2', '2', 'Niech was żaden nie zwodzi żadna miarą, abowiem nie przyjdzie dzień Krystusów, ażby pierwej przyszło odstąpienie, a był objawion człowiek on złościwy, który jest syn zatracenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '3', '3', 'Który się sprzeciwia i wynosi przeciwko wszelkiemu, którego zową Bogiem, abo któremu chwała bywa wyrządzana tak, iż siedzi w kościele Bożym jako Bóg, okazując się jakoby był Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '4', '4', 'Izali nie pamiętacie, iż jeszcze przy was będąc, tom wam powiedział?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '5', '5', 'A teraz wiecie co przeszkadza, aby on był objawion czasu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '6', '6', 'Abowiem się już sprawuje tajemnica nieprawości, tylkoż ten, który teraz zawadza, będzie zawadzał aż odjęt będzie z pośrzodku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '7', '7', 'A tedy objawion będzie on złoczyńca, którego Pan zniszczy Duchem ust swoich i znacznym przyściem swoim zgładzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '8', '8', 'Tego mówię, którego przyście jest z mocy szatańśkiej, z wszelaką możnością i znaki i cudami kłamliwemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '9', '9', 'I z wszelaką zdradą niesprawiedliwości w tych, którzy giną przeto, iż miłości prawdy nie przyjęli, aby byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '10', '10', 'Przeto tedy przyszle im Bóg skuteczną moc błędu, aby kłamstwu wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '11', '11', 'Iżby byli potępieni wszyscy, którzy nie uwierzyli prawdzie, ale przestali na niesprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '12', '12', 'Ale my powinnichmy Bogu dziękować zawsze za was bracia umiłowani od Pana, iż wybrał was Bóg od początku ku zbawieniu, przez poświęcenie Ducha i wiarę prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '13', '13', 'Na co was wezwał przez Ewanjeliją naszę, ku otrzymaniu chwały Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '14', '14', 'Przeto tedy bracia bądźcie stateczni, a trzymajcie podaną naukę, którejeście się nauczyli, choć przez powieść, choć przez list nasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '15', '15', 'A sam Pan nasz Jezus Krystus i Bóg Ociec nasz, który umiłował nas i dał pocieszenie wieczne i nadzieję dobrą przez łaskę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '1', '16', '16', 'Niech cieszy serca nasze i twierdzi nas we wszelakiej mowie i sprawie dobrej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '0', '0', 'Na ostatek bracia módlcie się za nas aby słowo Boże szło i było chwalone jako też u was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '1', '1', 'I abyśmy byli wybawieni od sprośnych i złych ludzi, abowiem nie wszytkich jest wiara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '2', '2', 'Ale wiernyć jest Pan, który utwierdzi was i zachowa od złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '3', '3', 'A dufamy przez Pana o was, iż ty rzeczy, które opowiadamy wam, czynicie i czynić będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '4', '4', 'Zatym Pan niechaj sprawuje serca wasze ku miłości Bożej i ku oczekawaniu Krystusowemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '5', '5', 'A opowiedam i wam bracia przez imię Pana naszego Jezu Krysta, iżbyście się odłączyli od każdego brata, który się nierządnie sprawuje, a nie według nauki podanej, którą wziął od nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '6', '6', 'Abowiem sami wiecie jako nas macie naśladować, gdyżeśmy się nierządnie nie sprawowali między wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '7', '7', 'Aniśmy od żadnego biorąc chleb darmo jedli, ale z pracą i z kłopotem, we dnie i w nocy robiąc dlatego, abyśmy żadnego z was nie obrażali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '8', '8', 'Nie abyśmy tego prawa nie mieli, ale abyśmy się wam sami stawili za wzór, abyście nas naśladowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '9', '9', 'Abowiem gdychmy byli u was, tochmy was opowiedali: Iż jeśliby kto nie chciał robić, ten aby nie jadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '10', '10', 'Bo słyszemy, iż niektórzy nierządnie się sprawują między wami nic nie robiąc, ale niepotrzebnych rzeczy patrzając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '11', '11', 'A tym, którzy są takowi opowiedamy i prosimy ich przez Pana naszego Jezu Krysta, aby z pokojem robiąc używali chleba swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '12', '12', 'A wy bracia nie osłabiajcie dobrze czyniąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '13', '13', 'A jeśli kto nie słucha powieści naszej, tego przez list oznajmicie, ani z nim obcujcie, aby się zawstydził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '14', '14', 'Ani go miejcie za nieprzyjaciela, ale upominajcie jako brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '15', '15', 'Ufam Pan pokoju, niech wam da pokój zawsze wszelakim obyczajem, Pan niech będzie z wami wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '16', '16', 'Pozdrowienie ręką moją Pawłową, który jest znak w każdym liście, tak piszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2th', '2', '17', '17', 'Łaska Pana naszego Jezu Krysta niechaj będzie z wami wszytkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '0', '0', 'Paweł Apostoł Jezusa Krystusa z zrządzenia Boga Zbawiciela naszego i Pana naszego Jezu Krysta, który jest nadzieją naszą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '1', '1', 'Tymoteuszowi własnemu synowi w wierze, łaska niech tobie będzie, miłosierdzie, pokój od Boga Ojca naszego i od Jezusa Krystusa Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '2', '2', 'Jakom cię prosił abyś został w Efezie gdym szedł do Macedonijej, zostańże abyś opowiedział niektórym żeby różnej nauki nie uczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '3', '3', 'A iżby się nie bawili baśniami i rodzajmi nieskończonymi, które radszej gadki przynoszą niż budowanie Boże, które jest przez wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '4', '4', 'A koniec roskazania jestci miłość z szczyrego serca i sumnienia dobrego i wiary nie obłudnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '5', '5', 'Od których rzeczy niektórzy ubłądziwszy, udali się na próżną mowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '6', '6', 'Chcąc być nauczycielmi zakonu, nie rozumieją co mówią, ani o których rzeczach twierdzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '7', '7', 'Iście wiemy, że dobry jest zakon, jeśliby go kto słusznie używał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '8', '8', 'Wiedząc, iż zakon nie jest położon sprawiedliwemu, ale tym, którzy zakonem gardzą, nie chcąc mu być poddanymi, niepobożnym i grzesznym, złoczyńcam i sprośnym, tym którzy zabijają ojca i matkę, mężobójcam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '9', '9', 'Nieczystym, Sodomczykom, tym którzy ludzi kradną, kłamcam, krzywoprzysiężcam i jeśli co inego jest przeciwnego zdrowej nauce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '10', '10', 'Która jest wedle chwalebnej Ewanjelijej błogosławionego Boga, która mnie jest zwierzona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '11', '11', 'Dziękuje tedy temu, który mię umocnił, to jest Krystusowi Jezusowi Panu memu, iż mię za wiernego miał, aby mię wysadził na posługę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '12', '12', 'Którym pierwej był bluźnierzem i prześladowcą i gwałtownikiem, ale się nademną zmiłował, abowiem nie wiedząc, tom czynił przez niedowiarstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '13', '13', 'Ale barzo obfitowała łaska Pana naszego z wiarą i z miłością, która jest w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '14', '14', 'Wiernać jest ta mowa i wszelakim obyczajem godna, abyśmy ją przyjęli, iż Krystus Jezus przyszedł na świat, aby grzeszniki zachował, z których jam jest pierwszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '15', '15', 'Ale się dlatego nademną zmiłował, aby we mnie pierwszym okazał Jezus Krystus wszelaką litość, żebym był wzorem tym, którzyby wierzyli weń ku żywotowi wiecznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '16', '16', 'A Królowi wiecznemu, nieśmiertelnemu, niewidomemu, samemu mądremu Bogu, cześć niech będzie i chwała na wieki wieków Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '17', '17', 'To roskazanie zalecamci synu Tymoteuszu, to jest, abyś wedle proroctw, które przyszły o tobie, walczył przez nie onym dobrym wojowaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '18', '18', 'Trzymając wiarę i dobre sumnienie, które odrzuciwszy niektórzy, poginęli od wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '0', '19', '19', 'Z których jest Hymeneus i Aleksander, którem podał szatanowi, aby się nauczyli nie bluźnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '0', '0', 'Napominam tedy przez wszytkimi rzeczami, aby były czynione prośby, modlitwy, żądania i dziękowania za wszytki ludzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '1', '1', 'Za króle i za wszytki przełożone, abyśmy cichy i spokojny żywot wiedli z wszelaką pobożnością i poczciwością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '2', '2', 'Abowiemci to jest rzecz dobra i przyjemna przed Bogiem Zbawicielem naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '3', '3', 'Który chce aby wszyscy ludzie byli zbawieni i aby przyszli ku uznaniu prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '4', '4', 'Boć jeden jest BÓG, jeden też POŚRZEDNIK Boga i ludzi, CZŁOWIEK Krystus Jezus,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '5', '5', 'Który samego siebie wydał zapłatą odkupienia za wszytki. Który jest onym świadectwem naznaczonym czasom swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '6', '6', 'Którego jestem postanowion woźnym i Apostołem (prawdeć mówię przez Krystusa, nie kłamam) nauczycielem poganów z wiarą i prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '7', '7', 'Chcę tedy, aby mężowie się modlili na każdym miejscu tak, iżby szczyre podnosili ręce bez gniewu i zwątpienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '8', '8', 'Także i niewiasty, aby się przychędażały ubiorem poczciwym z wstydem i skromnością, nie splecionymi włosy, abo złotem, abo perłami, abo ubiorem kosztownym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '9', '9', 'Ale dobremi uczynkami, co przystoi niewiastam, które ślubują być pobożnemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '10', '10', 'Niewiasta niech się uczy w milczeniu z wszelakim poddaństwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '11', '11', 'Abowiem nie dopuszczam niewieście uczyć, ani brać zwierzchność nad mężem, ale być w milczeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '12', '12', 'Bo Adam pirwszy sprawion jest, potym Ewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '13', '13', 'Adam też nie był zwiedzion, ale niewiasta zwiedziona. Zstała się winna przestępstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '1', '14', '14', 'A wszakże będzie zbawiona dzieci rodząc, jeśliby trwała w wierze i w miłości i świętobliwości, z miernością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '0', '0', 'Wiernać jest ta mowa, jeśli kto urzędu biskupiego żąda, zacnej sprawy żąda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '1', '1', 'Ma tedy być biskup bez naganienia, mąż jednej żony, czujny, trzeźwy, skromny, rad goście przyjmujący, godny ku uczeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '2', '2', 'Nie pijanica, nie targając się rękami na kogo, nie szukający zysku niepoczciwego, ale mierny, niezwadliwy, niełakomy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '3', '3', 'Któryby dom swój dobrze sprawował, któryby dziatki miał w karności, z wszelaką uczciwością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '4', '4', 'Abowiem jeśliby kto nie umiał własnego swego domu sprawować, jakoż pieczą mieć będzie o kościele Bożym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '5', '5', 'Nie nowotny, aby będąc nadęty nie wpadł w obwinowanie czartowskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '6', '6', 'Abowiem potrzeba jest aby miał dobre świadectwo od obcych, aby nie wpadł w pohańbienie i w sidło diabelskie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '7', '7', 'Diakonowie też mają być uczciwi, nie dwojakiego słowa, nie udawający się za wielkim piciem wina, nie żądając zysku niepoczciwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '8', '8', 'Trzymając tajemnicę wiary, z szczyrym sumieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '9', '9', 'A ci też niechaj pirwej będą doświadczeni, zatym niech służą, jeśliby byli przez naganienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '10', '10', 'Także żony ich niech będą uczciwe, nie potwarne, trzeźwe, wierne we wszytkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '11', '11', 'Diakonowie niech będą mężowie jednej żony, którzyby dziatki dobrze rządzili i własne domy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '12', '12', 'Abowiemci którzyby dobrze służyli, dostępują sami sobie miejsca dobrego i wielkiej wolności przez wiarę, która jest w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '13', '13', 'Ty rzeczy tobie piszę, nadziewając się iż przyjdę do ciebie barzo rychło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '14', '14', 'A jeślibym omieszkał, abyś wiedział jako się masz w domu Bożym sprawować, który jest kościół Boga żywego, słup i potwierdzenie prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '2', '15', '15', 'A bez wątpienia wielkać jest tajemnica pobożności, iż Bóg zstał się widomym w ciele, usprawiedliwiony w duchu, widzian jest od Aniołów, przepowiedzian jest poganom, uwierzono mu na świecie i jest wzgórę wzięt do chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '0', '0', 'A duch jawnie opowieda, iż ostatecznych czas&nbsp;ów odstąpią niektórzy od wiary, słuchając duchów zdradliwych nauk czartowskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '1', '1', 'Przez obłudność kłamców, których sumnienie gorącym żelazem jest odcięte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '2', '2', 'Którzy zakazują małżeństwa, każą się powścięgać od pokarmów, które Bóg stworzył ku używaniu wiernym z dziękowaniem i tym, którzy poznali prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '3', '3', 'Abowiem cokolwiek Bóg stworzył, dobre jest, a nic nie ma być odrzucono, jeśliby z dziękowaniem przyjmowano było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '4', '4', 'Gdyż poświecono bywa przez Słowo Boże i modlitwę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '5', '5', 'Jeśli to podasz braciej, dobrym będziesz sługą Jezu Krysta, wychowanym w słowiech wiary i nauki dobrej, którejeś statecznie naśladował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '6', '6', 'Zatym odrzuć baśni sprośne i babie, a ćwicz się ku pobożności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '7', '7', 'Abowiemci cielesne ćwiczenie mało pomoże, ale pobożność ku wszem rzeczam jest pożyteczna, mając obietnicę żywota teraźniejszego i przyszłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '8', '8', 'Wiernać jest ta powieść i godna aby była wszelakim obyczajem przyjmowana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '9', '9', 'Abowiem przeto też pracujemy i zelżeni bywamy, iż nadzieję pewną mamy w Bogu żywym, który zachowywa wszytki ludzi, a nawięcej wierne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '10', '10', 'Ty rzeczy opowiedaj i nauczaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '11', '11', 'Żaden niech młodości twojej nie wzgardza, ale bądź wzorem wiernych w mowie, w obcowaniu, w miłości, w duchu, w wierze, w czystości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '12', '12', 'Aż przyjdę, pilnuj czytania, napominania i nauki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '13', '13', 'Nie waż lekce daru, który jest w tobie, który tobie dan jest ku prorokowaniu, z włożeniem rąk starszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '14', '14', 'Ty rzeczy miej na pieczy, w tych zostań, aby postępek twój był znaczny między wszytkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '3', '15', '15', 'Pilnuj samego siebie i nauki, trwaj w tych rzeczach, bo jeśli to uczynisz i samego siebie zachowasz i tych, którzyby cię słuchali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '0', '0', 'Starszego nie fukaj, ale go jako ojca napominaj, a młodsze jako bracią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '1', '1', 'Niewiasty starsze jako matki, a młodsze jako siostry, z wszelaką czystością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '2', '2', 'Wdowy miej w uczciwości, które prawdziwie wdowy są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '3', '3', 'A jeśli która wdowa dzieci abo wnuczęta ma, niech się pirwej uczą być pobożnymi przeciwko domowi swemu i oddać dobrodziejstwo rodzicom. Abowiem to poczciwa rzecz jest i przyjemna przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '4', '4', 'A która prawdziwie wdowa jest i osierociała, ma nadzieję w Bogu i trwa w prośbach i w modlitwach w nocy i we dnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '5', '5', 'Ale która jest w rozkoszach, ta żywiąc umarła jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '6', '6', 'Ty tedy rzeczy opowiedaj, aby bez naganienia były.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '7', '7', 'A jeśli która nie opatruje swych, a zwłaszcza domowych, zaprzała się wiary i jest gorsza niźli niewierny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '8', '8', 'Wdowa niech będzie obrana ku posługiwaniu, nie młodsza sześćdziesiąt lat, któraby była żoną jednego męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '9', '9', 'Mając świadectwo w dobrych uczynkach, jeśli dzieci wychowała, jeśli goście rada przyjmowała, jeśli świętych nogi umywała, jeśli utrapionym pomagała i jeśli w każdym uczynku dobrym ustawiczną była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '10', '10', 'Ale młodszych wdów nie przyjmuj, bo gdyby poczęły być bujne przeciw Krystusowi, chcą za mąż idź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '11', '11', 'W tym godne przygany, iż pierwszą wiarę odrzuciły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '12', '12', 'A ktemu też i próżnujące, uczą się chodzić dom od domu, a nie tylko próżnują, ale też są świegotliwe, niepotrzebnemi rzeczami się bawiące, plotąc to co nie przystoi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '13', '13', 'Chcę tedy aby młodsze szły za mąż i dzieci rodziły, dom rządziły, przeciwnikowi żadnej przyczyny nie dawały ku złorzeczeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '14', '14', 'Abowiem już niektóre odstąpiły i za szatanem szły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '15', '15', 'A jeśli który wierny, abo która wierna ma wdowy, niech je wspomaga, a niechaj kościół nie będzie obciążony, aby wspomagał ty, które są prawdziwe wdowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '16', '16', 'Starsze, którzy się dobrze w urzędzie swym sprawują, niech mają godnymi dwojakiej czci, a zwłaszcza ty, którzy pracują w słowie i w nauce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '17', '17', 'Abowiem mówi Pismo: Wołowi wymłacającemu zboże, nie zawiążesz gęby i: Godnyć jest robotnik zapłaty swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '18', '18', 'Przeciwko starszemu nie przyjmuj skargi, wyjąwszy na dwu abo trzech świadkach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '19', '19', 'A ty, którzy grzeszą, karz przed wszytkimi, aby się i drudzy bali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '20', '20', 'Oświadczam się przed Bogiem i Panem Jezu Krystem i Anioły wybranymi, abyś ty rzeczy zachował, nie przekładając jednego nad drugiego, nie czyniąc nic przychylając się ku której stronie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '21', '21', 'Ręku ni na kogo nie wkładaj rychło, ani bądź uczestnikiem grzechów cudzych, samego siebie czystym zachowaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '22', '22', 'Już dalej wody nie pij, ale używaj po trosze wina dla żołądka twego i częstych chorób twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '23', '23', 'Niektórych ludzi grzechy przed tym bywają jawne, w przód idąc na potępienie, a za niektórymi idą pozad.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '4', '24', '24', 'Także też dobre uczynki przed tym jawne bywają, a ty, które się inaczej mają, nie mogą być tajne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '0', '0', 'Którzykolwiek słudzy są pod jarzmem, niech rozumieją pany swe godne być wszelakiej czci, aby imię Boże i nauka nie była bluźniona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '1', '1', 'A którzy mają pany wierne, niech ich nie wzgardzają, iż są bracia, ale radszej niech służą, iż są wierni i umiłowani i uczestniki dobrodziejstwa Bożego, tych rzeczy nauczaj i napominaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '2', '2', 'Jeśli kto różnej nauki uczy, a nie przystawa ku zdrowym powieściam Pana naszego Jezu Krysta i ku tej nauce, która jest wedle pobożności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '3', '3', 'Tenci jest nadęty nic nie umiejąc, ale szaleje około gadek i sporów o słowiech, z których roście zazdrość, swar, złorzeczeństwa, złe podejrzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '4', '4', 'Przewrotne ćwiczenia ludzi rozumu skażonego i którym odjęta jest prawda, mając pobożność za zysk, odłączże się od tych, którzy są takowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '5', '5', 'A jestci wielki zysk pobożności z umysłem, który przestawa na tym co ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '6', '6', 'Abowiem nie przynieślichmy nic na świat, zaiste ani też wynieść nic nie możemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '7', '7', 'Ale mając żywność i odzienie, na tym przestaniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '8', '8', 'Lecz którzy chcą być bogatymi, wpadają w pokuszenie i w sidło i w pożądliwości wiele szalonych i szkodliwych, które ponurzają ludzi na zatracenie i na zginienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '9', '9', 'Abowiem korzeń wszech złych rzeczy jest chciwość ku pieniądzam, których niektórzy pożądając, obłądzili się od wiary i sami siebie sprzebijali wiela boleści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '10', '10', 'Ale ty mężu Boży chroń się tych rzeczy, a naśladuj sprawiedliwości, pobożności, wiary, miłości, cierpliwości, cichości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '11', '11', 'Wiedź zacną bitwę wiary, chwyć się żywota wiecznego, dla którego jesteś wezwan i uczyniłeś zacne wyznanie przed wiela świadków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '12', '12', 'Opowiadamci przed Bogiem, który ożywia wszytki rzeczy i Jezusem Krystusem, który świadczył przed Poncjuszem Piłatem dobre wyznanie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '13', '13', 'Abyś zachował ty roskazania będąc bez zmazy i naganienia, aż do onego znacznego przyścia Pana naszego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '14', '14', 'Którego czasów swoich okaże on błogosławiony i sam mocarz, Król nad królmi i Pan nad pany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '15', '15', 'Który sam ma nieśmiertelność, mieszkając w światłości, do której przystępu nie masz, którego żaden z ludzi nie widział, ani widzieć może, jemu niech będzie cześć i moc, na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '16', '16', 'Bogatym na tym świecie opowiedaj, aby się sercem nie wynosili, ani nadzieję pokładali w bogactwiech niepewnych, ale w Bogu żywym, który daje nam wszytki rzeczy hojnie ku używaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '17', '17', 'Aby dobrze czynili i byli bogatymi w uczynkach dobrych, użytymi i skłonnymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '18', '18', 'Skarbiąc samym sobie grunt dobry napotym, aby dostąpili żywota wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '19', '19', 'O Tymoteuszu strzeż rzeczy zwierzonych, uchodząc sprośnego wołania około rzeczy nikczemnych i sprzeczania nauki fałszywie mianowanej');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1tm', '5', '20', '20', 'Z której się niektórzy chlubiąc, odstrzelili się od wiary. Łaska niech będzie z tobą. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '0', '0', 'Paweł Apostoł Jezusa Krystusa, przez wolą Bożą, według obietnice żywota, który jest w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '1', '1', 'Tymoteuszowi miłemu synowi, łaska, miłosierdzie, pokój od Boga Ojca i Krystusa Jezusa Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '2', '2', 'Dziękuję Bogu, któremu służę od przodków swoich z czystym sumnieniem, iż bez przestanku ciebie wspominam w prośbach moich, w nocy i we dnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '3', '3', 'Żądając ciebie widzieć, pamiętając na twoje łzy, abym był radości napełnion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '4', '4', 'Przywodząc sobie na pamięć onę wiarę, która jest w tobie nie obłudna, a pirwej mieszkała w babce twej Loidzie i w matce twojej Eunicy i mam zapewne, iż w tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '5', '5', 'Dla której przyczyny napominam cię, abyś wzbudził dar Boży, który w tobie jest, przez wkładanie rąk moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '6', '6', 'Abowiemci Bóg nie dał nam Ducha bojaźni, ale mocy i miłości i zdrowego rozumu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '7', '7', 'Nie wstydaj się tedy świadectwa Pana naszego, ani się wstydaj mnie, który jestem więźniem jego, ale bądź uczestnikiem ucisków Ewanjelijej z mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '8', '8', 'Który zachował nas i wezwał wezwaniem świętym, nie z uczynków naszych, ale z swego przejźrzenia i łaski, która nam jest dana w Krystusie Jezusie, przed czasy wiekuistemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '9', '9', 'Ale objawiona jest teraz przez ono znaczne przyście zbawiciela naszego Jezusa Krystusa, który też śmierć zgładził i żywot na światłość wywiódł i nieśmiertelność przez Ewanjeliją,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '10', '10', 'Której jestem postanowion woźnym i Apostołem i nauczycielem Poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '11', '11', 'Dla której przyczyny też ty rzeczy cirpię, ani się wstydam przeto, iż wiem komum uwierzył i mam zapewne, iż on może zwierzoną rzecz moję na on dzień chować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '12', '12', 'Trzymaj się wzoru zborowych powieści, któreś odemnie słyszał, z wiarą i miłością, która jest w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '13', '13', 'Zacną rzecz zwierzoną zachowaj przez Ducha świętego, który w nas mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '14', '14', 'Wiesz to iż odstąpili mię wszyscy, którzy są w Azjej, z których jest Fygellus i Hermogenes.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '15', '15', 'Niech Pan okaże miłosierdzie swoje nad domem Onesyforowym, abowiem często mię ochłodził i łańcucha się mego nie wstydził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '16', '16', 'I owszem, gdym był w Rzymie, barzo mię pilno szukał i znalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '0', '17', '17', 'Niech mu to Pan daje, aby nalazł miłosierdzie u Pana w on dzień, a ty lepiej wiesz jako mi wiele usługował w Efezie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '0', '0', 'Ty tedy synu mój bądź wzmocnion przez łaskę, która jest w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '1', '1', 'A ty rzeczy, któreś słyszał odemnie między wielą świadków, poruczaj wiernym ludziam, którzy też będą godni ku nauczaniu inszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '2', '2', 'A tak ty cierp ucisk jako dobry żołnierz Jezu Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '3', '3', 'Żaden, który walczy nie wikle się sprawami żywota, aby się temu podobał, który go obrał na rycerstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '4', '4', 'A jeśliby kto też walczył, nie bywa koronowan, jeśliby przystojnie nie walczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '5', '5', 'Musi oracz pirwej robić, niż owoców pożywać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '6', '6', 'Obaczaj ty rzeczy, które mówię, a Pan niechaj tobie dawa we wszem wyrozumienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '7', '7', 'Pamiętaj, iż Jezus Krystus wstał zmartwych, który jest z plemienia Dawidowego wedle Ewanjelijej mojej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '8', '8', 'W której cierpię utrapienie jakoby złoczyńca aż do związania, ale słowo Boże nie jestci związane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '9', '9', 'A przetoż wszytki rzeczy cierpię dla wybranych, aby i oni zbawienia dostąpili, które jest w Krystusie Jezusie z chwałą wieczną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '10', '10', 'Wiernać jest powieść, abowiem jeśliśmy z nim umarli, z nim też żyć będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '11', '11', 'Jeśli cierpiemy, z nim też królować będziemy; jeśli się go przemy i on się nas zaprzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '12', '12', 'Jeśli jesteśmy niewiernymi, wszakoż on trwa wiernym, zaprzeć samego siebie nie może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '13', '13', 'Ty rzeczy przypominaj, oświadczając przed Panem, aby się nie wdawali w spory około słów bez żadnego pożytku, owszem ku podwróceniu tych, którzy słuchają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '14', '14', 'Pilnuj tego abyś się stawił doświadczonym Bogu robotnikiem, któryby się nie wstydził i któryby prawie szafował słowo prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '15', '15', 'A sprośnego wołania o rzeczach nikczemnych powściągaj, bo dalej pójdzie ku więtszej niepobożności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '16', '16', 'A mowa ich wiada się jakoby piekielny ogień, z których jest Himeneus i Filetus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '17', '17', 'Którzy od prawdy odstrzelili się powiedając, iż już zstało się zmartwychwstanie i podwracają wiarę niektórych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '18', '18', 'A wszakże mocny stoi grunt Boży, mając tę pieczęć: Znać Pan, którzy są jego, i:  Niech odstąpi od niesprawiedliwości wszelki, który wzywa imienia Krystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '19', '19', 'Przytym w wielkim domu nie tylko są naczynia złote i srebrne, ale też drewniane i gliniane, a niektóre ku uczciwości, drugie zasię ku zelżywości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '20', '20', 'Jeśliby kto tedy oczyścił się od tych, będzie naczyniem ku uczciwości poświęconym i godnym Panu, gotowym na wszelką sprawę dobrą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '21', '21', 'A chroń się pożądliwości modzieńskich, ale naśladuj sprawiedliwości, miłości, wiary, pokoju, z tymi, którzy wzywają Pana z czystego serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '22', '22', 'Ktemu odrzuć precz głupie gadki i w których nauki nie masz wiedząc, iż oni rodzą zwady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '23', '23', 'A sługa Pański nie ma się wadzić, ale układnym być przeciwko każdemu, godnym ku nauczaniu, cierpiącym złe ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '24', '24', 'Z cichością, ucząc ty, którzy się sprzeciwiają, ażeby im kiedy Bóg dał upamiętanie ku poznaniu prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '1', '25', '25', 'Aby wybawieni będąc z sidła czartowskiego, w którym są pojmani, obaczyli się ku czynieniu wolej jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '0', '0', 'A wiedz to, iż ostatecznych czasów nastaną chwile niebespieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '1', '1', 'Abowiem będą ludzie sami siebie miłujący, łakomi, chlubni, pyszni, złorzeczący, rodzicom nieposłuszni, niewdzięczni, niepobożni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '2', '2', 'Bez miłości, nie trzymający przymierza, potwarce, niepowściągliwi, okrutni, dobrych niemiłujący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '3', '3', 'Zdrajce, skwapliwi, nadęci, w roskoszach się kochający radszej niż Boga miłujący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '4', '4', 'Mający podobieństwo pobożności, ale którzy się mocy jej zaprzeli, tych się tedy strzeż.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '5', '5', 'Abowiem z tych są ci, którzy wkradają się w domy i pojmane wiodą niewiastki, obciążone grzechy, uwodząc się za rozmaitemi pożądliwościami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '6', '6', 'Które się zawżdy uczą, a nigdy ku uznaniu prawdy przyjdź nie mogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '7', '7', 'A jako Jannes i Jambres zastawowali się Mojżeszowi, tak też ci przeciwiają się prawdzie, ludzie rozumu skażonego, odrzuceni co się wiary tycze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '8', '8', 'Ale się im dalej nie poszczęści, abowiem szaleństwo tych jawne będzie wszytkim, jako i onych było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '9', '9', 'A ty świadomeś nauki mojej i sposobu żywota mego, umysłu wiary, cichości, miłości i cierpliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '10', '10', 'Prześladowania, ucisków, które na mię przychodziły w Antjochijej, w Ikonjum, w Listrzech, którem prześladowania cirpiał, ale ze wszytkich wybawił mię Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '11', '11', 'A owszem wszyscy, którzy chcą pobożnie żyć w Krystusie Jezusie, prześladowania cierpieć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '12', '12', 'A źli ludzie i zwódzce poprawią się ku gorszemu zwodząc ine, a sami będąc zwiedzieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '13', '13', 'Ale ty trwaj w tych rzeczach, którycheś się nauczył i które tobie są zwierzone, wiedząc od kogoś się nauczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '14', '14', 'A iż od dzieciństwa świadomeś pism świętych, które cię mogą uczynić uczonym ku zbawieniu przez wiarę, która jest w Krystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '15', '15', 'Wszytko Pismo od Boga nadchnione jest i pożyteczne ku nauce, ku strofowaniu, ku karaniu i ku ćwiczeniu, które jest w sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '2', '16', '16', 'Aby człowiek Boży był doskonałym ku wszelkiej sprawie dobrej, dostatecznie nauczony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '0', '0', 'Ja tedy oświadczam się przed Bogiem i Panem Jezu Krystem, który sądzić będzie żywe i umarłe w ono zacne swe przyście i w królestwie swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '1', '1', 'Przepowiedaj słowo, przynaglaj wczas, nie wczas, karz, strofuj, napominaj z wszelaką cichością i nauką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '2', '2', 'Abowiem będzie czas, gdy zdrowej nauki nie ścierpią, ale mając świerzbiące uszy, wedle swoich pożądliwości sami sobie nauczyciele zgormadzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '3', '3', 'I odwrócą uszy swe od prawdy i obrócą się do baśni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '4', '4', 'Ale ty czuj we wszytkim, a bądź cierpliwym, wykonywaj sprawę Ewanjelisty, a okaż się doskonałym w urzędzie swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '5', '5', 'Abowiem już ja bywam ofiarowan, a już nadchodzi czas rozstania mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '6', '6', 'Walczyłem onę zacną walkę, bieg wykonałem, zachowałem wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '7', '7', 'Na ostatek odłożona mi jest korona sprawiedliwości, którą mi odda Pan sędzia sprawiedliwy w on dzień, a nie tylko mnie, ale i wszytkim, którzyby pożądali zacnego przyścia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '8', '8', 'Staraj się abyś do mnie przyszedł rychło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '9', '9', 'Abowiem Demas mię opuścił, umiłowawszy teraźnieszy wiek i szedł do Tesaloniki, Krescens do Galacijej, Tytus do Dalmacijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '10', '10', 'Łukasz sam jest zemną. Wziąwszy Marka przywiedź go z sobą, bo mi jest barzo pożyteczny ku posłudze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '11', '11', 'Tychika posłałem do Efezu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '12', '12', 'Opończa, któram zostawił w Troadzie u Karpusa, gdy przyjdziesz, z sobą przynieś i księgi, a zwłaszcza pargaminowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '13', '13', 'Aleksander rodgieser wiele mi złego wyrządził, niech mu odda Pan według uczynków jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '14', '14', 'Którego i ty się strzeż, abowiem barzo się sprzeciwił słowam naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '15', '15', 'W pierwszej obronie mojej żaden przy mnie nie stał, ale mię wszyscy opuścili, niechaj im nie będzie przyczytano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '16', '16', 'Ale Pan był przy mnie i umocnił mię, aby się przez mię pewne okazało przepowiedanie, którego słuchać mieli wszyscy pogani i jestem wyrwan z gęby lwiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '17', '17', 'I wybawi mię Pan od każdego uczynku złego, a zachowa do królestwa swego niebieskiego, któremu chwała na wieki wieków. Amen');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '18', '18', 'Pozdrów Pryszkę i Akillę i dom Onesyforów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '19', '19', 'Erastus został w Koryncie, a Trofimam zostawił w Milecie niemocnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '20', '20', 'Staraj się abyś przyszedł przed zimą. Pozdrawia cię Eubulus i Pudens i Linus i Klaudja i bracia wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2tm', '3', '21', '21', 'Pan Jezus Krystus niech będzie z duchem twoim. Łaska jego niech będzie z wami. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '0', '0', 'Paweł, sługa Boży, Apostoł Jezu Krystusów według wiary przebranych Bożych i uznania prawdy, która się ściąga ku pobożności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '1', '1', 'Ku nadziei żywota wiecznego, który Bóg on co nie kłama, obiecał przed czasy wiekuistemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '2', '2', 'I objawił czasów swoich ono Słowo swoje przez przepowiedanie, które mi jest zwierzone, według rozrządzenia Zbawiciela naszego, Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '3', '3', 'Tytusowi, własnemu synowi, w społecznej wierze, łaska niech tobie będzie, miłosierdzie i pokój od Boga Ojca i Pana Jezu Krysta zbawiciela naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '4', '4', 'Dlategom cię zostawił w Krecie, abyś do końca poprawił czego nie dostawa i postanowił starsze po miastach, jakom ja tobie był przykazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '5', '5', 'Jeśli kto jest krom naganienia, mąż jednej żony, dzieci wierne mając, nie obwinowane w zbytku, abo nieposłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '6', '6', 'Abowiem ma być biskup bez przygany, jako szafarz Boży nie hardzie się inym stawiący, nie gniewliwy nie pijanica, nie targając się ręką ni na kogo, nie szukając zysku niepoczciwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '7', '7', 'Ale rad goście przyjmujący, dobre miłujący, opatrzny, sprawiedliwy, pobożny, skromny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '8', '8', 'Mocnie trzymając wierne ono słowo, które ku nauce zależy, aby mógł też napominać przez zdrową naukę i odeprzeć tym, którzy się sprzeciwiają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '9', '9', 'Abowiemci jest wiele nieposłusznych i próżnomównych i zwodzicielów dusz, a zwłaszcza ci, którzy są z obrzezania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '10', '10', 'Którym trzeba usta zamknąć, którzy całe domy wywracają, ucząc co się nie godzi, dla zysku niepoczciwego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '11', '11', 'Powiedział niektóry z nich, własny ich prorok: Kretensowie zawżdy kłamcy, złe bestie, brzuchowie leniwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '12', '12', 'To świadectwo jest prawdziwe, a dla tej przyczyny karz je srodze, aby zdrowi byli w wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '13', '13', 'Nie bawiąc się żydowskiemi baśniami i mandaty ludzkiemi, którzy się od prawdy odwracają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '14', '14', 'Wszytkić rzeczy czyste są czystym, lecz i pokalanym i niewiernym nic nie jest czystego, ale pokalany jest ich umysł i sumnienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '0', '15', '15', 'Wyznawając, iż Boga znają, ale przą się go uczynkami, gdyż są obrzydłymi i nieposłusznymi i którzy się na żaden uczynek dobry nie godzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '0', '0', 'Ale ty mów co przystoi zdrowej nauce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '1', '1', 'Aby starszy byli trzeźwi, poważni, opatrzni, zdrowi w wierze, w miłości, w cierpliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '2', '2', 'A stare niewiasty też aby wiodły stan, któryby należał ku świętobliwości, nie potwarliwe, nie kochające się w wielu wina, któreby poczciwych rzeczy uczyły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '3', '3', 'Aby mogły wyćwiczyć młodsze, żeby męże swoje miłowały i dziatki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '4', '4', 'Iżby były poczciwe, czyste, doma mieszkające, dobre, poddane mężom swoim, aby słowo Boże nie było bluźnione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '5', '5', 'Młodzieńce też napominaj, aby byli skromni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '6', '6', 'Samego siebie stawiąc wzorem dobrych uczynków, w nauce zachowywając doskonałość i powagę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '7', '7', 'Słowo zdrowe, nienaganione, aby się przeciwnik zawstydził, nie mając nic, coby o was miał mówić złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '8', '8', 'Napominaj sługi, aby się panom swoim poddali, żeby się im we wszytkim upodobali, nie mrucząc przeciw nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '9', '9', 'Niczego nie korzyszcząc, ale na wszem dobrą wierność okazując, aby naukę Zbawiciela naszego, Boga we wszem ochędożyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '10', '10', 'Abowiem oświeciła się ona łaska Boża zbawienna wszytkim ludziam,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '11', '11', 'Która nas naucza, abyśmy zaprzawszy się niepobożności i świetskich pożądliwości, skromnie i sprawiedliwie i pobożnie żyli w teraźniejszym wieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '12', '12', 'Oczekawając onej nadzieje błogosławionej i zacnego przyścia chwały wielkiego Boga i zbawiciela naszego Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '13', '13', 'Który wydał samego siebie za nas, aby nas wykupił od wszelakiej nieprawości i oczyścił sobie samemu lud osobny, naśladujący dobrych uczynków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '1', '14', '14', 'Ty rzeczy mów i napominaj, a karz z wszelaką zwierzchnością, a żaden tobą niech nie gardzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '0', '0', 'Przypominaj im, aby zwierzchnościam i przełożeństwam poddanymi byli, żeby słuchali i na wszelki dobry uczynek gotowymi byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '1', '1', 'Niechaj o żadnym źle nie mówią, niech nie będą zwadliwi, ale mierni, okazując wszelaką układność przeciw wszem ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '2', '2', 'Abowiem i my byliśmy niekiedy szaleni, nieposłuszni, błądzący, służący pożądliwościam i roskoszam rozmaitym, w złości i w zazdrości mieszkając, obrzydłemi, jedni drugich mając w nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '3', '3', 'Ale gdy się okazała Zbawiciela naszego, Boga dobroć i miłość przeciw ludziam');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '4', '4', 'Nie z uczynków sprawiedliwych, którebyśmy uczynili, ale z miłosierdzia swego, zachował nas przez omycie odrodzenia i odnowienia Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '5', '5', 'Którego wylał na nas hojnie przez Jezusa Krystusa zbawiciela naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '6', '6', 'Abyśmy usprawiedliwieni łaską jego, zstali się dziedzicmi według nadzieje żywota wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '7', '7', 'Wiernać jest ta powieść, a chcę abyś twierdził ty rzeczy, iżby się starali jakoby dobre uczynki zachowali, którzy uwierzyli Bogu, a ty rzeczy poczciwe są i pożyteczne ludziam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '8', '8', 'Ale zawściągaj głupich gadek i rodzajów i swarów i sporów zakonnych, abowiem są niepożyteczne i próżne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '9', '9', 'Człowieka odszczepieńca po jednym i drugim upominaniu odrzuć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '10', '10', 'Wiedząc, iż takowy jest wywrócony i grzeszy, będąc sam od siebie osądzonym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '11', '11', 'Gdy poślę do ciebie Artemana abo Tychika, staraj się abyś do mnie przyszedł do Nikopolim, abowiem tamem umyślił zimować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '12', '12', 'Zenasa, uczonego w zakonie i Apolla pilnie odprowadź, aby im ni na czym nie schodziło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '13', '13', 'A niech się uczą i naszy dobrych uczynków zachować na pilne potrzeby, iżby nie byli nieużytecznymi');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'tyt', '2', '14', '14', 'Pozdrawiają cię ci, którzy są ze mną wszyscy. Pozdrów ty, którzy miłują nas w wierze. Łaska Boża niech będzie ze wszytkimi wami. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '0', '0', 'Paweł więzień Krystusa Jezusa i Tymoteusz brat, Filemonowi miłemu, a pomocnikowi naszemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '1', '1', 'I Afijej miłej i Archipowi, społecznemu z nami bojownikowi i kościołowi, który jest w domu twoim:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '2', '2', 'Łaska i pokój niech wam będzie od Boga Ojca naszego i Pana Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '3', '3', 'Dziękuję Bogu memu zawsze wspominając cię w modlitwach moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '4', '4', 'Słysząc twoję miłość i wiarę, którą masz przeciwko Panu Jezusowi i przeciw wszem świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '5', '5', 'Aby społeczność wiary twej była skuteczna, iżby znaczno było wszytko dobre cokolwiek w was jest przez Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '6', '6', 'Abowiem wesele wielkie mamy i pocieszenie w miłości twej bracie, iż wnętrzności świętych ochłodzone są przez cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '7', '7', 'A przeto, choć mam wielką wolność w Krytusie tobie roskazać, co no cię należy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '8', '8', 'A wszakże dla miłości radszej proszę, takowym będąc ja Paweł stary, a teraz też więzień Jezu Krystusów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '9', '9', 'Proszę tedy za synem moim Onesymem, któregom urodził w więzieniu moim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '10', '10', 'Niekiedy tobie niepożytecznym, ale teraz tobie i mnie barzo użytecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '11', '11', 'Któregom odesłał, a ty przyjmi go, to jest wnętrzności moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '12', '12', 'Któregom ja chciał przy sobie zatrzymać, aby za cię służył mnie w więzieniu Ewanjelijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '13', '13', 'Ale bez twego zdania nie chciałem nic czynić, aby dobrodziejstwo twoje nie było jako z przymuszenia, ale z dobrej wolej, abowiem snadź ustąpił na chwilę, abyś go przyjął wiecznie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '14', '14', 'Nie już jako sługę, ale zacniejszego niż sługa, to jest: brata miłego, a zwłaszcza mnie, a jakoż daleko więcej tobie według ciała i według Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '15', '15', 'Jeśli tedy ze mną masz społeczność, przyjmi go jako mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '16', '16', 'A jeśli cię w czym obraził, aboć co winien, to mnie przyczytaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '17', '17', 'Jam Paweł napisał ręką moją: Ja nagrodzę, choćci nie powiedam, iż też nadto i samegoś mi siebie wienien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '18', '18', 'Tak bracie niech to mam za dobrodziejstwo od ciebie, ochłodź wnętrzności moje w Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '19', '19', 'Pisałemci dufając posłuszeństwu twemu, wiedząc, iż też więcej niż powiedam uczynisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '20', '20', 'Zaraz też zgotuj mi gospodę, abowiem nadziewam się, iż przez modlitwy wasze będę wam darowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '21', '21', 'Pozdrawiają cię Epafras, który jest więźniem ze mną dla Krystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '22', '22', 'Marek, Arystarchus, Demas i Łukasz, pomocnicy moi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'phm', '0', '23', '23', 'Łaska Pana naszego Jezu Krysta niech będzie z duchem waszym. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '0', '0', 'Bóg częstokroć i rozmaitym obyczajem niekiedy mówił ojcom naszym przez proroki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '1', '1', 'A w ty dni ostateczne mówił nam przez Syna, którego postanowił dziedzicem wszytkich rzeczy, przez którego też świat stworzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '2', '2', 'Który będąc jasnością chwały i wyrażonym obrazem postaci jego i zatrzymawając wszytki rzeczy onym swoim mocnym słowem, sprawiwszy oczyścienie grzechów naszych przez samego siebie, usiadł na prawicy onego majestatu nawyższego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '3', '3', 'Tym się zacniejszym zstał nad Anioły, im osobliwszego imienia przed nim dostał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '4', '4', 'Abowiem któremu kiedy z Aniołów rzekł: Tyś jest Syn mój, jam ciebie dziś urodził? I zasię: Będę mu Ojcem, a on mi będzie Synem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '5', '5', 'A zasię gdy wwodzi pierworodnego na świat powieda: A niech się mu kłaniają wszyscy Aniołowie Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '6', '6', 'A o Aniołach mówi: Który posły swe czyni duchy, a sługi swe płomieniem ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '7', '7', 'Ale ku Synowi: Stolica twoja Boże, na wieki wieków sceptrum sprawiedliwe, jestci sceptrum królestwa twojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '8', '8', 'Umiłowałeś sprawiedliwość, a nienawidziałeś nieprawości; dlategoż pomazał cię Bóg, Bóg twój, olejkiem radości, nad towarzysze twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '9', '9', 'I: Tyś Panie na początku ugruntował ziemię, a niebiosa są sprawy rąk twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '10', '10', 'Onyć poginą, ale ty zostawasz, a wszytki jako szata zwiotszeją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '11', '11', 'A jako odzienie zwiniesz je i odmienią się, ale ty tenże jesteś i lata twoje nie ustaną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '12', '12', 'I któremuż z Aniołów kiedy rzekł: Siedź na prawicy mojej, aż położę nieprzyjaciele twoje podnóżkiem nóg twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '0', '13', '13', 'Izali wszyscy nie są duchowie posługujący, którzy na posługę bywają posłani dla tych, którzy dziedzicami będą zbawienia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '0', '0', 'A przeto mamy nawięcej pilnować tych rzeczy, któreśmy słyszeli, iżbyśmy się kiedy nierozstąpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '1', '1', 'Abowiem jeśli słowo powiedziane przez Anioły było trwałe, a każde przestępstwo i nieposłuszeństwo sprawiedliwą zapłatę odniosło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '2', '2', 'Jakoż my ujdziemy, jeślibyśmy takowego zbawienia zaniedbali, które gdy przez samego Pana napirwej opowiedziane było od tych, którzy je słyszeli, było nam potwierdzone?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '3', '3', 'A Bóg wespół świadczył i przez znaki, przez cuda i rozmaite mocy i przez udzielenie Ducha świętego, według swej wolej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '4', '4', 'Abowiemci Aniołom nie poddał onego świata przyszłego, o którym mówimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '5', '5', 'A niektóry świadczył na niektórym miejscu mówiąc: Cóż jest człowiek, iż go pamiętasz, abo syn człowieczy, iż nań wzgląd masz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '6', '6', 'Uczyniłeś go mało co nizszym nad Anioły. Ukoronowałeś go chwałą i czcią i postanowiłeś go nad wszytkimi sprawami rąk twoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '7', '7', 'Poddałeś wszytki rzeczy pod nogi jego. A zaiste przez to, iż mu wszytki rzeczy poddał, nic nie opuścił, coby jemu poddanego nie było. Jednak teraz jeszcze nie widzimy wszytkich rzeczy być mu poddanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '8', '8', 'Ale Jezusa onego obaczamy ukoronowanego chwałą i czcią, który się mało co zstał nizszym nad Anioły, dla męki śmierci, aby z łaski Bożej za wszytki śmierci skosztował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '9', '9', 'Abowiem godziło się aby ten, dla którego są wszytki rzeczy i przez którego są wszytki rzeczy, wiele synów do chwały przywodząc, książę zbawienia ich przez męki poświęcił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '10', '10', 'Bo i ten, który poświęca i ci, którzy bywają poświęceni, z jednego są wszyscy, dla której przyczyny nie wstyda się bracią ich nazywać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '11', '11', 'Mówiąc: Opowiem imię twoje braciej mojej, w pośrzodku kościoła pieśni tobie zaśpiewam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '12', '12', 'I zasię: Będę jemu dufał; a zasię: Oto ja i dzieci, które mi dał Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '13', '13', 'Iż tedy dzieci uczestniki są ciała i krwie, także i on zstał się uczestnikiem tych rzeczy, aby przez śmierć zniszczył tego, który miał państwo śmierci, to jest czarta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '14', '14', 'A iżby wyzwolił ty, którzykolwiek przez bojaźń śmierci, przez wszytek żywot poddani byli pod niewolą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '15', '15', 'Bo zaiste nie przyjmował Aniołów, ale plemię Abraamowe przyjmował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '16', '16', 'Zkąd miał we wszytkim być poddanym braciej, aby był miłosiernym i wiernym kapłanem w tych rzeczach, które się u Boga dziać miały, ku ubłaganiu grzechów ludzkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '1', '17', '17', 'Abowiem z tego co cierpiał, gdy był kuszon, może i ty, które kuszeni bywają podpomóc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '0', '0', 'A przetoż bracia święci, uczestnicy wezwania niebieskiego, obaczcie Apostoła i nawyższego kapłana wyznania naszego Krystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '1', '1', 'Wiernego temu, który go postanowił, jako i Mojżesz we wszytkim domu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '2', '2', 'Bo tym więcej chwały był godzien nad Mojżesza, im więtszą cześć ma ten, który zbudował dom, niż dom sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '3', '3', 'Abowiem każdy dom bywa zbudowan od kogo, a ten który ty wszytki rzeczy stworzył, Bógci jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '4', '4', 'A byłci Mojżesz wierny we wszytkim domu jego jako sługa, na świadectwo tych rzeczy, które potym miały być powiedane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '5', '5', 'Ale Krystus jako Syn, który rządzi dom swój, którego domem jestechmy my, jeśli ufanie i nadzieję onę, z której się chlubimy, aż do końca trwałą zdzierżymy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '6', '6', 'Dlategoż, jako mówi Duch święty: Dziś jeślibyście głos jego usłyszeli');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '7', '7', 'Nie zatwardzajcież serc waszych, jako w zajątrzeniu, w dzień pokuszenia onego na puszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '8', '8', 'Gdzie kusili mię ojcowie waszy, doświadczyli mię i widzieli sprawy moje przez czterdzieści lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '9', '9', 'Dlategom się rozgniewał na ten naród i rzekłem: Zawsze błądzą sercem, a nie poznali dróg moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '10', '10', 'A przeto przysiągłem w gniewie swoim, iż nie wnidą do odpoczynienia mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '11', '11', 'Baczcież bracia, iżby kiedy nie było w którym z was serce złe i niewierne, ku odstąpienia od Boga żywego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '12', '12', 'Ale napominajcie jedni drugie na każdy dzień, póki dzisiejszy dzień bywa nazwan, iżby który z was nie był zatwardzon zawiedzieniem grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '13', '13', 'Abowiemci zstalichmy się uczestniki Krystusa, jeśli mocnie zdzierżymy aż do końca grunt on, którym się podpieramy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '14', '14', 'Póki bywa rzeczono: Dziś jeślibyście głosu jego słuchali, nie zatwardzajcież serc waszych, jako w zajątrzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '15', '15', 'Abowiem niektórzy usłyszawszy, zjątrzyli Pana, a wszakże nie wszyscy oni, którzy byli wyszli z Egiptu przez Mojżesza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '16', '16', 'A na które się wżdy rozgniewał przez czterdzieści lat? Izali nie na ty, którzy byli zgrzeszyli, których ciała poległy na puszczy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '17', '17', 'A którym przysiągł, iż nie mieli wnidź do odpoczynienia jego, jedno tym, którzy nie byli posłusznymi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '2', '18', '18', 'A widzimy, iż nie mogli tam wnidź dla niedowiarstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '0', '0', 'Przetoż się bójmy, aby kiedy opuściwszy obietnicę weścia do odpoczynienia jego, nie zdał się który z was być omylonym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '1', '1', 'Abowiem opowiedziana jest nam Ewanjelija, jako i onym, ale nie pomogło im słowo, które słyszeli przeto, iż nie było złączone z wiarą tym, którzy słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '2', '2', 'Abowiem wchodziemy do odpoczynienia, którzy uwierzyliśmy jako rzekł: A przetom przysiągł w gniewie moim, iż nie wnidą do odpoczynienia mojego, choć już od założenia świata sprawy były dokończone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '3', '3', 'Abowiem rzekł na niektórym miejscu o siódmym dniu tym sposobem: I odpoczynął Bóg dnia siódmego od wszytkich spraw swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '4', '4', 'A tu zasię: Nie wnidą do odpoczynienia mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '5', '5', 'Iż tedy to zostawa, aby niektórzy weszli do niego, a którym pierwej opowiedziano było, nie weszli dla niedowiarstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '6', '6', 'Zasię naznacza dzień niektóry dziś, tak długo potym u Dawida mówiąc (jako jest rzeczono): Dziś, jeślibyście głos jego usłyszeli, nie zatwardzajcież serc swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '7', '7', 'Abowiem jeśliby był im Jozue dał odpoczynienie, nie mówiłby był potym żadnym obyczajem o inszym dniu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '8', '8', 'A tak zostało jakieś odpoczynienie ludowi Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '9', '9', 'Abowiem ten, który wszedł do odpoczynienia jego, odpoczywał też sam od spraw swoich, jako i Bóg od swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '10', '10', 'Starajmy się tedy abychmy weszli do tego odpoczynienia, iżby kto w on przykład nieposłuszeństwa nie wpadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '11', '11', 'Bo żywe jest Słowo Boże i skuteczne i więcej przerażające nad każdy miecz po obu stron ostry i dosięga aż do roździelenia duszę i ducha i stawów i szpików i rozeznawcą jest myśli i umysłów serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '12', '12', 'A nie masz żadnego stworzenia, któreby przed nim jawne nie było, ale wszytki rzeczy są gołe i otworzyste przed oczyma tego, z którym sprawę mamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '13', '13', 'Mając tedy Biskupa Wielkiego, który przeniknął nieba, Jezusa, Syna Bożego, trzymajmy to wyznanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '14', '14', 'Abowiem nie mamy Kapłana, któryby nie mógł czuć z nami krewkości naszych, ale kuszonego we wszytkich rzeczach takież, oprócz grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '3', '15', '15', 'Przystąpmysz tedy z ufaniem ku stolicy chwały, abyśmy dostąpili miłosierdzia i łaskę naleźli ku pomocy czasu potrzebnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '0', '0', 'Zaisteć każdy kapłan z ludzi wzięty i za ludzi bywa postanowion w tych rzeczach, które mają być sprawowane u Boga, aby ofiarował dary i zabite ofiary za grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '1', '1', 'Któryby mógł (ile potrzeba jest) zmiłować się nad tymi, którzy nie umieją i błądzą, gdyż i on był ogarnion krewkością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '2', '2', 'I dla tejże, jako i za lud, tak też za się, ma ofiarować za grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '3', '3', 'A żaden sobie tej godności nie bierze, ale który bywa wezwan od Boga, jako i Aaron.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '4', '4', 'Tak i Krystus nie przywłaszczył sobie tej chwały, aby zstał Kapłanem, ale ten, który mu rzekł: Tyś jest Syn mój, jam ciebie dziś urodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '5', '5', 'Jako indziej powiada: Tyś jest kapłanem na wieki, wedle porządku Melkisedekowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '6', '6', 'Który za dniów ciała swego, gdy modlitwy i prośby z wielkim wołaniem i płaczem ofiarował u tego, który mógł go zachować od śmierci i był wysłuchan z bojaźni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '7', '7', 'Choć był Syn, a wszakże się nauczył posłuszeństwa z tych rzeczy, które cierpiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '8', '8', 'A będąc poświęcony, zstał się powodem zbawienia wiecznego wszytkim, którzy go słuchają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '9', '9', 'Przyzwany od Boga Kapłanem Nawyższym, wedle porządku Melkisedekowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '10', '10', 'O którym wielebyśmy mieli mówić i trudno ku wykładaniu przeto, iżeście się zstali gnuśni ku słuchaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '11', '11', 'Abowiem wy, którzybyście mieli wedle czasu być nauczycielami, zasię potrzebujecie aby was uczono jako się poczynają powieści Boże i zstaliście się jako ci, którzy mleka potrzebują, a nie twardego pokarmu. Bo ten, którego się mlekiem bawi, nie umie słowa sprawiedliwości, gdyż jest dziecięciem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '4', '12', '12', 'Aleć twardy pokarm jest dorosłych, tych mianowicie, którzy dla nałogu smysły mają ćwiczone ku rozeznaniu dobrego i złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '0', '0', 'A przetoż opuściwszy słowo, które w Krystusie dawa początek nieumiejętnym, miejmy się ku doskonałości, nie z nowu zakładając gruntu pokuty od uczynków martwych i wiary w Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '1', '1', 'Krztów nauki i rąk wkładania i wstania zmartwych i sądu wiekuistego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '2', '2', 'A to uczynimy jeśli Bóg dopuści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '3', '3', 'Abowiem być nie może, aby ci, którzyby raz byli oświeceni i skosztowali daru niebieskiego i uczestniki się stali Ducha świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '4', '4', 'I skosztowali dobrego słowa Bożego i mocy przyszłego wieku,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '5', '5', 'Jeśliby upadli, aby powtóre byli odnowieni przez pokutę przeto, iż znowu krzyżują samym sobie syna Bożego i jawnie go sromocą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '6', '6', 'Abowiem ziemia, któraby deżdż na nię często przychodzący w się wpiła i zrodziła ziele użyteczne tym, przez które bywa sprawowana, bierze błogosławieństwo od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '7', '7', 'Lecz ta, która podawa ciernie i osty, odrzuconać jest i blisko przeklęctwa, której koniec idzie ku spaleniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '8', '8', 'Ale namilejszy pewnie o was sobie lepiej tuszymy ni&nbsp;ż to i co z zbawieniem jest złączono, choć tak mówimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '9', '9', 'Abowiemci nie jest Bóg niesprawiedliwy, aby zapamiętał sprawy waszej i pracowitej miłości, którąście okazali przeciw imieniu jego przeto, iżeście służyli świętym i służycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '10', '10', 'I żądamy, aby każdy z was toż staranie okazał ku zupełnej wiadomości nadzieje aż do końca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '11', '11', 'Abyście nie byli gnuśnemi, ale naśladowcami tych, którzy przez wiarę i cierpliwość otrzymawają obiecane dziedzictwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '12', '12', 'Abowiem Bóg obiecawszy Abrahamowi, gdy nie mógł przez kogo więtszego przysięgać, przysiągł przez siebie samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '13', '13', 'Mówiąc: Iście błogosławiąc, błogosławić tobie będę i rozmnażając, rozmnożę cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '14', '14', 'A gdy tak cierpliwie czekał, dostąpił obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '15', '15', 'Gdyż ludzie przez tego przysięgają, który jest więtszy i u których koniec każdego wsporu jestci przysięga ku podtwierdzeniu przydana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '16', '16', 'W której rzeczy chcąc Bóg dziedzicom obietnicę obficiej okazać, nieodmienność rady swej obiecał przysięgą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '17', '17', 'Abyśmy przez dwie rzeczy nieodmienne, w których być nie może żeby Bóg kłamał, mocne pocieszenie mieli, ku temu uciekając, abyśmy założoną nadzieję otrzymali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '18', '18', 'Którą mamy jako kotwicę duszę beśpieczną i mocną i która wchodzi aż do tych rzeczy, które są za zasłoną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '5', '19', '19', 'Gdzie Jezus bieżawszy wprzód przed nami, wszedł za nas według porządku Melchisedekowego, zostawszy kapłanem na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '0', '0', 'Abowiem ten Melchisedek był król Salem, kapłan Boga nawyższego, który zaszedł Abrahamowi gdy się wracał od porażki królów i błogosławił jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '1', '1', 'Któremu też dziesięciny z każdej rzeczy udzielił Abraham ( który naprzód wedle wykładania rzeczon jest król sprawiedliwości, przytym też król Salem, to jest król pokoju).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '2', '2', 'Bez Ojca, bez matki, bez rodu, ani początku dniów, ani końca żywota mając, ale przypodobany synowi Bożemu, trawa kapłanem na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '3', '3', 'Obaczcie tedy, jako wielki ten był, któremu też dziesięcinę z łupów dał Abraham patriarcha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '4', '4', 'A cić, którzy są z synów Lewi, sprawując kapłaństwo roskazanie mają, aby brali dziesięcinę od ludu wedle zakonu, to jest, od braciej swojej choć wyszli z biódr Abrahamowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '5', '5', 'Ale ten, którego naród nie bywa poczytan z nich, dziesięcinę brał od Abrahama i temu, który miał obietnicę błogosławił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '6', '6', 'A bez wszelakiego sporu mniejsze od więtszego bywa błogosławione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '7', '7', 'A tuć ludzie, którzy umierają, dziesięciny biorą. Aleć tam ten, o którym świadczono, iż żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '8', '8', 'Ktemu, abych tak rzekł, w Abrahamie brano dziesięciny od samego Lewi, który zwykł brać dziesięciny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '9', '9', 'Abowiem on jeszcze był w biodrach ojcowskich, gdy Melchisedek zaszedł Abrahamowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '10', '10', 'A jeśli doskonałość była przez kapłaństwo Lewi, (gdyż za tego dan jest zakon ludowi) cóż więcej było potrzeba, aby nastał inszy kapłan wedle porządku Melchisedekowego, który by nie był rzeczon wedle porządku Aaronowego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '11', '11', 'Abowiem gdy się odmienia kapłaństwo, musi się stać i odmiana zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '12', '12', 'Bo ten, o którym ty rzeczy bywają powiedane, należy na ine pokolenie, z którego żaden nie pilnuje ołtarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '13', '13', 'Gdyż jawna rzecz jest, iż z pokolenia Juda poszedł Pan nasz, którego pokolenia względem nic o kapłaństwie nie mówi Mojżesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '14', '14', 'A jeszcze jawniej to się okazuje, iż na podobieństwo Melchisedekowe nastawa kapłan inszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '15', '15', 'Który się nie stał kapłanem z zakonu, którego roskazanie jest cielesne, ale z mocy żywota, który zepsowan być nie może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '16', '16', 'Abowiem tak świadczy: Iż ty jesteś kapłanem na wieki wedle porządku Melchisedekowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '17', '17', 'Bo roskazanie przeszłe bywa zniszczone dlatego, iż żadnej mocy nie ma, a jest niepożyteczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '18', '18', 'Gdyż zakon nic nie poświęcił, ale nadeszła lepsza nadzieja, przez którą przybliżamy się ku Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '19', '19', 'A to, iż nie bez przysięgi, (boć oni bez przysięgi się kapłanami stali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '20', '20', 'Ale ten z przysięgą, przez tego, który mu rzekł: Przysiągł Pan a nie będzie żałował: Tyś jest kapłanem na wieki wedle porządku Melchisedekowego).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '21', '21', 'Tym lepszego przymierza zastępcą stał się Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '22', '22', 'A onych wiele się kapłany stało dlatego, iż im śmierć nie dopuściła przetrwać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '23', '23', 'Ale ten, iż na wieki trwa, wieczne ma kapłaństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '24', '24', 'Z tąd i zachować dostatecznie może ty, którzy przezeń przystępują do Boga, zawsze żywiąc, aby się przyczyniał za nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '25', '25', 'Abowiem godził się nam kapłan takowy pobożny, niewinny, niepokalany, oddalony od grzeszników i który się stał wyzszy nad niebiosa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '26', '26', 'Któryby nie potrzebował na każdy dzień, jako oni kapłani, pierwej za grzechy własne, zabitych ofiar sprawować, potem za ludzkie. Bo to uczynił raz, gdy samego siebie ofiarował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '6', '27', '27', 'Abowiemci zakon postanawia kapłany ludzie mdłe,  ale słowo przysięgi po zakonie Syna, który jest na wieki poświęcony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '0', '0', 'Dwa tych rzeczy, które powiedamy, toć jest zamknienie, iż takiego mamy Nawyższego Kapłana, który usiadł na prawicy stolicy majestatu niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '1', '1', 'Sługa miejsca naświętszego a prawdziwego namiotu onego, który rozbił Pan, a nie człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '2', '2', 'Abowiem każdy kapłan bywa postanowion ku ofiarowaniu darów i ofiar zabitych, zkąd potrzeba było aby ten też miał coby ofiarował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '3', '3', 'Bo jeśliby był na ziemi, nie byłby kapłanem, gdzie trwają oni kapłani, którzy wedle zakonu dary ofiarują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '4', '4', 'Jako ci, którzy służą kształtowi i cieniowi rzeczy niebieskich, jako od Boga powiedziano jest Mojżeszowi, gdy miał dokończyć przybytku, mówiąc: Obacz tedy abyś uczynił wszytki rzeczy według kształtu, który jest tobie okazan na górze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '5', '5', 'Ale teraz nasz kapłan dostał tym zacniejszego posługowania, im jest pośrzednikiem lepszego przymierza, które w lepszych obietnicach jest ustanowione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '6', '6', 'Abowiem jeśliby ono przymierze było takowe, aby w nim nie było co ganić, nie trzeba było szukać miejsca wtóremu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '7', '7', 'Abowiem winując je powieda: Oto dni przychodzą mówi Pan: gdy uczynię z domem izraelskim i z domem Juda przymierze nowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '8', '8', 'Nie według przymierza którem uczynił z ojcy ich, w dzień, w którym uchwycił rękę ich, abym je wywiódł z Egiptu. Abowiem oni nie wytrwali w onym moim przymierzu, a jam je wzgardził, mówi Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '9', '9', 'Bo toć jest przymierze, które uczynię z domem izraelskim po onych dniach mówi Pan: Podam prawa moje w myśli ich i w serca ich napiszę je, a będę im Bogiem, a oni mnie będą ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '10', '10', 'I nie będą uczyć żaden bliźniego swego, ani żadny brata swego mówiąc: Poznaj Pana, abowiem wszyscy mię poznają od namniejszego aż do nawiętszego między nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '11', '11', 'Abowiemci ja będę miłościw nieprawościam i grzechom ich, a złości ich już dalej pamiętać nie będę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '7', '12', '12', 'Gdy o Nowym powiada, pierwsze wiotchem uczynił, a to co wiotszeje i zstarzeje się, bliskieć jest zniszczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '0', '0', 'Miało tedy i pierwsze przymierze ustawy około służby Bożej i święte miejsce świeckie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '1', '1', 'Abowiem był pirwszy przybytek sprawion, w którym były lampy i stół i pokładne chleby, który zową miejsce święte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '2', '2', 'A za wtórą zasłoną był przybytek, który zową miejscem naświętszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '3', '3', 'Mając złotą kadzidlnicę i skrzynię przymierza, zewsząd złotem powleczoną, w której była czasza złota, mająca mannę i laska Aaronowa, która kwitnęła i tablice przymierza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '4', '4', 'A na tej byli Cherubini chwalebni zacimiający wieko miłosierdzia, o których rzeczach nie mamy teraz o każdej z osobna powiadać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '5', '5', 'Gdy tedy ty rzeczy tak postanowiono, kapłani którzy służby Boże sprawują do pierwszego przybytku zawsze wchodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '6', '6', 'Ale do wtórego raz na każdy rok sam kapłan nawyższy, nie bez krwie, którą ofiaruje za się i za obłędliwości ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '7', '7', 'Gdzie tym oznajmuje Duch Święty, iż jeszcze nie jest objawiona droga do miejsca świętego, póki jeszcze trwa pierwszy przybytek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '8', '8', 'Który był kształtem na on czas, gdy dary i zabite ofiary bywają sprawowane, które nie mogą w sumnieniu poświęcić chwalcę Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '9', '9', 'W pokarmach tylko i w piciu i w rozmaitym omyciu i w obchodziech cielesnych, aż do czasu naprawienia założone,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '10', '10', 'Ale Krystus przystąpiwszy kapłan dóbr przyszłych, przez więtszy i doskonalszy przybytek, nie ręką sprawiony, to jest, nie tego budowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '11', '11', 'Ani przez krew kozłów i cieląt, ale przez własną krew swoję, wszedł raz do miejsca świętego, dostawszy wiecznego odkupienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '12', '12', 'Abowiem jeśli krew cielców i kozłów i popiół jałowice, pokrapiając nieczyste poświęca ku czystości cielesnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '13', '13', 'Jako daleko więcej krew Krystusowa, który przez ducha wiecznego, samego siebie ofiarował nienaganionym Bogu, oczyści sumnienie wasze od martwych uczynków ku służbie Bogu żywemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '14', '14', 'A przetoż dlatego jest Nowego przymierza pośrzednikiem, aby przez śmierć, na odkupienie onych występków, które były pod pierwszym Testamentem, ci, którzy wezwani są, wzięli obietnicę wiecznego dziedzictwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '15', '15', 'Abowiem gdzie Testament jest, tamżeć musi być śmierć tego, który testament czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '16', '16', 'Bo testament w umarłych mocny jest, gdyż jeszcze nie jest ważny za żywota tego co ji uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '17', '17', 'Ztąd i on pierwszy bez krwie nie był utwierdzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '18', '18', 'Abowiem gdy Mojżesz wszytko przykazanie według zakonu oznajmił wszemu ludowi, wziąwszy krew z cieląt i kozłów, z wodą i wełną czerwoną i hizopem pospołu i samy księgi i lud wszytek pokropił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '19', '19', 'Mówiąc: Tać jest krew Testamentu, który do was Bóg roskazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '20', '20', 'Ktemu też i przybytek i wszytko naczynie służby świętej krwią takież pokropił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '21', '21', 'A wszytki rzeczy niemal według zakonu bywają oczyściane, a bez rozlania krwie nie stawa się odpuszczenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '22', '22', 'A tak potrzeba było, aby kształty onych rzeczy, które są na niebie, tymi rzeczami były oczyścione, a samy rzeczy niebieskie lepszymi ofiarami niźli ty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '23', '23', 'Abowiem Krystus nie wszedł do miejsca świętego, ręką uczynionego, któreby było kształtem prawdziwego. Ale do samego nieba, aby się teraz okazował przed oblicznością Boga za nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '24', '24', 'A nie, iżby często ofiarował samego siebie, jako kapłan wchodzi do miejsca świętego każdy rok ze krwią cudzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '25', '25', '(Boć by był musiał często cierpieć od założenia świata). Ale terazci się raz na dokończeniu wieków okazał, ku zgładzeniu grzechu przez ofiarowanie samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '26', '26', 'A jako to trwa, iż ludzie raz umrzeć muszą, a potem sąd będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '8', '27', '27', 'Tak i Krystus raz jest ofiarowan, aby zgładził grzechy wiela ich, drugi raz zasię krom grzechu okaże się tym, którzy go oczekawają na zbawienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '0', '0', 'Abowiem zakon, mając cień dóbr przyszłych, a nie sam obraz rzeczy, nigdy nie może tymi ofiarami które na każdy rok ustawicznie ofiarują poświęcić tych, którzy knim przystępują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '1', '1', 'Bo gdzieby inaczej, azaźby nie przestały być ofiarowane? Dlatego iż ci którzyby ofiarowali, żadnegoby sumnienia grzechów już nie mieli, będąc raz oczyścieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '2', '2', 'Ale w tych bywa przypominanie grzechów na każdy rok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '3', '3', 'Abowiem nie może krew cielców i kozłów odjąć grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '4', '4', 'Przetoż wchodząc na świat powieda: Nie chciałeś zabitej ofiary i daru ofiarowanego, aleś mi ciało sprawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '5', '5', 'W palonej ofierze i w ofierze za grzech nie kochałeś się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '6', '6', 'Tedym rzekł: Oto idę (na początku ksiąg napisano jest o mnie), abym czynił, o Boże, wolą twoję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '7', '7', 'Gdy wyższej powiedział: Nie chciałeś, aniś się kochał w zabitej ofierze i w darze ofiarowanym i w palonej ofierze i za grzech, które wedle zakonu bywają ofiarowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '8', '8', 'Tedy rzekł: Otóż idę, abym czynił o Boże wolą twoję. Odejmuje pierwsze, aby wtóre postanowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '9', '9', 'Przez którą wolą jesteśmy poświęceni. Przez ofiarę ciała Jezusa Krystusa raz uczynioną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '10', '10', 'Wszelki tedy kapłan stoi na każdy dzień posługując i też ofiary zabite częstokroć ofiarując, które nigdy nie mogą zgładzić grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '11', '11', 'Ale ten sprawiwszy jednę ofiarę za grzechy, wiecznie siedzi na prawicy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '12', '12', 'Oczekawając ostatka, ażby nieprzyjaciele jego stali się podnóżkiem nóg jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '13', '13', 'Gdy jedną ofiarą poświęcił na wieki ty, którzy bywają poświęceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '14', '14', 'A sam Duch Święty świadczy nam: Bo gdy przepowiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '15', '15', 'Toć jest przymierze, które uczynię z nimi po onych dnioch, mówi Pan: Podam prawa moje w serca ich, a napiszę je w myślach ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '16', '16', 'A dalej pamiętać nie będę grzechów i nieprawości ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '17', '17', 'Przytym gdzie jest odpuszczenie tych, jużci ofiary nie masz za grzech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '18', '18', 'Mając tedy, bracia, wolność wchodzić do miejsca świętego przez krew Jezusowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '19', '19', 'Ta droga, którą nam poświęcił świeżą i żywą przez zasłonę, to jest, przez ciało swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '20', '20', 'Mając też kapłana wielkiego, przełożonego w domu Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '21', '21', 'Przystąpmyż z prawdziwym sercem i z upewnieniem wiary, mając serca oczyścione od sumnienia złego i ciało omyte czystą wodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '22', '22', 'Trzymajmy wyznanie nadzieję nie chwiejąc się (boć wierny jest ten, który obiecał.)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '23', '23', 'I przypatrzajmy się jedni drugim ku pobudzeniu miłości i dobrych uczynków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '24', '24', 'Nie opuszczając społecznego zgromadzenia naszego, jako mają obyczaj niektórzy i owszem upominajmy jedni drugich, a to tym więcej, iż widzicie żeć się on dzień przybliża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '25', '25', 'Abowiem jeślibyśmy dobrowolnie zgrzeszyli po wzięciu uznania prawdy, jużci dalej nie zostawa ofiara za grzech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '26', '26', 'Ale straszliwe niejakie oczekawanie sądu i zapalenie ognia, który pożrze przeciwniki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '27', '27', 'Ktoby odrzucił zakon Mojżeszów, bez miłosierdzia na świadectwo dwu abo trzech umiera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '28', '28', 'Jakoż mniemacie, iż daleko sroższe karanie zasłuży ten, któryby Syna Bożego podeptał i krew przymierza, przez którą był poświęcony, pokalaną być rozumiał i Ducha łaski zelżył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '29', '29', 'Abowiem znamy tego, który powiedział: Mnieć należy pomsta, jać oddam, mówi Pan. I zasię: Pan sądzić będzie lud swój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '30', '30', 'Straszliwać rzecz jest wpaść w ręce Boga żywego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '31', '31', 'Rozpomnicież na dni pierwsze, w których będąc oświeceni podjęliście wielkie bojowanie ucisków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '32', '32', 'Częścią gdy lekkościami i uciski jesteście wystawieni na podziw, częścią też gdyście się stali towarzyszmi tych, z któremi się tak obchodzono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '33', '33', 'Abowiemeście z więzienia mego zemną żałość mieli i pobranie majętności waszych przyjęliście z weselem wiedząc, iż lepszą a trwałą majętność macie w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '34', '34', 'Nie odrzucajcież tedy ufania waszego, które ma wielką zapłatę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '35', '35', 'Abowiem cierpliwości potrzebujecie, abyście uczyniwszy wolą Bożą, otrzymali obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '36', '36', 'Boć jeszcze barzo maluczki czas jest, a ten, który ma przyść, przyjdzieć, a nie omieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '37', '37', 'A sprawiedliwy z wiary żyć będzie, ale jeśliby się kto skradał, nie kocha się w nim dusza moja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '9', '38', '38', 'Ale my nie jesteśmy takiemi, abyśmy się skradali na zginienie, ale którzy wierzymy na zbawienie duszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '0', '0', 'A wiarać jest grunt rzeczy, których się nadziewamy i okazanie niewidomych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '1', '1', 'Abowiemci przez nię świadectwo otrzymali przodkowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '2', '2', 'Wiarą rozumiemy, iż świat sprawion jest słowem Bożym, tak iż rzeczy, które widzimy stały się z rzeczy niewidomych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '3', '3', 'Wiarą Abel więtszą ofiarę sprawił Bogu niż Kain, przez którą świadectwo otrzymał, iż był sprawiedliwy, a Bóg świadectwo dał o darzech jego i umarszy przez nie jeszcze mówi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '4', '4', 'Wiarą Enoch był przeniesion aby śmierci nie oglądał, ani był nalezion przeto, iż go przeniósł Bóg; abowiem przed tem niż był przeniesion, otrzymał świadectwo, iż się Bogu upodobał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '5', '5', 'A bez wiary nie może być, aby się mu kto podobał, abowiem kto przystępuje do Boga, musi ten wierzyć iż jest Bóg, a iż zapłatę oddaje tym, którzy go szukają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '6', '6', 'Wiarą Noe od Boga napomniony o tych rzeczach, które jeszcze nie były widome, uczciwością przywiedziony, zgotował archę ku zachowaniu domu swego, przez którą archę osądził świat i stał się dziedzicem sprawiedliwości tej, która jest z wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '7', '7', 'Wiarą wezwany Abraham, usłuchał Boga, aby poszedł na miejsce, które dziedzicznie wziąć miał i wyszedł nie wiedząc gdzie przydź miał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '8', '8', 'Wiarą pomieszkał w ziemi obiecanej jako w cudzej, mieszkając w namieciech z Izaakiem i z Jakubem, którzy byli społeczni dziedzicy tejże obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '9', '9', 'Abowiem oczekiwał miasta mającego grunty, którego sprawcą i stworzycielem jest Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '10', '10', 'Wiarą i sama Sara wzięła moc ku przyjęciu nasienia i nad lata swe porodziła przeto, iż tego miała za wiernego, który był obiecał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '11', '11', 'Dlatego też z jednego i to jeszcze już obumarłego, zrodziło się potomków taka wielkość, jako gwiazd niebieskich i jakoby piasek niezliczony, który jest na brzegu morskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '12', '12', 'Wedle wiary umarli ci wszyscy, gdy niedostąpili obiecanych rzeczy, ale z daleka je widzieli i uwierzyli, a z dziękowaniem przyjmowali, wyznawszy się być gośćmi i przychodniami na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '13', '13', 'Bo którzy takowe rzeczy powiedają, jawnie okazują, iż szukają ojczyzny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '14', '14', 'A jeśliby byli pamiętali, z której byli wyszli, mieli czas aby się zaś wrócili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '15', '15', 'Ale teraz lepszej żądają, to jest niebieskiej, a przeto nie wstydą się Bóg sam być zwan Bogiem ich, gdyż im miasto zgotował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '16', '16', 'Wiarą Abraham, gdy był doświadczon, ofiarował Izaaka i owszem jednorodzonego ofiarował tenże, który był wziął obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '17', '17', 'Do którego rzeczono: W Izaaku tobie będzie wezwano plemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '18', '18', 'To u siebie obaczywszy, iż Bóg może i z umarłych wzbudzić, skąd go też nie jakiem podobieństwem zasię otrzymał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '19', '19', 'Wiarą około przyszłych rzeczy błogosławił Izaak Jakuba i Ezau.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '20', '20', 'Wiarą Jakub umierając każdego z synów Józefowych błogosławił i dał chwałę Bogu, podparszy się końcem laski swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '21', '21', 'Wiarą Józef umierając o wyściu synów izraelskich wspomionał i około kości swoich roskazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '22', '22', 'Wiarą gdy się narodził Mojżesz jest skryt przez trzy miesiące od rodziców swoich przeto, iż go widzieli nadobnym dzieciątkiem, ani się bali roskazania królewskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '23', '23', 'Wiarą Mojżesz już dorosły nie chciał być zwan synem córki Faraonowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '24', '24', 'Radszej to obrawszy, aby był utrapionym wespół z ludem Bożym, niźli używać doczesnych pożytków grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '25', '25', 'Rozumiejąc być więtszym bogactwem sromocenie Krystusowe, niźli skarby Egiptcjanów, bo się oglądał na odpłatę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '26', '26', 'Wiarą opuścił Egipt, nic się nie bojąc gniewu królewskiego, abowiem jakoby widział tego, który jest niewidomy, tak był i statecznego serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '27', '27', 'Wiarą obchodził Wielkanoc i wylanie krwie, aby ten, który pierworodne tracił, ich nie dotknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '28', '28', 'Wiarą przeszli przez Morze Czerwone jako po suchej ziemi, którego chcąc kusić Egiptcjanie, zatopieni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '29', '29', 'Wiarą mury Jerycha upadły będąc obroczone przez siedm dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '30', '30', 'Wiarą Rahab, wszetecznica ona, nie zginęła wespół z tymi, którzy nie byli posłusznymi, przyjąwszy wdzięcznie do gospody szpiegi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '31', '31', 'A cóż dalej mówię? Boć mi czasu nie zstanie, gdybych powiedał o Gedeonie, o Baraku, o Samsonie, o Jefte, o Dawidzie, o Samuelu i o prorocech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '32', '32', 'Którzy wiarą zwalczyli królestwa, czynili sprawiedliwość, dostąpili obietnicę i zawarli gęby lwów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '33', '33', 'Zagasili moc ognia, uszli ostrych mieczów, mocnymi się stali z niemocy, sił dostąpili na wojnie i wojska obcych tył im podawali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '34', '34', 'Niewiasty wzięły swoje umarłe z zmartwychwstania, a drudzy są rozciągnieni, nie dbając aby byli wybawieni, a ku temu aby lepszego zmartwych wstania dostąpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '35', '35', 'A drudzy zasię doznali pośmiewiska i biczowania, nadto i związek i więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '36', '36', 'Ukamionowani są, przecierani są, doświadczeni są, mieczem zabici polegli, błąkali się w owczych i w kozich skórach, opuszczeni, uciśnieni, utrapieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '37', '37', 'Których nie był godzien świat, tułając się po puszczech i górach i jamach i jaskiniach ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '38', '38', 'A ci wszyscy świadectwo otrzymawszy przez wiarę, obietnicę nie dostąpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '10', '39', '39', 'Iż Bóg o nas coś lepszego przejźrzał, aby bez nas nie byli poświęceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '0', '0', 'A tak i my będąc ogarnieni tak wielkim obłokiem świadków, odrzuciwszy wszelakie brzemię i grzech, który nas snadnie może zewsząd uwikłać, statecznie bieżmy w zawodzie, który nam jest założony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '1', '1', 'Oglądajcież się na Jezusa Hetmana i tego, który dokonał wiary, który miasto wesela jemu ustanowionego, cierpiał krzyż wzgardziwszy sromotę i usiadł na prawicy stolicę Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '2', '2', 'A tak obaczcie, który to jest, co takowe przeciwieństwo cierpiał od grzeszników przeciwko sobie, abyście mdlejąc na sercach waszych nie ustali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '3', '3', 'Jeszczeście się do krwie nie zastawili, walcząc przeciwko grzechowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '4', '4', 'Zapamiętaliście napomnienia, które wam jako synom mówi: Synu mój, nie wzgardzaj karania Pańskiego, ani ustawaj na sercu, gdy od niego bywasz karan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '5', '5', 'Abowiem kogo miłuje Pan, tego karze, a bije każdego syna, którego przyjmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '6', '6', 'Jeśli cierpicie karanie, ofiarnieć się wam Bóg jako synom, abowiem któryż jest syn któregoby ociec nie karał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '7', '7', 'A jeśli jesteście bez karania, którego wszyscy są uczestnikami, już złego łoża dziećmi jesteście a nie synmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '8', '8', 'Przytym, mieliśmy ojce, którzy karali ciała nasze i balichmy się, a zaź nie daleko więcej poddani będziemy Ojcowi duchów, a żyć będziemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '9', '9', 'Abowiem oni na mało dni jako się im widziało nas karali, a tenci nas karze ku pożytkowi naszemu, na to abyśmy byli uczestniki świątobliwości jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '10', '10', 'A wszelkie karanie teraźniejszego czasu nie zda się być wesela i owszem smutku, ale potym spokojny owoc sprawiedliwości oddaje tym, którzyby przez nie byli ćwiczeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '11', '11', 'A przetoż ręce osłabione, kolana zemdlone podnieście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '12', '12', 'A czyńcie koleje proste nogami naszemi, iżby to co jest chromego nie było odwiedzione z drogi, ale raczej było uzdrowione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '13', '13', 'Naśladujcie pokoju ze wszytkimi i świątobliwości, bez której żaden nie ogląda Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '14', '14', 'Patrząc pilnie żeby kto nie odstał od łaski Bożej, iżby który korzeń gorzki podrastając nie zatrfażał i przezeń wiele ich nie było pokalanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '15', '15', 'Niechaj nikt nie będzie nieczystym, abo sprośnym jako Ezaw, który za potrawę jednę przedał prawo swoje pierworództwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '16', '16', 'Abowiem wiecie, iż i potym gdy chciał dziedzicznym prawem otrzymać błogosławieństwo, był odrzucon: bo nie znalazł miejsca pokutowania, choć onego błogosławieństwa ze łzami pożądał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '17', '17', 'Gdyżeście nie przystąpili do góry, którejby się możono dotknąć i do ognia rozpalonego i do wichru i do ciemności i gwałtownej niepogody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '18', '18', 'I brzmienia trąby i głosu słów, który ci co słyszeli, prosili aby im dalej nie mówiono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '19', '19', '(Abowiem nie mogli znieść tego co im roskazowano, jeśliby się bydlę góry dotknęło, będzie ukamionowano, abo pociskiem przebito.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '20', '20', 'A Mojżesz przeto, iż tak straszne było widzenie, które się okazowało rzekł: Zlękłem się i zadrżałem.)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '21', '21', 'Aleście przyszli do góry Syjon i do miasta Boga żywego, do Jeruzalem niebieskiego i do zgromadzenia niezliczonych Aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '22', '22', 'I do zboru pierworodnych, którzy są spisani w niebie i do Boga sędziego wszytkich i do duchów sprawiedliwych i doskonałych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '23', '23', 'I do Jezusa pośrzednika Nowego Testamentu i do krwie pokropienia, która zacniejsze rzeczy mówi, niźli mówiła krew Ablowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '24', '24', 'Patrzajcie abyście tym nie wzgardzili, który mówi, abowiem jeśli ci nie uszli, którzy odrzucali tego, który mówił na ziemi, daleko więcej my jeśli się odwracamy od tego, który z nieba jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '25', '25', 'Którego głos zatrząsnął ziemią, ale teraz opowiedział mówiąc: Jeszcze ja raz zatrząsnę nie tylko ziemią ale i niebem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '26', '26', 'A ono słowo (jeszcze raz) znaczy odjęcie rzeczy chwiejących się, jako tych, które są uczynione, aby zostały ty, które są trwałe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '27', '27', 'A przeto przyjmując królestwo, które nie może być poruszone, trzymamy łaskę, przez którą tak służmy Bogu, abyśmy się jemu podobali z ućciwością i z bogobojnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '11', '28', '28', 'Abowiemci Bóg nasz jest ogniem trawiącym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '0', '0', 'Niechaj miłość braterska trwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '1', '1', 'Nie zapominajcie przymowania gości, abowiem przez to niektórzy nie wiedząc przyjęli Anioły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '2', '2', 'Pamiętajcie na więźnie, jakobyście byli wespół z nimi więźniami i na ty, którzy bywają utrapieni, jakobyście na ciele utrapieni byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '3', '3', 'Poczciweć jest małżeństwo między wszytkimi i łoże nie pokalane, a nieczyste i cudzołożniki osądzi Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '4', '4', 'Obyczaje niech będą oddalone od łakomstwa: Przestawajcie na teraźniejszych rzeczach bo on rzekł: Nie opuszczę cię ani cię zostawię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '5', '5', 'Tak iż śmiele mówimy: Pan mi jest pomocnikiem, ani się bać będę coby mi uczynił człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '6', '6', 'Pamiętajcie na wodze wasze, którzy wam mówili słowo Boże, a wiary ich naśladujecie, obaczając co za koniec był obcowania ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '7', '7', 'Jezus Krystus wczora i dziś, tenże też jest na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '8', '8', 'Naukam rozmaitym i cudzym nie dajcie się unosić, abowiem dobra rzecz jest, aby serce było utwierdzone łaską, nie pokarmy, które nie pomogły tym co się imi bawili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '9', '9', 'Mamy ołtarz, z którego nie godzi się tym jeść, którzy przybytkowi służą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '10', '10', 'Abowiem bydląt, których krew bywa wnoszona do miejsca świętego przez kapłana, tych ciała bywają palone przed obozem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '11', '11', 'Dlatego i Jezus, aby poświecił lud przez własną krew, przed braną cierpiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '12', '12', 'Wynidźmyż tedy do niego przed obóz, nosząc posromocenie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '13', '13', 'Abowiem nie mamy tu miasta trwającego, ale przyszłego szukamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '14', '14', 'Przez niego tedy ustawicznie ofiarujemy Bogu ofiarę chwały, to jest owoc warg naszych, wyznawających imieniowi jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '15', '15', 'Ani zapominajcie dobrotliwości i udzielania, abowiem się Bóg w takowych ofiarach kocha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '16', '16', 'Bądźcie posłuszni przełożonym waszym i bądźcie im poddani, abowiem oni czują o duszach waszych, jako ci, którzy liczbę oddać mają, aby to z weselem czynili, a nie wzdychając, boć to wam jest nie pożyteczno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '17', '17', 'Módlcie się za nami, abowiem dufamy iż mamy dobre sumnienie, we wszytkim żądając poczciwie się zachować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '18', '18', 'Jeszcze więcej was proszę, abyście to czynili, iżbym wam rychlej był przywrócon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '19', '19', 'A Bóg pokoju, który onego wielkiego przez krew przymierza wiecznego pasterza owiec Pana naszego Jezusa od umarłych wywiódł,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '20', '20', 'Niechaj was doskonałemi uczyni we wszelakim uczynku dobrym, ku czynieniu wolej jego, sprawując w was to, co by było wdzięcznego przed nim, przez Jezusa Krystusa, któremu niech będzie chwała na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '21', '21', 'Zatym proszę was bracia, znoście słowo napomnienia, bomci do was krótko pisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '22', '22', 'Wiecie, iż brat nasz Tymoteusz wypuszczon jest, z którym jeślibych rychlej przyszedł, nawiedzę was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '23', '23', 'Pozdrówcie wszytki wodze wasze i wszytki święte. Pozdrawiają was Włoszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'hebr', '12', '24', '24', 'Łaska niech będzie z wami wszytkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '0', '0', 'Jakub sługa Boży i Pana Jezusa Krystusa, dwiemanaście pokoleniam rozproszonym zdrowia życzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '1', '1', 'Bracia moi miejcie za nawiętszą radość, ilekroć wpadniecie w rozmaite pokusy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '2', '2', 'Wiedząc to, iż doświadczenie wiary waszej sprawuje cierpliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '3', '3', 'Ale potrzeba jest, aby cierpliwość doskonały uczynek miała, żebyście byli doskonali i zupełni tak, iżby wam ninaczym nie schodziło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '4', '4', 'Jeśli który z was potrzebuje mądrości, niech o nię Boga prosi, który ją da wszytkim hojnie ani wymawia i będzie jemu dana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '5', '5', 'Ale niech prosi z wiarą, nic nie wątpiąc, abowiem który wątpi, jest podobny wału morskiemu, który bywa poruszon i miotan od wiatru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '6', '6', 'Abowiem niech ten człowiek nie mnima, aby co miał wziąć od Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '7', '7', 'Człowiek dwojakiego serca, niestateczny jest we wszech drogach swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '8', '8', 'A brat niskiego stanu niech się chlubi w wywyższeniu swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '9', '9', 'Zasię, bogaty niech się chlubi w uniżeniu swoim, abowiem jako kwiat ziółka przeminie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '10', '10', 'Abowiem jako gdy słońce wzeszło z gorącością, tedy ziele uwiędło i kwiat jego upadł i znamienita piękność jego zginęła, tak też bogaty w drogach swoich uwiędnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '11', '11', 'Błogosławiony człowiek, który cierpi pokusę, bo gdy będzie doświadczony, weźmie koronę żywota, którą obiecał Pan tym, którzy go miłują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '12', '12', 'Żaden, gdy bywa kuszon, niech nie mówi, iż jest kuszon od Boga, abowiem Bóg złemi rzeczami kuszon być nie może, a sam żadnego nie kusi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '13', '13', 'Ale każdy bywa kuszon, gdy od własnej pożądliwości bywa odwodzon i przynęcon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '14', '14', 'Zatym pożądliwość począwszy rodzi grzech, a grzech będąc dokonany, przynosi śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '15', '15', 'Bracia namilszy, nie mylcież się!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '16', '16', 'Wszelkie darowanie dobre i wszelaki dar doskonały, jest z góry. Zstępując od Ojca światłości, u którego nie masz odmiany, ani zaćmienia wracającego się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '17', '17', 'Tenże, iż chciał, porodził nas słowem prawdy, abyśmy byli jako niektóre pierworództwa stworzenia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '18', '18', 'A tak bracia moi mili, niech wszelki człowiek będzie prędki ku słuchaniu, a nierychły ku mówieniu i leniwy ku gniewowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '19', '19', 'Abowiem gniew człowieczy nie czyni dosyć sprawiedliwości Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '20', '20', 'A tak odrzuciwszy wszelaką sprośność i omieciny złości, przyjmcie z cichością słowo, które bywa wszczepione w was, a może zachować dusze wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '21', '21', 'A skutecznie słowo pełńcie, ani bądźcie tylko słuchaczmi, oszukawając sami siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '22', '22', 'Abowiem jeśliby kto słuchał słowa, a nie pełniłby go, ten podobny jest mężowi, który przyrodzone oblicze swoje w zwierciedle ogląda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '23', '23', 'Bo samego siebie oglądawszy, gdy odejdzie, wnet zapomni jakim był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '24', '24', 'Ale ktoby pilnie wejźrzał w on doskonały zakon wolności i trwałby w nim, ten nie będąc słuchaczem zapamiętliwym, ale sprawcą uczynku, błogosławiony będzie w sprawie swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '25', '25', 'Jeśli kto się zda być nabożnym między wami, któryby nie powściągał języka swego, aleby serce swe zwodził, tegoć nabożeństwo próżne jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '0', '26', '26', 'Nabożeństwo sczyre i bez zmazy u Boga i Ojca to jest: Nawiedzać sieroty i wdowy w ucisku ich i zachować samego siebie niepokalanym od świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '0', '0', 'Bracia moi, nie miejcie wiary Pana naszego Jezu Krysta chwalebnego, z brakowaniem osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '1', '1', 'Abowiem jeśliby wszedł do zgromadzenia waszego mąż z złotym pierścieniem, w szacie ślicznej, a wszedłby też ubogi w plugawym odzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '2', '2', 'A wybyście wejźrzawszy na tego, który ma na sobie szatę śliczną rzekli mu: Ty siedź tu poczciwie, a rzeklibyście ubogiemu: Ty stój tam, abo siedź tu pod podnóżkiem moim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '3', '3', 'A zażeście nie uczynili różności u siebie i staliście się sędźmi przewrotnie rozważającemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '4', '4', 'Słuchajcież bracia moi mili. Azaź Bóg nie obrał ubogich świata tego, aby się stali bogatymi wiarą i dziedzicmi królestwa, które obiecał tym, którzy go miłują?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '5', '5', 'Aleście wy ubogiego zelżyli. Izali bogaci okrutnie was nie uciskają i ciągną was do sądów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '6', '6', 'Izali nie bluźnią onego zacnego imienia, którym się ozywacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '7', '7', 'A wszakże, jeśli zakon królewski pełnicie wedle pisma: Będziesz miłował bliźniego twego jako samego siebie; dobrze czynicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '8', '8', 'Ale jeśli osobą brakujecie, grzechu się dopuszczacie i bywacie karani od zakonu jako przestępce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '9', '9', 'Abowiem ktoby zachował wszytek zakon, a w jednymby upadł, jest winien wszytkiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '10', '10', 'Bo ten, który rzekł: Nie będziesz cudzołożył, powiedział też: Nie będziesz zabijał. A jeśli nie będziesz cudzołożył, ale będziesz zabijał, stałeś się przestępcą zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '11', '11', 'Tak mówcie i tak czyńcie, jako ci, którzy mają być sądzeni przez zakon wolności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '12', '12', 'Abowiem sąd bez miłosierdzia czynion będzie z tym, któryby miłosierdzia nie czynił, a miłosierdzie chlubi się przeciwko potępieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '13', '13', 'Bracia moi. Cóż za pożytek, jeśli by kto rzekł iż ma wiarę, a uczynków by nie miał? Izali go może wiara ona zbawić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '14', '14', 'A jeśliby brat abo siostra byli nadzy i potrzebowali powszedniej żywności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '15', '15', 'I rzekłby im który z was: Idźcie z pokojem, zgrzejcie się i nasyćcie się, a przedsię nie dalibyście im rzeczy potrzebnych ciału, cóż za pożytek będzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '16', '16', 'Takżeć też wiara, jeśliby nie miała uczynków, martwa jest w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '17', '17', 'I owszem rzecze kto: Ty wiarę masz, a ja mam uczynki. Okaż mi wiarę z uczynków twoich, a ja tobie okażę wiarę moję z uczynków moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '18', '18', 'Ty wierzysz, iż jest jeden Bóg, dobrze czynisz. I dziablić wierzą i drżą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '19', '19', 'A chcesz wiedzieć, o człowiecze próżny, iż wiara bez uczynków martwa jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '20', '20', 'Abraham ociec nasz, izali nie z uczynków usprawiedliwion jest? Gdy ofiarował Izaaka syna swego na ołtarzu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '21', '21', 'Widzisz iż wiara sprawowała uczynki jego, a iż z uczynków wiara doskonała była?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '22', '22', 'I wypełniło się Pismo, które mówi: Uwierzył Abraham Bogu i poczytano mu jest ku sprawiedliwości i nazwan jest przyjacielem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '23', '23', 'Widzicież tedy, iż z uczynków bywa usprawiedliwion człowiek, a nie z wiary tylko?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '24', '24', 'Także też i Rahab wszetecznica, izali z uczynków nie jest usprawiedliwiona, gdy przyjęła posły i inszą drogą wypuściła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '1', '25', '25', 'Abowiem jako ciało bez ducha jest martwe, tak i wiara ona, która jest bez uczynków, martwa jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '0', '0', 'Niechaj z was nie będzie wiele mistrzów bracia moi, wiedząc, iż sroższy sąd odniesiemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '1', '1', 'Abowiem w wielu rzeczach upadamy wszyscy; jeśli kto w mowie nie upada, ten jest mąż doskonały, który też może powściągać wszytkiego ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '2', '2', 'Oto koniom wędzidła w gęby wprawujemy, aby nam posłuszne były i wszytko ich ciało obracamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '3', '3', 'Oto też okręty, choć tak wielkie są i od wiatrów gwałtownych pędzone bywają, maluczki rudel obraca gdziekolwiek chce chuć sprawce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '4', '4', 'Tak też język jestci członek maluczki, a wielce się chlubi. Oto ogień mały jako wielka materia zapala?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '5', '5', 'Język też jest płomień i świat nieprawości, ktemu język jest tak postanowiony między członkami naszemi. Oszpecając wszytko ciało i zapala okrąg tego świata i bywa zapalan od ognia piekielnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '6', '6', 'Abowiem wszelkie przyrodzenie i zwierząt i ptaków i gadzin i morskich rzeczy bywa skrócone i jest okrócone od ludzkiego przyrodzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '7', '7', 'Ale języka żaden człowiek nie może okrócić, gdyż jest złość niepohamowana i pełny jest jadu śmiertelnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '8', '8', 'Przezeń błogosławimy Boga i Ojca i przezeń złorzeczymy ludziam, którzy na podobieństwo Boże stworzeni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '9', '9', 'Z tychże ust wychodzi błogosławieństwo i przeklęctwo. Bracia moi, nie mająć być ty rzeczy tak czynione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '10', '10', 'Izali stok z jednego źrzódła wypuscza słodką i gorzką wodę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '11', '11', 'Izali może bracia moi, figowe drzewo oliwy rodzić, abo winna macica figi? Tak żaden stok słonej i słodkiej wody podawać nie może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '12', '12', 'Któryż jest mądry i umiejętny między wami, niechaj pokaże z dobrego obcowania uczynki swe, z cichością mądrości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '13', '13', 'A jeśli zazdrość gorzką macie i spór w sercach waszych, nie chlubcież się, ani kłamajcie przeciwko prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '14', '14', 'Abowiem ta mądrość z góry nie zstępuje, owszem jest ziemska, ludzka i diabelska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '15', '15', 'Bo gdzie jest zazdrość i spór, tam rosterk i wszelaka zła sprawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '16', '16', 'Ale mądrość, która jest z góry, na przódci jest czysta, potym spokojna, mierna, powolna, pełna miłosierdzia i owoców dobrych, nie sądząca, ani obłudna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '2', '17', '17', 'A owoc sprawiedliwości z pokojem bywa siany spokojnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '0', '0', 'Skądże przychodzą walki i bitwy między wami? Izali nie ztąd? To jest z roskoszy waszych, które walczą w członkach waszych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '1', '1', 'Pożądacie, a nie macie, zajźrzycie i z chucią żądacie, a nie możecie otrzymać, bijecie się i walczycie, a wżdy nie otrzymawacie dlatego, iż nie prosicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '2', '2', 'Prosicie, a nie bierzecie przeto, iż źle prosicie, abyście to trawili na roskoszach waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '3', '3', 'Cudzołożnicy i cudzołożnice nie wiecie, iż przyjaźń świata jest nieprzyjaźnią z Bogiem? Ktobykolwiek tedy chciał być przyjacielem światu, nieprzyjacielem Bożym zostawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '4', '4', 'Azaź mnimacie, iż próżno Pismo mówi: Duch który mieszka w nas, izali pożąda ku zazdrości?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '5', '5', 'Ale więtszą łaskę ofiaruje w tym co mówi: Bóg pysznym się sprzeciwia, a pokornym łaskę dawa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '6', '6', 'Podajcie się tedy Bogu, przeciwcie się diabłu, a on uciecze od was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '7', '7', 'Przybliżajcie się ku Bogu, a on się do was przybliży. Oczyśćcie ręce grzesznicy i oczyściajcie serca wasze, wy umysłu dwojakiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '8', '8', 'Bywajcie utrapieni i żałujcie i płaczcie. Śmiech wasz niech się obraca w smutek, a wesele w żałość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '9', '9', 'Upokórzcie się przed oblicznością Pańską, a on was podniesie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '10', '10', 'Bracia. Nie mówcie jedni przeciw drugim. Który mówi przeciw bratu i który osądza brata swego, mówi przeciwko zakonowi i zakon osądza. A jeśli osądzasz zakon, już nie jesteś tym, który zachowywa zakon, ale sędzią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '11', '11', 'Jedenci jest, który dał zakon i może zachować i zagubić. Ty któżeś jest, który drugiego sądzisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '12', '12', 'Nuż teraz wy, którzy mówicie: Dziś abo jutro pójdziem do tego miasta i zmieszkamy tam rok jeden, a będziem handlować i zyszczemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '13', '13', '(Którzy nie wiecie co ma być nazajutrz. Abowiem jakiś jest żywot wasz? Bo para jest, która się na mały czas okazuje, a potym niszczeje).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '14', '14', 'Miasto tego cobyście mieli mówić: Jeśli Pan będzie raczył i będziemli żywi, uczynimy to abo owo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '15', '15', 'A teraz chlubicie się w pychach waszych, wszelakie chlubienie takowe jestci złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '3', '16', '16', 'A tak, który umie dobrze czynić, a nie czyni, ten jest winien grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '0', '0', 'Nuż teraz bogacze płaczcie, narzekając nad nędzami waszemi, które przyjdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '1', '1', 'Bogactwa wasze zgniły, w szaty wasze mole się wrzuciły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '2', '2', 'Złoto wasze i srebro wasze pordzewiało, a rdza ich będzie wam na świadectwo i zje ciała wasze jako ogień. Wyście sobie skarb zbierali na ostatnie dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '3', '3', 'Oto zapłata robotników, którzy żęli pola wasze (od was zatrzymana) woła i wołania żeńców weszły do uszu Pana zastępów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '4', '4', 'Wyście żyli w roskoszach na ziemi i bujaliście, nakarmiliście serca wasze jako w dzień ofiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '5', '5', 'Wyście osądzili i zamordowali sprawiedliwego, który się wam nie sprzeciwia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '6', '6', 'Bądźcie tedy cierpliwi bracia, aż do przyścia Pańskiego. Oto oracz oczekiwa drogiego owocu ziemie, cierpliwie go czekając, ażby otrzymał deżdż zaranny i odwieczorny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '7', '7', 'Wy tedy bądźcie cierpliwi i potwierdzajcie serca wasze, abowiem przyście Pańskie przybliża się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '8', '8', 'Bracia, nie wzdychajcie jeden przeciw drugiemu, abyście nie byli osądzeni: a otoć sędzia stoi przede drzwiami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '9', '9', 'Bierzcie bracia moi za wzór ucisku i cierpliwości proroki, którzy mówili w imię Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '10', '10', 'Oto mamy za błogosławione ty, którzy cierpią. Słyszeliście o cierpliwości Jobowej i widzieliście koniec Pański, abowiem barzo miłosierny jest Pan i litościwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '11', '11', 'A naprzód bracia moi, nie przysięgajcie ani na niebo, ani na ziemię, ani żadną inszą przysięgą, ale niech będzie słowo wasze; Jest, jest i nie nie, abyście nie wpadli w potępienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '12', '12', 'Bywa kto między wami utrapiony, niechaj się modli; bywa kto dobrej myśli, niechaj śpiewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '13', '13', 'Jest kto niemocny między wami? Niechaj wzowie starszych kościoła, a niech się za nim modlą, pomazując go olejkiem w imię Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '14', '14', 'A modlitwa wiary zachowa niemocnego i podniesie go Pan, a jeśli by się grzechów dopuścił, będą mu odpuszczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '15', '15', 'Wyznawajcie jeden drugiemu przestępstwa, a módlcie się jeden za drugiego, abyście byli uzdrowieni, abowiem wiele waży skuteczna modlitwa sprawiedliwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '16', '16', 'Eljasz był człowiek tymże niedostatkom poddany jako i my i nabożnie się modlił, aby deżdż nie szedł i nie szedł deżdż na ziemię półczwarta lata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '17', '17', 'I zasię się modlił, a niebo podało deżdż i ziemia wydała owoc swój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '18', '18', 'Bracia! Jeśliby się kto obłądził od prawdy i nawróciłby go kto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jas', '4', '19', '19', 'Niechże wie, iż ktoby nawrócił grzesznika od błędnej drogi jego, zachowa duszę od śmierci i zakryje wielkość grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '0', '0', 'Piotr, Apostoł Jezu Krystusów, przychodniom rosproszonym po Poncie, po Galacjej, Kappadocjej, Asijej i Bitynijej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '1', '1', 'Wybranym z przejźrzenia Boga Ojca, ku świątobliwości ducha przez posłuszeństwo i pokropienie krwie Jezusa Krystusa. Łaska niech wam będzie i pokój rozmnożony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '2', '2', 'Błogosławiony Bóg i Ojciec Pana naszego Jezu Krysta, który z wielkiego miłosierdzia swojego odrodził nas ku naszej nadziei żywej przez zmartwychwstanie Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '3', '3', 'Ku dziedzictwu, które zginąć nie może, ani być pomazane, ani zepsowane, a jest nam zachowane na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '4', '4', 'Którzy jesteśmy zachowani mocą Bożą przez wiarę ku zbawieniu, które gotowe jest aby było objawione czasu ostatniego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '5', '5', 'W której rzeczy radujecie się, teraz maluczko zasmuceni rozmaitemi pokusami jeśli potrzeba jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '6', '6', 'Aby doświadczenie wiary waszej daleko droższe niż doświadczenie złota, które ginie, a wżdy przez ogień bywa doświadczone, było znalezione być wam ku czci i chwale i ku poczciwości, gdy się okaże Jezus Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '7', '7', 'Którego nie widziawszy, miłujecie; którego nie widząc, przedsię mu wierzycie radując się weselem niewymownym i chwalebnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '8', '8', 'Odnosząc zapłatę wiary waszej, to jest, zbawienie dusz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '9', '9', 'O którym zbawieniu dowiedowali się i badali prorocy, którzy o przyszłej na nas łasce prorokowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '10', '10', 'Pilnie szukając, na który abo na jaki czas Duch Krystusów w nich wprzód świadcząc, oznajmił utrapienia, które miały przyjdź na Krystusa i chwałę, która po nich przyjdź miała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '11', '11', 'Którym i to było oznajmiono, iż nie sobie, ale nam tymi rzeczami posługowali, które teraz są opowiedziane przez ty, którzy wam przepowiedali Ewanjeliją, przez zesłanego z nieba Ducha świętego, na które rzeczy Aniołowie patrząć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '12', '12', 'A przetoż przepasawszy biodra umysłu waszego trzeźwością, doskonałą nadzieję miejcie w łasce, która wam jest przyniesiona, aż się okaże Jezus Krystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '13', '13', 'Jako synowie posłuszni, którzy się nie przypodobniacie przeszłym pożądliwościam, gdyście byli w nieumiejętności waszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '14', '14', 'Ale jako ten, który was wezwał święty jest, abyście też wy byli świętymi we wszelakim obcowaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '15', '15', 'Dlatego napisano jest: Bądźcie świętymi, abowiem ja święty jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '16', '16', 'A jeśli wy Ojcem nazywacie tego, który przez braku osób sądzi wedle uczynku każdego, patrzcie abyście w bojaźni chodząc dokonali czasu mieszkania waszego docześnego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '17', '17', 'Wiedząc, iż nie skażonymi rzeczami: srebrem abo złotem jesteście wybawieni od próżnego obcowania waszego, któreście podane wzięli od ojców,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '18', '18', 'Ale drogą krwią jako Baranka niepomazanego i niepokalanego, to jest Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '19', '19', 'Który był przejźrzan przed założeniem świata i był objawion czasów ostatecznych dla was,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '20', '20', 'Którzy przezeń wierzycie Bogu, który go wzbudził z martwych i dał mu chwałę, aby wiara i nadzieja wasza w Bogu była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '21', '21', 'Oczyściwszy dusze wasze posłusznymi będąc prawdzie przez Ducha z miłością braterską nie obłudną, z szczyrego serca jedni drugie miłujcie uprzejmie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '22', '22', 'Będąc odrodzeni nie z plemienia śmiertelnego, ale z nieśmiertelnego przez słowo Boga żywego i trwającego na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '23', '23', 'Abowiem wszelkie ciało jest jako trawa i wszelka chwała człowiecza jako kwiat z ziela, uschła trawa, a kwiat jej upadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '0', '24', '24', 'Ale słowo Boże trwa na wieki, a toć jest słowo, które jest wam przepowiedane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '0', '0', 'A przeto złożywszy wszelką złość i wszelką zdradę i obłudność i zazdrość i wszelakie obmowiska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '1', '1', 'Jako dopieroko narodzone dzieci szczyrego mleka słowa żądacie, abyście przez nie urośli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '2', '2', 'Jeśliście skosztowali, iż Pan jest dobrotliwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '3', '3', 'Do którego przystępując, który jest kamieniem żywym, odrzuconym od ludzi, ale u Boga wybranym i kosztownym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '4', '4', 'Abyście i wy jako żywe kamienie byli zbudowani dom duchowny, kapłaństwo święte ku sprawowaniu duchownych ofiar wdzięcznych Bogu przez Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '5', '5', 'Przeto też zamyka się w piśmie: Oto kładę na Syonie kamień, który ma być położon w naprzedniejszym węgle, wybrany i kosztowny, któremu kto uwierzy, nie będzie zawstydzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '6', '6', 'Wam tedy jest poczciwością, którzy wierzycie, a nieposłusznym kamień, który odrzucili budujący, ten zstał się przedniejszym u węgłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '7', '7', 'I kamieniem obrażenia i kamieniem zgorszenia, to jest tym nieposłusznym, którzy się obrażają słowem, ku czemu też są postanowieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '8', '8', 'Ale wy jesteście rodzajem wybranym, królewskiem kapłaństwem, narodem świętym, ludem, który sobie Bóg za własny poczyta, abyście przepowiedali cnoty tego, który z ciemności was wezwał ku dziwnej światłości swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '9', '9', 'Którzyście niekiedy byli nie ludem, a teraz jesteście ludem Bożym, którzyście niekiedy miłosierdzia nie dostąpili, a terażeście miłosierdzie otrzymali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '10', '10', 'Namilejszy! Proszę jako przychodniowie i cudzoziemcy powściągajcie się od pożądliwości cielesnych, które walczą przeciwko duszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '11', '11', ', A niech będzie obcowanie wasze poczciwe między pogany, aby w tym co mówią przeciwko wam jako złoczyńcami z dobrych uczynków, któreby obaczyli, chwalili Boga w dzień nawiedzenia swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '12', '12', 'Bądźcie tedy poddani każdemu urzędowi ludzkiemu dla Pana, chociaż królowi, jako przedniejszemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '13', '13', 'Choć przełożonym jako tym, którzy od niego posłani bywają tak na pomstę złoczyńców, jako ku chwale tych, którzy dobrze czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '14', '14', 'Abowiem tak jest wola Boża, abyście dobrze czyniąc, zahamowali nieumiejętność szalonych ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '15', '15', 'Jako wolni, a nie mając wolności; jako zasłony złości, ale jako słudzy Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '16', '16', 'Czcicie wszytki, braterstwo miłujcie, Boga się bójcie, króla w uczciwości miejcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '17', '17', 'Słudzy niech będą poddani panom z wszelką bojaźnią, nie tylko dobrym i ludzkim, ale też srogim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '18', '18', 'Abowiem to jest rzecz wdzięczna, jeśli kto dla sumnienia Bożego cierpi frasunki będąc niesłusznie utrapiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '19', '19', 'Bo co za chwała jest jeślibyście cierpieli, gdy grzesząc bywacie policzkowani? Ale jeśli dobrze czyniąc przedsię cięszkości cierpicie, toć jest wdzięczna rzecz u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '20', '20', ', Gdyż na to jesteście wezwani boć i Krystus cierpiał za nas, zostawując nam naukę abychmy naśladowali stóp jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '21', '21', 'Który grzechu nie uczynił, ani jest znaleziona zdrada w uściech jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '22', '22', 'Któremu gdy złorzeczono, nie oddawał złorzeczeństwa, gdy cierpiał nie groził, ale poruczył pomstę temu, który sprawiedliwie sądzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '23', '23', 'Który grzechy nasze nosił w ciele swoim na drzewie, abyśmy będąc umarłymi grzechom w sprawiedliwości żyli, którego siniałymi razy uzdrowieni jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '1', '24', '24', 'Abowiemeście byli jako owce błąkające się, ale teraz jesteście nawróceni do pasterza i dozorce dusz waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '0', '0', 'Także żony niech będą poddane mężom swoim, aby i ci, którzy nie są posłuszni słowu, przez obcowanie żon bez słowa byli pozyskani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '1', '1', 'Gdy ujźrzą uczciwe a czyste obcowanie wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '2', '2', 'Których ochędóstwo niechaj nie będzie owo zwierzchowne w splecieniu włosów, z okładką złota, abo w obleczeniu szat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '3', '3', 'Ale ono skryte, to jest człowiek serdeczny w nieskażonym ochędóstwie cichego i spokojnego ducha, który jest przed Bogiem kosztowny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '4', '4', 'Abowiem tak niekiedy i święte ony niewiasty, mając nadzieję w Bogu ubierały się, będąc poddane mężom swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '5', '5', 'Jako Sara była posłuszna Abrahamowi, nazywając go Panem, którejeście się stały córkami, gdy dobrze czynicie, a nie będąc ustraszone żadnym ulęknieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '6', '6', 'Także mężowie niech z nimi mieszkają jako należy rozumnym, wyrządzając cześć niewieście jako naczyniu mdlejszemu i jako społeczni dziedzicy łaski żywota, aby modlitwy wasze nie były przekażane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '7', '7', 'Na koniec bądźcie wszyscy zgodliwemi i czujcie doległości jedni drugich, mając w sobie braterską miłość, miłosierni i łaskawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '8', '8', 'Nie oddawając złem za złe, abo złorzeczeństwa za złorzeczeństwo i owszem błogosławiąc wierząc, iż jesteście na to wezwani, abyście błogosławieństwo dziedziczne otrzymali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '9', '9', 'Abowiem kto chce żywot miłować i oglądać dni dobre, niech powściąga języka swego od złego i warg swoich aby zdrady nie mówiły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '10', '10', 'Niech odstąpi od złego a czyni dobrze, niech szuka pokoju i naśladuje go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '11', '11', 'Abowiem oczy Pańskie patrzają na sprawiedliwe i uszy jego słuchają prośby ich, a oblicze Pańskie przeciwko tym, którzy źle czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '12', '12', 'Któż jest coby miał trapić was jeślibyście dobroci naśladowcami byli? Ale też jeślibyście co cierpieli dla sprawiedliwości, przedsię błogosławieni jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '13', '13', 'A wszakże nie lękajcie się strachu ich ani trwóżcie sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '14', '14', 'Ale Pana Boga poświęćcie w sercach waszych, a bądźcie zawsze gotowi odpowiedź dać każdemu pytającemu się o sprawie nadzieje onej, która w nas jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '15', '15', 'Z pokorą i uczciwością, mając sumnienie dobre, aby tym co złe mówią przeciwko wam jako złoczyńcam, zawstydzili się, którzy się targają na wasze dobre obcowanie w Krystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '16', '16', 'Abowiem lepiej abyście dobrze czyniąc, jeśliby tak chciała wola Boża, cierpieli, niźli źle czyniąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '17', '17', 'Bo i Krystus cierpiał raz za grzechy, sprawiedliwy za niesprawiedliwe, aby nas przywiódł do Boga, umartwionyć ciałem, aleć ożywiony duchem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '18', '18', 'Przez którego też szedszy przepowiedał duchom, którzy są w ciemnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '19', '19', 'Którzy niekiedy nieposłusznymi byli, gdy raz oczekawała Boża cierpliwość za dni Nego, kiedy archę gotowano, w której mało, to jest ośm dusz zachowane są na wodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '20', '20', 'Której teraz krzest równając się zachowywa nas też, nie zdejmując plugawości ciała, ale sprawując to, iż dobre sumnienie wzywa Boga przez zmartwychwstanie Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '2', '21', '21', 'Który jest na prawicy Bożej, szedszy do nieba, któremu poddani są sami Aniołowie i zwierzchności i mocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '0', '0', 'Gdyż tedy Krystus cierpiał za nas w ciele, wy też tąż myślą będźcie uzbrojeni, to jest, iż ten, który cierpiał w ciele, przestał od grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '1', '1', 'Ku temu aby już nie pożądliwościami ludzkimi, ale wolej Bożej ostatek czasu w ciele żył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '2', '2', 'Abowiem dosyć nam iżeśmy przeszłego czasu żywota czynili wolą poganów, chodząc w nieczystościach, pożądliwościach, w opilstwach, biesiadach, pijaństwach i brzydliwych bałwochwalstwach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '3', '3', 'A to się im zda coś obcego, iż wy nie biegacie z nimi ku temuś niemiernemu zbytkowi i bluźniąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '4', '4', 'Aleć liczbę dadzą temu, który gotowy jest sądzić żywe i zmarłe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '5', '5', 'Bo też dlatego umarłym przepowiedana jest Ewanjelija, aby byli osądzeni wedle ludzi w ciele i żyli duchem wedle Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '6', '6', 'Przytym koniec wszytkich rzeczy blisko jest: Bądźcież tedy trzeźwi i czujni ku modlitwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '7', '7', 'Przed wszytkimi rzeczami miejcie uprzejmą miłość jedni ku drugim, abowiem miłość zakrywa wielkość grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '8', '8', 'Radzi przyjmijcie goście społecznie bez szemrania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '9', '9', 'Jako każdy wziął dar, tak go jeden drugiemu użyczając jako dobrzy szafarze rozmaitej łaski Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '10', '10', 'Jeśli kto mówi, niechaj mówi jako powieści Boże, jeśli kto posługuje, niech to czyni jako z mocy, której Bóg dodawa, aby we wszem był Bóg chwalon przez Jezusa Krystusa, któremu jest chwała i moc na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '11', '11', 'Namilejszy nie trwóżcie sobą dla onego doświadczenia, które się w was przez ogień dzieje ku doznaniu waszemu jakoby się wam co dziwnego przydawało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '12', '12', 'Owszem w tym, iż jesteście uczestniki ucisków Krystusowych, weselcie się, abyście też, gdy się okaże chwała jego uweselili się z radością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '13', '13', 'Jeśli bywacie sromoceni w imię Krystusowe, błogosławieni jesteście, abowiem Duch chwały i Boży odpoczywa nad wami, którzy wedle nich bywa bluźnion, ale wedle was jest uwielbion.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '14', '14', 'Zatym niech żaden z was nie cierpi jako mężobójca, abo złodziej, abo złoczyńca, abo jako ten, który się wtrąca w cudze rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '15', '15', 'Ale jeśli jako krześcianin niech się nie sroma, owszem niech chwali Boga w tej mierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '16', '16', 'Abowiem czas jest, aby się sąd począł od domu Bożego, a jeśli naprzód poczyna się od nas, któryż koniec będzie tych, co nie są posłusznymi Ewanjelijej Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '17', '17', 'A jeśli sprawiedliwy ledwie bywa zachowan, niepobożny i grzeszny gdzież się okaże?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '3', '18', '18', 'A tak ci, którzy cierpią wedle wolej Bożej, niech dobrze czyniąc jemu poruczają duszę swę, jako stworzycielowi wiernemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '0', '0', 'Proszę starszych, którzy są między wami, którym też i ja jestem starszym i świadkiem ucisków Krystusowych i uczestnikiem chwały, która ma być objawiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '1', '1', 'Paście trzodą, która przy was jest, mając ją na pieczy, nie z przymuszenia, ale dobrowolnie, nie sromotnie zysku szukając, ale uprzejmem sercem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '2', '2', 'Ani jako panując nad dziedzictwem Bożym, ale jako będąc przykłady trzodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '3', '3', 'A gdy się okaże ono Książę Pasterzów, odniesiecie koronę chwały, która nie więdnieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '4', '4', 'Także młodszy bądźcie posłuszni starszym, aby wszyscy jedni drugim byli poddani, pokorą bądźcie we wnątrz ochędożeni, abowiem Bóg pysznym się sprzeciwia, a pokornym dawa łaskę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '5', '5', 'Bądźcie tedy upokorzeni pod mocną ręką Bożą, aby was czasu swego wywyższył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '6', '6', 'Wszytko staranie wasze nań kładąc, abowiem się on o was stara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '7', '7', 'Bądźcie trzeźwi, czujcie, abowiem przeciwnik wasz, diabeł, jako lew rycząc, szuka kogoby pożarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '8', '8', 'Któremu się sprzeciwajcie mocni przez wiarę, wiedząc iż tyż uciski bywają wypełnione od braciej waszej, którzy są na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '9', '9', 'Ale Bóg wszelkiej łaski, który wezwał nas ku wiecznej swej chwale w Krystusie Jezusie maluczko utrapione, ten niech nas uczyni doskonałymi, niech utwierdza, umacnia i ugruntuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '10', '10', 'Jemu chwała i moc na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '11', '11', 'Pisałem wam na krótce przez Sylwana wiernego brata, jako rozumiem napominając was i świadcząc, iż ta jest prawdziwa łaska Boża, w której stoicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '12', '12', 'Kościół który jest w Babilonie społecznie wybrany z wami pozdrawia was i Marek, syn mój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1pe', '4', '13', '13', 'Pozdrawiajcie jeden drugiego pocałowaniem miłości. Pokój wam wszytkim, którzy jesteście w Krystusie Jezusie. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '0', '0', 'Symeon Piotr, sługa i Apostoł Jezusów Krystusów, tym, którzy otrzymali wiarę tak kosztowne jako i my, przez sprawiedliwość Boga naszego i zbawiciela Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '1', '1', 'Łaska i pokój niechaj się wam rozmnoży przez uznanie Boga i Jezusa Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '2', '2', 'Gdyż Boska moc jego wszytki rzeczy nam dała, które należą ku żywotowi i ku pobożności przez uznanie tego, który wezwał nas ku chwale i cnocie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '3', '3', 'Z tego, iż nawięcsze i nakosztownejsze obietnice nam są darowane, abyście się przez ty stali uczestniki Boskiego przyrodzenia, uszedłszy skażenia, które jest na świecie przez pożądliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '4', '4', 'A ku temu samemu przyłożywszy wszelakiej pilności, przyłączcie cnotę z wiarą waszą, a z cnotą umiejętność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '5', '5', 'A z umiejętnością skromność, z skromnością cierpliwość, za się z cierpliwością pobożność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '6', '6', 'A z pobożnością uprzejmość ku braciej i z uprzejmością ku braciej miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '7', '7', 'Abowiem, gdy ty rzeczy w was są i obfitują, nie czynią was próżnymi ani niepożytecznymi ku poznaniu Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '8', '8', 'Bo który tych rzeczy nie ma, ten jest ślepy, z daleka nic nie widząc, zapamiętawszy, iż jest oczyścion od starych grzechów swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '9', '9', 'A przetoż bracia radszej się starajcie abyście mocne czynili wezwanie i wybranie wasze, bo jeślibyście ty rzeczy czynili, nigdy się nie powalicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '10', '10', 'Abowiem tym sposobem hojnie wam będzie dane weście do wiecznego królestwa Pana naszego i Zbawiciela Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '11', '11', 'A tak nie zaniedbam was zawsze upominać około tych rzeczy, choć umiejętni i potwierdzeni jesteście w teraźniejszy prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '12', '12', 'Abowiem mniemam, iż jest sprawiedliwą rzecz, pókim jest w tym przybytku, pobudzać was przez napominanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '13', '13', 'Wiedząc, iż rychło złożę ten mój przybytek, jako i Pan nasz Jezus Krystus mnie oznajmił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '14', '14', 'Ale też postaram się abyście po wyściu moim tych rzeczy wzmienkę czynić mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '15', '15', 'Abowiem nie oznajmiliśmy wam mocy i przyścia Pana naszego Jezu Krysta, bawiąc się baśniami zmyślonymi, ale oglądawszy oczyma naszymi majestat jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '16', '16', 'Bo był wziął od Boga Ojca cześć i chwałę, gdy mu był przyniesion głos takowy od wielmożnej chwały: Ten ci jest Syn mój miły, na którym ja przestawam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '17', '17', 'A myśmy ten głos słyszeli, który był z nieba przyniesion, będąc z nim na górze świętej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '18', '18', 'Ktemu mamy namocniejsze słowo prorockie, którego pilnując dobrze czynicie, jako kagańca świecącego na ciemnym miejscu, aż się dzień oświeci, a jutrzenka wzejdzie w sercach waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '19', '19', 'To naprzód wiedząc, iż żadne proroctwo pisma nie jest własnego przynucenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '0', '20', '20', 'Abowiem nie wolą ludzką przyniesione jest niekiedy proroctwo, ale święci ludzie Boży pobudzeni od Ducha świętego mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '0', '0', 'Byli też i fałszywi prorocy między ludem, jako też między wami będą fałszywi nauczycielowie, którzy potajemnie wwiodą kacerstwa zaraźliwe i Pana się też prząc, który je kupił, sami na się przywodząc prętkie zginienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '1', '1', 'A wiele naśladować będą zginienia ich, przez które droga prawdy będzie bluźniona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '2', '2', 'I przez łakomstwo zmyślonymi słowy wami kupczyć będą, którym potępienie już zdawna nie omieszkawa i których zatracenie nie drzemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '3', '3', 'Abowiem jeśli Bóg Aniołom, którzy byli zgrzeszyli nie przepuścił, ale łańcuchami ciemności strąciwszy je do piekła podał je zachowane ku potępieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '4', '4', 'I staremu światu nie przepuścił, ale Noego samo ósmego, opowiedacza sprawiedliwości zachował, przywiódłszy potop na świat niepobożnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '5', '5', 'I miasta Sodomeczyków i Gomorejczyków obróciwszy w popiół, wywróceniem potępił i uczynił je przykładem tych, którzy niepobożnie żyć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '6', '6', 'I sprawiedliwego Lota, spracowanego bezecnym obcowaniem haniebnych ludzi, wybawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '7', '7', 'Abowiem on sprawiedliwy mieszkając między nimi, na każdy dzień niepobożnymi ich sprawami widząc je i słysząc duszę sprawiedliwą dręczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '8', '8', 'Umieć Pan pobożne ludzi z pokuszenia wybawić, a niesprawiedliwe na sądny dzień ku karceniu zachować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '9', '9', 'A zwłaszcza ty, którzy za ciałem idąc w nieczystej pożądliwości chodzą i państwem gardzą, swowolni i w sobie się kochający, nie lękają się bluźnić przełożeństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '10', '10', 'Gdyż sami Aniołowie, którzy siłą i mocą więczszy są, nie czynią bluźnierskiego sądu przeciwko im u Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '11', '11', 'Ale ci, jako nierozumne bydlęta, które za przyrodzeniem swoim idą, uczynione ku temu końcowi, aby pojmane zginęły, ty rzeczy ganiąc, których nie wiedzą w skażeniu swoim zaginą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '12', '12', 'I odnosząc zapłatę niesprawiedliwości przeto, iż za wdzięczną rzecz sobie mają powszedne rozkoszy, plugastwem są i zmazami, którzy kochają się w zdradach swoich gdy z wami biesiadują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '13', '13', 'Oczy mają pełne cudzołóstwa i którzy nie umieją przestać od grzechu, wyłudzając dusze niestateczne, serce mając wyświczone ku wydzieraniu, ludzie przeklęci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '14', '14', 'Którzy opuściwszy prostą drogę, obłądzili się i udali się drogą Balaama syna Bosorowego, który zapłaty niesprawiedliwości umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '15', '15', 'Ale sztrofowanie przestępstwa swego cierpiał, abowiem niema oślica, jarzmo nosząca, ludzkim głosem mówiąc, zahamowała szaleństwo prorockie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '16', '16', 'Cić są stokami bez wody, obłoki od wichru pędzone, którym nagęstsze ciemności na wieki są zachowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '17', '17', 'Abowiem nadętą próżność mówiąc, przyłudzają przez pożądliwość ciała i roskoszy ty, którzy prawdziwie uciekli byli od onych, którzy w błędzie przemieszkawają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '18', '18', 'Wolność im obiecując, gdyż sami są słudzy skażenia, abowiem kto jest od kogo przezwyciężon, temu też jest zniewolony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '19', '19', 'Bo jeśliby uszedszy plugawości świata przez uznanie Pana i Zbawiciela Jezu Krysta, tymi się umotawszy byli zwyciężeni, tedy ostatni stan ich stał się gorszy niż pierwszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '20', '20', 'A lepiejby im było nie uznać drogi sprawiedliwości, niźli poznawszy, cofnąć się na zad od świętej nauki im podanej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '1', '21', '21', 'Ale się im przydało, co prawdziwą przypowieścią pospolicie mawiają: Pies wrócił się do zmieciska swego i świnia umyta do kałuże błota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '0', '0', 'Namilszy, już ten wtóry list do was piszę, w którym szczyrą myśl waszę przez napominanie wzbudzam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '1', '1', 'Abyście pamiętali słowa przepowiedziane od świętych proroków i naukę naszą, którzy jesteśmy Apostołowie Pana i zbawiciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '2', '2', 'To naprzód wiedząc, iż ostatnich dni przyjdą szyderze, którzy chodzić będą w swoich własnych roskoszach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '3', '3', 'A mówiąc: Gdzież jest obietnica przyścia jego? Abowiem od tego czasu jako ojcowie zasnęli wszytki rzeczy tak trwają od początku stworzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '4', '4', 'Bo dobrowolnie tego nie wiedzą, iż nieba już zdawna stały się przez słowo Boże i ziemia z wody i w wodzie postawiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '5', '5', 'Przeto świat, który był na ten czas zatopiony wodą zginął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '6', '6', 'A niebiosa i ziemia, które teraz są, tymże słowem schowane ku ogniowi na dzień potępienia i zginienia niepobożnych ludzi bywają zachowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '7', '7', 'Przy tym namilejszy niech wam to nie będzie tajno, iż jeden dzień u Pana jest jako tysiąc lat, a tysiąc lat jako jeden dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '8', '8', 'Nie omieszkawa Pan, który obiecał (jako niektórzy to mają za omieszkanie), ale cierpliwy jest przeciwko nam, nie chcąc, aby którzy zginęli, ale żeby wszyscy ku uznaniu przyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '9', '9', 'A on dzień Pański przyjdzie w nocy jako złodziej, gdy nieba z szumem przeminą i żywioły się zapaliwszy rozstąpią, a ziemia i sprawy, które na niej są, zgorają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '10', '10', 'Gdy tedy wszytkie rzeczy rozstąpią się, jakiemi być macie w świętym obcowaniu i w pobożnych sprawach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '11', '11', 'Oczekiwając i spiesząc się na przyjście dnia Bożego, w którym nieba gorając rozstapią się i żywioły zapalone rospuszczą się?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '12', '12', 'Ale nowych niebios i nowej ziemie wedle obietnice oczekawamy, w których sprawiedliwość mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '13', '13', 'Przeto namilejszy tych rzeczy oczekiwając, starajcie się abyście bez zmazy i bez naganienia od niego z pokojem byli znalezieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '14', '14', 'A cierpliwość Pana naszego rozumiejcie być zbawienie. Jako i miły brat nasz Paweł wedle danej jemu mądrości pisał wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '15', '15', 'Jako ten, który niemal we wszytkich liściech mówi o tych rzeczach, między którymi są niektóre trudne ku wyrozumieniu, które nie umiejętni i niestateczni wykręcają jako ine pisma ku zatraceniu samych siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '16', '16', 'Wy tedy namilejszy, którzy ty rzeczy przed tym wiecie, strzeżcie się, abyście będąc uwiedzieni haniebnem błędem ich, nie wypadli z własnej stałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2pe', '2', '17', '17', 'Ale róśćcie w łasce i w uznaniu Pana naszego Jezu Krysta, któremu niech będzie chwała i teraz i na wieczne czasy. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '0', '0', 'Co było od początku, cochmy słyszeli, cochmy widzieli oczyma naszymi, cochmy oglądali i ręce nasze macały o słowie żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '1', '1', '(Abowiemci żywot objawion jest i widzieliśmy i świadczemy i opowiedamy wam on żywot wieczny, który był u Ojca i objawion jest nam).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '2', '2', 'Cośmy widzieli i słyszeli, to wam oznajmujemy abyście też i wy społeczność mieli z nami, a iżby społeczność nasza była z Ojcem i z Synem Jego Jezusem Krystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '3', '3', 'A te rzeczy piszemy wam, aby wesele wasze było zupełne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '4', '4', 'Toć tedy jest poselstwo, któreśmy słyszeli od niego i oznajmujemy wam, iż Bóg jest światłością, a iż żadnej ciemności w nim nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '5', '5', 'Jeślibychmy rzekli, iż społeczność mamy z nim, a w ciemności chodzimy, kłamamy, a szczyrze się nie obchodzimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '6', '6', 'A jeśli w światłości chodzimy, jako on jest w światłości, społeczność mamy z nim zobopólną, a krew Jezu Krysta Syna jego oczyścia nas od wszelkiego grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '7', '7', 'Jeślibychmy rzekli, iż grzechu nie mamy, sami siebie oszukawamy, a prawdy w nas nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '8', '8', 'Jeślibyśmy wyznawali grzechy nasze, wiernyć jest i sprawiedliwy, aby nam odpuścił grzechy nasze i oczyścił nas od wszelakiej nieprawości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '0', '9', '9', 'Jeślibyśmy rzekli, iżeśmy nie zgrzeszyli, kłamcą go czyniemy, a słowa jego w nas nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '0', '0', 'Dziatki moje! Ty rzeczy piszę wam, abyście nie grzeszyli, a jeśliby kto zgrzeszył, przyczyńcę mamy u Ojca, Jezusa Krystusa sprawiedliwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '1', '1', 'A onci jest ubłaganiem za grzechy nasze, a nie tylko za nasze, ale też za grzechy wszystkiego świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '2', '2', 'A przez to wiemy, iżechmy go poznali, jeśli przykazania jego zachowywamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '3', '3', 'Który mówi: Znam go, a przykazania jego nie zachowywa, kłamcą jest i prawdy w nim nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '4', '4', 'A który zachowywa słowo jego, prawdziwie w nim miłość Boża jest wypełniona, przez to wiemy, iż w nim jesteśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '5', '5', 'A który mówi, iż w nim mieszka, ma jako on chodził, tak samo chodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '6', '6', 'Bracia! Nie piszeć wam nowej nauki, ale naukę starą, którąście mieli już od początku. Ta stara nauka jest ono słowo, któreście słyszeli od początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '7', '7', 'Zasię naukę nową piszę wam, która rzecz jest prawdziwa w nim i w was, abowiem ciemności przemijają, a prawdziwa ona światłość już świeci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '8', '8', 'Który mówi, iż jest w światłości, a brata swojego nienawidzi jeszcze dotąd jest w ciemnościach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '9', '9', 'Który miłuje brata swego, w światłości trwa i zgorszenia w nim nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '10', '10', 'A który nienawidzi brata swego w ciemnościach jest i w ciemnościach chodzi, a nie wie gdzie idzie, bo ciemności zaślepiły oczy jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '11', '11', 'Piszę wam dziatki, iż grzechy są wam odpuszczone dla imienia jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '12', '12', 'Piszę wam ojcowie, iż znacie tego, który jest od początku. Piszę wam młodzieńcy, iżeście złego onego zwyciężyli. Piszę wam dziateczki, iż znacie Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '13', '13', 'Piszę wam ojcowie, iż znacie tego, który jest od początku. Pisałem wam młodzieńcy, iż jesteście mocni, a słowo Boże mieszka w was i onego złego zwyciężyliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '14', '14', 'Nie miłujcie świata, ani tych rzeczy, które są na świecie. Jeśli kto miłuje świat, miłości Ojcowskiej w nim nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '15', '15', 'Abowiem wszytko co jest na świecie, pożądliwość ciała i żądza oczu i pycha żywota, nie jestci z Ojca, ale z świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '16', '16', 'A światci przemija i pożądliwość jego, ale kto czyni wolę Bożą, trwa na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '17', '17', 'Dziateczki! Jużci jest ostatni czas; a jakoście słyszeli, iż Antykryst ma przyjdź i teraz pełno antykrystów nastało; ztąd wiemy, iż jest ostatni czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '18', '18', 'Z naści wyszli, ale nie byli z nas, abowiem jeśliby z nas byli, iścieby byli z nami trwali. A to się stało aby się okazali, iż nie wszyscy z nas są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '19', '19', 'Ale wy pomazanie macie od onego świętego i wszystki rzeczy umiecie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '20', '20', 'Nie pisałciem wam, jakobyście prawdy nie umieli, ale iż wy ją znacie, a iż żadne kłamstwo nie jest z prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '21', '21', 'Któż jest kłamcą jedno ten, który przy iż Jezus nie jest Krystus? Tenci jest antykrystem prząc się Ojca i Syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '22', '22', 'A ktokolwiek Syna przy, ten ani Ojca ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '23', '23', 'Coście tedy słyszeli od początku, to niechaj w was trwa. Jeśliby w was trwało coście słyszeli od początku, wy też w Ojcu i w Synie trwać będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '24', '24', 'A tać jest ona obietnica, którą obiecał nam, to jest żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '25', '25', 'Te rzeczy pisałem wam o tych, którzy was zwodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '26', '26', 'Ale pomazanie, któreście wzięli od niego, trwa w was. A nie potrzebujecie, aby was kto nauczył, ale jako też pomazanie uczy was o wszytkich rzeczach, które też prawdziwe jest, a nie kłamliwe, a jako nauczyło was, w nim trwać będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '27', '27', 'Teraz tedy dziateczki trwajcie w nim, abyśmy gdy się okaże ufanie mieli, ani się wstydali przed nim w przyściu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '1', '28', '28', 'Jeśli wiecie, iż sprawiedliwy jest, wiecie, iż ktokolwiek czyni sprawiedliwość, z niego się narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '0', '0', 'Obaczcie jaką miłość dał nam Ociec, to jest, abyśmy byli nazwani syny Bożymi. Dlatego świat nas nie zna, iż też nie zna jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '1', '1', 'Namilejszy teraz synami Bożymi jesteśmy, ale się jeszcze nie okazało co będziemy, lecz wiemy, iż gdy on się okaże, jemu podobni będziemy, abowiem ujźrzymy go jako jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '2', '2', 'A ktokolwiek ma tę nadzieję w nim, oczyścia się jako i on czysty jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '3', '3', 'Ktokolwiek popełnia grzech, pogotowiu zakon przestępuje, gdyż grzech jest przestępstwo zakonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '4', '4', 'Przytym wiecie, iż on się okazał, aby grzechy nasze zgładził, a grzechów w nim nie masz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '5', '5', 'Ktokolwiek w nim mieszka nie grzeszy. Ktokolwiek grzeszy nie widział go, ani go zna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '6', '6', 'Synaczkowie, niechaj was żaden nie zwodzi, ktokolwiek czyni sprawiedliwość sprawiedliwy jest, jako i on jest sprawiedliwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '7', '7', 'Który grzech popełnia, ze diabła jest, gdyż od początku diabeł grzeszy. Ku temu się okazał Syn Boży, aby zepsować uczynki diabłowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '8', '8', 'Ktokolwiek się narodził z Boga, grzechu nie popełnia, gdyż nasienie jego w nim trwa, ani może grzeszyć przeto, iż z Boga się narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '9', '9', 'Tym znaczni są synowie Boży i synowie diabłowi. Ktokolwiek nie zachowywa sprawiedliwości, nie jestci z Boga i kto nie miłuje brata swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '10', '10', 'Abowiem toć jest poselstwo coście słyszeli od początku: abyśmy się społecznie miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '11', '11', 'Nie jako Kain, który był z onego Złego i brata swego zabił. Dla którejże go przyczyny zabił? Iż uczynki jego złe były, a brata jego sprawiedliwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '12', '12', 'Nie dziwujcie się bracia moi jeśli was nienawidzi świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '13', '13', 'My wiemy, iż jesteśmy przeniesieni z śmierci do żywota, iż miłujemy bracią. Kto nie miłuje brata trwa w śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '14', '14', 'Ktokolwiek nienawidzi brata swego mężobójca jest. A wiecie, iż wszelki mężobójca nie ma żywota wiecznego w sobie trwającego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '15', '15', 'W tymeśmy poznali miłość, iż on duszę swą za nas położył. A tak mamy za bracią duszę kłaść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '16', '16', 'A ktoby miał majętności świetskie i ujźrzał brata swego potrzebującego, a zawarłby wnętrzności swe przed nim, jakoż w nim trwa miłość Boża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '17', '17', 'Dziatki moje, nie miłujmy słowem, ani językiem tylko, ale uczynkiem i prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '18', '18', 'Po tym znamy, iż z prawdy jesteśmy i przed nim bespieczne serca nasze mamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '19', '19', 'Abowiem jeśliby nas osądziło serce nasze, mocniejszyć jest Bóg niż serce nasze i wie wszytki rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '20', '20', 'Namilejszy, jeśli serce nasze nas nie osądza, my ufanie mamy u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '21', '21', 'A oczkolowiek prosimy, bierzemy od niego, abowiem roskazania jego zachowywamy, a rzeczy wdzięczne przed oblicznością jego czyniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '22', '22', 'Tać jest tedy nauka jego, abyśmy wierzyli imieniowi Syna jego Jezu Krysta i miłowali jedni drugich jako nam przykazanie dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '2', '23', '23', 'Abowiem który chowa przykazania jego, w nim mieszka, a on w nim, a po tym wiemy iż mieszka w nas, to jest z Ducha, którego nam dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '0', '0', 'Namilejszy, nie wierzcie leda duchowi, ale doświadczajcie duchów jeśli z Boga są, abowiem wiele fałszywych proroków wyszło na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '1', '1', 'Po tym poznawajcie Ducha Bożego: Wszelki duch, który wyznawa, iż Jezus Krystus przyszedł w ciele, z Boga jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '2', '2', 'Ale którykolwiek duch nie wyznawa iż Jezus Krystus przyszedł w ciele, ten z Boga nie jest, ale ten jest duch on Antykrystów, o którymeście słyszeli, iż przybędzie i już jest na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '3', '3', 'Wy jesteście z Boga synaczkowie i zwyciężyliście je, bo mocniejszy jest ten, który w was jest, niż który na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '4', '4', 'Oni są ze świata, przeto świetskie rzeczy mówią, a świat ich słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '5', '5', 'My z Boga jestechmy. Który zna Boga, słucha nas. Który nie jest z Boga nie słucha nas, a ztąd znamy ducha prawdy i ducha błędu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '6', '6', 'Namilszy, miłujmy jedni drugie, abowiem miłość jest z Boga. Ktokolwiek miłuje, z Boga się narodził i zna Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '7', '7', 'Kto nie miłuje, nie zna Boga; abowiem Bóg jest miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '8', '8', 'W tym się okazała miłość Boża w nas, iż Syna swego jednorodzonego zesłał Bóg na świat, abychmy żyli przezeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '9', '9', 'W tymci jest miłość, nie iżbyśmy my umiłowali Boga, ale iż on umiłował nas i posłał Syna swego aby był ubłaganiem za grzechy nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '10', '10', 'Namilejszy, jeśli nas tak Bóg umiłował, mamy też my jedni drugie miłować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '11', '11', 'Boga żaden nigdy nie widział. Jeśli miłujem jedni drugie, Bóg w nas mieszka, a miłość jego wypełniła się w nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '12', '12', 'Po tym znamy, iż my w nim mieszkamy, a on w nas, iż z Ducha swego nam dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '13', '13', 'A myśmy widzieli i świadczemy, iż Ociec posłał Syna, aby był zbawicielem świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '14', '14', 'Ktobykolwiek wyznał, iż Jezus jest Synem Bożym, Bóg w nim mieszka, a on w Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '15', '15', 'Myśmy poznali i uwierzyli miłości, którą Bóg ma w nas. Bógci jest miłość, a kto trwa w miłości, w Bogu trwa, a Bóg w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '16', '16', 'Tymci się wypełniła miłość w nas, abyśmy ufanie mieli w dzień sądny, iż jaki on jest, tacy i my jestechmy na tym świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '17', '17', 'Nie maszci bojaźni w miłości. Zupełna miłość precz wyrzuca bojaźń, abowiem bojaźń ma udręczenie. A kto się boi, nie jestci zupełny w miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '18', '18', 'My go miłujemy, abowiem on nas pierwszy umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '19', '19', 'Jeśliby kto rzekł: Miłuję Boga, a brataby swego nienawidział, kłamcą jest. Abowiem kto nie miłuje brata swego, którego widział, Boga, którego nie widział, jako może miłować?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '3', '20', '20', 'I to roskazanie mamy od niego, aby ten który Boga miłuje, miłował też brata swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '0', '0', 'Ktokolwiek wie, iż Jezusa być Krystusem, z Boga się narodził, a ktokolwiek miłuje tego, który urodził, miłuje też tego, który z niego jest narodzon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '1', '1', 'W tym znamy, iż miłujemy Syny Boże, gdy Boga miłujemy i przykazania jego chowamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '2', '2', 'Abowiem tać jest miłość Boża abyśmy przykazania jego chowali, a przykazania jego nie są cięszkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '3', '3', 'Bo wszytko, co się narodziło z Boga zwycięża świat, a wiara nasza jest to zwycięstwo, które zwycięża świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '4', '4', 'I któż jest ten, który zwycięża świat, jedno ten, który wierzy iż Jezus jest Synem Bożym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '5', '5', 'Tenci jest on Jezus Krystus, który przyszedł przez wodę i krew; nie przez wodę tylko, ale przez wodę i krew. A duch ten jest który świadczy, iż Duch jest prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '6', '6', 'Abowiem trzy są, którzy świadczą na niebie: Ociec, Słowo i Duch święty, a ci trzej jednoć są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '7', '7', 'A trzy są, którzy świadczą na ziemi: duch i woda i krew, a ci trzej na jedno się zgadzają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '8', '8', 'Jeśli świadectwo ludzkie przyjmujemy, świadectwo Boże więcszeć jest, abowiem toć jest świadectwo Boże, które świadczył o Synie swym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '9', '9', 'Który wierzy w Syna Bożego, ma świadectwo sam w sobie. Który nie wierzy Bogu, czyni go kłamcą, gdyż nie uwierzył świadectwu, które Bóg świadczył o Synie swoim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '10', '10', 'A toć jest świadectwo, iż żywot wieczny dał nam Bóg, a ten żywot jest w Synie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '11', '11', 'Który ma Syna, ma żywot. Który nie ma Syna Bożego, nie ma żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '12', '12', 'Ty rzeczy pisałem wam, którzy wierzycie w imię Syna Bożego, żebyście wiedzieli, iż żywot wieczny macie i abyście wierzyli w imię Syna Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '13', '13', 'I toć jest ufanie, które mamy u Boga, iż jeślibyśmy o co prosili wedle wolej jego, słucha nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '14', '14', 'A jeśli wiemy, iż słucha nas, czegobyśmykolwiek żądali, wiemy, iż otrzymawamy prośby, którychśmy od niego prosili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '15', '15', 'Jeśliby kto widział brata swego a on grzeszy grzechem, który nie jest na śmierć, prosić będzie i da mu żywot, grzeszącym (mówię) nie na śmierć. Jestci grzech na śmierć, nie mówię aby zań prosił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '16', '16', 'Wszelka nieprawość jestci grzech, ale jestci grzech nie na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '17', '17', 'Wiemy, iż ktokolwiek narodził się z Boga, nie grzeszy, ale który się narodził z Boga, zachowywa samego siebie, a on Zły nie dotyka się go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '18', '18', 'Wiemy, iż z Boga jesteśmy, a świat wszytek we złem położon jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '19', '19', 'Ale wiemy, iż Syn Boży przyszedł i dał nam myśl, abyśmy poznali onego, który jest prawdziwy a jesteśmy w onym prawdziwym, to jest w Synie jego Jezusie Krystusie. Tenci jest prawdziwy Bóg i żywot wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '1jn', '4', '20', '20', 'Dziateczki, strzeżcie się bałwanów. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '0', '0', 'Starszy, zacnej paniej i synom jej, które ja prawdziwie miłuję, a nie ja tylko, ale i wszyscy, którzy poznali prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '1', '1', 'Dla prawdy, która trwa w nas i z nami będzie na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '2', '2', 'Niech będzie z wami łaska, miłosierdzie, pokój od Boga Ojca i od Pana Jezu Krysta Syna Ojcowego z prawdą i miłością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '3', '3', 'Uweseliłem się barzo, iżem nalazł z synów twoich, którzy chodzą w prawdzie, jakośmy roskazanie wzięli od Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '4', '4', 'A teraz proszę cię pani, nie jako przykazanie nowe pisząc tobie, ale któreśmy mieli od początku, abyśmy jedni drugie miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '5', '5', 'A tać jest miłość, abychmy chodzili wedle nauki jego. A tać jest nauka, abyście jako od początku tak w nim chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '6', '6', 'Abowiem wiele zwodzicielów wyszło na świat, którzy nie wyznawają, iż Jezus Krystus przyszedł w ciele; a który jest takowy, jest zwodzicielem i antykrystem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '7', '7', 'Strzeżcie samych siebie, żebyśmy nie tracili tych rzeczy, którechmy sprawowali, owszem abyśmy zupełną zapłatę wzięli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '8', '8', 'Ktokolwiek przestępuje a nie trwa w nauce Krystusowej, Boga nie ma, który trwa w nauce Krystusowej, ten i Ojca i Syna ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '9', '9', 'Jeśli kto przychodzi do was, a tej nauki nie przynosi, nie przyjmujcie go w dom, ani go pozdrawiajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '10', '10', 'Abowiem kto go pozdrawia, uczestnikiem jest złych jego uczynków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '11', '11', 'Mając wam wiele pisać, nie chciałem na papierze ani inkaustem, ale nadziewam się przyjdź do was i obecnie mówić, aby wesele nasze było zupełne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '2jn', '0', '12', '12', 'Pozdrawiają cię synowie siostry twojej zacnej. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '0', '0', 'Starszy, Gajowi miłemu, którego ja prawdziwie miłuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '1', '1', 'Namilszy! Naprzód żądam, aby się tobie szczęśliwie wodziło i abyś był zdrów, jako dusza twoja szczęśliwie się sprawuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '2', '2', 'Abowiemem się barzo uradował gdy przyszli bracia i świadczyli o szczerości twojej, jako ty szczyrze postępujesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '3', '3', 'Nad ty rzeczy więczszego wesela nie mam, gdy słyszę, iż synowie moi szczyrze postępują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '4', '4', 'Namilszy! Wiernie czynisz, cokolwiek czynisz przeciwko braciej i przeciw gościam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '5', '5', 'Którzy świadczyli o miłości twej przed zborem, dobrze uczynisz, jeśli je odprowadzisz, jako przystoi Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '6', '6', 'Abowiem dla imienia jego wyszli, nic nie biorąc od poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '7', '7', 'Takowe tedy mamy przyjmować, abyśmy byli pomocnicy prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '8', '8', 'Pisałem do zboru; ale Diotrefes, który między nimi chce być przedniejszym, nie przyjmuje nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '9', '9', 'A tak, jeślibych ja przyszedł, przypomnię uczynki jego, które czyni, słowy złościwemi szepcąc przeciwko nam, który też na tym dosyć nie mając, ani sam przyjmuje braciej i zabrania, którzy chcą je przyjmować i z kościoła wyrzuca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '10', '10', 'Namilszy!  Tego nie naśladuj co jest złego, ale co jest dobrego. Który dobrze czyni, z Boga jest;a kto źle czyni, nie widział Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '11', '11', 'Świadectwo jest dane Demetrjuszowi od wszytkich i od samej prawdy, ale i my świadectwo dawamy, a wiecie, iż świadectwo nasze prawdziwe jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '12', '12', 'Wielem miał pisać, ale nie chcę inkaustem ani piórem pisać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '13', '13', 'Lecz się nadziewam rychło cię widzieć, a tedy obecnie mówić będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, '3jn', '0', '14', '14', 'Pokój tobie. Pozdrawiają cię przyjaciele, a przyjaciele mianowicie pozdrawiaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '0', '0', 'Judas, sługa Jezusów Krystusów, a brat Jakubów, wezwanym, którzy od Boga Ojca są poświęceni i od Jezusa Krystusa zachowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '1', '1', 'Miłosierdzie i pokój i miłość, niech się wam rozmnoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '2', '2', 'Namilejszy, gdyż wszelaką pilność to czynię, abym wam pisał o społecznym zbawieniu, musiałem wam pisać, abych was napomniał, iżbyście bojowali o wiarę, która raz jest podana świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '3', '3', 'Abowiem wkradali się niektórzy ludzie niepobożni, dawno już przed tym spisani na to potępienie, którzy Boga naszego łaskę obracają na wszeteczną roskosz i Boga, który sam jest Panem i Pana naszego Jezu Krysta przą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '4', '4', 'Przytym chcę was napominać o tym, gdyż to raz wiecie, iż Pan wybawiwszy lud z Egiptu, zasię ty potracił, którzy nie wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '5', '5', 'I Anioły, którzy nie zachowali początku swego, owszem opuścili mieszkanie swoje, na sąd wielkiego onego dnia zwiąskami wiecznemi pod ciemnością zachował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '6', '6', 'Jako Sodoma i Gomorra i tym przyległe miasta, tymże sposobem jako i oni, nieczystości popełniwszy i udawszy się za cudzym ciałem, przełożeni są na przykład, ognia wiekuistego karanie cierpiąc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '7', '7', 'A wżdy także i ci ospali, ciałoć plugawią, a zwierzchność wygardzają i przełożeństwa bluźnią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '8', '8', 'Ale Michał Archanioł, gdy się z diabłem wadził, swarząc się o ciało Mojżeszowe, nie śmiał nań pomówić złorzeczeństwa ale rzekł: Niech cię Bóg karze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '9', '9', 'Ty rzeczy, których nie umieją sromocą, a które umieją z przyrodzenia, jako bydlęta nierozumne, w tych się psują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '10', '10', 'Bieda im! Abowiem drogą Kainową się udali i za omylnością zapłaty, którą był Balaam zwiedzion puścili się i przeciwieństwem Korego poginęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '11', '11', 'Cić są jako zmazy w świętych biesiadach waszych, gdy z wami używają, okrom bojaźni żądnego, sami siebie pasąc; obłoki bez wód, które pędzą wiatry, drzewa niepłodne dwa kroć martwe i wykorzenione.&nbsp;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '12', '12', 'Wały okrutne morskie, którzy swe własne sromoty jako piany wypuszczają, gwiazdy błąkające się, którym zaćmienie ciemności na wieki jest zachowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '13', '13', 'A prorokował też o tych Enoch, siódmy od Adama mówiąc: Oto przyszedł Pan z świętemi zastępy swemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '14', '14', 'Aby czynił sąd przeciw wszytkim i karał tych, którzykolwiek z nich są niepobożni, za uczynki wszytki, które niepobożnie popełnili i za wszytki przykre słowa, które mówili przeciw jemu grzesznicy niepobożni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '15', '15', 'Ci są szemracze, narzekacze, w roskoszach swoich chodzący i których usta nadęte słowa mówią, osobam się dla pożytku dziwując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '16', '16', 'Ale wy namilejszy pamiętajcie słowa, które są przed tym powiedane od Apostołów Pana naszego Jezu Krysta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '17', '17', 'Iż wam powiedali, że czasu ostatecznego nastaną pośmiewcy, którzy w niepobożnych roskoszach swoich chodzić będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '18', '18', 'Cić są, którzy się sami odłączają, cieleśni, ducha nie mający.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '19', '19', 'A wy, namilszy, budując sami siebie na naświętszej wierze waszej, przez Ducha świętego modląc się,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '20', '20', 'Jedni drugie w miłości Bożej zachowajcie, oczekiwając miłosierdzia Pana naszego Jezu Krysta, ku żywotowi wiecznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '21', '21', 'A jednych z rozsądkiem litujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '22', '22', 'A drugie zachowajcie w bojaźni, wyrywając je z płomienia, mając w nienawiści i suknię od ciała pokalaną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '23', '23', 'Przytym temu, który was może zachować wolne od grzechu i stawić przed oblicznością chwały swojej nie naganione z radością,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'jude', '0', '24', '24', 'To jest, samemu mądremu Bogu, Zbawicielowi naszemu, chwała i wielmożność i zwierzchność i moc i teraz i na wszytki wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '0', '0', 'Zjawienie Jezu Krysta, które mu dał Bóg, aby okazał sługam swoim rzeczy, które się mają zstać rychło, a on posławszy je przez Anioła swego, oznajmił Janowi słudze swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '1', '1', 'Który świadczył słowo Boże i świadectwo Jezu Krysta i którekolwiek rzeczy widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '2', '2', 'Błogosławiony, który czyta i błogosławieni, którzy słuchają słowa proroctwa tego i chowają ty rzeczy, które w nim są opisane, abowiem czas blisko jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '3', '3', 'Jan siedmi kościołom, które są w Azijej, łaska i pokój niech wam będzie od tego, który jest i który był i który przyjdzie i od siedmi duchów, którzy są przed oblicznością stolice jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '4', '4', 'I od Jezu Krysta, który jest wierny on świadek, pierworodny z umarłych i Książę królów ziemie. Temu, który nas umiłował i omył nas z grzechów naszych krwią swoją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '5', '5', 'I który nas uczynił królmi i kapłany Bogu i Ojcu swemu, temu niech będzie chwała i moc na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '6', '6', 'Oto idzie z obłokami i ujźrzy go każde oko. Temu i ci, którzy go przebili i będą narzekać przed nim wszytki pokolenia ziemie. Tak, Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '7', '7', 'Jam jest Alfa i Omega, to jest początek i koniec, mówi Pan, który jest i który był i który przyjdzie, to jest on wszechmogący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '8', '8', 'Ja Jan, którym jest bratem waszym i towarzyszem w ucisku i w królestwie i w cierpliwości Krystusa Jezusa byłem na onej wyspie, którą zową Patmos, dla słowa Bożego i dla świadectwa Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '9', '9', 'I byłem zachwycony od ducha w dzień niedzielny i słyszałem za sobą głos wielki jako trąby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '10', '10', 'Mówiąc: Jam jest Alfa i Omega, pierwszy i ostatni, a co widzisz napisz w księgi i pośli siedmi kościołom, które są w Azijej, w Efezie i w Smyrnie i w Pergamie i w Tyatyrijej i w Sardziech i w Filadelfijej i w Laodycei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '11', '11', 'Obróciłem się tedy, abym obaczył głos, który był mówił zemną, a obróciwszy się ujźrzałem siedm lichtarzów złotych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '12', '12', 'A w pośrzodku siedmi lichtarzów złotych niektórego podobnego Synowi człowieczemu, obleczonego w długą szatę i opasanego aż pod piersi pasem złotym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '13', '13', 'A głowa jego i włosy były białe jako wełna biała, jako śnieg, a oczy jego jako płomień ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '14', '14', 'Nogi też jego podobne mosiądzowi, jakoby w piecu gorające, a głos jego jako głos wiela wód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '15', '15', 'I miał na prawicy swej siedm gwiazd, a z ust jego miecz z obu stron ostry wychodził i oblicze jego było na kształt słońca, gdy w swojej mocy świeci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '16', '16', 'A gdym go ujźrzał, upadłem u nóg jego jakoby umarły, tedy włożył prawicę swą na mię mówiąc mi: Nie bój się. Jam jest pierwszy i ostatni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '17', '17', 'I który żywę chociam był umarł, a oto żywę na wieki wieków. Amen. I mam klucze piekła i śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '18', '18', 'Napisz ty rzeczy, któreś widział i które są i które na potym być mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '0', '19', '19', 'Tajemnice siedmi gwiazd, któreś widział na prawicy mojej i siedmi lichtarzów złotych. Siedm gwiazd sąć Aniołowie siedmi kościołów, a siedm lichtarzów, któreś widział, są siedm kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '0', '0', 'Napisz Aniołowi kościoła Efeskiego: Ty rzeczy mówi ten, który trzyma siedm gwiazd w prawicy swej i chodzi między siedmia lichtarzów złotych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '1', '1', 'Wiem sprawy twoje i pracą i cierpliwość twoję, a iż nie możesz znosić złych i doświadczyłeś tych, którzy się mienią być Apostoły, a nie są i nalazłeś je kłamcami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '2', '2', 'I byłeś uciśnion i cierpisz. I pracowałeś dla imienia mego, a nie ustałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '3', '3', 'Ale mam nieco przeciwko tobie, żeś miłość twoję pierwszą opuścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '4', '4', 'A tak pamiętaj skądeś wypadł i pokutuj, a pierwsze uczynki działaj, a gdzieby inaczej, przyjdę przeciwko tobie rychło, a ruszę lichtarz twój z miejsca jego, jeślibyś się nie upamiętał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '5', '5', 'Ale jednak to masz, iż nienawidzisz uczynków Nikolaitów, których i ja nienawidzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '6', '6', 'Który ma ucho, niech słucha, co Duch mówi kościołom: Temu, któryby zwyciężył, dam jeść z drzewa żywota, które jest w pośrzodku sadu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '7', '7', 'A Aniołowi kościoła Smyrneńskiego napisz: Ty rzeczy mówi pierwszy i ostatni, który był umarły i żywie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '8', '8', 'Znam sprawy twoje i ucisk i ubóstwo (acz jesteś bogaty) i bluźnierstwo tych, którzy się powiedają być Żydmi, a nie są, ale są bożnicą szatańską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '9', '9', 'Nic się nie bój tych rzeczy, które masz cierpieć, oto wrzuci diabeł niektóre z was do ciemnice, abyście byli doświadczeni, a będziecie mieć ucisk dziesiąci dni. Bądź wierny aż do śmierci, a damci koronę żywota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '10', '10', 'Który ma ucho, niechaj słucha, co Duch mówi kościołom: Ktoby zwyciężył, nie będzie obrażon od wtórej śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '11', '11', 'Przytym napisz Aniołowi kościoła Pergameńskiego: Ty rzeczy mówi ten, który ma miecz obojętny i ostry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '12', '12', 'Wiemci uczynki twoje, a gdzie mieszkasz, to jest, gdzie jest stolica szatańska, a iż trzymasz imię moje i nie zaprzałeś się wiary mojej, ani w ony dni, w które Antypas, świadek mój wierny, zabit jest u was, gdzie szatan mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '13', '13', 'Ale mam przeciwko tobie trochę, iż tam masz ty, którzy dzierżą naukę Balaamowę, który uczył Balaka, aby wrzucił zgorszenie przed syny izraelskie, żeby jedli z tych rzeczy, które bywają bałwanom ofiarowane i aby nieczystości się dopuścili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '14', '14', 'Także też masz ty, którzy trzymają naukę Nikolaitów, co mam w nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '15', '15', 'Upamiętaj się, bo gdzieby inaczej, przyjdę na cię  rychło i będę walczył z nimi mieczem ust moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '16', '16', 'Który ma ucho, niechaj słucha tego co Duch mówi kościołom: Temu, któryby zwyciężył, dam jeść z onej skrytej manny i dam mu kamyk biały i na kamyku imię nowe napisane, którego nikt nie zna, jedno ten, który je bierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '17', '17', 'Napisz też Aniołowi kościoła Tyatyrejskiego: Ty rzeczy mówi Syn Boży, który ma oczy jako płomień ogniowy, a którego nogi podobne są mosiądzowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '18', '18', 'Wiemci uczynki twoje i miłość i posługowanie i wiarę i cierpliwość twoję i sprawy twoje, i ostatnie rzeczy więtsze niż pierwsze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '19', '19', 'Ale mam trochę przeciwko tobie, iż niewieście Jezabeli, która się mieni być prorokinią, dopuszczasz uczyć i zwodzić sługi moje, aby się nieczystości dopuszczali i jedli z tych rzeczy, które bałwanom bywają ofiarowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '20', '20', 'I dałem jej czas, aby się upamiętała od złości swoich, ale nie pokutowała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '21', '21', 'Oto ja porzucę ją na łoże i ty, którzy z nią cudzołóstwa się dopuścili w ucisk wielki, jeśliby się nie upamiętali z uczynków swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '22', '22', 'A syny jej potracę śmiercią i poznają wszytki kościoły iżem ja jest ten, który badam się nerek i serc i dam każdemu z was wedle uczynków jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '23', '23', 'A wam mówię drugim Tyatyreńczykom, którzykolwiek nie mają tej nauki, ani poznali głębokości szatańskich, jako powiedają, nie włożę na was inego brzemienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '24', '24', 'A wszakże tego się dzierzcie co macie, aż przyjdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '25', '25', 'Abowiem jeśliby kto zwyciężył i zachował aż do końca uczynki moje, dam mu zwierzchność nad pogany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '26', '26', 'I będzie je rządził laską żelazną i jako naczynia gliniane będą skruszeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '27', '27', 'Jakom ja też wziął od Ojca mego i dam mu gwiazdę zaranną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '1', '28', '28', 'Który ma ucho, niechaj słyszy co Duch mówi kościołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '0', '0', 'I napisz Aniołowi kościoła, który jest w Sardziech: To mówi ten, który ma siedm duchów Bożych i siedm gwiazd: Wiem uczynki twoje, iż sława, że żywiesz, aleś umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '1', '1', 'Bądź czujny, a utwirdź drugie rzeczy, które pomrzeć mają, abowiemem nie nalazł uczynków twoich zupełnych przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '2', '2', 'Pamiętaj tedy coś wziął i słyszał, a tak zachowywaj i pokutuj. A jeśli czuć nie będziesz, przyjdę do ciebie jako złodziej, a nie zrozumiesz, której godziny przyjdę do ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '3', '3', 'Jednak masz kilka imion też w Sardziech, które nie pokalały szat swoich, przeto będą ze mną chodzić w białych szatach, abowiem godni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '4', '4', 'Ktoby zwyciężył, ten będzie obleczon w szaty białe, a nie wymażę imienia jego z ksiąg żywota, ale będę wyznawał imię jego przed Ojcem moim i przed Anioły jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '5', '5', 'Kto ma ucho, niech słyszy co Duch mówi kościołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '6', '6', 'A pisz Aniołowi kościoła Filadefijeńskiego: to mówi święty i prawdziwy on, który ma klucz Dawidów, który otwiera, a żaden nie zamyka, zamyka a żaden nie otwiera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '7', '7', 'Wiem uczynki twoje: Otom postawił przed tobą drzwi otworzone, a żaden nie może ich zamknąć, bo masz niejaką moc i zachowałeś słowo moje, aniś się zaprzał imienia mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '8', '8', 'Oto podam ty, którzy są z bożnice szatańskiej, to jest ty, którzy się powiedają być Żydmi, a nie są, ale kłamają. Oto przymuszę je przyść i kłaniać się przed nogami twemi i wiedzieć, iżem ciebie umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '9', '9', 'Abowiemeś zachował słowo cierpliwości mojej, ja też ciebie wybawię z czasu pokusy, co przyjdź ma na wszytek świat, ku doświadczeniu obywatelów ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '10', '10', 'Oto idę rychło, trzymaj co masz, aby żaden nie wziął korony twojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '11', '11', 'Jeśliby kto zwyciężył, uczynię to, aby był słupem w kościele Boga mojego, ani z niego już wynidzie. I napiszę na nim imię Boga mego i imię miasta Boga mego, to jest nowego Jeruzalem, które zstąpiło z nieba od Boga mego i imię moje nowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '12', '12', 'Kto ma ucho, niech słyszy co Duch mówi kościołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '13', '13', 'A Aniołowi kościoła Laodyceńskiego napisz: To mówi Amen świadek on wierny i prawdziwy, początek stworzenia Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '14', '14', 'Znam sprawy twoje, iżeś nie jest ani zimny ani gorący, Boże daj abyś był zimny, abo gorący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '15', '15', 'A tak, ponieważeś letni, a nie zimny ani gorący, wyrzucę cię z ust moich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '16', '16', 'Abowiem powiedasz: Jestem bogaty i zbogaciłem się, a w żadnej rzeczy niedostatku nie cierpię a nie wiesz iżeś nędzny, mizerny i ubogi i ślepy i nagi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '17', '17', 'Radzęć abyś kupił u mnie złota w ogniu doświadczonego, żebyś był bogatym i szat białych, abyś się przyoblekł, żeby się nie okazowała sromota nagości twojej, a pomaż wódką oczy twoje, abyś widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '18', '18', 'Ja, którekolwiek miłuję, ty karzę i nauczam, ruszże się i upamiętaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '19', '19', 'Oto stoję u drzwi i kołacę, jeśliby kto usłyszał głos mój i otworzył drzwi, wnidę do niego i będę z nim wieczerzał, a on ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '20', '20', 'Jeśliby kto zwyciężył, dam mu siedzieć ze mną na stolcu moim, jakom ja też zwyciężył i siedzę z Ojcem moim na stolcu jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '2', '21', '21', 'Kto ma ucho, niech słyszy co Duch mówi kościołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '0', '0', 'Zatymem widział, a oto drzwi otworzone w niebie, a głos pierwszy, którym słyszał, jako trąby mówiącej ze mną rzekł: Wstąp sam, a pokażęć ty rzeczy, które muszą być potym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '1', '1', 'Natychmiast tedy byłem zachwycon od Ducha, a oto stolec był postawion na niebie, a niektóry na stolcu siedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '2', '2', 'A ten, który siedział pojźrzeniem był podobny kamieniu jaspisowemu i sardynowemu, a wszędy około stolca była tęcza pojźrzeniem podobna smaragdowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '3', '3', 'A wszędy około stolca było stolców czterzy a dwadzieścia, a na stolcach widziałem dwadzieścia i czterzech starszych siedzących, obleczonych w szaty białe i mających na głowach swych korony złote.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '4', '4', 'A z stolca wychodziły łyskawice i gromy i głosy i siedm lamp ognistych gorających przed stolcem, które są siedm duchów Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '5', '5', 'Było też przed stolcem morze śklane, podobne kryształowi, a w pośrzodku i wszędy około stolca czworo źwierząt pełnych oczu z przodku i z tyłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '6', '6', 'A pierwsze źwierzę było podobne lwowi, a wtóre źwierzę podobne cielcowi, a trzecie źwierzę miało oblicze jako człowiek, a czwarte źwierzę podobne orłowi lecącemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '7', '7', 'A to czworo źwierząt każde z nich miało po sześci skrzydeł wokoło, a wewnątrz były pełne oczu, we dnie i w nocy bez przestanku mówiąc: Święty, święty, święty Pan, Bóg wszechmogący, który był i jest i przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '8', '8', 'A gdy ony źwierzęta dawały chwałę i cześć i dziękczynienie temu, który siedział na stolcu i żywie na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '9', '9', 'Upadali czterzej a dwadzieścia starszych przed onym, który siedział na stolcu i chwałę dawali żywiącemu na wieki wieków i zarzucali korony swe przed stolec, mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '3', '10', '10', 'Godzieneś Panie wziąć chwałę i cześć i moc, abowiemeś ty stworzył wszytki rzeczy, a dla wolej twojej zstały się i stworzone są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '0', '0', 'Zatymem widział na prawicy tego, który siedział na stolcu księgi napisane wewnątrz i z wierzchu, zapieczętowane siedmią pieczęci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '1', '1', 'I widziałem Anioła mocnego, który głosem wielkim opowiedał: Któż jest godzien otworzyć księgi i odjąć pieczęci ich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '2', '2', 'A żaden nie mógł ani w niebie, ani na ziemi, ani pod ziemią otworzyć ksiąg, ani na nie patrzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '3', '3', 'Jam tedy barzo płakał, iż żaden nie był nalezion godnym aby otworzył i czytał księgi, ani na nie patrzyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '4', '4', 'Tedy jeden z starszych rzekł mi: Nie płacz, oto zwyciężył on Lew z pokolenia Juda plemię Dawidowe, aby otworzył księgi i odjął ich siedm pieczęci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '5', '5', 'A takem patrzył, a oto między stolcem i cztermi źwierzęty i między starszemi baranek stojąc jako zabity, mając siedm rogów i oczy siedm, którzy są siedm duchów Bożych posłanych na wszytkę ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '6', '6', 'Ten przyszedł i wziął księgi z prawice tego, który na stolcu siedzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '7', '7', 'A gdy wziął księgi, ony czworo źwierząt i oni czterzej a dwadzieścia starszy upadli przed Barankiem, mając każdy arfy i bańki złote pełne kadzenia, które są modlitwy świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '8', '8', 'I zaśpiewali nową pieśń mówiąc: Godzieneś wziąć księgi i otworzyć pieczęci ich, abowiem zabit jesteś i odkupiłeś nas Bogu przez krew twoję ze wszytkiego pokolenia i języka i ludu i narodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '9', '9', 'I uczyniłeś nas Bogu naszemu królmi i kapłany i będziemy królować na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '10', '10', 'Tedym patrzył i słyszałem około stolca i źwierząt i starszych głos wiela Aniołów, których było tysiąc kroć sto tysięcy i dziesięć kroć sto tysięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '11', '11', 'Mówiących głosem wielkim: Godzien jest Baranek on zabity wziąć moc i bogactwa i mądrość i siłę i cześć i chwałę i błogosławieństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '12', '12', 'A wszelkie stworzenie, które jest na niebie i na ziemi i pod ziemią i w morzu i wszytki rzeczy, które w nich są, słyszałem mówiące: Siedzącemu na stolcu i Barankowi niech będzie błogosławieństwo i cześć i chwała i moc na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '4', '13', '13', 'A czworo źwierząt mówiły: Amen. A czterzej i dwadzieścia starszy upadli na oblicze swoje i chwałę dawali temu, który żywie na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '0', '0', 'Zatymem patrzył gdy otworzył Baranek pierwszą z pieczęci i słyszałem jedno ze czterzech zwierząt mówiące jakoby głos gromu: Chodź, a oglądaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '1', '1', 'Ujźrzałem tedy, a oto koń biały, a ten, który na nim siedział, miał łuk i dana mu jest korona i wyszedł zwycięzca, aby zwyciężył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '2', '2', 'A gdy otworzył wtórą pieczęć, słyszałem wtóre zwierzę mówiące: Chodź, a oglądaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '3', '3', 'I wyszedł drugi koń rydzy, a temu, który na nim siedział, dano jest, aby odjął pokój z ziemie, aby jedni drugie zabijali, a dan mu jest miecz wielki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '4', '4', 'A gdy otworzył trzecią pieczęć, słyszałem trzecie zwierzę mówiące: Chodź, a oglądaj. Patrzyłem tedy, a oto koń wrony, a który na nim siedział, miał szalę w ręce swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '5', '5', 'I słyszałem głos w pośrzodku czworga zwierząt mówiąc: Ćwierć pszenice za grosz, a trzy ćwierci jęczmienia za grosz, a nie czyń szkody olejowi i winu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '6', '6', 'A gdy otworzył czwartą pieczęć, słyszałem głos czwartego zwierzęcia mówiąc: Chodź, a oglądaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '7', '7', 'Tedym patrzył, a oto płowy koń, a tego, który siedział na nim, imię było śmierć, a piekło szło za nią i dana jest im moc nad czwartą częścią ziemie, aby mordowali mieczem i głodem i śmiercią i zwierzęty ziemskiemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '8', '8', 'Potym, gdy otworzył piątą pieczęć, widziałem pod ołtarzem dusze tych, którzy zabici byli dla słowa Bożego i dla świadectwa, którego bronili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '9', '9', 'I wołały głosem wielkim mówiąc: Pókiż Panie, któryś jest święty i prawdziwy, nie sądzisz i nie mścisz się krwie naszej nad tymi, którzy mieszkają na ziemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '10', '10', 'Tedy dane są każdemu szaty białe i powiedziano im, aby odpoczywali jeszcze na mały czas, ażby się też napełnili towarzysze ich i bracia ich, którzy mają być zabici jako i oni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '11', '11', 'Zatymem wejźrzał gdy otworzył szóstą pieczęć, oto trzęsienie ziemie wielkie zstało się, a słońce było czarne jako wór włosiany, a księżyc zstał się jako krew.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '12', '12', 'I gwiazdy z nieba upadły, jako figowe drzewo porzuca niedojrzałe figi swe, gdy od wiatru wielkiego zatrzęsione bywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '13', '13', 'A niebo ustąpiło jako księgi zwinione, a wszelka góra i wyspy z swoich miejsc przeniesione są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '14', '14', 'A królowie ziemie i książęta i bogaci i rotmistrze i mocarze i wszyscy, tak słudzy, jako i wolni, pokryli się w jamy i w skały gór.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '15', '15', 'I rzekli górom i skałom: Upadnicie na nas i zakryjcie nas od oblicza tego, który siedzi na stolcu i od gniewu Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '5', '16', '16', 'Abowiem przyszedł dzień on wielki. A któż się ostać może?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '0', '0', 'Potymem widział czterech Aniołów stojących na czterech węgłoch ziemie, a oni trzymają cztery wiatry ziemie, aby nie wiał wiatr na ziemie, ani na morze, ani na żadne drzewo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '1', '1', 'I widziałem Anioła wstępującego od wschodu słońca, mającego pieczęć Boga żywego, który wołał głosem wielkim ku czterem Aniołom, którym dano jest szkodzić ziemi i morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '2', '2', 'Mówiąc: Nie szkódźcie ani ziemi, ani morzu, ani drzewam, aż zapieczętujemy sługi Boga naszego na czelech ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '3', '3', 'I słyszałem liczbę zapieczętowanych, sto i czterdzieści i cztery tysiące były zapieczętowane, z wszelkiego pokolenia synów izraelskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '4', '4', 'Z pokolenia Juda dwanaście tysięcy zapieczętowanych. Z pokolenia Ruben dwanaście tysięcy zapieczętowanych. Z pokolenia Gad dwanaście tysięcy zapieczętowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '5', '5', 'Z pokolenia Asser dwanaście tysięcy zapieczętowanych. Z pokolenia Neftali dwanaście tysięcy zapieczętowanych. Z pokolenia Manasse dwanaście tysięcy zapieczętowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '6', '6', 'Z pokolenia Symeon dwanaście tysięcy zapieczętowanych. Z pokolenia Lewi dwanaście tysięcy zapieczętowanych. Z pokolenia Isaschar dwanaście tysięcy zapieczętowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '7', '7', 'Z pokolenia Zabulon dwanaście tysięcy zapieczętowanych. Z pokolenia Józef dwanaście tysięcy zapieczętowanych. Z pokolenia Benjamin dwanaście tysięcy zapieczętowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '8', '8', 'Potymem patrzył, a oto było zgromadzenie wielkie, którego nie mógł nikt zliczyć, ze wszech narodów i pokolenia i ludzi i języków, którzy stali przed stolcem i przed oblicznością Baranka, obleczeni w szaty białe, a palmy w ręku ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '9', '9', 'I wołali głosem wielkim mówiąc: Zbawienie jest od Boga naszego, który siedzi na stolcu i od Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '10', '10', 'A wszyscy Aniołowie stali wszędy około stolca i starszych i czworga zwierząt i upadli przed stolcem na oblicze swe i chwałę dali Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '11', '11', 'Mówiąc: Amen, dobrorzeczenie i chwała i mądrość i dzięk czynienie, cześć i moc i siła Bogu naszemu na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '12', '12', 'I odpowiedział jeden z starszych, mówiąc mi: Ci, którzy są obleczeni w szaty białe, którzyż są i skąd przyszli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '13', '13', 'I rzekłem mu: Panie ty wiesz. I powiedział mi: Ci są, którzy przyszli z ucisku wielkiego i omyli szaty swoje i pobielili je we krwi Barankowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '14', '14', 'A przeto są przed stolcem Bożym i służą mu we dnie i w nocy w kościele jego, a ten, który siedzi na stolcu będzie mieszkał z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '15', '15', 'Nie będą dalej łaknąć, ani będą więcej pragnąć, ani na nie słońce uderzy, ani żadna gorącość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '6', '16', '16', 'Abowiem Baranek, który jest w pośrzodku stolca, będzie je pasł i będzie je prowadził do żywych źródeł wód i otrze Bóg wszelaką łzę z oczu ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '0', '0', 'A zatym gdy otworzył siódmą pieczęć, zstało się milczenie na niebie, jakoby pół godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '1', '1', 'I ujźrzałem siedm onych Aniołów, którzy stoją przed Bogiem i dane im są siedm trąb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '2', '2', 'Tedy drugi Anioł przyszedł i stanął przed ołtarzem, mając kadzilnicę złotą, a dano mu wiele kadzenia, aby ofiarował z modlitwami wszech świętych na ołtarzu złotym, który jest przed stolcem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '3', '3', 'I wstąpił dym kadzenia z modlitwami świętych z ręki Anioła do obliczności Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '4', '4', 'Potym wziął Anioł kadzilnicę i napełnił ją ogniem z ołtarza i porzucił na ziemię i zstały się głosy i gromy i łyskawice i trzęsienia ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '5', '5', 'A siedm Aniołów, którzy mieli siedm trąb, nagotowali się, aby trąbili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '6', '6', 'Pierwszy tedy Anioł zatrąbił i zstał się grad i ogień zmieszane ze krwią i rzucone są na ziemię, a trzecia część drzew spalona jest i wszelkie siano zielone spalone jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '7', '7', 'Potym wtóry Anioł zatrąbił, a jakoby wielka góra ogniem pałająca rzucona jest w morze i zstała się krwią trzecia część morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '8', '8', 'I pozdychała trzecia część zwierząt stworzonych, które były w morzu, dusze mających i trzecia część okrętów poginęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '9', '9', 'Tedy Anioł trzeci zatrąbił i spadła z nieba gwiazda wielka, na kształt pochodniej gorająca i upadła na trzecią część rzek i na źrzódła wód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '10', '10', 'A imię gwiazdy jest rzeczono piołyn. Obróciła się tedy trzecia część wód w piołyn, a wiele ludzi pomarło od wód, iż się były gorzkiemi zstały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '11', '11', 'Potym czwarty Anioł zatrąbił i uderzona jest trzecia część słońca i trzecia część miesiąca i trzecia część gwiazd tak, iż zaćmiona jest trzecia część ich i dnia, aby nie świecił i nocy także.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '7', '12', '12', 'I patrzyłem a usłyszałem Anioła lecącego w pośrzodku nieba, a on rzekł głosem wielkim: Biada, biada, biada mieszkającym na ziemi od innych głosów trąby trzech Aniołów, którzy zatrąbią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '0', '0', 'Tedy piąty Anioł zatrąbił i widziałem gwiazdę, która spadła z nieba na ziemię, a dan jest jej klucz studniej przepaści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '1', '1', 'Otworzyła tedy studnię przepaści i wstąpił dym z studnie jako dym pieca wielkiego i zaćmione jest słońce i powietrze od dymu studniej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '2', '2', 'I wyszła z dymu studnicy szarańcza na ziemię, a dana jest jej moc, jako mają moc niedźwiadkowie ziemscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '3', '3', 'A zakazano jej jest, żeby nie szkodziła trawie ziemie, ani żadnej rzeczy zielonej, ani żadnemu drzewu, ale tylko ludziam, którzy nie mają pieczęci Bożej na czelech swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '4', '4', 'A dano jej jest, aby ich nie zabijała, ale aby byli męczeni przez pięć miesięcy, a iżby męczenie ich było, jako męczenie niedźwiadkowe, gdy człowieka obrazi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '5', '5', 'A tak onych dni ludzie śmierci szukać będą, ale nie najdą jej i będą żądać umrzeć, ale uciecze śmierć od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '6', '6', 'A kształty szarańczej były podobne koniom gotowym ku bitwie, a na głowach jej były korony podobne złotu i oblicza jej jako oblicza człowiecze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '7', '7', 'I miała włosy jako włosy niewieście, a zęby jej były jako lwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '8', '8', 'Miała też pancerze jako pancerze żelazne, a głos skrzydeł jej jako tęten wozów, gdy wiele koni zbiegają się ku bitwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '9', '9', 'A miała ogony podobne niedźwiadkom i były żądła w ogoniech jej, a moc jej była szkodzić ludziam przez pięć miesięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '10', '10', 'Przy tym ma nad sobą króla postawionego Anioła przepaści, któremu imię po żydowsku Abbadon, a po grecku Apollijon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '11', '11', 'Biada jedna przeszła, a oto idą jeszcze dwie biedy potym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '12', '12', 'Tedy szósty Anioł zatrąbił i słyszałem głos jeden ze czterech rogów ołtarza złotego, który jest przed oczyma Bożemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '13', '13', 'Mówiący szóstemu Aniołowi, który miał trąbę: Rozwiąż onych czterech Aniołów uwiązanych u wielkiej rzeki Eufrates.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '14', '14', 'Rozwiązani są tedy oni czterej Aniołowie zgotowani na godzinę i na dzień i na miesiąc i na rok, aby pomordowali trzecią część ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '15', '15', 'I była liczba wojska jezdnego dwieściekroć tysiąc tysięcy, abowiemem słyszał liczbę ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '16', '16', 'Takżem też widział konie w widzeniu i ty, którzy na nich siedzieli mając pancerze ogniste i hiacyntowe i siarczane, a były głowy koni jako łby lwie, a z gęby ich wychodził ogień i dym i siarka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '17', '17', 'Od tych trzech zamordowana jest trzecia część ludzi, to jest od ognia i od dymu i od siarki, które wychodziły z gęby ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '18', '18', 'Abowiem ich moc jest w gębie ich i w ogonach ich, bo ich ogony wężom są podobne, mając głowy, któremi szkodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '19', '19', 'A ini ludzie, którzy nie są pobici tymi plagami, nie upamiętali się od uczynków rąk swych, aby się nie kłaniali diabłom i bałwanom złotym i srebrnym i miedzianym i kamiennym i drewnianym, którzy ani widzieć mogą, ani słyszeć, ani chodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '8', '20', '20', 'Ani się upamiętali z mężobójstw swoich, ani od trucizn swoich, ani od nieczystości swoich, ani od złodziejstw swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '0', '0', 'Tedym widział drugiego Anioła mocnego zstępującego z nieba, obłokiem obleczonego, a tęcza była na głowie jego, a oblicze jego było jako słońce, a nogi jego jako słupy ogniste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '1', '1', 'Ktemu miał w ręce swej książki otworzone i postawił nogę swoję prawą na morzu, a lewą na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '2', '2', 'I zawołał głosem wielkim, jako lew ryczy, a gdy zawołał, mówiły siedm gromów głosy swoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '3', '3', 'A gdy siedm gromów mówiły głosy swoje, miałem był napisać, alem usłyszał głos z nieba, mówiąc mi: Zapieczętuj co mówiły siedm gromów, a tego nie pisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '4', '4', 'A Anioł, któregom widział stojącego na morzu i na ziemi, podniósł rękę swoję ku niebu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '5', '5', 'I przysiągł na tego, który żywie na wieki wieków, który stworzył niebo i to co w nim jest i ziemię i to co w niej jest i morze i to co w nim jest, że czasu już nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '6', '6', 'Ale we dni głosu Anioła siódmego, gdy pocznie trąbić, ma się dokonać tajemnica Boża, jako to oznajmił sługom swoim, prorokom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '7', '7', 'A głos, który był słyszał z nieba, powtóre mówił ze mną i rzekł: Idź, a weźmi książki otworzone, które są w ręce Anioła stojącego na morzu i na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '8', '8', 'Poszedłem tedy do Anioła, mówiąc mu aby mi dał książki. I rzekł mi: Bierz, a zjedz je i będąć gorzkie żołądkowi twemu, ale w uściech twych słodkie będą jako miód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '9', '9', 'Wziąłem tedy książki z ręki Anioła i zjadłem je i były w uściech moich słodkie jako miód, ale gdym je zjadł, brzuch mój zstał się gorzkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '9', '10', '10', 'Tedy rzekł mi: Musisz zasię prorokować przed narody i Pogany i języki i wielem królów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '0', '0', 'Potym dana mi jest trzcina podobna lasce i stanął przy mnie Anioł mówiąc: Wstań, a zmierz kościół Boży i ołtarz i ty, którzy w nim chwalą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '1', '1', 'Ale sień, która jest przed kościołem, wyrzuć precz, a nie mierz jej, abowiem dana jest poganom, a miasto święte podepcą przez czterdzieści dwa miesiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '2', '2', 'Lecz dam je dwiema świadkom moim, którzy prorokować będą przez tysiąc dni dwie ście i siedmdziesiąt, obleczeni w wory.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '3', '3', 'Ty są ony dwoje drzewa oliwne i dwa lichtarze postawione przed oblicznością Pana ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '4', '4', 'A jeśliby im kto chciał szkodzić, ogień wychodzi z ust ich, który pożyra nieprzyjaciele ich, abowiem jeśliby im kto chciał szkodzić, tak musi być zabit.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '5', '5', 'Ci mają moc zamykać niebo, co by deszcz nie szedł we dni proroctwa ich i mają moc nad wodami, aby je w krew odmieniali i uderzyć ziemię wszelaką plagą ileby kroć chcieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '6', '6', 'A gdy dokończą świadectwa swojego, bestia występując z przepaści podniesie walkę przeciwko nim, a zwycięży je i podbije je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '7', '7', 'Trupi też ich polegą w ulicach miasta wielkiego, które jest duchownie rzeczono Sodoma i Egipt, gdzie też Pan nasz był ukrzyżowan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '8', '8', 'I ujźrzą ludzie z pokolenia i z narodów i z języków i z poganów trupy ich przez półczwarta dnia, a nie dadzą trupów ich skłaść w groby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '9', '9', 'A obywatele ziemie będą się weselić z nich i radować się będą i upominki poślą jedni drugim, iż ci dwa prorocy trapili mieszkające na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '10', '10', 'Ale po półczwartu dniu duch żywota od Boga wychodząc, wnidzie w nie i będą stać na nogach swoich, a bojaźń wielka przypadnie na ty, którzy je oglądali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '11', '11', 'Potym słyszeli głos wielki z nieba, mówiąc do nich: Wstąpcie ówdzie. I wstąpili na niebo przez obłok, a oglądali je nieprzyjaciele ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '12', '12', 'I zstało się onej godziny wielkie trzęsienie ziemie, a dziesiąta część miasta upadła i pobite są w onym trzęsieniu ziemie imion ludzi siedm tysięcy, drudzy przestraszeni są i dali chwałę Bogu niebieskiemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '13', '13', 'Biada wtóra przeszła, a oto biada trzecia przyjdzie rychło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '14', '14', 'A tak siódmy Anioł zatrąbił i zstały się głosy wielkie na niebie mówiąc: Królestwa świata zstały się królestwy Pana naszego i Krysta jego, który będzie królował na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '15', '15', 'Tedy oni czterzej a dwadzieścia starszy, którzy przed oblicznością Bożą siedzą na stolcach swych, upadli na oblicza swe i chwałę dali Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '16', '16', 'Mówiąc: Dziękujemy tobie Panie Boże wszechmogący, który jesteś, któryś był i który masz przyść, iżeś wziął moc twą wielką i królowałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '17', '17', 'I rozgniewali się pogani i przyszedł gniew twój i czas umarłych, aby byli sądzeni i abyś oddał zapłatę sługam twoim prorokom i świętym i tym, którzy się boją imienia twego, małym i wielkim. I abyś zagubił ty, którzy psują ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '10', '18', '18', 'Tedy otworzon jest kościół Boży na niebie i widziana jest skrzynia przymierza jego w kościele jego i zstały się łyskawice i głosy i gromy i trzęsienia ziemie i grad wielki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '0', '0', 'I widzian jest znak wielki na niebie: Niewiasta obleczona słońcem, pod której nogami był księżyc, a na głowie korona z gwiazd dwunaście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '1', '1', 'I będąc brzemienna, wołała bolejąc i cięszkości cierpiała ku rodzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '2', '2', 'I widzian jest drugi znak na niebie, a oto smok wielki, rydzy, mając siedm głów i rogów dziesięć, a na głowach jego siedm koron.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '3', '3', 'Którego ogon ciągnął trzecią część gwiazd niebieskich, które zrzucił na ziemię i stanął on smok przed niewiastą, która miała porodzić, aby po porodzeniu syna jej pożarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '4', '4', 'I porodziła syna mężczyznę, który miał rządzić wszytki pogany laską żelazną i zachwycon jest syn jej do Boga i do stolice jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '5', '5', 'A niewiasta uciekła na pustynią, gdzie ma miejsce zgotowane od Boga, aby ją tam karmili przez tysiąc dni i dwieście i sześćdziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '6', '6', 'I zstała się bitwa na niebie. Michał i Aniołowie jego walczyli z smokiem, a smok też walczył i Aniołowie jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '7', '7', 'Ale nie wygrali, ani miejsce ich dalej znalezione jest na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '8', '8', 'I zrzucon jest smok wielki, wąż on starodawny, który jest rzeczon diabeł i szatan, który zwodzi wszystek okrąg ziemie i zrzucon jest na ziemię i Aniołowie jego z nim są zrzuceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '9', '9', 'A słyszałem głos wielki mówiący na niebie: Teraz ci się zstało zbawienie i moc i królestwo Boga naszego i zwierzchność Krysta jego, ponieważ zrzucon jest oskarżyciel braciej naszej, który je oskarżał przed oblicznością Boga naszego we dnie i w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '10', '10', 'Ale oni zwyciężyli go dla krwie Baranka i dla słowa świadectwa i nie litowali dusz swych aż do śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '11', '11', 'A przeto weselcie się nieba i wy, którzy mieszkacie na nich. Biada obywatelom ziemie i morza, abowiem zstąpił czart ku wam z gniewem wielkim przeto, iż wie, że mało czasu ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '12', '12', 'Gdy tedy widział smok, iż był zrzucon na ziemię, prześladował niewiastę która była porodziła mężczyznę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '13', '13', 'Ale dane są niewieście skrzydła dwie, orła wielkiego, aby leciała od obliczności wężowej na puste miejsce swoje, gdzie jest karmiona przez czas i czasy i przez połowicę czasu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '14', '14', 'I wypuścił wąż z gęby swojej za niewiastą wodę jako rzekę, aby ją rzeka porwała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '15', '15', 'Ale ratowała ziemia niewiastę i otworzyła ziemia usta swe i pożarła rzekę, którą był wypuścił smok z gęby swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '16', '16', 'Rozgniewał się tedy smok na niewiastę i szedł, aby walczył z drugimi z plemienia jej, którzy zachowywają przykazania Boże i mają świadectwo Jezusa Krystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '11', '17', '17', 'I stanąłem na piasku morskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '0', '0', 'Tedym widział bestią, a ona z morza występnie, która miała siedm głów i rogów dziesięć, a na rogach jej było dziesięć koron, a na głowach jej imię bluźnierstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '1', '1', 'I była ta bestia, któram widział, podobna lampartowi, a nogi jej jako nogi niedźwiedze, a gęba jej jako gęba lwia. I dał jej smok moc swoję i stolicę swoję i zwierzchność wielką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '2', '2', 'A widziałem jednę z głów jej jakoby na śmierć zabitą, ale rana jej śmiertelna jest uleczona. I dziwując się wszytka ziemia szła za bestią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '3', '3', 'I kłaniali się smokowi, który dał moc bestyjej i kłaniali się bestyjej mówiąc: Któż podobny jest bestyjej i któż z nią walczyć będzie mógł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '4', '4', 'I dane są jej usta mówiące wielkie rzeczy i bluźnierstwa i dana jej jest moc poczynać przez czterdzieści i dwa miesiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '5', '5', 'Otworzyła tedy usta swoje ku bluźnierstwu przeciwko Bogu, aby złorzeczyła imię jego i przybytek jego, i ty, którzy na niebie mieszkają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '6', '6', 'Ktemu dano jest jej walczyć z świętymi i zwyciężyć je, dana też jest jej moc nad wszelkim pokoleniem i językiem i narodem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '7', '7', 'A tak będą się jej kłaniać wszyscy obywatele ziemie, których imiona nie są napisane w księgi żywota, Baranka onego zabitego od założenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '8', '8', 'Jeśli kto ma ucho, niech słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '9', '9', 'Jeśli kto wiedzie w niewolą, w niewolą pójdzie. Jeśliby kto mieczem zabił, musi ten być mieczem zabit. Tuć jest cierpliwość i wiara świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '10', '10', 'Zatymem widział drugą bestyją, a ona wstępnie z ziemie, która miała dwa rogi podobne barankowym, ale mówiła jako smok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '11', '11', 'A sama używa wszelkiej mocy bestyjej przed nią, sprawując to, iż ziemia i obywatele jej kłaniają się bestyjej onej pierwszej, której śmiertelna rana była uleczona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '12', '12', 'I czyni cuda wielkie tak, iż też z nieba ogień zwodzi na ziemię przed ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '13', '13', 'I zwodzi obywatele ziemie przez znaki, które dano jej czynić przed bestią, mówiąc obywatelom ziemie, aby czynili obraz bestyjej, która mieczem raniona była, ale ożyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '14', '14', 'I dano jest jej, aby ducha wlała w bestyją, żeby też mówił obraz bestyjej i czynił to, którzybykolwiek nie kłaniali się obrazowi bestyjej, aby byli zabici.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '15', '15', 'Czyni też to, iżby wszyscy mali i wielcy, bogaci i ubodzy, wolni i niewolnicy, brali piątno na ręce swojej prawej, abo na czelech swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '16', '16', 'A iżby żaden nie mógł kupić ani przedać, jedno ten, który ma piątno, abo imię bestyjej, abo liczbę imienia jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '12', '17', '17', 'Tu jest mądrość. Który ma rozum, niech obliczy liczbę bestyjej: abowiemci jest liczba człowieka, a liczba jej sześć set sześćdziesiąt i sześć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '0', '0', 'Potymem patrzył, a oto Baranek stojąc na górze Syon, a z nim sto czterdzieści i cztery tysiące, mając imię Ojca jego napisane na czelech swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '1', '1', 'I słyszałem głos z nieba jako głos wiela wód i jako głos gromu wielkiego i słyszałem głos śpiewaków grających na arfach swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '2', '2', 'I śpiewających jakoby nową pieśń przed stolicą i przed czworgiem zwierząt i starszymi, ani się żaden mógł onej pieśni nauczyć, oprócz onych sta czterechdziesiąt i czterzech tysięcy, którzy są z ziemie kupieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '3', '3', 'Cić są, którzy się z niewiastami nie pokalali, bo dziewicami są, cić idą za Barankiem gdziekolwiek szedł, cić są z ludzi kupieni, pierworództwo Bogu i Barankowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '4', '4', 'A w ich uściech nie nalazła się zdrada; abowiem nie naganieni są przed stolicą Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '5', '5', 'Zatymem widział drugiego Anioła lecącego przez pośrodek nieba, a on miał Ewanjeliją wieczną, aby ją oznajmił obywatelom ziemie i wszelkiemu narodowi i pokoleniu i językowi i ludowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '6', '6', 'Mówiąc wielkim głosem: BÓJCIE SIĘ BOGA, a chwałę mu dajcie, abowiemci przyszła godzina sądu jego, a chwałę dajcie temu, który czynił niebo i ziemię i morze i źrzódła wód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '7', '7', 'A za nim szedł drugi Anioł, mówiąc: Upadłoć, upadło Babilon, miasto ono wielkie przeto, iż winem gniewu nieczystości swej napoiło wszytki narody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '8', '8', 'A trzeci Anioł szedł za nimi mówiąc głosem wielkim: Jeśliby kto chwałę dał bestyjej i obrazowi jej i przyjął piątno w czele swoim, abo w ręce swej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '9', '9', 'Napije się też ten wina gniewu Bożego, owszem szczerego nalewanego w kubku gniewu jego i będzie męczon ogniem i siarką przed Anioły świętymi i przed Barankiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '10', '10', 'A dym męki ich wstępuje na wieki wieków, ani będą mieć odpocznienia we dnie ani w nocy, którzy się kłaniają bestyjej i obrazowi jej i ktoby przyjął piątno imienia jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '11', '11', 'Tuć jest cierpliwość świętych, tuć są ci, którzy chowają przykazania Boże i wiarę Jezusowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '12', '12', 'Tedym słyszał głos z nieba mówiąc mi: Napisz: Błogosławieni napotym umarli, którzy umierają dla Pana. Takci mówi Duch, ponieważ odpoczywają od prac swoich, a uczynki ich za nimi idą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '13', '13', 'Zatymem patrzył, a oto obłok biały, a na obłoku siedział niektóry podobny człowiekowi, mając na głowie swej koronę złotą, a w ręce swej sierp ostry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '14', '14', 'A drugi Anioł wyszedł z kościoła, wołając głosem wielkim ku temu, który siedział na obłoku: Zapuść sierp twój, a żni, abowiemci przyszła godzina abyś żął, gdyż uschło żniwo ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '15', '15', 'Zapuścił tedy ten, który siedział na obłoku sierp swój na ziemię i jest pożęta ziemia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '16', '16', 'A drugi Anioł wyszedł z kościoła, który jest w niebie i ten mając sierp ostry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '17', '17', 'Potym drugi Anioł wyszedł z ołtarza, który miał moc nad ogniem i zawołał wielkim głosem ku temu, który miał sierp ostry mówiąc: Zapuść sierp twój ostry i zbieraj grona winnice ziemie, bo dojźrzały jagody jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '18', '18', 'Zapuścił tedy Anioł sierp swój ostry na ziemię, i zbierał winnice ziemie i wrzucił w wielką kadź gniewu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '13', '19', '19', 'I jest deptana kadź przed miastem i wyszła krew z kadzi aż do wędzidł końskich, przez tysiąc i sześćset stajań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '0', '0', 'A zatymem widział drugi znak w niebie wielki i dziwny. Siedm Aniołów mających siedm plag ostatecznych, a to iż przez nie wypełnion jest gniew Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '1', '1', 'I widziałem jakoby morze śklane zmieszane z ogniem i ty, którzy zwycięstwo otrzymali nad bestią i nad obrazem jej i nad piątnem jej i nad liczbą imienia jej, stojące u morza śklanego i mając Arfy Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '2', '2', 'Którzy też śpiewali pieśń Mojżesza sługi Bożego i pieśń Barankowę mówiąc: Wielkieć i dziwne są sprawy twoje Panie, Boże wszechmogący, sprawiedliweć są i prawdziwe drogi twoje Królu świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '3', '3', 'Któż się ciebie bać nie będzie Panie, a nie uwielbi imienia twego? Abowiemeś sam święty. Boć wszyscy narodowie przyjdą i przed tobą się kłaniać będą, ponieważ sądy objawione są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '4', '4', 'A potymem patrzył, a oto otworzon jest kościół przybytku świadectwa na niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '5', '5', 'I wyszło z kościoła siedm Aniołów, mając siedm plag, obleczonych szatami ze lnu czystego i białego i przepasanych około piersi złotemi pasy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '6', '6', 'A jedno ze czworga zwierząt dało siedmiam Aniołom siedm baniek złotych pełnych gniewu Boga, który żywie na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '14', '7', '7', 'I napełnion jest kościół dymu, który wyszedł od majestatu Bożego i od mocy jego, a nie mógł nikt wnidź do kościoła, ażby się wypełniły siedm plag siedmi Aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '0', '0', 'Tedym słyszał głos wielki z kościoła, mówiąc siedmi Aniołom: Idźcie, wylejcie siedm baniek gniewu Bożego na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '1', '1', 'Wyszedł tedy pierwszy i wylał bańkę swoję na ziemię i zstała się wielka i szkodliwa rana w ludziech, którzy mieli piątno bestyjej i w tych, którzy się kłaniali obrazowi jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '2', '2', 'Zatym wtóry Anioł wylał bańkę swoję na morze i zstało się jakoby krew trupia i każde zwierzę, które było żywe w morzu zdechło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '3', '3', 'Potym trzeci Anioł wylał bańkę swoję na rzeki i na źrzódła wód i zstały się krwią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '4', '4', 'I słyszałem Anioła wód, który mówił: Sprawiedliwyś Panie, któryś jest i któryś był i święty przeto, iżeś ty rzeczy osądził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '5', '5', 'Iż krew świętych i proroków wylali i napoiłeś je krwią, boć godni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '6', '6', 'I słyszałem drugiego z przybytku świętego, który mówił: I owszem Panie Boże wszechmogący. Prawdziwe i sprawiedliwe są sądy twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '7', '7', 'Potym czwarty Anioł wylał bańkę swoję na słońce i dano mu jest gorącością trapić ludzie przez ogień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '8', '8', 'I zapalili się ludzie wielkim zapaleniem i bluźnili imię Boga, który ma moc nad tymi plagami, a nie upamiętali się, aby mu chwałę dali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '9', '9', 'Zatym piąty Anioł wylał bańkę swoję na stolicę bestyjej i zstało się królestwo jej ciemne i żwali język swój od boleści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '10', '10', 'I bluźnili Boga niebieskiego dla boleści swoich i wrzodów swoich, ani się upamiętali od uczynków swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '11', '11', 'A szósty Anioł wylał bańkę swoję na onę wielką rzekę Eufrates i wyschła woda jej, aby była nagotowana droga królom przychodzącym od wschodu słońca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '12', '12', 'I widziałem z ust smokowych i z ust bestyjej i z ust onego fałszywego proroka trzy nieczyste duchy wychodzące podobne żabam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '13', '13', 'Abowiem są duchy czartów tych, którzy czynią cuda i którzy odchodzą do królów ziemie i wszytkiego świata, aby zebrali je ku bitwie onego wielkiego dnia Boga wszechmogącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '14', '14', 'Oto idę jako złodziej, błogosławiony, który czuje i chowa szaty swoje, aby nie chodził nago, a iżby jego sromoty nie widziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '15', '15', 'Zebrali je tedy na miejsce, które jest rzeczono po żydowsku Armagedon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '16', '16', 'Zatym siódmy Anioł wylał bańkę swoję na powietrze i wyszedł głos wielki z kościoła niebieskiego od stolice mówiąc: Zstało się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '17', '17', 'I zstały się głosy i gromy i łyskawice i zstało się wielkie trzęsienie ziemie jakie nigdy nie było, odkąd ludzie byli na ziemi, a to trzęsienie ziemie było tak wielkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '18', '18', 'I rozerwane jest miasto ono wielkie na trzy części i miasta poganów upadły i Babilon wielkie przyszło na pamięć przed Boga, aby jemu dał kubek wina popędliwości gniewu swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '19', '19', 'I wszelka wyspa uciekła, a nie nalazły się góry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '15', '20', '20', 'I wielki grad jako cetnarów i spadł z nieba na ludzi, a ludzie bluźnili Boga dla plagi gradu przeto, iż plaga jego barzo wielka była.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '0', '0', 'Przyszedł tedy jeden z siedmi Aniołów, którzy mieli siedm baniek i mówił ze mną rzekąc mi: Chodź sam, okażęć potępienie wielkiej nierządnice, która siedzi nad wodami wielkiemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '1', '1', 'Z którą nieczystości popełnili królowie ziemie i upili się winem nieczystości jej obywatele ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '2', '2', 'A gdy mię przez Ducha na puszczą przeniósł, widziałem niewiastę, która siedziała na czerwonej bestyjej, pełnej imion bluźnierstwa, mającej siedm głów i rogów dziesięć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '3', '3', 'A była niewiasta ona obleczona w szarłatne a czerwone szaty i uzłocona złotem i drogim kamieniem i perłami, mając kubek złoty w ręce swej, pełen brzydliwości i plugactwa nieczystości jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '4', '4', 'A na czele swoim imię napisane: TAJEMNICA, Babilon wielkie, matka nieczystości i brzydliwości ziemie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '5', '5', 'I widziałem niewiastę onę, pijaną krwią świętych i krwią męczenników Jezusowych, a obaczywszy ją, zadziwowałem się podziwienim wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '6', '6', 'Tedy rzekł ku mnie Anioł: Czemu się dziwujesz? Powiemci ja tobie tajemnicę niewiasty i bestyjej, która ją nosi i która ma siedm głów i rogów dziesięć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '7', '7', 'Bestia, którąś widział, byłać a nie jest i mać wstąpić z przepaści a iść na zginienie i zadziwują się, którzy mieszkają na ziemi (których imiona nie są napisane w księgach żywota od założenia świata) widząc bestyją, która była, a nie masz jej, a wżdy jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '8', '8', 'Tuć rozum, który ma mądrość. Siedm głów, jestci siedm gór, na których niewiasta siedzi, a sąć siedm królowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '9', '9', 'Pięć ich upadło, jeden jest, a jeszcze drugi nie przyszedł, a gdy przyjdzie, ma trwać przez mały czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '10', '10', 'A bestyja, która była, a nie jest, ten jest ósmy i jest jeden z siedmi, a idzie na zginienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '11', '11', 'A dziesięć rogów, któreś widział, jestci dziesięć królów, którzy królestwa jeszcze nie wzięli, ale wezmą moc jako królowie na jednę godzinę z bestyją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '12', '12', 'Cić jednę myśl mają i moc i zwierzchność swoję bestyjej podadzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '13', '13', 'Cić z Barankiem walczyć będą, a Baranek je zwycięży przeto, iż jest Panem panów i Królem królów, a którzy z nim wezwani i wybrani i wierni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '14', '14', 'Potym rzekł mi: Wody, któreś widział, gdzie nierządnica siedzi, sąć ludowie i tłuszcze i narody i języki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '15', '15', 'A dziesięć rogów, któreś widział na bestyjej, cić w nienawiści mieć będą nierządnicę i uczynią ją spustoszoną i nagą i ciało jej zjedzą i ogniem ją spalą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '16', '16', 'Abowiem Bóg dał w serca ich, aby czynili co się mu podoba i aby się zgadzali i dali królestwo swoje bestyjej, ażby się wypełniły słowa Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '16', '17', '17', 'A niewiasta, którąś widział, jestci miasto ono wielkie, które ma królestwo nad królmi ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '0', '0', 'Potymem widział drugiego Anioła zstępującego z nieba, który miał moc wielką, tak iż się ziemia oświeciła od chwały jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '1', '1', 'A zawołał barzo wielkim głosem mówiąc: Upadłoć upadło Babilon ono wielkie i zstało się mieszkaniem Czartów i ciemnicą wszego ducha nieczystego i ciemnicą wszelkiego ptaka nieczystego i nienawidzonego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '2', '2', 'Przeto, iż wino gniewu nieczystości jej piły wszytki narody, a królowie ziemie nieczystość z nią popełnili i kupcy ziemie z majętności rozkoszy jej wzbogacieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '3', '3', 'I słyszałem drugi głos z nieba mówiąc: Wynidźcie z niej ludzie mój, abyście nie byli uczestnikami grzechów jej i aby się wam z plag jej nie dostało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '4', '4', 'Abowiemci zgromadzone grzechy jej doszły aż do nieba i wspomniał Bóg na nieprawości jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '5', '5', 'Oddajcie jej jako też ona wam dała i we dwójnasób jej nagródźcie wedle uczynków jej: w kubku, w którym wam pić dała, tyle jej dwakroć pić dajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '6', '6', 'Jako się wiele chwaliła i urozkoszował, tak wielkie męczenie i smętek jej dajcie przeto, iż w sercu swym mówi: Siedzęć królowa i nie jestemci wdową i żałości nie ujźrzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '7', '7', 'Dlatego w jeden dzień przyjdą plagi jej, śmierć i smętek i głód i ogniem będzie spalona, iż mocny jest Pan Bóg, który ją osądzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '8', '8', 'Tedy jej będą płakać i narzekać na nie królowie ziemie, którzy z nią nieczystości się dopuścili i roskoszowali, gdy ujźrzą dym zapalenia jej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '9', '9', 'Z daleka stojąc prze bojaźń męki jej i mówiąc: Biada, biada, miasto ono wielkie Babilon, miasto ono mocne, którego sąd w jednę godzinę przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '10', '10', 'Ktemu i kupcy ziemie będą płakać i narzekać na nię przeto, iż towaru ich żaden nie kupuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '11', '11', 'Towaru złota i śrebra i kamienia drogiego, pereł, lnu cienkiego, szarłatu, jedwabiu i kosztownej farby czerwonej i wszelkiego drzewa tyinowego i wszelkiego naczynia z kości słoniowej i wszelkiego naczynia z drzewa nakosztowniejszego i z miedzi i z żelaza i z marmuru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '12', '12', 'I cynamonu i kadzenia i maści i kadzidła i wina i oleju i mąki czystej i pszenice i dobytku i owiec i koni i kolebek i niewolników i dusz ludzkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '13', '13', 'I owoce pożądliwości dusze twej odeszły od ciebie i wszytki rzeczy tłuste i znamienite odeszły od ciebie, ani ich dalej znajdziesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '14', '14', 'A tak kupcy tych rzeczy wzbogaciwszy się, od niej z daleka będą stali, dla bojaźni męki jej płacząc i lamentując.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '15', '15', 'I mówiąc: Biada, biada. Miasto ono wielkie, które było obleczone cienkim lnem i szarłatem i jedwabiem czerwonym i było uzłocone złotem i kamienim drogim i perłami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '16', '16', 'Jakoż jednej godziny opuszczone są tak wielkie bogactwa? Ktemu wszelki sprawca i wszelkie zgromadzenie, które bywa na okręciech i żeglarze i którzykolwiek z morza zysku nabywają, z daleka stać będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '17', '17', 'I zawołają, widząc dym zapalenia jego i rzeką: Któreż podobne było miastu temu wielkiemu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '18', '18', 'I posypawszy głowy prochem, zawołają płacząc i narzekając i mówiąc: Biada, biada, miasto ono wielkie, w którym się wzbogacili wszyscy, którzy okręty na morzu mieli, z zacności tego, które jednej godziny spustoszało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '19', '19', 'Rozraduj się nad nim, niebo i święci Apostołowie i prorocy, iż Bóg sądził i z niego dla was pomstę wziął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '20', '20', 'Tedy podniósł niektóry Anioł mocny kamień, jakoby młyński wielki i wrzucił w morze mówiąc: Takim gwałtem wrzucono będzie ono miasto wielkie Babilon i już dalej nie będzie naleziono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '21', '21', 'I głos lutnistów i śpiewaków i piszczków i trębaczów dalej w tobie słyszan nie będzie i żadny rzemieśnik któregokolwiek rzemiosła nie będzie dalej nalezion w tobie i głos młyna nie będzie dalej słyszan w tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '22', '22', 'A światłość lampy nie będzie się dalej świeciła w tobie i głos oblubieńca i oblubienice nie będzie dalej słyszan w tobie przeto, iż kupcy twoi byli książęta ziemie i czarami twemi są wszytki narody zwiedziony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '17', '23', '23', 'Ktemu w nim jest naleziona krew proroków i świętych i wszytkich, którzy są pobici na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '0', '0', 'Zatymem słyszał głos wielki zgromadzenia wielkiego mówiącego w niebie: Halleluja. Zbawienie i cześć i chwała i moc Panu, Bogu naszemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '1', '1', 'Przeto, iż prawdziwe i sprawiedliwe są sądy jego, a nierządnicę onę wielką, która psowała ziemię nieczystością swą, osądził i z ręki jej pomścił się krwie służebników swych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '2', '2', 'A powtóre rzekli: Halleluja, a dym jej wstąpił na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '3', '3', 'I upadli czterzej i dwadzieścia starszych i czworo zwierząt i chwalili Boga, który siedzi na stolicy, mówiąc: Amen. Halleluja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '4', '4', 'Tedy głos z stolice wyszedł mówiąc: Chwalcie Boga naszego wszyscy słudzy jego i którzy się go boicie, mali i wielcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '5', '5', 'I słyszałem jako głos wielkiego zgromadzenia i jako głos wiela wód i jako głos wielkich gromów mówiąc: Halleluja, abowiem królował Pan Bóg nasz wszechmogący.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '6', '6', 'Weselmy się i radujmy się, a jemu chwałę dajmy, iż przyszło wesele Barankowy, a żona jego zgotowała się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '7', '7', 'I dano jest jej aby była obleczona lnem czystym i znamienitym, bo ten len są usprawiedliwienia świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '8', '8', 'Tedy mi rzekł: Napisz: Błogosławieni, którzy na wieczerzą wesela Barankowego wezwani są. I rzekł mi: Tyć słowa Boże są prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '9', '9', 'I upadłem przed nogi jego abym go chwalił. Ale on mi rzekł: Patrz abyś nie czynił. Jestemci sługa z tobą jeden z braciej twojej, którzy mają świadectwo Jezusowe. Chwal Boga, abowiem świadectwo Jezusowe jest duchć proroctwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '10', '10', 'Zatymem widział niebo otworzone, a oto koń biały, a który na nim siedział, był rzeczon wierny i prawdziwy, a który sprawiedliwie sądzi i walczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '11', '11', 'A oczy jego były jako płomień ogniowy, a na głowie jego było wiele koron, i miał imię napisane, którego nikt nie wie, jedno sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '12', '12', 'A był obleczon w szatę farbowaną krwią, a imię jego rzeczone jest słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '13', '13', 'I szły za nim wojska, które są na niebie na koniech białych, obleczone lnem białym i czystym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '14', '14', 'A z ust jego wychodził miecz ostry, aby im bił narody, abowiemci je on będzie rządził laską żelazną, gdyż jest ten, który depce kadź wina popędliwości i gniewu Boga wszechmogącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '15', '15', 'I miał na szacie i na udzie swym imię napisane: Król królów i Pan panujących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '16', '16', 'Tedym widział Anioła stojącego na słońcu, który zawołał głosem wielkim mówiąc wszem ptakom, które latały przez pośrodek nieba: Chodźcie i zbierajcie się na wieczerzą wielkiego Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '17', '17', 'Abyście jedli ciała królów i ciała rotmistrzów i ciała mocarzów i ciała koni i tych, którzy na nich siedzą i ciała wszytkich wolnych i niewolników, małych i wielkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '18', '18', 'Zatymem widział bestyją onę i króle ziemie i wojska ich zebrane, aby walczyli z tym, który siedział na koniu i z wojskiem jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '19', '19', 'Ale uchwycona jest bestyja i z nią fałszywy prorok, który czynił cuda przed nią, któremi zwodził ty, którzy przyjęli piątno bestyjej i którzy się kłaniali obrazowi jej. Ci dwa zrzuceni byli żywi w jezioro ognia gorające siarką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '18', '20', '20', 'A drudzy pobici są mieczem tego, który siedzi na koniu wychodzącym z ust jego, a wszyscy ptacy nasyceni są mięsem ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '0', '0', 'A potymem widział Anioła zstępującego z nieba, który miał klucz przepaści i łańcuch wielki w ręce swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '1', '1', 'I uchwycił smoka, węża onego starego, który jest diabeł i szatan i związał go na tysiąc lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '2', '2', 'A wrzucił go w przepaść i zamknął go i zapieczętował nad nim, aby nie zwodził dalej narodów, ażby się wypełniły tysiąc lat, abowiem musi potym być rozwiązan na mały czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '3', '3', 'Zatymem widział stolice i siedzieli na nich, a sąd jest im dan i dusze tych, którzy pościnani są dla świadectwa Jezusowego i dla słowa Bożego, a którzy nie chwalili bestyjej, ani obrazu jej, ani przyjęli piątna na czelech swych, abo na rękach swoich i będą żyć i królować z Krystusem tysiąc lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '4', '4', 'A drudzy z martwych nie ożywą aż się wypełnią tysiąc lat, toć jest pierwsze zmartwych wstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '5', '5', 'Błogosławiony i święty, który ma część w pierwszym zmartwychwstaniu, abowiem nad tymi wtóra śmierć nie ma mocy i owszem będą kapłany Boga i Krystusa i będą królować z nim tysiąc lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '6', '6', 'A gdy się wypełnią tysiąc lat, będzie rozwiązan szatan z ciemnice swojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '7', '7', 'I wynidzie, aby zwodził narody, które są na czterzech węgłoch ziemie Gog i Magog, aby je zbierał na walkę, których liczba jest jako piasek morski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '8', '8', 'Wstąpili tedy na szerokość ziemie i obstąpili wojsko świętych i miasto umiłowane, ale zstąpił ogień od Boga z nieba, który je pożarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '9', '9', 'A diabeł, który je zwodził wrzucon jest w jezioro ognia i siarki, gdzie też bestyja i fałszywy prorok będą męczeni we dnie i w nocy na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '10', '10', 'Tedym widział stolicę wielką białą i niektórego siedzącego na niej, przed którego obliczem uciekła ziemia i niebo, a miejsce ich nie jest znalezione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '11', '11', 'I widziałem umarłe wielkie i małe stojących przed oblicznością Bożą, a otworzone są księgi i drugie księgi otworzone są, to jest, księgi żywota. I byli osądzeni umarli wedle tych rzeczy, które były napisane w księgach podług uczynków ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '12', '12', 'I wróciło morze umarłe, które miało, śmierć też i piekło wróciło umarłe, które miały i był sąd o każdym wedle uczynków ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '13', '13', 'A piekło i śmierć wrzuceni są w jezioro ognia i tać jest śmierć wtóra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '19', '14', '14', 'A który nie jest znalezion napisany w księgach żywota, wrzucon jest w jezioro ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '0', '0', 'Potymem widział niebo nowe i ziemię nową, abowiem pierwsze niebo i pierwsza ziemia odeszła była, a morza już nie było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '1', '1', 'A ja, Jan, widziałem święte miasto Jeruzalem nowe zstępujące z nieba, od Boga zgotowane, jako oblubienicę ubraną mężowi swemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '2', '2', 'I słyszałem głos wielki z nieba, mówiąc: Oto przybytek Boży jest z ludźmi i będzie mieszkał z nimi, a oni będą narodowie jego, a sam Bóg będzie z nimi bogiem ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '3', '3', 'I otrze Bóg wszelką łzę z oczu ich, a śmierć dalej nie będzie ani smętek, ani krzyk i boleść dalej nie będzie. Abowiem pierwsze rzeczy przeszły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '4', '4', 'I rzekł mi ten, który siedział na stolicy: Oto nowe czynię wszytki rzeczy. I rzekł mi: Napisz, abowiem ty słowa wierne są i prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '5', '5', 'A rzekł mi: Zstałoć się. Jam jest Alfa i Omega początek i koniec. Ja pragnącemu dam darmo źrzódła wody żywej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '6', '6', 'Ktoby zwyciężył, ten otrzyma dziedzicznie wszytki rzeczy i będę jemu Bogiem, a on mi będzie synem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '7', '7', 'A bojaźliwym i niedowiarkom i obmierzłym i mężobójcam i nieczystym i trującym i bałwochwalcam i wszytkim kłamcam część będzie w jezierze gorającym ogniem i siarką, a tać jest śmierć wtóra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '8', '8', 'Tedy przyszedł do mnie jeden z onych siedmi Aniołów, którzy mieli siedm baniek pełnych siedmi plag ostatecznych i mówił ze mną i rzekąc: Chodź sam, okażęć oblubienicę żonę Barankowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '9', '9', 'I wziął mnie przez ducha na górę wielką i wysoką i okazał mi miasto wielkie święte ono Jeruzalem zstępujące od Boga z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '10', '10', 'Mające chwałę Bożą, którego światłość podobna była kamieniowi nadroższemu jaspisowi na kształt kryształu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '11', '11', 'I miało mur wielki i wysoki, mający bran dwanaście i w branach dwanaście Aniołów i imiona napisane, które są imiona dwunaście pokolenia synów izraelskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '12', '12', 'Od wschodu trzy brany, od północy brany trzy, od południa brany trzy, od zachodu brany trzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '13', '13', 'A mur miasta miał gruntów dwanaście, w których były imiona dwanaście Apostołów Barankowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '14', '14', 'A ten, który mówił ze mną, miał trzcinę złotą, aby mierzył miasto i bramy jego i mur jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '15', '15', 'Miasto też jest na cztery węgły, którego dłuż tylka jest jako i szerz i mierzył miasto trzciną na dwunaście tysięcy stajań i równe są długość, szerz i wysokość jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '16', '16', 'I mierzył mur jego, sto czterdzieści i cztery łokcie miary człowieczej, która jest Aniołowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '17', '17', 'A budowanie muru jego był jaspis, samo też miasto złote szczyre, podobne śkłu czystemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '18', '18', 'A grunty muru miasta ozdobione wszelakim kamieniem drogim: Pierwszy grunt jaspis, wtóry szafir, trzeci kalcedon, czwarty smaragd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '19', '19', 'Piąty sardoniks, szósty sardyns, siódmy krysolitus, ósmy beryllus, dziewiąty topazjus, dziesiąty chrysoprassus, jedennasty hiacynt, dwanasty ametyst.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '20', '20', 'A dwanaście bran, dwanaście pereł, a każda brana z jednej perły była, a rynek miejscki złoto szczyre jako śkło przezroczyste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '21', '21', 'I nie widziałem kościoła w nim, abowiem Pan Bóg wszechmogący jest kościołem jego i Baranek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '22', '22', 'A nie potrzebuje to miasto ani słońca, ani miesiąca aby świeciły w nim; abowiem chwała Boża oświeciła je, a Baranek jest kagańcem jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '23', '23', 'A narodowie też, które będą zachowane, będą chodzić przy światłości jego, a królowie ziemie chwałę swoję i cześć do niego przyniosą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '24', '24', 'A brany jego nie będą zamknione we dnie, abowiem nocy tam nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '25', '25', 'I będzie przyniesiona cześć i chwała poganów do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '20', '26', '26', 'Nie wnidzie do niego nic co pokala, abo czyni co brzydliwego, abo kłamstwo, ale ci, którzy napisani są w księgach żywota Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '0', '0', 'Zatym ukazał mi czystą rzekę wody żywej przezroczystą jako krzyształ, wychodzącą z stolicy Bożej i Barankowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '1', '1', 'A w pośrodku rynku jego i z obudwu stron rzeki było drzewo żywota noszące owoców dwanaście, na każdy miesiąc podawając owoc swój i liście swoje, ku uzdrowieniu poganów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '2', '2', 'I nie będzie dalej żadne przeklęctwo, ale stolica Boża i Barankowa w nim będzie i słudzy jego jemu służyć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '3', '3', 'I oglądają oblicze jego, a imię ich na czelech ich będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '4', '4', 'Nocy też tam nie będzie, ani im potrzeba będzie światłości kagańcowej, ani światłości słonecznej przeto, iż Pan Bóg je oświeca i królować będą na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '5', '5', 'Tedy mi rzekł: Ty słowa wierneć są i prawdziwe, a Pan Bóg świętych proroków posłał Anioła swego, aby okazał sługam swym, które rzeczy rychło się zstać mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '6', '6', 'Oto idę rychło. Błogosławiony, który zachowywa słowa proroctwa ksiąg tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '7', '7', 'A ja Jan słyszałem i widział ty rzeczy. A gdym słyszał i widział, upadłem przed nogami Anioła, który mi ty rzeczy okazował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '8', '8', 'A on mi rzekł: Patrz abyś nie czynił, abowiem jestem towarzyszem twoim i braciej twej proroków i tych, którzy zachowywają słowa ksiąg tych. Bogu chwałę dawaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '9', '9', 'Potym rzekł ku mnie: Nie pieczętuj słów proroctwa ksiąg tych, abowiem czas blisko jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '10', '10', 'Który szkodzi, niech jeszcze szkodzi, a który jest plugawym, niech jeszcze będzie plugawym, a który jest sprawiedliwym, niech jeszcze będzie usprawiedliwion, a święty niech jeszcze będzie poświęconym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '11', '11', 'Oto idę rychło, a zapłata moja zemną jest, abym oddał każdemu jako będzie uczynek jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '12', '12', 'Jam jest Alfa i Omega, początek i koniec, pierwszy i ostateczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '13', '13', 'Błogosławieni, którzy zachowywają przykazania jego, aby prawo mieli w drzewie żywota i weszli branami do miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '14', '14', 'A zewnątrz będą psy i ci, którzy trują i nieczyści i mężobójce i bałwochwalce i którykolwiek miłuje i dopuszcza się kłamstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '15', '15', 'Ja Jezus posłałem Anioła mego, aby wam świadczył o tych rzeczach w kościelech. Jam jest korzeń i rodzaj on Dawidów, gwiazda jasna i zaranna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '16', '16', 'A Duch i oblubienica mówią: Pódź, a który słucha, niech rzecze: Chodź, który pragnie, niech przyjdzie, a kto chce, niech bierze wodę żywota darmo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '17', '17', 'Jać oświadczam każdemu, który słucha słów proroctwa ksiąg tych, jeśliby kto przydał tym rzeczam, włoży nań Bóg plagi napisane w księgach tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '18', '18', 'A jeśliby kto ujął co z słów ksiąg proroctwa tego, odejmie Bóg część jego z ksiąg żywota i z miasta świętego i z tych rzeczy, które są napisane w księgach tych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '19', '19', 'Powieda ten, który o tych rzeczach świadczy: Zaiste, idęć rychło. Amen, takci; chodź, Panie Jezusie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 5, 2, 'rev', '21', '20', '20', 'Łaska Pana naszego Jezusa Krystusa z wami wszytkimi. Amen.');
