INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '0', '0', 'Rodowód Jezusa, Mesjasza, potomka Dawida, potomka Abrahama.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '1', '1', 'Abraham miał syna Izaaka, Izaak miał syna Jakuba, Jakub miał syna Judę i jego braci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '2', '2', 'Juda miał z Tamarą synów Faresa i Zarę, Fares miał syna Esroma, Esrom miał syna Arama,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '3', '3', 'Aram miał syna Aminadaba, Aminadab miał syna Naassona, Naasson miał syna Salmona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '4', '4', 'Salmon miał syna Booza z Rahab, Booz miał syna Jobeda z Rut, Jobed miał syna Jesaja,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '5', '5', 'a Jesaj miał syna Dawida-króla. Dawid miał syna Salomona z Uriaszowej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '6', '6', 'Salomon miał syna Roboama, Roboam miał syna Abiasa, Abias miał syna Asafa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '7', '7', 'Asaf miał syna Jozafata, Jozafat miał syna Jorama, Joram miał syna Ozjasza,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '8', '8', 'Ozjasz miał syna Joatama, Joatam miał syna Achaza, Achaz miał syna Ezechiasza,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '9', '9', 'Ezechiasz miał syna Manassesa, Manasses miał syna Amosa, Amos miał syna Jozjasza,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '10', '10', 'Jozjasz miał syna Jechoniasza i jego braci przed wysiedleniem do Babilonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '11', '11', 'A po wysiedleniu do Babilonu Jechoniasz miał syna Salatiela, Salatiel miał syna Zorobabela,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '12', '12', 'Zorobabel miał syna Abiuda, Abiud miał syna Eliakima, Eliakim miał syna Azora,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '13', '13', 'Azor miał syna Sadoka, Sadok miał syna Achima, Achim miał syna Eliuda,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '14', '14', 'Eliud miał syna Eleazara, Eleazar miał syna Matana, Matan miał syna Jakuba,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '15', '15', 'Jakub miał syna Józefa, męża Maryi, z której narodził się Jezus (zwany) Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '16', '16', 'Tak więc (mamy) czternaście pokoleń od Abrahama do Dawida, od  Dawida do wysiedlenia babilońskiego czternaście pokoleń i od wysiedlenia babilońskiego do Chrystusa czternaście pokoleń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '17', '17', 'A z narodzeniem Jezusa było tak: Kiedy Jego matka, Maryja, poślubiła Józefa, znalazła się w stanie błogosławionym za sprawą Ducha Świętego, zanim rozpoczęli wspólne życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '18', '18', 'Ponieważ jej mąż, Józef, był człowiekiem sprawiedliwym i nie chciał jej zniesławić, zamierzał po cichu z nią się rozstać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '19', '19', 'Ale kiedy powziął tę myśl, anioł Pański ukazał się mu we śnie i powiedział: - Józefie, synu Dawida, nie bój się przyjąć twojej żony Maryi, bo to, co się w niej poczęło, pochodzi od Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '20', '20', 'Urodzi syna i nadasz Mu imię Jezus, albowiem On uwolni swój lud od grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '21', '21', 'A to wszystko się stało, aby się wypełniło, co Pan powiedział przez proroka:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '22', '22', '\'Oto panna pocznie i porodzi Syna i nadadzą Mu imię Emanuel\', to znaczy: \'Bóg z nami\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '23', '23', 'Józef przebudziwszy się uczynił tak, jak mu anioł Pański rozkazał, i przyjął swoją żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '0', '24', '24', 'Ale nie żył z nią do czasu, kiedy urodziła syna. I nadał Mu imię Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '0', '0', 'Kiedy Jezus narodził się w Betlejem w Judei, za czasów króla Heroda, zjawili się w Jerozolimie magowie ze Wschodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '1', '1', 'I pytali: - Gdzie jest nowo narodzony król żydowski? Bo widzieliśmy Jego wschodzącą gwiazdę i przybyliśmy złożyć Mu hołd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '2', '2', 'Herod usłyszawszy to przestraszył się bardzo, a z nim cała Jerozolima.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '3', '3', 'Wezwał więc wszystkich arcykapłanów i nauczających lud Prawa i wypytywał ich, gdzie się ma Mesjasz narodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '4', '4', 'A ci mu powiedzieli: - W Betlejem, w Judei, bo tak napisał prorok:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '5', '5', '\'A ty, Betlejem\', ziemio Judy, wcale nie \'jesteś najmniejsze z książąt Judy. Z ciebie bowiem wyjdzie wódz, który będzie pasterzem mego ludu izraelskiego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '6', '6', 'Wtedy Herod, wezwawszy potajemnie magów, wypytywał ich dokładnie o czas ukazania się gwiazdy');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '7', '7', 'i posyłając ich do Betlejem, powiedział: - Idźcie i wypytujcie się dokładnie o to Dziecko, a kiedy Je znajdziecie, dajcie mi znać, żebym i ja mógł pokłonić się Jemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '8', '8', 'Wysłuchawszy króla wyruszyli w drogę i (pokazała się im) ta gwiazda, którą widzieli wschodzącą, szła przed nimi, aż zatrzymała się nad miejscem, w którym było Dziecko. I stanęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '9', '9', 'Gdy zobaczyli gwiazdę, ogromnie się ucieszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '10', '10', 'Kiedy zaś weszli do domu, zobaczyli Dziecko z Jego matką Maryją. Upadli na twarz i złożyli Mu hołd. I otworzywszy szkatuły ofiarowali złoto, kadzidło i mirrę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '11', '11', 'A otrzymawszy we śnie polecenie, by nie wracali do Heroda, inną drogą odeszli do swego kraju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '12', '12', 'Po ich odejściu anioł Pański ukazuje się we śnie Józefowi i mówi: - Wstań, zabierz Dziecko i Jego matkę i uciekaj do Egiptu. Pozostaniesz tam,  aż nie dam ci znać, bo Herod będzie szukał Dziecka, żeby Je zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '13', '13', 'Józef wstał w nocy, zabrał Dziecko i Jego matkę i uciekł do Egiptu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '14', '14', 'Przebywał tam aż do śmierci Heroda, aby się wypełniło to, co Pan powiedział przez proroka: \'Z Egiptu wezwałem mojego syna\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '15', '15', 'Herod widząc, że magowie sprawili mu zawód, strasznie się rozgniewał i rozkazał pozabijać w Betlejem i w całej okolicy wszystkich chłopców poniżej dwóch lat, stosownie do czasu, o którym dowiedział się dokładnie od magów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '16', '16', 'Wtedy wypełniło się to, co powiedział prorok Jeremiasz:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '17', '17', '\'Usłyszano głos w Rama, płacz i jęk wielki: Rachel, opłakująca swoje dzieci, nie dała się pocieszyć po ich stracie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '18', '18', 'Po śmierci Heroda anioł Pański ukazuje się we śnie Józefowi w Egipcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '19', '19', 'I mówi: - Wstań, zabierz Dziecko i Jego matkę i wracaj do ziemi izraelskiej, bo już pomarli ci, którzy czyhali na życie Dziecka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '20', '20', 'A on wstał, zabrał Dziecko i Jego matkę i przyszedł do ziemi izraelskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '21', '21', 'Ponieważ jednak usłyszał, że Archelaos został po swoim ojcu, Herodzie, królem w Judei, bał się tam iść. A pouczony we śnie udał się do ziemi galilejskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '1', '22', '22', 'I zamieszkał tam w mieście zwanym Nazaret, aby się wypełniło to, co powiedzieli Prorocy: Nazarejczykiem będzie nazwany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '0', '0', 'W tym czasie pojawia się Jan Chrzciciel, nauczając na Pustyni Judzkiej: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '1', '1', 'Nawróćcie się, bo blisko jest królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '2', '2', 'O nim to właśnie mówił prorok Izajasz: \'Głos wołającego na pustyni: przygotujcie drogę Panu, prostujcie Mu ścieżki\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '3', '3', 'A Jan miał odzienie z sierści wielbłądziej i skórzany pas na biodrach. Pokarmem jego była szarańcza i miód leśny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '4', '4', 'Wtedy schodziła się do niego Jerozolima i cała Judea, i cała okolica nadjordańska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '5', '5', 'A ci, którzy wyznawali swoje grzechy, byli przez niego chrzczeni w Jordanie, rzece.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '6', '6', 'Zobaczywszy wielu faryzeuszów i saduceuszów, którzy przychodzili do niego po chrzest, powiedział: - Żmije! Któż was zapewnił, że unikniecie zbliżającego się gniewu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '7', '7', 'Przynoście więc owoc godny nawrócenia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '8', '8', 'a nie wmawiajcie sobie: Abrahama mamy za ojca, bo powiadam wam: Z tych kamieni Bóg może wywieść synów Abrahamowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '9', '9', 'Siekiera już jest przyłożona do korzenia drzew. A każde drzewo, które nie rodzi dobrego owocu, będzie wycięte i w ogień wrzucone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '10', '10', 'Ja was chrzczę wodą, abyście się nawrócili, a Ten, który przyjdzie po mnie, jest mocniejszy ode mnie. Ja zaś nie jestem wart nosić za Nim Jego sandałów. On będzie was chrzcił ogniem Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '11', '11', 'W Jego ręku sito do czyszczenia zboża! I oczyści swoje zboże. Ziarno zgromadzi w śpichlerzu, a plewy spali w nie gasnącym ogniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '12', '12', 'Wtedy Jezus przychodzi z Galilei nad Jordan do Jana, aby przyjąć chrzest od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '13', '13', 'Ale Jan wzbraniał się, mówiąc: - To ja powinienem przyjąć chrzest od Ciebie, a Ty przychodzisz do mnie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '14', '14', 'Jezus mu odpowiedział: - Zgódź się teraz, bo trzeba, abyśmy przez to wypełnili wszelką sprawiedliwość. Wtedy się zgodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '15', '15', 'Jezus wyszedł z wody natychmiast po chrzcie. I rozwarły się nad Nim niebiosa, i zobaczył Ducha Bożego zstępującego jak gołębica i przychodzącego doń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '2', '16', '16', 'A głos z nieba mówił: - To jest mój Syn ukochany, upodobałem Go sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '0', '0', 'Wtedy Duch zaprowadził Jezusa na pustynię, aby był kuszony przez diabła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '1', '1', 'Po czterdziestu dniach i czterdziestu nocach postu poczuł w końcu głód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '2', '2', 'Kusiciel przystąpiwszy do Niego powiedział: - Jeżeli jesteś Synem Bożym, powiedz, aby te kamienie stały się chlebem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '3', '3', 'On zaś odpowiedział: - Napisano: \'Nie samym chlebem będzie żył człowiek, ale każdym słowem, które wychodzi z ust Boga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '4', '4', 'Wtedy diabeł bierze Go ze sobą do świętego miasta i stawia Go na ganku świątyni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '5', '5', 'i mówi Mu: - Jeżeli jesteś Synem Bożym, rzuć się na dół. Napisano bowiem: \'Rozkazał aniołom swoim\' i (napisano też): \'będą Cię nosili na rękach, abyś nie uraził swej nogi o kamień\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '6', '6', 'Rzekł mu Jezus: - Napisano także: \'Nie będziesz kusił Pana, Boga Twego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '7', '7', 'Znowu bierze Go diabeł na bardzo wysoką górę i pokazuje Mu wszystkie królestwa świata z całym ich przepychem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '8', '8', 'i mówi Mu: - To wszystko dam Tobie, jeżeli upadniesz na twarz i złożysz mi hołd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '9', '9', 'Wtedy mówi mu Jezus: - Idź precz, szatanie, napisano bowiem: \'Panu, Bogu Twojemu, hołd składać będziesz i Jemu tylko będziesz służył\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '10', '10', 'Wtedy diabeł Go opuścił, a przyszli aniołowie i usługiwali Mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '11', '11', 'Kiedy (Jezus) usłyszał, że Jan został uwięziony, usunął się do Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '12', '12', 'A ominąwszy Nazaret zamieszkał w Kafarnaum nad morzem, na pograniczu (pokoleń) Zabulona i Neftalego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '13', '13', 'aby się wypełniło to, co powiedział prorok Izajasz:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '14', '14', '\'Ziemia Zabulona i ziemia Neftalego, droga morska, Zajordanie, Galilea pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '15', '15', 'Lud pogrążony w ciemności ujrzał wielkie światło, a przebywającym w kraju i w mroku śmierci zajaśniało światło\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '16', '16', 'Odtąd Jezus zaczął głosić (ewangelię) i mówić: - Nawróćcie się, bo królestwo niebieskie jest blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '17', '17', 'Idąc brzegiem Morza Galilejskiego, zobaczył dwóch braci: Szymona zwanego Piotrem i jego brata Andrzeja, którzy zarzucali sieci w morze; byli bowiem rybakami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '18', '18', 'I rzekł im: - Chodźcie za Mną, a Ja z was uczynię rybaków ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '19', '19', 'A oni porzuciwszy zaraz sieci poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '20', '20', 'Kiedy ruszył dalej, zobaczył dwóch innych braci: Jakuba (syna) Zebedeusza i jego brata Jana, którzy razem z ojcem swoim Zebedeuszem naprawiali sieci. I wezwał ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '21', '21', 'A oni pozostawiwszy zaraz łódź i ojca poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '22', '22', 'I obchodził całą Galileę, nauczając w synagogach i głosząc dobrą nowinę o królestwie, i uwalniając ludzi od wszelkich chorób i dolegliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '23', '23', 'Wieść o Nim rozeszła się po całej Syrii. I przynosili do Niego wszystkich cierpiących na różne choroby i słabości, opętanych i lunatyków, i sparaliżowanych. A On ich uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '3', '24', '24', 'I poszły za Nim wielkie tłumy z Galilei i z Dekapolu, i z Jerozolimy, i z Judei, i z Zajordania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '0', '0', '(Jezus) widząc tłumy, wszedł na górę. A kiedy usiadł, podeszli do Niego uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '1', '1', 'On zaś rozpoczął swoją naukę takimi słowami:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '2', '2', '- Szczęśliwi ubodzy w duchu, albowiem ich jest królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '3', '3', 'Szczęśliwi, którzy się smucą, albowiem będą pocieszeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '4', '4', 'Szczęśliwi łagodni, albowiem odziedziczą ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '5', '5', 'Szczęśliwi, którzy łakną i pragną sprawiedliwości, albowiem będą nasyceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '6', '6', 'Szczęśliwi miłosierni, albowiem dostąpią miłosierdzia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '7', '7', 'Szczęśliwi czystego serca, albowiem będą oglądali Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '8', '8', 'Szczęśliwi, którzy doprowadzają do pokoju, albowiem nazwani będą synami Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '9', '9', 'Szczęśliwi, którzy cierpią prześladowanie za sprawiedliwość, albowiem ich jest królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '10', '10', 'Szczęśliwi jesteście, jeśli z mojego powodu lżą was i prześladują, i kłamliwie przypisują wam wszelkie zło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '11', '11', 'Cieszcie się i radujcie, bo czeka was sowita zapłata w niebie. Tak bowiem prześladowali proroków, którzy żyli przed wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '12', '12', 'Wy jesteście solą ziemi, a jeśli sól utraci swój smak, to czym go przywrócić? Nie przydaje się na nic! Wyrzuca się ją z domu i ludzie będą po niej deptać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '13', '13', 'Wy jesteście światłem świata; nie da się ukryć miasta, które leży na górze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '14', '14', 'Nie zapala się też świecy i nie stawia pod korcem, ale na świeczniku - i świeci wszystkim w domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '15', '15', 'Tak wasze światło niech świeci przed ludźmi, aby widzieli wasze dobre czyny i chwalili Ojca waszego, który jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '16', '16', 'Nie sądźcie, że przyszedłem znieść Prawo albo Proroków. Nie przyszedłem znieść, ale wypełnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '17', '17', 'Zaprawdę, powiadam wam: Dopóki niebo i ziemia nie przeminą i dopóki się wszystko nie wypełni, jedna jota ani jedna kreska w Prawie nie ulegnie zmianie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '18', '18', 'Ktokolwiek więc zniósłby jeden z tych przepisów, choćby najmniejszy, i nauczał tego ludzi, ten będzie najmniejszy w królestwie niebieskim. A kto by ich przestrzegał i nauczał tego, ten będzie wielki w królestwie niebieskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '19', '19', 'I powiadam wam: Jeśli wasza sprawiedliwość nie będzie przewyższała (sprawiedliwości) nauczycieli Pisma i faryzeuszów, na pewno nie wejdziecie do królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '20', '20', 'Słyszeliście, że powiedziano praojcom: \'Nie będziesz zabijał\', a kto by zabił, winien będzie sądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '21', '21', 'A Ja wam powiadam: Każdy, kto gniewa się na swego brata, będzie winien sądu. A kto powie swemu bratu: głupcze, będzie winien Najwyższej Rady. A kto powie: bezbożniku, będzie winien piekła ognistego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '22', '22', 'Jeżeli więc składasz ofiarę na ołtarzu, a tam przypomnisz sobie, że twój brat ma coś przeciwko tobie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '23', '23', 'zostaw twoją ofiarę tam przed ołtarzem, a idź pojednać się najpierw z bratem. A potem wróć i złóż swoją ofiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '24', '24', 'Ugódź się szybko z twoim przeciwnikiem, dopóki jeszcze jesteś z nim w drodze do sądu, aby przeciwnik nie oddał cię sędziemu, a sędzia dozorcy. I zostałbyś zamknięty w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '25', '25', 'Zaprawdę, powiadam ci: Nie wyjdziesz stamtąd, zanim nie spłacisz długu co do grosza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '26', '26', 'Słyszeliście, że powiedziano prajocom: \'Nie będziesz cudzołożył\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '27', '27', 'A Ja wam powiadam: Każdy, kto z pożądaniem patrzy na kobietę, już popełnił cudzołóstwo w sercu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '28', '28', 'A jeśli twoje oko prawe pobudza cię do złego, wyłup je i odrzuć od siebie. Lepiej bowiem dla ciebie, abyś stracił jeden ze swoich członków, niż żeby całe twoje ciało wrzucono do piekła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '29', '29', 'I jeśli twoja prawa ręka pobudza cię do złego, odetnij ją i odrzuć od siebie. Lepiej bowiem dla ciebie, abyś stracił jeden ze swoich członków, niż żeby całe twoje ciało wrzucono do piekła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '30', '30', 'Powiedziano: \'Kto rozwodzi się z żoną, niech da jej list rozwodowy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '31', '31', 'A Ja wam powiadam: Każdy, kto rozwodzi się z żoną z wyjątkiem przypadku rozpusty, sprawia, że ona dopuszcza się cudzołóstwa. I kto żeni się z rozwiedzioną - dopuszcza się cudzołóstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '32', '32', 'Słyszeliście także, że powiedziano praojcom: \'Nie będziesz fałszywie przysięgał, bądź wierny przysięgom danym Panu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '33', '33', 'A ja wam powiadam: Nie przysięgajcie wcale. Ani na niebo, bo to tron Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '34', '34', 'Ani na ziemię, bo to podnóżek Jego stóp. Ani na Jerozolimę, bo to miasto wielkiego Króla.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '35', '35', 'Ani na swoją głowę nie przysięgaj, bo nawet jednego włosa nie możesz uczynić białym albo czarnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '36', '36', 'Niech wasza mowa będzie: tak - tak, nie - nie, a co ponadto - pochodzi od złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '37', '37', 'Słyszeliście, że powiedziano: \'Oko za oko, ząb za ząb\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '38', '38', 'A Ja wam powiadam: Nie zwalczajcie (zła) złem! Lecz jeśli ktoś uderzy cię w prawy policzek, nastaw mu drugi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '39', '39', 'A temu, kto chce procesować się z tobą i zabrać ci suknię, oddaj także płaszcz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '40', '40', 'A jeśli ktoś zmusza cię do jednego tysiąca (kroków), idź z nim dwa tysiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '41', '41', 'Daj temu, kto cię prosi, i nie odwracaj się od tego, kto chce od ciebie pożyczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '42', '42', 'Słyszeliście, że powiedziano: \'Będziesz kochał bliźniego, a nienawidził wroga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '43', '43', 'A Ja wam powiadam: Kochajcie waszych wrogów i módlcie się za prześladowców,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '44', '44', 'abyście stali się synami waszego Ojca, który jest w niebie. Bo Jego słońce wschodzi nad złymi i dobrymi, a deszcz spada na sprawiedliwych i niesprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '45', '45', 'Za cóż was nagradzać, jeśli kochacie tych, którzy was kochają? Czyż i celnicy tego nie czynią?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '46', '46', 'I cóż takiego wielkiego czynicie,  jeśli kochacie tylko waszych braci? Czyż i poganie tego nie czynią?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '4', '47', '47', 'Wy zatem bądźcie tak doskonali, jak doskonałym jest wasz Ojciec w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '0', '0', 'Strzeżcie się, abyście dobrych czynów nie spełniali na oczach ludzi, aby was podziwiano. Bo inaczej nie będziecie mieli zapłaty u waszego Ojca w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '1', '1', 'Kiedy więc dajesz jałmużnę, nie każ trąbić przed sobą, jak czynią obłudnicy w synagogach i na ulicach,  aby ich ludzie chwalili. Zaprawdę, powiadam wam: Już odebrali swoją zapłatę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '2', '2', 'A kiedy dajesz jałmużnę, niech twoja lewa ręka nie wie, co czyni prawa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '3', '3', 'aby twoja jałmużna pozostała w ukryciu. A twój Ojciec, który widzi to, co jest ukryte, odpłaci tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '4', '4', 'A kiedy się modlicie, nie postępujcie jak obłudnicy, bo oni lubią się modlić, wystawając w synagogach i na narożnikach ulic, aby się pokazać ludziom. Zaprawdę, powiadam wam: Już odebrali swoją zapłatę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '5', '5', 'A ty, kiedy się modlisz, wejdź do komórki i zamknąwszy drzwi, módl się w ukryciu do swego Ojca, a twój Ojciec, który widzi to, co jest ukryte, odpłaci tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '6', '6', 'A modląc się nie mówcie wiele jak poganie. Im się wydaje, że dzięki wielomówności zostaną wysłuchani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '7', '7', 'Nie upodabniajcie się do nich! Bo wasz Ojciec wie, czego potrzebujecie, zanim Go poprosicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '8', '8', 'Wy zatem tak się módlcie: Ojcze nasz, któryś jest w niebie, niech się święci Twoje Imię,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '9', '9', 'niech przyjdzie Twoje królestwo, niech spełnia się Twoja wola na ziemi, tak jak w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '10', '10', 'Chleba naszego powszedniego daj nam dzisiaj');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '11', '11', 'i przebacz nam nasze winy, jak i my przebaczyliśmy tym, którzy nam zawinili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '12', '12', 'I nie dozwól nam ulec pokusie, ale wybaw nas od złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '13', '13', 'Bo jeśli przebaczycie ludziom ich przewinienia, to i wam przebaczy wasz Ojciec niebieski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '14', '14', 'A jeśli ludziom nie przebaczycie, to i wasz Ojciec nie przebaczy waszych przewinień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '15', '15', 'A kiedy pościcie, nie udawajcie smutnych jak obłudnicy. Oni szpecą twarze, aby pokazać ludziom, że poszczą. Zaprawdę, powiadam wam: Już odebrali swoją zapłatę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '16', '16', 'A ty, kiedy pościsz, wonnym olejkiem pokrop głowę i umyj twarz,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '17', '17', 'aby było widać, że nie dla ludzi pościsz, ale dla twego Ojca, który jest ukryty. A twój Ojciec, który widzi to, co ukryte, odpłaci tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '18', '18', 'Nie gromadźcie sobie skarbów na ziemi, gdzie mól i rdza niszczy i gdzie złodzieje podkopują się i kradną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '19', '19', 'Gromadźcie sobie skarb w niebie, gdzie ani mól, ani rdza nie niszczy i gdzie złodzieje nie podkopują się i nie kradną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '20', '20', 'Bo gdzie jest twój skarb, tam będzie też i twoje serce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '21', '21', 'Światłem dla ciała jest oko. Jeśli więc oko twoje jest zdrowe, to całe twoje ciało będzie miało światło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '22', '22', 'Lecz jeśli oko twoje będzie chore, całe ciało pogrąży się w ciemności. Jeżeli  zatem światło, które masz w sobie, jest ciemnością, to jakaż to (wielka) ciemność!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '23', '23', 'Nikt nie może dwom panom służyć, bo albo jednego będzie nienawidził, a drugiego kochał, albo z jednym będzie trzymał, a drugim wzgardzi. Nie możecie służyć Bogu i mamonie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '24', '24', 'Dlatego wam powiadam: Nie troszczcie się zbytnio o swoje życie, co będziesz jeść lub co będziecie pić, ani o swoje ciało, w co je ubierzecie - czy życie nie jest ważniejsze od jedzenia a ciało od ubrania?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '25', '25', 'Przypatrzcie się ptakom, które latają w powietrzu, nie sieją i nie żniwują, i nie zbierają do spichlerzy, a wasz Ojciec niebieski żywi je. Czyż wy nie jesteście więcej warci od nich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '26', '26', 'A któż z was, tak zatroskanych (o wszystko), może dodać jedną chwilkę do swojego życia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '27', '27', 'I dlaczego martwicie się o ubranie? Przypatrzcie się kwiatom polnym, one nie pracują i nie przędą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '28', '28', 'a powiadam wam: Nawet Salomon w całym swoim przepychu nie był tak ubrany, jak jeden z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '29', '29', 'Jeżeli więc Bóg tak przystraja polną trawę, która dziś jest, a jutro w piec będzie wrzucona, to o ileż bardziej was, słabej wiary?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '30', '30', 'Nie troszczcie się zatem, mówiąc: Co będziemy jedli, albo: Co będziemy pili, albo: W co się ubierzemy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '31', '31', 'bo o to wszystko zabiegają poganie. A Ojciec wasz niebieski wie, że tego wszystkiego potrzebujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '32', '32', 'Szukajcie najpierw królestwa (Bożego) i jego sprawiedliwości, a to wszystko będzie wam przydane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '5', '33', '33', 'Nie troszczcie się zatem o jutro, bo jutro samo o siebie będzie się troszczyć. Dosyć ma dzień każdy swojej biedy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '0', '0', 'Nie osądzajcie, abyście nie zostali osądzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '1', '1', 'Bo jaki wyrok wydajecie, taki i na was wydadzą, i jaką miarą mierzycie, taką wam odmierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '2', '2', 'Dlaczego widzisz pyłek w oku brata, a w swoim oku belki nie dostrzegasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '3', '3', 'Lub jakżeż powiesz bratu: Pozwól, że ci wyjmę z oka pyłek, a oto belka w twoim oku!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '4', '4', 'Obłudniku, wyrzuć najpierw belkę ze swego oka, a potem będziesz lepiej widział, jak wyjąć pyłek z oka brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '5', '5', 'Nie dawajcie psom tego, co święte, i nie rzucajcie pereł przed wieprze, aby ich nie stratowały racicami, a potem was samych nie rozszarpały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '6', '6', 'Proście, a dadzą wam. Szukajcie, a znajdziecie. Kołaczcie, a otworzą wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '7', '7', 'Bo każdy, kto prosi, otrzymuje, a kto szuka, znajduje, i otwierają temu, kto kołacze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '8', '8', 'Albo czy jest wśród was człowiek, który dałby swemu synowi kamień, gdy on prosi o chleb?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '9', '9', 'Albo węża, gdy on prosi o rybę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '10', '10', 'Jeżeli więc wy, będąc złymi, umiecie dawać dobre rzeczy swoim dzieciom, to tym bardziej Ojciec wasz, który jest w niebie, da dobre rzeczy tym, którzy Go proszą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '11', '11', 'Wszystko więc, co byście chcieli, aby ludzie dla was czynili, i wy dla nich czyńcie. Bo to jest (istota) Prawa i Proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '12', '12', 'Wchodźcie przez wąską bramę, bo szeroka brama i szeroka droga prowadzą na zatracenie i wielu tędy idzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '13', '13', 'A jakże ciasna jest brama i jak wąska droga, która prowadzi do życia! I niewielu ją okrywa!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '14', '14', 'Strzeżcie się fałszywych proroków, którzy przychodzą do was w owczych skórach, a w środku są drapieżnymi wilkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '15', '15', 'Rozpoznacie ich po owocach: Czyż zbiera się winogrona z ostu, a figi z ciernia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '16', '16', 'Tak (to jest): Wszelkie drzewo dobre rodzi dobre owoce, a złe drzewo rodzi złe owoce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '17', '17', 'Nie może dobre drzewo rodzić złych owoców ani złe drzewo rodzić dobrych owoców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '18', '18', 'Każde drzewo, które nie rodzi dobrego owocu, będzie wycięte i w ogień wrzucone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '19', '19', 'Rozpoznacie ich więc po owocach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '20', '20', 'Nie każdy, kto mówi do Mnie: Panie, Panie, wejdzie do królestwa niebieskiego, ale ten, kto spełnia wolę mego Ojca, który jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '21', '21', 'W owym dniu wielu mi powie: Panie, Panie, czyż w Twoim imieniu nie prorokowaliśmy? I w Twoim imieniu nie wyrzucaliśmy czartów? I w Twoim imieniu nie dokonywaliśmy wielu cudów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '22', '22', 'Wtedy im powiem: Ja was nigdy nie znałem. Odejdźcie ode Mnie, zło czyniący!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '23', '23', 'Każdy więc, kto słucha tych moich słów i wprowadza je w czyn, podobny będzie do człowieka rozsądnego, który zbudował swój dom na skale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '24', '24', 'I spadł ulewny deszcz, i wezbrały rzeki, i zerwały się wichry, i uderzyły na ten dom - a nie zawalił się, bo był osadzony na skale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '25', '25', 'Każdy zaś, kto słucha tych moich słów, a nie wprowadza ich w czyn, podobny będzie do głupca, który zbudował swój dom na piasku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '26', '26', 'I spadł ulewny deszcz, i wezbrały rzeki, i zerwały się wichry, i uderzyły na ten dom - i zawalił się. I rozpadł się zupełnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '27', '27', 'Kiedy Jezus skończył tę mowę, tłumy podziwiały Jego naukę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '6', '28', '28', 'Nauczał ich bowiem jak mający władzę, a nie jak nauczyciele Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '0', '0', 'Kiedy zszedł z góry, postępowały za Nim wielkie tłumy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '1', '1', 'A oto trędowaty upadł przed Nim na twarz, mówiąc: - Panie, Ty możesz mnie oczyścić, jeżeli zechcesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '2', '2', 'A Jezus, wyciągnąwszy rękę, dotknął się go, mówiąc: - Chcę, bądź oczyszczony. I natychmiast został oczyszczony z trądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '3', '3', 'I mówi mu Jezus: - Uważaj, nie mów nikomu, ale idź, pokaż się kapłanowi i złóż dar, jaki przepisał Mojżesz na świadectwo im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '4', '4', 'A kiedy wszedł do Kafarnaum, podszedł do Niego setnik i prosił Go: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '5', '5', 'Panie, sługa mój leży w domu sparaliżowany i bardzo cierpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '6', '6', 'I mówi mu (Jezus): - Przyjdę go uzdrowić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '7', '7', 'A setnik powiedział: - Panie, nie jestem godzien, abyś wszedł pod mój dach. Ale wydaj tylko rozkaz, a mój sługa będzie uzdrowiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '8', '8', 'Bo i ja mam władzę nad sobą. Mam też pod sobą żołnierzy i mówię temu: Idź - to idzie, a innemu: Przyjdź - to przychodzi, a słudze: Zrób to - a robi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '9', '9', 'Kiedy Jezus to usłyszał, zdumiał się i powiedział do tych, którzy szli za Nim: - Zaprawdę, powiadam wam, nie znalazłem tak wielkiej wiary w Izraelu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '10', '10', 'A powiadam wam: Wielu przyjdzie ze Wschodu i Zachodu i zasiądą przy stole w królestwie niebieskim razem z Abrahamem, Izaakiem i Jakubem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '11', '11', 'synowie zaś królestwa będą wyrzuceni na zewnątrz, w ciemność. Tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '12', '12', 'A setnikowi Jezus powiedział: - Idź, niech ci się stanie tak, jak uwierzyłeś. I w tejże chwili sługa został uzdrowiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '13', '13', 'Kiedy Jezus wszedł do domu Piotra, ujrzał jego teściową leżącą z gorączką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '14', '14', 'I ujął ją za rękę, i opuściła ją gorączka. I wstała, i usługiwała Mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '15', '15', 'A kiedy nastał wieczór, przynieśli Mu wielu opętanych przez czartów, a On słowem wyrzucał (złe) duchy i uzdrawiał wszystkich chorych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '16', '16', 'Tak wypełniły się słowa proroka Izajasza, który mówi: \'On wziął na siebie nasze niemoce i znosił nasze choroby\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '17', '17', 'Kiedy Jezus zobaczył tłum wokół siebie, kazał odpłynąć na drugą stronę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '18', '18', 'A pewien nauczyciel Pisma powiedział Mu: - Nauczycielu, pójdę za Tobą, dokądkolwiek się udasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '19', '19', 'Jezus mu mówi: - Lisy mają jamy i ptaki latające w powietrzu mają gniazda, lecz Syn Człowieczy nie ma dachu nad głową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '20', '20', 'A ktoś inny, jeden z Jego uczniów, powiedział: - Panie, pozwól mi najpierw odejść i pogrzebać mojego ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '21', '21', 'Mówi mu Jezus: - Chodź za Mną, a grzebanie umarłych zostaw umarłym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '22', '22', 'Kiedy wsiadł do łodzi, weszli z Nim uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '23', '23', 'A oto rozpętała się na morzu burza tak wielka, że fale zalewały łódź. On zaś spał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '24', '24', 'I obudzili Go, wołając: - Panie, ratuj, giniemy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '25', '25', 'Mówi im: - Ludzie słabiej wiary, czemu się boicie? Potem wstał i rozkazał wichrom i morzu. I nastała wielka cisza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '26', '26', 'A ludzie dziwili się, mówiąc: - Kimże On jest, że wiatry, i morze są Mu posłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '27', '27', 'Kiedy przybył na drugi brzeg do kraju Gadareńczyków, zabiegli Mu drogę dwaj opętani przez czarta, którzy wyszli z grobowców. A byli tak bardzo niebezpieczni, że nikt nie mógł przejść tą drogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '28', '28', 'I naraz krzyknęli: - Czego chcesz od nas, Synu Boży, przyszedłeś tutaj przed czasem, aby nas dręczyć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '29', '29', 'Z daleka od nich pasło się wielkie stado świń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '30', '30', 'A czarty prosiły Go - Skoro nas wyrzucasz, poślij nas w to stado świń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '31', '31', 'Powiedział im: - Idźcie! Wtedy weszły w świnie. A oto całe stado rzuciło się z urwiska do morza i (świnie) zginęły w falach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '32', '32', 'A pasterze uciekli. A kiedy przyszli do miasta, opowiedzieli wszystko, również to, co stało się z opętanymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '7', '33', '33', 'Wtedy całe miasto wyszło Jezusowi naprzeciw, a zobaczywszy Go poprosili, aby odszedł z ich kraju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '0', '0', '(Jezus) wsiadłszy do łodzi przeprawił się (na drugi brzeg) i przyszedł do swojego miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '1', '1', 'I przynieśli Mu sparaliżowanego, który leżał na noszach. Jezus, widząc ich wiarę, powiedział sparaliżowanemu: - Ufaj, synu, twoje grzechy są odpuszczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '2', '2', 'Wtedy niektórzy nauczyciele Pisma pomyśleli sobie: - On bluźni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '3', '3', 'A Jezus, przejrzawszy ich myśli, powiedział: - Dlaczego wyciągacie złe wnioski?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '4', '4', 'Cóż łatwiej powiedzieć: Twoje grzechy są odpuszczone, czy też: Wstań i chodź?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '5', '5', 'Ale abyście wiedzieli, że Syn Człowieczy ma władzę odpuszczania grzechów na ziemi - zwrócił się do sparaliżowanego: - Wstań, weź swoje nosze i idź do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '6', '6', 'A on wstał i poszedł do swego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '7', '7', 'A tłumy, które to widziały, z bojaźnią wielbiły Boga dającego ludziom taką władzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '8', '8', 'Jezus odchodząc stamtąd zobaczył człowieka pobierającego opłaty, który nazywał się Mateusz. I mówi mu: - Pójdź za Mną! I poszedł za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '9', '9', 'A kiedy (Jezus) siedział w domu przy stole, wielu celników i grzeszników posilało się razem z Nim i Jego uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '10', '10', 'Widząc to faryzeusze mówili Jego uczniom: - Dlaczego wasz Nauczyciel jada z celnikami i grzesznikami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '11', '11', '(Jezus) usłyszawszy to powiedział: - Zdrowi nie potrzebują lekarza, tylko chorzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '12', '12', 'Zrozumiejcież wreszcie, co to znaczy? \'Miłosierdzia chcę bardziej niż ofiary\'. Bo nie przyszedłem wzywać sprawiedliwych, ale grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '13', '13', 'Wtedy przyszli do Niego uczniowie Jana, pytając: - Dlaczego my i faryzeusze wiele pościmy, a Twoi uczniowie nie poszczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '14', '14', 'Jezus im powiedział: - Czyż goście weselni mogą się smucić, dopóki pan młody jest z nimi? Ale przyjdzie czas, kiedy pan młody zostanie im zabrany - wtedy będą pościć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '15', '15', 'Nikt nie przyszywa łaty z nowego materiału do starego okrycia, bo taka łata rozdziera okrycie i dziura się powiększa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '16', '16', 'Nie wlewa się też młodego wina do starych worków skórzanych, bo jeśli się tak zrobi, skórzane worki pękają. I wino się wylewa, i marnują się worki. Ale młode wino wlewa się do nowych worków skórzanych. Wtedy zachowa się i jedno, i drugie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '17', '17', 'Kiedy to im mówił, jakiś przełożony synagogi przyszedł i padł przed Nim na twarz, mówiąc: - Moja córka właśnie skonała, ale połóż na nią rękę, a ożyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '18', '18', 'Jezus powstawszy poszedł za nim z uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '19', '19', 'A oto kobieta, która dwanaście lat cierpiała na krwotok, podszedłszy z tyłu dotknęła się kraju Jego okrycia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '20', '20', 'Mówiła bowiem sobie: - Jeżeli tylko dotknę się Jego okrycia, będę uzdrowiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '21', '21', 'A Jezus odwrócił się i zobaczywszy ją powiedział: - Ufaj, córko, twoja wiara cię uzdrowiła. I od tej chwili kobieta była zdrowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '22', '22', 'Kiedy Jezus przyszedł do domu przełożonego i zobaczył fletnistów i tłum zawodzący z żalu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '23', '23', 'mówił: - Idźcie sobie, bo dziewczynka nie umarła, ale śpi. I wyśmiewali się z Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '24', '24', 'Kiedy usunięto tłum, wszedł do wnętrza, ujął ją za rękę i dziewczynka wstała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '25', '25', 'A wieść o tym rozeszła się po całej tamtejszej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '26', '26', 'Za odchodzącym stamtąd Jezusem szli dwaj niewidomi, którzy głośno wołali: - Synu Dawida, zmiłuj się nad nami!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '27', '27', 'A kiedy wszedł do domu, niewidomi podeszli do Niego. I pyta ich Jezus: - Wierzycie, że mogę to uczynić? Mówią Mu: - Tak, Panie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '28', '28', 'Wtedy dotknął ich oczu, mówiąc: - Niech się wam stanie według waszej wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '29', '29', 'I odzyskali wzrok. A Jezus nakazał im surowo: - Uważajcie, niech się nikt o tym nie dowie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '30', '30', 'Oni jednak roznieśli wieść o Nim po całej tamtejszej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '31', '31', 'A kiedy odchodzili, przyprowadzono do Niego niemowę opętanego przez czarta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '32', '32', 'Po wyrzuceniu czarta niemy odzyskał mowę. A tłumy mówiły zdumione: - Nigdy w Izraelu nie widziano czegoś takiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '33', '33', 'Lecz faryzeusze mówili: - On mocą przywódcy czartów wyrzuca czarty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '34', '34', 'Jezus zaś obchodził wszystkie miasta i wioski, nauczając w synagogach, głosząc dobrą nowinę o królestwie i uzdrawiając z wszelkich chorób i wszelkiej niemocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '35', '35', 'Kiedy ujrzał tłumy, ulitował się nad nimi, bo były utrudzone i porzucone jak owce bez pasterza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '8', '36', '36', 'Wtedy mówi swoim uczniom: - Żniwo wprawdzie wielkie, ale robotników mało. Proście więc Pana żniwa, ażeby wysłał robotników na swoje żniwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '0', '0', 'I przywoławszy dwunastu swoich uczniów, dał im taką władzę nad nieczystymi duchami, że je wyrzucali i uzdrawiali wszelkie choroby i wszelką niemoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '1', '1', 'A oto imiona dwunastu apostołów: pierwszy Szymon, zwany Piotrem, i brat jego Andrzej, i Jakub, syn Zebedeusza, i brat jego Jan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '2', '2', 'Filip i Bartłomiej, Tomasz i celnik Mateusz, Jakub, syn Alfeusza, i Tadeusz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '3', '3', 'Szymon Kananejszyk i Judasz Iskariota, ten, który Go zdradził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '4', '4', 'Jezus wysłał tych dwunastu, dając im takie polecenie: - Nie chodźcie do pogan ani nie wchodźcie do miasta Samarytan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '5', '5', 'Idźcie raczej do owiec, które zginęły z domu Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '6', '6', 'Idźcie i głoście: Zbliżyło się królestwo niebieskie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '7', '7', 'Uzdrawiajcie chorych, wskrzeszajcie umarłych, oczyszczajcie trędowatych, wypędzajcie czarty. Darmoście wzięli, darmo dawajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '8', '8', 'Nie bierzcie z sobą złota ani srebra, ani drobnych pieniędzy do trzosów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '9', '9', 'Ani torby podróżnej na drogę, ani dwóch sukni, ani sandałów, ani laski, bo wart jest robotnik swojej strawy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '10', '10', 'A kiedy wejdziecie do jakiegoś miasta albo wioski, dowiedzcie się, czy jest tam ktoś godny, i zamieszkajcie u niego aż do waszego odejścia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '11', '11', 'I wchodząc do domu pozdrówcie go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '12', '12', 'A jeśli ten dom będzie na to zasługiwał, niech wasz pokój spłynie na niego, a jeśli nie będzie zasługiwał, niech wasz pokój powróci do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '13', '13', 'A jeśliby was ktoś nie przyjął i nie słuchał waszych nauk, wyjdźcie z tego domu czy miasta i otrząśnijcie pył z waszych nóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '14', '14', 'Zaprawdę, mówię wam: W dzień sądu lżej będzie ziemi Sodomitów i Gomorejczyków aniżeli temu miastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '15', '15', 'Oto Ja posyłam was jak owce pomiędzy wilki. Więc bądźcie mądrzy jak węże, a prości jak gołębie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '16', '16', 'Ale strzeżcie się ludzi, bo będą was stawiać przed sądami i biczować w synagogach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '17', '17', 'i z mego powodu będą was prowadzać przed namiestników i królów na świadectwo im i poganom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '18', '18', 'A kiedy was postawią przed sądem, nie troszczcie się, jak albo co macie powiedzieć, bo w tej godzinie dowiecie się, co macie powiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '19', '19', 'Bo nie wy będziecie tymi, którzy mówią, ale Duch Ojca waszego, który przez was będzie mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '20', '20', 'Brat wyda na śmierć brata, a ojciec syna; dzieci wystąpią przeciwko rodzicom i będą ich zabijali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '21', '21', 'I wszyscy was znienawidzą z powodu imienia mojego. Ale kto wytrwa do końca, ten będzie zbawiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '22', '22', 'Kiedy was będą ścigać w jednym mieście, uciekajcie do innego. Zaprawdę, powiadam wam: Wystarczy dla was miast izraelskich do czasu, gdy przyjdzie Syn Człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '23', '23', 'Nie jest uczeń nad mistrza ani sługa nad pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '24', '24', 'Wystarczy, że uczeń dorówna nauczycielowi, a niewolnik panu. Jeżeli pana domu nazwali Beelzebulem, to o ileż bardziej jego domowników!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '25', '25', 'Więc nie lękajcie się ich, nie ma bowiem nic skrytego, co by nie wyszło na jaw, ani tajemnego, o czym by się nie dowiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '26', '26', 'Co wam mówię w ciemności, mówcie w świetle, i co słyszycie na ucho, ogłaszajcie na dachach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '27', '27', 'I nie lękajcie się tych, co zabijają ciało, a duszy nie mogą zabić. Bójcie się raczej tego, który może duszę i ciało zatracić w piekle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '28', '28', 'Czyż nie sprzedaje się dwóch wróbli za jednego asa, a nawet jeden z nich nie spada na ziemię bez woli Ojca waszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '29', '29', 'Nawet wszystkie włosy na waszej głowie są policzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '30', '30', 'Więc nie lękajcie się! Wyście więcej warci niż wiele wróbli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '31', '31', 'A do każdego, kto przyzna się do Mnie przed ludźmi, przyznam się i Ja przed Ojcem moim w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '32', '32', 'A tego kto by zaparł się Mnie przed ludźmi, zaprę się i Ja przed Ojcem moim w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '33', '33', 'Nie sądźcie, że przyszedłem na ziemię, aby przynieść pokój. Nie przyszedłem, aby przynieść pokój, ale miecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '34', '34', 'Bo przyszedłem poróżnić syna \'z ojcem, a córkę z matką i synową z teściową\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '35', '35', 'I (tak): domownicy staną się wrogami człowieka\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '36', '36', 'Kto kocha ojca lub matkę bardziej niż Mnie, nie jest Mnie wart, i kto kocha syna albo córkę bardziej niż Mnie, nie jest Mnie wart.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '37', '37', 'I kto nie bierze swego krzyża i nie idzie za Mną, nie jest Mnie wart.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '38', '38', 'Kto zachowa życie - straci je, a kto straci życie z mojego powodu - zachowa je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '39', '39', 'Kto was przyjmuje, Mnie przyjmuje, a kto Mnie przyjmuje, przyjmuje Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '40', '40', 'Kto przyjmuje proroka jako proroka, otrzyma zapłatę za proroka. I kto przyjmuje sprawiedliwego jako sprawiedliwego, otrzyma zapłatę za sprawiedliwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '9', '41', '41', 'A jeśliby ktoś podał kubek wody jednemu z tych najmniejszych tylko dlatego, że jest uczniem, to zapewniam was, nie ominie go zapłata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '0', '0', 'Kiedy Jezus skończył dawanie poleceń swoim dwunastu uczniom, odszedł stamtąd, aby nauczać i głosić (ewangelię) w miastach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '1', '1', 'Jan zaś, usłyszawszy w więzieniu o czynach Jezusa, wysłał do Niego uczniów z zapytaniem:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '2', '2', '- Czy to Ty jesteś tym, który ma przyjść, czy też mamy czekać na innego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '3', '3', 'A Jezus im odpowiedział: - Idźcie, opowiedzcie Janowi, co słyszycie i co widzicie:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '4', '4', '\'Ślepi widzą\', ludzie o bezwładnych nogach chodzą, trędowaci zostają oczyszczeni, a głusi słyszą. Umarli zmartwychwstają, \'a ubogim głosi się dobrą nowinę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '5', '5', 'Szczęśliwy ten, kto nie zgorszy się z mojego powodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '6', '6', 'Kiedy odeszli, Jezus zaczął mówić do tłumów o Janie: - Coście wyszli oglądać na pustyni? Trzcinę chwiejącą się na wietrze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '7', '7', 'Ale coście wyszli zobaczyć? Człowieka otulonego w miękkie szaty? Ci, co noszą miękkie szaty, mieszkają w królewskich komnatach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '8', '8', 'Coście więc wyszli zobaczyć? Proroka? O tak! Powiadam wam, więcej niż proroka!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '9', '9', 'To jest Ten, o którym napisano: \'Oto ja posyłam przed tobą mojego posłańca, który przygotuje ci drogę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '10', '10', 'Zaprawdę, mówię wam: Nie narodził się nikt większy od Jana Chrzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '11', '11', 'A od czasów Jana Chrzciciela aż dotąd królestwo niebieskie zdobywa się wielkim wysiłkiem i (tylko) ludzie silni je zagarniają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '12', '12', 'Wszyscy Prorocy i Prawo prorokowali aż do czasów Jana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '13', '13', 'A jeśli zechcecie to przyjąć: on jest właśnie Eliaszem, który ma przyjść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '14', '14', 'Kto ma uszy, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '15', '15', 'Do kogo mógłbym przyrównać to pokolenie? Jest ono podobne do dzieci siedzących na rynku, które wołają do swoich rówieśników:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '16', '16', 'Graliśmy wam na flecie, a nie tańczyliście, i zawodziliśmy - a nie płakaliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '17', '17', 'Przyszedł Jan, nie je ani nie pije, a mówią: Czarta ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '18', '18', 'Przyszedł Syn Człowieczy, je i pije, a mówią: Oto obżartuch i pijak, przyjaciel celników i grzeszników. I znalazła mądrość usprawiedliwienie w swoich czynach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '19', '19', 'Wtedy zaczął czynić wymówki miastom, w których dokonał najwięcej cudów, że się nie nawróciły: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '20', '20', 'Biada ci, Chorazin, biada ci, Betsaido, bo gdyby w Tyrze albo w Sydonie działy się te cuda, które u was się działy, od dawna by się nawróciły we włosiennicy i w popiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '21', '21', 'Toteż powiadam wam: Tyrowi i Sydonowi lżej będzie w dzień sądu niż wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '22', '22', 'A ty, Kafarnaum, czyż \'aż do nieba będziesz się wynosić? Aż na dno piekła spadniesz!\' Bo gdyby w Sodomie działy się cuda, które działy się u ciebie, przetrwałaby do dzisiaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '23', '23', 'Toteż powiadam wam: Ziemi Sodomitów lżej będzie w dzień sądu niż tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '24', '24', 'W tym czasie Jezus powiedział: - Wysławiam Cię, Ojcze, Panie nieba i ziemi, że zakryłeś to przed mądrymi i uczonymi (w Prawie), a objawiłeś ludziom prostym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '25', '25', 'Tak, Ojcze, bo tak Ci się podobało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '26', '26', 'Ojciec dał mi wszystko, i nikt nie zna Syna, tylko Ojciec, i nikt nie zna Ojca, tylko Syn i ten, komu Syn zechce objawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '27', '27', 'Pójdźcie do Mnie wszyscy utrudzeni i uginający się pod ciężarem, a Ja wam dam wytchnienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '28', '28', 'Weźcie moje jarzmo i uczcie się ode Mnie, bo jestem łagodny i pokornego serca, a znajdziecie odpoczynek dla dusz waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '10', '29', '29', 'Albowiem moje jarzmo jest słodkie, a brzemię lekkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '0', '0', 'W tym czasie Jezus przechodził w szabat wśród zbóż; uczniowie Jego byli głodni, zaczęli więc zrywać kłosy i jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '1', '1', 'Ujrzawszy to faryzeusze, powiedzieli Mu: - Popatrz, Twoi uczniowie robią to, czego w szabat robić nie wolno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '2', '2', 'Powiedział im: - Czyście nie czytali o tym, co zrobił Dawid, kiedy był głodny, i jego towarzysze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '3', '3', 'Jak wszedł do domu Bożego i zjadł chleby złożone w ofierze, których nie wolno było jeść ani jemu, ani jego towarzyszom, lecz tylko kapłanom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '4', '4', 'Albo czyście nie czytali w Prawie, że w dzień szabatu kapłani naruszają szabat w świątyni, a są bez winy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '5', '5', 'A Ja wam powiadam: Tu jest coś więcej niż świątynia!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '6', '6', 'Gdybyście zrozumieli, co to znaczy: \'Miłosierdzia chcę bardziej niż ofiary\', nie potępialibyście niewinnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '7', '7', 'Syn Człowieczy jest Panem szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '8', '8', 'Kiedy odszedł stamtąd, wszedł do synagogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '9', '9', 'A był tam człowiek z uschniętą ręką. (Faryzeusze), aby oskarżyć (Jezusa), zapytali Go, czy w szabat można uzdrawiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '10', '10', 'On im powiedział: - Któż z was, będąc właścicielem jedynej owcy, która by w szabat wpadła do dołu, nie chwyciłby jej i nie wyciągnął?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '11', '11', 'A o ileż więcej wart jest człowiek od owcy. Można więc czynić dobrze w szabat!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '12', '12', 'Potem mówi do człowieka: - Wyciągnij twoją rękę. I wyciągnął ją. I była zdrowa jak ta druga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '13', '13', 'A faryzeusze, wyszedłszy, naradzali się, jak by Go zgładzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '14', '14', 'Wiedząc o tym, Jezus usunął się stamtąd. A szło za Nim wielu - i uzdrowił ich wszystkich,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '15', '15', 'i przykazał im surowo, aby tego nie rozgłaszali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '16', '16', 'Tak wypełniły się słowa proroka Izajasza:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '17', '17', '\'Oto mój sługa, którego wybrałem, ukochany mój, którego sobie upodobałem, dam Mu Ducha mojego, i ogłosi sąd narodom pogańskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '18', '18', 'Nie będzie się spierał ani krzyczał i nikt nie usłyszy Jego głosu na ulicach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '19', '19', 'nadłamanej trzciny nie dołamie i tlejącego się knota nie dogasi, aż nie doprowadzi sądu do końca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '20', '20', 'A w Jego imieniu narody pogańskie będą pokładały nadzieję\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '21', '21', 'Wtedy przyprowadzono do Niego ślepego niemowę, opętanego przez czarta. I uzdrowił go tak, że niemy mówił i widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '22', '22', 'I wszystkie tłumy z najwyższym podziwem mówiły: - Czy ten nie jest synem Dawida?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '23', '23', 'Faryzeusze zaś, usłyszawszy to, powiedzieli: - On wyrzuca czarty mocą Beelzebula, władcy czartów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '24', '24', 'A Jezus, wiedząc, co mieli na myśli, powiedział: - Każde królestwo, które niezgoda rozdziela, ulega spustoszeniu. I żadne miasto albo dom, które niezgoda rozdziela, nie ostoi się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '25', '25', 'A jeśli szatan wyrzuca szatana, to rozdzieliła ich niezgoda. Jakżeż więc ostoi się ich królestwo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '26', '26', 'Jeżeli zaś Ja wyrzucam czarty mocą Beelzebula, to czyją mocą wyrzucają je wasi synowie? Dlatego oni będą waszymi sędziami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '27', '27', 'A jeśli Ja wyrzucam czarty mocą Bożą, to przyszło do was królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '28', '28', 'A czy ktoś może wejść do domu mocarza i zagrabić jego mienie, jeżeli wpierw go nie zwiąże? Wtedy (dopiero) ograbi jego dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '29', '29', 'Kto nie jest ze Mną, jest przeciwko Mnie. I kto nie zbiera ze Mną, rozprasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '30', '30', 'Dlatego wam powiadam: Każdy grzech i bluźnierstwo będzie ludziom odpuszczone, ale bluźnierstwo przeciwko Duchowi nie będzie odpuszczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '31', '31', 'Jeśli ktoś powie słowo przeciwko Synowi Człowieczemu, będzie mu odpuszczone, lecz jeśli powie przeciwko Duchowi Świętemu, nie będzie mu odpuszczone ani w tym wieku, ani w przyszłym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '32', '32', 'Albo uznajecie, że drzewo jest dobre i jego owoc dobry, albo uznajecie, że drzewo jest złe i jego owoc zły, bo drzewo poznaje się po owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '33', '33', 'Żmije, jakżeż możecie mówić dobrze o Mnie, kiedy sami jesteście źli? To przecież ciśnie się na usta, co przepełnia serce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '34', '34', 'Dobry człowiek wydobywa dobre rzeczy ze skarbca, w którym jest dobro, a zły człowiek wydobywa złe rzeczy ze skarbca, w którym jest zło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '35', '35', 'A Ja wam powiadam: W dniu sądu ludzie zdadzą sprawę z każdego niepotrzebnego słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '36', '36', 'Bo na podstawie tych słów będziesz uznany za sprawiedliwego i na podstawie twoich słów zostaniesz uznany za winnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '37', '37', 'Wtedy odpowiedzieli Mu niektórzy nauczyciele Pisma i faryzeusze: - Nauczycielu, domagamy się znaku od Ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '38', '38', 'On im odpowiedział: - Plemię złe i wiarołomne poszukuje znaku, ale otrzyma tylko znak proroka Jonasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '39', '39', 'Jak bowiem \'Jonasz był trzy dni i trzy noce we wnętrzu wielkiej ryby\', tak Syn Człowieczy będzie trzy dni i trzy noce w sercu ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '40', '40', 'Mieszkańcy Niniwy staną na sądzie razem z tym pokoleniem i potępią ich, bo nawrócili się na nawoływanie Jonasza, a oto tutaj więcej niż Jonasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '41', '41', 'Królowa z Południa stanie na sądzie razem z tym pokoleniem i potępi ich, bo przyszła z krańców ziemi, aby słuchać mądrości Salomona, a oto tutaj więcej niż Salomon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '42', '42', 'Kiedy duch nieczysty wyjdzie z człowieka, błąka się po miejscach bezwodnych, szukając odpoczynku, lecz nie znajduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '43', '43', 'Wtedy mówi: Powrócę do mego domu, z którego wyszedłem. I powróciwszy zastaje dom nie zajęty, zamieciony i przyozdobiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '44', '44', 'Wtedy idzie i bierze ze sobą siedem innych duchów gorszych od siebie, i wchodzi tam, i mieszka. I koniec tego człowieka staje  się gorszy od początku. Tak też będzie z tym złym pokoleniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '45', '45', 'Kiedy On to jeszcze mówił do tłumu, matka Jego i bracia stanęli z boku, chcąc z Nim porozmawiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '46', '46', 'A ktoś Mu powiedział: - Matka i bracia Twoi stanęli z boku, chcąc porozmawiać z Tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '47', '47', 'On zaś odpowiedział mówiącemu doń: - Kto jest moją matką i kto moimi braćmi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '48', '48', 'I wskazując na swoich uczniów, powiedział: - Oto moja matka i moi bracia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '11', '49', '49', 'bo kto wypełnia wolę Ojca mego, który jest w niebie, ten jest moim bratem i siostrą, i matką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '0', '0', 'Tego dnia Jezus wyszedł z domu i siedział nad morzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '1', '1', 'I zgromadziły się wokół Niego tak wielkie tłumy, że wszedł do łodzi i usiadł. A cały tłum stał na brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '2', '2', 'I mówił im wiele w przypowieściach: - Oto siewca wyszedł, aby siać (ziarno).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '3', '3', 'A kiedy siał, jedne padły przy drodze i ptaki nadleciały i zjadły je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '4', '4', 'A inne upadły na grunt skalisty, gdzie nie miały wiele ziemi i zaraz powschodziły, bo nie było głęboko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '5', '5', 'Skoro jednak wzeszło słońce, wypaliło je i uschły, bo nie miały korzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '6', '6', 'A inne padły między osty i osty wyrosły wysoko, i zadusiły je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '7', '7', 'Inne zaś padły na ziemię, na dobrą, i wydały owoc: jedno stokrotny, drugie sześćdziesięciokrotny, a inne trzydziestokrotny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '8', '8', 'Kto ma uszy, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '9', '9', 'Uczniowie podszedłszy do Niego zapytali: - Dlaczego mówisz do nich w przypowieściach?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '10', '10', 'Odpowiedział: - Wam dano poznać tajemnicę królestwa niebieskiego, a im tego nie dano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '11', '11', 'Temu bowiem, kto ma, będzie (więcej) dodane i będzie miał w nadmiarze. A temu, kto nie ma, i to, co ma będzie zabrane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '12', '12', 'Dlatego mówię do nich w przypowieściach, że patrząc nie widzą, a słuchając nie słyszą ani nie rozumieją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '13', '13', 'I spełnia się na nich proroctwo Izajasza, które mówi: \'Będziecie słuchać, a nie zrozumiecie, będziecie patrzeć, a nie zobaczycie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '14', '14', 'bo skamieniało serce tego ludu; z trudem słyszeli i przymknęli oczy, aby nie zobaczyli i nie usłyszeli, i nie zrozumieli, i nie nawrócili się, i abym ich nie uleczył\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '15', '15', 'A wasze oczy szczęśliwe, że widzą, i uszy, że słyszą!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '16', '16', 'Bo zaprawdę, powiadam wam: Wielu proroków i sprawiedliwych pragnęło zobaczyć, co wy widzicie, a nie zobaczyli, i słyszeć to, co wy słyszycie, a nie słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '17', '17', 'Wy zatem słuchajcie przypowieści o siewcy:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '18', '18', 'Do każdego, kto słucha słów o królestwie i nie rozumie ich, przychodzi zły i porywa to, co było zasiane w jego sercu. To jest ten posiany obok drogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '19', '19', 'A posiany na gruncie skalistym to ten, co słucha słów i zaraz z radością je przyjmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '20', '20', 'Nie ma jednak w sobie korzenia, lecz jest niestały, i kiedy przychodzi ucisk lub prześladowanie z powodu słowa, zaraz się załamuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '21', '21', 'A posiany między ostami to ten, co słucha słów, ale troska tego świata i ułuda bogactwa zaduszają słowo. I nie wydaje owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '22', '22', 'Posiany zaś na dobrej ziemi to ten, co słucha słów i rozumie je. Ten oczywiście przynosi owoc i daje: jeden stokrotny, drugi sześćdziesięciokrotny, a inny trzydziestokrotny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '23', '23', 'Opowiedział im inną przypowieść: Królestwo niebieskie jest podobne do człowieka obsiewającego swoje pole dobrym ziarnem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '24', '24', 'A kiedy ludzie spali, przyszedł jego nieprzyjaciel i nasiał między zboże kąkolu, i odszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '25', '25', 'Kiedy zboże urosło i wydało owoc, ukazał się i kąkol.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '26', '26', 'Słudzy gospodarza zapytali: Panie, czyż nie obsiałeś swego pola dobrym ziarnem? Skąd zatem wziął się kąkol? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '27', '27', 'On odpowiedział: Nieprzyjazny człowiek to uczynił. - A słudzy mu mówią: Jeśli chcesz, pójdziemy go zebrać. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '28', '28', 'On zaś na to: Nie, abyście zbierając kąkol nie powyrywali razem z nim pszenicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '29', '29', 'Pozwólcie obojgu róść aż do żniw. A w czasie żniw powiem żeńcom: Zbierzcie najpierw kąkol i zwiążcie w snopki na spalenie, a pszenicę znieście do spichrza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '30', '30', 'Opowiedział im inną przypowieść: Królestwo niebieskie podobne jest do ziarnka gorczycy, które człowiek zasiał na swoim polu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '31', '31', 'Jest ono mniejsze od wszystkich nasion, ale kiedy wyrośnie, jest większe od jarzyn i staje się drzewem, tak że ptaki latające w powietrzu przylatują i wiją sobie gniazda w jego gałęziach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '32', '32', 'Inną przypowieść opowiedział im: Królestwo niebieskie jest podobne do kwasu, który kobieta zaczyniła w trzech miarach mąki, aż się wszystka zakwasiła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '33', '33', 'To wszystko powiedział Jezus tłumom w przypowieściach, a bez przypowieści nic im nie mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '34', '34', 'Tak wypełniły się słowa proroka: \'Będę mówił w przypowieściach, powiem rzeczy ukryte\' od założenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '35', '35', 'Potem, pozwoliwszy odejść tłumom, poszedł do domu. Podeszli do Niego uczniowie, prosząc: - Wyjaśnij nam przypowieść o kąkolu na roli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '36', '36', 'On odpowiedział: - Ten, który sieje dobre ziarno, to Syn Człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '37', '37', 'A polem jest świat, dobrym nasieniem synowie królestwa, kąkolem synowie złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '38', '38', 'A nieprzyjacielem, który posiał kąkol, jest diabeł. Żniwem jest koniec świata, a żeńcami aniołowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '39', '39', 'Jak więc zbiera się kąkol i spala w ogniu, tak będzie na końcu świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '40', '40', 'Syn Człowieczy wyśle swoich aniołów i zbiorą z Jego królestwa wszystkie zgorszenia i tych, którzy dopuszczają się nieprawości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '41', '41', 'i wrzucą ich do rozpalonego pieca. Tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '42', '42', 'Wtedy \'sprawiedliwi będą jaśnieć jak słońce w królestwie swego Ojca\'. Kto ma uszy, niechaj słucha!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '43', '43', 'Królestwo niebieskie jest podobne do skarbu ukrytego w ziemi. Człowiek, który go znalazł, ukrył go znowu. Uradowany idzie i sprzedaje wszystko, co ma, i kupuje tę ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '44', '44', 'Królestwo Boże podobne jest również do kupca, który poszukuje pięknych pereł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '45', '45', 'A kiedy znalazł jedną drogocenną perłę, sprzedał wszystko, co miał, i kupił ją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '46', '46', 'Królestwo niebieskie podobne jest także do niewodu zarzuconego w morze i zagarniającego wszelkiego rodzaju ryby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '47', '47', 'Kiedy się napełnił, wyciągnęli go na brzeg i wybrali dobre ryby do naczyń, a złe wyrzucili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '48', '48', 'Tak będzie przy końcu świata. Wyjdą aniołowie i wyłączą złych spośród sprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '49', '49', 'I wrzucą ich do rozpalonego pieca. Tam będzie płacz i zgrzytanie zębów. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '50', '50', 'Czyście to wszystko zrozumieli? Mówią Mu: - Tak.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '51', '51', 'A On im powiedział: - Dlatego każdy nauczyciel Pisma, który stał się uczniem w królestwie niebieskim, podobny jest do gospodarza, wydobywającego ze swego skarbca nowe i stare rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '52', '52', 'Kiedy Jezus skończył te przypowieści, odszedł stamtąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '53', '53', 'A przyszedłszy do swojej ojczyzny tak ich nauczał w synagodze, że byli zdumieni i pytali: - Skąd u Niego ta mądrość i nadziemskie moce?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '54', '54', 'Czyż to nie jest syn cieśli? Czyż Jego matka nie nazywa się Maryja, a Jego bracia Jakub i Józef, i Szymon, i Juda?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '55', '55', 'I czyż wszystkie jego siostry nie żyją wśród nas? Skądże więc u Niego to wszystko się bierze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '56', '56', 'I gorszyli się Nim. A Jezus im powiedział: - Tylko w swojej ojczyźnie i swoim domu prorok nie zaznaje czci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '12', '57', '57', 'I dla ich niedowiarstwa niewiele razy okazał tam nadziemską moc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '0', '0', 'W tym czasie tetrarcha Herod usłyszał o Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '1', '1', 'I powiedział do swoich dworzan: - To jest Jan Chrzciciel. On zmartwychwstał i dlatego działają przez niego nadziemskie moce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '2', '2', 'Herod ujął Jana, zakuł go w kajdany i wtrącił do więzienia z powodu Herodiady, żony jego brata, Filipa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '3', '3', 'Jan mówił mu bowiem: - Nie wolno ci jej mieć za żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '4', '4', 'I chciał (Herod) go zabić, ale bał się tłumu, bo mieli go za proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '5', '5', 'W dzień urodzin Heroda córka Herodiady tańczyła dla gości. I spodobała się Herodowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '6', '6', 'Toteż obiecał jej pod przysięgą, że spełni wszystko, o cokolwiek by poprosiła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '7', '7', 'Ona zaś namówiona przez swoją matkę powiedziała: - Daj mi tu zaraz na półmisku głowę Jana Chrzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '8', '8', 'Król zasmucił się, ale kazał dać ze względu na przysięgę i współbiesiadników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '9', '9', 'I kazał ściąć Jana w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '10', '10', 'I przyniesiono jego głowę na półmisku, i dano dziewczynie, a ona zaniosła ją matce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '11', '11', 'A uczniowie Jana zabrali ciało i pochowali je, i donieśli o tym Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '12', '12', 'Jezus usłyszawszy o tym odpłynął stamtąd łodzią na miejsce pustynne i ustronne. Lecz tłumy dowiedziawszy się o tym pociągnęły za Nim z miast pieszo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '13', '13', 'Kiedy wysiadł z łodzi, zobaczył wielki tłum. I ulitował się nad nimi,  i uzdrowił chorych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '14', '14', 'A kiedy nastał wieczór, podeszli do Niego uczniowie, mówiąc: - Miejsce jest odludne i pora już spóźniona. Rozpuść tłumy, niech idą do wsi i kupią sobie chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '15', '15', 'A Jezus im powiedział: - Nie muszą odchodzić, wy dajcie im jeść!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '16', '16', 'Mówią Mu: - Mamy tylko pięć chlebów i dwie ryby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '17', '17', 'Odpowiedział: - Przynieście mi je tutaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '18', '18', 'A gdy rozkazał tłumom usiąść na trawie i wziął pięć chlebów i dwie ryby, i podniósł oczy w niebo, wielbił Boga i połamawszy chleby dawał uczniom, a uczniowie rozdawali je tłumom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '19', '19', 'I wszyscy zjedli, i najedli się do syta. I zebrali resztki: dwanaście koszyków pełnych okruchów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '20', '20', 'A tych, którzy jedli, było około pięciu tysięcy mężczyzn, nie licząc kobiet i dzieci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '21', '21', 'Zaraz też nakłonił swoich uczniów, aby weszli do łodzi i popłynęli przed Nim, a On tymczasem rozpuści tłumy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '22', '22', 'Rozpuściwszy je wszedł na górę, aby się pomodlić w samotności. A kiedy nastał wieczór, był tam zupełnie sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '23', '23', 'Łódź zaś rzucona przez fale - bo wiał przeciwny wiatr - oddaliła się już od brzegu o wiele stadiów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '24', '24', 'O czwartej straży nocnej przyszedł do nich po morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '25', '25', 'Uczniowie zaś, ujrzawszy Go idącego po falach morskich, przestraszyli się, sądząc, że to zjawa, i krzyknęli ze strachu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '26', '26', 'A Jezus zaraz przemówił do nich: - Odwagi, to Ja, nie bójcie się!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '27', '27', 'Piotr Mu odpowiedział: - Jeśliś to Ty, Panie, każ mi przyjść do siebie po wodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '28', '28', 'On zaś powiedział: - Chodź! A Piotr wyszedł z łodzi i szedł po wodzie. I przyszedł do Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '29', '29', 'Ale widząc gwałtowną wichurę, przestraszył się i zaczął tonąć. I krzyknął: - Panie, ratuj mnie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '30', '30', 'A Jezus zaraz wyciągnąwszy rękę pochwycił go. I mówi mu: - Słabej wiary, czemuś zwątpił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '31', '31', 'A kiedy wsiedli do łodzi, wiatr ustał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '32', '32', 'A siedzący w łodzi oddali Mu pokłon, mówiąc: - Ty naprawdę jesteś Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '33', '33', 'Przeprawiwszy się na drugi brzeg, przybyli do ziemi Genezaret.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '34', '34', 'Kiedy tamtejsi mieszkańcy rozpoznali Go, rozesłali gońców po całej okolicy i poznosili do Niego wszystkich chorych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '13', '35', '35', 'I prosili Go, aby mogli się dotknąć choćby kraju Jego okrycia. A ci, którzy się dotknęli, odzyskiwali zdrowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '0', '0', 'Wtedy przyszli do Jezusa z Jerozolimy faryzeusze i nauczyciele Pisma, mówiąc: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '1', '1', 'Dlaczego Twoi uczniowie nie zachowują nakazu starszych: Nie obmywają rąk, kiedy mają jeść chleb?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '2', '2', 'Odpowiedział im: - A dlaczegoż to i wy nie zachowujecie przykazania Bożego dla zachowania waszych nakazów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '3', '3', 'Bóg bowiem powiedział: \'Czcij ojca i matkę, a temu, kto złorzeczy ojcu lub matce, śmierć!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '4', '4', 'A wy twierdzicie: Jeśli ktoś powie ojcu albo matce: to, co tobie miałem dać na utrzymanie, ofiarowałem Bogu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '5', '5', 'ten już nie potrzebuje czcić swego ojca lub matki. W ten sposób przekreślacie przykazanie Boże dla (zachowania) waszego nakazu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '6', '6', 'Obłudnicy! Słusznie powiedział o was prorok Izajasz:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '7', '7', '\'Ten lud czci mnie wargami, ale jego serce daleko jest ode mnie;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '8', '8', 'i czczą mnie na próżno, głosząc nauki, które są nakazami ludzkimi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '9', '9', 'I przywoławszy tłum powiedział: - Słuchajcie i zapamiętajcie to sobie:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '10', '10', 'Nie to plami człowieka, co wchodzi do ust, ale to go plami, co z ust wychodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '11', '11', 'A uczniowie podszedłszy do Niego zapytali: - Czy wiesz, że faryzeusze byli oburzeni, słysząc te słowa?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '12', '12', 'On zaś odpowiedział: - Każda roślina, której nie zasadził mój Ojciec niebieski, będzie wyrwana z korzeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '13', '13', 'Zostawcie ich w spokoju. To ślepi przewodnicy! A jeśli ślepy prowadzi ślepego, to obaj wpadną do dołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '14', '14', 'Piotr Mu odpowiedział: - Wyjaśnij nam tę przypowieść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '15', '15', 'On zaś rzekł: - To i wy jeszcze nie pojmujecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '16', '16', 'Nie rozumiecie, że wszystko, co wchodzi do ust, idzie do brzucha i zostaje wydalone na zewnątrz,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '17', '17', 'a co wychodzi z ust, z serca wychodzi, i to plami człowieka?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '18', '18', 'Bo z serca wychodzą złe myśli, zabójstwa, cudzołóstwa, nieczystości, kradzieże, fałszywe zeznania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '19', '19', 'To one plamią człowieka! A jedzenie nie obmytymi rękami nie plami człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '20', '20', 'I wyszedłszy stamtąd usunął się w okolice Tyru i Sydonu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '21', '21', 'A pochodząca z tamtejszych stron Chananejka wyszła i zawołała: - Panie, Synu Dawida, zmiłuj się nade mną! Czart bardzo dręczy moją córkę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '22', '22', 'On zaś ani słowem nie odezwał się do niej. A uczniowie podszedłszy do Niego prosili: - Odpraw ją, bo krzyczy za nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '23', '23', 'On odpowiedział: - Zostałem posłany tylko do tych owiec, które zginęły z domu Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '24', '24', 'Ona zaś oddała Mu pokłon, mówiąc: - Panie, pomóż mi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '25', '25', 'On zaś odpowiedział: -  Nie godzi się odejmować chleba od ust dzieciom, a rzucać go szczeniętom. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '26', '26', 'Tak, Panie! - odpowiedziała - ale i szczenięta jadają resztki spadające ze stołów ich panów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '27', '27', 'Wtedy Jezus odpowiedział jej: - Niewiasto, wielka jest twoja wiara, niech spełni się twoja prośba! I od tej chwili jej córka wyzdrowiała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '28', '28', 'A Jezus odszedł stamtąd i przyszedł nad Morze Galilejskie, wszedł na górę i tam usiadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '29', '29', 'I przyszły do Niego wielkie tłumy, prowadzące ze sobą kulawych, ślepych, kalekich, głuchych, niemych i wielu innych. Pokładli ich u Jego stóp. I uzdrowił ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '30', '30', 'A tłum dziwił się widząc, że niemi mówią, kalecy wracają do zdrowia, kulawi chodzą, a ślepi widzą. I wielbili Boga Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '31', '31', 'A Jezus przywoławszy swoich uczniów powiedział: - Żal mi tego tłumu, bo już trzy dni trwają przy Mnie i nie mają co jeść. A nie chcę ich puścić głodnych, aby nie ustali w drodze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '32', '32', 'Mówią Mu uczniowie: - Skąd na tym pustkowiu weźmiemy tyle chleba, aby taki tłum najadł się do syta?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '33', '33', 'A Jezus na to? - Ile macie chlebów? Odpowiedzieli: - Siedem i kilka rybek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '34', '34', 'I rozkazał tłumowi usiąść na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '35', '35', 'Wziął siedem chlebów i ryby i, odmówiwszy modlitwę dziękczynną, połamał chleb i podał uczniom, a uczniowie rozdali tłumom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '36', '36', 'I wszyscy jedli, i najedli się do syta. I zebrali resztki: siedem koszyków pełnych okruchów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '37', '37', 'A tych, którzy jedli, było około czterech tysięcy mężczyzn, nie licząc kobiet i dzieci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '14', '38', '38', 'Rozpuściwszy tłumy wszedł do łodzi i przybył na granice Magedan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '0', '0', 'Faryzeusze i saduceusze prosili Go podstępnie, aby dał im znak z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '1', '1', 'On zaś odpowiedział: - Kiedy zapada wieczór, mówicie: Będzie pogoda, bo niebo się czerwieni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '2', '2', 'a rano: Dzisiaj będzie deszcz, bo czerwieni się zachmurzone niebo. Pogodę po wyglądzie nieba umiecie rozpoznawać, a znaków czasu nie potraficie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '3', '3', 'Pokolenie złe i wiarołomne domaga się znaku, ale nie otrzyma innego znaku oprócz znaku Jonasza. I odszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '4', '4', 'Uczniowie, przeprawiając się na drugi brzeg, zapomnieli zabrać chleb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '5', '5', 'A Jezus im powiedział: - Strzeżcie się pilnie kwasu faryzeuszów i saduceuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '6', '6', 'Oni zaś mówili między sobą: - Nie zabraliśmy chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '7', '7', 'A Jezus, poznawszy to, powiedział: - Słabej wiary, dlaczego mówicie między sobą, że nie macie chleba?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '8', '8', 'Czyż jeszcze nie rozumiecie i nie pamiętacie tych pięciu chlebów dla pięciu tysięcy i tego, ileście koszyków wtedy zebrali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '9', '9', 'Ani o siedmiu chlebach dla czterech tysięcy i tego, ileście wtedy koszyków zebrali?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '10', '10', 'Jak możecie nie rozumieć, że nie miałem na myśli chlebów (mówiąc): Strzeżcie się kwasu faryzeuszów i saduceuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '11', '11', 'Wtedy zrozumieli, że nie kazał im wystrzegać się kwasu chlebowego, ale nauki faryzeuszów i saduceuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '12', '12', 'Przyszedłszy w okolice Cezarei Filipowej, Jezus spytał swoich uczniów: - Za kogo ludzie mają Syna Człowieczego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '13', '13', 'Odpowiedzieli: - Jedni za Jana Chrzciciela, drudzy za Eliasza, a jeszcze inni za Jeremiasza albo za jakiegoś innego proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '14', '14', 'Pyta ich: - A wy za kogo Mnie macie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '15', '15', 'Szymon Piotr odpowiedział: - Ty jesteś Mesjasz, Syn Boga żywego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '16', '16', 'A Jezus mu odrzekł: - Szczęśliwy jesteś, Szymonie, synu Jana, dlatego że nie człowiek ci to objawił, ale mój Ojciec, który jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '17', '17', 'A Ja tobie mówię: Ty jesteś opoką i na tej opoce zbuduję mój Kościół, a bramy piekielne nie zwyciężą go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '18', '18', 'I tobie dam klucze królestwa niebieskiego i cokolwiek zwiążesz na ziemi, będzie związane w niebie, a cokolwiek rozwiążesz na ziemi, będzie rozwiązane w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '19', '19', 'Wtedy surowo nakazał uczniom, żeby nikomu nie mówili o tym, że On jest Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '20', '20', 'Odtąd Jezus zaczął otwarcie mówić swoim uczniom, że musi iść do Jerozolimy, że ma wiele wycierpieć od starszyzny i arcykapłanów, i nauczycieli Pisma i umrzeć, a na trzeci dzień zmartwychwstać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '21', '21', 'A Piotr, wziąwszy Go na bok, zaczął Go strofować: - Miej litość nad sobą, Panie, to Cię nie może spotkać!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '22', '22', 'A (Jezus) odwróciwszy się powiedział Piotrowi: - Zejdź mi z oczu, kusicielu, nakłaniasz Mnie do złego, bo nie po Bożemu myślisz, ale po ludzku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '23', '23', 'A swoim uczniom powiedział: - Kto chce iść ze Mną, niech wyrzecze się samego siebie, niech weźmie swój krzyż i niech postępuje jak Ja..');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '24', '24', 'Kto chciałby zachować swoje życie, straci je. A kto straciłby swoje życie dla Mnie, odzyska je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '25', '25', 'Bo cóż pomoże człowiekowi, że cały świat zdobędzie, jeśli życie utraci? Albo czegóż by człowiek nie oddał za swoje życie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '26', '26', 'A Syn Człowieczy przyjdzie w chwale swego Ojca z aniołami swymi i wtedy odda każdemu według jego uczynków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '15', '27', '27', 'Zaprawdę, powiadam wam: Wśród obecnych tutaj są tacy, którzy nie umrą, zanim nie ujrzą Syna Człowieczego przychodzącego w swoim królestwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '0', '0', 'A po sześciu dniach Jezus bierze Piotra, Jakuba i jego brata Jana i prowadzi ich na wysoką, ustronną górę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '1', '1', 'I na ich oczach zmienił się Jego wygląd: twarz Jego zajaśniała jak słońce, a szaty stały się białe jak blask (słoneczny).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '2', '2', 'I ukazali się im Eliasz i Mojżesz rozmawiający z Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '3', '3', 'Wtedy Piotr odezwał się do Jezusa: - Dobrze nam tu, Panie. Jeśli chcesz, rozbiję tu trzy namioty: jeden dla Ciebie, jeden dla Mojżesza i jeden dla Eliasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '4', '4', 'Kiedy jeszcze mówił, okrył ich świetlisty obłok, a z obłoku odezwał się głos: - To jest mój Syn ukochany, upodobałem Go sobie, Jego słuchajcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '5', '5', 'Usłyszawszy to uczniowie upadli na twarz i bardzo się przestraszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '6', '6', 'A Jezus podszedł i dotknąwszy ich powiedział: - Wstańcie, nie bójcie się!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '7', '7', 'A podniósłszy oczy nikogo oprócz Jezusa nie zobaczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '8', '8', 'Kiedy schodzili z góry, Jezus nakazał im: - Nie mówcie nikomu o tym widzeniu, zanim Syn Człowieczy nie zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '9', '9', 'Uczniowie zapytali Go: - Dlaczego to nauczyciele Pisma mówią, że najpierw ma przyjść Eliasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '10', '10', 'On zaś odpowiedział: - Eliasz przyjdzie i wszystko naprawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '11', '11', 'Ale powiadam wam, że Eliasz już przyszedł, lecz go nie poznali i postąpili z nim, jak chcieli. A także Synowi Człowieczemu nie oszczędzą cierpień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '12', '12', 'Wtedy uczniowie zrozumieli, że to, co im powiedział, odnosiło się do Jana Chrzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '13', '13', 'A kiedy przyszli do tłumu, podszedł do Niego człowiek i upadłszy przed Nim na kolana,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '14', '14', 'powiedział: - Panie, zmiłuj się nad moim synem, bo jest lunatykiem i cierpi z tego powodu. Często wpada w ogień lub wodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '15', '15', 'Przyprowadziłem go do Twoich uczniów, ale nie mogli go uzdrowić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '16', '16', 'Jezus odpowiedział: - Przewrotne pokolenie niedowiarków, jak długo będę z wami? Jak długo was będę znosił? Przyprowadźcie mi go tutaj!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '17', '17', 'Jezus nakazał mu surowo (wyjść z chłopca) i czart wyszedł z niego. I od tej chwili chłopiec był zdrowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '18', '18', 'Wtedy uczniowie na osobności zapytali Jezusa: - Dlaczegoż to my nie mogliśmy go wyrzucić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '19', '19', 'On zaś im mówi: - Z powodu waszej słabej wiary. Zaprawdę, powiadam wam: Jeśli macie wiarę jak ziarnko gorczycy, a powiecie tej górze: Przesuń się tam - to się przesunie, i nie ma rzeczy, która byłaby dla was niemożliwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '20', '20', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '21', '21', 'Kiedy byli razem w Galilei, Jezus im powiedział: - Syn Człowieczy zostanie wydany w ręce ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '22', '22', 'I zabiją Go, i na trzeci dzień zmartwychwstanie. A oni zasmucili się bardzo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '23', '23', 'Kiedy przybyli do Kafarnaum, podeszli do Piotra ludzie, którzy pobierali didrachmy, i rzekli: - Czy wasz nauczyciel nie płaci didrachmy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '24', '24', 'A on mówi: - Tak. A kiedy wszedł do domu, Jezus pierwszy go zapytał: - Jak myślisz, Szymonie, od kogo królowie na ziemi ściągają daniny lub podatki? Od swoich synów czy od obcych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '25', '25', 'Kiedy odpowiedział: Od obcych - Jezus mu rzekł: - A więc synowie są wolni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '16', '26', '26', 'Abyśmy jednak nie dali złego przykładu, idź nad morze, zarzuć wędkę, weź pierwszą  rybę, którą złowisz, i otworzywszy jej pysk znajdziesz stater. Daj go im za Mnie i za siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '0', '0', 'Wówczas uczniowie podeszli do Jezusa, pytając: - Kto też jest największy w królestwie niebieskim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '1', '1', 'A przywoławszy do siebie dziecko, postawił je na środku');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '2', '2', 'i powiedział: - Zaprawdę, powiadam wam: Jeżeli się nie nawrócicie i nie staniecie się jak dziatki, nie wejdziecie do królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '3', '3', 'Kto zatem uniży się jak to dziecię, ten będzie największy w królestwie niebieskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '4', '4', 'I kto by przyjął takie jedno dziecię w imię moje, Mnie przyjmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '5', '5', 'A kto by doprowadził do złego jednego z tych maluczkich, którzy we Mnie wierzą, lepiej dla niego, aby zawieszono mu na szyi kamień młyński i utopiono w głębokościach morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '6', '6', 'Biada światu z powodu zgorszenia! Zgorszenie przyjść musi, ale biada człowiekowi, który je sieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '7', '7', 'Jeśli twoja ręka lub noga pobudza cię do złego, odetnij ją i wyrzuć. Lepiej dla ciebie wejść do żywota bez ręki lub nogi, aniżeli z dwiema rękami i z dwiema nogami wpaść w ogień wieczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '8', '8', 'I jeśli twoje oko pobudza cię do złego, wyłup je i wyrzuć. Lepiej dla ciebie wejść do żywota bez jednego oka, aniżeli z dwojgiem oczu być wyrzuconym do piekła ognistego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '9', '9', 'Uważajcie, abyście nie pogardzali żadnym z tych maluczkich. Powiadam wam bowiem, że ich aniołowie zawsze widzą twarz mego Ojca w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '10', '10', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '11', '11', 'Jak myślicie? Jeśli człowiekowi, który ma sto owiec, zginie jedna z nich, to czyż nie zostawi w górach dziewięćdziesięciu dziewięciu i nie ruszy na poszukiwanie zabłąkanej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '12', '12', 'A gdy mu się uda ją znaleźć, zaprawdę powiadam wam, że cieszy się nią więcej niż dziewięćdziesięciu dziewięcioma, które nie zbłądziły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '13', '13', 'Tak też Ojciec wasz w niebie nie chce, aby zginął choćby jeden z tych maluczkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '14', '14', 'Jeśliby twój brat zgrzeszył, to idź upomnij go w cztery oczy. Jeśli cię usłucha, pozyskałeś brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '15', '15', 'A jeśli cię nie usłucha, weź z sobą jeszcze jednego albo dwóch, aby \'na zeznaniu dwóch albo trzech świadków opierała się cała sprawa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '16', '16', 'A jeśli ich nie posłucha, powiedz Kościołowi, a jeśli Kościoła nie posłucha, to niech będzie dla ciebie jak poganin i celnik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '17', '17', 'Zaprawdę powiadam wam, że cokolwiek zwiążecie na ziemi będzie związane w niebie, a cokolwiek rozwiążecie na ziemi, będzie rozwiązane w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '18', '18', 'I powiadam wam: Jeśli dwóch z was zgodnie o coś poprosi na ziemi, Ojciec mój w niebie da im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '19', '19', 'A gdzie dwóch albo trzech zbierze się w imię moje, tam Ja jestem pośród nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '20', '20', 'Wtedy Piotr powiedział Mu: - Panie, ile razy mam przebaczyć bratu, jeśli mi zawini? Czyż aż siedem razy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '21', '21', 'Powiada mu Jezus: - Nie mówię ci: aż siedem razy, ale aż siedemdziesiąt siedem razy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '22', '22', 'Dlatego królestwo niebieskie podobne jest do człowieka-króla, który chciał zrobić obrachunek ze swymi sługami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '23', '23', 'Kiedy zaczął robić obrachunek, przyprowadzono do niego jednego sługę, który mu był winien dziesięć tysięcy talentów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '24', '24', 'Ponieważ nie mógł ich oddać, pan kazał sprzedać jego i żonę, i dzieci, i wszystko, co miał, i tak oddać dług.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '25', '25', 'A sługa upadłszy do nóg oddał mu pokłon, mówiąc: Bądź cierpliwy, a oddam ci wszystko. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '26', '26', 'Pan ulitował się nad tym sługą, zostawił go na wolności i dług mu darował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '27', '27', 'A kiedy ów sługa wyszedł, spotkał jednego ze swoich towarzyszy, który był mu winien sto denarów. I pochwycił go, i zaczął dusić wołając: Oddaj, coś winien! -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '28', '28', 'A jego towarzysz upadłszy do nóg prosił go: Bądź cierpliwy, a oddam ci. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '29', '29', 'On jednak nie chciał, tylko odszedłszy wtrącił go do więzienia, dopóki nie odda długu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '30', '30', 'Towarzysze jego widząc, co się stało, zasmucili się bardzo i opowiedzieli swemu panu wszystko, co zaszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '31', '31', 'Wtedy pan wezwawszy go powiedział mu: - Sługo niegodziwy, cały dług ci podarowałem dlatego, żeś mnie prosił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '32', '32', 'A czyż i ty nie powinieneś zlitować się nad twoim towarzyszem, tak jak ja się nad tobą zlitowałem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '33', '33', 'I rozgniewawszy się, pan oddał go oprawcom, dopóki nie odda długu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '17', '34', '34', 'Tak też Ojciec mój niebieski uczyni z wami, jeżeli z serca nie przebaczycie każdy swojemu bratu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '0', '0', 'Kiedy Jezus skończył to nauczanie, usunął się z Galilei i przybył za Jordan na teren Judei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '1', '1', 'Poszły za Nim wielkie tłumy i tam ich uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '2', '2', 'Podeszli do Niego faryzeusze i zapytali Go podstępnie: - Czy wolno mężowi rozwieść się z żoną z jakiegokolwiek powodu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '3', '3', 'Odpowiedział: - Czy nie czytaliście, że \'Stwórca na początku stworzył ich mężczyzną i kobietą\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '4', '4', 'I powiedział: \'Dlatego mężczyzna opuści ojca i matkę i połączy się z żoną i oni dwoje staną się jednym ciałem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '5', '5', 'Tak że już nie są dwojgiem (ludzi), ale jednym człowiekiem. Tak więc co Bóg złączył, niech człowiek nie rozdziela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '6', '6', 'Mówią Mu: - To dlaczego Mojżesz kazał \'dać list rozwodowy i rozwieść się\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '7', '7', 'Mówi im: - Mojżesz pozwolił wam rozwodzić się z powodu waszego zatwardziałego serca, ale na początku tak nie było.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '8', '8', 'Powiadam wam, że każdy, kto rozwodzi się z żoną z wyjątkiem przypadku rozpusty, a żeni się z inną, cudzołoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '9', '9', 'Mówią mu Jego uczniowie: - Jeśli tak się ma sprawa męża z żoną, to nie warto się żenić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '10', '10', 'On zaś powiedział: - Nie wszyscy to pojmują, ale tylko ci, którym  to jest dane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '11', '11', 'Bo są tacy, którzy już z łona matki wyszli niezdolni do małżeństwa, i są tacy, których ludzie uczynili niezdolnymi do małżeństwa. Są wreszcie tacy, którzy sami siebie uczynili niezdolnymi do małżeństwa dla królestwa niebieskiego. Kto może pojąć, niech pojmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '12', '12', 'Wtedy przyprowadzono do Niego dzieci, aby położył na nie ręce i pomodlił się. Lecz uczniowie strofowali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '13', '13', 'A Jezus powiedział: - Pozwólcie dzieciom przyjść do Mnie i nie powstrzymujcie ich, bo do takich (jak one) należy królestwo niebieskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '14', '14', 'I położywszy na nie ręce odszedł stamtąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '15', '15', 'Pewien człowiek przystąpiwszy do Niego powiedział: - Nauczycielu, co dobrego mam uczynić, aby posiąść życie wieczne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '16', '16', 'Odpowiedział mu: - Dlaczego Mnie pytasz o dobro? Jeden tylko jest dobry. Jeśli chcesz wejść do życia, to zachowuj przykazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '17', '17', 'Pyta Go: - Które? - \'Nie będziesz zabijał - odpowiedział Jezus - nie będziesz cudzołożył, nie będziesz kradł, nie będziesz fałszywie zeznawał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '18', '18', 'czcij ojca i matkę i będziesz kochał bliźniego jak samego siebie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '19', '19', 'Mówi Mu młodzieniec: - Przestrzegałem tego wszystkiego, czegoż mi jeszcze brakuje? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '20', '20', 'Jeśli chcesz być doskonałym - rzekł mu Jezus - idź, sprzedaj, co posiadasz, i rozdaj ubogim, a będziesz miał skarb w niebie. Potem przyjdź i chodź za Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '21', '21', 'Usłyszawszy to młodzieniec odszedł zasmucony. Miał bowiem wielkie bogactwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '22', '22', 'Jezus zaś powiedział swoim uczniom: - Zaprawdę powiadam wam: Bogaty z trudem wejdzie do królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '23', '23', 'Powtarzam: Prędzej wielbłąd przejdzie przez ucho igielne, aniżeli bogaty wejdzie do królestwa niebieskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '24', '24', 'Usłyszawszy to uczniowie bardzo się dziwili, mówiąc: - Któż  więc może być zbawiony?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '25', '25', 'A Jezus spojrzawszy (na nich) powiedział: - Ludzie nie mogą, ale Bóg może wszystko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '26', '26', 'Wtedy Piotr Go zapytał: - Oto my opuściliśmy wszystko i poszliśmy za Tobą, cóż więc otrzymamy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '27', '27', 'Jezus im powiedział: - Zaprawdę powiadam wam: W nowym istnieniu, kiedy Syn Człowieczy zasiądzie na tronie w całej swojej chwale, wy, którzyście poszli za Mną, zasiądziecie również na dwunastu krzesłach, sądząc dwanaście pokoleń Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '28', '28', 'A każdy, kto opuścił dom albo braci, albo siostry, albo ojca, albo matkę, albo dzieci, albo zagrody dla mojego imienia, otrzyma stokroć więcej: odziedziczy życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '18', '29', '29', 'Wielu z pierwszych będzie ostatnimi, a (wielu) z ostatnich pierwszymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '0', '0', 'Bo królestwo niebieskie jest podobne do gospodarza, który wyszedł wczesnym rankiem najmować robotników do swojej winnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '1', '1', 'Ugodziwszy się z robotnikami za denara dziennie, wysłał ich do swojej winnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '2', '2', 'I wyszedłszy około trzeciej godziny zobaczył innych, stojących bezczynnie na rynku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '3', '3', 'Powiedział im również: Idźcie i  wy do winnicy, dam wam, co się należy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '4', '4', 'Oni poszli. Wyszedł jeszcze o szóstej i o dziewiątej godzinie i uczynił to samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '5', '5', 'A kiedy wyszedł około jedenastej godziny, zastał innych stojących. I mówi do nich: Czemu tu stoicie cały dzień bezczynnie? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '6', '6', 'Mówią mu: Bo nikt nas nie najął. - Mówi im: Idźcie i wy do winnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '7', '7', 'A kiedy zapadł wieczór, pan winnicy mówi do swego rządcy: Zwołaj robotników i daj im zapłatę, zaczynając od ostatnich a kończąc na pierwszych. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '8', '8', 'I przyszli (którzy zostali najęci) około jedenastej godziny, i dostali po denarze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '9', '9', 'A pierwsi sądzili, że dostaną więcej. Ale i oni otrzymali po denarze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '10', '10', 'A wziąwszy go, szemrali przeciw gospodarzowi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '11', '11', 'mówiąc: Ci ostatni przepracowali tylko jedną godzinę, a zrównałeś ich z nami, którzyśmy przez cały dzień znosili trud i upał. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '12', '12', 'Pan zaś odpowiedział jednemu z nich: Przyjacielu, nie wyrządzam ci krzywdy. Czyż nie zgodziłeś się ze mną za denara?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '13', '13', 'Zabieraj, co twoje, i idź. Chcę i temu ostatniemu dać (tyle), co tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '14', '14', 'Czyż nie wolno mi uczynić, co chcę ze swymi pieniędzmi? Albo czyżbyś krzywo patrzył, że jestem dobry? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '15', '15', 'Tak to ostatni będą pierwszymi, a pierwsi ostatnimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '16', '16', 'Idąc do Jerozolimy Jezus wziął Dwunastu osobno i w drodze im powiedział: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '17', '17', 'Oto idziemy do Jerozolimy, a Syn Człowieczy będzie wydany arcykapłanom i nauczycielom Pisma, i skażą Go na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '18', '18', 'I wydadzą Go poganom, aby Go wydrwili, ubiczowali i ukrzyżowali; a na trzeci dzień zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '19', '19', 'Wtedy podeszła do Niego matka synów Zebedeusza razem z synami, oddając Mu pokłon i prosząc Go o coś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '20', '20', 'On ją zapytał: - Czego chcesz? Mówi Mu: - Rozkaż, aby ci dwaj moi synowie siedzieli w Twym królestwie jeden po Twojej prawej stronie a drugi po lewej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '21', '21', 'Jezus odpowiedział: - Nie wiecie, o co prosicie. Czy możecie wypić kielich, który ja pić będę? Mówią Mu: - Możemy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '22', '22', 'Mówi im: - Kielich mój wprawdzie wypijecie, nie do mnie jednak należy dać wam miejsce po mojej prawej i lewej stronie. Te miejsca zajmą ci, dla których przygotował je mój Ojciec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '23', '23', 'Dziesięciu usłyszawszy to oburzyło się na dwóch braci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '24', '24', 'Lecz Jezus przywołał ich do siebie i powiedział: - Wiecie, że władcy panują nad narodami, a wielmoże dają im odczuć swoją władzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '25', '25', 'U was tak nie będzie. Lecz kto chciałby być wielkim wśród was, niech będzie sługą wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '26', '26', 'A kto by chciał być pierwszym, niech będzie waszym sługą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '27', '27', 'Bo i Syn Człowieczy nie przyszedł po to, aby Mu służono, ale by służyć i oddać swoje życie na okup za wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '28', '28', 'Kiedy wychodzili z Jerycha, szedł za nimi wielki tłum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '29', '29', 'A kiedy dwaj ślepcy siedzący przy drodze usłyszeli, że to Jezus przechodzi, krzyknęli: - Zmiłuj się nad nami, Synu Dawida!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '30', '30', 'Z tłumu kazano im milczeć, lecz oni jeszcze bardziej krzyczeli: - Panie, Synu Dawida, zmiłuj się nad nami!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '31', '31', 'A Jezus przystanąwszy zawołał ich i powiedział: - Co mam zrobić dla was?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '32', '32', 'Mówią Mu: - Panie, niech otworzą się nasze oczy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '19', '33', '33', 'A Jezus ulitowawszy się dotknął ich oczu i natychmiast przejrzeli i poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '0', '0', 'A kiedy zbliżyli się do Jerozolimy i przyszli do Betfage na Górze Oliwnej, Jezus wysłał dwóch uczniów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '1', '1', 'mówiąc im: - Idźcie do wsi (leżącej) naprzeciwko was, a zaraz znajdziecie uwiązaną oślicę z oślęciem. Odwiążcie ją i przyprowadźcie mi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '2', '2', 'A gdyby was ktoś zapytał, odpowiedzcie: Pan ich potrzebuje, ale zaraz je odeśle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '3', '3', 'A to się stało, aby wypełniły się słowa proroka, który mówi:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '4', '4', '\'Powiedzcie córce syjońskiej: Oto twój król przychodzi do ciebie, łagodny, siedzi na ośle, na oślęciu, młodym zwierzęciu jucznym\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '5', '5', 'Uczniowie poszli i wykonali to, co im Jezus polecił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '6', '6', 'Przyprowadzili oślicę z oślęciem i położyli na nich okrycia, a On usiadł na nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '7', '7', 'Ogromny zaś tłum rozpościerał swoje okrycia na drodze. Inni zaś obłamywali gałęzie z drzew i rzucali je na drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '8', '8', 'A tłumy idące przed Nim i za Nim wołały głośno: \'Hossanna\' Synowi Dawidowemu! Chwała temu, który przychodzi w imię Pana, \'Hosanna\' na wysokościach!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '9', '9', 'A kiedy wszedł do Jerozolimy, poruszyło się całe miasto: - Kto to jest? - pytano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '10', '10', 'A tłumy odpowiadały: - To prorok, Jezus z Nazaretu Galilejskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '11', '11', 'Jezus wszedł do świątyni i wyrzucił wszystkich sprzedających i kupujących w świątyni, poprzewracał stoły wymieniających pieniądze i ławki sprzedawców gołębi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '12', '12', 'I mówi im: - Napisano: \'Mój dom będzie domem modlitwy\', a wy robicie z niego \'jaskinę zbójców\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '13', '13', 'W świątyni podeszli do Niego ślepi i kulawi. I uzdrowił ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '14', '14', 'Arcykapłani zaś i nauczyciele Pisma ujrzawszy zdumiewające czyny, których dokonał, i dzieci wołające głośno: Hosanna Synowi Dawidowemu, oburzyli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '15', '15', 'I powiedzieli Mu: - Czy słyszysz, co one wołają? A Jezus im mówi: - Tak! Czyż nie czytaliście nigdy: \'Z ust niemowląt i ssących zgotowałeś sobie chwałę\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '16', '16', 'I zostawiwszy ich wyszedł z miasta do Betanii, i tam zanocował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '17', '17', 'Wracając wczesnym rankiem do miasta, poczuł głód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '18', '18', 'A ujrzawszy przy drodze drzewo figowe, podszedł do niego, ale nic na nim nie znalazł oprócz liści. I mówi mu: - Już nigdy nie wydasz owocu. I figa natychmiast uschła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '19', '19', 'Zobaczywszy to uczniowie mówili zdziwieni: - Dlaczego ta figa natychmiast uschła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '20', '20', 'A Jezus odpowiedział: - Zaprawdę, powiadam wam: Jeśli będziecie mieli wiarę niezachwianą, zrobicie nie tylko to, (co zrobiłem) z tą figą, ale kiedy powiecie tej górze: Unieś się i rzuć się do morza - to tak się stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '21', '21', 'I wszystko, o co poprosicie, modląc się z wiarą, otrzymacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '22', '22', 'Kiedy wszedł do świątyni i nauczał, przystąpili do Niego arcykapłani i starsi (ludu), pytając: - Jakim prawem to czynisz i kto Ci dał to prawo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '23', '23', 'Jezus im odpowiedział: - Ja też was o jedno zapytam; jeśli mi odpowiecie, to i Ja wam powiem, jakim prawem to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '24', '24', 'Skąd pochodził chrzest Jana: z nieba czy od ludzi? Oni zaś naradzali się między sobą, mówiąc: - Jeśli odpowiemy: Z nieba, powie nam: To dlaczegoście mu nie uwierzyli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '25', '25', 'Jeżeli zaś powiemy: Od ludzi, będziemy musieli obawiać się tłumu, bo wszyscy mają Jana za proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '26', '26', 'I odpowiedzieli Jezusowi: - Nie wiemy. A On im odrzekł: - To i Ja wam nie powiem, jakim prawem to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '27', '27', '- A jak myślicie? Pewien człowiek miał dwóch synów. I powiedział pierwszemu z nich: Idź, chłopcze, dzisiaj pracować w winnicy. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '28', '28', 'A on odpowiedział: Nie chcę! - Ale później poczuł wyrzuty sumienia i poszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '29', '29', 'I powiedział drugiemu to samo. A ten odrzekł: Dobrze, panie! - Ale nie poszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '30', '30', 'Który z tych dwóch spełnił wolę ojca? Mówią: - Ten pierwszy. Jezus im na to: - Zaprawdę, powiadam wam: Celnicy i nierządnice wchodzą przed wami do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '31', '31', 'Bo Jan przyszedł do was drogą sprawiedliwości, ale wyście mu nie uwierzyli, a celnicy i nierządnice uwierzyli mu. Wyście na to patrzyli, lecz nawet później nie mieliście wyrzutów sumienia i nie uwierzyliście mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '32', '32', 'Posłuchajcie innej przypowieści: Był pewien gospodarz, który \'zasadził winnicę, otoczył ją murem, wykuł w niej dół na prasę, zbudował wieżę\', wydzierżawił winnicę rolnikom i wyjechał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '33', '33', 'A kiedy nadszedł czas winobrania, wysłał do rolników swoich służących, aby odebrali należną mu część zbiorów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '34', '34', 'Lecz rolnicy pochwyciwszy służących jednego pobili, drugiego zabili, a trzeciego obrzucili kamieniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '35', '35', 'Posłał znowu innych służących w większej liczbie niż pierwej - i zrobili z nimi to samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '36', '36', 'Na koniec posłał do nich swego syna, mówiąc: Syna mojego uszanują. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '37', '37', 'A rolnicy zobaczywszy syna powiedzieli do siebie: To jest dziedzic, chodźmy, zabijmy go, a zabierzemy jego dziedzictwo. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '38', '38', 'I pochwyciwszy go wyrzucili poza winnicę i zabili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '39', '39', 'Kiedy więc pan winnicy przyjdzie, co zrobi z tymi rolnikami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '40', '40', 'Mówią Mu: - Wytraci ich jak złoczyńców, a winncę wydzierżawi innym rolnikom, którzy oddadzą mu zbiory we właściwym czasie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '41', '41', 'Jezus im mówi? - Czyście nigdy nie czytali w Pismach: \'Właśnie ten kamień, który odrzucili budujący dom, stał się kamieniem węgielnym. Pan to sprawił, a nas to zdumiewa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '42', '42', 'Dlatego powiadam wam: Królestwo Boże będzie wam zabrane i dane narodowi, który przyniesie owoce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '43', '43', 'A kto na ten kamień upadnie, rozbije się, a na kogo on upadnie, ten będzie zmiażdżony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '44', '44', 'Wysłuchawszy Jego przypowieści arcykapłani i faryzeusze zrozumieli, że o nich mówi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '20', '45', '45', 'I starali się Go pojmać, ale bali się tłumów, bo miały Go za proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '0', '0', 'Jezus odpowiedział im znowu w przypowieściach: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '1', '1', 'Podobne jest królestwo niebieskie do człowieka - króla, który wyprawił wesele swojemu synowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '2', '2', 'I wysłał swoje sługi, aby wezwali zaproszonych na wesele. Ale oni nie chcieli przyjść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '3', '3', 'Wysłał jeszcze raz inne sługi, mówiąc: Powiedzcie zaproszonym: Oto przygotowałem ucztę, utuczone woły zabite i wszystko gotowe. Przybywajcie na wesele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '4', '4', 'Lecz oni nie dbając o to odeszli: jeden na swoje pole, a drugi do swego kupiectwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '5', '5', 'Pozostali zaś ujęli przemocą jego sługi, znieważyli je i pozabijali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '6', '6', 'Rozgniewał się król i wysławszy wojska zgładził owych zabójców, a miasto ich spalił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '7', '7', 'Potem mówi swoim sługom: Wesele wprawdzie gotowe, ale zaproszeni nie byli godni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '8', '8', 'Wyjdźcie więc na skrzyżowania dróg i zaproście na wesele wszystkich spotkanych. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '9', '9', 'Słudzy wyszli na drogi i sprowadzili wszystkich, których spotkali, i złych, i dobrych. I sala napełniła się biesiadnikami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '10', '10', 'A kiedy król wszedł, aby przyjrzeć się biesiadnikom, ujrzał tam człowieka, który nie przywdział odświętnego stroju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '11', '11', 'I mówi: Przyjacielu, jak tutaj wszedłeś bez weselnego stroju? - A on zamilkł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '12', '12', 'Wtedy król powiedział usługującym: Zwiążcie mu ręce i nogi i wyrzućcie go stąd w ciemności. Tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '13', '13', 'Bo wielu jest wezwanych, lecz mało wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '14', '14', 'Wtedy faryzeusze odeszli i naradzali się, jak podejść Go w słowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '15', '15', 'I wysyłają do Niego swoich uczniów razem z herodianami, mówiąc: - Nauczycielu, wiemy, że mówisz prawdę i nauczasz prawdziwej drogi  Bożej, i nie zależy Ci na nikim, bo nie oglądasz się na osoby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '16', '16', 'Powiedz nam zatem, co sądzisz: Wolno płacić podatek cesarzowi czy nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '17', '17', 'A Jezus poznawszy ich przewrotność powiedział: - Obłudnicy, czemu mnie podstępnie pytacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '18', '18', 'Pokażcie mi monetę podatkową. A oni podali Mu denara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '19', '19', 'I pyta ich: - Czyj to wizerunek i napis?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '20', '20', 'Mówią Mu: - Cesarski. Wtedy mówi im: - Oddajcie więc to, co cesarskie, cesarzowi, a co Boskie - Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '21', '21', 'Kiedy to usłyszeli, podziwiali Go. I odeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '22', '22', 'Owego dnia podeszli do Niego saduceusze, którzy twierdzą, że nie ma zmartwychwstania, i zapytali Go: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '23', '23', 'Nauczycielu, Mojżesz powiedział: \'Jeśli ktoś umrze bezdzietnie, brat jego ożeni się z jego żoną i da potomstwo swemu bratu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '24', '24', 'Było wśród nas siedmiu braci i pierwszy ożeniwszy się umarł, a nie mając dzieci pozostawił swoją żonę bratu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '25', '25', 'Podobnie też drugi i trzeci aż do siódmego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '26', '26', 'Na końcu umarła żona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '27', '27', 'Który z tych siedmiu będzie ją miał za żonę, kiedy zmartwychwstanie? Bo wszyscy ją mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '28', '28', 'A Jezus im odpowiedział: - Na tym polega wasz błąd, że nie znacie Pism ani mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '29', '29', 'Bo po zmartwychwstaniu ludzie nie będą się żenić ani za mąż wychodzić, lecz będą jak aniołowie w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '30', '30', 'Nie czytaliście, co Bóg powiedział o zmartwychwstaniu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '31', '31', '\'Ja jestem Bogiem Abrahama i Bogiem Izaaka, i Bogiem Jakuba\' - nie jest Bogiem umarłych, ale żywych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '32', '32', 'A tłumy usłyszawszy to były pełne podziwu dla Jego nauki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '33', '33', 'Faryzeusze zaś dowiedziawszy się, że zamknął usta saduceuszom, zebrali się przy Nim,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '34', '34', 'a jeden z nich, biegły w Prawie, zapytał Go podstępnie: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '35', '35', 'Nauczycielu, jakie przykazanie jest największe w Prawie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '36', '36', 'On zaś odrzekł: - \'Będziesz miłował Pana Boga swego z całego serca i z całej duszy\' i wszystkich myśli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '37', '37', 'To jest największe i pierwsze przykazanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '38', '38', 'A drugie jemu podobne: \'Będziesz miłował bliźniego, jak siebie samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '39', '39', 'Na tych dwóch przykazaniach opiera się całe Prawo i Prorocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '40', '40', 'A kiedy faryzeusze zebrali się razem, Jezus ich zapytał: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '41', '41', 'Co sądzicie o Mesjaszu? Czyim jest synem? Mówią Mu: - Dawida.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '42', '42', 'Mówi im: To czemuż Dawid nazywa Go panem mówiąc z natchnienia Ducha:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '43', '43', '\'Rzekł Pan mojemu Panu: Siądź po mojej prawicy, aż Twych nieprzyjaciół rzucę pod Twoje stopy\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '44', '44', 'Jeśli więc Dawid nazywa Go Panem, to jakżeż On może być jego synem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '21', '45', '45', 'I nikt nie umiał Mu na to odpowiedzieć. Nikt też od tego dnia nie odważył się zadawać Mu pytań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '0', '0', 'Wtedy Jezus przemówił do tłumu i do swoich uczniów: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '1', '1', 'Prawo Mojżeszowe objaśniają nauczyciele Pisma i faryzeusze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '2', '2', 'Róbcie więc wszystko i zachowujcie, czego was uczą, ale nie naśladujcie ich uczynków, bo mówią, ale nie czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '3', '3', 'Wiążą wielkie ciężary i obarczają nimi ludzi, a sami palcem nie chcą ich ruszyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '4', '4', 'Wszystko czynią po to, aby ich ludzie widzieli. Rozszerzają filakterie i wydłużają frędzle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '5', '5', 'Lubią pierwsze miejsca przy stole i pierwsze krzesła w synagogach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '6', '6', 'i pozdrowienia na rynkach, i to, że ich ludzie nazywają : rabbi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '7', '7', 'Leczy wy nie nazywajcie się: rabbi, bo macie tylko jednego Nauczyciela, a wy (wszyscy) braćmi jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '8', '8', 'I nikogo na ziemi nie nazywajcie ojcem, bo macie tylko jednego Ojca - w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '9', '9', 'Nie nazywajcie się też: mistrzami, bo macie jednego tylko Mistrza - Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '10', '10', 'A największy z was będzie waszym sługą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '11', '11', 'A kto się będzie wywyższał, będzie poniżony, a kto się będzie uniżał, ten będzie wywyższony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '22', '12', '12', 'Biada wam, nauczyciele Pisma i faryzeusze, obłudnicy! Bo zamykacie królestwo Boże przed ludźmi. Nie wchodzicie do niego sami i wchodzącym do niego wejść nie pozwalacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '0', '0', 'Jezus wyszedł ze świątyni. W drodze (na Górę Oliwną) podeszli do Niego uczniowie, żeby Mu pokazać świątynne zabudowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '1', '1', 'On odpowiedział: - Widzicie, to wszystko? Zaprawdę, powiadam wam: Nie zostanie tu kamień na kamieniu - ani jeden nie zostanie na swoim miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '2', '2', 'A kiedy usiadł na Górze Oliwnej, podeszli do Niego uczniowie na osobności prosząc: - Powiedz nam, kiedy to nastąpi i jaki będzie znak Twojego przyjścia i końca (tego) czasu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '3', '3', 'Jezus im odpowiedział: - Uważajcie, aby was kto nie zwiódł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '4', '4', 'Bo wielu przyjdzie w moim imieniu mówiąc: Ja jestem Mesjaszem. - I wielu zwiodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '5', '5', 'I dotrą do was słuchy o wojnach i wieści wojenne. Uważajcie, nie lękajcie się, tak musi się stać. Lecz to jeszcze nie koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '6', '6', 'Bo powstanie naród przeciw narodowi i królestwo  przeciw królestwu, a w niektórych miejscach nastanie głód i będą trzęsienia ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '7', '7', 'To wszystko początek boleści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '8', '8', 'Wtedy będą was uciskać i zabijać, i z powodu mojego imienia będą was nienawidzić wszystkie narody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '9', '9', 'Wtedy wielu się załamie i jedni drugich będą wydawać i nienawidzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '10', '10', 'I wystąpi wielu fałszywych proroków, i wielu zwiodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '11', '11', 'A ponieważ rozszerzy się nieprawość, oziębnie miłość wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '12', '12', 'Lecz kto wytrwa do końca, zostanie zbawiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '13', '13', 'A ta ewangelia o królestwie głoszona będzie na całej ziemi na świadectwo dla wszystkich narodów. I  wtedy nastąpi koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '14', '14', 'Kiedy więc ujrzycie \'obrzydliwość spustoszenia\' na miejscu świętym, o której mówił prorok Daniel - kto czyta, niech rozważy -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '15', '15', 'wtedy mieszkańcy Judei niech uciekają w góry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '16', '16', 'Kto jest na dachu, niech nie schodzi do domu po swoje rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '17', '17', 'A kto na polu, niech nie wraca po swoje okrycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '18', '18', 'A biada brzemiennym i karmiącym w owe dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '19', '19', 'A módlcie się, aby ucieczka wasza nie przypadła w zimie albo w szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '20', '20', 'Bo nastanie wtedy \'udręka\' tak wielka, \'jakiej od początku świata aż do dziś nie było\' i nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '21', '21', 'I gdyby tych dni nie skrócono, nikt by nie ocalał, ale ze względu na wybranych dni te będą skrócone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '22', '22', 'Wtedy jeśli ktoś wam powie: Mesjasz jest tu albo tam - nie wierzcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '23', '23', 'Pojawią się bowiem fałszywi mesjasze i fałszywi prorocy, i będą czynić znaki tak wielkie, że zwiedliby, gdyby to było możliwe, i wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '24', '24', 'To wam przepowiedziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '25', '25', 'Jeśli więc będą wam mówić: Oto jest na pustyni - nie wychodźcie! Oto w mieszkaniu - nie wierzcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '26', '26', 'Bo jak błyskawica zapala się na wschodzie a widać ją aż na zachodzie, tak będzie z przyjściem Syna Człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '27', '27', 'Gdziekolwiek jest padlina, tam zbiorą się i sępy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '28', '28', 'A zaraz po udręce tych dni \'słońce się zaćmi i księżyc nie da światła, i gwiazdy będą spadać\' z nieba i \'moce niebieskie\' będą wstrząśnięte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '29', '29', 'Wtedy ukaże się na niebie znak Syna Człowieczego. I \'płakać będą wszystkie pokolenia ziemi, i ujrzą Syna Człowieczego przychodzącego na obłokach niebieskich\' z wielką mocą i chwałą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '30', '30', 'I pośle swoich aniołów z wielkim trąbieniem, i zgromadzą ze wszystkich stron świata Jego wybranych od jednego krańca nieba do drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '31', '31', 'Uczcie się z przypowieści o drzewie figowym. Kiedy jego gałąź staje się już miękka i wypuszcza liście, widzicie, że lato już blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '32', '32', 'Tak i wy, kiedy zobaczycie to wszystko, wiedzcie, że jest blisko już w drzwiach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '33', '33', 'Zaprawdę, mówię wam: Jeszcze nie przeminie to pokolenie, a to wszystko się stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '34', '34', 'Niebo i ziemia przeminą, ale moje słowa nie przeminą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '35', '35', 'Nikt nie zna tego dnia i godziny, ani aniołowie w niebie, ani Syn, tylko sam Ojciec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '36', '36', 'A z nadejściem Syna Człowieczego będzie tak jak za czasów Noego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '37', '37', 'Jak przed potopem (ludzie) jedli i pili, żenili się i wydawali za mąż do dnia, w którym Noe wszedł do arki,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '38', '38', 'i nie domyślali się niczego, aż przyszedł potop i wszystkich pochłonął, tak też będzie z przyjściem Syna Człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '39', '39', 'Wtedy dwóch będzie na polu: jednego zabiorą, a drugiego zostawią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '40', '40', 'Dwie będą mleć na żarnach: jedną zabiorą, a drugą zostawią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '41', '41', 'Czuwajcie więc, bo nie wiecie, którego dnia Pan przyjdzie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '42', '42', 'A to miejcie na uwadze, że gdyby pan domu wiedział, o jakiej porze złodziej przyjdzie, to czuwałby i nie pozwoliłby mu włamać się do swego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '43', '43', 'Dlatego i wy bądźcie gotowi, aby Syn Człowieczy nie nadszedł w godzinie, w której się Go nie spodziewacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '44', '44', 'Jestże taki sługa wierny i roztropny, którego pan postawił nad swoją służbą, aby wydzielał jej żywność w określonym czasie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '45', '45', 'Szczęśliwy ten sługa, którego pan, nadszedłszy, zostanie przy tym zajęciu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '46', '46', 'Zaprawdę, powiadam wam: Postawi go nad całym swoim majątkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '47', '47', 'Lecz jeśli jakiś zły sługa powie sobie w myśli: Pan mój odwleka swoje przyjście -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '48', '48', 'i zacznie bić swoich towarzyszy, i będzie jadł, i upijał się z pijakami -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '49', '49', 'pan takiego sługi przyjdzie w dniu, w którym się go nie spodziewa, i o godzinie, której nie zna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '23', '50', '50', 'I usunie go. I podzieli on los obłudników. Tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '0', '0', 'Wtedy z królestwem niebieskim będzie jak z dziesięcioma pannami, które wzięły lampy i wyszły naprzeciw pana młodego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '1', '1', 'Pięć z nich było głupich, a pięć mądrych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '2', '2', 'Głupie wziąwszy lampy nie zabrały ze sobą oliwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '3', '3', 'A mądre wzięły razem z lampami naczynia z oliwą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '4', '4', 'Kiedy pan młody się spóźniał, ogarnęła je senność i wszystkie zasnęły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '5', '5', 'O północy rozległo się wołanie: Pan młody nadchodzi, wyjdźcie mu naprzeciw. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '6', '6', 'Wtedy wszystkie panny zerwały się ze snu i przygotowały lampy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '7', '7', 'A głupie powiedziały do mądrych: Dajcie nam trochę waszej oliwy, bo lampy nasze gasną. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '8', '8', 'Lecz mądre odpowiedziały: O nie! Nie wystarczyłoby dla was i dla nas. Idźcie raczej do sprzedawców i kupcie sobie. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '9', '9', 'A kiedy tamte poszły kupować, nadszedł pan młody i te, które były gotowe, weszły razem z nim na wesele. A drzwi zamknięto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '10', '10', 'W końcu przychodzą i pozostałe panny, mówiąc: Panie, panie, otwórz nam! -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '11', '11', 'On zaś odpowiedział: Zaprawdę, powiadam wam: Nie znam was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '12', '12', 'Czuwajcie zatem, bo nie znacie dnia ani godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '13', '13', 'Bo to jest tak jak z człowiekiem, który udając się w podróż, wezwał sługi i powierzył im swój majątek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '14', '14', 'Jednemu dał pięć talentów, drugiemu dwa, a trzeciemu jeden, każdemu według zdolności, i wyjechał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '15', '15', 'Ten, który otrzymał pięć talentów, zaraz puścił je w obieg i uzyskał drugie pięć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '16', '16', 'Podobnie ten, który otrzymał dwa talenty, uzyskał drugie dwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '17', '17', 'A ten, który otrzymał jeden talent, wykopał w ziemi dół i schował pieniądze swego pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '18', '18', 'Po długim czasie pan powraca i rozlicza się ze sługami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '19', '19', 'Ten, który otrzymał pięć talentów, przyniósł drugie pięć, mówiąc: Dałeś mi, panie, pięć talentów. Oto zyskałem pięć dalszych -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '20', '20', 'Rzekł mu pan: Dobrze, sługo dobry i wierny, byłeś wierny w małych sprawach, więc postawię cię nad wielkimi. Raduj się z twoim panem!.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '21', '21', 'Również ten, który otrzymał dwa talenty, powiedział: Dałeś mi, panie, dwa talenty, oto zyskałem dwa dalsze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '22', '22', 'Rzekł mu pan: Dobrze, sługo dobry i wierny, byłeś wierny w małych sprawach, więc postawię cię nad wielkimi. Raduj się z twoim panem!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '23', '23', 'Także ten, który otrzymał jeden talent, powiedział: Wiem, panie, żeś człowiek twardy, sprzątasz, czegoś nie zasadził, i zbierasz, czegoś nie posiadał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '24', '24', 'Toteż bojąc się ciebie, schowałem twój talent w ziemi. Masz tu twoją własność. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '25', '25', 'A pan mu odpowiedział: Sługo zły i leniwy, wiedziałeś, że sprzątam, czego nie zasadziłem, i zbieram, czego nie zasiałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '26', '26', 'Należało więc dać moje pieniądze bankierom, a ja po powrocie z zyskiem odebrałbym moją własność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '27', '27', 'Zabierzcie mu więc talent i dajcie temu, który ma dziesięć talentów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '28', '28', 'Każdemu bowiem, kto ma, będzie (więcej) dodane i będzie miał w nadmiarze. A temu, kto nie ma, i to, co ma, będzie zabrane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '29', '29', 'A sługę, z którego nie mam pożytku, wyrzućcie na zewnątrz w ciemności. Tam będzie płacz i zgrzytanie zębów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '30', '30', 'Kiedy Syn Człowieczy przyjdzie w chwale, a z Nim wszyscy Jego aniołowie, wtedy zasiądzie na tronie w całej swojej chwale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '31', '31', 'I zgromadzą się przed Nim narody całego świata, a On oddzieli jedne od drugich, jak pasterz oddziela owce od kozłów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '32', '32', 'Owce ustawi po prawej stronie, a kozły po lewej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '33', '33', 'Wtedy król powie tym po prawej stronie: Chodźcie, błogosławieni mojego Ojca, weźcie w posiadanie królestwo przygotowane dla was od stworzenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '34', '34', 'Bo byłem głodny, a nakarmiliście Mnie, byłem spragniony, a daliście Mi pić, byłem przychodniem, a przyjęliście Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '35', '35', 'Byłem nagi, a przyodzialiście Mnie, byłem chory, a odwiedziliście Mnie, byłem w więzieniu, a przyszliście do Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '36', '36', 'Wtedy sprawiedliwi odpowiedzą Mu: Panie, kiedyż to widzieliśmy Ciebie głodnym, a nakarmiliśmy Cię? Spragnionym, i daliśmy Ci pić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '37', '37', 'I kiedyż to widzieliśmy, żeś przybył, i przyjęliśmy Cię? Albo żeś był nagi, i przyodzialiśmy Cię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '38', '38', 'Kiedyż to widzieliśmy Cię chorego albo w więzieniu, i przyszliśmy do Ciebie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '39', '39', 'A król im odpowie: Zaprawdę, powiadam wam: Coście uczynili dla jednego z tych najmniejszych moich braci, uczyniliście dla Mnie. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '40', '40', 'Wtedy powie tym po lewej stronie: Precz ode Mnie, przeklęci, w ogień wieczny, zgotowany dla diabła i jego aniołów!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '41', '41', 'Bo byłem głodny, a nie nakarmiliście Mnie. Byłem spragniony, a  nie daliście Mi pić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '42', '42', 'Byłem przychodniem, a nie przyjęliście Mnie. Byłem nagi, a nie przyodzialiście Mnie. Byłem chory i w więzieniu, a nie odwiedziliście Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '43', '43', 'Wtedy i oni powiedzą: Panie, kiedyż to widzieliśmy Cię głodnego albo spragnionego, albo przybysza, albo nagiego, albo chorego, albo w więzieniu, i nie usłużyliśmy Ci?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '44', '44', 'Wtedy On odpowie im: Zaprawdę, powiadam wam: Czegoście nie zrobili dla jednego z tych najmniejszych, nie zrobiliście dla Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '24', '45', '45', 'I pójdą ci na mękę wieczną, a sprawiedliwi wejdą do życia wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '0', '0', 'Kiedy Jezus skończył te wszystkie pouczenia, powiedział swoim uczniom: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '1', '1', 'Wiecie, że za dwa dni nadejdzie Pascha, a Syn Człowieczy zostanie wydany na ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '2', '2', 'Wtedy zebrali się arcykapłani i starsi ludu w pałacu najwyższego kapłana zwanego Kajfaszem');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '3', '3', 'i postanowili podstępem ująć Jezusa i zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '4', '4', 'Ale mówili: - Nie w święto, żeby nie doszło do rozruchów wśród ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '5', '5', 'Kiedy Jezus przebywał w Betanii w domu Szymona Trędowatego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '6', '6', 'podeszła do Niego niewiasta z alabastrowym słoikiem kosztownego olejku i wylała Mu na głowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '7', '7', 'Uczniowie ujrzawszy to oburzali, się mówiąc: - Po co to marnotrawstwo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '8', '8', 'Przecież można to było drogo sprzedać i  rozdać pieniądze ubogim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '9', '9', 'Widząc to Jezus odezwał się do nich: - Dlaczego sprawiacie przykrość tej niewieście? Przecież spełniła dla Mnie dobry uczynek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '10', '10', 'Bo ubogich macie zawsze wśród siebie, ale Mnie nie zawsze macie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '11', '11', 'Ona zaś, skropiwszy tym olejkiem moje ciało, oddała Mi ostatnią posługę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '12', '12', 'Zaprawdę, powiadam wam: Na całym świecie, wszędzie, gdzie będzie głoszona ewangelia, będzie się opowiadać również o tym, co ona uczyniła, na jej pamiątkę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '13', '13', 'Wtedy jeden z Dwunastu zwany Judaszem Iskariotą udał się do arcykapłanów');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '14', '14', 'i powiedział: - Ile mi dacie, jeśli Go wam wydam? \'Oni zaś postanowili dać\' mu \'trzydzieści srebrników\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '15', '15', 'I od tej chwili szukał sposobności, aby Go wydać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '16', '16', 'W pierwszy Dzień Przaśników uczniowie podeszli do Jezusa, pytając: - Gdzie mamy poczynić przygotowania do spożycia paschy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '17', '17', 'On im odpowiedział: - Idźcie do miasta, do znanego wam człowieka, i powiedzcie mu: Nauczyciel mówi: Czas mój nadchodzi. Czy mógłbym spożywać u ciebie paschę razem z moimi uczniami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '18', '18', 'Uczniowie zrobili, jak im Jezus kazał, i przygotowali paschę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '19', '19', 'Kiedy nastał wieczór, zajął miejsce za stołem razem z dwunastoma uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '20', '20', 'A kiedy jedli, powiedział: - Zaprawdę, powiadam wam: Jeden z was Mnie wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '21', '21', 'Zasmucili się bardzo i jeden przez drugiego zaczęli Go pytać: - Chyba nie ja, Panie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '22', '22', 'On zaś odpowiedział: - Ten Mnie wyda, który umoczył razem ze Mną rękę w misie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '23', '23', 'Wprawdzie Syn Człowieczy odchodzi, jak o Nim napisano, lecz biada temu człowiekowi, który Go wyda. Lepiej by było dla niego, żeby się nie narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '24', '24', 'I Judasz, który Go wydał, zapytał: - Chyba nie ja, Rabbi? Mówi mu: - Tak, to ty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '25', '25', 'A podczas wieczerzy, kiedy Jezus wielbiąc Boga wziął chleb, połamał go i podając uczniom rzekł: - Bierzcie, jedzcie, to jest moje ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '26', '26', 'I wziął kielich, odmówił modlitwę dziękczynną i podał im, mówiąc: - Pijcie z niego wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '27', '27', 'To jest bowiem moja krew przymierza, która będzie wylana za wielu na odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '28', '28', 'Powiadam wam, że odtąd nie będę już pił tego napoju z winogron aż do tego dnia, w którym będę razem z wami pił nowy napój w królestwie mojego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '29', '29', 'Kiedy odmówili hymn, wyszli na Górę Oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '30', '30', 'Wtedy mówi im Jezus: - Wy wszyscy opuścicie Mnie tej nocy. Napisano bowiem: \'Uderzę Pasterza i stado owiec się rozproszy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '31', '31', 'A po moim zmartwychwstaniu pójdę przed wami do Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '32', '32', 'Na to Piotr Mu powiedział: - Choćby wszyscy opuścili Ciebie, ja nigdy Cię nie opuszczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '33', '33', 'Rzekł mu Jezus: - Zaprawdę, powiadam ci: Jeszcze tej nocy, zanim kogut zapieje, ty trzykroć się Mnie zaprzesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '34', '34', 'Mówi Mu Piotr: - Chociaż miałbym umrzeć z Tobą, nie zaprę się Ciebie. To samo mówili wszyscy uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '35', '35', 'Jezus przyszedł z nimi do posiadłości zwanej Gethsemani. I mówi uczniom: - Zostańcie tutaj, aż Ja tam się pomodlę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '36', '36', 'I wziął ze sobą Piotra i dwóch synów Zebedeusza. Wtedy zaczął się smucić i niepokoić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '37', '37', 'I mówi do nich: - Wielki smutek ogarnął moją duszę. Zostańcie tutaj i czuwajcie ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '38', '38', 'I odszedłszy nieco dalej upadł na twarz i tak się modlił: - Ojcze mój, jeśli to możliwe, niech ominie Mnie ten kielich. Lecz niech się stanie nie to, co Ja chcę, ale co Ty chcesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '39', '39', 'Przychodzi do uczniów, zastaje ich śpiących i mówi Piotrowi: - Jak to? Jednej godziny nie mogliście czuwać ze Mną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '40', '40', 'Czuwajcie i módlcie się, abyście nie ulegli pokusie. Duch wprawdzie jest ochoczy, ale ciało słabe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '41', '41', 'I po raz drugi odszedł i tak się modlił: - Ojcze mój, jeśli muszę go wypić, niech spełnia się Twoja wola.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '42', '42', 'I przyszedłszy znowu zastał ich śpiących, bo oczy im się kleiły do snu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '43', '43', 'Zostawił ich i znowu modlił się po raz trzeci, powtarzając te same słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '44', '44', 'A potem przychodzi do uczniów i mówi im: - Wy nadal śpicie i odpoczywacie? Oto nadeszła godzina - Syn Człowieczy będzie wydany w ręce grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '45', '45', 'Wstańcie, chodźmy, zbliża się ten, który mnie wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '46', '46', 'A kiedy On jeszcze mówił, nadszedł Judasz, jeden z Dwunastu, a z nim wielki tłum od arcykapłanów i starszych ludu z mieczami i kijami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '47', '47', 'Ten, który Go wydał, ustalił z nimi taki znak, mówiąc: - Ten, którego pocałuję, to On. Pochwyćcie Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '48', '48', 'I zaraz podszedłszy do Jezusa powiedział: - Witaj, Rabbi! I pocałował Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '49', '49', 'A Jezus powiedział mu: - Przyjacielu, po coś przyszedł? Wtedy podszedłszy zatrzymali Jezusa i ujęli Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '50', '50', 'A oto jeden z tych, co byli z Jezusem, wydobył miecz i zadawszy cios słudze arcykapłana odciął mu ucho.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '51', '51', 'Mówi mu Jezus: - Włóż miecz na swoje miejsce, bo wszyscy, którzy chwytają za miecz, od miecza giną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '52', '52', 'Czy myślisz, że nie mógłbym wezwać na pomoc mego Ojca, a dałby Mi zaraz więcej niż dwanaście legionów anielskich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '53', '53', 'Jakżesz więc wypełniłyby się Pisma, (które mówią), że tak ma się stać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '54', '54', 'W tejże chwili Jezus powiedział tłumom: - Wyszliście z mieczami i kijami jak na rozbójnika, aby Mnie pochwycić. Codziennie siadywałem w świątyni nauczając i nie ujęliście Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '55', '55', 'Ale to wszystko się stało po to, aby wypełniły się Pisma proroków. Wtedy wszyscy uczniowie opuściwszy Go uciekli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '56', '56', 'A ci, co ujęli Jezusa, zaprowadzili Go do najwyższego kapłana Kajfasza, gdzie zebrali się nauczyciele Pisma i starsi (ludu).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '57', '57', 'Piotr zaś szedł za nimi z daleka aż na dziedziniec arcykapłański. I wszedłszy do środka usiadł razem ze sługami, aby widzieć koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '58', '58', 'Arcykapłani zaś z całym sanhedrynem starali się zdobyć fałszywe zeznania przeciwko Jezusowi, aby Go skazać na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '59', '59', 'Ale nie zdobyli, chociaż zeznawało wielu fałszywych świadków. Na koniec dwaj podszedłszy (do Jezusa)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '60', '60', 'zeznali: - On powiedział: Mogę zburzyć świątynię i w trzech dniach ją odbudować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '61', '61', 'A najwyższy kapłan zapytał Go: - Nic nie odpowiadasz? Cóż to oni zeznają przeciwko Tobie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '62', '62', 'Lecz Jezus milczał. Najwyższy kapłan rzekł do Niego: - Zaklinam Cię na Boga Żywego, powiedz nam, czy Ty jesteś Mesjaszem, Synem Bożym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '63', '63', 'Mówi mu Jezus: - Tak, to Ja. A powiadam wam, odtąd zobaczycie \'Syna Człowieczego siedzącego po prawicy Mocy\' i przychodzącego na obłokach nieba\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '64', '64', 'Wtedy najwyższy kapłan rozdarł szaty, mówiąc: - Zbluźnił, na co nam jeszcze świadkowie! Otoście teraz usłyszeli bluźnierstwo,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '65', '65', 'co myślicie? A oni odpowiedzieli: - Winien jest śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '66', '66', 'Wtedy pluli Mu w twarz i bili Go, a inni policzkowali Go,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '67', '67', 'mówiąc: - Prorokuj nam, Mesjaszu, kto Cię uderzył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '68', '68', 'A Piotr siedział na zewnątrz, na dziedzińcu. I podeszła do niego jedna służąca, i powiedziała: - Ty też byłeś z Jezusem Galilejczykiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '69', '69', 'Lecz on zaprzeczył wobec wszystkich: - Nie wiem, co mówisz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '70', '70', 'A kiedy szedł w stronę bramy, zobaczyła go inna służąca. I mówi tym, co tam byli: - Ten był z Jezusem Nazareńczykiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '71', '71', 'I znowu zaprzeczył pod przysięgą: - Nie znam tego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '72', '72', 'Wkrótce potem stający tam powiedzieli Piotrowi: - Naprawdę, ty też jesteś (jednym) z nich, bo i twoja wymowa cię zdradza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '73', '73', 'Wtedy zaczął się zaklinać i przysięgać: - Nie znam tego człowieka. I zaraz zapisał kogut.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '25', '74', '74', 'A Piotr przypomniał sobie słowa Jezusa, który powiedział: \'Zanim kogut zapieje, ty trzykroć się Mnie zaprzesz\'. I wyszedłszy za bramę gorzko zapłakał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '0', '0', 'A kiedy nastał ranek, wszyscy arcykapłani i starsi ludu powzięli uchwałę skazującą Jezusa na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '1', '1', 'Związali Go, wyprowadzili i wydali namiestnikowi Piłatowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '2', '2', 'Kiedy Judasz, który Go wydał, zobaczył, że Go skazano, poczuł wyrzuty sumienia i odniósł trzydzieści srebrników arcykapłanom i starszym,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '3', '3', 'mówiąc: - Zgrzeszyłem, wydając człowieka niewinnego. Lecz oni odpowiedzieli: - Cóż nam do tego? To twoja rzecz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '4', '4', 'Wrzucił więc pieniądze do świątyni, odszedł stamtąd i powiesił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '5', '5', 'Arcykapłani zaś wziąwszy pieniądze orzekli: - Nie wolno ich kłaść do skarbony, bo są zapłatą za (wydanie na) śmierć człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '6', '6', 'A po naradzie kupili za nie Pole Garncarza na cmentarz dla pielgrzymów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '7', '7', 'Dlatego to pole nazywa się polem Krwi aż do dnia dzisiejszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '8', '8', 'Wtedy wypełniło się to, co powiedział prorok Jeremiasz: \'Wzięli trzydzieści srebrników, zapłatę za oszacowanego, którego oszacowali niektórzy synowie Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '9', '9', 'I dali je za Pole Garncarza, jak mi Pan rozkazał\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '10', '10', 'Jezus stanął przed namiestnikiem. I zapytał Go namiestnik: - Czy Ty jesteś królem żydowskim? - Jezus odrzekł: Tak, to Ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '11', '11', 'A kiedy arcykapłani i starsi stawiali Mu (różne) zarzuty, nic nie odpowiadał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '12', '12', 'Wtedy Piłat pyta Go: - Czy słyszysz, o jak wiele rzeczy Cię oskarżają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '13', '13', 'I nie odpowiedział mu na żaden zarzut, co bardzo zdziwiło namiestnika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '14', '14', 'Ze względu na święto namiestnik zwykł zwalniać jednego z więźniów, którego lud chciał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '15', '15', 'A mieli wtedy znacznego więźnia, zwanego Barabaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '16', '16', 'Kiedy więc się zebrali, powiedział im Piłat: - Kogo wam wypuścić: Barabasza czy Jezusa, zwanego Mesjaszem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '17', '17', 'Bo wiedział, że Go wydali z nienawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '18', '18', 'A kiedy siedział na podwyższeniu, żona powiedziała mu przez posłańca: - Nie wdawaj się w sprawę tego niewinnego człowieka, bo dziś we śnie wiele przez Niego wycierpiałam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '19', '19', 'Arcykapłani zaś i starsi podburzyli tłumy, aby zażądały Barabasza, a Jezusa wydały na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '20', '20', 'Namiestnik zapytał: - Którego z tych dwóch mam wypuścić? Oni zaś odpowiedzieli: - Barabasza!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '21', '21', 'A Piłat pyta: - A co wobec tego mam zrobić z Jezusem, zwanym Mesjaszem? Wszyscy krzyczą: - Na krzyż z Nim!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '22', '22', 'Piłat rzekł: - Co On zrobił złego? Lecz oni jeszcze głośniej krzyczeli: - Na krzyż z Nim!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '23', '23', 'Piłat widząc, że nic nie osiąga, lecz tylko powstaje większe zamieszanie, wziął wodę, na oczach tłumu umył ręce i powiedział: - Nie ja odpowiadam za krew (tego człowieka). To wasza sprawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '24', '24', 'A cały lud na to: - Krew Jego na nas i na dzieci nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '25', '25', 'Wtedy wypuścił im Barabasza, a Jezusa ubiczował i wydał na ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '26', '26', 'Żołnierze namiestnika zabrali Jezusa do pretorium, zwołali do niego cały oddział');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '27', '27', 'i zdjąwszy z Niego szaty okryli Go czerwonym płaszczem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '28', '28', 'włożyli Mu na głowę koronę z cierni, do prawej ręki dali trzcinę i bijąc przed Nim pokłony szydzili z Niego: - Bądź pozdrowiony, królu żydowski!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '29', '29', 'Pluli na Niego, brali trzcinę i bili Go po głowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '30', '30', 'A po tych szyderstwach zdjęli z Niego płaszcz, ubrali Go w Jego szaty i odprowadzili na ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '31', '31', 'Wychodząc spotkali Cyrynejczyka imieniem Szymon i zmusili go do niesienia krzyża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '32', '32', 'A kiedy przyszli na miejsce zwane Golgota, to jest Miejsce Czaszki,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '33', '33', 'dali Mu do picia wino zmieszane z żółcią, lecz skosztowawszy go nie chciał pić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '34', '34', 'A kiedy Go ukrzyżowali, podzielili się Jego szatami rzucając losy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '35', '35', 'A siedząc tam pilnowali Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '36', '36', 'Nad Jego głową wypisano Jego winę: To jest Jezus, król żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '37', '37', 'Razem z Nim ukrzyżowali dwóch łotrów: jednego po Jego prawej stronie, a drugiego po lewej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '38', '38', 'A przechodzący obok lżyli Go, kiwając głowami');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '39', '39', 'i mówiąc: - Ty, co rozwalasz świątynię i w trzy dni ją odbudowujesz, wybaw siebie samego! Jeśli jesteś Synem Bożym, zejdź z krzyża!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '40', '40', 'Podobnie arcykapłani z nauczycielami Pisma i starszymi szydzili: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '41', '41', 'Innych wybawiał, a siebie samego wybawić nie może! Jest królem Izraela, niech teraz zejdzie z krzyża, a uwierzymy w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '42', '42', '\'Zaufał w Bogu, niech teraz Go wyrwie, jeśli Go miłuje\'. Powiedział przecież: Jestem Synem Bożym!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '43', '43', 'Tak samo ubliżali Mu złoczyńcy, którzy byli z Nim ukrzyżowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '44', '44', 'A od szóstej godziny aż do dziewiątej godziny ciemność okryła całą ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '45', '45', 'A około dziewiątej godziny Jezus zawołał donośnym głosem: - \'Eli, Eli, lema sabachtani?\' To jest: \'Boże, mój, Boże mój, czemuś Mnie opuścił?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '46', '46', 'A niektórzy ze stojących tam usłyszawszy to mówili: - Eliasza woła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '47', '47', 'I zaraz jeden z nich podbiegł, wziął gąbkę, napełnił ją octem, zatknął na trzcinę i dał Mu pić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '48', '48', 'A pozostali mówili: - Poczekaj, zobaczymy, czy Eliasz przyjdzie Go wybawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '49', '49', 'A Jezus wydał znowu głośny okrzyk i wyzionął ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '50', '50', 'I oto zasłona w świątyni rozdarła się na dwie części od samej góry do dołu i ziemia się zatrzęsła, i skały popękały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '51', '51', 'I pootwierały się grobowce, i ciała wielu świętych spoczywających w grobach zmartwychwstały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '52', '52', 'I wyszedłszy z grobów po Jego zmartwychwstaniu weszli do miasta świętego i pokazali się wielu (ludziom).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '53', '53', 'A setnik i ci, którzy z nim pilnowali Jezusa, widząc trzęsienie ziemi i to, co się stało, w wielkim strachu mówili: - To naprawdę był Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '54', '54', 'Także wiele niewiast przyglądało się temu z daleka. Towarzyszyły one Jezusowi od Galilei i usługiwały Mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '55', '55', 'Była wśród nich Maria Magdalena i Maria, matka Jakuba i Józefa, i matka synów Zebedeusza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '56', '56', 'Kiedy zapadł wieczór, przyszedł bogaty człowiek z Arymatei, imieniem Józef, który też był uczniem Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '57', '57', 'Udał się do Piłata  i prosił o ciało Jezusa. Wtedy Piłat kazał (mu) je wydać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '58', '58', 'Józef zawinął ciało w czyste płótno pogrzebowe');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '59', '59', 'i złożył je w swoim grobie, który wykuł w skale. A zatoczywszy na wejście do grobowca wielki kamień odszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '60', '60', 'A Maria Magdalena i druga Maria siedziały tam naprzeciwko grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '61', '61', 'A w dzień, który następuje po Przygotowaniu, zebrali się arcykapłani i faryzeusze u Piłata,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '62', '62', 'i powiedzieli: - Panie, przypomnieliśmy sobie, że ten oszust jeszcze za życia powiedział: \'Po trzech dniach zmartwychwstanę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '63', '63', 'Każ więc zabezpieczyć grób aż do trzeciego dnia, aby Jego uczniowie nie wykradli Go i nie rozpowiadali wśród ludu: \'Powstał z martwych\'. To ostatnie oszustwo byłoby gorsze od pierwszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '64', '64', 'Rzekł im Piłat: - Macie straż. Idźcie, zabezpieczcie grób, jak umiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '26', '65', '65', 'Oni zabezpieczyli grób, pieczętując kamień i ustawiając straż.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '0', '0', 'Dopiero po szabacie, w pierwszy dzień tygodnia o świcie, Maria Magdalena i druga Maria przyszły zobaczyć grób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '1', '1', 'A oto nastąpiło wielkie trzęsienie ziemi, albowiem anioł Pański zstąpił z nieba i odsunął kamień. I siedział na nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '2', '2', 'Wyglądał jak błyskawica, a szaty jego były białe jak śnieg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '3', '3', 'Strażnicy zatrzęśli się ze strachu przed nim i jakby zmartwieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '4', '4', 'Anioł zaś powiedział do niewiast: - Wy się nie lękajcie. Wiem, że szukacie Jezusa ukrzyżowanego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '5', '5', 'Nie ma Go tutaj, zmartwychwstał, jak powiedział, chodźcie, oglądajcie miejsce, gdzie leżał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '6', '6', 'Idźcie szybko i powiedzcie Jego uczniom: Zmartwychwstał, a teraz idzie przed wami do Galilei. Tam Go zobaczycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '7', '7', 'One zaś przestraszone spiesznie odeszły od grobowca i z wielką radością pobiegły oznajmić to Jego uczniom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '8', '8', 'A oto Jezus zastąpił im drogę, mówiąc: - Witajcie! Podeszły do Niego, objęły Go za nogi i złożyły Mu pokłon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '9', '9', 'Wtedy Jezus im mówi: - Nie bójcie się, idźcie i oznajmijcie moim braciom, żeby szli do Galilei, tam Mnie zobaczą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '10', '10', 'Kiedy były w drodze, niektórzy strażnicy poszli do miasta i zawiadomili arcykapłanów o wszystkim, co się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '11', '11', 'Ci zaś zebrali się ze starszymi i po naradzie dali żołnierzom sporo pieniędzy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '12', '12', 'przykazując im: - Rozpowiadajcie: \'W nocy, kiedyśmy spali, Jego uczniowie wykradli Go\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '13', '13', 'A jeżeli to dojdzie do uszu namiestnika, my mu wytłumaczymy, a was uchronimy od przykrości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '14', '14', 'Żołnierze wzięli pieniądze i uczynili, jak ich pouczono. Tak rozeszła się ta wieść wśród Żydów i trwa do dziś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '15', '15', 'A jedenastu uczniów wyruszyło do Galilei na górę, którą im wskazał Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '16', '16', 'Ujrzawszy Go złożyli Mu pokłon. Niektórzy jednak wahali się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '17', '17', 'A Jezus powiedział im: - Otrzymałem wszelką władzę na niebie i na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '18', '18', 'Idźcie więc i nauczajcie wszystkie narody. Chrzcijcie je w imię Ojca i Syna, i Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mt', '27', '19', '19', 'Uczcie je zachowywać wszystko, co wam nakazałem. A oto Ja jestem z wami po wszystkie dni aż do końca (tego) czasu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '0', '0', 'Początek ewangelii o Jezusie Chrystusie, Synu Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '1', '1', 'Jak napisano u proroka Izajasza: \'Oto posyłam przed Tobą mojego wysłańca, który przygotowuje Ci drogę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '2', '2', '\'Głos wołającego: Na pustyni przygotujcie drogę Panu, prostujcie Mu ścieżki\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '3', '3', 'Jan chrzcił na pustyni i nawoływał do chrztu nawrócenia dla (uzyskania) odpuszczenia grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '4', '4', 'Przychodziła do niego cała okolica judejska i wszyscy mieszkańcy Jerozolimy, a ci, którzy wyznawali swoje grzechy, byli przez niego ochrzczeni w rzece Jordanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '5', '5', 'A Jan miał odzienie z sierści wielbłądziej i skórzany pas na biodrach; żywił się szarańczą i leśnym miodem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '6', '6', 'I wołał: - Idzie za mną mocniejszy ode mnie, a ja nie jestem wart tego, żeby pochylić się i rozwiązać rzemień u Jego sandałów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '7', '7', 'Ja ochrzciłem was wodą, On zaś będzie was chrzcił Duchem Świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '8', '8', 'W tym czasie przyszedł Jezus z Nazaretu Galilejskiego i został ochrzczony przez Jana w Jordanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '9', '9', 'I zaraz, gdy wyszedł z wody, ujrzał otwierające się niebiosa i Ducha zstępującego Nań jak gołębica.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '10', '10', 'I dał się słyszeć głos z nieba: Tyś mój Syn ukochany, Ciebie sobie upodobałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '11', '11', 'I zaraz Duch prowadzi Go na pustynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '12', '12', 'I przez czterdzieści dni kusił Go szatan na pustyni. I żył wśród dzikich zwierząt, a usługiwali Mu aniołowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '13', '13', 'Po uwięzieniu Jana Jezus przyszedł do Galilei i głosił ewangelię Bożą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '14', '14', 'mówiąc: - Nadszedł (już) czas, zbliża się królestwo Boże, nawróćcie się i uwierzcie w ewangelię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '15', '15', 'Kiedy szedł nad Morzem Galilejskim, zobaczył Szymona i Andrzeja, brata Szymonowego, zarzucających sieci w morze - byli bowiem rybakiami. I powiedział im Jezus: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '16', '16', 'Chodźcie za Mną, a uczynię was rybakami ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '17', '17', 'I zaraz, porzuciwszy sieci, poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '18', '18', 'A nieco dalej zobaczył Jakuba, syna Zebedeusza, i jego brata Jana, kiedy właśnie naprawiali sieci w łodzi. I zaraz ich wezwał (na uczniów).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '19', '19', 'I zostawiwszy w łodzi swojego ojca Zebedeusza razem z najemnikami, poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '20', '20', 'I weszli do Kafarnaum, i zaraz w szabat zaczął nauczać w synagodze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '21', '21', 'I byli pełni podziwu dla Jego nauki, bo nauczał ich z mocą, a nie jak nauczyciele Pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '22', '22', 'Zaraz też znalazł się w synagodze człowiek opętany przez ducha nieczystego. I zaczął krzyczeć: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '23', '23', 'Czego chcesz od nas, Jezusie z Nazaretu. Przyszedłeś nas zgubić? Wiem, kto jesteś: Świętym Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '24', '24', 'A Jezus nakazał mu surowo: - Milcz i wyjdź z niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '25', '25', 'A duch nieczysty, szarpnąwszy nim, wyszedł z niego z wielkim krzykiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '26', '26', 'I osłupieli wszyscy, tak że mówił jeden do drugiego: - Co to takiego? Nowa nauka? On siłą narzuca swoją wolę nawet duchom nieczystym i są mu posłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '27', '27', 'I zaraz rozeszła się wieść o Nim wszędzie, po całej ziemi galilejskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '28', '28', 'I zaraz wyszli z synagogi, i przyszli z Jakubem i Janem do domu Szymona i Andrzeja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '29', '29', 'A teściowa Szymona leżała z gorączką. Mówią Mu zaraz o niej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '30', '30', 'I podniósł ją, chwyciwszy za rękę. I opuściła ją gorączka, i usługiwała Mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '31', '31', 'Kiedy po zachodzie słońca zapadł zmrok, przynosili do Niego wielu chorych i opętanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '32', '32', 'I całe miasto zebrało się u drzwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '33', '33', 'I uzdrowił wielu cierpiących na różne choroby, i wiele czartów wypędził, i nie pozwalał tym czartom mówić, bo wiedziały, kim jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '34', '34', 'A rankiem, kiedy jeszcze było ciemno, wyszedł i udał się na miejsce pustynne, i tam się modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '35', '35', 'I pobiegł za Nim Szymon i jego towarzysze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '36', '36', 'a gdy Go znaleźli, mówią Mu: - Wszyscy Cię szukają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '37', '37', 'A on im mówi: - Chodźmy gdzie indziej, do sąsiednich miejscowości, abym i tam mógł głosić (ewangelię), bo po to przyszedłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '38', '38', 'I poszedł, głosząc (ewangelię) w synagogach całej Galilei i wyrzucając czarty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '39', '39', 'I przychodzi do Niego trędowaty, i woła (na klęczkach): - Jeśli zechcesz, możesz mnie oczyścić!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '40', '40', 'I ulitowawszy się, wyciągnął rękę, dotknął go i mówi mu: - Chcę, bądź oczyszcony!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '41', '41', 'I zaraz trąd zniknął z jego ciała, i został oczyszczony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '42', '42', 'I zagroziwszy mu surowo, zaraz go odprawił ze słowami: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '43', '43', 'Uważaj, nie mów nic nikomu, ale idź, pokaż się kapłanowi i złóż ofiarę za oczyszczenie, jak rozkazał Mojżesz, na świadectwo im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '0', '44', '44', 'On zaś wyszedł, zaczął to rozgłaszać i rozpowiadać o tym wydarzeniu, tak że (Jezus) nie mógł już jawnie wejść do miasta, ale przebywał poza nim na miejscach pustynnych. I zewsząd przychodzili do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '0', '0', 'Po pewnym czasie przyszedł znowu do Kafarnaum. I usłyszano, że jest w domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '1', '1', 'I zebrało się tak wielu (ludzi), że już nie było miejsca nawet przed drzwiami. A On ich nauczał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '2', '2', 'I przychodzą do Niego, niosąc sparaliżowanego. Dźwigało go czterech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '3', '3', 'Nie mogąc dojść do Niego z powodu ciżby, rozebrali dach nad miejscem, gdzie znajdował się (Jezus). I przez otwór, który zrobili, spuścili nosze ze sparaliżowanym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '4', '4', 'A Jezus, widząc ich wiarę, mówi sparaliżowanemu: - Synu, twoje grzechy są odpuszczone!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '5', '5', 'A siedzieli tam niektórzy nauczyciele Pisma i zastanawiali się: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '6', '6', 'Dlaczego On tak mówi? Bluźni! Któż oprócz Boga może odpuszczać grzechy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '7', '7', 'A Jezus zaraz poznał w swoim duchu, że oni tak myślą, i mówi im: - Dlaczego tak myślicie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '8', '8', 'Co jest łatwiej: czy powiedzieć sparaliżowanemu: Twoje grzechy są odpuszczone, czy też: Wstań, weź twoje nosze i chodź?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '9', '9', 'Ale abyście wiedzieli, że Syn Człowieczy ma władzę odpuszczania grzechów na ziemi - powiada sparaliżowanemu -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '10', '10', 'mówię ci: Wstań, weź nosze i idź do domu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '11', '11', 'I wstał, i zaraz, wziąwszy nosze, wyszedł na oczach wszystkich, tak że wszyscy się bardzo dziwili i wielbili Boga, mówiąc: - Nigdyśmy czegoś takiego nie widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '12', '12', 'I wyszedł znowu nad morze, a cały tłum przychodził do Niego, a On ich nauczał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '13', '13', 'Przechodząc zobaczył Lewiego, syna Alfeusza, pobierającego opłaty. I mówi mu: - Pójdź za Mną! I wstał, i poszedł za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '14', '14', 'I siada (Jezus) w jego domu przy stole, a wielu celników i grzeszników siedziało z Nim i z Jego uczniami - a było ich wielu i chodzili za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '15', '15', 'A nauczyciele Pisma ze (stronnictwa) faryzeuszów widząc, że On je z grzesznikami i celnikami, pytali się uczniów: - Dlaczego je i pije z celnikami i grzesznikami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '16', '16', 'Kiedy Jezus to usłyszał, mówi im: - Zdrowi nie potrzebują lekarza, ale chorzy. Nie przyszedłem wzywać sprawiedliwych, ale grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '17', '17', 'A wtedy uczniowie Jana i faryzeusze pościli. Przychodzą zatem i mówią Mu: - Dlaczego uczniowie Jana i uczniowie faryzeuszów poszczą, a Twoi uczniowie nie poszczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '18', '18', 'A Jezus im powiedział: - Czyż goście weselni mogą pościć, gdy pan młody jest z nimi? Jak długo mają pana młodego wśród siebie, nie mogą pościć!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '19', '19', 'Ale przyjdzie czas, kiedy pan młody zostanie spośród nich zabrany. Wtedy będą pościć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '20', '20', 'Nikt nie przyszywa łaty z nowego materiału do starego okrycia, bo gdyby to zrobił, nowa łata oderwałaby dalszą część od starego i dziura by się powiększyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '21', '21', 'Nikt też nie wlewa młodego wina do starych worków skórzanych, bo gdyby to uczynił, wino rozdarłoby worki: i wino przepada, i worki. Ale młode wino wlewa się do nowych worków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '22', '22', 'Przechodził w szabat wśród zbóż, a Jego uczniowie zaczęli zrywać po drodze kłosy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '23', '23', 'Faryzeusze zaś mówili Mu: - Patrz, dlaczego robią w szabat, to czego nie wolno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '24', '24', 'A On powiada: - Nie czytaliście nigdy o tym, co uczynił Dawid, kiedy znalazł się w potrzebie? On sam i ci, co z nim byli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '25', '25', 'Jak za czasów najwyższego kapłana Abiatara wszedł do domu Bożego i zjadł chleby poświęcone, których nie wolno jeść nikomu oprócz kapłanów, i dał też tym, którzy z nim byli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '26', '26', 'I mówił im: - Szabat został ustanowiony dla człowieka, a nie człowiek dla szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '1', '27', '27', 'Tak więc Syn Człowieczy jest również panem szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '0', '0', 'I wszedł znowu do synagogi. A był tam człowiek z uschniętą ręką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '1', '1', 'I podpatrywali, czy uzdrowi go w szabat, aby Go oskarżyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '2', '2', 'I mówi człowiekowi, który miał uschniętą rękę: - Wyjdź na środek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '3', '3', 'A do nich mówi: - Czy w szabat należy czynić dobrze, czy źle? Ratować życie czy zabijać? Lecz oni milczeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '4', '4', 'I spojrzał na nich z gniewem, i zasumcony ich zatwardziałością mówi człowiekowi: - Wyciągnij rękę. I wyciągnął. I odzyskał władzę w ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '5', '5', 'A faryzeusze wyszli i zaraz naradzali się z herodianami, jak by Go zgładzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '6', '6', 'Jezus z uczniami odszedł nad morze, a szedł za Nim wielki tłum z Galilei i z Judei,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '7', '7', 'i z Jerozolimy, i z Idumei, i z Zajordania, i z okolic Tyru i Sydonu. Wielki tłum, który usłyszał, jakich to rzeczy dokonał, przyszedł do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '8', '8', 'I powiedział swoim uczniom, aby mieli dla Niego w pogotowiu łódź ze względu na tłum, aby nie napierano na Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '9', '9', 'Bo wielu uzdrowił. Toteż ci, którzy mieli dolegliwości, cisnęli się do Niego, aby Go dotknąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '10', '10', 'I duchy nieczyste padały mu do nóg, kiedy Go widziały. I wołały: - Ty jesteś Syn Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '11', '11', 'On zaś wielokrotnie surowo im nakazywał, aby Go nie ujawniały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '12', '12', 'I wchodzi na górę, i przywołuje tych, których sam chciał. A oni podeszli do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '13', '13', 'I ustanowił Dwunastu, aby z Nim przebywali, aby ich wysłał głosić (ewangelię)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '14', '14', 'i aby mieli władzę wypędzania złych duchów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '15', '15', 'I ustanowił Dwunastu. Szymonowi nadał imię Piotr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '16', '16', 'I Jakuba, syna Zebedeusza, i Jana, brata Jakuba. Tym nadał imiona \'Boanerges\', to znaczy: \'Synowie Gromu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '17', '17', 'I Andrzeja, i Filipa, i Bartłomieja, i Mateusza, i Tomasza, i Jakuba, syna Alfeusza,  i Tadeusza, i Szymona Kananejczyka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '18', '18', 'i Judę Iskariotę, tego, który Go też i wydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '19', '19', 'I idzie do domu, a tłum też idzie z Nim, tak że nie mogli nawet zjeść chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '20', '20', 'Kiedy to usłyszeli Jego bliscy, wyszli, aby Go powstrzymać, bo mówiono: Stracił rozum!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '21', '21', 'A nauczyciele Pisma, którzy przyszli z Jerozolimy, mówili: - Beelzebula ma i wypędza czarty mocą czartowskiego władcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '22', '22', 'A On przywołał ich i mówił do nich w przypowieściach: - Jak może szatan wypędzać szatana?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '23', '23', 'Bo jeśli królestwo zostanie rozdwojone, to takie królestwo nie może się ostać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '24', '24', 'Jeśli dom zostanie rozdwojony, to taki dom nie będzie mógł się ostać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '25', '25', 'I szatan, jeżeli powstanie przeciwko sobie i jest rozdwojony, to nie może się ostać, ale koniec z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '26', '26', 'Lecz nikt, wszedłszy do domu mocarza, nie może zagrabić jego rzeczy, jeśli wpierw go nie zwiąże. I wtedy ograbi jego dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '27', '27', 'Zapewniam was: wszystkie grzechy będą ludziom odpuszczone, nawet bluźnierstwa, jeśliby zbluźnili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '28', '28', 'Kto by jednak zbluźnił przeciwko Duchowi Świętemu, nie otrzyma nigdy odpuszczenia, ale zawsze będzie winien grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '29', '29', 'Bo mówili: Ma ducha nieczystego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '30', '30', 'Przychodzi Jego matka i bracia. A stojąc na dworze posłali po Niego, wzywając Go do siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '31', '31', 'Tłum zaś siedział wokół Niego: Mówią Mu: - Twoja matka i bracia, i siostry szukają Cię na dworze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '32', '32', 'Odpowiedzał im: - Któż jest moją matką czy moim bratem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '33', '33', 'I spojrzawszy na siedzących wokół siebie, mówi: - Oto matka moja i bracia moi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '2', '34', '34', 'Bo ten, kto czyni wolę Boga, jest moim bratem i siostrą, i matką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '0', '0', 'I znowu zaczął nauczać nad morzem, i zgromadził się przy Nim tak wielki tłum, że wszedł do łodzi i usiadł w niej na morzu, a cały tłum stał na brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '1', '1', 'I nauczał ich wiele w przypowieściach. Dawał im taką naukę: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '2', '2', 'Słuchajcie! Oto siewca wyszedł siać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '3', '3', 'A kiedy siał, jedno (ziarno) padło na drogę: przyleciały ptaki i je zjadły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '4', '4', 'A inne padło na grunt skalisty, gdzie nie miało wiele ziemi, i zaraz powschodziło, bo nie miało grubej (warstwy) ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '5', '5', 'A kiedy wzeszło słońce, wypaliło je. I uschło, bo nie miało korzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '6', '6', 'A inne padło pomiędzy osty. I wyrosły osty wysoko, i zadusiły je, i nie wydało owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '7', '7', 'Lecz inne padły na ziemię, na dobrą i wydały owoc, który rósł i pomnażał się, i przyniosły plon: jedno trzydziestokrotny, drugie sześćdziesięciokrotny, a inne stokrotny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '8', '8', 'I mówił: - Kto ma uszy do słuchania, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '9', '9', 'Kiedy znalazł się sam, ci, którzy przy Nim byli razem z Dwunastoma, pytali Go o przypowieści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '10', '10', 'I mówił im: - Wam powierza się tajemnicę królestwa, innym zaś, ktrórzy nie są z wami, podaje się wszystko w przypowieściach:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '11', '11', '\'Aby patrzeli, a nie widzieli, słuchali, a nie rozumieli, i aby się przypadkiem nie nawrócili, i nie odkryli tajemnicy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '12', '12', 'I mówi im: - Nie rozumiecie tej przypowieści, to jak pojmiecie wszystkie (inne) przypowieści?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '13', '13', 'Siewca sieje słowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '14', '14', 'A to są ci na drodze, gdzie sieje się słowo: kiedy (je) usłyszą, zaraz przychodzi szatan i porywa słowo w nich zasiane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '15', '15', 'A owi, posiani na gruncie skalistym, kiedy usłyszą słowo, przyjmują je zaraz z radością,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '16', '16', 'ale nie mają w sobie korzenia, są niestali. Potem, gdy nastaje ucisk czy prześladowanie z powodu słowa, zaraz się załamują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '17', '17', 'A inni są zasiani między ostami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '18', '18', 'Ci usłyszeli słowo, ale troski doczesne, ułuda bogactwa, pożądanie innych rzeczy dochodzą do głosu i zagłuszają słowo. I nie przynosi owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '19', '19', 'Owi zaś, zasiani na ziemi dobrej, to ci, którzy słuchają słowa i przyjmują je. I przynoszą owoc, jedni trzydziestokrotny, drudzy sześćdziesięciokrotny, inni stokrotny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '20', '20', 'I mówił im: - Czyż po to wnosi się świecę, aby ją wstawić pod korzec albo pod łóżko? Czy raczej nie po to, aby ją postawić na świeczniku?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '21', '21', 'Bo nie ma nic ukrytego, co by nie miało być ujawnione, i nic, co się stało w ukryciu, a nie miało wyjść na jaw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '22', '22', 'Kto ma uszy do słuchania, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '23', '23', 'Mówi im także: - Uważajcie na to, czego słuchacie. Jaką miarą mierzycie, taką wam odmierzą i jeszcze dodadzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '24', '24', 'Bo temu, kto ma, będzie dodane; a temu, kto nie ma, i to, co ma, będzie zabrane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '25', '25', 'I mówił: - Z królestwem Bożym jest tak jak z człowiekiem, który rzuca ziarno w ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '26', '26', 'I śpi w nocy, i wstaje we dnie, a ziarno wschodzi i rośnie, on sam nie wie jak.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '27', '27', 'Ziemia sama z siebie przynosi owoc: najpierw źdźbło, potem kłos, potem pełne ziarno w kłosie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '28', '28', 'A kiedy wyda owoc, zaraz (człowiek) wysyła żniwiarza, bo nadeszło żniwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '29', '29', 'I mówił: - Z czym porównamy królestwo Boże, albo w jakiej przypowieści je przedstawimy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '30', '30', 'O ziarnku gorczycy. Kiedy się je sieje w ziemi, jest mniejsze od wszystkich innych nasion na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '31', '31', 'A kiedy się je zasieje, rośnie wysoko i staje się większe od wszystkich jarzyn, i wypuszcza gałęzie tak wielkie, że w ich cieniu mogą się gnieździć ptaki, które latają w powietrzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '32', '32', 'W wielu takich przypowieściach głosił im swoją naukę, o ile mogli słuchać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '33', '33', 'I nauczał ich tylko w przypowieściach, na osobności zaś wyjaśniał wszystko uczniom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '34', '34', 'Owego dnia, kiedy zapadł wieczór, mówi do nich: - Przeprawmy się na drugą stronę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '35', '35', 'Zostawiwszy więc tłum, biorą Go, tak jak był w łodzi. A także inne łodzie płynęły z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '36', '36', 'Wtedy zerwała się gwałtowna wichura. I fale wdzierały się do łodzi, tak że łódź napełniała się już wodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '37', '37', 'On zaś spał na rufie łodzi, na wezgłówku. I budzą Go, i mówią Mu: - Nauczycielu, nie obchodzi Cię to, że giniemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '38', '38', 'A On się obudził, zgromił wiatr i powiedział morzu: - Milcz, uspokój się! Wicher ustał i nastała wielka cisza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '39', '39', 'I powiedział im: - Czemuście tacy bojaźliwi? Jeszcze nie macie wiary?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '3', '40', '40', 'I zdjął ich wielki strach, i mówili jedni do drugich: - Kimże On jest, że wiatr, i morze są Mu posłuszne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '0', '0', 'Przybyli na drugą stronę morza, do kraju Gerazeńczyków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '1', '1', 'A kiedy wyszedł z łodzi, (zaraz) z grobowców wybiegł mu naprzeciw człowiek opętany przez ducha nieczystego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '2', '2', 'Miał on legowisko w grobowcach i nawet łańcuchami nikomu nie udawało się go związać,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '3', '3', 'bo często nakładano mu pęta i łańcuchy, a on zrywał łańcuchy i kruszył pęta, i nikt nie miał tyle siły, aby go poskromić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '4', '4', 'Całymi dniami i nocami krzyczał po grobowcach i po górach i tłukł sam siebie kamieniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '5', '5', 'Kiedy z daleka zobaczył Jezusa, przybiegł, upadł przed Nim na twarz');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '6', '6', 'i krzyknął głośno: - Czego chcesz ode mnie Jezusie, Synu Boga Najwyższego? Zaklinam Cię na Boga, nie dręcz mnie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '7', '7', 'Bo mówił mu: - Wyjdź duchu nieczysty z tego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '8', '8', 'I zapytał go: - Jakie jest twoje imię? Mówi Mu: - Legion imię moje, bo jest nas wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '9', '9', 'I bardzo Go prosił, aby ich nie wypędzał z tego kraju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '10', '10', 'A w tymże miejscu pod górą pasło się wielkie stado świń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '11', '11', 'I prosiły Go duchy: - Poślij nas w te świnie, abyśmy w nie weszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '12', '12', 'I pozwolił im. A duchy nieczyste wyszły z człowieka i weszły w świnie. I stado rzuciło się z urwistego brzegu w morze. Prawie dwa tysiące (świń) utonęło w morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '13', '13', 'A pasterze uciekli i przynieśli wieść o tym do miasta i do zagród, i ludzie przyszli zobaczyć, co się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '14', '14', 'Przyszli do Jezusa i widzą, że opętany przez czarta, ten sam, który miał legion, siedzi ubrany i przy zdrowych zmysłach. I strach ich ogarnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '15', '15', 'A ci, którzy to widzieli, opowiedzieli im, co się stało z opętanym przez czarta, a także o świniach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '16', '16', 'I zaczęli Go prosić, aby odszedł z ich granic.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '17', '17', 'A kiedy wchodził do łodzi, opętany przez czarta prosił Go, żeby mógł zostać przy Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '18', '18', '(Jezus) nie zgodził się jednak, lecz mówił doń: - Idź do swojego domu i do swoich i opowiedz im, jak wielkich rzeczy dokonał Pan dla ciebie i jak się nad tobą zmiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '19', '19', 'I odszedł, i zaczął rozgłaszać w Dekapolu, jak wszelkich rzeczy dokonał dla niego Jezus. A wszyscy się zdumiewali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '20', '20', 'Kiedy Jezus przeprawił się znowu na drugą stronę, wielki tłum zbiegł się do Niego. A On stał nad morzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '21', '21', 'Przychodzi jeden z przełożonych synagogi imieniem Jair. Kiedy Go zobaczył, pada Mu do nóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '22', '22', 'I bardzo Go prosi: - Córeczka moja kona, czy zechciałbyś przyjść i położyć na nią ręce, aby wyzdrowiała i żyła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '23', '23', 'I poszedł z nim. A szedł za Nim wielki tłum i napierali na Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '24', '24', 'Kobieta, która przez lat dwanaście chorowała na krwotok,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '25', '25', 'nachodziła się do wielu lekarzy i wydała wszystko, co miała, a nic jej to nie pomogło, ale się jeszcze gorzej czuła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '26', '26', 'Kiedy usłyszała o Jezusie, przyszła z tłumem i z tyłu dotknęła Jego okrycia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '27', '27', 'bo myślała: Jeśli tylko dotknę się Jego odkrycia, wyzdrowieję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '28', '28', 'Zaraz też ustał jej krwotok i poczuła ulgę w cierpieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '30', '30', 'A Jego uczniowie mówili Mu: - Widzisz tłum napierający na Ciebie, a pytasz się: Kto się Mnie dotknął?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '31', '31', 'Lecz On rozglądał się, aby zobaczyć tę, która to uczyniła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '32', '32', 'A kobieta, zalękniona i drżąca, i świadoma tego, co się z nią stało, przyszła, upadła przed Nim (na ziemię) i wyznała Mu całą prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '33', '33', 'On zaś powiedział do niej: - Córko, twoja wiara cię ocaliła, idź w pokoju i bądź wolna od twojej dolegliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '34', '34', 'Kiedy jeszcze mówił, przychodzą (wysłannicy) z domu przełożonego synagogi, mówiąc: - Twoja córka umarła, po cóż jeszcze trudzisz Nauczyciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '35', '35', 'Kiedy Jezus usłyszał, co mówili, rzecze przełożonemu synagogi: - Nie lękaj się, tylko wierz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '36', '36', 'I nie pozwolił nikomu iść z sobą tylko Piotrowi, Jakubowi i Janowi, bratu Jakuba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '37', '37', 'I przychodzą do domu przełożonego synagogi. I widzi zamieszanie, i płaczki, i głośno zawodzących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '38', '38', 'Kiedy wszedł, mówi do nich: - Dlaczego robicie zamieszanie i płaczecie? To dziecko nie umarło, ale śpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '39', '39', 'I wyśmiewali się z Niego. On zaś, wyrzuciwszy wszystkich, bierze ojca i matkę dziecka, i tych. którzy z Nim byli, i wchodzi tam, gdzie leżało dziecko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '40', '40', 'I ujął dziecko za rękę. I mówi mu: - Talitha kum, to znaczy: Dziewczynko, mówię ci, wstań!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '41', '41', 'I zaraz dziewczynka wstała i chodziła, a miała dwanaście lat. I osłupieli z wielkiego zdumienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '4', '42', '42', 'I wiele razy im nakazywał, aby nikt się o tym nie dowiedział. Powiedział też, aby jej dano jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '0', '0', 'I wyszedł stamtąd. I przychodzi w swoje strony rodzinne, a razem z Nim idą Jego uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '1', '1', 'Kiedy nastał szabat, zaczął nauczać w synagodzie, a wielu słuchaczy zdumiewało się: - Skąd się to u Niego bierze? - mówili. - I skąd wzięła się ta jego mądrość, a także moce nadziemskie, działające przez Jego ręce?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '2', '2', 'Czyż On nie jest cieślą, synem Maryi i bratem Jakuba i Józefa, i Judy, i Szymona? I czyż Jego siostry nie żyją tutaj, wśród nas? I byli Nim zgorszeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '3', '3', 'A Jezus im powiedział: - Tylko w swojej ojczyźnie wśród swoich krewnych i w swoim domu nie ma prorok uznania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '4', '4', 'I nie mógł tam okazać wcale swojej mocy. Uzdrowił tylko kilku chorych, włożywszy na nich ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '5', '5', 'I dziwił się ich niedowiarstwu, i obchodził okoliczne wioski i nauczał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '6', '6', 'I wzywa Dwunastu. I zaczął ich wysyłać po dwóch, i dał im władzę nad duchami nieczystymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '7', '7', 'I nakazał, aby poza kijem niczego nie brali na drogę: ani chleba, ani torby, ani trzosa z drobnymi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '8', '8', 'ani nie brali na siebie dwóch sukien, ale aby włożyli sandały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '9', '9', 'I mówił im: - Gdziekolwiek wejdziecie do domu, zostańcie tam aż do odejścia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '10', '10', 'A jeśli w jakimś miejscu nie przyjmą was ani nie będą was słuchać, odejdźcie stamtąd i otrząśnijcie pył z waszych nóg na świadectwo przeciwko nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '11', '11', 'I wzywali do nawrócenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '12', '12', 'Wyrzucali też wiele czartów, a wielu chorych namaszczali olejem i uzdrawiali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '13', '13', 'A król Herod usłyszał o Nim, bo Jego imię stało się głośne i mówiono: - To Jan Chrzciciel powstał z martwych i dlatego działają przezeń (nadziemskie) moce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '14', '14', 'Inni zaś mówili: - Jest Eliaszem. Jeszcze inni: - Prorokiem, podobnym do któregoś z proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '15', '15', 'Kiedy Herod usłyszał o tym, mówił: - Jan, którego ściąłem, zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '16', '16', 'Bo Herod kazał ująć Jana i trzymał go skutego w więzieniu z powodu Herodiady, żony swego brata Filipa, bo wziął ją sobie za żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '17', '17', 'Jan zaś mówił Herodowi: - Nie wolno ci trzymać przy sobie żony tego brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '18', '18', 'A Herodiada czyhała na niego i chciała go zabić, ale nie mogła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '19', '19', 'Bo Herod bał się Jana, wiedząc, że jest on człowiekiem sprawiedliwym i świętym. I chronił go. Kiedy go słuchał, bywał bardzo zakłopotany, ale słuchał go chętnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '20', '20', 'Kiedy nadszedł odpowiedni dzień, Herod w swoje urodziny wyprawił przyjęcie dla urzędników i wojskowych, i dostojników galilejskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '21', '21', 'A kiedy weszła córka Herodiady i zatańczyła, spodobała się Herodowi i współbiesiadnikom. - Proś mnie, o co chcesz, a dam tobie - powiedział król do dziewczyny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '22', '22', 'I przysiągł jej: - O cokowiek mnie poprosisz, dam tobie, nawet połowę mego królestwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '23', '23', 'Ona wyszła i zapytała matkę: - O co mam prosić? Ta powiedziała: - O głowę Jana Chrzciciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '24', '24', 'Zaraz też weszła z pośpiechem do króla i poprosiła: - Chcę, żebyś w tej chwili dał mi głowę Jana Chrzciciela na półmisku!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '25', '25', 'A król zasmucił się, ale nie chciał jej odmówić ze względu na przysięgę i na współbiesiadników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '26', '26', 'Zaraz też posłał żonierza i kazał mu przynieść głowę Jana. Ten ściął go w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '27', '27', 'I przyniósł jego głowę na półmisku, i podał ją dziewczynie, a dziewczyna dała ją swojej matce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '28', '28', 'Gdy uczniowie Jana dowiedzieli się o tym, przyszli i zabrali jego ciało, i złożyli w grobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '29', '29', 'Uczniowie powrócili do Jezusa i opowiedzieli mu wszystko, co zrobili i czego nauczali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '30', '30', 'I mówi im: - Pójdźcie osobno na miejsce ustronne i trochę odpocznijcie. Bo wielu (ludzi) przychodziło i odchodziło, tak że nie mieli nawet czasu, aby (coś) zjeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '31', '31', 'I odpłynęli łodzią na miejsce ustronne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '32', '32', 'Lecz wielu widziało, jak odpływali, i wiedzieli dokąd, i zbiegli się tam pieszo ze wszystkich miast, i byli już przed nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '33', '33', 'Kiedy (Jezus) wyszedł, zobaczył wielki tłum. I ulitował się nad nimi, bo byli jak owce bez pasterza. I zaczął ich nauczać o wielu rzeczach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '34', '34', 'A kiedy zrobiła się już późna godzina, podeszli do Niego uczniowie i mówili: - Miejsce jest odludne, a pora spóźniona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '35', '35', 'Rozpuść ich, niech idą do okolicznych zagród i wsi i kupią sobie coś do jedzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '36', '36', 'On zaś odpowiedział im: - Wy dajcie im jeść! Mówią Mu: - Czy mamy iść i kupić za dwieście denarów chleba i dać im jeść?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '37', '37', 'On zaś powiada: - Ile macie chlebów? Idźcie, zobaczcie! Kiedy zobaczyli, mówią: - Pięć. I dwie ryby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '38', '38', 'I kazał im posadzić wszystkich rzędami na zielonej murawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '39', '39', 'I posiadali grupami po stu i po pięćdziesięciu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '40', '40', 'A gdy wziął pięć chlebów i dwie ryby i wzniósł oczy do nieba, wielbił Boga i połamał chleby, i dawał uczniom, aby im rozdawali. I dwie ryby podzielił między wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '41', '41', 'I wszyscy zjedli, i najedli się do syta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '42', '42', 'I zebrali dwanaście koszyków, pełnych okruszyn i resztek z ryb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '43', '43', 'A najadło się pięć tysięcy mężczyzn.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '44', '44', 'I zaraz nakłonił swoich uczniów, aby weszli do łodzi i popłynęli przed Nim w kierunku Betsaidy, a On tymczasem rozpuści tłum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '45', '45', 'Uwolniwszy się od nich, poszedł się modlić na górze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '46', '46', 'A kiedy nastał wieczór, łódź była na pełnym morzu, a On sam jeden na lądzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '47', '47', 'I widział, jak ciężko pracują wiosłami, bo wiał przeciwny wiatr. Około czwartej straży nocnej idzie do nich po falach morskich. Chciał ich minąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '48', '48', 'Oni zaś, ujrzawszy Go, idącego po falach, myśleli, że to zjawa, i krzyknęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '49', '49', 'Bo wszyscy Go zobaczyli i przerazili się. On zaś zaraz przemówił do nich: - Odwagi, to Ja, nie bójcie się!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '50', '50', 'I wszedł do nich, do łodzi, a wiatr ustał. A oni jeszcze bardziej się dziwili,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '51', '51', 'bo nie zrozumieli zdarzenia z chlebami, lecz ich serce było zatwardziałe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '52', '52', 'I przeprawiwszy się, przybyli do brzegu i znaleźli się na ziemi Genezaret.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '53', '53', 'Kiedy wyszli z łodzi, ludzie Go zaraz poznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '54', '54', 'I biegali po tej całej krainie, i zaczęli znosić chorych na noszach tam, gdzie słyszeli, że (On) jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '5', '55', '55', 'I gdziekolwiek wchodził do wsi czy do miast, czy do zagród, kładli na placach chorych i prosili Go, aby mogli dotknąć się kraju Jego okrycia. A ci, którzy Go dotknęli, wracali do zdrowia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '0', '0', 'I zbierają się wokół Niego faryzeusze i jacyś nauczyciele Pisma, którzy przybyli z Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '1', '1', 'Wtedy zobaczyli, że niektórzy Jego uczniowie jedzą chleb zwyczajnie, to znaczy nie umywszy uprzednio rąk -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '2', '2', 'bo faryzeusze i wszyscy Żydzi, przestrzegając nakazu starszyzny, spożywają pokarmy tylko po starannym umyciu rąk -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '3', '3', 'i to, co z rynku przyniosą, spożywają dopiero po obmyciu. Istnieje też wiele innych rzeczy, których przestrzegają: obmycia kielichów i dzbanków, i naczyń miedzianych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '4', '4', 'I pytali Go faryzeusze i nauczyciele Pisma: - Dlaczego Twoi uczniowie nie postępują według nakazu przodków, ale jedzą chleb zwyczajnie, nie umytymi rękami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '5', '5', 'On zaś odpowiedział im: - Dobrze prorokował o was obłudnikach Izajasz, kiedy pisał: \'Lud ten czci mnie wargami, ale serce ich daleko jest ode mnie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '6', '6', 'czczą mnie jednak na próżno, głosząc nauki i zasady ludzkie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '7', '7', 'Zarzuciliście przykazanie Boże, a strzeżecie nakazów ludzkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '8', '8', 'I mówił im: - Zręcznie omijacie przykazanie Boże, aby zachować wasze nakazy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '9', '9', 'Bo Mojżesz powiedział: \'Czcij ojca twego i matkę twoją, a temu, kto złorzeczy ojcu lub matce - śmierć!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '10', '10', 'Wy zaś mówiąc: Jeśli ktoś powie ojcu albo matce: \'Korban\', to znaczy: to, co tobie miałem dać na utrzymanie, ofiarowałem Bogu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '11', '11', 'nie pozwalacie mu już nic uczynić dla ojca i dla matki');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '12', '12', 'i unieważniacie przykazanie Boże waszym nakazem, który przekazujecie potomnym. Czynicie też wiele rzeczy podobnych temu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '13', '13', 'A kiedy przywołał znowu tłum, powiedział im: - Słuchajcie Mnie wszyscy i zapamiętajcie to sobie:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '14', '14', 'Nie może splamić człowieka nic, co wchodzi do niego z zewnątrz, ale to plami człowieka, co wychodzi z niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '15', '15', 'Kto ma uszy do słuchania, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '16', '16', 'A kiedy wszedł do domu, z dala od tłumu zapytali Go uczniowie o tę przypowieść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '17', '17', 'I mówi im: - Czy wy też nie pojmujecie? Czyż nie rozumiecie, że wszystko, co z zewnątrz wchodzi do człowieka, nie może go uczynić nieczystym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '18', '18', 'Że nie wchodzi do jego serca, ale do brzucha i zostaje wydalone na zewnątrz? W ten sposób uznał wszystkie pokarmy za czyste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '19', '19', 'Powiedział: - Plami człowieka to właśnie, co z niego wychodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '20', '20', 'Z wnętrza bowiem, z serca ludzkiego pochodzą złe myśli i nieczystości, i kradzieże, i zabójstwa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '21', '21', 'i cudzołóstwa, i chciwość, i przewrotność, i podstęp, i wyuzdanie, i zazdrość, i bluźnierstwo, i pycha, i głupota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '22', '22', 'Całe to zło pochodzi z wnętrza człowieka i czyni go nieczystym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '23', '23', 'I odszedł stamtąd w okolice Tyru i Sydonu. Kiedy wszedł do domu, nie chciał, aby ktokolwiek o tym wiedział, nie udało się jednak tego ukryć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '24', '24', 'Ale zaraz usłyszała o Nim kobieta, której córeczka miała ducha nieczystego. Przyszła i upadła Mu do nóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '25', '25', 'A ta kobieta była Greczynką, Syrofenicjanką z pochodzenia, i prosiła Go, aby z jej córki wyrzucił czarta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '26', '26', 'I mówił jej: - Pozwól najpierw najeść się do syta dzieciom, nie godzi się bowiem odejmować chleb od ust dzieciom, a rzucać go szczeniętom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '27', '27', 'A ona Mu odpowiedziała: - Tak, Panie, ale i szczenięta jadają pod stołem resztki po dzieciach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '28', '28', 'I powiedział jej: - Dla tego, coś rzekła, idź, czart wyszedł z twojej córki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '29', '29', 'Kiedy przyszła do domu, znalazła dziecko leżące na łóżku. Czart już je opuścił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '30', '30', 'A kiedy znowu wyszedł z okolic Tyru, przez Sydon przyszedł nad Morze Galilejskie, środkiem Dekapolu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '31', '31', 'I przyprowadzają Mu głuchego, który miał bardzo niewyraźną wymowę, i proszą Go, aby położył na niego ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '32', '32', 'I wziąwszy go osobno, na bok od tłumu, włożył do jego uszu palce, splunął i własną śliną dotknął jego języka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '33', '33', 'Podniósłszy oczy w niebo westchnął i mówi mu: - Effatha, to znaczy: Otwórz się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '34', '34', 'I zaraz otworzyły się mu uszy i została usuniąta wada języka, i mówił wyraźnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '35', '35', 'I nakazał im, aby nikomu o tym nie mówili. Ale im bardziej zakazywał, tym więcej o tym rozpowiadali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '6', '36', '36', 'Niezwykle dziwili się mówiąc: - Wszystko dobrze czyni: i głuchym słuch przywraca, i niemym mowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '0', '0', 'W tym czasie, kiedy znowu zebrał się tłum i nie mieli co jeść, przywoławszy uczniów, mówi im: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '1', '1', 'Żal mi tego tłumu, bo już trzy dni pozostają przy Mnie i nie mają co jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '2', '2', 'A jeżeli ich puszczę głodnych do domu, ustaną w drodze, bo niektórzy z nich przyszli z daleka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '3', '3', 'Odpowiedzieli Mu uczniowie: - Jakże ktoś może nakarmić ich chlebem tutaj na pustyni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '4', '4', 'I pytał ich: - Ile macie chlebów? - Siedem - odpowiedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '5', '5', 'I kazał tłumowi usiąść na ziemi. A kiedy wziął siedem chlebów, odmówił modlitwę dziękczynną, połamał chleb i dał uczniom, aby rozdawali tłumowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '6', '6', 'Mieli też kilka rybek. Wielbiąc Boga rozkazał je też rozdać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '7', '7', 'I zjdeli, i najedli się do syta, i zebrali resztki: siedem koszyków okruchów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '8', '8', 'A było około cztery tysiące ludzi, i rozpuścił ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '9', '9', 'I zaraz wszedł z uczniami do łodzi, i przybył w okolice Dalmanuty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '10', '10', 'I wyszli faryzeusze, i zaczęli podstępnie i natarczywie domagać się od Niego znaku z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '11', '11', 'A westchnąwszy ciężko, mówi: - Czemu to pokolenie domaga się znaku? Zaprawdę powiadam wam, temu pokoleniu znak nie będzie dany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '12', '12', 'Potem ich opuścił, wszedł do łodzi i odpłynął na drugą stronę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '13', '13', 'I zapomnieli zabrać chleba, a mieli tylko jeden chleb ze sobą w łodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '14', '14', 'I nakazywał im: - Uważajcie, unikajcie kwasu faryzeuszów i kwasu Heroda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '15', '15', 'A oni wymawiali sobie nawzajem, że nie wzięli chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '16', '16', 'A Jezus to zauważył i mówi im: - Dlaczego wymawiacie sobie, że nie macie chleba? Czyż jeszcze nie rozumiecie ani nie pojmujecie i serce macie nieczułe?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '17', '17', '\'Macie oczy, a nie widzicie, macie uszy, a nie słyszycie\'. A nie pamiętacie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '18', '18', 'ileście zebrali koszyków pełnych okruszyn, gdy połamałem pięć chlebów dla pięciu tysięcy? Odpowiadają Mu: - Dwanaście. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '19', '19', 'A gdy siedem dla czterech tysięcy, ileście zebrali koszy napełnionych okruszynami? Odpowiadają: - Siedem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '20', '20', 'I powiedział im: - I jeszcze nie pojmujecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '21', '21', 'I przychodzą do Betsaidy. I przyprowadzają Mu niewidomego, i proszą Go, aby się go dotknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '22', '22', 'On zaś ujął niewidomego za rękę, wyprowadził go poza miasto, dotknął śliną jego oczu i położywszy na nim ręce dopytywał się, czy coś widzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '23', '23', 'On, podniósł oczy, powiedział: - Widzę ludzi poruszających się, ale patrzę na nich jak na (chwiejące się) drzewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '24', '24', 'Potem znowu położył ręce na jego oczach. I przejrzał, i odzyskał wzrok. I widział wszystko wyraźnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '25', '25', 'I odesłał go do domu, mówiąc: - A do miasta nie wstępuj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '26', '26', 'I poszedł Jezus z uczniami do wsi pod Cezareą Filipową. A w drodze pytał swoich uczniów: - Za kogo mnie ludzie mają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '27', '27', 'Oni zaś odpowiedzieli: - (Jedni) za Jana Chrzciciela, drudzy za Eliasza, a jeszcze inni za jednego z proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '28', '28', 'A On pytał ich znowu: - Wy zaś za kogo mnie macie? Piotr mu odpowiedział: - Tyś jest Mesjasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '29', '29', 'I przykazał im surowo, aby o Nim nikomu nie mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '30', '30', 'I zaczął ich nauczać; - Trzeba, aby Syn Człowieczy wiele cierpiał, został odrzucony przez starszyznę, arcykapłanów i nauczycieli Pisma, został zabity i po trzech dniach zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '31', '31', 'Mówił o tym otwarcie. A Piotr, przyciągnąwszy Go do siebie, zaczął Mu robić wymówki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '32', '32', 'On zaś odwrócił się, a zobaczywszy uczniów, skarcił ostro Piotra, mówiąc: - Zejdź mi z oczu, kusicielu, bo myślisz po ludzku, a nie po Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '33', '33', 'A przywoławszy tłum ze swoimi uczniami powiedział: - Kto chce iść za Mną, niech wyrzeknie się siebie, weźmie krzyż i postępuje jak Ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '34', '34', 'Kto chciałby zachować swoje życie - straci je, a kto straciłby swoje życie dla mnie i ewangelii - zachowa je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '35', '35', 'Bo cóż pomoże człowiekowi, że cały świat zdobędzie, jeśli życie straci?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '36', '36', 'Bo czegoż by człowiek nie oddał za swoje życie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '7', '37', '37', 'Kto by się wstydził Mnie i słów moich w tym wiarołomnym i grzesznym pokoleniu, tego i Syn Człowieczy będzie się wstydził, kiedy przyjdzie w chwale swego Ojca ze świętymi aniołami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '0', '0', 'I powiedział im: - Zaprawdę powiadam wam: Są wśród obecnych tutaj tacy, którzy nie umrą, aż nie ujrzą królestwa Bożego przychodzącego z mocą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '1', '1', 'Po sześciu dniach Jezus bierze Piotra, Jakuba i Jana i prowadzi ich samych na wysoką górę. I na ich oczach przemienił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '2', '2', 'Szaty Jego zajaśniały taką bielą, że żaden farbiasz na ziemi nie zdoła tak wybielić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '3', '3', 'I zobaczyli Eliasza i Mojżesza, którzy rozmawiali z Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '4', '4', 'Wtedy Piotr odezwał się do Jezusa: - Rabbi, dobrze nam tutaj; rozbijemy trzy namioty, jeden dla Ciebie, jeden dla Mojżesza i jeden dla Eliasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '5', '5', 'Nie zdawał sobie sprawy z tego, co powiedział; byli bowiem przestraszeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '6', '6', 'I chmura ich osłoniła, a z chmury dał się słyszeć głos: - To jest mój Syn ukochany, Jego słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '7', '7', 'Nagle, gdy rozejrzeli się wokoło, nie widzieli nikogo. Tylko sam Jezus (był) z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '8', '8', 'Kiedy schodzili z góry, nakazał im, aby nikomu nie rozpowiadali o tym, co zobaczyli, aż Syn Człowieczy nie zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '9', '9', 'I dobrze zachowali w pamięci to słowo, rozprawiając, co oznacza zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '10', '10', 'I pytali Go: - Nauczyciele Pisma mówią, że najpierw ma przyjść Eliasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '11', '11', 'On im powiedział: - Tak, Eliasz najpierw przyjdzie i wszystko naprawi. Czemu więc napisano o Synu Człowieczym, że wiele wycierpi i zostanie poniżony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '12', '12', 'Ale powiadam wam,Eliasz (już) przyszedł, a postąpili z Nim, jak chcieli, jak o Nim napisano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '13', '13', 'Kiedy przyszli do uczniów, zobaczyli wokół nich wielki tłum i nauczycieli Pisma rozprawiających z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '14', '14', 'A zaraz, kiedy Go zobaczono, cały tłum ogarnęło podniecenie. I kiedy przybiegli, pozdrawali Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '15', '15', 'I zapytał ich? - O czym rozprawiacie z nimi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '16', '16', 'I odpowiedział Mu jeden z tłumu: - Nauczycielu, przyprowadziłem do Ciebie mojego syna, który ma ducha niemego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '17', '17', 'I gdziekolwiek go pochwyci, rzuca na ziemię, a on się ślini i zgrzyta zębami, i drętwieje. Powiedziałem Twoim uczniom, żeby go wyrzucili, ale nie mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '18', '18', 'On zaś im odpowiedział: - Pokolenie niedowiarków, jak długo jeszcze będę z wami? Jak długo was jeszcze będę znosił? Przyprowadźcie go do Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '19', '19', 'I przyprowadzili go do Niego. Kiedy duch zobaczył Jezusa, zaraz szarpnął chłopca, który upadł na ziemię i tarzał się z pianą na ustach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '20', '20', 'I zapytał jego ojca: - Od kiedy to ma? On zaś odpowiedział: - Od dzieciństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '21', '21', 'I często rzucał go nawet w ogień czy w wodę, aby go zabić. Lecz jeśli coś możesz, ulituj się i pomóż nam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '22', '22', 'A Jezus powiedział: - Jeśli możesz? Wszystko jest możliwe dla tego, kto wierzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '23', '23', 'Ojciec dziecka zaraz krzyknął: - Wierzę, pomóż mi w moim niedowiarstwie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '24', '24', 'Kiedy Jezus zobaczył, że zbieg się tłum, rozkazał duchowi nieczystemu: - Duchu niemy i głuchy, rozkazuję ci: wyjdź z niego i nigdy już do niego nie wracaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '25', '25', 'I krzyknął, i szarpnął nim gwałtownie, i wyszedł. A chłopiec zrobił się jak martwy, tak że wielu mówiło: umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '26', '26', 'A Jezus ujął go za rękę i podniósł go. I wstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '27', '27', 'A kiedy wszedł do domu, uczniowie pytali Go na osobności: - Dlaczego to myśmy nie mogli go wyrzucić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '28', '28', 'I odpowiedział im: - Ten rodzaj nie może wyjść inaczej, jak tylko przez modlitwę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '29', '29', 'A kiedy wyszli stamtąd, przechodzili przez Galileę i nie chciał, aby o tym wiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '30', '30', 'Nauczał bowiem swoich uczniów i mówił im: - Syn Człowieczy zostanie wydany w ręce ludzi i zabiją Go, a trzy dni po swej śmierci zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '31', '31', 'Lecz oni nie rozumieli tego i bali się Go pytać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '32', '32', 'I przyszli do Kafarnaum. Kiedy znalazł się w domu, zapytał ich: - Nad czym zastanawialiście się w drodze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '33', '33', 'Oni zaś milczeli, bo w drodze rozmawiali o tym, kto jest większy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '34', '34', 'A kiedy usiadł, zawołał Dwunastu i mówi im: - Kto chce być pierwszy, niech będzie ostatnim z wszystkich i niech usługuje wszystkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '35', '35', 'I postawił dziecko na środku, przytulił do siebie i powiedział im: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '36', '36', 'Kto przyjmie jedno z tych dzieci w imię moje, ten Mnie przyjmie, a kto Mnie przyjmie, nie Mnie przyjmuje, ale Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '37', '37', 'Powiedział Mu Jan: - Nauczycielu, widzieliśmy kogoś, kto wyrzucał czarty w Twoje imię i zabranialiśmy mu, ponieważ nie chodzi razem z nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '38', '38', 'A Jezus powiedział: - Nie zabraniajcie mu, bo ten, przez którego działa (nadziemska) moc w imię moje, nie będzie mógł wkrótce Mi złorzeczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '39', '39', 'Bo kto nie jest przeciwko nam, jest z nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '40', '40', 'Ktokolwiek poda wam kubek wody w imię tego, żeście Chrystusowi, zaprawdę powiadam wam: zapłata go nie minie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '41', '41', 'A temu, kto doprowadził do złego jednego z tych małych, którzy wierzą, lepiej by było, żeby mu przywiązano kamień młyński do szyi i wrzucono w morze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '8', '42', '42', 'Jeśli twoja ręka doprowadza cię do złego, odetnij ją. Lepiej ci przecież wejść do życia bez ręki, aniżeli z dwiema rękami pójść do piekła, w ogień, który nie gaśnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '0', '0', 'Stamtąd idzie w okolicę Judei i na drugą stronę Jordanu. I znowu szły do Niego tłumy, i znowu je nauczał jak zwykle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '1', '1', 'A faryzeusze pytali Go podstępnie: - Czy wolno żonatemu rozwieść się z żoną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '2', '2', 'On zaś im odpowiedział: - Co wam nakazał Mojżesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '3', '3', 'Powiedzieli: - Mojżesz pozwolił napisać list rozwodowy i rozwieść się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '4', '4', 'Jezus zaś powiedział: - Dał wam takie prawo z powodu waszej zatwardziałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '5', '5', 'Lecz na początku stworzenia (Bóg) \'uczynił ich mężczyzną i kobietą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '6', '6', 'Dlatego opuści człowiek swojego ojca i swoją matkę, a połączy się z żoną');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '7', '7', 'i będą oboje jednym ciałem\' tak że już nie są więcej dwojgiem (ludzi), lecz jednym człowiekiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '8', '8', 'Co więc Bóg połączył, niech człowiek nie rozdziela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '9', '9', 'W domu uczniowie znowu Go o to pytali:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '10', '10', 'I mówi im: - Kto rozwiedzie się z żoną i ożeni się z inną, popłenia cudzołóstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '11', '11', 'Jeśli żona rozwiedzie się z mężem i wyjdzie za innego, popełnia cudzołóstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '12', '12', 'I przyprowadzili do Niego dzieci, aby położył na nie ręce. A uczniowie powstrzymywali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '13', '13', 'Kiedy to Jezus zobaczył, obruszył się na nich i powiedział: - Pozwólcie dzieciom przyjść do Mnie i nie powstrzymujcie ich, bo do takich należy królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '14', '14', 'Zaprawdę powiadam wam: Kto nie przyjmie królestwa Bożego tak jak dziecko, nie wejdzie do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '15', '15', 'I przytuliwszy je błogosławił, kładąc na nie ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '16', '16', 'Kiedy wyruszał w drogę, przybiegł jakiś (człowiek) i rzuciwszy się na kolana pytał: - Nauczycielu dobry, co mam czynić, aby zdobyć życie wieczne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '17', '17', 'A Jezus mu powiedział: - Dlaczego nazywasz Mnie dobrym? Nikt poza Bogiem nie jest dobry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '18', '18', 'Znasz przykazania: \'nie zabijaj, nie cudzołóż, nie kradnij, nie zeznawaj fałszywie, nie oszukuj, czcij ojca i matkę\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '19', '19', 'Powiedział Mu: - Nauczycielu, przestrzegałem tego wszystkiego od mojej młodości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '20', '20', 'A Jezus spojrzał na niego z miłością i powiedział mu: - Jednego ci brak: Idź, sprzedaj, co masz, i rozdaj ubogim, a będziesz miał skarb w niebie. A potem pójdź ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '21', '21', 'On zaś słuścił oczy na te słowa i odszedł smutny, bo posiadał wielkie bogactwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '22', '22', 'A Jezus spojrzawszy wokół mówi swoim uczniom: - Jakże trudno bogatym wejść do królestwa Bożego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '23', '23', 'Uczniowie dziwili się Jego słowom, a Jezus ponownie mówi do nich: - Dzieci, jakże trudno wejść do królestwa Bożego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '24', '24', 'Łatwiej wielbłądowi przejść przez ucho igielne, aniżeli bogatemu wejść do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '25', '25', 'Oni nie mogli się temu nadziwić i mówili: - Któż więc może być zbawiony?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '26', '26', 'Jezus zaś spojrzawszy na nich, mówi: - U ludzi to niemożliwe, możliwe jednak u Boga, bo Bóg wszystko może.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '27', '27', 'Wtedy Piotr zaczął mówić: - A myśmy opuścili wszystko i poszliśmy za Tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '28', '28', 'Jezus rzekł: - Zaprawdę powiadam wam: Ktokolwiek opuści dom albo braci, albo siostry, albo matkę, albo ojca, albo dzieci, albo gospodarstwo dla Mnie i dla ewangelii,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '29', '29', 'otrzyma strokroć więcej teraz, w tym czasie, domostw i braci, i sióstr, i matek, i dzieci, i ziemi z jej troskami a w przyszłym wieku życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '30', '30', 'Wielu pierwszych będzie ostatnimi, a ostatnich pierwszymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '31', '31', 'Kiedy znajdowali się w drodze do Jerozlimy, Jezus szedł przed nimi. I dziwili się, a lęk ogarniał tych, co szli za Nim. I znowu wziął ze sobą Dwunastu i zaczął im mówić o tym, co miało Go spotkać: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '32', '32', 'Oto idziemy do Jerozolimy, a Syn Człowieczy zostanie wydany arcykapłanom i nauczycielom Pisma i skażą Go na śmierć, i wydadzą poganom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '33', '33', 'I wyszydzą Go, i będą pluli na Niego, i ubiczują Go, i zabiją, lecz On po trzech dniach zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '34', '34', 'Jakub i Jan, synowie Zebedeusza, podeszli do Niego, mówiąc: - Nauczycielu, chcielibyśmy, żebyś zrobił dla nas to, o co Cię poprosimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '35', '35', 'On powiedział: - Co mam dla was zrobić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '36', '36', 'Odpowiedzieli: - Spraw, abyśmy siedzieli w Twojej chwale, jeden po Twojej prawej stronie, a drugi po lewej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '37', '37', 'A Jezus im powiedział: - Nie wiecie, o co prosicie. Czy możecie wypić kielich, który Ja piję? Albo przyjąć chrzest, który Ja przyjmuję?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '38', '38', 'Odpowiedzieli Mu: - Możemy! A Jezus im powiedział: - Kielich, który Ja piję, wypijecie i przyjmiecie chrzest, który Ja przyjmuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '39', '39', 'Nie do Mnie jednak należy sprawić, abyście siedzieli po mojej prawej stronie albo po lewej. Te miejsca należą do tych, dla których zostały przygotowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '40', '40', 'Dziesięciu, usłyszawszy to, zaczęło się obruszać na Jakuba i Jana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '41', '41', 'A Jezus przywołał ich do siebie i powiedział: -  Wiecie, że ci, których uważa się za władców, panują nad narodami, a ich wielmoże dają narodom odczuć swoją władzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '42', '42', 'U was tak nie będzie. Lecz kto chciałby być wielkim wśród was, niech wszystkim usługuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '43', '43', 'A kto by chciał być pierwszy, niech będzie niewolnikiem wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '44', '44', 'Bo i Syn Człowieczy nie przyszedł po to, aby Mu służono, lecz aby służyć i oddać swoje życie jako okup za wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '45', '45', 'I przychodzą do Jerycha. A kiedy On wychodził z Jerycha i uczniowie Jego, i wielki tłum, niewidomy syn Timajosa siedział przy drodze, żebrząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '46', '46', 'Kiedy usłyszał, że to jest Jezus z Nazaretu, zaczął krzyczeć i wołać: - Synu Dawida, Jezusie, zmiłuj się nade mną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '47', '47', 'I wszyscy kazali mu milczeć, lecz on jeszcze głośniej  krzyczał: - Synu Dawida, zmiłuj się nade mną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '48', '48', 'A Jezus przystanąwszy powiedział: - Zawołajcie go. I wołają niewidomego: - Bądź dobrej myśili, wstawaj, woła cię!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '49', '49', 'On zaś zrzucił swoje okrycie i, zerwawszy się z miejsca, przyszedł do Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '50', '50', 'A Jezus powiedział do niego: - Co mam zrobić dla ciebie? Rzekł Mu niewidomy - Rabbuni, abym przejrzał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '9', '51', '51', 'A Jezus mu powiedział: - Idź, twoja wiara cię uzdrowiła. I zaraz przejrzał, i szedł za Nim drogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '0', '0', 'Kiedy zbliżają się do Jerozolimy, do Betfage i Betanii na Górze Oliwnej, wysyła dwóch swoich uczniów');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '1', '1', 'i mówi im: - Idźcie do wsi (leżącej) naprzeciwko was, a zaraz, kiedy wejdziecie do niej, znajdziecie uwiązane oślę, którego nikt jeszcze nie dosiadł. Odwiążcie je i przyprowadźcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '2', '2', 'A gdyby was zapytano, dlaczego to czynicie, powiedzcie: Pan go potrzebuje, ale zaraz przyśle je tutaj z powrotem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '3', '3', 'Poszli i znaleźli oślę, uwiązane przy bramie, na zewnątrz, od strony drogi i odwiązali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '4', '4', 'A niektórzy ze stojących tam pytali ich: - Po co odwiązujecie to oślę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '5', '5', 'A oni odpowiedzieli, jak im Jezus powiedział. I nie sprzeciwili się im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '6', '6', 'Przyprowadzają oślę do Jezusa. Kładą na nie swoje okrycia. A On wsiadł na nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '7', '7', 'I wielu rozpościerało swoje okrycia na drodze, a inni zielone gałązki, ułamane na polu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '8', '8', 'Ludzie zaś idący przed Nim i za Nim wołali: - Hosanna, \'błogosławiony, który przychodzi w imię Pana\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '9', '9', 'Błogosławione królestwo Ojca naszego Dawida, które przychodzi. Hosanna na wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '10', '10', 'I wszedł do Jerozolimy, do świątyni. A obejrzawszy wszystko, wyszedł do Betanii razem z Dwunastoma, bo była już późna godzina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '11', '11', 'A kiedy nazajutrz wyszli z Betanii, poczuł głód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '12', '12', 'I ujrzawszy z daleka drzewo figowe, okryte liśćmi, podszedł (aby zobaczyć), czy czegoś na nim nie znajdzie. Lecz nie znalazł nic oprócz liści, bo to nie była pora na figi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '13', '13', 'I powiedział do drzewa: - Niech już nikt nigdy nie je twojego owocu. A słyszeli to Jego uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '14', '14', 'I wchodzą do Jerozolimy. A kiedy wszedł do świątyni, zaczął wyrzucać sprzedających i kupujących w świątyni. I poprzewracał stoły wymieniających pieniądze i ławki sprzedawców gołębi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '15', '15', 'I nie pozwolił, aby ktokolwiek przeniósł jakikolwiek sprzęt przez świątynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '16', '16', 'I pouczał ich, i mówił: - Czyż nie napisano: Mój dom będzie dla wszystkich narodów domem modlitwy, a wyście zrobili z niego \'jaskinię zbójców\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '17', '17', 'Arcykapłani i nauczyciele Pisma usłyszeli to i szukali sposobności, żeby Go zgładzić, albowiem bali się Go, bo cały tłum zachwycał się Jego nauką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '18', '18', 'A kiedy nadszedł wieczór, wychodzili poza miasto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '19', '19', 'A przechodząc rano, zobaczyli drzewo figowe uschnięte od korzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '20', '20', 'Piotr przypomniał sobie i mówi Mu: - Rabbi, spójrz, drzewo figowe, któreś przeklął, uschło!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '21', '21', 'Jezus im powiedział: - Miejcie ufność w Bogu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '22', '22', 'Zaprawdę powiadam wam: Jeśliby ktoś powiedział tej górze: podnieś się i rzuć w morze, a nie wątpił w to, ale wierzył, że stanie się to, co mówi, tak też będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '23', '23', 'Dlatego mówię wam: Wierzcie, że otrzymacie wszystko, o co się modlicie i o co prosicie. Tak też będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '10', '24', '24', 'A kiedy zabieracie się do modlitwy, a macie coś przeciwko komuś, przebaczcie mu, aby i Ojciec wasz w niebie przebaczył wam wasze winy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '0', '0', 'I zaczął mówić do nich w przypowieściach: - Człowiek \'zasadził winnicę i otoczył ją murem, wykuł w skale dół pod prasę, i zbudował wieżę\' i oddał winnicę w dzierżawę rolnikom, i wyjechał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '1', '1', 'I w odpowiednim czasie wysłał do rolników sługę, aby odebrał od nich część zbiorów z winnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '2', '2', 'Lecz oni pochwycili go, pobili i odesłali z niczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '3', '3', 'I znowu wysłał do nich innego sługę, ale i tego zranili w głowę i sponiewierali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '4', '4', 'I posłał innego, a tego zabili. Posłał też wielu innych: a jednych pobili, a drugich pozabijali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '5', '5', 'Miał jeszcze jednego swego syna ukochanego. Posłał go na końcu do nich myśląc: Syna mojego uszanują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '6', '6', 'Lecz oni powiedzieli do siebie: - To jest dziedzic, chodźmy, zabijmy go, a dziedzictwo będzie nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '7', '7', 'I zabili go, i wyrzucili poza winnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '8', '8', 'Cóż więc uczyni pan winnicy? Przyjdzie i wytraci rolników, i odda winnicę innym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '9', '9', 'Czyż nie czytaliście tego Pisma: \'Właśnie ten kamień, który odrzucili budujący dom, stał się kamieniem węgielnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '10', '10', 'Pan to sprawił, a nas to zdumiewa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '11', '11', 'I starali się Go pojmać, lecz bali się tłumu. Uznali bowiem, że do nich skierował tę przypowieść. I odeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '12', '12', 'I posyłają do Niego niektórych faryzeuszyów i herodianów, aby Go chwycić za słowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '13', '13', 'Kiedy przyszli, mówią Mu: - Nauczycielu, wiemy, że mówisz prawdę i na nikim Ci nie zależy, bo nie ogądasz się na osoby, ale nauczasz prawdziwej drogi Bożej. Czy godzi się płacić podatek cesarzowi, czy nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '14', '14', 'On zaś, przejrzawszy ich obłudę, powiedział: - Czemu Mnie kusicie? Przynieście Mi denar, niech go obejrzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '15', '15', 'Przynieśli. I pyta: - Czyj to wizerunek i napis? Odpowiedzieli: - Cesarski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '16', '16', 'A Jezus im powiedział: - Oddajcie cesarzowi, co cesarskie, a Bogu, co Boże. I podziwiali Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '17', '17', 'Przychodzą do Niego saduceusze, którzy twierdzą, że nie ma zmartwychwstania. I zapytują Go:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '18', '18', '- Nauczycielu, Mojżesz napisał nam: \'Jeśli czyjś brat umrze\' i zostawi żonę, \'a nie zostawi dziecka, niech jego brat weźmie żonę i da pomomstwo swemu bratu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '19', '19', 'Było siedmiu braci. Pierwszy ożenił się i umarł, i nie zostawił potomstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '20', '20', 'Potem ożenił się z nią drugi i umarł bezpotomnie. I trzeci tak samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '21', '21', 'Tak siedmiu nie zostawiło potomstwa. Na końcu umarła żona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '22', '22', 'Czyją ona będzie żoną, kiedy zmartwychwstaną?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '23', '23', 'Jezus zaś rzekł: - Wasz błąd na tym polega, że nie znacie Pisma ani mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '24', '24', 'Bo kiedy (ludzie) powstaną z martwych, nie będą się żenić ani za mąż wychodzić, lecz będą jak aniołowie w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '25', '25', 'A o tym, że umarli zmartwychwstaną, czyż nie czytaliście w księdze Mojżesza, (w opowiadaniu) \'O krzaku\'? Gdzie Bóg mówi: \'Jam Bóg Abrahama i Bóg Izaaka, i Bóg Jakuba\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '26', '26', 'Nie jest Bogiem umarłych, ale żyjących. Jesteście w wielkim błędzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '27', '27', 'A jeden z nauczycieli Pisma, usłyszawszy jak rozprawiali, a widząc, że dobrze im odpowiedział, zapytał się: - Jakie jest pierwsze przykazanie? Jezus odpowiedział: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '28', '28', 'Pierwsze jest: \'Słuchaj, Izraelu, Pan Bóg nasz jest jedynym Panem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '29', '29', 'I będziesz miłował Pana Boga twego z całego serca twego, z całej duszy\' i ze wszystkich myśli, \'i ze wszystkich sił\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '30', '30', 'Drugie zaś: \'Miłuj bliźniego swego jak siebie samego\'. Nie ma żadnego innego przykazania większego od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '31', '31', 'Powiedział Mu nauczyciel Pisma: - Dobrze, Nauczycielu, powiedziałeś prawdę. \'Jeden jest (Bóg) i nie ma innego oprócz Niego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '32', '32', 'I \'kochać Go z całej duszy, i wszystkimi myślami, i ze wszystkich sił\', i \'kochać bliźniego jak samego siebie\' to o wiele więcej niż wszystkie całopalenia i ofiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '33', '33', 'A Jezus zobaczywszy, że rozsądnie odpowiedział, rzekł mu: - Niedaleko jesteś od królestwa Bożego. I nikt już więcej nie miał odwagi zadawać Mu pytań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '34', '34', 'Kiedy Jezus nauczał w świątyni, mówił: - Jakżeż nauczyciele Pisma mogą mówić, że Mesjasz jest synem Dawida?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '35', '35', 'A sam Dawid Duchem Świętym natchniony powiedział: \'Rzekł Pan mojemu Panu: Siądź po mojej prawicy, aż twych nieprzyjaciół rzucę pod twoje stopy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '36', '36', 'Sam Dawid nazywa Go Panem, to jakżesz jest jego synem? A wielki tłum chętnie Go słuchał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '37', '37', 'I mówił w swojej nauce: - Strzeżcie się nauczycieli Pisma, którzy lubują się w powłóczystych szatach i pozdrowieniach na rynkach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '38', '38', 'i pierwszych krzesłach w synagogach, i pierwszych miejscach przy stołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '39', '39', 'Ci, którzy objadą domy wdów pod pozorem długich modlitw, dostaną surowszy wyrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '40', '40', 'Potem usiadł naprzeciw skarbony i przypatrywał się, jak tłum wrzuca do niej drobne pieniądze. Wielu bogatych wrzucało dużo (pieniędzy).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '41', '41', 'A jedna uboga wdowa wrzuciła dwa drobne pieniążki, czyli kodrant.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '42', '42', 'I przywołał swoich uczniów, i powiedział im: - Zaprawdę powiadam wam, ta uboga wdowa wrzuciła do skarbony więcej niż wszyscy wrzucający.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '11', '43', '43', 'Bo wszyscy wrzucili z tego, co im zbywało, a ona przy swoim ubóstwie wrzuciła wszystko, co miała na życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '0', '0', 'Kiedy wychodzi ze świątyni, mówi Mu jeden z Jego uczniów: - Nauczycielu, spójrz, co za kamienie i co za budowle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '1', '1', 'A Jezus mu odpowiedział: - Masz na myśli te wielkie budowle? Nie zostanie tu kamień na kamieniu, każdy będzie zwalony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '2', '2', 'A kiedy siedział na Górze Oliwnej, naprzeciw świątyni, pytał Go na osobności Piotr i Jakub, i Jan, i Andrzej: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '3', '3', 'Powiedz nam, kiedy to nastąpi? I co będzie znakiem tego, że to wszystko zbliża się ku końcowi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '4', '4', 'A Jezus zaczął im mówić: - Uważajcie, aby was kto nie zwiódł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '5', '5', 'Wielu przyjdzie w moim imieniu, mówiąc: Otom jest. I zwiodą wielu (ludzi).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '6', '6', 'A kiedy usłyszycie o wojnach i kiedy dotrą do was wieści z pola walki, nie lękajcie się! Tak musi się stać, lecz to jeszcze nie koniec!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '7', '7', 'Bo powstanie naród przeciw narodowi i królestwo przeciwko królestwu, w niektórych miejscach będą trzęsienia ziemi, nastanie głód - to początek boleści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '8', '8', 'A wy uważajcie na siebie. Będą was ciągali po sądach i będą was biczowali w synagogach, i będą was stawiać przed wodzami i królami z mojego powodu, na świadectwo przeciwko Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '9', '9', 'Najpierw jednak musi być głoszona ewangelia wszystkim narodom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '10', '10', 'A kiedy będą was prowadzić przed sąd, nie troszcie się naprzód o to, co powiecie, ale mówcie to, co będzie wam dane w owej godzinie. Bo to nie wy mówicie, ale Duch Święty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '11', '11', 'I brat wyda brata na śmierć, i ojciec dziecko, a dzieci powstaną przeciw rodzicom i będą ich zabijali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '12', '12', 'I będziecie znienawidzeni przez wszystkich dla mojego imienia. Lecz kto wytrwa do końca, ten będzie zbawiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '13', '13', 'A kiedy zobaczycie \'obrzydliwość spustoszenia\' tam, gdzie jej być nie powinno - kto czyta, niech rozważy - wtedy mieszkańcy Judei niech uciekają w góry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '14', '14', 'Kto jest na dachu, niech nie schodzi ani nie wchodzi do swojego domu, aby coś zabrać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '15', '15', 'A kto na polu, niech nie wraca po swoje okrycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '16', '16', 'A biada brzemiennym i karmiącym w owe dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '17', '17', 'A módlcie się, aby to nie stało się w zimie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '18', '18', 'bo dni te będą taką udręką, jakiej na świecie przez Boga stworzonym, od początku aż do dziś nie było i nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '19', '19', 'I gdyby Pan nie skrócił tych dni, nikt by nie ocalał. Ale ze względu na wybranych Pan skrócił te dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '20', '20', 'I jeśli wtedy ktoś wam powie: Oto tu jest (Mesjasz), oto tam - nie wierzcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '21', '21', 'Pojawią się bowiem fałszywi mejsasze i fałszywi prorocy, i będą czynić znaki zdumiewające, ażeby, jeśli to możliwe, zwieść wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '22', '22', 'A wy uważajcie! Wszystko wam przepowiedziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '23', '23', 'Wówczas, po tej udręce \'słońce się zaćmi i księżyc nie da światła');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '24', '24', 'i gwiazdy będą spadać\' z nieba, \'i moce niebieskie\' będą wstrząśnięte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '25', '25', 'I wtedy ujrzą Syna Człowieczego, przychodzącego na obłokach z wielką mocą i chwałą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '26', '26', 'A wtedy wyśle aniołów i zgromadzi swoich wybranych z czterech stron świata, od krańca ziemi aż do krańca nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '27', '27', 'A uczcie się przypowieści o drzewie figowym. Kiedy jego gałąź staje się już miękka i wypuszcza liście, widzicie, że lato już blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '28', '28', 'Tak i wy kiedy zobaczycie, że to się dzieje, wiedzcie, że (to) jest bliskie, w drzwiach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '29', '29', 'Zaprawdę powiadam wam: zanim to pokolenie przeminie, wszystko to się stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '30', '30', 'Niebo i ziemia przeminą, ale moje słowa nie przeminą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '31', '31', 'Ale nikt nie zna tego dnia ani godziny, ani aniołowie w niebie, ani Syn, tylko Ojciec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '32', '32', 'Uważajcie, czuwajcie, bo nie wiecie, kiedy ten czas nadejdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '33', '33', 'Bo to jest tak, jak z człowiekiem, który udając się w podróż opuścił dom i zdał władzę sługom, wyznaczył każdemu z nich zajęcie, a odźwiernemu nakazał, aby czuwał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '34', '34', 'Czuwajcie więc, bo nie wiecie, kiedy pan domu przyjdzie, czy wieczorem, czy o północy, czy o pianiu kogutów, czy rankiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '35', '35', 'aby przybywszy niespodzianie nie zastał was śpiących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '12', '36', '36', 'A to, co mówię do was, mówię wszystkim: Czuwajcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '0', '0', 'Po dwóch dniach była Pascha i Święta Przaśników. Arcykapłani i nauczyciele Pisma zastanawiali się, jak ująć Go podstępem i zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '1', '1', 'Mówili jednak: - Ale nie w święto, aby nie doszło do rozruchów wśród ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '2', '2', 'A kiedy w Betanii, w domu Symona Trędowatego siedział przy stole, przyszła kobieta z alabasrowym słoikiem czystego olejku nardowego, rozbiła alabastrowy słoik i wylała olejek na Jego głowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '3', '3', 'Niektórzy się oburzali, mówiąc: - Po co to marnowanie olejku?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '4', '4', 'Przecież można było sprzedać ten olejek drożej niż za trzysta denarów, a (pieniądze) rozdać ubogim. I szemrali na nią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '5', '5', 'A Jezus powiedział: - Dajcie jej spokój, dlaczego robicie jej przykrość? Spełniła dla Mnie dobry uczynek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '6', '6', 'Bo ubogich macie ze sobą zawsze i kiedy chcecie, możecie im dobrze czynić, ale Mnie nie zawsze macie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '7', '7', 'Ona zaś uczyniła, co mogła: już naprzód skropiła olejkami moje ciało na pogrzeb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '8', '8', 'Zaprawdę powiadam wam: Wszędzie, na całym świecie, gdziekolwiek będzie głoszona ewangelia, będzie się opowiadać również o tym, co ona uczyniła. Na jej pamiątkę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '9', '9', 'A Judasz Iskariota, jeden z Dwunastu, poszedł do arcykapłanów, aby Go (im) wydać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '10', '10', 'A oni, usłyszawszy to, ucieszyli się i przyrzekli dać mu pieniądze. I zastanawiał się, jak Go wydać w dogodnym czasie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '11', '11', 'A w pierwszy Dzień Przaśników, kiedy zabijano baranka paschalnego, mówią Mu Jego uczniowie: - Gdzie mamy poczynić przygotowania do spożycia paschy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '12', '12', 'I wysyła dwóch uczniów, i mówi im: - Idźcie do miasta. Tam spotka was człowiek, niosący dzban wody. Idźcie za nim');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '13', '13', 'i powiedzcie gospodarzowi domu, do którego on wejdzie: Nauczyciel mówi: Gdzie masz pokój, w którym mógłbym zjeść paschę z moimi uczniami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '14', '14', 'On wskaże wam na górze duży pokój z ustawionymi sofami. Tam poczyńcie przygotowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '15', '15', 'Uczniowie wyszli i przybyli do miasta, i zastali (wszystko),  jak im powiedział. I przygotowali paschę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '16', '16', 'A kiedy nastał wieczór, przychodzi z Dwunastoma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '17', '17', 'I kiedy już byli przy stole i jedli, Jezus powiedział: Zaprawdę powiadam wam: jeden z was Mnie wyda, ten, który je ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '18', '18', 'Zaczęli się smucić i pytać Go jeden po drugim: - Chyba nie ja?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '19', '19', 'On zaś im powiedział: - Jeden z Dwunastu, który macza ze Mną rękę w misie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '20', '20', 'Wprawdzie Syn Człowieczy odchodzi, jak o Nim napisano, lecz biada temu człowiekowi, który Go wyda. Lepiej by było dla niego, żeby się nie narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '21', '21', 'A podczas posiłku, kiedy wielbiąc Boga wziął chleb, połamał go, podał im i rzekł: - Bierzcie, to jest moje ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '22', '22', 'I wziął kielich, odmówił modlitwę dziękczynną i podał im.I wszyscy z niego pili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '23', '23', 'I powiedział im: - To jest moja krew Przymierza, która będzie wylana za wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '24', '24', 'Zaprawdę powiadam wam: Nie będę już pił napoju z winogron aż do tego dnia, w którym będę pił nowy (napój) w królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '25', '25', 'Kiedy odmówili hymn, wyszli na Górę Oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '26', '26', 'I mówi im Jezus: - Wszyscy Mnie opuścicie, bo napisano: \'Uderzę pasterza i rozproszą się owce\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '27', '27', 'Lecz po moim zmartwychwstaniu pójdę przed wami do Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '28', '28', 'Piotr zaś Mu odpowiedział: - Choćby wszyscy Ciebie opuścili, ja tego nie uczynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '29', '29', 'A Jezus mu mówi: - Zaprawdę, powiadam ci: (jeszcze) dzisiaj, tej nocy, zanim kogut zapieje dwa razy, ty trzykroć się Mnie zaprzesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '30', '30', 'On zaś tym bardziej zapewniał: - Chociaż miałbym umrzeć razem z Tobą, nie zaprę się Ciebie. To samo mówili i wszyscy (pozostali).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '31', '31', 'I przychodzą do posiadłości zwanej Getsemani. I mówi swoim uczniom: - Zostańcie tutaj, a Ja się pomodlę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '32', '32', 'Wziął ze sobą Piotra, Jakuba i Jana. I zaczął odczuwać wielki smutek i niepokój,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '33', '33', 'i mówi im: - Bezbrzeżny smutek ogarnia mą duszę. Zostańcie tutaj i czuwajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '34', '34', 'I poszedłszy trochę dalej, upadł na ziemię i modlił się, aby - jeśli to być może - ominęła Go ta godzina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '35', '35', 'I mówił: - Abba, Ojcze, Ty wszystko możesz, odsuń ode Mnie ten kielich, lecz (niech się stanie) nie to, co Ja chcę, ale co Ty chcesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '36', '36', 'I przychodzi, i zastaje ich śpiących. I mówi do Piotra: - Szymonie, śpisz? Nie mogłeś czuwać przez jedną godzinę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '37', '37', 'Czuwajcie i módlcie się, abyście nie ulegli pokusie, duch wprawdzie jest ochoczy, ale ciało słabe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '38', '38', 'I znów odszedł, i modlił się, powtarzając te same słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '39', '39', 'A wróciwszy znowu zastał ich śpiących, bo oczy im się kleiły do snu. I nie wiedzieli, co Mu odpowiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '40', '40', 'I przychodzi po raz trzeci, i mówi im: - Wy nadal śpicie i odpoczywacie? Dość już. Nadszedł czas, oto Syn Człowieczy zostanie wydany w ręce grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '41', '41', 'Wstańcie, chodźmy! Oto nadchodzi ten, który Mnie wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '42', '42', 'I zaraz, kiedy On jeszcze mówił, zjawia się Judasz, jeden z Dwunastu, a z nim razem tłum (wysłany przez) arcykapłanów i nauczycieli Pisma, i starszych z mieczami i kijami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '43', '43', 'Ten zaś, który Go wydał, ustalił z nimi taki znak: Ten, którego pocałuję, to On. Pochwyćcie Go i prowadźcie ostrożnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '44', '44', 'I zaraz przystąpił do Niego i powiedział: - Rabbi. I pocałował Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '45', '45', 'Oni zaś zatrzymali Go i ujęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '46', '46', 'A jeden z obecnych, dobywszy miecza, zadał cios słudze arcykapłana i odciął mu ucho.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '47', '47', 'I powiedział do nich Jezus: - Wyszliście jak na rozbójnika z mieczami i kijami, aby Mnie pochwycić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '48', '48', 'Codziennie byłem z wami, nauczając w świątyni, i nie ujęliście Mnie. Lecz (to się stało), aby się wypełniły Pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '49', '49', 'I opuściwszy Go, wszyscy uciekli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '50', '50', 'A jakiś młodzieniec szedł za Nim w narzuconym na gołe ciało prześcieradle. I ujęli go,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '51', '51', 'ale on pozostawiwszy (w ich rękach) prześcieradło, uciekł nagi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '52', '52', 'I odprowadzili Jezusa do arcykapłana. Tam schodzą się wszyscy: arcykapłani i starsi, i nauczyciele Pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '53', '53', 'A Piotr poszedł za Nim z daleka aż na środek dziedzińca arcykapłana. I siedział razem z sługami i grzał się przy ogniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '54', '54', 'Arcykapłani zaś z całym sanhedrynem szukali dowodów przeciwko Jezusowi, aby Go skazać na śmierć. Ale nie znaleźli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '55', '55', 'Bo wielu składało fałszywe zeznania przeciwko Niemu, ale zeznania te nie były zgodne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '56', '56', 'Niektórzy zeznawali fałszywie przeciwko Niemu:-');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '57', '57', 'Słyszeliśmy, jak mówił: Rozwalę tę świątynię wzniesioną rękami, a po trzech dniach zbuduję inną, nie wzniesioną rękami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '58', '58', 'Lecz i te zeznania nie były zgodne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '59', '59', 'Arcykapłan zaś, stanąwszy na środku, zapytał Jezusa: - Nic nie odpowiadasz? Cóż oni zaznają przeciwko Tobie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '60', '60', 'On jednak milczał i nic nie odpowiadał. Arcykapłan zapytał Go znowu: - Czyś Ty jest Mesjaszem, Synem Błogosławionego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '61', '61', 'A Jezus odpowiedział: - Tak, jestem i \'zobaczycie Syna Człowieczego, siedzącego po prawicy Mocy\' i \'przychodząłcego z obłokami nieba\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '62', '62', 'Arcykapłan zaś, rozdarłszy swoje szaty, mówi: - Na co nam jeszcze świadkowie?!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '63', '63', 'Usłyszeliście błuźnierstwo. Jak wam się zdaje? A wszyscy orzekli, że jest winien śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '64', '64', 'A niektórzy zaczęli pluć na Niego, zasłaniać Mu twarz, policzkować Go i wołać: - Prorokuj! Także słudzy bili Go po twarzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '65', '65', 'Kiedy Piotr był na dole, na dziedzińcu, przychodzi jedna ze służących arcykapłana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '66', '66', 'A zobaczywszy Piotra, grzejącego się i przyjrzawszy się jemu, rzekła: - I ty byłeś z Jezusem Nazarejczykiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '67', '67', 'On zaś zaprzeczył: - Ja nie wiem ani nie rozumiem, co ty mówisz. I wyszedł na zewnątrz, do przedsionka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '68', '68', 'A służąca, zobaczywszy go, znowu zaczęła mówić do tych, co tam stali: - To jest jeden z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '69', '69', 'On znowu zaprzeczał. Wkrótce potem stojący tam mówili Piotrowi: - Naprawdę jesteś jednym z nich, boś i Galilejczyk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '70', '70', 'On zaś zaczął się zaklinać i przysięgać: - Nie znam tego człowieka, o którym mówicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '13', '71', '71', 'I po raz drugi zapiał kogut. A Piotr przypomniał sobie słowa, które mu Jezus powiedział: Zanim kogut zapieje dwa razy, ty trzykroć się Mnie zaprzesz. I wybuchnął płaczem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '0', '0', 'I zaraz wczesnym rankiem arcykapłani ze starszymi (ludu), nauczycielami Pisma oraz całym sanhedrynem odbyli naradę, po czym, związawszy Jezusa, odprowadzili Go i wydali Piłatowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '1', '1', 'I pytał Go Piłat: - Czy Ty jesteś królem żydowskim? On odpowiedział: - Tak, jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '2', '2', 'Arcykapłani zaś stawiali Mu wiele zarzutów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '3', '3', 'A Piłat pytał Go znowu: - Nic nie odpowiadasz? Patrz, jakie zarzuty Ci stawiają!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '4', '4', 'A Jezus już nic nie odpowiedział, tak że Piłat się dziwił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '5', '5', 'A na święto uwalniał im jednego więźnia, którego sobie życzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '6', '6', 'Człowiek, zwany Barabaszem, był uwięziony razem z buntownikami, którzy podczas rozruchów dopuścili się zabójstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '8', '8', 'A Piłat im odpowiedział: - Jeśli chcecie, uwolnię wam króla żydowskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '9', '9', 'Wiedział bowiem, że arcykapłani wydali Go przez zawiść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '10', '10', 'Ale arcykapłani podburzyli tłum, aby Piłat uwolnił im raczej Barabasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '11', '11', 'A Piłat pytał ich znowu: - Cóż więc mam uczynić z tym, którego nazywacie królem żydowskim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '12', '12', 'A oni znowu krzyczeli: - Ukrzyżuj Go!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '13', '13', 'A Piłat powiedział im: - A cóż On uczynił złego? Oni zaś jeszcze głośniej krzyczeli: - Ukrzyżuj Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '14', '14', 'A Piłat, chcąc zadowolić tłumy, uwolnił Barabasza, a wydał Jezusa, aby był ubiczowany i ukrzyżowany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '15', '15', 'Żołnierze zaprowadzili Go do wnętrza pałacu - to jest do pretorium - i zwołali całą kohortę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '16', '16', 'Ubrali Go w purpurę, włożyli Mu wieniec, który upletli z ciernia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '17', '17', 'i zaczęli Go pozdrawiać: - Witajże, królu żydowski!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '18', '18', 'I bili Go po głowie trzciną, i pluli na Niego, i zginając kolana bili przed Nim pokłony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '19', '19', 'A kiedy Go już wyśmiali, zdjęli z Niego purpurę i włożyli nań Jego szaty, i wyprowadzili, aby ukrzyżować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '20', '20', 'I zmusili pewnego przechodnia wracającego z pola, Szymona z Cyreny, ojca Aleksandra i Rufusa, aby niósł Jego krzyż.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '21', '21', 'Prowadzą Go na miejsce (zwane) Golgota, to znaczy Trupia Głowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '22', '22', 'I podawali Mu wino, zmieszane z mirrą, którego nie przyjął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '23', '23', 'Potem krzyżują Go, rozdzielają Jego szaty, rzucając los, co kto weźmie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '24', '24', 'A była godzina trzecia, kiedy Go ukrzyżowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '25', '25', 'Był też napis, obwieszczający Jego winę: Król Judejczyków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '26', '26', 'Razem z Nim krzyżują dwóch łotrów. Jednego po prawej stronie, a drugiego po lewej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '27', '27', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '28', '28', 'A przechodnie urągali Mu. Potrząsając głowami, mówili: - Hej, Ty, co rozwalasz świątynię i w trzy dni ją budujesz,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '29', '29', 'wybaw sam siebie! Zejdź z krzyża!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '30', '30', 'Podobnie i arcykapłani razem z nauczycielami Pisma mówili drwiąco jedni do drugich: - Innych wybawiał, a siebie samego nie może wybawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '31', '31', 'Mesjasz! Król Izraela! Niech teraz zejdzie z krzyża, abyśmy zobaczyli i uwierzyli. Ukrzyżowani z Nim razem też z Niego szydzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '32', '32', 'Kiedy nadeszła szósta godzina, ciemność okryła całą ziemię aż do dziewiątej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '33', '33', 'O godzinie dziewiątej Jezus zawołał donośnym głosem: - \'Eloi, Eloi, lema sabachthani\', co znaczy: \'Boże mój, Boże mój, czemuś Mnie opuścił?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '34', '34', 'Usłyszawszy to, niektórzy z obok stojących mówili: - Patrz! Eliasza woła!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '35', '35', 'A ktoś podbiegł, zwilżył gąbkę octem, zatknął na trzcinę i dawał Mu pić, mówiąc: - Poczekajcie, zobaczymy, czy Eliasz przyjdzie Go zdjąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '36', '36', 'A Jezus z głośnym wołaniem wyzionął ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '37', '37', 'Zasłona świątyni rozdarła się na dwie części od (samej) góry do dołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '38', '38', 'A stojący naprzeciw Niego setnik, kiedy zobaczył, że tak wyzionął ducha, powiedział: - Ten człowiek był naprawdę Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '39', '39', 'A niewiasty przyglądały się z oddali. Była wśród nich Maria Magdalena i Maria matka Jakuba Mniejszego i Józefa, i Salome.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '40', '40', 'One towarzyszyły i usługiwały Jezusowi, kiedy był w Galilei. (Było tam) też wiele innych (kobiet), które przyszły z Nim do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '41', '41', 'A kiedy już zapadł wieczór - a było Przygotowanie, to znaczy dzień poprzedzający szabat -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '42', '42', 'Józef z Arymatei, poważany członek rady, który też oczekiwał królestwa Bożego, poszedł odważnie do Piłata i poprosił o ciało Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '43', '43', 'A Piłat zdziwił się, że (Jezus) już umarł, i zawoławszy setnika, zapytał go, czy już umarł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '44', '44', 'Otrzymawszy potwierdzenie od setnika, podarował zwłoki Józefowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '45', '45', 'Ten zaś kupił półtno (pogrzebowe), zdjął Jezusa, zawinął w płótno, złożył w grobie wykutym w skale i zatoczył kamień na wejście do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '14', '46', '46', 'A Maria Magdalena i Maria Józefowa przypatrywały się, gdzie Go położono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '0', '0', 'Kiedy minął szabat, Maria Magdalena i Maria Jakubowa, i Salome kupiły wonności, aby skropić nimi Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '1', '1', 'I wczesnym rankiem w pierwszy dzień tygodnia przychodzą do grobu, kiedy wzeszło słońce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '2', '2', 'I mówiły między sobą: - Kot nam odsunie kamień od wejścia do grobu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '3', '3', 'Podniósłszy oczy widzą, że kamień jest odsunięty, a był bardzo wielki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '4', '4', 'Kiedy weszły do grobu, zobaczyły młodzieńca, siedzącego po prawej stronie i odzianego w białą szatę. I strach je ogarnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '5', '5', 'A on im mówi: - Nie bójcie się, szukacie Jezusa Nazarejczyka, którego ukrzyżowano? Zmartwychwstał! Nie ma Go tu! Oto miejsce, gdzie Go położyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '6', '6', 'Ale idźcie powiedzieć Jego uczniom i Piotrowi: On idzie przed wami do Galilei. Tam Go zobaczycie, jak wam powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '7', '7', 'Wyszedłszy uciekły od grobu, bo dławił je strach i przerażenie. I nikomu nic nie powiedziały, bo się bały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '8', '8', 'Kiedy powstał (z grobu) rankiem w pierwszy dzień po szabacie, ukazał się najpierw Marii Magdalenie, z której wyrzucił siedem czartów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '9', '9', 'A ona przyszła oznajmić to Jego uczniom, zasmuconym i płaczącym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '10', '10', 'Lecz oni usłyszawszy, że żyje i że się jej ukazał, nie uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '11', '11', 'Potem ukazał się w zmienionej postaci dwom z nich, idącym na wieś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '12', '12', 'Oni, powróciwszy, oznajmili to pozostałym. Ale im również nie uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '13', '13', 'Na koniec ukazał się Jedenastu w czasie posiłku i wyrzucał im niedowiarstwo i zatwardziałość serca, bo nie uwierzyli tym, którzy Go widzieli wskrzeszonego (z martwych).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '14', '14', 'I powiedział im: - Idźcie na cały świat, głoście ewangelię całemu stworzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '15', '15', 'Kto uwierzy i przyjmie chrzest, będzie zbawiony, a kto nie uwierzy, będzie potępiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '16', '16', 'A tym, którzy uwierzą, będą towarzyszyły takie znaki: w moim imieniu będą wyrzucali czarty, będą mówić nowymi językami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '17', '17', 'i będą brać węże w ręce, a choćby wypili coś trującego, nie zaszkodzi im. Na chorych będą kłaść ręce, a oni powrócą do zdrowia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '18', '18', 'Po tych słowach Pan został wzięty do nieba i zasiadł po prawicy Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'mk', '15', '19', '19', 'Oni zaś głosili wszędzie (ewangelię), a Pan wspierał ich naukę i potwierdzał znakami, które jej towarzyszyły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '0', '0', 'Ponieważ wielu już starało się ułożyć opowiadanie o wydarzeniach, które wśród nas się dokonały,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '1', '1', 'tak jak przekazali nam ci, którzy pod początku byli naocznymi świadkami i sługami Słowa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '2', '2', 'uznałem i ja za stosowne, po zbadaniu wszystkiego od początku, opisać ci to po kolei, dostojny Teofilu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '3', '3', 'abyś przekonał się o prawdziwości tego, czego cię ustnie nauczono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '4', '4', 'Za czasów Heroda, króla Judei, żył pewien kapłan, imieniem Zachariasz, ze zmiany Abiasza, a jego żona z rodu Aarona miała na imię Elżbieta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '5', '5', 'Oboje byli sprawiedliwi przed Bogiem, zachowując wiernie wszystkie przykazania i przepisy Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '6', '6', 'Nie mieli jednak dziecka, bo Elżbieta była niepłodna. Oboje zaś byli już w podeszłym wieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '7', '7', 'Kiedy na jego zmianę przyszła kolej pełnienia służby kapłańskiej przed Bogiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '8', '8', 'według przyjętego przez kapłanów zwyczaju przypadło mu losem w udziale, aby wszedł do przybytku Pana i złożył w ofierze kadzidło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '9', '9', 'A całe mnóstwo ludu modliło się na zewnątrz (przybytku) podczas składania kadzidła w ofierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '10', '10', 'Jemu zaś ukazał się anioł Pański po prawej stronie kadzielnego ołtarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '11', '11', 'Kiedy Zachariasz go ujrzał, przestraszył się i lęk go ogarnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '12', '12', 'Ale anioł Pański powiedział do niego: - Nie bój się, Zachariaszu, bo twoja modlitwa została wysłuchana; twoja żona Elżbieta urodzi ci syna i nadasz mu imię Jan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '13', '13', 'Będziesz się cieszył i radował i wielu będzie się cieszyło z jego narodzin.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '14', '14', 'On bowiem będzie wielki przed Panem. Nie będzie pił wina ani sycery i już w łonie matki będzie napełniony Duchem Świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '15', '15', 'Wielu synów Izraela nawróci do ich Pana, Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '16', '16', 'a sam pójdzie przed Nim w duchu i mocy Eliasza, \'aby nawrócić serca ojców do synów\' i  nieposłusznych do roztropności sprawiedliwych i przygotować Panu lud dobrze usposobiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '17', '17', 'A Zachariasz powiedział do anioła: - Po czym to poznam? Bo ja już jestem stary, a moja żona jest także w podeszłym wieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '18', '18', 'Anioł mu odpowiedział: - Ja jestem Gabriel, stojący przed Panem, a zostałem posłany, aby ci przynieść i oznajmić tę dobrą nowinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '19', '19', 'A ty utracisz mowę i nie będziesz mógł mówić aż do dnia, w którym to się stanie, dlatego żeś nie uwierzył moim słowom, które się spełnią w swoim czasie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '20', '20', 'Tymczasem lud oczekiwał Zachariasza i dziwił się, że tak długo przebywa w przybytku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '21', '21', 'Kiedy wyszedł, nie mógł do nich mówić, więc się domyślili, że miał widzenie w przybytku. On zaś dawał im znaki i pozostał niemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '22', '22', 'Kiedy czas jego służby w świątyni dobiegł końca, powrócił do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '23', '23', 'Po tym czasie jego żona Elżbieta poczęła, lecz ukrywała to przez pięć miesięcy mówiąc (sobie): -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '24', '24', 'Pan to uczynił, kiedy raczył zmazać moją hańbę (jaką byłam okryta) wśród ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '25', '25', 'W szóstym miesiącu Bóg posłał anioła Gabriela do miasta galilejskiego, zwanego Nazaret,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '26', '26', 'do dziewicy zaślubionej mężowi, któremu było imię Józef z rodu Dawida, a dziewica miała na imię Maryja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '27', '27', '(Anioł) przyszedł do niej i rzekł: - Raduj się, łaski pełna, Pan z tobą!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '28', '28', 'Ona przestraszyła się na te słowa i zastanawiała się, co może oznaczać to pozdrowienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '29', '29', 'I rzekł do niej anioł: - Nie bój się, Maryjo,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '30', '30', 'bo znalazłaś łaskę u Boga. Oto poczniesz i urodzisz syna, i nadasz Mu imię Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '31', '31', 'On będzie wielki i Synem Najwyższego będzie nazwany, a Pan Bóg da mu tron Jego ojca Dawida.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '32', '32', 'I będzie królował nad domem Jakuba na wieki, a Jego królestwo nie będzie miało końca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '33', '33', 'Maryja rzekła do anioła: - Jakże się to stanie, skoro nie znam męża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '34', '34', 'Anioł zaś jej odpowiedział: - Duch Święty zstąpi na ciebie i moc Najwyższego okryje cię jak obłok. Dlatego też święte (dziecko), które się narodzi, będzie nazwane Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '35', '35', 'A oto twoja krewna, Elżbieta, również poczęła syna na starość i ta, którą nazywają niepłodną, jest już w szóstym miesiącu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '36', '36', 'bo \'bo dla Boga nie ma rzeczy niemożliwych\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '37', '37', 'Wtedy Maryja powiedziała: - Otom ja służebnica Pańska, niech mi się stanie według twego słowa. I odszedł od niej anioł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '38', '38', 'W tym czasie Maryja poszła spiesznie w góry, do miasta Judy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '39', '39', 'Weszła do domu Zachariasza i pozdrowiła Elżbietę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '40', '40', 'Skoro tylko Elżbieta usłyszała pozdrowienie Maryi, poruszyło się dziecko w jej łonie. Elżbieta została napełniona Duchem Świętym');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '41', '41', 'i zawołała głośno: - Błogosławionaś ty między niewiastami i błogosławiony owoc twojego łona!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '42', '42', 'Czym zasłużyłam sobie, że przychodzi do mnie matka mojego Pana?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '43', '43', 'Bo skoro tylko usłyszałam twoje pozdrowienie, dziecko poruszyło się z radości w moim łonie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '44', '44', 'O szczęśliwa, która uwierzyła, że spełni się to, co jej Pan powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '45', '45', 'Maryja rzekła: - Uwielbia dusza moja Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '46', '46', 'I rozradował się duch mój w Bogu, Zbawicielu moim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '47', '47', 'Bo spojrzał na swoją pokorną służebnicę. I odtąd nazywać mnie będą szczęśliwą wszystkie narody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '48', '48', 'Bo wielkie rzeczy uczynił dla mnie Wszechmocny. Święte jest Jego Imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '49', '49', 'A miłosierdzie Jego z pokolenia na pokolenie dla żyjących w bojaźni Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '50', '50', 'Okazał moc swojego ramienia. Udaremnił zamysły pysznych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '51', '51', 'Możnych usunął z tronu, a wywyższył pokornych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '52', '52', 'Potrzebujących obsypał dobrami, a bogaczy odesłał z niczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '53', '53', 'Wziął w opiekę swego sługę Izraela, pamiętając o miłosierdziu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '54', '54', 'które obiecał praojcom naszym: Abrahamowi i potomstwu jego na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '55', '55', 'Maryja zatrzymała się u niej około trzech miesięcy, a potem wróciła do swego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '56', '56', 'Dla Elżbiety nadszedł czas rozwiązania i urodziła syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '57', '57', 'Sąsiedzi jej i krewni dowiedzieli się, że Pan okazał jej tak wielkie miłosierdzie, i cieszyli się razem z nią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '58', '58', 'Ósmego dnia przyszli obrzezać dziecko i chcieli dać mu imię jego ojca Zachariasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '59', '59', 'Lecz matka odpowiedziała: - W żadnym razie, on będzie nazywał się Jan!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '60', '60', 'Powiedzieli jej: - Nie ma przecież nikogo w twoim rodzie, kto by nosił to imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '61', '61', 'I na migi pytali się ojca, jakie imię chciałby mu dać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '62', '62', 'On zaś poprosił o tabliczkę i napisał: - Jan jest jego imię. Zdziwili się wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '63', '63', 'I natychmiast odzyskał mowę, i wielbił Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '64', '64', 'Lęk ogarnął wszystkich sąsiadów i po górach całej Judei opowiadano sobie o tym wszystkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '65', '65', 'I wszyscy, którzy o tym usłyszeli, zastanawiali się, czymże też będzie to dziecko, albowiem ręka Pańska była z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '66', '66', 'A jego ojciec Zachariasz został napełniony Duchem Świętym i przemówił z jego natchnienia:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '67', '67', '\'Niech będzie uwielbiony Pan, Bóg Izraela\', Albowiem spojrzał na swój lud i odkupił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '68', '68', 'I zesłał Zbawiciela z domu swojego sługi, Dawida.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '69', '69', 'Jak przez usta swoich świętych proroków powiedział od wieków:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '70', '70', 'że wybawi nas od nieprzyjaciół i z ręki wszystkich, którzy nas nienawidzą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '71', '71', 'że okaże miłosierdzie praojcom naszym i będzie pamiętał o swoim świętym Przymierzu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '72', '72', 'o przysiędze, którą złożył ojcu naszemu Abrahamowi, i sprawi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '73', '73', 'że uwolnieni z rąk nieprzyjaciół, służyć Mu będziemy bez lęku');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '74', '74', 'w świętości i sprawiedliwości przed Nim przez wszystkie dni nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '75', '75', 'A ty, mój synu, będziesz prorokiem Najwyższego albowiem pójdziesz \'przed Panem przygotować Mu drogi\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '76', '76', 'dasz jego ludowi poznać zbawienie przez odpuszczenie grzechów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '77', '77', 'przez wielkie miłosierdzie Boga naszego, dzięki któremu spojrzało na nas z wysoka wschodzące słońce,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '78', '78', 'aby oświecić pogrążonych w ciemności i mroku śmierci i skierować nasze kroki na drogę pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '0', '79', '79', 'A dziecię rosło i rozwijało się duchowo. I żyło na pustyni aż do chwili wystąpienia przed Izraelem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '0', '0', 'W tym czasie wyszło zarządzenie cesarza Augusta, aby spisać mieszkańców całej ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '1', '1', 'Pierwszy ten spis odbył się za urzędowania Kwiryniusza w Syrii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '2', '2', 'Szli więc wszyscy do spisu, każdy do swego miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '3', '3', 'Poszedł również Józef z galilejskiego miasta Nazaret do Judei, do miasta Dawidowego, które nazywa się Betlejem, ponieważ pochodził z domu i z rodu Dawida,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '4', '4', 'aby zapisano go razem z żoną Maryją, która była brzemienna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '5', '5', 'Kiedy tam przebywali, nadszedł dla niej czas rozwiązania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '6', '6', 'I powiła syna pierworodnego, i owinęła w pieluszki, i położyła w żłobie, ponieważ nie było dla nich miejsca w gospodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '7', '7', 'W tejże okolicy pasterze pełnili straż nocą i czuwali nad swoim stadem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '8', '8', 'Wtedy anioł Pański stanął przy nich. I ogarnęła ich jasność Pana, i bardzo się przestraszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '9', '9', 'Anioł zaś powiedział do nich: - Nie bójcie się, bo zwiastuję wam wielką radość, która będzie udziałem całego ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '10', '10', 'Dziś w mieście Dawidowym narodził się wam Zbawiciel, który jest Mesjaszem, Panem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '11', '11', 'A to znak dla was: Znajdziecie dziecko owinięte w pieluszki i położone w żłobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '12', '12', 'Nagle przy aniele zjawił się tłum wojska niebieskiego, który wielbił Boga mówiąc: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '13', '13', 'Chwała Bogu na wysokości, a na ziemi pokój ludziom, których Bóg sobie upodobał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '14', '14', 'Kiedy aniołowie odeszli do nieba, pasterze mówili między sobą: - Chodźmy do Betlejem i zobaczmy, co się tam stało, a o czym dowiedzieliśmy się od Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '15', '15', 'Poszli więc spiesznie i znaleźli Maryję z Józefem i niemowlę');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '16', '16', 'leżące w żłobie. Kiedy to ujrzeli, zrozumieli wszystko, co im o tym dziecku powiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '17', '17', 'I wszyscy, którzy usłyszeli, dziwili się temu, co pasterze im opowiedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '18', '18', 'A Maryja zachowała wszystkie te wydarzenia i rozważała je w sercu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '19', '19', 'A pasterze wrócili, chwaląc i wielbiąc Boga za wszystko, co usłyszeli i ujrzeli, tak jak im było powiedziane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '20', '20', 'Kiedy nadszedł ósmy dzień, w którym obrzezywano dziecko, nadano Mu imię Jezus, wskazane przez anioła, zanim się poczęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '21', '21', 'Kiedy zaś nadszedł czas oczyszczenia według Prawa Mojżeszowego, zanieśli je do Jerozolimy, aby stawić przed Panem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '22', '22', 'zgodnie z tym, co napisano w Prawie Pańskim: \'Każdy syn pierworodny będzie poświęcony Panu\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '23', '23', 'i aby złożyć ofiarę według przepisu Prawa Pańskiego: \'parę synogarlic lub dwa młode gołębie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '24', '24', 'Żył wtedy w Jeruzalem człowiek imieniem Symeon - człowiek sprawiedliwy i bogobojny. Oczekiwał pocieszenia Izraela, a Duch Święty był z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '25', '25', 'Duch Święty objawił mu, że nie umrze, zanim nie zobaczy Mesjasza Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '26', '26', 'Wiedziony Duchem przyszedł do świątyni, kiedy rodzice wnosili dziecię Jezus, aby wypełnić nakaz Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '27', '27', 'Wziął dziecko na ręce i wielbił Boga. I powiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '28', '28', 'Teraz, wszechwładny Panie, zgodnie z Twym przyrzeczeniem, zwalniasz swego sługę w pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '29', '29', 'Bo moje oczy ujrzały Twoje zbawienie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '30', '30', 'które przygotowałeś wszystkim narodom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '31', '31', 'Światłość dla oświecenia pogan i chwałę Izraela, Twego ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '32', '32', 'A ojciec Jego i matka dziwili się temu, co o Nim mówiono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '33', '33', 'Symeon zaś błogosławił im, a do matki Jego, Maryi, rzekł: - On sprawi, że wielu w Izraelu upadnie i powstanie. On będzie znakiem, któremu się będą sprzeciwiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '34', '34', 'A twoje serce miecz przeszyje, aby wyszły na jaw zamysły wielu (ludzi).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '35', '35', 'Prorokini Anna, córka Fanuela z pokolenia Asera, była już bardzo sędziwa. Po wyjściu za mąż żyła siedem lat z mężem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '36', '36', 'a we wdowieństwie osiemdziesiąt cztery lata. Nie opuszczała świątyni oddając część Bogu przez post i modlitwę we dnie i w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '37', '37', 'Nadszedłszy właśnie, wielbiła Boga i rozpowiadała o Nim wszystkim, oczekującym wybawienia Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '38', '38', 'Kiedy wykonali wszystko zgodnie z Prawem Pańskim, powrócili do Galilei, do swojego miasta Nazaret.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '39', '39', 'Dziecko zaś rosło i nabierało sił, napełniając się mądrością, a łaska Boża była z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '40', '40', 'Rodzice Jego udawali się corocznie do Jeruzalem na Święto Paschy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '41', '41', 'Kiedy miał lat dwanaście, poszli (tam) z pielgrzymką, jak nakazywał świąteczny zwyczaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '42', '42', 'Święta się skończyły, oni wracali do domu, a chłopiec Jezus został w Jeruzalem, ale Jego rodzice  nie wiedzieli o tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '43', '43', 'Sądząc, że On wraca w gromadzie, uszli dzień drogi i zaczęli Go szukać wśród krewnych i znajomych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '44', '44', 'Kiedy Go nie znaleźli, powrócili do Jeruzalem i tam Go szukali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '45', '45', 'Po trzech dniach odnaleźli Go w świątyni: siedział wśród nauczycieli, słuchał ich i zadawał im pytania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '46', '46', 'A wszyscy, słuchając Go, nie mogli się nadziwić Jego mądrości i odpowiedziom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '47', '47', 'Ujrzawszy Go, zdziwili się bardzo, a matka rzekła do Niego: - Synu, dlaczegoś tak postąpił? Oto Twój ojciec i ja szukaliśmy Ciebie strapieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '48', '48', 'Powiedział do nich: - Dlaczegoście Mnie szukali? Czyż nie wiedzieliście, że powinienem być w domu mego Ojca?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '49', '49', 'Lecz oni nie zrozumieli tego, co im powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '50', '50', 'I poszedł z nimi, i przybył do Nazaretu, i był im posłuszny. A Jego matka zachowywała wszystkie te słowa w sercu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '1', '51', '51', 'Jezus zaś wzrastał w mądrości i w latach, i w łasce u Boga i u ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '0', '0', 'W piętnastym roku panowania cesarza Tyberiusza, kiedy Poncjusz Piłat zarządzał Judeą, a Herod był tetrarchą Galilei, Filip zaś, brat jego, tetrarchą Iturei i Krainy Trachonickiej, a Lizaniasz tetrarchą Abileny,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '1', '1', 'za arcykapłanów Annasza i Kajfasza, Bóg wezwał Jana, syna Zachariasza, (przebywającego) na pustyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '2', '2', 'Przeszedł on całą okolicę nadjordańską, nawołując do chrztu nawrócenia dla odpuszczenia grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '3', '3', 'Jak napisano w księdze mów proroka Izajasza: \'Głos wołającego: na pustyni przygotujcie drogę Panu. Prostujcie Mu ścieżki;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '4', '4', 'niech wszelka dolina będzie wypełniona, a każda góra i pagórek zrównane. Krzywe drogi zostaną wyprostowane, a wyboiste wygładzone');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '5', '5', 'i każdy człowiek zostanie zbawiony przez Boga\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '6', '6', 'A tak przemawiał do tłumów, które wychodziły, aby przyjąć od niego chrzest: - Żmije, któż was zapewnił, że unikniecie zbliżającego się gniewu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '7', '7', 'Przynoście więc owoce godne nawrócenia, a nie próbujcie wmawiać sobie: Abrahama mamy za ojca! Albowiem powiadam wam: Z tych kamieni Bóg może wywieść synów Abrahamowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '8', '8', 'Siekierę już przyłożono do korzenia drzew. Każde więc drzewo, które nie rodzi dobrego owocu, będzie wycięte i w ogień wrzucone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '9', '9', 'Tłumy zapytywały go: - Cóż zatem mamy czynić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '10', '10', 'Odpowiadał im: - Kto ma dwie suknie, niech da temu, kto nie ma, a kto ma żywność, niech czyni podobnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '11', '11', 'Przyszli też celnicy, aby przyjąć chrzest. I zapytali go: - Nauczycielu, co mamy czynić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '12', '12', 'On zaś powiedział do nich: - Nie czyńcie nic więcej nad to, co wam rozkazano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '13', '13', 'I żołnierze zapytywali go: - A my co mamy czynić? Powiedział im: - Nikogo nie bijcie ani nie krzywdźcie, ale poprzestawajcie na swoim żołdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '14', '14', 'Kiedy lud gubił się w domysłach, a wszyscy zastanawiali się, czy Jan przypadkiem nie jest Mesjaszem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '15', '15', 'on wobec wszystkich oświadczył: - Ja chrzczę was wodą, ale już nadchodzi mocniejszy ode mnie. Ja nie jestem wart rozwiązać rzemienia Jego sandałów. On was będzie chrzcił ogniem Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '16', '16', 'On (trzyma) w ręku sito do czyszczenia zboża, aby oczyścić swoje klepisko i (potem) zgromadzić ziarno w swoim spichlerzu. A plewy spali w niegasnącym ogniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '17', '17', 'Głosił więc dobrą nowinę, dając ludowi także wiele innych pouczeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '18', '18', 'A Herod tetrarcha, upominany przez niego z powodu Herodiady, żony jego brata, i z powodu wszystkich innych występków, jakich się dopuścił,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '19', '19', 'dorzucił i to do wszystkich: zamknął Jana w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '20', '20', 'Lud tłumnie przyjmował chrzest. A kiedy Jezus został ochrzczony i modlił się, otwarły się niebiosa');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '21', '21', 'i Duch Święty zstąpił na Niego w widzialnej jak gołębica postaci, a z nieba odezwał się głos: - Tyś mój syn ukochany, Ciebie sobie upodobałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '22', '22', 'A gdy Jezus rozpoczynał (działalność), miał około trzydziestu lat. Był, jak mniemano, synem Józefa, syna Hellego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '23', '23', 'syna Matata, syna Lewiego, syna Melchy, syna Jannaja, syna Józefa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '24', '24', 'syna Matatiasza, syna Amosa, syna Nahuma, syna Esliego, syna Nagaja,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '25', '25', 'syna Maatha, syna Matatiasza, syna Semeja, syna Josecha, syna Jody,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '26', '26', 'syna Joanana, syna Resy,  syna Zorobabela, syna Salatiela, syna Neriego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '27', '27', 'syna Melchy, syna Addiego, syna Kosama, syna Elmadama, syna Era,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '28', '28', 'syna Jezusa, syna Eliezera, syna Jorima, syna Matata, syna Lewiego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '29', '29', 'syna Symeona, syna Judy, syna Józefa, syna Jonama, syna Elikima,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '30', '30', 'syna Melei, syna Menny, syna Mataty, syna Natama, syna Dawida,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '31', '31', 'syna Jeseja, syna Jobeda, syna Booza, syna Sali, syna Naassona,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '32', '32', 'syna Aminadaba, syna Admina, syna Arniego, syna Esroma, syna Faresa, syna Judy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '33', '33', 'syna Jakuba, syna Izaaka, syna Abrahama, syna Tarego, syna Nachora,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '34', '34', 'syna Serucha, syna Ragana, syna  Faleka, syna Ebera, syna Sali,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '35', '35', 'syna Kainama, syna Arfaksada, syna Sema, syna Noego, syna Lamecha,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '36', '36', 'syna Matuzala, syna Enocha, syna Jareta, syna Maleleela, syna Kainama,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '2', '37', '37', 'syna Enosa, syna Seta, syna Adama, syna Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '0', '0', 'Jezus pełen Ducha Świętego, wrócił znad Jordanu i przebywał z natchnienia Ducha na pustyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '1', '1', 'Tam diabeł kusił Go przez czterdzieści dni, a On nic nie jadł w tych dniach. Kiedy one dobiegły końca, poczuł głód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '2', '2', 'Powiedział Mu diabeł: - Jeżeli jesteś Synem Bożym, powiedz temu kamieniowi, aby stał się chlebem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '3', '3', 'Jezus mu odpowiedział: - Napisano: \'Nie samym chlebem będzie żył człowiek\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '4', '4', 'I zaprowadziwszy Go na górę, pokazał Mu w okamgnieniu wszystkie królestwa świata');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '5', '5', 'i powiedział Mu diabeł: - Dam Ci tę całą potęgę i przepych, bo do mnie należy, i daję je, komu chcę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '6', '6', 'Jeśli więc złożysz mi hołd, wszystko będzie Twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '7', '7', 'Jezus mu odpowiedział: - Napisano: \'Panu Bogu twojemu, hołd składać będziesz i Jemu tylko będziesz służyć\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '8', '8', 'Zaprowadził Go do Jeruzalem i postawił na ganku świątyni, i powiedział: - Jeżeli jesteś Synem Bożym, rzuć się stąd na dół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '9', '9', 'Bo napisano: \'Rozkazał aniołom swoim, aby Cię strzegli\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '10', '10', 'i (napisano też): Będą Cię nosili na rękach, abyś nie uraził swej nogi o kamień\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '11', '11', 'Odpowiedział mu Jezus: - Powiedziano: \'Nie będziesz kusił Pana, Boga twego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '12', '12', 'A diabeł, zakończywszy całe kuszenie, odszedł od Niego do czasu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '13', '13', 'Jezus powrócił z mocą Ducha do Galilei, a wieść o Nim rozeszła się po całej okolicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '14', '14', 'I nauczał w synagogach, wychwalany przez wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '15', '15', 'Przybył też do Nazaretu, gdzie się wychowywał, i jak zwykle wszedł w szabat do synagogi, i zgłosił się do czytania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '16', '16', 'Podano Mu zwój z proroctwem Izajasza. Kiedy go rozwinął, znalazł miejsce, w którym było napisane:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '17', '17', '\'Duch Pański nade mną, dlatego mnie namaścił, abym głosił ubogim dobrą nowinę, posłał mnie, abym obwieszczał więźniom uwolnienie a ślepym odzyskanie wzroku. Abym wolnością obdarzał uciśnionych');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '18', '18', 'i ogłaszał rok zmiłowania Pańskiego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '19', '19', 'Zwinąwszy zwój oddał go słudze i usiadł. A oczy wszystkich w synagodze były w Niego utkwione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '20', '20', 'Zaczął więc mówić do nich: - Dzisiaj wypełniło się to Pismo, któreście słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '21', '21', 'A wszyscy to potwierdzali i podziwiali słowa pełne wdzięku, które płynęły z ust Jego. I mówili: - Czyż to nie syn Józefa?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '22', '22', 'I rzekł do nich: - Pewnie mi powiecie to przysłowie: Lekarzu, wylecz sam siebie. Tutaj w Twojej ojczyźnie dokonaj tych wielkich rzeczy, które - jak słyszeliśmy - działy się Kafarnaum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '23', '23', 'Powiedział: - Zaprawdę powiadam wam, żaden prorok nie ma uznania w swojej ojczyźnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '24', '24', 'Naprawdę wam mówię: Wiele wdów było w Izraelu za czasów Eliasza, kiedy niebo było zamknięte przez trzy lata i sześć miesięcy i wielki głód nastał w całym kraju,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '25', '25', 'a Eliasz nie został posłany do żadnej z nich, tylko do owdowiałej kobiety w Sarepcie Sydońskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '26', '26', 'I wielu było trędowatych w Izraelu za czasów proroka Elizeusza, a żaden z nich nie został oczyszczony z trądu, tylko Syryjczyk Naaman.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '27', '27', 'Na to wszyscy słuchający w synagodze wpadli w gniew');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '28', '28', 'i zerwawszy się wyrzucili Go za miasto, i zawlekli aż nad urwisko na górze, na której ich miasto było zbudowane, aby Go strącić w przepaść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '29', '29', 'Lecz On oddalił się, przeszedłszy przez sam środek (tłumu).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '30', '30', 'I poszedł do miasta galilejskiego Kafarnaum, i nauczał ich w szabaty,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '31', '31', 'i byli pełni podziwu dla Jego nauki, bo przemawiał z mocą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '32', '32', 'Był w synagodze człowiek, mający ducha nieczystego. I krzyknął głośno:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '33', '33', '- Och, czego Ty chcesz od nas, Jezusie Nazareński, przyszedłeś nas zgubić, wiem, kim jesteś - świętym Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '34', '34', 'A Jezus przykazał mu surowo: - Milcz i wyjdź z niego! Czart zaś, rzuciwszy go na środek, wyszedł z niego, nie wyrządzając mu żadnej krzywdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '35', '35', 'Lęk ogarnął wszystkich i mówili między sobą: - Cóż to za nauka?! Z niezwykłą mocą rozkazuje duchom nieczystym, a one wychodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '36', '36', 'I wieść o Nim docierała do każdego zakątka tego kraju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '37', '37', 'Po opuszczeniu synagogi (Jezus) wszedł do domu Szymona Piotra. Teściowa Szymona cierpiała na wielką gorączkę i prosił Go w jej sprawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '38', '38', 'A stanąwszy nad nią, rozkazał gorączce i opuściła ją. Natychmiast wstała i usługiwała im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '39', '39', 'Po zachodzie słońca wszyscy, którzy mieli różnych chorych, przyprowadzili ich do Niego. On zaś uzdrawiał ich, kładąc na każdym z nich rękę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '40', '40', 'Z wielu też (chorych) wychodziły czarty, krzycząc: - Ty jesteś Syn Boży. On zaś surowo zabraniał im to mówić, bo wiedziały, że jest Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '41', '41', 'Kiedy nastał dzień, wyruszył na miejsce pustynne, a tłumy szukały Go i przyszły do Niego. I prosili Go, aby od nich nie odchodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '42', '42', 'Lecz on powiedział do nich: - Także w innych miastach muszę głosić dobrą nowinę o królestwie Bożym, na to bowiem zostałem posłany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '3', '43', '43', 'I nauczał w synagogach judejskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '0', '0', 'Kiedy tłum napierał na Niego, aby słuchać słowa Bożego, a On stał nad jeziorem Genezaret,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '1', '1', 'ujrzał dwie łodzie przy brzegu, a rybacy wyszli z nich i płukali sieci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '2', '2', 'Wszedł do jednej z łodzi, która należała do Szymona, i poprosił go, aby nieco odpłynął od brzegu. I nauczał tłumy z łodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '3', '3', 'Kiedy przestał mówić, rzekł do Szymona: - Wypłyń na głębię i zarzućcie sieci na połów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '4', '4', 'Szymon odpowiedział: - Nauczycielu, mozoliliśmy się całą noc, aleśmy nic nie ułowili. Lecz na Twój rozkaz zarzucę sieci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '5', '5', 'Uczyniwszy to zagarnęli takie mnóstwo ryb, że sieci zaczęły się rwać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '6', '6', 'Skinęli więc na towarzyszy z drugiej łodzi, aby im pospieszyli z pomocą. I podpłynęli. I napełnili obie łodzie tak, że się prawie zanurzały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '7', '7', 'Widząc to Szymon-Piotr upadł do nóg Jezusa i powiedział: - Odejdź ode mnie, Panie, bom jest człowiek grzeszny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '8', '8', 'Albowiem z powodu takiego połowu ryb, którego dokonali, zdumienie i lęk ogarnęły go i wszystkich jego towarzyszy');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '9', '9', 'oraz Jakuba i Jana, synów Zebedeusza, którzy byli wspólnikami Szymona. A Jezus rzekł do Szymona: - Nie bój się, odtąd ludzi będziesz łowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '10', '10', 'Po wyciągnięciu łodzi na brzeg zostawili wszystko i poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '11', '11', 'Kiedy był w jednym z miast, pojawił się człowiek, cały pokryty trądem. Zobaczywszy Jezusa upadł na twarz i błagał Go, mówiąc: - Panie, jeśli zechcesz, możesz mnie oczyścić!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '12', '12', 'I wyciągnąwszy rękę dotknął go, mówiąc: - Chcę, bądź oczyszczony. I trąd natychmiast znikł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '13', '13', 'A On przykazał: - Nie mów o tym nikomu, ale pokaż się kapłanowi i złóż ofiarę za swoje oczyszczenie, jak nakazał Mojżesz im na świadectwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '14', '14', 'Wieść o Nim rozchodziła się coraz bardziej i wielkie tłumy przybywały, aby Go słuchać i uzyskać zdrowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '15', '15', 'On zaś odchodził na pustynię i tam się modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '16', '16', 'Któregoś dnia nauczał w obecności faryzeuszy i nauczycieli Pisma, którzy przybyli ze wszystkich miejscowości galilejskich i z Judei, i z Jeruzalem. A Pan miał moc uzdrawiania ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '17', '17', 'W pewnej chwili mężczyźni, dźwigając na noszach sparaliżowanego, próbowali go wnieść i położyć przed Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '18', '18', 'A nie widząc, jak się z nim przedostać przez tłum, weszli na dach i przez dach spuścili go z noszami do środka przed Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '19', '19', 'A On widząc ich wiarę, rzekł: - Człowieku, twoje grzechy są odpuszczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '20', '20', 'A nauczyciele Pisma i faryzeusze zaczęli się zastanawiać: - Kimże jest ten bluźnierca? Czyż oprócz Boga może ktoś odpuszczać grzechy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '21', '21', 'Jezus zaś, poznawszy ich myśli, rzekł do nich: - Nad czym się zastanawiacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '22', '22', 'Cóż jest łatwiej powiedzieć: Twoje grzechy są odpuszczone, czy: Wstań i chodź?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '23', '23', 'Ale byście wiedzieli, że Syn Człowieczy ma władzę odpuszczania grzechów na ziemi - powiedział do sparaliżowanego: - Mówię ci, wstań, weź nosze i idź do domu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '24', '24', 'I zaraz na ich oczach wstał, wziął nosze, na których leżał, i poszedł do domu, chwaląc Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '25', '25', 'A wszystkich ogarnęło zdumienie i chwalili Boga, i pełni bojaźni mówili: - Widzieliśmy dziś rzeczy niezwykłe!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '26', '26', 'Potem wyszedł i ujrzał celnika imieniem Lewi, który pobierał opłaty. I rzekł mu: - Pójdź za Mną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '27', '27', 'On zaś zostawił wszystko i poszedł za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '28', '28', 'Lewi wydał w swoim domu wielkie przyjęcie dla Niego. I zasiadł z Nim do stołu wielki tłum celników i innych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '29', '29', 'Faryzeusze a zwłaszcza nauczyciele Pisma z ich grona szemrali i powiedzieli do Jego uczniów: - Dlaczego jecie i pijecie z celnikami i grzesznikami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '30', '30', 'Jezus im odpowiedział: - Zdrowi nie potrzebują lekarza, tylko chorzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '31', '31', 'Nie przyszedłem wzywać do nawrócenia sprawiedliwych, tylko grzeszników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '32', '32', 'A oni powiedzieli do Niego: - Uczniowie Jana podobnie jak uczniowie  faryzeuszów często poszczą i odprawiają modły błagalne, Twoi zaś uczniowie jedzą i piją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '33', '33', 'A Jezus powiedział do nich: - Czyżbyście chcieli, żeby goście weselni pościli, gdy pan młody jest z nimi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '34', '34', 'Nadejdzie czas, kiedy pan młody zostanie spośród nich zabrany, wtedy będą pościć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '35', '35', 'Opowiedział im też przypowieść: - Nikt nie odrywa łaty z nowego okrycia, aby naprawić stare. Inaczej i nowe rozdziera, i łata z nowego nie nada się od starego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '36', '36', 'I nikt nie wlewa młodego wina do starych worków skórzanych. Inaczej nowe wino rozsadzi worki: I wino się wyleje, i worki zniszczeją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '37', '37', 'Ale młode wino wlewa się do nowych worków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '4', '38', '38', 'Nikt, kto wypił stare wino, nie chce młodego, bo mówi: Stare wino jest lepsze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '0', '0', 'Przechodził w szabat wśród zbóż, a Jego uczniowie zrywali kłosy i wykruszając je w rękach jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '1', '1', 'Niektórzy z faryzeuszów rzekli: - Czemu robicie to, czego w szabat nie wolno?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '2', '2', 'Jezus im odpowiedział: - Czyście nie czytali, co uczynił Dawid, kiedy on i jego towarzysze byli głodni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '3', '3', 'Jak wszedł do domu Bożego i zjadał poświęcone chleby, których jeść nie wolno nikomu oprócz samych kapłanów, i dał towarzyszom?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '4', '4', 'I mówił im: - Syn Człowieczy jest Panem szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '5', '5', 'W inny szabat wszedł do synagogi i nauczał. A był tam człowiek, który miał uschniętą prawą rękę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '6', '6', 'A nauczyciele Pisma i faryzeusze podpatrywali Go, czy uzdrawia w szabat, aby móc Go oskarżyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '7', '7', 'Lecz On znał ich myśli i rzekł człowiekowi, który miał uschniętą rękę: - Stań na środku! On podniósł się i stanął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '8', '8', 'A Jezus powiedział do nich: - Pytam was, czy w szabat należy czynić dobrze czy źle? Ratować życie czy niszczyć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '9', '9', 'I powiódłszy wokół po nich wszystkich oczami, rzekł mu: - Wyciągnij rękę. Uczynił to i odzyskał władzę w ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '10', '10', 'A oni postradali zmysły (ze złości) i naradzali się, co zrobić z Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '11', '11', 'Wówczas poszedł na górę, aby się modlić. Całą noc modlił się do Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '12', '12', 'a kiedy nastał dzień, wezwał swoich uczniów, wybrał z nich dwunastu i nazwał ich apostołami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '13', '13', '(Wybrał) Szymona, którego nazwał Piotrem, i jego brata Andrzeja, i Jakuba, i Jana, i Filipa, i Bartłomieja,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '14', '14', 'i Mateusza, i Tomasza, i Jakuba Alfeuszowego, i Szymona, zwanego Gorliwy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '15', '15', 'i Judę Jakubowego, i Judę Iskariotę, który został zdrajcą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '16', '16', 'Kiedy Jezus zszedł z nimi (z góry), zatrzymał się na równinie z wielkim tłumem uczniów, a mnóstwo ludu z całej Judei i z Jeruzalem, i z nadmorskiego Tyru, i Sydonu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '17', '17', 'przyszło, aby Go słuchać i odzyskać zdrowie. A ci, których dręczyły duchy nieczyste, powracali do zdrowia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '18', '18', 'I cały tłum starał się Go dotknąć, bo moc wychodziła z Niego i uzdrawiał wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '19', '19', 'On zaś skierował oczy na uczniów i mówił: - Szczęśliwi ubodzy, bo wasze jest królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '20', '20', 'Szczęśliwi, którzy teraz łakniecie, bo będziecie nasyceni. Szczęśliwi, którzy teraz płaczecie, bo będziecie się śmiali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '21', '21', 'Szczęśliwi będziecie, gdy ludzie będą was nienawidzili i kiedy was wyłączą ze swej społeczności, i będą wam złorzeczyć, i wymażą wasze imię jak zhańbione - z powodu Syna Człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '22', '22', 'W tym dniu weselcie się i radujcie, czeka was bowiem sowita zapłata w niebie, bo ojcowie ich to samo uczynili z prorokami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '23', '23', 'Ale biada wam, bogacze, bo macie swoją radość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '24', '24', 'Biada wam, którzy teraz syci jesteście, to będziecie łaknąć. Biada (wam), którzy się teraz śmiejecie, bo smucić się będziecie i płakać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '25', '25', 'Biada, kiedy wszyscy będą o was pochlebnie się wyrażać, bo ich ojcowie to samo czynili z fałszywymi prorokami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '26', '26', 'A wam, którzy Mnie słuchacie, mówię: Kochajcie nieprzyjaciół waszych, czyńcie dobrze tym, którzy was nienawidzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '27', '27', 'Dobrze mówcie o tych, którzy was przeklinają, módlcie się za tych, którzy was zniesławiają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '28', '28', 'Temu, kto uderzy cię w jeden policzek, nastaw drugi, a temu, kto zabiera ci suknię, nie odmawiaj też płaszcza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '29', '29', 'Daj każdemu, kto cię prosi, i nie żądaj zwrotu od tego, kto zabiera twoją własność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '30', '30', 'Tak postępujcie z ludźmi, jak byście chcieli, aby oni z wami postępowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '31', '31', 'Bo jeśli kochacie tych, co was kochają, to czymże się chcecie chwalić? Przecież nawet grzesznicy kochają tych, którzy ich kochają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '32', '32', 'A jeśli czynicie dobrze tym, którzy wam dobrze czynią, to czymże się chcecie chwalić? Przecież grzesznicy czynią to samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '33', '33', 'A jeśli pożyczacie tym, od których spodziewacie się zwrotu, to czymże się chcecie chwalić? Grzesznicy także pożyczają grzesznikom, aby to samo odebrać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '34', '34', 'Lecz kochajcie waszych nieprzyjaciół i czyńcie dobrze, i pożyczajcie, nie spodziewając się niczego, a zapłata wasza będzie sowita i będziecie synami Najwyższego, bo On jest dobry dla niewdzięcznych i złych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '35', '35', 'Bądźcie miłosierni, jak Ojciec wasz jest miłosierny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '36', '36', 'I nie osądzajcie, abyście nie zostali osądzeni, i nie potępiajcie, abyście nie zostali potępieni. Przebaczajcie, a i wam będzie przebaczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '37', '37', 'Dawajcie, a będzie wam dane. Miarą dobrą, czubatą, utrzęsioną, przelewającą się dadzą wam w zanadrze. Bo jaką miarą mierzycie, taką wam odmierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '38', '38', 'Opowiedział im też przypowieść: - Czyż ślepy może prowadzić ślepego? Czyż obaj nie wpadną do dołu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '39', '39', 'Nie jest uczeń nad mistrza, (wystarczy), że każdy będzie umiał tyle, co jego nauczyciel.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '40', '40', 'Dlaczego widzisz pyłek w oku brata, a nie dostrzegasz belki w swoim oku?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '41', '41', 'Jak możesz mówić bratu: Pozwól, bracie, że wyjmę pyłek, który jest w  twoim oku, skoro sam we własnym oku belki nie widzisz? Obłudniku! Wyjmij najpierw belkę ze swojego oka, a wtedy będziesz widział, jak wyjąć pyłek z oka swego brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '42', '42', 'Drzewo, które rodzi zły owoc, nie jest dobre, ani to, które rodzi dobry owoc, nie jest złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '43', '43', 'Każde drzewo poznaje się po owocu, bo nie zbiera się fig z ciernia ani winogron nie zrywa się z głogu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '44', '44', 'Dobry człowiek wynosi dobro z dobrego skarbca swego serca. Zły zaś ze złego wynosi zło. Bo z obfitości serca mówią jego usta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '45', '45', 'Dlaczego wołacie do Mnie: Panie, Panie, a nie czynicie tego, co mówię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '46', '46', 'Pokażę wam, do kogo jest podobny każdy, kto przychodzi do Mnie, słucha słów moich i wprowadza je w czyn:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '47', '47', 'Podobny jest do człowieka, który stawiając dom, wkopał się głęboko i założył fundament na skale. Kiedy przyszła powódź, rzeka uderzyła w ten dom, ale nie zdołała go naruszyć dlatego, że dobrze był postawiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '5', '48', '48', 'A kto słucha, ale słów moich w czyn nie wprowadza, podobny jest do człowieka, który postawił dom na ziemi bez fundamentu. Rzeka uderzyła w niego. I zawalił się od razu. I zamienił się ten dom w stos gruzów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '0', '0', 'Kiedy już to wszystko powiedział ludowi, wszedł do Kafarnaum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '1', '1', 'Tam sługa pewnego setnika chorował i był już umierający. Setnik bardzo go cenił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '2', '2', 'Słysząc o Jezusie, posłał do niego starszyznę żydowską z prośbą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '3', '3', 'aby uzdrowił jego sługę. Oni, stanąwszy przed Jezusem, prosili Go bardzo: - Zasłużył sobie na to, abyś mu pomógł:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '4', '4', 'Kocha nasz naród, to on właśnie zbudował nam synagogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '5', '5', 'Jezus wyruszył więc razem z nimi. Ale kiedy był już blisko domu, setnik wysłał przyjaciół, mówiąc: - Nie trudź się Panie, bo nie jestem godzien, abyś wszedł pod mój dach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '6', '6', 'Nie czułem się też godny prosić Cię osobiście. Lecz wydaj tylko rozkaz, a mój sługa wyzdrowieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '7', '7', 'Bo i ja mam władzę nad sobą, mam też żołnierzy pod sobą i mówię temu: Idź, a on idzie; a drugiemu: Przyjdź, to przychodzi, a słudze: Zrób to, a robi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '8', '8', 'Kiedy Jezus to usłyszał, pełen podziwu dla niego zwrócił się do tłumu, który szedł za Nim, i rzekł: - Powiadam wam: nawet w Izraelu nie znalazłem tak wielkiej wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '9', '9', 'Wysłańcy powróciwszy do domu zastali sługę zdrowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '10', '10', 'Potem poszedł do miasta zwanego Nain. Szli też z Nim uczniowie i wielki tłum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '11', '11', 'Kiedy zbliżył się do bramy miejskiej, wynoszono właśnie zmarłego, który był jedynym synem wdowy. I towarzyszył jej wielki tłum mieszkańców miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '12', '12', 'Gdy Pan ją zobaczył, ulitował się i powiedział: - Nie płacz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '13', '13', 'I dotknął się mar. Niosący je zatrzymali się. On zaś powiedział: - Młodzieńcze, mówię ci, wstań!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '14', '14', 'Zmarły usiadł i zaczął mówić. I oddał go matce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '15', '15', 'Wszystkich ogarnął strach i wysławiali Boga, mówiąc: - Prorok wielki zjawił się wśród nas. Oraz: - Bóg spojrzał na swój lud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '16', '16', 'I wieść o tym rozeszła się po całej Judei i jej okolicach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '17', '17', 'A uczniowie Jana donieśli mu o tym wszystkim. Jan zaś, wezwawszy dwóch swoich uczniów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '18', '18', 'wysłał ich do Pana z zapytaniem: - Czy Ty jesteś tym, który ma przyjść, czy też mamy czekać na innego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '19', '19', 'Ludzie ci, przyszedłszy do Niego, powiedzieli: - Jan Chrzciciel przysłał nas do Ciebie z zapytaniem: Czy to Ty jesteś tym, który ma przyjść, czy też mamy czekać na innego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '20', '20', 'Wtedy właśnie uwolnił (Jezus) wielu od chorób, od cierpień i od złych duchów i wielu ślepym przywrócił wzrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '21', '21', 'I dał im taką odpowiedź: - Powiedzcie Janowi, coście widzieli i słyszeli: \'ślepi widzą\', ludzie o bezwładnych nogach chodzą, trędowaci zostają oczyszczeni, głusi słyszą, umarli zmartwychwstają, \'a ubogim głosi się dobrą nowinę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '22', '22', 'Szczęśliwy ten, kto nie zgorszy się z mojego powodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '23', '23', 'Kiedy wysłańcy Jana odeszli, zaczął mówić do tłumów o Janie: - Coście wyszli oglądać na pustyni? Trzcinę chwiejącą się na wietrze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '24', '24', 'Ale coście wyszli oglądać? Człowieka odzianego w miękkie szaty? Ci w wytwornych szatach i w zbytku (żyjący) przebywają w pałacach królewskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '25', '25', 'Coście więc wyszli zobaczyć? Proroka? O tak, powiadam wam, więcej niż proroka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '26', '26', 'To jest ten, o którym napisano: \'Oto posyłam przed Tobą mego posłańca, który przygotuje Ci drogę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '27', '27', 'Mówię wam, nie narodził się człowiek większy nad Jana. Ale przecież najmniejszy w królestwie Bożym jest większy od niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '28', '28', 'A cały lud, który Go słuchał, i celnicy przyjąwszy chrzest Jana przyznali, że Bóg ma słuszność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '29', '29', 'Faryzeusze zaś i biegli w Prawie, nie przyjmując od niego chrztu, odrzucili wezwanie Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '30', '30', 'Do kogo mógłbym przyrównać ludzi tego pokolenia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '31', '31', 'Do kogóż oni są podobni? Podobni są do chłopców, którzy, siedząc na rynku, wołają jedni do drugich jak mówi przysłowie: Graliśmy wam na flecie, a wy nie tańczyliście, zawodziliśmy, a wy nie szlochaliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '32', '32', 'Bo przyszedł Jan Chrzciciel, który nie jada chleba ani nie pije wina, a wy mówicie: Czarta ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '33', '33', 'Przyszedł Syn Człowieczy, który je i pije, a wy mówicie: Oto obżartuch i pijak, przyjaciel celników i grzeszników!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '34', '34', 'I tak wszystkie dzieci mądrości przyznały jej słuszność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '35', '35', 'Jeden z faryzeuszów zaprosił Go do siebie na śniadanie. Wszedł więc do domu faryzeusza i zajął miejsce przy stole.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '36', '36', 'A oto kobieta, która była w mieście grzesznicą, dowiedziawszy się, że On posila się w domu faryzeusza, przyniosła alabastrowy słoik olejku');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '37', '37', 'i z płaczem, stanąwszy z tyłu u Jego stóp, zaczęła oblewać je łzami i wycierać włosami. I całowała Jego stopy, i skrapiała olejkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '38', '38', 'Widząc to faryzeusz, który Go zaprosił, pomyślał sobie: - Gdyby On był prorokiem, to by przecież wiedział, co to za jedna ta kobieta, która Go dotyka - że to grzesznica.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '39', '39', 'Jezus mu odpowiedział: - Szymonie, mam ci coś do powiedzenia. A on na to: - Nauczycielu, mów!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '40', '40', '- Pewien wierzyciel miał dwóch dłużników. Jeden był mu winien pięćset denarów, a drugi pięćdziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '41', '41', 'Ponieważ nie mieli z czego oddać, podarował dług jednemu i drugiemu. Który więc z nich będzie go bardziej kochał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '42', '42', 'Szymon odpowiedział: - Sądzę, że ten, któremu więcej podarował. On zaś powiedział: - Dobrześ osądził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '43', '43', 'I zwróciwszy się do kobiety, powiedział Szymonowi: - Czy widzisz tę kobietę? Wszedłem do twego domu, a nie dałeś Mi wody do nóg. Ona zaś łzami oblała moje nogi i wytarła swoimi włosami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '44', '44', 'Nie powitałeś Mnie pocałunkiem, a ona, odkąd weszła, nie przestaje całować moich stóp.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '45', '45', 'Nie skropiłeś olejkiem mojej głowy, ona zaś wonnym olejkiem skropiła moje nogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '46', '46', 'Dlatego mówię tobie: Odpuszczone są jej liczne grzechy, bo wiele umiłowała. Komu zaś się mało odpuszcza, ten mało miłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '47', '47', 'Do niej zaś rzekł: - Twoje grzechy są odpuszczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '48', '48', 'A posilający się razem z Nim zaczęli się zastanawiać: - Kimże On jest, że odpuszcza grzechy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '6', '49', '49', 'A do kobiety powiedział: - Wiara twoja cię zbawiła. Idź w pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '0', '0', 'Potem chodził po miastach i wioskach, głosząc i zwiastując królestwo Boże. Towarzyszyło Mu dwunastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '1', '1', 'I niektóre kobiety, uwolnione od złych duchów i od chorób: Maria zwana Magdaleną, z której wyszło siedmiu czartów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '2', '2', 'i Joanna, żona Chuzy, zarządcy Heroda, i Zuzanna, i wiele innych. Kobiety te służyły im pomocą  pieniężną ze swoich własnych zasobów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '3', '3', 'Kiedy zebrał się wielki tłum i z miast schodzili się do Niego, opowiedział przypowieść:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '4', '4', '- Siewca wyszedł zasiać ziarno. A kiedy siał, jedno upadło na drogę, zostało zdeptane i zjadły je ptaki latające w powietrzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '5', '5', 'A inne upadło na skałę i wzeszło, i uschło, bo nie miało wilgoci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '6', '6', 'A inne upadło między osty. I osty, które urosły razem z nim, zadusiły je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '7', '7', 'A inne upadło na dobrą ziemię i wzeszło, i przyniosło plon stokrotny. To mówiąc wołał: - Kto ma uszy do słuchania, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '8', '8', 'Uczniowie pytali Go: - Co znaczy ta przypowieść?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '9', '9', 'On zaś powiedział: - Wam dano poznać tajemnice królestwa Bożego, a innym w przypowieściach, aby \'patrzeli, a nie widzieli, i słuchali, a nie rozumieli\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '10', '10', 'A taka jest ta przypowieść: Ziarno to słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '11', '11', 'A na drodze to ci, którzy je wysłuchali. Potem przychodzi diabeł i porywa słowo z ich serca, aby nie uwierzyli i nie zostali zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '12', '12', 'Na skale zaś to ci, którzy z radością przyjmują słowo, kiedy je usłyszą. Nie mają jednak korzenia. Wierzą do czasu i podczas próby odstępują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '13', '13', 'A co padło między osty to ci, którzy wysłuchali słowa, lecz zostali przytłumieni przez troski, bogactwo i rozkosze życia i nie osiągają dojrzałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '14', '14', 'A na dobrej roli to ci, którzy usłyszawszy słowo zachowują je w swoim szlachetnym i dobrym sercu i przynoszą plon dzięki wytrwałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '15', '15', 'Nikt nie zapala lampy i nie przykrywa jej naczyniem ani nie stawia pod łóżkiem, ale na świeczniku, aby wchodzący widzieli światło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '16', '16', 'Nie ma nic skrytego, co by nie stało się jawne, ani ukrytego, co by nie miało być poznane i nie wyszło na jaw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '17', '17', 'Uważajcie więc, jak słuchacie. Bo kto ma, temu będzie dane; a kto nie ma, temu i to, co wydaje mu się, że posiada, zostanie zabrane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '18', '18', 'Przyszła matka i bracia Jego i nie mogli przedostać się do Niego z powodu tłumu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '19', '19', 'Powiedziano Mu: - Matka i bracia Twoi stoją na dworze i chcą się z Tobą zobaczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '20', '20', 'On zaś odpowiedział: - Matką moją i braćmi są ci, którzy słuchają słowa Bożego i wprowadzają je w czyn.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '21', '21', 'Pewnego dnia (Jezus) wszedł do łodzi razem z uczniami. I powiedział do nich: - Przeprawmy się na drugą stronę jeziora. I odbili od brzegu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '22', '22', 'A kiedy płynęli, zasnął. Wtem spadł na jezioro gwałtowny wicher. Fale ich zalewały i znaleźli się w niebezpieczeństwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '23', '23', 'I zbudzili Go, mówiąc: - Nauczycielu, Nauczycielu, giniemy! On zaś powstawszy rozkazał wichrowi i wzburzonym falom. I uspokoiły się, i nastała cisza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '24', '24', 'Powiedział im: - Gdzie wasza wiara? Oni zaś przestraszeni zdumiewali się, mówiąc między sobą: - Kimże On jest, że rozkazuje wiatrom i morzu, a one są Mu posłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '25', '25', 'I przypłynęli do kraju Gergezeńczyków, leżącego naprzeciwko Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '26', '26', 'Kiedy wyszedł na ląd, wybiegł naprzeciw Niego z miasta jakiś mężczyzna opętany przez czarta. Od dłuższego czasu chodził bez ubrania i nie mieszkał w domu, ale w grobowcach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '27', '27', 'Kiedy zobaczył Jezusa, z krzykiem przypadł do Niego i głośno zawołał: - Czego chcesz ode mnie. Jezusie, Synu Boga Najwyższego? Błagam Cię, nie dręcz mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '28', '28', '(Jezus) bowiem nakazał duchowi nieczystemu wyjść z człowieka, bo już od wielu lat trzymał go w swojej mocy. Wiązano go łańcuchami i nakładano pęta, lecz on zrywał więzy, a czart wypędzał go na pustynię. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '29', '29', 'Jakie jest twoje imię? - zapytał go Jezus. Odpowiedział: - Legion! - Ponieważ wiele czartów weszło w niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '30', '30', 'I prosiły Go, aby nie kazał im odejść do odchłani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '31', '31', 'A w tym miejscu na górze pasło się wielkie stado świń. I prosiły Go (czarty), aby im pozwolił wejść w te świnie. I pozwolił im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '32', '32', 'Czarty, które wyszły z człowieka, weszły w świnie. A stado rzuciło się z urwistego brzegu w dół do jeziora i utonęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '33', '33', 'Pasterze zobaczywszy, co się stało, uciekli i rozpowiedzieli o tym w mieście i po zagrodach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '34', '34', 'A ludzie wyszli zobaczyć, co się stało. Podeszli do Jezusa i ujrzeli człowieka, z którego wyszły czarty, siedzącego u stóp Jezusa, ubranego i przy zdrowych zmysłach. I strach ich ogarnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '35', '35', 'A ci, którzy to widzieli, opowiedzieli im, jak opętany został uwolniony od czartów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '36', '36', 'Wtedy cała ludność okolic Gergezy zaczęła Go prosić, aby odszedł od nich, ponieważ bardzo się bali. Wszedł więc do łodzi i zawrócił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '37', '37', 'A człowiek, z którego wyszły czarty, prosił Go, aby mógł zostać przy Nim. Lecz On nie zgodził się, mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '38', '38', '- Wróć do domu i opowiadaj, jak wielkie rzeczy uczynił ci Bóg. I odszedł, rozgłaszając po całym mieście, jak wielkie rzeczy uczynił mu Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '39', '39', 'Kiedy Jezus powrócił, przyjęto Go tłumnie, bo wszyscy Go oczekiwali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '40', '40', 'A oto przyszedł człowiek imieniem Jair. Był on przełożonym synagogi. Upadł do nóg Jezusowi i prosił Go, aby wszedł do jego domu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '41', '41', 'bo miał jedyną córkę około dwunastu lat i ta mu umierała. Kiedy Jezus tym podążał, tłumy napierały na Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '42', '42', 'A kobieta, która od lat dwunastu cierpiała na krwotok i cały swój majątek wydała na lekarzy, a żaden jej nie mógł uleczyć,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '43', '43', 'podeszła z tyłu i dotknęła się kraju Jego płaszcza. I natychmiast ustał krwotok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '44', '44', 'A Jezus zapytał: - Kto się Mnie dotknął? A że wszyscy się wypierali, Piotr powiedział: - Nauczycielu, tłumy się tłoczą wokół Ciebie i popychają Cię!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '45', '45', 'Ale Jezus rzekł: - Ktoś się Mnie dotknął, bo poczułem moc ze Mnie wychodzącą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '46', '46', 'Kiedy kobieta spostrzegła, że się nie ukryje, przyszła z drżeniem i przypadłszy do Niego wyznała wobec całego ludu, z jakiego powodu Go się dotknęła i że natychmiast została uzdrowiona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '47', '47', 'On zaś jej powiedział: - Córko, wiara twoja cię zbawiła. Idź w pokoju!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '48', '48', 'Kiedy jeszcze mówił, przyszedł ktoś z (domu) przełożonego synagogi i powiada: - Córka twoja umarła, nie trudź więcej Nauczyciela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '49', '49', 'Usłyszawszy to Jezus, odrzekł mu: - Nie lękaj się, wierz tylko, a będzie ocalona!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '50', '50', 'Przyszedłszy do domu, nie pozwolił nikomu wejść z sobą oprócz Piotra, Jana i Jakuba, i ojca i matki dziecka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '51', '51', 'A wszyscy zawodzili i opłakiwali ją. Jezus zaś powiedział: - Nie płaczcie, ona nie umarła, ale śpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '52', '52', 'I wyśmiewali się z Niego, bo widzieli, że umarła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '53', '53', 'On zaś ujął ją za rękę i zawołał: - Wstań, dziecko!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '54', '54', 'I powróciła do życia, i natychmiast wstała. I kazał jej dać jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '7', '55', '55', 'Rodzice jej osłupieli. On zaś nakazał im, aby nie mówili nikomu o tym, co zaszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '0', '0', 'Zwoławszy Dwunastu dał im władzę nad wszystkimi czartami i moc uzdrawiania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '1', '1', 'I wysłał ich, aby głosili królestwo Boże i uzdrawiali chorych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '2', '2', 'I rzekł do nich: - Nie bierzcie nic na drogę: ani laski, ani torby, ani chleba, ani pieniędzy. Nie miejcie też dwóch sukien.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '3', '3', 'A kiedy wejdziecie do jakiegoś domu, zostańcie tam i nie wychodźcie stamtąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '4', '4', 'A gdyby was gdzieś nie przyjęto, to wychodząc z tego miasta strząśnijcie proch z waszych nóg na świadectwo przeciwko nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '5', '5', 'Wyruszywszy zatem, rozeszli się po wioskach, głosząc wszędzie ewangelię i uzdrawiając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '6', '6', 'Herod tetrarcha usłyszał o wszystkim, co się stało, i niepokoił się, bo jedni mówili, że to Jan Chrzciciel zmartwychwstał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '7', '7', 'a drudzy, że Eliasz się ukazał, a jeszcze inni, że to zmartwychwstał jeden z dawnych proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '8', '8', 'A Herod powiedział: - Jana ściąłem. Kimże więc jest Ten, o którym słyszę takie rzeczy? I pragnął Go zobaczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '9', '9', 'Po powrocie apostołowie opowiedzieli o wszystkim, czego dokonali. I zabrawszy ich udał się na osobność pod miasto zwane Betsaida.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '10', '10', 'Tłumy jednak dowiedziały się i poszły za Nim. A On je przyjął, mówił im o królestwie Bożym i uzdrowił tych, którzy tego potrzebowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '11', '11', 'Dzień zaczynał się chylić ku zachodowi. Dwunastu więc powiedziało Mu: - Rozpuść tłum, niech idą do okolicznych wsi i zagród odpocząć i znaleźć sobie coś do zjedzenia, bo jesteśmy tu na pustkowiu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '12', '12', 'Rzekł do nich: - Wy dajcie im jeść. Odpowiedzieli: - Mamy tylko pięć chlebów i dwie ryby, chyba że pójdziemy kupić jedzenia dla tego całego ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '13', '13', 'A samych mężczyzn było tam około pięciu tysięcy. I rzekł do uczniów: - Każcie im usiąść grupami, mniej więcej po pięćdziesięciu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '14', '14', 'Tak też uczynili. I rozsadzili wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '15', '15', 'A kiedy wziął pięć chlebów i dwie ryby i wzniósł oczy do nieba, wielbił (Boga), połamał je i podawał uczniom, aby rozdawali tłumowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '16', '16', 'I zjedli, i wszyscy najedli się do syta. I zebrano dwanaście koszyków kawałków z tego, co zostało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '17', '17', 'Kiedy Jezus modlił się na osobności - a byli też z Nim uczniowie - zapytał ich: - Za kogo tłumy Mnie mają?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '18', '18', 'Odpowiedzieli: - Jedni za Jana Chrzciciela, drudzy za Eliasza, inni zaś uważają, że to któryś z dawnych proroków zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '19', '19', 'Zapytał ich: - A wy za kogo Mnie macie? A Piotr odpowiedział: - Za Mesjasza Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '20', '20', 'On zaś przykazał im i polecił, aby o tym nikomu nie mówili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '21', '21', 'Powiedział im: - Trzeba, aby Syn Człowieczy wiele wycierpiał i został odrzucony przez starszych, arcykapłanów i nauczycieli Pisma, i umarł, a trzeciego dnia powstał z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '22', '22', 'A do wszystkich mówił: - Jeśli kto chce iść za Mną, niech wyrzeknie się siebie i codziennie bierze swój krzyż, i idzie ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '23', '23', 'A kto by chciał zachować swoje życie, straci je. A kto by stracił swoje życie z powodu Mnie, ten je zachowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '24', '24', 'Bo cóż zyskuje człowiek, który zdobył cały świat, a siebie samego zgubił albo skrzywdził?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '25', '25', 'Bo kto by wstydził się Mnie i moich słów, tego i Syn Człowieczy, kiedy przyjdzie w chwale swojej i Ojca, i świętych aniołów, będzie się wstydził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '26', '26', 'Naprawdę wam mówię: Są wśród tu obecnych tacy, którzy ujrzą królestwo Boże, zanim umrą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '27', '27', 'Mniej więcej osiem dni po tych naukach Jezus wziął ze sobą Piotra, Jakuba i Jana i wszedł na górę, aby się modlić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '28', '28', 'W czasie modlitwy zmienił się wygląd Jego twarzy, a Jego odzienie stało się olśniewająco białe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '29', '29', 'I rozmawiali z Nim dwaj mężowie, którymi byli Mojżesz i Eliasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '30', '30', 'Ukazawszy się w chwale, mówili o Jego odejściu, które miało nastąpić w Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '31', '31', 'Piotra zaś i jego towarzyszy zmorzył sen. Kiedy się obudzili, ujrzeli Jego chwałę i tych dwóch mężów stojących przy Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '32', '32', 'A w chwili, kiedy oni się z Nim rozstawali, Piotr, nie wiedząc, co mówi, rzekł do Jezusa: - Nauczycielu, dobrze nam tutaj! Rozbijemy trzy namioty: Dla Ciebie jeden, dla Mojżesza jeden i jeden dla Eliasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '33', '33', 'A kiedy to mówił, pojawił się obłok i zaczął ich zakrywać. Kiedy tamci weszli w obłok, strach ogarnął uczniów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '34', '34', 'a z obłoku rozległ się głos mówiący: - To jest mój Syn wybrany, Jego słuchajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '35', '35', 'Kiedy ten głos się rozlegał, ujrzeli tylko samego Jezusa. A oni milczeli i wtedy nikomu nie powiedzieli o tym, co zobaczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '36', '36', 'Następnego dnia schodzili z góry. A wielki tłum wyszedł Mu naprzeciw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '37', '37', 'Wtem jakiś mężczyzna z tłumu zawołał: - Nauczycielu, błagam Cię, spojrzyj na mego syna, bo mam tylko jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '38', '38', 'A oto duch go chwyta, on zaś nagle krzyczy. (Duch) rzuca go na ziemię, a on się ślini. I dręcząc go z trudem odchodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '39', '39', 'I prosiłem Twoich uczniów, aby go wyrzucili, ale nie mogli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '40', '40', 'A Jezus odrzekł: - Przewrotne pokolenie niedowiarków! Jak długo jeszcze będę z wami? Jak długo jeszcze będę was znosił? Przyprowadź tutaj swojego syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '41', '41', 'Kiedy chłopiec zbliżał się do (Jezusa), czart go chwycił i szarpnął nim. Jezus zgromił ducha nieczystego, uzdrowił chłopca i oddał go ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '42', '42', 'A wszyscy byli pełni podziwu dla wielkości Boga.                                                                                                                                                           Ponowna zapowiedź męki                                                                                                                     Lecz kiedy podziwiali wszystko, co uczynił, On rzekł do swoich uczniów:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '43', '43', '- Zapamiętajcie sobie dobrze te słowa: Syn Człowieczy ma być wydany w ręce ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '44', '44', 'Oni jednak nie rozumieli tych słów; były przed nimi zakryte, aby ich nie pojęli. A bali się Go o to pytać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '45', '45', 'Zastanawiali się nad tym, kto też z nich jest największy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '46', '46', 'A Jezus, który wiedział, nad czym się zastanawiają, wziął dziecko, postawił je przy sobie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '47', '47', 'i powiedział im: - Kto przyjmie to dziecko w imię moje, Mnie przyjmuje. A kto Mnie przyjmuje, przyjmuje Tego, który Mnie posłał. Albowiem ten, kto spośród was wszystkich jest najmniejszy - jest największy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '48', '48', 'A Jan na to: - Nauczycielu, widzieliśmy kogoś, kto wyrzucał czarty w Twoje imię i zabranialiśmy mu, bo nie chodzi z nami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '49', '49', 'Jezus zaś rzekł do niego: - Nie zabraniajcie! Kto bowiem nie jest przeciwko wam, ten jest z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '50', '50', 'Kiedy zbliżał się czas, w którym Jezus miał być wzięty (do nieba), zwrócił swoje kroki w kierunku Jeruzalem');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '51', '51', 'i wysłał przed sobą posłańców. A oni weszli do wsi samarytańskiej, aby Mu przygotować miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '52', '52', 'Lecz (Samarytanie) Go nie przyjęli, ponieważ zdążał do Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '53', '53', 'Widząc to uczniowie Jakub i Jan rzekli: - Panie, jeśli tylko zechcesz, rozkażemy, aby \'ogień zstąpił z nieba i strawił ich\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '54', '54', 'Lecz On skarcił ich ostro.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '55', '55', 'I udali się do innej wioski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '56', '56', 'W drodze ktoś rzekł do Niego: - Pójdę za Tobą, dokądkolwiek się udasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '57', '57', 'Jezus mu powiedział: - Lisy mają nory i ptaki latające w powietrzu mają gniazda, a Syn Człowieczy nie ma dachu nad głową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '58', '58', 'Rzekł też do innego: - Chodź za Mną! Lecz on powiedział: - Panie, pozwól mi najpierw pogrzebać ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '59', '59', 'Powiedział mu: - Zostaw umarłym grzebanie umarłych, a ty głoś królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '60', '60', 'Inny zaś powiedział: - Pójdę za Tobą, Panie, ale pozwól mi najpierw pożegnać się z moimi domownikami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '8', '61', '61', 'Jezus rzekł: - Kto przyłożył rękę do pługa, a ogląda się za siebie, nie nadaje się do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '0', '0', 'Potem Pan wyznaczył innych siedemdziesięciu dwóch i rozesłał ich po dwóch przed sobą do każdego miasta i miejsca, do którego zamierzał się udać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '1', '1', 'I rzekł do nich: - Żniwo wprawdzie wielkie, ale robotników mało. Proście więc Pana żniwa, aby posłał robotników na swoje żniwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '2', '2', 'Idźcie, oto posyłam was jak jagnięta między wilki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '3', '3', 'Nie noście sakiewki ani torby, ani sandałów. I nie pozdrawiajcie nikogo w drodze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '4', '4', 'Kiedy wejdziecie do jakiegoś domu, powiedzcie najpierw: Pokój temu domowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '5', '5', 'A jeśli będzie tam syn pokoju, wasz pokój spocznie na nim; jeśli zaś nie, powróci do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '6', '6', 'Zostańcie w tym domu, jedząc i pijąc to, co mają. Bo wart jest robotnik swojej strawy. Nie przenoście się z domu do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '7', '7', 'A gdy wejdziecie do jakiegoś miasta i przyjmą was, jedzcie, co wam podadzą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '8', '8', 'i uzdrawiajcie w nim chorych. I mówcie: Przyszło do was królestwo Boże!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '9', '9', 'A gdy wejdziecie do jakiegoś miasta i nie przyjmą was, wyjdźcie na ulicę i powiedzcie:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '10', '10', 'Strząsamy wam nawet pył, który przylgnął do naszych stóp w waszym mieście. To jednak wiedzcie: Nadeszło królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '11', '11', 'Mówię wam: W owym dniu lżej będzie Sodomie aniżeli temu miastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '12', '12', 'Biada ci, Korozain, biada ci, Betsaido! Bo gdyby w Tyrze i Sydonie działały te moce nadziemskie, które działały u was, dawno by się nawróciły, siedząc we włosiennicy i popiele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '13', '13', 'Toteż Tyrowi i Sydonowi lżej będzie na sądzie aniżeli wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '14', '14', 'A ty Kafarnaum, \'czyż wyniesiesz się aż do nieba? Aż na dno piekła spadniesz!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '15', '15', 'Kto was słucha, Mnie słucha. Kto wami gardzi, Mną gardzi; a kto Mną gardzi, gardzi Tym, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '16', '16', 'Kiedy siedemdziesięciu dwóch powróciło, powiedzieli z radością: - Panie, na Twoje imię nawet czarty są nam posłuszne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '17', '17', 'Rzekł im: - Widziałem szatana spadającego z nieba jak błyskawica.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '18', '18', 'Oto dałem wam władzę deptania po wężach i skorpionach i ujarzmiania wszelkiej siły wrogiej. Nic wam nie zaszkodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '19', '19', 'Nie cieszcie się jednak, że duchy są wam posłuszne, ale cieszcie się, że wasze imiona zapisane są w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '20', '20', 'W tej chwili rozradował się Jezus w Duchu Świętym i rzekł: - Uwielbiam Cię, Ojcze, Panie nieba i ziemi, że zakryłeś to przed uczonymi i mądrymi, a objawiłeś ludziom prostym. Tak, Ojcze, albowiem tak się Tobie podobało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '21', '21', 'Ojciec dał Mi wszystko i nikt nie wie, kim jest Syn, tylko Ojciec, i kim jest Ojciec, tylko Syn i ten, komu Syn zechce objawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '22', '22', 'I zwracając się do uczniów powiedział na osobności: - Szczęśliwe oczy, które oglądają to, co wy oglądacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '23', '23', 'Bo mówię wam: Wielu proroków i królów chciało zobaczyć to, co wy widzicie, a nie zobaczyli, i usłyszeć, co wy słyszycie, a nie usłyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '24', '24', 'A oto pewien biegły w Prawie zapytał Go podstępnie: - Nauczycielu, co mam czynić, aby posiąść życie wieczne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '25', '25', 'On zaś powiedział: - Co napisano w Prawie? Jak czytasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '26', '26', 'Odrzekł: - Będziesz miłował Pana Boga z całego serca, z całej duszy, ze wszystkich sił\' i ze wszystkich myśli, \'a bliźniego swego jak siebie samego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '27', '27', 'Rzekł mu: - Dobrze powiedziałeś. Czyń to, a będziesz żył!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '28', '28', 'On jednak, chcąc się usprawiedliwić, zapytał Jezusa: - A kto jest moim bliźnim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '29', '29', 'Jezus, nawiązując do tego, powiedział: - Pewien człowiek schodził z Jeruzalem do Jerycha i wpadł między zbójców, którzy go obrabowali, pobili i odeszli, zostawiając na pół umarłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '30', '30', 'Przypadkiem pewien kapłan szedł ową drogą, a kiedy go zobaczył, ominął go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '31', '31', 'Podobnie i lewita, który znalazł się w tym miejscu i zobaczył go, ominął go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '32', '32', 'A pewien podróżny Samarytanin przyszedł na to miejsce i zobaczywszy go, ulitował się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '33', '33', 'Podszedł więc, nalał oliwy i wina na jego rany i obwiązał je. Wsadził go na swoje juczne zwierzę, zawiózł do gospody i pielęgnował go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '34', '34', 'Nazajutrz wyjął dwa denary, dał gospodarzowi i rzekł: Pielęgnuj go, a jeśli wydasz coś nadto, oddam ci, gdy będę wracał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '35', '35', 'Jak myślisz, który z tych trzech okazał się bliźnim dla tego, kto wpadł między zbójców?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '36', '36', 'A on odpowiedział: - Ten, który się nad nim ulitował. Rzekł mu Jezus: - Idź i czyń podobnie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '37', '37', 'W czasie podróży Jezus wszedł do wioski. A pewna kobieta imieniem Marta przyjęła Go w gościnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '38', '38', 'Miała siostrę, którą zwano Marią. Ta, siedząc u stóp Pana, słuchała Jego słów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '39', '39', 'Marta zaś krzątała się około wielu posług. Przystanąwszy powiedziała: - Panie, nic Cię to nie obchodzi, że moja siostra mnie jednej pozostawiła usługiwanie? Powiedz jej zatem, żeby mi pomogła!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '40', '40', 'A Pan jej odpowiedział: - Marto, Marto troszczysz się i niepokoisz o tak wiele,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '9', '41', '41', 'a przecież jednego tylko potrzeba. Maria wybrała najlepszą cząstkę, która nie zostanie jej zabrana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '0', '0', 'Jezus modlił się na pewnym miejscu. A kiedy skończył, jeden z Jego uczniów rzekł do Niego: - Panie, naucz nas się modlić, jak Jan nauczył swoich uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '1', '1', 'On zaś powiedział mu: - Kiedy się modlicie, mówcie: Ojcze, niech się święci imię Twoje. Niech przyjdzie Twoje królestwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '2', '2', 'Chleba naszego powszedniego daj nam na każdy dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '3', '3', 'I odpuść nam nasze grzechy, bo i my odpuszczamy każdemu, kto nam zawinił, i nie dopuść, abyśmy ulegli pokusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '4', '4', 'I rzekł do nich: - Ktoś z was ma przyjaciela, idzie do niego o północy i powiada: Przyjacielu, pożycz mi trzy chleby,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '5', '5', 'bo właśnie mój przyjaciel przybył do mnie prosto z drogi i nie mam mu co dać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '6', '6', 'A on odpowiada z wnętrza domu: Daj mi spokój! Drzwi już zamknięte na klucz, a moje dzieci już śpią i ja też. Nie mogę wstać, żeby ci podać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '7', '7', 'Powiadam wam: Jeśli nie wstanie, aby mu dać dlatego, że jest jego przyjacielem, to wstanie i da, ile mu potrzeba, z powodu jego natręctwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '8', '8', 'A Ja wam powiadam: proście, a otrzymacie, szukajcie, a znajdziecie, kołaczcie, a otworzą wam,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '9', '9', 'bo każdy, kto prosi, otrzymuje, kto szuka znajduje, a kołaczącemu otwierają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '10', '10', 'Gdy syn któregoś z was poprosi swego ojca o rybę, czyż on zamiast ryby da mu węża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '11', '11', 'Albo czy da skorpiona, gdy syn prosi o jajko?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '12', '12', 'Jeśli więc wy, będąc złymi, potraficie dawać swoim dzieciom dobre dary, to o ileż bardziej Ojciec, który jest w niebie, da Ducha Świętego tym, którzy Go proszą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '13', '13', '(Jezus) wyrzucał czarta (z człowieka), który był niemy. Kiedy czart wyszedł, niemy przemówił. I zdumiały się tłumy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '14', '14', 'Lecz niektórzy z nich rzekli: - Wyrzuca czarty mocą Beelzebula, władcy czartów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '15', '15', 'Inni zaś podstępnie domagali się od Niego znaku z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '16', '16', 'On zaś poznawszy ich myśli powiedział im: - Każde królestwo, które niezgoda rozdarła, ulega spustoszeniu i dom na dom się wali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '17', '17', 'I jeśli szatana rozdarła niezgoda, to jakżeż się ostoi jego królestwo? A wy mówicie, że Ja mocą Beelzebula wyrzucam czarty!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '18', '18', 'A jeśli Ja mocą Beelzebula wyrzucam czarty, to czyją mocą wyrzucają je wasi synowie? Dlatego oni będą waszymi sędziami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '19', '19', 'Ale jeżeli Ja Bożą mocą wyrzucam czarty, to już przyszło do was królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '20', '20', 'Gdy uzbrojony mocarz strzeże swego zamku, bezpieczne jest jego mienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '21', '21', 'Lecz kiedy przyjdzie mocniejszy od niego i pokona go, zabierze całą jego broń, której zawierzył, a łupy jego rozda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '22', '22', 'Kto nie jest ze Mną, jest przeciw Mnie, i kto nie zbiera ze Mną - rozprasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '23', '23', 'Kiedy duch nieczysty wyjdzie z człowieka, błąka się po miejscach bezwodnych, szukając odpoczynku. A gdy go nie znajduje, powiada: Wrócę do mego domu, który opuściłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '24', '24', 'A przyszedłszy zastaje go zamiecionym i upiększonym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '25', '25', 'Wtedy idzie i bierze siedmiu innych duchów gorszych od siebie i wchodzą tam, i zamieszkują. I koniec tego człowieka staje się gorszy od początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '26', '26', 'Kiedy On to mówił, jakaś kobieta z tłumu powiedziała głośno do Niego: - Szczęśliwe łono, które Cię nosiło, i piersi, które ssałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '27', '27', 'On zaś rzekł: -To prawda, ale tym bardziej szczęśliwsi są ci, którzy  słuchają słowa Bożego i strzegą go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '28', '28', 'Kiedy zebrały się tłumy, zaczął mówić: - To pokolenie jest złym pokoleniem. Domaga się znaku, lecz otrzyma tylko znak Jonasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '29', '29', 'Jak Jonasz stał się znakiem dla mieszkańców Niniwy, tak Syn Człowieczy będzie znakiem dla tego pokolenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '30', '30', 'Królowa z Południa stanie na sądzie razem z ludźmi tego pokolenia i potępi ich, bo przyszła z krańców ziemi, aby słuchać mądrości Salomona, a oto tutaj więcej niż Salomon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '31', '31', 'Mieszkańcy Niniwy staną na sądzie razem z tym pokoleniem i potępią je, bo nawrócili się dzięki przepowiadaniu Jonasza, a oto tutaj więcej niż Jonasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '32', '32', 'Nikt, zapaliwszy lampę, nie stawia jej w ukryciu ani pod korcem, ale na świeczniku, aby ci, co wchodzą, widzieli światło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '33', '33', 'Światłem dla ciała jest twoje oko. Jeśli twoje oko jest zdrowe, to i całe twoje ciało ma światło. Ale jeżeli jest chore, to i ciało twoje ogarnie ciemność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '34', '34', 'Uważaj więc, aby światło, które jest w tobie, nie stało się ciemnością!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '35', '35', 'Jeżeli więc całe twoje ciało ma dość światła i żadna jego część nie znajduje się w ciemności, to całe będzie w świetle, jak gdyby lampa oświeciła cię swym blaskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '36', '36', 'Podczas tej mowy pewien faryzeusz poprosił Go do siebie na posiłek. Zasiadł więc do stołu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '37', '37', 'Zobaczywszy to faryzeusz zdziwił się, że (Jezus) nie umył rąk przed jedzeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '38', '38', 'A Pan rzekł do niego: - Wy, faryzeusze, kielichy i misy teraz oczyszczacie z zewnątrz, ale wasze wnętrze pełne jest chciwości i zła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '39', '39', 'Głupi! Czyż Ten, co uczynił to, co jest z zewnątrz, nie uczynił także wnętrza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '40', '40', 'Dawajcie jako jałmużnę raczej to, co jest wewnątrz, a wtedy wszystko będzie dla was czyste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '41', '41', 'Lecz biada wam, faryzeusze, bo dajecie dziesięcinę z mięty i z ruty, i z wszystkich jarzyn, a nie troszczycie się o sprawiedliwość i miłość Boga. A przecież trzeba to czynić i tamtego nie zaniedbywać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '42', '42', 'Biada wam, faryzeusze, bo lubicie pierwsze miejsce w synagogach i pozdrowienia na rynkach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '43', '43', 'Biada wam, bo jesteście jak te groby niewidoczne, a ludzie chodzą po nich nie wiedząc, że to groby!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '44', '44', 'A jeden z biegłych w Prawie powiedział na to: - Nauczycielu, mówiąc takie rzeczy nas także obrażasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '45', '45', 'A On powiedział: - I wam biada, biegli w Prawie, bo nakładacie na ludzi ciężary nie do uniesienia, a sami ani jednym palcem ich nie dotykacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '46', '46', 'Biada wam, bo wznosicie grobowce prorokom, a to wasi ojcowie ich pozabijali!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '47', '47', 'Potwierdzacie i pochwalacie czyny waszych przodków: tamci ich pozabijali, wy zaś wznosicie im grobowce!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '48', '48', 'Dlatego mądrość Boża powiedziała: Poślę do nich proroków i apostołów. I jednych z nich zabiją, a innych będą prześladowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '49', '49', 'Tak to pokolenie poniesie karę za krew wszystkich proroków wylaną od powstania świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '50', '50', 'Od krwi Abla aż do krwi Zachariasza, który został zabity pomiędzy ołtarzem a Przybytkiem. Tak, mówię wam, to pokolenie poniesie karę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '51', '51', 'Biada wam, biegli w Prawie, bo wzięliście klucz do poznania (Prawa), aleście sami nie weszli, a tym, którzy usiłowali wejść, przeszkodziliście!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '52', '52', 'Kiedy wyszedł stamtąd, nauczyciele Pisma i faryzeusze zaczęli na Niego gwałtownie nastawać i zarzucać Go podstępnymi pytaniami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '10', '53', '53', 'aby Go pochwycić na jakimś słowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '0', '0', 'Pewnego dnia, kiedy zebrały się tysięczne tłumy, tak że jedni cisnęli się na drugich, (Jezus) zaczął mówić najpierw do swoich uczniów: - Strzeżcie się kwasu, jakim jest obłuda faryzeuszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '1', '1', 'Bo nie ma nic skrytego, co by nie wyszło na jaw, ani tajemnego, o czym by się nie dowiedziano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '2', '2', 'Dlaczego coście powiedzieli w ciemności, będzie słyszane w świetle, a coście po izbach szeptali do ucha, będzie ogłaszane z dachów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '3', '3', 'Mówię wam, przyjaciele moi, nie bójcie się tych, co zabijają ciało, i nic więcej uczynić nie mogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '4', '4', 'Ja wam pokażę, kogo macie się bać. Bójcie się tego, który, gdy zabije, może wrzucić do piekła. Tak powtarzam wam, tego się bójcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '5', '5', 'Czyż nie sprzedają pięciu wróbli za dwa asy, a przecież Bóg nie zapomina o żadnym z nich!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '6', '6', 'A nawet wszystkie włosy na waszej głowie są policzone. Nie bójcie się! Wy warci jesteście więcej niż wiele wróbli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '7', '7', 'Mówię wam: Kto przyzna się do Mnie przed ludźmi, do tego Syn Człowieczy przyzna się przed aniołami Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '8', '8', 'A kto się Mnie zaprze przed ludźmi, tego i Ja się zaprę przed aniołami Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '9', '9', 'Każdy kto występuje przeciwko Synowi Człowieczemu, uzyska przebaczenie, ale kto by zbluźnił przeciwko Duchowi Świętemu, nie uzyska przebaczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '10', '10', 'A kiedy was zaprowadzą do synagog i do urzędów, i przed władze - nie martwcie się o to, jak macie się bronić albo co macie mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '11', '11', 'Bo w tej właśnie godzinie Duch Święty pouczy was, co trzeba powiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '12', '12', 'Odezwał się do Niego ktoś z tłumu: - Nauczycielu, powiedz mojemu bratu, żeby podzielił się ze mną spadkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '13', '13', 'On zaś mu powiedział: - Człowieku, któż Mnie ustanowił waszym sędzią albo rozjemcą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '14', '14', 'A do nich rzekł: - Strzeżcie się bacznie wszelkiej chciwości, bo życie nie zależy od tego, czy ktoś opływa w dostatki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '15', '15', 'I opowiedział im przypowieść: - Pewnemu bogatemu człowiekowi pole dobrze obrodziło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '16', '16', 'I zastanawiał się: Co zrobić? Bo nie mam gdzie pomieścić swoich plonów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '17', '17', 'I powiedział sobie: Zrobię tak: zburzę moje spichrze i postawię większe, i tam pomieszczę wszystko zboże i cały mój dobytek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '18', '18', 'I powiem sobie: Masz wielki majątek odłożony na długie lata. Odpoczywaj, jedz, pij i baw się wesoło!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '19', '19', 'Ale Bóg mu powiedział: Głupcze, jeszcze tej nocy pozbawią cię życia i komuż przypadnie to, coś sobie przygotował?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '20', '20', 'Tak to bywa z tym, kto gromadzi skarby dla siebie, a nie jest bogaty u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '21', '21', 'A do uczniów rzekł: - Dlatego wam mówię: Nie troszczcie się zbytnio o swoje życie, co będziecie jedli, ani o ciało, czym je okryjecie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '22', '22', 'bo życie ważniejsze jest od jedzenia, a ciało od ubrania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '23', '23', 'Przypatrzcie się krukom! Nie sieją i nie zbierają, nie mają spiżarni ani spichlerzy, a Bóg je żywi. O ileż więcej wy jesteście warci od ptaków!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '24', '24', 'A któż z was przy całej swej trosce może przedłużyć chociaż o chwilkę swoje życie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '25', '25', 'Jeżeli więc nawet tak drobnej rzeczy uczynić nie możecie, to po co się troszczycie o resztę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '26', '26', 'Przypatrzcie się kwiatom, jak rosną! Nie pracują i nie przędą, a mówię wam, że nawet Salomon w całym swym przepychu nie był ubrany jak każdy z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '27', '27', 'Jeżeli więc Bóg tak przystraja trawę polną, która dziś jest, a jutro w piec będzie wrzucona, to o ileż bardziej was, słabej wiary!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '28', '28', 'Nie zabiegajcie zatem o to, co będziecie jedli i co będziecie pili, i nie przejmujcie się tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '29', '29', 'O to wszystko bowiem zabiegają poganie na świecie. Ojciec wasz wie, że tego potrzebujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '30', '30', 'Zabiegajcie raczej o Jego królestwo, a te rzeczy będą wam przydane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '31', '31', 'Nie bój się, mało gromadko, bo spodobało się Ojcu waszemu dać wam królestwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '32', '32', 'Sprzedajcie, co posiadacie, i dajcie jałmużnę. Sprawcie sobie trzosy, które się nie zużywają - skarb, który nie traci na wartości w niebie, gdzie złodziej się nie dostaje ani mól nie niszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '33', '33', 'Bo gdzie jest wasz skarb, tam będzie i wasze serce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '34', '34', 'Niech biodra wasze będą przepasane, a pochodnie zapalone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '35', '35', 'Wy zaś bądźcie podobni do ludzi, którzy czekają na powrót pana z wesela, aby otworzyć mu natychmiast, gdy zapuka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '36', '36', 'Szczęśliwi ci słudzy, których pan zastanie czuwających! Zapewniam was, przepasze się i postawi ich przy stole, i będzie im usługiwał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '37', '37', 'A jeśliby nadszedł o drugiej i o trzeciej straży i tak ich zastał, jakże będą szczęśliwi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '38', '38', 'Zrozumcież to, że gdyby pan domu wiedział, o której godzinie przyjdzie złodziej, to nie pozwoliłby mu włamać się do swego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '39', '39', 'A więc i wy bądźcie gotowi, bo Syn Człowieczy przyjdzie o godzinie, której się nie domyślacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '40', '40', '- Panie - zapytał Piotr - czy ta przypowieść jest dla nas, czy też dla wszystkich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '41', '41', 'A Pan powiedział: - Czyż jest taki wierny i roztropny zarządca, którego pan postawi nad swoją służbą, aby jej wydawał żywność w odpowiednim czasie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '42', '42', 'Szczęśliwy ów sługa, którego pan zastanie przy tym zajęciu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '43', '43', 'Naprawdę powiadam wam, powierzy mu całą swoją majętność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '44', '44', 'Lecz jeśliby ów sługa pomyślał sobie: Pan mój zwleka z przybyciem - i zacząłby bić służących i służące, jeść, pić i upijać się -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '45', '45', 'pan tego sługi przyjdzie w dniu, w którym on go nie oczekuje, i o godzinie, której on nie zna. I usunie go, i zrówna z niewiernymi sługami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '46', '46', 'A ten sługa, który znając wolę swego pana, nie przygotował się (na jego przyjście) i nie spełnił jego woli, otrzyma wiele plag.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '47', '47', 'Ten zaś, który uczynił coś karygodnego, nie znając woli pana, otrzyma ich mniej. Komu wiele dano, od tego wiele się będzie wymagać; komu wiele powierzono, od tego tym więcej będą żądać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '48', '48', 'Przyszedłem rzucić ogień na ziemię i pragnę tylko tego, aby już zapłonął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '49', '49', 'Mam przyjąć chrzest i jakiej doznaję udręki, dopóki to nie nastąpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '50', '50', 'Sądzicie, że przyszedłem, aby przynieść pokój na ziemi? Ależ nie - mówię wam - raczej rozdwojenie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '51', '51', 'Bo odtąd w jednym domu będzie pięciu podzielonych: trzech przeciwko dwom i dwóch przeciwko trzem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '52', '52', 'Podzielą się: ojciec przeciw synowi i \'syn przeciw ojcu\', matka przeciw córce i \'córka przeciw matce\', teściowa przeciw synowej i \'synowa przeciw teściowej\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '53', '53', 'A do tłumów mówił: - Kiedy ujrzycie chmurę pojawiającą się na zachodzie, zaraz mówicie: Deszcz nadchodzi. I tak bywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '54', '54', 'A kiedy wiatr wieje z południa mówicie: Będzie upał. I tak bywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '55', '55', 'Obłudnicy! Potraficie rozpoznać wygląd ziemi i nieba, czemuż więc nie umiecie rozpoznać tego czasu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '56', '56', 'Czemuż sami z siebie nie osądzacie, co jest słuszne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '57', '57', 'Kiedy idziesz z twoim przeciwnikiem do sądu, staraj się w drodze dojść z nim do zgody, aby cię nie zawlókł do sędziego, a sędzia nie oddał cię dozorcy, a dozorca osadziłby cię w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '11', '58', '58', 'Powiadam ci: nie wyjdziesz stamtąd, zanim nie oddasz długu co do grosza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '0', '0', 'W tym właśnie czasie nadeszli jacyś ludzie z wieścią o Galilejczykach, których Piłat zabił przy składaniu ofiar.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '1', '1', 'A Jezus na to: - Czy sądzicie, że ci Galilejczycy więcej zgrzeszyli od wszystkich innych Galilejczyków, bo tyle wycierpieli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '2', '2', 'Bynajmniej - powiadam wam - ale jeżeli się nie nawrócicie, wszyscy podobnie zginiecie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '3', '3', 'Albo tych osiemnastu, których zabiła wieża w Siloam, zwaliwszy się na nich - czy sądzicie, że oni bardziej zawinili aniżeli wszyscy mieszkańcy Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '4', '4', 'Bynajmniej - powiadam wam - ale jeśli się nie nawrócicie, wszyscy tak samo zginiecie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '5', '5', 'I opowiedział im taką przypowieść: - Ktoś posadził sobie drzewo figowe w winnicy. I szukał na nim owocu, ale nie znalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '6', '6', 'Powiedział więc do winogrodnika: Już od trzech lat szukam owocu na tym drzewie figowym, ale nie znajduję. Wytnij je, po cóż jeszcze zabiera miejsce!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '7', '7', 'A ten odpowiada: - Zostaw je, panie, jeszcze na ten rok, niech je okopię i obłożę nawozem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '8', '8', 'może na przyszły rok zaowocuje - a jeśli nie, to je wytniesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '9', '9', 'W szabat nauczał w jednej synagodze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '10', '10', 'A (była tam) kobieta, która już osiemnaście lat chorowała: chodziła pochylona i nie mogła się wyprostować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '11', '11', 'A Jezus, ujrzawszy ją, przywołał do siebie i powiedział: - Niewiasto, jesteś wolna od choroby!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '12', '12', 'I położył na nią ręce. I wyprostowała się natychmiast, i wielbiła Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '13', '13', 'Ale przełożony synagogi oburzony, że Jezus uzdrowił ją w szabat, mówił do tłumu: - Jest sześć dni przeznaczonych na pracę, wtedy więc przychodźcie po uzdrowienie, a nie w dzień szabatu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '14', '14', 'A Pan na to: - Obłudnicy! Czyż każdy z was w szabat nie odwiązuje od żłobu swego wołu lub osła i nie wyprowadza, aby go napoić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '15', '15', 'A tej córki Abrahama, którą szatan już osiemnaście lat trzymał w swoich pętach, nie wolno w dzień szabatu uwolnić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '16', '16', 'Kiedy to mówił, wstyd ogarnął wszystkich Jego przeciwników, cały zaś tłum radował się z Jego wspaniałych czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '17', '17', 'Mówił więc: - Do czego jest podobne królestwo Boże i z czym je mam  porównać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '18', '18', 'Podobne jest do ziarnka gorczycy, które człowiek zasiał w swoim ogrodzie. I wyrosło, i stało się drzewem, i ptaki latające w powietrzu uwiły sobie gniazda w jego gałęziach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '19', '19', 'I powiedział znowu: - Z czym mam porównać królestwo Boże?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '20', '20', 'Podobne jest do kwasu, który kobieta zaczyniła w trzech miarach mąki, aż wszystko się zakwasiło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '21', '21', 'Zdążając do Jerozolimy, przechodził przez miasta i wioski i nauczał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '22', '22', 'Zapytał Go ktoś: - Panie, czy tylko nieliczni będą zbawieni? On zaś powiedział do nich:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '23', '23', '- Starajcie się usilnie wejść przez ciasną bramę, bo - mówię wam - wielu będzie chciało wejść, ale nie zdoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '24', '24', 'Bo kiedy pan domu wstanie i zamknie drzwi, wy zostaniecie na zewnątrz i zaczniecie kołatać do drzwi, wołając: Panie, otwórz nam! - Lecz on wam odpowie: Nie wiem, skąd jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '25', '25', 'Wtedy zaczniecie mówić: Jadaliśmy i piliśmy z tobą, i nauczałeś na naszych ulicach. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '26', '26', 'Odpowie wam: Nie wiem, skąd jesteście, \'odejdźcie ode mnie wszyscy czyniący zło\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '27', '27', 'Tam będzie płacz i zgrzytanie zębów, kiedy zobaczycie Abrahama, Izaaka i Jakuba, i wszystkich proroków w królestwie Bożym, a sami znajdziecie się poza nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '28', '28', 'I przyjdą ze wschodu i z zachodu, z północy i z południa i zasiądą do stołu w królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '29', '29', 'Tak oto ostatni będą pierwszymi, a pierwsi będą ostatnimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '30', '30', 'W tej chwili podeszli jacyś faryzeusze, mówiąc Mu: - Uchodź stąd szybko, bo Herod chce Cię zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '31', '31', 'Odpowiedział im: - Idźcie i powiedzcie temu lisowi: Oto wyrzucam czarty i uzdrawiam dzisiaj i jutro, a na trzeci dzień, skończę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '32', '32', 'Ale trzeba, abym dzisiaj i jutro, i pojutrze był w drodze, bo prorok nie może umrzeć poza Jeruzalem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '33', '33', 'Jeruzalem, Jeruzalem, które zabijasz proroków i kamienujesz tych, którzy zostali wysłani do ciebie! Ileż to razy chciałem zebrać twoje dzieci, jak kokosz zbiera pisklęta pod swoje skrzydła, a nie chcieliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '12', '34', '34', 'Oto \'dom wasz opustoszeje\'! I mówię wam: Zobaczycie mnie dopiero wtedy, kiedy będziecie mówili: \'Chwała temu, który przychodzi w Imię Pana\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '0', '0', 'Kiedy w szabat wszedł do domu jednego ze znakomitszych faryzeuszów, aby się posilić, podpatrywali Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '1', '1', 'I stanął przed Nim pewien człowiek, chory na wodną puchlinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '2', '2', 'A Jezus zapytał biegłych w Prawie i faryzeuszów: - Wolno w szabat uzdrawiać czy nie wolno?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '3', '3', 'A oni zamilkli. On zaś dotknął go, uzdrowił i odprawił,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '4', '4', 'a do nich rzekł: - Jeśli syn albo wół któregoś z was wpadnie do studni, to czyż nie wyciągniecie go zaraz w dzień szabatu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '5', '5', 'I nie umieli Mu na to odpowiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '6', '6', 'A widząc, jak zaproszeni wybierają sobie pierwsze miejsca, opowiedział im przypowieść: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '7', '7', 'Kiedy ktoś zaprosi cię na wesele, nie siadaj na pierwszym miejscu, bo może został zaproszony ktoś godniejszy od ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '8', '8', 'I ten, kto ciebie i jego zaprosił, podejdzie i powie ci: Ustąp temu miejsca. - I wtedy ze wstydem będziesz musiał zająć miejsce ostatnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '9', '9', 'Ale gdy zostaniesz zaproszony, idź i usiądź na ostatnim miejscu, aby kiedy przyjdzie ten, który cię zaprosił, powiedział ci: Przyjacielu, posuń się wyżej! - Wtedy będziesz wyróżniony na oczach wszystkich współbiesiadników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '10', '10', 'Bo każdy, kto się wywyższa będzie poniżony, a kto się uniża, będzie wywyższony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '11', '11', 'Mówił też do tego, który go zaprosił: - Kiedy urządzasz obiad albo wieczerzę, to nie zapraszaj swoich przyjaciół ani braci, ani krewnych, ani bogatych sąsiadów, aby i oni nie odwzajemnili się zaproszeniem ciebie. I otrzymasz zapłatę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '12', '12', 'Ale kiedy urządzasz przyjęcie, zaproś ubogich, ułomnych, chromych, ślepych -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '13', '13', 'i będziesz szczęśliwy, bo oni nie mają nic, aby ci się odwzajemnić. A zapłatę otrzymasz przy zmartwychwstaniu sprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '14', '14', 'Kiedy usłyszał to jeden ze współbiesiadników, powiedział Mu: - Szczęśliwy ten, kto będzie jadł chleb w królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '15', '15', 'A On mu powiedział: - Pewien człowiek urządzał wielkie przyjęcie i zaprosił wiele gości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '16', '16', 'Kiedy nadeszła godzina przyjęcia, wysłał sługę, aby powiedział zaproszonym: Przychodźcie, bo przyjęcie jest już gotowe!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '17', '17', 'Ale wszyscy zaczęli się jednakowo wymawiać. Pierwszy mu powiedział: Kupiłem pole i muszę iść je obejrzeć. Proszę cię, miej mnie za usprawiedliwionego. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '18', '18', 'A drugi powiedział: Kupiłem pięć par wołów i idę je wypróbować. Proszę cię, miej mnie za usprawiedliwionego. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '19', '19', 'A jeszcze inny powiedział: Wziąłem sobie żonę i dlatego nie mogę przyjść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '20', '20', 'Powróciwszy sługa oznajmił to panu. Wtedy pan domu rozgniewał się i powiedział słudze: Idź szybko na ulice i zaułki miasta i sprowadź tutaj ubogich i ułomnych, i ślepych, i chromych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '21', '21', 'A sługa powiedział: Panie, zrobiłem, co kazałeś, ale jeszcze jest miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '22', '22', 'I rzekł pan do sługi: Wyjdź na drogi i w opłotki i zmuszaj do wejścia, aby mój dom się zapełnił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '23', '23', 'Bo mówię wam: Nikt z tamtych zaproszonych nie zakosztuje mojego przyjęcia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '24', '24', 'Szły za Nim wielkie tłumy. I obróciwszy się rzekł do nich: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '25', '25', 'Jeśli ktoś przychodzi do Mnie, a swego ojca i matkę, i żonę, i dzieci, i braci, i siostry, a nawet siebie samego kocha bardziej niż Mnie, nie może być moim uczniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '26', '26', 'Kto nie dźwiga swego krzyża i nie idzie ze Mną, nie może być moim uczniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '27', '27', 'Jeżeli ktoś z was chce budować wieżę, to czyż najpierw nie oblicza kosztów, czy ma na wykończenie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '28', '28', 'Aby przypadkiem ci wszyscy, którzy widzieli, że położył fundament, ale nie zdołał wykończyć budowy, nie zaczęli się z niego wyśmiewać:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '29', '29', 'Ten człowiek zaczął budować, ale nie zdołał dokończyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '30', '30', 'Albo król, który wyrusza na wojnę przeciwko drugiemu królowi, czy nie zasięga wpierw rady, czy może w dziesięć tysięcy zmierzyć się z tym, który idzie przeciwko niemu z dwudziestoma tysiącami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '31', '31', 'A jeżeli nie, to wysyła poselstwo z prośbą o pokój, kiedy tamten jest jeszcze daleko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '32', '32', 'Tak więc każdy z was, kto nie wyrzeka się wszystkiego, co posiada, nie może być moim uczniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '33', '33', 'Tak więc dobra jest sól, lecz czymże ją przyprawić, kiedy i ona straci swój smak?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '13', '34', '34', 'Nie nadaje się ani do (użyźnienia) ziemi, ani na nawóz. Wyrzucają ją! Kto ma uszy do słuchania, niechaj słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '0', '0', 'Przychodziło do niego wielu celników i grzeszników, aby Go słuchać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '1', '1', 'A faryzeusze i nauczyciele Pisma szemrali: - To Ten, co przyjmuje grzeszników i jada razem z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '2', '2', 'On zaś opowiedział im taką przypowieść:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '3', '3', 'Któż z was, zgubiwszy jedną ze stu owiec, które posiada, nie zostawia dziewięćdziesięciu dziewięciu na pustyni i nie chodzi za zgubioną, aż jej nie znajdzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '4', '4', 'A znalazłszy ją, kładzie z radością na ramiona,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '5', '5', 'wraca do domu, sprasza przyjaciół i sąsiadów i mówi im: Cieszcie się razem ze mną, bo znalazłem owcę, która mi zginęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '6', '6', 'Powiadam wam, że w niebie więcej będzie radości z jednego grzesznika, który się nawraca, aniżeli z dziewięćdziesięciu dziewięciu sprawiedliwych, którzy nie potrzebują nawrócenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '7', '7', 'Albo: Jeśli jakaś kobieta, mająca dziesięć drachm, zgubi jedną z nich, to czyż nie zapala światła, nie zamiata domu i nie szuka starannie, aż jej nie znajdzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '8', '8', 'A kiedy ją znajdzie, sprasza przyjaciółki i sąsiadki i mówi: Cieszcie się razem ze mną, bo znalazłam drachmę, która mi zginęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '9', '9', 'Tak samo - powiadam wam - aniołowie Boży radują się z jednego grzesznika, który się nawraca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '10', '10', 'Powiedział też: - Pewien człowiek miał dwóch synów');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '11', '11', 'i młodszy z nich rzekł do ojca: Ojcze, daj mi część majątku, która na mnie przypada. Ojciec podzielił między nich majątek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '12', '12', 'Niedługo potem młodszy syn, zabrawszy wszystko, odjechał do dalekiego kraju i żyjąc rozrzutnie roztrwonił majątek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '13', '13', 'A kiedy wszystko wydał, w owym kraju nastał ciężki głód. I zaczął cierpieć niedostatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '14', '14', 'Poszedł więc na służbę do jednego z obywateli tego kraju, a ten go posłał do swoich posiadłości, aby pasł świnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '15', '15', 'Tam pragnął najeść się chociaż strąkami, które żarły świnie; ale nikt mu ich nie dawał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '16', '16', 'Zastanowiwszy się nad sobą, powiedział: Iluż to najemników mego ojca ma chleba pod dostatkiem, a ja tu ginę z głodu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '17', '17', 'Pójdę zaraz do mego ojca i powiem mu: Ojcze, zgrzeszyłem przeciwko niebu i przeciw tobie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '18', '18', 'i nie jestem już godzien nazywać się twoim synem; zrób mnie choćby jednym z twoich najemników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '19', '19', 'I zaraz poszedł do swego ojca. Kiedy był jeszcze daleko, ojciec go zobaczył i wzruszył się bardzo. Pobiegł do niego i rzucił się mu na szyję, i ucałował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '20', '20', 'Syn mu powiedział: Ojcze, zgrzeszyłem przeciwko niebu i przeciw tobie. Nie jestem godzien nazywać się twoim synem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '21', '21', 'Lecz ojciec powiedział do swoich sług: Przynieście szybko najlepszą szatę i włóżcie na niego, dajcie mu też pierścień na palec i sandały na nogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '22', '22', 'Przyprowadźcie i zabijcie utuczone cielę, a będziemy z radością ucztować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '23', '23', 'Bo ten syn mój umarł, a ożył, zginął, ale się odnalazł. I zaczęli się weselić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '24', '24', 'A starszy brat jego był na polu. Kiedy wracając zbliżał się do domu, usłyszał muzykę i tańce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '25', '25', 'Przywoławszy więc jednego ze służących, zapytał: Co to takiego? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '26', '26', 'Ten mu odpowiedział: Twój brat wrócił, a ojciec zabił utuczone cielę, dlatego że go zdrowym odzyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '27', '27', 'On rozgniewał się i nie chciał wejść do domu; ale ojciec wyszedł i prosił, aby wszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '28', '28', 'On zaś rzekł do ojca: Tyle lat ci służę i nigdy nie przekroczyłem twojego rozkazu, a nie dałeś mi nigdy koźlęcia, abym się radował z przyjaciółmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '29', '29', 'Ale kiedy powrócił twój syn, który przehulał swój majątek z ladacznicami, zabiłeś dla niego utuczone cielę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '30', '30', 'Ale ojciec mu odpowiedział: Dziecko, ty zawsze jesteś ze mną i wszystko, co mam, jest twoje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '14', '31', '31', 'A trzeba się cieszyć i radować, bo ten twój brat umarł, a ożył, zginął, ale się odnalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '0', '0', 'Mówił też do uczniów: - Pewien bogaty człowiek miał zarządcę, którego oskarżono przed nim, że trwoni jego mienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '1', '1', 'Wezwawszy go zatem powiedział: Cóż to słyszę o tobie? Zdaj sprawę z twojej działalności, bo już nie będziesz dłużej zarządzał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '2', '2', 'A zarządca pomyślał sobie: Co pocznę, skoro mój pan pozbawia mnie zarządzania? Kopać nie umiem, żebrać się wstydzę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '3', '3', 'Już wiem, co zrobię, aby mnie przyjęli do swoich domów, kiedy nie będę już zarządcą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '4', '4', 'Wezwał do siebie dłużników swego pana, każdego z osobna. Pierwszemu powiedział: Ileś winien mojemu panu? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '5', '5', 'Ten odrzekł: Sto beczek oliwy. - Powiedział mu: Bierz swój zapis, siadaj i pisz szybko: pięćdziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '6', '6', 'Potem powiedział drugiemu: A ty ileś winien? - Ten odrzekł: Sto korców pszenicy. - Mówi mu: Bierz swój zapis i pisz: osiemdziesiąt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '7', '7', 'A pan pochwalił nieuczciwego zarządcę: Roztropnie to zrobił. Bo synowie tego świata są roztropniejsi w stosunku do ludzi swego pokolenia od synów światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '8', '8', 'A Ja wam powiadam: Pozyskujcie sobie przyjaciół za pomocą niegodziwej mamony, aby was przyjęli do wiecznych przybytków, gdy jej już nie stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '9', '9', 'Wierny w najmniejszym jest także wierny w wielkim, a nieuczciwy w małym jest także nieuczciwy w wielkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '10', '10', 'Jeżeli więc nie byliście wierni w zarządzaniu niegodziwą mamoną, to któż wam powierzy prawdziwe dobro?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '11', '11', 'A jeśli nie jesteście wierni w cudzym, to któż wam powierzy to, co wasze?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '12', '12', 'Żaden sługa nie może służyć dwom panom, bo albo jednego będzie nienawidził, a drugiego kochał, albo z jednym będzie trzymał, a drugiego zlekceważy. Nie możecie służyć Bogu i mamonie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '13', '13', 'Chciwi grosza faryzeusze przysłuchiwali się temu wszystkiemu i wyśmiewali się z Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '14', '14', 'I rzekł im: - To właśnie wy udajecie sprawiedliwych wobec ludzi, ale Bóg zna wasze serca. Bo Bóg brzydzi się tym, co jest wyniosłe w człowieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '15', '15', 'Prawo i Prorocy (sięgają) aż do Jana. A odtąd głosi się dobrą nowinę o królestwie Bożym i każdy usilnie się stara dostać do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '16', '16', 'Lecz prędzej niebo i ziemia przeminą, niż jedna kreska zniknie w Prawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '17', '17', 'Kto rozwodzi się z żoną i żeni się z inną, cudzołoży, i kto żeni się z rozwiedzioną, cudzołoży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '18', '18', 'Był pewien człowiek bogaty, ubierał się w purpurę i bisior i codziennie wystawnie ucztował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '19', '19', 'A pod jego drzwiami leżał ubogi, okryty wrzodami, imieniem Łazarz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '20', '20', 'Chciał najeść się tym, co spadało ze stołu bogacza, ale tylko psy przychodziły lizać jego wrzody. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '21', '21', 'I umarł ubogi, i aniołowie zanieśli go na łono Abrahama. Umarł także bogacz i złożono go w grobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '22', '22', 'I znosząc męki w otchłani, zobaczył w oddali Abrahama i Łazarza na jego łonie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '23', '23', 'I zawołał: Ojcze Abrahamie, zlituj się nade mną! Poślij Łazarza, aby umoczył koniec palca w wodzie i zwilżył mi język, bo bardzo cierpię w tym płomieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '24', '24', 'Lecz Abraham powiedział: Dziecko, przypomnij sobie, żeś otrzymał dostatki już za życia, a Łazarz cierpiał wtedy niedostatek. A teraz on tutaj doznaje pociechy, a ty cierpisz męki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '25', '25', 'Nadto pomiędzy nami a wami istnieje wielka przepaść, aby ci, co by chcieli, nie mogli się przedostać stąd do was ani też stamtąd do nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '26', '26', 'Powiedział: Wobec tego proszę cię ojcze, abyś go posłał do mojego ojca,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '27', '27', 'bo mam pięciu braci. Niech ich ostrzeże, aby i oni nie trafili na to miejsce męki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '28', '28', 'Abraham zaś na to: Mają Mojżesza i Proroków, niech ich słuchają!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '29', '29', 'On rzekł: O nie, ojcze Abrahamie, jeśliby jednak ktoś z umarłych do nich przyszedł, to się nawrócą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '15', '30', '30', 'Powiedział mu: Jeżeli Mojżesza i Proroków nie słuchają, to chociażby ktoś z martwych powstał, nie dadzą się przekonać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '0', '0', 'Rzekł do swoich uczniów: - Niepodobna, żeby nie przyszły zgorszenia, lecz biada temu, który je szerzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '1', '1', 'Lepiej, aby mu zawieszono kamień młyński na szyi i wrzucono do morza, niż żeby miał doprowadzić do złego jednego z tych maluczkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '2', '2', 'Miejcie się na baczności! Jeśli twój brat zgrzeszy, upomnij go. A jeśli żałuje, przebacz mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '3', '3', 'I gdyby siedem razy na dzień zgrzeszył przeciw tobie i siedem razy zwrócił się do ciebie, mówiąc: Żałuję - przebacz mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '4', '4', 'Apostołowie prosili Pana: - Wzmocnij naszą wiarę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '5', '5', 'Pan zaś rzekł: Gdybyście mieli wiarę jak ziarenko gorczycy i powiedzieli tej morwie: Wyrwij się z korzeniami i przesadź się w morze - będzie wam posłuszna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '6', '6', 'Czy kto z was powie swojemu słudze, który orze lub pasie i zszedł z pola do domu: Siadaj zaraz do stołu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '7', '7', 'Czy nie powie mu raczej: Przygotuj mi posiłek i przepasawszy się usługuj mi aż się najem i napiję, a potem sam zjesz i napijesz się?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '8', '8', 'Czy dziękuje słudze za wykonanie rozkazu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '9', '9', 'Tak i wy, kiedy wykonacie wszystko, co wam polecono, mówcie: Jesteśmy nieużytecznymi sługami, zrobiliśmy co trzeba było zrobić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '10', '10', 'W drodze do Jeruzalem przechodził przez Samarię i Galileę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '11', '11', 'Kiedy wchodził do pewnej wsi, zabiegło Mu drogę dziesięciu trędowatych, którzy stanęli z daleka');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '12', '12', 'i zawołali: - Jezusie, Nauczycielu, zmiłuj się nad nami!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '13', '13', 'Powiedział im: - Pokażcie się kapłanom! I w drodze zostali oczyszczeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '14', '14', 'A jeden z nich, widząc, że jest uzdrowiony, powrócił, wielbiąc głośno Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '15', '15', 'i upadł do Jego stóp, dziękując Mu. Był to Samarytanin.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '16', '16', 'Jezus odpowiedział: - Czyż nie dziesięciu zostało uzdrowionych? Gdzież jest dziewięciu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '17', '17', 'Żaden nie wrócił, aby uwielbić Boga, tylko ten cudzoziemiec?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '18', '18', 'A jemu powiedział: - Wstań, idź, wiara twoja cię uzdrowiła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '19', '19', 'Zapytany przez faryzeuszów, kiedy przyjdzie królestwo Boże, odpowiedział: - Królestwo Boże przyjdzie niepostrzeżenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '20', '20', 'Nie będą mówili: Jest tu, albo: Jest tam, bo królestwo Boże jest w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '21', '21', 'A do uczniów rzekł: - Przyjdzie czas, kiedy zapragniecie zobaczyć chociaż jeden dzień Syna Człowieczego, a nie ujrzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '22', '22', 'Powiedzą wam: On jest tam! albo: On jest tu! Nie odchodźcie i nie biegajcie tam,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '23', '23', 'bo jak błyskawica, zapałając się, rozświetla niebo od jednego końca do drugiego, tak będzie z Synem Człowieczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '24', '24', 'Najpierw jednak musi On wiele wycierpieć, a to pokolenie Go odrzuci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '25', '25', 'A jak było za czasów Noego, tak też będzie za czasów Syna Człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '26', '26', 'Jedli, pili, żenili się i za mąż wychodziły aż do dnia, w którym Noe wszedł do arki. I przyszedł potop, i wszystkich wygubił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '27', '27', 'Podobnie jak było za czasów Lota: jedli, pili, kupowali, sprzedawali, sadzili, budowali,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '28', '28', 'ale w dniu, w którym Lot wyszedł z Sodomy, spadł z nieba deszcz ognia i siarki i wszystkich wygubił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '29', '29', 'Tak też będzie w dniu, w którym objawi się Syn Człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '30', '30', 'Kto będzie wtedy na dachu, a swoje rzeczy będzie miał w domu, niech nie schodzi, aby je zabrać, a kto będzie na polu, także niech się nie wraca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '31', '31', 'Przypomnijcie sobie żonę Lota!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '32', '32', 'Kto by starał się zachować swoje życie, ten je straci, a kto je straci, ten je zachowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '33', '33', 'Powiadam wam: tej nocy dwóch będzie na jednym posłaniu i jeden będzie zabrany, a drugi zostanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '34', '34', 'Dwie (kobiety) będą mleć razem: jedna będzie zabrana, a druga zostanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '35', '35', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '16', '36', '36', 'Zapytali Go: - Gdzie, Panie? A On im powiedział: - Gdzie jest padlina, tam zbiorą się i sępy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '0', '0', 'Opowiedział im też przypowieść o tym, że trzeba się zawsze modlić i (nigdy) nie ustawać: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '1', '1', 'W pewnym mieście był sędzia, który nie bał się Boga i nie liczył się z ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '2', '2', 'Była też w tym mieście wdowa i przychodziła do niego prosząc: Obroń mnie przed moim przeciwnikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '3', '3', 'Przez jakiś czas wzbraniał się, ale potem pomyślał sobie: Chociaż Boga się nie boję, z ludźmi się nie liczę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '4', '4', 'to jednak obronię tę wdowę, bo mi się naprzykrza, aby w końcu nie przyszła i nie znieważyła mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '5', '5', 'Pan powiedział: Słuchajcie, co mówi ten niesprawiedliwy sędzia!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '6', '6', 'Czyżby Bóg nie wziął w obronę swoich wybranych, którzy wołają do Niego we dnie i w nocy, i czyżby zwlekał w ich sprawie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '7', '7', 'Powiadam wam, zaraz pośpieszy im z pomocą. Ale czy Syn Człowieczy, kiedy przyjdzie, znajdzie wiarę na ziemi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '8', '8', 'Tym, którzy siebie uważali za sprawiedliwych, a innymi pogardzali, powiedział taką przypowieść: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '9', '9', 'Dwóch ludzi weszło do świątyni, aby się pomodlić: jeden był faryzeuszem, a drugi celnikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '10', '10', 'Faryzeusz stojąc tak się modlił: Boże, dziękuję Ci, że nie jestem taki jak inni ludzie: złodzieje, krzywdziciele, cudzołożnicy, albo jak ten oto celnik.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '11', '11', 'Poszczę dwa razy w tygodniu, daję dziesięciny ze wszystkiego, co nabywam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '12', '12', 'A celnik stanął z daleka i nie śmiał nawet oczu podnieść do nieba, ale bił się w piersi mówiąc: Boże, bądź miłościw mnie grzesznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '13', '13', 'Powiadam wam: Ten odszedł do domu bardziej usprawiedliwiony niż tamten. Bo kto się wynosi, będzie poniżony, a kto się uniża, będzie wywyższony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '14', '14', 'Przynosili Mu także małe dzieci, aby ich dotknął. A uczniowie, widząc to, powstrzymywali je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '15', '15', 'Lecz Jezus przywołał je do siebie i rzekł: - Pozwólcie dzieciom przyjść do Mnie i nie przeszkadzajcie im, bo do takich należy królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '16', '16', 'Zaprawdę powiadam wam: Kto nie przyjmie królestwa Bożego jak dziecko, ten nie wejdzie do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '17', '17', 'I zapytał Go pewien dostojnik: - Nauczycielu dobry, co powinienem uczynić, aby osiągnąć życie wieczne?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '18', '18', 'Powiedział mu Jezus: - Dlaczego nazywasz Mnie dobrym? Tylko jeden Bóg jest dobry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '19', '19', 'Czy znasz przykazania: \'Nie będziesz cudzołożył, nie będziesz zabijał, nie będziesz kradł, nie będziesz fałszywie zeznawał, czcij ojca twego i matkę\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '20', '20', 'Odpowiedział: - Przestrzegałem tego wszystkiego od młodości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '21', '21', 'Usłyszawszy to Jezus rzekł: - Jednego ci jeszcze brak: sprzedaj wszystko, co masz, i rozdaj ubogim, a będziesz miał skarb w niebie. Potem przyjdź i chodź za Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '22', '22', 'Na te słowa zasmucił się, bo był bardzo bogaty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '23', '23', 'Zobaczywszy to Jezus powiedział: - Jak trudno bogatym wejść do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '24', '24', 'Łatwiej wielbłądowi przejść przez ucho igielne, aniżeli bogatemu wejść do królestwa Bożego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '25', '25', 'A ci, co to usłyszeli, zapytali: - Któż tedy może być zbawiony?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '26', '26', 'Odpowiedział: - Co niemożliwe dla ludzi, możliwe jest dla Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '27', '27', 'Odezwał się Piotr: - Oto opuściliśmy wszystko, cośmy mieli i poszliśmy za Tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '28', '28', 'Rzekł im: - Zaprawdę powiadam wam: każdy, kto opuścił dom albo żonę, albo brata albo rodziców, albo dzieci dla królestwa Bożego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '29', '29', 'otrzyma w zamian daleko więcej w tym czasie i życie wieczne w przyszłym wieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '30', '30', 'I wziął ze sobą Dwunastu, i rzekł do nich: - Oto idziemy do Jeruzalem i wypełni się wszystko, co prorocy napisali o Synu Człowieczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '31', '31', 'Zostanie wydany poganom i wyśmiany, i znieważony, i opluty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '32', '32', 'I ubiczowawszy Go zabiją. Lecz On trzeciego dnia zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '33', '33', 'Oni jednak nic z tego nie zrozumieli. Słowa te były dla nich tajemnicze i nie pojmowali tego, co mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '34', '34', 'Kiedy zbliżał się do Jerycha, jakiś ślepiec siedział przy drodze żebrząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '35', '35', 'A usłyszawszy, że tłum przechodzi, dopytywał się, co się dzieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '36', '36', 'Powiedzieli mu: - Jezus Nazarejczyk przechodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '37', '37', 'Wtedy zawołał: - Jezusie, Synu Dawida, zmiłuj się nade mną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '38', '38', 'Ci, co go mijali, uciszali go, lecz on tym głośniej wołał: - Synu Dawida, zmiłuj się nade mną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '39', '39', 'Jezus przystanął i kazał go przyprowadzić do siebie. A kiedy się zbliżył, zapytał go: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '40', '40', 'Co mam dla ciebie zrobić? Odpowiedział: - Panie, żebym widział! -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '41', '41', 'Przejrzyj - powiedział mu Jezus - twoja wiara cię uzdrowiła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '17', '42', '42', 'Natychmiast odzyskał wzrok i szedł za Nim, wielbiąc Boga. A cały lud, który to widział, chwalił Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '0', '0', 'Potem wszedł do Jerycha i przechodził przez miasto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '1', '1', 'A człowiek imieniem Zacheusz, przełożony nad celnikami i bogacz,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '2', '2', 'chciał zobaczyć, jak też Jezus wygląda, ale nie mógł, bo będąc małego wzrostu, ginął w tłumie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '3', '3', 'Pobiegł więc naprzód i wdrapał się na drzewo sykomory, aby Go zobaczyć, ponieważ miał tamtędy przechodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '4', '4', 'Gdy Jezus przyszedł na to miejsce, spojrzał w górę i rzekł do niego: - Zacheuszu, zejdź zaraz, bo chcę dzisiaj zatrzymać się w twoim domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '5', '5', 'I zszedł zaraz, i przyjął Go z radością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '6', '6', 'Lecz wszyscy, którzy to widzieli, szemrali: - Do grzesznika poszedł w gościnę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '7', '7', 'A Zacheusz oświadczył Panu: - Panie, połowę majątku daję ubogim, a jeżeli kogoś skrzywdziłem, to oddaję w czwórnasób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '8', '8', 'A Jezus rzekł do niego: - Zbawienie weszło dzisiaj do tego domu, bo i ten człowiek jest synem Abrahama.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '9', '9', 'Albowiem Syn Człowieczy przyszedł szukać i zbawiać to, co zginęło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '10', '10', 'Kiedy tego słuchali, opowiedział jeszcze przypowieść, był już bowiem blisko Jeruzalem, a im się wydawało, że natychmiast objawi się królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '11', '11', 'Powiedział więc: - Pewien człowiek szlachetnego rodu wyruszył do dalekiego kraju, aby uzyskać dla siebie tron królewski i wrócić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '12', '12', 'Przywołał swoich dziesięciu sług, dał im dziesięć min i powiedział do nich: Obracajcie nimi aż do mojego powrotu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '13', '13', 'Jego poddani nienawidzili go i wysłali za nim poselstwo z oświadczeniem: Nie chcemy, żeby był naszym królem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '14', '14', 'On jednak, uzyskawszy tron królewski, powrócił i kazał wezwać do siebie sługi, którym powierzył pieniądze, aby się dowiedzieć, co zarobili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '15', '15', 'Przyszedł więc pierwszy i powiedział: Panie, twoja mina przyniosła dziesięć min.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '16', '16', 'Powiedział mu: Dobrze, sługo dobry, ponieważ byłeś wierny w małym, zarządzaj dziesięcioma miastami. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '17', '17', 'Przyszedł drugi i powiedział: Panie, twoja mina przyniosła pięć min. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '18', '18', 'I do niego rzekł: I ty zarządzaj pięcioma miastami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '19', '19', 'Inny zaś przyszedł i powiedział: Panie, oto twoja mina. Trzymałem ją schowaną w chustce,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '20', '20', 'bo bałem się ciebie, ponieważ jesteś srogim człowiekiem: bierzesz, czegoś nie położył, i zbierasz, czegoś nie posiał. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '21', '21', 'Mówi mu: Zły sługo, osądzę cię według twoich własnych słów. Wiedziałeś, że jestem srogi, że biorę to, czegom nie położył, i zbieram czegom nie posiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '22', '22', 'Dlaczego więc nie dałeś moich pieniędzy do banku, abym po powrocie odebrał je z zyskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '23', '23', 'A do obecnych tam rzekł: Zabierzcie mu minę i dajcie temu, co ma dziesięć. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '24', '24', 'Powiedzieli Mu: Panie, on ma już dziesięć min.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '25', '25', 'Powiadam wam: każdemu, kto ma, będzie dodane, a temu, co nie ma, i to, co ma, zabiorą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '26', '26', 'A tych moich nieprzyjaciół, którzy nie chcą, abym był ich królem, przyprowadźcie tutaj i pozabijajcie na moich oczach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '27', '27', 'Po tych słowach wyruszył w dalszą drogę do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '28', '28', 'Kiedy zbliżył się do Betfage i Betanii, do góry zwanej Oliwną, wysłał dwóch uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '29', '29', 'Powiedział im: - Idźcie do wioski leżącej przed nami. Wchodząc do niej znajdziecie oślę przywiązane, którego nikt jeszcze nie dosiadł. Odwiążcie je i przyprowadźcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '30', '30', 'A gdyby was kto zapytał, dlaczego je odwiązujecie, powiedzcie tak: Pan go potrzebuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '31', '31', 'Wysłańcy poszli i zastali wszystko, jak im powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '32', '32', 'Kiedy odwiązywali oślę, odezwali się jego właściciele: - Dlaczego odwiązujecie oślę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '33', '33', 'Oni powiedzieli: - Pan go potrzebuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '34', '34', 'I przyprowadzili je do Jezusa. I włożywszy swoje okrycia na oślę, posadzili na nie Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '35', '35', 'A kiedy jechał, rozścielali swoje okrycia na drodze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '36', '36', 'a gdy zbliżał się do zbocza Góry Oliwnej, cały tłum rozradowanych uczniów zaczął głośno chwalić Boga za wszystkie (dzieła) nadziemskiej mocy, które widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '37', '37', 'Wołali: \'Błogosławiony\' Król, \'który przychodzi w imię Pana\'. Pokój na niebie i chwała na wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '38', '38', 'Niektórzy faryzeusze odezwali się z tłumu do Niego: - Nauczycielu, skarć Twoich uczniów!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '39', '39', 'On odpowiedział: - Powiadam wam: Jeśli oni umilkną, kamienie krzyczeć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '40', '40', 'A gdy się zbliżył, ujrzał miasto i zapłakał nad nim: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '41', '41', 'O, gdybyś i ty poznało w tym dniu, co służy pokojowi! Ale teraz to jest zakryte przed twoimi oczami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '42', '42', 'Przyjdą na ciebie dni, gdy wrogowie twoi otoczą cię wałem, oblegną i ścisną cię zewsząd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '43', '43', 'I powalą na ziemię ciebie i twoje dzieci mieszkające w twoich murach, i nie pozostawią w tobie kamienia na kamieniu dlatego, żeś nie rozpoznało czasu twego nawiedzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '44', '44', 'I wszedł do świątyni i zaczął wyrzucać sprzedających,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '45', '45', 'mówiąc im: - Napisano: \'Dom mój będzie domem modlitwy\', a wyście zrobili z niego \'jaskinię zbójców\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '46', '46', 'I nauczał codziennie w świątyni. Arcykapłani zaś i nauczyciele Pisma, a także przywódcy ludu zastanawiali się, jak Go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '18', '47', '47', 'Ale nie wiedzieli, jak to zrobić, bo cały lud słuchał Go z zapartym tchem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '0', '0', 'Kiedy pewnego dnia nauczał lud w świątyni i głosił dobrą nowinę, arcykapłani i nauczyciele Pisma razem ze starszymi przyszli');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '1', '1', 'i rzekli do Niego: - Powiedz nam, jaką mocą to czynisz albo kto Ci dał tę moc?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '2', '2', 'On zaś odpowiedział im: - Ja też was o coś zapytam: Powiedzcie mi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '3', '3', 'czy chrzest Jana pochodził z nieba, czy od ludzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '4', '4', 'Oni zastanawiali się wspólnie i mówili między sobą: - Jeżeli powiemy: Z nieba, zapyta: To dlaczego mu nie uwierzyliście?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '5', '5', 'Jeśli zaś powiemy: Od ludzi - to cały lud nas ukamienuje, bo jest przekonany, że Jan był prorokiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '6', '6', 'I odpowiedzieli, że nie wiedzą skąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '7', '7', 'Jezus powiedział im: - To i Ja wam nie powiem, jaką mocą to czynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '8', '8', 'I zaczął opowiadać ludowi tę przypowieść: - Pewien człowiek zasadził winnicę, wydzierżawił ją rolnikom i wyjechał na długi czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '9', '9', 'W odpowiedniej porze posłał do rolników sługę, aby mu oddali część zbiorów z winnicy. Ale rolnicy pobili go i odesłali z niczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '10', '10', 'I jeszcze raz posłał innego sługę, a oni go pobili, znieważyli i odesłali z niczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '11', '11', 'I jeszcze raz posłał trzeciego. A oni także tego poranili i wyrzucili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '12', '12', 'Wtedy pan winnicy powiedział: Co zrobić? Wyślę mego ukochanego syna, przecież jego uszanują!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '13', '13', 'Ale kiedy rolnicy go ujrzeli, zastanawiali się i mówili między sobą: To jest dziedzic, zabijmy go, a jego dziedzictwo nam przypadnie. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '14', '14', 'I wyrzuciwszy go poza winnicę, zabili. Cóż więc uczyni z nimi pan winnicy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '15', '15', 'Przyjdzie i wytraci tych rolników, a winnicę wydzierżawi innym. Kiedy to usłyszeli, zawołali: - Nie daj Boże, by tak się stało!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '16', '16', 'A On, spojrzawszy na nich, rzekł: Dlaczego więc napisano: \'Kamień, który odrzucili budujący, stał się kamieniem węgielnym\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '17', '17', 'Każdy, kto upadnie na ten kamień, rozbije się, a na kogo on spadnie, ten zostanie zmiażdżony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '18', '18', 'Nauczyciele Pisma i arcykapłani zrozumieli, że tę przypowieść powiedział przeciwko nim, i próbowali Go pochwycić, ale bali się ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '19', '19', 'Śledzili Go też pilnie. Wysłali też szpiegów, którzy udając sprawiedliwych mieli Go pochwycić na słowie, aby Go wydać w ręce sprawującego władzę namiestnika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '20', '20', 'I zapytali Go: - Nauczycielu, wiemy, że dobrze mówisz i dobrze uczysz. Nie kierujesz się względami ludzkimi, ale nauczasz drogi Bożej zgodnie z prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '21', '21', 'Czy mamy płacić podatek cesarzowi, czy nie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '22', '22', 'On, przejrzawszy ich podstęp, rzekł do nich: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '23', '23', 'Pokażcie mi denara! Czyj ma wizerunek i napis?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '24', '24', 'Odpowiedzieli: - Cesarski. Rzekł do nich: - Oddajcie więc, co cesarskie cesarzowi, a co Boskie Bogu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '25', '25', 'I nie mogli pochwycić Go na żadnym słowie (powiedzianym) do ludu, i zamilkli zdumieni Jego odpowiedzią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '26', '26', 'Zapytali Go niektórzy spośród saduceuszów, zaprzeczających zmartwychwstaniu: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '27', '27', 'Nauczycielu, Mojżesz nam napisał: Jeśli jakiś żonaty brat umrze bezdzietnie, to jego brat ma wziąć tę żonę i dać potomstwo swemu bratu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '28', '28', 'Było więc siedmiu braci. I pierwszy ożenił się, i umarł bezdzietnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '29', '29', 'I drugi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '30', '30', 'i trzeci ożenił się z nią. Tak wszyscy siedmiu pomarli nie zostawiwszy potomstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '31', '31', 'W końcu umarła też kobieta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '32', '32', 'Czyją żoną będzie więc ta kobieta przy zmartwychwstaniu? Bo siedmiu ją miało za żonę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '33', '33', 'A Jezus powiedział im: - Na tym świecie ludzie żenią się i za mąż wychodzą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '34', '34', 'ale uznani za godnych innego świata i zmartwychwstania nie żenią się ani za mąż nie wychodzą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '35', '35', 'ani też umrzeć nie mogą, są bowiem równi aniołom i zmartwychwstają, i są synami Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '36', '36', 'A że umarli zmartwychwstają, to i Mojżesz zaznaczył tam, gdzie, mówiąc \'O krzaku, nazywa Pana \'Bogiem Abrahama i Bogiem Izaaka, i Bogiem Jakuba\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '37', '37', 'A Bóg nie jest Bogiem umarłych, ale żywych, bo wszyscy żyją dla Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '38', '38', 'A niektórzy nauczyciele Pisma rzekli: - Nauczycielu, dobrze powiedziałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '39', '39', 'I nigdy już nie śmieli Go o nic pytać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '40', '40', 'I powiedział do nich: - Jakże można mówić, że Mesjasz jest synem Dawida?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '41', '41', 'A sam Dawid mówi w Księdze Psalmów: \'Rzekł Pan mojemu Panu: Siądź po mojej prawicy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '42', '42', 'aż położę Twych nieprzyjaciół jak podnóżek pod Twoje stopy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '43', '43', 'Skoro więc Dawid nazywa Go Panem, to jakże jest On jego synem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '44', '44', 'Gdy cały lud przysłuchiwał się, powiedział swoim uczniom: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '45', '45', 'Strzeżcie się nauczycieli Pisma, którzy chętnie chodzą w powłóczystych strojach, lubią pozdrowienia na rynkach, pierwsze krzesła w synagogach i pierwsze miejsca na przyjęciach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '19', '46', '46', 'Oni to objadają domy wdów i dla pozoru długo się modlą. Tych czeka sroższy wyrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '0', '0', 'I zobaczył bogaczy, którzy wrzucali ofiary do skarbony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '1', '1', 'Ujrzał też pewną biedną wdowę, która wrzuciła tam dwie drobne monety.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '2', '2', 'I powiedział: - Prawdę wam powiadam: Ta uboga wdowa wrzuciła więcej niż wszyscy inni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '3', '3', 'Bo ci wszyscy wrzucili z tego, co im zbywało. Ona zaś przy swoim ubóstwie wrzuciła wszystko, co miała na życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '4', '4', 'A kiedy niektórzy mówili, że świątynię zdobią piękne kamienie i klejnoty, powiedział: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '5', '5', 'Nastaną dni, w których z tego, co oglądacie, nie pozostanie kamień na kamieniu. Każdy będzie zwalony!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '6', '6', 'Zapytali Go: - Nauczycielu, kiedy więc to się stanie? I co będzie znakiem, że to już nadchodzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '7', '7', 'Odpowiedział: - Uważajcie, abyście się nie dali zwieść! Bo wielu przyjdzie w moim imieniu, mówiąc: Otom jest - i: Czas nadszedł. - Nie idźcie za nimi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '8', '8', 'A kiedy usłyszycie o wojnach i rozruchach, nie lękajcie się, bo to wszystko musi się najpierw stać, ale to jeszcze nie koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '9', '9', 'Wtedy mówił im: - Wystąpi naród przeciw narodowi i królestwo przeciw królestwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '10', '10', 'Będą też wielkie trzęsienia ziemi, a miejscami zarazy i głód. Na niebie zaś wielkie i przerażające znaki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '11', '11', 'Ale przed tym wszystkim pochwycą was i będą was prześladowali, wydając synagogom i do więzień i prowadząc do królów i urzędników z powodu mojego imienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '12', '12', 'To pozwoli wam dać świadectwo o Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '13', '13', 'Postanówcie sobie nie obmyślać naprzód, jak się będziecie bronić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '14', '14', 'Bo Ja dam wam słowa i mądrość, której żaden z waszych przeciwników nie zdoła się oprzeć ani przeciwstawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '15', '15', 'A wydawać was będą rodzice i bracia, krewni i przyjaciele. I zabiją niektórych z was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '16', '16', 'I wszyscy was znienawidzą z powodu mojego imienia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '17', '17', 'ale wam włos z głowy nie spadnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '18', '18', 'Przez waszą wytrwałość ocalicie wasze życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '19', '19', 'A kiedy zobaczycie Jeruzalem otoczone przez wojska, wtedy wiedzcie, że wkrótce zostanie zburzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '20', '20', 'Wtedy mieszkańcy Judei niech uciekają w góry, a ci, co będą w mieście, niech wyjdą z niego, ci zaś, co będą w okolicy. niechaj nie wchodzą do miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '21', '21', 'Bo są to dni kary, aby się wypełniło wszystko, co zostało napisane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '22', '22', 'Biada brzemiennym i karmiącym w tym czasie. Bo nastanie wielki ucisk na ziemi i gniew Boży na ten naród.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '23', '23', 'I poginą od miecza, i pójdą w niewolę do wszystkich narodów, i poganie deptać będą Jeruzalem, aż się dopełnią ich czasy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '24', '24', 'I będą znaki na słońcu i na księżycu i na gwiazdach, a na ziemi zwątpienie ludzi bezradnych wobec huku fal morskich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '25', '25', 'Ludzie będą umierać ze strachu i z oczekiwania tego, co ma przyjść na ziemię, bo \'moce niebieskie\' będą poruszone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '26', '26', 'Wtedy zobaczą \'Syna Człowieczego, przychodzącego na obłoku\' z wielką mocą i chwałą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '27', '27', 'Kiedy to się zacznie dziać, odetchnijcie z ulgą i podnieście głowy, dlatego że zbliża się wasze odkupienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '28', '28', 'I opowiedział im przypowieść: - Spójrzcie na drzewo figowe i na wszystkie drzewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '29', '29', 'Kiedy widzicie, że już wypuszczają liście, wiecie, iż lato jest blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '30', '30', 'Tak też i wy, kiedy zobaczycie, że to się dzieje, wiedzcie, iż królestwo Boże jest blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '31', '31', 'Zaprawdę powiadam wam, że to wszystko się stanie, zanim przeminie to pokolenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '32', '32', 'Niebo i ziemia przeminą, ale moje słowa nie przeminą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '33', '33', 'A wy uważajcie na siebie, aby obżarstwo, pijaństwo i troski życiowe nie obciążyły waszych serc, aby ów dzień nie zaskoczył was znienacka jak potrzask.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '34', '34', 'Bo zaskoczy on wszystkich ludzi na ziemi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '35', '35', 'Czuwajcie nieustannie i módlcie się, abyście zdołali unikać tego wszystkiego, co nastąpi, i stanąć przed Synem Człowieczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '36', '36', 'I nauczał za dnia w świątyni, a na noc wychodził i spędzał ją na górze zwanej Oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '20', '37', '37', 'Rano zaś cały lud przychodził do Niego, aby Go słuchać w świątyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '0', '0', 'Zbliżało się Święto Przaśników zwane Paschą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '1', '1', 'Arcykapłani i nauczyciele Pisma zastanawiali się, jak zgładzić Jezusa. Bali się bowiem ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '2', '2', 'A szatan opanował Judasza zwanego Iskariotą, jednego z Dwunastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '3', '3', 'I umówił się z arcykapłanami i dowódcami straży, jak im Go wydać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '4', '4', 'Oni zaś ucieszyli się i ułożyli, że dadzą mu pieniądze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '5', '5', 'Zgodził się i szukał sposobnej chwili, aby im Go wydać pod nieobecność ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '6', '6', 'Nadszedł Dzień Przaśników, w którym należało zabić baranka paschalnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '7', '7', '(Jezus) posłał Piotra i Jana z poleceniem: - Idźcie, poczyńcie przygotowania do spożycia paschy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '8', '8', 'Zapytali Go: - Gdzie mamy poczynić przygotowania?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '9', '9', 'On zaś im powiedział: - Kiedy wejdziecie do miasta, spotka was człowiek niosący dzban wody. Idźcie za nim do domu, do którego wejdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '10', '10', 'I powiedzcie gospodarzowi tego domu: Nauczyciel zapytuje, gdzie jest pokój gościnny, w którym mógłby spożyć paschę razem ze swymi uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '11', '11', 'On zaś wskaże wam na górze dużą salę z sofami. Tam przygotujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '12', '12', 'Poszli, znaleźli, jak im powiedział, i przygotowali paschę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '13', '13', 'A gdy nadeszła pora, zajął miejsce przy stole i apostołowie z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '14', '14', 'I rzekł do nich: - Bardzo pragnąłem spożyć razem z wami tę właśnie paschę, zanim będę cierpiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '15', '15', 'Bo powiadam wam: Nie będę jej więcej spożywał, aż wypełni się w królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '16', '16', 'A kiedy wziął kielich i dzięki czynił, powiedział: - Weźcie to i rozdzielcie między siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '17', '17', 'Powiadam wam bowiem: Odtąd nie będę pił z owocu winnego krzewu, aż nie nadejdzie królestwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '18', '18', 'Wziąwszy chleb, odmówił modlitwę dziękczynną, połamał go i podał im, mówiąc: - To jest ciało moje, które za was będzie wydane. To czyńcie na moją pamiątkę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '19', '19', 'Tak samo po wieczerzy (wziął) kielich, mówiąc: - Ten kielich to Nowe Przymierze w mojej krwi, która za was będzie wylana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '20', '20', 'A oto ręka tego, który Mnie wyda, jest przy mojej na stole.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '21', '21', 'Wprawdzie Syn Człowieczy odchodzi, jak zostało postanowione, lecz biada temu człowiekowi, który Go wydaje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '22', '22', 'A oni zaczęli pytać jeden drugiego, kto z nich miałby to zrobić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '23', '23', 'Powstał też między nimi spór, kto z nich uchodzi na największego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '24', '24', 'On zaś im powiedział: - Królowie panują nad narodami, a ich władców nazywa się dobroczyńcami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '25', '25', 'Ale wy tak nie postępujcie. Ale największy z was niech stanie się jak ten najmłodszy, a przełożony jak ten, co usługuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '26', '26', 'Bo kto jest większy? Czy ten, co siedzi przy stole, czy ten, co usługuje? Czy nie ten, co siedzi przy stole? A Ja jestem wśród was jak ten, co usługuje!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '27', '27', 'Wyście wytrwali przy Mnie w moich przeciwnościach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '28', '28', 'A Ja przekazuję wam królestwo, jak Ojciec Mi przekazał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '29', '29', 'abyście w moim królestwie jedli i pili przy moim stole i zasiadali na krzesłach sędziowskich, sądząc dwanaście pokoleń Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '30', '30', 'Szymonie, Szymonie, oto szatan uprosił sobie, że was przesieje jak pszenicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '31', '31', 'Ale Ja wstawiłem się za tobą, aby twoja wiara nie ustała. A ty ze swej strony umacniaj twoich braci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '32', '32', 'A (Piotr) powiedział Mu: - Panie, z Tobą gotów jestem iść do więzienia i na śmierć!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '33', '33', 'On zaś rzekł: - Mówię ci, Piotrze, zanim dzisiaj kogut zapieje, trzy razy powiesz, że mnie nie znasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '34', '34', 'I powiedział im: - Czy brakowało wam czegokolwiek, kiedy wysłałem was bez sakiewki i bez torby, i bez sandałów? Odpowiedzieli: - Nie, niczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '35', '35', 'Rzekł im: - Ale teraz kto ma sakiewkę, niech ją weźmie, a także torbę, kto zaś nie ma, niech sprzeda płaszcz i kupi miecz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '36', '36', 'Bo powiadam wam, że na Mnie musi się dokonać to, co napisano: \'Został zaliczony do złoczyńców\'. Albowiem spełnia się to, co Mnie dotyczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '37', '37', 'Oni zaś rzekli: - Panie, oto tutaj dwa miecze. Powiedział im: - Wystarczy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '38', '38', 'Potem udał się według zwyczaju na Górę Oliwną. A szli z Nim także uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '39', '39', 'Kiedy przybyli na miejsce, powiedział im: - Módlcie się, abyście nie ulegli pokusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '40', '40', 'Sam zaś oddalił się od nich mniej więcej na odległość rzutu kamieniem, padł na kolana i modlił się: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '41', '41', 'Ojcze, jeśli chcesz, zabierz ten kielich ode Mnie, ale nie moja, lecz Twoja wola niech się stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '42', '42', 'I ukazał Mu się anioł z nieba i umacniał Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '43', '43', 'A pogrążony w udręce modlił się jeszcze goręcej i pot wystąpił na Niego, jakby krople krwi spadające na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '44', '44', 'Kiedy wstał od modlitwy i przyszedł do uczniów, zastał ich śpiących ze smutku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '45', '45', 'I powiedział im: - Czemu śpicie? Módlcie się, abyście nie ulegli pokusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '46', '46', 'Kiedy jeszcze mówił, nadszedł tłum, a jeden z Dwunastu, zwany Judaszem szedł na ich czele. I zbliżył się do Jezusa, żeby go pocałować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '47', '47', 'A Jezus rzekł mu: - Judaszu, pocałunkiem wydajesz Syna Człowieczego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '48', '48', 'Kiedy towarzysze Jezusa zobaczyli, na co się zanosi, spytali: - Panie, czy mamy dobyć miecza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '49', '49', 'I któryś z nich uderzył mieczem sługę arcykapłana,  i uciął mu prawe ucho.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '50', '50', 'A Jezus odpowiedział: - Przestańcie, dość tego! I dotknąwszy ucha, uzdrowił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '51', '51', 'A do arcykapłanów i dowódców  straży świątynnej, i starszych rzekł. - Wyszliście z mieczami i z kijami jak na zabójcę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '52', '52', 'Kiedy codziennie byłem z wami w świątyni, nie podnieśliście na Mnie ręki. Ale to jest właśnie wasza godzina i moc ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '53', '53', 'Pochwycili Go i zabrali ze sobą i zaprowadzili do domu arcykapłana, Piotr zań szedł za Nim z daleka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '54', '54', 'Kiedy na środku podwórza rozpalili ognisko i usiedli przy nim, Piotr usiadł razem z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '55', '55', 'A jakaś służąca, zobaczywszy go siedzącego przy ogniu, przyjrzała się mu uważnie i powiedziała: - I ten był z Nim. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '56', '56', 'Kobieto, nie znam Go - zaprzeczył Piotr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '57', '57', 'Wkrótce potem ktoś inny, zobaczywszy go, powiedział: - Ty też jesteś z nich. A Piotr na to: - Człowieku, nie jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '58', '58', 'Po upływie mniej więcej godziny ktoś inny upierał się, mówiąc: - To prawda, on też był z Nim, bo jest Galilejczykiem!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '59', '59', 'A Piotr na to: - Człowieku, nie wiem, co mówisz. I zaraz, kiedy jeszcze mówił, zapiał kogut.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '60', '60', 'A Pan spojrzał na Piotra. Piotr przypomniał sobie słowa, które Pan mu powiedział: \'Dzisiaj, zanim kogut zapieje, trzy razy się Mnie wyprzesz\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '61', '61', 'I wyszedłszy na zewnątrz, gorzko zapłakał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '62', '62', 'A ludzie pilnujący Jezusa naigrawali się z Niego: bili Go');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '63', '63', 'i zasłoniwszy Mu oczy, zapytywali: - Prorokuj, kto Cię uderzył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '64', '64', 'I lżyli Go wielu innymi słowami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '65', '65', 'Gdy nastał dzień, zebrała się starszyzna ludu: arcykapłani i nauczyciele Pisma. Przyprowadzili Go przed radę');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '66', '66', 'i rzekli: - Powiedz nam, czy Ty jesteś Mesjaszem? Powiedział im: - Jeżeli wam powiem, nie uwierzycie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '67', '67', 'a jeżeli zapytam, nie odpowiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '68', '68', 'Odtąd \'Syn Człowieczy będzie siedział po prawicy Mocy Bożej\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '69', '69', 'Wtedy wszyscy zapytali: - Więc Ty jesteś Synem Bożym? On zaś rzekł do nich: - Tak jest - jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '21', '70', '70', 'Powiedzieli: - Na co nam jeszcze świadkowie? Słyszeliśmy przecież z Jego własnych ust.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '0', '0', 'Wszyscy zebrani zaprowadzili Go do Piłata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '1', '1', 'Tam zaczęli Go oskarżać: - Ustaliliśmy, że On podburza nasz naród, odwodzi od płacenia podatków cesarzowi i podaje się za Mesjasza-Króla. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '2', '2', 'Czy Ty jesteś Królem Judejczyków? - zapytał Go Piłat. Odpowiedział: - Jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '3', '3', 'Piłat rzekł do arcykapłanów i do tłumów: - Nie widzę żadnej winy w tym Człowieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '4', '4', 'Lecz oni nalegali: - Podburza lud nauczając w całej Judei, począwszy od Galilei aż dotąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '5', '5', 'Usłyszawszy to Piłat zapytał, czy jest Galilejczykiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '6', '6', 'A kiedy się dowiedział, że jest poddanym Heroda, odesłał Go do Heroda, który wówczas przebywał również w Jerozolimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '7', '7', 'Herod zobaczywszy Jezusa bardzo się ucieszył. Od dłuższego bowiem czasu chciał Go poznać, gdyż (wiele) słyszał o Nim, a spodziewał się, że ujrzy jakiś znak uczyniony przez Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '8', '8', 'Zasypywał Go pytaniami, ale On mu wcale nie odpowiadał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '9', '9', 'Arcykapłani zaś i nauczyciele Pisma gwałtownie Go oskarżali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '10', '10', 'Herod razem ze swymi żołnierzami wyszydził i wydrwił Go, ubrał w białą szatę i odesłał Piłatowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '11', '11', 'Tego dnia Herod i Piłat zostali przyjaciółmi, przedtem bowiem byli wrogami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '12', '12', 'Wezwawszy arcykapłanów, przełożonych i lud, Piłat');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '13', '13', 'przemówił do nich: - Przyprowadziliście do mnie tego Człowieka jako podżegacza ludu. Ja zaś, przesłuchawszy Go w waszej obecności, stwierdziłem, że ten Człowiek nie popełnił żadnego z przestępstw, które Mu zarzucacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '14', '14', 'Również Herod (nie stwierdził Jego winy), bo przysłał Go do mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '15', '15', 'Ubiczuję Go więc i wypuszczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '16', '16', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '17', '17', 'A wszyscy razem krzyczeli: - Strać Go, a uwolnij nam Barabasza!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '18', '18', 'Ten zaś został osadzony w więzieniu za jakiś rozruch wywołany w mieście i za zabójstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '19', '19', 'Piłat, chcąc uwolnić Jezusa, znowu przemówił do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '20', '20', 'Lecz oni wołali: - Ukrzyżuj Go, ukrzyżuj!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '21', '21', 'A (Piłat) po raz trzeci rzekł do nich: - Co On uczynił złego? Nie widzę w Nim nic takiego, czym by zasłużył na śmierć, więc ubiczuję Go i wypuszczę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '22', '22', 'Lecz oni nalegali i z głośnym krzykiem domagali się, żeby Go ukrzyżować. I wzmagały się ich okrzyki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '23', '23', 'Wtedy Piłat wydał wyrok, którego się domagali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '24', '24', 'Stosownie do ich prośby zwolnił uwięzionego za rozruchy i zabójstwo, a Jezusa skazał na śmierć - jak tego chcieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '25', '25', 'Kiedy Go wyprowadzili, zatrzymali niejakiego Szymona z Cyreny wracającego z pola i włożyli nań krzyż, żeby go niósł za Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '26', '26', 'A szedł za Nim wielki tłum ludu, a zwłaszcza kobiet, które płakały i zawodziły nad Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '27', '27', 'Jezus obróciwszy się do nich, powiedział: - Nie płaczcie nade Mną, płaczcie raczej nad sobą i nad swoimi dziećmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '28', '28', 'bo nadchodzą dni, kiedy będą mówić: Szczęśliwe niepłodne, które nie rodziły, i piersi, które nie karmiły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '29', '29', 'Wtedy zaczną \'mówić górom: Padnijcie na nas! I pagórkom: Przykryjcie nas!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '30', '30', 'Bo jeśli tak postępują z drzewem zielonym, to co się stanie z uschłym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '31', '31', 'Prowadzili też jeszcze dwóch złoczyńców, aby ich stracić razem z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '32', '32', 'Przyszli na miejsce zwane Czaszką. Tam ukrzyżowali Jezusa i złoczyńców, jednego po prawej, a drugiego po lewej stronie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '33', '33', 'A Jezus mówił: - Ojcze, przebacz im, bo nie wiedzą, co czynią. I \'dzieląc się jego szatami, rzucili losy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '34', '34', 'Lud stał przypatrując się, przełożeni zaś naśmiewali się z Niego: - Innych wybawiał, niech teraz siebie wybawi, jeśli jest wybranym przez Boga Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '35', '35', 'Żołnierze, którzy podawali Mu ocet, szydzili także z Niego: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '36', '36', 'Jeśliś jest królem żydowskim, wybaw sam siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '37', '37', 'Był też nad Nim napis: To jest król żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '38', '38', 'Jeden z powieszonych złoczyńców urągał Mu: - Czyż nie jesteś Mesjaszem? Wybaw samego siebie i nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '39', '39', 'A drugi karcąc go rzekł: - Czy ty (nawet teraz) nie boisz się Boga, kiedy cierpisz tę samą karę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '40', '40', 'My cierpimy sprawiedliwie, dostajemy to, na cośmy sami zasłużyli, lecz On nic złego nie uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '41', '41', 'I mówił: - Jezusie, pamiętaj o mnie, kiedy przyjdziesz do swego królestwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '42', '42', 'I rzekł mu: - Zaprawdę powiadam ci, jeszcze dziś będziesz ze Mną w raju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '43', '43', 'Była już prawie szósta godzina i ciemność zaległa całą ziemię aż do godziny dziewiątej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '44', '44', 'bo słońce się zaćmiło. Zasłona w świątyni rozdarła się na dwie części.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '45', '45', 'A Jezus zawołał mocnym głosem: - Ojcze, w ręce Twoje oddaję ducha mojego. I po tych słowach skonał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '46', '46', 'A setnik zobaczywszy, co się stało, wielbił Boga, mówiąc: - To był naprawdę sprawiedliwy człowiek!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '47', '47', 'I wszystkie tłumy, które zeszły się na to widowisko, ujrzawszy, co się stało, wracały, bijąc się w piersi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '48', '48', 'A wszyscy Jego znajomi, a zwłaszcza niewiasty, które szły za Nim od Galilei, patrzyli na to z daleka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '49', '49', 'Człowiek zaś imieniem Józef, członek rady, a zarazem dobry i sprawiedliwy człowiek,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '50', '50', 'który pochodził z Arymatei, miasta judejskiego, nie zgadzał się z radą i jej postępowaniem, ale oczekiwał królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '51', '51', 'On przyszedł do Piłata i poprosił o ciało Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '52', '52', 'I zdjąwszy je, owinął w płótno i złożył w grobie wykutym w skale, w którym nikt jeszcze nie leżał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '53', '53', 'Był Dzień Przygotowania i zbliżał się szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '54', '54', 'Przyszły też z nim niewiasty, które przybyły z Jezusem z Galilei, i przyglądały się grobowi i złożeniu do niego ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '22', '55', '55', 'A powróciwszy (do domu), przygotowały wonności i olejki. W szabat jednak odpoczywały według przykazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '0', '0', 'A w pierwszy dzień tygodnia, wczesnym rankiem przyszły do grobu niosąc wonności, które przygotowały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '1', '1', 'Kamień od grobu zastały odsunięty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '2', '2', 'A wszedłszy do wnętrza, nie znalazły ciała (Pana) Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '3', '3', 'Kiedy nie wiedziały, co o tym sądzić, stanęli przed nimi dwaj mężczyźni w lśniących szatach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '4', '4', 'Przestraszone pochyliły głowy ku ziemi, a oni rzekli: - Dlaczego szukacie żyjącego wśród umarłych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '5', '5', 'Nie ma Go tu, powstał z martwych! Przypomnijcie sobie, jak wam powiedział, kiedy był jeszcze w Galilei:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '6', '6', '\'Trzeba, aby Syn Człowieczy został wydany w ręce grzeszników i ukrzyżowany, a trzeciego dnia zmartwychwstał\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '7', '7', 'I przypomniały sobie Jego słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '8', '8', 'A powróciwszy od grobu, oznajmiły to wszystko Jedenastu i wszystkim pozostałym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '9', '9', 'A były to: Maria Magdalena i Joanna, i Maria Jakubowa. Również pozostałe kobiety razem z nimi opowiadały o tym apostołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '10', '10', 'Ale te słowa wydały się im prawie niedorzeczne i nie wierzyli im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '11', '11', 'Piotr zaś pobiegł do grobu, a schyliwszy się widzi same taśmy płócienne. I poszedł do siebie, dziwiąc się temu, co się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '12', '12', 'A dwóch z nich szło tego właśnie dnia do miasteczka, które leżało sześćdziesiąt stadiów od Jeruzalem, a zwało się Emmaus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '13', '13', 'Rozmawiali ze sobą o tym wszystkim, co zaszło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '14', '14', 'Kiedy tak rozmawiali i rozprawiali, sam Jezus zbliżył się do nich i szedł z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '15', '15', 'Lecz oczy ich były zasłonięte, aby Go nie poznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '16', '16', 'Rzekł do nich: - Cóż to za rozmowy prowadzicie ze sobą w drodze? Oni zatrzymali się zasmuceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '17', '17', 'A jeden z nich imieniem Kleofas odpowiedział Mu: - Jesteś chyba jedynym przybyszem w Jeruzalem, który nie wie, co się w nim stało w tych dniach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '18', '18', 'I zapytał ich: - Co takiego? Oni zaś opowiedzieli Mu o Jezusie Nazarejczyku, który był prorokiem potężnym w czynie i słowie wobec Boga i całego ludu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '19', '19', 'jak arcykapłani i przełożeni nasi skazali Go na śmierć i ukrzyżowali. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '20', '20', 'A myśmy się spodziewali, że to On właśnie odkupi Izraela. Już trzeci dzień upływa od tego wszystkiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '21', '21', 'Niektóre z naszych niewiast zdumiały nas. Wczesnym rankiem były u grobu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '22', '22', 'a nie znalazłszy Jego ciała, przyszły opowiadając, że widziały aniołów, którzy mówią, że On żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '23', '23', 'Także niektórzy z naszych poszli do grobu i zastali go tak, jak niewiasty mówiły, ale Jezusa nie zobaczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '24', '24', 'On zaś rzekł do nich: - Bezmyślni i nieskorzy do wierzenia we wszystko, co powiedzieli Prorocy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '25', '25', 'Czyż Mesjasz nie musiał tego wycierpieć i tak wejść do swojej chwały?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '26', '26', 'I począwszy od Mojżesza i Proroków wyjaśniał im, co było o Nim we wszystkich Pismach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '27', '27', 'I zbliżyli się do miasteczka, do którego zdążali, a On udał, że idzie dalej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '28', '28', 'Ale Go uprosili: - Zostań z nami, bo ma się ku wieczorowi i dzień się już nachylił. I wszedł, aby zostać z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '29', '29', 'Kiedy znalazł się z nimi przy stole i wziął chleb, odmówił modlitwę uwielbienia i połamawszy chleb, podawał im.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '30', '30', 'Wtedy otworzyły się ich oczy i poznali Go. Ale On im zniknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '31', '31', 'I mówili jeden do drugiego: - Czyż nie płonęło nam serce, kiedy mówił do nas w drodze i wyjaśniał nam Pisma?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '32', '32', 'I w tej samej chwili powrócili do Jeruzalem. Tam znaleźli Jedenastu zebranych razem oraz ich towarzyszy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '33', '33', 'którzy mówili: - Pan naprawdę zmartwychwstał i ukazał się Szymonowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '34', '34', 'Oni także opowiadali o tym co zaszło w drodze i jak Go poznali po łamaniu chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '35', '35', 'Kiedy o tym rozmawiali, On sam stanął wśród nich. I mówi im: - Pokój wam!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '36', '36', 'Przerażonym i wyklętym zdawało się, że widzą ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '37', '37', 'Powiedział więc im: - Czemuście tacy przerażeni i dlaczego wątpliwości budzą się w sercach waszych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '38', '38', 'Oglądajcie moje ręce i moje nogi, bo to Ja jestem! Dotykajcie Mnie i przekonajcie się, przecież duch nie ma ciała ani kości, a Ja mam - jak widzicie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '39', '39', '(Po tych słowach pokazał im ręce i nogi).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '40', '40', 'A kiedy z radości jeszcze nie wierzyli i zdumiewali się, rzekł im: - Czy macie tu coś do zjedzenia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '41', '41', 'Podali Mu kawałek pieczonej ryby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '42', '42', 'I wziąwszy go zjadł na ich oczach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '43', '43', 'I rzekł do nich: - To właśnie te słowa powiedziałem wam, kiedy jeszcze byłem z wami: Musi wypełnić się wszystko, co o Mnie napisano w Prawie Mojżeszowym, w Prorokach i w Psalmach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '44', '44', 'Wtedy nauczył ich rozumieć Pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '45', '45', 'I powiedział im: - Napisano tak: Mesjasz będzie cierpiał i zmartwychwstanie trzeciego dnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '46', '46', 'W Imię Jego wzywać się będzie wszystkie narody, począwszy od Jeruzalem, do nawrócenia dającego odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '47', '47', 'Wyście tego świadkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '48', '48', 'I ześlę na was dar obiecany przez mojego Ojca. Wy zaś zostańcie w mieście, aż nie otrzymacie mocy z wysokości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '49', '49', 'Wyprowadził ich z miasta w stronę Betanii i podniósłszy ręce błogosławił ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '50', '50', 'A kiedy ich błogosławił, odszedł od nich (i został zabrany do nieba).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '51', '51', 'Oni zaś (oddawszy Mu pokłon) powrócili z wielką radością do Jeruzalem');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'lk', '23', '52', '52', 'i nieustannie w świątyni wielbili Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '0', '0', 'Na początku było Słowo, a Słowo było u Boga, a Bogiem było Słowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '1', '1', 'Ono było na początku u Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '2', '2', 'Wszystko przez Nie się stało, a bez Niego nic się nie stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '3', '3', 'Co się stało, w Nim było życiem, a życie było światłością ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '4', '4', 'A światłość świeci w ciemności, a ciemność jej nie ogarnęła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '5', '5', 'Człowiek imieniem Jan został posłany przez Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '6', '6', 'Przyszedł, aby świadczyć o światłości, aby przez niego wszyscy uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '7', '7', 'Nie on był światłością, ale (przyszedł), aby świadczyć o światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '8', '8', 'Światłość prawdziwa, która oświeca każdego człowieka, przyszła na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '9', '9', 'Była na świecie i świat stał się przez Nią, a świat Jej nie poznał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '10', '10', 'Przyszła do swoich, a swoi Jej nie przyjęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '11', '11', 'Tym zaś, którzy Ją przyjęli, którzy uwierzyli w Jej imię, dała moc, aby się stali dziećmi Bożymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '12', '12', 'Oni nie narodzili się z ludzi: ani z pożądliwości ciała, ani z woli mężczyzny, ale z Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '13', '13', 'A Słowo stało się człowiekiem i zamieszkało między nami, i widzieliśmy Jego chwałę, chwałę, jaką otrzymał od Ojca Jednorodzony, pełen łaski i prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '14', '14', 'Jan daje o Nim świadectwo, wołając: Oto (był) Ten, o którym powiedziałem: Ten, który przychodzi po mnie, przewyższył mnie, bo był przede mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '15', '15', 'Z Jego pełni wszyscyśmy wzięli łaskę po łasce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '16', '16', 'Prawo zostało przekazane przez Mojżesza, łaskę zaś i prawdę otrzymaliśmy przez Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '17', '17', 'Boga nikt nigdy nie widział: Jednorodzony Bóg, który jest na łonie Ojca, dał nam Go poznać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '18', '18', 'A takie jest świadectwo Jana: Kiedy Judejczycy wysłali do niego z Jerozolimy kapłanów i lewitów, aby go zapytali: Kim jesteś? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '19', '19', 'on im oświadczył bez wahania: - Nie jestem Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '20', '20', 'I zapytał go: - To kim jesteś, Eliaszem? Powiada: - Nie jestem. - Jesteś prorokiem? Odpowiedział: - Nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '21', '21', 'Rzekli więc mu: - Kim jesteś, abyśmy mogli dać odpowiedź tym, co nas wysłali. Co mówisz sam o sobie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '22', '22', 'Odrzekł: - Jam \'Głos wołającego: Na pustyni prostujcie drogę Panu\', jak powiedział prorok Izajasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '23', '23', 'A wysłańcy byli ze (stronnictwa) faryzeuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '24', '24', 'I zapytali go znowu: - Dlaczego więc chrzcisz, skoro nie jesteś Mesjaszem ani Eliaszem, ani prorokiem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '25', '25', 'Jan im odpowiedział: - Ja chrzczę wodą, ale jest już pośród was Ten,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '26', '26', 'który przyjdzie po mnie - wy Go nie znacie - a ja nie jestem wart rozwiązać rzemienia u Jego sandała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '27', '27', 'Działo się to w Betanii za Jordanem, gdzie Jan chrzcił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '28', '28', 'Nazajutrz (Jan) widzi przychodzącego Jezusa i mówi: - Oto Baranek Boży, który gładzi grzech świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '29', '29', 'To Ten, o którym powiedziałem. Po mnie przyjdzie Mąż, który mnie przewyższy, bo był przede mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '30', '30', 'Ja Go nie znałem. Lecz po to przyszedłem chrzcić wodą, aby On został objawiony Izraelowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '31', '31', 'Jan dał świadectwo: - Ujrzałem Ducha zstępującego z nieba jak gołębica. I spoczął na Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '32', '32', 'Ja Go nie znałem, ale Ten, który mnie posłał chrzcić wodą, rzekł mi: Ten, nad którym ujrzysz Ducha zstępującego i pozostającego na Nim, jest Tym, który chrzci Duchem Świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '33', '33', 'Tak, ja to ujrzałem i oświadczam, że On jest Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '34', '34', 'Nazajutrz Jan znowu stał z dwoma uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '35', '35', 'I spojrzawszy na przechodzącego Jezusa, mówi: - Oto Baranek Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '36', '36', 'Obaj uczniowie usłyszeli, jak to mówił, i poszli za Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '37', '37', 'A Jezus odwrócił się, a zobaczywszy, że idą za Nim, zapytał: - Czego szukacie? Odpowiedzieli Mu: - Rabbi, to znaczy: Nauczycielu! Gdzie mieszkasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '38', '38', 'Mówi im: - Chodźcie zobaczyć. Poszli więc, zobaczyli, gdzie mieszka, i tego dnia pozostali u Niego. Było to około dziesiątej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '39', '39', 'Andrzej, brat Szymona Piotra, był jednym z tych dwóch uczniów, którzy usłyszeli Jana i poszli za Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '40', '40', 'On spotyka najpierw swego brata Szymona i mówi mu: - Spotkaliśmy Mesjasza, to znaczy Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '41', '41', 'I zaprowadził go do Jezusa. A Jezus spojrzawszy na niego rzekł: - Ty jesteś Szymon, syn Jana, ty będziesz zwał się Kefas, to znaczy opoka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '42', '42', 'Nazajutrz Jezus postanowił pójść do Galilei. Spotyka Filipa i mówi mu: - Chodź za Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '43', '43', 'A Filip pochodził z Betsaidy, z miasta Andrzeja i Piotra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '44', '44', 'Filip spotyka Natanaela i mówi mu: - Spotkaliśmy Tego, o którym pisał w Prawie Mojżesz i Prorocy, Jezusa syna Józefa, z Nazaretu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '45', '45', 'Powiedział mu Natanael: - Czy może być co dobrego z Nazaretu? Mówi mu Filip: - Chodź i zobacz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '46', '46', 'Jezus ujrzał Natanaela, który zbliżał się do Niego. I mówi o nim: - Oto Izraelita, w którym naprawdę nie masz zdrady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '47', '47', 'Pyta Go Natanael: - Skąd mnie znasz? Jezus mu odpowiedział: - Widziałem cię pod drzewem figowym, zanim cię Filip zawołał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '48', '48', 'Natanael Mu odrzekł: - Rabbi, Tyś jest Syn Boży! Tyś Król Izraela!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '49', '49', 'A Jezus tak mu odpowiedział: - Uwierzyłeś bo ci powiedziałem, że widziałem cię pod drzewem figowym. Zobaczysz więcej niż to.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '0', '50', '50', 'I mówi mu: - Zaprawdę, zaprawdę powiadam wam: Ujrzycie niebiosa otwarte i aniołów Bożych idących do nieba i przychodzących z nieba do Syna Człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '0', '0', 'Trzeciego dnia odbywało się wesele w Kanie Galilejskiej. I była tam matka Jezusowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '1', '1', 'Zaproszono też na wesele Jezusa i Jego uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '2', '2', 'A gdy zabrakło wina, matka Jezusa mówi do Niego: - Wina nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '3', '3', 'Jezus jej mówi: - Czego chcesz ode Mnie, niewiasto? Czyż nie nadeszła moja godzina?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '4', '4', 'Matka Jego mówi do usługujących: - Zróbcie, cokolwiek wam każe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '5', '5', 'A stało tam sześć stągwi kamiennych przeznaczonych do żydowskich oczyszczeń. Każda z nich zawierała dwa lub trzy wiadra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '6', '6', 'Mówi im Jezus: - Nalejcie wody do stągwi. Napełnili je po brzegi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '7', '7', 'Mówi im znowu: - Nabierzcie teraz i zanieście gospodarzowi wesela. I zanieśli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '8', '8', 'Kiedy gospodarz skosztował wody przemienionej w wino - a nie wiedział skąd (to wino) się wzięło, bo (tylko) usługujący, którzy nabrali wody, wiedzieli - wezwał pana młodego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '9', '9', 'i powiedział mu: - Każdy najpierw daje dobre wino, a gorsze, kiedy już sobie podpiją. Ty zaś zachowałeś dobre wino aż do tej pory.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '10', '10', 'Ten pierwszy znak uczynił Jezus w Kanie Galilejskiej i objawił swoją chwałę. I uwierzyli weń uczniowie Jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '11', '11', 'Potem udał się do Kafarnaum z matką, braćmi i uczniami swoimi. I pozostali tam kilka dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '12', '12', 'Zbliżała się Pascha żydowska. Jezus poszedł do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '13', '13', 'W świątyni zastał sprzedających woły, owce i gołębice oraz tych, co zmieniali pieniądze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '14', '14', 'Zrobiwszy więc bicz z powrozów, wyrzucił wszystkich ze świątyni razem z owcami i wołami; a tym, którzy wymieniali pieniądze, porozrzucał monety i powywracał stoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '15', '15', 'A do sprzedawców gołębi powiedział: - Zabierzcie to stąd, nie róbcie z domu Ojca mojego targowiska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '16', '16', 'Uczniowie Jego przypomnieli sobie, że napisano: \'Gorliwość o Twój dom pożera mnie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '17', '17', 'Judejczycy więc rzekli Mu: - Jakim znakiem dowiedziesz, że możesz to czynić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '18', '18', 'A Jezus im odpowiedział: - Zburzcie tę świątynię, a w trzy dni zbuduję ją na nowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '19', '19', 'A Judejczycy powiedzieli: - Czterdzieści sześć lat budowano tę świątynię, a Ty ją w trzy nie zbudujesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '20', '20', 'Lecz On mówił o świątyni swego ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '21', '21', 'Kiedy więc zmartwychwstał, uczniowie Jego przypomnieli sobie, że to mówił, i uwierzyli Pismu i słowu, które Jezus powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '22', '22', 'Kiedy przebywał w Jerozolimie podczas Paschy w dzień świąteczny, wielu, widząc znaki, które czynił, uwierzyło w Jego imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '23', '23', 'Ale Jezus nie zwierzał się im, bo znał wszystkich,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '1', '24', '24', 'i nie potrzebował, aby ktokolwiek świadczył o człowieku. Sam bowiem wiedział, co było w człowieku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '0', '0', 'Był wśród faryzeuszów pewien człowiek, dostojnik judejski, który miał na imię Nikodem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '1', '1', 'Przyszedł do Jezusa w nocy i rzekł Mu: - Rabbi, wiemy, żeś jest nauczycielem, który przyszedł od Boga, bo nikt nie może czynić takich znaków, jakie Ty czynisz, jeżeli Bóg nie jest z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '2', '2', 'A Jezus mu odpowiedział: - Zaprawdę, zaprawdę powiadam ci: Kto nie narodzi się na nowo, nie może ujrzeć królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '3', '3', 'Mówi do Niego Nikodem: - Jak może narodzić się człowiek będąc starcem? Czy może wejść po raz drugi do łona matki i narodzić się?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '4', '4', 'Jezus odrzekł: - Zaprawdę, zaprawdę powiadam ci: Kto nie narodzi się z wody i z Ducha, nie może wejść do królestwa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '5', '5', 'Co rodzi się z ciała, jest ciałem, a co z Ducha, jest Duchem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '6', '6', 'Nie dziw się, że powiedziałem tobie: musicie narodzić się na nowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '7', '7', 'Wiatr wieje gdzie chce. Szum jego słyszysz, lecz nie wiesz, skąd przychodzi i dokąd zmierza. Tak samo bywa z każdym, kto się z Ducha narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '8', '8', 'A Nikodem na to odpowiedział: - Jakże to może się stać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '9', '9', 'Jezus odrzekł: - Jesteś nauczycielem Izraela, a tego nie wiesz? Zaprawdę, zaprawdę powiadam ci, mówimy to, co wiemy, i dajemy świadectwo o tym, cośmy widzieli, a nie przyjmujecie naszego świadectwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '11', '11', 'Jeżeli nie wierzycie, kiedy wam mówię o rzeczach ziemskich, to jakżeż uwierzycie, gdy będę wam mówił o sprawach niebieskich?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '12', '12', 'Nikt nie wstąpił do nieba oprócz Tego, który z nieba zstąpił - Syna Człowieczego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '13', '13', 'I jak Mojżesz podwyższył węża na pustyni, tak też musi być podwyższony Syn Człowieczy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '14', '14', 'aby każdy, kto wierzy w Niego, miał życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '15', '15', 'Albowiem Bóg tak umiłował świat, że wydał swojego Syna Jednorodzonego, aby każdy, kto w Niego wierzy, nie zginął, ale miał życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '16', '16', 'Bóg bowiem posłał Syna na świat nie po to, aby świat potępił, ale po to, aby świat był przez Niego zbawiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '17', '17', 'Kto wierzy w Niego, nie będzie potępiony, a kto nie wierzy, już jest potępiony, bo nie uwierzył w imię Jednorodzonego Syna Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '18', '18', 'A przyczyna potępienia jest taka: Światłość przyszła na świat, ale ludzie bardziej umiłowali ciemności aniżeli światłość, bo ich uczynki były złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '19', '19', 'Każdy bowiem, kto źle czyni, nienawidzi światłości i nie zbliża się do światła, aby jego uczynki nie zostały potępione.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '20', '20', 'Kto zaś postępuje zgodnie z prawdą, podchodzi do światła, aby pokazać, że działa po Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '21', '21', 'Potem Jezus poszedł z uczniami do ziemi judzkiej. Tam przebywał z nimi i chrzcił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '22', '22', 'Jan zaś chrzcił w Ainon w pobliżu Salim, bo tam było wiele wody. Przychodzili ludzie i przyjmowali chrzest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '23', '23', 'A Jan nie był jeszcze uwięziony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '24', '24', 'Doszło więc do sporu między niektórymi uczniami Jana a pewnym Judejczykiem w sprawie oczyszczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '25', '25', 'I przyszli do Jana, i powiedzieli: - Rabbi, ten, który był z tobą na drugim brzegu Jordanu, o którym ty wydałeś świadectwo, chrzci i wszyscy przychodzą do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '26', '26', 'Jan im odpowiedział: - Człowiek nie może sobie przypisywać niczego, co mu nie jest dane z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '27', '27', 'Przecież wy sami jesteście świadkami, że powiedziałem: Nie jestem Mesjaszem, lecz zostałem posłany przed Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '28', '28', 'Kto ma oblubienicę, jest oblubieńcem, a przyjaciel oblubieńca, kiedy go słyszy, cieszy się bardzo jego głosem. Takiej właśnie radości w pełni doznaję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '29', '29', 'On ma wzrastać, a ja mam się umniejszać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '30', '30', 'Kto przychodzi z góry, przewyższa wszystkich. Kto z ziemi pochodzi, do ziemi należy i mówi o sprawach ziemskich. Kto pochodzi z nieba (przewyższa wszystkich).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '31', '31', 'Daje świadectwo o tym, co widział i słyszał, lecz nikt jego świadectwa nie przyjmuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '32', '32', 'Kto zaś przyjął jego świadectwo, ten przyłożył pieczęć na dowód, że Bóg mówi prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '33', '33', 'Ten bowiem, kogo Bóg posłał, głosi słowa Boga, który udziela Ducha bez miary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '34', '34', 'Ojciec miłuje Syna i wszystko złożył w Jego ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '2', '35', '35', 'Kto wierzy w Syna, ma życie wieczne, a kto nie chce wierzyć Synowi, nie osiągnie życia, lecz ściągnie na siebie gniew Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '0', '0', 'Kiedy Jezus się dowiedział, że faryzeusze usłyszeli, iż On chrzci i zyskuje więcej uczniów aniżeli Jan -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '1', '1', 'chociaż ściśle mówiąc Jezus sam nie chrzcił, lecz Jego uczniowie -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '2', '2', 'opuścił Judeę i ponownie udał się do Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '3', '3', 'Musiał przejść przez Samarię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '4', '4', 'Przychodzi więc do miasta samarytańskiego zwanego Sychar, blisko pola, które Jakub dał swojemu synowi Józefowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '5', '5', 'Była tam studnia Jakubowa. Jezus zmęczony drogą siedział przy studni. Było to około szóstej godziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '6', '6', 'Przychodzi kobieta z Samarii, aby nabrać wody. A Jezus mówi do niej: - Daj Mi pić!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '7', '7', 'Bo Jego uczniowie poszli do miasta kupić coś do jedzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '8', '8', 'Mówi Mu kobieta, ta Samarytanka: - Jak to, Ty, Judejczyk, prosisz mnie, Samarytankę, abym dała Ci pić? Judejczycy bowiem nie utrzymują żadnych stosunków z Samarytanami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '10', '10', 'Mówi Mu kobieta: - Panie, nie masz nawet czym nabrać, a studnia jest głęboka, skądże więc weźmiesz wodę żywą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '11', '11', 'Czyżbyś Ty był większy od naszego praojca Jakuba, który dał nam tę studnię, sam z niej pił i synowie jego, i trzody?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '12', '12', 'Jezus jej odpowiedział: - Kto tę wodę pije, będzie pragnął znowu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '13', '13', 'Kto zaś napije się tej wody, którą Ja mu dam, nigdy już nie zazna pragnienia. Ale woda, którą Ja mu dam, stanie się w nim źródłem wody tryskającej ku życiu wiecznemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '14', '14', 'Mówi do Niego kobieta: - Panie, daj mi tej wody, abym nie miała pragnienia i nie przychodziła tutaj po wodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '15', '15', 'Mówi jej: - Idź, zawołaj twego męża i wracaj tutaj!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '16', '16', 'Odrzekła Mu kobieta: - Nie mam męża. Mówi jej Jezus: - Dobrze powiedziałaś, że nie masz męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '17', '17', 'Miałaś ich pięciu, a ten, którego teraz masz, nie jest twoim mężem. To powiedziałaś prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '18', '18', 'Jezus jej odpowiedział: - Gdybyś wiedziała, co to za dar Boży i kim jest Ten, który mówi do ciebie: Daj Mi pić, to ty byś Go prosiła, a On dałby ci wody żywej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '19', '19', 'Na tej górze nasi praojcowie oddawali cześć Bogu, a wy mówicie, że w Jerozolimie jest miejsce, gdzie należy oddawać cześć Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '20', '20', 'Mówi jej Jezus: - Wierz mi, kobieto, że już nadchodzi godzina, kiedy ani na tej górze, ani w Jerozolimie nie będziecie oddawali czci Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '21', '21', 'Wy czcicie to, czego nie znacie, my zaś czcimy to, co znamy, bo zbawienie jest od Judejczyków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '22', '22', 'Ale nadchodzi godzina, w właściwie już nadeszła, kiedy prawdziwi czciciele będą oddawać cześć Ojcu w sposób prawdziwie duchowy. Bo i Ojciec szuka takich właśnie czcicieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '23', '23', 'Bóg jest duchem i Jego czciciele powinni oddawać Mu cześć w sposób prawdziwie duchowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '24', '24', 'Mówi do Niego kobieta: - Wiem, że przyjdzie Mesjasz zwany Chrystusem. Ale kiedy On przyjdzie, wszystko nam objawi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '25', '25', 'Mówi jej Jezus: - To jestem Ja, który z tobą rozmawiam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '26', '26', 'W tej chwili nadeszli Jego uczniowie. I dziwili się, że rozmawia z kobietą. Nikt jednak nie zapytał: Czego chcesz (od niej)? albo: Dlaczego z nią rozmawiasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '27', '27', 'Kobieta zaś zostawiła dzban i poszła do miasta. Tam mówi ludziom: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '28', '28', 'Chodźcie, zobaczcie człowieka, który mi powiedział wszystko, co zrobiłam. Czy to nie jest Mesjasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '29', '29', 'Wyszli więc z miasta i zdążali do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '30', '30', 'Tymczasem uczniowie prosili Go: - Rabbi, jedz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '31', '31', 'On im odrzekł: - Mam do jedzenia pokarm, którego wy nie znacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '32', '32', 'Uczniowie więc mówili między sobą: - Czyżby Mu kto przyniósł coś do jedzenia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '33', '33', 'Mówi im Jezus: - Moim pokarmem jest spełnienie woli Tego, który Mnie posłał, i dokonanie Jego dzieła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '34', '34', 'Czyż nie mówicie: Jeszcze cztery miesiące, a nadejdą żniwa? Oto powiadam wam: Rozejrzycie się - pola bieleją już pod żniwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '35', '35', 'Żniwiarz odbiera zapłatę i zbiera plon na życie wieczne, aby siewca i żniwiarz wspólnie się cieszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '36', '36', 'Bo tu sprawdza się przysłowie: Jeden sieje, a drugi zbiera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '37', '37', 'Ja was posłałem zbierać to, nad czym się nie utrudziliście. Inni się natrudzili, a wyście weszli w ich trud.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '38', '38', 'Wielu Samarytan z tego miasta uwierzyło w Niego dzięki kobiecie, która oświadczyła: Powiedział mi wszystko, co uczyniłam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '39', '39', 'Kiedy więc Samarytanie przyszli do Niego, prosili Go, aby u nich pozostał. I pozostali tam dwa dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '40', '40', 'I jeszcze więcej ich uwierzyło dzięki Jego nauce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '41', '41', 'A do kobiety mówili: - Wierzymy już nie dzięki twojemu opowiadaniu, ale dlatego, żeśmy Go sami usłyszeli i przekonaliśmy się, że On jest naprawdę Zbawicielem świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '42', '42', 'A po dwóch dniach poszedł stamtąd do Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '43', '43', 'Sam bowiem Jezus oświadczył, że prorok nie ma uznania w swojej ojczyźnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '44', '44', 'Kiedy więc przyszedł do Galilei, Galilejczycy Go przyjęli. Bo i oni przyszli na święto, i widzieli, czego dokonał podczas święta w Jerozolimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '45', '45', 'Przyszedł więc Jezus ponownie do Kany Galilejskiej, gdzie przemienił wodę w wino. A w Kafarnaum był pewien urzędnik królewski, którego syn chorował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '46', '46', 'Usłyszawszy, że Jezus przybył z Judei do Galilei, poszedł do Niego i prosił, aby przyszedł i uzdrowił jego syna, bo już konał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '47', '47', 'A Jezus powiedział do niego: - Jeśli nie ujrzycie znaków i cudów, to nie uwierzycie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '48', '48', 'Mówi do Niego urzędnik królewski: - Panie, przyjdź, zanim moje dziecko umrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '49', '49', 'Mówi mu Jezus: - Idź, twój syn żyje. Ów człowiek uwierzył słowu, które Jezus powiedział, i poszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '50', '50', 'Kiedy był jeszcze w drodze, słudzy jego wyszli mu naprzeciw, mówiąc, że jego syn żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '51', '51', 'Zapytał się więc, o której godzinie mu się polepszyło. Powiedzieli mu: - Wczoraj o siódmej godzinie opuściła go gorączka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '52', '52', 'Ojciec więc uświadomił sobie, że była to godzina, w której Jezus powiedział mu: Twój syn  żyje. I sam uwierzył, i cały jego dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '3', '53', '53', 'Był to już drugi znak, który Jezus uczynił po przyjściu z Judei do Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '0', '0', 'Potem było święto żydowskie. Ale Jezus udał się do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '1', '1', 'W Jerozolimie przy Owczej Bramie jest sadzawka, która nazywa się po hebrajsku Bethzatha i ma pięć krużganków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '2', '2', 'W tych krużgankach leżało mnóstwo chorych: ślepych, kulawych i sparaliżowanych (...).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '3', '3', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '4', '4', 'był tam też pewien człowiek, który chorował trzydzieści osiem lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '5', '5', 'Kiedy Jezus zobaczył go leżącego i dowiedział się, że już od dawna choruje, mówi do niego: - Chciałbyś wyzdrowieć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '6', '6', 'Odpowiedział Mu chory: - Panie, nie mam nikogo, kto by mnie zsunął do sadzawki, gdy woda się poruszy. A zanim ja sam się przyczołgam, już inny schodzi (do wody) przede mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '7', '7', 'Mówi mu Jezus: - Wstań, weź swoje nosze i chodź!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '8', '8', 'I natychmiast ten człowiek wyzdrowiał, wziął nosze i chodził. A w ten dzień przypadał szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '9', '9', 'Judejczycy więc mówili uzdrowionemu: - Dziś szabat! Nie wolno ci dźwigać noszy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '10', '10', 'Odpowiedział im: - Ten co mnie uzdrowił, rzekł do mnie: Weź swoje nosze i chodź!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '11', '11', 'Zapytali go: - Kim jest człowiek, który ci powiedział: Weź i chodź.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '12', '12', 'Uzdrowiony jednak nie wiedział, kto to był, bo Jezus oddalił się od tłumu, który zebrał się w tym miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '13', '13', 'Potem Jezus spotkał go w świątyni i powiedział mu: - Jesteś zdrów, nie grzesz więcej, aby cię nie spotkało co gorszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '14', '14', 'Poszedł ów człowiek i oświadczył Judejczykom, że to Jezus go uzdrowił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '15', '15', 'Judejczycy prześladowali Jezusa za to, że uczynił to w szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '16', '16', 'Rzekł im Jezus: - Ojciec mój działa, więc i Ja działam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '17', '17', 'Judejczycy tym bardziej starali się Go zabić; (Jezus) bowiem nie tylko naruszał szabat, lecz także Boga nazywał swoim Ojcem, czyniąc się równym Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '18', '18', 'Rzekł im Jezus: - Zaprawdę, zaprawdę powiadam wam: Jeśli Syn nie widzi, że Ojciec działa, to nie może nic czynić sam od siebie. Co bowiem On czyni, to i Syn czyni tak samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '19', '19', 'Ojciec miłuje Syna i pokazuje Mu wszystko, co sam czyni. I pokaże Mu jeszcze większe dzieła, żebyście je podziwiali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '20', '20', 'Jak bowiem Ojciec wskrzesza i ożywia umarłych, tak i Syn ożywia, kogo zechce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '21', '21', 'Ojciec nie sądzi nikogo, ale cały sąd oddał Synowi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '22', '22', 'aby wszyscy tak czcili Syna, jak czczą Ojca. Kto nie czci Syna, ten nie czci Ojca, który Go posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '23', '23', 'Zaprawdę, zaprawdę powiadam wam. Kto słucha mojej nauki i wierzy Temu, kto Mnie posłał, ma życie wieczne i nie będzie potępiony, ale już przyszedł ze śmierci do życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '24', '24', 'Zaprawdę, zaprawdę powiadam wam: Zbliża się godzina - a nawet już nadeszła - w której umarli usłyszą głos Syna Bożego. Ci zaś którzy usłyszeli, żyć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '25', '25', 'Bo jak Ojciec ma życie w sobie, tak też dał Synowi, aby miał życie w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '26', '26', 'Dał Mu też władzę sądzenia, bo jest Synem Człowieczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '27', '27', 'Nie dziwcie się temu, bo zbliża się godzina, w której wszyscy spoczywający w grobach usłyszą Jego głos.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '28', '28', 'Wtedy ci, którzy dobrze czynili, wyjdą (z grobów). aby zmartwychwstać i żyć, a ci, którzy źle postępowali, aby zmartwychwstać (i iść) na potępienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '29', '29', 'Sam z siebie nic uczynić nie mogę. Osądzam według tego, co słyszę, a wyrok mój jest sprawiedliwy, bo nie szukam swojej woli, ale woli Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '30', '30', 'Jeśli Ja sam daję świadectwo o sobie, to moje świadectwo nie jest prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '31', '31', 'Jest ktoś inny, kto wydaje o Mnie świadectwo, i wiem, że świadectwo,  które On wydaje o Mnie, jest prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '32', '32', 'Wysłaliście do Jana poselstwo i on dał świadectwo prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '33', '33', 'Ja nie potrzebuję świadectwa od ludzi, ale mówię dlatego, abyście byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '34', '34', 'Jan był pochodnią, która płonie i jasno świeci, a wyście zechcieli zbawiać się jej światłem przez chwilę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '35', '35', 'Ja mam świadectwo większe od Janowego. Dzieła, które Ojciec polecił Mi wykonać, te dzieła, których dokonuję, świadczą, że to Ojciec Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '36', '36', 'To właśnie Ojciec, który Mnie posłał, daje świadectwo o Mnie. Wyście nigdy nie słyszeli Jego głosu i nie widzieliście Jego oblicza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '37', '37', 'I nie zachowujecie w sobie Jego słowa, bo nie wierzycie Temu, którego On posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '38', '38', 'Badacie Pisma, bo sądzicie, że one zawierają słowa żywota wiecznego. A one dają o Mnie świadectwo!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '39', '39', 'I nie chcecie przyjść do Mnie, aby mieć życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '40', '40', 'Nie zabiegam o chwałę u ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '41', '41', 'Lecz wiem, że nie ma w was miłości Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '42', '42', 'Ja przyszedłem w imię mojego Ojca, ale wy Mnie nie uznajecie. Gdyby ktoś inny przyszedł we własnym imieniu, to byście go uznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '43', '43', 'Jakże możecie uwierzyć, wy, którzy zabiegacie o chwałę jedni u drugich, a o chwałę, która pochodzi od samego Boga, nie dbacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '44', '44', 'Nie sądźcie, że to Ja będę was oskarżał przed Ojcem. Waszym oskarżycielem jest Mojżesz, w którym pokładacie nadzieję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '45', '45', 'Bo gdybyście wierzyli Mojżeszowi, wierzylibyście i Mnie, o którym on napisał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '4', '46', '46', 'Lecz jeśli jego pismom nie wierzycie, to jakżeż uwierzycie moim słowom?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '0', '0', 'Potem Jezus odszedł na drugą stronę Morza Galilejskiego, czyli Tyberiadzkiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '1', '1', 'I szedł za Nim wielki tłum, bo widzieli znaki, które czynił nad chorymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '2', '2', 'Jezus wszedł na górę i siedział tam z uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '3', '3', 'Zbliżała się Pascha, święto żydowskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '4', '4', 'Kiedy Jezus zobaczył, że wielki tłum idzie do Niego, mówi do Filipa: - Gdzie kupimy chleba, aby oni się najedli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '5', '5', 'A mówił to, wystawiając Filipa na próbę, sam bowiem wiedział, co ma uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '6', '6', 'Odpowiedział Mu Filip: - Za dwieście denarów chleba nie starczy, aby każdy dostał choć trochę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '7', '7', 'Mówi Mu jeden z uczniów, Andrzej, brat Szymona Piotra: - Jest tutaj chłopiec, który ma pięć chlebów jęczmiennych i dwie ryby. Lecz co to jest na tylu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '9', '9', 'Jezus powiedział: - Każcie ludziom usiąść. A było w tym miejscu dużo trawy. Siadło więc około pięciu tysięcy mężczyzn.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '10', '10', 'Jezus wziął chleby i odmówiwszy modlitwę dziękczynną rozdał siedzącym, podobnie (rozdał) ryby, ile kto chciał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '11', '11', 'A kiedy się najedli, mówi do uczniów: - Zbierzcie resztki, które pozostały, żeby nic się nie zmarnowało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '12', '12', 'Zebrali więc i napełnili dwanaście koszyków resztkami, które z pięciu chlebów jęczmiennych pozostawili ci, co jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '13', '13', 'A ludzie zobaczywszy znak, który Jezus uczynił, mówili: - To jest rzeczywiście prorok, który ma przyjść na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '14', '14', 'Jezus zaś widząc, że chcą przyjść i porwać Go, aby obwołać królem, sam jeden odszedł znowu na górę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '15', '15', 'Kiedy zapadł wieczór, uczniowie Jego zeszli nad morze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '16', '16', 'I wszedłszy do łodzi płynęli na drugą stronę morza, do Kafarnaum. Ciemność już zapadła, a Jezus jeszcze do nich nie przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '17', '17', 'Morze było wzburzone, bo wiał silny wiatr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '18', '18', 'Przepłynąwszy około dwudziestu pięciu lub trzydziestu stadiów, widzą Jezusa idącego po morzu i zbliżającego się do łodzi. I przestraszyli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '19', '19', 'On zaś mówił im: - Nie bójcie się, to Ja Jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '20', '20', 'Chcieli wziąć Go do łodzi, lecz łódź natychmiast znalazła się przy brzegu, do którego zdążali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '21', '21', 'Nazajutrz tłum stojący na brzegu morza zobaczył, że była tam tylko jedna łódka i że Jezus nie wsiadł z uczniami do łodzi, lecz sami uczniowie odpłynęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '22', '22', 'Tymczasem w pobliże tego miejsca, gdzie zjedli chleb po dziękczynnej modlitwie Pana, przypłynęły inne łodzie z Tyberiady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '23', '23', 'Kiedy więc tłumy zobaczyły, że nie ma ani Jezusa ani Jego uczniów, wsiadły do łódek, przybyły do Kafarnaum i szukały Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '24', '24', 'Znalazłszy Go za morzem, zapytali: - Rabbi, kiedyś tu przybył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '25', '25', 'A Jezus im odpowiedział: - Zaprawdę, zaprawdę powiadam wam: Szukaliście Mnie nie dlatego, żeście widzieli znaki, ale dlatego, żeście najedli się chlebem do syta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '26', '26', 'Nie starajcie się o pokarm przemijający, ale o pokarm pozostający na życie wieczne, który da wam Syn Człowieczy. Na Nim bowiem Ojciec, Bóg wycisnął swoją pieczęć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '27', '27', 'Rzekli do Niego: - Co mamy czynić. aby wykonać dzieło Boże?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '28', '28', 'Odpowiedział im Jezus: - Dzieło Boże polega na tym, abyście uwierzyli w Tego, którego On posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '29', '29', 'Powiedzieli Mu: - Jaki Ty nam pokażesz znak, żebyśmy go ujrzeli i uwierzyli Ci? Czego dokonasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '30', '30', 'Praojcowie nasi jedli mannę na pustyni, jak napisano: \'Dał im do jedzenia chleb z nieba\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '31', '31', 'Powiedział im Jezus: - Zaprawdę, zaprawdę powiadam wam: To nie Mojżesz dał wam chleb z nieba; to mój Ojciec daje wam prawdziwy chleb z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '32', '32', 'Bo chlebem Bożym jest Ten, który zstępuje z nieba i daje życie światu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '33', '33', 'Rzekli więc do Niego: - Panie, dawaj  nam zawsze tego chleba!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '34', '34', 'Powiedział im Jezus: - Ja jestem chlebem życia. Kto przychodzi do Mnie, nie będzie łaknął, kto wierzy we Mnie, nigdy nie będzie pragnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '35', '35', 'Lecz powiedziałem wam: Widzieliście Mnie, a nie wierzycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '36', '36', 'Wszystko, co Ojciec Mi daje, przyjdzie do Mnie, a Ja nie odrzucę tego, który do Mnie przychodzi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '37', '37', 'bo zstąpiłem z nieba nie po to, aby spełniać własną wolę, ale wolę Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '38', '38', 'A jest wolą Tego, który Mnie posłał, żebym nie stracił nic z tego wszystkiego, co Mi da, ale wskrzesił to w dniu ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '39', '39', 'To jest bowiem wolą Ojca mojego, aby każdy, kto widzi Syna i wierzy w Niego, miał życie wieczne; a Ja wskrzeszę w dniu ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '40', '40', 'Szemrali więc Judejczycy przeciwko Niemu, że powiedział: Ja jestem chlebem, który  zstąpił z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '41', '41', 'I mówili: - Czyż to nie jest Jezus, syn Józefa? Znamy Jego ojca i matkę. Jak On teraz może mówić: Zstąpiłem z nieba?!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '42', '42', 'Jezus im odpowedział: - Nie szemrajcie między sobą!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '43', '43', 'Nikt nie może przyjść do Mnie, jeżeli nie pociągnie go Ojciec, który Mnie posłał. A Ja go wskrzeszę w dniu ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '44', '44', 'Napisano u proroków: \'Wszyscy będą uczniami Bożymi\'. Każdy, kto usłyszał od Ojca (wezwanie) i zrozumiał (je). przychodzi do Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '45', '45', 'Nie (znaczy to), że ktoś widział Ojca, bo tylko Ten, który jest (posłany) od Boga, widział Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '46', '46', 'Zaprawdę, zaprawdę, powiadam wam: Kto wierzy, ma życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '47', '47', 'Ja jestem chlebem życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '48', '48', 'Praojcowie wasi jedli mannę na pustyni i pomarali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '49', '49', 'To jest chleb, który zstępuje z nieba, aby ten, co go spożywa, nie umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '50', '50', 'Ja jestem chlebem żywym, który zstąpił z nieba. Kto spożywa ten chleb, żyć będzie na wieki. A chlebem, który Ja dam, jest moje ciało za życie świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '51', '51', 'Sprzeczali się więc Judejczycy między sobą, mówiąc: - Jakże On może dać nam swoje ciało do jedzenia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '52', '52', 'Więc Jezus im powiedział: - Zaprawdę, zaprawdę, powiadam wam: Jeżeli nie będziecie spożywali ciała Syna Człowieczego i nie będziecie pili krwi Jego, nie będziecie mieli życia w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '53', '53', 'Kto spożywa moje ciało i pije moją krew, ma życie wieczne, a Ja go wskrzeszę w dniu ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '54', '54', 'Bo moje ciało jest prawdziwym pokarmem, a krew moja jest prawdziwym napojem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '55', '55', 'Kto spożywa moje ciało i pije moją krew, pozostaje we Mnie, a Ja w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '56', '56', 'Jak Mnie posłał żyjący Ojciec, i Ja żyję przez Ojca, tak i ten, kto spożywa moje ciało, żyć będzie przeze Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '57', '57', 'To jest chleb, który zstąpił z nieba. Nie jak ten, który jedli praojcowie i pomarli. Kto spożywa ten chleb, żyć będzie na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '58', '58', 'Powiedział to nauczając w synagodze w Kafarnaum.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '59', '59', 'Wielu Jego uczniów usłyszawszy to powiedziało: - Twarda jest ta mowa, któż jej słuchać może?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '60', '60', 'A Jezus, wiedząc, że uczniowie szemrają, rzekł im: - To was gorszy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '61', '61', 'A jeśli zobaczycie Syna Człowieczego wstępującego tam, gdzie był przedtem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '62', '62', 'Duch ożywia, ciało nie ma znaczenia. Słowa, które wam powiedziałem, są duchem i życiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '63', '63', 'Ale są wśród was tacy, co nie wierzą. Jezus bowiem od początku wiedział, którzy nie wierzyli; wiedział też, kto Go wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '64', '64', 'I mówił: - Dlatego powiedziałem wam: Nikt nie może przyjść do Mnie, jeśliby to nie było mu dane przez Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '65', '65', 'Odtąd wielu Jego uczniów odeszło i już z Nim nie chodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '66', '66', 'Rzekł więc Jezus do Dwunastu - Czy i wy chcecie odejść?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '67', '67', 'Odpowiedział Mu Szymon Piotr: - Panie, do kogo pójdziemy? Ty masz słowa życia wiecznego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '68', '68', 'a myśmy uwierzyli i poznali, że Ty jesteś Święty Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '69', '69', 'Odpowiedział mu Jezus: - Czyż nie wybrałem was Dwunastu? A jeden z was jest diabłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '5', '70', '70', 'A mówił to o Judaszu, synu Szymona z Kariotu. On bowiem miał Go wydać, a był jednym z Dwunastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '0', '0', 'Potem Jezus chodził po Galilei, po Judei zaś nie chciał chodzić, bo Judejczycy usiłowali Go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '1', '1', 'Zbliżało się żydowskie Święto Namiotów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '2', '2', 'Jego bracia rzekli więc do Niego: - Wyjdź stąd i idź do Judei, żeby i Twoi uczniowie widzieli dzieła, jakich dokonujesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '3', '3', 'Bo kto chce, żeby Go poznano, nie działa w ukryciu. Skoro takich dzieł dokonujesz, pokaż się światu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '4', '4', 'Ale Jego bracia nie wierzyli w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '5', '5', 'Mówi więc do nich Jezus: - Mój czas jeszcze nie nadszedł, a dla was pora zawsze jest stosowna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '6', '6', 'Was nie może świat nienawidzić, a Mnie nienawidzi, bo Ja daję świadectwo, że jego uczynki są złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '7', '7', 'Idźcie na święto, Ja nie idę na to święto, bo mój czas jeszcze nie nadszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '8', '8', 'Powiedziawszy to pozostał w Galilei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '9', '9', 'A kiedy Jego bracia poszli na święto, On także poszedł, lecz potajemnie, nie ujawniając się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '10', '10', 'Judejczycy więc szukali Go w święto i pytali: - Gdzie On jest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '11', '11', 'A w tłumie wiele się o Nim mówiło. Jedni mówili: Jest dobry, a drudzy mówili: Gdzież tam, zwodzi tłum!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '12', '12', 'Nikt jednak nie mówił o Nim jawnie z obawy przed Judejczykami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '13', '13', 'Już w połowie świąt Jezus wszedł do świątyni i nauczał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '14', '14', 'Dziwili się więc Judejczycy: - Skąd On zna Pisma, skoro się nie uczył?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '15', '15', 'A Jezus im odpowiedział: - Moja nauka nie jest moja, ale Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '16', '16', 'Kto chce spełniać Jego wolę, ten pozna, czy ta nauka pochodzi od Boga, czy też Ja mówię od siebie samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '17', '17', 'Kto mówi od siebie samego, szuka własnej chwały, a kto szuka chwały Tego, który Go posłał, ten mówi prawdę i nie zwodzi nikogo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '18', '18', 'Czyż to nie Mojżesz dał wam Prawo? A nikt z was nie postępuje według tego Prawa! Dlaczego chcecie Mnie zabić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '19', '19', 'Tłum odpowiedział: - Czart Cię opętał! Kto chce Cię zabić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '20', '20', 'Jezus im odpowiedział: - Jednego dzieła dokonałem i wszyscy się zdumiewacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '21', '21', 'Mojżesz dał wam obrzezanie - chociaż nie pochodzi ono od Mojżesza, ale od praojców - i obrzezujecie człowieka w szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '22', '22', 'Jeśli więc obrzezuje się człowieka w szabat, aby nie naruszać Prawa Mojżeszowego, to dlaczego oburzacie się na Mnie, że w szabat uzdrowiłem całkowicie człowieka?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '23', '23', 'Nie sądźcie z pozorów, ale sądźcie sprawiedliwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '24', '24', 'Mówili więc niektórzy mieszkańcy Jerozolimy: - Czy to Ten, którego chcą zabić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '25', '25', 'A oto przemawia jawnie i nic Mu nie mówią. Czyżby władze przekonały się, że to jest naprawdę Mesjasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '26', '26', 'Ale my wiemy, skąd On pochodzi. A kiedy Mesjasz przyjdzie, nikt nie będzie wiedział, skąd pochodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '27', '27', 'Jezus więc nauczając w świątyni głośno zawołał: - I Mnie znacie, i wiecie, skąd pochodzę. Ale sam od siebie nie przyszedłem. Lecz Ten, który Mnie posłał, a którego wy nie znacie, mówi prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '28', '28', 'Ja Go znam, bo od Niego pochodzę i On Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '29', '29', 'Chcieli więc Go pojmać, nikt Go jednak nie zatrzymał, bo nie nadeszła jeszcze Jego godzina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '30', '30', 'A z tłumu wielu uwierzyło w Niego. I mówili: - Czyż Mesjasz, kiedy przyjdzie, uczyni więcej znaków od Niego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '31', '31', 'Faryzeusze usłyszeli, że w tłumie tak o Nim mówiono. Arcykapłani i faryzusze wysłali służbę, aby Go pojmała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '32', '32', 'Jezus więc powiedział: - Jeszcze przez krótki czas jestem z wami, a potem idę do Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '33', '33', 'Będziecie Mnie szukali, a nie znajdziecie, bo tam gdzie Ja będę, wy przyjść nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '34', '34', 'Więc Judejczycy mówili między sobą: - Dokąd On chce pójść, że my Go nie znajdziemy? Czyżby chciał udać się do Żydów rozproszonych wśród Greków i Greków nauczać?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '35', '35', 'Co znaczą te słowa, które powiedział: Będziecie Mnie szukać i nie znajdziecie - i te: Tam, gdzie Ja będę, wy przyjść nie możecie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '36', '36', 'W ostatni wielki dzień święta Jezus stanął i głośno zawołał: - Jeśli kto jest spragniony, niech przyjdzie do Mnie i pije!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '37', '37', 'A z wnętrza tego, kto wierzy we Mnie - jak powiedziano w Piśmie - płyną rzeki żywej wody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '38', '38', 'Mówił to o Duchu, którego mieli otrzymać ci, co w Niego uwierzyli. A Ducha jeszcze nie było, bo Jezus nie był jeszcze uwielbiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '39', '39', 'Usłyszawszy te słowa jedni z tłumu mówili: - To rzeczywiście jest prorok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '40', '40', 'Inni mówili: - To jest Mesjasz. A jeszcze inni mówili: - Czyż Mesjasz przyjdzie z Galilei?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '41', '41', 'Czyż Pismo nie powiada, że Mesjasz przyjdzie z rodu Dawida i z miasteczka Betlejem, z którego pochodzi Dawid?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '42', '42', 'Tak z Jego powodu doszło w tłumie do rozłamu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '43', '43', 'Niektórzy z nich chcieli Go nawet pochwycić, lecz nikt Go nie zatrzymał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '44', '44', 'Przyszli więc słudzy do arcykapłanów i faryzeuszów. A oni zapytali ich: - Czemuście Go nie przyprowadzili?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '45', '45', 'Słudzy odpowiedzieli: - Żaden człowiek nigdy tak nie przemawiał!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '46', '46', 'Faryzeusze więc odpowiedzieli im: - Czy i wy daliście się zwieść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '47', '47', 'Czy ktoś z przełożonych albo z faryzeuszów uwierzył w Niego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '48', '48', 'A ten tłum, który nie zna Prawa, niech będzie przeklęty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '49', '49', 'Mówi do nich jeden z ich (grona), Nikodem, który już przedtem przyszedł do Niego: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '50', '50', 'Czy nasze Prawo potępia człowieka, zanim go przedtem nie wysłucha i nie rozpatrzy tego, co on czyni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '51', '51', 'Odpowiedzieli mu: - Czy ty jesteś z Galilei? Zbadaj (Pisma), a zobaczysz, że prorok nie przyjdzie z Galilei!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '6', '52', '52', 'I rozeszli się wszyscy do swoich domów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '0', '0', 'Jezus udał się na Górę Oliwną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '1', '1', 'O świcie zaś znowu był w świątyni i cały lub schodził się do Niego. I nauczał ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '2', '2', 'Nauczyciele Prawa i faryzeusze przyprowadzają kobietę pochwyconą na cudzołóstwie. I postawiwszy ją na środku');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '3', '3', 'mówią Mu: - Nauczycielu, tę kobietę pochwycono właśnie na cudzołóstwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '4', '4', 'Mojżesz nakazał nam w Prawie takie kamienować, a Ty co powiesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '5', '5', 'Mówili to podstępnie, aby mieli Go o co oskarżyć. A Jezus pochyliwszy się pisał coś palcem na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '6', '6', 'Gdy jednak pytali Go natarczywie, wyprostował się i rzekł im: - Kto z was jest bez grzechu, niech pierwszy rzuci w nią kamieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '7', '7', 'I pochyliwszy się znowu pisał na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '8', '8', 'Usłyszawszy to wychodzili jeden po drugim, od starszyzny począwszy. Pozostał tylko Jezus i na środku kobieta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '9', '9', 'Jezus wyprostował się i rzekł: - Gdzież oni są, kobieto? Nikt cię nie potępił? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '10', '10', 'Nikt, Panie - odpowiedziała. Rzekł jej Jezus: - I Ja ciebie nie potępiam. Idź i odtąd nie grzesz więcej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '11', '11', 'Jezus więc znowu przemówił do nich: - Ja jestem światłością świata. Kto idzie za Mną, nie będzie chodził w ciemności, lecz będzie miał światło życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '12', '12', 'Powiedzieli więc Mu faryzeusze: - Ty dajesz świadectwo sam o sobie, Twoje świadectwo nie jest prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '13', '13', 'A Jezus im odpowiedział: - Jeśli nawet Ja sam daję świadectwo o sobie, moje świadectwo jest prawdziwe, bo wiem, skąd przyszedłem i dokąd idę. Wy zaś nie wiecie, skąd przychodzę ani dokąd idę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '14', '14', 'Wy osądzacie po ludzku. Ja nikogo nie sądzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '15', '15', 'A kiedy już będę sądził, sąd mój będzie prawdziwy, bo nie jestem sam, ale (jest nas dwóch), Ja i Ojciec, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '16', '16', 'A i w Prawie waszym napisano, że świadectwo dwóch ludzi jest prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '17', '17', 'Ja świadczę o sobie, i Ojciec, który Mnie posłał, też świadczy o Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '18', '18', 'Zapytali Go więc: - Gdzie jest Twój Ojciec? Jezus odrzekł: - Ani Mnie nie znacie, ani mojego Ojca. Gdybyście Mnie znali, znalibyście też mojego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '19', '19', 'Te słowa powiedział przy skarbcu, nauczając w świątyni. I nikt Go nie pojmał, bo jeszcze nie nadeszła Jego godzina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '20', '20', 'Powiedział więc im znowu: - Ja odchodzę i będziecie Mnie szukać, i poumieracie w waszym grzechu. Wy nie możecie przyjść tam, dokąd Ja idę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '21', '21', 'Powiedzieli więc Judejczycy: - Czyżby chciał się zabić? Bo mówili: Wy nie możecie przyjść tam, gdzie Ja idę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '22', '22', 'I powiedział im: - Wy jesteście z dołu, a Ja z góry. Wy jesteście z tego świata, a Ja nie jestem z tego świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '23', '23', 'Powiedziałem wam więc, że pomrzecie w waszych grzechach, bo jeżeli nie uwierzycie, że JA JESTEM, pomrzecie w waszych grzechach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '24', '24', 'Zapytali Go więc: - Kimże Ty jesteś? Jezus im powiedział: - Jestem tym, o którym wam mówię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '25', '25', 'Mógłbym o was wiele powiedzieć i w wielu sprawach potępić. Ten, który mnie posłał, jest wiarogodny. Ja zaś mówię wobec świata to, co od Niego usłyszałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '26', '26', 'Nie poznali, że mówi im o Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '27', '27', 'Jezus więc powiedział: - Gdy podwyższycie Syna Człowieczego, wtedy poznacie, że JA JESTEM i że nic od siebie nie czynię, ale mówię to, czego Mnie Ojciec nauczył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '28', '28', 'A ten, który Mnie posłał, jest ze Mną. Nie zostawił Mnie samego, bo Ja zawsze czynię to, co się Jemu podoba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '29', '29', 'Kiedy to mówił, wielu uwierzyło w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '30', '30', 'A do Judejczyków, którzy Mu uwierzyli, Jezus mówił: - Jeżeli trwacie przy mojej nauce, jesteście naprawdę moimi uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '31', '31', 'I poznacie prawdę, a prawda was wyzwoli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '32', '32', 'Odpowiedzieli Mu: - Jesteśmy potomkami Abrahama i nigdy nie byliśmy niczyimi niewolnikami! Jakże możesz mówić, że będziemy wolni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '33', '33', 'Jezus im odpowiedział: - Zaprawdę, zaprawdę powiadam wam: Każdy, kto grzeszy, jest niewolnikiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '34', '34', 'a niewolnik nie pozostaje w domu na zawsze. Syn pozostaje na zawsze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '35', '35', 'Jeżeli więc Syn wyzwoli was, będziecie naprawdę wolni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '36', '36', 'Wiem, że jesteście potomkami Abrahama, ale chcecie Mnie zabić, bo słowa mojego nie przyjmujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '37', '37', 'Ja mówię to, co ujrzałem u Ojca, więc i wy czyńcie, coście usłyszeli od Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '38', '38', 'Odpowiedzieli Mu: - Naszym ojcem jest Abraham. Mówi im Jezus: - Gdybyście byli dziećmi Abrahama, wypełnialibyście dzieło Abrahama.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '39', '39', 'A tymczasem wy chcecie zabić Mnie, człowieka, który wam powiedział prawdę usłyszaną od Boga. Abraham tego nie uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '40', '40', 'Postępujcie więc tak, jak wasz ojciec. Powiedzieli Mu: - My nie przyszliśmy na świat z nierządu. Mamy jednego Ojca - Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '41', '41', 'Powiedział im Jezus: - Gdyby Bóg był waszym Ojcem, kochalibyście Mnie, bo ja wyszedłem i przychodzę od Boga. Nie przyszedłem od siebie, ale On mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '42', '42', 'Dlaczego nie przyjmujecie mojej mowy? Bo nie potraficie słuchać mojego słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '43', '43', 'Diabeł jest waszym ojcem i chcecie spełniać jego życzenia. On był od początku zabójcą i nie wytrwał w prawdzie, bo nie ma w nim prawdy. Kiedy kłamie, mówi ze swego, bo jest kłamcą i ojcem kłamstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '44', '44', 'A wy Mi nie wierzycie, bo Ja mówię prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '45', '45', 'Któż z was udowodni Mi, że grzeszę? A jeśli mówię prawdę, dlaczego Mi nie wierzycie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '46', '46', 'Kto jest z Boga, słucha słów Bożych. Wy dlatego nie słuchacie, że nie jesteście z Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '47', '47', 'Odpowiedzieli Mu Judejczycy: - Czy nie mam racji, żeś Samarytanin i opętany przez czarta?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '48', '48', 'Jezus odrzekł: - Ja nie jestem opętany, ale czczę mojego Ojca, a wy Mnie znieważacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '49', '49', 'Ja nie szukam swojej  chwały. Jest ktoś, kto szuka i sądzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '50', '50', 'Zaprawdę, zaprawdę powiadam wam: Jeśli kto zachowa moją naukę, nigdy nie umrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '51', '51', 'Powiedzieli Mu Judejczycy: - Teraz widzimy, żeś opętany. Abraham umarł i prorocy umarli, a Ty powiadasz: Jeśli kto zachowa moją naukę, nigdy nie umrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '52', '52', 'Czyś Ty (jest) większy od naszego ojca Abrahama, który umarł? I prorocy poumierali! Za kogo się uważasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '53', '53', 'Jezus odrzekł: - Gdybym Ja chwalił samego siebie, to moja chwała byłaby niczym. Tym jednak, który Mnie chwali, jest mój Ojciec, o którym wy mówicie: On jest naszym Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '54', '54', 'Ale nie poznaliście Go. A Ja Go znam! I gdybym powiedział: Nie znam Go, byłbym podobnym do was kłamcą. Ale Ja Go znam i Jego naukę zachowuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '55', '55', 'Ojciec wasz, Abraham, cieszył się, że ujrzy mój dzień. I ujrzał, i uradował się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '56', '56', 'Judejczycy więc rzekli do Niego: - Nie masz jeszcze pięćdziesięciu lat, a Abrahama widziałeś?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '57', '57', 'Powiedział im Jezus: - Zaprawdę, zaprawdę powiadam wam: Zanim Abraham się narodził, JAM JEST!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '7', '58', '58', 'Porwali więc kamienie, aby rzucić na Niego, ale Jezus ukrył się i wyszedł ze świątyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '0', '0', 'Przechodząc ujrzał człowieka ślepego od urodzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '1', '1', 'I zapytali Go uczniowie: - Rabbi, kto zgrzeszył, że się narodził ślepy, on czy jego rodzice?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '2', '2', 'Jezus odrzekł: - Ani on nie zgrzeszył, ani jego rodzice, lecz (urodził się ślepy), aby przez niego objawiły się dzieła Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '3', '3', 'Musimy dokonać dzieła Tego, który Mnie posłał, dopóki jest dzień. Nadchodzi noc, a wtedy nikt nie może działać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '4', '4', 'jak długo jestem na świecie, jestem światłem świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '5', '5', 'To powiedziawszy spunąl na ziemię; zrobił błoto ze śliny, pomazał mu oczy błotem');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '6', '6', 'i rzekł: - Idź, obmyj się w sadzawce Siloam - to znaczy: posłany. Poszedł więc, obmył się i wrócił widząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '7', '7', 'A sąsiedzi i ci, którzy dawniej widywali go żebrzącego, pytali się: - Czy to ten, co siedział i żebrał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '8', '8', 'Jedni mówili: To ten, a drudzy: Ależ nie, jest tylko podobny do tamtego. On sam zaś mówił: - Tak, to ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '9', '9', 'Pytali go więc: - Ja to się stało, że widzisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '10', '10', 'Odpowiedział: - Ten człowiek, którego nazywają Jezusem, zrobił błoto, pomazał moje oczy i rzekł mi: Idź do Siloam i obmyj się. Poszedłem więc i obmyłem się, i widzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '11', '11', 'Zapytali go: - Gdzie On jest? Powiada: - Nie wiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '12', '12', 'Prowadzą tego (człowieka), niedawno jeszcze niewidomego, do faryzeuszów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '13', '13', 'A w dniu, w którym Jezus zrobił błoto i otworzył mu oczy, był szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '14', '14', 'Faryzeusze więc wypytywali go znowu, jak przejrzał. On zaś im powiedział: - Położył mi błoto na oczy i obmyłem się i widzę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '15', '15', 'Niektórzy faryzeusze powiedzieli więc: - Ten człowiek nie jest od Boga, bo nie przestrzega szabatu! Ale inni mówili: - Jakżeż człowiek grzeszny może czynić takie znaki? I doszło między nimi do rozłamu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '16', '16', 'Mówią więc znowu ślepemu: - Co ty sądzisz o Nim, jako że ci otworzył oczy? A on powiedział: - To prorok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '17', '17', 'Ale Judejczycy nie uwierzyli, że on był ślepy i że przejrzał, dopóki nie przywołali rodziców tego, co przejrzał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '18', '18', 'I zapytali ich: - Czy to jest ten wasz syn, o którym mówicie, że urodził się ślepy? Jak więc (to się stało, że) widzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '19', '19', 'A rodzice jego odpowiedzieli: - Wiemy, że to jest nasz syn i że urodził się ślepy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '20', '20', 'Lecz jak (to się stało, że) teraz widzi, tego nie wiemy. Nie wiemy też, kto otworzył mu oczy. Spytajcie go. Ma swoje lata, sam powie o sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '21', '21', 'Rodzice powiedzieli to z obawy przed Judejczykami, bo Judejczycy już postanowili wyłączyć z synagogi tego, kto uzna Jezusa za Mesjasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '22', '22', 'Dlatego rodzice jego powiedzieli: Ma swoje lata, spytajcie go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '23', '23', 'Przywołali więc po raz drugi człowieka, który był ślepy, i powiedzieli mu: - Powiedz prawdę! (Bo) my wiemy, że Ten człowiek jest grzesznikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '24', '24', 'Więc on odpowiedział: - Nie wiem, czy On jest grzesznikiem, jedno wiem, że byłem ślepy, a teraz widzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '25', '25', 'Więc mu powiedzieli: - Co z tobą zrobił? Jak otworzył ci oczy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '26', '26', 'Odrzekł im: - Już wam powiedziałem, czyżbyście nie słyszeli? Co jeszcze chcecie słyszeć? Czy i wy chcielibyście zostać Jego uczniami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '27', '27', 'Nawymyślali mu i powiedzieli: - Ty bądź Jego uczniem, a my jesteśmy uczniami Mojżesza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '28', '28', 'My wiemy, że Bóg przemówił do Mojżesza, a skąd Ten jest, nie wiemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '29', '29', 'A człowiek odpowiedział: - To bardzo dziwne: Wy nie wiecie skąd On jest, a otworzył mi oczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '30', '30', 'Przecież wiemy, że Bóg nie wysłuchuje grzeszników, ale tego, kto czci Boga i spełnia Jego wolę, wysłuchuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '31', '31', 'Jak świat światem nie słyszano, aby ktoś przywrócił wzrok ślepemu od urodzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '32', '32', 'Gdyby On nie był od Boga, nie mógłby nic uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '33', '33', 'Odpowiedzieli mu: - Narodziłeś się pełen grzechów i nas śmiesz pouczać?! I wyrzucili go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '34', '34', 'Jezus dowiedział się, że go wyrzucili, a spotkawszy go rzekł: - Czy ty wierzysz w Syna Człowieczego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '35', '35', 'A on odpowiedział: - Kto to jest, Panie, że miałbym w Niego wierzyć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '36', '36', 'Powiedział mu Jezus: - Widzisz Go, to Ten, który z tobą mówi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '37', '37', 'On zaś rzekł: - Wierzę, Panie. I oddał Mu pokłon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '38', '38', 'A Jezus powiedział: - Ja na ten świat przyszedłem na sąd, aby ci, którzy nie widzą, widzieli, a ci, którzy widzą, stali się ślepcami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '39', '39', 'Usłyszawszy to faryzeusze, którzy z Nim byli, i zapytali Go: - Czy my także jesteśmy ślepi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '8', '40', '40', 'Powiedział im Jezus: - Gdybyście byli ślepi, nie mielibyście grzechu. Lecz teraz mówicie: Widzimy! - trwacie w grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '0', '0', '- Zaprawdę, zaprawdę powiadam wam: Kto nie wchodzi do owczarni przez bramę, ale dostaje się inną drogą, jest złodziejem i rozbójnikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '1', '1', 'Kto zaś wchodzi przez bramę, jest pasterzem owiec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '2', '2', 'Jemu odźwierny otwiera i owce słuchają jego głosu. On zaś woła swoje owce po imieniu i wyprowadza je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '3', '3', 'A kiedy wyprowadzi wszystkie swoje owce, idzie przed nimi, a owce idą za nim, bo znają jego głos.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '4', '4', 'A za obcym nie pójdą, lecz będą uciekały od niego, bo nie znają głosu obcych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '5', '5', 'Jezus powiedział im tę przypowieść, lecz oni nie zrozumieli, o czym im mówi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '6', '6', 'Jezus więc powiedział znowu: - Zaprawdę, zaprawdę powiadam wam: Ja jestem bramą do owiec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '7', '7', 'Wszyscy, którzy przyszli przede Mną, to złodzieje i rozbójnicy. Lecz owce ich nie posłuchały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '8', '8', 'Ja jestem bramą, kto wejdzie przeze Mnie, będzie zbawiony: będzie przebywał ze Mną i znajdzie pastwisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '9', '9', 'Złodziej przychodzi tylko po to, aby kraść, zabijać i niszczyć. Ja przyszedłem, aby (owce) miały życie, i to w całej pełni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '10', '10', 'Ja jestem dobrym pasterzem. Dobry pasterz oddaje życie swoje za owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '11', '11', 'A najemnik, który nie jest pasterzem i do którego owce nie należą, widząc skradającego się wilka, porzuca owce i ucieka, a wilk porywa je i rozprasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '12', '12', '(Najemnik) nie troszczy się o owce, bo jest najemnikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '13', '13', 'Ja jestem dobrym pasterzem. Znam moje owce, a moje owce znają Mnie tak,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '14', '14', 'jak Mnie zna Ojciec, a Ja znam Ojca. I moje życie oddaję za owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '15', '15', 'Mam też inne owce, które nie są z tej owczarni, muszę je też przyprowadzić. I będą słuchać mojego głosu, i powstanie jedna owczarnia i jeden pasterz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '16', '16', 'Ojciec dlatego Mnie miłuje, że Ja oddaję moje życie, aby je znowu otrzymać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '17', '17', 'Nikt Mi go nie odbiera, lecz Ja sam oddaję je z własnej woli. W mojej mocy jest oddać życie i w mojej mocy otrzymać je znowu. Taki rozkaz otrzymałem od Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '18', '18', 'Wśród Judejczyków doszło do rozłamu z powodu tych słów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '19', '19', 'Wielu z nich mówiło: - Jest opętany i mówi od rzeczy. Czemu Go słuchacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '20', '20', 'Inni mówili: - To nie są słowa opętanego, czy czart może otwierać ślepym oczy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '21', '21', 'W Jerozolimie obchodzono wtedy uroczystość Poświęcenia Świątyni. Była zima.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '22', '22', 'Jezus przechadzał się po świątyni w krużganku Salomona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '23', '23', 'Otoczyli Go Judejczycy i zapytali: - Jak długo będziesz nas trzymał w niepewności? Odpowiedz nam jasno: Czy Ty jesteś Mesjaszem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '24', '24', 'Jezus im odrzekł: - Powiedziałem wam, lecz wy nie wierzycie. Dzieła, których dokonuje w imieniu mojego Ojca, świadczą o Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '25', '25', 'Ale wy nie wierzycie, bo nie należycie do moich owiec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '26', '26', 'Moje owce słuchają głosu mojego. Ja znam je, a one idą za Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '27', '27', 'Ja daję im życie wieczne i one nie zginą na wieki, i nikt nie wyrwie ich z mojej ręki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '28', '28', 'Mój Ojciec, który Mi je dał, jest większy od wszystkich i nikt nie może wyrwać (ich) z ręki mojego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '29', '29', 'Ja i Ojciec jedno jesteśmy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '30', '30', 'Judejczycy znowu chwycili kamienie, aby Go ukamienować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '31', '31', 'Jezus im powiedział: - Pokazałem wam wiele dobrych dzieł (pochodzących) od Ojca, za które z nich chcecie Mnie ukamienować?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '32', '32', 'Judejczycy odrzekli: - Nie kamienujemy Cię za dobre dzieła, ale za bluźnierstwo, że będąc człowiekiem czynisz się Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '33', '33', 'Odrzekł im Jezus: - Czyż w waszym Prawie nie napisano: \'Rzekłem: Bogami jesteście\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '34', '34', 'Pismo więc nazwało bogami tych, do których skierowane było słowo Boże - a Pisma nie można zmnienić -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '35', '35', 'wy zaś do Mnie, którego Ojciec uświęcił i posłał na świat, mówicie: Bluźnisz, bo powiedziałem: Jestem Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '36', '36', 'Jeżeli więc nie dokonuję dzieł mojego Ojca, nie wierzcie Mi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '37', '37', 'Ale jeżeli ich dokonuję, to choćbyście Mi nie wierzyli, wierzcie dziełom i przekonajcie się, że Ojciec jest we Mnie, a Ja w Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '38', '38', 'I znowu chcieli Go pochwycić. Ale im uszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '39', '39', 'I odszedł znowu za Jordan, na miejsce, gdzie dawniej Jan chrzcił. Tam pozostał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '40', '40', 'A wielu przychodziło do Niego. I mówili: - Jan nie uczynił żadnego znaku, ale wszystko, co Jan o Nim powiedział, było prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '9', '41', '41', 'I tam wielu uwierzyło w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '0', '0', 'Chorował Łazarz z Betanii, miasteczka Marii i jej siostry Marty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '1', '1', 'Była to ta Maria, która skropiła Pana olejkiem i wytarła Jego nogi swoimi włosami. Właśnie jej brat Łazarz chorował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '2', '2', 'Siostry więc przesłały Jezusowi wiadomość: - Panie, ten, którego miłujesz, jest chory.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '3', '3', 'Usłyszawszy to Jezus powiedział: - Ta choroba nie skończy się śmiercią, ale przyniesie chwałę Bogu. Dzięki niej Syn Boży będzie uwielbiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '4', '4', 'Jezus miłował Martę, jej siostrę i brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '5', '5', 'Ale chociaż usłyszał, że Łazarz choruje, pozostał dwa dni tam, gdzie był.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '6', '6', 'Dopiero potem mówi uczniom: - Idźmy do Judei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '7', '7', 'Mówią Mu uczniowie: - Rabbi, dopiero co Judejczycy chcieli Cię ukamienować i znowu tam idziesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '8', '8', 'Jezus odrzekł: - Czyż dzień nie ma dwunastu godzin? Kto chodzi za dnia, nie potknie się, bo widzi światło słoneczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '9', '9', 'Kto zaś chodzi w nocy, potyka się, bo nie ma światła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '10', '10', 'To rzekł. A potem mówi im: - Nasz przyjaciel Łazarz zasnął, ale idę go obudzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '11', '11', 'Powiedzieli Mu uczniowie: - Panie, jeżeli zasnął, wyzdrowieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '12', '12', 'Jezus mówił o jego śmierci, im zaś się wydawało, że mówi o zwykłym zaśnięciu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '13', '13', 'Wtedy więc Jezus powiedział im otwarcie: - Łazarz umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '14', '14', 'I cieszę się, że nas tam nie było. A to ze względu na was, abyście uwierzyli. Ale chodźmy do niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '15', '15', 'A Tomasz, zwany Bliźniakiem, powiedział do współuczniów: - I my chodźmy, aby umrzeć z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '16', '16', 'Kiedy Jezus przyszedł, zastał (Łazarza) leżącego od czterech dni w grobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '17', '17', 'Betania była blisko Jerozolimy, około piętnastu stadiów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '18', '18', 'Wielu Judejczyków przybyło do Marty i Marii pocieszając je po śmierci brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '19', '19', 'Kiedy więc Marta usłyszała, że Jezus Nadchodzi, wyszła Mu naprzeciw. A Maria siedziała w domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '20', '20', 'Marta więc rzekła do Jezusa: - Panie, gdybyś tu był, mój brat by nie umarł,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '21', '21', 'ale nawet teraz wiem, że Bóg da Ci wszystko, o co Go poprosisz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '22', '22', 'Mówi jej Jezus: - Brat twój zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '23', '23', 'Mówi Mu Marta: - Wiem, że powstanie z martwych przy zmartwychwstaniu w dniu ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '24', '24', 'Powiedział jej Jezus: - Ja jestem zmartwychwstaniem i życiem! Kto wierzy we Mnie, chociażby nawet umarł, żyć będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '25', '25', 'A każdy, kto żyje i wierzy we Mnie, nigdy nie umrze. Czy wierzysz w to?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '26', '26', 'Mówi Mu: - Tak, Panie. Ja uwierzyłam, że Ty jesteś Mesjaszem, Synem Bożym, który ma przyjść na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '27', '27', 'Po tych słowach poszła i po cichu wezwała swoją siostrę Marię, mówiąc: - Jest Nauczyciel i woła cię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '28', '28', 'Kiedy (Maria) to usłyszała, wstała szybko i poszła do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '29', '29', 'Jezus jeszcze nie wszedł do miasteczka, ale stał w miejscu, gdzie Marta zabiegła Mu drogę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '30', '30', 'A Judejczycy, którzy byli z Marią w domu i pocieszali ją, widząc, że szybko wstała i wyszła, poszli za nią przekonani, że idzie płakać do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '31', '31', 'Kiedy więc Maria doszła do Jezusa i ujrzała Go, padła Mu do nóg, mówiąc: - Panie, gdybyś tu był, mój brat by nie umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '32', '32', 'Kiedy Jezus zobaczył ją zapłakaną i płaczących Judejczyków, którzy z nią przyszli, wzruszył się głęboko i wstrząśnięty');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '33', '33', 'zapytał: - Gdzieście go pochowali? Mówią Mu: - Chodź, Panie, i zobacz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '34', '34', 'Jezus zapłakał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '35', '35', 'Mówili więc Judejczycy: - Jak On go miłował!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '36', '36', 'A niektórzy z nich powiedzieli: - Czy Ten, który otworzył oczy ślepemu, nie mógł sprawić, aby i ten nie umarł?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '37', '37', 'Jezus więc ponownie wstrząśnięty przychodzi do grobu. Była to jaskinia, a zamykał ją kamień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '38', '38', 'Mówi Jezus: - Odsuńcie kamień. Mówi Mu siostra zmarłego, Marta: - Panie, już cuchnie, bo od czterech dni tu leży!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '39', '39', 'Mówi jej Jezus: - Czyż ci nie powiedziałem, że jeżeli uwierzysz, ujrzysz chwałę Bożą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '41', '41', 'Odsunęli więc kamień, a Jezus podniósł oczy w górę i rzekł: - Ojcze, dziękuję Ci, żeś Mnie wysłuchał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '42', '42', 'Po tych słowach zawołał donośnym głosem: - Łazarzu, wyjdź z grobu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '43', '43', 'Umarły wyszedł z nogami i rękami powiązanymi taśmami, a twarz jego była owinięta chustą. Mówi im Jezus: - Rozwiążcie go i pozwólcie mu odejść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '44', '44', 'Wielu więc z tych Judejczyków, którzy przyszli do Marii i ujrzeli, co Jezus uczynił, uwierzyło w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '45', '45', 'A niektórzy z nich poszli do faryzeuszów i opowiedzieli im, co Jezus uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '46', '46', 'Arcykapłani i faryzeusze zwołali więc najwyższą radę i mówili: - Cóż mamy począć, bo ten człowiek czyni wiele znaków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '47', '47', 'Jeśli Mu na to pozwolimy, wszyscy uwierzą w Niego. I przyjdą Rzymianie, zniszczą nasze miejsce święte i naród.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '48', '48', 'A jeden z nich, Kajfasz, który tego roku był najwyższym kapłanem powiedział im: - Niczego nie rozumiecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '49', '49', 'Nie myślicie też o tym, że lepiej dla nas, aby jeden człowiek umarł za naród, niżby cały naród miał zginąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '50', '50', 'Tego jednak nie powiedział sam z siebie, ale będąc tego roku najwyższym kapłanem wypowiedział proroctwo, że Jezus miał umrzeć za naród,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '51', '51', 'nie tylko za naród, lecz także, aby zebrać razem rozproszone dzieci Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '52', '52', 'Tego więc dnia postanowili Go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '53', '53', 'Jezus więc już nie nauczał jawnie wśród Judejczyków, ale odszedł stamtąd w okolicę sąsiadującą z pustynią, do miasta o nazwie Efraim. I przebywał tam z uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '54', '54', 'Zbliżała  się Pascha żydowska. A przed Paschą wielu z tej okolicy udało się do Jerozolimy, aby odbyć rytualne oczyszczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '55', '55', 'Szukali więc Jezusa i stojąc w świątyni mówili między sobą: - Jak myślicie? Czy nie przyjdzie na święto?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '10', '56', '56', 'Najwyżsi kapłani zaś i faryzeusze wydali nakaz, aby każdy, kto wie, gdzie On jest doniósł im, aby mogli Go pojmać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '0', '0', 'Sześć dni przed Paschą Jezus przyszedł do Betani, gdzie mieszkał Łazarz, którego Jezus wskrzesił z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '1', '1', 'Przygotowali tam dla Niego przyjęcie: Marta usługiwała, Łazarz zaś był jednym z biesiadników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '2', '2', 'A Maria wzięła funt czystego kosztownego olejku nardowego, wylała na Jego nogi i wytarła je swoimi włosami. I po całym domu rozszedł się zapach olejku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '3', '3', 'A jeden z Jego uczniów, Judasz Iskariota, który miał Go wydać, mówi: -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '4', '4', 'Czemu nie sprzedano tego olejku za trzysta denarów i nie rozdano ich ubogim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '5', '5', 'A powiedział to nie dlatego, że troszczył się o ubogich, ale dlatego, że był złodziejem i mając trzos, podkradał z tego, co doń wkładano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '6', '6', 'Powiedział więc Jezus: - Daj jej spokój. Zachowała ten olejek na dzień mojego pogrzebu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '7', '7', 'Ubogich zawsze macie między sobą, Mnie zaś nie zawsze macie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '8', '8', 'Wielki tłum Judejczyków dowiedział się, że tam jest, i przyszli nie tylko dla samego Jezusa, lecz także aby zobaczyć Łazarza, którego wskrzesił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '9', '9', 'A najwyżsi kapłani postanowili zabić również Łazarza,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '10', '10', 'bo przez niego wielu odłączyło się od Judejczyków i uwierzyło w Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '11', '11', 'Nazajutrz wielkie tłumy, które przyszły na święta, usłyszawszy, że Jezus przychodzi do Jerozolimy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '12', '12', 'wzięły gałęzie palmowe, wyszły Mu naprzeciw i wołały: - \'Hosanna, błogosławiony, który przychodzi w imię Pana\' i: Król Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '13', '13', 'A Jezus znalazłszy osiołka wsiadł na niego zgodnie z tym, co napisano:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '14', '14', '\' Nie bój się, córko syjońska! Oto twój król przybywa siedząc na oślęciu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '15', '15', 'Z początku uczniowie Jego tego nie zrozumieli. Kiedy jednak Jezus został uwielbiony, wtedy przypomnieli sobie, że ludzie postąpili, jak było o Nim napisane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '16', '16', 'Tłum zatem, który był z Nim, kiedy wywołał z grobu i wskrzesił Łazarza, dawał świadectwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '17', '17', 'I dlatego tłum wyszedł mu naprzeciw, bo usłyszał, że On dokonał tego znaku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '18', '18', 'A faryzeusze mówili sobie: - Patrzcie, nic nie osiągnęliście, oto ludzie poszli za Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '19', '19', 'A wśród tych, którzy przybyli pokłonić się Bogu w święto, byli jacyś Grecy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '20', '20', 'Oni to podeszli do Filipa z Betsaidy Galilejskiej i poprosili go: - Panie, chcemy zobaczyć Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '21', '21', 'Przychodzi Filip i mówi to Andrzejowi. Andrzej z Filipem przychodzą i mówią o tym Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '22', '22', 'A Jezus odpowiedział im: - Nadeszła godzina, aby został uwielbiony Syn Człowieczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '24', '24', 'Kto kocha swoje życie, straci je, a kto nie ceni swego życia na tym świecie, zachowa je dla życia wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '25', '25', 'Kto chce Mi służyć, niech idzie za Mną, a gdzie Ja jestem, tam też będzie mój sługa. A mój Ojciec uczci tego, który Mi służy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '26', '26', 'Teraz lęk ogarnął moją duszę. I czyż mam powiedzieć: Ojcze, uwolnij Mnie od tej godziny? Ale po to przyszedłem, dla tej właśnie godziny!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '27', '27', 'Ojcze, uwielbij imię Twoje! A z nieba odezwał się głos: - Już uwielbiłem i znowu uwielbię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '28', '28', 'Tłum, który to usłyszał, mówił, że zagrzmiało. Inni zaś mówili: Anioł przemówił do Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '29', '29', 'A Jezus odpowiedział: - Ten głos odezwał się nie dla Mnie, ale dla was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '30', '30', 'Teraz odbywa się sąd nad tym światem. Teraz władca tego świata zostanie wyrzucony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '31', '31', 'Ja zaś, kiedy będę podniesionny nad ziemię, wszystkich przyciągnę do siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '32', '32', 'A mówił to, aby zaznaczyć, jaką śmiercią miał umrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '33', '33', 'Zaprawdę, zaprawdę powiadam wam. Jeśli ziarno pszenicy nie zostanie wrzucone w ziemię i nie umrze, zostanie samo, jeśli zaś umrze, przyniesie obfity plon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '34', '34', 'Powiedział im Jezus: - Jeszcze przez krótki czas macie światłość. Chodźcie, dopóki macie światłość, aby was ciemność nie ogarnęła. Kto chodzi w ciemności, nie wie, dokąd idzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '35', '35', 'Gdy macie światłość, wierzcie w światłość, abyście stali się synami światłości. Jezus powiedział to i ukrył się przed nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '36', '36', 'A chociaż uczynił na ich oczach tak wiele znaków, nie wierzyli w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '37', '37', 'I spełniły się słowa proroka Izajasza, który rzekł: \'Panie, któż uwierzył temu, cośmy usłyszeli, i ramię Pańskie komu się objawiło:\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '38', '38', 'A nie mogli uwierzyć, bo Izajasz powiedział znowu:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '39', '39', '\'Zaślepił ich oczy i zatwardził serce, żeby nie widzieli oczami, i nie zrozumieli sercem, i nie nawrócili się, i żebym ich nie uzdrowił\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '40', '40', 'Izajasz to rzekł, bo ujrzał chwałę Jezusa i właśnie o Nim powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '41', '41', 'A jednak i wielu z przełożonych uwierzyło w Niego, ale z obawy przed faryzeuszami nie wyznawali tego, aby ich nie wyłączono z synagogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '42', '42', 'Bo umiłowali bardziej chwałę u ludzi aniżeli chwałę Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '43', '43', 'Jezus zaś głośno zawołał: - Kto wierzy we Mnie, ten wierzy nie we Mnie, lecz w Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '44', '44', 'A kto Mnie widzi, widzi i Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '45', '45', 'Ja, światłość, przyszedłem na świat, aby nikt z wierzących we Mnie nie pozostawał w ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '46', '46', 'A jeśli ktoś usłyszy moją naukę, a nie będzie jej przestrzegał, to Ja go nie potępię, bo nie przyszedłem, aby świat potępić, lecz aby go zbawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '47', '47', 'A kto Mną gardzi i mojej nauki nie przyjmuje, ten ma swego sędziego: Nauka, którą głosiłem, osądzi go w dniu ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '48', '48', 'Bo Ja nie mówiłem sam z siebie, ale Ojciec, który Mnie posłał, nakazał Mi, co mam mówić i głosić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '11', '49', '49', 'A wiem, że Jego nakaz - to życie wieczne. To więc, co mówię, mówię tak, jak Mi powiedział Ojciec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '0', '0', 'Przed Świętem Paschy Jezus wiedząc, że nadeszła już godzina Jego przejścia z tego świata do Ojca, umiłowawszy swoich na tym świecie, okazał im największą miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '1', '1', 'W czasie wieczerzy, kiedy diabeł nakłonił już Judasza Iskariotę, syna Szymona, aby Go wydał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '2', '2', 'Jezus wiedząc, że Ojciec wszystko złożył w Jego ręce i że od Boga wyszedł i do Boga odchodzi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '3', '3', 'wstał od stołu, zdjął wierzchnie okrycie, wziął ręcznik i przepasał się nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '4', '4', 'Potem nalał wody do miednicy i zaczął myć nogi uczniom i wycierać ręcznikiem, którym był przepasany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '5', '5', 'Przychodzi więc do Szymona Piotra. (Piotr) mówi Mu: - Panie, Ty mnie myjesz nogi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '6', '6', 'A Jezus mu odpowiedział: - Ty teraz tego nie rozumiesz, co Ja robię, ale potem zrozumiesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '7', '7', 'Mówi Mu Piotr: - Nie, nigdy nie będziesz umywał mi nóg. Jezus mu odrzekł: - Jeśli ciebie nie umyję, nie będziesz miał więzi ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '8', '8', 'Mówi Mu Szymon Piotr: - Panie, (myj) nie tylko nogi, ale i ręce i głowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '9', '9', 'Mówi mu Jezus: - Temu, kto się wykąpał, wystarczy umyć nogi, bo cały jest czysty. I wy jesteście czyści, ale nie wszyscy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '10', '10', 'Wiedział bowiem, kto Go wyda, i dlatego powiedział: Nie wszyscy jesteście czyści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '11', '11', 'Kiedy więc umył im nogi, włożył okrycie, usiadł znowu przy stole i powiedział: - Czy wiecie, co uczyniłem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '12', '12', 'Wy nazywacie Mnie Nauczycielem i Panem i dobrze mówicie, bo jestem nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '13', '13', 'Jeżeli więc Ja, Pan i Nauczyciel, umyłem wam nogi, to i wy powinniście umywać sobie nogi nawzajem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '14', '14', 'Dałem wam przykład, abyście tak czynili, jak Ja wam uczyniłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '15', '15', 'Zaprawdę, zaprawdę powiadam wam: Sługa nie jest większy od pana ani posłaniec większy  od tego, który go posyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '16', '16', 'Czy wy to rozumiecie? A jeśli tak będziecie czynić, szczęśliwi będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '17', '17', 'Nie mówię o was wszystkich. Ja wiem, kogo wybrałem, lecz Pismo musi się spełnić: \'Kto jadał mój chleb, kopnął mnie nogą\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '18', '18', 'Mówię wam o tym teraz, zanim to nastąpi, abyście gdy to nastąpi, uwierzyli, że JA JESTEM.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '19', '19', 'Zaprawdę, zaprawdę powiadam wam: Kto przyjmuje tego, kogo Ja posyłam, Mnie przyjmuje. A kto Mnie przyjmuje, przyjmuje Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '20', '20', 'Po tych słowach Jezus wzruszył się głęboko i oświadczył: - Zaprawdę, zaprawdę powiadam wam, że jeden z was Mnie wyda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '21', '21', 'Uczniowie, nie wiedząc, o kim mówi, spoglądali jeden na drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '22', '22', 'A jeden z uczniów, ten którego Jezus miłował, siedział przy boku Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '23', '23', 'Więc Szymon Piotr dał mu znak, aby się dowiedział, kto to jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '24', '24', 'Ten oparł głowę na piersi Jezusa i zapytał: - Panie, kto to?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '25', '25', 'Jezus odpowiada: - Ten, któremu podam kawałek (chleba) umoczony (w sosie). Umoczywszy więc kawałek podaje Judaszowi, synowi Szymona Iskarioty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '26', '26', 'A po zjedzeniu tego kawałka szatan opanował Judasza. Jezus mu mówił: - Rób szybko, co zamierzasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '27', '27', 'Nikt jednak z siedzących przy stole nie rozumiał, po co mu to mówił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '28', '28', 'A że Judasz miał trzos, niektórzy myśleli, że Jezus mówi do niego: Zrób zakupy potrzebne na święta, albo kazał mu dać coś ubogim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '29', '29', 'Judasz wyszedł zaraz po zjedzeniu kawałka (chleba). A była noc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '30', '30', 'Kiedy on wyszedł, Jezus powiedział: - Teraz Syn Człowieczy został uwielbiony i Bóg został uwielbiony w Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '31', '31', 'Jeżeli Bóg został w Nim uwielbiony, to i Bóg uwielbi Go w sobie samym - i uwielbi Go niebawem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '32', '32', 'Dzieci, niedługo już pozostanę z wami. Będziecie Mnie szukać, ale - jak już powiedziałem Judejczykom - tak teraz i wam mówię: Tam, dokąd Ja idę, wy pójść nie możecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '33', '33', 'Daje wam nowe przykazanie, abyście się wzajemnie miłowali. Miłujcie się wzajemnie, jak Ja was umiłowałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '34', '34', 'Po tej miłości, którą będziecie mieć jedni do drugich, wszyscy poznają, żeście uczniami moimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '35', '35', 'Mówi Mu Szymon Piotr: - Panie, dokąd idziesz? Odrzekł mu Jezus: - Tam, gdzie Ja idę, ty teraz pójść za Mną nie możesz, ale pójdziesz za Mną później.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '36', '36', 'Mówi Mu Piotr: - Panie, dlaczego już teraz nie mogę pójść za Tobą? Życie moje oddam za Ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '12', '37', '37', 'Jezus odpowiedział: - Życie swoje oddasz za Mnie? Zaprawdę, zaprawdę powiadam ci: Jeszcze kogut nie zapieje. a ty trzykroć się Mnie zaprzesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '0', '0', 'Niech serce wasze się nie trwoży. Wierzcie w Boga - i we Mnie wierzcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '1', '1', 'W domu Ojca mego jest mieszkań wiele. Gdyby tak nie było powiedziałbym wam o tym, bo idę przygotować wam miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '2', '2', 'A kiedy odejdę i przygotuję wam miejsce, przyjdę znowu i zabiorę was do siebie, abyście i wy tam byli, gdzie Ja jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '3', '3', 'A drogę, dokąd Ja idę, znacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '4', '4', 'Mówi Mu Tomasz: - Panie, nie wiemy, dokąd idziesz. Jak więc możemy znać drogę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '5', '5', 'Mówi mu Jezus: - Ja jestem drogą prawdziwą do życia. Każdy dochodzi do Ojca tylko przeze Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '6', '6', 'Jeśli Mnie znacie, to poznacie też mojego Ojca. Od tej chwili znacie Go i widzieliście Go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '7', '7', 'Mówi Mu Filip: - Panie, pokaż nam Ojca i to nam wystarczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '8', '8', 'Mówi mu Jezus: - Tak dawno już jestem z wami i jeszcze Mnie nie znasz, Filipie? Kto Mnie widzi, widzi też Ojca. Jak ty możesz mówić: Pokaż nam Ojca?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '9', '9', 'Czy nie wierzysz, że ja jestem w Ojcu i że Ojciec jest we Mnie? To, co wam mówię, nie mówię od siebie. Ojciec, który mieszka we Mnie, dokonuje swoich dzieł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '10', '10', 'Wierzcie Mi, że Ja jestem w Ojcu, a Ojciec jest we Mnie. Jeśli zaś nie (wierzycie słowom), to wierzcie dla samych dzieł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '11', '11', 'Zaprawdę, zaprawdę powiadam wam: Kto wierzy we Mnie, dokona też dzieł, których Ja dokonuję, a nawet większych od tych dokona, bo Ja odchodzę do Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '12', '12', 'I o cokolwiek poprosicie w imię moje, spełnię, aby Ojciec został uwielbiony w Synu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '13', '13', 'O cokolwiek Mnie poprosicie w imię moje, Ja to spełnię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '14', '14', 'Jeśli Mnie miłujecie, będziecie przestrzegali moich przykazań.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '15', '15', 'A Ja poproszę Ojca, a da wam innego obrońcę, aby był z wami na zawsze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '16', '16', 'Ducha prawdy, którego świat przyjąć nie może, bo go nie widzi ani nie zna. A wy Go znacie, bo mieszka i jest w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '17', '17', 'Nie zostawię was sierotami. Powrócę do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '18', '18', 'Już niedługo, a świat będzie Mnie oglądał, lecz wy zobaczycie Mnie, bo Ja żyję i wy żyć będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '19', '19', 'Owego dnia poznacie, że Ja jestem w moim Ojcu i wy we Mnie, i Ja w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '20', '20', 'Kto ma moje przykazania i przestrzega ich, ten Mnie miłuje. A tego, kto Mnie miłuje, umiłuje mój Ojciec i Ja go umiłuję, i objawię mu siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '21', '21', 'Judasz - ale nie ten Iskariota - mówi mu: - Panie, co się stało, że masz objawić się nam, a nie światu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '22', '22', 'A Jezus mu odpowiedział: - Jeśli kto Mnie miłuje, będzie przestrzegał mojej nauki. A Ojciec mój umiłuje go i przyjdziemy do niego i będziemy mieszkać u niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '23', '23', 'Kto Mnie nie miłuje, nauki mojej nie przestrzega. A nauka, którą słyszeliście, nie jest moja, ale Ojca, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '24', '24', 'To powiedziałem wam, będąc z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '25', '25', 'A obrońca, Duch Święty, którego Ojciec pośle w moim imieniu, nauczy was wszystkiego i przypomni wam wszystko, co wam powiedziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '26', '26', 'Pokój wam zostawiam, pokój mój wam daję. Ja daję go wam nie jak świat daje. Niech się nie trwoży serce wasze ani się nie lęka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '27', '27', 'Słyszeliście, że wam powiedziałem: Odchodzę, ale powrócę do was. Gdybyście miłowali Mnie, cieszylibyście się, że idę do Ojca, bo Ojciec jest większy ode Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '28', '28', 'A teraz powiedziałem wam, zanim to nastąpi, abyście uwierzyli, gdy to nastąpi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '29', '29', 'Nie będę już z wami wiele mówił, nadchodzi bowiem władca tego świata, nie ma on we Mnie nic swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '13', '30', '30', 'Lecz (trzeba), aby świat poznał, że miłuję Ojca i że czynię tak, jak Ojciec Mi nakazał. Wstańcie, chodźmy stąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '0', '0', 'Ja jestem prawdziwym krzewem winnym, a mój Ojciec jest ogrodnikiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '1', '1', 'Każdą moją latorośl, która nie przynosi owocu, odcina. Każda zaś, która przynosi owoc, oczyszcza, aby przyniosła (jeszcze) więcej owocu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '2', '2', 'Wy już jesteście oczyszczeni przez naukę, którą wam przekazałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '3', '3', 'Pozostańcie złączeni ze Mną, a Ja z wami. Jak latorośl sama nie może przynosić owocu, jeśli nie jest złączona z winnym krzewem, tak i wy (nie możecie przynosić owocu), jeśli nie jesteście ze Mną złączeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '4', '4', 'Ja jestem krzewem winnym, a wy latoroślami. Kto pozostaje we Mnie, a Ja w nim, ten przynosi wiele owocu. Beze Mnie nic nie możecie uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '5', '5', 'Kto nie pozostaje złączony we Mnie, zostanie odrzucony jak latorośl i uschnie. Pozbierają ją, wrzucą do ognia i spłonie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '6', '6', 'Jeżeli będziecie złączeni ze Mną i moje słowa w was pozostaną, proście, o co chcecie, a prośba wasza zostanie spełniona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '7', '7', 'To, że przynosicie wiele owocu i jesteście moimi uczniami, szerzy chwałę mojego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '8', '8', 'Jak Ojciec Mnie umiłował. tak i Ja was umiłowałem. Wytrwajcie w mojej miłości!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '9', '9', 'Jeżeli będziecie przestrzegali moich przykazań, wytrwacie w mojej miłości, tak jak Ja przestrzegałem przykazań mego Ojca i trwam w Jego miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '10', '10', 'To wam powiedziałem, abyście dzielili ze Mną moją radość i aby wasza radość była pełna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '11', '11', 'A to jest moje przykazanie: Miłujcie się wzajemnie, jak Ja was umiłowałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '12', '12', 'Nikt nie ma większej miłości od tego, kto oddaje życie za przyjaciół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '13', '13', 'Jeśli czynicie to, co Ja wam nakazuję, jesteście moimi przyjaciółmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '14', '14', 'Już nie nazywam was sługami, bo sługa nie wie, co pan jego czyni. Nazywam was przyjaciółmi, bo wszystko, co usłyszałem od Ojca, przekazałem wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '15', '15', 'Nie wyście Mnie wybrali, ale Ja was wybrałem i sprawiłem, że pójdziecie i będziecie przynosili owoc, i owoc wasz będzie trwał. A Ojciec da wam wszystko, o co Go tylko poprosicie w imię moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '16', '16', 'A to wam nakazuję: Miłujcie się wzajemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '17', '17', 'A kiedy świat was nienawidzi, wiedzcie, że Mnie pierwej niż was znienawidził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '18', '18', 'Gdybyście byli ze świata, świat kochałby to, co jest jego. Ponieważ jednak nie jesteście ze świata - bo Ja was wybrałem ze świata - świat was nienawidzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '19', '19', 'Zapamiętajcie sobie słowa, które wam powiedziałem: Sługa nie jest większy od swego pana. Jeżeli Mnie prześladowali, i was będą prześladować. Jeżeli przestrzegali moich nauk, to i waszych będą przestrzegali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '20', '20', 'Ale to wszystko będą czynili przeciwko wam dla mojego imienia, bo nie znają Tego, który Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '21', '21', 'Gdybym nie przyszedł i nie mówił do nich, nie mieliby grzechu. Lecz teraz nie mają usprawiedliwienia dla swojego grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '22', '22', 'Kto Mnie nienawidzi, nienawidzi też mojego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '23', '23', 'Gdybym wśród nich nie dokonał dzieł, których nikt inny nie dokonał, nie mieliby grzechu, lecz teraz ujrzeli je, a jednak znienawidzili i Mnie, i mojego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '24', '24', 'Tak się wypełniło słowo zapisane w ich Prawie: \'Znienawidzili Mnie bez powodu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '25', '25', 'Kiedy przyjdzie obrońca, którego wam poślę od Ojca, Duch prawdy, który pochodzi od Ojca, da o Mnie świadectwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '14', '26', '26', 'Wy także będziecie dawać świadectwo, bo jesteście od początku ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '0', '0', 'To wam powiedziałem, abyście się nie załamali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '1', '1', 'Wyłączą was z synagogi, a nawet przyjdzie godzina, kiedy każdy, kto was zabije, będzie sądził, że oddaje cześć Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '2', '2', 'A będą to czynili, bo nie poznali Ojca ani Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '3', '3', 'Ale powiedziałem wam, abyście, gdy przyjdzie ich godzina, przypomnieli sobie, że Ja wam to powiedziałem.                                                                                                                                              Działalność Ducha Świętego                                                                                                                 Od początku nie mówiłem wam tego, bo byłem z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '4', '4', 'A teraz odchodzę do Tego, który Mnie posłał. A nikt z was nie pyta Mnie: Dokąd idziesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '5', '5', 'Ale dlatego, że wam to powiedziałem smutek napełnił wasze serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '6', '6', 'Lecz Ja mówię wam prawdę: Lepiej dla was, że Ja odchodzę, bo jeślibym nie odszedł, Obrońca nie przyszedłby do was, jeśli zaś nie odejdę, poślę Go do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '7', '7', 'A kiedy On przyjdzie, przekona świat o grzechu, o sprawiedliwości i o sądzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '8', '8', 'O grzechu - bo nie wierzą we Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '9', '9', 'O sprawiedliwości - bo odchodzę do Ojca i Mnie więcej nie zobaczycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '10', '10', 'O sądzie - bo władca tego świata został osądzony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '11', '11', 'Wiele mam jeszcze wam do powiedzenia, ale teraz nie możecie tego znieść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '12', '12', 'A kiedy On, Duch prawdy przyjdzie, będzie waszym przewodnikiem w drodze do pełnej prawdy. Nie będzie bowiem mówił od siebie, ale powie, co usłyszy, i oznajmi wam to, co ma nastąpić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '13', '13', 'On Mnie uwielbi, bo weźmie z mojego i wam oznajmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '14', '14', 'Wszystko, co ma Ojciec, jest moje. Dlatego powiedziałem: Z mojego weźmie i wam oznajmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '15', '15', 'Jeszcze chwila a nie zobaczycie Mnie, a potem znowu chwila i zobaczycie Mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '16', '16', 'A niektórzy Z Jego uczniów mówili między sobą: - Co to znaczy, co nam mówi: Jeszcze chwila a nie zobaczycie Mnie, a potem znowu chwila i zobaczycie Mnie. i: Bo idę do Ojca?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '17', '17', 'Pytali więc: - Co znaczy chwila? Nie rozumiemy, o czym mówi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '18', '18', 'Jezus wiedział, że chcieli Go zapytać, i powiedział im: - Zastanówcie się między sobą nad tym, że powiedziałem: Jeszcze chwila i nie zobaczycie Mnie, a potem znowu chwila i zobaczycie Mnie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '19', '19', 'Zaprawdę, zaprawdę powiadam wam, że będziecie płakać i zawodzić, a świat się będzie weselił, będziecie się smucić, ale smutek wasz w radość się obróci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '20', '20', 'Kiedy kobieta rodzi, smuci się bo nadeszła jej godzina. Ale kiedy urodzi dzieciątko, już nie pamięta o smutku z radości, że człowiek przyszedł na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '21', '21', 'Tak też i wy teraz jesteście smutni, ale zobaczę was znowu i serce wasze się rozraduje, a waszej radości nikt wam nie odbierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '22', '22', 'W owym dniu o nic Mnie pytać nie będziecie. Zaprawdę, zaprawdę powiadam wam: O cokolwiek poprosicie Ojca w imię moje, da wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '23', '23', 'Dotąd o nic nie prosiliście w moje imię. Proście, a otrzymacie, aby wasza radość była pełna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '24', '24', 'Mówiłem wam o tym w przypowieściach. Nadchodzi godzina, kiedy nie będę już mówił w przypowieściach, ale wyraźnie opowiem wam o Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '25', '25', 'W tym dniu będziecie prosić w imię moje. I nie mówię, że Ja będę wstawał się do Ojca za wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '26', '26', 'Ojciec bowiem sam was miłuje, bo wyście Mnie umiłowali i uwierzyliście, że Ja wyszedłem od Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '27', '27', 'Wyszedłem od Ojca i przyszedłem na świat. Teraz znowu opuszczam świat i idę do Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '28', '28', 'Mówią Jego uczniowie: - Oto teraz wyraźnie mówisz i nie opowiadasz żadnej przypowieści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '29', '29', 'Teraz wiemy, że wiesz wszystko i nie trzeba, żeby ktoś Cię pytał. Dlatego wierzymy, żeś wyszedł od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '30', '30', 'Odrzekł im Jezus: - Teraz wierzycie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '31', '31', 'Oto nadchodzi godzina, a nawet już nadeszła, że rozproszycie się, każdy w swoją stronę, a Mnie zostawcie samego. Ale nie jestem sam, bo Ojciec jest ze Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '15', '32', '32', 'Pozwiedzałem wam to, abyście we Mnie mieli pokój. Świat będzie was uciskał, lecz ufajcie, Jam zwyciężył świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '0', '0', 'Po tych słowach Jezus, podniósłwszy oczy w niebo, rzekł: - Ojcze, nadeszła godzina. Wsław swego Syna, aby i Syn wysławił Ciebie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '1', '1', 'stosownie do władzy, jaką Mu dałeś nad każdym człowiekiem, aby dał życie wieczne każdemu, którego Mu dałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '2', '2', 'A to jest życie wieczne: poznać Ciebie, jedynego, prawdziwego Boga, i Tego, którego posłałeś, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '3', '3', 'Ja wsławiłem Ciebie na ziemi, doprowadziwszy do końca dzieło, które Mi dałeś, abym wykonał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '4', '4', 'A teraz Ty, Ojcze, otocz Mnie u siebie swą wielką chwałą, którą miałem u Ciebie, zanim świat powstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '5', '5', 'Objawiłem Twoje imię ludziom, których Mi dałeś ze świata. Twoimi byli, a Tyś Mnie ich oddał. Oni zachowali Twoją naukę');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '6', '6', 'Teraz wiedzą, że wszystko, co Mi dałeś pochodzi od Ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '7', '7', 'Bo słowa, które Mi dałeś, dałem im i oni je przyjęli i naprawdę poznali, że od Ciebie wyszedłem, i uwierzyli, żeś Ty Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '8', '8', 'Ja proszę za nimi. Nie proszę za światem, ale za tymi, których Mi dałeś, bo są Twoimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '9', '9', 'Wszystko, co moje, jest Twoje, a co Twoje, jest moje, i w nich zostałem wysławiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '10', '10', 'Nie jestem już na świecie, ale oni są na świecie. Ja idę do Ciebie. Ojcze Święty, zachowaj ich w imię Twoje, które Mi dałeś, aby byli jedno jak my.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '11', '11', 'Kiedy byłem z nimi, Ja strzegłem w imię Twoje tych, których Mi dałeś. I ustrzegłem ich. Nikt z nich nie zginął oprócz syna zatracenia. Tak wypełniło się Pismo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '12', '12', 'A teraz idę do Ciebie, mówię zaś to będąc na świecie, aby w pełni zaznali mojej radości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '13', '13', 'Ja przekazałem im Twoje słowo, a świat ich znienawidził, bo nie są ze świata, tak jak Ja nie jestem ze świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '14', '14', 'Nie proszę (o to), abyś ich zabrał ze świata, ale abyś ich zachował od złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '15', '15', 'Nie są ze świata, jak i Ja nie jestem ze świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '16', '16', 'Poświęć ich prawdzie. Twoje słowo jest prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '17', '17', 'Jak Ty Mnie posłałeś na świat, tak Ja ich posłałem na świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '18', '18', 'I za nich poświęcam siebie, aby i oni byli poświęceni prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '19', '19', 'A nie tylko za nimi proszę, lecz także za tymi, którzy wierzą we Mnie dzięki ich nauczaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '20', '20', 'Aby wszyscy byli jedno, jak Ty, Ojcze, ze Mną, a Ja z Tobą, aby i oni byli z nami, aby świat uwierzył, żeś Ty mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '21', '21', 'Tę zaś chwałę, którą Mi dałeś, im przekazywałem, aby byli jedno, jak my jedno (jesteśmy).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '22', '22', 'Ja w nich, a Ty we Mnie. Aby byli tak dokonałą jednością, żeby świat poznał, żeś Ty Mnie posłał i umiłowałeś ich tak, jak Mnie umiłowałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '23', '23', 'Ojcze, chcę, aby ci, których Mi dałeś, byli ze Mną tam, gdzie Ja jestem. Aby oglądali moją chwałę, którą Mi dałeś, bo umiłowałeś Mnie, zanim świat powstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '24', '24', 'Ojcze sprawiedliwy! Świat Ciebie nie poznał, a Ja Ciebie poznałem i oni poznali, żeś Ty Mnie posłał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '16', '25', '25', 'Objawiałem im Twoje imię i nadal będzie objawiał, aby była w nich miłość, którą Mnie obdarzałeś, i Ja w nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '0', '0', 'Po tych słowach Jezus wyszedł z uczniami na drugi brzeg potoku Cedron, gdzie był ogród. I wszedł do niego z uczniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '1', '1', 'Miejsce to znał także Judasz, który Go wydał, bo Jezus i uczniowie często się tam zbierali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '2', '2', 'Judasz więc, wziąwszy oddział i straż od najwyższych kapłanów i faryzeuszów, przychodzi tam z latarniami, pochodniami i bronią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '3', '3', 'A Jezus, wiedząc o wszystkim, co miało Go spotkać, wyszedł i pyta ich: - Kogo szukacie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '4', '4', 'Odpowiedzieli Mu: - Jezusa Nazarejczyka. Mówi im: - Ja nim jestem. A był też z nimi Judasz, który Go wydał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '5', '5', 'Kiedy więc Jezus powiedział: Ja nim jestem - cofnęli się i upadli na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '6', '6', 'Zapytał więc ponownie: - Kogo szukacie? - Jezusa Nazarejczyka - odpowiedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '7', '7', 'Jezus odrzekł: - Powiedziałem wam, że Ja nim jestem. Jeżeli więc Mnie szukacie, pozwólcie tym odejść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '8', '8', 'Tak wypełniły się słowa, które (Jezus) wypowiedział: Nie straciłem nikogo z tych, których Mi dałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '9', '9', 'A Szymon Piotr, który miał przy sobie miecz, wyciągnął go i uderzył sługę najwyższego kapłana, i uciął mu prawe ucho. Sługa miał na imię Malchos.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '10', '10', 'A Jezus rzekł Piotrowi: - Schowaj miecz do pochwy, czy mam nie pić kielicha, który Mi dał Ojciec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '11', '11', 'Oddział więc, dowódca i słudzy Judejczyków wzięli ze sobą Jezusa. Związali Go');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '12', '12', 'i zaprowadzili najpierw do Annasza. Był on teściem Kajfasza, który w owym roku był najwyższym kapłanem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '13', '13', 'To właśnie Kajfasz doradził Judejczykom, że lepiej, aby jeden człowiek umarł za naród.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '14', '14', 'A za Jezusem szedł Szymon Piotr i inny uczeń. Uczeń ten był znany najwyższemu kapłanowi i wszedł razem z Jezusem na dziedziniec najwyższego kapłana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '15', '15', 'A Piotr zatrzymał się na zewnątrz, pod bramą. Wyszedł więc ten drugi uczeń, znany najwyższemu kapłanowi, pomówił z odźwierną i wprowadził Piotra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '16', '16', 'A służąca, która była odźwierną, mówi do Piotra: - Czy i ty jesteś uczniem tego człowieka? Mówi jej: - Nie jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '18', '18', 'A stali tam służący i strażnicy, którzy rozpalili ognisko - było bowiem zimno - i grzali się. Piotr też stał z nimi i grzał się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '19', '19', 'Jezus mu odrzekł: - Ja jawnie przemawiałem do ludzi. Zawsze nauczałem w synagogach albo w świątyni, gdzie zbierają się wszyscy Żydzi, a potajemnie nic nie powiedziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '20', '20', 'Czemu Mnie pytasz? Pytaj słuchaczy, co im powiedziałem. Oni wiedzą, co Ja powiedziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '21', '21', 'Kiedy to rzekł, jeden z służących uderzył Jezusa w twarz, mówiąc: - Tak odpowiadasz najwyższemu kapłanowi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '22', '22', 'Jezus mu odrzekł: - Jeśli źle odpowiedziałem, udowodnij to! A jeśli dobrze - dlaczego Mnie bijesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '23', '23', 'Annasz zatem odesłał związanego Jezusa do najwyższego kapłana, Kajfasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '24', '24', 'Szymon Piotr stał i grzał się. Mówili do niego: - Czy i ty jesteś Jego uczniem? A on zaprzeczył: - Nie jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '25', '25', 'Mówi jeden z służby najwyższego kapłana, krewny tego, któremu Piotr obciął ucho. - Czy nie widziałem cię z Nim w ogrodzie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '26', '26', 'Piotr ponownie zaprzeczył. I zaraz kogut zapiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '27', '27', 'Od Kajfasza prowadzą Jezusa do pretorium. Był wczesny ranek. Ale nie weszli do pretorium, aby się nie skalać, lecz aby mogli spożywać Paschę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '28', '28', 'Piłat więc wyszedł do nich (z pretorium) i mówi: - O co oskarżacie tego człowieka?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '29', '29', 'Odpowiedzieli Mu: - Gdyby On nie był przestępcą, nie wydalibyśmy Go Tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '30', '30', 'Powiedział im Piłat: - Zabierzcie Go i osądzicie według waszego Prawa. Judejczycy mu odrzekli: - Nam nie wolno zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '31', '31', 'Tak wypełniły się słowa, które wypowiedział Jezus, wskazując, jaką śmiercią miał umrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '32', '32', 'Piłat więc wszedł znowu do pretorium, zawołał do Jezusa i zapytał Go: - Czy Ty jesteś królem żydowskim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '33', '33', 'Jezus odrzekł: - Czy ty mówisz to sam od siebie, czy też inni powiedzieli ci o Mnie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '34', '34', 'Piłat odrzekł: - Czy ja jestem Żydem?! Twój naród i najwyżsi kapłani wydali mi Ciebie. Coś uczynił?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '35', '35', 'Jezus odrzekł: - Królestwo moje nie jest z tego świata. Gdyby moje królestwo było z tego świata, słudzy moi walczyliby, abym nie został wydany Judejczykom, a przecież moje królestwo nie jest stąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '36', '36', 'Powiedział Mu Piłat: - Jesteś więc królem? - Tak, jestem królem - odrzekł Jezus. - Jam się po to narodził i po to przyszedłem na świat, aby dać świadectwo prawdzie. Każdy, kto jest z prawdy, słucha mojego głosu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '37', '37', 'Mówi Mu Piłat: - Co to jest prawda? Po tych słowach wyszedł znowu do Judejczyków i mówi im: - Ja nie stwierdzam żadnej Jego winy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '17', '38', '38', 'Ale jest zwyczaj u was, że na Paschę uwalniam wam jednego (więźnia). Jeżeli więc chcecie, uwolnię wam króla żydowskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '0', '0', 'Wtedy Piłat kazał zabrać Jezusa i ubiczować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '1', '1', 'Żołnierze upletli koronę z cierni, włożyli Mu na głowę i narzucili na Niego purpurowy płaszcz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '2', '2', 'I podchodzili do Niego i mówili: - Witaj, królu żydowski. I bili Go po twarzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '3', '3', 'Piłat wyszedł znowu z pretorium i mówi im: - Oto wyprowadziłem Go do was, żebyście wiedzieli, że nie stwierdzam żadnej Jego winy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '4', '4', 'Jezus więc wyszedł z pretorium w koronie cierniowej i purpurowym płaszczu. I mówi im Piłat: - Oto człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '5', '5', 'Kiedy arcykapłani i słudzy ujrzeli Go, krzyknęli: - Ukrzyżuj, ukrzyżuj! Piłat im mówi: - Wy Go weźcie i ukrzyżujcie, bo ja nie stwierdzam Jego winy. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '6', '6', 'My mamy Prawo - odrzekli mu Judejczycy - a według tego Prawa musi umrzeć, bo czynił się Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '7', '7', 'Kiedy Piłat usłyszał te słowa bardziej zaniepokoił się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '8', '8', 'Wszedł znowu do pretorium i mówi do Jezusa: - Skąd Ty jesteś ? A Jezus nie dał mu odpowiedzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '9', '9', 'Mówi Mu Piłat: - Mnie nie odpowiadasz? Czy nie wiesz, że mam władzę Cię uwolnić i mam władzę Cię ukrzyżować?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '10', '10', 'Jezus mu odrzekł: - Nie miałbyś żadnej władzy nade Mną, gdyby ci jej z góry nie dano. Dlatego ten, który Mnie tobie wydał, bardziej zgrzeszył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '11', '11', 'Od tej chwili Piłat starał się Go uwolnić. A Judejczycy krzyczeli: - Jeśli Go uwolnisz, nie jesteś przyjacielem cesarza. Bo kto czyni się królem, występuje przeciw cesarzowi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '12', '12', 'Piłat usłyszawszy te słowa, wyprowadził Jezusa z pretorium i zasiadł na podwyższeniu na miejscu zwanym Lithostrotos, a po hebrajsku Gabbatha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '13', '13', 'Był to dzień przygotowania Paschy, godzina mniej więcej szósta. (Piłat) mówi Judajczykom: - Oto wasz król! -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '14', '14', 'Precz, precz z Nim, ukrzyżuj Go, ukrzyżuj Go - krzyknęli. Mówi im Piłat: - Waszego króla mam ukrzyżować? - Tylko cesarz jest naszym królem! - odrzekli najwyżsi kapłani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '15', '15', 'Wtedy (Piłat) wydał Go im na ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '16', '16', 'Zabrali więc Jezusa. I dźwigając krzyż wyszedł na tak zwane Miejsce Czaszki, a po hebrajsku Golgotha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '17', '17', 'Tam Go ukrzyżowali i razem z Nim po obu stronach dwóch innych, a Jezusa w środku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '18', '18', 'Piłat kazał sporządzić też napis i umieścić na krzyżu. A było napisane. Jezus Nazarejczyk, król żydowski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '19', '19', 'Wielu Judejczyków czytało ten napis, bo miejsce ukrzyżowania Jezusa znajdowało się blisko miasta, a napis był po hebrajsku, po łacinie i po grecku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '20', '20', 'Najwyżsi zaś kapłani żydowscy mówili Piłatowi: - Nie pisz: Król żydowski, ale że powiedział: Jestem królem żydowskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '21', '21', 'Piłat odrzekł: - Co napisałem, to napisałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '22', '22', 'Kiedy żołnierze ukrzyżowali Jezusa wzięli Jego odzież i podzielili na cztery części, dla każdego żołnierza część. Wzięli też chiton, który nie był szyty, ale tkany w całości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '23', '23', 'Mówili więc między sobą: - Nie dzielmy go, ale losujmy komu przypadnie. Tak wypełniło się Pismo, które mówi: \'Podzielili się moimi szatami, a o suknię moją los rzucili\'. Tak właśnie uczynili żołnierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '24', '24', 'A pod krzyżem Jezusa stała Jego matka i siostra Jego matki, Maria Kleofasowa, i Maria Magdalena.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '25', '25', 'Jezus zobaczywszy matkę i ucznia, którego miłował, mówi do matki: - Niewiasto, oto syn twój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '26', '26', 'Potem mówi uczniowi: - Oto twoja matka. I od tej chwili wziął ją uczeń do siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '27', '27', 'Potem Jezus wiedząc, że już wszystko się dokonało i że wypełniło się Pismo, mówi: - Pragnę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '28', '28', 'Stało tam naczynie napełnione octem. Zatknąwszy więc gąbkę pełną octu na włóczni, podali Mu od ust.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '29', '29', 'Kiedy Jezus zwilżył octem wargi, rzekł: - Dokonało się. I skłoniwszy głowę oddał ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '30', '30', 'Ponieważ był Dzień Przygotowania, Judejczycy (nie chcąc), aby ciała pozostawały przez szabat na krzyżu, bo dzień tego szabatu był wielkim świętem, poprosił Piłata, aby połamano golenie skazańcom i zdjęto ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '31', '31', 'Przyszli więc żołnierze i połamali golenie tym, którzy byli z Nim ukrzyżowani - jednemu i drugiemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '32', '32', 'Lecz kiedy podeszli do Jezusa zobaczyli, że już nie żyje, nie połamali Mu goleni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '33', '33', 'lecz jeden z żołnierzy przebił Mu bok włócznią. I zaraz wypłynęła krew i woda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '34', '34', 'A ten, który to widział, daje o tym świadectwo. A świadectwo jego jest prawdziwe i on wie, że mówi prawdę, byście i wy uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '35', '35', 'Bo to się stało, aby wypełniło się Pismo: \'Kości Jego łamać nie będziecie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '36', '36', 'I znowu inne Pismo mówi: \'Dowiedzą się, kogo przebodli\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '37', '37', 'Potem Józef z Arymatei - który był uczniem Jezusa, ale ukrytym z obawy przed Judejczykami - poprosił Piłata, żeby mógł zabrać ciało Jezusa. Piłat się zgodził. Przyszedł więc i zabrał Jego ciało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '38', '38', 'Przybył też Nikodem, który niegdyś przyszedł do Jezusa w nocy. Przyniósł około stu funtów mirry zmieszanej z aloesem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '39', '39', 'Wzięli więc ciało Jezusa i owinęli je płóciennymi taśmami razem  z wonnościami, stosownie do żydowskich zwyczajów pogrzebowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '40', '40', 'A w miejscu, gdzie Go ukrzyżowano, był ogród, a w ogrodzie zaś nowy grób, w którym nikt jeszcze nie był pochowany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '18', '41', '41', 'Tam więc pochowali Jezusa ze względu na żydowski Dzień Przygotowania. Bo grób był blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '0', '0', 'W pierwszy dzień tygodnia, wczesnym rankiem, kiedy jeszcze było ciemno, Maria Magdalena przychodzi do grobu i widzi kamień odsunięty od grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '1', '1', 'Biegnie więc, przychodzi do Szymona Piotra i do drugiego ucznia, którego Jezus miłował, i mówi im: - Zabrali Pana z grobu i nie wiemy, gdzie Go położyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '2', '2', 'Wyszedł więc Piotr i ten drugi uczeń i szli do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '3', '3', 'Obydwaj biegli razem, lecz ten drugi uczeń wyprzedził Piotra i pierwszy przybył do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '4', '4', 'I nachyliwszy się widzi leżące płócienne taśmy. Nie wszedł jednak do środka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '5', '5', 'Przychodzi też Szymon Piotr, który biegł za nim. I wszedł do grobowca, i widzi leżące płócienne taśmy');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '6', '6', 'i chustę, która była na Jego głowie, leżąca nie razem z płóciennymi taśmami, ale osobno zwinięta na innym miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '7', '7', 'Wtedy wyszedł też drugi uczeń, który był pierwszy przybył do grobowca. I zobaczył, i uwierzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '8', '8', 'Jeszcze bowiem nie rozumieli Pisma (które mówi), że On ma powstać z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '9', '9', 'Potem uczniowie odeszli do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '10', '10', 'A Maria stała na zewnątrz grobowca i płakała. Płacząc nachyliła się w stronę grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '11', '11', 'I widzi dwóch aniołów w bieli siedzących tam, gdzie leżało ciało Jezusa: jednego przy głowie, a drugiego przy nogach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '12', '12', 'Mówią do niej: - Niewiasto, czemu płaczesz? Mówi im: - Pana mego zabrali i nie wiem gdzie Go położyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '13', '13', 'Po tych słowach odwróciła się i ujrzała Jezusa, a nie wiedziała, że to jest Jezus. Mówi jej Jezus: - Niewiasto, czemu płaczesz? Kogo szukasz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '14', '14', 'Ona sądząc, że to ogrodnik, mówi Mu: - Panie, jeśli to ty Go zabrałeś, powiedz mi, gdzieś Go położył, a ja Go wezmę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '15', '15', 'Mówi jej Jezus: - Mario! Ona odwróciwszy się mówi Mu po hebrajsku: - Rabbuni! - To znaczy: Nauczycielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '16', '16', 'A Jezus jej mówi: - Nie zatrzymuj Mnie, bo jeszcze nie wstąpiłem do Ojca. Ale idź do moich braci i powiedz im, że wstępuję do Ojca mego i Ojca waszego, do Boga mojego i Boga waszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '17', '17', 'Przychodzi więc Maria Magdalena i oznajmia uczniom, że widziała Pana i to jej powiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '18', '18', 'Wieczorem, w ten pierwszy dzień tygodnia, tam gdzie uczniowie przebywali, zamknąwszy drzwi z obawy przed Judejczykami, Jezus przyszedł, stanął na środku i mówi: - Pokój wam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '19', '19', 'Po tych słowach pokazał im ręce i bok. Uradowali się więc uczniowie ujrzawszy Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '20', '20', 'Powiedział im znowu: - Pokój wam! Jak Mnie posłał Ojciec, tak i ja was posyłam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '21', '21', 'Po tych słowach tchnął na nich i mówi: - Weźcie Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '22', '22', 'Komu grzechy odpuścicie, temu są odpuszczone, a komu zatrzymacie, temu są zatrzymane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '23', '23', 'A Tomasz, jeden z Dwunastu, zwany Bliźniakiem, nie był z nimi, kiedy Jezus przyszedł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '24', '24', 'Mówili mu więc inni uczniowie: - Ujrzeliśmy Pana. A on im powiedział: - Jeżeli nie zobaczę śladów gwoździ na Jego rękach i nie włożę palca w Jego bok, nie uwierzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '25', '25', 'Po ośmiu dniach uczniowie znowu byli w domu i Tomasz z nimi. Przychodzi Jezus , chociaż drzwi były zamknięte, stanął na środku i rzekł: - Pokój wam!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '26', '26', 'Potem mówi Tomaszowi: - Włóż tutaj palec i oglądaj moje ręce, włóż także rękę do mojego boku i nie bądź niedowiarkiem, ale wierzącym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '27', '27', 'A Tomasz Mu odpowiedział: - Pan mój i Bóg mój!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '28', '28', 'Mówi mu Jezus: - Uwierzyłeś, boś Mnie ujrzał. Szczęśliwi ci, którzy nie ujrzeli, a uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '29', '29', 'Jezus dokonał jeszcze wielu innych znaków na oczach swoich uczniów. Nie zostały one zapisane w tej księdze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '19', '30', '30', 'Te zaś spisano, abyście uwierzyli, że Jezus jest Mesjaszem, Synem Bożym, i abyście wierząc mieli życie w imię Jego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '0', '0', 'Potem ukazał się znowu uczniom nad Morzem Tyberiadzikim. A ukazał się tak:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '1', '1', 'Byli razem Szymon Piotr i Tomasz, zwany Bliźniakiem, i Natanael z Kany Galilejskiej, i synowie Zebedeusza, i dwaj inni Jego uczniowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '2', '2', 'Mówi im Szymon Piotr: - Idę łowić ryby. Mówią mu: - My też idziemy z tobą. Wyszli i wsiedli do łodzi. Ale tej nocy nic nie złowili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '3', '3', 'A kiedy już nastał ranek, Jezus stanął na brzegu. Uczniowie jednak nie wiedzieli,  że to jest Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '4', '4', 'Mówi im Jezus: - Dzieci, macie coś do jedzenia: - Nie - odpowiedzieli Mu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '5', '5', 'On zaś rzekł im: - Zarzućcie sieć po prawej stronie łodzi, a znajdziecie! Zarzucili więc, ale nie mogli jej w żaden sposób wyciągnąć z powodu wielkiej ilości ryb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '6', '6', 'Mówi do Piotra ten uczeń, którego Jezus miłował: - To jest Pan! A Szymon Piotr usłyszawszy, że to jest Pan, odział się, bo był rozebrany, i rzucił się do morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '7', '7', 'Inni zaś uczniowie przypłynęli łodzią, ciągnąc sieć z rybakami, bo byli niedaleko od brzegu, tylko około dwunastu łokci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '8', '8', 'Kiedy więc wyszli na brzeg, widzą żarzące się węgle i leżącą na nim rybę i chleb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '9', '9', 'Mówi im Jezus: - Przynieście z ryb, któreście teraz złowili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '10', '10', 'Poszedł więc Szymon Piotr i wyciągnął na brzeg sieć pełną wielkich ryb - było ich sto pięćdziesiąt trzy. A chociaż było ich tak wiele, sieć się nie zerwała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '11', '11', 'Mówi im Jezus: - Chodźcie i jedzcie! A żaden z uczniów nie śmiał zapytać: Kto Ty jesteś - wiedząc, że to jest Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '12', '12', 'Jezus zaś zbliża się, bierze chleb i podaje im. Tak samo i rybę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '13', '13', 'To już po raz trzeci ukazał się Jezus uczniom po zmartwychwstaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '14', '14', 'Kiedy więc zjedli, Jezus mówi do Szymona Piotra: - Szymonie, synu Jana, czy miłujesz Mnie więcej niż ci? Mówi Mu (Piotr): - Tak, Panie, Ty wiesz, że Cię kocham. Mówi mu: - Paś moje baranki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '15', '15', 'Mówi mu znowu: - Szymonie, synu Jana, miłujesz Mnie? - Tak Panie, ty wiesz, że  cię kocham - odpowiada (Piotr). Mówi mu: - Paś moje owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '16', '16', 'Mówi mu po raz trzeci: - Szymonie, synu Jana, kochasz Mnie? Zasmucił się Piotr, że po raz trzeci go zapytał. Kochasz Mnie? - i mówi Mu: - Panie, Ty wiesz wszystko, Ty wiesz, że Cię kocham. Mówi mu: - Paś moje owce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '17', '17', 'Zaprawdę, zaprawdę powiadam ci: Gdy byłeś młodszy, przepasywałeś się sam i chodziłeś gdzie chciałeś. Lecz kiedy się zestarzejesz, wyciągniesz ręce i ktoś inny cię przepasze i poprowadzi, dokąd nie chcesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '18', '18', 'A powiedział to, aby wskazać, jaką śmiercią Piotr uwielbi Boga. Po tych słowach mówi mu: - Pójdź za Mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '19', '19', 'Piotr widzi idącego za nim ucznia, którego Jezus umiłował i który w czasie wieczerzy oparł głowę na Jego piersi i powiedział: Panie, kto Cię wyda?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '20', '20', 'Zobaczywszy go Piotr mówi Jezusowi: - Panie, a co z nim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '21', '21', 'Mówi mu Jezus: - A jeśli zechcę, aby pozostał dopóki nie przyjdę? Co tobie do tego? Ty pójdź za Mną!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '22', '22', 'Rozeszła się więc wśród braci taka wieść, że ten uczeń nie umrze. Ale Jezus nie powiedział Piotrowi, że (ten uczeń) nie umrze, ale: A jeśli zechcę, aby pozostał, dopóki nie przyjdę? (Co tobie do tego?)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '23', '23', 'To jest właśnie ten uczeń, który daje świadectwo i który spisał. I wiemy, że jego świadectwo jest prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jn', '20', '24', '24', 'Jezus dokonał jeszcze wielu innych rzeczy. Gdyby je chciano opisać, każdą z nich, to sądzę, że (cały) świat nie pomieściłby ksiąg, które by zostały napisane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '0', '0', 'Teofilu, pierwszą księgę napisałem o całej działalności i nauce Jezusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '1', '1', 'doprowadzając opis do czasu Jego wniebowstąpienia, przed którym przez Ducha Świętego dał misję wybranym przez siebie apostołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '2', '2', 'On to po męce przez wiele znaków dał niezbite dowody, że żyje, ukazując się im przez czterdzieści dni i nauczając o królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '3', '3', 'W czasie wspólnego posiłku nakazał im: \'Nie opuszczajcie Jerozolimy, ale czekajcie na obietnicę Ojca, o której ode mnie słyszeliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '4', '4', 'Jan bowiem chrzcił wodą, wy zaś niedługo otrzymacie chrzest Ducha Świętego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '5', '5', 'Zgromadzeni ludzie pytali Go więc: \'Panie, czy w tym czasie przywrócisz królestwo Izraelowi?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '6', '6', 'Odpowiedział im: \'Nie potrzebujecie znać czasu, a zwłaszcza tej chwili, którą Ojciec zastrzegł dla siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '7', '7', 'Otrzymacie moc Ducha Świętego, który na was zstąpi, i będziecie moimi świadkami w Jerozolimie, w całej Judei, w Samarii i na całym świecie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '8', '8', 'Po tych słowach na ich oczach uniósł się do góry i widzieli Go, dopóki obłok im Go nie zakrył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '9', '9', 'A gdy za odchodzącym uporczywie patrzyli w niebo, wtedy stanęło przy nich dwóch ludzi na biało ubranych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '10', '10', 'którzy powiedzieli: \'Galilejczycy, czemu stoicie patrząc w niebo? Ten Jezus wzięty spośród was do nieba przyjdzie w taki sam sposób, jak Go widzieliście odchodzącego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '11', '11', 'Wtedy wrócili do Jerozolimy z góry, zwanej Oliwną, znajdującej się w pobliżu, bo w odległości drogi szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '12', '12', 'I kiedy przyszli do domu, weszli na górę, tam gdzie zwykle się zatrzymywali. Byli to: Piotr, Jan, Jakub, Andrzej, Filip i Tomasz, Bartłomiej i Mateusz, Jakub, syn Alfeusza, Szymon zwany Gorliwym i Judasz, syn Jakuba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '13', '13', 'Wszyscy oni trwali na wspólnej modlitwie, a razem z nimi były też kobiety, Maryja, matka Jezusa, i Jego bracia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '14', '14', 'Wtedy to spośród nich wystąpił Piotr z przemówieniem (zebrało się tam wtedy około 120 osób):');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '15', '15', '\'Bracia! Musiały się wypełnić słowa zapowiedziane przez Ducha Świętego ustami Dawida o Judaszu, który stanął na czele tych, którzy pojmali Jezusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '16', '16', 'choć był przecież jednym z nas, to znaczy miał udział w tym samym posługiwaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '17', '17', 'Otrzymawszy za tę niegodziwość zapłatę, kupił rolę. Powiesił się, pękł na pół i wypłynęły z niego wszystkie wnętrzności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '18', '18', 'Wiadome to było wszystkim mieszkańcom Jerozolimy, tak że rola ta została nazwana Hakeldamach, to znaczy pole krwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '19', '19', 'Napisano bowiem w Księdze Psalmów: &gt;&gt;Niech dom jego stanie się pusty i niech nikt w nim nie zamieszkuje&lt;&lt;, a &gt;&gt;obowiązki jego niech przejmie inny&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '20', '20', 'Dlatego trzeba, aby to był jeden spośród tych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '21', '21', 'którzy z nami chodzili przez cały czas przebywania Pana Jezusa, od chrztu Jana aż do dnia wniebowstąpienia, i wraz z nami świadczył o Jego zmartwychwstaniu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '22', '22', 'Wysunęli dwóch: Józefa Barsabę, z przydomkiem Justus, i Macieja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '23', '23', 'I modlili się: \'Panie, Ty znasz serca wszystkich, wskaż więc, którego z tych dwóch');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '24', '24', 'wybrałeś na miejsce Judasza do służby i posłannictwa, któremu ten się sprzeniewierzył, odchodząc na właściwe sobie miejsce\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '0', '25', '25', 'Rozdali im losy, los padł na Macieja i dołączono go do Jedenastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '0', '0', 'W dniu Pięćdziesiątnicy znowu wszyscy się razem tam zebrali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '1', '1', 'Aż tu nagle powstał szum z nieba, jakby zerwał się gwałtowny wiatr i napełnił cały dom, w którym przebywali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '2', '2', 'Wtedy zobaczyli, że nad głowami każdego z nich pojawiły się jakby języki ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '3', '3', 'A Duch Święty napełnił ich wszystkich, i zaczęli mówić obcymi językami, jak im Duch dawał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '4', '4', 'Znajdowali się wówczas w Jerozolimie mieszkańcy Judei i ludzie pobożni ze wszystkich narodowości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '5', '5', 'Narastająca wrzawa ściągnęła mnóstwo ludzi, których zaskoczyło to, że każdy z nich słyszał, jak mówili w jego ojczystym języku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '6', '6', 'Zaskoczeni ze zdziwieniem pytali: \'Czyż ci wszyscy, którzy rozprawiają, nie są Galilejczykami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '7', '7', 'Jak to się dzieje, że każdy z nas słyszy swoją rodzimą mowę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '8', '8', 'My Partowie, Medowie, Eliamici, mieszkańcy Mezopotamii, Judei i Kapadocji, Pontu, Azji,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '9', '9', 'Frygii i Pamfilii, Egiptu, części Libii położonej koło Cyreny i pielgrzymi z Rzymu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '10', '10', 'Żydzi i prozelici, mieszkańcy Krety i Arabowie, słyszymy jak oni mówią w naszych językach o niezwykłych dziełach Bożych.\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '11', '11', 'Byli tym wszyscy zaskoczeni i oszołomieni, pytając jeden drugiego: \'Co to znaczy?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '12', '12', 'Niektórzy szydzili i mówili: \'Upili się młodym winem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '13', '13', 'Piotr, w otoczeniu Jedenastu, zabrał głos: \'Judejczycy i wszyscy mieszkańcy Jerozolimy! Słuchajcie, co wam powiem, a dowiecie się,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '14', '14', 'że oni nie są pijani, jak wy sądzicie, przecież to jest dopiero trzecia godzina dnia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '15', '15', 'ale to się właśnie spełnia przepowiednia proroka Joela:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '16', '16', '&gt;&gt;Tak będzie, mówi Bóg. W ostatnich dniach ześlę Ducha mego na każdego człowieka, synowie i córki wasze mówić będą z natchnienia Bożego, młodzież wasza mieć będzie widzenia, a starsi sny mieć będą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '17', '17', 'W tym też czasie ześlę Ducha mego na niewolników i niewolnice, którzy będą również mówić z natchnienia Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '18', '18', 'Ukażę takie rzeczy dziwne na niebie, na ziemi zaś znaki z krwi, ognia i dymu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '19', '19', 'Słońce się zaćmi, a księżyc stanie się czerwony jak krew, zanim nadejdzie dzień Pański wielki i wspaniały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '20', '20', 'A zbawiony zostanie każdy, kto wzywać będzie imienia Pańskiego&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '21', '21', 'Izraelici, słuchajcie: Ukrzyżowaliście Jezusa z Nazaretu, człowieka, którego posłannictwo Bóg nam potwierdził nadziemską mocą i zdumiewającymi znakami, o czym doskonale wiecie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '22', '22', 'chociaż został On wydany w ręce niesprawiedliwych z wyraźnej woli i wyroku Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '23', '23', 'A jednak zmartwychwstał, uwolnił Go Bóg z więzów śmierci, gdyż było niemożliwe, by ona Go pokonała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '24', '24', 'Powiedział bowiem o Nim Dawid: &gt;&gt;Zawsze blisko widziałem Pana, Nie zachwieję się, bo jest po prawicy mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '25', '25', 'Dlatego mam radość w sercu i na ustach, zwłaszcza, że ciało moje nie dozna rozkładu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '26', '26', 'Bo nie zostawisz duszy mej w otchłani i nie pozwolisz, by święty twój uległ zniszczeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '27', '27', 'Nauczyłeś mnie dróg życia, a obecność Twoja napełnia mnie radością&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '28', '28', 'Bracia! Niech mi wolno będzie wyraźnie powiedzieć, że patriarcha Dawid, który umarł i został pochowany, a grób jego u nas dotychczas się znajduje,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '29', '29', 'jako prorok mówił o zmartwychwstaniu Mesjasza; pamiętał bowiem o szczególnej obietnicy Bożej, że potomek jego zasiądzie na tronie. Powiedział o Nim:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '30', '30', '&gt;&gt;Nie zostanie bowiem w otchłani ani ciało Jego nie ulegnie zniszczeniu&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '31', '31', 'Tego właśnie Jezusa Bóg wskrzesił, czego my wszyscy jesteśmy świadkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '32', '32', 'Wyniósł Go także Bóg na swoją prawicę, a On zesłał obiecanego przez Ojca Ducha Świętego, co widzicie i słyszycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '33', '33', 'Nie Dawid przecież wstąpił do nieba, sam bowiem mówi: &gt;&gt;Powiedział Pan do mego Pana: Usiądź po prawicy mojej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '34', '34', 'a nieprzyjaciół twoich położę jako podnóżek pod twoje stopy&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '35', '35', 'Niech więc nikt w Izraelu nie ma żadnej wątpliwości, że Jezusa, którego wyście ukrzyżowali, Bóg uczynił Panem i Mesjaszem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '36', '36', 'Słowa te wstrząsnęły nimi głęboko. Zapytali więc Piotra i pozostałych apostołów: \'Bracia, co mamy czynić?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '37', '37', 'A Piotr do nich: \'Starajcie się o wewnętrzną przemianę i niech każdy z was ochrzci się w imię Jezusa Chrystusa dla odpuszczenia grzechów, a otrzymacie dar Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '38', '38', 'Macie bowiem obietnicę wy i dzieci wasze oraz wszyscy, których z oddali wezwie Pan, Bóg nasz\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '39', '39', 'Przytoczył im jeszcze wiele innych świadectw i zachęcał: \'Uwolnijcie się od przewrotności tego pokolenia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '40', '40', 'Ci, którzy przyjęli jego naukę, zostali ochrzczeni, a liczba ich w tym dniu wzrosła do trzech tysięcy osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '41', '41', 'W postępowaniu kierowali się nauką apostołów, żyli we wspólnocie, łamali chleb i modlili się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '42', '42', 'Wiele też zdumiewających znaków czynili apostołowie, a lęk ogarniał wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '43', '43', 'Wszyscy wierzący przebywali razem i mieli wszystko wspólne;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '44', '44', 'sprzedawali posiadłości i dobra, dzieląc je między wszystkich, stosownie do potrzeb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '45', '45', 'Codziennie wspólnie przebywali w świątyni, po domach łamali chleb, z radością i prostotą serca brali udział w posiłkach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '1', '46', '46', 'Chwalili Boga i mieli uznanie u wszystkich. A Pan codziennie dołączał do nich tych,  którzy mieli być zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '0', '0', 'Piotr i Jan wchodzili do świątyni na modlitwę o godzinie dziewiątej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '1', '1', 'Codziennie przed bramę świątyni zwaną Piękną przynoszono mężczyznę bezwładnego od urodzenia, aby prosił wchodzących o jałmużnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '2', '2', 'Gdy zobaczył Piotra i Jana, którzy zamierzali wejść do świątyni, poprosił ich o jałmużnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '3', '3', 'Wtedy Piotr i Jan utkwili w niego wzrok i powiedzieli: \'Spojrzyj na nas\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '4', '4', 'Z zainteresowaniem skierował na nich oczy, spodziewając się, że coś otrzyma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '5', '5', 'Wtedy Piotr powiedział: \'Nie mam złota ani srebra, ale daję ci to, co mam, w imię Jezusa Chrystusa z Nazaretu zacznij chodzić\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '6', '6', 'Wziął go za rękę i podniósł, natychmiast nogi jego nabrały takiej siły,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '7', '7', 'że zeskoczył, stanął, chodził, podskakiwał, wszedł razem z nimi do świątyni i wielbił Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '8', '8', 'Wszyscy ludzie wiedzieli, że chodzi i chwali Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '9', '9', 'Poznali, że to jest ten sam, który siadał przy bramie Pięknej świątyni i prosił o jałmużnę. Byli pełni najwyższego podziwu dla tego, co się zdarzyło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '10', '10', 'Gdy on się trzymał Piotra i Jana, wszyscy ludzie pełni podziwu zbiegli się do nich w portyku Salomona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '11', '11', 'Widząc to Piotr odezwał się do nich: \'Izraelici, dlaczego się tak wpatrujecie w nas i dziwicie, jakbyśmy własną mocą albo pobożnością sprawili, że on chodzi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '12', '12', 'To Bóg Abrahama i (Bóg) Izaaka, i (Bóg) Jakuba, i Bóg Ojców naszych uwielbił Sługę swego Jezusa, którego wy wydaliście i zaparliście się wobec Piłata, gdy postanowił Go uwolnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '13', '13', 'Zaparliście się więc świętego i sprawiedliwego, a zażądaliście wolności dla zabójcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '14', '14', 'Zabiliście Stwórcę życia, którego Bóg wskrzesił, czego my jesteśmy świadkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '15', '15', 'To właśnie wiara w imię Jego sprawiła, że ten, którego widzicie i znacie, stanął na nogi dzięki Niemu, a wiara w to imię na waszych oczach dała mu pełnię sił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '16', '16', 'Ale teraz wiem, bracia, że działaliście w nieświadomości, zarówno wy, jak i wasi przełożeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '17', '17', 'Bóg bowiem wypełnił w ten sposób to, co zapowiedział przez wszystkich proroków, że Mesjasz będzie cierpiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '18', '18', 'Starajcie się o wewnętrzną przemianę, nawróćcie się, a zostaną zgładzone wasze grzechy;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '19', '19', 'nastanie czas, kiedy Pan was pocieszy, zsyłając zapowiedzianego wam Mesjasza Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '20', '20', 'Niebo zatrzyma Go aż do czasu powszechnego odnowienia, o którym od dawna mówił Bóg przez świętych proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '21', '21', 'Powiedział bowiem Mojżesz: &gt;&gt;Pan Bóg da wam proroka, którego powoła jak mnie z waszych braci, słuchajcie, co wam powie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '22', '22', 'Każdy, kto nie będzie posłuszny temu prorokowi, wyłączony zostanie z tego ludu&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '23', '23', 'Dni te zapowiadali i mówili o nich kolejno wszyscy prorocy, począwszy od Samuela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '24', '24', 'A wy przecież jesteście synami proroków i Przymierza, które Bóg zawarł z waszymi ojcami, kiedy mówił do Abrahama: &gt;&gt;Przez twoje potomstwo wszyscy ludzie na ziemi zyskają błogosławieństwo&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '2', '25', '25', 'Jednak najpierw dla was wskrzesił Bóg swego Sługę posyłając Go wam, by dzięki Jego błogosławieństwu każdy z was odwrócił się od zła\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '0', '0', 'Kiedy przemawiali do ludu, zbliżyli się do nich kapłani, dowódca straży świątynnej i saduceusze');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '1', '1', 'oburzeni, że nauczają lud, głosząc zmartwychwstanie umarłych w Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '2', '2', 'Schwycili ich i wtrącili do więzienia, zatrzymując do następnego dnia, bo był już wieczór.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '3', '3', 'Wielu z tych, którzy słuchali nauki, uwierzyło, a liczba mężczyzn sięgała pięciu tysięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '4', '4', 'Następnego dnia zebrali się w Jerozolimie przełożeni, kapłani, nauczyciele Pisma');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '5', '5', '(arcykapłan Annasz, Kajfasz, Jan, Aleksander i wszyscy z rodu arcykapłańskiego),');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '6', '6', 'którzy wezwali uwięzionych do siebie i pytali: \'Jaką mocą lub w czyim imieniu to uczyniliście?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '7', '7', 'Wtedy Piotr pełen Ducha Świętego powiedział do nich: \'Przełożeni ludu i kapłani!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '8', '8', 'Skoro oskarżacie nas o to, że uzdrowiliśmy chorego człowieka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '9', '9', 'to wiedzcie zarówno wy, jak i cały lud izraelski, że on stoi przed nami zdrowy dzięki imieniu Jezusa Chrystusa, którego wyście ukrzyżowali, a Bóg wskrzesił z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '10', '10', 'To On właśnie &gt;&gt;stał się kamieniem węgielnym odrzuconym przez budowniczych&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '11', '11', 'Nie ma bowiem poza Nim zbawienia, gdyż na świecie nie ma innego imienia danego ludziom, dzięki któremu moglibyśmy być zbawieni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '12', '12', 'Widząc odwagę Piotra i Jana, ludzi prostych i niewykształconych, podziwiali ich i rozpoznali w nich tych, co przebywali z Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '13', '13', 'A patrząc na uzdrowionego człowieka stojącego z nimi nie wiedzieli, co odpowiedzieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '14', '14', 'Nakazawszy im więc opuścić sanhedryn, mówili między sobą:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '15', '15', '\'Co zrobić z tymi ludźmi? Nie możemy przecież zaprzeczyć, że dali oczywisty znak, o którym wiedzą wszyscy mieszkańcy Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '16', '16', 'By jednak ta sprawa nie nabierała większego rozgłosu wśród ludu, zabrońmy im nauczać kogokolwiek w to imię\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '17', '17', 'Wezwali ich i zakazali w ogóle przemawiać i nauczać w imię Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '18', '18', 'Na to Piotr i Jan tak im odpowiedzieli: \'Rozsądźcie, co jest słuszniejsze wobec Boga: was słuchać czy Boga?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '19', '19', 'Nie możemy nie mówić tego, cośmy widzieli i słyszeli\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '20', '20', 'Nie znajdując powodu do ukarania grozili dalej, ale wypuścili ich ze względu na lud, ponieważ wszyscy z powodu tego wydarzenia wielbili Boga;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '21', '21', 'a człowiek, który został cudownie uzdrowiony, miał lat ponad czterdzieści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '22', '22', 'Po wypuszczeniu przybyli do swoich i opowiedzieli, co im mówili arcykapłani i starsi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '23', '23', 'Gdy tego wysłuchali, modlili się wspólnie słowami: \'Panie, &gt;&gt;Stwórco nieba i ziemi, i morza, i wszystkiego, co się w nich znajduje&lt;&lt;,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '24', '24', 'który powiedziałeś przez Ojca naszego, pod wpływem Ducha Świętego, ustami sługi Twojego Dawida: &gt;&gt;Dlaczego zbuntowały się narody i ludy knuły rzeczy próżne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '25', '25', 'Powstali królowie ziemi i władcy zmówili się wspólnie przeciwko Panu i przeciwko Jego Pomazańcowi&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '26', '26', 'Rzeczywiście, przeciwko świętemu Słudze Twemu, Jezusowi, którego namaściłeś, zmówili się w tym mieście Herod i Poncjusz Piłat z narodami pogańskimi i pokoleniami Izraela,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '27', '27', 'aby dokonało się to, co Twoja wola zamierzyła uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '28', '28', 'A teraz, Panie, wejrzyj na ich groźby i daj Twoim sługom, by z pełną odwagą głosili Twoje słowo,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '29', '29', 'gdyż ręka Twoja dokona uzdrowienia, a zdumiewające znaki dziać się będą w imię świętego Sługi Twojego, Jezusa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '30', '30', 'A gdy się modlili, zadrżała ziemia w miejscu, gdzie się zebrali, i napełnieni zostali wszyscy Duchem Świętym, głosząc z odwagą słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '31', '31', 'Wszyscy wierzący stanowili jedną duszę i jedno serce, i nikt nie mówił, że cokolwiek jest jego własnością, wszystko bowiem mieli wspólne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '32', '32', 'Apostołowie z wielką mocą świadczyli o zmartwychwstaniu Pana Jezusa, życzliwość zaś ogromna towarzyszyła im wszystkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '33', '33', 'Nie było też wśród nich cierpiących niedostatku, gdyż właściciele sprzedawali swoje pola i domy, a pieniądze przynosili');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '34', '34', 'i kładli u stóp apostołów; rozdzielano zaś każdemu według jego potrzeb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '35', '35', 'Również Józef zwany przez apostołów Barnabą, to znaczy Synem Pocieszenia, Lewita, rodem z Cypru,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '3', '36', '36', 'sprzedał swoją ziemię, a pieniądze przyniósł i złożył u stóp apostołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '0', '0', 'Człowiek, który nazywał się Ananiasz, wspólnie ze swoją żoną sprzedał posiadłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '1', '1', 'Część pieniędzy za wiedzą żony zatrzymał dla siebie, a resztę przyniósł i złożył u stóp apostołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '2', '2', 'Piotr zaś powiedział: \'Ananiaszu, dlaczego szatan zawładnął tobą? Usiłowałeś oszukać Ducha Świętego, zatrzymując część zapłaty za posiadłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '3', '3', 'Czyż nie twoją własnością była posiadłość i czyż nie twoje były pieniądze z jej sprzedaży? Czemu w sercu twoim zrodziła się myśl o tym czynie? Nie ludzi oszukałeś, lecz Boga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '4', '4', 'Po tych słowach Ananiasz upadł i skonał, wszystkich zaś słuchających ogarnął wielki strach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '5', '5', 'Wstali młodzi ludzie, przykryli go, wynieśli i pochowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '6', '6', 'Po upływie około trzech godzin przyszła jego żona nie wiedząc, co się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '7', '7', 'Piotr odezwał się do niej: \'Powiedz mi, czy za tyle sprzedaliście posiadłość?\' Ona odpowiedziała: \'Tak, za tyle\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '8', '8', 'Piotr zaś do niej: \'Czemu zmówiliście się, by wystawić na próbę Ducha Pańskiego? Oto w drzwiach ci, którzy wynieśli i pochowali męża twego. Wyniosą też ciebie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '9', '9', 'Upadła nagle u jego stóp i skonała, a gdy weszli młodzieńcy, znaleźli ją martwą, wynieśli i pochowali obok jej męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '10', '10', 'A cały Kościół i wszystkich, którzy tego słuchali, ogarnął wielki strach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '11', '11', 'Apostołowie czynili wiele zdumiewających znaków wśród ludu. Wszyscy razem przebywali w portyku Salomona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '12', '12', 'Nikt z obcych nie miał odwagi przyłączyć się do nich, a lud ich wychwalał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '13', '13', 'Liczba wierzących w Pana, mężczyzn i kobiet, coraz bardziej wzrastała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '14', '14', 'Wynoszono nawet na ulice chorych i kładziono na łożach i lektykach, aby choć cień przechodzącego Piotra padł na którego z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '15', '15', 'Przychodziło też wielu ludzi z miast położonych wokół Jerozolimy, przynosząc chorych i dręczonych przez duchy nieczyste, a wszyscy zostali uzdrowieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '16', '16', 'Arcykapłan a razem z nim wszyscy należący do stronnictwa saduceuszów zapałali gniewem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '17', '17', 'zatrzymali apostołów i wtrącili ich do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '18', '18', 'A w nocy anioł Pański otworzył bramy więzienia i wyprowadzając ich powiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '19', '19', '\'Idźcie do świątyni i głoście ludowi całą naukę o tym życiu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '20', '20', 'Kiedy to usłyszeli, weszli o świcie do świątyni i nauczali. Tymczasem arcykapłan i jego otoczenie zwołali sanhedryn i radę starszych Izraela, nakazując przyprowadzić ich z więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '21', '21', 'Słudzy poszli, ale nie znaleźli ich w więzieniu. Wrócili wtedy i oznajmili:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '22', '22', '\'Więzienie zastaliśmy zamknięte i dobrze zabezpieczone, straże stały przed bramami, a gdyśmy je otworzyli, nie znaleźliśmy nikogo\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '23', '23', 'Gdy się o tym dowiedzieli dowódca straży świątynnej i arcykapłani, nie mogli pojąć, jak to się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '24', '24', 'Wtedy przyszedł ktoś i oznajmił: \'Ludzie, których wtrąciliście do więzienia, znajdują się w świątyni i nauczają lud\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '25', '25', 'Wówczas poszedł dowódca straży ze sługami i przyprowadził ich, nie stosując przemocy, obawiali się bowiem, by lud ich nie ukamienował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '26', '26', 'Przyprowadzili ich i stawili przed sanhedrynem. Zabierając głos arcykapłan');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '27', '27', 'powiedział: \'Czyż nie zabroniliśmy wam surowo nauczania w to imię? Wy jednak dalej głosicie waszą naukę w całej Jerozolimie i chcecie ściągnąć na nas krew tego człowieka\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '28', '28', 'Piotr i apostołowie odpowiedzieli na to: \'Bardziej trzeba słuchać Boga niż ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '29', '29', 'Bóg ojców naszych wskrzesił Jezusa, którego straciliście przez ukrzyżowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '30', '30', 'Bóg wywyższył Go jako Władcę i Zbawiciela po swojej prawicy, dając Izraelowi możność nawrócenia i odpuszczenia grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '31', '31', 'My właśnie jesteśmy tego świadkami, jak i Duch Święty, którego Pan dał tym, którzy Mu są posłuszni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '32', '32', 'Kiedy oni to usłyszeli, rozgniewali się bardzo i chcieli ich zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '33', '33', 'Wtedy (na posiedzeniu) sanhedrynu zabrał głos pewien faryzeusz Gamaliel, nauczyciel Prawa szanowany przez cały lud; polecił na chwilę wyprowadzić apostołów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '34', '34', 'a do nich powiedział: \'Izraelici, zastanówcie się, zanim postanowicie uczynić coś z tymi ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '35', '35', 'Niedawno temu wystąpił Teudas, podając się za kogoś niezwykłego, i przyłączyło się do niego około czterystu ludzi. Został zabity i wszyscy jego zwolennicy rozproszyli się, i nikt z nich nie został.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '36', '36', 'Potem w czasie spisu pojawił się Judasz z Galilei i także pociągnął za sobą lud, a kiedy zginął, wszyscy jego zwolennicy również się rozproszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '37', '37', 'Wobec tego mówię wam, zostawcie tych ludzi i zwolnijcie ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '38', '38', 'Jeżeli to jest wymysł czy dzieło czysto ludzkie, rozpadnie się, a jeżeli pochodzi od Boga, nie zdołacie go zniszczyć, a może się okazać, że walczycie z Bogiem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '39', '39', 'Zgodzili się z nim. Wezwali apostołów, ukarali chłostą nakazując, aby nie przemawiali w imię Jezusa i wypuścili ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '40', '40', 'Z radością wychodzili (z posiedzenia) sanhedrynu, że godni byli znosić zniewagę dla imienia Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '4', '41', '41', 'Nie przestawali codziennie nauczać w świątyni i w domach, głosząc dobrą nowinę o Jezusie Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '0', '0', 'W tym czasie, gdy wzrastała liczba uczniów, helleniści zaczęli szemrać przeciwko Hebrajczykom, że wspierając codziennie ubogich zaniedbywali ich wdowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '1', '1', 'W związku z tym Dwunastu zwołało wszystkich uczniów i oświadczyło: \'Niesłuszne jest, byśmy zostawili głoszenie słowa Bożego na rzecz obsługiwania stołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '2', '2', 'Rozejrzycie się więc, bracia, by wybrać spośród was siedmiu mężczyzn, cieszących się dobrą opinią, pełnych ducha i mądrości, którym zlecimy to zadanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '3', '3', 'My zaś oddawać się będziemy nieustannie modlitwie i posłudze słowa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '4', '4', 'Słowa te spodobały się całej gminie. Wybrano Szczepana, człowieka pełnego wiary i Ducha Świętego, Filipa, Prochora, Nikanora, Tymona, Parmenasa i Mikołaja, prozelitę z Antiochii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '5', '5', 'Zaprowadzono ich do apostołów, którzy w czasie modlitwy włożyli na nich ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '6', '6', 'A słowo Boże rozszerzało się i zwiększała się coraz bardziej liczba uczniów w Jerozolimie. Nawet wielu przysłuchujących się kapłanów przyjęło wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '7', '7', 'Szczepan, pełen łaski i mocy, dokonywał wśród ludu wiele zdumiewających znaków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '8', '8', 'Wtedy podjęli z nim dyskusję niektórzy ludzie, należący do synagogi Wyzwoleńców, Cyrenejczyków i Aleksandryjczyków, mieszkańcy Cylicji i Azji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '9', '9', 'Nie mogąc jednak dorównać mądrości i Duchowi, pod wpływem którego przemawiał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '10', '10', 'podstawili ludzi, aby zeznali: \'Słyszeliśmy, jak rzucał bluźnierstwa przeciwko Mojżeszowi i Bogu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '11', '11', 'Podburzyli lud, kapłanów i nauczycieli Pisma, podeszli do niego, schwytali i zaprowadzili przed sanhedryn,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '12', '12', 'a potem postawili fałszywych świadków, którzy oświadczyli: \'Człowiek ten nie przestaje bluźnić przeciwko miejscu świętemu i Prawu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '13', '13', 'Słyszeliśmy bowiem, jak mówił, że Jezus z Nazaretu, zburzy to miejsce i zmieni zwyczaje, które dał nam Mojżesz\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '5', '14', '14', 'Wszyscy zasiadający w sanhedrynie wpatrywali się w niego i widzieli, że twarz jego podobna jest do oblicza anioła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '0', '0', 'Zapytał go arcykapłan: \'Czy tak się sprawa przedstawia?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '1', '1', 'On zaś odpowiedział: \'Ojcowie i bracia, słuchajcie! Bóg chwały ukazał się ojcu naszemu Abrahamowi, gdy przebywał w Mezopotamii, zanim zamieszkał w Charan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '2', '2', '&gt;&gt;I powiedział do niego: Wyjdź ze swej ziemi i opuść rodzinę, a idź do ziemi, którą ci ukażę&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '3', '3', 'Opuścił wtedy ziemię chaldejską i zamieszkał w Charan, a stąd po śmierci ojca swego przeniósł się do tej ziemi, którą wy teraz zamieszkujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '4', '4', 'Nie dał mu jednak w posiadanie nawet skrawka ziemi, ale obiecał &gt;&gt;dać ją później na własność jemu i jego potomstwu&lt;&lt;, chociaż Abraham nie miał jeszcze syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '5', '5', 'Bóg powiedział: &gt;&gt;Potomstwo jego będzie przechodniem w obcej ziemi i podbiją je w niewolę, źle się z nim obchodząc przez czterysta lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '6', '6', 'A naród, któremu będą służyć, Ja osądzę&lt;&lt; - mówi Bóg - &gt;&gt;i wyjdzie potem, aby Mnie czcić na tym miejscu&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '7', '7', 'Potem zawarł z nim Przymierze, którego znakiem było obrzezanie. Wtedy urodził mu się Izaak. I obrzezał go ósmego dnia. Tak samo Izaak (obrzezał) Jakuba, a Jakub dwunastu patriarchów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '8', '8', 'Patriarchowie z zazdrości sprzedali Józefa do Egiptu, ale Bóg go nie opuścił,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '9', '9', 'uwalniając go od wszystkich utrapień. I dał mu wraz z mądrością życzliwość faraona, króla Egiptu, który ustanowił go zarządcą Egiptu i wszystkich posiadłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '10', '10', 'Wtedy zapanowała wielka klęska głodu w Egipcie i Kanaan; ojcowie nasi nie mieli co jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '11', '11', 'Jakub dowiedział się, że w Egipcie jest żywność, wysłał więc najpierw naszych ojców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '12', '12', 'Za drugim razem Józef dał już się poznać braciom i w ten sposób faraon dowiedział się o jego pochodzeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '13', '13', 'Józef odesłał ich i wezwał ojca swego Jakuba i całą rodzinę, składającą się z siedemdziesięciu pięciu osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '14', '14', 'Tak przeniósł się Jakub do Egiptu, gdzie umarł on sam i ojcowie nasi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '15', '15', 'Przeniesiono ich do Sychem i pochowano w grobowcu, który nabył tam Abraham za pieniądze od synów Hemora.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '16', '16', 'Gdy nadchodził czas wypełnienia obietnicy, którą dał Bóg Abrahamowi, lud rozrósł się i rozmnożył w Egipcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '17', '17', 'Trwało to aż do czasu nastania innego króla w Egipcie, który nie znał już Józefa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '18', '18', 'Działał on podstępnie przeciwko naszemu narodowi, źle traktując przodków, nakazał bowiem pozbawiać życia noworodki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '19', '19', 'W tym czasie urodził się Mojżesz, który był miły Bogu. Karmiono go przez trzy miesiące w domu ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '20', '20', 'Kiedy go wyrzucono, wzięła go córka faraona i wychowała jak własnego syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '21', '21', 'Mojżesz zdobył w Egipcie wszechstronne wykształcenie, odznaczając się siłą słowa i czynu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '22', '22', 'Gdy skończył czterdzieści lat, zapragnął odwiedzić swoich braci Izraelitów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '23', '23', 'Zobaczył wtedy, jak któregoś z nich krzywdzono. Wziął go w obronę i pomścił udręczonego, zabijając Egipcjanina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '24', '24', 'Sądził bowiem, że znajdzie zrozumienie u swoich rodaków, że przez niego Bóg chce ich wybawić. Oni jednak nie zrozumieli go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '25', '25', 'Następnego dnia zjawił się wśród nich, gdy się kłócili między sobą, a usiłując ich pogodzić, powiedział: &gt;&gt;Ludzie! Braćmi jesteście. Czemu krzywdzicie się wzajemnie?&lt;&lt;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '26', '26', 'Wtedy ten, który wyrządził krzywdę bliźniemu, odepchnął go i powiedział: &gt;&gt;Któż ustanowił cię zwierzchnikiem i sędzią nad nami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '27', '27', 'Czy mnie też chcesz zabić, jak wczoraj zabiłeś Egipcjanina?&lt;&lt;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '28', '28', 'Usłyszawszy te słowa Mojżesz uciekł i zamieszkał jako cudzoziemiec w ziemi Madian, gdzie urodziło mu się dwóch synów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '29', '29', 'Kiedy upłynęło czterdzieści lat, na pustyni góry Synaj w płomieniu krzaka ognistego ukazał mu się anioł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '30', '30', 'Mojżesz przypatrywał się temu zdumiony, a gdy się zbliżał, by to dokładniej zobaczyć, rozległ się głos Pana:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '31', '31', '&gt;&gt;Ja jestem Bogiem ojców twoich, Bogiem Abrahama, Izaaka i Jakuba&lt;&lt;. Przeraził się wtedy Mojżesz i nie śmiał nawet podnieść wzroku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '32', '32', 'Powiedział mu Pan: &gt;&gt;Zdejmij obuwie z nóg twoich, bo miejsce, na którym stoisz, jest ziemią świętą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '33', '33', 'Napatrzyłem się na krzywdy ludu mojego w Egipcie i jęki jego dochodziły do Mnie, dlatego zstąpiłem, aby go uwolnić. Chodź więc, poślę cię do Egiptu&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '34', '34', 'Posłał więc Bóg Mojżesza jako wodza i wybawcę, tego właśnie, którego zaparli się pytając: &gt;&gt;Kto cię ustanowił wodzem i sędzią?&lt;&lt; A dokonał tego z pomocą anioła, który ukazał mu się w krzaku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '35', '35', 'Mojżesz ich wyprowadził, dokonując znaków i cudów w ziemi egipskiej, na Morzu Czerwonym i przez czterdzieści lat na pustyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '36', '36', 'Tenże Mojżesz powiedział synom Izraela: &gt;&gt;Proroka powoła dla was Bóg, podobnie jak mnie, spośród braci waszych&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '37', '37', 'To właśnie Mojżesz otrzymał słowa życia, by nam je przekazać wtedy, gdy zebrali się na pustyni ojcowie nasi i anioł, z którym on rozmawiał na górze Synaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '38', '38', 'Ojcowie nasi nie chcieli go jednak słuchać, ale odepchnęli go, zwracając swe serca ku Egiptowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '39', '39', 'Powiedzieli do Aarona: &gt;&gt;Daj nam bogów, którzy będą nas prowadzić, bo nie wiemy, co się stało z Mojżeszem, który wyprowadził nas z ziemi egipskiej&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '40', '40', 'Wtedy właśnie uczynili sobie cielca i złożyli ofiarę bożkowi, ciesząc się dziełami rąk swoich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '41', '41', 'Wtedy Bóg odwrócił się i dopuścił, że oddawali część ciałom niebieskim, jak napisane zostało w Księdze Proroków: &gt;&gt;Ludu Izraela, czy nie składałeś mi darów i ofiar przez czterdzieści lat na pustyni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '42', '42', 'Dlaczego teraz obnosisz namiot Molocha? I gwiazdy boga twojego Remfana?&lt;&lt; Na ich część &gt;&gt;uczyniłeś obrazy, dlatego wyrzucę cię poza Babilon&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '43', '43', 'Namiot Przymierza mieli ojcowie nasi na pustyni, zgodnie z poleceniem danym Mojżeszowi, aby go zbudował według wzoru, który widział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '44', '44', 'Po otrzymaniu go ojcowie nasi pod wodzą Jozuego wnieśli go do ziemi, z której Bóg wypędził pogan dla ojców naszych; tak też pozostało do czasów Dawida.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '45', '45', 'Będąc w łasce u Boga, prosił o miejsce na dom dla Boga Jakuba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '46', '46', 'Dopiero jednak Salomon zbudował Mu dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '47', '47', 'Ale Najwyższy nie mieszka w domach zbudowanych ręką ludzką, jak mówi prorok:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '48', '48', '&gt;&gt;Tronem moim jest niebo, ziemia podnóżkiem nóg moich. Jaki dom możecie mi zbudować? - mówi Pan. Albo jakie miejsce może być dla mnie wytchnieniem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '49', '49', 'Przecież to wszystko moja ręka uczyniła&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '50', '50', 'Ludzie twardego karku, nieczułych serc i głuchych uszu, zawsze opieraliście się Duchowi Świętemu, zarówno wy, jak i ojcowie wasi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '51', '51', 'Którego to z proroków nie prześladowali ojcowie wasi? Zabijali nawet tych, którzy przepowiadali przyjście Sprawiedliwego; a wy Go zdradziliście i zabiliście!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '52', '52', 'Wy, którzy otrzymaliście prawo przez aniołów, a nie przestrzegaliście go!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '53', '53', 'Gdy go słuchali, zawrzało w ich sercach i zgrzytali zębami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '54', '54', 'Szczepan napełniony Duchem Świętym patrzył w niebo i widział chwałę Bożą oraz Jezusa po prawicy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '55', '55', 'Powiedział wtedy: \'Oto widzę niebo otwarte i Syna Człowieczego po prawicy Bożej\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '56', '56', 'Zaczęli przeraźliwie krzyczeć, pozatykali sobie uszy, rzucili się na niego, wyprowadzili za miasto i kamienowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '57', '57', 'Świadkowie złożyli swoje szaty u stóp młodzieńca zwanego Szawłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '58', '58', 'I kamienowali Szczepana, on zaś modlił się tymi słowami: \'Panie Jezu, przyjmij ducha mego!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '6', '59', '59', 'W końcu upadł na kolana i zawołał donośnym głosem: \'Panie, nie poczytaj im tego za grzech!\' Po tych słowach skonał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '0', '0', 'Szaweł zaś zgadzał się na jego śmierć. A było to w czasie wielkiego prześladowania Kościoła w Jerozolimie. Wszyscy oprócz apostołów rozproszyli się po terenach Judei i Samarii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '1', '1', 'Z płaczem i bólem pochowali Szczepana pobożni ludzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '2', '2', 'Szaweł zaś burząc Kościół wpadał do domów, wyciągał mężczyzn i kobiety i wtrącał do więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '3', '3', 'Rozproszeni po różnych miejscach głosili dobrą nowinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '4', '4', 'I tak Filip doszedł do miasta Samarii i nauczał tam o Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '5', '5', 'Tłumy z ogromną uwagą i zainteresowaniem słuchały słów Filipa patrząc na znaki, które czynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '6', '6', 'Z wielu bowiem opętanych z wielkim krzykiem wychodziły duchy nieczyste, wielu paralityków i kalek zostało uzdrowionych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '7', '7', 'co wywołało wielką radość w tym mieście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '8', '8', 'Mieszkał w tym mieście od dawna człowiek imieniem Szymon, zajmujący się magią. Uważał się za kogoś wielkiego i podziwiany był przez Samarytan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '9', '9', 'Słuchali go więc wszyscy od małego do wielkiego i mówili: \'Ten jest wielką mocą Bożą\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '10', '10', 'Od dawna go słuchali, zwodził ich bowiem magicznymi sztuczkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '11', '11', 'Gdy jednak uwierzyli Filipowi, który głosił dobrą nowinę o królestwie Bożym i o imieniu Jezusa Chrystusa, dali się ochrzcić zarówno mężczyźni, jak i kobiety.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '12', '12', 'Uwierzył nawet sam Szymon, został ochrzczony i nie odstępował od Filipa, był bowiem pełen podziwu na widok nadziemskiej mocy i wielu znaków, których (Filip) dokonywał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '13', '13', 'Kiedy apostołowie znajdujący się w Jerozolimie dowiedzieli się, że w Samarii przyjęto słowo Boże, wysłali tam Piotra i Jana,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '14', '14', 'którzy modlili się nad nimi, aby otrzymali Ducha Świętego;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '15', '15', 'dotychczas bowiem na nikogo z nich nie zstąpił, byli tylko ochrzczeni w imię Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '16', '16', 'I wtedy ci, na których wkładali ręce, otrzymywali Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '17', '17', 'Kiedy Szymon zobaczył, że przez wkładanie rąk apostołowie udzielają Ducha Świętego, przyniósł im pieniądze i powiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '18', '18', '\'Udzielcie i mnie tej władzy, abym przez wkładanie rąk mógł także udzielać Ducha Świętego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '19', '19', 'Wtedy Piotr powiedział do niego: \'Niech zginą twoje pieniądze i ty razem z nimi, sądziłeś bowiem, że dar Boży można nabyć za pieniądze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '20', '20', 'Nie możesz mieć żadnego udziału w tym dziele, gdyż w sercu twoim nie ma szczerości wobec Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '21', '21', 'Odwróć się od tego zła i proś Pana, a może ci przebaczy zamiar, który zrodził się w twoim sercu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '22', '22', 'Bo widzę, że jesteś pełen żółci i uwikłany w nieprawości\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '23', '23', 'Szymon odpowiedział na to: \'Módlcie się za mnie do Pana, aby nie spotkało mnie to, o czym mówiliście\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '24', '24', 'Kiedy już dali świadectwo słowu Pańskiemu, które głosili, udali się w powrotną drogę do Jerozolimy, w wielu jeszcze wioskach samarytańskich głosząc dobrą nowinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '25', '25', 'Anioł Pański powiedział Filipowi: \'Wstań i około południa udaj się na drogę, prowadzącą z Jerozolimy do Gazy; będzie ona pusta\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '26', '26', 'Wstał więc i poszedł. W tym czasie Etiopczyk, wysoki urzędnik królowej etiopskiej Kandaki, zarządzający całym jej skarbem, powracał z Jerozolimy. Był tam złożyć hołd Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '27', '27', 'Siedząc na wozie, czytał proroka Izajasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '28', '28', 'Powiedział Duch do Filipa: \'Zbliż się i idź przy wozie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '29', '29', 'Gdy Filip dobiegł, usłyszał, że czyta proroka Izajasza. Zapytał więc: \'Czy ty rozumiesz, co czytasz?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '30', '30', 'Odpowiedział: \'Skąd bym mógł, skoro mi nikt nie objaśni\' i poprosił Filipa, aby wsiadł do wozu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '31', '31', 'A urywek Pisma, który czytał, był następujący: \'Jak owca na zabicie prowadzona, jak milczący baranek, gdy go strzygą, tak on nie otwiera ust.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '32', '32', 'W chwili uniżenia nie przyznano mu słuszności, któż opisze jego ród, bo życie jego z ziemi będzie wymazane\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '33', '33', 'Zapytał dworzanin Filipa: \'Proszę cię, o kim to prorok mówi, o sobie, czy o kimś innym?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '34', '34', 'Nawiązując do przeczytanego tekstu opowiedział mu Filip dobrą nowinę o Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '35', '35', 'Jadąc tą drogą, przybyli nad jakąś wodę; wtedy powiedział dworzanin: \'Patrz, woda! Czy istnieje jakaś przeszkoda, abyś mnie ochrzcił?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '36', '36', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '37', '37', 'Zatrzymał więc wóz, weszli do wody i Filip ochrzcił dworzanina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '38', '38', 'Gdy wyszli z wody, Duch porwał Filipa i już go więcej dworzanin nie zobaczył, z radością jednak jechał dalej tą drogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '7', '39', '39', 'Filip zaś znalazł się w Azot i chodząc po różnych miastach głosił dobrą nowinę, aż doszedł do Cezarei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '0', '0', 'A Szaweł nadal pałał nienawiścią i chęcią mordowania uczniów Pana. Udał się do arcykapłana');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '1', '1', 'i poprosił go o listy do synagog w Damaszku, aby mógł uwięzić i przyprowadzić do Jerozolimy wyznawców Jezusa, mężczyzn i kobiety, gdyby ich znalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '2', '2', 'W drodze, gdy zbliżał się już do Damaszku, olśniło go nagle światło z nieba;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '3', '3', 'upadł wtedy na ziemię i usłyszał głos, skierowany do siebie: \'Szawle, Szawle, czemu Mnie prześladujesz?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '4', '4', 'A on zapytał: \'Kim jesteś, Panie?\' \'Ja jestem Jezusem, którego ty prześladujesz - odpowiedział. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '5', '5', 'Wstań, idź do miasta, a tam ci powiedzą, co powinieneś czynić\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '6', '6', 'Ludzie towarzyszący mu w drodze stanęli jak wryci, słyszeli bowiem głos, ale nikogo nie widzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '7', '7', 'Szaweł zaś podniósł się z ziemi, a gdy otworzył oczy, nic nie widział. Wtedy wzięli go pod ręce i przeprowadzili do Damaszku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '8', '8', 'Przez trzy dni nic nie widział, nie jadł i nie pił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '9', '9', 'W Damaszku mieszkał uczeń Ananiasz; do niego w widzeniu zwrócił się Pan: \'Ananiaszu\'. \'Jestem, Panie\' - odpowiedział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '10', '10', '\'Wstań, idź na ulicę Prostą - powiedział do niego - i zapytaj w domu Judy o Szawła z Tarsu - on się tam modli\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '11', '11', '(Zobaczył Szaweł w widzeniu Ananiasza, jak wchodzi, wkłada ręce, by przywrócić mu wzrok).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '12', '12', '\'Panie - odpowiedział Ananiasz - słyszałem od wielu, ile zła ten człowiek wyrządził świętym Twoim w Jerozolimie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '13', '13', 'ma nawet polecenie od arcykapłanów, by więzić wszystkich Twoich wyznawców\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '14', '14', 'Pan mu odpowiedział: \'Idź, bo on jest narzędziem przeze Mnie wybranym, on zaniesie imię moje narodom, królom i synom Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '15', '15', 'Ja mu ukażę, co będzie musiał wycierpieć dla imienia mego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '16', '16', 'Wtedy Ananiasz poszedł, udał się do owego domu, położył na nim ręce i powiedział: \'Szawle, bracie, Pan Jezus, Ten, co ukazał ci się w drodze, którą szedłeś, przysłał mnie, abyś odzyskał wzrok i napełnił cię Duch Święty\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '17', '17', 'Nagle z jego oczu spadły jakby łuski, odzyskał wzrok, wstał potem i przyjął chrzest,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '18', '18', 'następnie spożył pokarm i odzyskał siły. Przez kilka dni pozostał w Damaszku u uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '19', '19', 'Tam też w synagogach nauczał, że Jezus jest Synem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '20', '20', 'Wszyscy słuchający go dziwili się i pytali: \'Czy to nie ten, który prześladował w Jerozolimie wyznawców tego imienia, i właśnie po to tu przybył, aby ich uwięzić i zaprowadzić do arcykapłanów?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '21', '21', 'Lecz Szaweł występował coraz odważniej, wywołując zamieszanie wśród Żydów mieszkających w Damaszku, bo dowodził, że (Jezus) jest Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '22', '22', 'Po upływie dłuższego czasu Żydzi postanowili go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '23', '23', 'Szaweł dowiedział się o ich zamiarze. Aby móc go zgładzić, strzegli nawet bram we dnie i w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '24', '24', 'Ale uczniowie jego w nocy spuścili go w koszu przez mur.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '25', '25', 'Gdy przybył do Jerozolimy, starał się przyłączyć do uczniów, ale wszyscy bali się go, nie wierząc, że jest uczniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '26', '26', 'Wtedy Barnaba przyszedł z nim do apostołów i opowiedział im o tym, jak to w drodze Szaweł widział Pana, który przemówił do niego, i z jakim zapałem nauczał w Damaszku w imię Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '27', '27', 'Odtąd czuł się w Jerozolimie wśród nich swobodnie, nauczał w imię Pana,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '28', '28', 'rozprawiał i dyskutował z hellenistami, którzy próbowali go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '29', '29', 'Gdy bracia się o tym dowiedzieli, odprowadzili go do Cezarei i wysłali do Tarsu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '30', '30', 'A Kościół w całej Judei, Galilei i Samarii zażywał pokoju. Rozwijał się, trwał w bojaźni Pańskiej pocieszany przez Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '31', '31', 'Piotr odwiedzając wszystkich, przyszedł do świętych mieszkających w Lyddzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '32', '32', 'Spotkał tam człowieka imieniem Eneasz, który będąc paralitykiem od ośmiu lat leżał w łóżku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '33', '33', 'I powiedział do niego: \'Eneaszu, Jezus Chrystus cię uzdrawia. Wstań i zaściel swoje łóżko\', a on wstał natychmiast.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '34', '34', 'Gdy zobaczyli go wszyscy mieszkańcy Lyddy i Saronu, nawrócili się do Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '35', '35', 'W Joppie mieszkała uczennica imieniem Tabita, to znaczy Gazela. Spełniała wiele dobrych uczynków i rozdawała jałmużnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '36', '36', 'W tych właśnie dniach zachorowała i umarła; obmyto ją i położono w pokoju na górze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '37', '37', 'A Joppa leżała w pobliżu Lyddy. Gdy uczniowie dowiedzieli się, że przebywa tam Piotr, wysłali do niego dwóch ludzi z prośbą: \'Nie zwlekaj z przyjściem do nas\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '38', '38', 'Piotr więc poszedł razem z nimi. Gdy przyszedł, zaprowadzili go na piętro. Wtedy podeszły do niego z płaczem wszystkie wdowy i pokazywały mu chitony i okrycia, wykonane przez Gazelę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '39', '39', 'Piotr prosił, by wszyscy wyszli z mieszkania, upadł na kolana i modlił się, a potem zwrócił się do umarłej ze słowami: \'Tabito, wstań\'. Ona zaś otworzyła oczy, zobaczyła Piotra i usiadła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '40', '40', 'Podał jej rękę, by mogła wstać, zawołał świętych i wdowy i pokazał, że żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '41', '41', 'Gdy cała Joppa się o tym dowiedziała, wielu uwierzyło w Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '8', '42', '42', 'Przez dłuższy czas Piotr przebywał w Joppie u Szymona garbarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '0', '0', 'W Cezarei mieszkał setnik kohorty italskiej - Korneliusz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '1', '1', 'Był to człowiek pobożny, z całym swoim domem żył w bojaźni Bożej, hojnie rozdawał ubogim jałmużnę i nieustannie modlił się do Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '2', '2', 'Około godziny dziewiątej miał widzenie. Ujrzał wtedy anioła Bożego, który wchodząc do niego powiedział: \'Korneliuszu!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '3', '3', 'Ten zaś wpatrując się w niego, ze strachem zapytał: \'O co chodzi, panie?\'. \'Modlitwy twoje dotarły przed oblicze Boga, a jałmużny przypomniały Mu o tobie - odpowiedział anioł. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '4', '4', 'Poślij teraz ludzi do Joppy, niech przyprowadzą Szymona Piotra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '5', '5', 'Jest on gościem Szymona garbarza, którego dom położony jest nad morzem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '6', '6', 'Kiedy odszedł anioł, który z nim rozmawiał, zawołał dwóch domowników i pobożnego żołnierza spośród tych, którzy mu podlegali,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '7', '7', 'opowiedział o wszystkim i wysłał ich do Joppy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '8', '8', 'Następnego dnia, gdy byli w drodze i zbliżali się już do miasta, Piotr wszedł na dach, aby się modlić. Było około godziny szóstej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '9', '9', 'Odczuwał już głód i chciał coś zjeść, a gdy mu przygotowywano posiłek, wpadł w ekstazę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '10', '10', 'Zobaczył niebo otwarte i opadający przedmiot, coś w rodzaju wielkiego płótna, które czterema końcami spadało na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '11', '11', 'Były na nim wszystkie zwierzęta czworonożne, płazy i ptaki latające w powietrzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '12', '12', 'Usłyszał wówczas głos skierowany do niego: \'Piotrze, wstań, zabijaj i jedz!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '13', '13', 'Piotr odpowiedział: \'Panie, za nic w świecie, nigdy bowiem nie jadłem czegoś nieczystego i skażonego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '14', '14', 'I znowu usłyszał głos: \'Przestań nazywać skażonym to, co Bóg oczyścił\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '15', '15', 'Powtórzyło się to trzy razy, a przedmiot ów natychmiast został wzięty do nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '16', '16', 'Kiedy Piotr zastanawiał się nad znaczeniem tego widzenia, ludzie wysłani przez Korneliusza dowiedzieli się już, gdzie mieszka Szymon, stanęli u bramy');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '17', '17', 'i głośno pytali, czy jest tu w gościnie Szymon Piotr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '18', '18', 'A gdy Piotr jeszcze nadal zastanawiał się nad widzeniem, wtedy powiedział do niego Duch: \'Właśnie szuka cię dwóch mężczyzn.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '19', '19', 'Wstań więc, zejdź na dół i idź z nimi bez wahania, bo to Ja ich przysłałem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '20', '20', 'Zszedł do nich Piotr i powiedział: \'To ja właśnie jestem tym, kogo szukacie. Co was tu sprowadza?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '21', '21', 'Odpowiedzieli: \'Setnik Korneliusz, człowiek sprawiedliwy i bogobojny, cieszący się uznaniem całej ludności żydowskiej, otrzymał polecenie od świętego anioła, aby cię sprowadził do swego domu i wysłuchał, co mu powiesz\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '22', '22', 'Zaprosił ich więc w gościnę. Na drugi dzień wstał i wyruszył z nimi. Poszli z nim także niektórzy bracia z Joppy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '23', '23', 'Nazajutrz przybyli do Cezarei. Korneliusz, czekając na nich, zaprosił swoich krewnych i najbliższych przyjaciół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '24', '24', 'Kiedy Piotr wchodził do domu Korneliusza, ten wyszedł mu naprzeciw, upadł do nóg i oddał mu pokłon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '25', '25', 'Piotr zaś podniósł go i powiedział: \'Wstań, ja też jestem człowiekiem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '26', '26', 'Rozmawiając z nim wszedł do środka, gdzie zastał wielu zebranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '27', '27', 'Odezwał się więc do nich: \'Wiecie, że nie wolno Żydowi przebywać i przestawać z cudzoziemcem. Bóg jednak pouczył mnie, abym żadnego człowieka nie nazywał skażonym lub nieczystym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '28', '28', 'Dlatego wezwany przybyłem bez ociągania. Pytam więc, dlaczego mnie wezwaliście?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '29', '29', 'Korneliusz odpowiedział: \'Gdy cztery dni temu także o dziewiątej godzinie modliłem się w swoim domu, stanął przede mną człowiek w jaśniejącej szacie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '30', '30', 'i powiedział: &gt;&gt;Korneliuszu, wysłuchana została twoja modlitwa, a o jałmużnach twoich wspomniano przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '31', '31', 'Poślij więc do Joppy po Szymona Piotra, który zatrzymał się w domu garbarza nad morzem&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '32', '32', 'Zaraz więc po ciebie posłałem i dobrze zrobiłeś, że przyszedłeś. Teraz więc stoimy wszyscy przed Bogiem, aby wysłuchać wszystkiego, co ci Pan polecił\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '33', '33', 'Piotr zabrał głos: \'Teraz z całą pewnością rozumiem, że Bóg nie gardzi żadnym człowiekiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '34', '34', 'ale w każdym narodzie miły Mu jest ten, kto jest bogobojny i prawy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '35', '35', 'Posłał więc Bóg słowo synom Izraela, głosząc dobrą nowinę pokoju przez Jezusa Chrystusa - On jest Panem wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '36', '36', 'Wiecie przecież, co się wydarzyło w całej Judei, jak to poczynając od Galilei, po chrzcie, który głosił Jan,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '37', '37', 'Jezus z Nazaretu został przez Boga namaszczony Duchem Świętym i mocą, przeszedł dobrze czyniąc, uzdrawiał opętanych przez diabła, bo Bóg był z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '38', '38', 'A my jesteśmy świadkami tego wszystkiego, czego dokonał w całej ziemi żydowskiej i w Jerozolimie; Jego to zabili, zawiesiwszy na drzewie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '39', '39', 'Ale Bóg wskrzesił Go trzeciego dnia i pozwolił Mu się ukazać w widzialnej postaci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '40', '40', 'nie wszystkim jednak ludziom, ale nam, wybranym uprzednio przez Boga świadkom, którzyśmy razem z Nim jedli i pili po zmartwychwstaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '41', '41', 'Polecił nam głosić i świadczyć przed ludźmi, że Bóg ustanowił Go sędzią żywych i umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '42', '42', 'To o Nim właśnie dają świadectwo wszyscy prorocy głosząc, że każdy, kto wierzy w Niego, przez Jego imię otrzymuje odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '43', '43', 'Już w czasie przemówienia Piotra Duch Święty zstąpił na wszystkich, którzy słuchali jego nauki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '44', '44', 'A wierni żydowskiego pochodzenia, którzy przybyli z Piotrem, zdziwili się, że i poganie otrzymali dar Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '45', '45', 'Słyszeli bowiem, jak mówili językami i wielbili Boga. Wtedy zapytał Piotr:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '46', '46', '\'Któż może odmówić chrztu tym, którzy otrzymali Ducha Świętego tak samo jak my?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '9', '47', '47', 'Polecił więc ochrzcić ich w imię Jezusa Chrystusa. Wtedy prosili go, aby zatrzymał się jeszcze u nich przez kilka dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '0', '0', 'Apostołowie i bracia przebywający w Judei dowiedzieli się, że i poganie przyjęli słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '1', '1', 'Gdy więc Piotr przybył do Jerozolimy, ci, którzy byli pochodzenia żydowskiego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '2', '2', 'robili mu wymówki: \'Poszedłeś do ludzi nie obrzezanych i jadłeś z nimi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '3', '3', 'Piotr więc zaczął im po kolei wyjaśniać:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '4', '4', '\'Modliłem się w mieście Joppa i w zachwyceniu zobaczyłem jakiś przedmiot, podobny do wielkiego płótna, które spuszczając się czterema końcami z nieba opadało aż do mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '5', '5', 'Uważnie się w nie wpatrując, zobaczyłem czworonożne zwierzęta domowe i dzikie, płazy i ptaki, które latają w powietrzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '6', '6', 'Usłyszałem też głos skierowany do mnie: &gt;&gt;Wstań, Piotrze, zabijaj i jedz&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '7', '7', 'Ja na to odpowiedziałem: &gt;&gt;O nie, Panie! Nigdy bowiem do ust nie wziąłem nic skażonego lub nieczystego&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '8', '8', 'I powtórnie odezwał się głos z nieba: &gt;&gt;Nie nazywaj nieczystym tego, co Bóg oczyścił&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '9', '9', 'Powtórzyło się to trzy razy i wszystko z powrotem zostało wzięte do nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '10', '10', 'Potem przed domem, w którym mieszkałem, stanęło trzech ludzi przysłanych do mnie z Cezarei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '11', '11', 'Duch zaś powiedział do mnie, abym bez wahania z nimi poszedł. Wybrało się więc ze mną również sześciu braci i przybyliśmy do domu tego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '12', '12', 'On zaś opowiedział nam, jak w swoim domu ujrzał anioła, jak stanął przed nim i powiedział: &gt;&gt;Poślij do Joppy po Szymona Piotra,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '13', '13', 'który przekaże ci naukę, dzięki której sam się zbawisz i cały twój dom&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '14', '14', 'Gdy zacząłem przemawiać, Duch Święty zstąpił na nich, tak jak na nas na początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '15', '15', 'Przypomniałem sobie słowa Pana: &gt;&gt;Jan chrzcił wodą, wy zaś zostaniecie ochrzczeni Duchem Świętym&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '16', '16', 'Jeżeli więc Bóg dał im ten sam dar, co i nam wierzącym w Pana Jezusa Chrystusa, czyż mogłem się Bogu przeciwstawić?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '17', '17', 'Kiedy to usłyszeli, zamilkli, a wielbiąc Boga mówili: \'To już i poganom dał Bóg łaskę nawrócenia, która zapewnia życie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '18', '18', 'Ci więc, którzy się rozproszyli na skutek prześladowania, jakie wybuchło z powodu Szczepana, dotarli aż do Fenicji, Cypru i Antiochii, głosząc naukę jedynie Żydom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '19', '19', 'Wśród nich znajdowali się Cypryjczycy i Cyrenejczycy, którzy po przybyciu do Antiochii przemawiali także do Greków, głosząc dobrą nowinę o Panu Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '20', '20', 'A ręka Pańska była z nimi, wielka bowiem liczba uwierzyła i nawróciła się do Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '21', '21', 'Wieść o tym dotarła do Kościoła w Jerozolimie, dlatego do Antiochii wysłano Barnabę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '22', '22', 'Gdy przybył tam i zobaczył działanie łaski Bożej, uradował się; zachęcał też wszystkich, by całym sercem wytrwali przy Panu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '23', '23', 'był bowiem człowiekiem dobrym, pełnym Ducha Świętego i wiary. Bardzo wielu też uwierzyło w Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '24', '24', 'Następnie wyruszył do Tarsu, by poszukać Szawła,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '25', '25', 'a gdy go odnalazł, przyprowadził do Antiochii. Pracowali razem w Kościele przez cały rok, nauczając tłumy ludzi. W Antiochii po raz pierwszy nazwano uczniów chrześcijanami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '26', '26', 'W tym czasie przybyli prorocy z Jerozolimy do Antiochii. Jeden z nich imieniem Agabus');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '27', '27', 'pod natchnieniem Ducha Świętego przepowiedział wielki głód, który dotknie całą ziemię, co rzeczywiście miało miejsce za Klaudiusza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '28', '28', 'W związku z tym uczniowie postanowili, że każdy w miarę możności przyjdzie z pomocą braciom mieszkającym w Judei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '10', '29', '29', 'Uczynili to, posyłając jałmużnę do starszych w Jerozolimie przez Barnabę i Szawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '0', '0', 'W tym czasie król Herod zaczął prześladować niektórych członków Kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '1', '1', 'Ściął mieczem Jakuba, brata Jana, a gdy spostrzegł,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '2', '2', 'że to podobało się Żydom, kazał także uwięzić Piotra (było to w czasie Święta Przaśników).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '3', '3', 'Schwyconego osadził w więzieniu, gdzie pilnowały go cztery oddziały straży, z których każdy liczył czterech żołnierzy. Chciał bowiem po Święcie Paschy wydać go ludowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '4', '4', 'Gdy Piotr był strzeżony w więzieniu, Kościół zanosił za niego do Boga nieustanne modły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '5', '5', 'Tej nocy, po której Herod chciał go wydać ludowi, Piotr spał między dwoma żołnierzami skuty dwoma łańcuchami, a straże strzegły bramy więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '6', '6', 'Wtedy to zjawił się anioł Pański i światło zalało celę. Trąciwszy Piotra w bok zbudził go i powiedział: \'Wstań szybko\', a łańcuchy opadły mu z rąk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '7', '7', 'Wtedy powiedział do niego anioł: \'Przepasz się i włóż sandały\'. Zrobił to. A on do niego: \'Narzuć na siebie płaszcz i chodź za mną!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '8', '8', 'Kiedy już po wyjściu szli razem, nie wiedział, czy to, co anioł czyni, dzieje się naprawdę, bo zdawało mu się, że to tylko przewidzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '9', '9', 'Przeszli obok pierwszej straży, potem drugiej, a gdy zbliżyli się do żelaznej bramy prowadzącej do miasta - sama im się otworzyła; wyszli więc, przeszli jeszcze jedną ulicę i anioł nagle zniknął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '10', '10', 'Wówczas Piotr uświadomił sobie: \'Teraz wiem na pewno, że to Pan posłał anioła swego i uwolnił mnie z ręki Heroda i od tego wszystkiego, czego oczekiwał naród izraelski\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '11', '11', 'Po chwili zastanowienia udał się do domu Marii, matki Jana zwanego także Markiem, gdzie zastał wielu zebranych na modlitwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '12', '12', 'Gdy zakołatał do bramy wejściowej, wyszła służąca Rode i nasłuchiwała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '13', '13', 'Poznała Piotra po głosie i z radości zamiast otworzyć bramę wbiegła do domu, by donieść, że przed bramą stoi Piotr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '14', '14', 'A oni na to: \'Oszalałaś!\' Ona jednak upierała się przy swoim. Ci wtedy? \'To jego anioł\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '15', '15', 'Tymczasem Piotr dalej kołatał. Otworzywszy zobaczyli go i zdumieli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '16', '16', 'Dał im ręką znak, by się uciszyli, i zaczął im opowiadać, jak to Pan wyprowadził go z więzienia. \'Przekażcie to Jakubowi i braciom\' - powiedział i odszedł na inne miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '17', '17', 'Z nastaniem dnia wielki niepokój powstał wśród żołnierzy o to, co stało się z Piotrem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '18', '18', 'Herod kazał go poszukać, a gdy go nie znaleziono, sam przesłuchał strażników i skazał ich na śmierć. Następnie opuścił Judeę i zatrzymał się w Cezarei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '19', '19', '(Herod) oburzony był bardzo na mieszkańców Tyru i Sydonu. Oni zaś wysłali do niego wspólne poselstwo, które zyskało dla siebie przychylność Blasta, zarządcy skarbca królewskiego. Prosili o pokój, gdyż byli zależni gospodarczo od króla.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '20', '20', 'W wyznaczonym dniu Herod przywdział szaty królewskie, zasiadł na tronie i przemówił do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '21', '21', 'Lud zaś zaczął wołać: \'Tak przemawia bóg, a nie człowiek\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '22', '22', 'Natychmiast poraził go anioł Pański, ponieważ nie oddał chwały Bogu. Wyzionął ducha i stoczyło go robactwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '23', '23', 'Tymczasem słowo Boże wzrastało i rozszerzało się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '11', '24', '24', 'Barnaba zaś i Szaweł wykonali swe zadanie i wrócili z Jerozolimy, zabierając ze sobą Jana, którego nazywano także Markiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '0', '0', 'W Antiochii, w tamtejszym Kościele, prorokami i nauczycielami byli: Barnaba i Szymon Niger, Lucjusz z Cyreny, Manahen, który wychowywał się razem z Herodem tetrarchą, i Szaweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '1', '1', 'Gdy pełnili służbę Pańską, powiedział Duch Święty: \'Wyznaczcie mi Barnabę i Szawła do dzieła, do którego ich powołałem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '2', '2', 'Wtedy po modlitwie i poście oraz nałożeniu rąk odesłali ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '3', '3', 'Ci więc wysłani przez Ducha Świętego, udali się do Seleucji, a stamtąd odpłynęli na Cypr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '4', '4', 'Po przybyciu do Salaminy zaczęli w synagogach żydowskich głosić słowo Boże. Do pomocy mieli tam Jana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '5', '5', 'Gdy przeszli całą wyspę aż do Pafos, spotkali tam pewnego maga, fałszywego proroka żydowskiego imieniem Bar Jezus,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '6', '6', 'który należał do otoczenia prokonsula Sergiusza Paulusa, człowieka mądrego. Wezwał on Barnabę i Szawła, bo pragnął posłuchać słowa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '7', '7', 'Przeciwstawił się im Elymas - mag - to bowiem oznacza jego imię, i usiłował odwieść prokonsula od wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '8', '8', 'Szaweł zaś, zwany Pawłem, pełen Ducha Świętego, przenikliwie spojrzał na niego i powiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '9', '9', '\'Ty synu diabelski, pełen podstępu i wszelkiej podłości, wrogu wszystkiego, co prawe, czyż nie przestaniesz wykrzywiać &gt;&gt;prostych dróg Pańskich&lt;&lt;?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '10', '10', 'Dotknie cię teraz ręka Pańska: na jakiś czas zostaniesz ślepy i słońca nie ujrzysz\'. Wtem nagle ogarnął go mrok i ciemności, chodził w koło i szukał kogoś, kto by go za rękę prowadził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '11', '11', 'Patrząc na to prokonsul uwierzył i był głęboko przejęty nauką Pańską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '12', '12', 'Paweł ze swoim otoczeniem odpłynął z Pafos i przybył do Perge w Pamfilii. Jan natomiast odłączył się od nich i wrócił do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '13', '13', 'Następnie opuścili Perge, przybli do Antiochii Pizydyjskiej, gdzie w dzień szabatu udali się do synagogi i usiedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '14', '14', 'Kiedy przeczytano urywek z Prawa i Proroków, przełożeni synagogi zwrócili się do nich: \'Bracia! Jeśli chcecie przekazać jakieś słowo pociechy ludowi - mówcie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '15', '15', 'Na to wstał Paweł, dał znak ręką i przemówił: \'Izraelici i bojący się Boga, słuchajcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '16', '16', 'Bóg Izraela - tego właśnie ludu - wybrał ojców naszych i wywyższył ten lud w czasie pobytu na obczyźnie w ziemi egipskiej, &gt;&gt;On też wywiódł go z tej ziemi swym potężnym ramieniem&lt;&lt;,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '17', '17', 'a przez czterdzieści lat &gt;&gt;znosił ich cierpliwie na pustyni;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '18', '18', 'wyniszczył siedem szczepów w ziemi Kanaan&lt;&lt;, a mniej więcej po czterystu pięćdziesięciu latach');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '19', '19', 'oddał im na własność ich ziemię; następnie dał im sędziów aż do proroka Samuela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '20', '20', 'Potem zażądali króla i dał im Bóg na czterdzieści lat Saula, cyna Cisa, człowieka z pokolenia Beniamina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '21', '21', 'A gdy go odrzucili, powołał im na króla Dawida, któremu wystawił takie świadectwo: &gt;&gt;Znalazłem Dawida, syna Jessego, człowieka według serca mego, który spełni we wszystkim moją wolę&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '22', '22', 'Z tego to właśnie pokolenia, zgodnie z obietnicą, wyprowadził Izraelowi Zbawcę Jezusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '23', '23', 'którego przyjście poprzedziło głoszenie przez Jana chrztu nawrócenia dla całego ludu izraelskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '24', '24', 'A gdy Jan wypełnił swoją misję, powiedział: &gt;&gt;Nie jestem tym, za kogo mnie uważacie, ale za mną idzie Ten, któremu nie jestem godzien rozwiązać obuwia na Jego nogach&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '25', '25', 'Bracia, synowie pokolenia Abrahama i ci spośród was, którzy się Boga boją! Nam zostało przekazane słowo zbawienia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '26', '26', 'które mieszkańcy Jerozolimy i ich przełożeni odrzucili. A skazując Jezusa, wypełnili słowa proroków, czytane w każdy szabat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '27', '27', 'Choć nie znaleźli niczego, czym zasługiwałby na śmierć, zażądali od Piłata, aby Go zgładził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '28', '28', 'Gdy wykonali to wszystko, co o Nim zostało napisane, zdjęli Go z drzewa krzyża i złożyli w grobie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '29', '29', 'ale Bóg wskrzesił Go z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '30', '30', 'Przez wiele dni ukazywał się tym, którzy z Nim przyszli z Galilei do Jerozolimy, oni to teraz są świadkami wobec ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '31', '31', 'A my zwiastujemy wam dobrą nowinę obiecaną ojcom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '32', '32', 'którą wobec nas, ich dzieci, Bóg wypełnił, wskrzeszając Jezusa, jak to zostało napisane w drugim psalmie: &gt;&gt;Tyś Synem moim, Ja Ciebie dziś zrodziłem&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '33', '33', 'A że Go wskrzesił z martwych, aby już więcej nie uległ rozkładowi, tak to zapowiedział: &gt;&gt;Wypełnię obietnicę daną Dawidowi, w którą wierzyć należało&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '34', '34', 'Dlatego to na innym miejscu mówi: &gt;&gt;Nie pozwolisz, aby święty Twój uległ zniszczeniu&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '35', '35', 'Dawid bowiem, wypełniwszy za życia swego wolę Bożą, umarł. Został złożony przy swoich ojcach i uległ rozkładowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '36', '36', 'Ten zaś, którego Bóg wskrzesił, nie uległ rozkładowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '37', '37', 'Bracia, bądźcie więc przekonani, że to przez Niego głosi się wam odpuszczenie tych wszystkich grzechów, których nie mogło usprawiedliwić Prawo Mojżeszowe,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '38', '38', 'bo w Nim właśnie każdy, kto wierzy, dostępuje usprawiedliwienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '39', '39', 'Uważajcie więc, aby na was nie sprawdziła się przepowiednia proroków:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '40', '40', '&gt;&gt;Wy, którzy pogardzacie, spójrzcie, podziwiajcie i przepadnijcie, bo ja dokonuję dzieła za dni waszych, takiego dzieła, w które byście nie uwierzyli, gdyby wam ktoś opowiadał&lt;&lt;\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '41', '41', 'Rozchodzący się prosili, by w następny szabat przemawiali do nich na ten sam temat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '42', '42', 'Po rozejściu się zebranych wielu Żydów i pobożnych prozelitów  towarzyszyło dalej Pawłowi i Barnabie, którzy rozmawiając z nimi, starali się ich zachęcać do wytrwania w łasce Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '43', '43', 'W następny zaś szabat zebrało się już prawie całe miasto, aby słuchać słowa Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '44', '44', 'Na widok tłumów zazdrość ogarnęła Żydów, którzy rzucając bluźnierstwa, sprzeciwiali się słowom Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '45', '45', 'Na to Paweł i Barnaba otwarcie odpowiedzieli: \'To wam najpierw powinno być głoszone słowo Boże, skoro jednak je odrzucacie, sami uważacie się za niegodnych życia wiecznego, dlatego zwracamy się do pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '46', '46', 'Tak bowiem nam Pan polecił: &gt;&gt;Powołałem cię, byś był światłością dla pogan i zaniósł zbawienie aż po krańce ziemi&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '47', '47', 'Słysząc to poganie cieszyli się wysławiając słowo Pańskie, a uwierzyli ci, którzy zostali powołani do życia wiecznego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '48', '48', 'i słowo Pańskie rozchodziło się po całej okolicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '49', '49', 'Tymczasem Żydzi podburzyli pobożne a wpływowe kobiety oraz wybitne osobistości w mieście, zaczęli prześladować Pawła i Barnabę, wyrzucając ich poza swoje granice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '50', '50', 'Wtedy, strząsając na nich proch ze swoich nóg, udali się do Ikonium,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '12', '51', '51', 'a uczniowie pozostali pełni radości i Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '0', '0', 'W Ikonium zdarzyło się podobnie: weszli do synagogi żydowskiej i przemawiali tak, że mnóstwo Żydów i Greków uwierzyło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '1', '1', 'Ci jednak spośród Żydów, którzy nie uwierzyli, podburzali i źle usposabiali pogan przeciw braciom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '2', '2', 'Przebywali tam dość długo przemawiając odważnie, pełni ufności do Pana, który potwierdzał słowo swojej łaski przez cuda i znaki, jakie się działy przez ich ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '3', '3', 'Ludność miasta podzieliła się: jedni byli za Żydami, inni zaś za apostołami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '4', '4', 'Gdy się dowiedzieli, że rozjątrzeni poganie i Żydzi ze swymi przełożonymi chcieli ich znieważyć i ukamienować,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '5', '5', 'uciekli do miast Lykaonii, do Lystry i Derbe oraz okolicy');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '6', '6', 'i tam głosili dobrą nowinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '7', '7', 'W Lystrze żył pewien człowiek o bezwładnych nogach, kaleka od urodzenia, który nigdy jeszcze nie chodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '8', '8', 'Słuchał on przemówienia Pawła, który spojrzał na niego przenikliwym wzrokiem, a przekonany, że ma wiarę, dzięki której może być uzdrowiony,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '9', '9', 'powiedział donośnym głosem: \'Stań prosto na nogach!\', a on poderwał się i zaczął chodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '10', '10', 'Tłumy zaś widząc, czego Paweł dokonał, mówiły w swoim likaońskim  języku: \'Bogowie w ludzkiej postaci zstąpili do nas\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '11', '11', 'nazywając Barnabę Zeusem, a Pawła Hermesem, ponieważ Paweł wtedy przemawiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '12', '12', 'Kapłan Zeusa z podmiejskiej świątyni przyprowadził woły i przyniósł wieńce przed bramę, i razem z tłumem chciał złożyć ofiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '13', '13', 'Słysząc to apostołowie Paweł i Barnaba rozdarli szaty i wpadli w tłum z krzykiem:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '14', '14', '\'Ludzie, co wy czynicie, przecież my, podobnie jak wy, jesteśmy tylko ludźmi, podlegającymi cierpieniom. Głosimy wam dobrą nowinę, abyście odstąpili od rzeczy próżnych do Boga żywego, który stworzył niebo i ziemię, morza i wszystko, co się w nich znajduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '15', '15', 'W minionych wiekach pozwolił On wszystkim narodom chodzić własnymi drogami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '16', '16', 'nie przestając jednak dawać świadectwa o sobie i udzielać dobrodziejstw: zsyłał bowiem deszcz z nieba w czasie urodzaju, dawał pokarm i radość sercom waszym\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '17', '17', 'Tymi słowami z trudem powstrzymywali ich od złożenia im ofiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '18', '18', 'Tymczasem z Antiochii i Ikonium przybyli Żydzi podburzając tłumy, obrzucali Pawła kamieniami i wywlekli go za miasto sądząc, że nie żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '19', '19', 'Gdy jednak obstąpili go uczniowie, podniósł się i wszedł do miasta. Następnego dnia udał się z Barnabą do Derbe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '20', '20', 'Głosząc dobrą nowinę w tym mieście, zdobyli sobie sporą liczbę uczniów, a potem wrócili do Lystry, Ikonium i Antiochii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '21', '21', 'Umacniając przekonania uczniów, zachęcali ich do wytrwania w wierze tymi słowami: \'Trzeba pokonać wiele trudności, by wejść do królestwa Bożego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '22', '22', 'Przez wkładanie rąk w czasie postów i modlitw powoływali w każdym Kościele prezbiterów, powierzając ich Panu, w którego uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '23', '23', 'Następnie przeszli przez Pizydię i doszli do Pamfilii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '24', '24', 'Kiedy skończyli nauczanie w Perge, przybyli do Attalii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '25', '25', 'Stamtąd popłynęli do Antiochii, gdzie za łaską Bożą byli wyznaczeni do dzieła, które właśnie wykonali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '26', '26', 'Po swym przybyciu opowiedzieli zebranemu tam Kościołowi, jak wielkich rzeczy dokonał Bóg będąc z nimi i jak poganom otworzył bramy wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '13', '27', '27', 'Pozostali wśród uczniów dłuższy czas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '0', '0', 'Niektórzy przybywający z Judei nauczali braci: \'Jeżeli nie zostaniecie obrzezani według zwyczaju Mojżeszowego, nie możecie być zbawieni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '1', '1', 'Ponieważ wywołało to niemałe zamieszanie i spór między nimi a Pawłem i Barnabą, dlatego postanowili, że Paweł i Barnaba oraz kilku innych spośród nich udadzą się w tej sprawie do apostołów i starszych w Jerozolimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '2', '2', 'Wysłańcy Kościoła, odbywając drogę przez Fenicję i Samarię, opowiadali o nawróceniu pogan, co wszystkim braciom sprawiało wielką radość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '3', '3', 'Gdy przybyli do Jerozolimy, zostali przyjęci przez Kościół, apostołów i starszych i opowiedzieli o tym, czego to Bóg przez nich dokonał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '4', '4', 'Sprzeciwili się temu niektórzy nawróceni z sekty faryzeuszów i powiedzieli: \'Trzeba ich zobowiązać do obrzezania i zachowania Prawa Mojżeszowego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '5', '5', 'Zebrali się więc apostołowie i starsi, aby tę sprawę rozpatrzyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '6', '6', 'Kiedy już dość długo nad tym dyskutowano, zabrał głos Piotr: \'Bracia, wiecie, że Bóg wybrał mnie od dawna spośród was, aby poganie właśnie z moich ust usłyszeli słowa dobrej nowiny i uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '7', '7', 'A Bóg znający serca świadczył za nimi, udzielając im Ducha Świętego, tak samo jak i nam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '8', '8', 'Nie wprowadzał też żadnej różnicy między nami a nimi, oczyszczając wiarą ich serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '9', '9', 'Wobec tego czemu kusicie Boga, wkładając na uczniów jarzmo, którego ani ojcowie nasi, ani my sami nie mogliśmy udźwignąć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '10', '10', 'Jednak dzięki łasce Pana Jezusa wierzymy, że będziemy tak samo zbawieni, jak i oni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '11', '11', 'Umilkli wszyscy zebrani i słuchali opowiadania Barnaby i Pawła o znakach i cudach, jakie Bóg zdziałał przez nich wśród pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '12', '12', 'A gdy skończyli, odezwał się Jakub: \'Posłuchajcie mnie, bracia!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '13', '13', 'Szymon opowiedział, jak Bóg zatroszczył się najpierw o to, aby wybrać dla siebie lud spośród pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '14', '14', 'Potwierdzają to zresztą słowa napisane przez proroków:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '15', '15', '&gt;&gt;Potem powrócę i odbuduję upadły dom Dawida, odbuduję jego ruiny i podźwignę go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '16', '16', 'Aby pozostali ludzie szukali Pana i wszystkie ludy, nad którymi wzywa się Imienia mego - mówi Pan, który to wszystko czyni&lt;&lt;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '17', '17', '- słowa znane od wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '18', '18', 'Dlatego sądzę, że nie należy czynić trudności tym spośród pogan, którzy nawracają się do Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '19', '19', 'Należy jednak napisać im, aby powstrzymali się od ofiar nieczystych składanych bogom, od nierządu, od pokarmów ze zwierząt uduszonych i krwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '20', '20', 'Od dawna bowiem w miastach są tacy, którzy głoszą naukę Mojżesza, czytając go w każdy szabat w synagogach\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '21', '21', 'Wtedy apostołowie i starsi wspólnie z całym Kościołem postanowili wysłać do Antiochii z Pawłem i Barnabą wybranych spośród siebie ludzi - Judę Barsabasa i Sylasa, którzy zajmowali wśród braci czołowe miejsce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '22', '22', 'Wręczyli im pismo następującej treści: \'Apostołowie i starsi braciom pogańskiego pochodzenia w Antiochii, Syrii i Cylicji przesyłają pozdrowienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '23', '23', 'Ponieważ dowiedzieliśmy się, że niektórzy z naszego grona zaniepokoili was głoszoną, bez naszego upoważnienia, nauką, powodując zamęt w umysłach waszych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '24', '24', 'postanowiliśmy wspólnie posłać do was wybranych ludzi z umiłowanymi naszymi Barnabą i Pawłem -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '25', '25', 'ludźmi, którzy poświęcili swe życie dla Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '26', '26', 'Wysłaliśmy więc Judę i Sylasa, którzy ustnie powtórzą wam to samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '27', '27', 'Natchnieni Duchem Świętym postanowiliśmy nie nakładać na was żadnych ciężarów, prócz rzeczy koniecznych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '28', '28', 'a więc powstrzymajcie się od spożywania pokarmów składanych bogom na ofiarę, od krwi, od mięsa ze zwierząt uduszonych i nierządu. Jeżeli to zachowacie, dobrze uczynicie. Bądźcie zdrowi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '29', '29', 'Gdy wysłańcy przybyli do Antiochii, zwołali zebranie i przekazali list.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '30', '30', 'Przeczytano go i uradowano się jego pocieszającą treścią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '31', '31', 'Juda i Sylas, będący również prorokami, często pocieszali i umacniali braci słowem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '32', '32', 'Po pewnym czasie bracia, do których zostali posłani, odesłali ich z powrotem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '33', '33', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '34', '34', 'Paweł i Barnaba spędzili pewien czas w Antiochii i z wielu innymi nauczali i głosili słowo Pańskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '35', '35', 'Po pewnym czasie powiedział Paweł do Barnaby: \'Wróćmy i zobaczmy, jak żyją bracia we wszystkich miastach, w których głosiliśmy słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '36', '36', 'Barnaba chciał zabrać ze sobą Jana, zwanego Markiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '37', '37', 'Paweł natomiast był zdania, by go nie zabierać, gdyż odszedł od nich w Pamfilii i nie brał udziału w ich pracy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '38', '38', 'Z tego powodu doszło między nimi do ostrej sprzeczki, tak że się rozłączyli: Barnaba z Markiem odpłynął na Cypr,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '14', '39', '39', 'Paweł dobrał sobie Sylasa i wyruszył w drogę, polecony łasce Bożej przez braci; szedł przez Syrię i Cylicję, umacniając Kościoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '0', '0', 'Przybył (Paweł) do Derbe i Lystry, gdzie żył uczeń imieniem Tymoteusz, syn nawróconej Żydówki, którego ojcem był Grek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '1', '1', 'Bracia w Lystrze i Ikonium wystawili o nim dobre świadectwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '2', '2', 'Paweł chciał, aby udał się razem z nim w drogę. I obrzezał go ze względu na Żydów mieszkających w tych okolicach. Wszyscy bowiem wiedzieli, że ojciec jego był Grekiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '3', '3', 'Przechodząc przez miasta nakazywali im przestrzegać uchwał powziętych przez apostołów i starszych w Jerozolimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '4', '4', 'Kościoły więc umacniały się w wierze, a liczba wiernych wzrastała z dnia na dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '5', '5', 'Przeszli przez Frygię i Galację, ponieważ Duch Święty zabronił im głosić nauki w Azji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '6', '6', 'Gdy skierowali się ku Myzji, próbowali wyruszyć do Bitynii, ale znowu nie pozwalał im Duch Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '7', '7', 'Ominęli więc Myzję i przybyli do Troady.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '8', '8', 'W nocy miał Paweł widzenie: stanął przed nim jakiś Macedończyk i zapraszając go powiedział: \'Przyjdź do Macedonii i pomóż nam!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '9', '9', 'Po tym widzeniu natychmiast staraliśmy się wyruszyć do Macedonii w przekonaniu, że Bóg wzywa nas do głoszenia im dobrej nowiny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '10', '10', 'Odpłynąwszy z Troady, udaliśmy się wprost do Samotraki, a nazajutrz do Neapolis.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '11', '11', 'Stamtąd zaś do Filippi, głównego miasta tego okręgu macedońskiego, które jest kolonią (rzymską). Zatrzymaliśmy się w tym mieście kilka dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '12', '12', 'W dzień szabatu wyszliśmy za miasto nad rzekę, gdzie, jak sądziliśmy, odbywały się modlitwy. Usiedliśmy i zaczęliśmy rozmawiać z kobietami, które się tam zeszły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '13', '13', 'Przysłuchiwała się nam też pewna \'bojąca się Boga\' kobieta imieniem Lidia, sprzedawczyni purpury, z miasta Tiatyry. Pan sprawił, że uważnie słuchała słów Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '14', '14', 'Gdy ze swym domem przyjęła chrzest, prosiła ich: \'Skoro uznaliście mnie za wierzącą w Pana, wejdźcie do mego domu i zatrzymajcie się\'. I wymusiła to na nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '15', '15', 'Gdy potem udawaliśmy się na modlitwę, zabiegła nam drogę pewna dziewczyna, która miała ducha wieszczego i dzięki wróżbom przynosiła duże dochody swoim właścicielom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '16', '16', 'Biegnąc za Pawłem i nami wykrzykiwała: \'Ci ludzie są sługami Boga Najwyższego i głoszą wam drogę zbawienia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '17', '17', 'Czyniła  tak przez wiele dni. Rozdrażniony tym Paweł odwrócił się i powiedział do ducha: \'Nakazuję ci w imię Jezusa Chrystusa, abyś z niej wyszedł!\' I w tej chwili wyszedł z niej duch.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '18', '18', 'Kiedy jej właściciele zorientowali się, że przepadły zyski, na które liczyli, pochwycili Pawła i Sylasa, zawlekli na rynek i postawili przed władzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '19', '19', 'Gdy ich stawili przed strategów, powiedzieli: \'Ci ludzie są Żydami i wywołują zamieszanie w naszym mieście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '20', '20', 'Głoszą bowiem obyczaje, których my, Rzymianie, nie możemy przyjmować, a tym bardziej zachowywać\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '21', '21', 'A gdy przeciw nim wystąpił także tłum, strategowie zdarli z nich szaty i kazali wychłostać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '22', '22', 'Po surowej chłoście wrzucili ich do więzienia, polecając strażnikowi, aby ich dobrze pilnował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '23', '23', 'Na ten rozkaz wtrącił ich do lochu, a nogi ich zakuł w kajdany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '24', '24', 'O północy Paweł i Sylas modląc się śpiewali na cześć Boga, a więźniowie im się przysłuchiwali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '25', '25', 'Nagle powstało tak wielkie trzęsienie ziemi, że zachwiały się fundamenty więzienia i natychmiast pootwierały się wszystkie drzwi, ze wszystkich zaś więźniów opadły kajdany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '26', '26', 'Strażnik przebudziwszy się zobaczył, że otwarte są bramy więzienia. Wyciągnął miecz i chciał sobie odebrać życie, sądził bowiem, że więźniowie pouciekali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '27', '27', 'Wtedy Paweł krzyknął na cały głos: \'Nie czyń sobie nic złego, przecież wszyscy tu jesteśmy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '28', '28', 'Zażądał więc światła, wbiegł do lochu i ze drżeniem przypadł do nóg Pawła i Sylasa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '29', '29', 'Wyprowadził ich na zewnątrz i zapytał: \'Panowie, co mam czynić, aby się zbawić?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '30', '30', 'Oni odpowiedzieli: \'Uwierz w Pana Jezusa, a będziesz zbawiony ty i twój dom\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '31', '31', 'Przekazali naukę Pana jemu i wszystkim, którzy byli w jego domu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '32', '32', 'jeszcze bowiem tej nocy zabrał ich do siebie, obmył rany, zaraz też przyjął chrzest ze wszystkimi domownikami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '33', '33', 'Wprowadziwszy ich do domu, zastawił stół i radował się ze wszystkimi domownikami, że uwierzył w Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '34', '34', 'Nazajutrz strategowie posłali liktorów z rozkazem: \'Zwolnij tych ludzi!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '35', '35', 'Wiadomość tę przekazał strażnik Pawłowi: \'Strategowie nakazali was uwolnić, wyjdźcie więc teraz i idźcie w pokoju\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '36', '36', 'A na to Paweł: \'Jak to, bez sądu nas, obywateli rzymskich, publicznie wychłostali i wtrącili do więzienia, a teraz potajemnie nas wyrzucają? O, nie! Niech przyjdą i sami nas wyprowadzą!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '37', '37', 'Liktorzy donieśli o tym strategom. Gdy ci się dowiedzieli, że to są obywatele rzymscy, wystraszyli się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '38', '38', 'Przyszli więc zaraz, przeprosili ich i wyprowadzając błagali o opuszczenie miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '15', '39', '39', 'Opuściwszy więzienie wstąpili do Lidii, zobaczyli się z braćmi, pocieszyli ich i odeszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '0', '0', 'Gdy przeszli przez Amfipolis i Apolonię, przybyli do Tesaloniki, gdzie znajdowała się synagoga żydowska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '1', '1', 'Paweł, zgodnie ze swoim zwyczajem, udał się tam i przez trzy szabaty rozprawiał z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '2', '2', 'Posługując się Pismem świętym udowadniał: \'Mesjasz musiał cierpieć i zmartwychwstać, Mesjaszem tym jest Jezus, którego ja wam głoszę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '3', '3', 'Niektórzy z nich dali się przekonać. Przyłączyli się oni do Pawła i Sylasa. To samo uczyniła wielka liczba pobożnych Greków i niemało poważnych kobiet.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '4', '4', 'Powodowani zazdrością Żydzi namówili ludzi z mętów społecznych, wywołując zbiegowisko i zamieszanie w mieście. Napadli na dom Jazona, usiłując stawić ich przed ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '5', '5', 'Ponieważ ich nie znaleźli, chwycili Jazona i niektórych braci i zawlekli ich z krzykiem przed politarchów: \'Oto właśnie ci, którzy czynili zamęt w mieście!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '6', '6', 'Gościł ich Jazon! A przecież oni wszyscy postępują wbrew zarządzeniom cesarza nauczając, że jest inny król - Jezus\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '7', '7', 'Zaniepokoiło to lud i politarchów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '8', '8', 'ponieważ jednak Jazon i inni wzięli za nich odpowiedzialność, zwolnili ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '9', '9', 'Tej samej jeszcze nocy bracia wysłali Pawła i Sylasa do Berei. Po przybyciu tam udali się oni do synagogi Żydów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '10', '10', 'Ci szlachetnością przewyższali mieszkańców Tesaloniki, z całą gotowością przyjęli naukę i codziennie zastanawiali się na podstawie Pisma, czy to tak istotnie jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '11', '11', 'Wielu więc z nich przyjęło wiarę, także niemało znakomitych kobiet greckich i mężczyzn.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '12', '12', 'Gdy zaś dowiedzieli się Żydzi z Tesaloniki, że i w Berei głosi Paweł słowo Boże, przybyli tam i podburzyli tłumy, wprowadzając zamieszanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '13', '13', 'Bracia więc zaraz wysłali Pawła nad morze, Sylas i Tymoteusz pozostali na miejscu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '14', '14', 'Ludzie towarzyszący Pawłowi zaprowadzili go aż do Aten. Wracając zaś przynieśli Sylasowi i Tymoteuszowi polecenie, by jak najprędzej do niego przybyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '15', '15', 'Paweł, czekając na nich w Atenach, był do głębi oburzony na widok miasta oddanego bałwochwalstwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '16', '16', 'W synagodze dyskutował z Żydami i \'bojącymi się Boga\', a na rynku codziennie z tymi, których tam spotykał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '17', '17', 'Dochodziło też do wymiany słów między nim a niektórymi filozofami epikurejskimi i stoickimi. Jedni mówili: \'Co ten gaduła chce nam powiedzieć?\' Drudzy zaś: \'Może on jest wysłannikiem obcych bogów?\' Głosił im bowiem dobrą nowinę o Jezusie i zmartwychwstaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '18', '18', 'Zabrali go więc ze sobą na areopag i zapytali: \'Czy możemy się dowiedzieć, na czym polega ta nowa nauka, którą głosisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '19', '19', 'Niezwykłe bowiem rzeczy docierają do naszych uszu, chcemy więc wiedzieć, co to właśnie znaczy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '20', '20', 'Ogół bowiem Ateńczyków i mieszkających tam cudzoziemców na nic innego nie poświęcał tyle czasu, co właśnie na słuchanie i opowiadanie ostatnich wiadomości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '21', '21', 'Stanął więc Paweł pośrodku areopagu i przemówił: \'Ateńczycy! Widzę, że jesteście ludźmi pod każdym względem bardzo religijnymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '22', '22', 'Przechodząc bowiem widziałem czczone przez was bóstwa. Natrafiłem też na ołtarz z napisem: &gt;&gt;Nieznanemu Bogu&lt;&lt;. Ja właśnie głoszę wam Tego, którego wy, nie znając, czcicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '23', '23', 'Bóg, który stworzył świat i wszystko, co się na nim znajduje, będąc Panem nieba i ziemi, nie mieszka w świątyniach wzniesionych ręką ludzką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '24', '24', 'Nie oddaje Mu się też usług rękami ludzkimi, niczego bowiem nie potrzebuje. Sam daje wszystkim życie, tchnienie i wszystko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '25', '25', 'Z jednego (człowieka) wywiódł wszystkie plemiona ludzkie, aby zamieszkały całą ziemię. Określił też im właściwy czas i granice zamieszkania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '26', '26', 'aby szukały Boga, czując Jego obecność, i aby Go znalazły, bo rzeczywiście jest On niedaleko od każdego z nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '27', '27', 'W Nim bowiem żyjemy, poruszamy się i jesteśmy, jak to też powiedzieli niektórzy z waszych poetów: &gt;&gt;z Jego bowiem rodu jesteśmy&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '28', '28', 'Będąc więc z rodu Bożego, nie powinniśmy sądzić, że bóstwo podobne jest do złota, srebra czy kamienia, wytworu ręki czy myśli ludzkiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '29', '29', 'Mimo że był okres nieznajomości Boga, wzywa On teraz wszędzie wszystkich ludzi, aby się nawrócili;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '30', '30', 'wyznaczył bowiem dzień, w którym będzie sądził świat sprawiedliwie przez Człowieka, którego w tym celu powołał, czego wiarygodność potwierdził dla wszystkich, wskrzeszając Go z martwych\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '31', '31', 'Gdy usłyszeli o zmartwychwstaniu, jedni naśmiewali się, a inni mówili: \'O tym posłuchamy się kiedy indziej\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '32', '32', 'Po tych słowach Paweł odszedł od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '16', '33', '33', 'Niektórzy jednak ludzie przyłączyli się do niego i uwierzyli. Wśród nich także Dionizy, Areopagita i kobieta imieniem Damaris, a wraz z nimi i inni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '0', '0', 'Po tym, co zaszło, Paweł opuścił Ateny i przybył do Koryntu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '1', '1', 'Spotkał tam Żyda Akwilę, pochodzącego z Pontu, który niedawno wraz ze swą żoną Pryscyllą przybył z Italii. Klaudiusz wydał bowiem zarządzenie, by wszyscy Żydzi opuścili Rzym; przyłączył się więc do nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '2', '2', 'Ponieważ zaś uprawiał to samo rzemiosło, zamieszkał u nich i razem pracowali. Zajmowali się bowiem wyrobem namiotów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '3', '3', 'W każdy szabat zabierał głos w synagogdze, starając się przekonać Żydów i Greków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '4', '4', 'Kiedy Sylas i Tymoteusz przybyli z Macedonii, Paweł poświęcił się całkowicie głoszeniu słowa, przytaczając świadectwa, że Jezus jest Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '5', '5', 'Gdy jednak zaczęli mu się sprzeciwiać i rzucać bluźnierstwa, strzepał pył ze swego ubrania i powiedział im: \'Krew wasza na głowy wasze, ja nie ponoszę tu winy, od tej chwili zwracam się do pogan\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '6', '6', 'Odchodząc stamtąd wstąpił do domu Tytusa Justusa, \'człowieka bojącego się Boga\', którego dom sąsiadował z synagogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '7', '7', 'Kryspus, przełożony synagogi, uwierzył w Pana wraz z całym swoim domem, a także wielu słuchających go Koryntian uwierzyło i przyjęło chrzest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '8', '8', 'W nocy podczas widzenia powiedział Pan do Pawła: \'Nie bój się! Ale mów i nie milcz,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '9', '9', 'jestem bowiem z tobą i nikt ci nic złego nie zrobi, bo w tym właśnie mieście mam wiele ludu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '10', '10', 'Pozostał tam przez półtora roku, głosząc im słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '11', '11', 'Gdy zaś w Achai prokonsulem został Gallio, Żydzi wystąpili zgodnie przeciwko Pawłowi, postawili go przed sądem');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '12', '12', 'i oskarżyli: \'Namawia on ludzi by oddawali cześć Bogu niezgodnie z Prawem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '13', '13', 'Kiedy Paweł zamierzał przemówić, wtedy Gallio powiedział do Żydów: \'Słuchajcie, Żydzi! Gdyby chodziło o jakieś bezprawie czy czyn niegodziwy, wstawiłbym się za wami;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '14', '14', 'skoro jednak spór dotyczy słów, nazw i waszego Prawa, sami się tym zajmijcie. Ja nie chcę być sędzią w tej sprawie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '15', '15', 'I przepędził ich z sądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '16', '16', 'Wtedy wszyscy rzucili się na Sostenesa, przełożonego synagogi, i bili go przed sądem, lecz Gallio nie zwracał na to uwagi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '17', '17', 'Paweł przebywał tam jeszcze przez dłuższy czas, potem pożegnał się z braćmi i wyruszył z Pryscyllą i Akwilą do Syrii. W Kenchrach kazał sobie ostrzyc głowę, bo złożył taki ślub.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '18', '18', 'Gdy przybyli do Efezu, odłączył się od nich i udał się do synagogi, gdzie dyskutował z Żydami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '19', '19', 'którzy prosili go, aby jeszcze trochę u nich został.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '20', '20', 'Nie zgodził się jednak na to i na pożegnanie powiedział im: \'Jeżeli Bóg zechce, znowu do was powrócę\'. I odpłynął z Efezu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '21', '21', 'Potem przybył do Cezarei, gdzie odwiedził i pozdrowił Kościół, udając się następnie do Antiochii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '22', '22', 'Po krótkim pobycie udał się w dalszą drogę, idąc kolejno przez Galację i Frygię, umacniał w wierze wszystkich uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '23', '23', 'Do Efezu przybył Żyd Apollos, pochodzący z Aleksandrii, znakomity mówca i znawca Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '24', '24', 'Znał on już drogę Pana. Pełen zapału nauczał wiernie o tym, co dotyczy Jezusa, choć znał tylko chrzest Jana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '25', '25', 'Gdy zaczął śmiało nauczać w synagodze, usłyszeli go Pryscylla i Akwila, wzięli go do siebie i dokładnie wyłożyli mu naukę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '26', '26', 'Kiedy zamierzał wyruszyć do Achai, bracia napisali list polecający do uczniów, aby go przyjęli. Kiedy tam przybył, dzięki swoim zaletom pomagał bardzo tym, którzy uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '17', '27', '27', 'Śmiało zbijał twierdzenia Żydów, publicznie wykazując na podstawie Pisma, że Jezus jest Mesjaszem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '0', '0', 'W tym czasie, gdy Apollos przebywał w Koryncie, Paweł przeszedł przez górzyste okolice i dotarł do Efezu, gdzie spotkał niektórych uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '1', '1', 'I zapytał ich: \'Czy przyjmując wiarę otrzymaliście Ducha Świętego?\' A oni odpowiedzieli: \'Nawet nie słyszeliśmy, że istnieje Duch Święty\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '2', '2', 'On pyta dalej: \'Jaki więc chrzest przyjęliście?\' Odpowiedzieli mu: \'Chrzest Janowy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '3', '3', 'Wyjaśnił im więc: \'Jan udzielał chrztu nawrócenia i wzywał lud, aby uwierzył w Tego, który po nim przyjdzie, to znaczy w Jezusa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '4', '4', 'Po tych słowach przyjęli chrzest w imię Pana Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '5', '5', 'Potem Paweł położył na nich ręce i zstąpił na nich Duch Święty, mówili (różnymi) językami i prorokowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '6', '6', 'Było ich wszystkich około dwunastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '7', '7', 'Chodził do synagogi i przez trzy miesiące prowadził śmiało rozmowy i głosił przekonywające nauki o królestwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '8', '8', 'Znaleźli się jednak tacy, którzy trwając w uporze nie dawali się przekonać i publicznie źle mówili o nauce Pańskiej. Zerwał więc z nimi, odłączył od nich uczniów i codziennie nauczał w szkole Tyrannosa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '9', '9', 'Trwało to dwa lata, podczas których mieszkańcy Azji, Żydzi i Grecy, słuchali słowa Pańskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '10', '10', 'Bóg sprawiał, że przez ręce Pawła działały (nadziemskie) moce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '11', '11', 'Chusty i przepaski, które nosił, kładziono na chorych, przez co ustępowały choroby i wychodziły duchy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '12', '12', 'Niektórzy też wędrowni egzorcyści żydowscy próbowali wzywać imienia Pana Jezusa nad tymi, którzy mieli złe duchy, mówiąc: \'Zaklinam was na Jezusa, którego głosi Paweł\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '13', '13', 'Czyniło to siedmiu synów Skewy, żydowskiego arcykapłana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '14', '14', 'Zły duch odpowiedział na to: \'Znam Jezusa, znam również Pawła, a wy coście za jedni?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '15', '15', 'I rzucił się na nich człowiek opętany przez złego ducha, powalił ich i pobił tak mocno, że nadzy i poranieni musieli uciekać z owego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '16', '16', 'Dowiedzieli się o tym wszyscy Żydzi i Grecy mieszkający w Efezie, a strach padł na wszystkich i wielbili imię Pana Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '17', '17', 'Wielu z tych, którzy przyjęli wiarę, przychodziło, by wyznać i ujawnić swoje uczynki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '18', '18', 'Także wielu z tych, którzy zajmowali się wróżbiarstwem, przynosiło książki i paliło je na oczach wszystkich. Wartość tych ksiąg oceniono na pięćdziesiąt tysięcy drachm w srebrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '19', '19', 'Tak to, dzięki pomocy Pana, rozszerzała się i umacniała Jego nauka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '20', '20', 'Po tych wydarzeniach postanowił Paweł, pod wpływem Ducha Świętego, udać się do Jerozolimy przez Macedonię i Achaję. \'A skoro już tam przybędę - powiedział - to muszę jeszcze Rzym zobaczyć\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '21', '21', 'Do Macedonii wysłał dwóch swoich pomocników: Tymoteusza i Erasta, a sam pozostał jeszcze przez pewien czas w Azji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '22', '22', 'W tym to właśnie czasie z powodu nauki Pańskiej powstało poważne zamieszanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '23', '23', 'Złotnik Demetriusz wyrabiał świątyńki Artemidy, zapewniając swoim pracownikom niemałe zarobki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '24', '24', 'Zebrał ich oraz tych, którzy tym samym rzemiosłem się trudnili, i powiedział: \'Ludzie, wiadomo wam, jakie z tego rzemiosła mamy zyski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '25', '25', 'Widzicie też i słyszycie, jak ten Paweł, nie tylko zresztą w Efezie, ale prawie w całej Azji, zjednał sobie wielu ludzi i przekonał ich, że nie są bogami ci, którzy są dziełem rąk ludzkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '26', '26', 'Grozi nam, że nie tylko upadnie nasze rzemiosło, ale i świątynię wielkiej bogini Artemidy będą mieli za nic i że ta, którą czci cała Azja i świat, będzie odarta z majestatu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '27', '27', 'Gdy to usłyszeli, wpadli w gniew i zaczęli krzyczeć: \'Wielka Artemida Efeska!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '28', '28', 'Zawrzało w całym mieście. Wszyscy pobiegli do teatru, porwali ze sobą Macedończyków Gajusa i Arystarcha - towarzyszy Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '29', '29', 'Paweł chciał wyjść do ludu, ale uczniowie mu nie pozwolili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '30', '30', 'Nawet niektórzy spośród azjarchów, którzy byli mu życzliwi, prosili go, aby się nie udawał do teatru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '31', '31', 'A tam każdy krzyczał coś innego, powstało takie zamieszanie w zgromadzeniu, że większość nie wiedziała nawet, po co w ogóle przyszli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '32', '32', 'Wypchnięto z tłumu Aleksandra, ponieważ Żydzi wysunęli go do przodu. Aleksander dał znak ręką, że chce się wytłumaczyć przed ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '33', '33', 'Gdy poznali, że jest Żydem, prawie przez dwie godziny wszyscy krzyczeli jedno i to samo: \'Wielka Artemida Efeska!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '34', '34', 'Wreszcie pisarz uspokoił tłum i powiedział: \'Efezjanie! Któż z ludzi nie wie, że miasto Efez jest stróżem świątyni wielkiej Artemidy i jej posągu, który spadł z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '35', '35', 'Skoro więc nie można temu zaprzeczyć, powinniście się uspokoić i niczego nie czynić pochopnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '36', '36', 'A wy przyprowadziliście tu ludzi, którzy nie są ani świętokradcami, ani bluźniercami naszej bogini.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '37', '37', 'Jeśli więc Demetriusz i jego współpracownicy mają przeciwko komuś jakieś (zarzuty), to są od tego sądy i prokonsulowie, niech się więc tam oskarżają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '38', '38', 'Jeśli wam chodzi o coś jeszcze innego, to zostanie rozstrzygnięte na prawomocnym zebraniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '18', '39', '39', 'Grozi nam bowiem oskarżenie o dzisiejsze rozruchy. Nie ma bowiem żadnego powodu, którym moglibyśmy usprawiedliwić to zbiegowisko\'. Po tych słowach rozwiązał zgromadzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '0', '0', 'Gdy zapanował spokój, Paweł wezwał uczniów, skierował do nich słowa zachęty, pożegnał się z nimi i udał w drogę do Macedonii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '1', '1', 'Odwiedzając te okolice, w wielu przemówieniach napominał uczniów, a potem wyruszył do Grecji,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '2', '2', 'gdzie pozostał przez trzy miesiące. Miał zamiar popłynąć do Syrii, ale ponieważ Żydzi urządzili na niego zasadzkę, postanowił wrócić przez Macedonię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '3', '3', 'Towarzyszyli mu w drodze: Sopater, syn Pyrrusa Berei, Arystarch i Sekundus z Tesaloniki, Gajus z Derbe, Tymoteusz, Tychik i Trofim z Azji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '4', '4', 'Oni poszli pierwsi i czekali na nas w Troadzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '5', '5', 'My natomiast po Święcie Przaśników odpłynęliśmy z Filippi i po pięciu dniach przybyliśmy do nich do Troady, gdzie spędziliśmy siedem dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '6', '6', 'Gdy zebraliśmy się pierwszego dnia po szabacie na łamanie chleba, Paweł przemówił do nich, a ponieważ nazajutrz zamierzał odjechać, przeciągnął przemówienie aż do północy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '7', '7', 'W sali na piętrze, gdzieśmy się zebrali, paliło się wiele lamp.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '8', '8', 'Na oknie siedział młodzieniec Eutyches; zmorzony snem w czasie przemówienia Pawła spadł z trzeciego piętra i podniesiono go nieżywego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '9', '9', 'Paweł zszedł na dół, podbiegł do niego, podniósł go i powiedział: \'Nie martwcie się, on żyje\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '10', '10', 'Wrócił na górę, łamał chleb, spożywał go, a potem rozmawiał z nimi jeszcze długo, aż do świtu. Takie było ich rozstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '11', '11', 'Chłopca zaś przyprowadzono żywego, z czego się ogromnie cieszyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '12', '12', 'My wcześniej wyruszyliśmy na statek i odpłynęliśmy do Assos, skąd mieliśmy zabrać Pawła, tak bowiem zarządził, zamierzając odbyć tę drogę pieszo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '13', '13', 'Kiedy więc spotkał się z nami w Assos, wzięliśmy go ze sobą i przybyliśmy do Mityleny,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '14', '14', 'skąd znowu odpłynęliśmy i następnego dnia znaleźliśmy się na wysokości Chios. Nazajutrz zawinęliśmy do Samos, a w dzień później przybyliśmy do Miletu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '15', '15', 'Paweł bowiem postanowił ominąć Efez, aby nie tracić czasu w Azji, chciał mianowicie zdążyć, jeśli się uda, na Święta Pięćdziesiątnicy do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '16', '16', 'Z Miletu posłał po starszych Kościoła z Efezu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '17', '17', 'Gdy przed nim stanęli, powiedział do nich: \'Wiadomo wam, jak od pierwszego dnia mego pobytu w Azji cały czas spędzałem z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '18', '18', 'Jak służyłem Panu z całym oddaniem we łzach, doświadczeniach i zasadzkach, jakie na mnie zgotowali Żydzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '19', '19', 'Nie uchylałem się też od głoszenia wam tego, co pożyteczne, nauczałem was publicznie i po domach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '20', '20', 'Niosłem świadectwo Żydom i Grekom, by się nawrócili do Boga i uwierzyli w Pana naszego Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '21', '21', 'A teraz, przynaglany przez Ducha Świętego, muszę iść do Jerozolimy, choć nie wiem, co mnie tam spotka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '22', '22', 'To jedno wiem, bo o tym zapewnia mnie Duch Święty w każdym mieście, że czeka mnie prześladowanie i więzienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '23', '23', 'Lecz ja nie cenię sobie ponad miarę swego życia, bylebym dobiegł do mety i wypełnił zadanie, który otrzymałem od Pana Jezusa, (to jest) dał świadectwo o ewangelii, która jest darem Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '24', '24', 'A teraz wiem na pewno, że mnie już nigdy nie zobaczycie, wy wszyscy wśród których przebywałem, głosząc królestwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '25', '25', 'Dlatego oświadczam wam w dniu dzisiejszym: (odtąd) za nikogo z was nie odpowiadam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '26', '26', 'Bo nie zaniedbywałem nigdy głoszenia wam pełnej woli Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '27', '27', 'Troszczcie się o siebie samych i całą owczarnię. Duch Święty postawił was w niej, abyście doglądali i dostarczali pokarmu wspólnocie Boga, który ją nabył na własność przez swoją śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '28', '28', 'Zdaję sobie sprawę, że po moim odejściu wejdą między was wilki drapieżne, które nie oszczędzą owczarni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '29', '29', 'Nawet wśród was znajdą się tacy, którzy będą głosili zgubne nauki, aby pociągnąć za sobą uczniów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '30', '30', 'Dlatego czuwajcie i pamiętajcie, że przez trzy lata we dnie i w nocy nie przestawałem każdego z was we łzach napominać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '31', '31', 'A teraz polecam was Bogu i słowu Jego łaski, które ma moc budowania i przekazania dziedzictwa wszystkim uświęconym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '32', '32', 'Nie pragnąłem złota ani srebra, ani czyjeś szaty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '33', '33', 'Sami przecież wiecie, że swoimi rękoma zaspokajałem własne potrzeby i tych, którzy ze mną byli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '34', '34', 'W każdym wypadku ukazywałem wam, jak trzeba pracować wspierając słabych, mając w pamięci słowa Pana Jezusa, który sam powiedział: &gt;&gt;Lepiej jest dawać, aniżeli brać&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '35', '35', 'Gdy wypowiedział te słowa, upadł na kolana i ze wszystkimi razem się modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '36', '36', 'Wtedy wszyscy zaczęli głośno płakać i całowali Pawła, rzucając mu się na szyję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '19', '37', '37', 'Najbardziej jednak boleli nad słowami przez niego wypowiedzianymi, że już nigdy go nie zobaczą, i odprowadzili go na statek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '0', '0', 'Gdyśmy się z nimi rozstali, odpłynęliśmy prosto od Kos, a nazajutrz na Rodos, stamtąd zaś do Patary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '1', '1', 'Natrafiliśmy tam na statek płynący do Fenicji, wsiedliśmy na niego i odpłynęliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '2', '2', 'Ujrzeliśmy Cypr i zostawiliśmy go po lewej stronie, popłynęliśmy do Syrii i wylądowaliśmy w Tyrze; tam bowiem miał być wyładowany ze statku towar.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '3', '3', 'Odnalazłszy tam uczniów, zatrzymaliśmy się siedem dni. Niektórzy z nich pod wpływem Ducha Świętego odradzali Pawłowi podróż do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '4', '4', 'Kiedy zakończył się tam nasz pobyt, wyruszyliśmy w drogę. Odprowadzili nas wszyscy z żonami i dziećmi aż za miasto; uklękliśmy na wybrzeżu i razem modliliśmy się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '5', '5', 'Pożegnaliśmy się, wsiedliśmy na statek, a oni wrócili do domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '6', '6', 'My zaś po odbyciu morskiej podróży z Tyru zawinęliśmy do Ptolomaidy, gdzie pozdrowiliśmy braci, zatrzymując się u nich jeden dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '7', '7', 'Nazajutrz przybyliśmy do Cezarei i udaliśmy się do domu Filipa ewangelisty, który był jednym z siedmiu. Tam zamieszkaliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '8', '8', 'Miał on cztery córki, dziewice, które były prorokiniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '9', '9', 'Gdy przebywaliśmy tam jakiś czas, zjawił się z Judei prorok Agabos.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '10', '10', 'Przyszedł on do nas, wziął pas Pawła, opasał sobie nim nogi i ręce i powiedział: \'To mówi Duch Święty: Człowieka, do którego należy ten pas, tak zwiążą Żydzi w Jerozolimie i wydadzą w ręce pogan\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '11', '11', 'Słysząc to prosiliśmy Pawła zarówno my, jak i miejscowi, aby nie udawał się do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '12', '12', 'Odpowiedział na to Paweł: \'Czemu płaczecie i rozdzieracie mi serce. Ja przecież gotów jestem dla imienia Pana Jezusa dać się nie tylko uwięzić w Jerozolimie, ale nawet i śmierć ponieść\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '13', '13', 'Nie mogąc go nakłonić, daliśmy mu wreszcie spokój i powiedzieliśmy: \'Niech się dzieje wola Pańska.\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '14', '14', 'Po upływie tych dni przygotowaliśmy się do podróży i wyruszyliśmy do Jerozolimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '15', '15', 'Towarzyszyli nam uczniowie z Cezarei i zaprowadzili nas do Mnazona z Cypru, dawnego ucznia, który przyjął nas w gościnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '16', '16', 'Gdy dotarliśmy do Jerozolimy, bracia przyjęli nas z radością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '17', '17', 'Następnego dnia Paweł udał się z nami do Jakuba, u którego byli zebrani wszyscy starsi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '18', '18', 'Pozdrowił ich, opowiedział im dokładnie, czego to Bóg dokonał przez jego posługę wśród pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '19', '19', 'Gdy usłyszeli te słowa, wielbili Boga i powiedzieli do niego: \'Widzisz, bracie, ile to tysięcy jest nawróconych wśród Żydów, a wszyscy gorliwie przestrzegają Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '20', '20', 'O tobie zaś powiedzieli, że nauczasz Żydów, mieszkających wśród pogan, odstępstwa od Mojżesza, nie zalecając obrzezania dzieci i przestrzegania zwyczajów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '21', '21', 'Co robić? Z pewnością dowiedzą się, że przyszedłeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '22', '22', 'Zrób więc tak, jak ci radzimy. Jest wśród nas czterech ludzi, którzy złożyli ślub.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '23', '23', 'Razem z nimi poddaj się oczyszczeniu, pokryj za nich koszta, aby mogli ostrzyc głowy, a wszyscy dowiedzą się, że to, co o tobie słyszeli, nie jest prawdą, gdyż ty sam przestrzegasz Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '24', '24', 'A jeśli chodzi o nawrócenie pogan, wysłaliśmy list z poleceniem, aby wystrzegali się rzeczy ofiarowanych bożkom, pokarmu ze zwierząt uduszonych i nierządu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '25', '25', 'Następnego dnia Paweł wraz z tymi ludźmi poddał się oczyszczeniu, następnie udał się do świątyni, by ogłosić, że dni oczyszczenia dobiegły końca i że złożona zostanie za każdego z nich ofiara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '26', '26', 'Pod koniec tych siedmiu dni Żydzi z Azji zobaczyli go w świątyni, podburzyli cały tłum i rzucili się na niego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '27', '27', 'krzycząc: \'Izraelici, pomóżcie! Jest tu ten człowiek, który naucza wszystkich i wszędzie przeciwko ludowi, Prawu i temu miejscu, a nawet Greków wprowadził do świątyni, bezczeszcząc to święte miejsce\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '28', '28', 'Widzieli bowiem przedtem w mieście razem z nimi Trofima z Efezu i sądzili teraz, że Paweł wprowadził go do świątyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '29', '29', 'Poruszyli tym całe miasto, doszło do zbiegowiska, chwycili Pawła, wywlekli go na zewnątrz świątyni i natychmiast zamknęli drzwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '30', '30', 'Gdy oni usiłowali go zabić, do trybuna kohorty doszła wieść, że w całej Jerozolimie jest ogromne zamieszanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '31', '31', 'Ten natychmiast zebrał żołnierzy i setników i pobiegł do nich. Na widok trybuna kohorty i żołnierzy przestali bić Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '32', '32', 'Trybun zbliżył się do niego, kazał go zatrzymać, związać dwoma łańcuchami oraz wypytywał kim jest i co uczynił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '33', '33', 'W tłumie każdy wykrzykiwał co innego. Nie mogąc się więc nic pewnego dowiedzieć z powodu wrzawy, kazał zaprowadzić do twierdzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '34', '34', 'Gdy znalazł się przy schodach, doszło do tego, że żołnierze musieli go nieść, bo tłum silnie nacierał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '35', '35', 'Wielki tłum szedł bowiem na nim i krzyczał: \'Zabij go!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '36', '36', 'Przy samym wejściu do twierdzy Paweł zapytał trybuna: \'Czy wolno mi coś ci powiedzieć?\' A o na to: \'Umiesz po grecku?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '37', '37', 'To ty nie jesteś Egipcjaninem, który przed paru dniami wywołał rozruchy, wyprowadzając na pustynię cztery tysiące sykaryjczyków?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '38', '38', 'Paweł odpowiedział: \'Ja jestem Żydem z Tarsu, obywatelem niemałego miasta w Cylicji. Proszę cię, pozwól mi przemówić do ludu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '20', '39', '39', 'Gdy mu pozwolił, Paweł stanął na schodach, dał ręką znak ludowi, a gdy zapadła głęboka cisza, odezwał się do nich po hebrajsku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '0', '0', 'Bracia i ojcowie! Posłuchajcie teraz mojej obrony\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '1', '1', 'Usłyszawszy, że odezwał się do nich po hebrajsku, jeszcze bardziej się uciszyli, a on zaczął mówić:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '2', '2', '\'Jestem Żydem, urodzonym w Tarsie w Cylicji, lecz wychowywałem się tutaj, w tym mieście, u stóp Gamaliela. Otrzymałem staranne wykształcenie w ojczystym Prawie. Byłem tak gorliwy w służbie Bożej, jak wy dziś jesteście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '3', '3', 'Prześladowałem wyznawców tej nauki, głosując za karą śmierci, zakuwałem w kajdany i wtrącałem do więzienia mężczyzn i kobiety.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '4', '4', 'Świadkiem jest mi arcykapłan i cała najwyższa rada. Od nich też wziąłem listy do braci w Damaszku, gdzie się udałem, aby i tamtych zakuć w kajdany, przyprowadzić do Jerozolimy i wymierzyć karę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '5', '5', 'Gdy w czasie drogi zbliżałem się do Damaszku, około południa nagle oślepiło mnie światło z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '6', '6', 'Upadłem na ziemię i usłyszałem głos, skierowany do mnie: &gt;&gt;Szawle, Szawle, dlaczego mnie prześladujesz?&lt;&lt;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '7', '7', 'Wtedy zapytałem: &gt;&gt;Kim jesteś, Panie?&lt;&lt; - Odpowiedział mi: &gt;&gt;Ja jestem Jezusem z Nazaretu, którego prześladujesz&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '8', '8', 'Ci, którzy mi towarzyszyli, widzieli światło, ale nie słyszeli głosu skierowanego do mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '9', '9', 'A ja pytałem dalej: &gt;&gt;Panie, co mam czynić?&lt;&lt; Pan mi odpowiedział: &gt;&gt;Wstań, idź do Damaszku, a tam ci wszystko powiedzą, co masz czynić&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '10', '10', 'A ponieważ nic nie widziałem oślepiony blaskiem owego światła, towarzysze moi wzięli mnie za rękę i zaprowadzili do Damaszku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '11', '11', 'A tam Ananiasz, człowiek pobożny, żyjący według Prawa, cieszący się dobrą opinią u wszystkich tamtejszych Żydów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '12', '12', 'przyszedł, stanął przede mną i powiedział: &gt;&gt;Szawle, bracie, przejrzyj!&lt;&lt; W tej samej chwili spojrzałem na niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '13', '13', 'On zaś powiedział: &gt;&gt;Bóg Ojców naszych powołał cię, abyś poznał Jego wolę, oglądał Sprawiedliwego i usłyszał głos z Jego ust;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '14', '14', 'będziesz Mu świadkiem u wszystkich ludzi tego, co widziałeś i słyszałeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '15', '15', 'Czemu się więc wahasz? Wstań, ochrzcij się, oczyść się z grzechów twoich, wzywając Jego imienia!&lt;&lt;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '16', '16', 'Potem wróciłem do Jerozolimy, a gdy modliłem się w świątyni, miałem widzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '17', '17', 'Zobaczyłem Go, jak mówi do mnie: &gt;&gt;Spiesz się i opuść prędko Jerozolimę, bo tu nie przyjmą twego świadectwa o Mnie&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '18', '18', 'A ja na to: &gt;&gt;Panie, oni przecież wiedzą, że to właśnie ja wtrącałem do więzienia i kazałem bić w synagogach tych, którzy w Ciebie uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '19', '19', 'Gdy Szczepan krwią swoją dawał Tobie świadectwo, byłem przy tym, zgadzałem się na to i strzegłem szat tych, którzy go zabijali&lt;&lt;,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '20', '20', 'I powiedział do mnie: &gt;&gt;Idź, wyślę cię w daleką drogę do pogan&lt;&lt;\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '21', '21', 'Aż do tych słów słuchali go i wtedy podnieśli krzyk: \'Precz z nim stąd! Nie godzi się, aby ten człowiek żył na ziemi!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '22', '22', 'Krzyczeli, zrzucali okrycia, ciskali piaskiem do góry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '23', '23', 'Trybun kazał zaprowadzić go do twierdzy, polecił przesłuchać, stosując biczowanie, aby się dowiedzieć, dlaczego tak przeciwko niemu krzyczeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '24', '24', 'Kiedy związywano go rzemieniami, Paweł powiedział do stojącego obok setnika: \'Czy wolno wam obywatela rzymskiego biczować bez wyroku?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '25', '25', 'Słysząc to setnik podszedł do trybuna i powiedział mu: \'I co ty chcesz zrobić? Człowiek ten jest obywatelem rzymskim\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '26', '26', 'Trybun zbliżył się do niego i zapytał: Powiedz mi, czy ty jesteś obywatelem rzymskim?\' A on odpowiedział: \'Tak\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '27', '27', 'Trybun na to: \'Ja nabyłem to obywatelstwo za wysoką sumę\'. \'A ja mam je od urodzenia\' - odparł Paweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '28', '28', 'Natychmiast odstąpili od niego ci, którzy mieli go przesłuchać, a trybun dowiedziawszy się, że jest obywatelem rzymskim, przestraszył się, iż kazał go związać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '21', '29', '29', 'Nazajutrz, chcąc się upewnić, o co go Żydzi oskarżają, uwolnił go z więzów, kazał zawołać arcykapłanów i cały sanhedryn, przyprowadził Pawła i postawił go przed nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '0', '0', 'Paweł utkwił wzrok w członków sanhedrynu i powiedział: \'Bracia! Aż po dzień dzisiejszy żyję z czystym sumieniem wobec Boga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '1', '1', 'Wtedy arcykapłan Ananiasz kazał tym, którzy blisko niego stali, uderzyć go w twarz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '2', '2', 'A Paweł na to: \'Uderzy cię Bóg, ściano pobielana. Zasiadłeś, aby mnie sądzić według Prawa, a każesz mnie bić wbrew Prawu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '3', '3', 'Stojący przy nim zwrócili mu uwagę: \'Ubliżasz arcykapłanowi Boga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '4', '4', 'Paweł odparł: \'Bracia, nie wiedziałem, że to arcykapłan, jest bowiem napisane: &gt;&gt;Nie będziesz źle mówił o przełożonym ludu&lt;&lt;\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '5', '5', 'Świadomy, że część sanhedrynu stanowili saduceusze, a resztę faryzeusze, Paweł zawołał: \'Bracia! Jestem faryzeuszem, synem faryzeuszów, a sądzi mnie się z powodu nadziei w zmartwychwstanie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '6', '6', 'Słowa te wywołały spór między faryzeuszami i saduceuszami i uczestnicy posiedzenia podzielili się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '7', '7', 'Saduceusze bowiem uczą, że nie ma ani zmartwychwstania, ani aniołów, ani duchów; faryzeusze zaś uznają jedno i drugie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '8', '8', 'Doszło do tak wielkiej kłótni, że niektórzy nauczyciele Pisma ze stronnictwa faryzeuszów aż powstawali i zawzięcie twierdzili: \'Nie znajdujemy nic złego w tym człowieku. A może rzeczywiście przemawia przez niego jakiś duch albo anioł\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '9', '9', 'A gdy spór dalej się zaostrzał, trybun kohorty, w obawie by nie rozszarpano Pawła, kazał zejść żołnierzom, wyrwać go spośród nich i zaprowadzić do twierdzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '10', '10', 'Następnej nocy ukazał mu się Pan i powiedział: \'Odwagi! Jak dawałeś o Mnie świadectwo w Jerozolimie, tak musisz świadczyć o Mnie w Rzymie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '11', '11', 'Z nastaniem dnia Żydzi uknuli spisek, składając przysięgę, że nie będą jedli ani pili, dopóki nie zabiją Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '12', '12', 'Spiskowców tych było ponad czterdziestu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '13', '13', 'Przyszli oni do arcykapłanów i starszych i powiedzieli: \'Złożyliśmy przysięgę, że niczego nie weźmiemy do ust, póki nie zabijemy Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '14', '14', 'Postarajcie się więc razem z sanhedrynem nakłonić trybuna, aby go do was przyprowadził, niby to celem dokładniejszego zbadania sprawy; gdy on się zbliży, my będziemy już przygotowani, by go zabić\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '15', '15', 'O przygotowywanej zasadzce dowiedział się siostrzeniec Pawła. Przybył do twierdzy, wszedł do Pawła i powiadomił go o tym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '16', '16', 'Wtedy Paweł przywołał jednego z setników i powiedział mu: \'Zaprowadź tego chłopca do trybuna, ma mu bowiem coś przekazać\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '17', '17', 'Ten zabrał go ze sobą, zaprowadził do trybuna i powiedział: \'Więzień Paweł przywołał mnie i poprosił, abym tego chłopca przyprowadził do ciebie, ma on ci coś powiedzieć\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '18', '18', 'Trybun kohorty wziął go za rękę, odszedł na bok i zapytał: \'O czym to masz mnie powiadomić?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '19', '19', 'Ten odpowiedział: \'Żydzi postanowili prosić cię, abyś jutro przyprowadził Pawła przed sanhedryn, niby w celu dokładniejszego zbadania jego sprawy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '20', '20', 'Nie daj się więc namówić, czyha bowiem na niego ponad czterdziestu ludzi spośród nich. Złożyli oni przysięgę, że nie będą ani jeść, ani pić, póki go nie zabiją; są już gotowi, czekają tylko na twoją zgodę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '21', '21', 'Odchodzącemu chłopcu trybun przykazał, aby nikomu nie mówił, że go o tym powiadomił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '22', '22', 'Potem wezwał dwóch setników i wydał rozkaz: \'Przygotujcie w drogę do Cezarei na trzecią godzinę w nocy dwustu żołnierzy, siedemdziesięciu konnych i dwustu oszczepników;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '23', '23', 'miejcie też w pogotowiu zwierzęta juczne, aby na nich przewieść bezpiecznie Pawła i przekazać go namiestnikowi Feliksowi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '24', '24', 'Napisał również list następującej treści:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '25', '25', '\'Klaudiusz Lyzjasz najdostojniejszemu namiestnikowi Feliksowi przesyła pozdrowienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '26', '26', 'Żydzi napadli na tego człowieka, a kiedy już mieli go zabić, dobiegłem razem z żołnierzami i wyrwałem go. Dowiedziałem się bowiem, że jest obywatelem rzymskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '27', '27', 'Chcąc zaś dowiedzieć się, z jakiego powodu go oskarżają, zaprowadziłem go przed ich sanhedryn.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '28', '28', 'Stwierdziłem, że oskarżają go o sporne kwestie z ich Prawa oraz że nie popełnił żadnego przestępstwa, za które zasługiwałby na karę śmierci lub więzienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '29', '29', 'A gdy mi doniesiono, że przygotowano na niego zamach, wysłałem go do ciebie i powiadomiłem oskarżycieli, by tobie powiedzieli, co mają przeciwko niemu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '30', '30', 'Żołnierze zgodnie z rozkazem wzięli Pawła i nocą odstawili do Antypatris.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '31', '31', 'Nazajutrz zostawili konnych, by jechali z nim dalej, a sami wrócili do twierdzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '32', '32', 'Tamci zaś przybyli do Cezarei, oddali list namiestnikowi i przekazali mu Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '33', '33', 'A on przeczytawszy list zapytał, z jakiej prowincji pochodzi; a dowiedziawszy się, że z Cylicji,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '22', '34', '34', 'powiedział: \'Przesłucham cię, gdy przybędą tu twoi oskarżyciele\'. Jego zaś rozkazał pilnować w pałacu Heroda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '0', '0', 'Po upływie pięciu dni przybył arcykapłan Ananiasz z kilkoma starszymi i retorem Tertullosem, aby wobec namiestnika wytoczyć Pawłowi sprawę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '1', '1', 'Wezwano go więc, a wtedy Tertullos rozpoczął swoją oskarżycielską mowę: \'Dzięki tobie cieszymy się pełnym pokojem, dzięki twojej mądrości przeprowadzono zmiany dla dobra tego narodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '2', '2', 'Uznając to, dostojny Feliksie, jesteśmy ci za to zawsze i wszędzie wdzięczni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '3', '3', 'By ci jednak nie zabierać zbyt wiele czasu, proszę, byś był łaskaw choć przez chwilę nas posłuchać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '4', '4', 'Stwierdziliśmy bowiem, że człowiek ten, będąc przywódcą sekty nazarejczyków, jest rozsadnikiem zła i szerzy niepokój wśród wszystkich Żydów na całym świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '5', '5', 'Chwyciliśmy go więc, gdyż usiłował zbezcześcić świątynię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '6', '6', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '7', '7', 'Zresztą gdy go sam przesłuchasz, zorientujesz się, o co go oskarżamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '8', '8', 'Skargę tę potwierdzili także (inni) Żydzi, oświadczając, że sprawa tak rzeczywiście wygląda\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '9', '9', 'Wtedy namiestnik dał znak, a Paweł zabrał głos: \'Wiedząc, że od wielu lat jesteś sędzią nad tym narodem, spokojnie mogę przystąpić do obrony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '10', '10', 'Nie upłynęło więcej niż dwanaście dni - możesz to zresztą sprawdzić - jak przybyłem do Jerozolimy, by oddać cześć Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '11', '11', 'Nie przychwycili mnie ani w świątyni, ani w synagogach, ani gdzie indziej w mieście, bym prowadził z kimś spory lub podburzał tłumy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '12', '12', 'Nie mogą więc udowodnić mi tego, o co mnie oskarżają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '13', '13', 'Przyznaję jednak, że służę Bogu ojców naszych zgodnie z nauką, którą oni nazywają sektą. Wierzę we wszystko, co jest napisane w Prawie i u Proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '14', '14', 'Mam w Bogu nadzieję, którą i oni podzielają, że nastąpi kiedyś zmartwychwstanie sprawiedliwych i niesprawiedliwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '15', '15', 'Dlatego staram się usilnie o to, bym zawsze miał czyste sumienie wobec Boga i ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '16', '16', 'Przybyłem tu po wielu latach, aby mojemu narodowi przynieść jałmużny i złożyć ofiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '17', '17', 'Właśnie wtedy znaleźli mnie po oczyszczeniu w świątyni, ale bez tłumu i zgiełku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '18', '18', 'Powinni więc tu stanąć przed tobą niektórzy Żydzi z Azji i oskarżać, jeśli mają coś przeciwko mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '19', '19', 'Albo tu obecni niechaj powiedzą, jakiego przestępstwa dopatrzyli się u mnie, gdy stanąłem przed sanhedrynem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '20', '20', 'Chyba że chodzi im o ten jeden okrzyk, który wydałem, stojąc między nimi: Z powodu zmartwychwstania umarłych stoję dziś wśród was jako oskarżony\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '21', '21', 'Feliks zaś, który dość dokładnie znał tę naukę, odłożył sprawę i powiedział: \'Rozpatrzę waszą sprawę, gdy przybędzie trybun Lyzjasz\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '22', '22', 'Setnikowi zaś polecił strzec go, nie broniąc okazywania mu względów i pozwalając, by jego przyjaciele oddawali mu usługi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '23', '23', 'Po kilku dniach przybył Feliks ze swoją żoną Druzyllą, która była Żydówką, polecił przyprowadzić Pawła i przysłuchiwał się jego nauce o wierze w Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '24', '24', 'Gdy zaczął mówić o usprawiedliwieniu, wstrzemięźliwości i przyszłym sądzie, zaniepokojony tym Feliks powiedział: \'Na razie wystarczy, możesz odejść. Wezwę cię znowu w odpowiedniej chwili\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '25', '25', 'Spodziewając się jednak, że otrzyma od Pawła jakieś pieniądze, dość często po niego posyłał i przeprowadzał z nim rozmowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '23', '26', '26', 'Po upływie dwóch lat po Feliksie nastał Porcjusz Festus, a Feliks, chcąc się przypodobać Żydom, pozostawił Pawła w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '0', '0', 'Festus, kiedy przybył do prowincji, po trzech dniach udał się z Cezarei do Jerozolimy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '1', '1', 'gdzie arcykapłan i przełożeni żydowscy wytoczyli wobec niego sprawę przeciwko Pawłowi. Nalegając na niego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '2', '2', 'prosili, by w tej sprawie okazał im przychylność i kazał sprowadzić go do Jerozolimy; zamierzali bowiem w drodze urządzić na niego zasadzkę, aby go zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '3', '3', 'Festus powiedział im, że Paweł jest strzeżony w Cezarei i że on sam wkrótce tam przybędzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '4', '4', '\'Niech więc upoważnieni spośród was - powiedział - jadą ze mną i oskarżą go, jeśli ten człowiek popełnił jakieś przestępstwo\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '5', '5', 'Spędził wśród nich osiem do dziesięciu dni i odjechał do Cezarei. Nazajutrz rozpoczął sądy i kazał sprowadzić Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '6', '6', 'Gdy ten się stawił, Żydzi, którzy przybyli z Jerozolimy, obstąpili go, oskarżając o wiele ciężkich przestępstw, nie mogli jednak mu ich udowodnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '7', '7', 'Paweł się bronił: \'Nie poczuwam się do żadnej winy: ani przeciwko Prawu żydowskiemu, ani przeciwko świątyni, ani też przeciwko cesarzowi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '8', '8', 'Festus chcąc przypodobać się Żydom powiedział do Pawła: \'Czy może chcesz jechać do Jerozolimy, by tam stanąć przed moim sądem?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '9', '9', 'Paweł na to: \'Stoję przed sądem cesarskim, przed nim też powinienem być sądzony. Bardzo dobrze wiesz o tym, że Żydom nie wyrządziłem żadnej krzywdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '10', '10', 'Jeżeli więc popełniłem coś złego, przez co zasługiwałbym na karę śmierci, nie boję się umrzeć; jeżeli zaś nie poczuwam się do niczego, o co mnie oskarżają, nikt nie może mnie rzucić na ich łaskę. Odwołuję się do cesarza\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '11', '11', 'Wtedy Festus omówiwszy to z doradcami powiedział: \'Odwołałeś się do cesarza, więc przed cesarzem staniesz\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '12', '12', 'Po upływie kilku dni do Cezarei przybył król Agryppa z Bereniką na powitanie Festusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '13', '13', 'Spędzili tam szereg dni. Wtedy Festus przedstawił królowi sprawę Pawła: \'Feliks zostawił w więzieniu pewnego człowieka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '14', '14', 'którego sprawę w czasie mego pobytu w Jerozolimie wnieśli arcykapłani i starsi żydowscy, domagając się, abym go skazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '15', '15', 'Odpowiedziałem im: Rzymianie nie mają zwyczaju wydawać człowieka na czyjąś łaskę, zanim oskarżony nie stanie twarzą w twarz wobec oskarżycieli i nie będzie miał sposobności obronić się przed zarzutami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '16', '16', 'Gdy więc ze mną przybyli, bez zwłoki rozpocząłem sądy i kazałem przyprowadzić tego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '17', '17', 'Oskarżyciele nie podali żadnej winy, której ja się spodziewałem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '18', '18', 'a raczej jakieś sporne sprawy dotyczące ich wierzeń wysuwali przeciw niemu i jakiemuś Jezusowi, który już umarł, a o którym Paweł mówi, że żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '19', '19', 'Miałem więc wątpliwości, jak to załatwić, dlatego zapytałem, czy nie zechciałby udać się do Jerozolimy, by tam tę sprawę rozpatrzyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '20', '20', 'Paweł zaś wniósł odwołanie, by go pozostawić pod strażą aż do rozstrzygnięcia cesarskiego. Poleciłem go więc strzec, dopóki nie odeślę do cesarza\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '21', '21', 'Na to Agryppa powiedział do Festusa: \'Chciałbym i ja usłyszeć tego człowieka\'. \'Usłyszysz go jutro\' - odpowiedział (Festus).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '22', '22', 'Następnego dnia, gdy Agryppa i Berenika z całą okazałością weszli do sali przyjęć w towarzystwie trybunów i znakomitych obywateli miasta, na rozkaz Festusa przyprowadzono Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '23', '23', 'Festus przemówił: \'Królu Agryppo i wszyscy tu obecni! Oto widzicie człowieka, w sprawie którego cały lud żydowski wywierał na mnie nacisk zarówno w Jerozolimie, jak i tutaj, wołając, że nie powinien już dłużej żyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '24', '24', 'A ja stwierdziłem, że nie popełnił on nic takiego, czym by zasługiwał na śmierć. Ponieważ jednak apelował do cesarza, postanowiłem go odesłać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '25', '25', 'Nie mam jednak nic pewnego, co mógłbym władcy napisać, dlatego postawiłem go przed wami, a zwłaszcza przed tobą, królu Agryppo, abym po przeprowadzeniu przesłuchania wiedział, co napisać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '24', '26', '26', 'Bo wydaje mi się czymś nierozsądnym posyłać więźnia nie podając, o co się go oskarża\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '0', '0', 'Agryppa zwrócił się do Pawła: \'Wolno ci mówić we własnej obronie\'. Wtedy Paweł wyciągnął rękę i rozpoczął obronę:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '1', '1', '\'Królu Agryppo! Uważam się za szczęśliwego, że mogę dziś przed tobą bronić się wobec zarzutów, jakie stawiają mi Żydzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '2', '2', 'Tym bardziej że znasz się doskonale na wszystkich zwyczajach żydowskich i spornych sprawach. Proszę cię więc, abyś mnie cierpliwie wysłuchał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '3', '3', 'Wszyscy Żydzi znają moje życie. Od młodości upływało ono wśród mego narodu w Jerozolimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '4', '4', 'Znają mnie od dawna i gdyby chcieli, mogliby zaświadczyć, że żyłem jako faryzeusz wierny zasadom najsurowszej sekty religijnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '5', '5', 'A teraz oskarżają mnie, ponieważ pokładam nadzieję w obietnicy danej przez Boga naszym ojcom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '6', '6', 'której wypełnienia spodziewa się dwanaście naszych pokoleń, modląc się dniem i nocą. Królu, z powodu tej właśnie nadziei oskarżają mnie Żydzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '7', '7', 'Dlaczego uważacie za rzecz nie do wiary, że Bóg wskrzesza zmarłych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '8', '8', 'Ja sam kiedyś uważałem, że trzeba wszelkimi sposobami występować przeciwko Jezusowi z Nazaretu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '9', '9', 'Tak postępowałem w Jerozolimie, gdzie wielu świętych wtrąciłem do więzienia, mając pełnomocnictwa arcykapłanów. Kiedy zaś ich skazywano na śmierć, głosowałem za tym wyrokiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '10', '10', 'Wymierzając kary zmuszałem ich często do bluźnierstwa po wszystkich synagogach. Bez opamiętania i umiaru prześladowałem ich w okolicznych miastach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '11', '11', 'Z takim nastawieniem wyruszyłem do Damaszku, mając pełnomocnictwo i polecenie od arcykapłanów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '12', '12', 'Królu! W samo południe, w czasie drogi ujrzałem światło z nieba, jaśniejsze od słońca, które olśniło mnie i tych, którzy ze mną jechali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '13', '13', 'Gdy wszyscy upadliśmy na ziemię, usłyszałem głos skierowany do mnie w języku hebrajskim: &gt;&gt;Szawle, Szawle, dlaczego Mnie prześladujesz? Trudno ci wierzgać przeciwko ościeniowi&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '14', '14', 'A ja zapytałem: &gt;&gt;Kim jesteś, Panie?&lt;&lt; Pan odpowiedział: &gt;&gt;Jestem Jezusem, którego ty prześladujesz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '15', '15', 'Ale podnieś się i stań na nogi. Ukazałem ci się po to, aby cię ustanowić sługą i świadkiem tego, że Mnie widziałeś i że jeszcze Mnie zobaczysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '16', '16', '- ´Obronię cię´ przed ludem i ´przed poganami, do których cię posyłam,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '17', '17', 'abyś otworzył im oczy, odwrócił ich od ciemności do światła, od władzy szatana do Boga, aby przez wiarę we Mnie dostąpili odpuszczenia grzechów i dziedzictwa ze świętymi&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '18', '18', 'Królu Agryppo, dlatego nie mogłem nie ulec temu widzeniu z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '19', '19', 'Najpierw więc nawoływałem do pokuty mieszkańców Damaszku, a potem Jerozolimy i całej Judei, a także pogan, by się nawrócili do Boga, spełniając uczynki pokutne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '20', '20', 'Z tego powodu Żydzi pochwycili mnie w świątyni i usiłowali zabić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '21', '21', 'Dzięki jednak opiece Bożej żyję do dzisiejszego dnia i daję świadectwo małym i wielkim, nie nauczając nic ponad to, co już przepowiedzieli prorocy i Mojżesz:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '22', '22', 'że Mesjasz musi cierpieć, że jako pierwszy powstanie z martwych i stanie się światłem ludu wybranego i pogan\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '23', '23', 'W czasie tej obrony Festus głośno zawołał: \'Szalejesz, Pawle! Wielka uczoność doprowadza cię do szaleństwa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '24', '24', 'Paweł na to powiedział: \'Nie szaleję, dostojny Festusie, ale wypowiadam słowa prawdy i mądrości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '25', '25', 'Wie bowiem o tym król, wobec którego mówię otwarcie będąc przekonanym, że nic z tych spraw nie uszło jego uwagi, bo przecież nic nie działo się w ukryciu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '26', '26', 'Czy wierzysz prorokom, królu Agryppo? Wiem, że wierzysz!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '27', '27', 'A na to Agryppa do Pawła: \'O mało byś mnie nie nakłonił, bym został chrześcijaninem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '28', '28', '\'Dziękowałbym Bogu - powiedział na to Paweł - gdyby w krótkim lub dłuższym czasie nie tylko ty, ale wszyscy, którzy mnie dziś słuchają, stali się takimi, jak ja jestem, pominąwszy te więzy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '29', '29', 'Powstał król, namiestnik, Berenika i ci, którzy z nimi siedzieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '30', '30', 'Oddalając się rozmawiali między sobą: \'Człowiek ten nie popełnił nic takiego, przez co zasłużyłby na śmierć lub więzienie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '25', '31', '31', 'Agryppa zaś powiedział do Festusa: \'Można by zwolnić tego człowieka, gdyby się nie odwołał do cesarza\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '0', '0', 'Gdy już postanowiono, że mamy odpłynąć do Italii, Pawła i innych więźniów przekazano Juliuszowi, setnikowi kohorty cesarskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '1', '1', 'Wsiedliśmy na okręt adramyteński, który miał płynąć do portów azjatyckich. Był też z nami Arystarch, Macedończyk z Tesaloniki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '2', '2', 'Następnego dnia przybywaliśmy do Sydonu. Juliusz zaś, odnosząc się życzliwie do Pawła, pozwolił mu udać się do przyjaciół, by się o niego zatroszczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '3', '3', 'Wyruszywszy stamtąd płynęliśmy wzdłuż Cypru, gdyż wiatry były przeciwne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '4', '4', 'Przepłynęliśmy więc morze na wysokości Cylicji i Pamfilii, przybyliśmy do Myry w Licji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '5', '5', 'Tam setnik zastał okręt aleksandryjski, płynący do Italii, i umieścił nas na nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '6', '6', 'Tak wolno płynęliśmy przez wiele dni i przybyliśmy zaledwie na wysokość Knidos; ponieważ jednak wiatr nie pozwalał posuwać się naprzód, popłynęliśmy wzdłuż Krety, w kierunku Salmone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '7', '7', 'I płynąc wzdłuż jej brzegów z trudem dotarliśmy do miejsca zwanego Kaloi Limenes, przystani w pobliżu miasta Lasaia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '8', '8', 'Ponieważ straciliśmy wiele czasu, żegluga stawała się już niebezpieczna (minął bowiem dzień postu). Dlatego Paweł ich ostrzegał:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '9', '9', '\'Ludzie - mówił do nich - przewiduje, że dalsza żegluga będzie ryzykowna zarówno dla ładunku i okrętu, jak i dla naszego życia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '10', '10', 'Setnik jednak bardziej wierzył sternikowi i właścicielowi statku niż słowom Pawła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '11', '11', 'Ponieważ zaś port nie nadawał się do przezimowania, większość z nich uchwaliła wyruszyć w dalszą drogę, by dobić do Feniksu, portu na Krecie, osłoniętego od wiatrów południowo-zachodnich i północno-zachodnich i tam spędzić zimę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '12', '12', 'Kiedy powiał wiatr z południa, podnieśli kotwicę w przekonaniu, że będą mogli wykonać swój zamiar, i popłynęli wzdłuż Kredy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '13', '13', 'Wkrótce jednak od strony wyspy zerwał się straszliwy huragan zwany eurakwilo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '14', '14', 'Porwał on okręt, tak że nie mógł mu się oprzeć. Poddani losowi zostaliśmy uniesieni przez fale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '15', '15', 'Płynąc pod osłoną wyspy Kauda, z trudem udało się nam chwycić łódź ratunkową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '16', '16', 'Wciągnąwszy ją na pokład, zabezpieczono okręt opasując go linami. Bojąc się, by nie wpaść na Syrtę, zrzucili ruchomą kotwicę i pozwolili się nieść falom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '17', '17', 'Nazajutrz, gdy miotała nami gwałtowna burza, zaczęto wyrzucać ładunek,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '18', '18', 'a trzeciego dnia wyrzucono własnymi rękami sprzęt okrętowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '19', '19', 'A kiedy przez wiele dni nie było widać ani słońca, ani gwiazd, a burza niemniej szalała, zaczęła znikać jakakolwiek nadzieja ocalenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '20', '20', 'Gdy już długo byli bez posiłku, wtedy Paweł stanął wśród nich i powiedział: \'Ludzie, trzeba było mnie posłuchać i nie wyjeżdżać z Krety, oszczędzilibyście sobie tego niebezpieczeństwa i szkody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '21', '21', 'Ale i w tej sytuacji wzywam was, bądźcie dobrej myśli, nikt bowiem z was nie zginie, tylko okręt zatonie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '22', '22', 'Tej bowiem nocy stanął przy mnie anioł Boga, do którego należę i któremu cześć oddaję,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '23', '23', 'i powiedział: &gt;&gt;Nie bój się, Pawle, musisz stanąć przed cesarzem. Ze względu na ciebie Bóg ocali wszystkich, którzy z tobą płyną&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '24', '24', 'Bądźcie więc dobrej myśli, ludzie, wierzę bowiem Bogu, że tak będzie, jak mi zostało powiedziane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '25', '25', 'Wylądujemy na jakiejś wyspie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '26', '26', 'A gdy już czternastą noc unosiły nas fale po Adriatyku, około północy wydawało się żeglarzom, że zbliżają się do jakiegoś lądu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '27', '27', 'Spuściwszy więc sondę stwierdzili, że głębokość wynosi dwadzieścia sążni, a popłynąwszy nieco dalej znowu zmierzyli stwierdzając, że jest już tylko piętnaście sążni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '28', '28', 'W obawie więc, by nie wpaść na skaliste miejsca, zrzucili z rufy cztery kotwice i czekali, aż nastanie dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '29', '29', 'Kiedy żeglarze usiłowali zbiec z okrętu i pod pozorem zrzucenia kotwicy z dziobu okrętu spuścili w morze łódź ratunkową,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '30', '30', 'Paweł powiedział do setnika i żołnierzy: \'Jeżeli ci (żeglarze) nie pozostaną na okręcie, wy nie możecie się uratować\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '31', '31', 'Wtedy żołnierze odcięli liny od łodzi ratunkowej i pozwolili jej spaść do morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '32', '32', 'Gdy zaczęło się rozwidniać, Paweł zachęcał wszystkich do spożycia posiłku: \'Upływa już czternasty dzień jak czekacie głodni, nie spożywając żadnego posiłku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '33', '33', 'Dlatego wzywam was, byście się posilili, co przyczyni się do waszego ocalenia, nikomu bowiem z was włos z głowy nie spadnie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '34', '34', 'Po tych słowach na oczach wszystkich wziął chleb, odmówił modlitwę dziękczynną do Boga i połamawszy zaczął jeść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '35', '35', 'Wtedy wszyscy nabrali otuchy i zabrali się do jedzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '36', '36', 'Na okręcie było nas wszystkich dwieście siedemdziesiąt sześć osób.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '37', '37', 'Posilili się i aby odciążyć okręt, zboże wyrzucili do morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '38', '38', 'Kiedy nastał dzień nie rozpoznali lądu, ale zauważyli jakąś małą zatokę o płytkim wybrzeżu, do której, o ile to będzie możliwe, chcieli podprowadzić okręt.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '39', '39', 'Odcięli więc kotwice, które pozostały na morzu, a równocześnie rozluźnili wiązania sterowe, ustawili przedni żagiel pod wiatr i zmierzali w kierunku wybrzeża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '40', '40', 'Wpadłszy na mieliznę, osadzili na niej okręt. Dziób okrętu wrył się głęboko i został unieruchomiony, rufę zaś rozbiły fale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '41', '41', 'Wtedy żołnierze postanowili zabić więźniów, aby któryś z nich po dopłynięciu do brzegu nie uciekł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '42', '42', 'W tym zamiarze przeszkodził im setnik, który chciał uratować Pawła. Dał im rozkaz, aby ci, którzy umieją pływać, pierwsi wskoczyli do wody i wyszli na brzeg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '26', '43', '43', 'Pozostali zaś mieli to czynić albo na deskach, albo na resztkach okrętu. W ten sposób wszyscy cało wyszli na ląd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '0', '0', 'Po ocaleniu dowiedzieliśmy się, że wyspa nazywa się Malta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '1', '1', 'Mieszkańcy jej okazali nam niezwykłą życzliwość, rozpalili ognisko, zajęli się nami wszystkimi, zaczął bowiem padać deszcz i zrobiło się zimno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '2', '2', 'Paweł nazbierał trochę chrustu i rzucał go na ogień. Pod wpływem ciepła wypełzła żmija i uczepiła się jego ręki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '3', '3', 'Miejscowa ludność spostrzegła zwisającego gada u jego ręki, zaczęła mówić do siebie: \'Ten człowiek jest na pewno mordercą. Chociaż wyszedł cało z morza, to jednak bogini zemsty nie pozwala mu żyć\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '4', '4', 'On zaś strząsnął gada w ogień i nic złego mu się nie stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '5', '5', 'Ludzie zaś spodziewali się, że albo zaraz spuchnie, albo też trupem padnie; ale gdy długo czekali i widzieli, że nic osobliwego nie zaszło, zmienili zdanie i mówili, że jest bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '6', '6', 'W pobliżu tego miejsca była posiadłość naczelnika wyspy Publiusza, który przyjął nas i przez trzy dni gościł serdecznie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '7', '7', 'W tym czasie ojciec Publiusza leżał właśnie w gorączce i chorował na biegunkę. Paweł podszedł do niego, pomodlił się, położył na nim ręce i uzdrowił go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '8', '8', 'Po tym wydarzeniu inni chorzy mieszkańcy wyspy przychodzili do niego, a on ich uzdrawiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '9', '9', 'Okazywali nam też wiele szacunku, a przed wyjazdem zaopatrzyli nas w to, co było potrzebne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '10', '10', 'Po trzech miesiącach pobytu wyruszyliśmy w drogę na okręcie aleksandryjskim, z godłem Dioskurów, który zimował na wyspie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '11', '11', 'Trzy dni zatrzymaliśmy się w Syrakuzach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '12', '12', 'stąd, płynąc wzdłuż wybrzeży, dotarliśmy do Regium i po upływie jednego dnia, gdy zerwał się wiatr południowy, na drugi dzień przybyliśmy do Puteoli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '14', '14', 'Miejscowi bracia, gdy dowiedzieli się o nas, wyszli nam na spotkanie aż do Forum Appiusza i Trzech Gospód. Na ich widok Paweł podziękował Bogu i podniósł się na duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '15', '15', 'Gdy weszliśmy już do Rzymu, pozwolono Pawłowi zamieszkać osobno, ale z żołnierzem, który miał go pilnować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '16', '16', 'Po trzech dniach zaprosił do siebie wpływowych Żydów, a gdy się zjawili, powiedział do nich: \'Bracia! Chociaż nie uczyniłem nic złego memu narodowi ani nie występowałem przeciw zwyczajom ojczystym, to jednak wydano mnie z Jerozolimy, jako więźnia, w ręce Rzymian.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '17', '17', 'Ci zaś po przesłuchaniu chcieli mnie zwolnić, ponieważ nie było żadnego powodu, by wydać na mnie wyrok śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '18', '18', 'Temu jednak sprzeciwili się Żydzi, musiałem więc odwołać się do cesarza, nie mając oczywiście zamiaru oskarżać swego narodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '19', '19', 'Dlatego też was zaprosiłem, by się z wami zobaczyć i porozmawiać, bo dla nadziei Izraela dźwigam te kajdany\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '20', '20', 'Oni mu odpowiedzieli: \'Nie otrzymaliśmy z Judei żadnego listu, nie przybył też nikt z braci, który by doniósł lub powiedział o tobie coś złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '21', '21', 'Chętnie jednak usłyszymy, co ty myślisz, bo wiadomo nam, że ta sekta wszędzie spotyka się ze sprzeciwem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '22', '22', 'W wyznaczonym dniu przyszli jeszcze liczniej do jego mieszkania. Od rana aż do wieczora przedstawiał im sprawę dając świadectwo o królestwie Bożym, przekonywająco mówił o Jezusie na podstawie Prawa Mojżeszowego i Proroków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '23', '23', 'Jedni dali się przekonać jego słowom, a inni nie wierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '24', '24', 'Nie mogąc się porozumieć, zaczęli się rozchodzić, a wtedy Paweł rzucił im słowa: \'Dobrze Duch Święty powiedział ojcom waszym przez proroka Izajasza:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '25', '25', '&gt;&gt;Idź do ludu tego i mów: Słuchać będziecie, a nie zrozumiecie. Będziecie patrzeć, a nie zobaczycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '26', '26', 'Otępiało bowiem serce tego ludu. Uszy ich dotknęła głuchota, zamknęli swe oczy, żeby nie zobaczyć oczyma, żeby nie słyszeć uszami, a sercem nie rozumieć i nie nawrócić się, ażebym ich nie uleczył&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '27', '27', 'Niech wam to będzie wiadome, że zbawienie Boże zostało posłane do pogan, a oni będą słuchać\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '28', '28', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '29', '29', 'Spędził tam całe dwa lata w wynajętym przez siebie mieszkaniu i przyjmował wszystkich, którzy do niego przychodzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ac', '27', '30', '30', 'Głosił królestwo Boże i naukę o Panu Jezusie Chrystusie z całą odwagą i bez żadnych przeszkód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '0', '0', 'Paweł, sługa Chrystusa Jezusa, wezwany do misji apostolskiej, wyznaczony do głoszenia ewangelii, pochodzącej od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '1', '1', 'Ewangelię tę zapowiadali niegdyś prorocy w pismach świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '2', '2', 'Dotyczy ona Jego Syna, który - co do ciała - należy do pokolenia Dawida,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '3', '3', 'dzięki zaś działaniu Ducha Świętego posiada potęgę Syna Bożego, ukazaną w zmartwychwstaniu. Jest to Jezus Chrystus, nasz Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '4', '4', 'Dzięki Niemu otrzymaliśmy dar apostolstwa wśród wszystkich narodów, aby je nakłaniać do posłuszeństwa przejawiającego się w wierze na chwałę Jego imienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '5', '5', 'Wśród nich jesteście i wy, wybrańcy Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '6', '6', 'Wszystkim, którzy przebywają w Rzymie, umiłowanym przez Boga, świętym wybrańcom - łaska wam i pokój od Boga, Ojca naszego, i Pana, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '7', '7', 'Wyrażam najpierw wdzięczność mojemu Bogu, poprzez Jezusa Chrystusa, za was wszystkich - ponieważ cały świat rozgłasza waszą wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '8', '8', 'Bóg bowiem, któremu składam hołd duchem moim, głosząc ewangelię o Jego Synu, jest mi świadkiem, jak nieustannie o was pamiętam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '9', '9', 'Pamiętam zawsze w moich modlitwach, prosząc Boga, bym wreszcie kiedyś, kiedy On zechce, przybył do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '10', '10', 'Bardzo bowiem pragnę ujrzeć was, aby wam przekazać stosowny dla waszego umocnienia dar duchowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '11', '11', 'Oznacza to, że chciałbym doznać pociechy wśród was dzięki wzajemnej wierze - waszej i mojej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '12', '12', 'Chcę, bracia, abyście wiedzieli, że już wielokrotnie zamierzałem udać się do was i za każdym razem, aż do tej pory, napotykałem przeszkody. Pragnąłem bowiem i wśród was zebrać jakieś owoce, tak jak to było pośród innych narodów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '13', '13', 'Jestem dłużnikiem wobec Greków i nie-Greków, wobec mędrców i prostych ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '14', '14', 'Dlatego pragnę i wam, mieszkańcom Rzymu, głosić ewangelię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '15', '15', 'Nie wstydzę się ewangelii! Jest ona przecież potęgą Bożą, która prowadzi do zbawienia każdego wierzącego - najpierw Żyda, a potem Greka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '16', '16', 'Objawia się bowiem w niej usprawiedliwienie, jakie Bóg daje dzięki wierze, która jednocześnie wzrasta. Tak też napisano: \'Ten żyć będzie, kto jest sprawiedliwy dzięki wierze\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '17', '17', 'Gniew Boży spada z nieba na wszystkich bezbożnych i niesprawiedliwych ludzi, którzy przez niesprawiedliwość zadają gwałt prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '18', '18', 'W łatwy sposób przecież mogą poznać Boga - wszak Bóg im się objawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '19', '19', 'To bowiem, co było w Nim niewidzialne, mianowicie wieczna Jego moc i bóstwo, stało się od początku świata poznawalne dzięki dziełu stworzenia. Nie mają więc wymówki,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '20', '20', 'gdyż poznali Boga, a nie okazali Mu czci i wdzięczności, jakie są Bogu należne; przeciwnie, stali się niemądrzy w swoich rozważaniach i ciemność otoczyła nierozumne ich serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '21', '21', 'Uważając się za mądrych, stali się głupimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '22', '22', 'Chwałę nieśmiertelnego Boga zamienili na wyobrażenia przedstawiające śmiertelnego człowieka, ptaki, czworonogi i płazy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '23', '23', 'Z tego powodu Bóg rzucił ich na pastwę żądz nieczystych, gnieżdżących się w ich sercach, tak że wzajemnie bezcześcili swoje ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '24', '24', 'Oni to prawdę Bożą zamienili na kłamstwo, oddawali cześć i hołd temu, co stworzone, a zaniedbali Stwórcę, który jest błogosławiony na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '25', '25', 'Z tego powodu Bóg rzucił ich na pastwę bezwstydnej namiętności. I tak żony ich zamieniły naturalny sposób pożycia na przeciwny naturze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '26', '26', 'Podobnie też mężczyźni, porzucając zgodne z naturą współżycie z żoną, zapałali żądzą jedni do drugich - mężczyźni z mężczyznami dokonywali czynów nierządnych. Tak właśnie w nich samych miała się ujawnić zapłata za ich błąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '27', '27', 'A ponieważ nie czuli się zobowiązani poznać Boga, Bóg rzucił ich na pastwę przewrotnego rozumu, tak że dokonywali złych czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '28', '28', 'Pełni byli wszelkiego rodzaju niesprawiedliwości, przewrotności, chciwości i złości, pełni zazdrości, morderstwa, kłótliwości, zdrady, złośliwości, spotwarzania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '29', '29', 'oszczercy, nienawidzący Boga, zuchwalcy, pyszni, próżni, szukający zła, nieposłuszni rodzicom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '30', '30', 'niemądrzy, wiarołomni, bez serca i bez miłosierdzia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '0', '31', '31', 'Oni też właśnie, chociaż znają dekret Boży - że ci, którzy takich czynów się dopuszczają, winni są śmierci - nie tylko to czynią, ale również chwalą tych, którzy dopuszczają się takich czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '0', '0', 'Dlatego nie masz wymówki, człowieku - kimkolwiek jesteś - gdy wydajesz wyrok. W tym bowiem, na co wydajesz wyrok, samego siebie potępiasz. Wydajesz bowiem wyrok na innych, a sam zwykłeś czynić to samo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '1', '1', 'Wiemy zaś, że sąd Boży jest zgodny z prawdą i dosięga tych, którzy popełniają takie czyny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '2', '2', 'Czyż więc sądzisz, człowieku, że gdy wydajesz wyrok na innych, którzy popełniają takie czyny, a sam je popełniasz - to unikasz wyroku Bożego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '3', '3', 'Albo może śmiesz lekceważyć nieprzebraną Jego dobroć, spokój i cierpliwość, nie zdając sobie sprawy, że dobroć Boga chce cię doprowadzić do zmiany postępowania?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '4', '4', 'Wskutek zaś twego uporu i braku opamiętania ściągasz na siebie gniew, który cię dotknie w dniu gniewu, kiedy to objawi się sprawiedliwość sądu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '5', '5', 'Bóg właśnie odpłaci każdemu zgodnie z jego uczynkami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '6', '6', 'Tym, którzy wytrwale czynią dobro i w ten sposób szukają chwały, czci i nieśmiertelności, da życie wieczne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '7', '7', 'tym zaś, którzy, powodowani chciwością, nie są posłuszni prawdzie, lecz okazują posłuszeństwo nieprawości, grozi straszny gniew.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '8', '8', 'Ucisk i udręka będzie udziałem każdego, kto popełnia zło - najpierw Żyda, potem Greka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '9', '9', 'chwała zań, cześć i pokój będą udziałem każdego, kto czyni dobro - najpierw Żyda, potem Greka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '10', '10', 'Bóg bowiem nie zwraca uwagi na to, co zewnętrzne u człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '11', '11', 'Ci przecież, którzy zgrzeszyli, będąc poza Prawem, zginą też poza Prawem. Ci zaś, którzy będąc pod panowaniem Prawa popełnili grzech, otrzymają wyrok zgodny z Prawem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '12', '12', 'Bo nie tych, którzy są słuchaczami Prawa, uzna Bóg za sprawiedliwych, lecz ci, którzy pełnią uczynki zgodne z Prawem, doznają usprawiedliwienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '13', '13', 'Ilekroć bowiem poganie, którzy nie posiadają Prawa, kierowani jedynie naturą, spełniają uczynki zgodne z Prawem, tylekroć oni sami - którzy nie mają Prawa - są dla siebie Prawem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '14', '14', 'Oni to właśnie czynem ukazują Prawo wypisane w ich sercach przy jednoczesnym świadectwie ich świadomości i myśli, spośród których jedne ich oskarżają, a inne bronią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '15', '15', 'W dniu owym Bóg będzie sądził ukryte sprawy ludzkie - zgodnie z moją ewangelią - przez Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '16', '16', 'Jeśli zaś ty, który nosisz miano Żyda i z poczuciem bezpieczeństwa kierujesz się Prawem oraz chlubisz się Bogiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '17', '17', 'który znasz Jego wolę i pouczony przez Prawo umiesz wybrać to, co jest lepsze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '18', '18', 'który upewniłeś się, że jesteś przewodnikiem dla niewidomych a światłem dla tych, co przebywają w ciemnościach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '19', '19', 'który jesteś wychowawcą niemądrych, nauczycielem niczego nie umiejących, który rzekomo posiadasz znajomość Prawa i prawdę z niego płynącą (...)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '20', '20', 'Ty więc, który uczysz innego, a samego siebie nie uczysz? Ty, który zakazujesz kraść, a sam kradniesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '21', '21', 'Mówisz, żeby nie cudzołożyć, a sam cudzołożysz? Brzydzisz się bożyszczami, a popełniasz świętokradztwo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '22', '22', 'Chlubisz się Prawem, a wskutek wykroczeń przeciw Prawu znieważasz Boga?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '23', '23', 'Tak bowiem napisano: \'Z waszego powodu poganie bluźnią przeciw imieniu Boga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '24', '24', 'Obrzezanie wtedy przynosi korzyść, gdy postępujesz zgodnie z Prawem. Jeśli zaś występujesz przeciw Prawu, obrzezanie twoje traci znaczenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '25', '25', 'Jeśli więc poganin kieruje się sprawiedliwością nakazaną przez Prawo, to czyż jego nieobrzezanie nie będzie uznane za równe obrzezaniu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '26', '26', 'Dlatego ten, kto jest nie obrzezany tylko zewnętrznie, a wypełnia Prawo, potępi ciebie, który posiadasz literę Prawa i obrzezanie, a przeciw Prawu wykraczasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '27', '27', 'Nie ten bowiem jest prawdziwym Żydem, kto jest nim na zewnątrz, ani nie to obrzezanie jest prawdziwe, które widać na ciele,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '1', '28', '28', 'lecz ten jest prawdziwym Żydem, kto jest nim wewnątrz. Liczy się również obrzezanie serca, które wynika z ducha, a nie z litery. Prawdziwa też chwała nie pochodzi od ludzi, lecz od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '0', '0', 'Jakaż więc wyższość Żyda? Jakiż pożytek z obrzezania?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '1', '1', 'Wielki, i to pod każdym względem. Najpierw dlatego, że Żydom powierzono słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '2', '2', 'Cóż z tego, jeżeli niektórzy z nich nie przyjęli wiary? Czyż nie wiara ich przekreśliła wierność Bożą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '3', '3', 'Na pewno nie! Niech jednak Bóg okaże się prawdomówny, każdy zaś człowiek kłamliwy, jak to napisano: \'Abyś okazał się sprawiedliwy i odniósł zwycięstwo podczas sądu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '4', '4', 'Jeśli zaś nasza nieprawość podkreśla sprawiedliwość Bożą, to cóż powiemy? Czyż Bóg - mówię to na sposób ludzki - jest niesprawiedliwy, gdy okazuje swój gniew?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '5', '5', 'Na pewno nie! W jaki sposób Bóg sądzić będzie świat?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '6', '6', 'Jeśli bowiem prawdomówność Boga dzięki memu kłamstwu powiększyła Jego chwałę, to dlaczego jeszcze ja mam być uważany za grzesznika?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '7', '7', 'I czyż niektórzy nie rzucają na nas oszczerstw, twierdząc, że my mówimy: Popełniajmy zło, aby wynikło dobro? Sprawiedliwy jest wyrok przeciw nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '8', '8', 'Cóż więc? Czy odnosimy triumf? W żaden sposób. Przecież oskarżyliśmy już wszystkich Żydów i Greków o to, że tkwią w grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '9', '9', 'Tak też napisano: \'Nie ma sprawiedliwego, nie ma ani jednego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '10', '10', 'nie ma człowieka mądrego, nie ma takiego, który szuka Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '11', '11', 'Wszyscy zbłądzili, wszyscy stali się nieużyteczni, nie ma takiego, który czyni dobro, nie ma ani jednego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '12', '12', 'Grobem otwartym stało się ich gardło, kłamstwo rozsiewają językami swymi, jad żmii pod ich wargami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '13', '13', 'usta ich pełne przekleństw i goryczy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '14', '14', 'stopy ich spieszą do przelewu krwi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '15', '15', 'zniszczenie i groza znaczą ich drogi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '16', '16', 'a drogi pokoju nie poznali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '17', '17', 'Żadnej bojaźni Bożej nie masz przed ich oczami!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '18', '18', 'Wiemy zaś, że Prawo przemawia do tych, którzy je uznają, a mówi dlatego, aby zamknęły się każde usta i aby cały świat uległ wyrokowi Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '19', '19', 'ponieważ żaden człowiek nie dozna usprawiedliwienia wobec Niego na podstawie uczynków nakazanych przez Prawo. Wskutek Prawa bowiem nastąpiło poznanie grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '20', '20', 'Teraz zaś poza Prawem objawiła się sprawiedliwość Boga, o której świadectwo dają Prawo i Prorocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '21', '21', 'A sprawiedliwość Boża spełnia się we wszystkich wierzących dzięki przyjęciu wiary w Jezusa Chrystusa. Nie ma bowiem różnicy:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '22', '22', 'wszyscy zgrzeszyli i utracili chwałę Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '23', '23', 'darmo natomiast dostąpili usprawiedliwienia dzięki Jego darowi, dzięki odkupieniu dokonanemu przez Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '24', '24', 'Jego to Bóg ustanowił ofiarą przebłagalną przez wiarę i przelanie Jego krwi, chcąc okazać w ten sposób swoją sprawiedliwość, dzięki której odpuszcza dawniej popełnione grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '25', '25', 'Sprawiła to cierpliwość Boga, który chce okazać swoją sprawiedliwość w obecnym czasie i jako sprawiedliwy udziela usprawiedliwienia każdemu, kto przyjmuje wiarę w Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '26', '26', 'Gdzież więc powód do chełpliwości? - Wykluczony. Jakim prawem? Prawem domagającym się uczynków? - Nie! Prawem wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '27', '27', 'Sądzimy bowiem, że człowiek doznaje usprawiedliwienia dzięki wierze, bez uczynków nakazanych przez Prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '28', '28', 'Czyż Bóg miałby być jedynie Bogiem Żydów? Czyż nie jest także Bogiem pogan? Oczywiście, że i pogan,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '29', '29', 'skoro jest jedynym Bogiem, który obrzezanym udziela usprawiedliwienia dzięki wierze oraz nie obrzezanym również dzięki wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '2', '30', '30', 'Czyż więc poprzez wiarę przekreślamy Prawo? Wręcz przeciwnie. My Prawo umacniamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '0', '0', 'Czyż więc powiemy, że Abraham, nasz praojciec, zawdzięcza cokolwiek swemu ludzkiemu pochodzeniu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '1', '1', 'Jeśli Abraham doznał usprawiedliwienia dzięki uczynkom, to ma powód do dumy - ale nie wobec Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '2', '2', 'Cóż bowiem mówi Pismo? \'Abraham zawierzył Bogu i to uznano mu jako usprawiedliwienie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '3', '3', 'Pracownikowi przyznaje się zapłatę jako należność, a nie z łaskawości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '4', '4', 'Temu zaś, który nie pracuje - wierzy natomiast w Tego, który bezbożnemu daje usprawiedliwienie - uznaje się wiarę za tytuł do usprawiedliwienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '5', '5', 'Tak również Dawid nazywa błogosławionym człowieka, którego Bóg usprawiedliwia bez uczynków:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '6', '6', '\'Błogosławieni ci, którym nieprawości odpuszczono i których grzechy wymazano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '7', '7', 'Błogosławiony człowiek, któremu Pan nie pamięta grzechu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '8', '8', 'Błogosławieństwo to dotyczy więc obrzezanych czy też nie obrzezanych? Mówimy bowiem: \'Abrahamowi uznano wiarę za usprawiedliwienie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '9', '9', 'Kiedy uznano? Gdy był już obrzezany czy jeszcze nie obrzezany? Wówczas na pewno nie był jeszcze obrzezany!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '10', '10', 'Obrzezanie otrzymał potem jako znak - pieczęć stwierdzającą usprawiedliwienie dzięki wierze, którą wyznawał jeszcze przed obrzezaniem. I tak stał się ojcem wszystkich wierzących - nie obrzezanych, by w ten sposób doznali usprawiedliwienia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '11', '11', 'a także obrzezanych. Nie tylko więc obrzezanych, lecz i naśladujących wiarę ojca naszego, Abrahama, gdy nie był jeszcze obrzezany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '12', '12', 'Abraham - jak i potomstwo jego - nie dzięki Prawu otrzymał obietnicę, że świat stanie się jego własnością, lecz dzięki usprawiedliwieniu wynikającemu z wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '13', '13', 'Jeśli właścicielami stają się ci, którzy podlegają Prawu, wówczas daremna jest wiara i nieskuteczna obietnica.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '14', '14', 'Prawo bowiem rozpala karzący gniew. Tam zaś, gdzie nie ma Prawa, nie ma wykroczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '15', '15', 'A dlatego można stać się właścicielem dzięki wierze, że jest to wynikiem daru i że obietnica z całą pewnością dotyczy całego potomstwa - nie tylko tego, które według Prawa jest potomstwem Abrahama, lecz i tego, które wywodzi się z wiary Abrahama, ojca nas wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '16', '16', 'Tak też napisano: \'Ustanowiłem cię ojcem wielu narodów\'. Zawierzył on Bogu, Temu, który ożywia umarłych i powołuje do bytu to, co nie istnieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '17', '17', 'Abraham wbrew nadziei - w oparciu jednak o nadzieję - uwierzył, że on właśnie stanie się ojcem wielu narodów, zgodnie z tym, co mu powiedziano: \'Tak będzie istnieć twe potomstwo\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '18', '18', 'I nie osłabła jego wiara, choć widział obumarłe swe ciało - miał już prawie sto lat - i obumarłe łono Sary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '19', '19', 'Dzięki obietnicy danej przez Boga nie poddał się zwątpieniu, przeciwnie, wzmocnił swą wiarę, czcząc w ten sposób Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '20', '20', 'Był przekonany, że Ten, który daje obietnicę, zdolny też jest ją spełnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '21', '21', 'Dlatego również to \'uznano mu za usprawiedliwienie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '22', '22', 'Nie tylko ze względu na niego napisano: \'uznano mu\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '23', '23', 'lecz i ze względu na nas, którym uzna się to za usprawiedliwienie - nam, co uwierzyliśmy w Tego, który wskrzesił Jezusa, Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '3', '24', '24', 'On to został wydany z powodu naszych wykroczeń i zmartwychwstał dla naszego usprawiedliwienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '0', '0', 'Doznając usprawiedliwienia wypływającego z wiary, cieszymy się pokojem, który otrzymaliśmy od Boga przez Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '1', '1', 'Dzięki Niemu i poprzez wiarę mamy dostęp do tego daru, który jest naszym udziałem, i chlubimy się, będąc pełni nadziei oglądania chwały Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '2', '2', 'Ale nie tylko z tego powodu. Chlubimy się też pośród udręk, wiedząc, że udręka staje się powodem wytrwałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '3', '3', 'Wytrwałość daje początek stałości, a stałość nadziei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '4', '4', 'Nadzieja zaś nie doznaje zawodu, ponieważ miłość, jaką nas Bóg umiłował, napełnia nasze serca dzięki Duchowi Świętemu, którego otrzymaliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '5', '5', 'Gdy jeszcze bowiem byliśmy słabi, Chrystus - w ściśle wyznaczonym czasie - umarł za bezbożnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '6', '6', 'A przecież z największą trudnością oddaje ktoś życie nawet za sprawiedliwego, choć za dobrego może ktoś odważyłby się umrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '7', '7', 'Bóg natomiast okazał miłość swoją wobec nas przez to, że Chrystus umarł za nas wtedy, gdy my byliśmy jeszcze grzesznikami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '8', '8', 'Tym bardziej więc teraz, gdy zostaliśmy usprawiedliwieni dzięki Jego krwi, będziemy wybawieni od Jego gniewu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '9', '9', 'Jeżeli bowiem, będąc nieprzyjaciółmi, zostaliśmy pojednani z Bogiem dzięki śmierci Jego Syna, to tym bardziej dostąpimy zbawienia dzięki Jego życiu, gdy jesteśmy z nim pojednani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '10', '10', 'I nie tylko to. Chlubimy się też w Bogu dzięki Panu naszemu Jezusowi Chrystusowi, przez którego teraz uzyskaliśmy pojednanie z Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '11', '11', 'A jak przez jednego człowieka pojawił się na świecie grzech, wskutek zaś grzechu śmierć, i w ten sposób śmierć dotknęła wszystkich ludzi, ponieważ wszyscy zgrzeszyli (...)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '12', '12', 'Jeszcze bowiem przed Prawem grzech istniał na świecie. Nie liczy się jednak grzechu tym, którzy nie są pod panowaniem Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '13', '13', 'A przecież śmierć panowała od Adama aż do Mojżesza również nad tymi, którzy nie zgrzeszyli takim wykroczeniem, jakie popełnił Adam, który jest zapowiedzią Tego, co miał przyjść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '14', '14', 'Lecz z przestępstwem nie jest tak jak z darem. Jeżeli bowiem wskutek przestępstwa jednego człowieka wielu ludzi umarło, to łaskawość Boga i dar udzielony dzięki tej łaskawości przez jednego człowieka, Jezusa Chrystusa, tym obficiej spłynęły na wielu ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '15', '15', 'I nie tak jest z darem jak z grzechem popełnionym przez jednego człowieka. Sąd bowiem nad jednym przestępstwem prowadzi do potępienia, dar natomiast uniewinnia od wielu przestępstw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '16', '16', 'Jeżeli bowiem wskutek przestępstwa popełnionego przez jednego człowieka zapanowała śmierć, to tym bardziej ci, którzy doznali obfitej łaskawości i otrzymali dar usprawiedliwienia, znajdą się w królestwie życia dzięki jednemu człowiekowi, Jezusowi Chrystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '17', '17', 'A więc jak wskutek przestępstwa jednego człowieka spadło potępienie na wszystkich ludzi, tak też dzięki sprawiedliwemu postępowaniu jednego Człowieka na wszystkich ludzi zstąpiła sprawiedliwość, która napełnia życiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '18', '18', 'Jak bowiem przez nieposłuszeństwo jednego człowieka wielu stało się grzesznikami, tak dzięki posłuszeństwu Jednego wielu stało się sprawiedliwymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '19', '19', 'Prawo zaś pojawiło się po to, aby dopełniła się miara przestępstwa. Tam natomiast, gdzie rozpanoszył się grzech, jeszcze pełniej zatriumfowała łaska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '4', '20', '20', 'A jak panowanie grzechu ujawniło się w śmierci, tak również panowanie łaski ujawniło się w usprawiedliwieniu, które prowadzi do życia wiecznego przez Jezusa Chrystusa, naszego Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '0', '0', 'Cóż więc powiemy? Czyż mamy trwać w grzechu po to, aby łaska w pełni się okazała?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '1', '1', 'Na pewno nie! Jakżeż my wszyscy, którzy umarliśmy dla grzechu, mielibyśmy jeszcze żyć w nim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '2', '2', 'Czyż nie wiecie, że wszystkich nas, których przez chrzest zanurzono w Chrystusie Jezusie, w Jego śmierci zanurzono?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '3', '3', 'Pogrzebano nas z Nim razem dzięki zanurzeniu w śmierci, dlatego abyśmy - wzorem Chrystusa zmartwychwstałego dzięki chwale Ojca - i my postępowali według zasad nowego życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '4', '4', 'Jeśli bowiem jak szczep zrośliśmy się z podobieństwem Jego śmierci, to tak samo zrośniemy się z Jego zmartwychwstaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '5', '5', 'Wiemy o tym, że stary nasz człowiek został z Nim współukrzyżowany, aby zniszczyć ciało, które było własnością grzechu, i abyśmy nie byli w niewoli grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '6', '6', 'Ten bowiem, kto umarł, wolny jest od grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '7', '7', 'Jeśli zaś umarliśmy z Chrystusem, wierzymy, że również żyć z Nim będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '8', '8', 'Wiemy, że zmartwychwstały Chrystus nie umiera już więcej i śmierć nie ma już nad Nim żadnej władzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '9', '9', 'To bowiem, co umarło, raz na zawsze umarło dla grzechu; to zaś, co żyje, żyje dla Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '10', '10', 'Tak również i wy uważajcie się za takich, którzy umarli dla grzechu, a żyją dla Boga w zjednoczeniu z Chrystusem Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '11', '11', 'Niech więc nie króluje grzech w waszym umarłym ciele, abyście nie musieli ulegać jego namiętnościom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '12', '12', 'Nie czyńcie również ze swego ciała broni dla nieprawości grzechu, lecz jako zmartwychwstali poświęćcie się Bogu, a wasze ciało niech będzie bronią sprawiedliwości Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '13', '13', 'Grzech bowiem nie będzie panował nad wami, bo nie jesteście już pod panowaniem Prawa, lecz pod panowaniem łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '14', '14', 'Cóż więc? Czy mamy grzeszyć, ponieważ nie jesteśmy pod panowaniem Prawa, lecz pod panowaniem łaski? Na pewno nie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '15', '15', 'Czyż nie wiecie, że jesteście niewolnikami tego, którego słuchacie, a więc oddaliście się w niewolę: czy to grzechowi, prowadzącemu do śmierci, czy też posłuszeństwu, które prowadzi do usprawiedliwienia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '16', '16', 'Dzięki Bogu za to, że - będąc niegdyś niewolnikami grzechu - okazaliście płynące z serca posłuszeństwo nauce, która stała się wzorem waszego postępowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '17', '17', 'Uwolnieni od grzechu staliście się niewolnikami sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '18', '18', 'Znając słabość waszego ciała, mówię na sposób ludzki: Jak niegdyś wydaliście swe ciało w niewolę nieczystości i bezprawia, pogrążającego was w większe jeszcze bezprawie, tak teraz wydajecie swe ciało w niewolę sprawiedliwości, która przywiedzie was do uświęcenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '19', '19', 'Niegdyś byliście niewolnikami grzechu, teraz zaś jesteście wolni wobec sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '20', '20', 'Jakież wówczas zbieraliście owoce? Wstydzicie się ich teraz. Śmierć przecież jest ich końcem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '21', '21', 'Obecnie jednak uwolnieni od grzechu staliście się sługami Boga i zbieracie owoce, które wiodą was ku świętości, a końcem ich jest życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '5', '22', '22', 'Zapłatę bowiem za grzech stanowi śmierć, darem zaś Boga jest życie wieczne w Chrystusie Panu naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '0', '0', 'Czyż nie wiecie, bracia - mówię do tych, którzy znają Prawo - że Prawo sprawuje władzę nad człowiekiem tak długo, dopóki on żyje?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '1', '1', 'Podobnie zamężna kobieta podlega prawu męża, dopóki on żyje. Skoro mąż umrze, nie wiąże jej prawo męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '2', '2', 'Gdyby więc za jego życia należała do innego mężczyzny, nazwano by ją cudzołożnicą. Skoro zaś mąż umrze, uwalnia się ona spod prawa, i należąc do innego mężczyzny, nie popełnia cudzołóstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '3', '3', 'Tak i wy, moi bracia, dzięki ciału Chrystusa umarliście dla Prawa, by stać się własnością innego, Zmartwychwstałego, abyśmy owoc nieśli dla Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '4', '4', 'Gdy bowiem żyliśmy tylko ciałem, podlegaliśmy grzesznym żądzom, istniejącym w nas i podniecanym przez Prawo oraz przynoszącym owoc, który sprowadzał śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '5', '5', 'Teraz, umarli dla Prawa, które nas wiązało, wolni jesteśmy od niego, tak że jesteśmy sługami, którzy cieszą się nowym duchem a nie starą literą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '6', '6', 'Cóż więc powiemy? Czy Prawo jest grzechem? Na pewno nie! Ale prawdą jest, że grzech poznałem tylko dzięki Prawu. Nie znałbym bowiem namiętności, gdyby mi Prawo nie powiedziało: \'Nie będziesz pożądał\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '7', '7', 'Grzech więc doznał podniety i wskutek przykazania wzbudził we mnie wszelkiego rodzaju namiętności. Poza Prawem bowiem grzech umiera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '8', '8', 'Niegdyś żyłem poza Prawem. Skoro zaś pojawiło się przykazanie, ożył grzech,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '9', '9', 'a ja umarłem. Okazało się, że przykazanie, które miało prowadzić do życia, poprowadziło mnie do śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '10', '10', 'Grzech bowiem, który doznał podniety i wskutek przykazania zwiódł mnie, wskutek niego też mnie uśmiercił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '11', '11', 'Święte jest zatem Prawo, święte także, sprawiedliwe i dobre jest przykazanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '12', '12', 'Czyż więc to, co dobre, stało się dla mnie śmiercią? Na pewno nie! To grzech, by się ujawnić jako grzech, z pomocą dobra zadał mi śmierć, a z pomocą przykazania jeszcze pełniej ujawnił swą grzeszność!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '13', '13', 'Wiemy, że Prawo jest duchowe, ja natomiast jestem cielesny i podlegam grzechowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '14', '14', 'Nie umiem bowiem pojąć tego, co czynię. Nie czynię tego, co chcę, lecz to czynię, czego nienawidzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '15', '15', 'Jeśli zaś czynię to, czego nie chcę, przyznaję Prawu, że jest ono dobre.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '16', '16', 'Dlatego już nie ja to czynię, lecz grzech, który we mnie przebywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '17', '17', 'Wiem, że nie mieszka we mnie - to znaczy w moim ciele - dobro. Chęć bowiem dobrego czynu szybko zjawia się we mnie, wykonanie jednak - nie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '18', '18', 'Bo nie czynię dobra, którego chcę, lecz popełniam zło, którego nie chcę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '19', '19', 'Jeśli zaś czynię to, czego nie chcę, nie ja to wykonuję, lecz grzech, który we mnie przebywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '20', '20', 'Wyraźnie więc doświadczam, że we mnie, który chcę czynić dobro, przebywa zło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '21', '21', 'Zgadzam się bowiem z Prawem Boga, lecz dzieje się to wbrew temu wszystkiemu, co jest we mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '22', '22', 'Dostrzegam w sobie inne prawo - przeciwstawiające się prawu mojego umysłu i poddające mnie w niewolę prawa podyktowanego przez grzech, który we mnie przebywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '23', '23', 'Jestem nieszczęśliwym człowiekiem! Któż mnie wyrwie z ciała podległego tej śmierci?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '6', '24', '24', 'Bogu niech będą dzięki przez Jezusa Chrystusa, naszego Pana. Tak więc ja sam, dzięki umysłowi, jestem sługą Prawa Bożego, a przez ciało jestem niewolnikiem prawa podyktowanego przez grzech.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '0', '0', 'Żadne więc potępienie nie grozi tym, którzy stanowią jedność z Chrystusem Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '1', '1', 'Prawo Ducha, który daje życie w Chrystusie Jezusie, uwolniło mnie od prawa grzechu i śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '2', '2', 'Co bowiem było niemożliwe dla Prawa - pozbawionego mocy przez nasze ciało - było możliwe dla Boga. On to, dla zniszczenia grzechu, wysłał swego Syna, który przyjął ciało podobne do naszego, podlegającego grzechowi, i w tym ciele dokonał sądu nad grzechem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '3', '3', 'W ten sposób nakaz Prawa urzeczywistnił się w nas, którzy nie kierujemy się pragnieniami ciała, lecz nakazami Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '4', '4', 'Ci bowiem, którzy kierują się pragnieniami ciała, pożądają dóbr cielesnych, ci zaś, którzy kierują się nakazami Ducha, pożądają dóbr duchowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '5', '5', 'Pożądanie wypływające z ciała sprowadza śmierć, pożądanie zaś z Ducha daje życie i pokój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '6', '6', 'Dzieje się tak dlatego, że pożądanie z ciała jest wrogiem Boga, ponieważ nie poddaje się Prawu Bożemu - bo też nie może tego uczynić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '7', '7', 'Ci, którzy kierują się pożądaniami ciała, nie mogą podobać się Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '8', '8', 'Wy natomiast nie kierujecie się pożądaniami ciała, lecz złączeni jesteście z Duchem, bo przecież Duch Boży w was mieszka. A gdy ktoś nie posiada Ducha Chrystusowego, nie jest Jego własnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '9', '9', 'Jeśli zaś Chrystus jest w was, to chociaż ciało umarło z powodu grzechu, duch jednak jest pełen życia dzięki usprawiedliwieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '10', '10', 'A jeśli mieszka w was Duch Tego, który dokonał zmartwychwstania Jezusa, to Ten sam, który dokonał zmartwychwstania Chrystusa, ożywi również wasze śmiertelne ciała dzięki swemu Duchowi, który zamieszkuje w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '11', '11', 'Tak więc, bracia, nie powinniśmy żyć dla ciała, ulegając jego pragnieniom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '12', '12', 'Jeśli bowiem pragnienia ciała kierować będą waszym życiem - umrzecie. Gdy natomiast, dzięki Duchowi, zadacie śmierć działaniu ciała - żyć będziecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '13', '13', 'Ci wreszcie, którymi Duch Boży kieruje, są synami Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '14', '14', 'Nie otrzymaliście przecież ducha niosącego niewolę, która by znów miała rodzić strach, lecz otrzymaliście Ducha dającego synostwo, dzięki czemu możemy wołać: Abba! Ojcze!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '15', '15', 'Ten właśnie Duch świadczy wobec naszego ducha, że jesteśmy dziećmi Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '16', '16', 'A jeśli jesteśmy dziećmi, to i spadkobiercami. Jesteśmy spadkobiercami Boga oraz współspadkobiercami z Chrystusem, skoro bowiem razem z Nim cierpimy, razem też z Nim otrzymamy chwałę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '17', '17', 'Sądzę wreszcie, że cierpienia obecnego czasu są nie do porównania z chwałą jaka ma nas opromienić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '18', '18', 'Całe bowiem stworzenie z wielką tęsknotą wyczekuje objawienia się synów Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '19', '19', 'Bo całe stworzenie podlega marności - nie z własnej woli, lecz z woli tego, który to sprawił. Ma ono jednak nadzieję,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '20', '20', 'że doczeka się uwolnienia z niewoli powodującej zagładę i otrzyma wolność, która darzy chwałą, jaką cieszą się dzieci Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '21', '21', 'Wszak wiemy, że całe stworzenie cierpi straszne udręki aż do tej pory.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '22', '22', 'Zresztą nie tylko ono, ale i my, którzy posiadamy Ducha dającego gwarancję przyszłej chwały, cierpimy, wyczekując pełnego synostwa - odkupienia naszego ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '23', '23', 'W tej chwili zbawienie jeszcze jest przedmiotem nadziei. Któż bowiem spodziewać się będzie tego, na co już patrzy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '24', '24', 'Jeżeli więc spodziewamy się tego, czego nie widzimy, to tego też wytrwale oczekujemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '25', '25', 'W ten sam sposób Duch wspiera nas w naszej słabości. Nie wiem bowiem, o co i w jaki sposób mamy się modlić, dlatego sam Duch wstawia się za nami przez niewyrażalne błagania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '26', '26', 'Bo Ten, który doskonale zna wnętrze człowieka, wie, jakie jest pragnienie Ducha. Duch przecież zgodnie z wolą Bożą wstawia się za świętymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '27', '27', 'Wiemy, że wszystko współpracuje w pomnażaniu dobra z tymi, którzy miłują Boga. Oni - według postanowienia Bożego - są wezwani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '28', '28', 'Bóg bowiem tych, których przed wiekami poznał, tych też wyznaczył, by byli ukształtowani na obraz Jego Syna - aby On był pierworodnym pośród wielu braci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '29', '29', 'A których wyznaczył, tych także powołał. Których zaś powołał, tych też obdarzył sprawiedliwością. A których obdarzył sprawiedliwością, tych także opromienił chwałą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '30', '30', 'Cóż więc na to wszystko powiemy? Jeśli sam Bóg stoi za nami, to któż przeciwko nam?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '31', '31', 'On, który nie oszczędził własnego Syna, lecz wydał Go za nas wszystkich - czyż nie udzieli nam wszystkiego innego razem z Nim?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '32', '32', 'Któż wystąpi ze skargą przeciw wybrańcom Boga? Jedynie Bóg obdarza sprawiedliwością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '33', '33', 'Któż będzie potępiał? Chrystus Jezus, który umarł, a także zmartwychwstał, który też zajmuje miejsce po prawej stronie Boga oraz wstawia się za nami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '34', '34', 'Któż nas oddzieli od miłości Chrystusa? Ucisk, udręka czy prześladowanie, głód czy nagość, niebezpieczeństwo czy miecz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '35', '35', 'Tak też napisano: \'Ze względu na Ciebie przez cały dzień ponosimy śmierć, oceniono nas jak owce przeznaczone na rzeź\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '36', '36', 'Znamienne, że we wszystkich tych okolicznościach odnosimy wspaniałe zwycięstwo dzięki Temu, który nas umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '37', '37', 'I jestem pewny, że ani śmierć, ani życie, ani aniołowie, ani potęgi, ani sprawy obecne, ani przyszłe, ani moce,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '7', '38', '38', 'ani to, co jest wysoko, ani to co nisko, ani jakiekolwiek inne stworzenie nie będzie mogło nas oddzielić od miłości Boga, która okazuje się w Chrystusie Jezusie, naszym Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '0', '0', 'Mówię prawdę - Chrystus mi świadkiem - nie kłamię, świadczy za mną moje sumienie wraz z Duchem Świętym,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '1', '1', 'że opanował mnie wielki smutek, a w sercu zapanował niezmierny ból.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '2', '2', 'Ja sam bowiem chciałbym przyjąć na siebie przekleństwo i odsunięcie od Chrystusa za braci moich, należących do mego narodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '3', '3', 'Są to Izraelici, których przywilejem jest synostwo, chwała, przymierza, prawodawstwo, kult i obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '4', '4', 'Z nich są patriarchowie i z nich wywodzi się Chrystus według ludzkiego pochodzenia. On jest Bogiem ponad wszystkim, błogosławiony na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '5', '5', 'Na pewno nie było próżne słowo Boga. Nie ci bowiem wszyscy są Izraelitami, którzy wywodzą się z Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '6', '6', 'Ani też nie ci wszyscy są dziećmi, którzy są potomstwem Abrahama, lecz: \'Synów Izaaka uznasz za swe potomstwo\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '7', '7', 'Oznacza to, że nie ci są dziećmi Boga, którzy są dziećmi z naturalnego pochodzenia, lecz za potomstwo uważa się dzieci zrodzone dzięki obietnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '8', '8', 'Tak bowiem brzmi słowo obietnicy: \'Powrócę o tej samej porze i Sara mieć będzie syna\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '9', '9', 'Tak też i Rebeka stała się matką dzięki współżyciu z Izaakiem, naszym ojcem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '10', '10', 'Jeszcze się synowie nie narodzili ani nie uczynili niczego dobrego lub złego, by się okazało, że zamiar Boży urzeczywistnia się według wyboru,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '11', '11', 'nie na podstawie czynów, lecz z woli Tego, który powołuje - gdy powiedziano jej: \'Starszy służyć będzie młodszemu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '12', '12', 'Tak też napisano: \'Jakuba umiłowałem bardziej, Ezawa zaś mniej umiłowałem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '13', '13', 'Cóż więc powiemy? Czyż Bóg postępuje niesprawiedliwie? Na pewno nie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '14', '14', 'Mówi bowiem do Mojżesza: \'Miłosierdzie okażę temu, komu zechcę okazać miłosierdzie, zlituję się nad tym, nad kim zechcę się zlitować\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '15', '15', 'Tak więc zbawienie dokonuje się nie dzięki czyjemuś pragnieniu lub zabiegom, lecz dzięki miłosierdziu Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '16', '16', 'Tak bowiem Pismo mówi do faraona: \'Po to właśnie stworzyłem cię, bym mógł ukazać w tobie potęgę moją i bym mógł rozgłosić moje imię po całej ziemi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '17', '17', 'Tak więc Bóg, komu chce, okazuje miłosierdzie, a komu chce, okazuje zagniewanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '18', '18', 'Powiesz mi więc: Czemuż Bóg jeszcze uskarża się, któż bowiem przeciwstawi się Jego woli? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '19', '19', 'Człowieku, kimże ty jesteś, że odważasz się stawiać zarzut Bogu? Czyż garnek powie garncarzowi: Dlaczego uczyniłeś mnie takim? -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '20', '20', 'Czyż garncarz nie ma władzy nad gliną? Przecież może ulepić z jej masy jedno naczynie przeznaczone do szlachetnego użytku, a inne do pospolitego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '21', '21', 'Jeśli zaś Bóg, chcąc okazać gniew i dać poznać swoją moc, znosił w wielkiej cierpliwości naczynia gniewu przeznaczone na zniszczenie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '22', '22', 'by w ten sposób ukazać bogactwo swej chwały wobec naczyń miłosierdzia, które przeznaczył do chwały -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '23', '23', 'nas, których powołał nie tylko spośród Żydów, lecz i spośród pogan (...)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '24', '24', 'Tak też mówi w proroctwie Ozeasza: \'Nazwę ludem moim ten lud, który nie był moim, umiłowaną nazwę tę, która nie była umiłowaną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '25', '25', 'Ci, o których mówiono: &gt;&gt;Nie jesteście moim ludem&lt;&lt;, będą nazwani synami Boga Żyjącego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '26', '26', 'Izajasz zaś woła o Izraelu: \'Choćby nawet liczba synów Izraela była tak wielka, jak piasek morski, to jednak tylko Reszta będzie zbawiona\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '27', '27', 'Pan bowiem dokładnie i szybko spełni swą obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '28', '28', 'Tak również przepowiadał Izajasz: \'Gdyby Pan Zastępów nie zostawił nam potomstwa, stalibyśmy się jak Sodoma, bylibyśmy podobni do Gomory\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '29', '29', 'Cóż więc powiemy? Że poganie, którzy nie szukali usprawiedliwienia, otrzymali usprawiedliwienie, ale usprawiedliwienie wynikające z przyjęcia wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '30', '30', 'Izrael zaś, który szukał Prawa mającego dać usprawiedliwienie, nie doszedł do wypełnienia Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '31', '31', 'Dlaczego? Dlatego, że szukali sprawiedliwości opartej nie na wierze, lecz na uczynkach. Rozbili się o kamień, który powoduje obrażenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '8', '32', '32', 'Tak też napisano: \'Oto kładę na Syjonie kamień powodujący obrażenie oraz skałę powodującą zgorszenie, kto wierzy w niego, nie dozna wstydu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '0', '0', 'Bracia, zbawienie ich jest pragnieniem mego serca i przedmiotem modlitwy do Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '1', '1', 'Świadczę bowiem za nimi, że pałają żarliwością do Boga, lecz prawdziwie Go nie znają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '2', '2', 'Ponieważ nie poznali Bożej sprawiedliwości i usiłowali własną ustanowić, więc nie poddali się sprawiedliwości Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '3', '3', 'Kresem bowiem Prawa jest Chrystus. On wiedzie do sprawiedliwości - każdego, kto przyjmuje wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '4', '4', 'Mojżesz pisze, że sprawiedliwość otrzymuje się dzięki Prawu: \'Człowiek, który wypełnia Prawo, żyć będzie w niej\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '5', '5', 'A ponieważ sprawiedliwość otrzymuje się dzięki przyjęciu wiary, dlatego powiada: \'Nie mów w sercu swoim: Któż wstąpi do nieba?\' - po to, aby Chrystusa sprowadzić,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '6', '6', 'albo: \'Któż zstąpi do otchłani?\' - po to, aby Chrystusa wywieść spośród umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '7', '7', 'Lecz cóż powiada? \'Blisko ciebie jest słowo, w ustach twoich i w sercu twoim\'. Jest to właśnie słowo, w które wierzymy i które głosimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '8', '8', 'Jeśli więc uroczyście wyznasz ustami swymi, że Jezus jest Panem, i sercem swoim uwierzysz, że Bóg spowodował Jego zmartwychwstanie, otrzymasz zbawienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '9', '9', 'Wiara przyjęta sercem prowadzi do usprawiedliwienia, uroczyste zaś wyznanie jej ustami prowadzi ku zbawieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '10', '10', 'Pismo bowiem mówi: \'Każdy, kto wierzy w Niego, nie dozna wstydu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '11', '11', 'Bo nie ma różnicy między Żydem i Grekiem, gdyż Ten sam jest Panem wszystkich, hojnym dla wszystkich, którzy Go wzywają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '12', '12', 'Każdy bowiem, kto wezwie imienia Pana, dostąpi zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '13', '13', 'Jakże więc będą Go wzywać, skoro nie uwierzyli w Niego? A jak będą wierzyć, skoro nie usłyszeli o Nim? Jak zaś będą słyszeć, skoro im nikt o Nim nie głosi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '14', '14', 'A jak ktoś będzie głosił, skoro nie został wysłany? Tak też napisano: \'Jak piękne stopy tych, którzy głoszą dobro!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '15', '15', 'Nie wszyscy jednak dali posłuch ewangelii. Izajasz bowiem mówi: \'Panie, któż uwierzył naszemu nauczaniu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '16', '16', 'Wiara więc rodzi się z przyjęcia słowa, a przyjęcie to następuje dzięki słowu samego Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '17', '17', 'Powiadam jednak: Czyż nie mieli możności usłyszeć? - Bynajmniej. \'Po całej ziemi rozszedł się ich głos, aż na krańce świata ich słowa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '18', '18', 'Pytam jednak: Czyż Izrael nie zrozumiał? Już Mojżesz powiada: \'Przywiodę was do zazdrości z powodu ludu, który nie jest moim, przywiodę was do gniewu wobec niemądrego ludu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '19', '19', 'Izajasz natomiast nie lęka się powiedzieć: \'Znaleźli Mnie ci, którzy Mnie nie szukali, objawiłem się tym, którzy nie pytali o Mnie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '9', '20', '20', 'O Izraelu zaś mówi: \'Przez cały dzień wyciągałem ręce do ludu, który nie pozwala się pouczyć i który Mi się sprzeciwia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '0', '0', 'Pytam więc: Czyżby Bóg odrzucił swój lud? Na pewno nie! Wszak i ja jestem Izraelitą, z rodu Abrahama, z pokolenia Beniamina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '1', '1', '\'Bóg nie odrzucił swego ludu\', który uprzednio poznał. Czyż nie wiecie, co mówi Pismo o Eliaszu? Tak występował wobec Boga przeciw Izraelowi:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '2', '2', '\'Panie, oni pozabijali Twoich proroków, zburzyli Twoje ołtarze, zostałem tylko ja sam i czyhają na moje życie!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '3', '3', 'Jaką jednak usłyszał odpowiedź Bożą? \'Pozostawiłem sobie siedem tysięcy mężczyzn, którzy nie zgięli kolan przed Baalem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '4', '4', 'Tak więc i w obecnym czasie stało się wszystko zgodnie z wyborem, który jest podyktowany łaską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '5', '5', 'Jeśli zaś stało się dzięki łasce, to już nie dzięki uczynkom. Gdyby bowiem było inaczej, to łaska nie byłaby już łaską.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '6', '6', 'Cóż więc? Izrael nie znalazł tego, czego poszukuje. Znaleźli to wybrani, reszta zaś stała się oporna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '7', '7', 'Tak też napisano: \'Bóg im dał ducha zamroczenia, takie oczy, aby nie widzieli, takie uszy, aby nie słyszeli, i to aż do dnia dzisiejszego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '8', '8', 'A Dawid powiada: \'Niech stół ich stanie się pułapką i sidłem oraz zasadzką i odpłatą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '9', '9', 'Niech oczy ich zaćmią się tak, by nie widzieli, a kark ich zegnij na zawsze\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '10', '10', 'Pytam więc: Czyż tak się potknęli, że upadali? Na pewno nie! Ale wskutek ich przestępstwa poganie uzyskali zbawienie, aby w ten sposób wzbudzić w nich zazdrość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '11', '11', 'A jeśli ich przestępstwo stało się powodem bogactwa dla świata, a mała liczba wierzących spośród nich stała się bogactwem dla pogan, to o ileż większym bogactwem będzie ich pełna liczba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '12', '12', 'Wam zaś, poganie, powiadam: O ile ja sam jestem apostołem pogan, o tyle chlubić się będę mym posługiwaniem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '13', '13', 'abym w ten sposób wzbudził zazdrość braci z mego narodu i zbawił choćby niektórych z nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '14', '14', 'Jeśli bowiem ich odrzucenie stało się pojednaniem dla świata, to czyż powrót ich nie stanie się powrotem do życia z krainy umarłych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '15', '15', 'Jeśli zakwas jest święty, to i cały zaczyn, jeśli korzeń święty, to i gałęzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '16', '16', 'A jeśli niektóre gałęzie odłamano, ciebie zaś, dziką oliwkę, wszczepiono w ich miejsce i poprzez korzeń czerpałeś soki oliwne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '17', '17', 'to nie wynoś się ponad gałęzie. Jeśli natomiast chcesz okazać swą wyższość, to wiedz, że nie ty dźwigasz korzeń, lecz korzeń ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '18', '18', 'Powiesz więc: Wycięto gałęzie, aby mnie wszczepić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '19', '19', 'Słusznie. Wycięto je z powodu ich niewierności, ty natomiast stoisz dzięki wierze. Nie unoś się pychą, lecz lękaj się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '20', '20', 'Jeśli bowiem Bóg nie oszczędził naturalnych gałęzi, nie oszczędzi również ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '21', '21', 'Zważaj więc na dobroć i surowość Boga: wobec upadłych Bóg kieruje się surowością, wobec ciebie zaś dobrocią. Jeśli nie wytrwasz w dobroci, wówczas i ty będziesz wycięty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '22', '22', 'A także i oni, jeśli nie będą z uporem trwać w niewierności, będą zaszczepieni, bo władny jest Bóg ponownie ich zaszczepić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '23', '23', 'Jeśli bowiem ty zostałeś odcięty od dzikiej oliwki, z którą w sposób naturalny byłeś zespolony, a zostałeś wszczepiony w szlachetną oliwkę, tak różną co do natury od ciebie, to ileż łatwiej oni będą wszczepieni we własną oliwkę, do której należą z natury.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '24', '24', 'Chcę, bracia, byście nie uchodzili za mądrych we własnym mniemaniu i znali tę tajemnicę, że upór części Izraela trwać będzie dopóty, dopóki nie wejdą do Kościoła wszyscy poganie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '25', '25', 'I wówczas dopiero cały Izrael dostąpi zbawienia. Tak też napisano: \'Z Syjonu przybędzie oswobodziciel i usunie bezbożność z Jakuba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '26', '26', 'Takie to będzie moje przymierze z nimi, kiedy zgładzę ich grzechy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '27', '27', 'Oni to - w stosunku do ewangelii - są wrogami ze względu na was, ze względu jednak na wybór są umiłowani dzięki patriarchom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '28', '28', 'Nieodwołalne bowiem są dary i powołanie Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '29', '29', 'Jak wy niegdyś byliście nieposłuszni Bogu, a teraz doznaliście miłosierdzia dzięki ich nieposłuszeństwu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '30', '30', 'tak i oni teraz stali się nieposłuszni wskutek okazanego wam miłosierdzia, by sami teraz miłosierdzia dostąpili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '31', '31', 'Bóg bowiem wszystkich uwięził w nieposłuszeństwie, by wszystkim okazać miłosierdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '32', '32', 'O głębio bogactwa, mądrości i poznania Bożego! Jakżeż nieuchwytne są Jego wyroki i nie do wyśledzenia Jego drogi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '33', '33', '\'Któż bowiem poznał myśl Pana? Albo kto był Jego doradcą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '34', '34', 'Albo kto pierwszy Go obdarował, tak iż byłby On jego dłużnikiem?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '10', '35', '35', 'Gdyż z Niego i przez Niego, i dla Niego wszystko. Jemu chwała na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '0', '0', 'Nawołuję więc was, bracia, przez miłosierdzie Boże, abyście oddali się Bogu na ofiarę żywą, świętą i przyjemną - będzie to wasza duchowa służba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '1', '1', 'Nie ulegajcie też obyczajom tego świata, lecz przemieniajcie się, odnawiając waszego ducha, abyście umieli rozpoznać, co jest wolą Bożą, co jest dobre, miłe Bogu i doskonałe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '2', '2', 'Powiadam bowiem - zgodnie z udzielonym mi darem - każdemu przebywającemu wśród was, aby nie mniemał o sobie więcej niż należy. Niech każdy mądrze myśli o sobie. Bóg każdemu przydzielił wiarę według wyznaczonej mu miary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '3', '3', 'Jak bowiem w jednym ciele posiadamy wiele członków, a wszystkie one spełniają różne czynności,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '4', '4', 'tak również my, choć jest nas wielu, stanowimy jedno ciało w Chrystusie, a każdy z nas jest cząstką drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '5', '5', 'Mamy zaś różne dary według udzielonej nam łaski; czy to dar proroctwa, którego należy używać zgodnie z zasadami wiary,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '6', '6', 'czy to dar usługiwania, który okazujemy w usługiwaniu, czy to dar nauczania, którym cieszy się nauczający,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '7', '7', 'czy to wreszcie dar zachęcania, którym cieszy się zachęcający. Kto daje jałmużnę, niech czyni to wspaniałomyślnie. Kto przewodniczy społeczności, niech będzie gorliwy. Kto pełni dzieło miłosierdzia, niech czyni to ochoczo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '8', '8', 'Miłość ma być bez obłudy. Brzydząc się złem, podążajcie za dobrem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '9', '9', 'We wzajemnej miłości braterskiej bądźcie serdeczni. We wzajemnym szacunku bądźcie uprzedzający.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '10', '10', 'W gorliwości nie bądźcie opieszali. Bądźcie płomiennego ducha. Służcie Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '11', '11', 'Cieszcie się nadzieją. W prześladowaniu bądźcie wytrwali. Trwajcie w modlitwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '12', '12', 'Wczuwajcie się w potrzeby świętych. Starajcie się być gościnni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '13', '13', 'Błogosławcie tym, którzy was prześladują. Błogosławcie, a nie przeklinajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '14', '14', 'Cieszcie się z tymi, którzy się cieszą, a płaczcie z tymi, którzy płaczą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '15', '15', 'Bądźcie złączeni wspólnym uczuciem. Nie mniemajcie o sobie zbyt wiele, lecz uniżajcie się wobec pokornych. Nie uważajcie się za mądrych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '16', '16', 'Nie odpłacajcie nikomu złem za zło. \'Starajcie się czynić dobrze wszystkim ludziom\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '17', '17', 'Jeśli to możliwe, jeśli to od was zależy, żyjcie w pokoju, ze wszystkimi ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '18', '18', 'Umiłowani, nie wymierzajcie sami kary, lecz pozwólcie działać sprawiedliwości Bożej. Napisano bowiem: \'Do Mnie należy pomsta, ja się odwzajemnię\' - mówi Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '19', '19', 'Lecz: \'Jeśli twój wróg jest głodny, nakarm go, jeśli jest spragniony, napój go - w ten sposób postępując, złożysz na jego głowie rozżarzone węgle\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '11', '20', '20', 'Nie daj się pokonać złu, lecz zło pokonaj dobrem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '0', '0', 'Każdy człowiek niech będzie poddany swoim władzom. Tylko w Bogu bowiem władza ma swe źródło. Bo Bóg ustanowił wszystkie władze, które istnieją.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '1', '1', 'Dlatego ten, kto władzy się sprzeciwia, przeciwstawia się zarządzaniu Boga. A ci, którzy się przeciwstawiają, ściągną na siebie wyrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '2', '2', 'Władcy bowiem nie są postrachem dla dobrego czynu, lecz dla złego. Nie chcesz lękać się władzy? Czyń dobrze, a ona cię pochwali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '3', '3', 'Jest ona bowiem sługą Boga dla twego dobra. A jeśli popełniasz zło, lękaj się, bo nie na próżno nosi ona miecz. Jest bowiem sługą Boga wymierzającego karę temu, kto źle czyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '4', '4', 'Dlatego koniecznie trzeba być uległym nie tylko z obawy przed karą, lecz też ze względu na sumienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '5', '5', 'Z tego powodu również podatki płacicie - sługami Boga bowiem są ci, którzy tym się zajmują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '6', '6', 'Oddajcie wszystkim to, co jest należne: zbierającemu podatek - podatek, żądającemu cła - cło, temu komu należy jest hołd - hołd, komu zaś szacunek - obdarzcie go szacunkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '7', '7', 'Nikomu nic nie bądźcie dłużni - jedynie wzajemną miłość. Bo kto miłością darzy drugiego człowieka, wypełnia Prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '8', '8', 'To bowiem: \'Nie cudzołóż, nie zabijaj, nie kradnij, nie pożądaj\' oraz każde inne przykazanie znajduje uwieńczenie w tym jednym: \'Kochaj twego bliźniego jak samego siebie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '9', '9', 'Miłość nie wyrządza zła drugiemu człowiekowi. Miłość więc jest wypełnieniem Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '10', '10', 'Poznajcie też obecną chwilę - już nadszedł czas, byśmy ze snu powstali. Teraz bowiem bliższe jest nasze zbawienie niż było wówczas, gdy przyjęliśmy wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '11', '11', 'Noc przeminęła, nastał dzień. Odrzućmy więc dzieła należące do ciemności, a weźmy broń światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '12', '12', 'Postępujmy szlachetnie jak w ciągu dnia, nie bierzmy udziału w hulankach i pijaństwie, w rozpuście i wyuzdaniu, w kłótni i zawiści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '12', '13', '13', 'Przyobleczcie się natomiast w Pana, Jezusa Chrystusa, a troski o ciało nie łączcie z pożądliwościami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '0', '0', 'A słabych w wierze przyjmujcie do siebie i nie spierajcie się o drobiazgi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '1', '1', 'Bo jeden sądzi, że może wszystko spożywać, inny natomiast - słaby - jada tylko jarzyny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '2', '2', 'Ten, który je, niech nie potępia tego, który nie je, a ten, kto nie je, niech nie osądza jedzącego - Bóg bowiem każdemu daje dostęp do siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '3', '3', 'Kim ty jesteś, że osądzasz cudzego sługę? Jego pana sprawą jest, czy stoi on, czy upada. A pan nie pozwoli mu upaść, bo może go podtrzymać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '4', '4', 'Jeden wyżej stawia jakiś dzień przed innym dniem, a według oceny innego są one równe - niech każdy we własnym przekonaniu znajduje uzasadnienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '5', '5', 'Kto przestrzega wybranych dni, czyni to dla czci Pana. A także ten, kto je, dla czci Pana je, składa bowiem dziękczynienie Bogu. Również ten, który nie je, dla czci Pana nie je i składa dziękczynienie Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '6', '6', 'Nikt bowiem z nas nie żyje dla siebie i nikt nie umiera dla siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '7', '7', 'Jeśli bowiem żyjemy, żyjemy dla czci Pana, a jeśli umieramy, dla czci Pana umieramy. Czy to bowiem żyjemy, czy umieramy, jesteśmy własnością Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '8', '8', 'Chrystus dlatego umarł i powrócił do życia, aby być Panem żywych i umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '9', '9', 'Ty natomiast dlaczego osądzasz twego brata? A ty dlaczego potępiasz twego brata? Wszyscy przecież staniemy przed trybunałem Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '10', '10', 'gdyż napisano: \'Na moje życie, mówi Pan, zegnie się przede Mną wszelkie kolano i wszelki język sławić będzie Boga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '11', '11', 'Każdy więc z nas będzie przed Bogiem odpowiadał za siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '12', '12', 'A więc już nie osądzajmy się wzajemnie, lecz raczej uważajmy, aby nie dawać bratu okazji do grzechu lub zgorszenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '13', '13', 'Wiem bowiem i jestem przekonany - w Panu Jezusie - że nic samo przez się nie jest nieczyste. A jeśli ktoś mniema, że jest nieczyste, to jest ono nieczyste tylko dla niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '14', '14', 'Jeśli z powodu pokarmu urazisz twego brata, to już nie postąpisz zgodnie z miłością. Z powodu pokarmu nie narażaj na zgubę tego, za którego umarł Chrystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '15', '15', 'Niech nie mówi się źle o waszym dobru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '16', '16', 'Królestwo Boże bowiem nie jest pokarmem i napojem, lecz sprawiedliwością, pokojem i radością w Duchu Świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '17', '17', 'Dlatego ten, kto służy Chrystusowi, podoba się Bogu i cieszy się uznaniem u ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '18', '18', 'Troszczmy się więc o pokój i wzajemne zbudowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '19', '19', 'Nie unicestwiaj dzieła Bożego ze względu na pokarm. Wszystko jest czyste, ale złe jest dla tego człowieka, który w sposób grzeszny spożywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '20', '20', 'Dobrą jest rzeczą nie jeść mięsa i nie pić wina oraz nie czynić tego, co doprowadzić by mogło twego brata do grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '21', '21', 'Wolność, jaką ci daje wiara, zachowaj dla siebie wobec Boga. Szczęśliwy ten, kto nie ma wątpliwości w swoich postanowieniach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '13', '22', '22', 'Ten zaś, kto spożywa pokarmy mimo wątpliwości, jest potępiony, gdyż działa wbrew przekonaniu. A wszystko, co nie wynika z przekonania, jest grzechem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '0', '0', 'My zaś, mający trwałe przekonanie, winniśmy cierpliwie znosić słabości wątpiących, a nie troszczyć się o własne dobro.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '1', '1', 'Każdy z nas niech troszczy się o dobro i zbudowanie drugiego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '2', '2', 'I Chrystus bowiem nie troszczył się o własne dobro, lecz jak napisano: \'Spadły na Mnie złorzeczenia Tobie złorzeczących\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '3', '3', 'To, co niegdyś napisano, dla naszego pouczenia napisano, abyśmy - dzięki cierpliwości i pociesze, jaką dają Pisma - mieli nadzieję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '4', '4', 'A Bóg, który jest źródłem tej cierpliwości i pociechy, niech wam pozwoli złączyć się wspólnym uczuciem - zgodnie z wolą Jezusa Chrystusa -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '5', '5', 'abyście jednogłośnie wielbili Boga i Ojca Pana naszego, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '6', '6', 'Dlatego serdecznie przyjmujcie do siebie jedni drugich, jak i Chrystus przyjął nas - dla Bożej chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '7', '7', 'A powiadam, że Chrystus stał się sługą obrzezanym - dla prawdy Bożej, aby potwierdzić obietnice dane ojcom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '8', '8', 'Poganie zaś uwielbili Boga za miłosierdzie. Tak też napisano: \'Dlatego wysławiać Cię będę wśród pogan i śpiewać będę na chwałę Twego imienia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '9', '9', 'I znów mówi Pismo: \'Poganie, cieszcie się z ludem Jego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '10', '10', 'I znów: \'Chwalcie Pana, wszyscy poganie, i niech Go wysławiają wszystkie ludy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '11', '11', 'I znów Izajasz mówi: \'Pojawi się korzeń Jessego, Ten, który rządzić będzie poganami. W Nim poganie nadzieję pokładać będą\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '12', '12', 'A Bóg, źródło nadziei, niech was napełni wszelką radością i pokojem dzięki wyznawaniu wiary, aby rosła wasza nadzieja dzięki mocy Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '13', '13', 'A ja sam, moi bracia, jestem przekonany o was, że jesteście pełni dobroci i bogaci we wszelką wiedzę, że możecie jedni drugich upominać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '14', '14', 'Może o niektórych sprawach napisałem wam zbyt odważnie, chcąc je wam przypomnieć. Postąpiłem tak stosownie do łaski, jaką mi Bóg dał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '15', '15', 'abym był sługą Jezusa Chrystusa wobec pogan, głosząc ewangelię Bożą, i aby ofiara pogan stała się miła Bogu i uświęcona przez Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '16', '16', 'A to, co dotyczy Boga, uważam za swoją chlubę - dzięki Chrystusowi Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '17', '17', 'Nie będę bowiem miał odwagi mówić o czymkolwiek, czego nie dokonał Chrystus przeze mnie - aby poganie stali się posłuszni Bogu - słowem i czynem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '18', '18', 'dzięki mocy znaków i cudów, dzięki mocy Ducha, tak że mogłem, od Jerozolimy poczynając, aż po Ilirię w pełni głosić ewangelię o Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '19', '19', 'I tak uważam za punkt honoru, by głosić ewangelię tam, gdzie nie wzywano jeszcze imienia Chrystusa, gdyż nie chcę budować na cudzym fundamencie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '20', '20', 'lecz jak napisano: \'Ujrzą ci, którym nie oznajmiono, zrozumieją ci, którzy o Nim nie słyszeli\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '21', '21', 'Już kilkakrotnie nie pozwalały mi przyjść do was różne przeszkody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '22', '22', 'Teraz zaś, ponieważ już nie ma dla mnie pracy w tych okolicach, od wielu lat pragnę przybyć do was,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '23', '23', 'gdy będę się udawał do Hiszpanii. Spodziewam się bowiem, że w czasie tej podróży zobaczę was i wy mnie tam wyślecie, lecz najpierw chciałbym trochę nacieszyć się wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '24', '24', 'Teraz natomiast udaję się do Jerozolimy z posługą świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '25', '25', 'Macedonia bowiem i Achaja z wielką chęcią udowodniły wspólnotę z ubogimi świętymi, którzy mieszkają w Jerozolimie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '26', '26', 'Uczyniły to z wielką chęcią, bo też są ich dłużnikami. Jeśli bowiem poganie stali się uczestnikami ich dóbr duchowych, to winni im służyć dobrami materialnymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '27', '27', 'Gdy dokonam tego i w ten sposób dam im świadectwo wspólnoty, udam się - odwiedzając was - do Hiszpanii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '28', '28', 'A wiem, że przybywając do was, przybędę z pełnią błogosławieństwa Chrystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '29', '29', 'Proszę was, bracia, przez naszego Pana Jezusa Chrystusa i przez miłość Ducha, wspierajcie mnie w walce modlitwami zanoszonymi za mnie do Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '30', '30', 'abym się wyrwał z rąk niewierzących w Judei i aby moja posługa, sprawowana w Jerozolimie, stała się miła świętym;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '31', '31', 'abym z radością przychodząc do was - dzięki woli Bożej - pokrzepił się wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '14', '32', '32', 'Bóg zaś, dawca pokoju, niech będzie z wami wszystkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '0', '0', 'Polecam waszej opiece Febe, naszą siostrę, która przebywa i usługuje Kościołowi w Kenchrach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '1', '1', 'Przyjmijcie ją w Panu godnie - jak przystało świętym - i pośpieszcie jej z pomocą w każdej potrzebie, bo i ona opiekowała się wieloma, a także mną samym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '2', '2', 'Pozdrówcie Pryscyllę i Akwilę, moich współpracowników w Chrystusie Jezusie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '3', '3', 'którzy dla ratowania mego życia nadstawiali swe głowy i którym nie tylko ja okazuję wdzięczność, lecz także wszystkie Kościoły wywodzące się z pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '4', '4', 'Pozdrówcie też Kościół, który gromadzi się w ich domu. Pozdrówcie mego ukochanego Epaneta, który pierwszy w Azji przyjął wiarę w Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '5', '5', 'Pozdrówcie Marię, która wiele się natrudziła dla waszego dobra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '6', '6', 'Pozdrówcie Andronika i Junię, moich krewnych i współwięźniów, którzy należą do grona wybitnych apostołów, którzy jeszcze przede mną należeli do Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '7', '7', 'Pozdrówcie mojego Ampliata, ukochanego w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '8', '8', 'Pozdrówcie Urbana, naszego współpracownika w Chrystusie, i mego kochanego Stachysa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '9', '9', 'Pozdrówcie Apellesa, wypróbowanego w Chrystusie.  Pozdrówcie tych, którzy należą do domu Arystobula.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '10', '10', 'Pozdrówcie Herodiona, mego krewnego. Pozdrówcie tych, którzy należą do domu Narcyza i stanowią jedność w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '11', '11', 'Pozdrówcie Tryfanę i Tryfozę, które się trudzą dla Pana. Pozdrówcie kochaną Persydę, która wiele natrudziła się dla Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '12', '12', 'Pozdrówcie Rufusa, wybrańca Pańskiego, oraz jego matkę, która jest także moją matką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '13', '13', 'Pozdrówcie Asynkrita, Flegona, Hermesa, Patrobasa, Hermasa i będących z nimi braci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '14', '14', 'Pozdrówcie Filologa i Julię, Nereusa i jego siostrę oraz Olimpasa, a z nimi wszystkich świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '15', '15', 'Pozdrówcie jedni drugich świętym pocałunkiem. Pozdrawiają was wszystkie Kościoły Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '16', '16', 'Proszę was, bracia, byście strzegli się tych, którzy powodują rozłamy i zgorszenia wbrew nauce, którą przyjęliście. Odłączcie się od nich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '17', '17', 'Tacy bowiem nie służą naszemu Panu Chrystusowi, lecz własnemu brzuchowi, a przyjemnymi i pochlebnymi słowami zwodzą serca prostych ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '18', '18', 'Wasze zaś posłuszeństwo wszyscy znają. Cieszę się więc z waszego powodu. Chcę natomiast, byście byli mądrzy wobec dobra, a na zło odporni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '19', '19', 'A Bóg pokoju szybko zetrze szatana pod waszymi nogami. Łaska naszego Pana Jezusa niech będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '20', '20', 'Pozdrawia was Tymoteusz, mój współpracownik, oraz Lucjusz, Jazon i Sozypater, moi krewni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '21', '21', 'Pozdrawiam was w Panu i ja, Tercjusz, który ten list napisałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '22', '22', 'Pozdrawia was Gajusz, gospodarz mój i całego Kościoła. Pozdrawia was Erast, skarbnik miasta, oraz brat Kwartus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '23', '23', '()');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '24', '24', 'Temu, który może was utwierdzić według mej ewangelii i przepowiadania o Jezusie Chrystusie, według objawienia tajemnicy zakrytej od wiecznych czasów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '25', '25', 'a teraz objawionej i oznajmionej wszystkim narodom, zgodnie z pismami prorockimi, według nakazu Boga wiecznego, celem wzbudzenia posłuszeństwa wierze -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'ro', '15', '26', '26', 'samemu mądremu Bogu, przez Jezusa Chrystusa, niech będzie chwała na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '0', '0', 'Paweł, z woli Bożej powołany na apostoła Jezusa Chrystusa, oraz brat Sostenes');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '1', '1', 'do Kościoła Bożego w Koryncie, do tych, którzy zostali uświęceni w Jezusie Chrystusie i powołani do świętości wraz ze wszystkimi, którzy na każdym miejscu wzywają imienia Pana naszego Jezusa Chrystusa, zarówno ich jak i naszego Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '2', '2', 'Łaska wam i pokój od Boga, Ojca naszego, i od Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '3', '3', 'Zawsze dziękuję Bogu mojemu za was z powodu udzielenia wam łaski Bożej w Jezusie Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '4', '4', 'W Nim zostaliście bowiem wzbogaceni pod każdym względem we wszelkie słowo i wszelkie poznanie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '5', '5', 'ponieważ zostało wśród was utwierdzone świadectwo o Jezusie Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '6', '6', 'Z tych względów nie brakuje żadnego daru Bożego wam, oczekującym objawienia się Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '7', '7', 'On będzie was utwierdzał aż do końca, abyście okazali się nienaganni w dniu Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '8', '8', 'Wierny jest Bóg, który powołał was do wspólnoty ze swoim Synem Jezusem Chrystusem, Panem naszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '9', '9', 'Błagam was, bracia, w imię Pana naszego Jezusa Chrystusa, abyście wszyscy mówili to samo i aby nie było wśród was rozłamów. Żyjcie w zgodzie, bądźcie wszyscy tej samej myśli i tego samego zdania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '10', '10', 'Bracia moi! Powiadomili mię o was ludzie Chloe, że są wśród was spory.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '11', '11', 'Mam na myśli to, że każdy z was mówi: \'Ja jestem po stronie Pawła\', \'Ja Apollosa\', \'Ja należę do Piotra\', a \'Ja do Chrystusa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '12', '12', 'Czy Chrystus może być rozdzielony? Czy Paweł został za was ukrzyżowany? Czy ochrzczono was w imię Pawła?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '13', '13', 'Dziękuję Bogu, że poza Kryspusem i Gajusem nikogo z was nie ochrzciłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '14', '14', 'Nikt nie może więc powiedzieć, że został ochrzczony w imię moje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '15', '15', 'Wprawdzie ochrzciłem jeszcze i rodzinę Stefanasa, ale poza tym nie pamiętam, czy jeszcze kogoś ochrzciłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '16', '16', 'Chrystus bowiem nie posłał mię, abym chrzcił, lecz abym głosił dobrą nowinę, nie opierając się na uczonych wywodach, które mogą pozbawić mocy krzyż Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '17', '17', 'Nauka o krzyżu wydaje się głupotą tym, którzy kroczą drogą zatracenia. Natomiast jest mocą Bożą dla nas, których udziałem jest zbawienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '18', '18', 'Napisano bowiem: \'Zniszczę mądrość mędrców, a roztropność roztropnych zniweczę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '19', '19', 'Gdzie zatem jest mędrzec? Gdzie uczony w Piśmie? Gdzie badacz tego świata? Czy Bóg nie zamienił mądrości świata w głupotę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '20', '20', 'Skoro bowiem świat za pomocą swojej mądrości nie poznał Boga w całej Jego mądrości, wówczas spodobało się Bogu zbawić tych, którzy wierzą przez głoszenie nauki uważanej przez świat za głupotę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '21', '21', 'I tak, gdy Żydzi domagają się znaków, a Grecy szukają mądrości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '22', '22', 'my głosimy Chrystusa ukrzyżowanego. Wywołuje to zgorszenie u Żydów, poganom wydaje się głupotą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '23', '23', 'Natomiast tym, którzy są powołani zarówno spośród Żydów jak i Greków, głosimy Chrystusa jako moc i mądrość Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '24', '24', 'Ponieważ to, co w postępowaniu Boga wygląda na głupotę, jest mądrzejsze od ludzi, a to, co na słabość, silniejsze od ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '25', '25', 'Bracia! Przypatrzcie się waszemu powołaniu. Niewielu wśród was mądrych w pojęciu ludzkim, niewielu wpływowych i szlachetnie urodzonych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '26', '26', 'Bóg wybrał bowiem to, co głupie według świata, aby zawstydzić mądrych, i co słabe według świata wybrał Bóg, aby zawstydzić to, co mocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '27', '27', 'Bóg wybrał również to, co świat ma za nic i uważa za nieszlachetnie urodzone oraz godne pogardy, aby unicestwić to, co czymś jest,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '28', '28', 'w tym celu, aby nikt z ludzi nie chlubił się przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '29', '29', 'Dzięki Niemu jesteście w łączności z Chrystusem Jezusem, który stał się dla nas mądrością od Boga, sprawiedliwością, uświęceniem i odkupieniem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '0', '30', '30', 'aby zgodnie z tym, co zostało napisane: \'w Panu chlubił się ten, kto się chlubi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '0', '0', 'Bracia! I ja, kiedy przybyłem do was, aby wam oznajmić świadectwo o Bogu, nie zamierzałem zabłysnąć słowem lub mądrością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '1', '1', 'Postanowiłem bowiem, że nie będę wśród was znał niczego poza Jezusem Chrystusem i to ukrzyżowanym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '2', '2', 'I tak przybyłem do was ze słabością i z bojaźnią oraz z wielkim drżeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '3', '3', 'Moja mowa i nauczanie nie opierały się na przekonywających słowach mądrości, lecz na okazaniu ducha i mocy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '4', '4', 'aby wasza wiara nie wypływała z ludzkiej mądrości, ale z mocy Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '5', '5', 'Głosimy wprawdzie mądrość, lecz wśród dojrzałych duchowo. Nie jest to jednak mądrość zgodna z pojęciami tego świata lub jego władców, którzy przemijają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '6', '6', 'Głosimy bowiem mądrość Bożą pełną tajemnic i ukrytą, którą Bóg już przed wiekami przeznaczył ku naszej chwale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '7', '7', 'Tej mądrości nie pojął nikt z władców tego świata, bo gdyby ją byli pojęli, nie ukrzyżowaliby Pana chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '8', '8', 'My głosimy mądrość, o której napisano: \'Czego oko nie widziało ani ucho nie słyszało, czego serce człowieka nie zdołało pojąć, to przygotował Bóg tym, którzy Go miłują\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '9', '9', 'Nam zaś objawił to Bóg przez Ducha. Duch bowiem wszystko przenika, nawet głębię samego Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '10', '10', 'Kto spośród ludzi wie, co jest w człowieku, jeśli nie duch człowieka, który w nim jest: Podobnie i tego, co w Bogu jest, nikt nie zna, oprócz Ducha Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '11', '11', 'Otóż my nie otrzymaliśmy ducha, którym świat się kieruje, lecz Ducha, który od Boga pochodzi. Otrzymaliśmy go zaś w tym celu, aby poznać dary, którymi Bóg nas obdarzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '12', '12', 'Nie głosimy tego słowami wypływającymi z ludzkiej mądrości, lecz słowami, których uczy Duch. Objaśniamy sprawy duchowe ludziom, którzy poddają się działaniu Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '13', '13', 'Człowiek kierujący się tylko zasadami naturalnymi nie pojmuje spraw Ducha Bożego. Uważa je za głupotę i nie jest w stanie tych spraw zrozumieć, ponieważ należy je rozstrzygać w sposób duchowy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '14', '14', 'Człowiek zaś kierujący się Duchem rozstrzyga wszystko, natomiast jego nikt nie osądza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '1', '15', '15', '\'Któż zatem poznał myśl Pana tak, aby mógł Go pouczać?\' My właśnie mamy myśl Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '0', '0', 'Bracia! Nie mogłem przemawiać do was jak do ludzi kierujących się Duchem, lecz jak do tych, którzy ulegają swoim ludzkim słabościom, jak do niemowląt w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '1', '1', 'Nakarmiłem was mlekiem, a nie stałym pokarmem, ponieważ nie mogliście go jeszcze przyjąć. Zresztą i teraz jeszcze nie możecie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '2', '2', 'ponieważ ulegacie swoim ludzkim słabościom. Jeżeli bowiem panuje między wami zazdrość i niezgoda, to czy nie kierujecie się swoimi ludzkimi słabościami i czy nie postępujecie tylko po ludzku?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '3', '3', 'Skoro bowiem ktoś mówi: \'Ja należę do Pawła\', a inny: \'Ja do Apollosa\', to czy nie okazujecie się tylko ludźmi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '4', '4', 'Kim bowiem jest Apollos, kim jest Paweł? Sługami, dzięki którym uwierzyliście, a każdy z nich działał w taki sposób, na jaki pozwolił mu Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '5', '5', 'Ja zasadziłem, Apollos podlewał, lecz Bóg powodował wzrost.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '6', '6', 'Przeto nie liczy się ani ten, który sadzi, ani ten, który podlewa, lecz tylko ten, który daje wzrost, czyli Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '7', '7', 'I ten, kto sadzi, i ten, kto podlewa, mają jednakową wartość, a każdy z nich otrzyma nagrodę odpowiednią do własnego trudu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '8', '8', 'Jesteśmy bowiem współpracownikami Boga, a wy jesteście uprawną ziemią Bożą, budowlą Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '9', '9', 'Dzięki udzielonej mi przez Boga łasce położyłem fundament jako zręczny architekt, ale buduje ktoś inny. Niech każdy jednak patrzy na to, jak buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '10', '10', 'Nikt bowiem nie może położyć innego fundamentu, oprócz tego, który już został położony, a jest nim Jezus Chrystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '11', '11', 'Czy ktoś buduje na tym fundamencie złotem, srebrem czy drogimi kamieniami, czy też drzewem, sianem lub słomą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '12', '12', 'okaże się w dzień sądu, który ujawni dzieło każdego. Objawi się on w ogniu, a ogień ten wypróbuje, jakie jest dzieło każdego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '13', '13', 'Jeżeli czyjeś dzieło ostoi się, to budowniczy otrzyma nagrodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '14', '14', 'Jeżeli zaś spłonie, to utraci on nagrodę, choć sam ocaleje, lecz będzie podobny do tego, kto przeszedł przez ogień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '15', '15', 'Czy nie wiecie, że jesteście świątynią Boga, a Duch Boży mieszka w was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '16', '16', 'Jeżeli ktoś niszczy świątynię Boga, tego i Bóg zniweczy, ponieważ jest ona święta. Wy właśnie jesteście tą świątynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '17', '17', 'Niech nikt nie zwodzi samego siebie. Jeśli komuś z was wydaje się, że jest mędrcem w pojęciu tego świata, niech uważa się za głupca, a wówczas stanie się mądrym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '18', '18', 'Mądrość bowiem tego świata jest u Boga głupotą, bo napisano, że: \'Bóg zaskoczy mędrców mimo ich chytrości\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '19', '19', 'A w innym miejscu: \'Pan zna zmysły mędrców i wie, że są bez wartości\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '20', '20', 'Przeto niechaj nikt nie chlubi się ludźmi, bo do was należy wszystko:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '21', '21', 'Paweł, Apollos czy Piotr, świat, życie i śmierć, teraźniejszość i przyszłość. Wszystko jest wasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '2', '22', '22', 'Wy zaś należycie do Chrystusa, a Chrystus do Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '0', '0', 'Niechaj każdy człowiek uważa nas zatem za poddanych Chrystusa i szafarzy tajemnic Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '1', '1', 'W tej dziedzinie wymaga się od szafarza szczególnie wierności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '2', '2', 'Mnie zaś nie zależy na tym, czy będę osądzony przez was czy przez inny ludzki trybunał, zresztą nawet sam siebie nie osądzam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '3', '3', 'Sumienie wprawdzie niczego mi nie wyrzuca, ale to nie wystarcza, abym czuł się niewinny. Moim sędzią jest Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '4', '4', 'Powstrzymajcie się zatem od wydawania sądów aż do czasu, gdy przyjdzie Pan. On oświetli rzeczy ukryte w ciemnościach i wyjawi zamiary serc, wtedy każdy otrzyma pochwałę od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '5', '5', 'To wszystko zastosowałem do siebie i do Apollosa ze względu na was, abyście na naszym przykładzie nauczyli się następującej zasady: \'Nic ponad to, co zostało napisane\' oraz abyście nie szczycili się jednymi, a drugimi pogardzali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '6', '6', 'Kto ciebie wyróżnia? Co posiadasz, czego byś nie otrzymał? Jeśli zaś otrzymałeś, to dlaczego chlubisz się tym tak, jakbyś nie otrzymał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '7', '7', 'Już jesteście nasyceni i wzbogaceni wewnętrznie? Bez nas macie udział w królestwie? Obyście nadal posiadali królestwo, a my abyśmy wraz z wami mieli w nim udział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '8', '8', 'Wydaje mi się, że Bóg wyznaczył nam, apostołom, ostatnie miejsce, jak ludziom skazanym na śmierć. Staliśmy się bowiem widowiskiem dla świata, aniołów i ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '9', '9', 'My jesteśmy głupi z powodu Chrystusa, wy mądrzy w Chrystusie, my słabi, wy silni, wy cieszycie się uznaniem, my jesteśmy pozbawieni czci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '10', '10', 'Aż do tego czasu ciągle jeszcze odczuwamy głód i pragnienie, jesteśmy nadzy i policzkowani, tułamy się');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '11', '11', 'i trudzimy pracą rąk własnych. Rzucają na nas obelgi, a my błogosławimy, prześladują nas, a my to cierpliwie znosimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '12', '12', 'Zniesławiają nas, a my odwzajemniamy się uprzejmością. Staliśmy się jakby odpadami świata oraz brudem ze wszystkiego zeskrobanym i jesteśmy nim aż dotąd.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '13', '13', 'Nie piszę tego, aby was zawstydzić, lecz żeby was upomnieć jak moje dzieci ukochane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '14', '14', 'Chociaż mielibyście dziesięć tysięcy wychowawców w Chrystusie, to jednak niewielu macie ojców. Ja natomiast stałem się waszym ojcem w Chrystusie Jezusie, ponieważ oznajmiłem wam dobrą nowinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '15', '15', 'Błagam więc was: naśladujcie mnie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '16', '16', 'Dlatego posłałem do was Tymoteusza, moje ukochane i wierne w Panu dziecko. On przypomni wam moje nauki o postępowaniu w Chrystusie Jezusie; to, czego nauczam wszędzie i w każdym kościele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '17', '17', 'Niektórzy unieśli się pychą, ponieważ nie przybywam do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '18', '18', 'Wkrótce jednak przybędę, o ile Pan zechce, a wówczas nie będę brał pod uwagę słów tych, których pycha uniosła, lecz ich czyny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '19', '19', 'Bo królestwo Boże nie polega na słowie, lecz czynie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '3', '20', '20', 'Co wolicie? Czy mam przybyć do was z rózgą, czy też z miłością i dobrocią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '0', '0', 'Ogólnie słyszy się o rozpuście wśród was i to takiej, jakiej się nie spotyka nawet u pogan. Doszło do tego, że ktoś żyje z żoną swego ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '1', '1', 'Wy zaś macie jeszcze odwagę unosić się pychą, zamiast raczej okryć się wstydem i domagać się, aby usunięto spośród was tego, który dopuścił się takiego czynu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '2', '2', 'Ja - znajdując się wprawdzie daleko od was, ale obecny wśród was duchowo - wydałem już wyrok na sprawcę tego występku, tak jakbym znajdował się wśród was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '3', '3', 'W imię Pana naszego Jezusa: na waszym zgromadzeniu, złączeni duchowo ze mną i z mocą Pana naszego Jezusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '4', '4', 'wydajcie tego człowieka szatanowi na zatracenie ciała, aby duch dostąpił zbawienia w dniu Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '5', '5', 'To niedobrze, że się przechwalacie. Czy nie wiecie, że odrobina kwasu zakwasza całe ciasto?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '6', '6', 'Wyrzućcie więc stary kwas, abyście mogli się stać \'nowym ciastem\', ponieważ jesteście wolni od kwasu. Chrystus bowiem został złożony w ofierze jako nasza Pascha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '7', '7', 'Obchodźmy zatem święta wielkanocne bez starego kwasu, bez kwasu, który jest symbolem przewrotności, a z niekwaszonymi chlebami symbolizującymi czystość i prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '8', '8', 'Napisałem wam w liście, abyście nie przebywali w towarzystwie rozpustników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '9', '9', 'Nie miałem jednak na myśli rozpustników tego świata w ogóle ani chciwców, ani też rabusiów czy bałwochwalców, ponieważ w tym wypadku musielibyście opuścić ten świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '10', '10', 'Napisałem wam, abyście nie przebywali w towarzystwie takiego, który nazywa siebie \'bratem\', a w rzeczywistości jest rozpustnikiem lub chciwcem, bałwochwalcą czy oszczercą, potwarcą lub pijakiem. Z takim człowiekiem nie należy wspólnie spożywać posiłków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '11', '11', 'Czy do mnie należy wydawanie sądu o tych, którzy znajdują się poza wspólnotą? Natomiast czy wy nie możecie sądzić tych, którzy należą do wspólnoty?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '4', '12', '12', 'Tych, którzy znajdują się poza wspólnotą, będzie sądził Bóg. \'Usuńcie przewrotnego spośród was\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '0', '0', 'Czy naprawdę odważyłby się ktoś z was zwrócić w sprawie spornej po rozstrzygnięcie do niesprawiedliwych zamiast do świętych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '1', '1', 'Czy nie wiecie, że święci będą sądzić świat? Jeżeli zaś wy będziecie sądzić świat, to czy nie zasługujecie na to, aby wyrokować w tak błahych sprawach?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '2', '2', 'Czy nie wiecie, że będziemy sądzić nawet aniołów? Tym bardziej więc możemy rozstrzygać o sprawach życia codziennego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '3', '3', 'Dlaczego więc do wydawania wyroków o tych sprawach wybieracie na sędziów tych, którzy w Kościele nic nie znaczą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '4', '4', 'Mówię to, aby was zawstydzić. Czy nie ma wśród was ani jednego mądrego, który mógłby rozstrzygać spory wśród braci?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '5', '5', 'Jak to wygląda, że brat z bratem się procesuje i to jeszcze przed niewierzącymi?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '6', '6', 'Już sam fakt, że wnosicie przeciwko sobie skargi do sądu, świadczy o was niekorzystnie. Dlaczego raczej nie znosicie cierpliwie krzywd? Dlaczego raczej nie zapominacie o wyrządzonych wam szkodach?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '7', '7', 'Lecz wy sami wyrządzacie krzywdy oraz dopuszczacie się grabieży i to jeszcze na braciach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '8', '8', 'Czy nie wiecie, że niesprawiedliwi nie osiągną królestwa Bożego? Nie łudźcie się! Królestwa Bożego nie osiągną rozpustnicy, bałwochwalcy i cudzołożnicy, ludzie nie umiejący się oprzeć rozkoszom i mężczyźni współżyjący ze sobą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '9', '9', 'złodzieje, chciwcy oraz pijacy, oszczercy i rabusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '10', '10', 'Niektórzy z was takimi wprawdzie byli, lecz już zostaliście obmyci, uświęceni i usprawiedliwieni w imię Pana Jezusa Chrystusa i Ducha Boga naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '11', '11', 'Wszystko mi wolno, ale nie wszystko przynosi korzyść. Wszystko mi wolno, ale ja niczemu nie ulegnę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '12', '12', 'Pokarm przeznaczony jest dla żołądka, a żołądek dla pokarmu. Bóg zaś unicestwi zarówno jedno, jak i drugie. Natomiast ciało nie jest dla rozpusty, ale dla Pana, a Pan dla ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '13', '13', 'Bóg zaś swoją mocą wskrzesi nasze ciała do życia, tak jak wskrzesił Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '14', '14', 'Czy nie wiecie, że wasze ciała są członkami Chrystusa? Czy wezmę członki Chrystusa i uczynię je członkami nierządnicy? Przenigdy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '15', '15', 'Albo czy nie wiecie, że ten, kto łączy się z nierządnicą, tworzy z nią jedno ciało? Pismo Święte powiada bowiem: \'Będą dwoje w jednym ciele\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '16', '16', 'Kto natomiast łączy się z Panem, staje się z Nim jednym duchem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '17', '17', 'Unikajcie rozpusty! Każdy inny grzech, jakiego dopuszcza się człowiek, nie dotyczy ciała, ale w wypadku rozpusty człowiek grzeszy przeciw własnemu ciału.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '18', '18', 'Czyż nie wiecie, że ciała wasze są świątynią Świętego Ducha, który w was przebywa? Otrzymaliście Go od Boga i nie należycie do siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '5', '19', '19', 'Bóg nabył was na własność za wielką cenę. Niech więc ciała wasze chwalą Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '0', '0', 'Przechodzę teraz do spraw, o których pisaliście. Otóż dobrze jest dla mężczyzny, jeżeli nie żyje z kobietą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '1', '1', 'Ze względu jednak na niebezpieczeństwo rozpusty niech każdy ma swoją żonę, a każda kobieta swojego męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '2', '2', 'Niech mąż oddaje powinność małżeńską żonie, a żona mężowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '3', '3', 'Żona nie może swobodnie dysponować własnym ciałem, lecz jej mąż. Podobnie i mąż nie może swobodnie dysponować własnym ciałem, lecz jego żona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '4', '4', 'Nie uchylajcie się od współżycia małżeńskiego, chyba za wspólną zgodą i tylko na pewien czas, aby poświęcić się modlitwie. Potem znowu podejmijcie współżycie, aby nie kusił was szatan, wykorzystując waszą niepowściągliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '5', '5', 'Mówię wam o tych sprawach nie w formie nakazu, lecz z wyrozumiałością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '6', '6', 'Pragnąłbym bowiem, aby wszyscy ludzie żyli tak jak ja. Każdy jednak otrzymuje od Boga własny dar: jeden taki, a drugi inny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '7', '7', 'Ci, którzy nie zawarli związków małżeńskich, i wdowy dobrze postąpią, gdy tak jak ja wytrwają w tym stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '8', '8', 'Jeśli jednak nie są powściągliwi, niech wstąpią w związki małżeńskie. Lepiej jest bowiem zawrzeć małżeństwo, niż płonąć ogniem pożądliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '9', '9', 'Do tych zaś, którzy wstąpili w związek małżeński, zwracam się z nakazem nie swoim, lecz danym przez Pana: niech żona nie odchodzi od męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '10', '10', 'Jeśliby zaś odeszła, niech pozostanie samotna lub ponownie połączy się ze swoim mężem. Podobnie i mąż niech nie oddala od siebie żony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '11', '11', 'Pozostałym małżeństwom oświadczam ja, a nie Pan: Jeżeli któryś z braci ma żonę niewierzącą, a ona zgadza się z nim nadal mieszkać, niech jej nie oddala.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '12', '12', 'Podobnie jeżeli jakaś kobieta wierząca ma niewierzącego męża, a ten zgadza się mieszkać z nią, niech nie zrywa z nim związku małżeńskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '13', '13', 'Mąż niewierzący zostaje bowiem uświęcony przez żonę, a żona niewierząca przez brata. Gdyby było inaczej, wasze dzieci byłyby nieczyste, a tymczasem są święte.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '14', '14', 'Gdyby jednak strona niewierząca chciała odejść, niech odejdzie. W takim wypadku ani brat, ani siostra nie są skrępowani jak niewolnicy, ponieważ Bóg powołał was do życia w pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '15', '15', 'Stąd bowiem wiesz, żono, że zbawisz męża? Albo skąd wiesz, mężu, że zbawisz żonę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '16', '16', 'Zresztą niech każdy pozostaje w tym stanie, który mu Bóg przeznaczył i do którego powołał. W ten sposób rozporządzam we wszystkich Kościołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '17', '17', 'Jeżeli ktoś został powołany do wiary jako obrzezany, niech znaku obrzezania nie ukrywa. Jeżeli ktoś został powołany jako nie obrzezany, niech się obrzezaniu nie poddaje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '18', '18', 'Zarówno obrzezanie jak i nie obrzezanie nie ma bowiem znaczenia, liczy się tylko przestrzeganie przykazań Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '19', '19', 'Każdy niechaj wytrwa w tym stanie, w jakim zastało go powołanie do wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '20', '20', 'Zostałeś powołany jako niewolnik? Nie martw się tym! Jeśli możesz nawet uzyskać wolność, nie ubiegaj się o nią, lecz wykorzystaj swój stan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '21', '21', 'Ponieważ ten, kto został powołany w Panu jako niewolnik, jest wyzwoleńcem Pana. Podobnie i ten, który został powołany jako wolny, jest niewolnikiem Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '22', '22', 'Bóg nabył was na własność za wielką cenę. Nie bądźcie zatem niewolnikami ludzi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '23', '23', 'Bracia! Niech każdy wytrwa przed Bogiem w tym stanie, w jakim zastało go powołanie do wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '24', '24', 'Nie mam natomiast nakazu od Pana dotyczącego dziewic. Daję jednak radę jako człowiek zasługujący na zaufanie, dzięki miłosierdziu, jakiego doznałem od Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '25', '25', 'Uważam więc, że dziewictwo jest dobre w obecnej trudnej sytuacji. Dobrze jest dla mężczyzny, aby trwał w tym stanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '26', '26', 'Jesteś związany z żoną? Nie szukaj uwolnienia! Jesteś wolny? Nie szukaj żony!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '27', '27', 'Jeśli jednak zawrzesz związek małżeński, nie zgrzeszysz. Podobnie i dziewica, jeśli wychodzi za mąż, nie grzeszy. Tacy jednak będą doznawać w życiu udręki ze strony ciała, a ja chciałbym jej wam zaoszczędzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '28', '28', 'Mówię wam, bracia: czas (życia ludzkiego) jest krótki. Trzeba więc, aby żonaci tak żyli, jakby żon nie mieli,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '29', '29', 'płaczący, jakby nie płakali, cieszący się, jakby się nie cieszyli, kupujący, jakby niczego na stale nie posiadali,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '30', '30', 'korzystający z tego świata, jakby z niego nie korzystali. Przemija bowiem postać tego świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '31', '31', 'Chciałbym, żebyście byli wolni od trosk. Ten, kto nie ma żony, troszczy się tylko o sprawy Pana i zabiega o to, aby się Panu podobać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '32', '32', 'Natomiast mężczyzna żonaty troszczy się o sprawy świata i zabiega o to, aby podobać się żonie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '33', '33', 'Musi więc dzielić sprawy Pana i sprawy ziemskie. Podobnie kobieta niezamężna oraz dziewica troszczy się tylko o sprawy Pana i o to, aby była święta zarówno ciałem, jak i duchem. Natomiast ta, która wyszła za mąż, troszczy się o sprawy świata i o to, aby podobać się mężowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '34', '34', 'A mówię o tym dla waszego osobistego dobra, ze względu na piękno tego stanu i możliwość trwania przy Panu bez rozproszeń, a nie po to, aby zastawić na was pułapkę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '35', '35', 'Jeżeli jednak ktoś uważa, że nie postępuje uczciwie wobec powierzonej jego opiece dziewicy, widząc jak przekracza ona wiek odpowiedni do zawarcia małżeństwa, niech czyni, co chce. Nie grzeszy. Niech się pobierają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '36', '36', 'Kto natomiast nie zmuszony żadną koniecznością i w pełni panując nad swoją wolą powziął mocną decyzję, że zachowa swą dziewicę w tym stanie, dobrze uczyni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '37', '37', 'Wobec tego dobrze czyni ten, kto poślubia swą dziewicę, lecz ten, kto jej nie poślubia, czyni lepiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '38', '38', 'Żona związana jest węzłem małżeńskim, dopóki żyje jej mąż. Jeżeli mąż umrze, może swobodnie poślubić, kogo tylko zechce pod warunkiem, że będzie to małżeństwo w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '6', '39', '39', 'Będzie jednak szczęśliwsza, jeżeli pozostanie tak, jak jest. Taka jest moja rada; a wydaje mi się, że i ja mam Ducha Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '0', '0', 'Z kolei sprawa ofiar składanych bożkom. Wiadomo, że wszyscy mamy o tym należytą wiedzę. Wiedza jednak nadyma pychą, miłość natomiast buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '1', '1', 'Jeżeli komuś wydaje się nawet, że coś wie, to nie pojął on jeszcze, w jaki sposób należy z tej wiedzy korzystać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '2', '2', 'A jeżeli ktoś kocha Boga, tego Bóg uznaje za swego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '3', '3', 'W wypadku więc pokarmów pochodzących z ofiar składanych bożkom wiemy, że nie ma na świecie żadnego bożka i żadnego bóstwa. Jest tylko jeden Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '4', '4', 'Chociaż mówi się o istnieniu bogów czy to na niebie, czy to na ziemi - jakby rzeczywiście było wielu bogów i panów -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '5', '5', 'to dla nas istnieje tylko jeden Bóg Ojciec. Wszystko pochodzi od Niego, a my dla Niego żyjemy. Jeden jest również Pan - Jezus Chrystus, dzięki któremu wszystko istnieje i przez którego my także jesteśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '6', '6', 'Nie wszyscy mają jednak odpowiednią wiedzę. Niektórzy na skutek przyzwyczajenia się do kultu bożków jeszcze teraz spożywają mięso pochodzące z ofiar w przekonaniu, że jest to pokarm sakralny, ofiarowany bożkom i na skutek tego ich błędne sumienie plami się grzechem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '7', '7', 'Sam pokarm nie zbliża nas do Boga. Gdy przestajemy jeść, niczego nie tracimy, a gdy jemy, niczego nie zyskujemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '8', '8', 'Uważajcie jednak, aby wasza swoboda w tej dziedzinie nie była powodem zgorszenia dla tych, którzy mają błędne sumienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '9', '9', 'Gdyby bowiem ktoś o błędnym sumieniu zobaczył ciebie, który uchodzisz za posiadającego odpowiednią wiedzę, jak zasiadasz do stołu w świątyni pogańskiej, czy nie skłoniłoby to jego do spożywania pokarmów pochodzących z ofiar składanych bożkom?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '10', '10', 'Na skutek tego twoja wiedza sprowadziłaby zgubę na brata o błędnym sumieniu, za którego przecież umarł Chrystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '11', '11', 'W ten sposób grzesząc przeciw braciom i raniąc ich błędne sumienie, grzeszycie przeciw Chrystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '7', '12', '12', 'Jeżeli więc pokarm gorszy mego brata, przenigdy nie będę jadł mięsa, aby go nie zgorszyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '0', '0', 'Czy nie jestem wolny? Czy nie jestem apostołem? Czy nie widziałem Jezusa, Pana naszego? Czy wy nie jesteście moim dziełem w Panu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '1', '1', 'Jeśli nawet dla innych nie jestem apostołem, to dla was na pewno nim jestem. Wy jesteście pieczęcią mego urzędu apostolskiego w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '2', '2', 'Oto moja obrona wobec tych, którzy wysuwają przeciwko mnie zarzuty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '3', '3', 'Czy nie mamy prawa do utrzymania przez was?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '4', '4', 'Czy nie wolno nam zabrać ze sobą siostry, kobiety, podobnie jak czynią to pozostali apostołowie oraz bracia Pana i Kefas?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '5', '5', 'Czy tylko mnie i Barnabie nie wolno zwalniać się od pracy ręcznej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '6', '6', 'Któż pełni kiedykolwiek służbę żołnierską na własny koszt? Kto uprawia winnicę, a nie kosztuje jej owocu? Albo kto pasie trzodę i nie kosztuje mleka, które trzoda daje?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '7', '7', 'Czy mówię tylko o tym według ludzkiego sposobu myślenia? Czy nie wspomina o tym również Prawo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '8', '8', 'Otóż w Prawie Mojżesza jest napisane: \'Nie zawiążesz pyska młócącemu wołowi\'. Czy Bóg, który to powiedział, troszczy się o woły?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '9', '9', 'Czy też wypowiedział to ze względu na nas? Przecież nas ma na uwadze (Pismo Święte), kiedy pisze, że z nadzieją zysku ma rolnik orać, a młócący młócić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '10', '10', 'Jeżeli zasialiśmy wśród was dobra duchowe, to cóż w tym wielkiego, że mamy udział w plonach waszych dóbr materialnych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '11', '11', 'Jeżeli innym wolno domagać się, abyście ich utrzymywali, to tym bardziej to nam przysługuje. Nie skorzystaliśmy jednak z tej możliwości, lecz sami ponosimy cały ciężar utrzymywania, aby nie stwarzać żadnej przeszkody dobrej nowinie Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '12', '12', 'Czy nie wiecie, że ci, którzy sprawują święte obrzędy, żywią się na koszt świątyni, a ci, którzy służą ołtarzowi, mają udział w ofiarach składanych na ołtarzu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '13', '13', 'Tak samo i Pan postanowił, aby ci, którzy głoszą dobrą nowinę, żyli z głoszenia dobrej nowiny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '14', '14', 'Ja nie skorzystałem jednak z żadnego z tych praw. Nie piszę również o tych sprawach dlatego, aby coś tą drogą osiągnąć. Wolałbym raczej umrzeć, niż ... Nikt nie pozbawi mnie mojej chluby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '15', '15', 'Fakt, że głoszę ewangelię, nie jest dla mnie powodem do chluby. Jest to obowiązek, który został na mnie nałożony. Biada mi, gdybym nie głosił ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '16', '16', 'Gdybym czynił to z własnej woli, otrzymałbym zapłatę. Jeśli jednak nie czynię tego z własnej woli, to znaczy, że spełniam tylko powierzone mi czynności szafarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '17', '17', 'Za co wobec tego przysługuje mi zapłata? Otóż za to, że głosząc ewangelię bez wynagrodzenia, nie korzystam z przysługujących mi stąd praw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '18', '18', 'Chociaż jestem od nikogo niezależny, to jednak stałem się niewolnikiem wszystkich, abym ich jak najwięcej pozyskał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '19', '19', 'Dla Żydów stałem się Żydem, aby ich pozyskać. Dla tych, którzy zachowują Prawo (starotestamentalne), i ja wypełniam to Prawo - chociaż mu nie podlegam - aby ich pozyskać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '20', '20', 'Dla tych, którzy nie kierują się tym Prawem, i ja się do niego nie stosuję - chociaż nie jestem wolny od Prawa Bożego, bo podlegam Prawu Chrystusa - aby ich pozyskać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '21', '21', 'Dla słabych stałem się słaby, aby ich pozyskać. Dla wszystkich stałem się wszystkim, aby chociaż niektórych zbawić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '22', '22', 'Wszystko to zaś czynię dla ewangelii, aby mieć w niej swój udział.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '23', '23', 'Czy nie wiecie, że chociaż na stadionie biegną wszyscy zawodnicy, to jednak tylko jeden otrzymuje nagrodę. Dlatego tak biegnijcie, abyście ją osiągnęli!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '24', '24', 'Każdy, kto bierze udział w zawodach sportowych, odmawia sobie wszystkiego. Oni walczą, aby otrzymać w nagrodę wieniec, który przemija, a my walczymy o nagrodę nie przemijającą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '8', '25', '25', 'Ja więc nie biegnę na oślep, a kiedy uderzam, nie trafiam w próżnię, lecz poskramiam i ujarzmiam moje ciało, abym nauczając innych sam nie odpadł (w zawodach).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '0', '0', 'Bracia! Pragnę, abyście wiedzieli, że nasi ojcowie wszyscy znajdowali się w obecności Obłoku i wszyscy przeszli przez morze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '1', '1', 'Wszyscy zjednoczyli się z Mojżeszem, przechodząc chrzest Obłoku i morza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '2', '2', 'Wszyscy również spożywali ten sam pokarm');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '3', '3', 'i pili ten sam napój o duchowym znaczeniu. Pili zaś napój wytryskujący ze skały o duchowym znaczeniu, która szła za nimi, a skała ta była symbolem Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '4', '4', 'Lecz większość z nich nie podobała się Bogu, bo pustynia została zasłana ich zwłokami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '5', '5', 'Te wydarzenia powinny nam posłużyć za przykład ostrzegający, abyśmy nie pożądali złych rzeczy tak jak oni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '6', '6', 'Nie stawajcie się bałwochwalcami, jak niektórzy z nich, zgodnie z tym, co zostało napisane: \'Zasiadł lud, aby jeść i pić, potem zaś powstali, aby się bawić\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '7', '7', 'Nie oddawajmy się rozpuście, jak niektórzy z nich czynili, a w jednym dniu padło ich dwadzieścia trzy tysiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '8', '8', 'Nie kuśmy również Pana, jak niektórzy spośród nich kusili i poginęli od (jadu) wężów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '9', '9', 'Nie szemrajcie, jak uczynili to niektórzy z nich i zostali wytraceni przez wysłannika Bożego, wykonującego Jego wyroki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '10', '10', 'To wszystko, co ich spotkało, było zapowiedzią wydarzeń czasów ostatecznych, w których żyjemy, i zostało spisane dla nas w formie przestrogi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '11', '11', 'Niech zatem ten, komu wydaje się, że stoi, uważa, aby nie upadł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '12', '12', 'Pokusa, która was ogarnęła, nie przekracza ludzkich możliwości. Bóg jest wierny i nie pozwoli, abyście byli kuszeni ponad siły, lecz dopuszczając pokusę, równocześnie wskaże wyjście z niej, abyście mogli ją przezwyciężyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '13', '13', 'Kochani moi! Unikajcie więc bałwochwalstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '14', '14', 'Odwołuję się do waszego rozsądku: sami osądźcie, co mówię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '15', '15', 'Czy kielich błogosławieństwa, który błogosławimy, nie jest udziałem we krwi Chrystusa? Czy chleb, który łamiemy, nie jest udziałem w Ciele Chrystusa?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '16', '16', 'Ponieważ jest tylko jeden chleb, więc tworzymy jedno ciało, chociaż jest nas wielu, bo wszyscy spożywamy wspólnie ten sam chleb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '17', '17', 'Przypatrzcie się narodowi izraelskiemu. Czy ci, którzy spożywają dary składane na ołtarzu, nie tworzą wspólnoty zgromadzonej przy tym ołtarzu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '18', '18', 'Co chcę przez to powiedzieć? Czy może czymś jest ofiara złożona bożkom lub sam bożek?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '19', '19', 'Nie! Lecz stwierdzam, że poganie składają ofiary demonom, a nie Bogu. Nie chcę zaś, abyście mieli coś wspólnego z demonami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '20', '20', 'Nie możecie pić z kielicha Pana i z kielicha demonów. Nie możecie zasiadać przy stole Pana i przy stole demonów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '21', '21', 'Czy chcemy pobudzać Pana do gniewu? Czy jesteśmy mocniejsi od Niego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '22', '22', 'Wszystko wolno, ale nie wszystko przynosi korzyść. Wszystko wolno, ale nie wszystko buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '23', '23', 'Niech nikt nie szuka własnej korzyści, lecz dobra bliźniego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '24', '24', 'Wszelkie mięso, które sprzedają w sklepie, możecie jeść, nie pytając się dla spokoju sumienia, skąd pochodzi mięso,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '25', '25', '\'Pana jest ziemia i wszystko, co ją napełnia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '26', '26', 'Jeśli zaprosi was ktoś z niewierzących, a wy przyjmiecie jego zaproszenie, możecie jeść wszystko, co wam podadzą, nie pytając dla spokoju sumienia, skąd pochodzi pokarm.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '27', '27', 'Gdyby jednak ktoś wam powiedział: \'To było złożone na ofiarę\' bóstwom, nie jedzcie przez wzgląd na tego, który was ostrzegł, i ze względu na spokój sumienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '28', '28', 'Mam zaś na myśli nie wasze sumienie, lecz bliźniego. Dlaczego bowiem moja wolność ma być sądzona przez czyjeś sumienie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '29', '29', 'Jeżeli spożywam pokarm dziękując Bogu, to dlaczego mają o mnie źle mówić z powodu tego, za co ja dziękuję Bogu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '30', '30', 'Czy więc jecie, czy pijecie, czy co innego czynicie, wszystko czyńcie na chwałę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '31', '31', 'Nie dawajcie zgorszenia ani Żydom, ani Grekom, ani zgromadzeniu ludu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '9', '32', '32', 'Postępujcie tak jak ja, starajcie się wszystkim we wszystkim dogodzić, nie szukając swojej własnej korzyści, lecz kierując się dobrem innych, aby byli zbawieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '0', '0', 'Naśladujcie mnie, tak jak ja naśladuję Chrystusa!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '1', '1', 'Pochwalam was za to, że we wszystkich sprawach pamiętacie o mnie i zachowujecie zwyczaje w takiej formie, w jakiej wam je przekazałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '2', '2', 'Pragnę, abyście wiedzieli, że głową każdego mężczyzny jest Chrystus, głową kobiety mężczyzna, a głową Chrystusa - Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '3', '3', 'Każdy mężczyzna, który modli się lub przemawia z natchnienia Bożego z nakrytą głową, hańbi swoją głowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '4', '4', 'Natomiast kobieta odwrotnie, jeżeli modli się lub naucza z natchnienia Bożego z odkrytą głową, hańbi ją. Wygląda bowiem tak, jakby miała głowę ogoloną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '5', '5', 'Jeżeli więc kobieta nie nakrywa głowy, to niech ją ostrzyże. Jeżeli zaś wstydzi się ostrzyc lub ogolić, to niech wobec tego nakryje swoją głowę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '6', '6', 'Mężczyzna nie powinien nakrywać swojej głowy, bo jest obrazem i odblaskiem Boga, a kobieta jest odblaskiem mężczyzny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '7', '7', 'Mężczyzna nie pochodzi od kobiety, lecz kobieta od mężczyzny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '8', '8', 'Podobnie mężczyzna nie został stworzony dla kobiety, lecz ona dla niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '9', '9', 'Dlatego kobieta powinna nosić na głowie nakrycie na znak zależności od władzy - ze względu na aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '10', '10', 'Zresztą w Panu zarówno kobieta zależy od mężczyzny, jak i mężczyzna od kobiety.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '11', '11', 'Kobieta bowiem pochodzi wprawdzie od mężczyzny, ale z kolei mężczyzna rodzi się z kobiety. Wszystko zaś pochodzi od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '12', '12', 'Sami zresztą osądźcie, czy wypada, aby kobieta modliła się do Boga z odkrytą głową?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '13', '13', 'Czy nie poucza nas sama natura, że byłoby hańbą dla mężczyzny nosić długie włosy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '14', '14', 'gdy dla kobiety jest to zaszczyt? Bóg dał jej włosy na okrycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '15', '15', 'Jeżeli ktoś chciałby się o to spierać, to powtarzam: takiego zwyczaju nie ma ani u nas, ani na zebraniach ludu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '16', '16', 'Nie pochwalam was za to, że wspólne zebrania nie prowadzą was do dobrego, lecz do złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '17', '17', 'Przede wszystkim słyszę, że gdy schodzicie się na zebranie, dochodzi wśród was do podziału. Częściowo w to wierzę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '18', '18', 'bo muszą pojawić się wśród was stronnictwa, aby się okazało, kto jest wypróbowany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '19', '19', 'Wasze wspólne zgromadzenia nie są spożywaniem wieczerzy Pańskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '20', '20', 'Każdy bowiem w czasie wieczerzy zabiera się najpierw do własnego jedzenia, a skutek jest taki, że gdy jeden jest (jeszcze) głodny, drugi już jest nietrzeźwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '21', '21', 'Czy nie macie domów, aby tam jeść i pić? Czy chcecie okazać pogardę zebranemu ludowi Bożemu i zawstydzić tych, którzy niczego nie posiadają? Cóż powinienem wam powiedzieć? Może pochwalę was? Nie, za to was nie pochwalę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '22', '22', 'To, co otrzymałem od Pana, przekazałem wam, a mianowicie, że Pan Jezus tej nocy, której został wydany (w ręce wrogów), wziął chleb');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '23', '23', 'i odmówiwszy dziękczynną modlitwę połamał go i rzekł: \'To jest Ciało moje, za was wydane. Czyńcie to na moją pamiątkę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '24', '24', 'Podobnie po wieczerzy (wziął) kielich i powiedział: \'Ten kielich jest Nowym Przymierzem we Krwi mojej. Ile razy będziecie z niego pić, czyńcie to na moją pamiątkę\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '25', '25', 'Ilekroć bowiem spożywacie ten chleb i pijecie z tego kielicha, głosicie śmierć Pana do czasu, aż On przyjdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '26', '26', 'Ktokolwiek zatem niegodnie spożywa chleb lub pije z kielicha Pana, ten będzie odpowiadał za spożywanie Ciała i Krwi Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '27', '27', 'Każdy więc człowiek, zanim przystąpi do spożywania tego chleba i picia z kielicha, powinien naprzód zbadać siebie samego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '28', '28', 'Kto bowiem spożywa i pije niegodnie, ściąga na siebie wyrok potępienia, ponieważ nie odróżnia Ciała Pana od zwykłego chleba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '29', '29', 'Oto dlaczego jest wśród nas tylu chorych i słabych, a niektórzy nawet pomarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '30', '30', 'Gdybyśmy sami siebie należycie osądzali, nie bylibyśmy sądzeni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '31', '31', 'lecz kiedy Pan wymierza nam karę, to nas upomina, abyśmy nie zostali potępieni wraz ze światem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '32', '32', 'Bracia! Gdy zbieracie się, aby spożywać Ciało i Krew Pana, czekajcie jedni na drugich!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '10', '33', '33', 'Jeśli ktoś jest głodny, niech się naje w domu, abyście przez wasze wspólne zebrania nie ściągali na siebie kary (Bożej). Pozostałe sprawy załatwię, gdy do was przybędę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '0', '0', 'Pragnę, bracia, pouczyć was o darach duchowych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '1', '1', 'Wiecie, że gdy byliście jeszcze poganami, jakaś siła, której nie mogliście się oprzeć, prowadziła was do niemych bożków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '2', '2', 'Dlatego oznajmiam wam: podobnie jak nikt nie może powiedzieć pod wpływem Ducha Bożego: \'Niech Jezus będzie przeklęty\', tak samo bez wpływu Ducha Świętego nikt nie może mówić: \'Jezus jest Panem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '3', '3', 'Istnieją rozmaite dary Boże, lecz Duch jest ten sam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '4', '4', 'Są rozmaite rodzaje posług, a ten sam Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '5', '5', 'Istnieją rozmaite sposoby działania, lecz ten sam jest Bóg, działający wszystko we wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '6', '6', 'Duch objawia się każdemu dla wspólnego dobra.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '7', '7', 'Jednemu udziela daru wyrażania słowem mądrości, drugiemu wyrażania słowem wiedzy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '8', '8', 'innemu (udziela daru) wiary. Jeszcze innym ten sam Duch udziela daru uzdrawiania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '9', '9', 'a innemu dokonywania niezwykłych dzieł, a jeszcze innemu (daru) przemawiania z natchnienia Bożego i rozpoznawania duchów, modlenia się w różnych językach i tłumaczenia ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '10', '10', 'Wszystkie te dary są dziełem jednego i tego samego Ducha, który je rozdziela, tak jak chce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '11', '11', 'Ciało jest jedną całością, a składa się z wielu członków. Poszczególne zaś członki, chociaż jest ich wiele, tworzą to jedno ciało. Podobnie jest z Chrystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '12', '12', 'My wszyscy tworzymy jedno ciało, ponieważ zostaliśmy ochrzczeni w jednym Duchu: zarówno Żydzi, jak i Grecy, niewolnicy, jak i wolni. Wszyscy zostaliśmy też napojeni jednym Duchem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '13', '13', 'Ciało nie składa się z jednej tylko części, lecz z wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '14', '14', 'Gdyby noga powiedziała: \'Ponieważ nie jestem ręką, nie należę do ciała\', czy wskutek tego rzeczywiście nie należy do ciała?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '15', '15', 'Podobnie gdyby ucho powiedziało: \'Ponieważ nie jestem okiem, nie należę do ciała\', czy również nie należy do ciała?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '16', '16', 'Gdyby całe ciało było wzrokiem, to gdzież podziałby się słuch? Gdyby całe ciało było słuchem, to gdzież byłoby powonienie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '17', '17', 'Bóg rozmieścił poszczególne części ciała tak, jak zechciał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '18', '18', 'Gdyby tylko jedna część stanowiła całe ciało, czy można wówczas mówić o ciele?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '19', '19', 'Wobec tego, chociaż wiele jest członków, to ciało jest jedno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '20', '20', 'Oko nie może powiedzieć ręce: \'Nie potrzebuję ciebie\', ani głowa nogom: \'Nie potrzebuję was\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '21', '21', 'Wprost przeciwnie, te członki ciała, które uważamy za słabsze, są właśnie bardziej potrzebne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '22', '22', 'mniej zaszczytne otaczamy większym szacunkiem, a wstydliwe traktujemy szczególnie przyzwoicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '23', '23', 'Przyzwoite członki ciała nie potrzebują takiej troski. Bóg ukształtował bowiem ciało w taki sposób, że członki, które z natury są mniej godne szacunku, obdarzył większą czcią,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '24', '24', 'aby w ciele nie było rozdwojenia, lecz żeby wszystkie jego członki troszczyły się wzajemnie o siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '25', '25', 'Dlatego gdy jeden członek cierpi, pozostałe uczestniczą w jego cierpieniu, gdy zaś doznaje czci, cieszą się razem z nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '26', '26', 'Otóż wy jako Kościół jesteście Ciałem Chrystusa, a każdy z was oddzielnie jest członkiem tego Ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '27', '27', 'Bóg zaś umieścił w Kościele na pierwszym miejscu apostołów, na drugim tych, co przemawiają z natchnienia Bożego, na trzecim nauczycieli. Dalej idą: nadziemskie moce i dar uzdrawiania, umiejętność niesienia pomocy i rządzenia oraz dar modlenia się w różnych językach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '28', '28', 'Czy wszyscy są apostołami? Czy wszyscy przemawiają z natchnienia Bożego lub są nauczycielami? Czy wszyscy mają nadziemskie moce');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '29', '29', 'lub dar uzdrawiania? Czy wszyscy modlą się obcymi językami lub potrafią je tłumaczyć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '11', '30', '30', 'Starajcie się gorliwie o większe dary Boże; ja wskażę wam drogę jeszcze doskonalszą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '0', '0', 'Gdybym mówił językami ludzi i aniołów, a miłości bym nie miał, stałbym się miedzią dźwięczącą lub cymbałem brzmiącym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '1', '1', 'Gdybym posiadał dar przemawiania z natchnienia Bożego i znał wszystkie tajemnice, i posiadał wszelką wiedzę, i wiarę miał taką, żebym mógł góry przenosić, a miłości bym nie miał, niczym jestem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '2', '2', 'Gdybym cały swój majątek rozdał na żywienie ubogich i własne ciało wydał na spalenie, a miłości bym nie miał, nic mi nie pomoże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '3', '3', 'Miłość jest cierpliwa, uprzejma jest. Miłość nie zazdrości, nie przechwala się, nie unosi się pychą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '4', '4', 'nie zachowuje się nietaktownie, nie szuka swego, nie unosi się gniewem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '5', '5', 'nie pamięta złego, nie cieszy się z nieprawości, lecz raduje się z triumfu prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '6', '6', 'Miłość wszystko znosi, każdemu wierzy, każdemu ufa, wszystko przetrzyma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '7', '7', 'Miłość nigdy nie ustaje, chociaż dar przemawiania z natchnienia Bożego zniknie, chociaż dar modlenia się w obcych językach przeminie, chociaż wiedzy zabraknie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '8', '8', 'Niedoskonała jest nasza wiedza, niedoskonałe nasze przemawianie z natchnienia Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '9', '9', 'Gdy przyjdzie życie doskonałe, niedoskonałe zniknie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '10', '10', 'Gdy byłem dzieckiem, mówiłem jak dziecko, myślałem jak dziecko, rozumowałem jak dziecko. Gdy stałem się dojrzałym człowiekiem, pozbyłem się cech dziecinnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '11', '11', 'Teraz widzimy niejasno jak w zwierciadle, potem zobaczymy bezpośrednio - twarzą w twarz. Teraz poznaję częściowo, potem poznam (całkowicie), tak jak sam jestem poznany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '12', '12', '12', 'Teraz ważne są trzy rzeczy: wiara, nadzieja, miłość. A z nich największa jest miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '0', '0', 'Starajcie się o miłość, ubiegajcie się gorliwie o dary duchowe, a najbardziej o dar przemawiania z natchnienia Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '1', '1', 'Ten bowiem, kto modli się obcymi językami, nie mówi do ludzi, lecz do Boga. Nikt go nie rozumie, a on pod wpływem Ducha (Świętego) wypowiada tajemnicze rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '2', '2', 'Natomiast mówiący z natchnienia Bożego przemawia do ludzi, aby ich zbudować, pocieszyć i zachęcić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '3', '3', 'Ten, kto modli się w obcych językach, buduje tylko siebie, podczas gdy mówiący z natchnienia Bożego - całe zgromadzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '4', '4', 'Chciałbym, abyście wszyscy modlili się obcymi językami, ale najbardziej zależy mi na tym, abyście z natchnienia Bożego przemawiali. Przemawiający z natchnienia Bożego jest większy od tego, kto modli się obcymi słowami, chyba że ktoś je tłumaczy, aby zgromadzenie przyjęło te słowa ze zbudowaniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '5', '5', 'Bracia! Przypuśćmy teraz, że przybywam do was i modlę się obcymi słowami. Czy przyniesie wam to jakąś korzyść, jeżeli w mojej mowie nie znajdziecie objawienia nieznanych spraw lub wiedzy, pouczenia z natchnienia Bożego lub nauki?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '6', '6', 'Podobnie jest z przedmiotami  martwymi - instrumentami muzycznymi, jak flet czy cytra. Jeżeli nie pozwalają one odróżnić poszczególnych dźwięków, to jak można rozpoznać, co się na nich gra?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '7', '7', 'Albo jeżeli trąba daje niewyraźny sygnał, czy ktoś będzie się przygotowywał do walki?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '8', '8', 'Tak samo jest z wami. Jeżeli modlicie się obcymi słowami, niezrozumiałymi dla otoczenia, to kto zrozumie, co mówicie? Będziecie mówić na wiatr.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '9', '9', 'Na świecie jest takie mnóstwo głosów, a żaden z nich nie jest bez znaczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '10', '10', 'Jeżeli nie znam znaczenia jakiegoś słowa, wówczas dla tego, który je wypowiada, jestem obcokrajowcem, podobnie jak i on dla mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '11', '11', 'Skoro więc ubiegacie się żarliwie o dary duchowe, starajcie się posiąść takie, które przyczyniają się do zbudowania całego zgromadzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '12', '12', 'Dlatego kto modli się obcymi słowami, niech prosi Boga, aby umiał je przetłumaczyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '13', '13', 'Jeżeli bowiem podczas modlitwy posługuję się obcymi słowami, duch mój jest wprawdzie zatopiony w modlitwie, a umysł nie odnosi stąd żadnych korzyści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '14', '14', 'Cóż więc mam robić? Otóż będę się modlił zarówno duchem, jak i umysłem, będą nucił psalmy tak duchem, jak i umysłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '15', '15', 'Jeżeli bowiem będziesz Boga błogosławił tylko w duchu, jakże wtedy ktoś niewtajemniczony odpowie \'Amen\' na twoje dziękczynienie, skoro nie wie, co mówisz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '16', '16', 'Wprawdzie pięknie wyrażasz swoje dziękczynienie, ale drugiego to nie buduje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '17', '17', 'Dziękuję za to Bogu, że modlę się obcymi językami lepiej niż wy wszyscy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '18', '18', 'ale na wspólnym zgromadzeniu wolę powiedzieć pięć słów przemyślanych, aby innych pouczyć, niż dziesięć tysięcy niezrozumiałych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '19', '19', 'Bracia! Nie rozumujcie jak dzieci. Wobec zła zachowujcie się jak dzieci, ale wasz sposób rozumowania niech będzie dojrzały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '20', '20', 'W Piśmie Świętym zostało napisane: \'Będę przemawiał do tego ludu przez ludzi obcych języków i ust, ale oni nie wysłuchają Mnie - mówi Pan\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '21', '21', 'Dar modlenia się obcymi językami nie jest znakiem dla wiernych, lecz dla niewierzących. Natomiast dar przemawiania z natchnienia Bożego odwrotnie, nie jest znakiem dla niewierzących, lecz dla wierzących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '22', '22', 'Jeżeli więc na zebraniu wiernych wszyscy zaczną modlić się obcymi słowami, a wejdzie na to ktoś obcy lub niewierzący, czy nie powie, że jesteście szaleni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '23', '23', 'Natomiast gdyby wszyscy przemawiali z natchnienia Bożego, a wszedłby człowiek niewierzący lub obcy, wasze słowa zmuszą go do zastanowienia i przekonają go.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '24', '24', 'Wtedy on odsłoni swe serce, upadnie na twarz, odda pokłon Bogu i wyzna, że Bóg rzeczywiście jest wśród was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '25', '25', 'Jak wobec tego postępować, bracia? Otóż każdy z was posiada jakiś dar, jak: łaskę śpiewania hymnów, umiejętność nauczania i objawiania nieznanych spraw, dar modlenia się obcymi językami i tłumaczenia ich. Kiedy zbieracie się razem, korzystajcie z tych darów dla ogólnego zbudowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '26', '26', 'Obcymi językami niech modlą się kolejno dwaj lub najwyżej trzej, a jeden niech tłumaczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '27', '27', 'Gdyby jednak nie było tłumacza, powinni oni milczeć na wspólnym zgromadzeniu i mówić tylko do samych siebie i do Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '28', '28', 'Podobnie niech przemawia dwóch lub trzech mówiących z natchnienia Bożego, a inni niech to rozważają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '29', '29', 'Gdyby komuś z uczestników zgromadzenia została objawiona nieznana prawda, wówczas powinien zamilknąć ten, kto ma głos.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '30', '30', 'W ten sposób możecie wszyscy kolejno przemawiać z natchnienia Bożego, aby pouczyć i podnieść na duchu całe zgromadzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '31', '31', 'Przemawiający z natchnienia Bożego niech tak posługują się swoimi darami duchowymi, aby zachować porządek,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '32', '32', 'ponieważ Bóg jest Bogiem pokoju, a nie zamieszania. Istnieje taka praktyka we wszystkich zgromadzeniach ludu świętego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '33', '33', 'aby kobiety milczały na wspólnych zebraniach. Nie dopuszcza się ich do głosu, lecz mają być poddane, jak przewiduje to Prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '34', '34', 'Gdyby zaś chciały się czegoś nauczyć, niech pytają o to w domu swoich mężów, nie wypada bowiem, aby kobieta odzywała się na wspólnych zgromadzeniach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '35', '35', 'Czy słowo Boże wyszło od was, kobiet, czy raczej tylko do was dotarło?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '36', '36', 'Jeżeli komuś wydaje się, że przemawia z natchnienia Bożego lub posiada jakiś inny dar duchowy, niech zapozna się z tymi zarządzeniami, ponieważ są one nakazem Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '37', '37', 'Gdyby ktoś je zlekceważył, tego i Pan zlekceważy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '38', '38', 'A zatem, bracia moi, ubiegajcie się pilnie o (dar) przemawiania z natchnienia Bożego i nie zabraniajcie nikomu modlić się obcymi językami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '13', '39', '39', 'Wszystko niech się jednak odbywa z godnością i w należytym porządku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '0', '0', 'Bracia! Pragnę pogłębić wśród was znajomość ewangelii, którą wam głosiłem, a wy przyjęliście ją i przy niej trwacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '1', '1', 'Dzięki ewangelii dostąpicie zbawienia, o ile zachowacie ją w takiej formie, w jakiej wam ogłosiłem, chyba że uwierzyliście lekkomyślnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '2', '2', 'Przede wszystkim przekazałem wam naukę, którą przejąłem z tradycji, a mianowicie że Chrystus umarł za nasze grzechy, zgodnie z zapowiedzią Pisma,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '3', '3', 'został pogrzebany, trzeciego dnia zmartwychwstał, zgodnie z zapowiedzią Pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '4', '4', 'Ukazał się Kefasowi, potem Dwunastu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '5', '5', 'następnie przeszło pięciuset braciom, z których większość żyje dotąd, a niektórzy pomarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '6', '6', 'Z kolei Chrystus ukazał się Jakubowi, następnie wszystkim apostołom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '7', '7', 'a na koniec mnie jako poronionemu płodowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '8', '8', 'Jestem bowiem najmniej ważnym z apostołów i nawet nie zasługuję na to miano, ponieważ prześladowałem Kościół Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '9', '9', 'Jednak dzięki łasce Boga jestem tym, kim jestem. Nie zmarnowałem Jego łaski, lecz trudziłem się więcej od wszystkich apostołów. Zresztą nie ja się trudziłem, ale łaska Boża, która jest ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '10', '10', 'Obojętnie więc, czy chodzi o mnie, czy o innych apostołów, ważne jest, że taką naukę wam głosimy i w taką uwierzyliście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '11', '11', 'Jeżeli głosi się naukę, że Chrystus zmartwychwstał, to na jakiej podstawie niektórzy z was mówią, że nie ma zmartwychwstania ciał?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '12', '12', 'Jeżeli nie ma zmartwychwstania ciał, to wobec tego Chrystus nie zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '13', '13', 'Jeżeli zaś Jezus nie zmartwychwstał, to bezsensowne jest nasze nauczanie i bezsensowna wasza wiara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '14', '14', 'Okazuje się nadto, że nie jesteśmy prawdziwymi świadkami Boga, ponieważ złożyliśmy świadectwo przeciw Bogu twierdząc, że wskrzesił Chrystusa, skoro Go nie wskrzesił, jeżeli rzeczywiście umarli nie zmartwychwstają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '15', '15', 'Jeżeli bowiem umarli nie zmartwychwstają, to i Chrystus nie zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '16', '16', 'Skoro zaś Chrystus nie zmartwychwstał, to w takim razie wasza wiara jest bezskuteczna, bo nadal żyjecie w grzechach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '17', '17', 'a ci, którzy umarli w wierze w Chrystusa, poginęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '18', '18', 'Gdyby nasza nadzieja, jaką pokładamy w Chrystusie, dotyczyła tylko obecnego życia, bylibyśmy najbardziej godni politowania spośród wszystkich ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '19', '19', 'A jednak Chrystus zmartwychwstał i to pierwszy spośród tych, którzy posnęli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '20', '20', 'Skoro bowiem człowiek sprowadził śmierć, to również dzięki człowiekowi nastąpi zmartwychwstanie umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '21', '21', 'Podobnie jak na skutek łączności z Adamem wszyscy umierają, tak samo dzięki łączności z Chrystusem wszyscy zostaną ożywieni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '22', '22', 'każdy według swojej kolejności: pierwszy Chrystus, potem w dniu Jego powtórnego przyjścia ci, którzy do Niego należą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '23', '23', 'Wreszcie nadejdzie koniec. Wtedy Chrystus zniszczy wszelkie księstwo, władzę i potęgę, a królestwo przekaże Bogu Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '24', '24', 'Bo trzeba, aby Chrystus królował tak długo, aż \'wszystkich nieprzyjaciół położy u swoich stóp\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '25', '25', 'Śmierć będzie ostatnim wrogiem, który zostanie zniszczony,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '26', '26', 'zgodnie ze słowami Pisma Świętego: \'Wszystko rzucił pod Jego stopy\'. Skoro Pismo Święte stwierdza, że wszystko zostało Mu poddane, to oczywiście nie ma na myśli Tego, który Mu wszystko poddał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '27', '27', 'Kiedy zaś wszystko zostanie Mu poddane, wówczas sam Syn podporządkuje się Bogu, który dał Mu władzę nad wszystkim. W ten sposób Bóg będzie wszystkim we wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '28', '28', 'W przeciwnym razie cóż osiągną ci, którzy przyjmują chrzest za zmarłych? Jeżeli zmarli nie zmartwychwstają, to po co przyjmować za nich chrzest?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '29', '29', 'Dlaczego także i my wystawiamy się niemal w każdej godzinie na niebezpieczeństwo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '30', '30', 'Bracia! Przysięgam wam, którzy przynosicie mi chlubę w Chrystusie Jezusie Panu naszym, że codziennie narażam się na śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '31', '31', 'Jeżeli z czysto ludzkich pobudek walczyłem w Efezie z dzikimi zwierzętami, to jaką mam z tego korzyść? Skoro umarli nie zmartwychwstają, to: \'Jedzmy i pijmy, ponieważ jutro pomrzemy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '32', '32', 'Nie dajcie się zwieść: \'Złe rozmowy psują dobre obyczaje\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '33', '33', 'Weźcie się mocno w karby i nie grzeszcie! Niektórzy nie mają bowiem wiedzy Bożej. Mówię to po to, abyście się zawstydzili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '34', '34', 'Kto może jednak powiedzieć: Jak umarli zmartwychwstają? W jakim ciele powrócą?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '35', '35', 'Niemądry! To, co siejesz, nie wyda nowego życia, dopóki nie ulegnie rozkładowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '36', '36', 'To, co siejesz, nie ma kształtu rośliny, która wyrośnie, lecz jest tylko zwykłym ziarnem, na przykład pszenicy lub innym,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '37', '37', 'Bóg zaś temu ziarnu nadaje taki kształt, jaki chce, każdemu odpowiedni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '38', '38', 'Nie wszystkie ciała są jednakowe: inne ciała mają ludzie, inne zaś zwierzęta, ptaki i ryby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '39', '39', 'Rozróżniamy również ciała niebieskie i ziemskie, ale odmienna jest ich wspaniałość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '40', '40', 'Inny jest blask słońca, a inny księżyca i gwiazd. Poszczególne bowiem gwiazdy różnią się od siebie blaskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '41', '41', 'Tak samo przedstawia się sprawa ze zmartwychwstaniem umarłych. Sieje się ciało podległe zniszczeniu - powstaje ciało niezniszczalne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '42', '42', 'sieje się pozbawione zaszczytów - powstaje okryte chwałą, sieje się słabe - powstaje mocne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '43', '43', 'sieje się ciało podległe zmysłom - powstaje ciało podległe duchowi. Jeżeli istnieje ciało podległe zmysłom, to musi również istnieć ciało podległe duchowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '44', '44', 'Tak zresztą zostało napisane: \'Pierwszy człowiek Adam stał się żywą istotą\', a ostatni Adam tym, który daje nowe życie duchowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '45', '45', 'Życie podległe duchowi nie występuje pierwsze, lecz pojawia się dopiero po życiu podległym zmysłom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '46', '46', 'Pierwszy człowiek pochodzi z ziemi, więc jest ziemski, a ten Drugi pochodzi z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '47', '47', 'Jaki był ten ziemski, tacy są ludzie żyjący na ziemi, a jaki jest Ten z nieba, takimi będą ludzie żyjący w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '48', '48', 'Podobnie jak w tym życiu byliśmy obrazem człowieka ziemskiego, tak samo w przyszłym życiu będziemy obrazem Człowieka z nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '49', '49', 'Zapewniam was bracia, że śmiertelne ciało ludzkie nie jest w stanie osiągnąć królestwa Bożego, a to, co podlega zniszczeniu, nie może osiągnąć niezniszczalności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '50', '50', 'Oto oznajmiam wam tajemnicę - nie wszyscy umrzemy, ale wszyscy będziemy przemienieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '51', '51', 'W jednym momencie, w okamgnieniu, kiedy zabrzmi trąba zwiastująca koniec, umarli powstaną w niezniszczalnej postaci, a my będziemy w tę postać przemienieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '52', '52', 'Trzeba bowiem, aby to, co podlega zniszczeniu, przybrało postać niezniszczalną, a to, co śmiertelne - nieśmiertelną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '53', '53', 'Kiedy to, co podlega zniszczeniu, przybierze postać niezniszczalną, a to, co śmiertelne, przybierze postać nieśmiertelną, wówczas spełni się to, co napisano. Zwycięstwo pochłonęło śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '54', '54', 'Gdzież jest zwycięstwo twe, o śmierci? Gdzie jest, o śmierci, twe żądło?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '55', '55', 'Żądłem śmierci jest grzech, a siłą grzechu jest prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '56', '56', 'Bogu niech będą dzięki za zwycięstwo, którym nas obdarza, przez Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '14', '57', '57', 'A zatem, bracia moi najmilsi, bądźcie wytrwali i niewzruszeni, wyróżniajcie się zawsze w pracy dla Pana, wiedząc o tym, że wasz trud dla Niego nie jest daremny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '0', '0', 'W sprawie zbiórki ofiar na rzecz świętych, trzymajcie się zarządzeń, jakie wydałem w Kościołach Galacji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '1', '1', 'Niech każdy z was co niedzielę odkłada sobie i gromadzi oszczędności, aby nie urządzać zbiórki dopiero wówczas, gdy ja przybędę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '2', '2', 'Skoro zaś przybędę, wówczas prześlę wasze dary z listami polecającymi do Jerozolimy za pośrednictwem tych, których uznacie za odpowiednich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '3', '3', 'Gdyby i mnie wypadło tam się udać, to pójdą ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '4', '4', 'Przybędę do was, gdy przejdę przez Macedonię. Przez Macedonię bowiem tylko przejdę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '5', '5', 'a u was może zatrzymam się lub nawet przezimuję. Gdy zaś wyprawię się w dalszą podróż, odprowadzicie mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '6', '6', 'Nie chciałbym bowiem widzieć was tylko przelotnie. Mam nadzieję, że pozostanę u was jakiś czas, jeśli Pan pozwoli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '7', '7', 'W Efezie pozostanę aż do Świąt Pięćdziesiątnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '8', '8', 'Otworzyły się bowiem przede mną olbrzymie i obiecujące możliwości pracy, a przeciwników jest wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '9', '9', 'Jeżeliby przybył do was Tymoteusz, zatroszczcie się, aby się niczego nie obawiał, ponieważ podobnie jak ja pracuje dla sprawy Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '10', '10', 'Niechaj nikt go nie lekceważy. Odprowadzcie go w  pokoju, aby przybył do mnie, ponieważ oczekujemy go wraz z braćmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '11', '11', 'Jeżeli zaś chodzi o Apollosa - brata, to bardzo go prosiłem, aby przybył do was razem z braćmi, ale obecnie w żaden sposób nie chce się wyprawić. Przybędzie w odpowiednim czasie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '12', '12', 'Czuwajcie, trwajcie w wierze, bądźcie mężni i umacniajcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '13', '13', 'Niech wszystko u was dzieje się w miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '14', '14', 'Proszę was, bracia, o jeszcze jedno. Otóż znacie rodzinę Stefanasa. Wiecie, że jest to pierwszy owoc wiary w Achai, że poświęciła się służbie dla świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '15', '15', 'Podporządkujcie się takim ludziom oraz wszystkim, którzy uczestniczą w waszej pracy i trudzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '16', '16', 'Cieszę się z przybycia Stefanasa, Fortunata i Achaika, ponieważ zastąpili mi waszą obecność');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '17', '17', 'oraz pokrzepili mego i waszego ducha. Doceniajcie tych ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '18', '18', 'Pozdrawiają was Kościoły Azji. Serdecznie pozdrawia was w Panu Akwila i Pryscylla wraz z wiernymi, którzy gromadzą się w ich domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '19', '19', 'Pozdrawiają was wszyscy bracia. Przekażcie sobie wzajemnie pozdrowienia przez pocałunek, jaki przystoi świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '20', '20', 'Dołączam własnoręcznie pozdrowienie, Paweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '21', '21', 'Jeśli ktoś nie kocha Pana, niech będzie przeklęty. Maranatha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '22', '22', 'Łaska Pana Jezusa niech będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1co', '15', '23', '23', 'Miłuję was wszystkich złączonych z Chrystusem Jezusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '0', '0', 'Paweł, z woli Bożej apostoł Jezusa Chrystusa, i brat Tymoteusz do zgromadzenia ludu Bożego w Koryncie oraz do wszystkich świętych w całej Achai.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '1', '1', 'Łaska wam i pokój od Boga, Ojca naszego, i Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '2', '2', 'Niech będzie błogosławiony Bóg i Ojciec Pana naszego Jezusa Chrystusa, Ojciec miłosierdzia i Bóg wszelkiej pociechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '3', '3', 'Ten, który nas pociesza w każdym utrapieniu naszym, abyśmy wzmocnieni pociechą doznaną od Boga, mogli pocieszać tych, którzy znaleźli się w jakimkolwiek utrapieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '4', '4', 'Podobnie jak przeogromny jest nasz udział w męce Chrystusa, tak samo przeogromna jest nasza pociecha dzięki Chrystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '5', '5', 'I utrapienia znosimy za waszą pociechę i zbawienie, i pociech doznajemy za waszą pociechę. A sprawia ona, że w cierpliwości znosicie męki, których i my doznajemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '6', '6', 'A nasza nadzieja co do was jest utwierdzona. I wiemy, że jak udziałem waszym jest męka, tak samo będzie pociecha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '7', '7', 'Otóż nie chciałbym, bracia, abyście nie wiedzieli o naszym utrapieniu, jakiego doznaliśmy w Azji. Byliśmy przygnębieni ponad wszelką miarę i ponad siły, tak że zwątpiliśmy już w ocalenie życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '8', '8', 'Wprost przeciwnie, osobiście byliśmy przekonani nawet o wyroku śmierci. Nie należy bowiem polegać na sobie samym, lecz na Bogu, który wskrzesza umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '9', '9', 'On wybawił nas z tak wielkich niebezpieczeństw śmierci i znowu wybawi. Mocno ufamy Jemu, że jeszcze wybawi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '10', '10', 'przy współudziale waszej usilnej modlitwy za nas. W ten sposób z wielu ust popłynie dziękczynienie za nas, za dar udzielony nam dzięki modlitwom wielu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '11', '11', 'Naszą chlubą jest świadectwo naszego sumienia o tym, że kierowaliśmy się w świecie, a tym bardziej wobec was, prostotą i szczerością otrzymaną od Boga. Nie kierowaliśmy się mądrością naturalną, lecz łaską Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '12', '12', 'Nie piszemy wam o niczym innym, jak tylko o tym, co czytacie i co rozumiecie, a mam nadzieję, że do końca życia będziecie to rozumieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '13', '13', 'Zresztą częściowo już nas zrozumieliście, a mianowicie to, że w dniu przyjścia Pana Jezusa będziemy się chlubić wami, a wy nami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '14', '14', 'Z tą ufnością postanowiłem już wcześniej do was przybyć, abyście po raz drugi dostąpili łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '15', '15', 'Od was zamierzałem wyruszyć do Macedonii i z Macedonii znowu przybyć do was, abyście wyprawili mnie do Judei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '16', '16', 'Czy mając taki zamiar postąpiłem lekkomyślnie? Albo czy powziąwszy takie postanowienie kierowałem się czysto ludzkimi względami? Czy u mnie \'tak, tak\' znaczy równocześnie \'nie, nie\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '17', '17', 'Przysięgam na wierność Boga, że słowo, które do was kieruję, nie zawiera jednocześnie \'tak\' i \'nie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '18', '18', 'Syn Boży, Jezus Chrystus, którego głosiliśmy wam - to znaczy ja, Sylwan i Tymoteusz - nie był równocześnie \'tak\' i \'nie\'. W Nim było tylko \'tak\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '19', '19', 'ponieważ ile tylko było obietnic Bożych, wszystkie w Nim się wypełniły. Dlatego przez Niego wypowiadamy \'Amen\' na chwałę Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '20', '20', 'Bóg zaś jest tym, który nas i was utwierdził w Chrystusie, namaścił nas');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '21', '21', 'oraz opieczętował i dał naszym sercom Ducha jako zadatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '22', '22', 'Wzywam Boga na świadka i zaklinam się przed Nim na moje życie, że tylko dlatego nie przybyłem jeszcze do Koryntu, aby was oszczędzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '0', '23', '23', 'Nie jesteśmy panami waszej wiary, lecz współtwórcami waszej radości, bo przy wierze mocno stoicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '0', '0', 'Postanowiłem sobie nie przybywać do was i nie sprawiać wam ponownie smutku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '1', '1', 'Jeżeli was zasmucam, to któż mię rozweseli? Czy nie ten, którego ja zasumcam?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '2', '2', 'Napisałem o tym, aby przybywając do was nie doznać smutku od tych, którzy powinni sprawić mi radość. Jestem zaś przekonany o was wszystkich, że moja radość jest waszą radością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '3', '3', 'Pisałem do was w wielkiej udręce i ucisku serca, wśród wielu łez, nie po to, aby was zasmucić, lecz abyście poznali, jak szczególną miłością was darzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '4', '4', 'Jeżeli ktoś zasmucił mnie, to właściwie nie mnie zasmucił, lecz częściowo - aby nie przesadzać - was wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '5', '5', 'Niech takiemu wystarczy kara, jaką otrzymał od większości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '6', '6', 'Powinniście raczej mu darować i pocieszyć go, aby nie pogrążył się w bezgranicznym smutku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '7', '7', 'Dlatego błagam was, abyście jego sprawę rozstrzygnęli z miłością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '8', '8', 'Napisałem do was w tym celu, aby was wypróbować, czy we wszystkim jesteście posłuszni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '9', '9', 'Komu zaś cokolwiek przebaczycie, temu i ja przebaczę. A ja jeżeli coś przebaczyłem - o ile w ogóle mam coś do przebaczenia - to uczyniłem to dla was i ze względu na Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '10', '10', 'Uczyniłem to po to, aby nie oszukał was szatan, ponieważ znamy jego zamiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '11', '11', 'Kiedy przybyłem do Troady, aby głosić dobrą nowinę Chrystusa i otworzyły się przede mną możliwości pracy w Panu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '12', '12', 'nie zaznałem spokoju, bo nie zastałem Tytusa, mego brata. Pożegnałem się zatem i wyruszyłem do Macedonii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '13', '13', 'Bogu niech będą dzięki, Temu, który zawsze prowadzi nas do zwycięstwa w Chrystusie, Temu, który wszędzie roznosi przez nas wiedzę o Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '14', '14', 'Jesteśmy miłą Bogu wonnością Chrystusa, wśród dążących do zbawienia i zmierzających do potępienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '15', '15', 'Dla jednych jest to woń śmiercionośna, która zabija, dla drugich jest to woń ożywcza, która daje życie. Kto jest do tego zdolny?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '1', '16', '16', 'Nie jesteśmy bowiem - jak wielu innych, handlarzami słowa Bożego i w obliczu Boga w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '0', '0', 'Czy ponownie zaczynamy sami siebie polecać? Albo czy potrzebujemy, jak inni, listów polecających do was lub od was?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '1', '1', 'Wy jesteście naszym listem polecającym, napisanym w sercach naszych, a znanym i czytanym przez wszystkich ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '2', '2', 'Wiadomo, że jesteście listem Chrystusa napisanym przez naszą posługę. Listem napisanym nie atramentem, lecz Duchem Boga żywego, nie na tablicach kamiennych, lecz na żywych tablicach serc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '3', '3', 'Takie zaufanie mamy do Boga dzięki Chrystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '4', '4', 'Sami z siebie nie jesteśmy zdolni nawet niczego pomyśleć, bo nasze uzdolnienie pochodzi od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '5', '5', 'On też uzdolnił nas do posługiwania Nowemu Przymierzu, które nie polega na literze, lecz na Duchu. Litera bowiem zabija, Duch zaś ożywia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '6', '6', 'Jeżeli służba śmierci, czyli Prawu wyrytemu literami na kamieniach, była otaczana chwałą, tak że synowie Izraela nie mogli przypatrywać się obliczu Mojżesza z powodu chwały, która promieniowała z jego oblicza, a która przeminęła,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '7', '7', 'to o ileż bardziej otoczona będzie chwałą służba Ducha?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '8', '8', 'Jeżeli posługiwanie prowadzące na potępienie było otoczone chwałą, to o ileż bardziej obfituje w chwałę służba sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '9', '9', 'Wobec przeogromnej chwały Nowego Przymierza częściowa chwała Starego w ogóle nie wygląda na chwałę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '10', '10', 'Jeżeli bowiem to, co przemijające, miało chwałę, o wiele bardziej będzie otoczone chwałą to, co trwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '11', '11', 'Mając zatem taką nadzieję, korzystamy z wielkiej swobody działania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '12', '12', 'Nie postępujemy tak jak Mojżesz, który \'kładł zasłonę na swoje oblicze\',  aby synowie Izraela nie przypatrywali się zanikaniu chwały, które było symbolem przemijania jego posługi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '13', '13', 'Stępiały ich umysły, ponieważ aż do dnia dzisiejszego ta sama zasłona okrywa ich podczas czytania Starego Przymierza. Nie została ona z nich zdjęta, ponieważ usuwa ją dopiero Chrystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '14', '14', 'I tak aż do dnia dzisiejszego zasłona spoczywa na ich sercach, kiedy czytają Mojżesza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '15', '15', 'Skoro jednak ktoś nawraca się do Pana, zasłona opada.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '16', '16', 'Pan zaś jest Duchem, a gdzie jest Duch Pana, tam jest i wolność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '2', '17', '17', 'My wszyscy natomiast z odsłoniętym obliczem oglądamy jak w zwierciadle chwałę Pana i przemieniamy się w ten sam obraz, osiągając coraz większy stopień chwały, a sprawcą tego jest Pan - Duch.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '0', '0', 'Dlatego nie zniechęcamy się, skoro z miłosierdzia Bożego wypełniamy ten rodzaj posługi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '1', '1', 'Unikamy rzeczy, które się wstydliwie ukrywa, a w postępowaniu nie kierujemy się chytrością i nie fałszujemy słowa Bożego. Natomiast głosząc jawnie prawdę poddajemy się w obliczu Boga ocenie każdego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '2', '2', 'Jeżeli nawet w ewangelii, którą głosimy, nie wszystko jest jasne, to tylko dla tych, którzy idą na potępienie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '3', '3', 'dla niewierzących, których umysły zaślepił bóg tego świata, aby nie dostrzegli światła dobrej nowiny o chwale Chrystusa, który jest obrazem Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '4', '4', 'Nie głosimy siebie, lecz Jezusa Chrystusa jako Pana, a siebie uważamy za wasze sługi dzięki Jezusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '5', '5', 'Albowiem Bóg, który rozkazał, aby z ciemności zabłysło światło, sam rozjaśnił nasze serca światłością poznania chwały Bożej na obliczu Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '6', '6', 'Skarb ten przechowujemy w glinianych naczyniach, po to, aby (wiedziano), że ta przeogromna moc pochodzi od Boga, a nie z nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '7', '7', 'Zewsząd doznajemy utrapień, lecz nie jesteśmy tym przytłoczeni, przeżywamy wątpliwości, ale nie rozpaczamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '8', '8', 'Znosimy prześladowania, lecz nie jesteśmy opuszczeni, obalają nas na ziemię, ale nie giniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '9', '9', 'Zawsze nosimy w sobie umieranie Jezusa, aby i życie Jezusa pokazało się w nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '10', '10', 'Nieustannie bowiem my, którzy żyjemy, jesteśmy wydawani na śmierć z powodu Jezusa, aby i życie Jezusa pokazało się w naszym śmiertelnym ciele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '11', '11', 'Tak więc w nas działa śmierć, a w was życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '12', '12', 'Mamy tego samego ducha wiary, o którym napisano: \'Uwierzyłem, dlatego przemówiłem\'. My również wierzymy i dlatego przemawiamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '13', '13', 'Wiemy przecież, że Ten, który wskrzesił Pana Jezusa, i nas razem z Jezusem wskrzesi i wraz z wami postawi przed swoim trybunałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '14', '14', 'Wszystko to czynimy dla was, aby łaska pomnożona na skutek wzrastającej liczby wiernych spotęgowała dziękczynienie na chwałę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '15', '15', 'Dlatego nie zachęcamy się. Chociaż nasze doczesne ludzkie ciało ulega zniszczeniu, to jednak życie wewnętrzne odnawia się z dnia na dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '16', '16', 'Lekkie bowiem utrapienia, które teraz znosimy, przyniosą w wieczności bardzo wielką chwałę nam,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '3', '17', '17', 'którzy wpatrujemy się nie w to, co widzialne, lecz w to, co niewidzialne. To, co widzialne, trwa tylko do czasu, a to, co niewidzialne, trwa wiecznie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '0', '0', 'Wiemy bowiem, że kiedy nasz namiot, w którym na ziemi przebywamy, ulegnie zniszczeniu, otrzymamy od Boga dom w niebie, wieczny i nie zbudowany ręką ludzką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '1', '1', 'Dlatego wzdychamy, pragnąc przyodziać to mieszkanie z nieba na obecne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '2', '2', 'o ile jednak będziemy przyodziani, a nie pozbawieni okrycia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '3', '3', 'Przebywając więc w tym namiocie wzdychamy uciśnieni, ponieważ nie chcielibyśmy go rozebrać, lecz tylko przyodziać na niego mieszkanie z nieba, aby to, co śmiertelne, zostało wchłonięte przez życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '4', '4', 'Bóg, który nas do tego przeznaczył, dał nam Ducha jako zadatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '5', '5', 'Ufamy więc zawsze i wiemy, że jak długo przebywamy w ciele, jesteśmy pielgrzymami z daleka od Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '6', '6', 'W postępowaniu kierujemy się wiarą, a nie widzeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '7', '7', 'Ufamy. Chcielibyśmy raczej opuścić ciało i przebywać przy Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '8', '8', 'Dlatego obojętnie, czy przebywamy z Nim, czy też jesteśmy od Niego oddaleni, staramy się usilnie Jemu podobać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '9', '9', 'Wszyscy bowiem musimy stanąć przed trybunałem Chrystusa, aby każdy otrzymał zapłatę, na jaką zasłużył za życia w ciele, dobrą lub złą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '10', '10', 'Wiedząc, co to jest bojaźń Pańska, staramy się ludzi przekonywać - dla Boga wszystko w nas jest jawne. Mam też nadzieję, że i dla was jest jawne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '11', '11', 'Nie mamy zamiaru ponownie polecać wam siebie, lecz tylko pragniemy dać wam okazję do chlubienia się nami, abyście mogli odpowiedzieć tym, którzy chlubę widzą w zewnętrznej powierzchowności, a nie w sercu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '12', '12', 'Jeżeli postępujemy jak szaleni, to dla Boga, a jeżeli z umiarem, to dla was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '13', '13', 'Przynagla nas bowiem miłość Chrystusa. Doszliśmy do przekonania, że jeżeli jeden umarł za wszystkich, to wszyscy umarli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '14', '14', 'A za wszystkich umarł Chrystus po to, aby ci, którzy żyją, już więcej nie żyli dla siebie, lecz dla Tego, który za nich umarł i zmartwychwstał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '15', '15', 'Tak więc odtąd nie kierujemy się w ocenie czysto ludzkimi względami. Jeżeli nawet kiedyś ocenialiśmy w ten sposób Chrystusa, to obecnie już Go tak nie oceniamy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '16', '16', 'Kto jest w łączności z Chrystusem, ten staje się nowym stworzeniem. Stare przeminęło, a powstało nowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '17', '17', 'Wszystko to pochodzi od Boga, który pojednał nas ze sobą za pośrednictwem Chrystusa, a nam zlecił posługę pojednywania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '18', '18', 'Bóg pojednał w Chrystusie świat ze sobą, nie pamięta o grzechach ludzi, a nam nakazał głosić pojednanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '19', '19', 'Jesteśmy zatem wysłańcami Chrystusa i sam Bóg przez nas upomina. W imieniu Chrystusa prosimy: pojednajcie się z Bogiem!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '4', '20', '20', 'On dla nas zrzucił winę na Tego, który nie popełnił żadnego grzechu, abyśmy w Nim osiągnęli sprawiedliwość Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '0', '0', 'Jako współpracownicy Boga napominamy was, abyście nie przyjmowali łaski Bożej na próżno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '1', '1', 'Mówi bowiem (Pismo Święte): \'W czasie mojej łaskawości wysłuchałem cię, w dniu zbawienia przyszedłem ci z pomocą\'. Oto teraz czas szczególnej łaskawości, oto teraz dzień zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '2', '2', 'Nikomu w niczym nie dajemy powodu do zgorszenia, aby nie wyszydzono naszego posługiwania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '3', '3', 'W każdej okoliczności okazujemy, że jesteśmy sługami Boga, a mianowicie - we wszelkiej cierpliwości i w utrapieniach, wśród przeciwności i ucisków,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '4', '4', 'w chłostach i w więzieniach, podczas rozruchów i wśród trudów, w czuwaniach nocnych i postach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '5', '5', 'przez czystość i wiedzę, przez wielkoduszność i dobroć, przez dary Ducha Świętego i nieobłudną miłość,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '6', '6', 'przez słowo prawdy i objawy mocy Bożej, przez korzystanie w obronie i w natarciu z oręża, który służy sprawiedliwości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '7', '7', 'czy otacza nas chwała, czy hańba, czy okrywa nas sława, czy też niesława. Uważają nas za oszustów, a my jesteśmy prawdomówni, traktują nas jak nieznanych, a przecież jesteśmy znani,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '8', '8', 'wyglądamy na umierających, a oto żyjemy, jesteśmy karceni, lecz nie uśmierceni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '9', '9', 'wyglądamy na smutnych, a zawsze jesteśmy radośni, na ubogich, a wielu wzbogacamy, na tych, którzy niczego nie mają, a posiadamy wszystko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '10', '10', 'Koryntianie! Do was otworzyły się usta nasze i dla was rozszerzyło się nasze serce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '11', '11', 'Dla was w naszym sercu nie ma ograniczenia, ale wasze uczucia są ograniczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '12', '12', 'Odpłaćcie się tym samym! Mówię jak do swoich dzieci - rozszerzcie i wy swoje serca!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '13', '13', 'Nie wprzęgajcie się w jedno jarzmo z niewierzącymi. Co ma bowiem wspólnego sprawiedliwość z nieprawością? Albo co ma wspólnego światło z ciemnością?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '14', '14', 'Czy może istnieć harmonia pomiędzy Chrystusem a Belialem? Co ma wspólnego wierzący z niewierzącym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '15', '15', 'Co łączy świątynię Boga z bożkami? My bowiem jesteśmy świątynią Boga żywego, zgodnie z tym, co Bóg powiedział: \'Zamieszkam wśród nich i pomiędzy nimi będę się przechadzał, i będę ich Bogiem, a oni będą moim ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '16', '16', 'Przeto wyjdźcie spośród pogan i odłączcie się od nich - mówi Pan. Nie dotykajcie się tego, co nieczyste, a Ja was przyjmę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '5', '17', '17', 'Będę wam Ojcem, a wy będziecie moimi synami i córkami. To mówi Pan wszechmogący.\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '0', '0', 'Mając zatem, najmilsi, takie obietnice, oczyszczajmy się z wszelkiego brudu ciała i duszy i do końca doprowadźmy dzieło uświęcenia w bojaźni Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '1', '1', 'Zróbcie nam więcej miejsca (w swoich sercach)! Nikogo przecież nie skrzywdziliśmy, nikogo nie zrujnowaliśmy, nikogo nie oszukaliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '2', '2', 'Nie mówię tego, aby was potępiać. Już powiedziałem kiedyś, że w naszych sercach jesteście z nami złączeni na śmierć i na życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '3', '3', 'Mam do was wielkie zaufanie. Wy jesteście moją największą chlubą. Jesteście moją największą pociechą i radością w każdym utrapieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '4', '4', 'Gdy przybyliśmy do Macedonii, nie zaznaliśmy spokoju, lecz pod każdym względem przeżywaliśmy udręki. Od zewnątrz walki, a wewnątrz obawy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '5', '5', 'Lecz Bóg, pocieszający pokornych, pocieszył nas przybyciem Tytusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '6', '6', 'Zresztą sprawił to nie tylko przez jego przybycie, ale i przez pociechę, jaką sprawiliście jemu. Opowiadał mi o waszej tęsknocie, o waszym płaczu, o waszym oddaniu dla mnie tak, że jeszcze bardziej się radowałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '7', '7', 'Może i zasmuciłem was swoim listem, lecz nie żałuję tego. Jeżeli nawet żałowałem, widząc, że ów list zasmucił was na pewien czas,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '8', '8', 'to obecnie się raduję. Nie z tego, że zasmuciliście się, lecz z tego, że smutek skłonił was do zmiany usposobienia. Znieśliście ten smutek po Bożemu i dlatego nie wyrządziliśmy wam żadnej szkody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '9', '9', 'Smutek bowiem, znoszony po Bożemu, dokonuje zbawiennej zmiany usposobienia, czego się nigdy nie żałuje. Natomiast światowy smutek powoduje śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '10', '10', 'Oto jaką gorliwość spowodował wśród was ten smutek znoszony po Bożemu. Nie tylko gorliwość spowodował, lecz również obronę, oburzenie, bojaźń, tęsknotę, żarliwość i potrzebę wymierzenia kary. Dowiedliście pod każdym względem, że w tej sprawie jesteście czyści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '11', '11', 'Jeżeli więc napisałem do was, to nie z powodu tego, który wyrządził krzywdę, ani tego, który jej doznał, lecz aby okazać wam przed Bogiem naszą troskę o was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '12', '12', 'Dlatego doznaliśmy pociechy, a ta nasza pociecha spotęgowała się jeszcze bardziej przez radość, jakiej doznaliśmy na widok radości Tytusa z tego, żeście go wszyscy pokrzepili na duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '13', '13', 'Jeżeli nawet chlubiłem się wami przed nim, to nie doznałem wstydu. A jak wszystko powiedzieliśmy wam zgodnie z prawdą, tak też chlubne świadectwo, jakie wydaliśmy o was Tytusowi, okazało się prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '14', '14', 'Jego uczucia do was są bardzo głębokie, kiedy wspomina wasze posłuszeństwo oraz to, jak przyjęliście go z bojaźnią i drżeniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '6', '15', '15', 'Cieszę się, że mogę wam we wszystkim zaufać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '0', '0', 'Donosimy wam, bracia, o łasce, jaką Bóg obdarzył Kościoły w Macedonii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '1', '1', 'Z ogromną radością zniosły one ciężką próbę utrapienia, a ich skrajne ubóstwo zdobyło się na nadzwyczajną hojność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '2', '2', 'Zaświadczam, że według swoich możliwości, a nawet ponad możliwości, dobrowolnie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '3', '3', 'skierowali do nas gorącą prośbę o tę łaskę: o współudział w posłudze na rzecz świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '4', '4', 'Nie tylko tak uczynili, jak tego się spodziewaliśmy, lecz zgodnie z wolą Bożą oddali siebie samych najpierw Panu, a potem nam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '5', '5', 'Wobec tego poprosiliśmy Tytusa, aby to dzieło, które już u was rozpoczął, doprowadził do końca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '6', '6', 'Obyście wyróżnili się w tym dziele tak, jak wyróżniacie się wiarą i słowem, gorliwością w każdej dziedzinie i naszą miłością do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '7', '7', 'Nie mówię tego w formie rozkazu, lecz aby wskazać wam przykład gorliwości innych i wypróbować, czy wasza miłość jest szczera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '8', '8', 'Wiecie przecież, na czym polega łaska Pana naszego Jezusa Chrystusa. Oto będąc bogatym stał się dla was ubogim, abyście zostali wzbogaceni Jego ubóstwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '9', '9', 'Daję wam tylko radę w tej sprawie, a to przyniesie wam korzyść. W ubiegłym roku wyraziliście chęć i rozpoczęliście realizację tego dzieła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '10', '10', 'Teraz więc doprowadźcie je do końca. Obyście to dzieło tak ochoczo doprowadzili do końca według waszych możliwości, jak ochoczo zgodziliście się je podjąć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '11', '11', 'Jeżeli bowiem istnieje ochota, to łączy się ona z przyjemnością tylko wówczas, gdy się daje z tego, co się ma, a nie z tego, czego się nie ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '12', '12', 'Nie chodzi bowiem o to, aby innym sprawiać ulgę, a wam utrapienie, lecz aby nastąpiło wyrównanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '13', '13', 'W obecnym czasie to, co wam zbywa w zakresie dóbr materialnych, niech zapobiega ich niedostatkowi, aby z kolei to, co im zbywa w zakresie dóbr duchowych, zapobiegło waszemu niedostatkowi w tym względzie. W ten sposób nastąpi wyrównanie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '14', '14', 'zgodnie z tym, co zostało napisane: \'Kto miał dużo, nie miał za wiele, a kto miał niewiele, nie miał za mało.\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '15', '15', 'Bogu niech będą dzięki za to, że wszczepił w serce Tytusa tę samą troskliwość o was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '16', '16', 'Przyjął on moją zachętę, a będąc szczególnie gorliwym, dobrowolnie wyruszył do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '17', '17', 'Wraz z nim posłaliśmy brata, któremu głoszenie dobrej nowiny przyniosło chwałę we wszystkich Kościołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '18', '18', 'Ponadto Kościoły wyznaczyły go na towarzysza naszej podróży w tym dziele łaski, któremu poświęcamy się ochoczo, ku chwale Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '19', '19', 'Staramy się nie stwarzać okazji, aby na nas narzekano z powodu hojnego daru, którego zebraniem zajęliśmy się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '20', '20', 'Troszczymy się bowiem nie tylko o to, co jest dobre wobec Pana, lecz także o to, co jest dobre wobec ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '21', '21', 'Wraz z nimi posyłaliśmy naszego brata, którego gorliwość wypróbowaliśmy przy wielu okazjach, a który obecnie wykazuje jeszcze większą gorliwość, wypływającą z zaufania do was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '22', '22', 'Jeżeli chodzi o Tytusa, to jest on moim towarzyszem i współpracuje ze mną dla waszego dobra. Jeżeli zaś chodzi o naszych braci, to są oni wysłannikami Kościołów i chwałą Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '7', '23', '23', 'Dajcie więc dowód waszej miłości i udowodnijcie im, że słusznie chlubimy się wami wobec wszystkich Kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '0', '0', 'Uważam za zbyteczne pisać wam o posłudze na rzecz świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '1', '1', 'Znam bowiem waszą ochotę i z tego powodu chlubię się wami przed Macedończykami mówiąc, że Achaja jest przygotowana od zeszłego roku. Wasza gorliwość pobudziła wielu do współzawodnictwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '2', '2', 'Posłałem zaś braci, abyśmy w tej sprawie bezpodstawnie nie chlubili się wami, abyście byli przygotowani, tak jak powiedziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '3', '3', 'Gdyby przypadkiem  przybyli ze mną Macedończycy i zastali was nie przygotowanych, zawstydzilibyśmy się - już nie mówiąc o was - z powodu tej ufności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '4', '4', 'Uważałem przeto za konieczne poprosić braci, aby wcześniej przybyli do was i przygotowali uprzednio obiecany hojny dar. Gotowy już dar będzie oznaką hojności, a nie sknerstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '5', '5', 'Pamiętajcie o tym, że kto skąpo sieje, ten będzie zbierał skąpe plony, a kto hojnie sieje, ten zbierze bogate plony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '6', '6', 'Każdy niech tak postąpi, jak postanowił, nie z żalem lub z konieczności, ponieważ \'chętnego dawcę Bóg\' miłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '7', '7', 'Bóg może was obdarzyć obfitymi łaskami, abyście mając zawsze wszystkiego tyle, ile wam potrzeba, spełniali jak najczęściej wszelkie dobre uczynki,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '8', '8', 'zgodnie z tym, co zostało napisane: \'Rozdzielił i dał ubogim, sprawiedliwość Jego trwa na wieki\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '9', '9', 'A ten, który wydziela siewcy ziarno oraz chleba na pokarm dostarcza, pomnoży wasze ziarno i zwiększy owoce waszej sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '10', '10', 'Wzbogaceni we wszystko okażecie wszelaką hojność, która sprawia, że za naszym pośrednictwem składane jest Bogu dziękczynienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '11', '11', 'Posługa bowiem związana z tą społeczną potrzebą nie tylko zaradzi niedostatkom świętych, lecz również obficie zwiększy dziękczynienia, które wielu będzie składać Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '12', '12', 'Przekonani dowodami, jakich dostarczycie im przez tę posługę, będą chwalić Boga za wasze posłuszeństwo w wyznawaniu ewangelii o Chrystusie i za hojność, z jaką dzielicie się swoimi dobrami, zarówno z nimi, jak i ze wszystkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '13', '13', 'W swojej gorącej modlitwie wyrażą tęsknotę za wami z powodu przeogromnej łaski Bożej, którą posiadacie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '8', '14', '14', 'Bogu niech będą dzięki za Jego dar niewysłowiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '0', '0', 'Ja, Paweł, osobiście was zaklinam na cichość i łagodność Chrystusa, ja, który podobno w waszej obecności jestem nieśmiały, a z dala od was śmiało sobie poczynam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '1', '1', 'Proszę was, abym nie musiał, gdy przybędę, okazać swojej śmiałości i pewności siebie, z jaką zamierzam wystąpić wobec tych, którzy uważają, że w postępowaniu kierujemy się czysto ludzkimi względami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '2', '2', 'Chociaż żyjemy jako ludzie, to jednak w walce nie kierujemy się ludzkimi względami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '3', '3', 'Oręż, którym posługujemy się w walce, nie jest bowiem czysto ludzki, lecz zawiera moce Boże, które burzą nawet warowne twierdze. Burzymy rozumowania');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '4', '4', 'i wszelką wyniosłość, która podnosi się przeciw poznaniu Boga, a każdy umysł skłaniamy do posłuszeństwa Chrystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '5', '5', 'Jesteśmy gotowi karać każdy objaw nieposłuszeństwa, skoro tylko urzeczywistni się w pełni wasze posłuszeństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '6', '6', 'Zauważcie to, co jest oczywiste! Jeżeli ktoś jest pewny, że należy do Chrystusa, to niech pomyśli także o tym, że podobnie jak on należy do Chrystusa, tak samo i my.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '7', '7', 'Gdybym jeszcze bardziej chlubił się z władzy, którą Pan nas obdarzył, abym was budował, a nie burzył - nie wstydziłbym się tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '8', '8', 'W przeciwnym razie wyglądałoby na to, że chcę was straszyć listami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '9', '9', '\'Listy - powiadają bowiem - są groźne i mocne, lecz gdy zjawi się osobiście, jest słaby, a jego słowo nic nie znaczy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '10', '10', 'Kto tak mówi, niech sobie zapamięta, że jakie jest nasze słowo w listach, gdy jesteśmy nieobecni, takimi okażemy się w czynie, gdy przybędziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '11', '11', 'Nie mamy odwagi zaliczać się do tych, którzy polecają siebie samych, lub się z nimi porównywać. Nie postępują oni rozsądnie, mierząc siebie swoją własną miarą i porównując się tylko ze sobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '12', '12', 'My nie będziemy chlubić się ponad miarę, lecz nasza chluba dotyczy określonego przez Boga pola działania, w którym i wy się znajdujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '13', '13', 'Nie rozciągamy swojej władzy nad wami bezprawnie, tak jakbyśmy jeszcze nie dotarli do was, ponieważ dotarliśmy do was z ewangelią o Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '14', '14', 'Nie chlubimy się ponad miarę kosztem cudzych trudów. Mamy jednak nadzieję, że wraz ze wzrostem waszej wiary coraz bardziej będzie się powiększał teren naszego działania');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '15', '15', 'i z ewangelią wyjdziemy poza wasze granice, nie chlubiąc się tym, czego już inni dokonali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '16', '16', '\'Kto się chlubi, niech w Panu się chlubi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '9', '17', '17', 'Nie ten zasługuje na uznanie, kto sam siebie poleca, lecz ten, kogo Pan poleca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '0', '0', 'O, gdybyście znieśli trochę szaleństwa z mej strony! Przecież wy i mnie znosicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '1', '1', 'Jestem o was zazdrosny zazdrością Boga. Zaślubiłem was bowiem z jednym Mężem i pragnę was przedstawić Chrystusowi jako czystą dziewicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '2', '2', 'Obawiam się jednak, aby wasze umysły nie zostały skażone i nie utraciły prostoty oraz czystości wobec Chrystusa, podobnie jak przydarzyło się to Ewie, która została oszukana chytrością węża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '3', '3', 'Jeżeli bowiem przychodzi ktoś do was i przepowiada wam innego Jezusa, a nie tego, którego przepowiadaliśmy, lub bierzecie innego Ducha, a nie tego, którego wzięliście od nas, lub przyjmujecie ewangelię odmienną od tej, którą przyjęliście - spokojnie to znosicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '4', '4', 'Uważam, że wcale nie jestem gorszy od największych apostołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '5', '5', 'Chociaż bowiem nie jestem biegły w sztuce wymowy, to jednak nie brakuje mi wiedzy, jak to okazaliśmy wam pod każdym względem i we wszystkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '6', '6', 'Czy popełniłem grzech, upokarzając siebie, aby was wywyższyć, gdy za  darmo głosiłem wam Bożą ewangelię?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '7', '7', 'Ograbiłem inne Kościoły, przyjmując od nich zasiłek na utrzymanie, abym mógł wśród was pełnić posługę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '8', '8', 'Gdy przebywałem u was i cierpiałem niedostatek, dla nikogo nie byłem ciężarem, ponieważ moje braki uzupełnili bracia, którzy przybyli z Macedonii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '9', '9', 'Przysięgam na prawdę Chrystusa, którą posiadam, że nikt nie pozbawi mię tego tytułu do chluby z okręgu Achai.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '10', '10', 'Dlaczego? Czy dlatego, że was nie kocham? Bóg to wie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '11', '11', 'To, co czynię i nadal będę czynił, aby nie stworzyć okazji do chlubienia się tym, którzy jej szukają, żeby mieli taki sam tytuł dla chluby, jak my.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '12', '12', 'Ci fałszywi apostołowie działają podstępnie i udają apostołów Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '13', '13', 'I nic dziwnego, ponieważ szatan również udaje anioła światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '14', '14', 'Nie ma w tym nic wielkiego, że jego słudzy w podobny sposób udają sługi sprawiedliwości. Koniec ich będzie taki, jak ich uczynki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '15', '15', 'Powtarzam jeszcze raz: Niech nikt nie uważa mnie za szaleńca! Jeżeli jednak sądzicie, że jestem szalony, to przyjmijcie mnie jak szaleńca, abym i ja mógł się choć trochę pochwalić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '16', '16', 'To, co powiem, nie powiem w duchu Pana, lecz jakby w przystępie szaleństwa, w przekonaniu, że jest się czym chwalić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '17', '17', 'Skoro wielu zwyczajnie po ludzku się chwali, i ja będę się chwalił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '18', '18', 'Chętnie przecież znosicie szalonych, choć sami jesteście rozumni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '19', '19', 'Znosicie bowiem, gdy ktoś bierze was do niewoli lub objada, wyzyskuje, traktuje z góry lub bije po twarzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '20', '20', 'Muszę ze wstydem wyznać, że tak wygląda, jakbyśmy okazali naszą słabość. Na co inni odważają się - mówię to jak szalony - i ja się odważę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '21', '21', 'Hebrajczykami są? Ja też. Izraelitami są? Ja również. Potomstwem Abrahama są? Ja także.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '22', '22', 'Sługami Chrystusa są? Teraz zdobędę się na szaleństwo - ja jestem nim jeszcze bardziej, bo większe niż inni ponosiłem trudy, znosiłem cięższe więzienie i daleko większe chłosty. Często znajdowałem się w niebezpieczeństwie śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '23', '23', 'Pięć razy byłem skazany przez Żydów na chłostę i otrzymałem owych \'czterdzieści uderzeń bez jednego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '24', '24', 'Trzy razy bito mnie rózgami, raz kamienowano, trzy razy przeżyłem rozbicie okrętu i (jako rozbitek) całą dobę spędziłem na głębinie morskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '25', '25', 'Często podróżowałem i groziły mi niebezpieczeństwa na rzekach i ze strony zbójców, własnego narodu i pogan. Niebezpieczeństwa w mieście, na pustyni i na morzu, ze strony fałszywych braci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '26', '26', 'Spędziłem życie w trudzie i mozole, często wśród nie przespanych nocy, w głodzie i pragnieniu, w częstych postach, w zimnie i nagości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '27', '27', 'Oprócz tego wszystkiego dręczy mnie codziennie troska o wszystkie Kościoły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '28', '28', 'Kto jest słaby, a ja nie odczuwam tej słabości? Kto się gorszy, a ja z tego powodu nie płonę?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '29', '29', 'Jeżeli już trzeba się chlubić, to będę się chlubił z moich słabości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '30', '30', 'Bóg i Ojciec Pana Jezusa, który na wieki jest błogosławiony, wie, że nie kłamię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '31', '31', 'W Damaszku namiestnik króla Aretasa strzegł miasta Damasceńczyków, aby mnie chwycić,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '10', '32', '32', 'ale spuszczono mnie w koszu przez okno w murze i w ten sposób wymknąłem się z jego rąk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '0', '0', 'A skoro trzeba się chlubić - chociaż nie wypada - przejdę do wizji i objawień Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '1', '1', 'Znam człowieka (wierzącego) w Chrystusa, który przed czternastu laty - nie wiem, czy fizycznie, czy też w duchu, Bóg to wie - został porwany aż do trzeciego nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '2', '2', 'Wiem również, że ów człowiek - nie wiem, czy fizycznie, czy w duchu, Bóg to wie -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '3', '3', 'został w zachwycie porwany aż do raju i tam usłyszał tajemnicze słowa, których człowiekowi nie wolno wymawiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '4', '4', 'Z tego będę się chlubił, natomiast ze siebie nie będę się chlubił, chyba że z moich słabości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '5', '5', 'Gdybym nawet chciał się chlubić, nie byłbym szaleńcem, ponieważ powiedziałbym tylko prawdę. Powstrzymuję się jednak od tego, aby mi nikt nie przypisywał więcej ponad to, co we mnie widzi lub ode mnie słyszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '6', '6', 'Abym zaś ogromem objawień nie pysznił się, w moje ciało został wbity kolec, jakby wysłannik szatana, który mnie policzkuje, abym nie unosił się pychą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '7', '7', 'Dlatego trzy razy prosiłem Pana, aby odstąpił ode mnie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '8', '8', 'ale Pan nie odpowiedział: \'Wystarcza ci moja łaska. Moc okazuje swoją skuteczność w słabości\'. Najchętniej więc będę się chlubił z moich słabości, aby zamieszkała we mnie moc Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '9', '9', 'Z tych względów upodobałem sobie słabości, poniewierki i niedostatki, prześladowania i uciski (znoszone) dla Chrystusa. Kiedy bowiem odczuwam słabość, wtedy właśnie jestem mocny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '10', '10', 'Oszalałem, ale wy zmusiliście mnie do tego. To wy powinniście byli mnie polecić. W niczym bowiem nie ustępowałem największym apostołom, chociaż jestem niczym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '11', '11', 'Znaki mego apostolatu urzeczywistniały się wśród was, a były to - niewyczerpana cierpliwość, znaki, zdumiewające czyny i nadziemskie moce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '12', '12', 'W czym więc ustępujecie innym Kościołom? Chyba tylko tym, że nie byłem dla was ciężarem. Wybaczcie mi tę krzywdę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '13', '13', 'Oto jestem gotowy po raz trzeci przybyć do was i nie będę dla was ciężarem. Nie szukam bowiem tego, co wasze, lecz was samych. Nie dzieci rodzicom powinny gromadzić skarby, lecz rodzice dzieciom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '14', '14', 'Ja najchętniej wydam wszystko, a nawet siebie samego za wasze dusze. - Czy im bardziej was kocham, tym mniej mam być kochany?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '15', '15', 'Przypuszczam jednak, że nie byłem dla was ciężarem, ale może podszedłem was podstępem jak człowiek chytry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '16', '16', 'Czy rzeczywiście oszukałem was przez kogoś z tych, których do was posłałem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '17', '17', 'Prosiłem Tytusa i wysłałem z nim brata. Czy Tytus was oszukał? Czy nie postępowaliśmy w tym samym duchu? Czy nie szliśmy tymi samymi śladami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '18', '18', 'Od dawna już sądzicie, że bronimy się przed waszymi zarzutami. Przemawiamy w Chrystusie w obliczu Boga, a wszystko to, najmilsi, dla waszego zbudowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '19', '19', 'Obawiam się, że skoro przybędę, nie zastanę was takimi, jakimi chciałbym was zastać, a i wy nie zobaczycie mnie takim, jakim chcielibyście mnie zobaczyć. Oby nie było wśród was sporu i zazdrości, gniewów i rywalizacji, obmów i szemrania, wynoszenia się i zamieszek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '11', '20', '20', 'Obawiam się, żeby Bóg nie upokorzył mnie wobec was, gdy znowu do was przybędę. Obym nie musiał opłakiwać wielu, którzy poprzednio popełnili grzechy nieczystości, rozpusty i rozwiązłości i nie zerwali z nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '0', '0', 'Obecnie już po raz trzeci wybieram się do was. \'Na świadectwie dwóch albo trzech świadków opiera się każda sprawa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '1', '1', 'Kiedy byłem u was po raz drugi, zapowiedziałem już i teraz - chociaż jestem nieobecny - znów zapowiadam tym, którzy poprzednio popełnili grzechy, oraz wszystkim innym, że gdy ponownie przybędę, nie oszczędzę nikogo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '2', '2', 'Szukacie dowodu na to, że przemawia przeze mnie Chrystus. Nie jest On słaby wobec was, lecz właśnie wśród was okazuje swoją moc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '3', '3', 'Chociaż został ukrzyżowany wskutek słabości, to jednak żyje dzięki mocy Bożej. My również jesteśmy w Nim słabi, lecz razem z Nim żyć będziemy i okażemy wam moc Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '4', '4', 'Wypróbujcie samych siebie, czy trwacie w wierze, i doświadczajcie siebie samych. Czy nie znacie siebie i nie wiecie, że Jezus Chrystus jest wśród was? Chyba że nie wytrzymujecie próby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '5', '5', 'Mam nadzieję, że wiecie o tym, iż my próbę wytrzymujemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '6', '6', 'Modlimy się zaś do Boga, abyście nic złego nie uczynili. Nie idzie o to, aby okazać, że my próbę wytrzymujemy, lecz abyście czynili to, co dobre, a my, żebyśmy wyglądali na tych, którzy próby nie wytrzymują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '7', '7', 'Nie możemy bowiem niczego zrobić przeciwko prawdzie, lecz tylko dla prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '8', '8', 'Cieszymy się, gdy my jesteśmy słabi, a wy mocni, i modlimy się o wasze udoskonalenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '9', '9', 'Dlatego chociaż jestem wśród was nieobecny, piszę o tych sprawach, abym po przybyciu do was nie musiał korzystać z władzy, którą Pan mi dał po to, abym budował, a nie burzył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '10', '10', 'Zresztą, bracia, cieszcie się, dążcie do doskonałości i przyjmujcie moje napomnienia. Bądźcie jednej myśli i żyjcie w pokoju, a Bóg, który obdarza miłością i pokojem, będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '11', '11', 'Pozdrówcie jedni drugich świętym pocałunkiem. Pozdrawiają was wszyscy święci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2co', '12', '12', '12', 'Łaska Pana Jezusa Chrystusa i miłość Boga oraz dar jedności w Duchu Świętym niech będą z wami wszystkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '0', '0', 'Paweł - powołany na apostoła nie przez ludzi ani jakiegokolwiek człowieka, lecz przez Jezusa Chrystusa i przez Boga Ojca, który wskrzesił Go z martwych -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '1', '1', 'oraz wszyscy bracia, którzy przebywają ze mną, przekazują pozdrowienia Kościołom w Galacji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '2', '2', 'Łaska niech będzie z wami i pokój Boga, naszego Ojca i Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '3', '3', 'On to ofiarował siebie samego za nasze grzechy, aby nas uwolnić od zła, które towarzyszy temu życiu. Taka była wola Boga, naszego Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '4', '4', 'Jemu chwała na całą wieczność. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '5', '5', 'Dziwię się, że tak szybko odstępujecie od Tego, który was powołał do uczestnictwa w łasce Chrystusowej, i zwracacie się ku innej ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '6', '6', 'Nie miej innej! Są  jedynie tacy, którzy sieją zamęt wśród was i chcą sfałszować ewangelię Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '7', '7', 'Ale choćbyśmy sami czy nawet anioł z nieba głosił wam coś innego, niż myśmy wam głosili, niech będzie wyklęty!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '8', '8', 'Jak poprzednio stwierdziliśmy, tak i teraz powtarzam: Gdyby wam ktoś głosił ewangelię inną od tej, którą otrzymaliście, niech będzie wyklęty!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '9', '9', 'Czyż bowiem teraz ludziom chcę się przypodobać, czy Bogu? Czy szukam uznania wśród ludzi? Gdybym jeszcze teraz szukał uznania wśród ludzi, nie byłbym sługą Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '10', '10', 'Oświadczam wam, bracia, że ewangelia, którą głosiłem, nie pochodzi od człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '11', '11', 'Bo ja sam nie otrzymałem jej od człowieka ani jej się nauczyłem, lecz mam ją dzięki objawieniu Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '12', '12', 'Słyszeliście bowiem o moim dawnym postępowaniu - kiedy byłem wyznawcą judaizmu - że zaciekle prześladowałem Kościół Boży i usiłowałem go zniszczyć,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '13', '13', 'że jako wyznawca judaizmu wszystkich moich rówieśników z mego narodu prześcignąłem nadzwyczajną gorliwością wobec ojczystych tradycji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '14', '14', 'A kiedy Ten, który wybrał mnie w łonie matki i powołał dzięki swej łasce, raczył');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '15', '15', 'mi objawić swego Syna, abym głosił Jego ewangelię poganom, wtedy nie kierowałem się już względami czysto ludzkimi');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '16', '16', 'ani nie wstępowałem do Jerozolimy, by tam spotkać tych, którzy już przede mną byli, apostołów, lecz udałem się do Arabii, a potem wróciłem znów do Damaszku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '17', '17', 'A po trzech latach udałem się do Jerozolimy, by poznać Kefasa. Pozostałem u niego przez piętnaście dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '18', '18', 'Nikogo z apostołów poza Jakubem, bratem Pana, nie widziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '19', '19', 'To, co wam piszę - Bóg mi jest świadkiem - nie jest kłamstwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '20', '20', 'Potem udałem się do Syrii i Cylicji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '21', '21', 'A Kościoły Chrystusowe w Judei nie znały mnie osobiście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '22', '22', 'Słyszały one tylko tyle: Dawny nasz prześladowca głosi teraz tę wiarę, którą niegdyś usiłował wytępić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '0', '23', '23', 'I wielbili Boga z mego powodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '0', '0', 'Po czternastu latach znów udałem się do Jerozolimy z Barnabą, zabierając ze sobą Tytusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '1', '1', 'Udałem się tam pod wpływem otrzymanego objawienia i otwarcie wszystkim, a na osobności tym, którzy cieszą się szczególnym poważaniem, przedstawiłem treść ewangelii, którą głoszę pośród pogan. Chciałem bowiem, bym przypadkiem - czy to teraz, czy kiedy indziej - nie biegł daremnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '2', '2', 'Tymczasem nawet mojego współtowarzysza Tytusa, który był Grekiem, nie zmuszano do obrzezania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '3', '3', 'A fałszywi bracia - którzy podstępnie wdarli się do nas, aby podpatrzeć nasz sposób życia, oparty na wolności, jaką mamy dzięki Chrystusowi Jezusowi - chcieli nas uczynić niewolnikami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '4', '4', 'Lecz nie ustąpiliśmy ani przez chwilę ich żądaniu. Chodziło przecież o to, by zachować prawdę ewangelii wśród was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '5', '5', 'Co zaś do tych, którzy cieszą się poważaniem - nie obchodzi mnie, jacy oni kiedyś byli: Bóg nie patrzy na wygląd człowieka - otóż ci, cieszący się poważaniem, żadnych uzupełnień mi nie zlecili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '6', '6', 'Wręcz przeciwnie: widząc, że powierzono mi głoszenie ewangelii wśród pogan, podobnie jak Piotrowi wśród Żydów -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '7', '7', 'Ten bowiem, który dał Piotrowi moc sprawowania posłannictwa wśród Żydów, dał ją i mnie wśród pogan -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '8', '8', 'i wiedząc o łasce, jako otrzymałem, Jakub, Kefas i Jan, uważani za filary, podali ręce mnie i Barnabie na znak wspólnoty. Myśmy mieli pójść do pogan, oni natomiast do Żydów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '9', '9', 'Mieliśmy również pamiętać o ubogich, co też skwapliwie czyniłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '10', '10', 'Kiedy jednak Kefas przybył do Antiochii, sprzeciwiłem się mu otwarcie, ponieważ sobie na to zasłużył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '11', '11', 'Zanim bowiem przybyli pewni ludzie od Jakuba, spożywał posiłki razem z nawróconymi z pogaństwa. Skoro zaś tamci przybyli, zaczął się wycofywać i unikać pogan w obawie przed Żydami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '12', '12', 'Podobnie też zaczęli postępować pozostali Żydzi. Nawet Barnaba nie oparł się temu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '13', '13', 'Kiedy spostrzegłem, że nie postępują zgodnie z prawdą ewangeliczną, powiedziałem Kefasowi wobec wszystkich: Jeżeli ty, będąc Żydem, postępujesz według zwyczaju pogańskiego, a nie żydowskiego, to dlaczego zmuszasz pogan, aby żyli na sposób żydowski?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '14', '14', 'My z urodzenia jesteśmy Żydami, a nie grzesznymi poganami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '15', '15', 'Wiemy jednak, że człowiek dostępuje usprawiedliwienia nie przez spełnianie uczynków nakazanych przez Prawo, lecz dzięki wierze w Chrystusa Jezusa. Dlatego myśmy właśnie uwierzyli w Chrystusa Jezusa, aby doznać usprawiedliwienia dzięki wierze w Chrystusa, a nie dzięki spełnianiu uczynków nakazanych przez Prawo. \'Żaden człowiek bowiem nie osiągnie usprawiedliwienia\' przez spełnianie uczynków nakazanych przez Prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '16', '16', 'Jeżeli jednak szukamy usprawiedliwienia w Chrystusie, a mimo wszystko poczytuje się nas za grzeszników, to czyż Chrystus miałby być sługą grzechu? Na pewno nie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '17', '17', 'Jeśli odbudowuję to, co zburzyłem, staję się przestępcą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '18', '18', 'Ja bowiem właśnie dzięki Prawu umarłem dla Prawa, abym żył dla Boga. Z Chrystusem jestem współukrzyżowany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '19', '19', 'Już nie ja żyję, lecz żyje we mnie Chrystus. Żyję teraz na ziemi, żyję w wierze Syna Bożego, który mnie umiłował i ofiarował się za mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '1', '20', '20', 'Nie lekceważę łaski Boga. Jeśli bowiem usprawiedliwienie zyskuje się dzięki Prawu, to Chrystus umarł na próżno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '0', '0', 'O, niemądrzy Galatowie! Któż was zwiódł? A przecież przed oczami mieliście obraz Jezusa Chrystusa ukrzyżowanego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '1', '1', 'Jednego tylko chcę się od was dowiedzieć: otrzymaliście Ducha dzięki uczynkom nakazanym przez Prawo czy też dzięki posłuszeństwu wobec wiary?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '2', '2', 'Czy jesteście aż tak niemądrzy? Rozpocząć od ducha, a teraz skończyć w ciele!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '3', '3', 'Czyż na próżno tak dużo doświadczyliście? Czyżby rzeczywiście na próżno?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '4', '4', 'Czyż Ten, który udziela wam Ducha i działa pośród was cuda, czyni to dla uczynków nakazanych przez Prawo czy też dla posłuszeństwa wobec wiary?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '5', '5', 'Podobnie było z Abrahamem: \'uwierzył Bogu i to poczytano mu za usprawiedliwienie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '6', '6', 'Zrozumiejcie więc, że synami Abrahama są ci właśnie, którzy wierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '7', '7', 'Pismo zaś przewidując, że Bóg usprawiedliwi pogan dzięki wierze, dało Abrahamowi tę radosną nowinę: \'Ty będziesz błogosławieństwem dla wszystkich narodów\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '8', '8', 'Tak więc ci, którzy wierzą: mają błogosławieństwo razem z wierzącym Abrahamem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '9', '9', 'Ci natomiast, którzy powołują się na uczynki nakazane przez Prawo, ściągają na siebie przekleństwo. Napisano bowiem: Przeklęty niech będzie każdy, kto nie zachowuje wiernie wszystkich przepisów zawartych w Księdze Prawa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '10', '10', 'Jest rzeczą zrozumiałą, że nikt nie doznaje usprawiedliwienia u Boga przez Prawo, ponieważ napisano: \'Sprawiedliwy żyć będzie dzięki wierze\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '11', '11', 'Prawo nie uwzględnia wiary, lecz powiada: \'Kto wypełnia przepisy Prawa, żyć będzie dzięki nim\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '12', '12', 'Chrystus uwolnił nas od przekleństwa Prawa, stając się ze względu na nas przekleństwem. Napisano bowiem: \'Przeklęty każdy, kto wisi na drzewie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '13', '13', 'Stało się tak dlatego, aby zstąpiło błogosławieństwo na pogan w Jezusie Chrystusie, udzielone Abrahamowi i byśmy dzięki wierze otrzymali obiecanego Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '14', '14', 'Bracia! Posłużę się przykładem: Nikt nie unieważnia i nie zmienia prawnie sporządzonego testamentu, choć jest on tylko dziełem człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '15', '15', 'Otóż Abrahamowi właśnie i jego potomstwu dano obietnice. Nie powiedziano: potomstwu, jakby chodziło o wielu, lecz mówiono o jednym: \'I potomkowi twemu\', to jest Chrystusowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '16', '16', 'Chcę przez to powiedzieć: Testamentu, sporządzonego prawnie przez Boga, nie może znieść Prawo, które powstało po czterystu trzydziestu latach, bo inaczej obietnica byłaby bez znaczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '17', '17', 'Gdyby bowiem dziedzictwo wywodziło się z Prawa, nie wywodziłoby się z obietnicy - tymczasem Bóg przez obietnicę okazał Abrahamowi swoją łaskę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '18', '18', 'Czymże więc jest Prawo? Dodano je dla ujawnienia wykroczeń - do czasu przyjścia potomstwa, dla którego była przeznaczona obietnica. Prawo to przekazali aniołowie przez pośrednika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '19', '19', 'Tam, gdzie jest jeden, nie potrzeba pośrednika, a właśnie Bóg jest jeden.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '20', '20', 'Czyż więc Prawo sprzeciwia się obietnicom Boga? Na pewno nie! Gdyby bowiem nadane Prawo mogło obdarzać życiem, to rzeczywiście źródłem usprawiedliwienia byłoby Prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '21', '21', 'Pismo jednak obwarowało wszystko grzechem, by obietnica spełniała się wobec wierzących dzięki wierze w Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '22', '22', 'Zanim zaś nastały czasy wiary, strzegło nas Prawo. Byliśmy zamknięci aż do momentu objawienia się wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '23', '23', 'Tak więc Prawo stało się opiekunem czuwającym nad nami aż do czasu przyjścia Chrystusa, byśmy dzięki wierze doznali usprawiedliwienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '24', '24', 'Gdy zaś nastały czasy wiary, już nie jesteśmy poddani opiekunowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '25', '25', 'Wszyscy bowiem dzięki wierze jesteście synami Boga - w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '26', '26', 'Wy bowiem, którzyście przez chrzest zanurzyli się w Chrystusie, przyoblekliście się w Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '27', '27', 'Nie istnieje odtąd Żyd ani Grek, nie istnieje niewolnik ani wolny, nie istnieje mężczyzna i kobieta - wy wszyscy bowiem stanowicie jedność w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '2', '28', '28', 'A jeżeli należycie do Chrystusa, to jesteście potomstwem Abrahama i - zgodnie z daną obietnicą - spadkobiercami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '0', '0', 'A powiadam, że dopóki spadkobierca jest małoletni, w niczym nie różni się od niewolnika. Choć jest panem wszystkiego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '1', '1', 'to jednak poddany jest nadzorcom i zarządcom aż do dnia ustalonego przez ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '2', '2', 'Tak jest i z nami. Będąc małoletnimi, byliśmy oddani w niewolę spraw świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '3', '3', 'Gdy zaś wypełnił się czas, Bóg zesłał swego Syna, który urodził się z kobiety, urodził się pod panowaniem Prawa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '4', '4', 'aby uwolnić tych, którzy byli pod panowaniem Prawa, abyśmy otrzymali przybrane synostwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '5', '5', 'Aby zaś udowodnić, że jesteście synami, Bóg zesłał do naszych serc Ducha Syna swego, który woła: Abba, Ojcze!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '6', '6', 'Nie jesteś już więc niewolnikiem, lecz synem. A jeśli jesteś synem, to i spadkobiercą - z łaski Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '7', '7', 'Kiedyś wprawdzie, gdy nie znaliście Boga, byliście niewolnikami tego wszystkiego, co w rzeczywistości nie jest Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '8', '8', 'Teraz zaś, gdy poznaliście Boga - a jeszcze bardziej Bóg was poznał - jakżeż możecie znów powracać do słabych i nędznych spraw? Czyż ponownie chcecie być w ich niewoli?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '9', '9', 'Dokładnie przestrzegacie dni, miesięcy, pór roku i lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '10', '10', 'Lękam się, że na próżno trudziłem się dla was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '11', '11', 'Proszę was, bracia, stańcie się tacy jak ja, ponieważ ja stałem się taki jak wy. Nie wyrządziliście mi w niczym krzywdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '12', '12', 'Wiecie natomiast, że gdy po raz pierwszy głosiłem wam ewangelię, trapiła mnie choroba,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '13', '13', 'natomiast wy nie tylko nie wzgardziliście mną i nie odepchnęliście mnie, choć moje ciało mogło dawać do tego powód, lecz przyjęliście mnie jak wysłańca Boga, jak samego Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '14', '14', 'Gdzież więc wasz tytuł do szczęścia? Mogę bowiem wam oświadczyć, że - gdyby to było możliwe - wyjęlibyście sobie oczy i dalibyście je mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '15', '15', 'Czyż więc, mówiąc wam prawdę, stałem się waszym wrogiem?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '16', '16', 'Sprzyjają wam nieszczerze, bo chcą was oddzielić, abyście wy im sprzyjali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '17', '17', 'Przyjemnie mi jest, gdy zawsze szczerą darzycie mnie życzliwością, a nie tylko wtedy, gdy jestem wśród was obecny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '18', '18', 'Dzieci moje najmilsze! Znów rodzę was w boleściach, aż ukształtuje się w was Chrystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '19', '19', 'Chciałbym już teraz być wśród was i odpowiednio do was przemówić, gdyż nawiedzają mnie wątpliwości w waszej sprawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '20', '20', 'Powiedzcie mi, wy, którzy chcecie być pod panowaniem Prawa! Czy nie słyszycie, co mówi Prawo?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '21', '21', 'Napisano przecież, że Abraham miał dwóch synów: jeden był zrodzony z niewolnicy, a drugi z wolnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '22', '22', 'Syn z niewolnicy był zrodzony zwyczajnie, według praw ciała, a syn z wolnej - dzięki obietnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '23', '23', 'Ma to znaczenie przenośne. Kobiety te bowiem oznaczają dwa przymierza: jedno zawarte na górze Synaj - rodzące niewolę - wyobraża je Hagar.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '24', '24', 'Symbolizuje ona górę Synaj, która znajduje się w Arabii. Obecnie zaś Hagar wyobraża Jeruzalem, ponieważ jest ono w niewoli ze swoimi dziećmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '25', '25', 'Jeruzalem zaś górne cieszy się wolnością. Ono to właśnie jest naszą matką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '26', '26', 'Napisano bowiem: \'Ciesz się, niepłodna, która nie rodziłaś, wykrzykuj z radości ty, która nie doznałaś bólów rodzenia, gdyż więcej dzieci mieć będzie kobieta opuszczona, niż ta, która jest zamężna\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '27', '27', 'Właśnie wy, bracia, jesteście - jak Izaak - dziećmi obietnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '28', '28', 'Lecz jak niegdyś syn zrodzony zwyczajnie, według praw ciała, prześladował syna zrodzonego według praw ducha, tak jest i teraz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '29', '29', 'Ale co mówi Pismo? \'Wypędź niewolnicę i jej syna, gdyż syn niewolnicy nigdy nie będzie spadkobiercą razem z synem\' wolnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '3', '30', '30', 'Dlatego, bracia, nie jesteśmy dziećmi niewolnicy, lecz wolnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '0', '0', 'Chrystus wyprowadził nas z niewoli na wolność. Trwajcie więc w niej i nie pozwólcie ponownie nałożyć sobie jarzma niewoli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '1', '1', 'Oto ja, Paweł, mówię wam: jeśli teraz pozwolicie się obrzezać, Chrystus nic wam nie pomoże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '2', '2', 'Ponownie oświadczam każdemu człowiekowi, który pozwala się obrzezać: Jest on zobowiązany zachowywać wszystkie przepisy Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '3', '3', 'Nie macie nic wspólnego z Chrystusem, wy wszyscy, którzy usiłujecie szukać usprawiedliwienia w Prawie. Utraciliście łaskę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '4', '4', 'My bowiem dzięki Duchowi przez wiarę wyczekujemy spodziewanego usprawiedliwienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '5', '5', 'W Chrystusie Jezusie bowiem nie ma znaczenia ani obrzezanie, ani jego brak, lecz liczy się wiara ujawniająca swą moc dzięki miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '6', '6', 'Biegliście pięknie! Któż wam przeszkodził trwać w posłuszeństwie wobec prawdy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '7', '7', 'Wpływ ten nie pochodzi od Tego, który was wzywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '8', '8', 'Mała ilość kwasu zakwasza całe ciasto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '9', '9', 'Ja ufam w Panu, że wy wcale inaczej nie myślicie. Ten zaś, kto rozsiewa zamęt wśród was - kimkolwiek on jest - poniesie zasłużoną karę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '10', '10', 'Ja natomiast, bracia, jeżeli rzekomo głoszę obrzezanie, to dlaczego jestem jeszcze prześladowany? Przecież ustałoby wówczas zgorszenie płynące z krzyża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '11', '11', 'Oby już całkowicie okaleczyli się ci, którzy was podburzają!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '12', '12', 'Wy bowiem, bracia, do wolności zostaliście powołani, ale nie do tej wolności, która służy naturalnym skłonnościom ludzkim, lecz która - ożywiona miłością - każe wzajemnie sobie usługiwać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '13', '13', 'Całe bowiem Prawo wypełnia się w tym jednym nakazie: \'Będziesz miłował bliźniego twego jak siebie samego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '14', '14', 'Jeśli bowiem wzajemnie się kąsacie i pożeracie, to uważajcie, abyście się nawzajem nie zjedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '15', '15', 'I powiadam: Postępujcie zgodnie z duchem i nie schlebiajcie pragnieniom ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '16', '16', 'Ciało bowiem żywi pragnienia przeciwne duchowi, duch natomiast pragnie czego innego niż ciało - tak bardzo są sobie przeciwstawne - i dlatego czynicie to, czego nie chcecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '17', '17', 'Jeżeli jednak duch wami kieruje, nie jesteście pod panowaniem Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '18', '18', 'Czyny  wywodzące się z ciała są znane: cudzołóstwo, nieczystość, rozpusta,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '19', '19', 'bałwochwalstwo, magia, nienawiść, kłótnia, zawiść, gniewy, ambicje, niezgody, rozłamy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '20', '20', 'zazdrości, pijaństwo, hulanki i tym podobne. Zapowiadam wam, jak to już dawniej czyniłem, że ci, którzy takie czyny popełniają, królestwa Bożego nie odziedziczą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '21', '21', 'Owoc zaś wywodzący się z ducha to: miłość, radość, pokój, cierpliwość, dobroć, życzliwość, wierność,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '22', '22', 'łagodność, opanowanie. Dla tych, którzy to wszystko czynią, Prawo nie istnieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '23', '23', 'Ci zaś, którzy stanowią jedność z Chrystusem Jezusem, ukrzyżowali ciało razem z namiętnościami i pożądaniami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '24', '24', 'Jeśli żyjemy dzięki Duchowi, postępujemy również zgodnie ze wskazaniami Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '4', '25', '25', 'Nie ubiegajmy się o próżną chwałę, wzajemnie się zwalczając i wzajemnie sobie zazdroszcząc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '0', '0', 'Bracia, a jeśli ktoś niebacznie popełni jakieś wykroczenie, wówczas wy, obdarzeni łaską Ducha, sprowadźcie takiego na prawą drogę w duchu łagodności. Uważaj jednak sam, byś nie uległ pokusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '1', '1', 'Dźwigajcie jedni drugich ciężary, a w ten sposób wypełnicie prawo Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '2', '2', 'Jeśli bowiem ktoś sądzi, że coś znaczy - choć niczym nie jest - ten okłamuje samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '3', '3', 'Niech każdy osądza swoje własne dzieło, a wówczas tylko sobą samym chlubić się będzie, a nie kimś innym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '4', '4', 'Każdy bowiem poniesie własny ciężar.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '5', '5', 'Otrzymujący zaś naukę niech dzieli się wszystkimi dobrami z tym, który go naucza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '6', '6', 'Nie łudźcie się, Bóg nie pozwala szydzić z siebie. Co człowiek posieje, to będzie zbierał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '7', '7', 'Bo kto wsiewa w rolę ciała swego, z ciała tego zbierać będzie zgubę. Kto zaś wsiewa w rolę ducha, z ducha tego zbierać będzie życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '8', '8', 'Czyniąc dobrze nie zniechęcajmy się. Nie ulegając bowiem zniechęceniu, będziemy zbierać owoc we właściwym czasie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '9', '9', 'Dopóki więc mamy czas, czyńmy dobrze wszystkim, a szczególnie braciom tej samej wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '10', '10', 'Zobaczcie, jak wielkimi literami napisałem do was - moją własną ręką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '11', '11', 'Ci, którzy pragną przypodobać się ludziom, zmuszają was do obrzezania po to, aby nie cierpieć prześladowania z powodu krzyża Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '12', '12', 'Zwolennicy obrzezania nie troszczą się o zachowanie Prawa, lecz dlatego chcą was poddać obrzezaniu, aby się chlubić waszym ciałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '13', '13', 'Ja natomiast chcę jedynie chlubić się krzyżem naszego Pana, Jezusa Chrystusa, dzięki któremu świat stał się dla mnie ukrzyżowany, a ja dla świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '14', '14', 'Ani bowiem obrzezanie nic nie znaczy, ani brak jego. Liczy się jedynie nowe stworzenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '15', '15', 'A na tych, którzy tą normą się kierują, i na Izraela, naród Boga, niech zstąpi pokój i miłosierdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '16', '16', 'Niech odtąd nikt nie wyrządza mi przykrości, bo ja noszę w sobie znamiona Jezusa!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'gal', '5', '17', '17', 'Bracia, niech łaska naszego Pana, Jezusa Chrystusa, będzie z duchem waszym. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '0', '0', 'Paweł, z woli Boga apostoł Chrystusa Jezusa, do świętych, którzy są (w Efezie)  i do wiernych w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '1', '1', 'Łaska wam i pokój od Boga, naszego Ojca, i od Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '2', '2', 'Uwielbiony (niech będzie) Bóg i Ojciec naszego Pana Jezusa Chrystusa. On z wyżyn niebieskich obsypał nas wszelkim błogosławieństwem duchowym w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '3', '3', 'W Nim wybrał nas przed stworzeniem świata, abyśmy byli święci i nieskalani w Jego obecności, w miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '4', '4', 'Przeznaczył nas za przyczyną Jezusa Chrystusa - zgodnie z postanowieniem swojej woli - dla siebie, do synostwa Bożego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '5', '5', 'dla ukazania wspaniałości swojej łaski, którą obdarzył nas w Umiłowanym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '6', '6', 'W Nim, przez Jego Krew, mamy odkupienie, odpuszczenie grzechów stosownie do bogactwa Jego łaski,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '7', '7', 'której szczodrze nam udzielił wraz z pełnią mądrości i zrozumienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '8', '8', 'On pozwolił nam poznać tajemnicę swej woli zgodnie z powziętym co do Niego planem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '9', '9', 'aby go wykonać, gdy nadejdzie pełnia czasu: (aby) wszystko na nowo zjednoczyć w Chrystusie jako Głowie, i to, co w niebie, i to, co na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '10', '10', 'W Nim też jesteśmy powołani do udziału w dziedzictwie i zgodnie z zamysłem Jego woli przeznaczeni do tego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '11', '11', 'abyśmy istnieli ku chwale Jego majestatu, my którzy już dawno złożyli nadzieję w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '12', '12', 'Dzięki Niemu i wy, usłyszawszy naukę o prawdzie, radosną nowinę o waszym zbawieniu, przyjęliście wiarę i zostaliście naznaczeni pieczęcią Ducha Świętego - Obietnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '13', '13', 'On jest zadatkiem naszego dziedzictwa i przygotowuje odkupienie (ludu), który (Bóg) nabył sobie na własność, na chwałę swojego majestatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '14', '14', 'Toteż ja, usłyszawszy o waszej wierze w Pana Jezusa oraz miłości do wszystkich świętych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '15', '15', 'nie przestaję dziękować (Bogu), pamiętając o was w moich modlitwach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '16', '16', '(Modlę się), aby Bóg naszego Pana Jezusa Chrystusa, Ojciec chwały, dał wam ducha mądrości i objawienia dla Jego poznania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '17', '17', 'Niech otworzy oczy (waszego) serca, abyście wiedzieli, czym jest wasze powołanie do nadziei, czym jest bogactwo chwały Jego dziedzictwa wśród świętych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '18', '18', 'czym jest niewysłowiony ogrom Jego mocy dla nas wierzących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '19', '19', 'Jest to (ta sama) wszechwładna moc, którą okazał w Chrystusie, kiedy wskrzesił Go z martwych i w niebie po swojej prawicy posadził');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '20', '20', 'ponad wszelką zwierzchnością, władzą, mocą, panowaniem i ponad każdą istotą, jaka może być wymieniona nie tylko teraz, ale i w przyszłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '21', '21', 'Wszystko też Mu położył pod stopy, a Jego samego (postawił) ponad wszelkim stworzeniem i ustanowił Głową Kościoła,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '0', '22', '22', 'który jest Jego Ciałem, pełnią Tego, który napełnia wszystko we wszystkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '0', '0', 'I wyście umarli wskutek występków i grzechów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '1', '1', 'w których niegdyś żyliście za przykładem tego świata, według władcy sfer powietrza, to jest ducha, który teraz działa w buntownikach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '2', '2', 'Dawniej i myśmy wszyscy na równi z nimi żyli w pożądliwościach cielesnych, spełniając zachcianki ciała i zdrożnych myśli. I podobnie jak inni bylibyśmy z natury zasługującymi na gniew,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '3', '3', 'lecz Bóg w ogromie miłosierdzia i swojej wielkiej miłości, jaką nas umiłował, przywrócił nam życie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '4', '4', 'razem z Chrystusem, chociaż przez grzechy byliśmy pogrążeni w śmierci. Wasze zbawienie z łaski zostało dokonane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '5', '5', 'Wskrzesił nas z martwych w Chrystusie Jezusie i razem (z Nim) umieścił w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '6', '6', 'Tak w przyszłych wiekach na przykładzie wyświadczonej nam dobroci w Chrystusie Jezusie chciał ukazać niewyczerpane bogactwo swej łaski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '7', '7', 'Przez łaskę bowiem jesteście zbawieni mocą wiary. Nie pochodzi to z waszej zasługi, lecz z daru Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '8', '8', 'Nie z uczynków, aby się nikt nie przechwalał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '9', '9', 'Dziełem Jego jesteśmy, stworzeni w Chrystusie Jezusie dla dobrych czynów, które Bóg od dawna określił, abyśmy je spełniali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '10', '10', 'Pamiętajcie zatem, że ci, którzy uważają się za obrzezanych na skutek znaku wykonywanego ręką na ciele, nazywali niegdyś nie obrzezanymi was, którzy z urodzenia byliście poganami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '11', '11', 'W tym czasie bowiem żyliście bez Chrystusa, wykluczeni ze społeczności Izraela, bez udziału w przymierzach obietnicy, bez nadziei i bez Boga w świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '12', '12', 'Ale wy (chociaż) kiedyś byliście daleko, teraz jesteście blisko przez łączność z Chrystusem Jezusem i dzięki Jego Krwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '13', '13', 'On jest naszym pokojem. On wprowadził jedność w rozdartą ludzkość, ponieważ usunął mur, który ją odgradzał i dzielił. On w swoim ciele');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '14', '14', 'pozbawił mocy Prawo (z jego) przepisami i nakazami. Przywrócił pokój, tworząc w swojej osobie z dwóch (nieprzyjaznych) stron jednego nowego człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '15', '15', 'Obydwie strony, złączywszy w jedno ciało, przez krzyż pojednał z Bogiem i w sobie położył kres wrogości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '16', '16', 'A przyszedłszy ogłosił ewangelię pokoju wam, będącym daleko, oraz pokój tym, co są blisko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '17', '17', 'Przez Niego mamy jedni i drudzy dostęp do Ojca w jednym Duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '18', '18', 'A zatem nie jesteście już obcymi i przychodniami, ale współobywatelami świętych i domowników Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '19', '19', 'Zbudowani jesteście na fundamencie apostołów i proroków, gdzie kamieniem węgielnym jest sam Chrystus Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '20', '20', 'W nim cała budowla zespolona rozrasta się w świętą Panu świątynię,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '1', '21', '21', 'aby przez Ducha przygotować mieszkanie Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '0', '0', 'Toteż ja, Paweł, więzień Chrystusa (Jezusa), jestem wśród was, pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '1', '1', 'I słyszeliście o łasce powołania, udzielonej mi ze względu na was przez Boga:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '2', '2', 'przez objawienie została mi odsłonięta tajemnica, którą krótko opisałem powyżej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '3', '3', 'Kiedy więc to czytacie, możecie się dowiedzieć, ile wiem o tajemnicy Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '4', '4', 'Nie była ona ujawniona ludziom w minionych czasach, teraz zaś została objawiona przez Ducha Świętego apostołom i prorokom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '5', '5', 'to znaczy że poganie są współdziedzicami i współczłonkami Ciała i współuczestnikami obietnicy w Chrystusie Jezusie przez ewangelię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '6', '6', 'A ja, Paweł, stałem się sługą tej ewangelii dzięki darowi łaski Bożej, udzielonej mi przez Jego skuteczne działanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '7', '7', 'Ta łaska została dana mnie, najmniejszemu ze wszystkich świętych: mam zwiastować poganom niewyczerpane bogactwo Chrystusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '8', '8', 'wyjawić (wszystkim). na czym polega tajemnica planu (zbawienia), ukryta od wieków w Bogu, Stwórcy wszechrzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '9', '9', 'Teraz za pośrednictwem Kościoła zwierzchności i władze w przestworzach mają poznać wieloraką mądrość Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '10', '10', 'zgodnie z odwiecznym (Jego) postanowieniem, które urzeczywistnił w Chrystusie Jezusie, naszym Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '11', '11', 'W Nim i dzięki wierze w Niego możemy śmiało i z ufnością dojść (do Ojca).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '12', '12', 'Dlatego proszę was, abyście się nie załamywali z powodu prześladowań, które za was znoszę. To wasza chluba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '13', '13', 'Przeto zginam kolana przed Ojcem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '14', '14', 'od którego wywodzi swoją nazwę wszelki ród w niebie i na ziemi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '15', '15', 'żeby pozwolił wam według bogactwa swej chwały, przez Ducha swojego, umocnić w sobie wewnętrznego człowieka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '16', '16', 'aby Chrystus zamieszkał w waszych sercach przez wiarę, zakorzeniając i ugruntowując w (was) miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '17', '17', 'Wtedy będziecie mogli wraz ze wszystkimi świętymi zgłębić (swoim) umysłem Szerokość i Długość, Wysokość i Głębokość');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '18', '18', 'oraz poznać miłość Chrystusa, która przewyższa wszelką wiedzę i osiągnąć całą pełnię (darów) Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '19', '19', 'Temu, który działającą w nas mocą może uczynić daleko więcej niż to, o co prosimy lub co pojmujemy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '2', '20', '20', 'chwała w Kościele i w Chrystusie Jezusie przez wszystkie pokolenia, na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '0', '0', 'Ja, więzień w Panu, zachęcam was przeto, abyście postępowali w sposób godny powołania, do którego zostaliście wezwani:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '1', '1', 'z całą pokorą, łagodnością i cierpliwością, znosząc się nawzajem z miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '2', '2', 'Starajcie się strzec jedności ducha za pomocą więzi, którą jest pokój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '3', '3', 'Jest jedno Ciało i jeden Duch, jak jest tylko jedna nadzieja, którą daje wasze powołanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '4', '4', 'Jest tylko jeden Pan, jedna wiara, jeden chrzest;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '5', '5', 'jeden Bóg i Ojciec wszystkich, który (jest i działa) ponad wszystkimi, przez wszystkich i we wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '6', '6', 'Każdemu z nas została dana łaska według miary daru Chrystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '7', '7', 'Dlatego mówi Pismo: \'Wstępując na wysokości, powiódł z sobą jeńców, rozdał ludziom dary\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '8', '8', 'Co znaczy to, że \'wstąpił\'? Czyż nie to, że (najpierw) zstąpił na niskości ziemskie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '9', '9', 'Ten, który zstąpił, jest i Tym, który wstąpił ponad niebiosa, aby wszystko napełnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '10', '10', 'On to ustanowił jednych apostołami, innych prorokami, (jeszcze) innych ewangelistami, pasterzami i nauczycielami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '11', '11', 'aby przygotowali świętych do dzieła posługi, do budowy Ciała Chrystusowego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '12', '12', 'aż do osiągnięcia jedności w wierze i poznaniu Syna Bożego, do osiągnięcia doskonałego człowieczeństwa i wielkości na miarę Pełni Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '13', '13', 'Już nie będziemy dziećmi, które się zdają na igraszkę fal i poddają prądom nauki prowadzącej przez oszustwa i przebiegłość ludzką na manowce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '14', '14', 'Ale żyjąc według zasad miłości, będziemy dorastali pod każdym względem do Niego, czyli Chrystusa, który jest Głową.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '15', '15', 'W Nim całe Ciało zespala się i wiąże dzięki spełnianiu przez poszczególne członki wyznaczonych funkcji. Tak dokonuje się wzrost Ciała, które buduje się samo w sobie przez miłość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '16', '16', 'A więc mówię (wam) i zaklinam w Panu, abyście już dłużej nie postępowali jak poganie, którzy żyją w próżności umysłu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '17', '17', 'a przebywając w mrokach poznania, nie mają dostępu do życia Bożego z powodu trwającej w nich niewiedzy i zatwardziałości serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '18', '18', 'Zatracali oni wyczucie tego, co szlachetne, tarzają się w rozpuście, oddając się bez opamiętania wszelkiego rodzaju nieczystości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '19', '19', 'Ale wy nie tak poznawaliście Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '20', '20', 'Słyszeliście o Nim i zostaliście pouczeni zgodnie z prawdą, która jest w Nim, w Jezusie:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '21', '21', '(że trzeba) zrzucić z siebie starego człowieka z jego dawnym sposobem życia, którego zwodnicze żądze doprowadzają do upadku,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '22', '22', 'odnowić ducha i myśli,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '23', '23', 'przyoblec się w nowego człowieka, stworzonego na obraz Boga w sprawiedliwości i prawdziwej świętości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '24', '24', 'Dlatego odrzuciwszy kłamstwo, niech każdy mówi prawdę bliźniemu swemu, bo jesteście członkami jednego Ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '25', '25', 'Możecie się gniewać, ale nie grzeszcie. Słońce niech nie zachodzi nad waszym gniewem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '26', '26', 'nie dawajcie przystępu diabłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '27', '27', 'Kto kradł, niech zaprzestanie kradzieży i zabierze się do pracy, zdobywa własnym trudem pieniądze, aby mógł udzielać potrzebującemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '28', '28', 'Niech z ust waszych nie wychodzi żadne brzydkie słowo, lecz tylko dobre, potrzebne ku zbudowaniu, by przyniosło łaskę tym, którzy go słuchają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '29', '29', 'Nie zasmucajcie Ducha Świętego Boga, który was opatrzył (swoją) pieczęcią na dzień odkupienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '30', '30', 'Wszelka gorycz, wybuchowość, gniew, krzyki i przekleństwa, i jakiekolwiek zło niech u was zniknie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '3', '31', '31', 'Bądźcie dla drugich dobrzy i miłosierni. Wybaczajcie sobie nawzajem, jak i wam przebaczył Bóg w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '0', '0', 'Naśladujcie Boga jako (Jego) dzieci umiłowane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '1', '1', 'Żyjcie w miłości, jak i Chrystus umiłował nas i wydał siebie samego za nas jako dar i ofiarę na wdzięczną wonność Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '2', '2', 'A nierząd, wszelka nieczystość czy chciwość niechaj nie będą nawet wspominane wśród was, bo tak przystoi świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '3', '3', 'Podobnie bezwstyd, niedorzeczne gadanie i nieprzyzwoite żarty nie przystoją wam. Oddajcie się raczej modlitwie dziękczynnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '4', '4', 'Trzeba (wam) o tym wiedzieć, że żaden rozpustnik lub bezwstydnik, żaden chciwiec, który jest bałwochwalcą, nie ma dziedzictwa w królestwie Chrystusa i Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '5', '5', 'Nie dajcie się omamić próżnymi słowami, bo to wszystko sprowadza gniew Boży na buntowników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '6', '6', 'Nie stawajcie się ich wspólnikami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '7', '7', 'Wprawdzie niegdyś byliście ciemnością, lecz teraz jesteście światłością w Panu. Postępujecie więc jak dzieci światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '8', '8', 'A owocem światłości jest wszelka dobroć, sprawiedliwość i prawda.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '9', '9', 'Baczcie na to, co jest miłe Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '10', '10', 'I nie miejcie nic wspólnego z bezowocnymi czynami ciemności, co więcej, piętnujcie je.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '11', '11', 'Bo nawet wstyd mówić o tym, czego oni w skrytości się dopuszczają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '12', '12', 'Wszystko zaś, co jest napiętnowane, staje się jawne dzięki światłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '13', '13', 'Wszystko też, co zostaje ujawnione, staje się światłem. Dlatego (hymn) mówi: Zbudź się z uśpienia i powstań z martwych, a Chrystus cię oświeci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '14', '14', 'Baczcie zatem uważnie, jak postępujecie. Nie postępujcie nierozsądnie, lecz mądrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '15', '15', 'Wykorzystujcie każdą sposobną chwilę, bo nadeszły złe dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '16', '16', 'Dlatego też nie bądźcie bezmyślni, lecz starajcie się zrozumieć, jaka jest wola Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '17', '17', 'Nie upijajcie się winem, bo to prowadzi do rozwiązłości, lecz napełnieni Duchem (Świętym)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '18', '18', 'odmawiajcie wspólnie psamly, hymny i pieśni duchowe. Śpiewajcie Panu z całego serca pieśń pochwalną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '19', '19', 'Zawsze za wszystko dziękujcie Bogu i Ojcu w imię naszego Pana, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '20', '20', 'Bądźcie podlegli sobie nawzajem w bojaźni Chrystusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '21', '21', 'Żony swoim mężom jak Panu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '22', '22', 'gdyż mąż jest dla żony głową, jak Chrystus jest Głową dla Kościoła, On, Zbawiciel Ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '23', '23', 'Jak Kościół podległy Chrystusowi, tak niech żony będą podległe we wszystkim mężom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '24', '24', 'Mężowie, miłujcie (wasze) żony, jak Chrystus umiłował Kościół i wydał zań samego siebie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '25', '25', 'aby go uświęcić, oczyściwszy przez obmycie wodą, połączone ze słowem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '26', '26', 'aby przygotować sobie Kościół chwalebny, bez skazy, bez jakichkolwiek uchybień, lecz święty i nieskalany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '27', '27', 'Mężowie powinni miłować swoje żony jak własne ciało. Kto miłuje swoją żonę, sam siebie miłuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '28', '28', 'Bo nikt nigdy nie miał w nienawiści swojego ciała, ale żywi je i troszczy się o nie jak Chrystus o Kościół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '29', '29', 'My zaś jesteśmy członkami Jego Ciała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '30', '30', 'Dlatego też \'mężczyzna opuści ojca i matkę, przyłączy się do swojej żony i tak tych dwoje staną się jednym ciałem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '31', '31', 'Wielka to tajemnica! Ja to mówię w odniesieniu do Chrystusa i do Kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '4', '32', '32', 'A zatem każdy z was niech miłuje swoją żonę, jak siebie samego, a żona niech odnosi się ze czcią do swojego męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '0', '0', 'Dzieci, bądźcie posłuszne swoim rodzicom ze względu na Pana, gdyż tego wymaga sprawiedliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '1', '1', '\'Czcij swojego ojca i swoją matkę\' - to pierwsze przykazanie, które łączy się z obietnicą:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '2', '2', '\'abyś długo żył na świecie i aby ci dobrze się powodziło\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '3', '3', 'Wy zaś, ojcowie, nie stwarzajcie okazji do złego u swoich dzieci, lecz wychowujcie je w duchu karności i posłuszeństwa wobec Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '4', '4', 'Niewolnicy, bądźcie posłuszni (tym), którzy są waszymi panami w sprawach doczesnych. (Odnoście się do nich) z szacunkiem i bojaźnią, w prostocie swego serca, jak do Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '5', '5', 'Nie służcie im dla oka i dla ludzkiej pochwały, lecz jako niewolnicy Chrystusa, którzy świadomie pełnią wolę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '6', '6', 'Służcie z życzliwością jako Panu, a nie ludziom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '7', '7', 'Bądźcie przekonani. że każdy, czy to niewolnik, czy wolny, jeśli czyni dobrze, otrzyma od Pana nagrodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '8', '8', 'Wy zaś, panowie, tak samo odnoście się do nich i zaniechajcie groźby, świadomi tego, że tak ich, jak wasz Pan jest w niebie, a On nie ma względu na osoby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '9', '9', 'W końcu umacniajcie się w Panu siłą Jego wielkiej potęgi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '10', '10', 'Włóżcie na siebie zbroję Bożą, abyście mogli przeciwstawić się zakusom diabła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '11', '11', 'Nie walczymy z ludźmi, ale ze zwierzchnościami, władzami, rządcami świata ciemności i złymi duchami w przestworzach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '12', '12', 'Dlatego włóżcie na siebie pełną zbroję Bożą, abyście mogli w złym dniu stawić opór, a pokonawszy wszystko, ostać się zwycięsko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '13', '13', 'Stańcie więc (do walki) przepasawszy swoje biodra prawdą, włożywszy pancerz sprawiedliwości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '14', '14', 'a na nogi obuwie dla gotowości (niesienia) ewangelii o pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '15', '15', 'W każdej sytuacji osłaniajcie się tarczą wiary, dzięki której będziecie mogli ochronić się przed wszystkimi ognistymi pociskami przeciwnika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '16', '16', 'Weźcie przyłbicę zbawienia i miecz Ducha, to znaczy słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '17', '17', 'Przy wszelkiej sposobności, w każdej prośbie o pomoc módlcie się do Ducha (Świętego). I tak czuwajcie z całą wytrwałością w modlitwie błagalnej za wszystkich świętych');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '18', '18', 'i za mnie, aby dana mi była możność otwarcia ust do głoszenia nauki, do jawnego obwieszczenia radosnej nowiny,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '19', '19', 'dla której sprawuję poselstwo jako więzień, abym mógł ją głosić jawnie, jak do tego jestem zobowiązany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '20', '20', 'Chciałbym też, abyście wiedzieli, co dzieje się ze mną i co porabiam. Odpowie wam tu Tychik, umiłowany brat i wierny sługa w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '21', '21', 'Wysłałem go, aby was powiadomił o moich sprawach i pokrzepił was na duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '22', '22', 'Braciom pokój i miłość z wiarą od Boga Ojca i naszego Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'eph', '5', '23', '23', 'Łaska z wszystkimi, którzy wiernie miłują naszego Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '0', '0', 'Paweł i Tymoteusz, słudzy Chrystusa Jezusa, do wszystkich świętych w Chrystusie Jezusie, zamieszkałych w Filippi, wraz z biskupami i diakonami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '1', '1', 'Łaska wam i pokój od Boga, Ojca naszego, i Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '2', '2', 'Ilekroć was wspominam, dziękuję Bogu mojemu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '3', '3', 'za was wszystkich w każdej z radością odmawianej modlitwie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '4', '4', 'za wasz udział w szerzeniu ewangelii od pierwszego dnia aż do chwili obecnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '5', '5', 'Stąd żywię nadzieję, że ten, który zapoczątkował w was dobre dzieło, doprowadzi je do końca na dzień Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '6', '6', 'Słusznie mogę tak o was wszystkich myśleć, ponieważ kocham was za to, że uczestniczycie w udzielonej mi łasce, jaką jest dźwiganie kajdan, obrona i umacnianie ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '7', '7', 'Bóg mi świadkiem, że miłość Chrystusa Jezusa wyzwala we mnie serdeczne uczucia dla was wszystkich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '8', '8', 'Przeto modlę się, aby miłość wasza z każdym dniem wzrastała');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '9', '9', 'dla głębszego poznania i oceny tego, co lepsze. A wtedy w dniu Chrystusa Jezusa czystość wasza zajaśnieje jak słońce, będziecie bez skazy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '10', '10', 'obfitujący w owoc sprawiedliwości, (nabyty) dzięki Jezusowi Chrystusowi na chwałę i cześć Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '11', '11', 'Bracia, chciałby, abyście wiedzieli, że to co mnie spotkało, przyczyniło się raczej do rozszerzenia ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '12', '12', 'Wieść bowiem o moim uwięzieniu dla  Chrystusa rozeszła się po całym pretorium i wśród wszystkich innych ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '13', '13', 'Dlatego wielu z braci, ośmielonych moimi kajdanami dla Pana, ze wzmożoną odwagą głosi słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '14', '14', 'Niektórzy wprawdzie powodowani zazdrością i chęcią rywalizacji,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '15', '15', 'pozostali jednak z dobrej woli zwiastują Chrystusa. (Czynią to) i z miłości, bo wiedzą, że jestem powołany do obrony ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '16', '16', 'Tamci natomiast przepowiadają Chrystusa nieszczerze. Kierowani niezdrową ambicją sądzą, że w ten sposób jeszcze bardziej dokuczą mi w więzieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '17', '17', 'Ale cóż z tego? Byleby tylko, obłudnie czy szczerze, we wszelki sposób głoszono Chrystusa. Z tego się raduję. I będę się radował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '18', '18', 'Wiem bowiem, że to dzięki waszej modlitwie błagalnej i pomocy Ducha Jezusa Chrystusa przyczyni się do mojego zbawienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '19', '19', 'Za tym tęsknię i mam nadzieję, że w niczym nie doznam zawodu, ale jak dotąd tak i teraz Chrystus będzie jawnie uwielbiony w moim ciele czy to przez życie, czy przez śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '20', '20', 'Dla mnie bowiem życiem jest Chrystus, a śmierć zyskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '21', '21', 'Jeśli jednak życie umożliwiłoby mi owocną pracę, nie wiedziałbym, co wybrać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '22', '22', 'Nęci mnie jedno i drugie. Pragnę rozstać się z życiem i być z Chrystusem, co byłoby lepsze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '23', '23', 'chociaż pozostanie przy życiu byłoby ze względu na was bardziej pożyteczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '24', '24', 'Stąd  jestem przekonany, że będę żył nadal i pozostanę wśród wszystkich dla waszego dalszego rozwoju i radości z wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '25', '25', 'Tak, kiedy znów do was przybędę, będziecie we mnie mieli powód do chluby w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '26', '26', 'Tylko postępujcie w sposób godny ewangelii Chrystusa, bym mógł bądź to, gdy do was przybędę - zobaczyć, bądź to, gdy pozostanę z dala, usłyszeć, że jesteście jednej myśli i wspólnymi siłami walczycie o wiarę w ewangelię,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '27', '27', 'nie pozwalając się przeciwnikom w niczym zastraszyć. To jest właśnie dla nich zapowiedzią zatracenia, a dla was znakiem zbawienia (danym) przez Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '28', '28', 'Wam bowiem została udzielona łaska, że możecie nie tylko wierzyć w Chrystusa, ale i dla Niego cierpieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '0', '29', '29', 'Toczycie bowiem tę samą (co ja) walkę. Dawniej patrzyliście na nią, a teraz o niej słyszycie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '0', '0', 'Jeśli ma znaczenie zachęta (w imię) Chrystusa, pociecha płynąca z miłości, wspólnota w Duchu, serdeczne współczucie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '1', '1', 'dopełnijcie mojej radości, bądźcie jednomyślni i miejcie te same pragnienia, tę samą miłość, wspólnotę ducha, wspólne dążenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '2', '2', 'Nie czyńcie niczego dla niezdrowego współzawodnictwa czy pustej chwały, lecz w pokorze stawiajcie innych wyżej od siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '3', '3', 'Niech każdy ma na uwadze nie tylko własne sprawy, ale i innych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '4', '4', 'Postępujcie względem siebie na wzór Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '5', '5', 'On to, istniejąc w naturze Bożej, nie skorzystał ze sposobności, aby być na równi z Bogiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '6', '6', 'lecz sam siebie poniżył, przyjąwszy naturę sługi. Stał się podobny do ludzi i w zewnętrznej postaci uznany za człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '7', '7', 'Uniżył samego siebie, stał się posłuszny aż do śmierci i to śmierci krzyżowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '8', '8', 'Dlatego też Bóg wywyższył Go ponad wszystko i darował Mu imię, które jest ponad wszelkie imię,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '9', '9', 'aby na imię Jezusa zginało się każde kolano w niebie, na ziemi i pod ziemią,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '10', '10', 'i aby wszelki język wyznawał, że Jezus Chrystus jest Panem ku chwale Boga Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '11', '11', 'Umiłowani moi, ponieważ zawsze byliście posłuszni, nie tylko wtedy, kiedy byłem z wami, ale jeszcze bardziej teraz pod moją nieobecność, zabiegajcie usilnie z lękiem i drżeniem o własne zbawienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '12', '12', 'Bóg bowiem jest sprawcą waszych pragnień i czynów wedle (swego) upodobania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '13', '13', 'Czyńcie wszystko bez szemrania czy wahania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '14', '14', 'abyście będąc bez zarzutu i winy, stali się nienagannymi dziećmi Bożymi wśród zepsutego i przewrotnego ludu, dla którego jesteście tym, czym światło w świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '15', '15', 'Trzymajcie się mocno Słowa Życia, abym mógł chlubić się w dniu Chrystusa, że nie biegałem na próżno i nie na darmo się trudziłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '16', '16', 'A jeśli nawet i krew moja ma być dolana do ofiary i świętej posługi około waszej wiary, jestem szczęśliwy i cieszę się razem z wami wszystkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '17', '17', 'Wy także się cieszcie i dzielcie radość ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '18', '18', 'Mam nadzieję w Panu Jezusie, że wkrótce wyślę do was Tymoteusza, abym dowiedziawszy się, co u was (słychać), mógł być spokojniejszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '19', '19', 'Nie mam bowiem poza nim nikogo, kto by się ze mną tak zgadzał, a zarazem tak szczerze troszczył się o was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '20', '20', 'Bo wszyscy zabiegają o swoje sprawy, a nie o Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '21', '21', 'Wiecie, że on już przeszedł próbę, bo jak syn ojcu służył mi w krzewieniu ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '22', '22', 'Mam nadzieję, że będę mógł go do was posłać zaraz, jak tylko zorientuję się w moim położeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '23', '23', 'Ufam też w Panu, że ja sam wkrótce przybędę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '24', '24', 'Poza tym uznałem, że powinienem odesłać wam Epafrodyta, mojego brata, który dzielił ze mną trud pracy i walki, waszego wysłannika, tego, który służył mi pomocą w moich potrzebach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '25', '25', 'Stęsknił się bowiem za wami wszystkimi i zadręczał myślą, że dowiedzieliście się o jego chorobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '26', '26', 'Bo rzeczywiście tak się rozchorował, że był już bliski śmierci. Bóg jednak zlitował się nie tylko nad nim, ale i nade mną, abym nie miał jednego smutku po drugim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '27', '27', 'Dlatego też tym spieszniej go wysyłam, żebyście, ujrzawszy go, ucieszyli się znowu; ja zaś będę mniej strapiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '28', '28', 'Przyjmijcie go zatem w Panu z całą radością i miejcie takich (jak on) w poszanowaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '1', '29', '29', 'Bo dla sprawy Chrystusa był bliski śmierci i naraził swoje życie, aby zastąpić was w ofiarnej posłudze, której sami nie mogliście wypełnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '0', '0', 'Na koniec, bracia moi, radujcie się w Panu. Powtarzanie tego nie jest dla mnie uciążliwe, a was umacnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '1', '1', 'Strzeżcie się psów, strzeżcie się złych pracowników, strzeżcie się fałszywych obrzezańców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '2', '2', 'Bo prawdziwie obrzezani to my jesteśmy, którzy pod wpływem Ducha czcimy Boga i chlubimy się w Chrystusie Jezusie, a nie pokładamy ufności w ciele.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '3', '3', 'Ja wprawdzie mógłbym pokładać ufność w ciele. Jeśli bowiem komuś się wydaje, że może swoją ufność pokładać w ciele, to ja tym bardziej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '4', '4', 'Obrzezany zostałem ósmego dnia, jestem z rodu Izraela, z pokolenia Beniamina, Hebrajczyk z Hebrajczyków, w stosunku do Prawa byłem faryzeuszem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '5', '5', 'a pod względem gorliwości prześladowcą Kościoła, w wypełnianiu legalnej sprawiedliwości - bez zarzutu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '6', '6', 'Ale to wszystko, co miało dla mnie wartość, ze względu na Chrystusa uznaję za bezwartościowe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '7', '7', 'I naprawdę uważam za stratę to wszystko w porównaniu z najwyższą wartością, jaką jest poznanie Chrystusa Jezusa, mojego Pana. Właśnie dla Niego wyzbyłem się tego wszystkiego i uważam to za śmieci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '8', '8', 'byle bym tylko mógł zdobyć Chrystusa i w Nim się znaleźć, nie mając mojej sprawiedliwości (płynącej) z Prawa, ale tę,  którą daje wiara w Chrystusa - Bożą, opartą na wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '9', '9', 'Dzięki niej poznam zarówno Jego, jak i moc Jego zmartwychwstania, będę miał udział w Jego cierpieniach (w nadziei), że upodobniając się do Niego w śmierci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '10', '10', 'dojdę do chwalebnego powstania z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '11', '11', 'Nie dlatego, żebym już to zdobył i osiągnął doskonałość, lecz staram się ze wszystkich sił pochwycić Chrystusa Jezusa, tak jak ja (przez Niego) zostałem pochwycony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '12', '12', 'Bracia, ja nie myślę, że już to osiągnąłem. Ale jedno czynię: zapominając o tym, co poza mną, usilnie podążam ku temu, co przede mną,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '13', '13', 'biegnę naprzód po nagrodę w niebie, dokąd wzywa Bóg w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '14', '14', 'Przeto wszyscy doskonali taką przyjmijmy postawę, a jeśli co z tego inaczej pojmujecie, Bóg wam i to odsłoni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '15', '15', 'Trzeba wytrwać przy tym, co już osiągnęliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '16', '16', 'Bracia, bądźcie moimi naśladowcami, uważnie patrzcie na tych, którzy idą za wzorem, jaki w nas macie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '17', '17', 'Bo wielu postępuje tak - o czym wam już nieraz mówiłem, a teraz ze łzami powtarzam - jak wrogowie krzyża Chrystusowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '18', '18', 'Ich końcem zatracenie, bogiem - brzuch, a chwała ich w tym, czego winni się wstydzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '2', '20', '20', 'który przemieni nasze ciało poniżone i uczyni je podobnym do swego ciała uwielbionego swoją mocą, mogącą poddać wszystko pod Jego panowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '0', '0', 'Przeto, bracia moi umiłowani i utęsknieni, radości moja i chwało, trwajcie niewzruszenie w Panu, ukochani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '1', '1', 'Napominam Ewodię i napominam Syntychę, aby były jednomyślne w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '2', '2', 'Proszę także i ciebie, wierny Sydzygu, wspieraj je, bo trudziły się dla ewangelii razem ze mną, z Klemensem i innymi moimi współpracownikami, których imiona są w księdze życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '3', '3', 'Radujcie się zawsze w Panu. Powtarzam raz jeszcze: radujcie się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '4', '4', 'Skromność wasza niech będzie znana wszystkim ludziom. Pan jest blisko!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '5', '5', 'O nic przesadnie się nie troskajcie, lecz wszystkie wasze potrzeby polecajcie Bogu w modlitwie błagalnej (połączonej) z dziękczynieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '6', '6', 'A pokój Boży, który przewyższa wszelki umysł, niech strzeże w Chrystusie Jezusie serc i myśli waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '7', '7', 'Wreszcie, bracia, niech myśli wasze zwracają się ku temu wszystkiemu, co prawdziwe, godne, sprawiedliwe, czyste, miłe i chlubne, co cnotliwe i zasługujące na pochwałę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '8', '8', 'Czyńcie to, czegoście się nauczyli, co przyjęliście, coście usłyszeli i zobaczyli u mnie, a Bóg pokoju będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '9', '9', 'Bardzo się ucieszyłem w Panu, że znowu rozkwitła wasza miłość w trosce o mnie. Zawsze staraliście się o to, ale nie mieliście ku temu sposobności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '10', '10', 'Nie mówię tego bynajmniej z powodu niedostatku. Nauczyłem się bowiem przestawać na tym, co mam.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '11', '11', 'Potrafię żyć w nędzy i umiem żyć w dostatku. Zakosztowałem w życiu wszystkiego: wiem, co znaczy być sytym i głodnym, opływać w dostatki i cierpieć niedostatek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '12', '12', 'Wszystko mogę w tym, który mnie umacnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '13', '13', 'W każdym razie dobrze uczyniliście, przychodząc mi z pomocą w udręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '14', '14', 'Sami przecież wiecie, moi Filipianie, że w początkach ewangelizacji, gdy wyszedłem z Macedonii, poza wami żaden (inny) Kościół nie brał udziału w moim rozrachunku: daję - biorę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '15', '15', 'Już nawet do Tesaloniki raz po raz przysyłaliście mi pomoc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '16', '16', 'Wspominam o tym nie po to, żebym pragnął daru, myślę o obfitej zapłacie, jaką zapisano na wasze dobro.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '17', '17', 'Teraz bowiem mam wszystko, i to w obfitości: jestem dobrze zaopatrzony otrzymawszy od was przez Epafrodyta wdzięczną wonność, ofiarę miłą i podobającą się Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '18', '18', 'Bóg mój wspaniałomyślnie zaspokoi wszelką waszą potrzebę w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '19', '19', 'Bogu i Ojcu naszemu chwała na wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '20', '20', 'Pozdrówcie każdego świętego w Chrystusie Jezusie. Pozdrawiają was bracia, którzy są ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '21', '21', 'Pozdrawiają was wszyscy święci, zwłaszcza ci z domu Cezara.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'php', '3', '22', '22', 'Łaska Pana Jezusa Chrystusa z wami wszystkimi. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '0', '0', 'Paweł, z woli Bożej apostoł Chrystusa Jezusa, i Tymoteusz, brat (nasz),');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '1', '1', 'do świętych i wiernych w Chrystusie braci w Kolosach. Łaska wam i pokój od Boga, Ojca Naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '2', '2', 'Nieustannie zanosimy za was modlitwę dziękczynną do Boga, Ojca naszego Pana, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '3', '3', 'Usłyszeliśmy bowiem o waszej wierze w Chrystusa Jezusa i miłości, jaką okazujecie wszystkim świętym,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '4', '4', 'oczekując (spełnienia się) nadziei w niebie. Słyszeliście już o niej przedtem, kiedy wam głoszono prawdziwą ewangelię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '5', '5', 'Podobnie jak na całym świecie, tak i u was owocuje ona i rozszerza się od tego dnia, w którym usłyszeliście o niej i poznaliście łaskę Boga w prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '6', '6', 'Zostaliście jej uczniami dzięki umiłowanemu naszemu współpracownikowi Epafrasowi, wiernemu słudze Chrystusa, który działa w naszym imieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '7', '7', 'On też ukazał nam miłość, jaką Duch w was  roznieca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '8', '8', 'Dlatego też i my od dnia, w którym to usłyszeliśmy, nie poprzestajemy modlić się za was i prosić (Boga), abyście w pełni poznali Jego wolę z całą mądrością i zrozumieniem duchowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '9', '9', 'Abyście postępowali w sposób godny Pana i we wszystkim podobali się Jemu, przynosząc owoc wszelkich dobrych uczynków i wzrastając w poznaniu Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '10', '10', 'Jego chwalebna moc obdarzy was ogromną siłą, tak że będziecie mieć wielką cierpliwość i stałość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '11', '11', 'Z radością będziecie dziękować Ojcu, który was przysposobił do udziału w dziedzictwie świętych, to jest (do udziału) w światłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '12', '12', 'On to nas wyrwał spod władzy ciemności i przeniósł do królestwa swego umiłowanego Syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '13', '13', 'W Nim mamy odkupienie - odpuszczenie grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '14', '14', 'On jest obrazem Boga niewidzialnego. Pierworodnym wszelkiego stworzenia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '15', '15', 'bo w Nim zostało wszystko stworzone i to, co w niebie, i to, co na ziemi, co widzialne i niewidzialne. Trony, władztwa, zwierzchności, potęgi. Wszystko zostało stworzone przez Niego i dla Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '16', '16', 'On jest przed wszystkim i wszystko w Nim istnieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '17', '17', 'On jest też Głową Ciała - Kościoła, On jest Początkiem, On pierwszy spośród umarłych narodził się do życia, aby On sam we wszystkim miał pierwszeństwo,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '18', '18', 'bo spodobało się (Bogu), by w Nim zamieszkała wszelka Pełnia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '19', '19', 'aby przez Niego, przez Jego Krew przelaną na krzyżu, przywrócić (dawny) pokój: temu, co na ziemi, i temu, co w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '20', '20', 'U wyście niegdyś byli obcymi i nieprzyjaciółmi (Boga) w waszych myślach i złych uczynkach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '21', '21', 'Ale teraz pojednał (On was ze sobą) przez śmierć Syna w ludzkim ciele, abyście mogli stanąć przed Nim święci, nieskalani i nienaganni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '22', '22', 'jeżeli tylko będziecie trwali mocno i niewzruszenie w wierze i będziecie mieli niezachwianą nadzieję, którą daje przyniesiona przeze mnie ewangelia, głoszona wszystkim ludziom na świecie. Ja, Paweł, jestem jej sługą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '23', '23', 'Teraz raduję się z utrapień, które znoszę za was, oraz z tego, że ja sam w sobie przez nie dopełniam cierpień Chrystusa dla dobra Jego Ciała, którym jest Kościół.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '24', '24', 'Jego sługą jestem na mocy powierzonego mi przez Boga włodarstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '25', '25', 'Mam przekazać wam całą pełnię słowa Bożego. Tajemnica dla (dawnych) wieków i pokoleń zakryta, teraz została objawiona Jego świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '26', '26', 'Im Bóg zechciał oznajmić, co za bogactwo chwały zawiera dla pogan ta tajemnica. Jest nią Chrystus pośród nas, nadzieja chwały!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '27', '27', 'Jego my zwiastujemy, nauczając każdego człowieka i napominając z całą mądrością, by go doprowadzić do doskonałości w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '0', '28', '28', 'Dla tego celu trudzę się i walczę, wsparty Jego mocą, która silnie we mnie działa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '0', '0', 'Pragnę bowiem, abyście wiedzieli, jak wielki bój staczam o was, o tych, co są w Laodycei, a również i tych, którzy nie poznali mnie osobiście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '1', '1', 'Oby nabrali otuchy i zjednoczeni w miłości doszli do całego bogactwa i pełnego zrozumienia, do głębszego poznania tajemnicy Boga - Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '2', '2', 'W Nim są wszystkie skarby mądrości i poznania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '3', '3', 'Mówię to, abyście nie dali się zwieść nikomu fałszywymi dowodami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '4', '4', 'Bo chociaż nieobecny ciałem, duchem jestem z wami i raduję się, znając wasz ład i stałość waszej wiary w Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '5', '5', 'Przyjęliście Chrystusa Jezusa (jako swego) Pana, działajcie też w łączności z Nim:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '6', '6', 'zakorzenieni w Nim i wbudowani w Niego umacniajcie się w wierze, zgodnie z tym, czegoście się nauczyli, i nieustannie składajcie dziękczynienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '7', '7', 'Uważajcie, aby ktoś z was nie został zwabiony w niewolę (błędu) przez filozofię lub chytry podstęp, wywodzące się z ludzkich podań o siłach kosmicznych, a nie z Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '8', '8', 'W Nim bowiem, w Jego ciele mieszka cała Pełnia Bóstwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '9', '9', 'A wy, dzięki łączności z Nim, uczestniczycie w tej Pełni. Jest On Głową wszelkiej zwierzchności i potęgi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '10', '10', 'W Nim też otrzymaliście obrzezanie, nie z ludzkiej ręki, lecz obrzezanie Chrystusowe (polegające) na wyzbyciu się ciała zmysłowego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '11', '11', 'Z Nim jesteście pogrzebani przez chrzest. Z Nim też razem zostaliście wskrzeszeni przez wiarę w moc Boga, który wskrzesił Go z martwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '12', '12', 'Wespół z Nim i was umarłych z powodu grzechów i waszego nie obrzezanego ciała przywrócił do życia. Wspaniałomyślnie odpuścił nam wszystkie grzechy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '13', '13', 'wymazując bezpowrotnie obciążający nas skrypt dłużny. To właśnie, co przemawiało przeciwko nam, usunął przygwoździwszy do krzyża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '14', '14', 'Przez Niego rozbroił zwierzchności i potęgi, na publiczne wystawił pośmiewisko, ciągnąc je w swoim triumfalnym pochodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '15', '15', 'Niechaj nikt nie odważa się krytykować was czy to z powodu pokarmu i napoju, czy też z powodu święta, nowiu księżyca lub szabatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '16', '16', 'To są tylko cienie rzeczy przyszłych, a rzeczywistością jest Ciało Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '17', '17', 'Niechaj nikt, kto lubuje się w fałszywej pokorze lub przesadnej czci aniołów, opierając się na (rzekomych) widzeniach, nie odsądza was od nagrody, pyszniąc się bez powodu swoim przyziemnym sposobem myślenia -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '18', '18', 'nie podporządkowuje się (wtedy) Głowie. A Jej całe Ciało zawdzięcza pożywienie i powiązanie w całość, dzięki zespalającym członkom wzrasta, (sprawcą zaś tego) wzrostu jest Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '19', '19', 'Jeśli tedy razem z Chrystusem umarliście dla żywiołów świata, dlaczego - jak byście jeszcze żyli w świecie - pozwalacie sobie narzucać takie zakazy:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '20', '20', '\'Nie dotykaj, nie jedz, nie ruszaj\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '21', '21', 'Czyż te wszystkie rzeczy zgodnie z ludzkimi nakazami i naukami nie są (przeznaczone) na spożycie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '1', '22', '22', 'Przepisy te mają tylko pozory mądrości na skutek czy to przeceniania własnego pomysłu, czy poniżania samego siebie, czy surowego umartwiania ciała, ale nie posiadają żadnego znaczenia dla opanowania żądzy zmysłów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '0', '0', 'Jeśli razem z Chrystusem powstaliście z martwych, szukajcie tego, co w górze, gdzie jest Chrystus siedzący po prawicy Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '1', '1', 'Zdążajcie do tego, co w górze, a nie do tego, co na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '2', '2', 'Albowiem umarliście i wasze życie jest ukryte z Chrystusem w Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '3', '3', 'A kiedy Chrystus - nasze życie ukaże się, wtedy i wy razem z Nim zajaśniejecie w chwale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '4', '4', 'Uśmiercajcie zatem w ciele waszym to, co jest przyziemne: rozpustę, nieczystość, lubieżność, złą pożądliwość i żądze nadmiernego posiadania, która jest bałwochwalstwem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '5', '5', 'To właśnie ściąga karząca sprawiedliwość Boga na zbuntowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '6', '6', 'Wy także niegdyś wiedliście podobne życie, kiedyście żyli w tych (grzechach).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '7', '7', 'Lecz teraz wyrzućcie daleko od siebie to wszystko: gniew, niechęć, złość, bluźnierstwo, a nieprzyzwoite słowa niech niekalają waszych ust.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '8', '8', 'Nie okłamujcie się wzajemnie, zwlekliście przecież z siebie \'starego człowieka\' z jego uczynkami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '9', '9', 'a przyoblekliście się w \'nowego człowieka\', który odnawia się ustawicznie ku głębszemu poznaniu Tego, co go stworzył na swoje podobieństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '10', '10', 'A tam już nie ma Greka ni Żyda, obrzezanego i nie obrzezanego, nie ma barbarzyńcy ni Scyty, niewolnika ani wolnego, lecz wszystkim we wszystkich jest Chrystus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '11', '11', 'Przeto jako wybrańcy Boga, święci i umiłowani, obleczcie się w tkliwe miłosierdzie, dobroć, pokorę, łagodność i cierpliwość,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '12', '12', 'znosząc jedni drugich i wybaczając sobie wzajemnie, jeśliby ktoś zawinił przeciwko wam. Jak Pan darował wam, tak i wy innym darujcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '13', '13', 'A ponad wszystko kierujcie się miłością, która jest spójnią (wszelkich) cnót.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '14', '14', 'Niech w sercach waszych panuje pokój Chrystusowy, do którego zostaliście powołani, by tworzyć jedno Ciało. I składajcie dziękczynienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '15', '15', 'Niech słowo Chrystusa obfituje w was: nauczajcie i napominajcie jedni drugich z prawdziwą mądrością. Śpiewajcie Bogu z całego serca i z wdzięcznością psalmy, hymny i natchnione pieśni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '16', '16', 'Cokolwiek czynicie lub mówicie, czyńcie wszystko w imię Pana Jezusa, dziękując przez Niego Bogu Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '17', '17', 'Żony, bądźcie poddane (swoim) mężom, jak to przystoi w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '18', '18', 'Mężowie, miłujcie (wasze) żony i nie bądźcie dla nich przykrymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '19', '19', 'Dzieci, bądźcie we wszystkim posłuszne rodzicom, gdyż to jest miłe Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '20', '20', 'Ojcowie, nie bądźcie zbyt surowi dla swoich dzieci, by nie upadały na duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '21', '21', 'Niewolnicy, bądźcie posłuszni swoim panom ziemskim. Wypełniajcie swoją służbę nie dla oka, aby pozyskać ich ludzkie względy, lecz ze szczerego serca i z bojaźni Pańskiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '22', '22', 'Co czynicie, czyńcie szczerze ze względu na Pana, a nie na ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '23', '23', 'Wiecie przecież, że Pan da wam w nagrodę dziedzictwo wieczne. Służcie więc Chrystusowi - Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '2', '24', '24', 'Kto bowiem czyni bezprawie, poniesie karę za bezprawie. On nie ma względu na osobę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '0', '0', 'Panowie, oddajcie niewolnikom to, co sprawiedliwe i słuszne, wiedząc, że i wy macie Pana w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '1', '1', 'Bądźcie wytrwali w modlitwie, która podtrzymuje czujność i (pobudza) do dziękczynienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '2', '2', 'Módlcie się też za nas, aby Bóg dał nam możność głoszenia nauki o tajemnicy Chrystusowej, dla której aż do tej pory pozostaje więźniem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '3', '3', 'abym ją obwieścił zgodnie z moim obowiązkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '4', '4', 'Z niechrześcijanami postępujcie mądrze, wykorzystując dla ich pozyskania stosowną chwilę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '5', '5', 'Niech wasza mowa będzie zawsze serdeczna, treściwa, uwzględniająca umiejętność udzielenia każdemu właściwej odpowiedzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '6', '6', 'Tychik, umiłowany brat, wierny sługa i współpracownik w Panu, opowie wam wszystko o mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '7', '7', 'Posyłam go do was właśnie po to, abyście się dowiedzieli, co się z nami dzieje, i aby podniósł was na duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '8', '8', 'Dodaję mu wiernego i umiłowanego brata Onezyma, który jest waszym rodakiem. Oni doniosą wam o wszystkim, co jest tutaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '9', '9', 'Pozdrawia was Arystarch, dzielący ze mną więzienie za tę samą sprawę, i Marek, kuzyn Barnaby, co do którego otrzymaliście polecenia, aby go przyjąć, gdy do was zawita,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '10', '10', 'a także Jezus zwany Justusem. To są jedyni Żydzi, którzy trudzili się ze mną dla królestwa Bożego i stali się moją pociechą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '11', '11', 'Pozdrawia was Epafras, wasz krajan, sługa Chrystusa (Jezusa), który nieustannie oręduje za wami w modlitwie, abyście niewzruszenie trwali w doskonałości i we wszystkim zdawali się całkowicie na wolę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '12', '12', 'Oświadczam, że jest przejęty głęboką troską o was i o tych, co są w Laodycei i Hierapolis.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '13', '13', 'Macie także pozdrowienia od Łukasza, kochanego lekarza, i od Demasa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '14', '14', 'Pozdrówcie braci w Laodycei oraz Nimfasa i Kościół gromadzący się w jego domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '15', '15', 'Kiedy już list ten zostanie u was przeczytany, postarajcie się, aby zapoznał się z nim Kościół w Laodycei. Wy zaś przeczytajcie list do Laodycei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '16', '16', 'Archipowi powiedzcie: Uważaj, abyś wypełnił posługę, którą przyjąłeś od Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'col', '3', '17', '17', 'Własnoręcznie dopisuję pozdrowienie od siebie - Paweł. Pamiętajcie o moich kajdanach. Łaska z wami!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '0', '0', 'Paweł, Sylwan i Tymoteusz przesyłają pozdrowienia Kościołowi Tesaloniczan w Bogu Ojcu i Panu Jezusie Chrystusie: Łaska wam i pokój.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '1', '1', 'Składamy Bogu nieustanne dzięki za was wszystkich, wspominając was w naszych modlitwach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '2', '2', 'Pamiętamy ciągle przed Bogiem i Ojcem naszym o dziełach, jakich dokonuje wasza wiara, o waszej czynnej miłości i wytrwałej nadziei w Panu naszym Jezusie Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '3', '3', 'Wiemy też, bracia umiłowani przez Boga, że należycie do Jego wybranych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '4', '4', 'Ewangelię naszą głosiliśmy wam nie tylko słowami, lecz również mocą Ducha Świętego i z głębokim przekonaniem. Wiecie przecież, jakimi byliśmy dla was, gdyśmy u was przebywali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '5', '5', 'A wy, przyjmując słowo w ucisku wielkim, z radością Ducha Świętego, zostaliście naśladowcami naszymi i naśladowcami Pana do tego stopnia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '6', '6', 'że staliście się wzorem dla wszystkich wierzących w Macedonii i Achai.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '7', '7', 'Dzięki wam słowo Pańskie rozeszło się nie tylko po Macedonii i Achai, ale wieść o waszej wierze w Boga dotarła wszędzie, tak że nie trzeba nawet o tym mówić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '8', '8', 'Ludzie bowiem sami opowiadają, jakiego przyjęcia doznaliśmy u was i jak, porzuciwszy bożków, zwróciliście się do Boga, żeby służyć Bogu żywemu i prawdziwemu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '0', '9', '9', 'i oczekiwać przyjścia z niebios Jego Syna, którego Bóg wskrzesił z martwych, Jezusa. On nas obroni przed mającym nadejść gniewem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '0', '0', 'Bo sami wiecie o tym, bracia, że nasze przybycie do was nie było bezowocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '1', '1', 'Po cierpieniach i zniewagach doznanych w Filippi - o czym wam wiadomo - zaufawszy Bogu odważyliśmy się głosić wam, pomimo trudności, ewangelię Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '2', '2', 'Nauczanie nasze nie opiera się na błędzie. Nie kieruje nim nieczysta pobudka ani podstęp,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '3', '3', 'lecz tak głosimy ewangelię, jak Bóg, który nas uznał godnymi tego, nam ją powierzył, nie aby przypodobać się ludziom, lecz Bogu badającemu nasze serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '4', '4', 'Nigdy przecież nie posługiwaliśmy się - jak wiecie - pochlebstwami ani nie kierowaliśmy się ukrytą chciwością. Bóg tego świadkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '5', '5', 'Nie szukaliśmy też sławy u ludzi - ani u was, ani u innych; chociaż mogliśmy występować z całą powagą apostołów Chrystusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '6', '6', 'to jednak byliśmy dla was łagodni jak matka, która troskliwie opiekuje się swoimi dziećmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '7', '7', 'Kochając was tak bardzo, pragnęliśmy dać wam nie tylko ewangelię Bożą, lecz i nasze życie, ponieważ staliście się nam drodzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '8', '8', 'Pamiętacie przecież, bracia, nasz trud i mozoły, jak pracując we dnie i w nocy głosiliśmy wam ewangelię Bożą, żeby nikomu z was nie być ciężarem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '9', '9', 'I Bóg, i wy jesteście świadkami, jak święcie, sprawiedliwe i nienagannie żyliśmy wśród was, którzyście uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '10', '10', 'Wiecie i to, że prosiliśmy, zachęcaliśmy i zaklinaliśmy was tak, jak ojciec swoje dzieci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '11', '11', 'żebyście postępowali w sposób godny Boga, który was wzywa do swego królestwa i swojej chwały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '12', '12', 'Dlatego też nieustannie dziękujemy Bogu, że usłyszawszy słowo Boże, przez nas głoszone, przyjęliście je nie jak słowo ludzkie, lecz - jak jest w rzeczywistości - jak słowo Boże. Ono też działa w was, którzyście uwierzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '13', '13', 'Wy zaś, bracia, staliście się naśladowcami Kościołów w Bogu na terenie Judei, złączonych z Jezusem Chrystusem, ponieważ cierpieliście to samo ze strony współmieszkańców, co one ze strony Żydów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '14', '14', 'którzy zabili Pana Jezusa i proroków, a nas prześladowali. Są oni i Bogu niemili, i wszystkim ludziom przeciwni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '15', '15', 'Zabraniają nam głosić naukę o zbawieniu, w ten sposób nieustannie dopełniają miary swoich grzechów. Ale spadł na nich srogi gniew Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '16', '16', 'Bracia, my zaś pozbawieni na krótko waszego widoku, lecz nie rozłączeni sercem, bardzo tęsknimy za waszą obecnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '17', '17', 'Dlatego pragnęliśmy przybyć do was, zwłaszcza ja, Paweł, i to dwukrotnie, lecz szatan nam w tym przeszkodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '18', '18', 'Któż bowiem, jeżeli nie wy, jest naszą nadzieją, radością i wieńcem chwały,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '1', '19', '19', 'z którego dumni będziemy przed Panem naszym Jezusem w chwili Jego przyjścia?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '0', '0', 'Ponieważ nie mogliśmy dłużej tego znosić, postanowiliśmy sami pozostać w Atenach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '1', '1', 'a Tymoteusza, brata naszego i sługę Bożego w głoszeniu ewangelii Chrystusowej, wysłaliśmy z poleceniem, aby was umocnił i zachęcił do wytrwania w waszej wierze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '2', '2', 'tak aby się nikt nie zachwiał w obecnych udrękach. Sami bowiem wiecie, że do tego jesteśmy wezwani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '3', '3', 'Już wtedy, gdy byliśmy wśród was, zapowiadaliśmy, że będziemy cierpieć udręki, tak też - jak wiecie - się stało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '4', '4', 'Nie mogąc dłużej tego znosić, posłałem go do was, aby zasięgnąć wiadomości o wierze waszej, czy przypadkiem nie zwiódł was kusiciel i czy nasz trud nie poszedł na marne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '5', '5', 'Dopiero co przybył od was Tymoteusz i przyniósł nam dobre nowiny o waszej wierze i miłości wzajemnej i o tym, że zawsze macie nas we wdzięcznej pamięci i tęsknicie za naszym widokiem, podobnie jak my za waszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '6', '6', 'Wy i wasza stałość w wierze są nam pociechą w naszych przeciwnościach i udręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '7', '7', 'Ożyliśmy bowiem na wieść, że mocno stoicie przy Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '8', '8', 'Jakże mamy dziękować Bogu za was i za całą radość, jakiej z waszego powodu przed Bogiem naszym doznajemy?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '9', '9', 'We dnie i w nocy modlimy się gorąco, żebyśmy mogli zobaczyć was osobiście i dopełnić tego, czego brakuje waszej wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '10', '10', 'Drogę do was niech nam utoruje sam Bóg, nasz Ojciec, i Pan nasz Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '11', '11', 'Niech też Pan napełnia was coraz większą miłością do siebie nawzajem i do wszystkich ludzi, taką miłością, jaką ja was miłuję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '2', '12', '12', 'Niech też utwierdzi wasze serca w nienagannej świętości przed Bogiem i Ojcem naszym na powtórne przyjście Pana naszego Jezusa, razem ze wszystkimi Jego świętymi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '0', '0', 'Bracia, w końcu więc prosimy was i zaklinamy w Panu Jezusie, abyście tak postępowali, Bogu się podobając, jak was pouczaliśmy. Tak więc postępujcie, a będziecie wzrastali w doskonałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '1', '1', 'Znacie bowiem nakazy, jakie daliśmy wam od Jezusa Chrystusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '2', '2', 'Bożą wolą jest wasza świętość, wystrzegajcie się rozpusty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '3', '3', 'Niech każdy współżyje ze swoją żoną w świętości i czci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '4', '4', 'a nie ulega naporowi żądzy, jak to czynią nie znający Boga poganie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '5', '5', 'Niech nikt w tej dziedzinie nie narusza prawa bliźniego, gdyż Pan jest mścicielem tego wszystkiego, o czym was już przedtem pouczyliśmy, powołując się na Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '6', '6', 'Bóg bowiem nie powołał nas do nieczystości, ale do świętości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '7', '7', 'Kto więc odrzuca te słowa, odrzuca nakazy nie ludzi, lecz Boga, który udziela wam swego Ducha Świętego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '8', '8', 'A co do wzajemnej miłości, nie ma potrzeby wam o tym pisać, gdyż Bóg sam was pouczył, abyście się wzajemnie miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '9', '9', 'Czynicie tak przecież wobec wszystkich braci w całej Macedonii. Zachęcamy was jedynie, abyście wzrastali w doskonałości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '10', '10', 'Starajcie się usilnie wieść życie skromne, obowiązki swoje wypełniać sumiennie i pracować własnymi rękoma, jak to wam przykazaliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '11', '11', 'Wówczas w oczach ludzi spoza Kościoła postępować będziecie z godnością i nie będziecie potrzebowali niczyjej pomocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '12', '12', 'Nie chcemy zaś, bracia, abyście nie wiedzieli o losie waszych zmarłych; nie smućcie się jak ci, którzy nadziei nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '13', '13', 'Jeżeli bowiem wierzymy, że Jezus umarł i zmartwychwstał, to musimy wierzyć, że Bóg przyprowadzi także zmarłych razem z Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '14', '14', 'A mówimy to wam na podstawie słowa Pana, że my, żywi i pozostawieni na powtórne przyjście Pana, nie uprzedzimy zmarłych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '15', '15', 'że na dany znak, na głos archanioła i dźwięk trąby Bożej, sam Pan zstąpi z nieba, a zmarli w Chrystusie powstaną pierwsi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '16', '16', 'Następnie my, pozostawieni wśród żyjących, razem z nimi zostaniemy uniesieni w przestworza, na obłoki naprzeciwko Pana, i tak już na zawsze z Panem pozostaniemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '3', '17', '17', 'Tą więc nauką pocieszajcie się wzajemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '0', '0', 'Nie potrzeba też, bracia, żebym wam podał dokładnie dzień i godzinę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '1', '1', 'Sami nazbyt dobrze wiecie o tym, że dzień Pański nadejdzie tak jak złodziej w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '2', '2', 'Kiedy więc mówić będą: \'Pokój i bezpieczeństwo\', nagle spadnie na nich zagłada, jak bóle na rodzącą, i nie ujdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '3', '3', 'Lecz wy, bracia nie przebywacie w ciemnościach, aby ten dzień miał was zaskoczyć jak złodziej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '4', '4', 'Wy wszyscy bowiem jesteście synami dnia i światłości. Nie należymy więc ani do nocy, ani do ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '5', '5', 'Przeto nie śpijmy jak inni, ale czuwajmy i bądźmy trzeźwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '6', '6', 'Bo ci, którzy śpią, śpią nocą, ci zaś, którzy się upijają, są w nocy pijani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '7', '7', 'Lecz my należymy do dnia, przywdziejmy więc pancerz wiary i miłości, włóżmy hełm zbawiennej nadziei i bądźmy trzeźwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '8', '8', 'Bóg nie chce naszej zagłady, lecz zbawienia w Panu naszym Jezusie Chrystusie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '9', '9', 'który za nas umarł, abyśmy, czy żywi, czy umarli, razem z Nim żyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '10', '10', 'Dlatego zachęcajcie się wzajemnie i budujcie jedni drugich dobrym przykładem, jak to zresztą czynicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '11', '11', 'Prosimy was, bracia, poważajcie swoich przełożonych, którzy się trudzą i napominają was w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '12', '12', 'Otaczajcie ich szczególną miłością ze względu na ich pracę. Żyjcie ze sobą w pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '13', '13', 'Zachęcamy was, bracia, upominajcie niekarnych, pocieszajcie małodusznych, słabych podtrzymujcie na duchu, a dla wszystkich bądźcie cierpliwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '14', '14', 'Czuwajcie nad tym, aby jeden drugiemu złem za zło nie odpłacał, lecz zawsze dobro wyświadczajcie sobie nawzajem, jak i wszystkim ludziom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '15', '15', 'Zawsze miejcie radość w sercu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '16', '16', 'nieustannie się módlcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '17', '17', 'Dziękujcie Bogu za wszystko, bo taka jest wola Boża, przekazana nam przez Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '18', '18', 'Ducha nie gaście,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '19', '19', 'daru proroctwa nie lekceważcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '20', '20', 'Sprawdzajcie wszystko, a zachowujcie to, co szlachetne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '21', '21', 'Unikajcie wszystkiego, co złe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '22', '22', 'A sam Bóg pokoju niech was w pełni uświęca. Niech wasz duch i dusza, i ciało bez najmniejszej skazy zostaną zachowane aż do powtórnego przyjścia Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '23', '23', 'Ten, który was wzywa, dochowuje wierności i dokona tego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '24', '24', 'Bracia, módlcie się także za nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '25', '25', 'Pozdrówcie wszystkich braci świętym pocałunkiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '26', '26', 'Zaklinam was na Pana, niech list ten będzie odczytany wszystkim braciom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1th', '4', '27', '27', 'Łaska Pana naszego Jezusa Chrystusa niech będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '0', '0', 'Paweł, Sylwan i Tymoteusz przesyłają pozdrowienia Kościołowi Tesaloniczan w Bogu, Ojcu naszym, i Panu Jezusie Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '1', '1', 'Łaska wam i pokój od Boga Ojca i Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '2', '2', 'Słuszną jest rzeczą, bracia, abyśmy nieustannie składali Bogu dzięki za was, gdyż wiara wasza bardzo wzrasta i pogłębia się miłość wzajemna,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '3', '3', 'tak że chlubimy się w Kościołach Bożych waszą wytrwałą wiarą i cierpliwością, z jaką znosicie wszystkie przeciwności i uciski');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '4', '4', 'z myślą o sprawiedliwym sądzie Bożym, który uzna was za godnych królestwa Bożego, dla którego cierpicie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '5', '5', 'Będzie to sprawiedliwe, że Bóg odpłaci uciskiem tym, którzy was uciskają,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '6', '6', 'a wam, uciśnionym, da odpoczynek razem z nami w dniu objawienia, gdy Pan Jezus zstąpi z nieba w płomieniach ognia, z aniołami, zwiastunami swej mocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '7', '7', 'Wymierzy On zapłatę tym, którzy nie chcą uznać Boga i nie są posłuszni ewangelii Pana naszego Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '8', '8', 'Poniosą oni karę wiecznego odrzucenia od oblicza Pana i od Jego potężnego majestatu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '9', '9', 'w dniu, w którym przyjdzie, aby być uwielbiony w swoich świętych i podziwiany przez wszystkich wierzących w Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '10', '10', 'A wy uwierzyliście naszemu świadectwu i dlatego nieustannie modlimy się za was. Niech Bóg nasz uczyni was godnymi powołania i swoją mocą spełni wszystkie wasze dobre pragnienia - dzieło wiary waszej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '0', '11', '11', 'Aby było w was uwielbione imię Pana naszego Jezusa Chrystusa, a wy w Nim, za łaską Boga naszego i Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '0', '0', 'Bracia! W sprawach dotyczących przyjścia Pana naszego Jezusa Chrystusa i naszego z Nim spotkania prosimy was,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '1', '1', 'nie dajcie się wyprowadzić zbyt łatwo z równowagi ducha ani nie trwóżcie się z powodu jakiegoś objawienia prorockiego, nauczania lub przypisywanego nam listu, jakoby już nadchodził dzień Pański.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '2', '2', 'Niech was nikt w żaden sposób nie wprowadza w błąd. Bo najpierw musi dojść do odstępstwa i objawi się człowiek grzechu, syn zatracenia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '3', '3', 'przeciwnik, wynoszący się ponad wszystko, co nazwane jest Bogiem lub odbiera cześć Boską. Zasiądzie on nawet w świątyni Boga dowodząc, że sam jest Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '4', '4', 'Czy nie pamiętacie, że wam mówiłem o tym, przebywając jeszcze z wami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '5', '5', 'Wiecie przecież o tej przeszkodzie, która nie pozwala mu działać, aż czas jego nadejdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '6', '6', 'Nieprawość już potajemnie rozwija swoją działalność, najpierw jednak musi ustąpić ten, który ją powstrzymuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '7', '7', 'Wtedy wystąpi jawnie ów gwałciciel prawa, ale Pan Jezus powali go tchnieniem ust swoich, zniszczy go w chwili swego przyjścia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '8', '8', 'Działalności jego towarzyszyć będą za sprawą szatana cuda kłamliwe, znaki i dziwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '9', '9', 'Tych, którzy nie umiłowali prawdy mającej ich zbawić, zwiedzie wszelkimi pokusami zła ku ich zgubie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '10', '10', 'Bóg dopuści, że omamieni błędem wciągnięci zostaną w sidła i dadzą wiarę kłamstwu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '11', '11', 'Tak wszyscy, którzy nie uwierzyli prawdzie, lecz upodobali sobie zło, zostaną potępieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '12', '12', 'My zaś powinniśmy nieustannie składać Bogu dzięki, umiłowani przez Pana bracia, że Bóg wybrał was pierwszych do zbawienia przez Ducha Świętego i wierność prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '13', '13', 'Bo wybrał was przez nasze głoszenie ewangelii do osiągnięcia chwały Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '14', '14', 'Trwajcie więc, bracia, niewzruszenie w nauce, którą otrzymaliście czy przez ustne nauczanie, czy to przez nasz list.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '15', '15', 'A sam Pan nasz Jezus Chrystus i Bóg, Ojciec nasz, który nas ukochał i obdarzył nas wieczną radością i ufną nadzieją,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '1', '16', '16', 'niech krzepi wasze serca i umocni w każdym dobrym czynie i słowie');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '0', '0', 'Poza tym, bracia, módlcie się za nas, aby nauka Pańska rozszerzała się i zdobywała uznanie, jak to było i u was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '1', '1', 'Módlcie się, abyśmy zostali uwolnieni od ludzi przewrotnych i złośliwych, nie wszyscy bowiem wierzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '2', '2', 'Trzeba zawierzyć Panu, który was utwierdzi i zachowa od zła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '3', '3', 'Mamy do was zaufanie w Panu, że postępujecie i będziecie postępowali zgodnie z tym, co wam głosiliśmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '4', '4', 'A Pan niech serca wasze skłoni do miłości Boga i cierpliwości Chrystusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '5', '5', 'Bracia! W imię Pana naszego Jezusa Chrystusa nakazujemy wam, unikajcie każdego brata, który narusza porządek i nie postępuje zgodnie z podaną wam przez nas nauką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '6', '6', 'Sami bowiem wiecie, jak nas macie naśladować; nie wzbudzaliśmy wśród was niepokoju');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '7', '7', 'ani u nikogo nie jedliśmy chleba za darmo, lecz pracowaliśmy dniem i nocą w trudzie i mozole, aby nikomu z was nie być ciężarem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '8', '8', 'Nie dlatego, żebyśmy nie mieli do tego prawa, lecz aby dać wam przykład do naśladowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '9', '9', 'Tak wam bowiem nakazywaliśmy, będąc u was: kto nie chce pracować, niech też i nie je!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '10', '10', 'Słyszymy, że niektórzy z was oddają się próżniactwu, zamiast pracować tracą czas na błahostki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '11', '11', 'Tych surowo napominamy w Panu Jezusie Chrystusie, żeby spokojnie pracując własny chleb jedli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '12', '12', 'Wy zaś, bracia, nie zniechęcajcie się w spełnianiu dobrych uczynków,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '13', '13', 'a jeśli ktoś nie posłucha słów naszego listu, zapamiętajcie go sobie i nie szukajcie jego towarzystwa, niech się zawstydzi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '14', '14', 'Lecz nie uważajcie go za wroga, ale upominajcie jak brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '15', '15', 'Sam Pan pokoju niech da wam pokój zawsze i wszędzie. Niech Pan będzie z wami wszystkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '16', '16', 'Ręką moją Pawłową  kreślę pozdrowienie. To jest mój znak w każdym liście. Tak piszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2th', '2', '17', '17', 'Łaska Pana naszego Jezusa Chrystusa z wami wszystkimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '0', '0', 'Paweł, apostoł Chrystusa Jezusa z rozkazu Boga, Zbawiciela naszego, i Chrystusa Jezusa, nadziei naszej,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '1', '1', 'do Tymoteusza, swego rodzonego dziecka w wierze: łaska, miłosierdzie i pokój od Boga Ojca i Chrystusa Jezusa. Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '2', '2', 'Wybierając się do Macedonii, poleciłem ci pozostać w Efezie. Miałeś napomnieć niektórych, aby nie nauczali inaczej niż my,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '3', '3', 'by nie zajmowali się baśniami i rodowodami bez końca, które prowadzą raczej do sporów, a nie służą zbawczemu działaniu Bożemu, polegającemu na wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '4', '4', 'Celem tego napominania jest miłość, płynąca z czystego serca, dobrego sumienia i wiary nieobłudnej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '5', '5', 'Niektórzy, nie troszcząc się o to, zagubili się w pustej gadaninie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '6', '6', 'Chcieliby uchodzić za nauczycieli Prawa, a nie rozumieją dobrze tego, co mówią, ani tego, przy czym się upierają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '7', '7', 'Wiemy przecież, że Prawo jest dobre, jeśli je ktoś stosuje zgodnie z duchem Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '8', '8', 'Wiadomo, że Prawo nie występuje przeciwko człowiekowi sprawiedliwemu, ale przeciw łamiącemu Prawo i niesfornym, bezbożnym i grzesznikom, świętokradcom i bluźniercom, ojcobójcom i matkobójcom, przeciw mordercom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '9', '9', 'rozpustnym zboczeńcom, handlującym ludźmi, kłamcom, krzywoprzysięzcom i w ogóle przeciw temu wszystkiemu, co sprzeciwia się zdrowej nauce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '10', '10', 'Zawiera ją dobra nowina, głosząca chwałę błogosławionego Boga. Mnie ją powierzono.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '11', '11', 'Dzięki składam Temu, który mnie umocnił, Chrystusowi Jezusowi, naszemu Panu, za to, że obdarzył mnie zaufaniem wyznaczając do posługiwania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '12', '12', 'Chociaż przedtem byłem bluźniercą i okrutnym prześladowcą, to jednak dostąpiłem miłosierdzia, bo działałem w nieświadomości, nie mając wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '13', '13', 'Taki był bezmiar łaski Pana naszego, a z nią zaufania i miłości, która jest w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '14', '14', 'Godna to wiary nauka i warta, aby ją wszyscy przyjęli: Chrystus Jezus przyszedł na świat zbawić grzeszników, a pierwszym z nich jestem ja.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '15', '15', 'Dostąpiłem jednak miłosierdzia po to, żeby na mnie pierwszym Chrystus Jezus mógł pokazać całą swą wspaniałomyślność, abym był przykładem dla tych, którzy mają w Niego uwierzyć i osiągnąć życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '16', '16', 'Królowi Wieków, nieśmiertelnemu i niewidzialnemu, jedynemu Bogu cześć i chwała na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '17', '17', 'Oto napomnienie, jakie przekazuję ci, Tymoteuszu, dziecko moje, zgodnie z głoszonymi o tobie proroctwami. Wsparty nimi stawaj dzielnie w boju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '18', '18', 'Zachowaj wiarę i czyste sumienie, bo niektórzy, nie dbając o nie, utracili też wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '0', '19', '19', 'Są między nimi Hymenajos i Aleksander: oddałem ich szatanowi, niech oduczą się bluźnić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '0', '0', 'Polecam więc przede wszystkim zanosić błagania, modlić się, prosić i dzięki czynić za wszystkich ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '1', '1', 'za królów i wszystkich piastujących władzę, żebyśmy mogli żyć spokojnie i cicho w wielkiej pobożności i uczciwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '2', '2', 'Oto co jest dobre i miłe w oczach Zbawiciela naszego, Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '3', '3', 'który chce, aby wszyscy ludzie byli zbawieni i doszli do poznania prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '4', '4', 'Bo jeden jest Bóg, jeden też pośrednik między Bogiem a ludźmi, człowiek - Chrystus Jezus.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '5', '5', 'Dał On siebie samego na okup za wszystkich. Oto świadectwo dla tych właśnie czasów;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '6', '6', 'Jego głosicielem i apostołem ja zostałem ustanowiony - prawdę mówię, nie kłamię - nauczającym (wszystkie) narody prawdziwej wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '7', '7', 'Chcę więc, żeby mężczyźni modlili się na każdym miejscu, wznosząc czyste ręce, bez gniewu i kłótni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '8', '8', 'Tak samo, żeby strój kobiety był przyzwoity, aby ubierały się one skromnie i prosto. Nie w wyszukane fryzury i złoto, nie w perły i kosztowne szaty,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '9', '9', 'ale w dobre czyny, jak przystoi kobietom naprawdę pobożnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '10', '10', 'Kobieta niech przyjmuje pouczenie spokojnie i ulegle.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '11', '11', 'Nie pozwalam natomiast kobiecie ani nauczać, ani przewodzić mężowi; niech żyje w cichości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '12', '12', 'Przecież pierwszy został stworzony Adam, a potem Ewa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '13', '13', 'I nie Adam został zwiedzony, ale kobieta dała się zwieść i popełniła przestępstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '1', '14', '14', 'Zbawi się zaś przez macierzyństwo, byleby tylko wytrwała w wierze, miłości, uświęcaniu się i skromności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '0', '0', 'Godna to wiary nauka: ten, kto ubiega się o biskupstwo, pragnie podjąć się pięknego zadania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '1', '1', 'A zatem - biskupem ma być człowiek bez zarzutu, mąż jednej żony, trzeźwy. skromny, przyzwoity, gościnny, przykładny,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '2', '2', 'nie upijający się, nie awanturnik, ale człowiek łagodny. niekłótliwy, niechciwy pieniędzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '3', '3', 'Niech umie dobrze kierować własnym domem, utrzymując dzieci w posłuszeństwie i wszelkiej przyzwoitości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '4', '4', 'Bo kto własnym domem nie umie kierować, jakżeż potrafi dbać o Kościół Boży?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '5', '5', 'Nie może to być człowiek świeżo nawrócony, by go nie zaślepiła pycha i by nie popadł w diabelskie potępienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '6', '6', 'Powinien też mieć dobre świadectwo ze strony tych, którzy nie należą do nas, żeby nie dostał się na ludzkie języki i w sidła diabelskie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '7', '7', 'Diakonami tak samo mają być ludzie przyzwoici, dotrzymujący słowa, nie nadużywający wina ani też niechciwi brudnego zysku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '8', '8', 'Niechaj tajemnicę wiary noszą w czystym sumieniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '9', '9', 'Trzeba ich najpierw wypróbować, potem dopiero niech posługują jako diakoni, będąc bez zarzutu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '10', '10', 'Kobiety tak samo mają być przyzwoite, nie obmawiające innych, trzeźwe i wierne we wszystkim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '11', '11', 'Diakoni niech będą mężami jednej żony, niech dobrze kierują dziećmi i własnymi domami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '12', '12', 'Bo ci, którzy dobrze sprawują posługę diakona, zdobywają sobie zaszczytny stopień oraz pełną ufność w wierze, którą oparli na Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '13', '13', 'Piszę ci to, chociaż wkrótce spodziewam się przybyć do ciebie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '14', '14', 'żebyś - gdybym się opóźniał - wiedział, jak trzeba postępować w domu Bożym, który jest Kościołem Boga żywego, filarem i podporą prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '2', '15', '15', 'Bo z całą oczywistością ujawnia się wielka tajemnica pobożności: Objawił się nam jako człowiek, Duch Go potwierdził, ukazał się aniołom, ogłoszony został narodom, świat w Niego wierzy, do nieba został wzięty w chwale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '0', '0', 'Duch wyraźnie mówi o tym, że w czasach ostatnich niektórzy odstąpią od wiary, ulegając zwodniczym duchom i naukom szatańskim,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '1', '1', 'przez obłudę kłamców, którzy swoje własne sumienie naznaczyli piętnem występku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '2', '2', 'Zabraniają zawierania małżeństw, przyjmowania pokarmów, które Bóg stworzył na to, by spożywali je z dziękczynieniem ci, którzy wierzą, i poznali prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '3', '3', 'Bo wszystko, co Bóg stworzył, jest dobre, i nie trzeba niczego odrzucać, co spożywa się z dziękczynieniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '4', '4', 'Poświęcone jest przecież przez słowo Boże i modlitwę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '5', '5', 'Przedstawiając to braciom, staniesz się dobrym sługą Chrystusa Jezusa, karmionym słowami wiary i dobrej nauki, za którą poszedłeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '6', '6', 'Zwalczaj bezbożne i niedorzeczne bajki.                                                                                                  Zachęta do pobożności                                                                                                                       Ćwicz się w pobożności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '7', '7', 'Albowiem ćwiczenie ciała nie na wiele się przydaje, pobożność zaś mająca obietnicę życia teraźniejszego i przyszłego zawsze ma wielką wartość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '8', '8', 'Godna to wiary nauka i warta, aby ją wszyscy przyjęli:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '9', '9', 'Dlatego trudzimy się i walczymy, ponieważ całą nadzieję pokładamy w Bogu żywym, który jest zbawicielem wszystkich ludzi, a zwłaszcza wierzących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '10', '10', 'Tak napominaj i nauczaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '11', '11', 'Niech nikt nie lekceważy twojego młodego wieku. Bądź raczej wzorem dla wierzących - w mowie, w zachowaniu, w miłości, w wierze, w czystości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '12', '12', 'Dopóki nie przybędę, przykładaj się do czytania, napominania i nauczania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '13', '13', 'Nie zaniedbuj w sobie daru, który zgodnie z proroctwem został ci dany przez włożenie rąk prezbiterów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '14', '14', 'Staraj się o to z całkowitym oddaniem, aby twój postęp był dla wszystkich widoczny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '3', '15', '15', 'Pilnuj siebie samego i pilnuj nauczania, bądź w tym wytrwały. Czyniąc tak zbawisz i siebie, i tych, którzy ciebie słuchają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '0', '0', 'Starszego człowieka nie upominaj surowo, ale proś go jak ojca, młodszych - jak braci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '1', '1', 'starsze kobiety - jak matki, młodsze - jak siostry, z największą skromnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '2', '2', 'Wdowy otaczaj szacunkiem, te, które rzeczywiście są wdowami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '3', '3', 'A jeśli jakaś wdowa ma dzieci albo wnuki, to niech najpierw uczą się wypełniać obowiązki wobec własnej rodziny, odwdzięczać się swoim rodzicom - bo to jest miłe w oczach Bożych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '4', '4', 'Wdowa, która rzeczywiście jest wdową, samotna, pokłada ufność w Bogu i nie szczędzi czasu na błagalne modlitwy we dnie i w nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '5', '5', 'Ta zaś, która źle się prowadzi, umarła za życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '6', '6', 'Tak więc napominaj, aby były bez zarzutu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '7', '7', 'Jeśli zaś ktoś nie stara się o swoich, zwłaszcza o członków rodziny, to zaparł się wiary i gorszy jest od niewierzącego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '8', '8', 'Za wdowę można uznać kobietę, mającą przynajmniej sześćdziesiąt lat, żonę jednego męża,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '9', '9', 'znaną z dobrych uczynków, która wychowała dzieci, udzielała gościny, umywała nogi świętym, śpieszyła nieszczęśliwym z pomocą i korzystała z każdej sposobności czynienia dobrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '10', '10', 'Młodszym zaś wdowom odmawiaj przyjęcia do grona tamtych. Kiedy bowiem lekceważą swoje zobowiązania wobec Chrystusa, chcą wychodzić za mąż');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '11', '11', 'i stają się winne złamania pierwszego przyrzeczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '12', '12', 'Przy tym uczą się próżniactwa, krążąc po domach. I nie tylko próżnują, ale, będąc gadatliwe i ciekawe, rozpowiadają to, czego się nie godzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '13', '13', 'Chcę więc, aby młodsze wychodziły za mąż, były matkami, zajmowały się domem i w ten sposób nie dawały przeciwnikowi powodu do rzucania potwarzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '14', '14', 'Już niektóre bowiem zeszły na manowce za szatanem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '15', '15', 'Jeśli któraś z wierzących ma w rodzinie wdowy, niech się nimi zajmuje; niech Kościół będzie wolny od tego ciężaru, aby mógł zająć się tymi, które rzeczywiście są wdowami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '16', '16', 'Prezbiterzy, którzy dobrze kierują wspólnotą, zasługują na podwójną część, zwłaszcza ci, którzy dźwigają cały ciężar głoszenia słowa i nauczania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '17', '17', 'Mówi przecież Pismo: \'Nie zawiążesz pyska wołowi młócącemu\' i \'Godzien jest rolnik swojej zapłaty\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '18', '18', 'Nie przyjmuj skargi przeciw prezbiterowi, chyba że na podstawie zeznań dwóch albo trzech świadków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '19', '19', 'Tych, którzy grzeszą, upominaj wobec wszystkich, żeby inni również przejmowali się bojaźnią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '20', '20', 'Zaklinam cię na Boga i Chrystusa Jezusa, i na wybranych aniołów, żebyś trzymał się tego bez zastrzeżeń i zachował bezstronność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '21', '21', 'Rąk na nikogo pochopnie nie wkładaj i nie stawaj się współwinnym grzechów, które inni popełniają. Siebie samego czystym zachowaj!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '22', '22', 'Nie pij samej wody, ale używaj po trosze wina ze względu na twój żołądek i częste niedomagania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '23', '23', 'Przestępstwa jednych ludzi są jawne, znane jeszcze przed wyrokiem sądu, a inne dopiero po wyroku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '4', '24', '24', 'Tak samo i dobre czyny są jawne, a te, które nimi nie są, ukryć się nie mogą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '0', '0', 'Wszyscy, którzy dźwigają jarzmo niewolnictwa, niech uważają swoich panów za godnych wszelkiej czci, żeby nikt nie bluźnił imieniu Boga i nauce Chrystusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '1', '1', 'Ci zaś, którzy mają panów wierzących, niechaj ich nie lekceważą z tego powodu, że są to ich bracia. Niechaj raczej tym lepiej im służą, bo wierzącymi i ukochanymi braćmi są ci, którym usługują.                                                                                                                                                     Pożytki z prawdziwej pobożności                                                                                                     Tego nauczaj i to nakazuj!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '2', '2', 'Jeśli ktoś naucza inaczej i nie postępuje według zbawiennych słów Pana naszego Jezusa Chrystusa oraz nauki zgodnej z pobożnością,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '3', '3', 'to zaślepia go pycha. Niczego nie rozumie, cierpi na manię spierania się i walki o słowa. Z tego pochodzą zawiść, gniew, przekleństwa, złośliwe podejrzenia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '4', '4', 'ciągłe kłótnie ludzi o przewrotnym usposobieniu, którzy zagubili poczucie prawdy i myślą, że z pobożności można zrobić źródło zysku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '5', '5', 'Wielkim zaś zyskiem jest pobożność, umiejąca poprzestać na tym, co konieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '6', '6', 'Niczego przecież nie przynieśliśmy na ten świat, niczego zeń wynieść nie potrafimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '7', '7', 'Mając więc pokarm i ubranie, na tym poprzestawajmy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '8', '8', 'Ci, którzy chcą się wzbogacić, wpadają w sidła pokus, ulegają wielu nierozumnym i szkodliwym pożądliwościom, które doprowadzają ludzi do zguby i zatracenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '9', '9', 'Albowiem korzeniem wszelkiego zła jest miłość pieniądza. Niektórzy jej ulegli, odeszli od wiary i narazili się na wiele cierpień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '10', '10', 'Ty zaś, człowiecze Bogu oddany, unikaj tego. Zabiegaj o sprawiedliwość, pobożność, wiarę, miłość, cierpliwość i łagodność.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '11', '11', 'Stawaj dzielnie w zawodach - w zawodach wiary. Zdobądź życie wieczne, do którego zostałeś powołany i wyznałeś uroczyście swą wiarę wobec wielu świadków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '12', '12', 'Nakazuję ci wobec Boga, który wszystko ożywia, i wobec Chrystusa Jezusa, który uroczyście dał świadectwo o sobie pod Poncjuszem Piłatem:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '13', '13', 'zachowej te przepisy ściśle i dokładnie, aż do objawienia się Pana naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '14', '14', 'On je pozwoli oglądać we właściwym czasie, błogosławiony i jedyny Władca, Król nad królującymi i Pan panujących.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '15', '15', 'Tylko On jeden jest nieśmiertelny i mieszka w niedostępnej światłości, nikt z ludzi Go nie widział i widzieć nie może. Jemu cześć i moc wiekuista. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '16', '16', 'Możnym tego świata nakazuj, żeby się nie wynosili i nie pokładali nadziei w niepewnym bogactwie, ale w Bogu, który pozwala nam korzystać z wszystkiego, co nam daje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '17', '17', 'Niech czynią dobrze, i pomnażają dobre czyny, niech będą hojni i dzielą się z drugimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '18', '18', 'Z tego położą fundament pod swoją przyszłość, tak że będą mogli osiągnąć prawdziwe życie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '19', '19', 'Tymoteuszu, strzeż powierzonego ci dobra. Unikaj czczej gadaniny i przeciwstawnych twierdzeń, które głosi rzekoma wiedza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1tm', '5', '20', '20', 'Niektórzy spodziewając się ją posiąść odpadli od wiary. Łaska niech będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '0', '0', 'Paweł, z woli Boga apostoł Chrystusa Jezusa dla głoszenia obietnicy życia, które jest w Chrystusie Jezusie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '1', '1', 'do Tymoteusza, swego umiłowanego dziecka: łaska, miłosierdzie i pokój od Boga Ojca i Chrystusa Jezusa, Pana naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '2', '2', 'Dzięki składam Bogu - któremu służę jak moi przodkowie z czystym sumieniem - gdy nieustannie wspominam ciebie w swoich modlitwach dniem i nocą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '3', '3', 'Pamiętając o twoich łzach, chciałbym ciebie zobaczyć i nacieszyć się tobą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '4', '4', 'Dobrze pamiętam twoją szczerą wiarę; mieszkała ona najpierw w sercu twojej babki, Lois, i twojej matki, Eunice, a przekonałem się, że mieszka także i w tobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '5', '5', 'Dlatego właśnie przypominam ci, żebyś na nowo rozpalił dar Boży, który otrzymałeś przez włożenie moich rąk.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '6', '6', 'Bóg bowiem nie dał nam ducha lęku, ale mocy, miłości i karności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '7', '7', 'Więc nie wstydź się świadectwa o Panu naszym ani mnie - Jego więźnia, ale wraz ze mną znoś przeciwności dla ewangelii, licząc na moc Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '8', '8', 'On nas zbawił i powołał swoim świętym wezwaniem nie ze względu na nasze czyny, ale ze względu na swoje własne postanowienie i łaskę, darowaną nam przed wiekami w Chrystusie Jezusie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '9', '9', 'a objawioną właśnie teraz przez przyjście Zbawiciela naszego, Chrystusa Jezusa. On to zniszczył śmierć, a przez ewangelię ukazał światło życia i nieśmiertelność,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '10', '10', 'ja zaś zostałem powołany na głosiciela, apostoła i nauczyciela tej dobrej nowiny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '11', '11', 'Dlatego też znoszę obecne cierpienia: ale nie wstydzę się tego, bo wiem, komu zawierzyłem, i jestem przekonany, że ma On moc przechować powierzone mi dobro aż do owego Dnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '12', '12', 'Miej za wzór zdrowe nauki, jakieś ode mnie słyszał, w wierze i miłości, którą mamy w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '13', '13', 'Strzeż tego cennego dobra z pomocą Ducha Świętego, który w nas mieszka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '14', '14', 'Wiesz o tym, że odwrócili się ode mnie wszyscy, którzy są w Azji, a wśród nich Figelos i Hermogenes.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '15', '15', 'Niech Pan okaże miłosierdzie domowi Onezyfora za to, że często mnie pokrzepiał i łańcucha mojego się nie wstydził,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '16', '16', 'ale skoro tylko przybył do Rzymu, szukał mnie wszędzie, aż wreszcie odnalazł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '0', '17', '17', 'Daj Boże, aby w owym Dniu Pan okazał mu miłosierdzie. A jak bardzo przysłużył mi się w Efezie, ty wiesz lepiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '0', '0', 'Niech łaska Chrystusa umacnia cię, moje dziecko,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '1', '1', 'a to, co usłyszałeś ode mnie - wobec wielu świadków - przekazuj ludziom godnym zaufania, którzy będą zdolni nauczać także innych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '2', '2', 'Znoś razem ze mną trudy jak dobry żołnierz Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '3', '3', 'Nikt, zaciągając się do wojska, nie zajmuje się swymi zwykłymi sprawami, jeśli chce zadowolić tego, kto go do wojska zaciągnął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '4', '4', 'I jeśli ktoś staje do zawodów, nie zdobywa wieńca, jeżeli nie walczył zgodnie z przepisami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '5', '5', 'Rolnik, który się trudzi, powinien pierwszy korzystać z plonów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '6', '6', 'Pomyśl nad tym, co mówię. A Pan ci pozwoli wszystko zrozumieć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '7', '7', 'Pamiętaj o potomku Dawida Jezusie Chrystusie, który zmartwychwstał, jak to głoszę w mojej ewangelii,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '8', '8', 'dla której jak złoczyńca znoszę cierpienia, nawet więzy. Ale słowa Bożego nie można związać!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '9', '9', 'Dlatego wszystko znoszę ze względu na wybranych, żeby i oni dostąpili zbawienia i wiecznej chwały przez Chrystusa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '10', '10', 'Godna to wiary nauka: Jeśliśmy razem z nim umarli, to i żyć razem będziemy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '11', '11', 'Jeśli wytrwamy, to i królować razem z Nim będziemy. Jeśli się Go wyprzemy, to i On się nas wyprze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '12', '12', 'A choćbyśmy nie dochowali wiary, On pozostaje wierny, bo nie może przeczyć samemu sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '13', '13', 'Przypominaj o tym, zaklinając na Boga, aby nie spierali się o słowa, bo z tego nie ma żadnego pożytku, tylko zguba dla słuchających.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '14', '14', 'Staraj się stanąć przed Bogiem jak wypróbowany pracownik, nie przynoszący wstydu, przestrzegający wiernie prawdziwej nauki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '15', '15', 'Unikaj czczej gadaniny, bo ci, którzy ją uprawiają, będą się pogrążać w coraz większej bezbożności,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '16', '16', 'a ich nauka będzie się szerzyć jak gangrena. Do takich należy Hymenajos i Filetos,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '17', '17', 'którzy odeszli od prawdy głosząc, że zmartwychwstanie już nastąpiło; i tak niszczą wiarę niektórych ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '18', '18', 'A przecież mocno stoi fundament założony przez Boga, na nim zaś taka pieczęć: \'Zna Pan tych, co doń należą\' oraz \'Niech porzuci nieprawość każdy, kto wzywa imienia Pańskiego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '19', '19', 'W wielkim domu są nie tylko naczynia złote i srebrne, ale też drewniane i gliniane; jedne do szlachetnego użytku, inne do pospolitego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '20', '20', 'Kto więc zachowa siebie czystym od tego wszystkiego, ten będzie naczyniem do użytku szlachetnego, poświęconym, miłym Panu, przygotowanym do każdego dobrego dzieła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '21', '21', 'Unikaj płochych zachcianek młodości, a zabiegaj o sprawiedliwość, wiarę, miłość i pokój z tymi, którzy czystym sercem wzywają Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '22', '22', 'Wystrzegaj się głupich i niedorzecznych dociekań - wiesz przecież, że rodzą się z nich spory.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '23', '23', 'Sługa zaś Pana nie powinien się spierać; ma być dla wszystkich uprzejmy, ma być przykładny i wyrozumiały.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '24', '24', 'Powinien łagodnie przekonywać przeciwników, bo może Bóg da im kiedyś nawrócenie i poznają prawdę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '1', '25', '25', 'wyrwą się z sideł diabła, który ujął ich żywcem i poddał swej woli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '0', '0', 'A to wiedz, że w ostatecznych czasach nastaną trudne chwile.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '1', '1', 'Ludzie bowiem będą samolubni, chciwi, chełpliwi, zarozumiali, bluźniący, nie szanujący rodziców, niewdzięczni, bezbożni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '2', '2', 'bez serca, zawzięci, oszczerczy, gwałtowni, okrutni, nienawidzący dobra,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '3', '3', 'zdradliwi, zuchwali, butni, miłujący bardziej rozkosze niż Boga;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '4', '4', 'zachowają pozory pobożności, a wyprą się tego, co jest jej siłą. Od takich trzymaj się z dala!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '5', '5', 'Z nich przecież biorą się ci, którzy wkradają się do domów i pozyskują sobie lekkomyślne kobiety, pełne grzechów i dręczone rozmaitymi pożądaniami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '6', '6', 'takie, które zawsze się uczą, a nigdy nie mogą dojść do poznania prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '7', '7', 'A jak Jannes i Jambres sprzeciwili się Mojżeszowi, tak też i oni sprzeciwiają się prawdzie. Ludzie o zaćmionym umyśle; ich wiara nie wytrzymała próby.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '8', '8', 'Ale nic już więcej nie osiągną, bo ich szaleństwo stanie się dla wszystkich widoczne, jak tamtych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '9', '9', 'Ty zaś poszedłeś za moją nauką, za moim sposobem życia, zasadami, wiarą, cierpliwością, miłością, wytrwałością,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '10', '10', 'za prześladowaniami i cierpieniami, które spotkały mnie w Antiochii, w Ikonium i w Lystrze. Wielkie zniosłem prześladowania! I ze wszystkich wyrwał mnie Pan!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '11', '11', 'Tak też wszyscy, którzy chcą żyć pobożnie w Chrystusie Jezusie, będą prześladowani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '12', '12', 'A źli ludzie, zwodziciele, sami błądząc i drugich w błąd wprowadzając, pogrążać się będą w zło coraz bardziej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '13', '13', 'Ty natomiast trwaj przy tym, czego się nauczyłeś i co uznałeś za godne wiary, bo wiesz, od kogo się nauczyłeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '14', '14', 'Również dlatego, że od dziecka, znasz Pisma święte, które mogą ci dać mądrość, prowadzącą do zbawienia przez wiarę, jaką mamy w Chrystusie Jezusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '15', '15', 'Każde Pismo jest natchnione przez Boga i przydatne do nauczania i przekonywania, do poprawiania i wychowywania w sprawiedliwości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '2', '16', '16', 'aby człowiek Bogu oddany był doskonały, przygotowany do wszelkich dobrych uczynków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '0', '0', 'Zaklinam cię na Boga i Chrystusa Jezusa, który będzie sądził żywych i umarłych, na Jego przyjście i Jego królestwo:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '1', '1', 'głoś naukę, nalegaj - w porę czy nie w porę - przekonuj, karć, napominaj z całą cierpliwością i umiejętnością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '2', '2', 'Bo przyjdzie czas, kiedy ludzie nie zechcą przyjąć zdrowej nauki, ale według własnych upodobań dobiorą sobie nauczycieli, spragnieni tego, co miłe dla ucha,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '3', '3', 'nie będą słuchać prawdy, a zwrócą się do baśni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '4', '4', 'A ty trzeźwo patrz na wszystko, znoś trudy, wykonuj zadanie głosiciela ewangelii, służbie swojej całkowicie się oddaj.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '5', '5', 'Bo ja już mam złożyć życie swoje w ofierze, nadszedł czas mojego odejścia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '6', '6', 'Stoczyłem piękną walkę, bieg ukończyłem, wiary dochowałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '7', '7', 'Teraz już czeka mnie wieniec sprawiedliwości. W owym Dniu poda mi go Pan, sprawiedliwy Sędzia, a poda nie tylko mnie, ale wszystkim, którzy umiłowali Jego przyjście.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '8', '8', 'Staraj się przybyć do mnie szybko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '9', '9', 'Bo Demas mnie opuścił, umiłowawszy ten świat, i udał się do Tesaloniki, Krescens - do Galacji, Tytus - do Dalmacji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '10', '10', 'Tylko Łukasz jest ze mną. Marka zabierz i przyprowadź z sobą, bo jest mi przydatny w posługiwaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '11', '11', 'Tychika posłałem do Efezu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '12', '12', 'Przynieś po drodze płaszcz, który zostawiłem w Troadzie u Karposa, i księgi, a zwłaszcza pergaminy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '13', '13', 'Brązownik Aleksander wyrządził mi wiele złego; odpłacił mu Pan według jego czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '14', '14', 'I ty się go strzeż, bo występował ostro przeciw naszym naukom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '15', '15', 'Podczas pierwszej mojej obrony nikogo przy mnie nie było: wszyscy mnie opuścili. Niech im to nie będzie policzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '16', '16', 'Ale Pan był przy mnie i umacniał mnie, aby przeze mnie w pełni dokonało się głoszenie ewangelii i żeby ją usłyszały wszystkie narody. Toteż zostałem wyrwany z lwiej paszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '17', '17', 'Wyrwie mnie Pan z każdej zasadzki złych ludzi i ocali dla swojego królestwa niebieskiego. Jemu chwała po wszystkie wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '18', '18', 'Pozdrów Pryskę i Akwilę oraz dom Onezyfora.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '19', '19', 'Erast został w Koryncie, a chorego Trofima zostawiłem w Milecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '20', '20', 'Postaraj się przybyć przed zimą. Pozdrawia cię Eubulos, Pudens, Linus, Klaudia i wszyscy bracia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2tm', '3', '21', '21', 'Pan z duchem twoim! Łaska z wami!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '0', '0', 'Paweł, sługa Boży, apostoł Jezusa Chrystusa, posłany, aby doprowadzić wybrańców Bożych do wiary i do poznania prawdziwej pobożności,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '1', '1', 'która spodziewa się życia wiecznego, jakie prawdomówny Bóg obiecał przed wiekami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '2', '2', 'On to we właściwym czasie objawił swoje słowo, którego głoszenie zostało mi powierzone z rozkazu naszego Zbawiciela, Boga -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '3', '3', 'do Tytusa, swojego rodzonego dziecka we wspólnej wierze: łaska i pokój od Boga Ojca i Chrystusa Jezusa, Zbawiciela naszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '4', '4', 'Zostawiłem cię na Krecie w tym celu, żebyś uporządkował resztę spraw i ustanowił w miastach prezbiterów, tak jak ci poleciłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '5', '5', 'Ma to być człowiek bez zarzutu, mąż jednej żony, którego dzieci są wierzące, którym nie można zarzucić rozwiązłości czy nieposłuszeństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '6', '6', 'Bo biskup  - włodarz Boży  - ma być bez zarzutu, nie samowolny, nie skory do gniewu, nie pijak, nie awanturnik, nie chciwy brudnego zysku,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '7', '7', 'ale gościnny, miłujący dobro, skromny, sprawiedliwy, pobożny, wstrzemięźliwy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '8', '8', 'trzymający się wiernie przekazywanej nauki, aby mógł zachęcać przez zdrowe pouczenia i przekonywać przeciwników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '9', '9', 'Wielu bowiem nie chce słuchać, mówi rzeczy próżne, wielu jest zwodzicieli, zwłaszcza spośród obrzezanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '10', '10', 'Trzeba im zamknąć usta, gdyż dla brudnego zysku doprowadzają do zguby całe domy, nauczając rzeczy niewłaściwych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '11', '11', 'Powiedział któryś z nich, własny ich wieszcz: \'Kreteńczycy  - to wieczni kłamcy, złe bestie, brzuchy leniwe\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '12', '12', 'Świadectwo to jest prawdziwe. Z tego powodu karć ich ostro, żeby pozostali zdrowi w wierze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '13', '13', 'a nie zważali na żydowskie baśnie i na to, co nakazują ludzie, którzy odwrócili się od prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '14', '14', 'Bo wszystko jest czyste dla czystych, ale dla skalanych i niewiernych nie ma nic czystego, skalany jest tak ich umysł jak i sumienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '0', '15', '15', 'Mówią, że znają Boga, a czynami temu przeczą  - są wstrętni, oporni, do żadnego czynu dobrego nie są zdolni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '0', '0', 'Ty zaś mów to, co zgodne jest ze zdrową nauką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '1', '1', 'Że starcy mają być trzeźwi, poważni, skromni, że mają się odznaczać szczerą wiarą, miłością i cierpliwością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '2', '2', 'Że starsze kobiety tak samo, mają żyć pobożnie, nie oczerniać, nie nadużywać wina, ale dawać dobry przykład.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '3', '3', 'Niech uczą młodsze, jak kochać mężów i dzieci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '4', '4', 'jak być żoną skromną, czystą, gospodarną, dzielną, uległą mężowi  - żeby nikt nie mógł bluźnić słowu Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '5', '5', 'Młodzieńców tak samo zachęcaj do karności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '6', '6', 'Pod każdym względem sam bądź wzorem dobrego postępowania przez nieskażone nauczanie, powagę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '7', '7', 'zdrową i nienaganną naukę, aby przeciwnik musiał ustąpić ze wstydem, nie mogąc o nas powiedzieć nic złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '8', '8', 'Niewolnicy niech będą we wszystkim poddani swoim panom, niech będą chętni, niech się im nie sprzeciwiają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '9', '9', 'Niechaj sobie niczego nie przywłaszczają, ale wciąż okazują  doskonałą wierność, aby nauce naszego Zbawiciela, Boga, we wszystkim mogli przynieść chlubę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '10', '10', 'Bo objawiła się łaska Boga, która zbawia wszystkich ludzi');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '11', '11', 'i uczy nas, abyśmy wyrzekli się bezbożności i ziemskich pożądań, a żyli na tym świecie skromnie, sprawiedliwie i pobożnie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '12', '12', 'oczekując z radosną nadzieją chwalebnego przyjścia wielkiego Boga i Zbawiciela naszego, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '13', '13', 'On to ofiarował siebie za nas, aby wykupić i oczyścić od wszelkiej nieprawości nas, swój lud nabyty na własność i gorliwie spełniający dobre uczynki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '1', '14', '14', 'Tak przemawiaj, zachęcaj i przekonuj z całą powagą, niechaj nikt nie śmie cię lekceważyć!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '0', '0', 'Przypominaj im, żeby poddając się władzom zwierzchnim, słuchali ich i byli gotowi do każdego dobrego czynu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '1', '1', 'żeby nikomu nie ubliżali, nie byli kłótliwi, ale łagodni i zawsze uprzejmi dla wszystkich ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '2', '2', 'Przecież i my sami byliśmy kiedyś nierozumni, nieposłuszni, błądziliśmy, będąc niewolnikami rozmaitych pożądliwości i rozkoszy, pędziliśmy życie w złości i zazdrości budząc obrzydzenie i nienawidząc się nawzajem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '3', '3', 'A kiedy objawiła się dobroć naszego Zbawiciela, Boga, i Jego miłość do ludzi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '4', '4', 'to wybawił nas nie ze względu na nasze sprawiedliwe czyny, ale dzięki swojemu miłosierdziu przez obmycie, które odradza, i przez odnowienie Duchem Świętym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '5', '5', 'Wylał go na nas obficie przez Jezusa Chrystusa, Zbawiciela naszego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '6', '6', 'abyśmy, usprawiedliwieni Jego łaską, odziedziczyli  - w co ufamy  - życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '7', '7', 'Jest to nauka godna wiary, chcę, żebyś to wciąż podkreślał: Ci, którzy uwierzyli Bogu, niech się starają usilnie spełniać dobre uczynki. To właśnie jest dobre i pożyteczne dla ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '8', '8', 'Głupich zaś dociekań i rodowodów, sporów i kłótni o Prawo unikaj, są bowiem bezużyteczne i próżne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '9', '9', 'A człowieka, który rozbija jedność  - po jednym lub drugim upomnieniu  - unikaj');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '10', '10', 'wiedząc, że jest przewrotny i grzeszny i sam na siebie wydaje wyrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '11', '11', 'Kiedy poślę do ciebie Artemasa lub Tychika, postaraj się szybko przybyć do mnie do Nikopolis, gdyż tam postanowiłem przezimować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '12', '12', 'Biegłego w Prawie Zenasa i Apollosa troskliwie zaopatrz na drogę, żeby im nic nie brakowało.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '13', '13', 'Niech i nasi (bracia) pokażą, że umieją spełniać dobre uczynki, zaspokajając konieczne potrzeby. Niech i oni mają zasługę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'tyt', '2', '14', '14', 'Pozdrawiają cię wszyscy, którzy są ze mną. Pozdrów wierzących, tych, którzy nas miłują. Łaska z wami wszystkimi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '0', '0', 'Paweł, więzień Chrystusa Jezusa. i brat Tymoteusz do naszego przyjaciela i współpracownika Filemona,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '1', '1', 'do siostry Apfii, do Archipa, który trudzi się z nami (dla ewangelii) i do Kościoła (gromadzącego się) w twoim domu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '2', '2', 'Łaska wam i pokój od Boga Ojca naszego i od Pana Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '3', '3', 'Dziękuję Bogu mojemu za każdym razem, ilekroć cię wspominam w moich modlitwach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '4', '4', 'Dowiaduję się bowiem o twojej miłości i zaufaniu, jakie żywisz do Pana Jezusa i do wszystkich świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '5', '5', 'Oby twój udział w wierze doprowadził cię do głębszego poznania wszelkiego dobra, które jest wśród nas, ku chwale Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '6', '6', 'Ucieszyłeś mnie już, bracie, i podniosłeś na duchu swoją miłością, która sprawia świętym radość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '7', '7', 'Dlatego też, mimo że mam od Chrystusa pełną władzę wymagać od ciebie tego, do czego jesteś zobowiązany,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '8', '8', 'ja Paweł, (twój) przełożony, chociaż teraz dźwigam kajdany dla Chrystusa Jezusa, wolę raczej poprosić ciebie w imię miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '9', '9', 'Proszę cię zatem w sprawie mojego syna Onezyma, dla którego w więzieniu stałem się ojcem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '10', '10', 'Dawniej nic on dla ciebie nie znaczył, a teraz znaczy wiele i dla ciebie, i dla mnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '11', '11', 'Odsyłam go, to jest serce moje, do ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '12', '12', 'Chętnie zatrzymałbym go przy sobie, aby w twoim imieniu usługiwał mi w więzieniu (w którym przebywam dla) ewangelii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '13', '13', 'Nie chciałem jednak tego uczynić bez twojej zgody, aby twój wspaniałomyślny gest był dobrowolny, a nie wymuszony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '14', '14', 'Może właśnie dlatego na krótko oddalił się od ciebie, abyś odzyskał go na zawsze,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '15', '15', 'już nie jak niewolnika, ale o wiele więcej nad niewolnika, jak umiłowanego brata, najbardziej mi bliskiego. O ile bardziej (bliski on) tobie i jako człowiek, i jako (współwyznawca) w Panu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '16', '16', 'Jeżeli zatem uważasz się za mojego przyjaciela, przyjmij go tak, jakbyś mnie przyjął.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '17', '17', 'A jeśli wyrządził ci jakąś szkodę albo jest ci coś winien, zapisz to na mój rachunek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '18', '18', 'Ja, Paweł, zobowiązałem się na piśmie (wszystko) ci spłacić, nie wymawiając, że (właściwie) ty sam do mnie należysz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '19', '19', 'Tak, bracie, liczę więc na twoją przysługę dla Pana. Spraw mi tę radość w Chrystusie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '20', '20', 'Piszę te słowa w nadziei, że spełnisz moją prośbę, a nawet jestem przekonany, że uczynisz więcej niż to, o co proszę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '21', '21', 'Przygotuj mi też gościnę, spodziewam się bowiem, że powrócę do was dzięki waszym modlitwom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '22', '22', 'Pozdrawia cię Epafras, który jest razem ze mną w więzieniu w Chrystusie Jezusie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '23', '23', 'i współpracownicy: Marek, Arystarch, Demas i Łukasz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'phm', '0', '24', '24', 'Łaska Pana Jezusa Chrystusa niech będzie z wami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '0', '0', 'Bóg, który niegdyś przemawiał do ojców wielokrotnie i w różny sposób przez proroków,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '1', '1', 'w tych czasach ostatecznych przemówił do nas przez Syna. Jego ustanowił spadkobiercą wszystkich dóbr, przez Niego też uczynił wszystko, co jest stworzone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '2', '2', 'On, jako odbicie ukazujące Jego chwałę i jako obraz Jego istoty, podtrzymuje wszystko swoim potężnym słowem. On dokonał oczyszczenia z grzechów i zasiadł w niebie po prawicy (Bożego) Majestatu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '3', '3', 'Stał się tym wyższy od aniołów, że odziedziczył imię wybitniejsze niż oni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '4', '4', 'Któremu to z aniołów kiedykolwiek rzekł (Bóg): \'Synem moim jesteś, jam cię dziś zrodził\'? I znowu: \'Ja będę dla niego ojcem, a on będzie dla mnie synem\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '5', '5', 'Kiedy zaś powtórnie wprowadza pierworodnego na świat, powiada: \'Niech padną przed Nim na twarz wszyscy aniołowie Boży\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '6', '6', 'O aniołach wprawdzie powiada: \'On czyni aniołów swymi wichrami, a sługi swoje płomieniami ognistymi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '7', '7', 'O synu jednak: \'Tron Twój, o Boże, na wieki wieków, a berłem sprawiedliwym jest berło Twego panowania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '8', '8', 'Umiłowałeś sprawiedliwość a znienawidziłeś bezprawie, dlatego namaścił Ciebie, Boże, Twój Bóg olejem wesela, a nie żadnego z Twych towarzyszy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '9', '9', 'Ponadto: \'Ty, Panie, na początku umocniłeś ziemię i dziełem Twoich rąk są niebiosa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '10', '10', 'one przeminą, ale Ty przetrwasz, wszystkie zestarzeją się jak szata,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '11', '11', 'jak zwierzchnie okrycie je zwiniesz jak płaszcz - zostaną zmienione. Ty sam natomiast trwasz i Twoje lata nie ustaną\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '12', '12', 'O kim z aniołów kiedykolwiek powiedział: \'Siądź po mej prawicy, aż położę twych nieprzyjaciół jak podnóżek pod twoje stopy\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '0', '13', '13', 'Czyż oni wszyscy nie są duchami służebnymi, przeznaczonymi do posługi tym, którzy mają uczestniczyć w zbawieniu?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '0', '0', 'Musimy zatem bardzo pilnie zważać na to, cośmy usłyszeli, żebyśmy nie minęli się z celem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '1', '1', 'Jeśli bowiem słowo wypowiedziane przez aniołów okazało się tak prawomocne, że wszelkie wykroczenie i nieposłuszeństwo zostało słusznie ukarane,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '2', '2', 'to jakżeż my ocalejemy, jeśli nie zatroszczymy się o tak cenne zbawienie? Najpierw było ono ogłaszane przez Pana, następnie zostało nam przekazane jako pewne przez tych, którzy je słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '3', '3', 'Sam Bóg dołączył swoje świadectwo przez znaki, cuda i różnorodne czyny wykazujące moc oraz przejawy działania Ducha Świętego rozdzielane według Jego upodobania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '4', '4', 'Przecież nie aniołom poddał ten przyszły świat, o którym mówimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '5', '5', 'Ktoś to już przecież gdzieś stwierdził, mówiąc: \'Kim jest człowiek, że pamiętasz o nim, czy syn człowieczy, że nim się zajmujesz?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '6', '6', 'Uczyniłeś go niewiele mniejszym od aniołów, chwałą i czcią go uwieńczyłeś,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '7', '7', 'wszystko poddałeś pod jego stopy\'. Skoro zatem poddał wszystko, nie zostawił niczego, co byłoby mu niepoddane. A przecież obecnie widzimy, że nie wszystko jest mu poddane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '8', '8', 'Owym zaś \'niewiele mniejszym od aniołów\' jest, jak widzimy, Jezus przez śmierć męczeńską \'chwałą i czcią uwieńczony\', skoro z łaski Bożej za wszystkich umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '9', '9', 'Wypadało przecież, żeby Ten, dla którego jest wszystko i przez którego wszystko się stało, gdy miał doprowadzić wielu synów do chwały, uczynił pierwszego sprawcę ich zbawienia doskonałym przez cierpienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '10', '10', 'Uświęcający bowiem i uświęceni - wszyscy pochodzą od Jednego, dlatego nie wstydzi się nazwać ich braćmi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '11', '11', 'mówiąc: \'Ogłoszę moim braciom Twoje imię, w zgromadzeniu będę Cię wystawiał\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '12', '12', 'i znowu: \'Ja będę w Nim pokładał ufność\', i znowu: \'Otom ja i dzieci, które mi dał Bóg\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '13', '13', 'Skoro zaś dzieci uczestniczą w naturze ludzkiej, On także otrzymał w niej udział, aby przez śmierć pozbawić mocy tego, który rządził śmiercią, mianowicie diabła,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '14', '14', 'i aby uwolnić wszystkich, którzy w lęku przed śmiercią przez całe życie byli skazani na niewolę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '15', '15', 'Przecież nie aniołami się zajmuje, ale \'troszczy się o potomstwo Abrahama\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '16', '16', 'Zatem musiał pod każdym względem upodobnić się do braci, aby stać się miłosiernym i wiernym arcykapłanem w sprawach Bożych, dla przejednania za winy ludu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '1', '17', '17', 'Właśnie dlatego, że On sam doznał, czym jest pokusa, może wspomagać kuszonych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '0', '0', 'Przeto, święci bracia, uczestnicy niebieskiego powołania, rozważcie, że Jezus, apostoł i arcykapłan naszego wyznania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '1', '1', 'wierny jest Temu, który go ustanowił, jak \'Mojżesz w całym Jego domu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '2', '2', 'On przy tym godzien jest o tyle większej chwały niż Mojżesz, o ile większą czcią cieszy się budowniczy niż jego dom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '3', '3', 'Każdy bowiem dom bywa przez kogoś zbudowany, a tym, który wszystko zbudował, jest Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '4', '4', 'I Mojżesz wprawdzie był wierny w całym Jego domu, zarządzając i przekazując to, co miało być głoszone,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '5', '5', 'ale Chrystus (wierny) był (mając władzę) nad Jego domem jako Syn. Tym domem zaś my jesteśmy, jeśli zachowamy niezłomną ufność i chlubną nadzieję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '6', '6', 'Dlatego, jak powiada Duch Święty: \'Dzisiaj, gdy usłyszycie głos Jego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '7', '7', 'nie znieczulajcie serc waszych, jak w &gt;&gt;Rozgoryczeniu&lt;&lt; w dniu pokusy na pustyni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '8', '8', 'gdzie prowokowali mnie ojcowie wasi, doświadczając mnie, a poznawali dzieła moje przez czterdzieści lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '9', '9', 'Przeto obrzydziłem sobie to pokolenie i rzekłem: Zawsze zwodzi ich serce. Oto oni nie poznali mych dróg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '10', '10', 'Przeto przysiągłem w gniewie: Na pewno nie wejdą do mego odpoczynku!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '11', '11', 'Uważajcie, bracia, żeby wśród was nie było jakiegoś zepsutego przez niewiarę serca, które by odeszło od Boga żywego,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '12', '12', 'ale zachęcajcie się co dzień wzajemnie, dopóki trwa owo \'dzisiaj\', ażeby ktoś z was zawiedziony przez grzech nie stał się nieczuły (na Boga).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '13', '13', 'Jesteśmy bowiem współtowarzyszami Chrystusa, jeśli tylko zachowamy aż do końca niewzruszoną nadzieję, jaką mieliśmy na początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '14', '14', 'Skoro powiedziano: \'Dzisiaj, gdy usłyszycie głos Jego, nie znieczulajcie serc waszych jak w &gt;&gt;Rozgoryczeniu&lt;&lt;\', (trzeba by zapytać),');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '15', '15', 'którzy to, gdy usłyszeli, doznali rozgoryczenia? Czy nie wszyscy, którzy wyszli z Egiptu pod wodzą Mojżesza?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '16', '16', 'Kogo zaś \'obrzydził sobie przez czterdzieści lat\'? Czyż nie tych, co zgrzeszyli, których \'zwłoki legły na pustyni\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '17', '17', 'A komu \'przysiągł, że nie wejdą do Jego odpoczynku\', jeśli nie tym, którzy okazali się nieposłuszni?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '2', '18', '18', 'Widzimy zatem, że nie mogli (tam) wejść z powodu niewiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '0', '0', 'Lękajmy się więc, aby - skoro trwa jeszcze obietnica wejścia do tego odpoczynku - nie okazało się, że ktoś z was przyszedł za późno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '1', '1', 'Otrzymaliśmy bowiem dobrą nowinę tak samo jak i oni, lecz im nie pomogło usłyszane słowo, ponieważ nie połączyło się z wiarą tych, co słuchali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '2', '2', 'Wchodzimy bowiem do odpoczynku jako ci, którzy uwierzyli, zgodnie z tym, co powiedział (Bóg): \'Przysiągłem w mym gniewie: Na pewno nie wejdą do mego odpoczynku\', chociaż Jego dzieła istnieją  od założenia świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '3', '3', 'Powiedział gdzieś bowiem o tym siódmym dniu: \'I odpoczął Bóg w siódmym dniu od wszystkich swych dzieł\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '4', '4', 'A tu znów: \'Na pewno nie wejdą do mojego odpoczynku\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '5', '5', 'Ponieważ więc ktoś jeszcze ma wejść do niego, a ci, którym najpierw zwiastowano dobrą nowinę, nie weszli z powodu niewiary,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '6', '6', 'zatem znowu wyznaczą pewien dzień - \'dzisiaj\' - u Dawida, po długim czasie mówiąc tak, jak było powiedziane przedtem: \'Dzisiaj, gdy usłyszycie głos Jego, nie znieczulajcie serc waszych\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '7', '7', 'Gdyby bowiem Jozue dał mi odpoczynek,  nie byłoby mowy po tylu dniach o innym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '8', '8', 'Przeto pozostaje jeszcze odpoczynek sobotni dla ludu Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '9', '9', 'Ten bowiem, kto wejdzie do Jego odpoczynku, odpocznie od swych czynów, tak jak Bóg od swych własnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '10', '10', 'Pospieszmy się więc z wejściem do owego odpoczynku, żeby ktoś nie zginął przez niewiarę, jak to zostało pokazane na przykładzie nieposłuszeństwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '11', '11', 'Przecież słowo Boże jest żywe, skuteczne i ostrzejsze niż obosieczny miecz i tak przenikliwe, że aż rozdziela spojenia i rdzenie duszy i ducha, a zdolne jest rozsądzać myśli i zamiary serca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '12', '12', 'Żadne stworzenie nie ukryje się przed Nim, lecz wszystko jest obnażone i odsłonięte dla oczu Tego, przed którym mamy zdać sprawę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '13', '13', 'Mając zatem wielkiego arcykapłana, który wszedł do nieba, Jezusa, Syna Bożego, mocno trwajmy przy wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '14', '14', 'Nie mamy przecież arcykapłana, który nie potrafiłby współczuć w naszych słabościach, ale takiego, który podobnie jak my doznał tego wszystkiego, czego my doznajemy z wyjątkiem grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '3', '15', '15', 'Przystąpmy więc z ufnością do tronu łaski, abyśmy otrzymali zmiłowanie i znaleźli pomocną łaskę w potrzebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '0', '0', 'Każdy przecież arcykapłan brany jest spośród ludzi i ustanawiany nad ludźmi w sprawach Bożych, żeby składał dary i ofiary za grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '1', '1', 'Może on okazywać łagodność wobec niewiedzących i błądzących, ponieważ i sam doznaje słabości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '2', '2', 'wszak musi tak za lud, jak i za siebie składać ofiarę za grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '3', '3', 'A nikt nie bierze sobie tego zaszczytu, jeśli nie jest powołany przez Boga tak jak Aaron.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '4', '4', 'Również i Chrystus nie sam siebie obdarzył godnością arcykapłańską, ale Ten, kto powiedział do Niego: \'Synem moim jesteś, jam ciebie dziś zrodził\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '5', '5', 'jak i na innym miejscu mówi: \'Tak jesteś kapłanem na wieki na podobieństwo Melchizedecha\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '6', '6', 'On to za swego życia na ziemi z głośnym wołaniem i łzami zanosił modły i błagania do Tego,  który mógł Go ocalić od śmierci. I został wysłuchany, bo okazał uległość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '7', '7', 'Chociaż był Synem, z tego, co wycierpiał, doznał, czym jest posłuszeństwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '8', '8', 'A poznawszy je tak doskonale, stał się sprawcą zbawienia wiecznego dla wszystkich, którzy są Mu posłuszni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '9', '9', 'Nazwany został przez Boga arcykapłanem \'na podobieństwo Melchizedecha\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '10', '10', 'Na ten temat trzeba będzie wam dużo powiedzieć i włożyć wiele trudu w wyjaśnienie, bo niechętni z was słuchacze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '11', '11', 'A chociaż byłby już czas na to, żebyście (innych) nauczali, trzeba was na nowo pouczać o najprostszych zasadach nauki Bożej, czyli dawać mleko zamiast stałego pokarmu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '12', '12', 'A przecież każdy, kto żywi się mlekiem, nieświadom jest nauki o sprawiedliwości, bo jest dzieckiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '4', '13', '13', 'Dorośli natomiast, ci mianowicie, którzy przez ćwiczenie wyrobili w sobie łatwość rozróżniania dobra od zła, potrzebują stałego pokarmu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '0', '0', 'Stąd też, pomijając podstawy nauki o Chrystusie, przystąpmy do spraw poważniejszych, byśmy nie zakładali od nowa fundamentu, (którym jest) zerwanie z uczynkami martwymi, wiara w Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '1', '1', 'nauka dotycząca obmyć, wkładania rąk, zmartwychwstania umarłych czy sądu wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '2', '2', 'Przystąpmy zatem z Bożą pomocą do tego dzieła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '3', '3', 'Otóż niemożliwe jest, aby ci, którzy raz zostali oświeceni, zakosztowali daru niebieskiego, stali się uczestnikami Ducha Świętego');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '4', '4', 'i zakosztowali dobrego słowa Bożego oraz przejawów mocy przyszłego wieku,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '5', '5', 'a mimo to odpadli - ponownie się nawrócili. Oni dla siebie znów ukrzyżowali i zelżyli Syna Bożego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '6', '6', 'Przecież ziemia, która wchłania często padający deszcz i wydaje roślinność użyteczną dla tych, co ją uprawiają, uczestniczy w błogosławieństwie Bożym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '7', '7', 'Ta zaś, która \'przynosi ciernie i osty\', jest nieużyteczna i grozi jej przekleństwo, a losem jej wypalenie (przez słońce).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '8', '8', 'O was, umiłowani, chociaż tak mówimy, mamy jednak lepsze mniemanie, które pozwala ufać w wasze zbawienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '9', '9', 'Bóg nie jest bowiem niesprawiedliwy i nie zapomni o dziełach waszej miłości, jaką okazywaliście, gdy ze względu na Niego wspomagaliście i nadal wspomagacie świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '10', '10', 'Pragniemy zaś, żeby każdy z was do końca troskliwie zabiegał o coraz doskonalszą nadzieję.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '11', '11', 'Żebyście nie stawali się opieszali, lecz naśladowali tych, którzy dla swej wiary i wielkoduszności odziedziczyli obietnice.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '12', '12', 'Kiedy bowiem Bóg dawał obietnice Abrahamowi, nie widząc nikogo większego od siebie, na kogo mógłby przysiąc, \'przysięgał na samego siebie\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '13', '13', 'mówiąc: \' Zaprawdę, obficie cię ubłogosławię i niepomiernie rozmnożę\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '14', '14', 'Tak więc wytrwawszy w cierpliwości (Abraham) otrzymał obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '15', '15', 'Ludzie przecież  przysięgają na kogoś wyższego i przysięga stanowi dla nich ostateczne potwierdzenie w każdej spornej sprawie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '16', '16', 'Kiedy więc Bóg chciał okazać dziedzicom obietnicy, że Jego postanowienie jest trwałe, sam stał się poręczycielem przysięgi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '17', '17', 'abyśmy dzięki tym dwom niezmiennym wydarzeniom, w których Bóg nie może kłamać, uciekając się do Niego mieli niezachwianą pewność, że umocnimy się w danej nam nadziei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '18', '18', 'Jest ona dla nas jakby duchową kotwicą niezawodną i pewną, a sięgającą do wnętrza, \'poza zasłonę\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '5', '19', '19', 'dokąd, poprzedzając nas, wszedł Jezus, który stał się \'na podobieństwo Melchizedecha\' arcykapłanem \'na wieki\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '0', '0', 'Ów bowiem Melchizedech, król Salemu, kapłan Boga Najwyższego, wyszedłszy na spotkanie Abrahama pobłogosławił mu, gdy wracał on po rozgromieniu królów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '1', '1', 'Jemu to wręczył \'Abraham dziesiątą część ze wszystkiego\'. Pierwotnie bowiem imię jego znaczy \'król sprawiedliwości\', następnie też \'król Salemu\', czyli król pokoju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '2', '2', 'Bez wzmianki o ojcu czy matce, bez rodowodu, bez wskazania początku dni i końca życia - upodobniony zatem do Syna Bożego - pozostaje kapłanem na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '3', '3', 'Widzicie więc, jak wielki był ten, któremu Abraham jako głowa narodu dał dziesiątą część z najlepszych zdobyczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '4', '4', 'Wprawdzie i tym synom Lewiego, którym powierzono funkcje kapłańskie, Prawo nakazuje pobierać dziesięciny od ludu, czyli od ich braci, chociaż i oni wywodzą się z potomstwa Abrahama.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '5', '5', 'Ale ów przyjął dziesięcinę od Abrahama, nie należąc do ich rodu, i pobłogosławił tego, który otrzymał obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '6', '6', 'Bezsprzecznie, niższy bywa błogosławiony przez wyższego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '7', '7', 'Tutaj biorą dziesięciny ludzie śmiertelni, tam zaś ktoś, o kim się świadczy, że żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '8', '8', 'I można powiedzieć, że przez Abrahama zapłacił dziesięcinę także Lewi, który bierze dziesięciny,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '9', '9', 'bo Lewi jeszcze w ogóle nie istniał, kiedy jego ojcu \'wyszedł naprzeciw Melchizedech\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '10', '10', 'Jeśli więc doskonałość była osiągana przez kapłaństwo lewickie, bo przecież naród w związku z nim otrzymał Prawo, to po co było ustanawiać kogoś innego \'na wzór Melchizedeha\' i mówić, że nie jest \'na wzór Aarona\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '11', '11', 'Przecież ze zmianą kapłaństwa zachodzi konieczność zmienienia także i Prawa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '12', '12', 'Ten, o którym mowa, jest członkiem innego pokolenia, z którego nikt nie poświęcał się służbie ołtarza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '13', '13', 'Powszechnie wiadomo, że Pan nasz urodził się w pokoleniu Judy, a Mojżesz nic nie mówił o kapłanach z tego pokolenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '14', '14', 'Jest to tym bardziej widoczne, jeśli \'na podobieństwo Melchizedecha\' ustanawia się innego kapłana,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '15', '15', 'który zostaje nim nie na mocy przepisu Prawa o naturalnym pochodzeniu, ale mocą niezniszczalnego życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '16', '16', 'Stwierdza się bowiem: \'Ty jesteś kapłanem na wieki na podobieństwo Melchizedecha\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '17', '17', 'Dochodzi zatem do uchylenia poprzedniego Prawa z powodu jego niestałości i nieużyteczności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '18', '18', 'Prawo bowiem nie doprowadziło do doskonałości, przeto daje się (w jego miejsce) doskonalszą nadzieję, przez którą zbliżamy się do Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '19', '19', 'I to nie bez stwierdzenia pod przysięgą. Gdy owi zostawali kapłanami bez przysięgi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '20', '20', 'On - przez przysięgę Tego, który Mu powiedział: \'Przysiągł Pan i nie odwoła tego: Ty jesteś kapłanem na wieki\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '21', '21', 'Dlatego Jezus jest poręczycielem lepszego przymierza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '22', '22', 'Tamtych kapłanów było wielu, bo śmierć przeszkadzała im w spełnianiu funkcji.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '23', '23', 'On natomiast, ponieważ trwa na wieki, posiada kapłaństwo nieprzemijające.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '24', '24', 'Przeto i zbawiać może w sposób doskonały tych, którzy przez Niego przychodzą do Boga, bo zawsze żyje, aby wstawiać się za nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '25', '25', 'Wypadało, żebyśmy mieli takiego arcykapłana - świętego, dobrego, czystego, odłączonego od grzeszników i wywyższonego ponad niebiosa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '26', '26', 'Nie potrzebuje on codziennie, jak arcykapłani, składać ofiar najpierw za własne grzechy, a potem za grzechy narodu; uczynił to bowiem raz na zawsze, złożywszy siebie samego w ofierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '6', '27', '27', 'Tak więc Prawo ustanawia arcykapłanami ludzi podległych słabości, a słowa przysięgi poza Prawem (ustanawiają arcykapłanem) - Syna doskonałego na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '0', '0', 'Z tego, co mówimy, najważniejsze jest to: Mamy arcykapłana, który zasiadł na prawicy Majestatu, na Jego tronie w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '1', '1', 'On spełnia służbę w świątyni, to jest w namiocie prawdziwym, jaki zbudował Pan, a nie człowiek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '2', '2', 'Otóż każdy arcykapłan ustanawiany bywa po to, ażeby składał ofiary bezkrwawe i krwawe; trzeba zatem, żeby i on miał co ofiarować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '3', '3', 'Jeśli zaś byłby na ziemi, nie byłby nawet kapłanem, skoro są ci, którzy składają ofiary zgodnie z Prawem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '4', '4', 'Oni spełniają swą służbę w świątyni, która jest tylko obrazem i cieniem świątyni niebieskiej, o czym został pouczony Mojżesz, gdy miał zbudować namiot. \'Patrz - zatem powiada (Bóg) - masz zrobić wszystko według wzoru, jaki był ci pokazany na górze\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '5', '5', 'Obecnie zaś otrzymał zaszczytniejsze posługiwanie, bo też jest poręczycielem lepszego Przymierza, które uprawomocnione zostało mocniejszymi obietnicami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '6', '6', 'Jeśli bowiem owo pierwsze byłoby w pełni doskonałe, nie miałoby sensu szukanie drugiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '7', '7', 'Ganiąc ich wszakże, powiada: \'Oto nadchodzą dni, mówi Pan, a zawrę ostatecznie z domem Izraela i z domem Judy Nowe Przymierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '8', '8', 'Nie takie jak Przymierze, jakie zawarłem z ich ojcami w dniu, kiedy wziąłem ich za rękę, aby ich wyprowadzić z ziemi egipskiej. Ponieważ oni nie wytrwali w Przymierzu ze mną, i ja zaniedbałem ich, powiada Pan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '9', '9', 'Takie oto będzie Przymierze, które zawrę z domem Izraela: Po owych dniach - powiada Pan - wyryję moje Prawa w ich myślach i napiszę je na ich sercach, będę ich Bogiem, a oni będą moim ludem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '10', '10', 'Nikt już nie będzie pouczał swojego rodaka ani swego brata, mówiąc: Poznaj Pana! Albowiem wszyscy będą mnie znali, od najmłodszego do najstarszego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '11', '11', 'Będę wyrozumiały dla ich wykroczeń i nie będę pamiętał ich grzechów\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '7', '12', '12', 'Skoro mówi \'nowe\', to uważa, że poprzednie się zestarzało; a to, co jest przedawnione i przestarzałe, nadaje się do usunięcia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '0', '0', 'Przecież i pierwsze Przymierze miało swoje przepisy dotyczące służby Bożej i ziemskie sanktuarium.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '1', '1', 'Sporządzono namiot - pierwszy, w którym znajdował się świecznik i stół oraz chleby poświęcone, nazywał się miejscem świętym,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '2', '2', 'za drugą zaś zasłoną był namiot zwany miejscem najświętszym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '3', '3', 'Znajdowała się tam złota kadzielnicy a także arka przymierza, która była pokryta cała złotem, a w niej dzban złoty zawierający mannę, laska Aarona, która wypuściła kiedyś pędy, oraz tablice Przymierza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '4', '4', 'Nad nią znajdowały się cheruby Chwały ocieniające przykrycie arki. O tych rzeczach nie potrzebuję teraz opowiadać szczegółowo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '5', '5', 'Tak to wszystko było urządzone. Do pierwszego namiotu zawsze wchodzą kapłani, aby spełniać służbę Bożą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '6', '6', 'natomiast do drugiego - raz w roku sam arcykapłan, i to nie bez krwi, którą ofiaruje za przekroczenia swoje i narodu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '7', '7', 'Przez to Duch Święty daje do zrozumienia, że dopóki istnieje pierwszy namiot, dopóty nie jest otwarta droga do miejsca poświęconego Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '8', '8', 'Taki jest obraz czasów składania ofiar bezkrwawych i krwawych, które nie potrafią udoskonalić wewnętrznie tego, kto spełnia służbę Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '9', '9', 'Są to przecież tylko różne przepisy dotyczące naturalnego porządku: o pokarmach, napojach i obmyciach, nadane do czasu ostatecznego uporządkowania tych spraw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '10', '10', 'Natomiast Chrystus, arcykapłan dóbr obecnych, wszedł raz na zawsze do miejsca poświęconego Bogu przez lepszy, doskonalszy namiot, nie sporządzony ręką, to znaczy nie należący do świata doczesnych stworzeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '11', '11', 'Wszedł z własną krwią, a nie z krwią koźląt i cieląt, i osiągnął wieczne odkupienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '12', '12', 'Jeśli już pokropienie krwią koźląt i cieląt, oraz popiołem jałówki uświęca skalanych, dając im oczyszczenie zewnętrzne,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '13', '13', 'to tym bardziej krew Chrystusa, który potęgą ducha wieczystego złożył w ofierze Bogu samego siebie nieskalanego, oczyści nasze sumienie od uczynków martwych, abyśmy oddawali cześć Bogu żyjącemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '14', '14', 'Dlatego też jest pośrednikiem nowego \'testamentu\', aby ci, którzy zostali powołani do dziedzictwa wiecznego, otrzymali obietnicę, poniósł bowiem śmierć dla odkupienia wykroczeń popełnionych za czasów pierwszego \'testamentu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '15', '15', 'Przecież tam, gdzie chodzi o testament, trzeba, żeby ten, który go sporządził, umarł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '16', '16', 'Testament staje się prawomocny ze śmiercią, nigdy bowiem nie ma mocy, gdy żyje ten, który go sporządził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '17', '17', 'Stąd też i poprzedni nabrał mocy przez krew.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '18', '18', 'Kiedy Mojżesz odczytał całemu ludowi wszystkie przepisy Prawa, wziąwszy krew cieląt z wodą, wełnę szkarłatną i hizop skropił samą księgę i cały lud,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '19', '19', 'mówiąc: \'To jest krew testamentu, który jest dla was Bożym przykazaniem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '20', '20', 'W ten sposób skropił również namiot i wszystkie sprzęty używane w służbie Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '21', '21', 'Powiedziałbym, że według Prawa wszystko oczyszcza się krwią, a bez przelania krwi nie ma odpuszczenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '22', '22', 'Jeżeli więc trzeba było oczyszczać samo tylko wyobrażenie tego, co jest w niebie, to o ileż większych ofiar wymaga to sanktuarium, co jest w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '23', '23', 'Chrystus bowiem nie wszedł do świętego namiotu, który sporządziły ludzkie ręce jako namiastkę tego prawdziwego, ale wszedł do samego nieba. Teraz oręduje za nami przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '24', '24', 'I nie wchodzi co roku do miejsca świętego z cudzą krwią jak arcykapłan po to, aby wielokrotnie ofiarować samego siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '25', '25', 'W takim razie musiałby bowiem wielokrotnie cierpieć od założenia świata. Lecz On pojawił się tylko jeden raz,  kiedy wypełniły się czasy, aby pozbawić grzech mocy przez swoją ofiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '26', '26', 'Podobnie jak ludzie muszą raz umrzeć, a potem następuje sąd,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '8', '27', '27', 'tak i Chrystus jeden raz ofiarował się, aby wziąć na siebie grzechy wielu. Ukaże się po raz drugi już nie w związku z grzechem, ale dla zbawienia tych, którzy pilnie Go wyczekują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '0', '0', 'Skoro zatem Prawo dotyczyło cienia dóbr przyszłych a nie ich samych, nigdy nie potrafi doprowadzić do doskonałości tych, którzy co roku przynoszą ofiary bezustannie składane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '1', '1', 'Czyż nie zaprzestanoby ich składać, gdyby uczestnicy tych obrzędów raz oczyszczeni nie mieli już świadomości grzechów?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '2', '2', 'Ale przez nie corocznie przypomina się grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '3', '3', 'Niemożliwe jest przecież, żeby krew cieląt i koźląt gładziła grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '4', '4', 'Dlatego też wchodząc na świat powiada: \'Ofiary krwawej czy bezkrwawej nie chciałeś, lecz ciało mi przysposobiłeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '5', '5', 'Ofiary całopalne i składane za grzech nie spodobały ci się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '6', '6', 'Zatem rzekłem: Oto przyszedłem - napisano o mnie w zwoju księgi - żeby spełniać, o Boże, wolę Twoją\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '7', '7', 'Najpierw mówi: \'Ofiar krwawych i bezkrwawych ani całopalnych i składanych za grzech nie chciałeś, bo nie podobały Ci się\' - a takie składa się zgodnie z Prawem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '8', '8', 'Następnie powiada: \'Oto przychodzę, aby spełniać Twoją wolę\' - znosi więc owo pierwsze, aby zaprowadzić to drugie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '9', '9', 'Dzięki tej woli zostaliśmy uświęceni przez ofiarę z ciała Jezusa Chrystusa raz na zawsze złożoną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '10', '10', 'Wszak każdy kapłan spełnia codziennie służbę Bożą i składa wciąż swoje ofiary, które jednak nigdy nie mogą zgładzać grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '11', '11', 'Ten natomiast raz na zawsze złożył jedną ofiarę krwawą za grzech i \'zasiadł po prawicy Bożej\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '12', '12', 'Odtąd oczekuje, \'aż jego nieprzyjaciele zostaną rzuceni jak podnóżek pod jego stopy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '13', '13', 'Przez tę jedną ofiarę uczynił doskonałymi na zawsze tych, którzy mają być uświęceni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '14', '14', 'Daje nam przecież świadectwo i Duch Święty, skoro powiedział:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '15', '15', '\'To jest przymierze, które zawrę z nimi po owych dniach, powiada Pan; włożę moje prawa w ich serca i wypiszę je w ich myślach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '16', '16', 'a ich grzechów i nieprawości nie będę już więcej pamiętał\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '17', '17', 'A gdzie jest darowanie wszystkiego, tam nie ma już ofiary za grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '18', '18', 'Mając zatem, bracia, radosną ufność, że wejdziemy do sanktuarium dzięki krwi Jezusa -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '19', '19', 'a to wejście poza zasłonę otworzył nam przez drogę nową i żywą, to jest przez ciało swoje,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '20', '20', '(mając) też wielkiego kapłana nad domem Bożym -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '21', '21', 'przystąpmy z czystymi sercami i pełnym zaufaniem, skorośmy przez pokropienie oczyścili serca ze świadomych grzechów i obmyli ciało oczyszczającą wodą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '22', '22', 'Trwajmy niewzruszenie w nadziei, którą żyjemy, wierny jest bowiem Ten, który dał obietnicę!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '23', '23', 'Troszczmy się wspólnie o to, jak się zachęcać wzajemnie do miłości i dobrych uczynków!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '24', '24', 'Nie opuszczajmy wspólnego zgromadzenia, jak niektórzy mają w zwyczaju, dodawajmy sobie otuchy i to tym bardziej, im bliższy, jak widzicie, jest ów dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '25', '25', 'Jeśli bowiem grzeszylibyśmy dobrowolnie, kiedy już otrzymaliśmy jasne poznanie prawdy, to nie ma dla nas nawet ofiary za grzechy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '26', '26', 'lecz jakieś przerażające oczekiwanie sądu i żar ognia, który ma pożreć przeciwników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '27', '27', 'Jeśli ktoś przekroczył Prawo Mojżeszowe \'w oparciu o zeznanie dwóch lub trzech świadków, ponosi śmierć\' bez miłosierdzia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '28', '28', 'Jak myślicie? O ileż większej kary wart jest ten, kto zdeptał Syna Bożego i poczytał za rzecz bezwartościową krew Przymierza, przez którą został uświęcony, i odniósł się ze wzgardą do Ducha miłości?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '29', '29', 'Znamy przecież Tego, który mówi: \'Do mnie należy pomsta, ja odpłacę!\' I znowu: \'Pan będzie sądził swój lud\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '30', '30', 'Straszną jest rzeczą wpaść w ręce Boga żyjącego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '31', '31', 'Przypomnijcie sobie owe pierwsze dni, w których, gdy już zostaliście oświeceni, znosiliście cierpliwie częste zmagania z cierpieniem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '32', '32', 'czy to publicznie wyszydzani i poniewierani, czy to jako współtowarzysze żyjących w takich warunkach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '33', '33', 'Przecież i uwięzionym okazaliście współczucie i z radością znieśliście grabież waszego mienia, wiedząc, że sami posiadacie dobra lepsze i trwałe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '34', '34', 'Nie traćcie więc cierpliwości, która otrzymuje wielką nagrodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '35', '35', 'Potrzeba wam przecież wytrwania, abyście wypełniając wolę Bożą osiągnęli to, co wam było obiecane.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '36', '36', 'Jeszcze bowiem \'bardzo mała chwilka, Ten, który ma przyjść, nadejdzie i nie będzie zwlekał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '37', '37', 'a mój sprawiedliwy dzięki wierze żyć będzie, a jeśli bojaźliwie odstąpi, nie będzie mi się podobał\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '9', '38', '38', 'My jednak nie należymy do bojaźliwych, którzy mają zginąć, lecz do wierzących, którzy mają żyć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '0', '0', 'Wiara jest mocnym przekonaniem, że istnieje to, czego się spodziewamy, jest świadectwem o wydarzeniach, których nie widzimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '1', '1', 'Ona wystawia świadectwo naszym przodkom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '2', '2', 'Przez wiarę poznajemy, że światy zostały stworzone słowem Boga, że to, co jest dostrzegalne, powstało z niewidzialnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '3', '3', 'Dzięki wierze Abel złożył Bogu lepszą ofiarę niż Kain, która mu wystawia świadectwo, że jest doskonały, Bóg bowiem zaświadczył o jego darach; dzięki niej też, chociaż umarł, jeszcze przemawia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '4', '4', 'Dzięki wierze Henoch był przeniesiony (z tego świata), aby nie umarł, a \'nie został znaleziony; ponieważ przeniósł go Bóg\'. Oto przed przeniesieniem otrzymał świadectwo, że \'bardzo podobał się Bogu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '5', '5', 'Bez wiary więc nie można podobać się Bogu, bowiem kto zbliża się do Boga, musi wierzyć, że On istnieje i że wynagradza tych, którzy Go szukają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '6', '6', 'Dzięki wierze Noe ostrzeżony przed tym, czego jeszcze nie było widać, pełen czci, przygotował arkę dla ratowania swej rodziny. Dzięki wierze wydał wyrok na świat i dostąpił sprawiedliwości opartej na wierze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '7', '7', 'Dzięki wierze Abraham, gdy został wezwany, posłusznie poszedł na miejsce, które miał otrzymać jako dziedzictwo, a wyszedł nie wiedząc nawet, dokąd ma iść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '8', '8', 'Dzięki wierze zawędrował do Ziemi Obiecanej jako obcej, zamieszkując w namiotach wraz z Izaakiem i Jakubem, współdziedzicami tej samej obietnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '9', '9', 'Oczekiwał bowiem miasta o stałych fundamentach, które Bóg miał zaplanować i zbudować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '10', '10', 'Dzięki wierze nawet i (bezpłodna) Sara mimo podeszłego wieku mogła począć syna, ponieważ zaufała Temu, który dał obietnicę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '11', '11', 'Dlatego z jednego i to niemal obumarłego człowieka powstało liczne potomstwo: \'Jak gwiazdy na niebie i jak nie przeliczony piasek na brzegu morskim\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '12', '12', 'Zachowując wiarę poumierali oni wszyscy, nie osiągnąwszy obiecanych dóbr, lecz z daleka je oglądali i pozdrawiali, a przyznawali, że są na tej ziemi cudzoziemcami i wędrowcami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '13', '13', 'Tak mówiąc okazywali, że szukają ojczyzny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '14', '14', 'A jeśliby myśleli o tej, z której wyszli, mieli przecież czas powrócić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '15', '15', 'Zdążają zatem do lepszej, mianowicie do niebieskiej. Dlatego Bóg pozwala się nazywać ich Bogiem, bo przecież przygotował im miasto.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '16', '16', 'Dzięki wierze Abraham poddany próbie złożył w ofierze Izaaka; ten, który z wiarą przyjął obietnicę, składał w ofierze jedynego syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '17', '17', 'Ten, któremu powiedziano: \'W Izaaku będziesz miał potomstwo\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '18', '18', 'był przekonany, że Bóg potrafi wskrzesić nawet umarłego, dlatego otrzymał Izaaka także po to, aby stał się on wyobrażeniem Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '19', '19', 'Dzięki wierze w to, co miał się stać, Izaak dał błogosławieństwo Jakubowi i Ezawowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '20', '20', 'Dzięki wierze umierający Jakub pobłogosławił obydwóch synów Józefa i \'schylił się nad wierzchołkiem swej laski\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '21', '21', 'Dzięki wierze Józef u kresu życia przypomniał sobie o wyjściu Izraelitów i wydał polecenie co do (przeniesienia) swych kości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '22', '22', 'Dzięki wierze rodzice ukrywali Mojżesza przez trzy miesiące po jego urodzeniu, ponieważ widzieli, że chłopiec był urodziwy, a nie ulękli się rozporządzenia królewskiego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '23', '23', 'Dzięki wierze Mojżesz, gdy dorósł, nie chciał uchodzić za syna córki faraona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '24', '24', 'Wolał raczej cierpieć z ludem Bożym, aniżeli korzystać z przemijających owoców grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '25', '25', 'Znoszenie zelżywości dla Pomazańca (Izraela) uważał za większe bogactwo niż wszystkie skarby Egiptu, bo myślał o czekającej go nagrodzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '26', '26', 'Dzięki wierze opuścił Egipt, nie bojąc się gniewu królewskiego; okazał taką wytrwałość, jakby widział Niewidzialnego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '27', '27', 'Dzięki wierze urządził paschę i pomazanie krwią, aby niszczyciel nie tknął ich pierworodnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '28', '28', 'Dzięki wierze przeszli Morze Czerwone jak po suchej ziemi, próbujących tego samego Egipcjan pochłonęły fale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '29', '29', 'Dzięki wierze (oblegających) upadły mury Jerycha, okrążane siedem dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '30', '30', 'Dzięki wierze nierządna Rahab nie zginęła razem ze stawiającymi opór, bo życzliwie przyjęła wywiadowców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '31', '31', 'Cóż bym jeszcze powiedział? Zabrakłoby mi czasu, gdybym miał opowiadać o Gedeonie, Baraku, Samsonie,  Jeftem czy Dawidzie i Samuelu oraz prorokach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '32', '32', 'Oni dzięki swej wierze zwalczyli królestwa, szerzyli sprawiedliwość, dostąpili skutków obietnic, zamknęli paszczę lwów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '33', '33', 'zwyciężyli się ognia, uniknęli ostrza miecza, podnieśli się z choroby, okazali waleczność w boju, rozbili szyki wrogów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '34', '34', 'kobiety otrzymały swoich zmarłych przywróconych życiu. Inni natomiast, nie przyjąwszy ofiarowanej im wolności, byli łamani kołem, aby osiągnąć większe dobro - zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '35', '35', 'Jeszcze inni zaznali szyderstw i chłosty a także kajdan i więzienia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '36', '36', 'byli kamienowani, przecinani piłą, ginęli od miecza, okryci skórami owiec i kozłów tułali się, cierpieli niedostatek, udrękę i poniewierkę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '37', '37', 'Świat nie był ich wart! Błąkali się po pustkowiach, po górach, jaskiniach i rozpadlinach ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '38', '38', 'I to ci wszyscy, chociaż godni uznania dla swej wiary, nie doczekali się spełnienia obietnicy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '10', '39', '39', 'Bóg zatroszczył się dla nas o coś lepszego, nie chciał, by oni bez nas osiągnęli doskonałość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '0', '0', 'Dlatego też i my, otoczeni tak wielkim zastępem świadków, zrzuciwszy z siebie wszystko, co krępuje swobodę ruchów, a zwłaszcza obciążający nas grzech, biegnijmy wytrwale do czekającej nas walki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '1', '1', 'Spoglądajmy na Jezusa, który wprowadził nas na drogę wiary i przewodzi na niej aż do końca, który wolał raczej przyjąć krzyż aniżeli ofiarowaną Mu radość, nie lękał się niesławy i zasiadł na tronie po prawicy Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '2', '2', 'Rozważcie, że On tak wiele wycierpiał od zbuntowanych przeciwko Niemu grzeszników, abyście nie osłabli i nie upadli na duchu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '3', '3', 'Jeszcze bowiem nie opieraliście się aż do krwi, gdybyście przeciwstawiali się grzechowi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '4', '4', 'nie zapomnieliście też zachęty skierowanej do was jak do synów: \'Synu mój, nie lekceważ sobie karcenia Pańskiego ani też karcony nie załamuj się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '5', '5', 'Kogo bowiem Pan miłuje, tego wychowuje w karności, biczuje zaś każdego, kogo uznaje za syna\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '6', '6', 'Przyjmujcie cierpliwie to, co służy waszemu wychowaniu. Bóg was traktuje jak synów. Czy jest syn, którego ojciec nie karci?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '7', '7', 'Gdyby was nie karcono jak karci się wszystkich, bylibyście potomstwem nieprawym a nie synami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '8', '8', 'Poza tym mieliśmy na wychowawców naszych ojców i nie wstydziliśmy się, kiedy nas karcili. Czyż tym bardziej nie będziemy ulegli Ojcu dusz, aby żyć?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '9', '9', 'Oni przecież na krótkie dni wychowywali nas tak, jak potrafili. On zaś (czyni to) dla naszego dobra, abyśmy uczestniczyli w Jego świętości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '10', '10', 'Każde  zaś karcenie z początku nie wydaje się przyjemne, lecz raczej przykre, ale potem przynosi wyćwiczonym w karności słodki owoc sprawiedliwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '11', '11', 'Przeto \'wyprostujcie znowu zwiotczałe ręce i osłabione kolana\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '12', '12', 'i \'stawiajcie proste kroki\', aby kulejący nie upadł, lecz został uleczony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '13', '13', 'Starajcie się o pokój ze wszystkimi i o uświęcenie, bez którego nikt nie będzie oglądał Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '14', '14', 'Strzeżcie się, aby nikt z was nie był opieszały wobec łaski Bożej, aby jakiś gorzki korzeń wypuszczający pęd w górę nie wywołał zamieszania i aby nie zaraził wielu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '15', '15', 'aby nie było rozpustnego lub lekkomyślnego jak Ezaw, który za miskę strawy sprzedał swoje pierworodztwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '16', '16', 'Wiecie przecież, że i potem, kiedy chciał uzyskać błogosławieństwo, został odrzucony, chociaż ze łzami dopraszał się o nie, bo nie mógł już nic zmienić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '17', '17', 'Nie przystąpiliście bowiem do góry, której można dotknąć, i do ognia, który płonie, do ciemności, chmury, burzy, (nie słyszeliście)');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '18', '18', 'brzmienia trąby i dźwięku słów. Ci, którzy to słyszeli, prosili, aby (Bóg) więcej do nich nie przemawiał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '19', '19', 'Nie mogli bowiem znieść tego, co było nakazane: \'Nawet jeśliby zwierzę dotknęło góry, ma być ukamienowane\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '20', '20', 'A tak straszliwe było to widowisko, że Mojżesz rzekł: \'Przerażony jestem\' i drżę z bojaźni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '21', '21', 'Wy jednak przystąpiliście do góry Syjonu i do miasta Boga żywego, do niebieskiej Jerozolimy i do wielkiej liczby aniołów, do uroczystego zebrania');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '22', '22', 'i do zgromadzenia pierwotnych zapisanych w niebie i do sędziego, Boga wszystkich, i do duchów (ludzi) sprawiedliwych udoskonalonych w pełni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '23', '23', 'i do Jezusa, pośrednika Nowego Przymierza, i do pokropienia krwią wołającą głośniej niż Abel.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '24', '24', 'Strzeżcie się, żebyście nie odtrącili Przemawiającego do was. Jeśli bowiem oni nie uszli kary, odtrąciwszy tego, który mówił do nich na ziemi, to tym bardziej my nie ujdziemy kary, wzgardziwszy Przemawiającym do nas z nieba,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '25', '25', 'którego głos wstrząsnął wtedy ziemią, a teraz zapowiedział tymi słowy: \'Jeszcze raz wstrząsnę\' nie tylko \'ziemią\', lecz także \'niebem\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '26', '26', 'Owo zaś \'jeszcze raz\' wskazuje na przemienienie stworzeń, które podlegają zmianom, aby trwało to, co zmianom nie podlega.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '27', '27', 'Dlatego otrzymując trwałe królestwo, bądźmy wdzięczni. Tak najlepiej służyć będziemy Bogu z czcią i bojaźnią Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '11', '28', '28', 'bo przecież nasz \'Bóg jest ogniem trawiącym\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '0', '0', 'Miłość braterska niech zawsze trwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '1', '1', 'Nie zapominajcie o gościnności, bo dzięki niej niektórzy, nie wiedząc o tym, ugościli aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '2', '2', 'Pamiętajcie o więźniach, jakbyście razem z nimi byli uwięzieni, o dręczonych, bo przecież i sami jesteście ludźmi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '3', '3', 'Małżeństwo niech będzie we czci u wszystkich, a łoże małżeńskie niech będzie nieskalane, Bóg bowiem osądzi rozpustników i cudzołożników.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '4', '4', 'Unikajcie chciwości, bądźcie zadowoleni z tego, co posiadacie - On sam bowiem powiedział: \'Ani cię nie opuszczę, ani też cię nie zostawię\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '5', '5', 'Będąc dobrej myśli, mówcie: \'Pan jest mi obrońcą, a nie będę się lękał, cóż mi zrobi człowiek?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '6', '6', 'Pamiętajcie o waszych przełożonych, którzy głosili wam słowo Boże, a rozważając ostatnie chwile ich życia naśladujcie ich wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '7', '7', 'Jezus Chrystus jest ten sam wczoraj, dzisiaj i na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '8', '8', 'Nie dajcie się zwodzić rozmaitym obcym naukom, dobrze jest bowiem umacniać serce łaską, a nie pokarmami nic nie dającymi tym, którzy o nie zabiegają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '9', '9', 'Mamy ołtarz, z którego nie mają prawa spożywać ci, którzy służą namiotowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '10', '10', 'Mięso tych zwierząt, których krew wnosi arcykapłan do sanktuarium jako ofiarę za grzech, spala się przecież poza obozem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '11', '11', 'Dlatego też Jezus, aby uświęcić lud własną krwią, poniósł mękę poza bramą (miasta).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '12', '12', 'Wyjdźmy przeto do Niego poza obóz, dzieląc z Nim Jego zniesławienie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '13', '13', 'Nie mamy przecież tutaj trwałego miasta, ale dążymy do tego, które nas czeka w przyszłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '14', '14', 'Przez Niego więc składajmy Bogu ustawicznie ofiarę uwielbienia, to jest owoc ust, które wysławiają Jego imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '15', '15', 'Nie zapominajcie zaś o czynieniu dobra i zachowaniu jedności, bo takimi ofiarami cieszy się Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '16', '16', 'Ufajcie waszym przełożonym i bądźcie im posłuszni, bo oni odpowiadając za was czuwają nad waszymi duszami. Niech czynią to z radością a nie z narzekaniem, bo to przyniosłoby wam szkodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '17', '17', 'Módlcie się za nas, jesteśmy bowiem przekonani, że mamy czyste sumienie i pragniemy we wszystkim dobrze postępować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '18', '18', 'Proszę, módlcie się zwłaszcza o to, abym mógł jak najszybciej do was powrócić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '19', '19', 'A Bóg pokoju, Ten, który przez krew wiecznego przymierza wyprowadził spomiędzy umarłych wielkiego Pasterza owiec, Pana naszego Jezusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '20', '20', 'oby was udoskonalił we wszystkim, co dobre, abyście spełniali Jego wolę. Niech sprawia w was to, co jest Mu miłe, przez Jezusa Chrystusa, któremu chwała na wieki (wieków). Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '21', '21', 'Proszę zaś was, bracia, znieście cierpliwie te napomnienia, krótko je przecież ująłem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '22', '22', 'Wiedzcie, że nasz brat Tymoteusz został uwolniony. Jeżeli się pospieszy, to razem was odwiedzimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '23', '23', 'Pozdrówcie wszystkich waszych przełożonych i wszystkich świętych. Pozdrawiają was (bracia) z Italii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'hebr', '12', '24', '24', 'Łaska z wami wszystkimi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '0', '0', 'Jakub, sługa Boga i Pana Jezusa Chrystusa, (przesyła) pozdrowienie dwunastu pokoleniom w diasporze!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '1', '1', 'Bracia moi, przyjmujcie z pełną radością rozmaite doświadczenia, przez które przechodzicie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '2', '2', 'świadomi, że próba wiary waszej rodzi wytrwałość!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '3', '3', 'Wytrwałość zaś niech przejawia się w doskonałym czynie, abyście (sami) byli doskonali, nienaganni, w niczym nie uchybiając.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '4', '4', 'Jeżeli zaś komu z was brakuje mądrości, niech błaga Boga, który wszystkich chętnie obdarza - niczego nie wypominając - a będzie mu dana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '5', '5', 'A niech błaga z wiarą i niezachwianie, bo chwiejący się podobny jest do morskiej fali, którą wicher pędzi i miota.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '6', '6', 'Niech taki człowiek nie sądzi, że cokolwiek otrzyma od Pana:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '7', '7', 'to mąż wewnętrznie rozdarty, niestały w całym swym postępowaniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '8', '8', 'A brat ubogi niech się chlubi swoim wywyższeniem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '9', '9', 'bogacz zaś - swoim uniżeniem, gdy przeminie niby kwiat polnej trawy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '10', '10', 'Bo wzeszło słońce a jego żar wypalił trawę; kwiat jej opadł i stracił swój powab. Tak też obrócą się wniwecz przedsięwzięcia bogacza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '11', '11', 'Szczęśliwy ten człowiek, który zwycięży pokusę! Gdy bowiem ostoi się w próbie, otrzyma wieniec życia, jaki Pan obiecał tym, którzy Go miłują.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '12', '12', 'Niech nikt wśród pokus nie mówi: Jestem kuszony przez Boga. Bóg bowiem nie podlega pokusie do złego, ani też sam nikogo nie kusi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '13', '13', 'Każdy bowiem doznaje pokusy od własnej pożądliwości, która go pociąga i nęci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '14', '14', 'Potem, gdy pożądliwość już zapanuje, rodzi grzech, a grzech popełniony sprowadza śmierć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '15', '15', 'Nie ulegajcie błędom, bracia moi umiłowani!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '16', '16', 'Każde dobro i wszelki dar doskonały przychodzą (do nas) z góry od Ojca światłości, który nie podlega zmianom i kolejnym zaćmieniom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '17', '17', 'Z własnej woli zrodził nas przez słowo Prawdy, abyśmy byli jakby zaczątkiem Jego stworzeń.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '18', '18', 'Wiedzcie (o tym), bracia moi umiłowani!                                                                                                Mamy obowiązki wobec ewangelii                                                                                                    Niech więc każdy będzie chętny do słuchania, nieskory do mówienia i nieskory do gniewu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '19', '19', 'Bo rozgniewany człowiek nie postępuje zgodnie ze sprawiedliwością Bożą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '20', '20', 'Dlatego odrzuciwszy wszelki brud i bezmiar zła, z wdzięcznością przyjmijcie wszczepione w was słowo, zdolne zbawić wasze dusze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '21', '21', 'Wprowadzajcie słowo w czyn, a nie bądźcie tylko słuchaczami, którzy oszukują samych siebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '22', '22', 'Jeśli ktoś tylko słucha słowa, a nie wprowadza go w czyn, podobny jest do człowieka, który przygląda się w lustrze swojemu odbiciu:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '23', '23', 'przyjrzał się sobie, odszedł i zaraz zapomniał jak wyglądał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '24', '24', 'Kto zaś uważnie wniknął w doskonałe Prawo wolności i wytrwał w nim, stając się nie słuchaczem zapominającym, ale wykonawcą dzieła, ten będzie szczęśliwy dzięki swemu postępowaniu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '25', '25', 'Jeżeli ktoś uważa się za człowieka pobożnego, ale - zwodząc swe serce - nie panuje nad swoim językiem, to jego pobożność jest bezwartościowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '0', '26', '26', 'Szczera i nieskazitelna wobec Boga i Ojca pobożność - to troska o sieroty i wdowy w ich niedoli, niewinność i wolność od (wpływów tego) świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '0', '0', 'Bracia moi, okazujcie waszą wiarę w Pana naszego Jezusa, uwielbionego Mesjasza, i nie czyńcie różnicy między ludźmi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '1', '1', 'Gdyby bowiem przyszedł na wasze zgromadzenie człowiek wspaniale ubrany ze złotym pierścieniem na palcu i wszedłby także biedak nędznie odziany,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '2', '2', 'a wy spojrzawszy na ubranego wspaniale powiedzielibyście mu: Siądź tutaj, na pierwszym miejscu! - a biedakowi powiedzielibyście: \'Stój tam! albo: Siadaj u mojego podnóżka! -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '3', '3', 'to czyż nie czynilibyście różnic między sobą i nie osądzali niesprawiedliwie?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '4', '4', 'Słuchajcie, bracia moi umiłowani! Czyż Bóg nie wybrał tych, których  świat uważa za biednych, na bogatych w wierze i na dziedziców królestwa obiecanego tym, którzy Go miłują?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '5', '5', 'Wy zaś okazaliście wzgardę ubogiemu. Czyż to nie bogacze was gnębią i czy nie oni wodzą was po sądach?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '6', '6', 'Czyż to nie oni bluźnią wzniosłemu Imieniu, którego wzywano nad wami?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '7', '7', 'Jeżeli jednak wypełniacie królewskie Prawo zgodnie z Pismem: \'Będziesz miłował bliźniego swego jak siebie samego\', to dobrze postępujecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '8', '8', 'Jeżeli zaś czynicie różnice między ludźmi, popełniacie grzech, piętnowani przez (samo) Prawo jako przestępcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '9', '9', 'Kto zachowuje całe Prawo, a w jednej rzeczy wykracza, wykracza przeciw całemu (Prawu).');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '10', '10', 'Ten bowiem, który powiedział: \'Nie cudzołóż!\', powiedział także: \'Nie zabijaj!\' Jeśli więc nie dopuszczasz się cudzołóstwa, ale zabijasz, naruszyłeś Prawo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '11', '11', 'Tak mówcie i tak postępujcie jak ci, którzy mają być sądzeni przez Prawo wolności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '12', '12', 'Sąd bowiem bez miłosierdzia nad tym, kto nie okazywał miłosierdzia. Miłosierdzie jest ponad sądem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '13', '13', 'Cóż pomoże, bracia moi, jeśliby ktoś twierdził, że wierzy, a nie spełniałby uczynków? Czyż taka wiara może go zbawić?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '14', '14', 'Gdyby brat lub siostra nie mieli się w co ubrać i brakło im codziennego chleba,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '15', '15', 'a ktoś z was im powie: Idźcie w pokoju, ogrzejcie się i nasyćcie - a nie da im tego, co jest konieczne do życia, to cóż to pomoże?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '16', '16', 'Tak też wiara nie przejawiająca się w czynach martwa jest sama w sobie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '17', '17', 'Lecz powie ktoś: Ty masz wiarę, ja zaś mam uczynki. - Pokaż mi twoją wiarę bez uczynków, a ja z uczynków moich pokażę ci wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '18', '18', 'Ty wierzysz, że Bóg jest jeden? - Dobrze czynisz; ale demony też wierzą i drżą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '19', '19', 'A czy chcesz przekonać się, słaby człowiecze, że wiara bez uczynków jest bezowocna?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '20', '20', 'Czyż praojciec nasz Abraham nie dzięki uczynkom został usprawiedliwiony, złożywszy syna Izaaka na ołtarzu ofiarnym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '21', '21', 'Widzisz więc, że wiara współdziała z jego uczynkami i dzięki uczynkom stała się doskonałą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '22', '22', 'Tak wypełniło się Pismo, które mówi: \'Uwierzył Abraham Bogu i poczytano mu to za sprawiedliwość\', i nazwany został przyjacielem Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '23', '23', 'Widzicie więc, że człowiek dostępuje usprawiedliwienia dzięki uczynkom, a nie przez samą tylko wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '24', '24', 'Podobnie tak i nierządnica Rahab czyż nie dzięki uczynkom dostąpiła usprawiedliwienia, przyjmując zwiadowców a inną drogą ich wyprawiając?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '1', '25', '25', 'Jak bowiem ciało bez ducha jest martwe, tak też martwa jest wiara bez uczynków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '0', '0', 'Bracia moi, niech zbyt wielu z was nie uważa się za nauczycieli, wiedząc, że surowszy sąd będzie naszym udziałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '1', '1', 'Wszyscy bowiem błądzimy w wielu rzeczach. Kto nie grzeszy słowami, ten jest człowiekiem doskonałym, zdolnym też całego siebie trzymać na wodzy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '2', '2', 'Bo jeżeli wkładamy w pyski koniom wędzidła, aby były nam uległe, to kierujemy nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '3', '3', 'Oto i okręty, choć duże i silnymi wichrami pędzone, kierowane są maleńkim sterem tam, dokąd zechce wola sternika.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '4', '4', 'Tak i język jest wprawdzie niewielki, ale chlubi się wielkimi rzeczami. Oto maleńki ogień podpala wielki las.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '5', '5', 'I język - to ogień, (cały) świat nieprawości! Język jest tym naszym członkiem, który plami całego człowieka. Spala bieg naszego życia, sam rozpalany przez piekło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '6', '6', 'Wszystkie bowiem gatunki zwierząt i ptaków, płazów i stworzeń morskich można ujarzmić i (rzeczywiście) zostały one ujarzmione przez rodzaj ludzki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '7', '7', 'Języka natomiast nikt z ludzi ujarzmić nie zdoła; niespokojne to zło, pełne śmiercionośnego jadu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '8', '8', 'Wysławiamy nim Pana i Ojca, nim też złorzeczymy ludziom stworzonym na podobieństwo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '9', '9', 'Z tych samych ust wychodzi błogosławieństwo i złorzeczenie. Nie powinno tak być, bracia moi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '10', '10', 'Czyż z tego samego źródła tryska równocześnie (woda) słodka i gorzka?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '11', '11', 'Czyż może, bracia moi, drzewo figowe rodzić oliwki albo krzew winny - figi? Tak i słone źródło nie da wody słodkiej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '12', '12', 'Kto wśród was jest mądry i rozumny? Niech to pokaże dobrym postępowaniem, swymi czynami dobrymi i mądrymi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '13', '13', 'Jeżeli jednak gorzką zazdrość i kłótliwość żywicie w swych sercach, nie macie się czym przechwalać; zadajecie kłam prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '14', '14', 'To nie jest mądrość przychodząca z góry, lecz przyziemna, zmysłowa, szatańska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '15', '15', 'Gdzie bowiem zazdrość i kłótliwość, tam niestałość i wszelki czyn przewrotny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '16', '16', 'Natomiast mądrość (pochodząca) z góry jest przede wszystkim skromna, dalej pełna pokoju, pobłażliwa, uległa, pełna miłosierdzia i dobrych owoców, jest stała i szczera.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '2', '17', '17', 'A owoc sprawiedliwości w pokoju wschodzi dla tych, którzy pokój czynią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '0', '0', 'Skąd (pochodzą) spory i skąd walki wśród was? Czyż nie z waszych namiętności podnoszących bunt z was samych?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '1', '1', 'Pożądacie - a nie osiągacie, zabijacie i zabiegacie gorliwie - a nie możecie zdobyć; prowadzicie spory i walki. Nie osiągacie - bo nie prosicie;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '2', '2', '(a jeśli) prosicie - nie otrzymujecie, bo źle się modlicie, myśląc (tylko) o zaspokojeniu swoich pragnień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '3', '3', 'Wiarołomni, czyż nie wiecie, że przyjaźń z tym światem oznacza nieprzyjaźń z Bogiem? Kto bowiem chciałby być przyjacielem świata, stałby się nieprzyjacielem Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '4', '4', 'Albo czyż sądzicie, że na próżno Pismo powiada: \'Niemal zazdrośnie strzeże (On) ducha, któremu w nas zgotował mieszkanie\'?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '5', '5', 'Owszem, większą daje łaskę, skoro mówi: \'Bóg pysznym się sprzeciwia, a łaskę daje pokornym\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '6', '6', 'Bądźcie więc ulegli Bogu, a przeciwstawiajcie się szatanowi, wtedy ucieknie od was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '7', '7', 'Zbliżajcie się do Boga, a On przybliży się do was. Oczyśćcie ręce, grzesznicy, i uświęćcie serca, ludzie rozdarci wewnętrznie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '8', '8', 'Odczuwając swą marność, biadajcie i płaczcie! Śmiech wasz niech zamieni się w żałość, a radość wasza - w smutek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '9', '9', 'Uniżcie się przed Panem, a On was wywyższy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '10', '10', 'Bracia! Nie ubliżajcie sobie nawzajem! Kto ubliża bratu albo osądza brata swego, uwłacza Prawu i osądza Prawo. Jeśli zaś osądzasz Prawo, nie jesteś wykonawcą Prawa, lecz sędzią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '11', '11', '(A przecież) jeden jest (tylko) Prawodawca i Sędzia, który może zbawiać i zatracać. Kim natomiast jesteś ty, który osądzasz bliźniego?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '12', '12', 'A teraz wy, którzy mówicie: Dzisiaj lub jutro udamy się do tego lub innego miasta, spędzimy tam rok, będziemy handlować i ciągnąć zyski! -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '13', '13', 'Wy, którzy nie wiecie, co jutro przyniesie! Bo czymże jest wasze życie? Dymem jesteście, który zjawia się na chwilę, a potem znika!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '14', '14', 'Powinniście natomiast mówić: Jeżeli Pan zechce, dożyjemy i zrobimy to lub owo. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '15', '15', 'Teraz zaś chełpicie się i przechwalacie. Wszelka taka chełpliwość jest zła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '3', '16', '16', 'Ten zaś, kto umie dobrze czynić, a nie czyni - dopuszcza się grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '0', '0', 'A teraz wy, bogacze, opłakujcie gorzko cierpienia, które was czekają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '1', '1', 'Przepadło wasze bogactwo, mole pożarły szaty!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '2', '2', 'Złoto i srebro zardzewiało, ta rdza będzie świadczyć przeciwko wam i zniszczy was jak ogień. Nagromadziliście sobie skarbów na dni (już) ostateczne!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '3', '3', 'Oto nie wypłacona przez was należność robotników, którzy żęli wasze łany, woła (o pomstę), i wołania żeńców dotarły do Pana Zastępów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '4', '4', 'Żyliście na ziemi w zbytku i rozkoszy, utuczyliście się na dzień rzezi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '5', '5', 'Potępiliście i zabili sprawiedliwego, a on się nie bronił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '6', '6', 'Wytrwajcie więc cierpliwie, bracia, aż do przyjścia Pana! Oto rolnik, wyczekuje drogocennego plonu ziemi, cierpliwie wypatrując, aż spadnie deszcz jesienny i wiosenny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '7', '7', 'I wy także trwajcie cierpliwie i umacniajcie swe serca, gdyż bliskie jest przyjście Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '8', '8', 'Nie uskarżajcie się, bracia, jedni na drugich, abyście nie byli sądzeni. Oto Sędzia (już) stoi u drzwi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '9', '9', 'W znoszeniu trudów i w cierpliwości bierzcie, bracia, przykład z proroków, którzy przemawiali w imieniu Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '10', '10', 'Oto jako błogosławionych czcimy tych, którzy wytrwali. Słyszeliście o wytrwałości Joba i jak Pan go wynagrodził, bo Pan jest miłosierny i litościwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '11', '11', 'Przede wszystkim zaś, bracia moi, nie przysięgajcie: ani na niebo, ani na ziemię, ani na nic innego się nie zaklinajcie. Niech wasze \'tak\' - znaczy \'tak\', a \'nie\' - \'nie\', abyście nie byli sądzeni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '12', '12', 'Cierpi kto pośród was? Niech się modli! Weseli się kto? Niech śpiewa psalmy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '13', '13', 'Choruje ktoś wśród was? Niech wzywa prezbiterów Kościoła i niech się modlą nad nim, namaszczając go olejem w imię Pana!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '14', '14', 'A modlitwa (płynąca z) wiary zbawi dotkniętego słabością i Pan mu ulży, a jeśliby popełnił grzechy, będą mu odpuszczone.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '15', '15', 'Wyznawajcie więc jedni drugim grzechy i módlcie się wzajemnie za siebie, abyście zostali uzdrowieni! Wielką ma moc wytrwała modlitwa sprawiedliwego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '16', '16', 'Eliasz był człowiekiem podobnie jak my podległym cierpieniu, a usilnie się modlił o to, by nie padał deszcz, i nie spadł deszcz na ziemię przez trzy lata i sześć miesięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '17', '17', 'A kiedy znów się pomodlił, niebo zesłało deszcz i ziemia wydała swój plon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '18', '18', 'Bracia moi! Jeśliby ktoś z was zboczył z (drogi) prawdy, a ktoś inny nawróciłby go,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jas', '4', '19', '19', 'niech wie, że ten, kto nawrócił grzesznika z błędnej drogi, wybawi duszę jego od śmierci i przyczyni się do odpuszczenia wielu grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '0', '0', 'Piotr, apostoł Jezusa Chrystusa - do przychodniów w diasporze Pontu, Galacji, Kapadocji, Azji i Bitynii,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '1', '1', 'do wybranych zgodnie z uprzednim planem Boga Ojca, przez uświęcenie w Duchu, do uległości (ewangelii) i do pokropienia Krwią Jezusa Chrystusa! Łaska i pokój niech będzie w pełni waszym udziałem!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '2', '2', 'Niech będzie uwielbiony Bóg i Ojciec Pana naszego Jezusa Chrystusa, który w swoim wielkim miłosierdziu przez zmartwychwstanie Jezusa Chrystusa zrodził nas na nowo do nadziei pełnej życia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '3', '3', 'do niezniszczalnego, nieskalanego, wiecznego dziedzictwa, zachowanego dla was w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '4', '4', 'Wy zaś strzeżeni jesteście mocą Bożą przez wiarę, aby przygotowane zbawienie objawiło się w czasie ostatecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '5', '5', 'Dlatego radujecie się, chociaż na razie różne doświadczenia zasmucają was czasem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '6', '6', 'aby wasza wypróbowana wiara okazała się daleko cenniejsza od zniszczalnego złota, które próbuje się w ogniu. Przyniesie to wam chwałę, cześć i sławę w dniu objawienia się Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '7', '7', 'Jego to miłujecie, chociaż nie widzieliście Go, a i teraz nie widząc - wierzycie w Niego, a wierząc - cieszyć się będziecie radością niewymowną i pełną chwały,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '8', '8', 'gdy osiągniecie cel waszej wiary - zbawienie dusz!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '9', '9', 'Zbawienie to było przedmiotem rozważań i dociekań proroków, którzy mówili z natchnienia Bożego o łasce dla was (przewidzianej),');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '10', '10', 'badając, na kogo i na jaki czas wskazywał działający w nich Duch Chrystusa, naprzód świadcząc o cierpieniach Chrystusa i o uwielbieniach, jakie po nich nastąpią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '11', '11', 'Zostało im objawione, że nie im, lecz raczej wam służyło to, co teraz zwiastowali wam głosiciele ewangelii za sprawą Ducha Świętego zesłanego z nieba, a w co wgłębić się pragną (nawet) aniołowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '12', '12', 'Dlatego czuwajcie nad sobą w pełni trzeźwi, pokładajcie swoją nadzieję w łasce, którą wam daje objawienie Jezusa Chrystusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '13', '13', 'bądźcie posłuszni, nie kierujcie się dawnymi pożądliwościami, którym ulegaliście, gdy żyliście (jeszcze) w nieświadomości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '14', '14', 'lecz w całym postępowaniu stańcie się świętymi, tak jak świętym jest Ten, który was powołał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '15', '15', 'Napisane jest bowiem: \'Świętymi bądźcie, bo Ja jestem Święty!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '16', '16', 'A skoro Ojcem nazywacie Tego, który sądzi bezstronnie każdego według jego uczynków, bogobojnie przeżywajcie czas waszego pielgrzymowania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '17', '17', 'świadomi, że nie srebro ani złoto, których wartość przemija, było okupem uwalniającym was z marnego sposobu życia odziedziczonego po przodkach,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '18', '18', 'ale drogocenna Krew Chrystusa - Baranka niepokalanego i bez skazy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '19', '19', 'Był on przewidziany wprawdzie już przed stworzeniem świata, ale został objawiony w czasach ostatecznych ze względu na was.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '20', '20', 'Przez Niego uwierzyliście w Boga, który wskrzesił Go z martwych i obdarzył chwałą, tak że wasza wiara i nadzieja jest w Bogu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '21', '21', 'Oczyściwszy dusze przez posłuszeństwo Prawdzie, gorąco miłujcie się wzajemnie czystym sercem, aby osiągnąć szczerą miłość braterską');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '22', '22', 'i narodzić się na nowo nie z nasilenia, które podlega zniszczeniu, lecz (z nasienia) niezniszczalnego, przez słowo Boga, który żyje i trwa na wieki!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '23', '23', '\'Bo wszelkie stworzenie jest niby trawa i wszelka jego wspaniałość niby kwiat polny! Usycha trawa, kwiat więdnie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '0', '24', '24', 'ale słowo Pana trwa na wieki!\' Ono właśnie jest ogłoszoną wam dobrą nowiną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '0', '0', 'Odrzuciwszy więc wszelkie zło i fałsz, obłudę, zazdrości i wszelkie obmowy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '1', '1', 'jak nowo narodzone niemowlęta pragnijcie duchowego, niesfałszowanego mleka, abyście dzięki niemu rośli i osiągnęli zbawienie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '2', '2', 'Wszak \'doświadczyliście, że dobry jest Pan!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '3', '3', 'Przystępując do Niego, do żywego Kamienia, odrzuconego wprawdzie przez ludzi, ale przez Boga wybranego i okrytego chwałą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '4', '4', 'sami również jak żywe kamienie budujcie swój dom duchowy, święte kapłaństwo, aby przez Jezusa Chrystusa składać ofiary duchowe, miłe Bogu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '5', '5', 'Bo w Piśmie Świętym jest (zdanie): \'Oto kładę na Syjonie kamień węgielny, wybrany, drogocenny, a ten, kto weń wierzy, nie zawstydzi się!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '6', '6', 'Wam przeto, wierzącym, chwała! Dla niewierzących zaś ten \'Kamień, który odrzucili budujący, stał się podstawą węgła\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '7', '7', 'oraz \'Kamieniem potknięcia  i skałą zgorszenia\'. Oni nieposłuszni słowu potykają się; taki ich los spotyka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '8', '8', 'Wy natomiast jesteście \'plemieniem wybranym, królewskim kapłaństwem, narodem świętym, ludem odkupionym\'. \'abyście głosili chwalebne dzieła\' Tego, który powołał was z ciemności do swojego przedziwnego światła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '9', '9', 'Niegdyś byliście \'nie-ludem\', teraz zaś jesteście \'ludem Bożym\', (niegdyś) byliście \'tymi, co nie doznawali miłosierdzia\', teraz zaś jesteście \'tymi, którzy miłosierdzia dostąpili\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '10', '10', 'Umiłowani! Upominam was jako przechodniów i pielgrzymów, abyście nie ulegali złym ludzkim pożądliwościom, które walczą przeciwko duszy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '11', '11', 'żyjąc wśród pogan, starajcie się szlachetnie postępować, aby patrząc na (wasze) dobre uczynki, wielbili Boga w dzień nawiedzenia za to, co wam (dziś) zarzucają jak złoczyńcom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '12', '12', 'Ze względu na Pana bądźcie ulegli każdej zwierzchności ludzkiej, czy to królowi sprawującemu najwyższą władzę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '13', '13', 'czy to namiestnikom, którzy z jego polecenia karzą złoczyńców i udzielają pochwały dobrym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '14', '14', 'Taka jest bowiem wola Boga, abyście dobrze czyniąc zamykali usta ludziom nierozsądnym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '15', '15', 'Nie (postępujcie) jak ci, którzy zasłaniają się wolnością, aby czynić zło, ale jak ludzie naprawdę wolni, jak słudzy Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '16', '16', 'Szanujcie wszystkich, miłujcie braci, zachowujcie bojaźń Bożą, szanujcie króla.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '17', '17', 'Służba domowa niech z lękiem i czcią będzie uległa swoim panom nie tylko dobrym i łagodnym, ale i surowym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '18', '18', 'Jest to bowiem zasługą, jeżeli ktoś z myślą o Bogu znosi przykrości, cierpiąc niesłusznie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '19', '19', 'Jakaż to bowiem chwała, jeżeli znosicie policzkowanie za grzechy? Ale jest to zasługą u Boga, jeżeli znosicie cierpienia, czyniąc dobrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '20', '20', 'Do tego bowiem jesteście powołani,  gdyż i Chrystus cierpiał za was, pozostawiając wam wzór, abyście postępowali Jego śladami!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '21', '21', 'On, \'który nie dopuścił się grzechu, ani fałsz nie znalazł się w Jego ustach\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '22', '22', 'Gdy Mu złorzeczono, nie odpłacał złorzeczeniem, cierpiąc nie odgrażał się, zawierzywszy Temu, który sądzi sprawiedliwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '23', '23', 'On na swoich ramionach wyniósł nasze grzechy na drzewo (krzyża), abyśmy umarłszy dla grzechu żyli dla świętości! Jego to ranami zostaliście uzdrowieni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '1', '24', '24', 'Byliście bowiem jak owce zbłąkane, ale powróciliście do Pasterza i Stróża dusz waszych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '0', '0', 'Podobnie żony bądźcie posłuszne swoim mężom, aby nawet ci z nich, którzy nie słuchają słowa (ewangelii), dzięki sposobowi życia swoich żon zostali pozyskani (dla wiary), (nawet) bez słowa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '1', '1', 'widząc nienaganny i pełen bojaźni (Bożej) sposób waszego życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '2', '2', 'Niech ozdobą ich nie będzie to, co zewnętrzne: piękne uczesanie, obwieszanie się złotem ani też wyszukany krój sukni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '3', '3', 'Niech będzie nią niezniszczalne wnętrze człowieka, w którym kryje się łagodne serce i duch spokojny, tak wysoko ceniony przez Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '4', '4', 'Tak samo niegdyś przyozdabiały się święte niewiasty, pokładając w Bogu nadzieję, posłuszne swoim mężom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '5', '5', 'Tak Sara była posłuszna Abrahamowi, nazywając go panem. Jesteście jej córkami, jeżeli dobrze czynicie i nie lękacie się żadnego zastraszenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '6', '6', 'Podobnie mężowie we wspólnym pożyciu z żonami pamiętajcie o tym, że one są słabsze, i darzcie je szacunkiem jako współuczestniczące w łasce życia, aby wasze modlitwy nie napotykały przeszkód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '7', '7', 'Na koniec - wszyscy (bądźcie) jednomyślni, pełni współczucia, miłości braterskiej, miłosierdzia i pokory!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '8', '8', 'Nie odpłacajcie złem za złe ani złorzeczeniem za złorzeczenie, lecz przeciwnie: błogosławcie, gdyż do tego jesteście powołani, abyście błogosławieństwo otrzymali w dziedzictwie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '9', '9', '\'Ten bowiem, kto chce życie miłować i oglądać dni szczęśliwe, niech powstrzymuje swój język od złego i wargi od fałszu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '10', '10', 'Niech unika zła, a czyni dobro, niech szuka pokoju i za nim idzie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '11', '11', 'gdyż oczy Pana (zwrócone są) na prawych i Jego uszy słuchają ich błagania, lecz oblicze Pana przeciwko tym, którzy źle czynią\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '12', '12', 'I któż wam zaszkodzi, jeżeli gorliwie zabiegać będziecie o dobro?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '13', '13', 'Lecz jeślibyście nawet doznali jakiegoś cierpienia dla sprawiedliwości, to szczęśliwi jesteście! \'A groźby ich się nie lękajcie ani nie poddawajcie się trwodze\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '14', '14', 'lecz Pana - Chrystusa pobożnie czcijcie w sercach waszych, zawsze gotowi do obrony przed każdym, kto żąda od was uzasadnienia waszej nadziei.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '15', '15', 'Ale (czyńcie to) z łagodnością i z szacunkiem. Zachowujcie czyste sumienie, aby ci, którzy obrzucają obelgami wasze uczciwe życie w Chrystusie, musieli się wstydzić swoich oszczerstw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '16', '16', 'Lepiej bowiem cierpieć - jeśli taka byłaby wola Boża - dobrze czyniąc, niż (cierpieć) za złe postępowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '17', '17', 'Bo i Chrystus raz jeden cierpiał za grzechy, Sprawiedliwy za niesprawiedliwych, aby was doprowadzić do Boga; poniósłszy śmierć jako człowiek, został wskrzeszony do życia przez Ducha,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '18', '18', 'mocą którego poszedł też głosić naukę duchom (uwięzionym) w otchłani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '19', '19', 'Były one nieposłuszne wtedy, gdy za dni Noego Bóg cierpliwie czekał i budowana była arka, w której niewielu, to jest osiem osób, zostało ocalonych przez wodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '20', '20', 'Była ona obrazem tej (wody), która zbawia was również teraz przez chrzest: nie przez usunięcie brudu ciała, ale przez modlitwę do Boga o czyste sumienie, dzięki zmartwychwstaniu Jezusa Chrystusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '2', '21', '21', 'który wstąpiwszy do nieba, siedzi po prawicy Boga, a poddani Mu są Aniołowie, Moce i Potęgi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '0', '0', 'Skoro więc Chrystus cierpiał za życia na ziemi, wy również uzbrójcie się tą myślą, że kto za życia na ziemi cierpiał (z Chrystusem), wyrzeka się grzechu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '1', '1', 'aby jego dalsze życie na ziemi wolne już było od ludzkich namiętności, a płynęło według woli Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '2', '2', 'Bo w minionym czasie dosyć już ulegaliście woli pogan żyjąc w rozwiązłości, namiętnościach, pijaństwie, ucztach, hulankach i służąc niegodziwym i fałszywym bóstwom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '3', '3', 'Lżąc (was) dziwią się teraz temu, że już razem z nimi nie rzucacie się w nurt tego samego zepsucia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '4', '4', 'Zdadzą oni sprawę Temu, który gotów jest (już) sądzić żywych i umarłych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '5', '5', 'Po to bowiem nawet umarłym głoszona była ewangelia, aby - skazani wprawdzie na śmierć wszyscy w ciele jako ludzie (żyjący na ziemi) - żyli jednak duchem po Bożemu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '6', '6', 'A bliski jest koniec wszystkiego. Postępujcie więc roztropnie, czuwajcie i módlcie się!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '7', '7', 'Przede wszystkim zaś kochajcie się wzajemnie z całego serca, bo \'miłość przyczynia się do odpuszczenia wielu grzechów\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '8', '8', 'Bez szemrania przyjmujcie jedni drugich w gościnę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '9', '9', 'służcie sobie wzajemnie otrzymanymi darami, jak dobrzy szafarze różnorodnej łaski Bożej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '10', '10', 'Jeżeli ktoś przemawia, niech będą to jakby słowa Boże! Jeśli kto usługuje, niech to (czyni) mocą, jakiej Bóg udziela, aby we wszystkim wielbiony był Bóg przez Jezusa Chrystusa. Jemu chwała i moc na całą wieczność! Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '11', '11', 'Umiłowani! Nie dziwcie się pożodze rozszalałej wśród was dla waszego wypróbowania, jakby spotykało was coś niezwykłego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '12', '12', 'Ale gdy uczestniczycie w cierpieniach Chrystusa, radujcie się, abyście mogli jeszcze bardziej się radować, gdy Jego chwała się objawi!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '13', '13', 'Jeśli doznajecie zniewag dla imienia Chrystusa, szczęśliwi jesteście, bo Duch chwały i (Duch) Boga na was spoczywa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '14', '14', 'Nikt bowiem z was nie powinien cierpieć jako zabójca lub złodziej, złoczyńca czy mieszający się do cudzych spraw.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '15', '15', 'Jeśli natomiast (cierpi) jako chrześcijanin, niech się nie wstydzi, lecz niech po chrześcijańsku wielbi Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '16', '16', 'Czas bowiem stosowny, aby sąd rozpoczął się od domu Bożego. A skoro najpierw od nas, to jaki będzie koniec tych, którzy nieposłuszni są ewangelii Bożej?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '17', '17', 'I skoro sprawiedliwy z trudem się zbawia, to jakże ostoi się bezbożnik i grzesznik?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '3', '18', '18', 'Dlatego też ci, którzy cierpią zgodnie z wolą Bożą, niech czyniąc dobrze polecają dusze swemu wiernemu Stwórcy!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '0', '0', 'Proszę zatem prezbiterów, którzy znajdują się wśród was, ja - także prezbiter i świadek męki Chrystusa oraz uczestnik przyszłej chwały, która ma się objawić:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '1', '1', 'Paście powierzoną wam trzodę Bożą, czuwając nad nią, nie z przymusu, lecz z oddaniem, po Bożemu, nie dla brudnego zysku, lecz ze szczerej chęci,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '2', '2', 'i nie jak ci, którzy dają odczuć swą władzę nad wspólnotami, ale jak ci, którzy stanowią wzór dla trzody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '3', '3', 'A gdy zjawi się Najwyższy Pasterz, otrzymacie niewiędnący wieniec chwały!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '4', '4', 'Podobnie wy, młodsi, bądźcie ulegli prezbiterom! Wszyscy zaś przyobleczcie się w pokorę! \'Bóg bowiem sprzeciwia się pysznym, a pokornym daje łaskę!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '5', '5', 'Uniżajcie się więc pod potężną ręką Boga, aby wywyższył was w stosownym czasie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '6', '6', '\'wszelką swą troskę poruczając Jemu\', gdyż On troszczy się o was!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '7', '7', 'Bądźcie trzeźwi! Czuwajcie! Przeciwnik wasz, diabeł, niby lew ryczący krąży, szukając, kogo by pożreć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '8', '8', 'Opierajcie się mu, silni w wierze, świadomi, że takie same cierpienia jak wasze znoszą wasi bracia na (całym) świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '9', '9', 'Bóg zaś wszelkiej łaski, który powołał was do swej wiecznej chwały w Chrystusie, po krótkich cierpieniach sam udoskonali (was), umocni, utwierdzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '10', '10', 'Jemu chwała i moc na wieki wieków! Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '11', '11', 'Krótko, jak sądzę, napisałem wam przez Sylwana, wiernego brata, zachęcając was i świadcząc, że to jest prawdziwa łaska Boża; w niej trwajcie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '12', '12', 'Pozdrawia was wybrana wspólnota w Babilonie i syn mój Marek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1pe', '4', '13', '13', 'Pozdrówcie się wzajemnie pocałunkiem miłości! Pokój wam wszystkim, którzy (trwacie) w Chrystusie!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '0', '0', 'Szymon Piotr, sługa i apostoł Jezusa Chrystusa, tym, którzy dzięki sprawiedliwości naszego Boga i Zbawiciela Jezusa Chrystusa otrzymali tę samą, co my, cenną wiarę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '1', '1', 'Łaska i pokój niech się w was pomnaża przez poznanie Boga i Jezusa, Pana naszego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '2', '2', 'Jego Boska moc darowała nam wszystko, co do życia i pobożności należy, przez poznanie Tego, który powołał nas mocą własnej swej chwały i godności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '3', '3', 'Dzięki nim dane nam zostały cenne i największe obietnice, abyście przez nie stali się uczestnikami Bożej natury, uchroniwszy się od zepsucia, jakie w świecie szerzy pożądliwość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '4', '4', 'Dlatego właśnie, dokładając wszelkich starań, łącznie z wiarą dzielność, z dzielnością poznanie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '5', '5', 'z poznaniem zaś umiarkowanie, a z umiarkowaniem wytrwałość, z wytrwałością - pobożność,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '6', '6', 'z pobożnością - przyjaźń braterską, a z braterską przyjaźnią - miłość!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '7', '7', 'Gdy je bowiem w pełni osiągniecie, wasze dążenie do poznania Pana naszego Jezusa Chrystusa nie będzie próżne ani bezowocne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '8', '8', 'Bo ten, komu ich brak, jest ślepy, krótkowzroczny; zapomni o oczyszczeniu się z dawnych swych grzechów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '9', '9', 'Dlatego, bracia, tym więcej starajcie się umocnić wasze powołanie i wybranie! To bowiem czyniąc, nigdy nie upadniecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '10', '10', 'W ten sposób zapewni wam to w pełni wejście do wiecznego królestwa Pana i Zbawiciela naszego Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '11', '11', 'Toteż wciąż przypominać wam będę o tym, choć wiecie i jesteście utwierdzeni w prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '12', '12', 'Uważam jednak za słuszne - jak długo mieszkam w tym namiocie - raz po raz pobudzać was zachętą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '13', '13', 'świadom, że zwinięcie mojego namiotu jest już bliskie według tego, co i Pan nasz, Jezus Chrystus, mi objawił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '14', '14', 'Będę się więc starał, abyście także po moim odejściu przy każdej sposobności mogli to sobie przypominać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '15', '15', 'Nie posłużyliśmy się zmyślonymi opowiadaniami, gdy zapoznawaliśmy was z mocą i z przyjściem Pana naszego, Jezusa Chrystusa, ale byliśmy naocznymi świadkami Jego wielkości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '16', '16', 'Otrzymał bowiem od Boga Ojca cześć i chwałę, gdy ze wspaniałego Majestatu (Boga) doszedł do Niego taki głos: \'To jest mój Syn ukochany, upodobałem Go sobie!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '17', '17', 'Słyszeliśmy ten głos, dochodzący z nieba, gdy byliśmy z Nim na górze świętej!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '18', '18', 'Mamy także całkiem pewną wypowiedź proroków, i dobrze uczynicie, idąc za nią jak za pochodnią świecącą w ciemnym miejscu, aż zabłyśnie dzień i jutrzenka wzejdzie w waszych sercach!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '19', '19', 'Przede wszystkim pamiętajcie, że nie można dowolnie wyjaśniać żadnej wypowiedzi proroka w Piśmie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '0', '20', '20', 'Bo nie z woli człowieka głoszone były niegdyś proroctwa, lecz natchnieni Duchem Świętym przemawiali święci ludzie Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '0', '0', 'Byli jednak i fałszywi prorocy wśród ludu, jak i pośród was będą fałszywi nauczyciele, tacy, którzy doprowadzają do zgubnych podziałów, a zapierając się nawet Pana, który ich odkupił, ściągną na siebie rychłą zgubę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '1', '1', 'Wielu będzie naśladować ich rozwiązłość i przez nich droga Prawdy dozna zniewagi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '2', '2', 'Z chciwości oszukiwać was będą fałszywymi słowami. Wyrok na nich od dawna (gotowy) nie traci swej mocy, a zguba ich nie ominie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '3', '3', 'Jeżeli bowiem Bóg nie przebaczył aniołom, którzy zgrzeszyli, lecz strącił ich do ciemnych czeluści Tartaru, zatrzymując ich (tam aż) do (chwili) sądu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '4', '4', 'i nie darował staremu światu, a zesławszy potop na świat bezbożnych, ocalił tylko ośmiu, a wśród nich Noego, głosiciela sprawiedliwości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '5', '5', 'jeżeli miasta Sodomę i Gomorę skazał na zagładę i obrócił je w perzynę dając przykład kary, jaka spotka bezbożnych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '6', '6', 'a wybawił sprawiedliwego Lota udręczonego rozpustnym życiem występnych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '7', '7', 'bo ten sprawiedliwy, mieszkając wśród nich, dzień po dniu cierpiał w swej sprawiedliwej duszy z powodu czynów występnych, jakie widział i słyszał -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '8', '8', 'tak Pan umie wybawiać pobożnych z doświadczeń, a niesprawiedliwych zachować na dzień sądu dla ukarania,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '9', '9', 'tych zwłaszcza, którzy ulegają nieczystym pożądliwościom ciała i urągają godności Pana. Zarozumiali zuchwalcy, którzy nie obawiają się bluźnić aniołom,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '10', '10', 'podczas gdy aniołowie, choć przeważają ich potęgą i władzą, nie oskarżają ich przed Panem o bluźnierstwo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '11', '11', 'Oni zaś jak nierozumne zwierzęta, z natury swej zrodzone na to, aby je chwytać i zabijać, bluźnią temu, czego nie znają, i zginą w swym zepsuciu, ponosząc karę za swoją nieprawość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '12', '12', 'Szczęście swe upatrują w codziennych rozkoszach, plugawi i słabi oddają się swym uciechom siedząc razem z wami przy stole.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '13', '13', 'Ciągle wypatrują okazji do cudzołóstwa, nie przestają grzeszyć, uwodzą dusze słabe, przeklęci, serca swe zaprzedali chciwości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '14', '14', 'Opuściwszy drogę prawą, zbłądzili, zbaczając na drogę Balaama, syna Bosora, który dał się skusić zapłatą za nieprawość,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '15', '15', 'ale został skarcony za swą niegodziwość; nieme juczne bydlę przemówiwszy ludzkim głosem zapobiegło szaleństwu wieszczka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '16', '16', 'Są to źródła bez wody i chmury gnane wichrem gwałtownym! Czeka ich mrok ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '17', '17', 'Rozprawiając bowiem wyniośle a bezsensownie, kuszą do rozkoszy cielesnych i rozpusty tych, którzy dopiero co uciekli od pogrążonych w błędzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '18', '18', 'Obiecują im wolność, sami będąc niewolnikami zepsucia. Jest się bowiem niewolnikiem tego, komu się uległo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '19', '19', 'Bo jeśli uszli przed brudami świata dzięki poznaniu Pana i Zbawiciela, Jezusa Chrystusa, a pogrążając się w nie znowu im ulegają, to koniec ich staje się gorszy od początku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '20', '20', 'Lepiej by bowiem było dla nich nie poznać drogi sprawiedliwości, niż poznawszy ją - odwrócić się od świętych przykazań, które im dano.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '1', '21', '21', 'Sprawdza się na nich przysłowie: \'Wrócił pies do swoich wymiocin\' i wymyta świnia - do tarzania się w błocie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '0', '0', 'Ten to już drugi list piszę do was, umiłowani! Pokrzepiam w nich szczerego ducha waszego, przypominając,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '1', '1', 'abyście zachowywali w pamięci ogłoszone niegdyś przez świętych proroków wypowiedzi i nakazy Pana i Zbawiciela głoszone przez apostołów waszych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '2', '2', 'świadomi przede wszystkim, że przyjdą w dniach ostatecznych drwiący szydercy, którzy będą postępowali według swych własnych pragnień,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '3', '3', 'mówiąc,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '4', '4', 'Rozmyślnie bowiem tają to przed sobą, że od dawna istniały niebiosa i ziemia utworzona Bożym słowem spośród wód i przez wodę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '5', '5', 'przez nie ówczesny świat zginął w klęsce potopu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '6', '6', 'A to samo słowo zachowuje dla ognia obecne niebiosa i ziemię i otacza je opieką aż do dnia sądu i zagłady ludzi bezbożnych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '7', '7', 'O tym jednym zaś nie zapominajcie, umiłowani, że jeden dzień dla Pana jest jak tysiąc lat, a tysiąc lat jak jeden dzień.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '8', '8', 'Nie odwleka Pan ogłoszonej obietnicy - choć niektórzy sądzą, że odwleka - lecz jest dla was wspaniałomyślny, nie chcąc, aby ktokolwiek zginął, lecz aby wszyscy doszli do nawrócenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '9', '9', 'Dzień zaś Pana nadejdzie, jak złodziej. Przeminą niebiosa, żywioły ogarnięte żarem rozpadną się, a ziemia i (będące) na niej dzieła ulegną spaleniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '10', '10', 'Skoro to wszystko w ten sposób ma ulec zagładzie, to jak święcie i pobożnie powinniście żyć,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '11', '11', 'oczekując i przyspieszając przyjście dnia Pana, (dnia), w którym spłoną niebiosa rozżarzone ogniem i spalą się fundamenty świata!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '12', '12', 'Lecz my oczekujemy nowych niebios i nowej ziemi, w których zamieszka sprawiedliwość, zgodnie z Jego obietnicą!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '13', '13', 'Toteż, umiłowani, oczekując tego, starajcie się gorliwie, aby On zastał was nieskalanych i bez zmazy w pokoju!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '14', '14', 'A cierpliwość Pana naszego uważajcie za zbawienie, jak to dzięki udzielonej mu mądrości napisał wam również umiłowany brat nasz Paweł.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '15', '15', 'Pisze też o tym i we wszystkich listach. Są w nich pewne sprawy trudne do zrozumienia, które niedouczeni i nieutwierdzeni przekręcają na własną swoją zgubę, podobnie jak inne Pisma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '16', '16', 'Wy natomiast, umiłowani, wiedząc już o tym, strzeżcie się, abyście nie dali się uwieść błędom ludzi bezbożnych i zachowali swą stałość.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2pe', '2', '17', '17', 'Wzrastajcie w łasce i poznaniu Pana naszego i Zbawiciela, Jezusa Chrystusa! Jemu chwała i teraz , i po dzień wieczności! Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '0', '0', 'Piszemy wam o Słowie życia, o tym, które było od początku, któreśmy słyszeli i widzieli na własne oczy, w któreśmy się wpatrywali i którego dotykały nasze dłonie. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '1', '1', 'Życie to objawiło się, a my widzieliśmy je i dlatego dajemy świadectwo i głosimy wam życie wieczne, które było u Ojca, a nam się objawiło. -');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '2', '2', 'To, cośmy widzieli i słyszeli, głosimy również wam, abyście i wy mieli z nami łączność. A nasza łączność jest (łącznością) z Ojcem i Synem Jego Jezusem Chrystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '3', '3', 'A piszemy po to, aby radość wasza była pełna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '4', '4', 'A takie jest orędzie, które słyszeliśmy od Niego i wam głosimy: Bóg jest światłością i nie ma w Nim żadnej ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '5', '5', 'Jeżeli mówimy, że mamy z Nim łączność, a chodzimy w ciemności, to kłamiemy i nie postępujemy zgodnie z prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '6', '6', 'Jeżeli zaś chodzimy w światłości, tak jak i On jest w światłości, to wtedy pozostajemy w łączności ze sobą, a krew Jezusa, Jego Syna, oczyszcza nas z wszelkiego grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '7', '7', 'Jeżeli mówimy, że nie mamy grzechu, to sami siebie zwodzimy i nie ma w nas prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '8', '8', 'Jeżeli wyznajemy nasze grzechy, to On jest tak wierny i sprawiedliwy, że odpuści nam grzeszy i oczyści nas z wszelkiej nieprawości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '0', '9', '9', 'Jeżeli mówimy, żeśmy nie zgrzeszyli, to czynimy Go kłamcą, a wtedy nie ma w nas Jego słowa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '0', '0', 'Dzieci moje, piszę wam, abyście nie grzeszyli. Gdyby jednak ktoś zgrzeszył, to obrońcę mamy u Ojca, Jezusa Chrystusa, Sprawiedliwego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '1', '1', 'On właśnie jest ofiarą przebłagalną za nasze grzechy, nie tylko zresztą za nasze, lecz również za grzechy całego świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '2', '2', 'Po tym poznajemy, że Go znamy, iż wypełniamy Jego przykazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '3', '3', 'Kto mówi: \'Znam Go\', a przykazań Jego nie zachowuje, ten jest kłamcą i nie ma w nim prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '4', '4', 'Kto zaś zachowuje Jego słowo, w tym miłość Boga jest naprawdę doskonała. Po tym również poznajemy, że jesteśmy w Nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '5', '5', 'Kto mówi, że w Nim trwa, ten powinien tak postępować, jak On postępował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '6', '6', 'Umiłowani, nie piszę wam o nowym przykazaniu, lecz o przykazaniu starym, które mieliście od początku; tym dawnym przykazaniem jest słowo, któreście słyszeli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '7', '7', 'A jednak piszę wam znowu o nowym przykazaniu - które jest prawdziwe w Nim i w was - ponieważ ciemność mija, a prawdziwa światłość już świeci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '8', '8', 'Kto mówi, że jest w światłości, a nienawidzi swego brata, ten wciąż jeszcze jest w ciemności.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '9', '9', 'Kto miłuje swego brata, ten trwa w światłości i nic go nie zachwieje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '10', '10', 'Kto zaś nienawidzi swego brata, ten żyje i chodzi w ciemności, i nie wie, dokąd idzie, bo ciemność oślepiła jego oczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '11', '11', 'Piszę wam, dzieci, że przez Jego imię zostały wam odpuszczone grzechy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '12', '12', 'Piszę wam, ojcowie, że poznaliście Tego, który był od początku. Piszę wam, młodzieńcy, że zwyciężyliście Złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '13', '13', 'Napisałem wam, dzieci, że poznaliście Ojca. Napisałem wam, ojcowie, że poznaliście Tego, który był od początku. Napisałem wam, młodzieńcy, że jesteście mocni, słowo Boże w was trwa i zwyciężyliście Złego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '14', '14', 'Nie miłujcie świata ani tego, co jest na świecie. Jeżeli ktoś miłuje świat, nie ma w nim miłości Ojca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '15', '15', 'Albowiem wszystko, co jest na świcie: pożądliwość ciała, pożądliwość oczu i chełpienie się bogactwem - nie pochodzi od Ojca, lecz ze świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '16', '16', 'A świat przemija wraz ze swą pożądliwością; kto zaś spełnia wolę Boga, ten trwa na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '17', '17', 'Dzieci, nadeszła już ostatnia godzina: tak jak słyszeliście, Antychryst przychodzi. Teraz wielu jest antychrystów; po tym właśnie poznajemy, że nadeszła ostatnia godzina.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '18', '18', 'Oni wyszli z nas, lecz z nas nie byli; jeśliby bowiem z nas byli, to pozostaliby z nami. Lecz w ten sposób wyszło na jaw, że nie wszyscy do nas należą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '19', '19', 'A wy macie namaszczenie od Świętego i wszyscy posiadacie wiedzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '20', '20', 'Nie napisałem wam, że nie znacie prawdy, lecz że ją znacie i wiecie, iż żadne kłamstwo nie pochodzi z prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '21', '21', 'Czyż nie ten jest kłamcą, kto przeczy, że Jezus jest Chrystusem? Ten jest Antychrystem, kto wypiera się Ojca i Syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '22', '22', 'Każdy, kto wypiera się Syna, nie ma też i Ojca;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '23', '23', 'W was zaś niech trwa to, co słyszeliście od początku. Jeżeli w was będzie trwać to, co słyszeliście od początku, to i wy trwać będziecie w Synu i Ojcu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '24', '24', 'A obietnicą, którą On nam dał jest życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '25', '25', 'To napisałem wam o tych, którzy was zwodzą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '26', '26', 'A namaszczenie, które otrzymaliście od Niego, trwa w was i dlatego nie ma potrzeby, żeby was ktoś jeszcze pouczał; lecz ponieważ namaszczenie Jego poucza was o wszystkim - a jest ono prawdziwe, a nie kłamliwe - dlatego trwajcie w nim tak, jak was ono pouczyło.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '27', '27', 'A teraz, dzieci, trwajcie w Nim, abyśmy w chwili Jego objawienia z ufnością a nie wstydem mogli stanąć przed Nim w dniu Jego ponownego przyjścia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '1', '28', '28', 'Jeżeli wiecie, że jest On sprawiedliwy, to uznajcie również, że każdy postępujący sprawiedliwie z Niego się narodził.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '0', '0', 'Patrzcie, jak wielką miłością obdarzył nas Ojciec, że zostaliśmy nazwani dziećmi Bożymi i jesteśmy (nimi). A świat dlatego nas nie zna, ponieważ i Jego nie poznał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '1', '1', 'Umiłowani, już teraz jesteśmy dziećmi Bożymi, a jeszcze się nie okazało, czym będziemy. Wiemy, że gdy się On ukaże, staniemy się do Niego podobni, ponieważ ujrzymy Go takim, jakim jest.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '2', '2', 'A każdy, kto ma w Nim tę nadzieję, oczyszcza się, jak i On jest czysty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '3', '3', 'Każdy, kto popełnia grzech, dopuszcza się bezprawia, grzech bowiem jest bezprawiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '4', '4', 'A przecież wiecie, że On objawił się po to, aby zgładzić grzechy; w Nim samym zaś nie ma grzechu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '5', '5', 'Każdy, kto w Nim trwa, ten nie grzeszy; nikt zaś,  kto grzeszy, nie widział Go i nie poznał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '6', '6', 'Dzieci, niech was nikt nie zwodzi. Kto postępuje sprawiedliwie, ten jest sprawiedliwy, tak jak i On jest sprawiedliwy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '7', '7', 'Kto popełnia grzech, ten jest (dzieckiem) diabła, ponieważ diabeł grzeszy od początku. Po to właśnie objawił się Syn Boży, aby zniszczyć dzieła diabła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '8', '8', 'Każdy, kto narodził się z Boga, nie popełnia grzechu, albowiem trwa w nim nasienie Boże; taki człowiek nie może trwać w grzechu, bo narodził się z Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '9', '9', 'Po tym można rozpoznać dzieci Boże i dzieci diabła: każdy, kto nie postępuje sprawiedliwie i nie miłuje brata swego, ten nie pochodzi od Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '10', '10', 'Takie bowiem jest orędzie, które słyszeliście od początku: miłujcie się wzajemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '11', '11', 'Nie tak, jak Kain, który był (dzieckiem) Złego i zamordował swego brata. A dlaczego zamordował go? Ponieważ czyny jego były złe, a brata jego sprawiedliwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '12', '12', 'Nie dziwcie się, bracia, jeżeli nienawidzi was świat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '13', '13', 'My wiemy, że przeszliśmy ze śmierci do życia, ponieważ miłujemy braci; a kto nie miłuje, ten trwa w śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '14', '14', 'Każdy, kto nienawidzi swego brata, jest mordercą, a wiecie, że żaden morderca nie ma w sobie życia wiecznego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '15', '15', 'Po tym poznaliśmy miłość, że On oddał za nas swe życie. My również powinniśmy życie dawać za braci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '16', '16', 'Jeśliby ktoś, posiadając dobra tego świata, zauważył, że brat jego jest w biedzie a zamknąłby przed nim swe serce - to jakże w nim może pozostawać miłość Boża?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '17', '17', 'Dzieci, nie miłujmy słowem i językiem, ale czynem i prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '18', '18', 'Po tym poznamy, że jesteśmy z prawdy, i uspokoimy przed Nim nasze serce,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '19', '19', 'choćby nas ono w czymś oskarżało, ponieważ większy jest Bóg od naszego serca i zna wszystko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '20', '20', 'Umiłowani, jeśli serce nas nie oskarża, to możemy mieć ufność w Bogu');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '21', '21', 'i o cokolwiek prosilibyśmy, otrzymamy to od Niego, ponieważ przykazania Jego zachowujemy i czynimy to, co Mu się podoba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '22', '22', 'A to jest Jego przykazanie, abyśmy wierzyli w imię Syna Jego, Jezusa Chrystusa, i abyśmy się wzajemnie miłowali, zgodnie z przykazaniem, jakie nam dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '2', '23', '23', 'Kto wypełnia Jego przykazania, ten trwa w Bogu, a Bóg w nim; a że On trwa w nas, poznajemy po Duchu, którego nam dał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '0', '0', 'Umiłowani, nie wierzcie każdemu duchowi, lecz badajcie duchy, czy pochodzą od Boga, gdyż wielu fałszywych proroków pojawiło się na tym świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '1', '1', 'Po tym poznacie ducha Bożego: wszelki duch, który wyznaje, że Jezus Chrystus przyszedł w ciele, z Boga jest,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '2', '2', 'wszelki zaś duch, który nie wyznaje Jezusa, nie jest z Boga. Jest to duch Antychrysta, o którym słyszeliście, że przyjdzie, a teraz jest już na świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '3', '3', 'Wy zaś, dzieci, z Boga jesteście, i zwyciężyliście ich, ponieważ większy jest Ten, który jest w was, niż ten, który jest na tym świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '4', '4', 'Oni są ze świata, dlatego mówią tak, jak (mówi) świat, a świat ich słucha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '5', '5', 'My zaś jesteśmy z Boga. Kto zna Boga, ten nas słucha, a kto nie jest z Boga, ten nas nie słucha. Po tym właśnie rozpoznajemy ducha prawdy i ducha błędu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '6', '6', 'Umiłowani, miłujmy się wzajemnie, ponieważ miłość pochodzi od Boga, a każdy, kto miłuje, z Boga się narodził i zna Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '7', '7', 'Kto nie miłuje, nie poznał Boga, ponieważ Bóg jest miłością.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '8', '8', 'W tym objawiła się miłość Boga do nas, że Syna swego Jednorodzonego posłał na świat, abyśmy żyli przez Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '9', '9', 'Na tym zaś polega miłość, że nie my umiłowaliśmy Boga, lecz że On nas umiłował i posłał swego Syna jako przebłagalną ofiarę za grzechy nasze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '10', '10', 'Umiłowani, jeśli Bóg tak nas umiłował, to i my powinniśmy się wzajemnie miłować.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '11', '11', 'Boga nikt nigdy nie widział; jeśli miłujemy się wzajemnie, to Bóg trwa w nas, a miłość Jego jest w nas doskonała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '12', '12', 'Po tym poznajemy, że trwamy w Nim a On w nas, że dał nam swego Ducha.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '13', '13', 'A my widzieliśmy i świadczymy, że Ojciec posłał Syna, który jest Zbawicielem świata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '14', '14', 'Ktokolwiek wyznaje, że Jezus jest Synem Boga, w tym trwa Bóg, a on w Bogu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '15', '15', 'A myśmy poznali i uwierzyli w miłość, jaką Bóg ma do nas. Bóg jest miłością, a kto trwa w miłości, ten trwa w Bogu, a Bóg trwa w nim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '16', '16', 'Po to doskonała stała się w nas miłość, aby w dniu sądu nie zabrakło nam ufności, ponieważ jak On jest, tak i my jesteśmy na tym świecie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '17', '17', 'W miłości nie ma bojaźni, lecz doskonała miłość usuwa bojaźń, ponieważ bojaźń powstaje z powodu kary, a kto się boi, nie kocha doskonale.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '18', '18', 'My miłujemy, ponieważ On nas najpierw umiłował.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '19', '19', 'Jeśliby ktoś mówił: \'Miłuję Boga\', a równocześnie nienawidziłby swego brata, ten jest kłamcą. Kto bowiem nie miłuje swego brata, którego widzi, nie może miłować Boga, którego nie widzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '3', '20', '20', 'A takie przykazanie mamy od Niego, aby ten, kto miłuje Boga, miłował również swego brata.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '0', '0', 'Każdy, kto wierzy, że Jezus jest Chrystusem, narodził się z Boga, a każdy, kto miłuje Tego, który zrodził, miłuje również Tego, który z Niego został zrodzony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '1', '1', 'Po tym poznajemy, że miłujemy dzieci Boże, iż Boga miłujemy i wypełniamy Jego przykazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '2', '2', 'Na tym bowiem polega miłość do Boga, że wypełniamy Jego przykazania.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '3', '3', 'A przykazania Jego nie są trudne, ponieważ wszystko, co zrodziło się z Boga, zwycięża świat, a zwycięstwem, które zwyciężyło świat, jest wiara nasza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '4', '4', 'Któż zaś jest tym, który zwycięża świat, jeśli nie ten, kto wierzy, że Jezus jest Synem Bożym?');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '5', '5', 'To On właśnie - Jezus Chrystus - jest Tym, który przyszedł przez wodę i krew; nie w wodzie tylko, lecz w wodzie i krwi; a Duch jest Tym, który daje świadectwo, ponieważ Duch jest prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '6', '6', 'Albowiem trzej dają świadectwo:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '7', '7', 'Duch i woda, i krew, a ci trzej stanowią jedno.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '8', '8', 'Jeśli przyjmujemy świadectwo ludzi, to tym bardziej świadectwo Boga, które je przewyższa; a takie jest świadectwo, które Bóg dał o swoim Synu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '9', '9', 'Kto wierzy w Syna Bożego, ten ma świadectwo w sobie; kto zaś nie wierzy Bogu, czyni go kłamcą, ponieważ nie wierzy świadectwu, jakie Bóg daje o swoim Synu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '10', '10', 'A świadectwo to jest takie: Bóg nam dał życie wieczne, a życie to jest w Jego Synu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '11', '11', 'Kto ma Syna, ten ma życie, a kto nie ma Syna Bożego, ten i życia nie ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '12', '12', 'To napisałem wam - wierzącym w imię Syna Bożego - abyście wiedzieli, że macie życie wieczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '13', '13', 'A to jest ufność, jaką w Nim pokładamy, że On wysłuchuje nas, jeśli tylko prosimy zgodnie z Jego wolą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '14', '14', 'A jeśli wiemy, że wysłuchuje nas, o cokolwiek prosimy, to wiemy również, że od Niego mamy to, o co prosimy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '15', '15', 'Jeśli ktoś widzi brata popełniającego grzech, który nie sprowadza śmierci, to niech prosi, a (Bóg) takiemu - i wszystkim nie popełniającym grzechu, który sprowadza śmierć - da życie. Istnieje grzech sprowadzający śmierć; i nie mówię, żebyś się za tego (kto go popełnia) modlił.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '16', '16', 'Każda nieprawość jest grzechem, ale jest także grzech, który nie sprowadza śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '17', '17', 'Wiemy, że każdy, który został zrodzony z Boga, nie grzeszy, albowiem Zrodzony z Boga strzeże go, a Zły mu nie szkodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '18', '18', 'Wiemy, że jesteśmy z Boga, a cały świat pogrążony jest w Złym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '19', '19', 'Wiemy także, że Syn Boży przyszedł i dał nam zrozumieć i poznać Prawdziwego (Boga). My jesteśmy w tym Prawdziwym - w Jego Synu, Jezusie Chrystusie. On jest prawdziwym Bogiem i Życiem wiecznym.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '1jn', '4', '20', '20', 'Dzieci strzeżcie się fałszywych bogów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '0', '0', '(Ja) prezbiter - do Wybranej Pani i jej dzieci, które miłuję w prawdzie, nie tylko ja sam, lecz także ci wszyscy, którzy poznali prawdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '1', '1', 'A (miłuję ich z powodu) prawdy, która w nas trwa i (będzie) z nami na wieki.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '2', '2', 'Łaska , miłosierdzie i pokój od Boga Ojca i od Jezusa Chrystusa, Syna Ojca, niech będą z wami w prawdzie i miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '3', '3', 'Ucieszyłem się bardzo, gdy znalazłem wśród twoich dzieci takie, które postępują według prawdy zgodnie z tym, co nakazał nam Ojciec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '4', '4', 'A teraz proszę cię, Pani, nie tak, jakbym ci dawał jakieś nowe przykazanie, lecz przykazanie, które mieliśmy od początku, abyśmy się wzajemnie miłowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '5', '5', 'A miłość polega na tym, abyśmy postępowali według Jego przykazań; jest to przykazanie, o którym już od początku słyszeliście. Postępujcie według niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '6', '6', 'Pojawiło się na świecie wielu zwodzicieli, którzy nie uznają, że Jezus Chrystus przychodzi w ciele; taki właśnie jest zwodzicielem, Antychrystem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '7', '7', 'Uważajcie na samych siebie, abyście nie stracili tego, coście z trudem zdobyli, lecz żebyście otrzymali pełną nagrodę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '8', '8', 'Każdy, kto wychodzi poza naukę Chrystusa, a nie trwa w niej, ten nie ma Boga; kto zaś trwa w tej nauce, ten ma i Ojca, i Syna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '9', '9', 'Jeśli ktoś przychodzi do was, a takiej nauki z sobą nie przynosi, to nie przyjmujcie go w swoim domu, a nawet go nie pozdrawiajcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '10', '10', 'Kto bowiem takiego pozdrawia, staje się uczestnikiem złych czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '11', '11', 'Wiele miałbym wam jeszcze do pisania, nie chciałem jednak tego czynić listownie, spodziewam się bowiem przybyć do was i osobiście z wami porozmawiać, aby radość nasza była pełna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '2jn', '0', '12', '12', 'Pozdrawiają cię dzieci wybranej twej siostry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '0', '0', '(Ja) prezbiter - do kochanego Gajusa, którego miłuję w prawdzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '1', '1', 'Umiłowany, przede wszystkim proszę Boga, żeby ci się dobrze powodziło i żebyś był zdrowy, podobnie jak zdrowa jest twoja dusza.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '2', '2', 'Ucieszyłem się bardzo z przybycia braci i ze świadectwa, jakie dali o twojej prawdzie - to jest o tym, jak ty postępujesz zgodnie z prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '3', '3', 'Nie znam większej radości niż ta, kiedy słyszę, że dzieci moje postępują zgodnie z prawdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '4', '4', 'Umiłowany, postępujesz zgodnie z wiarą, jeśli pomagasz braciom, a szczególnie przybyszom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '5', '5', 'To oni właśnie świadczyli wobec Kościoła o twej miłości; dobrze również czynisz zaopatrując ich na drogę, jak tego pragnie Bóg.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '6', '6', 'Wyruszyli oni bowiem dla imienia (Jezusa), nic nie przyjmując od pogan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '7', '7', 'My więc winniśmy takich wspierać: w ten sposób będziemy z nimi współpracować dla prawdy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '8', '8', 'Napisałem kilka słów do Kościoła, lecz Diotrefes, który lubi zajmować wśród nich pierwsze miejsce, nie uznaje nas.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '9', '9', 'Dlatego gdy przybędę, przypomnę jego oszczerstwa, z którymi przeciwko nam wystąpił; nie poprzestając na tym nawet braci nie przyjmuje, a tym, którzy chcą ich przyjąć, przeszkadza i usuwa ich z Kościoła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '10', '10', 'Umiłowany, nie naśladuj zła, lecz dobro. Kto czyni dobro, z Boga jest; kto zaś czyni zło; ten nie widział Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '11', '11', 'Demetriuszowi dane zostało świadectwo przez wszystkich, a nawet przez samą prawdę; my również świadczymy, a ty wiesz, że świadectwo nasze jest prawdziwe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '12', '12', 'Wiele miałbym ci jeszcze do powiedzenia, lecz nie chcę czynić tego pisemnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '13', '13', 'Spodziewam się wkrótce cię zobaczyć i osobiście z tobą porozmawiać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, '3jn', '0', '14', '14', 'Pokój tobie. Pozdrawiają cię przyjaciele. Pozdrów przyjaciół, każdego z osobna.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '0', '0', 'Juda, sługa Jezusa Chrystusa a brat Jakuba, do powołanych, umiłowanych w Bogu Ojcu i zachowanych dla Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '1', '1', 'Miłosierdzie, pokój i miłość niech będą w pełni waszym udziałem!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '2', '2', 'Najdrożsi! Przejęty wielkim pragnieniem pisania do was o naszym wspólnym zbawieniu, odczuwałem potrzebę zachęcenia was tym listem, abyście walczyli o wiarę raz (na zawsze) przekazaną świętym!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '3', '3', 'Zakradli się bowiem jacyś ludzie bezbożni od dawna, o których potępieniu Pismo mówi, że zamieniają łaskę Boga naszego na wyuzdanie i zapierają się jedynego Władcy i Pana naszego, Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '4', '4', 'Wam natomiast, którzyście raz wszystko poznali, pragnę przypomnieć, że Pan, wybawiwszy lud z ziemi egipskiej, potem wytracił tych, którzy nie mieli wiary.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '5', '5', 'Aniołów zaś, którzy nie zachowali swego pierwotnego stanu, lecz porzucili swoje mieszkanie, trzyma skrępowanych wiecznymi więzami w ciemnościach aż do sądu Wielkiego Dnia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '6', '6', 'W podobny też sposób Sodoma i Gomora oraz okoliczne miasta, które żyły w nieczystości i oddawały się rozpuście przeciw naturze, poniosły karę wiecznego ognia i stały się (odstraszającym) przykładem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '7', '7', 'Podobnie i ci fałszywi marzyciele kalają ciało, w pogardzie mają godność Pana, bluźnią aniołom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '8', '8', 'Gdy archanioł Michał toczył z szatanem zacięty bój o ciało Mojżesza, nie śmiał wydać potępiającego wyroku, lecz powiedział: \'Niech cię Pan zgromi!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '9', '9', 'Ci natomiast bluźnią wszystkiemu i temu, czego nie znają, a ku swej zgubie używają tych rzeczy, które pojmują w sposób przyrodzony, jak bezrozumne zwierzęta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '10', '10', 'Biada im, gdy zeszli na drogę Kaina i dla pieniędzy popełnili błąd Balaama, a giną zbuntowani jak Kore.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '11', '11', 'Oni przynoszą wstyd agapom, biorą w nich udział bez skrępowania, pasąc samych siebie. (Są jak) chmury bezwodne, pędzone wichrami, drzewa (nawet) późną jesienią nie dające owocu, dwakroć obumarłe, wyrwane z korzeniami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '12', '12', 'rozhukane bałwany morskie, pieniące się własną hańbą, błędne gwiazdy, na które czekają mroki wiekuistych ciemności!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '13', '13', 'O nich to prorokował Henoch, siódmy (patriarcha) po Adamie, głosząc: \'Oto nadchodzi Pan z dziesiątkami tysięcy swych świętych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '14', '14', 'aby dokonać sądu nad wszystkimi i ukarać wszystkich bezbożnych za wszelkie bezbożne ich czyny, które bezbożnie popełnili, i za wszelkie słowa bezecne, jakie wypowiedzieli przeciwko Niemu bezbożni grzesznicy\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '15', '15', 'Oni to szemrzą, narzekają na swój los, ulegając swym pożądliwościom, a usta ich wypowiadają słowa napuszone, dla korzyści schlebiające ludziom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '16', '16', 'Ale wy, umiłowani, pamiętajcie o naukach głoszonych ongiś przez apostołów Pana naszego Jezusa Chrystusa,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '17', '17', 'którzy mówili wam: \'W czasach ostatecznych pojawiają się szydercy żyjący według swych pożądliwości jak bezbożni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '18', '18', 'Oni to wywołują rozłamy, ulegają zmysłom, Ducha (Bożego) nie mają.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '19', '19', 'Wy zaś, umiłowani, wznoście swoją budowę na waszej najświętszej wierze, módlcie się w Duchu Świętym,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '20', '20', 'trwajcie w miłości Bożej, wyczekując miłosierdzia Pana naszego Jezusa Chrystusa (dającego) życie wieczne!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '21', '21', 'Jednych - chwiejnych - przekonujcie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '22', '22', 'drugich ratujcie, wyrywając (ich) z ognia, innym okazujcie litość, zachowując ostrożność z lękiem i (nawet unikając zetknięcia) ze skalaną przez ciało szatą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '23', '23', 'Temu zaś, który może ustrzec was od upadku i postawić nieskalanych wobec swej chwały w radości,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'jude', '0', '24', '24', 'Jedynemu Bogu, Zbawcy naszemu, przez Jezusa Chrystusa, Pana naszego, chwała i uwielbienie, moc i potęga przed wszystkimi wiekami, teraz i po wszystkie wieki. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '0', '0', 'Objawienie Jezusa Chrystusa, które dał Mu Bóg, aby ukazać swym sługom, co musi stać się niebawem, a (On) wysławszy swego anioła oznajmił przez niego za pomocą znaków słudze swojemu Janowi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '1', '1', 'Ten poświadcza, że wszystko, co widział, jest słowem Bożym i świadectwem Jezusa Chrystusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '2', '2', 'Szczęśliwy, który czyta, i (ci) którzy słuchają słów Proroctwa, a strzegą tego, co w nim napisane, bo chwila jest bliska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '3', '3', 'Jan do siedmiu Kościołów, które są w Azji: Łaska wam i pokój od Tego, który jest, który był i który przychodzi, i od siedmiu Duchów, które są przed Jego tronem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '4', '4', 'i od Jezusa Chrystusa, Świadka Wiernego, Pierworodnego umarłych i Władcy królów ziemi. Temu, który nas miłuje i który przez swoją krew uwolnił nas od naszych grzechów');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '5', '5', 'i uczynił nas królestwem - kapłanami dla Boga, Ojca swojego, Jemu chwała i moc na wieki wieków. Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '6', '6', 'Oto nadchodzi z obłokami i ujrzy Go wszelkie oko i wszyscy, którzy Go przebili. I będą Go opłakiwać wszystkie pokolenia ziemi. Tak (jest): Amen.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '7', '7', 'Jam jest Alfa i Omega, mówi Pan Bóg, który jest, który był i który przychodzi, Władca wszech rzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '8', '8', 'Ja, Jan, wasz brat i współuczestnik w ucisku i królestwie, i wytrwałości w Jezusie, byłem na wyspie, zwanej Patmos, z powodu słowa Bożego i świadectwa Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '9', '9', 'Doznałem zachwycenia w dzień Pański i posłyszałem za sobą potężny głos jak gdyby trąby');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '10', '10', 'mówiącej: \'Co widzisz, zapisz w księdze i poślij siedmiu Kościołom: do Efezu, Smyrny, Pergamu, Tiatyry, Sardów, Filadelfii i Laodycei\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '11', '11', 'I obróciłem się, chcąc widzieć, co za głos do mnie mówił, a obróciwszy się ujrzałem siedem złotych świeczników,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '12', '12', 'i pośród świeczników kogoś podobnego do Syna Człowieczego, obleczonego (w szatę) do stóp i przepasanego na piersiach złotym pasem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '13', '13', 'Głowa Jego i włosy białe jak biała wełna, jak śnieg, a oczy Jego jak płomień ognisty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '14', '14', 'Stopy Jego podobne do drogocennego metalu jak gdyby w piecu rozżarzonego, a głos Jego jak głos wielu wód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '15', '15', 'W prawej ręce miał siedem gwiazd i z Jego ust wychodził miecz obosieczny, ostry. A Jego wygląd - jak słońce, kiedy jaśnieje w swej mocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '16', '16', 'Kiedym Go ujrzał, upadłem do Jego stóp jak martwy, a On położył na mnie prawą rękę, mówiąc: \'Przestań się lękać! Jam jest Pierwszy i Ostatni,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '17', '17', 'i żyjący. Byłem umarły, a oto jestem żyjący na wieki wieków i mam klucze śmierci i Otchłani.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '18', '18', 'Zapisz więc to, co widziałeś, i to, co jest, i to, co potem musi się stać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '0', '19', '19', 'A oto tajemnica siedmiu gwiazd, któreś ujrzał w mojej prawej ręce, i siedmiu złotych świeczników: siedem gwiazd - to aniołowie siedmiu Kościołów, a siedem świeczników - to siedem Kościołów\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '0', '0', 'Aniołowi Kościoła w Efezie napisz: To mówi Ten, który trzyma siedem gwiazd w prawej ręce, Ten, który się przechadza wśród siedmiu złotych świeczników:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '1', '1', 'Znam twoje czyny, trud i twoją wytrwałość, i to, że złych nie możesz znieść; próbie poddałeś tych, którzy sami nazywają się apostołami, a nimi nie są, i przekonałeś się, że są kłamcami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '2', '2', 'Ty masz wytrwałość i zniosłeś cierpienia dla imienia mego - niezmordowany.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '3', '3', 'Ale mam tobie za złe, że odstąpiłeś od twej pierwotnej miłości.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '4', '4', 'Pamiętaj więc, skąd spadłeś, i nawróć się, i pierwsze czyny podejmij! A jeśli nie,  jeśli się nie nawrócisz, przyjdę do ciebie i ruszę twój świecznik z jego miejsca.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '5', '5', 'Ale masz to do siebie, że nienawidzisz czynów nikolaitów, których (to czynów) i Ja nienawidzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '6', '6', 'Kto ma uszy, niech usłyszy, co mówi Duch do Kościołów. Zwycięzcy dam spożyć owoc z drzewa życia, które jest w raju Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '7', '7', 'Aniołowi Kościoła w Smyrnie napisz: To mówi Pierwszy i Ostatni, który był martwy, a ożył.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '8', '8', 'Znam twój ucisk i ubóstwo, ale ty jesteś bogaty, i (znam) obelgę doznaną od tych, co samych siebie zowią Żydami, a nie są nimi, lecz synagogą szatana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '9', '9', 'Przestań się lękać tego, co masz cierpieć. Oto diabeł ma niektórych z was wtrącić do więzienia, abyście zostali poddani próbie, i znosić będziecie ucisk przez dziesięć dni. Bądź wierny aż do śmierci, a dam ci wieniec życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '10', '10', 'Kto ma uszy, nie usłyszy, co mówi Duch do Kościołów. Zwycięzcy śmierć druga na pewno nie wyrządzi szkody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '11', '11', 'Aniołowi Kościoła w Pergamie napisz: To mówi Ten, który ma miecz obosieczny, ostry.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '12', '12', 'Wiem, gdzie mieszkasz: tam, gdzie jest tron szatana, a trzymasz się mego imienia i wiary mojej się nie zaparłeś nawet we dni Antypasa mojego wiernego świadka, który został zabity u was, tam gdzie mieszka szatan.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '13', '13', 'Ale trochę mam tobie za złe, że masz tam takich, co się trzymają nauki Balaama, który pouczył Balaka, jak podsunąć synowi Izraela sposobność do grzechu przez spożycie ofiar składanych bożkom i uprawianie rozpusty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '14', '14', 'Tak i ty także masz tych, co się trzymają podobnie nauki nikolaitów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '15', '15', 'Nawróć się zatem! Bo jeśli nie - przyjdę do ciebie niebawem i będę z nimi walczył mieczem moich ust.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '16', '16', 'Kto ma uszy, niech usłyszy, co mówi Duch do Kościołów. Zwycięzcy dam manny ukrytej i dam mu biały kamyk, a na kamyku wypisane imię nowe, którego nikt nie zna oprócz tego, kto (je) otrzymuje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '17', '17', 'Aniołowi Kościoła w Tiatyrze napisz: To mówi Syn Boży: Ten, który ma oczy jak płomień ognisty, a nogi jego są podobne do drogocennego metalu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '18', '18', 'Znam twoje czyny, miłość, wiarę, posługę i twoją wytrwałość, i czyny twoje ostatnie liczniejsze od pierwszych,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '19', '19', 'ale mam tobie za złe, że pozwalasz działać niewieście Jezabel, która nazywa siebie prorokinią, a naucza i zwodzi moje sługi, tak że uprawiają rozpustę i spożywają ofiary składane bożkom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '20', '20', 'Dałem jej czas, by się mogła nawrócić, ale ona nie chce się odwrócić od swojej rozpusty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '21', '21', 'Oto rzucam ją na łoże boleści, a na tych, co z nią cudzołożą, ześlę wielkie utrapienie, jeśli od czynów jej się nie odwrócą,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '22', '22', 'a dzieci jej porażę śmiercią. I poznają wszystkie Kościoły, że Ja jestem tym, który przenika nerki i serca, i oddam każdemu z was według waszych czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '23', '23', 'Wam zaś, pozostałym w Tiatyrze, mówię, wam wszystkim, co nie macie tej nauki, wam, którzyście - jak mówicie - nie poznali \'głębin szatana\': Nie wkładam na was nowego brzemienia,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '24', '24', 'to jednak, co macie, zatrzymajcie, aż przyjdę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '25', '25', 'A zwycięzcy i temu, kto czynów mych strzeże do końca, dam władzę nad poganami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '26', '26', 'a będzie ich pasł żelazną laską: jak naczynie gliniane będą rozbici');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '27', '27', '- jak i Ja to wziąłem od mego Ojca - i dam mu gwiazdę poranną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '1', '28', '28', 'Kto ma uszy, niech usłyszy, co mówi Duch do Kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '0', '0', 'Aniołowi Kościoła w Sardach napisz: To mówi Ten, co ma siedem Duchów Boga i siedem gwiazd: Znam twoje czyny - masz imię (które mówi), że żyjesz, a jesteś umarły.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '1', '1', 'Stań się czujny i umocnij resztę, która miała umrzeć, bo nie uznałem twoich uczynków za doskonałe wobec Boga mojego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '2', '2', 'Pamiętaj więc, jak wziąłeś i usłyszałeś, strzeż się tego i nawróć się! Jeśli więc czuwać nie będziesz, przyjdę jak złodziej, i nie będziesz wiedział, o której godzinie przyjdę do ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '3', '3', 'Lecz w Sardach masz kilka osób, które swoich szat nie splamiły i będą chodzić za mną w bieli, bo głodni są.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '4', '4', 'Tak szaty białe przywdzieje zwycięzca i z księgi życia imienia jego nie wymażę. I wyznam imię jego przed moim Ojcem i Jego aniołami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '5', '5', 'Kto ma uszy, niech usłyszy, co mówi Duch do Kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '6', '6', 'Aniołowi Kościoła w Filadelfii napisz: To mówi Święty, Prawdomówny, który ma klucz Dawida, ten, co otwiera, a nikt nie może zamknąć, i co zamyka, a nikt nie może otworzyć:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '7', '7', 'Znam twoje czyny. Oto postanowiłem jako dar przed tobą drzwi otwarte, których nikt nie może zamknąć, bo ty, chociaż moc masz znikomą, zachowałeś moje słowo i nie wyparłeś się mego imienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '8', '8', 'Oto Ja ci daję (ludzi) z synagogi szatana, spośród tych, którzy mówią o sobie, że są Żydami - a nie są nimi, lecz kłamią. Oto sprawię, że przyjdą i padną na twarz u twoich stóp, a poznają, że Ja umiłowałem ciebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '9', '9', 'Skoro bowiem zachowałeś nakaz mojej wytrwałości i Ja ciebie zachowam od godziny próby, która ma przyjść na cały obszar zamieszkany, by wypróbować tych, co mieszkają na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '10', '10', 'Przyjdę niebawem: Trzymaj, co masz, by nikt nie odebrał ci wieńca!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '11', '11', 'Zwycięzcę uczynię filarem w świątyni Boga mojego i już nie wyjdzie na zewnątrz. I napiszę na nim imię Boga mojego i imię miasta Boga mojego, Nowego Jeruzalem, co z nieba zstępuje od mego Boga, i moje nowe imię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '12', '12', 'Kto ma uszy, niech usłyszy, co mówi Duch do Kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '13', '13', 'Aniołowi Kościoła w Laodycei napisz: To mówi Amen. Świadek wierny i prawdomówny, Początek stworzenia Bożego:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '14', '14', 'Znam twoje czyny, że nie jesteś zimny ani gorący. Obyś był zimny albo gorący!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '15', '15', 'A tak, skoro jesteś letni i ani gorący, ani zimny, mam cię wyrzucić z moich ust.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '16', '16', 'Ty bowiem mówisz: \'jestem bogaty\' i \'wzbogaciłem się\', i \'niczego mi nie potrzeba\', a nie wiesz, że to ty jesteś nieszczęsny i godzien litości, i biedny, i ślepy, i nagi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '17', '17', 'Radzę ci kupić u mnie złota w ogniu oczyszczonego, abyś się wzbogacił,  i białe szaty, abyś się odział, i aby nie widziano twojej haniebnej nagości, i balsamu do namaszczenia twoich oczu, abyś przejrzał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '18', '18', 'Ja wszystkich, których kocham, karcę i wychowuję. Bądź więc gorliwy i nawróć się.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '19', '19', 'Oto stoję u drzwi i kołaczę: jeśli kto usłyszy mój głos i drzwi otworzy, wejdę do niego i będę z nim wieczerzał, a on ze mną.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '20', '20', 'Zwycięzcy dam zasiąść ze sobą na moim tronie, jak i Ja zwyciężyłem i zasiadłem z mym Ojcem na Jego tronie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '2', '21', '21', 'Kto ma uszy, niech usłyszy, co mówi Duch do Kościołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '0', '0', 'Potem ujrzałem: Oto drzwi otwarte w niebie, a głos, ów pierwszy, jaki usłyszałem, jak gdyby trąby mówiącej ze mną, powiedział: \'Wstąp tutaj, a ukażę ci to, co potem musi się stać\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '1', '1', 'Doznałem natychmiast zachwycenia: A oto w niebie stał tron i ktoś zasiadał na tronie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '2', '2', 'A Zasiadający był podobny z wyglądu do jaspisu i do krwawnika, a tęcza dokoła tronu  -   podobna z wyglądu do szmaragdu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '3', '3', 'Dokoła tronu  -   dwadzieścia cztery trony, a na tronach siedziało dwudziestu czterech Starców odzianych w białe szaty, a na ich głowach złote wieńce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '4', '4', 'Z tronu wychodzą błyskawice i (słychać) huk gromów, siedem lamp ognistych płonie przed tronem, to jest siedem Duchów Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '5', '5', 'Przed tronem niby szklane morze podobne do kryształu, a w środku i dokoła tronu cztery Zwierzęta pełne oczu z przodu i z tyłu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '6', '6', 'Zwierzę pierwsze podobne do lwa, Zwierzę drugie podobne do wołu, Zwierzę trzecie z twarzą jak gdyby ludzką, a Zwierzę czwarte podobne do orła w locie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '7', '7', 'I cztery zwierzęta  - a każde z nich ma po sześć skrzydeł  - dokoła i wewnątrz są pełne oczu, i nie mają spoczynku, mówiąc dniem i nocą: \'Święty, Święty, Święty, Pan, Bóg, Władca wszech rzeczy, który był, który jest i który przychodzi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '8', '8', 'A ilekroć Zwierzęta oddadzą chwałę i cześć, i dziękczynienie Zasiadającemu na tronie, Żyjącemu na wieki wieków,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '9', '9', 'upada dwudziestu czterech Starców przed Zasiadającym na tronie i oddaje pokłon Żyjącemu na wieki wieków, i składa przed tronem swoje wieńce, mówiąc:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '3', '10', '10', '\'Godzien jesteś, Panie i Boże nasz, odebrać chwałę i cześć, i moc, boś Ty stworzył wszystko, a dzięki Twojej woli wszystko istniało i zostało stworzone\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '0', '0', 'Potem ujrzałem na prawej ręce Zasiadającego na tronie księgę zapisaną wewnątrz i zewnątrz, zapieczętowaną na siedem pieczęci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '1', '1', 'I ujrzałem potężnego anioła, obwieszczającego głosem donośnym: \'Kto godzien jest otworzyć księgę i złamać jej pieczęcie?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '2', '2', 'A nie mógł nikt  - na niebie ani na ziemi, ani pod ziemią - otworzyć księgi ani na nią patrzeć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '3', '3', 'A ja bardzo płakałem, że nikt nie znalazł się godzien, by otworzyć księgę ani patrzeć na nią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '4', '4', 'I mówi do mnie jeden ze Starców: \'Przestań płakać\': Oto zwyciężył Lew z pokolenia Judy, Odrośl Dawida, tak że otworzy księgę i siedem jej pieczęci\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '5', '5', 'I ujrzałem między tronem z czworgiem Zwierząt a kręgiem Starców stojącego Baranka jakby zabitego. A miał siedem rogów i siedem oczu, to jest siedem Duchów Boga wysłanych na całą ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '6', '6', 'On poszedł i wziął księgę z prawicy zasiadającego na tronie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '7', '7', 'A kiedy wziął księgę, czworo Zwierząt i dwudziestu czterech Starców upadło przed Barankiem, każdy mając harfę i złote czasze pełne kadzideł, są to modlitwy świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '8', '8', 'I taką nową pieśń śpiewają: \'Godzien jesteś wziąć księgę i otworzyć jej pieczęcie, bo zostałeś zabity i nabyłeś dla Boga swoją krwią ludzi z każdego pokolenia, różnojęzycznych narodów i ludów,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '9', '9', 'i uczyniłeś ich dla Boga naszego królestwem i kapłanami, a będą królować na ziemi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '10', '10', 'I ujrzałem, i usłyszałem głos wielu aniołów dokoła tronu i zwierząt, i Starców, a liczba ich była miriady miriad i tysiące tysięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '11', '11', 'Mówili oni głosem donośnym: \'Baranek zabity jest godzien wziąć potęgę i bogactwo, i mądrość, i moc, i cześć, i chwałę, i uwielbienie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '12', '12', 'A wszelkie stworzenie, które jest w niebie i na ziemi, i pod ziemią i na morzu, i wszystko, co w nich przebywa, usłyszałem, jak mówiło: \'Zasiadającemu na tronie i Barankowi uwielbienie i cześć, i chwała, i moc na wieki wieków\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '4', '13', '13', 'A czworo Zwierząt mówiło: \'Amen\'. Starcy zaś upadli i oddali pokłon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '0', '0', 'I ujrzałem: Gdy Baranek otworzył pierwszą z siedmiu pieczęci, usłyszałem pierwsze z czterech Zwierząt mówiące jakby hukiem gromu: \'Przyjdź!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '1', '1', 'I ujrzałem: Oto biały koń, a siedzący na nim miał łuk. Dano mu wieniec, a on, zwycięzca, wyruszył, by dalej zwyciężać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '2', '2', 'A gdy otworzył drugą pieczęć, usłyszałem drugie Zwierzę mówiące: \'Przyjdź!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '3', '3', 'I wyszedł inny koń - koloru płomienia, a siedzącemu na nim pozwolono odebrać ziemi pokój, tak iż ludzie wzajemnie się zabijali. I dano mu wielki miecz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '4', '4', 'A gdy otworzył trzecią pieczęć, usłyszałem trzecie Zwierzę, mówiące: \'Przyjdź!\' I ujrzałem: A oto czarny koń, a siedzący na nim miał w ręce wagę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '5', '5', 'I usłyszałem jakby głos w pośrodku czterech Zwierząt, mówiący: \'Miara pszenicy za denara i trzy miary jęczmienia za denara, a nie niszcz oliwy i wina!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '6', '6', 'A gdy otworzył czwartą pieczęć, usłyszałem głos czwartego Zwierzęcia mówiącego: \'Przyjdź!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '7', '7', 'I ujrzałem: Oto koń trupio blady, a imię siedzącego na nim Śmierć, i Otchłań mu towarzyszyła. I dano im władzę nad czwartą częścią ziemi, aby miecz i głód, i mór, i dzikie zwierzęta zabijały ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '8', '8', 'A gdy otworzył piątą pieczęć, ujrzałem pod ołtarzem dusze zabitych dla Słowa Bożego i dla świadectwa, jakie mieli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '9', '9', 'I głosem donośnym tak zawołały: \'Władco święty i prawdziwy! Jak długo jeszcze będziesz zwlekał z wyrokiem i karą za naszą krew na tych, co mieszkają na ziemi?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '10', '10', 'I dano każdemu z nich białą szatę, i powiedziano im, by jeszcze przez krótki czas odpoczęli, aż dopełni się liczba ich współsług oraz braci, którzy, jak i oni, mają być zabici.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '11', '11', 'Potem ujrzałem: Gdy otworzył szóstą pieczęć, nastąpiło wielkie trzęsienie ziemi i słońce stało się czarne jak włosiany wór, a cały księżyc stał się jak krew.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '12', '12', 'I gwiazdy spadły z nieba na ziemię, tak jak drzewo figowe wstrząsane silnym wiatrem zrzuca na ziemię swe niedojrzałe owoce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '13', '13', 'Niebo zostało usunięte jak księga, która się zwija, a każda góra i każda wyspa zostały ruszone ze swoich miejsc.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '14', '14', 'A królowie ziemscy, wielmoże i wodzowie, bogacze i możni, i każdy niewolnik, i wolny, ukryli się w jaskiniach i w skalistych górach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '15', '15', 'I mówią do gór i do skał: \'Padnijcie na nas i ukryjcie nas przed obliczem Zasiadającego na tronie i przed gniewem Baranka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '5', '16', '16', 'bo nadszedł Wielki Dzień Jego gniewu, a któż zdoła się ostać?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '0', '0', 'Potem ujrzałem czterech aniołów stojących na czterech narożnikach ziemi, powstrzymujących cztery wiatry ziemi, aby wiatr nie wiał na ziemi ani na morzu, ani na żadne drzewo.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '1', '1', 'I ujrzałem innego anioła wstępującego od wschodu słońca, mającego pieczęć Boga żywego. Zawołał on donośnym głosem do czterech aniołów, którym dano moc szkodzić ziemi i morzu:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '2', '2', '\'Nie wyrządzajcie szkody ziemi ani morzu, ani drzewom, aż popieczętujemy na czołach sługi Boga żywego\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '3', '3', 'I usłyszałem liczbę pieczętowanych: sto czterdzieści cztery tysiące opieczętowanych ze wszystkich pokoleń synów Izraela:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '4', '4', 'z pokolenia Judy dwanaście tysięcy opieczętowanych, z pokolenia Rubena dwanaście tysięcy, z pokolenia Gada dwanaście tysięcy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '5', '5', 'z pokolenia Asera dwanaście tysięcy, z pokolenia Neftalego dwanaście tysięcy, z pokolenia Manassesa dwanaście tysięcy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '6', '6', 'z pokolenia Symeona dwanaście tysięcy, z pokolenia Lewiego dwanaście tysięcy, z pokolenia Issachara dwanaście tysięcy,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '7', '7', 'z pokolenia Zabulona dwanaście tysięcy, z pokolenia Józefa dwanaście tysięcy, z pokolenia Beniamina dwanaście tysięcy opieczętowanych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '8', '8', 'Potem ujrzałem: A oto wielki tłum, którego nikt nie mógł zliczyć, (ludzie) ze wszystkich narodów, pokoleń i ludów różnojęzycznych, stojący przed tronem i przed Barankiem, odziani w białe szaty, a palmy w ich ręku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '9', '9', 'I głosem donośnym tak wołają: \'Zbawienie (u) Boga naszego zasiadającego na tronie i (u) Baranka\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '10', '10', 'A wszyscy aniołowie stanęli wokół tronu i Starców, i czworga Zwierząt, i padli na twarz przed tronem, i oddali Bogu pokłon,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '11', '11', 'mówiąc: \'Amen. Uwielbienie i chwała, i mądrość, i dziękczynienie, i cześć, i moc, i potęga Bogu naszemu na wieki wieków. Amen!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '12', '12', 'A jeden ze Starców odezwał się, mówiąc do mnie tymi słowami: \'A ci przyodziani w białe szaty kim są i skąd przybyli?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '13', '13', 'I powiedziałem do niego: \'Panie, ty wiesz\'. I rzekł do mnie: \'To ci, którzy przychodzą z wielkiego ucisku i opłukali swoje szaty, i wybielili je w krwi Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '14', '14', 'Dlatego są przed tronem Boga i w Jego świątyni cześć Mu oddają we dnie i w nocy. A Zasiadający na tronie rozciągnie namiot nad nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '15', '15', 'Nie będą już łaknąć ani nie będą już pragnąć, i nie porazi ich słońce ani żaden upał,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '6', '16', '16', 'bo paść ich będzie Baranek, który jest w pośrodku tronu, i poprowadzi ich do źródeł wód życia, a Bóg każdą łzę otrze z ich oczu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '0', '0', 'A gdy otworzył siódmą pieczęć, zapanowała w niebie cisza jakby na półgodziny.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '1', '1', 'Potem ujrzałem siedmiu aniołów, którzy stoją przed Bogiem. Dano im siedem trąb.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '2', '2', 'I przyszedł inny anioł, i stanął przy ołtarzu, mając złote naczynie na żar. I dano mu wiele kadzideł,  aby złożył je w ofierze, jako modlitwy wszystkich świętych na złotym ołtarzu, który jest przed tronem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '3', '3', 'I wzniósł się dym kadzideł, jako modlitwy świętych, z ręki anioła przed Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '4', '4', 'Anioł zaś wziął naczynie na żar, napełnił je ogniem z ołtarza i zrzucił na ziemię. I zahuczały gromy, zajaśniały błyskawice i zatrzęsła się ziemia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '5', '5', 'A siedmiu aniołów, mających siedem trąb, przygotowało się, aby zatrąbić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '6', '6', 'Pierwszy zatrąbił: a powstał grad i ogień - pomieszane z krwią - i spadły na ziemię. I spłonęła trzecia część ziemi, i spłonęła trzecia część drzew, i spłonęła wszystka trawa zielona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '7', '7', 'Drugi anioł zatrąbił: i jakby wielka góra płonąca ogniem została rzucona w morze, a trzecia część morza zamieniała się w krew');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '8', '8', 'i wyginęła w morzu trzecia część stworzeń - te, które są ożywione - i trzecia część okrętów uległa zniszczeniu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '9', '9', 'Trzeci anioł zatrąbił: i spadła z nieba wielka gwiazda, płonąca jak pochodnia, a spadła na trzecią część rzek i na źródła wód.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '10', '10', 'A gwiazda nazywa się Piołun. I trzecia część wód zamieniła się w piołun, i wielu ludzi pomarło od wód, bo stały się gorzkie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '11', '11', 'Czwarty anioł zatrąbił: i została rażona trzecia część słońca i trzecia część księżyca, i trzecia część gwiazd, tak iż zaćmiła się trzecia ich część i dzień nie  jaśniał w trzeciej swej części, i noc - podobnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '7', '12', '12', 'Potem ujrzałem i usłyszałem jednego orła lecącego przez środek nieba, mówiącego donośnym głosem: \'Biada, biada, biada mieszkańcom ziemi z powodu głosów trąby trzech pozostałych aniołów, którzy mają (jeszcze) trąbić!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '0', '0', 'Piąty anioł zatrąbił: i ujrzałem gwiazdę, która spadła z nieba na ziemię, i dano jej klucz od studni Przepaści.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '1', '1', 'Otworzyła studnię Przepaści, a dym się uniósł ze studni jak dym z wielkiego pieca, i od dymu studni zaćmiło się słońce i powietrze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '2', '2', 'A z dymu wyszła na ziemię szarańcza, której dano moc, jaką mają ziemskie skorpiony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '3', '3', 'I powiedziano jej, by nie szkodziła trawie na ziemi ani żadnej innej zieleni ani żadnemu drzewu, lecz tylko ludziom, którzy nie mają pieczęci Boga na czołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '4', '4', 'Nakazano jej też, by ich nie zabijała, ale miała przez pięć miesięcy zadawać im katusze. A katusze przez nią zadane są jak katusze zadane przez skorpiona, kiedy ukąsi człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '5', '5', 'W owe dni ludzie szukać będą śmierci, ale jej nie znajdą, i będą chcieli umrzeć, ale śmierć od nich ucieknie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '6', '6', 'A szarańcza tak wyglądała: podobna (była) do koni w szyku bojowym, na głowach jakby wieńce podobne do złotych, oblicza ich były jakby twarze ludzkie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '7', '7', 'i miały włosy jakby włosy kobiece, a zęby ich były jak zęby lwów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '8', '8', 'Przody tułowi jakby pancerze żelazne, a łoskot ich skrzydeł jak łoskot wielokonnych wozów pędzących do boju.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '9', '9', 'I mają ogony podobne do skorpionowych oraz żądła, a swymi ogonami mogą szkodzić ludziom przez pięć miesięcy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '10', '10', 'Mają nad sobą króla - anioła Przepaści. Imię jego po hebrajsku brzmi ABADDON, a w greckim języku ma imię APOLLYON.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '11', '11', 'Minęło pierwsze \'biada\', oto jeszcze dwa inne \'biada\' nadchodzą potem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '12', '12', 'Szósty anioł zatrąbił: i usłyszałem jeden głos od czterech rogów złotego ołtarza, który jest przed Bogiem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '13', '13', 'mówiący do szóstego anioła, który ma trąbę: \'Uwolnij czterech aniołów związanych nad wielką rzeką Eufratem!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '14', '14', 'I zostali uwolnieni czterej aniołowie, gotowi na godzinę, dzień, miesiąc i rok, aby zabić trzecią część ludzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '15', '15', 'A liczba wojsk konnicy: dwie miriady miriad - posłyszałem ich liczbę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '16', '16', 'I ujrzałem w widzeniu konie i siedzących na nich (jeźdźców) w pancerzach barwy ognia, hiacyntu i siarki. A głowy koni jak głowy lwów, a z pysków ich wychodzi ogień, dym i siarka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '17', '17', 'Trzecia część ludzi zginęła od tych trzech plag: od ognia, dymu i siarki wychodzących z ich pysków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '18', '18', 'Bo siła koni jest w ich pyskach i w ogonach, bo ich ogony - podobne do węży: mają głowy i szkodzą nimi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '19', '19', 'Pozostali zaś ludzie - nie zabici przez te plagi - nie zerwali z dziełami swoich rąk,  nie zaprzestali (też) wielbić demonów ani bożków złotych, srebrnych, spiżowych, kamiennych, drewnianych, które nie mogą widzieć ani słyszeć, ani chodzić.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '8', '20', '20', 'Ani nie zerwali z zabójstwami, czarami, nierządem i kradzieżą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '0', '0', 'I ujrzałem innego potężnego anioła, zstępującego z nieba, odzianego w obłok. Tęcza była nad jego głową, a oblicze jego było jak słońce, a nogi miał jak słupy ogniste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '1', '1', 'W prawej ręce miał książeczkę otwartą. Prawą nogą stanął na morzu, a lewą na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '2', '2', 'I zawołał głosem tak donośnym jak ryk lwa. A kiedy zawołał, siedem gromów przemówiło swym głosem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '3', '3', 'Kiedy przemówiło siedem gromów, zabrałem się do pisania, lecz usłyszałem głos mówiący z nieba: \'Zapieczętuj to, co siedem gromów powiedziało, i nie pisz tego!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '4', '4', 'Anioł zaś, którego ujrzałem stojącego na morzu i na ziemi, podniósł ku niebu prawą rękę');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '5', '5', 'i przysiągł na Żyjącego na wieki wieków, który stworzył niebo i to, co w nim jest, i ziemię, i to, co w niej jest, i morze, i to, co w nim jest, że nie będzie już zwłoki,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '6', '6', 'lecz w dniach głosu siódmego anioła, gdy będzie miał trąbić, misterium Boga się dokona, tak jak podał On dobrą nowinę sługom swym prorokom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '7', '7', 'A głos, który z nieba słyszałem, znów usłyszałem, jak zwracał się do mnie w słowach: \'Idź, weź księgę otwartą z ręki anioła stojącego na morzu i na ziemi!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '8', '8', 'Poszedłem więc do anioła, mówiąc mu, aby mi dał książeczkę. Rzekł do mnie: \'weź i zjedz ją, a napełni wnętrzności twe goryczą, lecz w ustach twych będzie słodka jak miód\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '9', '9', 'Wziąłem zatem książeczkę z ręki anioła i połknąłem ją, a w ustach moich stała się słodka jak miód, a gdy ją zjadłem, wnętrzności moje napełniły się goryczą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '9', '10', '10', 'I mówią mi: \'Trzeba ci znów prorokować o ludach, narodach różnojęzycznych i o wielu królach\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '0', '0', 'Potem dano mi trzcinę podobną do mierniczego pręta i powiedziano: \'Wstań i zmierz Świątynię Bożą i ołtarz, i tych, co wielbią w niej Boga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '1', '1', 'Dziedziniec zewnętrzny Świątyni pomiń całkowicie i nie mierz go, bo został dany poganom, i będą deptać Miasto Święte przez czterdzieści dwa miesiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '2', '2', 'Dwom moim Świadkom dam władzę, a będą prorokować obleczeni w wory przez tysiąc dwieście sześćdziesiąt dni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '3', '3', 'Oni są dwoma drzewami oliwnymi i dwoma świecznikami, co stoją przed Panem ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '4', '4', 'A jeśli kto chce ich skrzywdzić, ogień wychodzi z ich ust i pożera wrogów. A jeśli ktokolwiek zechciałby ich skrzywdzić, w ten sposób musi być zabity.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '5', '5', 'Mają oni władzę zamknąć niebo, aby nie padał deszcz za dni ich prorokowania, i mają władzę nad wodami: (mogą) przemienić je w krew i (mogą) porazić ziemię wszelkimi plagami, ilekroć zechcą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '6', '6', 'A gdy dopełnią swojego świadectwa, Bestia, która wychodzi z Przepaści, stoczy z nimi walkę, zwycięży ich i zabije.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '7', '7', 'A zwłoki ich (leżeć będą) na placu wielkiego miasta, które przenośnie zwie się Sodoma i Egipt,  gdzie także ukrzyżowano ich Pana.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '8', '8', 'I (wielu) spośród ludów, szczepów i różnojęzycznych narodów przez trzy i pół dnia patrzy na ich zwłoki, i nie zezwala ich złożyć do grobu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '9', '9', 'Mieszkańcy ziemi cieszą się i radują z ich powodu. I będą sobie nawzajem posyłali dary, bo ci dwaj prorocy mieszkańcom ziemi zadali katusze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '10', '10', 'A po trzech i pół dniach wstąpił do nich duch życia od Boga i stanęli na nogi. A wielki strach padł na tych, którzy na nich patrzyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '11', '11', 'Oni zaś usłyszeli donośny głos z nieba mówiący do nich: \'Wstąpcie tutaj!\' I wstąpili do nieba w obłoku, a ich wrogowie (tak) ich zobaczyli.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '12', '12', 'W owej godzinie nastąpiło wielkie trzęsienie ziemi i runęła dziesiąta część miasta, a od tego trzęsienia ziemi zginęło siedem tysięcy osób. A pozostałych ogarnęło przerażenie i oddali chwałę Bogu nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '13', '13', 'Minęło drugie \'biada\', a oto trzecie \'biada\' niebawem nadchodzi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '14', '14', 'Siódmy anioł zatrąbił. A w niebie zabrzmiały donośne głosy mówiące: \'Nastało królowanie Pana naszego i Jego Pomazańca nad światem, i będzie królować na wieki wieków!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '15', '15', 'A dwudziestu czterech Starców zasiadających na tronach przed Bogiem padło na twarz i oddało pokłon Bogu,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '16', '16', 'mówiąc: \'Dziękujemy Tobie, Panie, Boże, Władco wszechrzeczy, który jesteś i który byłeś, żeś objął wielką Twą władzę i rozpoczął królowanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '17', '17', 'Rozgniewały się narody. A nadszedł Twój gniew i pora na osądzenie umarłych, i na wynagrodzenie sług Twoich proroków i świętych, i tych, którzy się boją Twojego imienia, małych i wielkich, i na zgładzenie tych, którzy niszczą ziemię\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '10', '18', '18', 'A potem Świątynia Boga w niebie się otwarła i Arka Jego Przymierza ukazała się w Jego Świątyni, i ukazały się błyskawice, zahuczały gromy, zatrzęsła się ziemia i spadł wielki grad.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '0', '0', 'Potem ukazał się wielki znak na niebie: Niewiasta obleczona w słońce i księżyc pod jej stopami, a na jej głowie wieniec z gwiazd dwunastu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '1', '1', 'Jest brzemienna i woła w bólach i mękach rodzenia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '2', '2', 'Ukazał się też inny znak na niebie: Oto wielki Smok ognisty, ma siedem głów i dziesięć rogów, a na głowach siedem diademów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '3', '3', 'Ogon jego zamiata trzecią część gwiazd z nieba i rzucił je na ziemię. Smok stanął przed mającą urodzić Niewiastą, ażeby, skoro tylko porodzi, pożreć jej Dziecko.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '4', '4', 'I porodziła Syna - mężczyznę, który będzie pasł wszystkie narody rózgą żelazną. Dziecko jej zostało porwane do Boga i do Jego tronu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '5', '5', 'Niewiasta zaś zbiegła na pustynię, gdzie ma miejsce przygotowane przez Boga, tam będzie żywiona przez tysiąc dwieście sześćdziesiąt dni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '6', '6', 'Nastąpiła walka w niebie: Michał i jego aniołowie mieli walczyć ze Smokiem. I wystąpił do walki Smok i jego aniołowie,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '7', '7', 'ale nie przemógł i nie było już dla nich miejsca w niebie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '8', '8', 'Strącony został wielki Smok, Wąż Starodawny, nazywany diabłem i szatanem, zwodzący całą zamieszkałą ziemię, a z nim strąceni zostali jego aniołowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '9', '9', 'I usłyszałem donośny głos mówiący w niebie: \'Teraz nastało zbawienie, potęga i królowanie Boga naszego i władza Jego Pomazańca, bo strącony został oskarżyciel naszych braci, który dniem i nocą oskarża ich przed naszym Bogiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '10', '10', 'A oni zwyciężyli dzięki krwi Baranka i dzięki słowu swojego świadectwa, i nie umiłowali życia swego aż do śmierci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '11', '11', 'Dlatego radujcie się niebiosa i ich mieszkańcy! Biada ziemi i morzu,  bo zstąpił do was diabeł, pałając wielkim gniewem, świadom, że mało ma czasu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '12', '12', 'A kiedy Smok spostrzegł, że został strącony na ziemię, zaczął prześladować Niewiastę, która porodziła Mężczyznę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '13', '13', 'A dano Niewieście dwa skrzydła orła wielkiego, aby leciała na pustynię na swoje miejsce. Tam ją żywią przez czas i czasy, i połowę czasu z dala od Węża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '14', '14', 'A Wąż wypuścił z paszczy za Niewiastą wodę jak rzekę, żeby ją rzeka porwała.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '15', '15', 'Lecz ziemia przyszła z pomocą Niewieście: otworzyła ziemia swoją paszczę i pochłonęła rzekę, którą Smok wypuścił z paszczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '16', '16', 'I rozgniewał się Smok na Niewiastę, i odszedł, by rozpocząć walkę z resztą jej potomstwa, z tymi, co strzegą przykazań Bożych i mają świadectwo Jezusa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '11', '17', '17', 'I stanął na piaszczystym brzegu morskim.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '0', '0', 'Potem ujrzałem Bestię wychodzącą z morza, mającą dziesięć rogów i siedem głów, a na rogach jej dziesięć diademów, a na jej głowach imiona bluźniercze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '1', '1', 'Bestia, którą widziałem, podobna była do pantery, łapy jej - jak u niedźwiedzia, paszcza jej - jak paszcza lwa. A Smok dał jej swoją moc, swój tron i wielką władzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '2', '2', 'I (ujrzałem) jedną z jej głów śmiertelnie zranioną, lecz (ta) śmiertelna rana została uleczona. A cała ziemia w podziwie powiodła wzrokiem za Bestią,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '3', '3', 'i pokłon oddali Smokowi, bo dał władzę Bestii. Oddali też pokłon Bestii, mówiąc: \'Któż jest podobny do Bestii i któż potrafi rozpocząć z nią walkę?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '4', '4', 'Pozwolono jej przechwalać się i bluźnić, i dano jej swobodę działania przez czterdzieści dwa miesiące.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '5', '5', 'Zatem otworzyła swe usta dla bluźnierstw przeciwko Bogu, by bluźnić Jego imieniu i Jego przybytkowi, i mieszkańcom nieba.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '6', '6', 'I pozwolono jej stoczyć walkę ze świętymi i zwyciężyć ich. Dano jej władzę nad wszystkimi szczepami, ludami różnojęzycznymi i narodami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '7', '7', 'Wszyscy mieszkańcy ziemi będą oddawać pokłon władcy, każdy, którego imię nie jest zapisane od założenia świata w księdze życia zabitego Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '8', '8', 'Jeśli kto ma uszy, niech usłyszy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '9', '9', 'Jeśli kto jest do niewoli przeznaczony - idzie do niewoli, jeśli kto na zabicie mieczem - musi być mieczem zabity. Tu się okazuje wytrwałość i wiara świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '10', '10', 'Potem ujrzałem inną Bestię wychodzącą z ziemi: miała dwa rogi podobne do rogów Baranka, a mówiła jak Smok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '11', '11', 'Ona całą władzę pierwszej Bestii przed nią wykonuje i sprawia, że ziemia i wszyscy jej mieszkańcy oddają pokłon pierwszej Bestii, której rana śmiertelnie została uleczona.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '12', '12', 'I czyni wielkie znaki. Sprawia, że nawet ogień na oczach ludzi zstępuje z nieba na ziemię.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '13', '13', 'I zwodzi mieszkańców ziemi znakami, które jej dano czynić przed Bestią, mówiąc mieszkańcom ziemi, aby wykonali obraz Bestii, która ma ranę od miecza, a ożyła.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '14', '14', 'Pozwolono jej nawet tchnąć ducha w obraz Bestii, tak iż nawet przemówił obraz Bestii i sprawił, że zostaną zabici wszyscy, którzy nie oddadzą pokłonu obrazowi Bestii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '15', '15', 'I sprawia, że wszyscy: mali i wielcy, bogaci i ubodzy, wolni i niewolnicy otrzymują znamię na prawej ręce albo na czole,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '16', '16', 'tak że nikt nie może kupić lub sprzedać, kto nie ma znamienia: imienia Bestii lub liczby jej imienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '12', '17', '17', 'Tu jest (potrzebna) mądrość. Kto ma rozum, niech przeliczy liczbę Bestii, liczba to bowiem człowieka. A liczba jego sześćset sześćdziesiąt sześć.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '0', '0', 'Potem ujrzałem: A oto Baranek stoi na górze Syjon, a z nim sto czterdzieści cztery tysiące, mają imię Jego i imię Jego Ojca wypisane na czołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '1', '1', 'I usłyszałem głos z nieba jakby szum wielkich wód i jakby huk potężnego gromu. A głos, który usłyszałem, (brzmiał) jak dźwięk harfiarzy grający na harfach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '2', '2', 'I śpiewają jakby nową pieśń przed tronem i przed czterema Zwierzętami i przed Starcami; a nikt tej pieśni nie mógł się nauczyć prócz stu czterdziestu czterech tysięcy wykupionych z ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '3', '3', 'To ci, którzy z kobietami się nie splamili, bo są dziewicami; to oni właśnie towarzyszą Barankowi, dokądkolwiek idzie. Oni zostali wykupieni spośród ludzi na pierwociny dla Boga i dla Baranka,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '4', '4', 'a usta ich nigdy nie splamiły się kłamstwem: są nienaganni.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '5', '5', 'Potem ujrzałem innego anioła lecącego przez środek nieba, mającego odwieczną Dobrą Nowinę do obwieszczenia tym, którzy siedzą na ziemi, wszystkim narodom, szczepom różnojęzycznym i ludom.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '6', '6', 'Wołał on głosem donośnym: \'Ulęknijcie się Boga i dajcie Mu chwałę, bo nadeszła godzina Jego sądu. Oddajcie pokłon Temu, który uczynił niebo i ziemię, i morze, i źródła wód!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '7', '7', 'A inny, drugi anioł przyszedł za nim, mówiąc: \'Upadł, upadł wielki Babilon, który winem szaleńczego nierządu napoił wszystkie narody!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '8', '8', 'A inny, trzeci anioł, przyszedł za nimi, mówiąc donośnym głosem: \'Jeśli kto wielbi Bestię i obraz jej, i bierze sobie jej znamię na czoło lub rękę,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '9', '9', 'ten również będzie pił wino zapalczywości Boga, przygotowane, nie rozcieńczone w kielichu Jego gniewu, i będzie katowany ogniem i siarką wobec świętych aniołów i wobec Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '10', '10', 'A dym ich katuszy na wieki wieków unosi się w górę. I nie mają wytchnienia we dnie i w nocy czciciele Bestii i jej obrazu, i każdy, kto bierze znamię jej imienia\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '11', '11', 'Tu się okazuje wytrwałość świętych, którzy strzegą przykazań Bożych i wiary Jezusowej.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '12', '12', 'I usłyszałem głos, mówiący z nieba: \'Napisz: Szczęśliwi, którzy w Panu umierają - już teraz. Zaiste, mówi Duch, niech odpoczną od swoich mozołów, bo idą wraz z nimi ich czyny\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '13', '13', 'Potem ujrzałem: Oto biały obłok, a Siedzący na obłoku, podobny do Syna Człowieczego, miał złoty wieniec na głowie, a w ręku ostry sierp.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '14', '14', 'I wyszedł inny anioł ze świątyni, wołając donośnym głosem do Siedzącego na obłoku: \'Zapuść swój sierp i dokonaj żniwa, bo pora dokonania żniwa już nadeszła, bo na ziemi (zboże) dojrzało do żniwa\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '15', '15', 'A Siedzący na obłoku rzucił swój sierp na ziemię i ziemia została zżęta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '16', '16', 'Potem wyszedł inny anioł ze świątyni, która jest w niebie, on także miał ostry sierp.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '17', '17', 'Od ołtarza zaś wyszedł inny anioł, który ma władzę nad ogniem, i zawołał donośnym głosem do tego, który ma ostry sierp: \'Zapuść twój ostry sierp i poobcinaj grono winorośli ziemi, bo jagody jej dojrzały!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '18', '18', 'Rzucił więc anioł swój sierp na ziemię, obrał z gron winorośl ziemi i wrzucił ją do ogromnej tłoczni Bożego gniewu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '13', '19', '19', 'Tłocznię wydeptano poza miastem, a z tłoczni krew wytrysnęła aż do wędzideł końskich na tysiąc i sześćset stadiów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '0', '0', 'Potem ujrzałem inny znak na niebie, wielki i godzien podziwu: Siedmiu aniołów trzymających siedem plag - tych ostatecznych, bo w nich się dopełnił gniew Boży.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '1', '1', 'I ujrzałem jakby morze szklane, zmieszane z ogniem, i tych, co zwyciężają Bestię i jej obraz, i liczbę jej imienia, stojących nad morzem szklanym, mających harfy Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '2', '2', 'Śpiewają oni pieśń Mojżesza, sługi Bożego i pieśń Baranka: \'Dzieła Twoje są wielkie i godne podziwu, Panie, Boże, Władco wszechrzeczy! Sprawiedliwe i wierne są Twoje drogi, Królu Narodów!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '3', '3', 'Któż by się Ciebie nie bał, o Panie, i nie uczcił Twego imienia? Bo tylko Ty jesteś Święty, albowiem wszystkie narody przyjdą i padną na twarz przed Tobą, bo ujawniły się Twoje sprawiedliwe wyroki\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '4', '4', 'Potem ujrzałem: W niebie została otwarta świątynia Przybytku Świadectwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '5', '5', 'A ze świątyni wyszło siedmiu aniołów mających siedem plag, odzianych w czysty, lśniący len, przepasanych na piersiach złotymi pasami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '6', '6', 'Jedno z czterech Zwierząt podało siedmiu aniołom siedem czasz złotych, pełnych gniewu Boga żyjącego na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '14', '7', '7', 'A świątynia napełnia się dymem od chwały Boga i Jego potęgi. I nikt nie mógł wejść do świątyni, aż się spełniło siedem plag siedmiu aniołów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '0', '0', 'Potem usłyszałem donośny głos ze świątyni, mówiący do siedmiu aniołów: \'Idźcie i wylejcie siedem czasz gniewu Bożego na ziemię!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '1', '1', 'I poszedł pierwszy, i wylał swą czaszę na ziemię. A wrzód złośliwy, bolesny, wystąpił na ludziach, co mają znamię Bestii, i na tych, co wielbią jej obraz.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '2', '2', 'Drugi wylał swą czaszę na morze. A stało się ono jakby krew z zabitego, i wszystkie istoty żywe poniosły śmierć, te które są w morzu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '3', '3', 'Trzeci wylał swą czaszę na rzeki i źródła wód. I stały się krwią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '4', '4', 'Usłyszałem anioła wód, mówiącego: \'Sprawiedliwy jesteś, Ty, który jesteś, który byłeś, o Święty, że tak osądziłeś.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '5', '5', 'Ponieważ oni wylali krew świętych i proroków, krew również pić im dałeś. Godni są tego!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '6', '6', 'I usłyszałem, jak mówił ołtarz: \'Tak, Panie, Boże, Władco wszechrzeczy, prawdziwe i sprawiedliwe są Twoje wyroki\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '7', '7', 'Czwarty wylał swą czaszę na słońce, i dano mu władzę dotknąć ludzi ogniem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '8', '8', 'Wielkim upałem ludzie zostali dotknięci i bluźnili imieniu Boga, który ma moc nad tymi plagami, a nie nawrócili się, by oddać Mu chwałę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '9', '9', 'Piąty wylał swą czaszę na tron Bestii, w jej królestwie nastały ciemności, a ludzie z bólu gryźli języki');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '10', '10', 'i bluźnili Bogu nieba z powodu swoich boleści i wrzodów, ale od czynów swoich się nie odwrócili.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '11', '11', 'Szósty wylał swą czaszę na rzekę wielką, na Eufrat. A wyschła jej woda, by dla królów ze wschodu droga słońca stanęła otworem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '12', '12', 'I ujrzałem wychodzące z paszczy Smoka i z paszczy Bestii, i z ust Fałszywego Proroka trzy duchy nieczyste, jak ropuchy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '13', '13', 'Są to duchy czyniące znaki - demony, które wychodzą do królów całej zamieszkałej ziemi, by zgromadzić ich na wojnę w wielkim dniu Boga, Władcy wszechrzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '14', '14', 'Oto przyjdę jak złodziej: szczęśliwy, który czuwa i strzeże swych szat, by nie chodził nago i aby nie widziano jego sromoty.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '15', '15', 'I zgromadziły ich na miejsce zwane po hebrajsku Har - Magedon.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '16', '16', 'Siódmy wylał swą czaszę w powietrze, a ze świątyni od tronu dobył się głos donośny, mówiący: \'Stało się!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '17', '17', 'I zajaśniały błyskawice, i zahuczały gromy, i nastąpiło wielkie trzęsienie ziemi, tak wielkie trzęsienie ziemi, jakiego nie było, odkąd jest człowiek na ziemi.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '18', '18', 'A wielkie miasto rozpadło się na trzy części i miasta pogan runęły. I wspomniał Bóg na Wielki Babilon, aby mu dać kielich z winem swego zapalczywego gniewu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '19', '19', 'Zniknęły wszystkie wyspy i góry przepadły na zawsze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '15', '20', '20', 'Olbrzymi grad o wadze jakby talentu spadł z nieba na ludzi. A ludzie bluźnili Bogu z powodu plagi gradowej, bo to była bardzo ciężka plaga.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '0', '0', 'Potem przyszedł jeden z siedmiu aniołów trzymających siedem czasz i tak odezwał się do mnie: \'Chodź, ukażę ci sąd nad Wielką Nierządnicą siedzącą nad wielu wodami,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '1', '1', 'z którą nierządu się dopuścili królowie ziemi, a mieszkańcy ziemi upili się winem jej nierządu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '2', '2', 'I zaniósł mnie w stanie zachwycenia na pustynię. I ujrzałem Niewiastę siedzącą na Bestii szkarłatnej, pełnej imion bluźnierczych, mającej siedem głów i dziesięć rogów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '3', '3', 'A Niewiasta była odziana w purpurę i szkarłat, cała zdobna w złoto, drogi kamień i perły, miała w swej ręce złoty puchar pełen obrzydliwego i plugawego nierządu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '4', '4', 'Na jej czole wypisane tajemnicze imię: \'Wielki Babilon, Macierz nierządnic i obrzydliwości ziemi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '5', '5', 'I ujrzałem Niewiastę pijaną krwią świętych i krwią świadków Jezusa, a widząc ją bardzo się zdumiałem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '6', '6', 'I rzekł do mnie anioł: \'Czemu się zdumiałeś? Ja ci wyjaśnię tajemnicę Niewiasty i Bestii, która ją nosi, a ma siedem głów i dziesięć rogów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '7', '7', 'Bestia, którą widziałeś, była, a nie ma jej, i ma wyjść z Przepaści, i idzie na zatracenie. Zdumieją się mieszkańcy ziemi, - ci, których imię nie jest zapisane w księdze życia od założenia świata - spoglądając na Bestię, iż była, i już jej nie ma, ale ma przyjść.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '8', '8', 'Tu trzeba zrozumienia, o mający mądrość. Siedem głów to jest siedem gór, na których siedzi Niewiasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '9', '9', 'Królów jest siedmiu: pięciu upadło, jeden jest, a inny jeszcze nie przyszedł, a kiedy przyjdzie, ma na krótko pozostać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '10', '10', 'A Bestia, która była, a której już nie ma - ona jest ósmym (królem) i należy do tych siedmiu, i idzie na zatracenie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '11', '11', 'A dziesięć rogów, które widziałeś, to dziesięć królów, którzy władzy królewskiej jeszcze nie objęli, lecz na jedną godzinę wraz z Bestią wezmą władzę jakby królowie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '12', '12', 'Ci mają jeden zmysł: potęgę i władzę swoją oddają Bestii.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '13', '13', 'Ci będą walczyć z Barankiem, a Baranek ich zwycięży, bo jest Panem nad panami i Królem nad królami, a także ci, którzy są Nim (zwyciężą): powołani, wybrani i wierni\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '14', '14', 'I mówił do mnie: \'Wody, które widziałeś, gdzie Nierządnica ma siedzibę, to ludy są i tłumy, i narody różnojęzyczne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '15', '15', 'A dziesięć rogów, które widziałeś, i Bestia  nienawidzić będą Nierządnicy, spustoszą ją i obnażą, i będą jedli jej ciało, i spalą ją ogniem,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '16', '16', 'bo natchnął Bóg ich serca, tak iż wykonają Jego zamysł, ten jeden zamysł wykonają: oddadzą Bestii swoją władzę królewską, aż Boże słowa się spełnią.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '16', '17', '17', 'A Niewiasta, którą widziałeś, to owo Wielkie Miasto, mające władzę królewską nad królami ziemi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '0', '0', 'Potem ujrzałem innego anioła, zstępującego z nieba i mającego wielką władzę, a ziemia zajaśniała od jego blasku.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '1', '1', 'I zawołał potężnym głosem: \'Upadł, upadł Babilon - stolica. I stała się siedliskiem demonów i kryjówką wszelkiego ducha nieczystego, i kryjówką wszelkiego ptaka nieczystego i budzącego wstręt,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '2', '2', 'bo winem swego szaleńczego nierządu napoiła wszystkie narody, i królowie ziemi dopuścili się z nią nierządu, a kupcy ziemi wzbogacili się na jej ogromnym przepychu\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '3', '3', 'I usłyszałem inny głos z nieba mówiący: \'Wychodź z niej, mój ludu, byście nie uczestniczyli w jej grzechach, aby was nie dotknęły jej plagi,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '4', '4', 'bo grzechy jej dosięgły nieba, i wspominał Bóg o jej zbrodniach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '5', '5', 'Odpłaćcie jej tak, jak ona (wam) odpłaciła, i w dwójnasób oddajcie za to, co uczyniła: w kielichu, w którym przyrządzała (wam) wino, podwójny dział dla niej przyrządźcie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '6', '6', 'Ile się wsławiła i osiągnęła przepychu, tyle jej zadajcie katuszy i smutku. Bo mówi o swoim sercu: &gt;&gt;Zasiadam jako królowa i nie jestem wdową, i z pewnością nie zaznam żałoby&lt;&lt;.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '7', '7', 'Dlatego w jednym dniu przyjdą na nią plagi: śmierć i smutek, i głód. Ogniem będzie spalona, bo mocny jest Pan Bóg, który ją osądził\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '8', '8', 'Będą nad nią płakać i lamentować królowie ziemi, którzy nierządu z nią się dopuścili i żyli w przepychu, kiedy zobaczą dym jej pożaru.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '9', '9', 'Stanąwszy z daleka ze strachu przed jej katuszami, powiedzą: \'Biada, biada! Wielka stolico, Babilonie, stolico potężna, w jednej godzinie sąd przyszedł na ciebie!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '10', '10', 'A kupcy ziemi z żalem ją opłakują, bo ich towaru nikt już nie kupuje,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '11', '11', 'towaru: złota i srebra, drogiego kamienia i pereł, bisioru i purpury, jedwabiu i szkarłatu, wielkiego drewna tujowego i przedmiotów z kości słoniowej, wszelkich przedmiotów z drogocennego drewna, spiżu, żelaza, marmuru;');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '12', '12', 'cynamonu i wonnej maści amonum, pachnideł, olejku, kadzidła, wina, oliwy, najczystszej mąki, pszenicy, bydła i owiec, koni, powozów oraz niewolników i jeńców.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '13', '13', 'Dojrzały owoc, pożądanie twej duszy, odszedł od ciebie, i przepadły dla ciebie wszystkie rzeczy wyborne i świetne, i już ich nie znajdą.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '14', '14', 'Handlujący tymi towarami, którzy wzbogacili się na niej, staną z daleka ze strachu przed jej katuszami, płacząc i żaląc się w słowach:');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '15', '15', '\'Biada, biada, wielka stolico, odziana w bisior, purpurę i szkarłat, cała zdobna w złoto, drogie kamienie i perły,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '16', '16', 'bo w jednej godzinie przepadło tak wielkie bogactwo\'.                                                                                                                                                  Treny żeglarzy                                                                                                                                        A wszyscy sternicy i wszyscy żeglarze przybrzeżni i marynarze, i wszyscy, którzy pracują na morzu, stanęli z daleka');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '17', '17', 'i patrząc na dym jej pożaru wołali: \'Któreż miasto mogło równać się z tą stolicą?\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '18', '18', 'I sypali sobie proch na głowy, i opłakując ją z żalem, wołali: \'Biada, biada, bo wielka stolica, w której się wzbogacili wszyscy, co mają okręty na morzu, dzięki jej dostatkowi, przepadła w jednej godzinie\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '19', '19', 'Weselcie się niebiosa i święci, apostołowie i prorocy, bo w waszej sprawie Bóg wydał na nią wyrok.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '20', '20', 'A jeden potężny anioł podniósł kamień, wielki jak kamień młyński, i rzucił w morze, mówiąc: \'Z taką siłą Babilon, wielka stolica, zostanie rzucona i już jej więcej nie będzie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '21', '21', 'Głosu harfiarzy, śpiewaków, fletonistów, trębaczy już w tobie się nie usłyszy. I już nie znajdzie się w tobie żadnego mistrza jakiejkolwiek sztuki. I terkotu żaren już w tobie nie będzie słychać.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '22', '22', 'I światło lampy już w tobie nie zabłyśnie. I głosu oblubieńca i oblubienicy już w tobie się nie usłyszy: bo twoi kupcy byli możnowładcami na ziemi, bo twymi czarami omamione zostały wszystkie narody.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '17', '23', '23', 'W niej znalazła się krew proroków i świętych, i wszystkich zabitych na ziemi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '0', '0', 'Potem usłyszałem jak gdyby głos potężny wielkiego tłumu na niebie, który mówił: \'Alleluja! Zbawienie i chwała, i moc u Boga naszego!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '1', '1', 'Bo prawdziwe i sprawiedliwe są Jego wyroki, bo skazał Wielką Nierządnicę, która swoim nierządem znieprawiała ziemię, i zażądał od niej poniesienia kary za krew swoich sług\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '2', '2', 'I rzekli powtórnie: \'Alleluja!\' A dym jej wznosi się na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '3', '3', 'Dwudziestu czterech Starców padło (na twarze) i czworo Zwierząt, i oddali pokłon Bogu zasiadającemu na tronie, mówiąc: \'Amen, Alleluja!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '4', '4', 'Od tronu zaś wyszedł głos: \'Chwalcie Boga naszego, wszyscy Jego słudzy, którzy się Go boicie, mali i wielcy!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '5', '5', 'I usłyszałem jakby głos wielkiego tłumu i jakby szum potężnych wód, i jakby huk potężnych gromów, które mówiły: \'Alleluja! Bo Pan Bóg nasz, Władca wszechrzeczy objął królewską swą władzę.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '6', '6', 'Weselmy się i radujmy, i dajmy Mu chwałę, bo nadeszły Gody Baranka, a Jego Małżonka się przystroiła,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '7', '7', 'i dano jej odziać się w bisior lśniący i czysty\' - bisior bowiem oznacza sprawiedliwe czyny świętych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '8', '8', 'I mówi mi: \'Napisz: Szczęśliwi, którzy są wezwani na ucztę weselną Baranka!\' I mówi mi: \'Te prawdziwe słowa są Boże\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '9', '9', 'Ja upadłem do jego stóp, by oddać mu pokłon, ale mi mówi: \'Uważaj, byś tego nie czynił! Ja jestem współsługą twoim i twoich braci, którzy mają świadectwo Jezusa: Bogu samemu złóż pokłon!\' Świadectwem bowiem Jezusa jest duch proroctwa.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '10', '10', 'Potem ujrzałem niebo otwarte: a oto - biały koń, a Ten, co na nim siedzi, zwany Wiernym i Prawdziwym, oto sprawiedliwie sądzi i walczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '11', '11', 'Oczy Jego jak płomień ognia, a na Jego głowie wiele diademów. Ma wypisane imię, którego nikt nie zna oprócz Niego.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '12', '12', 'Odziany jest w szatę we krwi skąpaną, a imię Jego nazwano: Słowo Boże.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '13', '13', 'Wojska, które są w niebie, towarzyszyły Mu na białych koniach, wszyscy (byli) odziani w biały bisior.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '14', '14', 'A z Jego ust wychodzi ostry miecz, aby nim uderzyć narody. On paść je będzie rózgą żelazną i On wyciska w tłoczni wino zapalczywego gniewu Boga, Władcy wszechrzeczy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '15', '15', 'A na szacie i na biodrze ma napisane imię: KRÓL KRÓLÓW I PAN NAD PANAMI.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '16', '16', 'Potem ujrzałem innego anioła stojącego w słońcu; zawołał on głosem donośnym do wszystkich ptaków lecących środkiem nieba: \'Pójdźcie, zgromadźcie się na wielką ucztę (przygotowaną wam przez) Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '17', '17', 'aby pożreć trupy królów, trupy wodzów i trupy mocarzy, trupy koni i tych, co ich dosiadają, trupy wszystkich: wolnych i niewolników, małych i wielkich!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '18', '18', 'Potem ujrzałem Bestię i królów ziemi, i wojska ich zgromadzone, by stoczyć bój z Siedzącym na koniu i z Jego wojskiem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '19', '19', 'I pochwycono Bestię, a z nią Fałszywego Proroka, co czyni wobec niej znaki, którymi zwiódł tych, co wzięli znamię Bestii i pokłon oddawali jej obrazowi. Oboje żywcem zostali wyrzuceni do ognistego jeziora, gorejącego siarką.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '18', '20', '20', 'A inni zostali zabici mieczem Siedzącego na koniu, (mieczem), który wyszedł z ust Jego, a wszystkie ptaki nasyciły się ich trupami.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '0', '0', 'Potem ujrzałem anioła zstępującego z nieba, który miał klucz od Przepaści i wielki łańcuch w ręce.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '1', '1', 'Pochwycił on Smoka, tego węża starodawnego, którym jest diabeł i szatan, i związał go na tysiąc lat.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '2', '2', 'Strącił go do Przepaści i zamknął, i pieczęć nad nim umieścił, tak że już nie będzie zwodził narodów aż tysiąc lat się dopełni. A potem ma być na krótki czas uwolniony.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '3', '3', 'Potem ujrzałem trony, a na nich zasiedli (sędziowie), i dano im władzę sądzenia. Ujrzałem też dusze ściętych dla świadectwa Jezusa i dla Słowa Bożego, i tych, którzy pokłonu nie oddali Bestii ani jej obrazowi i nie wzięli znamienia na czoło ani na rękę. Ożyli oni i tysiąc lat królowali z Chrystusem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '4', '4', 'A nie ożyli inni zmarli, aż się tysiąc lat skończyło. To jest pierwsze zmartwychwstanie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '5', '5', 'Szczęśliwy i święty, kto ma udział w pierwszym zmartwychwstaniu! Śmierć druga nie ma władzy nad nimi, lecz będą kapłanami Boga i Chrystusa: tysiąc lat z Nim będą królowali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '6', '6', 'A gdy się skończył tysiąc lat, szatan zostanie zwolniony z więzienia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '7', '7', 'I wyjdzie, by omamić narody z czterech narożników ziemi, Goga i Magoga, aby ich zgromadzić na bój. A liczba ich jak piasek morski.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '8', '8', 'I rozlali się jak ziemia szeroka i otoczyli obóz świętych i miasto umiłowane. A od Boga z nieba zstąpił ogień i pochłonął ich.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '9', '9', 'Diabła zaś, który ich zwodzi, wrzucono do jeziora ognia i siarki, tam, gdzie i Bestia, i Fałszywy Prorok. A będą cierpieć katusze we dnie i w nocy na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '10', '10', 'Potem ujrzałem wielki biały tron i na nim Zasiadającego. Sprzed Jego oblicza uciekła ziemia i niebo, i zniknęły bez śladu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '11', '11', 'I ujrzałem umarłych - wielkich i małych - stojących przed tronem. I otwarto księgi. Inną też księgę otwarto, która jest księgą życia. Zatem osądzono umarłych według tego, co w księgach zapisano - według ich czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '12', '12', 'Morze wydało umarłych, co w nim byli, i Śmierć, i Otchłań wydały umarłych, co w nich byli, i osądzono każdego według jego czynów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '13', '13', 'A Śmierć i Otchłań wrzucono do jeziora ognia. To jest śmierć druga - jezioro ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '19', '14', '14', 'Kogo zaś nie znaleziono wpisanego do księgi życia, wrzucono do jeziora ognia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '0', '0', 'Potem ujrzałem niebo nowe i ziemię nową, bo pierwsze niebo i pierwsza ziemia przeminęły i morza już nie ma.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '1', '1', 'I Miasto Święte - Nowe Jeruzalem ujrzałem zstępujące z nieba od Boga, przystrojone jak oblubienica zdobna w klejnoty dla swego męża.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '2', '2', 'I usłyszałem donośny głos mówiący od tronu: \'Oto przybytek Boga z ludźmi: i zamieszka wraz z nimi, i będą oni Jego ludem, a On będzie BOGIEM Z NIMI.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '3', '3', 'Otrze z ich oczu wszelką łzę, a odtąd już nie będzie śmierci. Ani żałoby, ani krzyku, ani trudu już (odtąd) nie będzie, bo pierwsze rzeczy przeminęły\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '4', '4', 'I rzekł Zasiadający na tronie: \'Oto wszystko czynię nowe\'. I mówi: \'Napisz: Słowa te wiarygodne są i prawdziwe\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '5', '5', 'I rzekł mi: \'Stało się. Jam jest Alfa i Omega, Początek i Koniec, Ja spragnionemu dam darmo pić ze źródła wody życia.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '6', '6', 'Zwycięzca to odziedziczy i będę mu Bogiem, a on Mi będzie synem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '7', '7', 'A udziałem tchórzów, niewiernych, obmierzłych, zabójców, rozpustników, guślarzy, bałwochwalców i wszelkich kłamców (będzie) jezioro gorejące ogniem i siarką. To jest śmierć druga\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '8', '8', 'Potem przyszedł jeden z siedmiu aniołów, co trzymają siedem czasz pełnych siedmiu plag ostatecznych, i tak się do mnie odezwał: \'Chodź, ukażę ci Oblubienicę, Małżonkę Baranka\',');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '9', '9', 'I uniósł mnie w zachwyceniu na górę wielką i wysoką, i ukazał mi Miasto Święte - Jeruzalem zstępujące z nieba od Boga,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '10', '10', 'mające chwałę Bożą. Źródło jego światła podobne do kamienia drogocennego, jakby do jaspisu o przejrzystości kryształu.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '11', '11', 'Miało ono mur wielki a wysoki, miało dwanaście bram, na bramach - dwunastu aniołów, i wypisane imiona, które są imionami dwunastu pokoleń synów Izraela.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '12', '12', 'Od wschodu trzy bramy, od północy trzy bramy, od południa trzy bramy i od zachodu trzy bramy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '13', '13', 'A mur Miasta ma dwanaście warstw fundamentu, a na nich dwanaście imion dwunastu apostołów Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '14', '14', 'A ten, który mówił ze mną, trzymał miarę - złotą trzcinę, aby nią zmierzyć Miasto i jego bramy, i jego mur.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '15', '15', 'A Miasto zbudowane jest w czworobok, a długość jego jest taka jak i szerokość. I zmierzył Miasto trzciną: miało dwanaście tysięcy stadiów, długość, szerokość i wysokość jego są równe.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '16', '16', 'I zmierzył jego mur: (miał) sto czterdzieści cztery łokcie, miara, którą ma anioł, jest miarą człowieka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '17', '17', 'Mur jego jest zbudowany z jaspisu, a samo Miasto - to czyste złoto do szkła czystego podobne.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '18', '18', 'Warstwy fundamentu pod murem Miasta zdobione są wszelkim drogim kamieniem. Warstwa pierwsza - jaspis, druga - szafir, trzecia - chalcedon, czwarta - szmaragd,');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '19', '19', 'piąta - sardoniks, szósta - krwawnik, siódma - chryzolit, ósma - beryl, dziewiąta - topaz, dziesiąta - chryzopraz, jedenasta - hiacynt, dwunasta - ametyst.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '20', '20', 'A dwanaście bram - to dwanaście pereł. Każda brama była z jednej perły. Rynek Miasta - to czyste złoto jak szkło przezroczyste.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '21', '21', 'A świątyni w nim dojrzałem, bo Pan, Bóg wszechrzeczy, jest jego świątynią i Baranek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '22', '22', 'Miastu nie trzeba słońca ani księżyca, by mu świeciły, bo jasność Boża je oświetliła, a lampą jego - Baranek.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '23', '23', 'W jego świetle będą chodziły narody, a królowie ziemi wniosą do niego swój przepych.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '24', '24', 'Za dnia bramy jego nie będą zamknięte, bo już nie będzie tam nocy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '25', '25', 'I przyniosą do niego przepych i skarby narodów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '20', '26', '26', 'A nic nieczystego do niego nie wejdzie, ani ten, co czyni ohydne rzeczy i popełnia kłamstwo, lecz tylko zapisani w księdze życia Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '0', '0', 'Potem ukazał mi rzekę wody życia, lśniącą jak kryształ, wypływającą z tronu Boga i Baranka.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '1', '1', 'Pomiędzy rynkiem Miasta a rzeką, po obu stronach, drzewo życia rodzące dwanaście owoców - wydające swój owoc każdego miesiąca - a liście drzewa (służą) do leczenia narodów.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '2', '2', 'Nic godnego klątwy już odtąd nie będzie, a będzie w nim tron Boga i Baranka, słudzy zaś Jego będą cześć Mu oddawali.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '3', '3', 'Oglądać będą Jego oblicze, a imię Jego - na ich czołach.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '4', '4', 'I (odtąd) już nocy nie będzie. A nie potrzeba im światła lampy ani światła słońca, bo Pan Bóg będzie świecił nad nimi i będą królowali na wieki wieków.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '5', '5', 'I rzekł mi: \'Te słowa wiarygodne są i prawdziwe, a Pan, Bóg natchnień prorockich, posłał swojego anioła, aby sługom swoim ukazać, co musi stać się niebawem.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '6', '6', 'A oto przyjdę niebawem. Szczęśliwy, kto strzeże proroczych słów tej księgi\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '7', '7', 'To właśnie ja, Jan, słyszę i widzę te rzeczy. A kiedym usłyszał i ujrzał, upadłem, by złożyć pokłon u stóp anioła, który mi to ukazał.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '8', '8', 'Na to rzekł do mnie: \'Uważaj, byś tego nie czynił, bo jestem współsługą twoim i braci twoich proroków, i tych, którzy strzegą słów tej księgi. Bogu samemu złóż pokłon!\'');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '9', '9', 'Dalej powiedział do mnie: \'Nie kładź pieczęci na słowa proroctwa tej księgi, bo chwila jest bliska.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '10', '10', 'Kto krzywdzi, niech jeszcze krzywdę wyrządzi, i plugawy niech się jeszcze splugawi, a sprawiedliwy niech jeszcze wypełni sprawiedliwość, a święty niechaj się jeszcze uświęci.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '11', '11', 'Oto przyjdę niebawem i mam ze sobą zapłatę: i oddam każdemu według jego pracy.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '12', '12', 'Jam Alfa i Omega, Pierwszy i Ostatni, Początek i Koniec.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '13', '13', 'Szczęśliwi, którzy płuczą swe szaty, aby władza nad drzewem życia do nich należała i aby bramami wchodzili do Miasta.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '14', '14', 'Na zewnątrz są psy, guślarze, rozpustnicy, zabójcy, bałwochwalcy i każdy, kto kłamstwo kocha i nim żyje.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '15', '15', 'Ja, Jezus, posłałem mojego anioła, aby wam świadczyć o tym, co dotyczy Kościoła. Jam jest Odrośl i Potomstwo Dawida, Jasna gwiazda poranna\'.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '16', '16', 'A Duch i Oblubienica mówią: \'Przyjdź!\' A kto słyszy, niech powie: \'Przyjdź!\' I kto spragniony, niech przyjdzie, kto chce, niech wody życia darmo zaczerpnie.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '17', '17', 'Ja świadczę każdemu, kto słucha proroczych słów tej księgi: jeśliby ktoś cokolwiek do nich dodał, to Bóg doda mu plag zapisanych w tej księdze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '18', '18', 'A jeśliby ktoś ujął co z proroczych słów tej księgi, Bóg ujmie jego uczestnictwo w drzewie życia i w Mieście Świętym, opisanych w tej księdze.');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '19', '19', 'Mówi Ten, który o tym świadczy: \'Zaiste, przyjdę niebawem\'. Amen. Przyjdź, Panie Jezu!');
INSERT INTO %s (parent_id, verses_type, translation_id, numbering_id, book, chapter, start, stop, content) VALUES (0, 2, 6, 2, 'rev', '21', '20', '20', 'Łaska Pana Jezusa ze wszystkimi!');
